  INSERT INTO words (word, word_type, description) VALUES  (    'Smoke', 'n.', 'Hence, to burn; to be kindled; to rage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoke', 'n.', 'To raise a dust or smoke by rapid motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoke', 'n.', 'To draw into the mouth the smoke of tobacco burning in a\n   pipe or in the form of a cigar, cigarette, etc.; to habitually use\n   tobacco in this manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoke', 'n.', 'To suffer severely; to be punished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoke', 'v. t.', 'To apply smoke to; to hang in smoke; to disinfect, to\n   cure, etc., by smoke; as, to smoke or fumigate infected clothing; to\n   smoke beef or hams for preservation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoke', 'v. t.', 'To fill or scent with smoke; hence, to fill with incense;\n   to perfume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoke', 'v. t.', 'To smell out; to hunt out; to find out; to detect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoke', 'v. t.', 'To ridicule to the face; to quiz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoke', 'v. t.', 'To inhale and puff out the smoke of, as tobacco; to burn\n   or use in smoking; as, to smoke a pipe or a cigar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoke', 'v. t.', 'To subject to the operation of smoke, for the purpose of\n   annoying or driving out; -- often with out; as, to smoke a woodchuck\n   out of his burrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoke-dry', 'v. t.', 'To dry by or in smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smokehouse', 'n.', 'A building where meat or fish is cured by subjecting it\n   to a dense smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smokejack', 'n.', 'A contrivance for turning a spit by means of a fly or\n   wheel moved by the current of ascending air in a chimney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smokeless', 'a.', 'Making or having no smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoker', 'n.', 'One who dries or preserves by smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoker', 'n.', 'One who smokes tobacco or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoker', 'n.', 'A smoking car or compartment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smokestack', 'n.', 'A chimney; esp., a pipe serving as a chimney, as the\n   pipe which carries off the smoke of a locomotive, the funnel of a steam\n   vessel, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smokily', 'adv.', 'In a smoky manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smokiness', 'n.', 'The quality or state of being smoky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoking', '', 'a. & n. from Smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoky', 'superl.', 'Emitting smoke, esp. in large quantities or in an\n   offensive manner; fumid; as, smoky fires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoky', 'superl.', 'Having the appearance or nature of smoke; as, a smoky\n   fog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoky', 'superl.', 'Filled with smoke, or with a vapor resembling smoke;\n   thick; as, a smoky atmosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoky', 'superl.', 'Subject to be filled with smoke from chimneys or\n   fireplace; as, a smoky house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoky', 'superl.', 'Tarnished with smoke; noisome with smoke; as, smoky\n   rafters; smoky cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoky', 'superl.', 'Suspicious; open to suspicion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoldered', 'imp. & p. p.', 'of Smoulder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smouldered', '', 'of Smoulder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoldering', 'p. pr. & vb. n.', 'of Smoulder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smouldering', '', 'of Smoulder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smolder', 'v. i.', 'Alt. of Smoulder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoulder', 'v. i.', 'To burn and smoke without flame; to waste away by a\n   slow and supressed combustion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoulder', 'v. i.', 'To exist in a state of suppressed or smothered\n   activity; to burn inwardly; as, a smoldering feud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smolder', 'v. t.', 'Alt. of Smoulder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoulder', 'v. t.', 'To smother; to suffocate; to choke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smolder', 'n.', 'Alt. of Smoulder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoulder', 'n.', 'Smoke; smother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoldering', 'a.', 'Alt. of Smouldering');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smouldering', 'a.', 'Being in a state of suppressed activity; quiet but not\n   dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smolderingness', 'n.', 'Alt. of Smoulderingness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoulderingness', 'n.', 'The state of smoldering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoldry', 'a.', 'Alt. of Smouldry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smouldry', 'a.', 'Smoldering; suffocating; smothery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smolt', 'n.', 'A young salmon two or three years old, when it has acquired\n   its silvery color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooch', 'v. t.', 'See Smutch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoor', 'v. t.', 'To suffocate or smother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooth', 'superl.', 'Having an even surface, or a surface so even that no\n   roughness or points can be perceived by the touch; not rough; as,\n   smooth glass; smooth porcelain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooth', 'superl.', 'Evenly spread or arranged; sleek; as, smooth hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooth', 'superl.', 'Gently flowing; moving equably; not ruffled or\n   obstructed; as, a smooth stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooth', 'superl.', 'Flowing or uttered without check, obstruction, or\n   hesitation; not harsh; voluble; even; fluent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooth', 'superl.', 'Bland; mild; smoothing; fattering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooth', 'superl.', 'Causing no resistance to a body sliding along its\n   surface; frictionless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooth', 'adv.', 'Smoothly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooth', 'n.', 'The act of making smooth; a stroke which smooths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooth', 'n.', 'That which is smooth; the smooth part of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoothed', 'imp. & p. p.', 'of Smooth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoothing', 'p. pr. & vb. n.', 'of Smooth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooth', 'a.', 'To make smooth; to make even on the surface by any means;\n   as, to smooth a board with a plane; to smooth cloth with an iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooth', 'a.', 'To free from obstruction; to make easy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooth', 'a.', 'To free from harshness; to make flowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooth', 'a.', 'To palliate; to gloze; as, to smooth over a fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooth', 'a.', 'To give a smooth or calm appearance to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooth', 'a.', 'To ease; to regulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooth', 'v. i.', 'To flatter; to use blandishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoothbore', 'a.', 'Having a bore of perfectly smooth surface; --\n   distinguished from rifled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoothbore', 'n.', 'A smoothbore firearm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooth-chinned', 'a.', 'Having a smooth chin; beardless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoothen', 'v. t.', 'To make smooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoother', 'n.', 'One who, or that which, smooths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoothing', 'a. & n.', 'fr. Smooth, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoothly', 'adv.', 'In a smooth manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoothness', 'n.', 'Quality or state of being smooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooth-spoken', 'a.', 'Speaking smoothly; plausible; flattering;\n   smooth-tongued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smooth-tongued', 'a.', 'Having a smooth tongue; plausible; flattering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smore', 'v. t.', 'To smother. See Smoor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smorzando', 'a.', 'Alt. of Smorsato');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smorsato', 'a.', 'Growing gradually fainter and softer; dying away;\n   morendo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smote', '', 'imp. (/ rare p. p.) of Smite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoterlich', 'a.', 'Dirty; foul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smothered', 'imp. & p. p.', 'of Smother');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smothering', 'p. pr. & vb. n.', 'of Smother');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smother', 'v. t.', 'To destroy the life of by suffocation; to deprive of\n   the air necessary for life; to cover up closely so as to prevent\n   breathing; to suffocate; as, to smother a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smother', 'v. t.', 'To affect as by suffocation; to stife; to deprive of\n   air by a thick covering, as of ashes, of smoke, or the like; as, to\n   smother a fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smother', 'v. t.', 'Hence, to repress the action of; to cover from public\n   view; to suppress; to conceal; as, to smother ones displeasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smother', 'v. i.', 'To be suffocated or stifled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smother', 'v. i.', 'To burn slowly, without sufficient air; to smolder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smother', 'v. t.', 'Stifling smoke; thick dust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smother', 'v. t.', 'A state of suppression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smotheriness', 'n.', 'The quality or state of being smothery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smotheringly', 'adv.', 'In a smothering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smothery', 'a.', 'Tending to smother; stifling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smouch', 'v. t.', 'To kiss closely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smouch', 'v. t.', 'To smutch; to soil; as, to smouch the face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smouch', 'n.', 'A dark soil or stain; a smutch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smoulder', 'v. i.', 'See Smolder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smouldry', 'a.', 'See Smoldry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smudge', 'n.', 'A suffocating smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smudge', 'n.', 'A heap of damp combustibles partially ignited and burning\n   slowly, placed on the windward side of a house, tent, or the like, in\n   order, by the thick smoke, to keep off mosquitoes or other insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smudge', 'n.', 'That which is smeared upon anything; a stain; a blot; a\n   smutch; a smear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smudged', 'imp. & p. p.', 'of Smudge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smudging', 'p. pr. & vb. n.', 'of Smudge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smudge', 'v. t.', 'To stifle or smother with smoke; to smoke by means of a\n   smudge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smudge', 'v. t.', 'To smear; to smutch; to soil; to blacken with smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smudginess', 'n.', 'The quality or state of being smudged, soiled, or\n   blurred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smug', 'a.', 'Studiously neat or nice, especially in dress; spruce;\n   affectedly precise; smooth and prim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smugged', 'imp. & p. p.', 'of Smug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smugging', 'p. pr. & vb. n.', 'of Smug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smug', 'v. t.', 'To make smug, or spruce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smuggled', 'imp. & p. p.', 'of Smuggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smuggling', 'p. pr. & vb. n.', 'of Smuggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smuggle', 'v. t.', 'To import or export secretly, contrary to the law; to\n   import or export without paying the duties imposed by law; as, to\n   smuggle lace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smuggle', 'v. t.', 'Fig.: To convey or introduce clandestinely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smuggle', 'v. i.', 'To import or export in violation of the customs laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smuggler', 'n.', 'One who smuggles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smuggler', 'n.', 'A vessel employed in smuggling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smugly', 'adv.', 'In a smug manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smugness', 'n.', 'The quality or state of being smug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smut', 'v. t.', 'Foul matter, like soot or coal dust; also, a spot or soil\n   made by such matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smut', 'v. t.', 'Bad, soft coal, containing much earthy matter, found in\n   the immediate locality of faults.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smut', 'v. t.', 'An affection of cereal grains producing a swelling which\n   is at length resolved into a powdery sooty mass. It is caused by\n   parasitic fungi of the genus Ustilago. Ustilago segetum, or U. Carbo,\n   is the commonest kind; that of Indian corn is Ustilago maydis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smut', 'v. t.', 'Obscene language; ribaldry; obscenity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smutted', 'imp. & p. p.', 'of Smut');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smutting', 'p. pr. & vb. n.', 'of Smut');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smut', 'v. t.', 'To stain or mark with smut; to blacken with coal, soot, or\n   other dirty substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smut', 'v. t.', 'To taint with mildew, as grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smut', 'v. t.', 'To blacken; to sully or taint; to tarnish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smut', 'v. t.', 'To clear of smut; as, to smut grain for the mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smut', 'v. i.', 'To gather smut; to be converted into smut; to become\n   smutted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smut', 'v. i.', 'To give off smut; to crock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smutch', 'n.', 'A stain; a dirty spot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smutched', 'imp. & p. p.', 'of Smutch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smutching', 'p. pr. & vb. n.', 'of Smutch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smutch', 'v. t.', 'To blacken with smoke, soot, or coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smutchin', 'n.', 'Snuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smutty', 'superl.', 'Soiled with smut; smutted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smutty', 'superl.', 'Tainted with mildew; as, smutty corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smutty', 'superl.', 'Obscene; not modest or pure; as, a smutty saying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smyrniot', 'a.', 'Of or pertaining to Smyrna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Smyrniot', 'n.', 'A native or inhabitant of Smyrna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snack', 'v. t.', 'A share; a part or portion; -- obsolete, except in the\n   colloquial phrase, to go snacks, i. e., to share.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snack', 'v. t.', 'A slight, hasty repast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snacket', 'n.', 'See Snecket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snacot', 'n.', 'A pipefish of the genus Syngnathus. See Pipefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snaffle', 'n.', 'A kind of bridle bit, having a joint in the part to be\n   placed in the mouth, and rings and cheek pieces at the ends, but having\n   no curb; -- called also snaffle bit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snaffled', 'imp. & p. p.', 'of Snaffle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snaffling', 'p. pr. & vb. n.', 'of Snaffle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snaffle', 'v. t.', 'To put a snaffle in the mouth of; to subject to the\n   snaffle; to bridle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snag', 'n.', 'A stump or base of a branch that has been lopped off; a short\n   branch, or a sharp or rough branch; a knot; a protuberance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snag', 'n.', 'A tooth projecting beyond the rest; contemptuously, a broken\n   or decayed tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snag', 'n.', 'A tree, or a branch of a tree, fixed in the bottom of a river\n   or other navigable water, and rising nearly or quite to the surface, by\n   which boats are sometimes pierced and sunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snag', 'n.', 'One of the secondary branches of an antler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snagged', 'imp. & p. p.', 'of Snag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snagging', 'p. pr. & vb. n.', 'of Snag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snag', 'v. t.', 'To cut the snags or branches from, as the stem of a tree;\n   to hew roughly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snag', 'v. t.', 'To injure or destroy, as a steamboat or other vessel, by a\n   snag, or projecting part of a sunken tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snagged', 'a.', 'Full of snags; snaggy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snaggy', 'a.', 'Full of snags; full of short, rough branches or sharp\n   points; abounding with knots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snaggy', 'a.', 'Snappish; cross; ill-tempered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snail', 'n.', 'Any one of numerous species of terrestrial air-breathing\n   gastropods belonging to the genus Helix and many allied genera of the\n   family Helicidae. They are abundant in nearly all parts of the world\n   except the arctic regions, and feed almost entirely on vegetation; a\n   land snail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snail', 'n.', 'Any gastropod having a general resemblance to the true\n   snails, including fresh-water and marine species. See Pond snail, under\n   Pond, and Sea snail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snail', 'n.', 'Hence, a drone; a slow-moving person or thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snail', 'n.', 'A spiral cam, or a flat piece of metal of spirally curved\n   outline, used for giving motion to, or changing the position of,\n   another part, as the hammer tail of a striking clock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snail', 'n.', 'A tortoise; in ancient warfare, a movable roof or shed to\n   protect besiegers; a testudo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snail', 'n.', 'The pod of the sanil clover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snailfish', 'n.', 'See Sea snail (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snail-like', 'a.', 'Like or suiting a snail; as, snail-like progress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snail-like', 'adv.', 'In the manner of a snail; slowly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snail-paced', 'a.', 'Slow-moving, like a snail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snails', 'interj.', 'Gods nails, or His nails, that is, the nails with\n   which the Savior was fastened to the cross; -- an ancient form of oath,\n   corresponding to Ods bodikins (dim. of body, i.e., Gods dear body).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snake', 'n.', 'Any species of the order Ophidia; an ophidian; a serpent,\n   whether harmless or venomous. See Ophidia, and Serpent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snaked', 'imp. & p. p.', 'of Snake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snaking', 'p. pr. & vb. n.', 'of Snake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snake', 'v. t.', 'To drag or draw, as a snake from a hole; -- often with\n   out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snake', 'v. t.', 'To wind round spirally, as a large rope with a smaller,\n   or with cord, the small rope lying in the spaces between the strands of\n   the large one; to worm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snake', 'v. i.', 'To crawl like a snake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakebird', 'n.', 'Any one of four species of aquatic birds of the genus\n   Anhinga or Plotus. They are allied to the gannets and cormorants, but\n   have very long, slender, flexible necks, and sharp bills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakebird', 'n.', 'The wryneck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakefish', 'n.', 'The band fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakefish', 'n.', 'The lizard fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakehead', 'n.', 'A loose, bent-up end of one of the strap rails, or flat\n   rails, formerly used on American railroads. It was sometimes so bent by\n   the passage of a train as to slip over a wheel and pierce the bottom of\n   a car.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakehead', 'n.', 'The turtlehead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakehead', 'n.', 'The Guinea-hen flower. See Snakes-head, and under\n   Guinea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakeneck', 'n.', 'The snakebird, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakeroot', 'n.', 'Any one of several plants of different genera and\n   species, most of which are (or were formerly) reputed to be efficacious\n   as remedies for the bites of serpents; also, the roots of any of these.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakes-head', 'n.', 'The Guinea-hen flower; -- so called in England\n   because its spotted petals resemble the scales of a snakes head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakestone', 'n.', 'A kind of hone slate or whetstone obtained in Scotland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakestone', 'n.', 'An ammonite; -- so called from its form, which\n   resembles that of a coiled snake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakes-tongue', 'n.', 'Same as Adders-tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakeweed', 'n.', 'A kind of knotweed (Polygonum Bistorta).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakeweed', 'n.', 'The Virginia snakeroot. See Snakeroot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakewood', 'n.', 'An East Indian climbing plant (Strychnos colubrina)\n   having a bitter taste, and supposed to be a remedy for the bite of the\n   hooded serpent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakewood', 'n.', 'An East Indian climbing shrub (Ophioxylon serpentinum)\n   which has the roots and stems twisted so as to resemble serpents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakewood', 'n.', 'Same as Trumpetwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakewood', 'n.', 'A tropical American shrub (Plumieria rubra) which has\n   very fragrant red blossoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakewood', 'n.', 'Same as Letterwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snakish', 'a.', 'Having the qualities or characteristics of a snake; snaky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snaky', 'a.', 'Of or pertaining to a snake or snakes; resembling a snake;\n   serpentine; winding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snaky', 'a.', 'Sly; cunning; insinuating; deceitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snaky', 'a.', 'Covered with serpents; having serpents; as, a snaky rod or\n   wand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snapped', 'imp. & p. p.', 'of Snap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snapping', 'p. pr. & vb. n.', 'of Snap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'n.', 'To break at once; to break short, as substances that are\n   brittle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'n.', 'To strike, to hit, or to shut, with a sharp sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'n.', 'To bite or seize suddenly, especially with the teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'n.', 'To break upon suddenly with sharp, angry words; to treat\n   snappishly; -- usually with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'n.', 'To crack; to cause to make a sharp, cracking noise; as, to\n   snap a whip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'n.', 'To project with a snap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'v. i.', 'To break short, or at once; to part asunder suddenly; as,\n   a mast snaps; a needle snaps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'v. i.', 'To give forth, or produce, a sharp, cracking noise; to\n   crack; as, blazing firewood snaps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'v. i.', 'To make an effort to bite; to aim to seize with the teeth;\n   to catch eagerly (at anything); -- often with at; as, a dog snapsat a\n   passenger; a fish snaps at the bait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'v. i.', 'To utter sharp, harsh, angry words; -- often with at; as,\n   to snap at a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'v. i.', 'To miss fire; as, the gun snapped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'v. t.', 'A sudden breaking or rupture of any substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'v. t.', 'A sudden, eager bite; a sudden seizing, or effort to\n   seize, as with the teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'v. t.', 'A sudden, sharp motion or blow, as with the finger sprung\n   from the thumb, or the thumb from the finger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'v. t.', 'A sharp, abrupt sound, as that made by the crack of a\n   whip; as, the snap of the trigger of a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'v. t.', 'A greedy fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'v. t.', 'That which is, or may be, snapped up; something bitten\n   off, seized, or obtained by a single quick movement; hence, a bite,\n   morsel, or fragment; a scrap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'v. t.', 'A sudden severe interval or spell; -- applied to the\n   weather; as, a cold snap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'v. t.', 'A small catch or fastening held or closed by means of a\n   spring, or one which closes with a snapping sound, as the catch of a\n   bracelet, necklace, clasp of a book, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'v. t.', 'A snap beetle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'v. t.', 'A thin, crisp cake, usually small, and flavored with\n   ginger; -- used chiefly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'v. t.', 'Briskness; vigor; energy; decision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snap', 'v. t.', 'Any circumstance out of which money may be made or an\n   advantage gained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snapdragon', 'n.', 'Any plant of the scrrophulariaceous genus Antirrhinum,\n   especially the cultivated A. majus, whose showy flowers are fancifully\n   likened to the face of a dragon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snapdragon', 'n.', 'A West Indian herb (Ruellia tuberosa) with curiously\n   shaped blue flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snapdragon', 'n.', 'A play in which raisins are snatched from a vessel\n   containing burning brandy, and eaten; also, that which is so eaten. See\n   Flapdragon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snape', 'v. t.', 'To bevel the end of a timber to fit against an inclined\n   surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snaphance', 'n.', 'A spring lock for discharging a firearm; also, the\n   firearm to which it is attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snaphance', 'n.', 'A trifling or second-rate thing or person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snaphead', 'n.', 'A hemispherical or rounded head to a rivet or bolt; also,\n   a swaging tool with a cavity in its face for forming such a rounded\n   head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snapper', 'n.', 'One who, or that which, snaps; as, a snapper up of\n   trifles; the snapper of a whip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snapper', 'n.', 'Any one of several species of large sparoid food fishes of\n   the genus Lutjanus, abundant on the southern coasts of the United\n   States and on both coasts of tropical America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snapper', 'n.', 'A snapping turtle; as, the alligator snapper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snapper', 'n.', 'The green woodpecker, or yaffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snapper', 'n.', 'A snap beetle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snapping', '', 'a. & n. from Snap, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snappish', 'a.', 'Apt to snap at persons or things; eager to bite; as, a\n   snapping cur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snappish', 'a.', 'Sharp in reply; apt to speak angrily or testily; easily\n   provoked; tart; peevish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snappy', 'a.', 'Snappish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snapsack', 'n.', 'A knapsack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snapweed', 'n.', 'See Impatiens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snar', 'v. i.', 'To snarl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snare', 'n.', 'A contrivance, often consisting of a noose of cord, or the\n   like, by which a bird or other animal may be entangled and caught; a\n   trap; a gin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snare', 'n.', 'Hence, anything by which one is entangled and brought into\n   trouble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snare', 'n.', 'The gut or string stretched across the lower head of a drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snare', 'n.', 'An instrument, consisting usually of a wireloop or noose,\n   for removing tumors, etc., by avulsion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snared', 'imp. & p. p.', 'of Snare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snaring', 'p. pr. & vb. n.', 'of Snare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snare', 'v. t.', 'To catch with a snare; to insnare; to entangle; hence, to\n   bring into unexpected evil, perplexity, or danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snarer', 'n.', 'One who lays snares, or entraps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snarled', 'imp. & p. p.', 'of Snarl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snarling', 'p. pr. & vvb. n.', 'of Snarl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snarl', 'v. t.', 'To form raised work upon the outer surface of (thin metal\n   ware) by the repercussion of a snarling iron upon the inner surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snarl', 'v. t.', 'To entangle; to complicate; to involve in knots; as, to\n   snarl a skein of thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snarl', 'v. t.', 'To embarrass; to insnare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snarl', 'n.', 'A knot or complication of hair, thread, or the like,\n   difficult to disentangle; entanglement; hence, intricate complication;\n   embarrassing difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snarl', 'v. i.', 'To growl, as an angry or surly dog; to gnarl; to utter\n   grumbling sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snarl', 'v. i.', 'To speak crossly; to talk in rude, surly terms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snarl', 'n.', 'The act of snarling; a growl; a surly or peevish expression;\n   an angry contention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snarler', 'n.', 'One who snarls; a surly, growling animal; a grumbling,\n   quarrelsome fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snarler', 'n.', 'One who makes use of a snarling iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snarling', '', 'a. & n. from Snarl, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snary', 'a.', 'Resembling, or consisting of, snares; entangling; insidious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snast', 'v. t.', 'The snuff, or burnt wick, of a candle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snatched', 'imp. & p. p.', 'of Snatch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snatching', 'p. pr. & vb. n.', 'of Snatch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snatch', 'n.', 'To take or seize hastily, abruptly, or without permission\n   or ceremony; as, to snatch a loaf or a kiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snatch', 'n.', 'To seize and transport away; to rap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snatch', 'v. i.', 'To attempt to seize something suddenly; to catch; --\n   often with at; as, to snatch at a rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snatch', 'n.', 'A hasty catching or seizing; a grab; a catching at, or\n   attempt to seize, suddenly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snatch', 'n.', 'A short period of vigorous action; as, a snatch at weeding\n   after a shower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snatch', 'n.', 'A small piece, fragment, or quantity; a broken part; a\n   scrap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snatch block', '', 'a kind of block with an opening in one side to receive\n   the bight of a rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snatcher', 'n.', 'One who snatches, or takes abruptly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snatchingly', 'adv.', 'By snatching; abruptly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snatch', 'n.', 'The handle of a scythe; a snead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snathe', 'v. t.', 'To lop; to prune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snattock', 'n.', 'A chip; a alice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snaw', 'n.', 'Snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snead', 'n.', 'A snath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snead', 'n.', 'A line or cord; a string.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneaked', 'imp. & p. p.', 'of Sneak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneaking', 'p. pr. & vb. n.', 'of Sneak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneak', 'v. i.', 'To creep or steal (away or about) privately; to come or\n   go meanly, as a person afraid or ashamed to be seen; as, to sneak away\n   from company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneak', 'imp. & p. p.', 'To act in a stealthy and cowardly manner; to\n   behave with meanness and servility; to crouch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneak', 'v. t.', 'To hide, esp. in a mean or cowardly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneak', 'n.', 'A mean, sneaking fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneak', 'n.', 'A ball bowled so as to roll along the ground; -- called also\n   grub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneak-cup', 'n.', 'One who sneaks from his cups; one who balks his glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneaker', 'n.', 'One who sneaks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneaker', 'n.', 'A vessel of drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneakiness', 'n.', 'The quality of being sneaky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneaking', 'a.', 'Marked by cowardly concealment; deficient in openness and\n   courage; underhand; mean; crouching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneaksby', 'n.', 'A paltry fellow; a sneak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneaky', 'n.', 'Like a sneak; sneaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneap', 'v. t.', 'To check; to reprimand; to rebuke; to chide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneap', 'v. t.', 'To nip; to blast; to blight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneap', 'n.', 'A reprimand; a rebuke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneath', 'n.', 'Alt. of Sneathe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneathe', 'n.', 'See Snath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneb', 'v. t.', 'To reprimand; to sneap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneck', 'v. t.', 'To fasten by a hatch; to latch, as a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneck', 'n.', 'A door latch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snecket', 'n.', 'A door latch, or sneck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sned', 'v. t.', 'To lop; to snathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sned', 'n.', 'Alt. of Sneed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneed', 'n.', 'See Snath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneered', 'imp. & p. p.', 'of Sneer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneering', 'p. pr. & vb. n.', 'of Sneer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneer', 'v. i.', 'To show contempt by turning up the nose, or by a\n   particular facial expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneer', 'v. i.', 'To inssinuate contempt by a covert expression; to speak\n   derisively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneer', 'v. i.', 'To show mirth awkwardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneer', 'v. t.', 'To utter with a grimace or contemptuous expression; to\n   utter with a sneer; to say sneeringly; as, to sneer fulsome lies at a\n   person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneer', 'v. t.', 'To treat with sneers; to affect or move by sneers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneer', 'n.', 'The act of sneering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneer', 'n.', 'A smile, grin, or contortion of the face, indicative of\n   contempt; an indirect expression or insinuation of contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneerer', 'n.', 'One who sneers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneerful', 'a.', 'Given to sneering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneeringly', 'adv.', 'In a sneering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneezed', 'imp. & p. p.', 'of Sneeze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneezing', 'p. pr. & vb. n.', 'of Sneeze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneeze', 'v. i.', 'To emit air, chiefly through the nose, audibly and\n   violently, by a kind of involuntary convulsive force, occasioned by\n   irritation of the inner membrane of the nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneeze', 'n.', 'A sudden and violent ejection of air with an audible sound,\n   chiefly through the nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneezeweed', 'n.', 'A yellow-flowered composite plant (Helenium autumnale)\n   the odor of which is said to cause sneezing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneezewood', 'n.', 'The wood of a South African tree. See Neishout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneezewort', 'n.', 'A European herbaceous plant (Achillea Ptarmica) allied\n   to the yarrow, having a strong, pungent smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sneezing', 'n.', 'The act of violently forcing air out through the nasal\n   passages while the cavity of the mouth is shut off from the pharynx by\n   the approximation of the soft palate and the base of the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snell', 'a.', 'Active; brisk; nimble; quick; sharp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snell', 'n.', 'A short line of horsehair, gut, etc., by which a fishhook is\n   attached to a longer line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snet', 'n.', 'The fat of a deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snet', 'v. t.', 'The clear of mucus; to blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snew', 'v. i.', 'To snow; to abound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snib', 'v. t.', 'To check; to sneap; to sneb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snib', 'n.', 'A reprimand; a snub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snick', 'n.', 'A small cut or mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snick', 'n.', 'A slight hit or tip of the ball, often unintentional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snick', 'n.', 'A knot or irregularity in yarn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snick', 'n.', 'A snip or cut, as in the hair of a beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snicked', 'imp. & p. p.', 'of Snick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snicking', 'p. pr. & vb. n.', 'of Snick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snick', 'v. t.', 'To cut slightly; to strike, or strike off, as by cutting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snick', 'v. t.', 'To hit (a ball) lightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snick', 'n. & v. t.', 'See Sneck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snickered', 'imp. & p. p.', 'of Snicker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snickering', 'p. pr. & vb. n.', 'of Snicker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snicker', 'v. i.', 'To laugh slyly; to laugh in ones sleeve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snicker', 'v. i.', 'To laugh with audible catches of voice, as when persons\n   attempt to suppress loud laughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snicker', 'n.', 'A half suppressed, broken laugh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snide', 'a.', 'Tricky; deceptive; contemptible; as, a snide lawyer; snide\n   goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sniffed', 'imp. & p. p.', 'of Sniff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snift', '', 'of Sniff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sniffing', 'p. pr. & vb. n.', 'of Sniff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sniff', 'v. t.', 'To draw air audibly up the nose; to snuff; -- sometimes\n   done as a gesture of suspicion, offense, or contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sniff', 'v. t.', 'To draw in with the breath through the nose; as, to sniff\n   the air of the country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sniff', 'v. t.', 'To perceive as by sniffing; to snuff, to scent; to smell;\n   as, to sniff danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sniff', 'n.', 'The act of sniffing; perception by sniffing; that which is\n   taken by sniffing; as, a sniff of air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sniffing', 'n.', 'A rapid inspiratory act, in which the mouth is kept shut\n   and the air drawn in through the nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sniffle', 'v. i.', 'To snuffle, as one does with a catarrh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snifted', 'imp. & p. p.', 'of Snift');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snifting', 'p. pr. & vb. n.', 'of Snift');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snift', 'v. i.', 'To snort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snift', 'v. i.', 'To sniff; to snuff; to smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snift', 'n.', 'A moment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snift', 'n.', 'Slight snow; sleet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snifting', '', 'a. & n. from Snift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snig', 'v. t.', 'To chop off; to cut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snig', 'v. i.', 'To sneak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snig', 'n.', 'Alt. of Snigg');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snigg', 'n.', 'A small eel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sniggger', 'v. i.', 'See Snicker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snigger', 'n.', 'See Snicker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sniggled', 'imp. & p. p.', 'of Sniggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sniggling', 'p. pr. & vb. n.', 'of Sniggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sniggle', 'v. i.', 'To fish for eels by thrusting the baited hook into\n   their holes or hiding places.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sniggle', 'v. t.', 'To catch, as an eel, by sniggling; hence, to hook; to\n   insnare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snipped', 'imp. & p. p.', 'of Snip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snipping', 'p. pr. & vb. n.', 'of Snip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snip', 'v. t.', 'To cut off the nip or neb of, or to cut off at once with\n   shears or scissors; to clip off suddenly; to nip; hence, to break off;\n   to snatch away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snip', 'n.', 'A single cut, as with shears or scissors; a clip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snip', 'n.', 'A small shred; a bit cut off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snip', 'n.', 'A share; a snack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snip', 'n.', 'A tailor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snip', 'n.', 'Small hand shears for cutting sheet metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snipe', 'n.', 'Any one of numerous species of limicoline game birds of the\n   family Scolopacidae, having a long, slender, nearly straight beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snipe', 'n.', 'A fool; a blockhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snipebill', 'n.', 'A plane for cutting deep grooves in moldings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snipebill', 'n.', 'A bolt by which the body of a cart is fastened to the\n   axle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snipefish', 'n.', 'The bellows fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snipefish', 'n.', 'A long, slender deep-sea fish (Nemichthys scolopaceus)\n   with a slender beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snippack', 'n.', 'The common snipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snipper', 'n.', 'One who snips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snipper-snaper', 'n.', 'A small, insignificant fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snippet', 'n.', 'A small part or piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snippety', 'a.', 'Ridiculously small; petty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snip-snap', 'n.', 'A tart dialogue with quick replies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snip-snap', 'a.', 'Quick; short; sharp; smart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snypy', 'a.', 'Like a snipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snite', 'n.', 'A snipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snite', 'v. t.', 'To blow, as the nose; to snuff, as a candle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snithe', 'a.', 'Alt. of Snithy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snithy', 'a.', 'Sharp; piercing; cutting; -- applied to the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sniveled', 'imp. & p. p.', 'of Snivel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snivelled', '', 'of Snivel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sniveling', 'p. pr. & vb. n.', 'of Snivel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snivelling', '', 'of Snivel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snivel', 'v. i.', 'To run at the nose; to make a snuffling noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snivel', 'v. i.', 'To cry or whine with snuffling, as children; to cry\n   weakly or whiningly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snivel', 'v. i.', 'Mucus from the nose; snot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sniveler', 'n.', 'One who snivels, esp. one who snivels habitually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snively', 'a.', 'Running at the nose; sniveling pitiful; whining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snob', 'n.', 'A vulgar person who affects to be better, richer, or more\n   fashionable, than he really is; a vulgar upstart; one who apes his\n   superiors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snob', 'n.', 'A townsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snob', 'n.', 'A journeyman shoemaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snob', 'n.', 'A workman who accepts lower than the usual wages, or who\n   refuses to strike when his fellows do; a rat; a knobstick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snobbery', 'n.', 'The quality of being snobbish; snobbishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snobbish', 'a.', 'Of or pertaining to a snob; characteristic of, or\n   befitting, a snob; vulgarly pretentious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snobbishness', 'n.', 'Vulgar affectation or ostentation; mean admiration of\n   mean things; conduct or manners of a snob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snobbism', 'n.', 'Snobbery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snobby', 'a.', 'Snobbish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snobling', 'n.', 'A little snob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snobocracy', 'n.', 'Snobs, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snod', 'n.', 'A fillet; a headband; a snood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snod', 'a.', 'Trimmed; smooth; neat; trim; sly; cunning; demure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snoff', 'n.', 'A short candle end used for igniting a fuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snood', 'n.', 'The fillet which binds the hair of a young unmarried woman,\n   and is emblematic of her maiden character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snood', 'n.', 'A short line (often of horsehair) connecting a fishing line\n   with the hook; a snell; a leader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snood', 'v. t.', 'To bind or braid up, as the hair, with a snood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snooded', 'a.', 'Wearing or having a snood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snook', 'v. i.', 'To lurk; to lie in ambush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snook', 'n.', 'A large perchlike marine food fish (Centropomus undecimalis)\n   found both on the Atlantic and Pacific coasts of tropical America; --\n   called also ravallia, and robalo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snook', 'n.', 'The cobia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snook', 'n.', 'The garfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snooze', 'n.', 'A short sleep; a nap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snoozed', 'imp. & p. p.', 'of Snooze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snoozing', 'p. pr. & vb. n.', 'of Snooze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snooze', 'v. i.', 'To doze; to drowse; to take a short nap; to slumber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snored', 'imp. & p. p.', 'of Snore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snoring', 'p. pr. & vb. n.', 'of Snore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snore', 'v. i.', 'To breathe with a rough, hoarse, nasal voice in sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snore', 'n.', 'A harsh nasal noise made in sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snorer', 'n.', 'One who snores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snoring', 'n.', 'The act of respiring through the open mouth so that the\n   currents of inspired and expired air cause a vibration of the uvula and\n   soft palate, thus giving rise to a sound more or less harsh. It is\n   usually unvoluntary, but may be produced voluntarily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snorted', 'imp. & p. p.', 'of Snort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snorting', 'p. pr. & vb. n.', 'of Snort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snort', 'v. i.', 'To force the air with violence through the nose, so as to\n   make a noise, as do high-spirited horsed in prancing and play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snort', 'v. i.', 'To snore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snort', 'v. i.', 'To laugh out loudly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snort', 'n.', 'The act of snorting; the sound produced in snorting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snort', 'v. t.', 'To expel throught the nostrils with a snort; to utter\n   with a snort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snorter', 'n.', 'One who snorts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snorter', 'n.', 'The wheather; -- so called from its cry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snot', 'n.', 'Mucus secreted in, or discharged from, the nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snot', 'n.', 'A mean, insignificant fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snot', 'v. t.', 'To blow, wipe, or clear, as the nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snotter', 'v. i.', 'To snivel; to cry or whine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snotter', 'n.', 'A rope going over a yardarm, used to bend a tripping line\n   to, in sending down topgallant and royal yards in vessels of war; also,\n   the short line supporting the heel of the sprit in a small boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snottery', 'n.', 'Filth; abomination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snotty', 'a.', 'Foul with snot; hence, mean; dirty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snout', 'n.', 'The long, projecting nose of a beast, as of swine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snout', 'n.', 'The nose of a man; -- in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snout', 'n.', 'The nozzle of a pipe, hose, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snout', 'n.', 'The anterior prolongation of the head of a gastropod; --\n   called also rostrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snout', 'n.', 'The anterior prolongation of the head of weevils and allied\n   beetles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snout', 'v. t.', 'To furnish with a nozzle or point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snouty', 'a.', 'Resembling a beasts snout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snow', 'n.', 'A square-rigged vessel, differing from a brig only in that\n   she has a trysail mast close abaft the mainmast, on which a large\n   trysail is hoisted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snow', 'n.', 'Watery particles congealed into white or transparent crystals\n   or flakes in the air, and falling to the earth, exhibiting a great\n   variety of very beautiful and perfect forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snow', 'n.', 'Fig.: Something white like snow, as the white color (argent)\n   in heraldry; something which falls in, or as in, flakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowed', 'imp. & p. p.', 'of Snow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowing', 'p. pr. & vb. n.', 'of Snow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snow', 'v. i.', 'To fall in or as snow; -- chiefly used impersonally; as,\n   it snows; it snowed yesterday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snow', 'v. t.', 'To scatter like snow; to cover with, or as with, snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowball', 'n.', 'A round mass of snow pressed or roller together, or\n   anything resembling such a mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowball', 'n.', 'The Guelder-rose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowballed', 'imp. & p. p.', 'of Snowball');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowballing', 'p. pr. & vb. n.', 'of Snowball');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowball', 'v. t.', 'To pelt with snowballs; to throw snowballs at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowball', 'v. i.', 'To throw snowballs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowberry', 'n.', 'A name of several shrubs with white berries; as, the\n   Symphoricarpus racemosus of the Northern United States, and the\n   Chiococca racemosa of Florida and tropical America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowbird', 'n.', 'An arctic finch (Plectrophenax, / Plectrophanes, nivalis)\n   common, in winter, both in Europe and the United States, and often\n   appearing in large flocks during snowstorms. It is partially white, but\n   variously marked with chestnut and brown. Called also snow bunting,\n   snowflake, snowfleck, and snowflight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowbird', 'n.', 'Any finch of the genus Junco which appears in flocks in\n   winter time, especially J. hyemalis in the Eastern United States; --\n   called also blue snowbird. See Junco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowbird', 'n.', 'The fieldfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snow-blind', 'a.', 'Affected with blindness by the brilliancy of snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snow-bound', 'a.', 'Enveloped in, or confined by, snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snow-broth', 'n.', 'Snow and water mixed, or snow just melted; very cold\n   liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowcap', 'n.', 'A very small humming bird (Microchaera albocoronata)\n   native of New Grenada.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snow-capped', 'a.', 'Having the top capped or covered with snow; as,\n   snow-capped mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowdrift', 'n.', 'A bank of drifted snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowdrop', 'n.', 'A bulbous plant (Galanthus nivalis) bearing white\n   flowers, which often appear while the snow is on the ground. It is\n   cultivated in gardens for its beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowflake', 'n.', 'A flake, or small filmy mass, of snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowflake', 'n.', 'See Snowbird, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowflake', 'n.', 'A name given to several bulbous plants of the genus\n   Leucoium (L. vernum, aestivum, etc.) resembling the snowdrop, but\n   having all the perianth leaves of equal size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowfleck', 'n.', 'See Snowbird, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowl', 'n.', 'The hooded merganser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowless', 'a.', 'Destitute of snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowplow', 'n.', 'Alt. of Snowplough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowplough', 'n.', 'An implement operating like a plow, but on a larger\n   scale, for clearing away the snow from roads, railways, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowshed', 'n.', 'A shelter to protect from snow, esp. a long roof over an\n   exposed part of a railroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowshoe', 'n.', 'A slight frame of wood three or four feet long and about\n   one third as wide, with thongs or cords stretched across it, and having\n   a support and holder for the foot; -- used by persons for walking on\n   soft snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowshoer', 'n.', 'One who travels on snowshoes; an expert in using\n   snowshoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowshoeing', 'n.', 'Traveling on snowshoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowslip', 'n.', 'A large mass or avalanche of snow which slips down the\n   side of a mountain, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowstorm', 'n.', 'A storm with falling snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snow-white', 'a.', 'White as snow; very white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowy', 'a.', 'White like snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowy', 'a.', 'Abounding with snow; covered with snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snowy', 'a.', 'Fig.: Pure; unblemished; unstained; spotless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snub', 'v. i.', 'To sob with convulsions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snubbed', 'imp. & p. p.', 'of Snub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snubbing', 'p. pr. & vb. n.', 'of Snub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snub', 'v. t.', 'To clip or break off the end of; to check or stunt the\n   growth of; to nop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snub', 'v. t.', 'To check, stop, or rebuke, with a tart, sarcastic reply or\n   remark; to reprimand; to check.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snub', 'v. t.', 'To treat with contempt or neglect, as a forward or\n   pretentious person; to slight designedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snub', 'n.', 'A knot; a protuberance; a song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snub', 'n.', 'A check or rebuke; an intended slight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snub-nosed', 'a.', 'Having a short, flat nose, slightly turned up; as, the\n   snub-nosed eel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snudge', 'v. i.', 'To lie snug or quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snudge', 'n.', 'A miser; a sneaking fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuff', 'v. t.', 'The part of a candle wick charred by the flame, whether\n   burning or not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuffed', 'imp. & p. p.', 'of Snuff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuffing', 'p. pr. & vb. n.', 'of Snuff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuff', 'v. t.', 'To crop the snuff of, as a candle; to take off the end of\n   the snuff of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuff', 'v. i.', 'To draw in, or to inhale, forcibly through the nose; to\n   sniff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuff', 'v. i.', 'To perceive by the nose; to scent; to smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuff', 'v. i.', 'To inhale air through the nose with violence or with\n   noise, as do dogs and horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuff', 'v. i.', 'To turn up the nose and inhale air, as an expression of\n   contempt; hence, to take offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuff', 'n.', 'The act of snuffing; perception by snuffing; a sniff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuff', 'n.', 'Pulverized tobacco, etc., prepared to be taken into the\n   nose; also, the amount taken at once.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuff', 'n.', 'Resentment, displeasure, or contempt, expressed by a\n   snuffing of the nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuffbox', 'n.', 'A small box for carrying snuff about the person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuffer', 'n.', 'One who snuffs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuffer', 'n.', 'The common porpoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuffers', 'n. pl.', 'An instrument for cropping and holding the snuff of a\n   candle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuffingly', 'adv.', 'In a snuffing manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuffled', 'imp. & p. p.', 'of Snuffle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuffling', 'p. pr. & vb. n.', 'of Snuffle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuffle', 'v. i.', 'To speak through the nose; to breathe through the nose\n   when it is obstructed, so as to make a broken sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuffle', 'n.', 'The act of snuffing; a sound made by the air passing\n   through the nose when obstructed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuffle', 'n.', 'An affected nasal twang; hence, cant; hypocrisy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuffle', 'n.', 'Obstruction of the nose by mucus; nasal catarrh of infants\n   or children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuffler', 'n.', 'One who snuffles; one who uses cant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuffy', 'a.', 'Soiled with snuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuffy', 'a.', 'Sulky; angry; vexed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snug', 'superl.', 'Close and warm; as, an infant lies snug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snug', 'superl.', 'Close; concealed; not exposed to notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snug', 'superl.', 'Compact, convenient, and comfortable; as, a snug farm,\n   house, or property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snug', 'n.', 'Same as Lug, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snugged', 'imp. & p. p.', 'of Snug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snugging', 'p. pr. & vb. n.', 'of Snug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snug', 'v. i.', 'To lie close; to snuggle; to snudge; -- often with up, or\n   together; as, a child snugs up to its mother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snug', 'v. t.', 'To place snugly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snug', 'v. t.', 'To rub, as twine or rope, so as to make it smooth and\n   improve the finish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuggeries', 'pl. ', 'of Snuggery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuggery', 'n.', 'A snug, cozy place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuggled', 'imp. & p. p.', 'of Snuggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuggling', 'p. pr. & vb. n.', 'of Snuggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snuggle', 'v. t.', 'To move one way and the other so as to get a close\n   place; to lie close for comfort; to cuddle; to nestle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snugly', 'adv.', 'In a snug manner; closely; safely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snugness', 'n.', 'The quality or state of being snug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sny', 'n.', 'An upward bend in a piece of timber; the sheer of a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Snying', 'n.', 'A curved plank, placed edgewise, to work in the bows of a\n   vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'So', 'adv.', 'In that manner or degree; as, indicated (in any way), or as\n   implied, or as supposed to be known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'So', 'adv.', 'In like manner or degree; in the same way; thus; for like\n   reason; whith equal reason; -- used correlatively, following as, to\n   denote comparison or resemblance; sometimes, also, following inasmuch\n   as.');
  INSERT INTO words (word, word_type, description) VALUES  (    'So', 'adv.', 'In such manner; to such degree; -- used correlatively with as\n   or that following; as, he was so fortunate as to escape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'So', 'adv.', 'Very; in a high degree; that is, in such a degree as can not\n   well be expressed; as, he is so good; he planned so wisely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'So', 'adv.', 'In the same manner; as has been stated or suggested; in this\n   or that condition or state; under these circumstances; in this way; --\n   with reflex reference to something just asserted or implied; used also\n   with the verb to be, as a predicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'So', 'adv.', 'The case being such; therefore; on this account; for this\n   reason; on these terms; -- used both as an adverb and a conjuction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'So', 'adv.', 'It is well; let it be as it is, or let it come to pass; --\n   used to express assent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'So', 'adv.', 'Well; the fact being as stated; -- used as an expletive; as,\n   so the work is done, is it?');
  INSERT INTO words (word, word_type, description) VALUES  (    'So', 'adv.', 'Is it thus? do you mean what you say? -- with an upward tone;\n   as, do you say he refuses? So?');
  INSERT INTO words (word, word_type, description) VALUES  (    'So', 'adv.', 'About the number, time, or quantity specified; thereabouts;\n   more or less; as, I will spend a week or so in the country; I have read\n   only a page or so.');
  INSERT INTO words (word, word_type, description) VALUES  (    'So', 'conj.', 'Provided that; on condition that; in case that; if.');
  INSERT INTO words (word, word_type, description) VALUES  (    'So', 'interj.', 'Be as you are; stand still; stop; that will do; right as\n   you are; -- a word used esp. to cows; also used by sailors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soaked', 'imp. & p. p.', 'of Soak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soaking', 'p. pr. & vb. n.', 'of Soak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soak', 'v. t.', 'To cause or suffer to lie in a fluid till the substance\n   has imbibed what it can contain; to macerate in water or other liquid;\n   to steep, as for the purpose of softening or freshening; as, to soak\n   cloth; to soak bread; to soak salt meat, salt fish, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soak', 'v. t.', 'To drench; to wet thoroughly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soak', 'v. t.', 'To draw in by the pores, or through small passages; as, a\n   sponge soaks up water; the skin soaks in moisture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soak', 'v. t.', 'To make (its way) by entering pores or interstices; --\n   often with through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soak', 'v. t.', 'Fig.: To absorb; to drain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soak', 'v. i.', 'To lie steeping in water or other liquid; to become\n   sturated; as, let the cloth lie and soak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soak', 'v. i.', 'To enter (into something) by pores or interstices; as,\n   water soaks into the earth or other porous matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soak', 'v. i.', 'To drink intemperately or gluttonously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soakage', 'n.', 'The act of soaking, or the state of being soaked; also,\n   the quantity that enters or issues by soaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soaker', 'n.', 'One who, or that which, soaks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soaker', 'n.', 'A hard drinker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soaking', 'a.', 'Wetting thoroughly; drenching; as, a soaking rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soaky', 'a.', 'Full of moisture; wet; soppy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soal', 'n.', 'The sole of a shoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soal', 'n.', 'See Sole, the fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soal', 'n.', 'A dirty pond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soam', 'n.', 'A chain by which a leading horse draws a plow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soap', 'n.', 'A substance which dissolves in water, thus forming a lather,\n   and is used as a cleansing agent. Soap is produced by combining fats or\n   oils with alkalies or alkaline earths, usually by boiling, and consists\n   of salts of sodium, potassium, etc., with the fatty acids (oleic,\n   stearic, palmitic, etc.). See the Note below, and cf. Saponification.\n   By extension, any compound of similar composition or properties,\n   whether used as a cleaning agent or not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soaped', 'imp. & p. p.', 'of Soap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soaping', 'p. pr. & vb. n.', 'of Soap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soap', 'v. t.', 'To rub or wash over with soap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soap', 'v. t.', 'To flatter; to wheedle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soapberry tree', '', 'Any tree of the genus Sapindus, esp. Sapindus\n   saponaria, the fleshy part of whose fruit is used instead of soap in\n   washing linen; -- also called soap tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soapfish', 'n.', 'Any serranoid fish of the genus Rhypticus; -- so called\n   from the soapy feeling of its skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soapiness', 'n.', 'Quality or state of being soapy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soaproot', 'n.', 'A perennial herb (Gypsophila Struthium) the root of which\n   is used in Spain as a substitute for soap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soapstone', 'n.', 'See Steatite, and Talc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soapsuds', 'n. pl.', 'Suds made with soap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soapwort', 'n.', 'A common plant (Saponaria officinalis) of the Pink\n   family; -- so called because its bruised leaves, when agitated in\n   water, produce a lather like that from soap. Called also Bouncing Bet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soapy', 'superl.', 'Resembling soap; having the qualities of, or feeling\n   like, soap; soft and smooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soapy', 'superl.', 'Smeared with soap; covered with soap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soared', 'imp. & p. p.', 'of Soar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soaring', 'p. pr. & vb. n.', 'of Soar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soar', 'v. i.', 'To fly aloft, as a bird; to mount upward on wings, or as\n   on wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soar', 'v. i.', 'Fig.: To rise in thought, spirits, or imagination; to be\n   exalted in mood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soar', 'n.', 'The act of soaring; upward flight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soar', 'a.', 'See 3d Sore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soar', 'a.', 'See Sore, reddish brown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soaring', '', 'a. & n. from Soar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soave', 'a.', 'Sweet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soavemente', 'adv.', 'Sweetly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sob', 'v. t.', 'To soak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sobbed', 'imp. & p. p.', 'of Sob');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sobbing', 'p. pr. & vb. n.', 'of Sob');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sob', 'v. i.', 'To sigh with a sudden heaving of the breast, or with a kind\n   of convulsive motion; to sigh with tears, and with a convulsive drawing\n   in of the breath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sob', 'n.', 'The act of sobbing; a convulsive sigh, or inspiration of the\n   breath, as in sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sob', 'n.', 'Any sorrowful cry or sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sobbing', 'n.', 'A series of short, convulsive inspirations, the glottis\n   being suddenly closed so that little or no air enters into the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sober', 'superl.', 'Temperate in the use of spirituous liquors; habitually\n   temperate; as, a sober man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sober', 'superl.', 'Not intoxicated or excited by spirituous liquors; as,\n   the sot may at times be sober.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sober', 'superl.', 'Not mad or insane; not wild, visionary, or heated with\n   passion; exercising cool, dispassionate reason; self-controlled;\n   self-possessed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sober', 'superl.', 'Not proceeding from, or attended with, passion; calm;\n   as, sober judgment; a man in his sober senses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sober', 'superl.', 'Serious or subdued in demeanor, habit, appearance, or\n   color; solemn; grave; sedate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sobered', 'imp. & p. p.', 'of Sober');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sobering', 'p. pr. & vb. n.', 'of Sober');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sober', 'v. t.', 'To make sober.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sober', 'v. i.', 'To become sober; -- often with down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soberize', 'v. t. & i.', 'To sober.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soberly', 'adv.', 'In a sober manner; temperately; cooly; calmly; gravely;\n   seriously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soberly', 'a.', 'Grave; serious; solemn; sad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sober-minded', 'a.', 'Having a disposition or temper habitually sober.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soberness', 'n.', 'The quality or state of being sober.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soboles', 'n.', 'A shoot running along under ground, forming new plants at\n   short distances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soboles', 'n.', 'A sucker, as of tree or shrub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soboliferous', 'a.', 'Producing soboles. See Illust. of Houseleek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sobriety', 'n.', 'Habitual soberness or temperance as to the use of\n   spirituous liquors; as, a man of sobriety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sobriety', 'n.', 'Habitual freedom from enthusiasm, inordinate passion, or\n   overheated imagination; calmness; coolness; gravity; seriousness; as,\n   the sobriety of riper years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sobriquet', 'n.', 'An assumed name; a fanciful epithet or appellation; a\n   nickname.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soc', 'n.', 'The lords power or privilege of holding a court in a\n   district, as in manor or lordship; jurisdiction of causes, and the\n   limits of that jurisdiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soc', 'n.', 'Liberty or privilege of tenants excused from customary\n   burdens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soc', 'n.', 'An exclusive privilege formerly claimed by millers of grinding\n   all the corn used within the manor or township which the mill stands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socage', 'n.', 'A tenure of lands and tenements by a certain or determinate\n   service; a tenure distinct from chivalry or knights service, in which\n   the obligations were uncertain. The service must be certain, in order\n   to be denominated socage, as to hold by fealty and twenty shillings\n   rent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socager', 'n.', 'A tennant by socage; a socman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'So-called', 'a.', 'So named; called by such a name (but perhaps called thus\n   with doubtful propriety).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sociability', 'n.', 'The quality of being sociable; sociableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'SSociable', 'a.', 'Capable of being, or fit to be, united in one body or\n   company; associable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'SSociable', 'a.', 'Inclined to, or adapted for, society; ready to unite\n   with others; fond of companions; social.');
  INSERT INTO words (word, word_type, description) VALUES  (    'SSociable', 'a.', 'Ready to converse; inclined to talk with others; not\n   taciturn or reserved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'SSociable', 'a.', 'Affording opportunites for conversation; characterized\n   by much conversation; as, a sociable party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'SSociable', 'a.', 'No longer hostile; friendly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sociable', 'n.', 'A gathering of people for social purposes; an informal\n   party or reception; as, a church sociable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sociable', 'n.', 'A carriage having two double seats facing each other, and\n   a box for the driver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sociableness', 'n.', 'The quality of being sociable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sociably', 'adv.', 'In a sociable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Social', 'a.', 'Of or pertaining to society; relating to men living in\n   society, or to the public as an aggregate body; as, social interest or\n   concerns; social pleasure; social benefits; social happiness; social\n   duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Social', 'a.', 'Ready or disposed to mix in friendly converse;\n   companionable; sociable; as, a social person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Social', 'a.', 'Consisting in union or mutual intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Social', 'a.', 'Naturally growing in groups or masses; -- said of many\n   individual plants of the same species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Social', 'a.', 'Living in communities consisting of males, females, and\n   neuters, as do ants and most bees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Social', 'a.', 'Forming compound groups or colonies by budding from basal\n   processes or stolons; as, the social ascidians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socialism', 'n.', 'A theory or system of social reform which contemplates a\n   complete reconstruction of society, with a more just and equitable\n   distribution of property and labor. In popular usage, the term is often\n   employed to indicate any lawless, revolutionary social scheme. See\n   Communism, Fourierism, Saint-Simonianism, forms of socialism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socialist', 'n.', 'One who advocates or practices the doctrines of\n   socialism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socialist', 'a.', 'Alt. of Socialistic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socialistic', 'a.', 'Pertaining to, or of the nature of, socialism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sociality', 'n.', 'The quality of being social; socialness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socialize', 'v. t.', 'To render social.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socialize', 'v. t.', 'To subject to, or regulate by, socialism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socially', 'adv.', 'In a social manner; sociably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socialness', 'n.', 'The quality or state of being social.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sociate', 'a.', 'Associated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sociate', 'n.', 'An associate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sociate', 'v. i.', 'To associate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Societarian', 'a.', 'Of or pertaining to society; social.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Societary', 'a.', 'Societarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Societies', 'pl. ', 'of Society');
  INSERT INTO words (word, word_type, description) VALUES  (    'Society', 'n.', 'The relationship of men to one another when associated in\n   any way; companionship; fellowship; company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Society', 'n.', 'Connection; participation; partnership.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Society', 'n.', 'A number of persons associated for any temporary or\n   permanent object; an association for mutual or joint usefulness,\n   pleasure, or profit; a social union; a partnership; as, a missionary\n   society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Society', 'n.', 'The persons, collectively considered, who live in any\n   region or at any period; any community of individuals who are united\n   together by a common bond of nearness or intercourse; those who\n   recognize each other as associates, friends, and acquaintances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Society', 'n.', 'Specifically, the more cultivated portion of any community\n   in its social relations and influences; those who mutually give receive\n   formal entertainments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socinian', 'a.', 'Of or pertaining to Socinus, or the Socinians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socinian', 'n.', 'One of the followers of Socinus; a believer in\n   Socinianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socinianism', 'n.', 'The tenets or doctrines of Faustus Socinus, an Italian\n   theologian of the sixteenth century, who denied the Trinity, the deity\n   of Christ, the personality of the Devil, the native and total depravity\n   of man, the vicarious atonement, and the eternity of future punishment.\n   His theory was, that Christ was a man divinely commissioned, who had no\n   existence before he was conceived by the Virgin Mary; that human sin\n   was the imitation of Adams sin, and that human salvation was the\n   imitation and adoption of Christs virtue; that the Bible was to be\n   interpreted by human reason; and that its language was metaphorical,\n   and not to be taken literally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socinianized', 'imp. & p. p.', 'of Socinianize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socinianizing', 'p. pr. & vb. n.', 'of Socinianize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socinianize', 'v. t.', 'To cause to conform to Socinianism; to regulate by,\n   or imbue with, the principles of Socinianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sociologic', 'a.', 'Alt. of Sociological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sociological', 'a.', 'Of or pertaining to sociology, or social science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sociologist', 'n.', 'One who treats of, or devotes himself to, the study of\n   sociology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sociology', 'n.', 'That branch of philosophy which treats of the\n   constitution, phenomena, and development of human society; social\n   science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sock', 'n.', 'A plowshare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sock', 'n.', 'The shoe worn by actors of comedy in ancient Greece and Rome,\n   -- used as a symbol of comedy, or of the comic drama, as distinguished\n   from tragedy, which is symbolized by the buskin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sock', 'n.', 'A knit or woven covering for the foot and lower leg; a\n   stocking with a short leg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sock', 'n.', 'A warm inner sole for a shoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sockdolager', 'n.', 'That which finishes or ends a matter; a settler; a\n   poser, as a heavy blow, a conclusive answer, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sockdolager', 'n.', 'A combination of two hooks which close upon each\n   other, by means of a spring, as soon as the fish bites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socket', 'n.', 'An opening into which anything is fitted; any hollow thing\n   or place which receives and holds something else; as, the sockets of\n   the teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socket', 'n.', 'Especially, the hollow tube or place in which a candle is\n   fixed in the candlestick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socketed', 'a.', 'Having a socket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sockless', 'a.', 'Destitute of socks or shoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socky', 'a.', 'Wet; soaky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socle', 'n.', 'A plain block or plinth forming a low pedestal; any base;\n   especially, the base of a statue, column, or the like. See Plinth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socle', 'n.', 'A plain face or plinth at the lower part of a wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socmen', 'pl. ', 'of Socman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socman', 'n.', 'One who holds lands or tenements by socage; a socager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socmanry', 'n.', 'Tenure by socage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socome', 'n.', 'A custom of tenants to grind corn at the lords mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socotrine', 'a.', 'Of or pertaining to Socotra, an island in the Indian\n   Ocean, on the east coast of Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socotrine', 'n.', 'A native or inhabitant of Socotra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socratic', 'a.', 'Alt. of Socratical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socratical', 'a.', 'Of or pertaining to Socrates, the Grecian sage and\n   teacher. (b. c. 469-399), or to his manner of teaching and\n   philosophizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socratically', 'adv.', 'In the Socratic method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socratism', 'n.', 'The philosophy or the method of Socrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Socratist', 'n.', 'A disciple or follower of Socrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sod', 'n.', 'The rock dove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sod', '', 'imp. of Seethe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sod', 'n.', 'That stratum of the surface of the soil which is filled with\n   the roots of grass, or any portion of that surface; turf; sward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodden', 'imp. & p. p.', 'of Sod');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodding', 'p. pr. & vb. n.', 'of Sod');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sod', 'v. t.', 'To cover with sod; to turf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soda', 'n.', 'Sodium oxide or hydroxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soda', 'n.', 'Popularly, sodium carbonate or bicarbonate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodaic', 'a.', 'Pertaining to, or containing, soda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodalite', 'n.', 'A mineral of a white to blue or gray color, occuring\n   commonly in dodecahedrons, also massive. It is a silicate of alumina\n   and soda with some chlorine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodalities', 'pl. ', 'of Sodality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodality', 'n.', 'A fellowship or fraternity; a brotherhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodality', 'n.', 'Specifically, a lay association for devotion or for\n   charitable purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodamide', 'n.', 'A greenish or reddish crystalline substance, NaNH2,\n   obtained by passing ammonia over heated sodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodden', 'p. p.', 'Boiled; seethed; also, soaked; heavy with moisture;\n   saturated; as, sodden beef; sodden bread; sodden fields.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodden', 'v. i.', 'To be seethed; to become sodden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodden', 'v. t.', 'To soak; to make heavy with water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodden-witted', 'a.', 'Heavy; dull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soddy', 'a.', 'Consisting of sod; covered with sod; turfy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soder', 'n. & v. t.', 'See Solder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodic', 'a.', 'Of or pertaining to sodium; containing sodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodio-', '', 'A combining form (also used adjectively) denoting the\n   presence of sodium or one of its compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodium', 'n.', 'A common metallic element of the alkali group, in nature\n   always occuring combined, as in common salt, in albite, etc. It is\n   isolated as a soft, waxy, white, unstable metal, so readily oxidized\n   that it combines violently with water, and to be preserved must be kept\n   under petroleum or some similar liquid. Sodium is used combined in many\n   salts, in the free state as a reducer, and as a means of obtaining\n   other metals (as magnesium and aluminium) is an important commercial\n   product. Symbol Na (Natrium). Atomic weight 23. Specific gravity 0.97.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodomite', 'n.', 'An inhabitant of Sodom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodomite', 'n.', 'One guilty of sodomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodomitical', 'a.', 'Pertaining to, or of the nature of, sodomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sodomy', 'n.', 'Carnal copulation in a manner against nature; buggery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soe', 'n.', 'A large wooden vessel for holding water; a cowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soever', '', 'A word compounded of so and ever, used in composition with\n   who, what, where, when, how, etc., and indicating any out of all\n   possible or supposable persons, things, places, times, ways, etc. It is\n   sometimes used separate from the pronoun or adverb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sofas', 'pl. ', 'of Sofa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sofa', 'n.', 'A long seat, usually with a cushioned bottom, back, and ends;\n   -- much used as a comfortable piece of furniture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soffit', 'n.', 'The under side of the subordinate parts and members of\n   buildings, such as staircases, entablatures, archways, cornices, or the\n   like. See Illust. of Lintel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sofis', 'pl. ', 'of Sofi');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sofi', 'n.', 'Same as Sufi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sofism', 'n.', 'Same as Sufism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft', 'superl.', 'Easily yielding to pressure; easily impressed, molded,\n   or cut; not firm in resisting; impressible; yielding; also, malleable;\n   -- opposed to hard; as, a soft bed; a soft peach; soft earth; soft wood\n   or metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft', 'superl.', 'Not rough, rugged, or harsh to the touch; smooth;\n   delicate; fine; as, soft silk; a soft skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft', 'superl.', 'Hence, agreeable to feel, taste, or inhale; not\n   irritating to the tissues; as, a soft liniment; soft wines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft', 'superl.', 'Not harsh or offensive to the sight; not glaring;\n   pleasing to the eye; not exciting by intensity of color or violent\n   contrast; as, soft hues or tints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft', 'superl.', 'Not harsh or rough in sound; gentle and pleasing to the\n   ear; flowing; as, soft whispers of music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft', 'superl.', 'Easily yielding; susceptible to influence; flexible;\n   gentle; kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft', 'superl.', 'Expressing gentleness, tenderness, or the like; mild;\n   conciliatory; courteous; kind; as, soft eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft', 'superl.', 'Effeminate; not courageous or manly, weak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft', 'superl.', 'Gentle in action or motion; easy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft', 'superl.', 'Weak in character; impressible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft', 'superl.', 'Somewhat weak in intellect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft', 'superl.', 'Quiet; undisturbed; paceful; as, soft slumbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft', 'superl.', 'Having, or consisting of, a gentle curve or curves; not\n   angular or abrupt; as, soft outlines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft', 'superl.', 'Not tinged with mineral salts; adapted to decompose\n   soap; as, soft water is the best for washing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft', 'superl.', 'Applied to a palatal, a sibilant, or a dental consonant\n   (as g in gem, c in cent, etc.) as distinguished from a guttural mute\n   (as g in go, c in cone, etc.); -- opposed to hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft', 'superl.', 'Belonging to the class of sonant elements as\n   distinguished from the surd, and considered as involving less force in\n   utterance; as, b, d, g, z, v, etc., in contrast with p, t, k, s, f,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft', 'n.', 'A soft or foolish person; an idiot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft', 'adv.', 'Softly; without roughness or harshness; gently; quietly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft', 'interj.', 'Be quiet; hold; stop; not so fast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Softa', 'n.', 'Any one attached to a Mohammedan mosque, esp. a student of\n   the higher branches of theology in a mosque school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Softened', 'imp. & p. p.', 'of Soften');
  INSERT INTO words (word, word_type, description) VALUES  (    'Softening', 'p. pr. & vb. n.', 'of Soften');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soften', 'v. t.', 'To make soft or more soft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soften', 'v. t.', 'To render less hard; -- said of matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soften', 'v. t.', 'To mollify; to make less fierce or intractable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soften', 'v. t.', 'To palliate; to represent as less enormous; as, to\n   soften a fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soften', 'v. t.', 'To compose; to mitigate; to assuage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soften', 'v. t.', 'To make less harsh, less rude, less offensive, or less\n   violent, or to render of an opposite quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soften', 'v. t.', 'To make less glaring; to tone down; as, to soften the\n   coloring of a picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soften', 'v. t.', 'To make tender; to make effeminate; to enervate; as,\n   troops softened by luxury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soften', 'v. t.', 'To make less harsh or grating, or of a quality the\n   opposite; as, to soften the voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soften', 'v. i.', 'To become soft or softened, or less rude, harsh, severe,\n   or obdurate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Softener', 'n.', 'One who, or that which, softens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Softening', '', 'a. & n. from Soften, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft-finned', 'a.', 'Having the fin rays cartilaginous or flexible; without\n   spines; -- said of certain fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft-headed', 'a.', 'Weak in intellect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft-hearted', 'a.', 'Having softness or tenderness of heart; susceptible\n   of pity or other kindly affection; gentle; meek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Softish', 'a.', 'Somewhat soft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Softling', 'n.', 'A soft, effeminate person; a voluptuary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Softly', 'adv.', 'In a soft manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Softner', 'n.', 'See Softener.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Softness', 'n.', 'The quality or state of being soft; -- opposed to\n   hardness, and used in the various specific senses of the adjective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft-shell', 'a.', 'Alt. of Soft-shelled');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft-shelled', 'a.', 'Having a soft or fragile shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soft-spoken', 'a.', 'Speaking softly; having a mild or gentle voice; hence,\n   mild; affable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sogginess', 'n.', 'The quality or state of being soggy; soddenness;\n   wetness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soggy', 'superl.', 'Filled with water; soft with moisture; sodden; soaked;\n   wet; as, soggy land or timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soho', 'interj.', 'Ho; -- a word used in calling from a distant place; a\n   sportsmans halloo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soi-disant', 'a.', 'Calling himself; self-styled; pretended; would-be.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soiled', 'imp. & p. p.', 'of Soil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soiling', 'p. pr. & vb. n.', 'of Soil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soil', 'v. t.', 'To feed, as cattle or horses, in the barn or an inclosure,\n   with fresh grass or green food cut for them, instead of sending them\n   out to pasture; hence (such food having the effect of purging them), to\n   purge by feeding on green food; as, to soil a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soil', 'n.', 'The upper stratum of the earth; the mold, or that compound\n   substance which furnishes nutriment to plants, or which is particularly\n   adapted to support and nourish them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soil', 'n.', 'Land; country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soil', 'n.', 'Dung; faeces; compost; manure; as, night soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soil', 'v. t.', 'To enrich with soil or muck; to manure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soil', 'n.', 'A marshy or miry place to which a hunted boar resorts for\n   refuge; hence, a wet place, stream, or tract of water, sought for by\n   other game, as deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soil', 'n.', 'To make dirty or unclean on the surface; to foul; to dirty;\n   to defile; as, to soil a garment with dust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soil', 'n.', 'To stain or mar, as with infamy or disgrace; to tarnish; to\n   sully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soil', 'v. i.', 'To become soiled; as, light colors soil sooner than dark\n   ones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soil', 'n.', 'That which soils or pollutes; a soiled place; spot; stain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soiliness', 'n.', 'Stain; foulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soilless', 'a.', 'Destitute of soil or mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soilure', 'n.', 'Stain; pollution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soily', 'a.', 'Dirty; soiled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soiree', 'n.', 'An evening party; -- distinguished from levee, and matinee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soja', 'n.', 'An Asiatic leguminous herb (Glycine Soja) the seeds of which\n   are used in preparing the sauce called soy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sojourned', 'imp. & p. p.', 'of Sojourn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sojourning', 'p. pr. & vb. n.', 'of Sojourn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sojourn', 'v. i.', 'To dwell for a time; to dwell or live in a place as a\n   temporary resident or as a stranger, not considering the place as a\n   permanent habitation; to delay; to tarry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sojourn', 'v. i.', 'A temporary residence, as that of a traveler in a\n   foreign land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sojourner', 'n.', 'One who sojourns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sojourning', 'n.', 'The act or state of one who sojourns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sojournment', 'n.', 'Temporary residence, as that of a stranger or a\n   traveler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soke', 'n.', 'See Soc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soke', 'n.', 'One of the small territorial divisions into which\n   Lincolnshire, England, is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sokeman', 'n.', 'See Socman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sokemanry', 'n.', 'See Socmanry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soken', 'n.', 'A toll. See Soc, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soken', 'n.', 'A district held by socage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soko', 'n.', 'An African anthropoid ape, supposed to be a variety of the\n   chimpanzee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sol', 'n.', 'The sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sol', 'n.', 'Gold; -- so called from its brilliancy, color, and value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sol', 'n.', 'A syllable applied in solmization to the note G, or to the\n   fifth tone of any diatonic scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sol', 'n.', 'The tone itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sol', 'n.', 'A sou.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sol', 'n.', 'A silver and gold coin of Peru. The silver sol is the unit of\n   value, and is worth about 68 cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sola', 'a.', 'See Solus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sola', 'n.', 'A leguminous plant (Aeschynomene aspera) growing in moist\n   places in Southern India and the East Indies. Its pithlike stem is used\n   for making hats, swimming-jackets, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solace', 'v. t.', 'Comfort in grief; alleviation of grief or anxiety; also,\n   that which relieves in distress; that which cheers or consoles; relief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solace', 'v. t.', 'Rest; relaxation; ease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solaced', 'imp. & p. p.', 'of Solace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solacing', 'p. pr. & vb. n.', 'of Solace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solace', 'n.', 'To cheer in grief or under calamity; to comfort; to relieve\n   in affliction, solitude, or discomfort; to console; -- applied to\n   persons; as, to solace one with the hope of future reward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solace', 'n.', 'To allay; to assuage; to soothe; as, to solace grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solace', 'v. i.', 'To take comfort; to be cheered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solacement', 'n.', 'The act of solacing, or the state of being solaced;\n   also, that which solaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solacious', 'a.', 'Affording solace; as, a solacious voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solanaceous', 'a.', 'Of or pertaining to plants of the natural order\n   Solanaceae, of which the nightshade (Solanum) is the type. The order\n   includes also the tobacco, ground cherry, tomato, eggplant, red pepper,\n   and many more.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soland', 'n.', 'A solan goose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solander', 'n.', 'See Sallenders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solan goose', '', 'The common gannet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solania', 'n.', 'Solanine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solanicine', 'n.', 'An alkaloid produced by the action of hydrochloric acid\n   on solanidine, as a tasteless yellow crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solanidine', 'n.', 'An alkaloid produced by the decomposition of solanine,\n   as a white crystalline substance having a harsh bitter taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solanine', 'n.', 'A poisonous alkaloid glucoside extracted from the berries\n   of common nightshade (Solanum nigrum), and of bittersweet, and from\n   potato sprouts, as a white crystalline substance having an acrid,\n   burning taste; -- called also solonia, and solanina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solano', '', 'A hot, oppressive wind which sometimes blows in the\n   Mediterranean, particularly on the eastern coast of Spain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solanoid', 'a.', 'Resembling a potato; -- said of a kind of cancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solanum', 'n.', 'A genus of plants comprehending the potato (S. tuberosum),\n   the eggplant (S. melongena, and several hundred other species;\n   nightshade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solar', 'a.', 'A loft or upper chamber; a garret room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solar', 'a.', 'Of or pertaining to the sun; proceeding from the sun; as,\n   the solar system; solar light; solar rays; solar influence. See Solar\n   system, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solar', 'a.', 'Born under the predominant influence of the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solar', 'a.', 'Measured by the progress or revolution of the sun in the\n   ecliptic; as, the solar year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solar', 'a.', 'Produced by the action of the sun, or peculiarly affected by\n   its influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solaria', 'pl. ', 'of Solarium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solarium', 'n.', 'An apartment freely exposed to the sun; anciently, an\n   apartment or inclosure on the roof of a house; in modern times, an\n   apartment in a hospital, used as a resort for convalescents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solarium', 'n.', 'Any one of several species of handsome marine spiral\n   shells of the genus Solarium and allied genera. The shell is conical,\n   and usually has a large, deep umbilicus exposing the upper whorls.\n   Called also perspective shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solarization', 'n.', 'Injury of a photographic picture caused by exposing\n   it for too long a time to the suns light in the camera; burning;\n   excessive insolation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solarized', 'imp. & p. p.', 'of Solarize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solarizing', 'p. pr. & vb. n.', 'of Solarize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solarize', 'v. t.', 'To injure by too long exposure to the light of the sun\n   in the camera; to burn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solarize', 'v. i.', 'To become injured by undue or too long exposure to the\n   suns rays in the camera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solary', 'a.', 'Solar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solas', 'n.', 'Solace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solatium', 'n.', 'Anything which alleviates or compensates for suffering or\n   loss; a compensation; esp., an additional allowance, as for injured\n   feelings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sold', '', 'imp. & p. p. of Sell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sold', 'n.', 'Solary; military pay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldan', 'n.', 'A sultan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldanel', 'n.', 'A plant of the genus Soldanella, low Alpine herbs of the\n   Primrose family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldanrie', 'n.', 'The country ruled by a soldan, or sultan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solder', 'n.', 'A metal or metallic alloy used when melted for uniting\n   adjacent metallic edges or surfaces; a metallic cement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solder', 'n.', 'anything which unites or cements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldered', 'imp. & p. p.', 'of Solder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldering', 'p. pr. & vb. n.', 'of Solder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solder', 'n.', 'To unite (metallic surfaces or edges) by the intervention\n   of a more fusible metal or metallic alloy applied when melted; to join\n   by means of metallic cement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solder', 'n.', 'To mend; to patch up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solderer', 'n.', 'One who solders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldering', '', 'a. & n. from Solder, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldier', 'n.', 'One who is engaged in military service as an officer or a\n   private; one who serves in an army; one of an organized body of\n   combatants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldier', 'n.', 'Especially, a private in military service, as\n   distinguished from an officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldier', 'n.', 'A brave warrior; a man of military experience and skill,\n   or a man of distinguished valor; -- used by way of emphasis or\n   distinction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldier', 'n.', 'The red or cuckoo gurnard (Trigla pini.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldier', 'n.', 'One of the asexual polymorphic forms of white ants, or\n   termites, in which the head and jaws are very large and strong. The\n   soldiers serve to defend the nest. See Termite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldier', 'v. i.', 'To serve as a soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldier', 'v. i.', 'To make a pretense of doing something, or of performing\n   any task.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldieress', 'n.', 'A female soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldiering', 'n.', 'The act of serving as a soldier; the state of being a\n   soldier; the occupation of a soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldiering', 'n.', 'The act of feigning to work. See the Note under\n   Soldier, v. i., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldierlike', 'a.', 'Like a soldier; soldierly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldierly', 'a.', 'Like or becoming a real soldier; brave; martial; heroic;\n   honorable; soldierlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldiership', 'n.', 'Military qualities or state; martial skill; behavior\n   becoming a soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldierwood', 'n.', 'A showy leguminous plant (Calliandra purpurea) of the\n   West Indies. The flowers have long tassels of purple stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldiery', 'n.', 'A body of soldiers; soldiers, collectivelly; the\n   military.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldiery', 'n.', 'Military service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldi', 'pl. ', 'of Soldo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soldo', 'n.', 'A small Italian coin worth a sou or a cent; the twentieth\n   part of a lira.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sole', 'n.', 'Any one of several species of flatfishes of the genus Solea\n   and allied genera of the family Soleidae, especially the common\n   European species (Solea vulgaris), which is a valuable food fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sole', 'n.', 'Any one of several American flounders somewhat resembling the\n   true sole in form or quality, as the California sole (Lepidopsetta\n   bilineata), the long-finned sole (Glyptocephalus zachirus), and other\n   species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sole', 'n.', 'The bottom of the foot; hence, also, rarely, the foot itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sole', 'n.', 'The bottom of a shoe or boot, or the piece of leather which\n   constitutes the bottom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sole', 'n.', 'The bottom or lower part of anything, or that on which\n   anything rests in standing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sole', 'n.', 'The bottom of the body of a plow; -- called also slade; also,\n   the bottom of a furrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sole', 'n.', 'The horny substance under a horses foot, which protects the\n   more tender parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sole', 'n.', 'The bottom of an embrasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sole', 'n.', 'A piece of timber attached to the lower part of the rudder,\n   to make it even with the false keel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sole', 'n.', 'The seat or bottom of a mine; -- applied to horizontal veins\n   or lodes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soled', 'imp. & p. p.', 'of Sole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soling', 'p. pr. & vb. n.', 'of Sole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sole', 'v. t.', 'To furnish with a sole; as, to sole a shoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sole', 'a.', 'Being or acting without another; single; individual; only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sole', 'a.', 'Single; unmarried; as, a feme sole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solecism', 'n.', 'An impropriety or incongruity of language in the\n   combination of words or parts of a sentence; esp., deviation from the\n   idiom of a language or from the rules of syntax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solecism', 'n.', 'Any inconsistency, unfitness, absurdity, or impropriety,\n   as in deeds or manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solecist', 'n.', 'One who commits a solecism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solecistic', 'a.', 'Solecistical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solecistical', 'a.', 'Pertaining to, or involving, a solecism; incorrect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solecistically', 'adv.', 'In a solecistic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solecize', 'v. i.', 'To commit a solecism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solely', 'adv.', 'Singly; alone; only; without another; as, to rest a cause\n   solely one argument; to rely solelyn ones own strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemn', 'a.', 'Marked with religious rites and pomps; enjoined by, or\n   connected with, religion; sacred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemn', 'a.', 'Pertaining to a festival; festive; festal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemn', 'a.', 'Stately; ceremonious; grand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemn', 'a.', 'Fitted to awaken or express serious reflections; marked by\n   seriousness; serious; grave; devout; as, a solemn promise; solemn\n   earnestness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemn', 'a.', 'Real; earnest; downright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemn', 'a.', 'Affectedly grave or serious; as, to put on a solemn face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemn', 'a.', 'Made in form; ceremonious; as, solemn war; conforming with\n   all legal requirements; as, probate in solemn form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemness', 'n.', 'Solemnness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemnities', 'pl. ', 'of Solemnity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemnity', 'n.', 'A rite or ceremony performed with religious reverence;\n   religious or ritual ceremony; as, the solemnity of a funeral, a\n   sacrament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemnity', 'n.', 'ceremony adapted to impress with awe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemnity', 'n.', 'Ceremoniousness; impressiveness; seriousness; grave\n   earnestness; formal dignity; gravity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemnity', 'n.', 'Hence, affected gravity or seriousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemnity', 'n.', 'Solemn state or feeling; awe or reverence; also, that\n   which produces such a feeling; as, the solemnity of an audience; the\n   solemnity of Westminster Abbey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemnity', 'n.', 'A solemn or formal observance; proceeding according to\n   due form; the formality which is necessary to render a thing done\n   valid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemnizate', 'v. t.', 'To solemnize; as, to solemnizate matrimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemnization', 'n.', 'The act of solemnizing; celebration; as, the\n   solemnization of a marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemnized', 'imp. & p. p.', 'of Solemnize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemnizing', 'p. pr. & vb. n.', 'of Solemnize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemnize', 'v. t.', 'To perform with solemn or ritual ceremonies, or\n   according to legal forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemnize', 'v. t.', 'To dignify or honor by ceremonies; to celebrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemnize', 'v. t.', 'To make grave, serious, and reverential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemnize', 'n.', 'Solemnization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemnizer', 'n.', 'One who solemnizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemnly', 'adv.', 'In a solemn manner; with gravity; seriously; formally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solemnness', 'n.', 'The state or quality of being solemn; solemnity;\n   impressiveness; gravity; as, the solemnness of public worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solempne', 'a.', 'Solemn; grand; stately; splendid; magnificent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solen', 'n.', 'A cradle, as for a broken limb. See Cradle, 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solen', 'n.', 'Any marine bivalve mollusk belonging to Solen or allied\n   genera of the family Solenidae; a razor shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solenacean', 'n.', 'Any species of marine bivalve shells belonging to the\n   family Solenidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solenaceous', 'a.', 'Of or pertaining to the solens or family Solenidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soleness', 'n.', 'The state of being sole, or alone; singleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solenette', 'n.', 'A small European sole (Solea minuta).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solenoconcha', 'n. pl.', 'Same as Scaphopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solenodon', 'n.', 'Either one of two species of singular West Indian\n   insectivores, allied to the tenrec. One species (Solendon paradoxus),\n   native of St. Domingo, is called also agouta; the other (S. Cubanus),\n   found in Cuba, is called almique.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solenogastra', 'n. pl.', 'An order of lowly organized Mollusca belonging to\n   the Isopleura. A narrow groove takes the place of the foot of other\n   gastropods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solenoglyph', 'a.', 'Pertaining to the Selenoglypha. See Ophidia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solenoglyph', 'n.', 'One of the Selenoglypha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solenoglypha', 'n. pl.', 'A suborder of serpents including those which have\n   tubular erectile fangs, as the viper and rattlesnake. See Fang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solenoid', 'n.', 'An electrodynamic spiral having the conjuctive wire\n   turned back along its axis, so as to neutralize that component of the\n   effect of the current which is due to the length of the spiral, and\n   reduce the whole effect to that of a series of equal and parallel\n   circular currents. When traversed by a current the solenoid exhibits\n   polarity and attraction or repulsion, like a magnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solenostomi', 'n. pl.', 'A tribe of lophobranch fishes having a tubular\n   snout. The female carries the eggs in a ventral pouch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soleplate', 'n.', 'A bedplate; as, the soleplate of a steam engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soleplate', 'n.', 'The plate forming the back of a waterwheel bucket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soler', 'n.', 'Alt. of Solere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solere', 'n.', 'A loft or garret. See Solar, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solert', 'a.', 'Skillful; clever; crafty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solertiousness', 'n.', 'The quality or state of being solert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soleship', 'n.', 'The state of being sole, or alone; soleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sol-faed', 'imp. & p. p.', 'of Sol-fa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sol-faing', 'p. pr. & vb. n.', 'of Sol-fa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sol-fa', 'v. i.', 'To sing the notes of the gamut, ascending or descending;\n   as, do or ut, re, mi, fa, sol, la, si, do, or the same in reverse\n   order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sol-fa', 'n.', 'The gamut, or musical scale. See Tonic sol-fa, under Tonic,\n   n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solfanaria', 'n.', 'A sulphur mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solfatara', 'n.', 'A volcanic area or vent which yields only sulphur\n   vapors, steam, and the like. It represents the stages of the volcanic\n   activity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solfeggiare', 'v. i.', 'To sol-fa. See Sol-fa, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solfeggio', 'n.', 'The system of arranging the scale by the names do, re,\n   mi, fa, sol, la, si, by which singing is taught; a singing exercise\n   upon these syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solferino', 'n.', 'A brilliant deep pink color with a purplish tinge, one\n   of the dyes derived from aniline; -- so called from Solferino in Italy,\n   where a battle was fought about the time of its discovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soli', 'n.', 'pl. of Solo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solicited', 'imp. & p. p.', 'of Solicit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soliciting', 'p. pr. & vb. n.', 'of Solicit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solicit', 'v. t.', 'To ask from with earnestness; to make petition to; to\n   apply to for obtaining something; as, to solicit person for alms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solicit', 'v. t.', 'To endeavor to obtain; to seek; to plead for; as, to\n   solicit an office; to solicit a favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solicit', 'v. t.', 'To awake or excite to action; to rouse desire in; to\n   summon; to appeal to; to invite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solicit', 'v. t.', 'To urge the claims of; to plead; to act as solicitor\n   for or with reference to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solicit', 'v. t.', 'To disturb; to disquiet; -- a Latinism rarely used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solicitant', 'n.', 'One who solicits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solicitate', 'a.', 'Solicitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soliitation', 'n.', 'The act of soliciting; earnest request; persistent\n   asking; importunity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soliitation', 'n.', 'Excitement; invitation; as, the solicitation of the\n   senses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solicitor', 'n.', 'One who solicits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solicitor', 'n.', 'An attorney or advocate; one who represents another in\n   court; -- formerly, in English practice, the professional designation\n   of a person admitted to practice in a court of chancery or equity. See\n   the Note under Attorney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solicitor', 'n.', 'The law officer of a city, town, department, or\n   government; as, the city solicitor; the solicitor of the treasury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solicitor-general', 'n.', 'The second law officer in the government of\n   Great Britain; also, a similar officer under the United States\n   government, who is associated with the attorney-general; also, the\n   chief law officer of some of the States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solicitous', 'v. t.', 'Disposed to solicit; eager to obtain something\n   desirable, or to avoid anything evil; concerned; anxious; careful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solicitress', 'n.', 'A woman who solicits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solicitude', 'n.', 'The state of being solicitous; uneasiness of mind\n   occasioned by fear of evil or desire good; anxiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solid', 'a.', 'Having the constituent parts so compact, or so firmly\n   adhering, as to resist the impression or penetration of other bodies;\n   having a fixed form; hard; firm; compact; -- opposed to fluid and\n   liquid or to plastic, like clay, or to incompact, like sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solid', 'a.', 'Not hollow; full of matter; as, a solid globe or cone, as\n   distinguished from a hollow one; not spongy; dense; hence, sometimes,\n   heavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solid', 'a.', 'Having all the geometrical dimensions; cubic; as, a solid\n   foot contains 1,728 solid inches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solid', 'a.', 'Firm; compact; strong; stable; unyielding; as, a solid pier;\n   a solid pile; a solid wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solid', 'a.', 'Applied to a compound word whose parts are closely united\n   and form an unbroken word; -- opposed to hyphened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solid', 'a.', 'Fig.: Worthy of credit, trust, or esteem; substantial, as\n   opposed to frivolous or fallacious; weighty; firm; strong; valid; just;\n   genuine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solid', 'a.', 'Sound; not weakly; as, a solid constitution of body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solid', 'a.', 'Of a fleshy, uniform, undivided substance, as a bulb or\n   root; not spongy or hollow within, as a stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solid', 'a.', 'Impenetrable; resisting or excluding any other material\n   particle or atom from any given portion of space; -- applied to the\n   supposed ultimate particles of matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solid', 'a.', 'Not having the lines separated by leads; not open.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solid', 'a.', 'United; without division; unanimous; as, the delegation is\n   solid for a candidate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solid', 'n.', 'A substance that is held in a fixed form by cohesion among\n   its particles; a substance not fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solid', 'n.', 'A magnitude which has length, breadth, and thickness; a part\n   of space bounded on all sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidago', 'n.', 'A genus of yellow-flowered composite perennial herbs;\n   golden-rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidare', 'n.', 'A small piece of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidarity', 'n.', 'An entire union or consolidation of interests and\n   responsibilities; fellowship; community.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidary', 'a.', 'Having community of interests and responsibilities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidate', 'v. t.', 'To make solid or firm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidifiable', 'a.', 'Capable of being solidified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidification', 'n.', 'Act of solidifying, or state of being solidified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidified', 'imp. & p. p.', 'of Solidify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidifying', 'p. pr. & vb. n.', 'of Solidify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidify', 'v. t.', 'To make solid or compact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidify', 'v. i.', 'To become solid; to harden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidism', 'n.', 'The doctrine that refers all diseases to morbid changes\n   of the solid parts of the body. It rests on the view that the solids\n   alone are endowed with vital properties, and can receive the impression\n   of agents tending to produce disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidist', 'n.', 'An advocate of, or believer in, solidism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidity', 'n.', 'The state or quality of being solid; density;\n   consistency, -- opposed to fluidity; compactness; fullness of matter,\n   -- opposed to openness or hollowness; strength; soundness, -- opposed\n   to weakness or instability; the primary quality or affection of matter\n   by which its particles exclude or resist all others; hardness;\n   massiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidity', 'n.', 'Moral firmness; soundness; strength; validity; truth;\n   certainty; -- as opposed to weakness or fallaciousness; as, the\n   solidity of arguments or reasoning; the solidity of principles,\n   triuths, or opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidity', 'n.', 'The solid contents of a body; volume; amount of inclosed\n   space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidly', 'adv.', 'In a solid manner; densely; compactly; firmly; truly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidness', 'n.', 'State or quality of being solid; firmness; compactness;\n   solidity, as of material bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidness', 'n.', 'Soundness; strength; truth; validity, as of arguments,\n   reasons, principles, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidungula', 'n. pl.', 'A tribe of ungulates which includes the horse,\n   ass, and related species, constituting the family Equidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidungular', 'a.', 'Solipedous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidungulate', 'n.', 'Same as Soliped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solidungulous', 'a.', 'Solipedous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solifidian', 'n.', 'One who maintains that faith alone, without works, is\n   sufficient for justification; -- opposed to nullifidian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solifidian', 'a.', 'Holding the tenets of Solifidians; of or pertaining to\n   the solifidians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solifidianism', 'n.', 'The state of Solifidians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soliform', 'a.', 'Like the sun in form, appearance, or nature; resembling\n   the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solifugae', 'n. pl.', 'A division of arachnids having large, powerful fangs\n   and a segmented abdomen; -- called also Solpugidea, and Solpugides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soliloquized', 'imp. & p. p.', 'of Soliloquize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soliloquizing', 'p. pr. & vb. n.', 'of Soliloquize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soliloquize', 'v. i.', 'To utter a soliloquy; to talk to ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soliloquies', 'pl. ', 'of Soliloquy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soliloquy', 'n.', 'The act of talking to ones self; a discourse made by\n   one in solitude to ones self; monologue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soliloquy', 'n.', 'A written composition, reciting what it is supposed a\n   person says to himself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soliped', 'n.', 'A mammal having a single hoof on each foot, as the horses\n   and asses; a solidungulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solipedous', 'a.', 'Having single hoofs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solipsism', 'n.', 'Egotism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solipsism', 'n.', 'Egoism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solisequious', 'a.', 'Following the course of the sun; as, solisequious\n   plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solitaire', 'n.', 'A person who lives in solitude; a recluse; a hermit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solitaire', 'n.', 'A single diamond in a setting; also, sometimes, a\n   precious stone of any kind set alone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solitaire', 'n.', 'A game which one person can play alone; -- applied to\n   many games of cards, etc.; also, to a game played on a board with pegs\n   or balls, in which the object is, beginning with all the places filled\n   except one, to remove all but one of the pieces by \"jumping,\" as in\n   draughts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solitaire', 'n.', 'A large extinct bird (Pezophaps solitaria) which\n   formerly inhabited the islands of Mauritius and Rodrigeuz. It was\n   larger and taller than the wild turkey. Its wings were too small for\n   flight. Called also solitary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solitaire', 'n.', 'Any species of American thrushlike birds of the genus\n   Myadestes. They are noted their sweet songs and retiring habits. Called\n   also fly-catching thrush. A West Indian species (Myadestes sibilans) is\n   called the invisible bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solitarian', 'n.', 'A hermit; a solitary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soliitariety', 'n.', 'The state of being solitary; solitariness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solitarily', 'adv.', 'In a solitary manner; in solitude; alone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solitariness', 'n.', 'Condition of being solitary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solitary', 'a.', 'Living or being by ones self; having no companion\n   present; being without associates; single; alone; lonely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solitary', 'a.', 'Performed, passed, or endured alone; as, a solitary\n   journey; a solitary life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solitary', 'a.', 'ot much visited or frequented remote from society;\n   retired; lonely; as, a solitary residence or place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solitary', 'a.', 'Not inhabited or occupied; without signs of inhabitants\n   or occupation; desolate; deserted; silent; still; hence, gloomy;\n   dismal; as, the solitary desert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solitary', 'a.', 'Single; individual; sole; as, a solitary instance of\n   vengeance; a solitary example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solitary', 'a.', 'Not associated with others of the same kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solitary', 'n.', 'One who lives alone, or in solitude; an anchoret; a\n   hermit; a recluse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solitude', 'a.', 'state of being alone, or withdrawn from society; a lonely\n   life; loneliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solitude', 'a.', 'Remoteness from society; destitution of company;\n   seclusion; -- said of places; as, the solitude of a wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solitude', 'a.', 'solitary or lonely place; a desert or wilderness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solivagant', 'a.', 'Wandering alone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solivagous', 'a.', 'Solivagant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sollar', 'n.', 'See Solar, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sollar', 'n.', 'A platform in a shaft, especially one of those between the\n   series of ladders in a shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sollar', 'v. t.', 'To cover, or provide with, a sollar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sollein', 'a.', 'Sullen; sad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solleret', 'n.', 'A flexible steel shoe (or one of the plates forming such\n   a shoe), worn with mediaeval armor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solmization', 'n.', 'The act of sol-faing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solos', 'pl. ', 'of Solo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soli', 'pl. ', 'of Solo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solo', 'a.', 'A tune, air, strain, or a whole piece, played by a single\n   person on an instrument, or sung by a single voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soloist', 'n.', 'One who sings or plays a solo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solomon', 'n.', 'One of the kings of Israel, noted for his superior wisdom\n   and magnificent reign; hence, a very wise man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solon', 'n.', 'A celebrated Athenian lawmaker, born about 638 b. c.; hence,\n   a legislator; a publicist; -- often used ironically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solpugid', 'a.', 'Of or pertaining to the Solifugae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solpugid', 'n.', 'One of the Solifugae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solpugidea', 'n. pl.', 'Same as Solifugae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solstice', 'v. i.', 'A stopping or standing still of the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solstice', 'v. i.', 'The point in the ecliptic at which the sun is farthest\n   from the equator, north or south, namely, the first point of the sign\n   Cancer and the first point of the sign Capricorn, the former being the\n   summer solstice, latter the winter solstice, in northern latitudes; --\n   so called because the sun then apparently stands still in its northward\n   or southward motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solstice', 'v. i.', 'The time of the suns passing the solstices, or\n   solstitial points, namely, about June 21 and December 21. See Illust.\n   in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solstitial', 'a.', 'Of or pertaining to a solstice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solstitial', 'a.', 'Happening at a solstice; esp. (with reference to the\n   northern hemisphere), happening at the summer solstice, or midsummer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solubility', 'n.', 'The quality, condition, or degree of being soluble or\n   solvable; as, the solubility of a salt; the solubility of a problem or\n   intricate difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solubility', 'n.', 'The tendency to separate readily into parts by spurious\n   articulations, as the pods of tick trefoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soluble', 'a.', 'Susceptible of being dissolved in a fluid; capable of\n   solution; as, some substances are soluble in alcohol which are not\n   soluble in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soluble', 'a.', 'Susceptible of being solved; as, a soluble algebraic\n   problem; susceptible of being disentangled, unraveled, or explained;\n   as, the mystery is perhaps soluble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soluble', 'a.', 'Relaxed; open or readily opened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solubleness', 'n.', 'Quality or state of being soluble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solus', 'fem. a.', 'Alt. of Sola');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sola', 'fem. a.', 'Alone; -- chiefly used in stage directions, and the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solute', 'a.', 'Loose; free; liberal; as, a solute interpretation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solute', 'a.', 'Relaxed; hence; merry; cheerful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solute', 'a.', 'Soluble; as, a solute salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solute', 'a.', 'Not adhering; loose; -- opposed to adnate; as, a solute\n   stipule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solute', 'v. t.', 'To dissolve; to resolve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solute', 'v. t.', 'To absolve; as, to solute sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solution', 'n.', 'The act of separating the parts of any body, or the\n   condition of undergoing a separation of parts; disruption; breach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solution', 'n.', 'The act of solving, or the state of being solved; the\n   disentanglement of any intricate problem or difficult question;\n   explanation; clearing up; -- used especially in mathematics, either of\n   the process of solving an equation or problem, or the result of the\n   process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solution', 'n.', 'The state of being dissolved or disintegrated;\n   resolution; disintegration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solution', 'n.', 'The act or process by which a body (whether solid,\n   liquid, or gaseous) is absorbed into a liquid, and, remaining or\n   becoming fluid, is diffused throughout the solvent; also, the product\n   reulting from such absorption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solution', 'n.', 'release; deliverance; discharge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solution', 'n.', 'The termination of a disease; resolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solution', 'n.', 'A crisis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solution', 'n.', 'A liquid medicine or preparation (usually aqueous) in\n   which the solid ingredients are wholly soluble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solutive', 'a.', 'Tending to dissolve; loosening; laxative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solvability', 'n.', 'The quality or state of being solvable; as, the\n   solvability of a difficulty; the solvability of a problem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solvability', 'n.', 'The condition of being solvent; ability to pay all\n   just debts; solvency; as, the solvability of a merchant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solvable', 'a.', 'Susceptible of being solved, resolved, or explained;\n   admitting of solution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solvable', 'a.', 'Capable of being paid and discharged; as, solvable\n   obligations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solvable', 'a.', 'Able to pay ones debts; solvent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solvableness', 'n.', 'Quality of being solvable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solved', 'imp. & p. p.', 'of Solve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solving', 'p. pr. & vb. n.', 'of Solve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solve', 'v. t.', 'To explain; to resolve; to unfold; to clear up (what is\n   obscure or difficult to be understood); to work out to a result or\n   conclusion; as, to solve a doubt; to solve difficulties; to solve a\n   problem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solve', 'n.', 'A solution; an explanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solvency', 'n.', 'The quality or state of being solvent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solvend', 'n.', 'A substance to be dissolved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solvent', 'a.', 'Having the power of dissolving; dissolving; as, a solvent\n   fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solvent', 'a.', 'Able or sufficient to pay all just debts; as, a solvent\n   merchant; the estate is solvent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solvent', 'n.', 'A substance (usually liquid) suitable for, or employed in,\n   solution, or in dissolving something; as, water is the appropriate\n   solvent of most salts, alcohol of resins, ether of fats, and mercury or\n   acids of metals, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solvent', 'n.', 'That which resolves; as, a solvent of mystery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solver', 'n.', 'One who, or that which, solves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Solvible', 'a.', 'See Solvable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soly', 'adv.', 'Solely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soma', 'n.', 'The whole axial portion of an animal, including the head,\n   neck, trunk, and tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somaj', 'n.', 'Alt. of Samaj');
  INSERT INTO words (word, word_type, description) VALUES  (    'Samaj', 'n.', 'A society; a congregation; a worshiping assembly, or church,\n   esp. of the Brahmo-somaj.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somali', 'n.', 'Alt. of Somal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somal', 'n.', 'A Hamitic people of East Central Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somatic', 'a.', 'Of or pertaining to the body as a whole; corporeal; as,\n   somatic death; somatic changes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somatic', 'a.', 'Of or pertaining to the wall of the body; somatopleuric;\n   parietal; as, the somatic stalk of the yolk sac of an embryo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somatical', 'a.', 'Somatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somatics', 'n.', 'The science which treats of the general properties of\n   matter; somatology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somatist', 'n.', 'One who admits the existence of material beings only; a\n   materialist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somatocyst', 'n.', 'A cavity in the primary nectocalyx of certain\n   Siphonophora. See Illust. under Nectocalyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somatology', 'n.', 'The doctrine or the science of the general properties\n   of material substances; somatics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somatology', 'n.', 'A treatise on the human body; anatomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somatome', 'n.', 'See Somite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somatopleure', 'n.', 'The outer, or parietal, one of the two lamellae into\n   which the vertebrate blastoderm divides on either side of the\n   notochord, and from which the walls of the body and the amnion are\n   developed. See Splanchnopleure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somatopleuric', 'a.', 'Of or pertaining to the somatopleure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somatotropism', 'n.', 'A directive influence exercised by a mass of matter\n   upon growing organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somber', 'a.', 'Alt. of Sombre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sombre', 'a.', 'Dull; dusky; somewhat dark; gloomy; as, a somber forest; a\n   somber house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sombre', 'a.', 'Melancholy; sad; grave; depressing; as, a somber person;\n   somber reflections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somber', 'v. t.', 'Alt. of Sombre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sombre', 'v. t.', 'To make somber, or dark; to make shady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somber', 'n.', 'Alt. of Sombre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sombre', 'n.', 'Gloom; obscurity; duskiness; somberness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somberly', 'adv.', 'Alt. of Sombrely');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sombrely', 'adv.', 'In a somber manner; sombrously; gloomily; despondingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somberness', 'n.', 'Alt. of Sombreness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sombreness', 'n.', 'The quality or state of being somber; gloominess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sombrero', 'n.', 'A kind of broad-brimmed hat, worn in Spain and in Spanish\n   America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sombrous', 'a.', 'Gloomy; somber.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-some', '', 'A combining form or suffix from Gr. sw^ma (gen. sw`matos) the\n   body; as in merosome, a body segment; cephalosome, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-some', 'a.', 'An adjective suffix having primarily the sense of like or\n   same, and indicating a considerable degree of the thing or quality\n   denoted in the first part of the compound; as in mettlesome, full of\n   mettle or spirit; gladsome, full of gladness; winsome, blithesome, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Some', 'a.', 'Consisting of a greater or less portion or sum; composed of a\n   quantity or number which is not stated; -- used to express an\n   indefinite quantity or number; as, some wine; some water; some persons.\n   Used also pronominally; as, I have some.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Some', 'a.', 'A certain; one; -- indicating a person, thing, event, etc.,\n   as not known individually, or designated more specifically; as, some\n   man, that is, some one man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Some', 'a.', 'Not much; a little; moderate; as, the censure was to some\n   extent just.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Some', 'a.', 'About; near; more or less; -- used commonly with numerals,\n   but formerly also with a singular substantive of time or distance; as,\n   a village of some eighty houses; some two or three persons; some hour\n   hence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Some', 'a.', 'Considerable in number or quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Some', 'a.', 'Certain; those of one part or portion; -- in distinct from\n   other or others; as, some men believe one thing, and others another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Some', 'a.', 'A part; a portion; -- used pronominally, and followed\n   sometimes by of; as, some of our provisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somebody', 'n.', 'A person unknown or uncertain; a person indeterminate;\n   some person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somebody', 'n.', 'A person of consideration or importance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somedeal', 'adv.', 'In some degree; somewhat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somehow', 'adv.', 'In one way or another; in some way not yet known or\n   designated; by some means; as, the thing must be done somehow; he lives\n   somehow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somersault', 'n.', 'Alt. of Somerset');
  INSERT INTO words (word, word_type, description) VALUES  (    'Somerset', 'n.', 'A leap in which a person turns his heels over his head\n   and lights upon his feet; a turning end over end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Something', 'n.', 'Anything unknown, undetermined, or not specifically\n   designated; a certain indefinite thing; an indeterminate or unknown\n   event; an unspecified task, work, or thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Something', 'n.', 'A part; a portion, more or less; an indefinite quantity\n   or degree; a little.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Something', 'n.', 'A person or thing importance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Something', 'adv.', 'In some degree; somewhat; to some extent; at some\n   distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sometime', 'adv.', 'At a past time indefinitely referred to; once;\n   formerly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sometime', 'adv.', 'At a time undefined; once in a while; now and then;\n   sometimes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sometime', 'adv.', 'At one time or other hereafter; as, I will do it\n   sometime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sometime', 'a.', 'Having been formerly; former; late; whilom.');
