  INSERT INTO words (word, word_type, description) VALUES  (    'Sophistical', 'a.', 'Of or pertaining to a sophist; embodying sophistry;\n   fallaciously subtile; not sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sophisticated', 'imp. & p. p.', 'of Sophisticate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sophisticating', 'p. pr. & vb. n.', 'of Sophisticate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sophisticate', 'v. t.', 'To render worthless by admixture; to adulterate;\n   to damage; to pervert; as, to sophisticate wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sophisticate', 'a.', 'Alt. of Sophisticated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sophisticated', 'a.', 'Adulterated; not pure; not genuine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sophistication', 'n.', 'The act of sophisticating; adulteration; as, the\n   sophistication of drugs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sophisticator', 'n.', 'One who sophisticates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sophistry', 'n.', 'The art or process of reasoning; logic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sophistry', 'n.', 'The practice of a sophist; fallacious reasoning;\n   reasoning sound in appearance only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sophomore', 'n.', 'One belonging to the second of the four classes in an\n   American college, or one next above a freshman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sophomoric', 'a.', 'Alt. of Sophomorical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sophomorical', 'a.', 'Of or pertaining to a sophomore; resembling a\n   sophomore; hence, pretentious; inflated in style or manner; as,\n   sophomoric affectation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sophora', 'n.', 'A genus of leguminous plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sophora', 'n.', 'A tree (Sophora Japonica) of Eastern Asia, resembling the\n   common locust; occasionally planted in the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sophta', 'n.', 'See Softa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sopite', 'v. t.', 'To lay asleep; to put to sleep; to quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sopition', 'n.', 'The act of putting to sleep, or the state of being put to\n   sleep; sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sopor', 'n.', 'Profound sleep from which a person can be roused only with\n   difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soporate', 'v. t.', 'To lay or put to sleep; to stupefy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soporiferous', 'a.', 'Causing sleep; somniferous; soporific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soporific', 'a.', 'Causing sleep; tending to cause sleep; soporiferous; as,\n   the soporific virtues of opium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soporific', 'n.', 'A medicine, drug, plant, or other agent that has the\n   quality of inducing sleep; a narcotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soporose', 'a.', 'Alt. of Soporous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soporous', 'a.', 'Causing sleep; sleepy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sopper', 'n.', 'One who sops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soppy', 'a.', 'Soaked or saturated with liquid or moisture; very wet or\n   sloppy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sopra', 'adv.', 'Above; before; over; upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sopranist', 'n.', 'A treble singer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sopranos', 'pl. ', 'of Soprano');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soprani', 'pl. ', 'of Soprano');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soprano', 'n.', 'The treble; the highest vocal register; the highest kind\n   of female or boys voice; the upper part in harmony for mixed voices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soprano', 'n.', 'A singer, commonly a woman, with a treble voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sopsavine', 'n.', 'See Sops of wine, under Sop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sora', 'n.', 'A North American rail (Porzana Carolina) common in the\n   Eastern United States. Its back is golden brown, varied with black and\n   white, the front of the head and throat black, the breast and sides of\n   the head and neck slate-colored. Called also American rail, Carolina\n   rail, Carolina crake, common rail, sora rail, soree, meadow chicken,\n   and orto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorance', 'n.', 'Soreness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorb', 'n.', 'The wild service tree (Pyrus torminalis) of Europe; also, the\n   rowan tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorb', 'n.', 'The fruit of these trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorbate', 'n.', 'A salt of sorbic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorbefacient', 'a.', 'Producing absorption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorbefacient', 'n.', 'A medicine or substance which produces absorption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorbent', 'n.', 'An absorbent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorbet', 'n.', 'A kind of beverage; sherbet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorbic', 'a.', 'Pertaining to, or obtained from, the rowan tree, or sorb;\n   specifically, designating an acid, C/H/CO/H, of the acetylene series,\n   found in the unripe berries of this tree, and extracted as a white\n   crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorbile', 'a.', 'Fit to be drunk or sipped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorbin', 'n.', 'An unfermentable sugar, isomeric with glucose, found in the\n   ripe berries of the rowan tree, or sorb, and extracted as a sweet white\n   crystalline substance; -- called also mountain-ash sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorbite', 'n.', 'A sugarlike substance, isomeric with mannite and dulcite,\n   found with sorbin in the ripe berries of the sorb, and extracted as a\n   sirup or a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorbition', 'n.', 'The act of drinking or sipping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorbonical', 'a.', 'Belonging to the Sorbonne or to a Sorbonist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorbonist', 'n.', 'A doctor of the Sorbonne, or theological college, in the\n   University of Paris, founded by Robert de Sorbon, a. d. 1252. It was\n   suppressed in the Revolution of 1789.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorcerer', 'n.', 'A conjurer; an enchanter; a magician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorceress', 'n.', 'A female sorcerer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorcering', 'n.', 'Act or practice of using sorcery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorcerous', 'a.', 'Of or pertaining to sorcery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorceries', 'pl. ', 'of Sorcery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorcery', 'n.', 'Divination by the assistance, or supposed assistance, of\n   evil spirits, or the power of commanding evil spirits; magic;\n   necromancy; witchcraft; enchantment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sord', 'n.', 'See Sward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sordes', 'n.', 'Foul matter; excretion; dregs; filthy, useless, or rejected\n   matter of any kind; specifically (Med.), the foul matter that collects\n   on the teeth and tongue in low fevers and other conditions attended\n   with great vital depression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sordet', 'n.', 'A sordine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sordid', 'a.', 'Filthy; foul; dirty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sordid', 'a.', 'Vile; base; gross; mean; as, vulgar, sordid mortals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sordid', 'a.', 'Meanly avaricious; covetous; niggardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sordidly', 'n.', 'Sordidness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sordidly', 'adv.', 'In a sordid manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sordidness', 'n.', 'The quality or state of being sordid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sordine', 'n.', 'See Damper, and 5th Mute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sore', 'n.', 'Reddish brown; sorrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sore', 'n.', 'A young hawk or falcon in the first year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sore', 'n.', 'A young buck in the fourth year. See the Note under Buck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sore', 'superl.', 'Tender to the touch; susceptible of pain from pressure;\n   inflamed; painful; -- said of the body or its parts; as, a sore hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sore', 'superl.', 'Fig.: Sensitive; tender; easily pained, grieved, or\n   vexed; very susceptible of irritation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sore', 'superl.', 'Severe; afflictive; distressing; as, a sore disease;\n   sore evil or calamity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sore', 'superl.', 'Criminal; wrong; evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sore', 'a.', 'A place in an animal body where the skin and flesh are\n   ruptured or bruised, so as to be tender or painful; a painful or\n   diseased place, such as an ulcer or a boil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sore', 'a.', 'Fig.: Grief; affliction; trouble; difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sore', 'a.', 'In a sore manner; with pain; grievously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sore', 'a.', 'Greatly; violently; deeply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soredia', 'n.', 'pl. of Soredium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorediate', 'a.', 'Sorediiferous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sordiferous', 'a.', 'Alt. of Sorediiferous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorediiferous', 'a.', 'Bearing soredia; sorediate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soredia', 'pl. ', 'of Soredium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soredium', 'n.', 'A patch of granular bodies on the surface of the thallus\n   of lichens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soree', 'n.', 'Same as Sora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorehead', 'n.', 'One who is disgruntled by a failure in politics, or the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorehon', 'n.', 'Formerly, in Ireland, a kind of servile tenure which\n   subjected the tenant to maintain his chieftain gratuitously whenever he\n   wished to indulge in a revel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorel', 'n.', 'A young buck in the third year. See the Note under Buck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorel', 'n.', 'A yellowish or reddish brown color; sorrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorely', 'adv.', 'In a sore manner; grievously; painfully; as, to be sorely\n   afflicted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorema', 'n.', 'A heap of carpels belonging to one flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soreness', 'n.', 'The quality or state of being sore; tenderness; painfull;\n   as, the soreness of a wound; the soreness of an affliction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorex', 'n.', 'A genus of small Insectivora, including the common shrews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorgne', 'n.', 'The three-beared rocking, or whistlefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorghum', 'n.', 'A genus of grasses, properly limited to two species,\n   Sorghum Halepense, the Arabian millet, or Johnson grass (see Johnson\n   grass), and S. vulgare, the Indian millet (see Indian millet, under\n   Indian).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorghum', 'n.', 'A variety of Sorghum vulgare, grown for its saccharine\n   juice; the Chinese sugar cane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorgo', 'n.', 'Indian millet and its varieties. See Sorghum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sori', 'n.', 'pl. of Sorus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soricine', 'a.', 'Of or pertaining to the Shrew family (Soricidae); like a\n   shrew in form or habits; as, the soricine bat (Glossophaga soricina).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorites', 'n.', 'An abridged form of stating of syllogisms in a series of\n   propositions so arranged that the predicate of each one that precedes\n   forms the subject of each one that follows, and the conclusion unites\n   the subject of the first proposition with the predicate of the last\n   proposition');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soritical', 'a.', 'Of or pertaining to a sorites; resembling a sorites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorn', 'v. i.', 'To obtrude ones self on another for bed and board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorner', 'n.', 'One who obtrudes himself on another for bed and board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sororal', 'a.', 'Relating to a sister; sisterly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sororicide', 'n.', 'The murder of ones sister; also, one who murders or\n   kills ones own sister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sororize', 'v. i.', 'To associate, or hold fellowship, as sisters; to have\n   sisterly feelings; -- analogous to fraternize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorosis', 'n.', 'A womans club; an association of women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorosis', 'n.', 'A fleshy fruit formed by the consolidation of many flowers\n   with their receptacles, ovaries, etc., as the breadfruit, mulberry, and\n   pineapple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorrage', 'n.', 'The blades of green or barley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorrance', 'n.', 'Same as Sorance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorrel', 'a.', 'Of a yellowish or redish brown color; as, a sorrel horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorrel', 'n.', 'A yellowish or redish brown color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorrel', 'n.', 'One of various plants having a sour juice; especially, a\n   plant of the genus Rumex, as Rumex Acetosa, Rumex Acetosella, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorrento work', '', 'Ornamental work, mostly carved in olivewood, decorated\n   with inlay, made at or near Sorrento, Italy. Hence, more rarely,\n   jig-saw work and the like done anywhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorrily', 'adv.', 'In a sorry manner; poorly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorriness', 'n.', 'The quality or state of being sorry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorrow', 'n.', 'The uneasiness or pain of mind which is produced by the\n   loss of any good, real or supposed, or by diseappointment in the\n   expectation of good; grief at having suffered or occasioned evil;\n   regret; unhappiness; sadness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorrowed', 'imp. & p. p.', 'of Sorrow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorrowing', 'p. pr. & vb. n.', 'of Sorrow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorrow', 'n.', 'To feel pain of mind in consequence of evil experienced,\n   feared, or done; to grieve; to be sad; to be sorry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorrowed', 'a.', 'Accompanied with sorrow; sorrowful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorrowful', 'a.', 'Full of sorrow; exhibiting sorrow; sad; dejected;\n   distressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorrowful', 'a.', 'Producing sorrow; exciting grief; mournful; lamentable;\n   grievous; as, a sorrowful accident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorrowless', 'a.', 'Free from sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorry', 'a.', 'Grieved for the loss of some good; pained for some evil;\n   feeling regret; -- now generally used to express light grief or\n   affliction, but formerly often used to express deeper feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorry', 'a.', 'Melancholy; dismal; gloomy; mournful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorry', 'a.', 'Poor; mean; worthless; as, a sorry excuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sortes', 'pl. ', 'of Sors');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sors', 'n.', 'A lot; also, a kind of divination by means of lots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sort', 'n.', 'Chance; lot; destiny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sort', 'n.', 'A kind or species; any number or collection of individual\n   persons or things characterized by the same or like qualities; a class\n   or order; as, a sort of men; a sort of horses; a sort of trees; a sort\n   of poems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sort', 'n.', 'Manner; form of being or acting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sort', 'n.', 'Condition above the vulgar; rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sort', 'n.', 'A chance group; a company of persons who happen to be\n   together; a troop; also, an assemblage of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sort', 'n.', 'A pair; a set; a suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sort', 'n.', 'Letters, figures, points, marks, spaces, or quadrats,\n   belonging to a case, separately considered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorted', 'imp. & p. p.', 'of Sort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorting', 'p. pr. & vb. n.', 'of Sort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sort', 'v. t.', 'To separate, and place in distinct classes or divisions,\n   as things having different qualities; as, to sort cloths according to\n   their colors; to sort wool or thread according to its fineness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sort', 'v. t.', 'To reduce to order from a confused state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sort', 'v. t.', 'To conjoin; to put together in distribution; to class.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sort', 'v. t.', 'To choose from a number; to select; to cull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sort', 'v. t.', 'To conform; to adapt; to accommodate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sort', 'v. i.', 'To join or associate with others, esp. with others of the\n   same kind or species; to agree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sort', 'v. i.', 'To suit; to fit; to be in accord; to harmonize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sortable', 'a.', 'Capable of being sorted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sortable', 'a.', 'Suitable; befitting; proper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sortably', 'adv.', 'Suitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sortal', 'a.', 'Pertaining to a sort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sortance', 'v. i.', 'Suitableness; agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorter', 'n.', 'One who, or that which, sorts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sortes', 'n.', 'pl. of Sors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sortie', 'n.', 'The sudden issuing of a body of troops, usually small, from\n   a besieged place to attack or harass the besiegers; a sally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sortilege', 'n.', 'The act or practice of drawing lots; divination by\n   drawing lots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sortilegious', 'a.', 'Pertaining to sortilege.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sortilegy', 'n.', 'Sortilege.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sortition', 'n.', 'Selection or appointment by lot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sortment', 'n.', 'Assortiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sori', 'pl. ', 'of Sorus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorus', 'n.', 'One of the fruit dots, or small clusters of sporangia, on\n   the back of the fronds of ferns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorwe', 'n. & v.', 'Sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sorweful', 'a.', 'Sorrowful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sory', 'n.', 'Green vitriol, or some earth imregnated with it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'So-so', 'a.', 'Neither very good nor very bad; middling; passable;\n   tolerable; indifferent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'So-so', 'adv.', 'Tolerably; passably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soss', 'v. i.', 'To fall at once into a chair or seat; to sit lazily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soss', 'v. t.', 'To throw in a negligent or careless manner; to toss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soss', 'n.', 'A lazy fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soss', 'n.', 'A heavy fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soss', 'n.', 'Anything dirty or muddy; a dirty puddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sostenuto', 'a.', 'Sustained; -- applied to a movement or passage the\n   sounds of which are to sustained to the utmost of the nominal value of\n   the time; also, to a passage the tones of which are to be somewhat\n   prolonged or protacted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sot', 'n.', 'A stupid person; a blockhead; a dull fellow; a dolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sot', 'n.', 'A person stupefied by excessive drinking; an habitual\n   drunkard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sot', 'a.', 'Sottish; foolish; stupid; dull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sot', 'v. t.', 'To stupefy; to infatuate; to besot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sot', 'v. i.', 'To tipple to stupidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sotadean', 'a.', 'Sotadic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sotadic', 'a.', 'Pertaining to, or resembling, the lascivious compositions\n   of the Greek poet Sotades.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sotadic', 'n.', 'A Sotadic verse or poem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sote', 'a.', 'Sweet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sotel', 'a.', 'Alt. of Sotil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sotil', 'a.', 'Subtile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soteriology', 'n.', 'A discourse on health, or the science of promoting and\n   preserving health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soteriology', 'n.', 'The doctrine of salvation by Jesus Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sothe', 'a.', 'Sooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sothiac', 'a.', 'Alt. of Sothic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sothic', 'a.', 'Of or pertaining to Sothis, the Egyptian name for the Dog\n   Star; taking its name from the Dog Star; canicular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sotilte', 'n.', 'Subtlety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sotted', '', 'a. & p. p. of Sot. Befooled; deluded; besotted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sottery', 'n.', 'Folly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sottish', 'a.', 'Like a sot; doltish; very foolish; drunken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sotto voce', '', 'With a restrained voice or moderate force; in an\n   undertone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sotto voce', '', 'Spoken low or in an undertone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sous', 'pl. ', 'of Sou');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sou', 'n.', 'An old French copper coin, equivalent in value to, and now\n   displaced by, the five-centime piece (/ of a franc), which is popularly\n   called a sou.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souari nut', '', 'The large edible nutlike seed of a tall tropical American\n   tree (Caryocar nuciferum) of the same natural order with the tea plant;\n   -- also called butternut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soubah', 'n.', 'See Subah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soubahdar', 'n.', 'See Subahdar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soubrette', 'n.', 'A female servant or attendant; specifically, as a term\n   of the theater, a ladys maid, in comedies, who acts the part of an\n   intrigante; a meddlesome, mischievous female servant or young woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soubriquet', 'n.', 'See Sobriquet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souce', 'n.', 'See 1st Souse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souce', 'v. t. & i.', 'See Souse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souchong', 'n.', 'A kind of black tea of a fine quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soudan', 'n.', 'A sultan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souded', 'a.', 'Alt. of Soudet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soudet', 'a.', 'United; consolidated; made firm; strengthened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souffle', 'n.', 'A murmuring or blowing sound; as, the uterine souffle\n   heard over the pregnant uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souffle', 'n.', 'A side dish served hot from the oven at dinner, made of\n   eggs, milk, and flour or other farinaceous substance, beaten till very\n   light, and flavored with fruits, liquors, or essence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sough', 'n.', 'A sow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sough', 'n.', 'A small drain; an adit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sough', 'v. i.', 'The sound produced by soughing; a hollow murmur or\n   roaring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sough', 'v. i.', 'Hence, a vague rumor or flying report.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sough', 'v. i.', 'A cant or whining mode of speaking, especially in\n   preaching or praying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sough', 'v. i.', 'To whistle or sigh, as the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sought', '', 'imp. & p. p. of Seek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souke', 'v. t. & i.', 'To suck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soul', 'a.', 'Sole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soul', 'a.', 'Sole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soul', 'v. i.', 'To afford suitable sustenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soul', 'n.', 'The spiritual, rational, and immortal part in man; that part\n   of man which enables him to think, and which renders him a subject of\n   moral government; -- sometimes, in distinction from the higher nature,\n   or spirit, of man, the so-called animal soul, that is, the seat of\n   life, the sensitive affections and phantasy, exclusive of the voluntary\n   and rational powers; -- sometimes, in distinction from the mind, the\n   moral and emotional part of mans nature, the seat of feeling, in\n   distinction from intellect; -- sometimes, the intellect only; the\n   understanding; the seat of knowledge, as distinguished from feeling. In\n   a more general sense, \"an animating, separable, surviving entity, the\n   vehicle of individual personal existence.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soul', 'n.', 'The seat of real life or vitality; the source of action; the\n   animating or essential part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soul', 'n.', 'The leader; the inspirer; the moving spirit; the heart; as,\n   the soul of an enterprise; an able general is the soul of his army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soul', 'n.', 'Energy; courage; spirit; fervor; affection, or any other\n   noble manifestation of the heart or moral nature; inherent power or\n   goodness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soul', 'n.', 'A human being; a person; -- a familiar appellation, usually\n   with a qualifying epithet; as, poor soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soul', 'n.', 'A pure or disembodied spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soul', 'v. t.', 'To indue with a soul; to furnish with a soul or mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souled', 'a.', 'Furnished with a soul; possessing soul and feeling; -- used\n   chiefly in composition; as, great-souled Hector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soulili', 'n.', 'A long-tailed, crested Javan monkey (Semnopithecus\n   mitratus). The head, the crest, and the upper surface of the tail, are\n   black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soulless', 'a.', 'Being without a soul, or without greatness or nobleness\n   of mind; mean; spiritless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soullessly', 'adv.', 'In a soulless manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soun', 'n. & v.', 'Sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'n.', 'The air bladder of a fish; as, cod sounds are an esteemed\n   article of food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'n.', 'A cuttlefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'superl.', 'Whole; unbroken; unharmed; free from flaw, defect, or\n   decay; perfect of the kind; as, sound timber; sound fruit; a sound\n   tooth; a sound ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'superl.', 'Healthy; not diseased; not being in a morbid state; --\n   said of body or mind; as, a sound body; a sound constitution; a sound\n   understanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'superl.', 'Firm; strong; safe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'superl.', 'Free from error; correct; right; honest; true;\n   faithful; orthodox; -- said of persons; as, a sound lawyer; a sound\n   thinker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'superl.', 'Founded in truth or right; supported by justice; not to\n   be overthrown on refuted; not fallacious; as, sound argument or\n   reasoning; a sound objection; sound doctrine; sound principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'superl.', 'heavy; laid on with force; as, a sound beating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'superl.', 'Undisturbed; deep; profound; as, sound sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'superl.', 'Founded in law; legal; valid; not defective; as, a\n   sound title to land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'adv.', 'Soundly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'n.', 'A narrow passage of water, or a strait between the mainland\n   and an island; also, a strait connecting two seas, or connecting a sea\n   or lake with the ocean; as, the Sound between the Baltic and the german\n   Ocean; Long Island Sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sounded', 'imp. & p. p.', 'of Sound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sounding', 'p. pr. & vb. n.', 'of Sound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'v. t.', 'To measure the depth of; to fathom; especially, to\n   ascertain the depth of by means of a line and plummet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'v. t.', 'Fig.: To ascertain, or try to ascertain, the thoughts,\n   motives, and purposes of (a person); to examine; to try; to test; to\n   probe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'v. t.', 'To explore, as the bladder or urethra, with a sound; to\n   examine with a sound; also, to examine by auscultation or percussion;\n   as, to sound a patient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'v. i.', 'To ascertain the depth of water with a sounding line or\n   other device.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'n.', 'Any elongated instrument or probe, usually metallic, by\n   which cavities of the body are sounded or explored, especially the\n   bladder for stone, or the urethra for a stricture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'n.', 'The peceived object occasioned by the impulse or vibration\n   of a material substance affecting the ear; a sensation or perception of\n   the mind received through the ear, and produced by the impulse or\n   vibration of the air or other medium with which the ear is in contact;\n   the effect of an impression made on the organs of hearing by an impulse\n   or vibration of the air caused by a collision of bodies, or by other\n   means; noise; report; as, the sound of a drum; the sound of the human\n   voice; a horrid sound; a charming sound; a sharp, high, or shrill\n   sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'n.', 'The occasion of sound; the impulse or vibration which would\n   occasion sound to a percipient if present with unimpaired; hence, the\n   theory of vibrations in elastic media such cause sound; as, a treatise\n   on sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'n.', 'Noise without signification; empty noise; noise and nothing\n   else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'v. i.', 'To make a noise; to utter a voice; to make an impulse of\n   the air that shall strike the organs of hearing with a perceptible\n   effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'v. i.', 'To be conveyed in sound; to be spread or published; to\n   convey intelligence by sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'v. i.', 'To make or convey a certain impression, or to have a\n   certain import, when heard; hence, to seem; to appear; as, this reproof\n   sounds harsh; the story sounds like an invention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'v. t.', 'To causse to make a noise; to play on; as, to sound a\n   trumpet or a horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'v. t.', 'To cause to exit as a sound; as, to sound a note with the\n   voice, or on an instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'v. t.', 'To order, direct, indicate, or proclain by a sound, or\n   sounds; to give a signal for by a certain sound; as, to sound a\n   retreat; to sound a parley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'v. t.', 'To celebrate or honor by sounds; to cause to be reported;\n   to publish or proclaim; as, to sound the praises of fame of a great man\n   or a great exploit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'v. t.', 'To examine the condition of (anything) by causing the\n   same to emit sounds and noting their character; as, to sound a piece of\n   timber; to sound a vase; to sound the lungs of a patient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound', 'v. t.', 'To signify; to import; to denote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soundable', 'a.', 'Capable of being sounded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soundage', 'n.', 'Dues for soundings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sound-board', 'n.', 'A sounding-board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sounder', 'n.', 'One who, or that which; sounds; specifically, an\n   instrument used in telegraphy in place of a register, the\n   communications being read by sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sounder', 'n.', 'A herd of wild hogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sounding', 'a.', 'Making or emitting sound; hence, sonorous; as, sounding\n   words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sounding', 'n.', 'The act of one who, or that which, sounds (in any of the\n   senses of the several verbs).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sounding', 'n.', 'measurement by sounding; also, the depth so ascertained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sounding', 'n.', 'Any place or part of the ocean, or other water, where a\n   sounding line will reach the bottom; -- usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sounding', 'n.', 'The sand, shells, or the like, that are brought up by the\n   sounding lead when it has touched bottom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sounding-board', 'n.', 'A thin board which propagates the sound in a piano,\n   in a violin, and in some other musical instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sounding-board', 'n.', 'A board or structure placed behind or over a pulpit\n   or rostrum to give distinctness to a speakers voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sounding-board', 'n.', 'See Sound boarding, under Sound, a noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soundless', 'a.', 'Not capable of being sounded or fathomed; unfathomable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soundless', 'a.', 'Having no sound; noiseless; silent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soundly', 'adv.', 'In a sound manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soundness', 'n.', 'The quality or state of being sound; as, the soundness\n   of timber, of fruit, of the teeth, etc.; the soundness of reasoning or\n   argument; soundness of faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soune', 'v. t. & i.', 'To sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sounst', 'a.', 'Soused. See Souse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soup', 'n.', 'A liquid food of many kinds, usually made by boiling meat and\n   vegetables, or either of them, in water, -- commonly seasoned or\n   flavored; strong broth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soup', 'v. t.', 'To sup or swallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soup', 'v. t.', 'To breathe out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soup', 'v. t.', 'To sweep. See Sweep, and Swoop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soupe-maigre', 'n.', 'Soup made chiefly from vegetables or fish with a\n   little butter and a few condiments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souple', 'n.', 'That part of a flail which strikes the grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soupy', 'a.', 'Resembling soup; souplike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sour', 'superl.', 'Having an acid or sharp, biting taste, like vinegar, and\n   the juices of most unripe fruits; acid; tart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sour', 'superl.', 'Changed, as by keeping, so as to be acid, rancid, or\n   musty, turned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sour', 'superl.', 'Disagreeable; unpleasant; hence; cross; crabbed;\n   peevish; morose; as, a man of a sour temper; a sour reply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sour', 'superl.', 'Afflictive; painful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sour', 'superl.', 'Cold and unproductive; as, sour land; a sour marsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sour', 'n.', 'A sour or acid substance; whatever produces a painful effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sour', 'v. t.', 'To cause to become sour; to cause to turn from sweet to\n   sour; as, exposure to the air sours many substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sour', 'v. t.', 'To make cold and unproductive, as soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sour', 'v. t.', 'To make unhappy, uneasy, or less agreeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sour', 'v. t.', 'To cause or permit to become harsh or unkindly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sour', 'v. t.', 'To macerate, and render fit for plaster or mortar; as, to\n   sour lime for business purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soured', 'imp. & p. p.', 'of Sour');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souring', 'p. pr. & vb. n.', 'of Sour');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sour', 'v. i.', 'To become sour; to turn from sweet to sour; as, milk soon\n   sours in hot weather; a kind temper sometimes sours in adversity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Source', 'n.', 'The act of rising; a rise; an ascent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Source', 'n.', 'The rising from the ground, or beginning, of a stream of\n   water or the like; a spring; a fountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Source', 'n.', 'That from which anything comes forth, regarded as its cause\n   or origin; the person from whom anything originates; first cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sourcrout', 'n.', 'See Sauerkraut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sourde', 'v. i.', 'To have origin or source; to rise; to spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souring', 'n.', 'Any sour apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sourish', 'a.', 'Somewhat sour; moderately acid; as, sourish fruit; a\n   sourish taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sourkrout', 'n.', 'Same as Sauerkraut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sourly', 'adv.', 'In a sour manner; with sourness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sourness', 'n.', 'The quality or state of being sour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sours', 'n.', 'Source. See Source.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soursop', 'n.', 'The large succulent and slightly acid fruit of a small\n   tree (Anona muricata) of the West Indies; also, the tree itself. It is\n   closely allied to the custard apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sourwood', 'n.', 'The sorrel tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sous', 'n.', 'Alt. of Souse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souse', 'n.', 'A corrupt form of Sou.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souse', 'n.', 'Pickle made with salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souse', 'n.', 'Something kept or steeped in pickle; esp., the pickled ears,\n   feet, etc., of swine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souse', 'n.', 'The ear; especially, a hogs ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souse', 'n.', 'The act of sousing; a plunging into water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soused', 'imp. & p. p.', 'of Souse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sousing', 'p. pr. & vb. n.', 'of Souse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souse', 'v. t.', 'To steep in pickle; to pickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souse', 'v. t.', 'To plunge or immerse in water or any liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souse', 'v. t.', 'To drench, as by an immersion; to wet throughly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souse', 'v. t.', 'To swoop or plunge, as a bird upon its prey; to fall\n   suddenly; to rush with speed; to make a sudden attack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souse', 'v. t.', 'To pounce upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souse', 'n.', 'The act of sousing, or swooping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souse', 'adv.', 'With a sudden swoop; violently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souslik', 'n.', 'See Suslik.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sout', 'n.', 'Soot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soutache', 'n.', 'A kind of narrow braid, usually of silk; -- also known as\n   Russian braid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soutage', 'n.', 'That in which anything is packed; bagging, as for hops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soutane', 'n.', 'A close garnment with straight sleeves, and skirts\n   reaching to the ankles, and buttoned in front from top to bottom;\n   especially, the black garment of this shape worn by the clergy in\n   France and Italy as their daily dress; a cassock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souter', 'n.', 'A shoemaker; a cobbler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souterly', 'a.', 'Of or pertaining to a cobbler or cobblers; like a\n   cobbler; hence, vulgar; low.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souterrain', 'n.', 'A grotto or cavern under ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'South', 'n.', 'That one of the four cardinal points directly opposite to\n   the north; the region or direction to the right or direction to the\n   right of a person who faces the east.');
  INSERT INTO words (word, word_type, description) VALUES  (    'South', 'n.', 'A country, region, or place situated farther to the south\n   than another; the southern section of a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'South', 'n.', 'Specifically: That part of the United States which is south\n   of Mason and Dixons line. See under Line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'South', 'n.', 'The wind from the south.');
  INSERT INTO words (word, word_type, description) VALUES  (    'South', 'a.', 'Lying toward the south; situated at the south, or in a\n   southern direction from the point of observation or reckoning;\n   proceeding toward the south, or coming from the south; blowing from the\n   south; southern; as, the south pole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'South', 'adv.', 'Toward the south; southward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'South', 'adv.', 'From the south; as, the wind blows south.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southed', 'imp. & p. p.', 'of South');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southing', 'p. pr. & vb. n.', 'of South');
