  INSERT INTO words (word, word_type, description) VALUES  (    'Southdown', 'n.', 'A Southdown sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southeast', 'n.', 'The point of the compass equally distant from the south\n   and the east; the southeast part or region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southeast', 'a.', 'Of or pertaining to the southeast; proceeding toward, or\n   coming from, the southeast; as, a southeast course; a southeast wind.');
