  INSERT INTO words (word, word_type, description) VALUES  (    'Southeaster', 'adv.', 'Toward the southeast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southeastern', 'a.', 'Of or pertaining to the southeast; southeasterly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southeastward', 'adv.', 'Alt. of Southeastwardly');
