  INSERT INTO words (word, word_type, description) VALUES  (    'Souther', 'n.', 'A strong wind, gale, or storm from the south.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southerliness', 'n.', 'The quality or state of being southerly; direction\n   toward the south.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southerly', 'a.', 'Southern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southern', 'a.', 'Of or pertaining to the south; situated in, or proceeding\n   from, the south; situated or proceeding toward the south.');
