  INSERT INTO words (word, word_type, description) VALUES  (    'Southerner', 'n.', 'An inhabitant or native of the south, esp. of the\n   Southern States of North America; opposed to Northerner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southernliness', 'n.', 'Southerliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southernly', 'a.', 'Somewhat southern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southernly', 'adv.', 'In a southerly manner or course; southward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southernmost', 'a.', 'Farthest south.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southernwood', 'n.', 'A shrubby species of wormwood (Artemisia Abrotanum)\n   having aromatic foliage. It is sometimes used in making beer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southing', 'n.', 'Tendency or progress southward; as, the southing of the\n   sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southing', 'n.', 'The time at which the moon, or other heavenly body,\n   passes the meridian of a place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southing', 'n.', 'Distance of any heavenly body south of the equator; south\n   declination; south latitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southing', 'n.', 'Distance southward from any point departure or of\n   reckoning, measured on a meridian; -- opposed to northing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southly', 'adv.', 'Southerly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southmost', 'a.', 'Farthest toward the south; southernmost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southness', 'n.', 'A tendency in the end of a magnetic needle to point\n   toward the south pole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southren', 'a.', 'Southern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southron', 'n.', 'An inhabitant of the more southern part of a country;\n   formerly, a name given in Scotland to any Englishman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southsay', 'v. i.', 'See Soothsay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southsayer', 'n.', 'See Soothsayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'South southerly', '', 'the old squaw; -- so called in imitation of its cry.\n   Called also southerly, and southerland. See under Old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southward', 'adv.', 'Alt. of Southwards');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southwards', 'adv.', 'Toward the south, or toward a point nearer the south\n   than the east or west point; as, to go southward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southward', 'a.', 'Toward the south.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southward', 'n.', 'The southern regions or countries; the south.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southwardly', 'adv.', 'In a southern direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Southwest', 'n.', 'The point of the compass equally from the south and the\n   west; the southwest part or region.');
