  INSERT INTO words (word, word_type, description) VALUES  (    'Souvenir', 'n.', 'That which serves as a reminder; a remembrancer; a\n   memento; a keepsake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sovereign', 'a.', 'Supreme or highest in power; superior to all others;\n   chief; as, our sovereign prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sovereign', 'a.', 'Independent of, and unlimited by, any other; possessing,\n   or entitled to, original authority or jurisdiction; as, a sovereign\n   state; a sovereign discretion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sovereign', 'a.', 'Princely; royal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sovereign', 'a.', 'Predominant; greatest; utmost; paramount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sovereign', 'a.', 'Efficacious in the highest degree; effectual;\n   controlling; as, a sovereign remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sovereign', 'n.', 'The person, body, or state in which independent and\n   supreme authority is vested; especially, in a monarchy, a king, queen,\n   or emperor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sovereign', 'n.', 'A gold coin of Great Britain, on which an effigy of the\n   head of the reigning king or queen is stamped, valued at one pound\n   sterling, or about $4.86.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sovereign', 'n.', 'Any butterfly of the tribe Nymphalidi, or genus\n   Basilarchia, as the ursula and the viceroy.');
