  INSERT INTO words (word, word_type, description) VALUES  (    'Sovereignly', 'adv.', 'In a sovereign manner; in the highest degree;\n   supremely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sovereignties', 'pl. ', 'of Sovereignty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sovereignty', 'n.', 'The quality or state of being sovereign, or of being a\n   sovereign; the exercise of, or right to exercise, supreme power;\n   dominion; sway; supremacy; independence; also, that which is sovereign;\n   a sovereign state; as, Italy was formerly divided into many\n   sovereignties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sovran', 'a.', 'A variant of Sovereign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sow', 'v. i.', 'To sew. See Sew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sow', 'n.', 'The female of swine, or of the hog kind.');
