  INSERT INTO words (word, word_type, description) VALUES  (    'Spartan', 'n.', 'A native or inhabitant of Sparta; figuratively, a person\n   of great courage and fortitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sparteine', 'n.', 'A narcotic alkaloid extracted from the tops of the\n   common broom (Cytisus scoparius, formerly Spartium scoparium), as a\n   colorless oily liquid of aniline-like odor and very bitter taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'parterie', 'n.', 'Articles made of the blades or fiber of the Lygeum\n   Spartum and Stipa (/ Macrochloa) tenacissima, kinds of grass used in\n   Spain and other countries for making ropes, mats, baskets, nets, and\n   mattresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sparth', 'n.', 'An Anglo-Saxon battle-ax, or halberd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sparve', 'n.', 'The hedge sparrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spary', 'a.', 'Sparing; parsimonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spasm', 'v. t.', 'An involuntary and unnatural contraction of one or more\n   muscles or muscular fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spasm', 'v. t.', 'A sudden, violent, and temporary effort or emotion; as, a\n   spasm of repentance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spasmatical', 'a.', 'Spasmodic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spasmodic', 'a.', 'Of or pertaining to spasm; consisting in spasm; occuring\n   in, or characterized by, spasms; as, a spasmodic asthma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spasmodic', 'a.', 'Soon relaxed or exhausted; convulsive; intermittent; as,\n   spasmodic zeal or industry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spasmodic', 'n.', 'A medicine for spasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spasmodical', 'a.', 'Same as Spasmodic, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spastic', 'a.', 'Of or pertaining to spasm; spasmodic; especially,\n   pertaining to tonic spasm; tetanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spastically', 'adv.', 'Spasmodically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spasticity', 'n.', 'A state of spasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spasticity', 'n.', 'The tendency to, or capability of suffering, spasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spat', '', 'imp. of Spit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spat', 'n.', 'A young oyster or other bivalve mollusk, both before and\n   after it first becomes adherent, or such young, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spat', 'v. i. & t.', 'To emit spawn; to emit, as spawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spat', 'n.', 'A light blow with something flat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spat', 'n.', 'Hence, a petty combat, esp. a verbal one; a little quarrel,\n   dispute, or dissension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spat', 'v. i.', 'To dispute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatted', 'imp. & p. p.', 'of Spat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatting', 'p. pr. & vb. n.', 'of Spat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spat', 'v. t.', 'To slap, as with the open hand; to clap together; as the\n   hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatangoid', 'a.', 'Of or pertaining to the Spatangoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatangoid', 'n.', 'One of the Spatangoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatangoidea', 'n. pl.', 'An order of irregular sea urchins, usually having\n   a more or less heart-shaped shell with four or five petal-like\n   ambulacra above. The mouth is edentulous and situated anteriorly, on\n   the under side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatangus', 'n.', 'A genus of heart-shaped sea urchins belonging to the\n   Spatangoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatchcock', 'n.', 'See Spitchcock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spate', 'n.', 'A river flood; an overflow or inundation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spathae', 'pl. ', 'of Spatha');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatha', 'n.', 'A spathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spathaceous', 'a.', 'Having a spathe; resembling a spathe; spathal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spathal', 'a.', 'Furnished with a spathe; as, spathal flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spathe', 'n.', 'A special involucre formed of one leaf and inclosing a\n   spadix, as in aroid plants and palms. See the Note under Bract, and\n   Illust. of Spadix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spathed', 'a.', 'Having a spathe or calyx like a sheath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spathic', 'a.', 'Like spar; foliated or lamellar; spathose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spathiform', 'a.', 'Resembling spar in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spathose', 'a.', 'See Spathic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spathose', 'a.', 'Having a spathe; resembling a spathe; spatheceous;\n   spathal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spathous', 'a.', 'Spathose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spathulate', 'a.', 'See Spatulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatial', 'a.', 'Of or pertaining to space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatially', 'adv.', 'As regards space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatiate', 'v. t.', 'To rove; to ramble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spattered', 'imp. & p. p.', 'of Spatter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spattering', 'p. pr. & vb. n.', 'of Spatter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatter', 'v. t.', 'To sprinkle with a liquid or with any wet substance, as\n   water, mud, or the like; to make wet of foul spots upon by sprinkling;\n   as, to spatter a coat; to spatter the floor; to spatter boots with mud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatter', 'v. t.', 'To distribute by sprinkling; to sprinkle around; as, to\n   spatter blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatter', 'v. t.', 'Fig.: To injure by aspersion; to defame; to soil; also,\n   to throw out in a defamatory manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatter', 'v. i.', 'To throw something out of the mouth in a scattering\n   manner; to sputter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatterdashed', 'a.', 'Wearing spatterdashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatterdashes', 'n. pl.', 'Coverings for the legs, to protect them from\n   water and mud; long gaiters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatter-dock', 'n.', 'The common yellow water lily (Nuphar advena).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spattle', 'n.', 'Spawl; spittle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spattle', 'n.', 'A spatula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spattle', 'n.', 'A tool or implement for mottling a molded article with\n   coloring matter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spattling-poppy', 'n.', 'A kind of catchfly (Silene inflata) which is\n   sometimes frothy from the action of captured insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatula', 'n.', 'An implement shaped like a knife, flat, thin, and somewhat\n   flexible, used for spreading paints, fine plasters, drugs in\n   compounding prescriptions, etc. Cf. Palette knife, under Palette.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spatulate', 'a.', 'Shaped like spatula, or like a battledoor, being\n   roundish, with a long, narrow, linear base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spauld', 'n.', 'The shoulder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spavin', 'n.', 'A disease of horses characterized by a bony swelling\n   developed on the hock as the result of inflammation of the bones; also,\n   the swelling itself. The resulting lameness is due to the inflammation,\n   and not the bony tumor as popularly supposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spavined', 'a.', 'Affected with spavin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spaw', 'n.', 'See Spa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spawl', 'n.', 'A splinter or fragment, as of wood or stone. See Spall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spawl', 'n.', 'Scattered or ejected spittle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spawled', 'imp. & p. p.', 'of Spawl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spawling', 'p. pr. & vb. n.', 'of Spawl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spawl', 'v. i. & t.', 'To scatter spittle from the mouth; to spit, as\n   saliva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spawling', 'n.', 'That which is spawled, or spit out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spawned', 'imp. & p. p.', 'of Spawn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spawning', 'p. pr. & vb. n.', 'of Spawn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spawn', 'v. t.', 'To produce or deposit (eggs), as fishes or frogs do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spawn', 'v. t.', 'To bring forth; to generate; -- used in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spawn', 'v. i.', 'To deposit eggs, as fish or frogs do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spawn', 'v. i.', 'To issue, as offspring; -- used contemptuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spawn', 'v. t.', 'The ova, or eggs, of fishes, oysters, and other aquatic\n   animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spawn', 'v. t.', 'Any product or offspring; -- used contemptuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spawn', 'v. t.', 'The buds or branches produced from underground stems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spawn', 'v. t.', 'The white fibrous matter forming the matrix from which\n   fungi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spawner', 'n.', 'A mature female fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spawner', 'n.', 'Whatever produces spawn of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spayed', 'imp. & p. p.', 'of Spay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spaying', 'p. pr. & vb. n.', 'of Spay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spay', 'v. t.', 'To remove or extirpate the ovaries of, as a sow or a\n   bitch; to castrate (a female animal).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spay', 'v. t.', 'The male of the red deer in his third year; a spade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spayad', 'n.', 'Alt. of Spayade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spayade', 'n.', 'A spay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoke', 'imp.', 'of Speak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spake', '', 'of Speak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoken', 'p. p.', 'of Speak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoke', '', 'of Speak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speaking', 'p. pr. & vb. n.', 'of Speak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speak', 'v. i.', 'To utter words or articulate sounds, as human beings; to\n   express thoughts by words; as, the organs may be so obstructed that a\n   man may not be able to speak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speak', 'v. i.', 'To express opinions; to say; to talk; to converse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speak', 'v. i.', 'To utter a speech, discourse, or harangue; to adress a\n   public assembly formally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speak', 'v. i.', 'To discourse; to make mention; to tell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speak', 'v. i.', 'To give sound; to sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speak', 'v. i.', 'To convey sentiments, ideas, or intelligence as if by\n   utterance; as, features that speak of self-will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speak', 'v. t.', 'To utter with the mouth; to pronounce; to utter\n   articulately, as human beings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speak', 'v. t.', 'To utter in a word or words; to say; to tell; to declare\n   orally; as, to speak the truth; to speak sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speak', 'v. t.', 'To declare; to proclaim; to publish; to make known; to\n   exhibit; to express in any way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speak', 'v. t.', 'To talk or converse in; to utter or pronounce, as in\n   conversation; as, to speak Latin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speak', 'v. t.', 'To address; to accost; to speak to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speakable', 'a.', 'Capable of being spoken; fit to be spoken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speakable', 'a.', 'Able to speak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speaker', 'n.', 'One who speaks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speaker', 'n.', 'One who utters or pronounces a discourse; usually, one who\n   utters a speech in public; as, the man is a good speaker, or a bad\n   speaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speaker', 'n.', 'One who is the mouthpiece of others; especially, one who\n   presides over, or speaks for, a delibrative assembly, preserving order\n   and regulating the debates; as, the Speaker of the House of Commons,\n   originally, the mouthpiece of the House to address the king; the\n   Speaker of a House of Representatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speaker', 'n.', 'A book of selections for declamation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speakership', 'n.', 'The office of speaker; as, the speakership of the\n   House of Representatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speaking', 'a.', 'Uttering speech; used for conveying speech; as, man is a\n   speaking animal; a speaking tube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speaking', 'a.', 'Seeming to be capable of speech; hence, lifelike; as, a\n   speaking likeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speking', 'n.', 'The act of uttering words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speking', 'n.', 'Public declamation; oratory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spear', 'n.', 'A long, pointed weapon, used in war and hunting, by\n   thrusting or throwing; a weapon with a long shaft and a sharp head or\n   blade; a lance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spear', 'n.', 'Fig.: A spearman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spear', 'n.', 'A sharp-pointed instrument with barbs, used for stabbing\n   fish and other animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spear', 'n.', 'A shoot, as of grass; a spire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spear', 'n.', 'The feather of a horse. See Feather, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spear', 'n.', 'The rod to which the bucket, or plunger, of a pump is\n   attached; a pump rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speared', 'imp. & p. p.', 'of Spear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spearing', 'p. pr. & vb. n.', 'of Spear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spear', 'v. t.', 'To pierce with a spear; to kill with a spear; as, to\n   spear a fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spear', 'v. i.', 'To shoot into a long stem, as some plants. See Spire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spearer', 'n.', 'One who uses a spear; as, a spearer of fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spearfish', 'n.', 'A large and powerful fish (Tetrapturus albidus) related\n   to the swordfish, but having scales and ventral fins. It is found on\n   the American coast and the Mediterranean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spearfish', 'n.', 'The carp sucker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spearhead', 'n.', 'The pointed head, or end, of a spear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spearmen', 'pl. ', 'of Spearman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spearman', 'n.', 'One who is armed with a spear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spearmint', 'n.', 'A species of mint (Mentha viridis) growing in moist\n   soil. It vields an aromatic oil. See Mint, and Mentha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spearwood', 'n.', 'An Australian tree (Acacia Doratoxylon), and its tough\n   wood, used by the natives for spears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spearwort', 'n.', 'A name given to several species of crowfoot (Ranunculus)\n   which have spear-shaped leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speary', 'a.', 'Having the form of a spear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spece', 'n.', 'Species; kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specht', 'n.', 'A woodpecker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Special', 'a.', 'Of or pertaining to a species; constituting a species or\n   sort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Special', 'a.', 'Particular; peculiar; different from others;\n   extraordinary; uncommon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Special', 'a.', 'Appropriate; designed for a particular purpose, occasion,\n   or person; as, a special act of Parliament or of Congress; a special\n   sermon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Special', 'a.', 'Limited in range; confined to a definite field of action,\n   investigation, or discussion; as, a special dictionary of commercial\n   terms; a special branch of study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Special', 'a.', 'Chief in excellence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Special', 'n.', 'A particular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Special', 'n.', 'One appointed for a special service or occasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specialism', 'n.', 'Devotion to a particular and restricted part or branch\n   of knowledge, art, or science; as, medical specialism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specialist', 'n.', 'One who devotes himself to some specialty; as, a\n   medical specialist, one who devotes himself to diseases of particular\n   parts of the body, as the eye, the ear, the nerves, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specialities', 'pl. ', 'of Speciality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speciality', 'n.', 'A particular or peculiar case; a particularity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speciality', 'n.', 'See Specialty, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speciality', 'n.', 'The special or peculiar mark or characteristic of a\n   person or thing; that for which a person is specially distinguished; an\n   object of special attention; a special occupation or object of\n   attention; a specialty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speciality', 'n.', 'An attribute or quality peculiar to a species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specialization', 'n.', 'The act of specializing, or the state of being\n   spezialized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specialization', 'n.', 'The setting apart of a particular organ for the\n   performance of a particular function.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specialize', 'v. t.', 'To mention specially; to particularize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specialize', 'v. t.', 'To apply to some specialty or limited object; to\n   assign to a specific use; as, specialized knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specialize', 'v. t.', 'To supply with an organ or organs having a special\n   function or functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specially', 'adv.', 'In a special manner; particularly; especially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specially', 'adv.', 'For a particular purpose; as, a meeting of the\n   legislature is specially summoned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specialties', 'pl. ', 'of Specialty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specialty', 'n.', 'Particularity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specialty', 'n.', 'A particular or peculiar case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specialty', 'n.', 'A contract or obligation under seal; a contract by deed;\n   a writing, under seal, given as security for a debt particularly\n   specified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specialty', 'n.', 'That for which a person is distinguished, in which he is\n   specially versed, or which he makes an object of special attention; a\n   speciality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specie', '', 'abl. of L. species sort, kind. Used in the phrase in specie,\n   that is, in sort, in kind, in (its own) form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specie', 'n.', 'Coin; hard money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Species', 'n.', 'Visible or sensible presentation; appearance; a sensible\n   percept received by the imagination; an image.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Species', 'n.', 'A group of individuals agreeing in common attributes, and\n   designated by a common name; a conception subordinated to another\n   conception, called a genus, or generic conception, from which it\n   differs in containing or comprehending more attributes, and extending\n   to fewer individuals. Thus, man is a species, under animal as a genus;\n   and man, in its turn, may be regarded as a genus with respect to\n   European, American, or the like, as species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Species', 'n.', 'In science, a more or less permanent group of existing\n   things or beings, associated according to attributes, or properties\n   determined by scientific observation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Species', 'n.', 'A sort; a kind; a variety; as, a species of low cunning; a\n   species of generosity; a species of cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Species', 'n.', 'Coin, or coined silver, gold, ot other metal, used as a\n   circulating medium; specie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Species', 'n.', 'A public spectacle or exhibition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Species', 'n.', 'A component part of compound medicine; a simple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Species', 'n.', 'An officinal mixture or compound powder of any kind; esp.,\n   one used for making an aromatic tea or tisane; a tea mixture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Species', 'n.', 'The form or shape given to materials; fashion or shape;\n   form; figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specifiable', 'a.', 'Admitting specification; capable of being specified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specific', 'a.', 'Of or pertaining to a species; characterizing or\n   constituting a species; possessing the peculiar property or properties\n   of a thing which constitute its species, and distinguish it from other\n   things; as, the specific form of an animal or a plant; the specific\n   qualities of a drug; the specific distinction between virtue and vice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specific', 'a.', 'Specifying; definite, or making definite; limited;\n   precise; discriminating; as, a specific statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specific', 'a.', 'Exerting a peculiar influence over any part of the body;\n   preventing or curing disease by a peculiar adaption, and not on general\n   principles; as, quinine is a specific medicine in cases of malaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specific', 'n.', 'A specific remedy. See Specific, a., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specific', 'a.', 'Anything having peculiar adaption to the purpose to which\n   it is applied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specifical', 'a.', 'Specific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specifically', 'adv.', 'In a specific manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specificalness', 'n.', 'The quality of being specific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specificate', 'v. t.', 'To show, mark, or designate the species, or the\n   distinguishing particulars of; to specify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specification', 'n.', 'The act of specifying or determining by a mark or\n   limit; notation of limits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specification', 'n.', 'The designation of particulars; particular mention;\n   as, the specification of a charge against an officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specification', 'n.', 'A written statement containing a minute description\n   or enumeration of particulars, as of charges against a public officer,\n   the terms of a contract, the description of an invention, as in a\n   patent; also, a single article, item, or particular, an allegation of a\n   specific act, as in a charge of official misconduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Soecificness', 'n.', 'The quality or state of being specific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specified', 'imp. & p. p.', 'of Specify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specifying', 'p. pr. & vb. n.', 'of Specify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specify', 'v. t.', 'To mention or name, as a particular thing; to designate\n   in words so as to distinguish from other things; as, to specify the\n   uses of a plant; to specify articles purchased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specollum', 'n.', 'See Stylet, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specimen', 'n.', 'A part, or small portion, of anything, or one of a number\n   of things, intended to exhibit the kind and quality of the whole, or of\n   what is not exhibited; a sample; as, a specimen of a mans handwriting;\n   a specimen of painting; aspecimen of ones art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speciocities', 'pl. ', 'of Speciosity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speciosity', 'n.', 'The quality or state of being specious; speciousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speciosity', 'n.', 'That which is specious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specious', 'a.', 'Presenting a pleasing appearance; pleasing in form or\n   look; showy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specious', 'a.', 'Apparently right; superficially fair, just, or correct,\n   but not so in reality; appearing well at first view; plausible; as,\n   specious reasoning; a specious argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speck', 'n.', 'The blubber of whales or other marine mammals; also, the fat\n   of the hippopotamus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speck', 'n.', 'A small discolored place in or on anything, or a small place\n   of a color different from that of the main substance; a spot; a stain;\n   a blemish; as, a speck on paper or loth; specks of decay in fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speck', 'n.', 'A very small thing; a particle; a mite; as, specks of dust;\n   he has not a speck of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speck', 'n.', 'A small etheostomoid fish (Ulocentra stigmaea) common in the\n   Eastern United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specked', 'imp. & p. p.', 'of Speck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specking', 'p. pr. & vb. n.', 'of Speck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speck', 'v. t.', 'To cause the presence of specks upon or in, especially\n   specks regarded as defects or blemishes; to spot; to speckle; as, paper\n   specked by impurities in the water used in its manufacture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speckle', 'n.', 'A little or spot in or anything, of a different substance\n   or color from that of the thing itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speckled', 'imp. & p. p.', 'of Speckle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speckling', 'p. pr. & vb. n.', 'of Speckle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speckle', 'v. t.', 'To mark with small spots of a different color from that\n   of the rest of the surface; to variegate with spots of a different\n   color from the ground or surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speckled', 'a.', 'Marked or variegated with small spots of a different\n   color from that of the rest of the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speckled-belly', 'n.', 'The gadwall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speckled-bill', 'n.', 'The American white-fronted goose (Anser albifrons).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speckledness', 'n.', 'The quality of being speckled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specksioneer', 'n.', 'The chief harpooner, who also directs in cutting up\n   the speck, or blubber; -- so called among whalers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speckt', 'n.', 'A woodpecker. See Speight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectacle', 'n.', 'Something exhibited to view; usually, something\n   presented to view as extraordinary, or as unusual and worthy of special\n   notice; a remarkable or noteworthy sight; a show; a pageant; a\n   gazingstock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectacle', 'n.', 'A spy-glass; a looking-glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectacle', 'n.', 'An optical instrument consisting of two lenses set in a\n   light frame, and worn to assist sight, to obviate some defect in the\n   organs of vision, or to shield the eyes from bright light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectacle', 'n.', 'Fig.: An aid to the intellectual sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectacled', 'a.', 'Furnished with spectacles; wearing spectacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectacled', 'a.', 'Having the eyes surrounded by color markings, or\n   patches of naked skin, resembling spectacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectacular', 'a.', 'Of or pertaining to a shows; of the nature of a show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectacular', 'a.', 'Adapted to excite wonder and admiration by a display\n   of pomp or of scenic effects; as, a spectacular celebration of some\n   event; a spectacular play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectacular', 'a.', 'Pertaining to spectacles, or glasses for the eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectant', 'a.', 'Looking forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectation', 'n.', 'Regard; aspect; appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectator', 'n.', 'One who on; one who sees or beholds; a beholder; one who\n   is personally present at, and sees, any exhibition; as, the spectators\n   at a show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectatorial', 'a.', 'Of or pertaining to a spectator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectatorship', 'n.', 'The office or quality of a spectator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectatorship', 'n.', 'The act of beholding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectatress', 'n.', 'Alt. of Spectatrix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectatrix', 'n.', 'A female beholder or looker-on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specter', 'n.', 'Alt. of Spectre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectre', 'n.', 'Something preternaturally visible; an apparition; a ghost;\n   a phantom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectre', 'n.', 'The tarsius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectre', 'n.', 'A stick insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectioneer', 'n.', 'Same as Specsioneer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectral', 'a.', 'Of or pertaining to a specter; ghosty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectral', 'a.', 'Of or pertaining to the spectrum; made by the spectrum;\n   as, spectral colors; spectral analysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectrally', 'adv.', 'In the form or manner of a specter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectre', 'n.', 'See Specter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectrological', 'a.', 'Of or pertaining to spectrology; as, spectrological\n   studies or experiments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectrology', 'n.', 'The science of spectrum analysis in any or all of its\n   relations and applications.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectrometer', 'n.', 'A spectroscope fitted for measurements of the\n   luminious spectra observed with it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectrophotometer', 'n.', 'An instrument for measuring or comparing the\n   intensites of the colors of the spectrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectroscope', 'n.', 'An optical instrument for forming and examining\n   spectra (as that of solar light, or those produced by flames in which\n   different substances are volatilized), so as to determine, from the\n   position of the spectral lines, the composition of the substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectroscopic', 'a.', 'Alt. of Spectroscopical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectroscopical', 'a.', 'Of or pertaining to a spectroscope, or\n   spectroscopy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectroscopist', 'n.', 'One who investigates by means of a spectroscope;\n   one skilled in the use of the spectroscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectroscopy', 'n.', 'The use of the spectroscope; investigations made with\n   the spectroscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectra', 'pl. ', 'of Spectrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectrum', 'n.', 'An apparition; a specter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectrum', 'n.', 'The several colored and other rays of which light is\n   composed, separated by the refraction of a prism or other means, and\n   observed or studied either as spread out on a screen, by direct vision,\n   by photography, or otherwise. See Illust. of Light, and Spectroscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spectrum', 'n.', 'A luminous appearance, or an image seen after the eye has\n   been exposed to an intense light or a strongly illuminated object. When\n   the object is colored, the image appears of the complementary color, as\n   a green image seen after viewing a red wafer lying on white paper.\n   Called also ocular spectrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specular', 'a.', 'Having the qualities of a speculum, or mirror; having a\n   smooth, reflecting surface; as, a specular metal; a specular surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specular', 'a.', 'Of or pertaining to a speculum; conducted with the aid of\n   a speculum; as, a specular examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specular', 'a.', 'Assisting sight, as a lens or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specular', 'a.', 'Affording view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculated', 'imp. & p. p.', 'of Speculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculating', 'p. pr. & vb. n.', 'of Speculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculate', 'v. i.', 'To consider by turning a subject in the mind, and\n   viewing it in its different aspects and relations; to meditate; to\n   contemplate; to theorize; as, to speculate on questions in religion; to\n   speculate on political events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculate', 'v. i.', 'To view subjects from certain premises given or\n   assumed, and infer conclusions respecting them a priori.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculate', 'v. i.', 'To purchase with the expectation of a contingent\n   advance in value, and a consequent sale at a profit; -- often, in a\n   somewhat depreciative sense, of unsound or hazardous transactions; as,\n   to speculate in coffee, in sugar, or in bank stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculate', 'v. t.', 'To consider attentively; as, to speculate the nature\n   of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculation', 'n.', 'The act of speculating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculation', 'n.', 'Examination by the eye; view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculation', 'n.', 'Mental view of anything in its various aspects and\n   relations; contemplation; intellectual examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculation', 'n.', 'The act or process of reasoning a priori from premises\n   given or assumed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculation', 'n.', 'The act or practice of buying land, goods, shares,\n   etc., in expectation of selling at a higher price, or of selling with\n   the expectation of repurchasing at a lower price; a trading on\n   anticipated fluctuations in price, as distinguished from trading in\n   which the profit expected is the difference between the retail and\n   wholesale prices, or the difference of price in different markets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculation', 'n.', 'Any business venture in involving unusual risks, with\n   a chance for large profits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculation', 'n.', 'A conclusion to which the mind comes by speculating;\n   mere theory; view; notion; conjecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculation', 'n.', 'Power of sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculation', 'n.', 'A game at cards in which the players buy from one\n   another trumps or whole hands, upon a chance of getting the highest\n   trump dealt, which entitles the holder to the pool of stakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculatist', 'n.', 'One who speculates, or forms theories; a speculator; a\n   theorist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculative', 'a.', 'Given to speculation; contemplative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculative', 'a.', 'Involving, or formed by, speculation; ideal;\n   theoretical; not established by demonstration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculative', 'a.', 'Of or pertaining to vision; also, prying; inquisitive;\n   curious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculative', 'a.', 'Of or pertaining to speculation in land, goods,\n   shares, etc.; as, a speculative dealer or enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculator', 'n.', 'One who speculates. Specifically: (a) An observer; a\n   contemplator; hence, a spy; a watcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculator', 'n.', 'One who forms theories; a theorist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculator', 'n.', 'One who engages in speculation; one who buys and sells\n   goods, land, etc., with the expectation of deriving profit from\n   fluctuations in price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculatorial', 'a.', 'Speculatory; speculative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculatory', 'a.', 'Intended or adapted for viewing or espying; having\n   oversight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculatory', 'a.', 'Exercising speculation; speculative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculist', 'n.', 'One who observes or considers; an observer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Specula', 'pl. ', 'of Speculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculum', 'pl. ', 'of Speculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculum', 'n.', 'A mirror, or looking-glass; especially, a metal mirror,\n   as in Greek and Roman archaeology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculum', 'n.', 'A reflector of polished metal, especially one used in\n   reflecting telescopes. See Speculum metal, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculum', 'n.', 'An instrument for dilating certain passages of the body,\n   and throwing light within them, thus facilitating examination or\n   surgical operations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speculum', 'n.', 'A bright and lustrous patch of color found on the wings\n   of ducks and some other birds. It is usually situated on the distal\n   portions of the secondary quills, and is much more brilliant in the\n   adult male than in the female.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sped', '', 'imp. & p. p. of Speed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speece', 'n.', 'Species; sort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speech', 'n.', 'The faculty of uttering articulate sounds or words; the\n   faculty of expressing thoughts by words or articulate sounds; the power\n   of speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speech', 'n.', 'he act of speaking; that which is spoken; words, as\n   expressing ideas; language; conversation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speech', 'n.', 'A particular language, as distinct from others; a tongue; a\n   dialect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speech', 'n.', 'Talk; mention; common saying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speech', 'n.', 'formal discourse in public; oration; harangue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speech', 'n.', 'ny declaration of thoughts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speech', 'v. i. & t.', 'To make a speech; to harangue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speechful', 'a.', 'Full of speech or words; voluble; loquacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speechification', 'n.', 'The act of speechifying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speechifier', 'n.', 'One who makes a speech or speeches; an orator; a\n   declaimer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speechified', 'imp. & p. p.', 'of Speechify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speechifying', 'p. pr. & vb. n.', 'of Speechify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speechify', 'v. i.', 'To make a speech; to harangue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speechifying', 'n.', 'The act of making a speech or speeches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speeching', 'n.', 'The act of making a speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speechless', 'a.', 'Destitute or deprived of the faculty of speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speechless', 'a.', 'Not speaking for a time; dumb; mute; silent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speechmaker', 'n.', 'One who makes speeches; one accustomed to speak in a\n   public assembly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speed', 'n.', 'Prosperity in an undertaking; favorable issue; success.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speed', 'n.', 'The act or state of moving swiftly; swiftness; velocity;\n   rapidly; rate of motion; dispatch; as, the speed a horse or a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speed', 'n.', 'One who, or that which, causes or promotes speed or success.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sped', 'imp. & p. p.', 'of Speed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speeded', '', 'of Speed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speeding', 'p. pr. & vb. n.', 'of Speed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speed', 'n.', 'To go; to fare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speed', 'n.', 'To experience in going; to have any condition, good or ill;\n   to fare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speed', 'n.', 'To fare well; to have success; to prosper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speed', 'n.', 'To make haste; to move with celerity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speed', 'n.', 'To be expedient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speed', 'v. t.', 'To cause to be successful, or to prosper; hence, to aid;\n   to favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speed', 'v. t.', 'To cause to make haste; to dispatch with celerity; to\n   drive at full speed; hence, to hasten; to hurry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speed', 'v. t.', 'To hasten to a conclusion; to expedite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speed', 'v. t.', 'To hurry to destruction; to put an end to; to ruin; to\n   undo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speed', 'v. t.', 'To wish success or god fortune to, in any undertaking,\n   especially in setting out upon a journey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speeder', 'n.', 'One who, or that which, speeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speeder', 'n.', 'A machine for drawing and twisting slivers to form\n   rovings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speedful', 'a.', 'Full of speed (in any sense).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speedfully', 'adv.', 'In a speedful manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speedily', 'adv.', 'In a speedy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speediness', 'n.', 'The quality or state of being speedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speedless', 'a.', 'Being without speed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speedwell', 'n.', 'Any plant of the genus Veronica, mostly low herbs with\n   pale blue corollas, which quickly fall off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speedy', 'superl.', 'Not dilatory or slow; quick; swift; nimble; hasty;\n   rapid in motion or performance; as, a speedy flight; on speedy foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speer', 'n.', 'A sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speer', 'v. t.', 'To ask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speet', 'v. t.', 'To stab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speight', 'n.', 'A woodpecker; -- called also specht, spekt, spight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speir', 'v. i.', 'To ask. See Spere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speiskobalt', 'n.', 'Smaltite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speiss', 'n.', 'A regulus consisting essentially of nickel, obtained as a\n   residue in fusing cobalt and nickel ores with silica and sodium\n   carbonate to make smalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spekboom', 'n.', 'The purslane tree of South Africa, -- said to be the\n   favorite food of elephants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speke', 'v. i. & t.', 'To speak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spekehouse', 'n.', 'The parlor or reception room of a convent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spelding', 'n.', 'A haddock or other small fish split open and dried in the\n   sun; -- called also speldron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spelicans', 'n. pl.', 'See Spilikin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spelk', 'n.', 'A small stick or rod used as a spike in thatching; a\n   splinter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spell', 'n.', 'A spelk, or splinter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spelled', 'imp. & p. p.', 'of Spell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spelling', 'p. pr. & vb. n.', 'of Spell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spell', 'v. t.', 'To supply the place of for a time; to take the turn of,\n   at work; to relieve; as, to spell the helmsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spell', 'n.', 'The relief of one person by another in any piece of work or\n   watching; also, a turn at work which is carried on by one person or\n   gang relieving another; as, a spell at the pumps; a spell at the\n   masthead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spell', 'n.', 'The time during which one person or gang works until\n   relieved; hence, any relatively short period of time, whether a few\n   hours, days, or weeks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spell', 'n.', 'One of two or more persons or gangs who work by spells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spell', 'n.', 'A gratuitous helping forward of anothers work; as, a\n   logging spell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spell', 'n.', 'A story; a tale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spell', 'n.', 'A stanza, verse, or phrase supposed to be endowed with\n   magical power; an incantation; hence, any charm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spelled', 'imp. & p. p.', 'of Spell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spelt', '', 'of Spell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spelling', 'p. pr. & vb. n.', 'of Spell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spell', 'v. t.', 'To tell; to relate; to teach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spell', 'v. t.', 'To put under the influence of a spell; to affect by a\n   spell; to bewitch; to fascinate; to charm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spell', 'v. t.', 'To constitute; to measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spell', 'v. t.', 'To tell or name in their proper order letters of, as a\n   word; to write or print in order the letters of, esp. the proper\n   letters; to form, as words, by correct orthography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spell', 'v. t.', 'To discover by characters or marks; to read with\n   difficulty; -- usually with out; as, to spell out the sense of an\n   author; to spell out a verse in the Bible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spell', 'v. i.', 'To form words with letters, esp. with the proper letters,\n   either orally or in writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spell', 'v. i.', 'To study by noting characters; to gain knowledge or learn\n   the meaning of anything, by study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spellable', 'a.', 'Capable of being spelt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spellbound', 'a.', 'Bound by, or as by, a spell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speller', 'n.', 'One who spells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Speller', 'n.', 'A spelling book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spellful', 'a.', 'Abounding in spells, or charms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spelling', 'n.', 'The act of one who spells; formation of words by letters;\n   orthography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spelling', 'a.', 'Of or pertaining to spelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spellken', 'n.', 'A theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spellwork', 'n.', 'Power or effect of magic; that which is wrought by\n   magic; enchantment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spelt', '', 'imp. & p. p. of Spell. Spelled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spelt', 'n.', 'A species of grain (Triticum Spelta) much cultivated for\n   food in Germany and Switzerland; -- called also German wheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spelt', 'n.', 'Spelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spelt', 'v. t. & i.', 'To split; to break; to spalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spelter', 'n.', 'Zinc; -- especially so called in commerce and arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spelunc', 'n.', 'A cavern; a cave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spence', 'n.', 'A place where provisions are kept; a buttery; a larder; a\n   pantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spence', 'n.', 'The inner apartment of a country house; also, the place\n   where the family sit and eat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spencer', 'n.', 'One who has the care of the spence, or buttery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spencer', 'n.', 'A short jacket worn by men and by women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spencer', 'n.', 'A fore-and-aft sail, abaft the foremast or the mainmast,\n   hoisted upon a small supplementary mast and set with a gaff and no\n   boom; a trysail carried at the foremast or mainmast; -- named after its\n   inventor, Knight Spencer, of England [1802].');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spent', 'imp. & p. p.', 'of Spend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spending', 'p. pr. & vb. n.', 'of Spend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spend', 'v. t.', 'To weigh or lay out; to dispose of; to part with; as, to\n   spend money for clothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spend', 'v. t.', 'To bestow; to employ; -- often with on or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spend', 'v. t.', 'To consume; to waste; to squander; to exhaust; as, to\n   spend an estate in gaming or other vices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spend', 'v. t.', 'To pass, as time; to suffer to pass away; as, to spend a\n   day idly; to spend winter abroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spend', 'v. t.', 'To exhaust of force or strength; to waste; to wear away;\n   as, the violence of the waves was spent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spend', 'v. i.', 'To expend money or any other possession; to consume, use,\n   waste, or part with, anything; as, he who gets easily spends freely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spend', 'v. i.', 'To waste or wear away; to be consumed; to lose force or\n   strength; to vanish; as, energy spends in the using of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spend', 'v. i.', 'To be diffused; to spread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spend', 'v. i.', 'To break ground; to continue working.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spender', 'n.', 'One who spends; esp., one who spends lavishly; a prodigal;\n   a spendthrift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spending', 'n.', 'The act of expending; expenditure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spendthrift', 'n.', 'One who spends money profusely or improvidently; a\n   prodigal; one who lavishes or wastes his estate. Also used\n   figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spendthrift', 'a.', 'Prodigal; extravagant; wasteful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spendthrifty', 'a.', 'Spendthrift; prodigal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spenserian', 'a.', 'Of or pertaining to the English poet Spenser; --\n   specifically applied to the stanza used in his poem \"The Faerie\n   Queene.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spent', 'a.', 'Exhausted; worn out; having lost energy or motive force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spent', 'a.', 'Exhausted of spawn or sperm; -- said especially of fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sper', 'v. t.', 'Alt. of Sperre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sperre', 'v. t.', 'To shut in; to support; to inclose; to fasten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sperable', 'a.', 'Within the range of hpe; proper to be hoped for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sperable', 'n.', 'See Sperable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sperage', 'n.', 'Asperagus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sperate', 'a.', 'Hoped for, or to be hoped for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spere', 'v. i.', 'To search; to pry; to ask; to inquire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spere', 'n.', 'A sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sperge', 'n.', 'A charge of wash for the still.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sperling', 'n.', 'A smelt; a sparling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sperling', 'n.', 'A young herring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sperm', 'n.', 'The male fecundating fluid; semen. See Semen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sperm', 'n.', 'Spermaceti.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermaceti', 'n.', 'A white waxy substance obtained from cavities in the\n   head of the sperm whale, and used making candles, oilments, cosmetics,\n   etc. It consists essentially of ethereal salts of palmitic acid with\n   ethal and other hydrocarbon bases. The substance of spermaceti after\n   the removal of certain impurities is sometimes called cetin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermalist', 'n.', 'See Spermist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermaphore', 'n.', 'That part of the ovary from which the ovules arise;\n   the placenta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermary', 'n.', 'An organ in which spermatozoa are developed; a sperm\n   gland; a testicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermathecae', 'pl. ', 'of Spermatheca');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatheca', 'n.', 'A small sac connected with the female reproductive\n   organs of insects and many other invertebrates, serving to receive and\n   retain the spermatozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatic', 'a.', 'Of or pertaining to semen; as, the spermatic fluid, the\n   spermatic vessels, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatical', 'a.', 'Spermatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatin', 'n.', 'A substance allied to alkali albumin and to mucin,\n   present in semen, to which it is said to impart the mucilaginous\n   character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatism', 'n.', 'The emission of sperm, or semen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatia', 'pl. ', 'of Spermatium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatium', 'n.', 'One of the motionless spermatozoids in the conceptacles\n   of certain fungi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatize', 'v. i.', 'To yield seed; to emit seed, or sperm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermato-', '', 'Alt. of Spermo-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermo-', '', 'Combining forms from Gr. spe`rma, -atos, seed, sperm, semen\n   (of plants or animals); as, spermatoblast, spermoblast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatoblast', 'n.', 'Same as Spermoblast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatocyte', 'n.', 'Same as Spermoblast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatogemma', 'n.', 'Same as Spermosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatogenesis', 'n.', 'The development of the spermatozoids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatogenetic', 'a.', 'Relating to, or connected with, spermatogenesis;\n   as, spermatogenetic function.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatogenous', 'a.', 'Sperm-producing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatogonium', 'n.', 'A primitive seminal cell, occuring in masses in the\n   seminal tubules. It divides into a mass (spermosphere) of small cells\n   (spermoblast), which in turn give rise to spermatozoids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatoid', 'a.', 'Spermlike; resembling sperm, or semen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatoa', 'pl. ', 'of Spermatoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatoon', 'n.', 'A spermoblast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatophore', 'n.', 'Same as Spermospore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatophore', 'n.', 'A capsule or pocket inclosing a number of\n   spermatozoa. They are present in many annelids, brachiopods, mollusks,\n   and crustaceans. In cephalopods the structure of the capsule is very\n   complex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatophorous', 'a.', 'Producing seed, or sperm; seminiferous; as, the\n   so-called spermatophorous cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatorrhea', 'n.', 'Alt. of Spermatorrhoea');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatorrhoea', 'n.', 'Abnormally frequent involuntary emission of the\n   semen without copulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatospore', 'n.', 'Same as Spermospore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatozoid', 'n.', 'The male germ cell in animals and plants, the\n   essential element in fertilization; a microscopic animalcule-like\n   particle, usually provided with one or more cilia by which it is\n   capable of active motion. In animals, the familiar type is that of a\n   small, more or less ovoid head, with a delicate threadlike cilium, or\n   tail. Called also spermatozoon. In plants the more usual term is\n   antherozoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatozooid', 'n.', 'A spermatozoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatozoa', 'pl. ', 'of Spermatozoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermatozoon', 'n.', 'Same as Spermatozoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermic', 'a.', 'Of or pertaining to sperm, or semen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermidia', 'pl. ', 'of Spermidium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermidium', 'n.', 'An achenium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermist', 'n.', 'A believer in the doctrine, formerly current, of\n   encasement in the male (see Encasement), in which the seminal thread,\n   or spermatozoid, was considered as the real animal germ, the head being\n   the true animal head and the tail the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermoblast', 'n.', 'One of the cells formed by the division of the\n   spermospore, each of which is destined to become a spermatozoid; a\n   spermatocyte; a spermatoblast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermococcus', 'n.', 'The nucleus of the sperm cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermoderm', 'n.', 'The covering of a seed; -- sometimes limited to the\n   outer coat or testa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermogonium', 'n.', 'A conceptacle of certain lichens, which contains\n   spermatia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermologist', 'n.', 'One who treats of, or collects, seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermophile', 'n.', 'Any ground squirrel of the genus Spermophilus; a\n   gopher. See Illust. under Gopher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermophore', 'n.', 'A spermatophore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermophyta', 'n. pl.', 'Plants which produce seed; phaenogamia. These\n   plants constitute the highest grand division of the vegetable kingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermophyte', 'n.', 'Any plant which produces true seeds; -- a term\n   recently proposed to replace ph/nogam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermophytic', 'a.', 'Capable of producing seeds; ph/nogamic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermoplasma', 'n.', 'The protoplasm of the sperm cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermosphere', 'n.', 'A mass or ball of cells formed by the repeated\n   division of a male germinal cell (spermospore), each constituent cell\n   (spermoblast) of which is converted into a spermatozoid; a\n   spermatogemma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermospore', 'n.', 'The male germinal or seminal cell, from the breaking\n   up of which the spermoblasts are formed and ultimately the\n   spermatozoids; a spermatospore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spermule', 'n.', 'A sperm cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sperm whale', '', 'A very large toothed whale (Physeter macrocephalus),\n   having a head of enormous size. The upper jaw is destitute of teeth. In\n   the upper part of the head, above the skull, there is a large cavity,\n   or case, filled with oil and spermaceti. This whale sometimes grows to\n   the length of more than eighty feet. It is found in the warmer parts of\n   all the oceans. Called also cachalot, and spermaceti whale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sperrylite', 'n.', 'An arsenide of platinum occuring in grains and minute\n   isometric crystals of tin-white color. It is found near Sudbury,\n   Ontario Canada, and is the only known compound of platinum occuring in\n   nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sperse', 'v. t.', 'To disperse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spessartite', 'n.', 'A manganesian variety of garnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spet', 'v. t.', 'To spit; to throw out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spet', 'n.', 'Spittle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spetches', 'n. pl.', 'Parings and refuse of hides, skins, etc., from which\n   glue is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spewed', 'imp. & p. p.', 'of Spew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spewing', 'p. pr.& vb. n.', 'of Spew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spew', 'v. t.', 'To eject from the stomach; to vomit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spew', 'v. t.', 'To cast forth with abhorrence or disgust; to eject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spew', 'v. i.', 'To vomit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spew', 'v. i.', 'To eject seed, as wet land swollen with frost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spew', 'n.', 'That which is vomited; vomit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spewer', 'n.', 'One who spews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spewiness', 'n.', 'The state of being spewy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spewy', 'a.', 'Wet; soggy; inclined to spew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphacel', 'n.', 'Gangrene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphacelated', 'imp. & p. p.', 'of Spacelate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphacelating', 'p. pr. & vb. n.', 'of Spacelate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spacelate', 'v. i.', 'To die, decay, or become gangrenous, as flesh or\n   bone; to mortify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphacelate', 'v. t.', 'To affect with gangrene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphacelate', 'a.', 'Alt. of Sphacelated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphacelated', 'a.', 'Affected with gangrene; mortified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphacelation', 'n.', 'The process of becoming or making gangrenous;\n   mortification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphacelus', 'n.', 'Gangrenous part; gangrene; slough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphaerenchyma', 'n.', 'Vegetable tissue composed of thin-walled rounded\n   cells, -- a modification of parenchyma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphaeridia', 'pl. ', 'of Sphaeridium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphaeridium', 'n.', 'A peculiar sense organ found upon the exterior of most\n   kinds of sea urchins, and consisting of an oval or sherical head\n   surmounting a short pedicel. It is generally supposed to be an\n   olfactory organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphaerospore', 'n.', 'One of the nonsexual spores found in red algae; a\n   tetraspore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphaerulite', 'n.', 'Same as Spherulite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphagnicolous', 'a.', 'Growing in moss of the genus Sphagnum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphagnous', 'a.', 'Pertaining to moss of the genus Sphagnum, or bog moss;\n   abounding in peat or bog moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphagnum', 'n.', 'A genus of mosses having white leaves slightly tinged\n   with red or green and found growing in marshy places; bog moss; peat\n   moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphalerite', 'n.', 'Zinc sulphide; -- called also blende, black-jack, false\n   galena, etc. See Blende (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphene', 'n.', 'A mineral found usually in thin, wedge-shaped crystals of a\n   yellow or green to black color. It is a silicate of titanium and\n   calcium; titanite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphenethmoid', 'a.', 'Of or pertaining to both the sphenoidal and the\n   ethmoidal regions of the skull, or the sphenethmoid bone;\n   sphenethmoidal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphenethmoid', 'n.', 'The sphenethmoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphenethmoidal', 'a.', 'Relating to the sphenoethmoid bone; sphenoethmoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spheniscan', 'n.', 'Any species of penguin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spheno-', '', 'A combining form used in anatomy to indicate connection\n   with, or relation to, the sphenoid bone; as in sphenomaxillary,\n   sphenopalatine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphenodon', 'n.', 'Same as Hatteria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphenoethmoidal', 'a.', 'Sphenethmoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphenogram', 'n.', 'A cuneiform, or arrow-headed, character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphenographer', 'n.', 'One skilled in sphenography; a sphenographist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphenographic', 'a.', 'Of or pertaining to sphenography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphenographist', 'n.', 'A sphenographer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphenography', 'n.', 'The art of writing in cuneiform characters, or of\n   deciphering inscriptions made in such characters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphenoid', 'a.', 'Wedge-shaped; as, a sphenoid crystal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphenoid', 'a.', 'Of or pertaining to the sphenoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphenoid', 'n.', 'A wedge-shaped crystal bounded by four equal isosceles\n   triangles. It is the hemihedral form of a square pyramid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphenoid', 'n.', 'The sphenoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphenoidal', 'a.', 'Sphenoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphenoidal', 'a.', 'Pertaining to, or resembling, a sphenoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphenotic', 'a.', 'Of, pertaining to, or designating, the sphenotic bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphenotic', 'n.', 'The sphenotic bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spheral', 'a.', 'Of or pertaining to a sphere or the spheres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spheral', 'a.', 'Rounded like a sphere; sphere-shaped; hence, symmetrical;\n   complete; perfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphere', 'n.', 'A body or space contained under a single surface, which in\n   every part is equally distant from a point within called its center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphere', 'n.', 'Hence, any globe or globular body, especially a celestial\n   one, as the sun, a planet, or the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphere', 'n.', 'The apparent surface of the heavens, which is assumed to be\n   spherical and everywhere equally distant, in which the heavenly bodies\n   appear to have their places, and on which the various astronomical\n   circles, as of right ascension and declination, the equator, ecliptic,\n   etc., are conceived to be drawn; an ideal geometrical sphere, with the\n   astronomical and geographical circles in their proper positions on it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphere', 'n.', 'In ancient astronomy, one of the concentric and eccentric\n   revolving spherical transparent shells in which the stars, sun,\n   planets, and moon were supposed to be set, and by which they were\n   carried, in such a manner as to produce their apparent motions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphere', 'n.', 'The extension of a general conception, or the totality of\n   the individuals or species to which it may be applied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphere', 'n.', 'Circuit or range of action, knowledge, or influence;\n   compass; province; employment; place of existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphere', 'n.', 'Rank; order of society; social positions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphere', 'n.', 'An orbit, as of a star; a socket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphered', 'imp. & p. p.', 'of Sphere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphering', 'p. pr. & vb. n.', 'of Sphere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphere', 'v. t.', 'To place in a sphere, or among the spheres; to insphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphere', 'v. t.', 'To form into roundness; to make spherical, or spheral;\n   to perfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spherical', 'a.', 'Alt. of Spheric');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spheric', 'a.', 'Having the form of a sphere; like a sphere; globular;\n   orbicular; as, a spherical body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spheric', 'a.', 'Of or pertaining to a sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spheric', 'a.', 'Of or pertaining to the heavenly orbs, or to the sphere or\n   spheres in which, according to ancient astronomy and astrology, they\n   were set.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphericity', 'n.', 'The quality or state of being spherial; roundness; as,\n   the sphericity of the planets, or of a drop of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphericle', 'n.', 'A small sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spherics', 'n.', 'The doctrine of the sphere; the science of the properties\n   and relations of the circles, figures, and other magnitudes of a\n   sphere, produced by planes intersecting it; spherical geometry and\n   trigonometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spherobacteria', 'n. pl.', 'See the Note under Microbacteria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spheroconic', 'n.', 'A nonplane curve formed by the intersection of the\n   surface of an oblique cone with the surface of a sphere whose center is\n   at the vertex of the cone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spherograph', 'n.', 'An instrument for facilitating the practical use of\n   spherics in navigation and astronomy, being constructed of two\n   cardboards containing various circles, and turning upon each other in\n   such a manner that any possible spherical triangle may be readily\n   found, and the measures of the parts read off by inspection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spheroid', 'n.', 'A body or figure approaching to a sphere, but not\n   perfectly spherical; esp., a solid generated by the revolution of an\n   ellipse about one of its axes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spheroidal', 'a.', 'Having the form of a spheroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spheroidic', 'a.', 'Alt. of Spheroidical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spheroidical', 'a.', 'See Spheroidal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spheroidicity', 'n.', 'Alt. of Spheroidity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spheroidity', 'n.', 'The quality or state of being spheroidal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spheromere', 'n.', 'Any one of the several symmetrical segments arranged\n   around the central axis and composing the body of a radiate anmal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spherometer', 'n.', 'An instrument for measuring the curvature of spherical\n   surface, as of lenses for telescope, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spherosiderite', 'n.', 'Siderite occuring in spheroidal masses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spherosome', 'n.', 'The body wall of any radiate animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spherulate', 'a.', 'Covered or set with spherules; having one or more rows\n   of spherules, or minute tubercles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spherule', 'n.', 'A little sphere or spherical body; as, quicksilver, when\n   poured upon a plane, divides itself into a great number of minute\n   spherules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spherulite', 'n.', 'A minute spherical crystalline body having a radiated\n   structure, observed in some vitreous volcanic rocks, as obsidian and\n   pearlstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spherulitic', 'a.', 'Of or pertaining to a spherulite; characterized by the\n   presence of spherulites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphery', 'a.', 'Round; spherical; starlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphery', 'a.', 'Of or pertaining to the spheres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphex', 'n.', 'Any one of numerous species of sand wasps of the genus Sphex\n   and allied genera. These wasps have the abdomen attached to the thorax\n   by a slender pedicel. See Illust. of Sand wasp, under Sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphigmometer', 'n.', 'See Sphygmometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphincter', 'n.', 'A muscle which surrounds, and by its contraction tends\n   to close, a natural opening; as, the sphincter of the bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphincter', 'a.', 'Of, pertaining to, or designating, a sphincter; as, a\n   sphincter muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphingid', 'n.', 'A sphinx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphingid', 'a.', 'Of or pertaining to a sphinx, or the family Sphingidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphinx', 'n.', 'In Egyptian art, an image of granite or porphyry, having a\n   human head, or the head of a ram or of a hawk, upon the wingless body\n   of a lion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphinx', 'n.', 'On Greek art and mythology, a she-monster, usually\n   represented as having the winged body of a lion, and the face and\n   breast of a young woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphinx', 'n.', 'Hence: A person of enigmatical character and purposes,\n   especially in politics and diplomacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphinx', 'n.', 'Any one of numerous species of large moths of the family\n   Sphingidae; -- called also hawk moth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphinx', 'n.', 'The Guinea, or sphinx, baboon (Cynocephalus sphinx).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphragide', 'n.', 'Lemnian earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphragistics', 'n.', 'The science of seals, their history, age,\n   distinctions, etc., esp. as verifying the age and genuiness of\n   documents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphrigosis', 'n.', 'A condition of vegetation in which there is too\n   abundant growth of the stem and leaves, accompanied by deficiency of\n   flowers and fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphygmic', 'a.', 'Of or pertaining to the pulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphygmogram', 'n.', 'A tracing, called a pulse tracing, consisting of a\n   series of curves corresponding with the beats of the heart, obtained by\n   the application of the sphygmograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphygmograph', 'n.', 'An instrument which, when applied over an artery,\n   indicates graphically the movements or character of the pulse. See\n   Sphygmogram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphygmographic', 'a.', 'Relating to, or produced by, a sphygmograph; as, a\n   sphygmographic tracing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphygmometer', 'n.', 'An instrument for measuring the strength of the pulse\n   beat; a sphygmograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphygmophone', 'n.', 'An electrical instrument for determining by the ear\n   the rhythm of the pulse of a person at a distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphygmoscope', 'n.', 'Same as Sphygmograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sphyraenoid', 'a.', 'Of or pertaining to the Sphyraenidae, a family of\n   marine fishes including the barracudas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spial', 'n.', 'A spy; a scout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicae', 'pl. ', 'of Spica');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spica', 'n.', 'A kind of bandage passing, by successive turns and crosses,\n   from an extremity to the trunk; -- so called from its resemblance to a\n   spike of a barley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spica', 'n.', 'A star of the first magnitude situated in the constellation\n   Virgo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicate', 'a.', 'Alt. of Spicated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicated', 'a.', 'Having the form of a spike, or ear; arranged in a spike\n   or spikes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiccato', 'a.', 'Detached; separated; -- a term indicating that every note\n   is to be performed in a distinct and pointed manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spice', 'n.', 'Species; kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spice', 'n.', 'A vegetable production of many kinds, fragrant or aromatic\n   and pungent to the taste, as pepper, cinnamon, nutmeg, mace, allspice,\n   ginger, cloves, etc., which are used in cookery and to flavor sauces,\n   pickles, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spice', 'n.', 'Figuratively, that which enriches or alters the quality of a\n   thing in a small degree, as spice alters the taste of food; that which\n   gives zest or pungency; a slight flavoring; a relish; hence, a small\n   quantity or admixture; a sprinkling; as, a spice of mischief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiced', 'imp. & p. p.', 'of Spice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicing', 'p. p. & vb. n.', 'of Spice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spice', 'v. t.', 'To season with spice, or as with spice; to mix aromatic\n   or pungent substances with; to flavor; to season; as, to spice wine; to\n   spice ones words with wit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spice', 'v. t.', 'To fill or impregnate with the odor of spices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spice', 'v. t.', 'To render nice or dainty; hence, to render scrupulous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicebush', 'n.', 'Spicewood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicenut', '', 'A small crisp cake, highly spiced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicer', 'n.', 'One who seasons with spice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicer', 'n.', 'One who deals in spice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicery', 'n.', 'Spices, in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicery', 'n.', 'A repository of spices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicewood', 'n.', 'An American shrub (Lindera Benzoin), the bark of which\n   has a spicy taste and odor; -- called also Benjamin, wild allspice, and\n   fever bush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiciferous', 'a.', 'Bearing ears, or spikes; spicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiciform', 'a.', 'Spike-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicily', 'adv.', 'In a spicy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiciness', 'n.', 'The quality or state of being spicy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spick', 'n.', 'A spike or nail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicknel', 'n.', 'An umbelliferous herb (Meum Athamanticum) having finely\n   divided leaves, common in Europe; -- called also baldmoney, mew, and\n   bearwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicose', 'a.', 'Having spikes, or ears, like corn spikes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicosity', 'n.', 'The state of having, or being full of, ears like corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicous', 'a.', 'See Spicose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiculae', 'pl. ', 'of Spicula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicula', 'n.', 'A little spike; a spikelet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicula', 'n.', 'A pointed fleshy appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicular', 'a.', 'Resembling a dart; having sharp points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiculate', 'a.', 'Covered with, or having, spicules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiculate', 'a.', 'Covered with minute spiculae, or pointed fleshy\n   appendages; divided into small spikelets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiculate', 'v. t.', 'To sharpen to a point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicule', 'n.', 'A minute, slender granule, or point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicule', 'n.', 'Same as Spicula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicule', 'n.', 'Any small calcareous or siliceous body found in the\n   tissues of various invertebrate animals, especially in sponges and in\n   most Alcyonaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiculiform', 'a.', 'Having the shape of a spicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiculigenous', 'a.', 'Producing or containing spicules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiculispongiae', 'n. pl.', 'A division of sponges including those which\n   have independent siliceous spicules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicula', 'pl. ', 'of Spiculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiculum', 'n.', 'Same as Spicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicy', 'superl.', 'Flavored with, or containing, spice or spices;\n   fragrant; aromatic; as, spicy breezes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicy', 'superl.', 'Producing, or abounding with, spices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spicy', 'superl.', 'Fig.: Piquant; racy; as, a spicy debate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spider', 'n.', 'Any one of numerous species of arachnids comprising the\n   order Araneina. Spiders have the mandibles converted into poison fangs,\n   or falcers. The abdomen is large and not segmented, with two or three\n   pairs of spinnerets near the end, by means of which they spin threads\n   of silk to form cocoons, or nests, to protect their eggs and young.\n   Many species spin also complex webs to entrap the insects upon which\n   they prey. The eyes are usually eight in number (rarely six), and are\n   situated on the back of the cephalothorax. See Illust. under Araneina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spider', 'n.', 'Any one of various other arachnids resembling the true\n   spiders, especially certain mites, as the red spider (see under Red).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spider', 'n.', 'An iron pan with a long handle, used as a kitchen utensil\n   in frying food. Originally, it had long legs, and was used over coals\n   on the hearth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spider', 'n.', 'A trevet to support pans or pots over a fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spider', 'n.', 'A skeleton, or frame, having radiating arms or members,\n   often connected by crosspieces; as, a casting forming the hub and\n   spokes to which the rim of a fly wheel or large gear is bolted; the\n   body of a piston head; a frame for strengthening a core or mold for a\n   casting, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spidered', 'a.', 'Infested by spiders; cobwebbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiderlike', 'a.', 'Like a spider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spider web', '', 'Alt. of Spiders web');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiders web', '', 'The silken web which is formed by most kinds of\n   spiders, particularly the web spun to entrap their prey. See Geometric\n   spider, Triangle spider, under Geometric, and Triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiderwort', 'n.', 'An American endogenous plant (Tradescantia Virginica),\n   with long linear leaves and ephemeral blue flowers. The name is\n   sometimes extended to other species of the same genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spied', '', 'imp. & p. p. of Spy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiegeleisen', 'n.', 'See Spiegel iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiegel iron', '', 'A fusible white cast iron containing a large amount of\n   carbon (from three and a half to six per cent) and some manganese. When\n   the manganese reaches twenty-five per cent and upwards it has a\n   granular structure, and constitutes the alloy ferro manganese, largely\n   used in the manufacture of Bessemer steel. Called also specular pig\n   iron, spiegel, and spiegeleisen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spight', 'n. & v.', 'Spite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spight', 'n.', 'A woodpecker. See Speight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spignel', 'n.', 'Same as Spickenel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spignet', 'n.', 'An aromatic plant of America. See Spikenard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spigot', 'n.', 'A pin or peg used to stop the vent in a cask; also, the\n   plug of a faucet or cock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spigurnel', 'n.', 'Formerly the title of the sealer of writs in chancery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spike', 'n.', 'A sort of very large nail; also, a piece of pointed iron set\n   with points upward or outward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spike', 'n.', 'Anything resembling such a nail in shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spike', 'n.', 'An ear of corn or grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spike', 'n.', 'A kind of flower cluster in which sessile flowers are\n   arranged on an unbranched elongated axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiked', 'imp. & p. p.', 'of Spike');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiking', 'p. pr. & vb. n.', 'of Spike');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spike', 'v. t.', 'To fasten with spikes, or long, large nails; as, to spike\n   down planks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spike', 'v. t.', 'To set or furnish with spikes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spike', 'v. t.', 'To fix on a spike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spike', 'v. t.', 'To stop the vent of (a gun or cannon) by driving a spike\n   nail, or the like into it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spike', 'n.', 'Spike lavender. See Lavender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spikebill', 'n.', 'The hooded merganser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spikebill', 'n.', 'The marbled godwit (Limosa fedoa).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiked', 'a.', 'Furnished or set with spikes, as corn; fastened with\n   spikes; stopped with spikes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spikefish', 'n.', 'See Sailfish (a)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spikelet', 'n.', 'A small or secondary spike; especially, one of the\n   ultimate parts of the in florescence of grasses. See Illust. of Quaking\n   grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spikenard', 'n.', 'An aromatic plant. In the United States it is the Aralia\n   racemosa, often called spignet, and used as a medicine. The spikenard\n   of the ancients is the Nardostachys Jatamansi, a native of the\n   Himalayan region. From its blackish roots a perfume for the hair is\n   still prepared in India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spikenard', 'n.', 'A fragrant essential oil, as that from the Nardostachys\n   Jatamansi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiketail', 'n.', 'The pintail duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiky', 'a.', 'Like a spike; spikelike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiky', 'a.', 'Having a sharp point, or sharp points; furnished or armed\n   with spikes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spile', 'n.', 'A small plug or wooden pin, used to stop a vent, as in a\n   cask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spile', 'n.', 'A small tube or spout inserted in a tree for conducting sap,\n   as from a sugar maple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spile', 'n.', 'A large stake driven into the ground as a support for some\n   superstructure; a pile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spile', 'v. t.', 'To supply with a spile or a spigot; to make a small vent\n   in, as a cask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spilikin', 'n.', 'One of a number of small pieces or pegs of wood, ivory,\n   bone, or other material, for playing a game, or for counting the score\n   in a game, as in cribbage. In the plural (spilikins');
  INSERT INTO words (word, word_type, description) VALUES  (    'spilikins', 'pl. ', 'of Spilikin\n   ), a game played with such pieces; pushpin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spill', 'n.', 'A bit of wood split off; a splinter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spill', 'n.', 'A slender piece of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spill', 'n.', 'A peg or pin for plugging a hole, as in a cask; a spile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spill', 'n.', 'A metallic rod or pin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spill', 'n.', 'A small roll of paper, or slip of wood, used as a\n   lamplighter, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spill', 'n.', 'One of the thick laths or poles driven horizontally ahead of\n   the main timbering in advancing a level in loose ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spill', 'n.', 'A little sum of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spilt', 'imp. & p. p.', 'of Spill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spilling', 'p. pr. & vb. n.', 'of Spill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spill', 'v. t.', 'To cover or decorate with slender pieces of wood, metal,\n   ivory, etc.; to inlay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spilled', 'imp. & p. p.', 'of Spill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spilt', '', 'of Spill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spilling', 'p. pr. & vb. n.', 'of Spill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spill', 'v. t.', 'To destroy; to kill; to put an end to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spill', 'v. t.', 'To mar; to injure; to deface; hence, to destroy by\n   misuse; to waste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spill', 'v. t.', 'To suffer to fall or run out of a vessel; to lose, or\n   suffer to be scattered; -- applied to fluids and to substances whose\n   particles are small and loose; as, to spill water from a pail; to spill\n   quicksilver from a vessel; to spill powder from a paper; to spill sand\n   or flour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spill', 'v. t.', 'To cause to flow out and be lost or wasted; to shed, or\n   suffer to be shed, as in battle or in manslaughter; as, a man spills\n   anothers blood, or his own blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spill', 'v. t.', 'To relieve a sail from the pressure of the wind, so that\n   it can be more easily reefed or furled, or to lessen the strain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spill', 'v. i.', 'To be destroyed, ruined, or wasted; to come to ruin; to\n   perish; to waste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spill', 'v. i.', 'To be shed; to run over; to fall out, and be lost or\n   wasted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiller', 'n.', 'One who, or that which, spills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiller', 'n.', 'A kind of fishing line with many hooks; a boulter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spillet fishing', '', 'Alt. of Spilliard fishing');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spilliard fishing', '', 'A system or method of fishing by means of a number\n   of hooks set on snoods all on one line; -- in North America, called\n   trawl fishing, bultow, or bultow fishing, and long-line fishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spillikin', 'n.', 'See Spilikin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spillway', 'n.', 'A sluiceway or passage for superfluous water in a\n   reservoir, to prevent too great pressure on the dam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spilt', '', 'imp. & p. p. of Spill. Spilled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spilter', 'n.', 'Any one of the small branches on a stags head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spilth', 'n.', 'Anything spilt, or freely poured out; slop; effusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spun', 'imp. & p. p.', 'of Spin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Span', 'imp.', 'of Spin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinning', 'p. pr. & vb. n.', 'of Spin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spin', 'v. t.', 'To draw out, and twist into threads, either by the hand or\n   machinery; as, to spin wool, cotton, or flax; to spin goats hair; to\n   produce by drawing out and twisting a fibrous material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spin', 'v. t.', 'To draw out tediously; to form by a slow process, or by\n   degrees; to extend to a great length; -- with out; as, to spin out\n   large volumes on a subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spin', 'v. t.', 'To protract; to spend by delays; as, to spin out the day\n   in idleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spin', 'v. t.', 'To cause to turn round rapidly; to whirl; to twirl; as, to\n   spin a top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spin', 'v. t.', 'To form (a web, a cocoon, silk, or the like) from threads\n   produced by the extrusion of a viscid, transparent liquid, which\n   hardens on coming into contact with the air; -- said of the spider, the\n   silkworm, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spin', 'v. t.', 'To shape, as malleable sheet metal, into a hollow form, by\n   bending or buckling it by pressing against it with a smooth hand tool\n   or roller while the metal revolves, as in a lathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spin', 'v. i.', 'To practice spinning; to work at drawing and twisting\n   threads; to make yarn or thread from fiber; as, the woman knows how to\n   spin; a machine or jenny spins with great exactness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spin', 'v. i.', 'To move round rapidly; to whirl; to revolve, as a top or a\n   spindle, about its axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spin', 'v. i.', 'To stream or issue in a thread or a small current or jet;\n   as, blood spinsfrom a vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spin', 'v. i.', 'To move swifty; as, to spin along the road in a carriage,\n   on a bicycle, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spin', 'n.', 'The act of spinning; as, the spin of a top; a spin a bicycle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spin', 'n.', 'Velocity of rotation about some specified axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spina bifida', '', 'A congenital malformation in which the spinal column is\n   cleft at its lower portion, and the membranes of the spinal cord\n   project as an elastic swelling from the gap thus formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinaceous', 'a.', 'Of, pertaining to, or resembling, the plant spinach, or\n   the family of plants to which it belongs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinach', 'n.', 'Alt. of Spinage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinage', 'n.', 'A common pot herb (Spinacia oleracea) belonging to the\n   Goosefoot family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinal', 'a.', 'Of, pertaining to, or in the region of, the backbone, or\n   vertebral column; rachidian; vertebral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinal', 'a.', 'Of or pertaining to a spine or spines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinate', 'a.', 'Bearing a spine; spiniform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindle', 'n.', 'The long, round, slender rod or pin in spinning wheels by\n   which the thread is twisted, and on which, when twisted, it is wound;\n   also, the pin on which the bobbin is held in a spinning machine, or in\n   the shuttle of a loom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindle', 'n.', 'A slender rod or pin on which anything turns; an axis; as,\n   the spindle of a vane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindle', 'n.', 'The shaft, mandrel, or arbor, in a machine tool, as a\n   lathe or drilling machine, etc., which causes the work to revolve, or\n   carries a tool or center, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindle', 'n.', 'The vertical rod on which the runner of a grinding mill\n   turns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindle', 'n.', 'A shaft or pipe on which a core of sand is formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindle', 'n.', 'The fusee of a watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindle', 'n.', 'A long and slender stalk resembling a spindle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindle', 'n.', 'A yarn measure containing, in cotton yarn, 15,120 yards;\n   in linen yarn, 14,400 yards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindle', 'n.', 'A solid generated by the revolution of a curved line about\n   its base or double ordinate or chord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindle', 'n.', 'Any marine univalve shell of the genus Rostellaria; --\n   called also spindle stromb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindle', 'n.', 'Any marine gastropod of the genus Fusus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindled', 'imp. & p. p.', 'of Spindle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindling', 'p. pr. & vb. n.', 'of Spindle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindle', 'v. i.', 'To shoot or grow into a long, slender stalk or body; to\n   become disproportionately tall and slender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindle-legged', 'a.', 'Having long, slender legs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindlelegs', 'n.', 'A spindleshanks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindle-shanked', 'a.', 'Having long, slender legs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindleshanks', 'n.', 'A person with slender shanks, or legs; -- used\n   humorously or in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindle-shaped', 'a.', 'Having the shape of a spindle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindle-shaped', 'a.', 'Thickest in the middle, and tapering to both ends;\n   fusiform; -- applied chiefly to roots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindletail', 'n.', 'The pintail duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindleworm', 'n.', 'The larva of a noctuid mmoth (Achatodes zeae) which\n   feeds inside the stalks of corn (maize), sometimes causing much damage.\n   It is smooth, with a black head and tail and a row of black dots across\n   each segment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spindling', 'a.', 'Long and slender, or disproportionately tall and\n   slender; as, a spindling tree; a spindling boy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spine', 'n.', 'A sharp appendage to any of a plant; a thorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spine', 'n.', 'A rigid and sharp projection upon any part of an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spine', 'n.', 'One of the rigid and undivided fin rays of a fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spine', 'n.', 'The backbone, or spinal column, of an animal; -- so called\n   from the projecting processes upon the vertebrae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spine', 'n.', 'Anything resembling the spine or backbone; a ridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spineback', 'n.', 'A fish having spines in, or in front of, the dorsal\n   fins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinebill', 'n.', 'Any species of Australian birds of the genus\n   Acanthorhynchus. They are related to the honey eaters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spined', 'a.', 'Furnished with spines; spiny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spine-finned', 'a.', 'Having fine supported by spinous fin rays; -- said of\n   certain fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinel', 'n.', 'Alt. of Spinelle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinelle', 'n.', 'A mineral occuring in octahedrons of great hardness and\n   various colors, as red, green, blue, brown, and black, the red variety\n   being the gem spinel ruby. It consist essentially of alumina and\n   magnesia, but commonly contains iron and sometimes also chromium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinel', 'n.', 'Bleached yarn in making the linen tape called inkle;\n   unwrought inkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spineless', 'a.', 'Having no spine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinescent', 'a.', 'Becoming hard and thorny; tapering gradually to a\n   rigid, leafless point; armed with spines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinet', 'n.', 'A keyed instrument of music resembling a harpsichord, but\n   smaller, with one string of brass or steel wire to each note, sounded\n   by means of leather or quill plectrums or jacks. It was formerly much\n   used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinet', 'n.', 'A spinny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinetail', 'n.', 'Any one or several species of swifts of the genus\n   Acanthylis, or Chaetura, and allied genera, in which the shafts of the\n   tail feathers terminate in rigid spines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinetail', 'n.', 'Any one of several species of South American and Central\n   American clamatorial birds belonging to Synallaxis and allied genera of\n   the family Dendrocolaptidae. They are allied to the ovenbirds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinetail', 'n.', 'The ruddy duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spine-tailed', 'a.', 'Having the tail quills ending in sharp, naked tips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spineted', 'a.', 'Slit; cleft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiniferous', 'a.', 'Producing spines; bearing thorns or spines; thorny;\n   spiny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiniform', 'a.', 'Shaped like a spine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinigerous', 'a.', 'Bearing a spine or spines; thorn-bearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spininess', 'n.', 'Quality of being spiny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinii-spirulate', 'a.', 'Having spines arranged spirally. See Spicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spink', 'n.', 'The chaffinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinnaker', 'n.', 'A large triangular sail set upon a boom, -- used when\n   running before the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinner', 'n.', 'One who, or that which, spins one skilled in spinning; a\n   spinning machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinner', 'n.', 'A spider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinner', 'n.', 'A goatsucker; -- so called from the peculiar noise it\n   makes when darting through the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinner', 'n.', 'A spinneret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinneret', 'n.', 'One of the special jointed organs situated on the under\n   side, and near the end, of the abdomen of spiders, by means of which\n   they spin their webs. Most spiders have three pairs of spinnerets, but\n   some have only two pairs. The ordinary silk line of the spider is\n   composed of numerous smaller lines jointed after issuing from the\n   spinnerets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinnerule', 'n.', 'One of the numerous small spinning tubes on the\n   spinnerets of spiders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinneys', 'pl. ', 'of Spinney');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinney', 'n.', 'Same as Spinny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinning', '', 'a. & n. from Spin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinnies', 'pl. ', 'of Spinny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinny', 'n.', 'A small thicket or grove with undergrowth; a clump of\n   trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinny', 'a.', 'Thin and long; slim; slender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinose', 'a.', 'Full of spines; armed with thorns; thorny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinosity', 'n.', 'The quality or state of being spiny or thorny;\n   spininess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinous', 'a.', 'Spinose; thorny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinous', 'a.', 'Having the form of a spine or thorn; spinelike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinozism', 'n.', 'The form of Pantheism taught by Benedict Spinoza, that\n   there is but one substance, or infinite essence, in the universe, of\n   which the so-called material and spiritual beings and phenomena are\n   only modes, and that one this one substance is God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinozist', 'n.', 'A believer in Spinozism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinster', 'n.', 'A woman who spins, or whose occupation is to spin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinster', 'n.', 'A man who spins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinster', 'n.', 'An unmarried or single woman; -- used in legal\n   proceedings as a title, or addition to the surname.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinster', 'n.', 'A woman of evil life and character; -- so called from\n   being forced to spin in a house of correction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinstress', 'n.', 'A woman who spins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinstry', 'n.', 'The business of one who spins; spinning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinule', 'n.', 'A minute spine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinulescent', 'a.', 'Having small spines; somewhat thorny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinulose', 'a.', 'Alt. of Spinulous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spinulous', 'a.', 'Covered with small spines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiny', 'a.', 'Full of spines; thorny; as, a spiny tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiny', 'a.', 'Like a spine in shape; slender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiny', 'a.', 'Fig.: Abounding with difficulties or annoyances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiny', 'n.', 'See Spinny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiodea', 'n. pl.', 'An extensive division of marine Annelida, including\n   those that are without oral tentacles or cirri, and have the gills,\n   when present, mostly arranged along the sides of the body. They\n   generally live in burrows or tubes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirable', 'a.', 'Capable of being breathed; respirable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiracle', 'n.', 'The nostril, or one of the nostrils, of whales,\n   porpoises, and allied animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiracle', 'n.', 'One of the external openings communicating with the air\n   tubes or tracheae of insects, myriapods, and arachnids. They are\n   variable in number, and are usually situated on the sides of the thorax\n   and abdomen, a pair to a segment. These openings are usually\n   elliptical, and capable of being closed. See Illust. under Coleoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiracle', 'n.', 'A tubular orifice communicating with the gill cavity of\n   certain ganoid and all elasmobranch fishes. It is the modified first\n   gill cleft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiracle', 'n.', 'Any small aperture or vent for air or other fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiracular', 'a.', 'Of or pertaining to a spiracle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiraea', 'n.', 'A genus of shrubs or perennial herbs including the\n   meadowsweet and the hardhack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiraeic', 'a.', 'Of, pertaining to, or derived from, the meadowsweet\n   (Spiraea); formerly, designating an acid which is now called salicylic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiral', 'a.', 'Winding or circling round a center or pole and gradually\n   receding from it; as, the spiral curve of a watch spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiral', 'a.', 'Winding round a cylinder or imaginary axis, and at the same\n   time rising or advancing forward; winding like the thread of a screw;\n   helical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiral', 'a.', 'Of or pertaining to a spiral; like a spiral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiral', 'a.', 'A plane curve, not reentrant, described by a point, called\n   the generatrix, moving along a straight line according to a\n   mathematical law, while the line is revolving about a fixed point\n   called the pole. Cf. Helix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiral', 'a.', 'Anything which has a spiral form, as a spiral shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirality', 'n.', 'The quality or states of being spiral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirally', 'adv.', 'In a spiral form, manner, or direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiralozooid', 'n.', 'One of the special defensive zooids of certain\n   hydroids. They have the form of long, slender tentacles, and bear lasso\n   cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirant', 'n.', 'A term used differently by different authorities; -- by\n   some as equivalent to fricative, -- that is, as including all the\n   continuous consonants, except the nasals m, n, ng; with the further\n   exception, by others, of the liquids r, l, and the semivowels w, y; by\n   others limited to f, v, th surd and sonant, and the sound of German ch,\n   -- thus excluding the sibilants, as well as the nasals, liquids, and\n   semivowels. See Guide to Pronunciation, // 197-208.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiranthy', 'n.', 'The occasional twisted growth of the parts of a flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiration', 'n.', 'The act of breathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spire', 'v. i.', 'To breathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spire', 'n.', 'A slender stalk or blade in vegetation; as, a spire grass or\n   of wheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spire', 'n.', 'A tapering body that shoots up or out to a point in a\n   conical or pyramidal form. Specifically (Arch.), the roof of a tower\n   when of a pyramidal form and high in proportion to its width; also, the\n   pyramidal or aspiring termination of a tower which can not be said to\n   have a roof, such as that of Strasburg cathedral; the tapering part of\n   a steeple, or the steeple itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spire', 'n.', 'A tube or fuse for communicating fire to the chargen in\n   blasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spire', 'n.', 'The top, or uppermost point, of anything; the summit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spired', 'imp. & p. p.', 'of Spire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiring', 'p. pr. & vb. n.', 'of Spire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spire', 'v. i.', 'To shoot forth, or up in, or as if in, a spire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spire', 'n.', 'A spiral; a curl; a whorl; a twist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spire', 'n.', 'The part of a spiral generated in one revolution of the\n   straight line about the pole. See Spiral, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spired', 'a.', 'Having a spire; being in the form of a spire; as, a spired\n   steeple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiricle', 'n.', 'One of certain minute coiled threads in the coating of\n   some seeds. When moistened these threads protrude in great numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirifer', 'n.', 'Any one of numerous species of fossil brachipods of the\n   genus Spirifer, or Delthyris, and allied genera, in which the long\n   calcareous supports of the arms form a large spiral, or helix, on each\n   side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirillum', 'n.', 'A genus of common motile microorganisms (Spirobacteria)\n   having the form of spiral-shaped filaments. One species is said to be\n   the cause of relapsing fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiring', 'a.', 'Shooting up in a spire or spires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirit', 'n.', 'Air set in motion by breathing; breath; hence, sometimes,\n   life itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirit', 'n.', 'A rough breathing; an aspirate, as the letter h; also, a\n   mark to denote aspiration; a breathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirit', 'n.', 'Life, or living substance, considered independently of\n   corporeal existence; an intelligence conceived of apart from any\n   physical organization or embodiment; vital essence, force, or energy,\n   as distinct from matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirit', 'n.', 'The intelligent, immaterial and immortal part of man; the\n   soul, in distinction from the body in which it resides; the agent or\n   subject of vital and spiritual functions, whether spiritual or\n   material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirit', 'n.', 'Specifically, a disembodied soul; the human soul after it\n   has left the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirit', 'n.', 'Any supernatural being, good or bad; an apparition; a\n   specter; a ghost; also, sometimes, a sprite,; a fairy; an elf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirit', 'n.', 'Energy, vivacity, ardor, enthusiasm, courage, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirit', 'n.', 'One who is vivacious or lively; one who evinces great\n   activity or peculiar characteristics of mind or temper; as, a ruling\n   spirit; a schismatic spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirit', 'n.', 'Temper or disposition of mind; mental condition or\n   disposition; intellectual or moral state; -- often in the plural; as,\n   to be cheerful, or in good spirits; to be downhearted, or in bad\n   spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirit', 'n.', 'Intent; real meaning; -- opposed to the letter, or to\n   formal statement; also, characteristic quality, especially such as is\n   derived from the individual genius or the personal character; as, the\n   spirit of an enterprise, of a document, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirit', 'n.', 'Tenuous, volatile, airy, or vapory substance, possessed of\n   active qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirit', 'n.', 'Any liquid produced by distillation; especially, alcohol,\n   the spirits, or spirit, of wine (it having been first distilled from\n   wine): -- often in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirit', 'n.', 'Rum, whisky, brandy, gin, and other distilled liquors\n   having much alcohol, in distinction from wine and malt liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirit', 'n.', 'A solution in alcohol of a volatile principle. Cf.\n   Tincture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirit', 'n.', 'Any one of the four substances, sulphur, sal ammoniac,\n   quicksilver, or arsenic (or, according to some, orpiment).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirit', 'n.', 'Stannic chloride. See under Stannic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirited', 'imp. & p. p.', 'of Spirit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiriting', 'p. pr. & vb. n.', 'of Spirit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirit', 'v. t.', 'To animate with vigor; to excite; to encourage; to\n   inspirit; as, civil dissensions often spirit the ambition of private\n   men; -- sometimes followed by up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirit', 'v. t.', 'To convey rapidly and secretly, or mysteriously, as if\n   by the agency of a spirit; to kidnap; -- often with away, or off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritally', 'adv.', 'By means of the breath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirited', 'a.', 'Animated or possessed by a spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirited', 'a.', 'Animated; full of life or vigor; lively; full of spirit\n   or fire; as, a spirited oration; a spirited answer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritful', 'a.', 'Full of spirit; spirited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritism', 'n.', 'Spiritualsm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritist', 'n.', 'A spiritualist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritless', 'a.', 'Destitute of spirit; wanting animation; wanting\n   cheerfulness; dejected; depressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritless', 'a.', 'Destitute of vigor; wanting life, courage, or fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritless', 'a.', 'Having no breath; extinct; dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritoso', 'a. & adv.', 'Spirited; spiritedly; -- a direction to perform a\n   passage in an animated, lively manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritous', 'a.', 'Like spirit; refined; defecated; pure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritous', 'a.', 'Ardent; active.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritousness', 'n.', 'Quality of being spiritous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritual', 'a.', 'Consisting of spirit; not material; incorporeal; as, a\n   spiritual substance or being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritual', 'a.', 'Of or pertaining to the intellectual and higher\n   endowments of the mind; mental; intellectual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritual', 'a.', 'Of or pertaining to the moral feelings or states of the\n   soul, as distinguished from the external actions; reaching and\n   affecting the spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritual', 'a.', 'Of or pertaining to the soul or its affections as\n   influenced by the Spirit; controlled and inspired by the divine Spirit;\n   proceeding from the Holy Spirit; pure; holy; divine; heavenly-minded;\n   -- opposed to carnal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritual', 'a.', 'Not lay or temporal; relating to sacred things;\n   ecclesiastical; as, the spiritual functions of the clergy; lords\n   spiritual and temporal; a spiritual corporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritual', 'n.', 'A spiritual function, office, or affair. See\n   Spirituality, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritualism', 'n.', 'The quality or state of being spiritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritualism', 'n.', 'The doctrine, in opposition to the materialists, that\n   all which exists is spirit, or soul -- that what is called the external\n   world is either a succession of notions impressed on the mind by the\n   Deity, as maintained by Berkeley, or else the mere educt of the mind\n   itself, as taught by Fichte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritualism', 'n.', 'A belief that departed spirits hold intercourse with\n   mortals by means of physical phenomena, as by rappng, or during\n   abnormal mental states, as in trances, or the like, commonly manifested\n   through a person of special susceptibility, called a medium; spiritism;\n   the doctrines and practices of spiritualists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritualist', 'n.', 'One who professes a regard for spiritual things only;\n   one whose employment is of a spiritual character; an ecclesiastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritualist', 'n.', 'One who maintains the doctrine of spiritualism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritualist', 'n.', 'One who believes in direct intercourse with departed\n   spirits, through the agency of persons commonly called mediums, by\n   means of physical phenomena; one who attempts to maintain such\n   intercourse; a spiritist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritualist', 'a.', 'Spiritualistic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritualistic', 'a.', 'Relating to, or connected with, spiritualism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritualities', 'pl. ', 'of Spirituality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirituality', 'n.', 'The quality or state of being spiritual;\n   incorporeality; heavenly-mindedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirituality', 'n.', 'That which belongs to the church, or to a person as\n   an ecclesiastic, or to religion, as distinct from temporalities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirituality', 'n.', 'An ecclesiastical body; the whole body of the clergy,\n   as distinct from, or opposed to, the temporality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritualization', 'n.', 'The act of spiritualizing, or the state of being\n   spiritualized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritualized', 'imp. & p. p.', 'of Spiritualize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritualizing', 'p. pr. & vb. n.', 'of Spiritualize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritualize', 'v. t.', 'To refine intellectiually or morally; to purify\n   from the corrupting influence of the world; to give a spiritual\n   character or tendency to; as, to spiritualize soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritualize', 'v. t.', 'To give a spiritual meaning to; to take in a\n   spiritual sense; -- opposed to literalize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritualize', 'v. t.', 'To extract spirit from; also, to convert into, or\n   impregnate with, spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritualizer', 'n.', 'One who spiritualizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritually', 'adv.', 'In a spiritual manner; with purity of spirit; like a\n   spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritual-minded', 'a.', 'Having the mind set on spiritual things, or\n   filled with holy desires and affections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritualness', 'n.', 'The quality or state of being spiritual or\n   spiritual-minded; spirituality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritualty', 'n.', 'An ecclesiastical body; a spirituality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiritielle', 'a.', 'Of the nature, or having the appearance, of a spirit;\n   pure; refined; ethereal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirituosity', 'n.', 'The quality or state of being spirituous;\n   spirituousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirituous', 'a.', 'Having the quality of spirit; tenuous in substance, and\n   having active powers or properties; ethereal; immaterial; spiritual;\n   pure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirituous', 'a.', 'Containing, or of the nature of, alcoholic (esp.\n   distilled) spirit; consisting of refined spirit; alcoholic; ardent; as,\n   spirituous liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirituous', 'a.', 'Lively; gay; vivid; airy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirituousness', 'n.', 'The quality or state of being spirituous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirketing', 'n.', 'The planking from the waterways up to the port sills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirling', 'n.', 'Sparling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirobacteria', 'n. pl.', 'See the Note under Microbacteria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirochaeta', 'n.', 'Alt. of Spirochaete');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirochaete', 'n.', 'A genus of Spirobacteria similar to Spirillum, but\n   distinguished by its motility. One species, the Spirochaete Obermeyeri,\n   is supposed to be the cause of relapsing fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirograph', 'n.', 'An instrument for recording the respiratory movements,\n   as the sphygmograph does those of the pulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirometer', 'n.', 'An instrument for measuring the vital capacity of the\n   lungs, or the volume of air which can be expelled from the chest after\n   the deepest possible inspiration. Cf. Pneumatometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirometry', 'n.', 'The act or process of measuring the chest capacity by\n   means of a spirometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiroscope', 'n.', 'A wet meter used to determine the breathing capacity of\n   the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiroylic', 'a.', 'Alt. of Spiroylous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiroylous', 'a.', 'Pertaining to, or designating, a substance now called\n   salicylal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirt', 'v. & n.', 'Same as Spurt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirtle', 'v. t.', 'To spirt in a scattering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirula', 'n.', 'A genus of cephalopods having a multilocular, internal,\n   siphunculated shell in the form of a flat spiral, the coils of which\n   are not in contact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spirulate', 'n.', 'Having the color spots, or structural parts, arranged\n   spirally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiry', 'a.', 'Of a spiral form; wreathed; curled; serpentine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiry', 'a.', 'Of or pertaining to a spire; like a spire, tall, slender,\n   and tapering; abounding in spires; as, spiry turrets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiss', 'a.', 'Thick; crowded; compact; dense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spissated', 'a.', 'Rendered dense or compact, as by evaporation;\n   inspissated; thickened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spissitude', 'n.', 'The quality or state of being spissated; as, the\n   spissitude of coagulated blood, or of any coagulum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spit', 'n.', 'A long, slender, pointed rod, usually of iron, for holding\n   meat while roasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spit', 'n.', 'A small point of land running into the sea, or a long, narrow\n   shoal extending from the shore into the sea; as, a spit of sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spit', 'n.', 'The depth to which a spade goes in digging; a spade; a\n   spadeful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitted', 'imp. & p. p.', 'of Spit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitting', 'p. pr. & vb. n.', 'of Spit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spit', 'n.', 'To thrust a spit through; to fix upon a spit; hence, to\n   thrust through or impale; as, to spit a loin of veal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spit', 'n.', 'To spade; to dig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spit', 'v. i.', 'To attend to a spit; to use a spit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spit', 'imp. & p. p.', 'of Spit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spat', '', 'of Spit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitting', 'p. pr. & vb. n.', 'of Spit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spit', 'n.', 'To eject from the mouth; to throw out, as saliva or other\n   matter, from the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spit', 'n.', 'To eject; to throw out; to belch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spit', 'n.', 'The secretion formed by the glands of the mouth; spitle;\n   saliva; sputum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spit', 'v. i.', 'To throw out saliva from the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spit', 'v. i.', 'To rain or snow slightly, or with sprinkles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spital', 'n.', 'A hospital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitalhouse', 'n.', 'A hospital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitball', 'n.', 'Paper chewed, and rolled into a ball, to be thrown as a\n   missile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitbox', 'n.', 'A vessel to receive spittle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitchcock', 'v. t.', 'To split (as an eel) lengthwise, and broil it, or\n   fry it in hot fat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitchcock', 'n.', 'An eel split and broiled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitchcocked', 'a.', 'Broiled or fried after being split lengthwise; --\n   said of eels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spit curl', '', 'A little lock of hair, plastered in a spiral form on the\n   temple or forehead with spittle, or other adhesive substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spite', 'n.', 'Ill-will or hatred toward another, accompanied with the\n   disposition to irritate, annoy, or thwart; petty malice; grudge;\n   rancor; despite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spite', 'n.', 'Vexation; chargrin; mortification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spited', 'imp. & p. p.', 'of Spite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiting', 'p. pr. & vb. n.', 'of Spite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spite', 'v. t.', 'To be angry at; to hate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spite', 'v. t.', 'To treat maliciously; to try to injure or thwart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spite', 'v. t.', 'To fill with spite; to offend; to vex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spiteful', 'a.', 'Filled with, or showing, spite; having a desire to vex,\n   annoy, or injure; malignant; malicious; as, a spiteful person or act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitfire', 'n.', 'A violent, irascible, or passionate person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitfuls', 'pl. ', 'of Spitful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitful', 'n.', 'A spadeful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitous', 'a.', 'Having spite; spiteful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitously', 'adv.', 'Spitefully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitscocked', 'a.', 'Spitchcocked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitted', 'a.', 'Put upon a spit; pierced as if by a spit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitted', 'a.', 'Shot out long; -- said of antlers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitted', '', 'p. p. of Spit, v. i., to eject, to spit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitter', 'n.', 'One who ejects saliva from the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitter', 'n.', 'One who puts meat on a spit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitter', 'n.', 'A young deer whose antlers begin to shoot or become sharp;\n   a brocket, or pricket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spittle', 'n.', 'See Spital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spittle', 'v. t.', 'To dig or stir with a small spade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spittle', 'n.', 'A small sort of spade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spittle', 'n.', 'The thick, moist matter which is secreted by the salivary\n   glands; saliva; spit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spittly', 'a.', 'Like spittle; slimy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spittoon', 'n.', 'A spitbox; a cuspidor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spit-venom', 'n.', 'Poison spittle; poison ejected from the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitz dog', '', 'A breed of dogs having erect ears and long silky hair,\n   usually white; -- called also Pomeranian dog, and louploup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spitzenburgh', 'n.', 'A kind of red and yellow apple, of medium size and\n   spicy flavor. It originated at Newtown, on Long Island.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Splanchnapophyses', 'pl. ', 'of Splanchnapophysis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Splanchnapophysis', 'n.', 'Any element of the skeleton in relation with the\n   alimentary canal, as the jaws and hyoidean apparatus.');
