  INSERT INTO words (word, word_type, description) VALUES  (    'Spleen', 'n.', 'A peculiar glandlike but ductless organ found near the\n   stomach or intestine of most vertebrates and connected with the\n   vascular system; the milt. Its exact function in not known.');
