  INSERT INTO words (word, word_type, description) VALUES  (    'Spleen', 'n.', 'A fit of anger; choler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spleen', 'n.', 'A sudden motion or action; a fit; a freak; a whim.');
