  INSERT INTO words (word, word_type, description) VALUES  (    'Spoilable', 'a.', 'Capable of being spoiled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoiler', 'n.', 'One who spoils; a plunderer; a pillager; a robber; a\n   despoiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoiler', 'n.', 'One who corrupts, mars, or renders useless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoilfive', 'n.', 'A certain game at cards in which, if no player wins\n   three of the five tricks possible on any deal, the game is said to be\n   spoiled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoilful', 'a.', 'Wasteful; rapacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoilsmen', 'pl. ', 'of Spoilsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoilsman', 'n.', 'One who serves a cause or a party for a share of the\n   spoils; in United States politics, one who makes or recognizes a demand\n   for public office on the ground of partisan service; also, one who\n   sanctions such a policy in appointments to the public service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoilsmonger', 'n.', 'One who promises or distributes public offices and\n   their emoluments as the price of services to a party or its leaders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoke', '', 'imp. of Speak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoke', 'n.', 'The radius or ray of a wheel; one of the small bars which\n   are inserted in the hub, or nave, and which serve to support the rim or\n   felly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoke', 'n.', 'A projecting handle of a steering wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoke', 'n.', 'A rung, or round, of a ladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoke', 'n.', 'A contrivance for fastening the wheel of a vehicle, to\n   prevent it from turning in going down a hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoked', 'imp. & p. p.', 'of Spoke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoking', 'p. pr. & vb. n.', 'of Spoke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoke', 'v. t.', 'To furnish with spokes, as a wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoken', 'a.', 'Uttered in speech; delivered by word of mouth; oral; as, a\n   spoken narrative; the spoken word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoken', 'a.', 'Characterized by a certain manner or style in speaking; --\n   often in composition; as, a pleasant-spoken man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spokeshave', 'n.', 'A kind of drawing knife or planing tool for dressing\n   the spokes of wheels, the shells of blocks, and other curved work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spokesmen', 'pl. ', 'of Spokesman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spokesman', 'n.', 'One who speaks for another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoliated', 'imp. & p. p.', 'of Spoliate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoliating', 'p. pr. & vb. n.', 'of Spoliate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoliate', 'v. t.', 'To plunder; to pillage; to despoil; to rob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoliation', 'v. t.', 'The act of plundering; robbery; deprivation;\n   despoliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoliation', 'v. t.', 'Robbery or plunder in war; especially, the\n   authorized act or practice of plundering neutrals at sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoliation', 'v. t.', 'The act of an incumbent in taking the fruits of his\n   benefice without right, but under a pretended title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoliation', 'v. t.', 'A process for possession of a church in a spiritual\n   court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoliation', 'v. t.', 'Injury done to a document.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoliative', 'a.', 'Serving to take away, diminish, or rob; esp. (Med.),\n   serving to diminish sensibily the amount of blood in the body; as,\n   spoliative bloodletting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoliator', 'n.', 'One who spoliates; a spoiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoliatory', 'a.', 'Tending to spoil; destructive; spoliative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spondaic', 'a.', 'Alt. of Spondaical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spondaical', 'a.', 'Or of pertaining to a spondee; consisting of spondees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spondaical', 'a.', 'Containing spondees in excess; marked by spondees; as,\n   a spondaic hexameter, i. e., one which has a spondee instead of a\n   dactyl in the fifth foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spondee', 'n.', 'A poetic foot of two long syllables, as in the Latin word\n   leges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spondulics', 'n.', 'Money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spondyl', 'n.', 'Alt. of Spondyle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spondyle', 'n.', 'A joint of the backbone; a vertebra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spong', 'n.', 'An irregular, narrow, projecting part of a field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponge', 'n.', 'Any one of numerous species of Spongiae, or Porifera. See\n   Illust. and Note under Spongiae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponge', 'n.', 'The elastic fibrous skeleton of many species of horny\n   Spongiae (keratosa), used for many purposes, especially the varieties\n   of the genus Spongia. The most valuable sponges are found in the\n   Mediterranean and the Red Sea, and on the coasts of Florida and the\n   West Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponge', 'n.', 'One who lives upon others; a pertinaceous and indolent\n   dependent; a parasite; a sponger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponge', 'n.', 'Any spongelike substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponge', 'n.', 'Dough before it is kneaded and formed into loaves, and\n   after it is converted into a light, spongy mass by the agency of the\n   yeast or leaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponge', 'n.', 'Iron from the puddling furnace, in a pasty condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponge', 'n.', 'Iron ore, in masses, reduced but not melted or worked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponge', 'n.', 'A mop for cleaning the bore of a cannon after a discharge.\n   It consists of a cylinder of wood, covered with sheepskin with the wool\n   on, or cloth with a heavy looped nap, and having a handle, or staff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponge', 'n.', 'The extremity, or point, of a horseshoe, answering to the\n   heel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponged', 'imp. & p. p.', 'of Sponge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponging', 'p. pr. & vb. n.', 'of Sponge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponge', 'v. t.', 'To cleanse or wipe with a sponge; as, to sponge a slate\n   or a cannon; to wet with a sponge; as, to sponge cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponge', 'v. t.', 'To wipe out with a sponge, as letters or writing; to\n   efface; to destroy all trace of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponge', 'v. t.', 'Fig.: To deprive of something by imposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponge', 'v. t.', 'Fig.: To get by imposition or mean arts without cost;\n   as, to sponge a breakfast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponge', 'v. i.', 'To suck in, or imbile, as a sponge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponge', 'v. i.', 'Fig.: To gain by mean arts, by intrusion, or hanging on;\n   as, an idler sponges on his neighbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponge', 'v. i.', 'To be converted, as dough, into a light, spongy mass by\n   the agency of yeast, or leaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spongelet', 'n.', 'See Spongiole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spongeous', 'a.', 'Resembling sponge; having the nature or qualities of\n   sponge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponger', 'n.', 'One who sponges, or uses a sponge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponger', 'n.', 'One employed in gathering sponges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponger', 'n.', 'Fig.: A parasitical dependent; a hanger-on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spongiae', 'n. pl.', 'The grand division of the animal kingdom which\n   includes the sponges; -- called also Spongida, Spongiaria, Spongiozoa,\n   and Porifera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spongida', 'n. pl.', 'Spongiae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spongiform', 'a.', 'Resembling a sponge; soft and porous; porous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spongilla', 'n.', 'A genus of siliceous spongea found in fresh water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spongin', 'n.', 'The chemical basis of sponge tissue, a nitrogenous,\n   hornlike substance which on decomposition with sulphuric acid yields\n   leucin and glycocoll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponginess', 'n.', 'The quality or state of being spongy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponging', '', 'a. & n. from Sponge, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spongiole', 'n.', 'A supposed spongelike expansion of the tip of a rootlet\n   for absorbing water; -- called also spongelet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spongiolite', 'n.', 'One of the microsporic siliceous spicules which occur\n   abundantly in the texture of sponges, and are sometimes found fossil,\n   as in flints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spongiopilin', 'n.', 'A kind of cloth interwoven with small pieces of\n   sponge and rendered waterproof on one side by a covering of rubber.\n   When moistend with hot water it is used as a poultice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spongiose', 'a.', 'Alt. of Spongious');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spongious', 'a.', 'Somewhat spongy; spongelike; full of small cavities like\n   sponge; as, spongious bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spongiozoa', 'n. pl.', 'See Sponglae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spongoblast', 'n.', 'One of the cells which, in sponges, secrete the\n   spongin, or the material of the horny fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spongoid', 'a.', 'Resembling sponge; like sponge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spongy', 'a.', 'Soft, and full of cavities; of an open, loose, pliable\n   texture; as, a spongy excrescence; spongy earth; spongy cake; spongy\n   bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spongy', 'a.', 'Wet; drenched; soaked and soft, like sponge; rainy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spongy', 'a.', 'Having the quality of imbibing fluids, like a sponge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponk', 'n.', 'See Spunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponsal', 'a.', 'Relating to marriage, or to a spouse; spousal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponsible', 'a.', 'responsible; worthy of credit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponsion', 'n.', 'The act of becoming surety for another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponsion', 'n.', 'An act or engagement on behalf of a state, by an agent\n   not specially authorized for the purpose, or by one who exceeds the\n   limits of authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponsional', 'a.', 'Of or pertaining to a pledge or agreement; responsible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponson', 'n.', 'One of the triangular platforms in front of, and abaft,\n   the paddle boxes of a steamboat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponson', 'n.', 'One of the slanting supports under the guards of a\n   steamboat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponson', 'n.', 'One of the armored projections fitted with gun ports, used\n   on modern war vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponsor', 'n.', 'One who binds himself to answer for another, and is\n   responsible for his default; a surety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponsor', 'n.', 'One who at the baptism of an infant professore the\n   christian faith in its name, and guarantees its religious education; a\n   godfather or godmother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spnsorial', 'a.', 'Pertaining to a sponsor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sponsorship', 'n.', 'State of being a sponsor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spontaneities', 'pl. ', 'of Spontaneity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spontaneity', 'n.', 'The quality or state of being spontaneous, or acting\n   from native feeling, proneness, or temperament, without constraint or\n   external force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spontaneity', 'n.', 'The tendency to undergo change, characteristic of both\n   animal and vegetable organisms, and not restrained or cheked by the\n   environment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spontaneity', 'n.', 'The tendency to activity of muscular tissue, including\n   the voluntary muscles, when in a state of healthful vigor and\n   refreshment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spontaneous', 'a.', 'Proceding from natural feeling, temperament, or\n   disposition, or from a native internal proneness, readiness, or\n   tendency, without constraint; as, a spontaneous gift or proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spontaneous', 'a.', 'Proceeding from, or acting by, internal impulse,\n   energy, or natural law, without external force; as, spontaneous motion;\n   spontaneous growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spontaneous', 'a.', 'Produced without being planted, or without human\n   labor; as, a spontaneous growth of wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spontoon', 'n.', 'A kind of half-pike, or halberd, formerly borne by\n   inferior officers of the British infantry, and used in giving signals\n   to the soldiers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spook', 'n.', 'A spirit; a ghost; an apparition; a hobgoblin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spook', 'n.', 'The chimaera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spool', 'n.', 'A piece of cane or red with a knot at each end, or a hollow\n   cylinder of wood with a ridge at each end, used to wind thread or yarn\n   upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spooled', 'imp. & p. p.', 'of Spool');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spooling', 'p. pr. & vb. n.', 'of Spool');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spool', 'v. t.', 'To wind on a spool or spools.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spooler', 'n.', 'One who, or that which, spools.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoom', 'v. i.', 'To be driven steadily and swiftly, as before a strong\n   wind; to be driven before the wind without any sail, or with only a\n   part of the sails spread; to scud under bare poles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoon', 'v. i.', 'See Spoom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoon', 'n.', 'An implement consisting of a small bowl (usually a shallow\n   oval) with a handle, used especially in preparing or eating food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoon', 'n.', 'Anything which resembles a spoon in shape; esp. (Fishing), a\n   spoon bait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoon', 'n.', 'Fig.: A simpleton; a spooney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoon', 'v. t.', 'To take up in, or as in, a spoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoon', 'v. i.', 'To act with demonstrative or foolish fondness, as one in\n   love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoonbill', 'n.', 'Any one of several species of wading birds of the genera\n   Ajaja and Platalea, and allied genera, in which the long bill is\n   broadly expanded and flattened at the tip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoonbill', 'n.', 'The shoveler. See Shoveler, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoonbill', 'n.', 'The ruddy duck. See under Ruddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoonbill', 'n.', 'The paddlefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoon-billed', 'a.', 'Having the bill expanded and spatulate at the end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoondrift', 'n.', 'Spray blown from the tops waves during a gale at sea;\n   also, snow driven in the wind at sea; -- written also spindrift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spooney', 'a.', 'Weak-minded; demonstratively fond; as, spooney lovers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spooneye', 'pl. ', 'of Spooney');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spooney', 'n.', 'A weak-minded or silly person; one who is foolishly fond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoonfuls', 'pl. ', 'of Spoonful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoonful', 'n.', 'The quantity which a spoon contains, or is able to\n   contain; as, a teaspoonful; a tablespoonful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoonful', 'n.', 'Hence, a small quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoonily', 'adv.', 'In a spoony manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoon-meat', 'n.', 'Food that is, or must be, taken with a spoon; liquid\n   food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoonwood', 'n.', 'The mountain laurel (Kalmia latifolia).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoonworm', 'n.', 'A gephyrean worm of the genus Thalassema, having a\n   spoonlike probiscis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoonwort', 'n.', 'Scurvy grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoony', 'a. & n.', 'Same as Spooney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoor', 'n.', 'The track or trail of any wild animal; as, the spoor of an\n   elephant; -- used originally by travelers in South Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoor', 'v. i.', 'To follow a spoor or trail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporades', 'n. pl.', 'Stars not included in any constellation; -- called\n   also informed, or unformed, stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporadial', 'a.', 'Sporadic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporadic', 'a.', 'Occuring singly, or apart from other things of the same\n   kind, or in scattered instances; separate; single; as, a sporadic\n   fireball; a sporadic case of disease; a sporadic example of a flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporadical', 'a.', 'Sporadic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporadically', 'adv.', 'In a sporadic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporangiophore', 'n.', 'The axis or receptacle in certain ferns (as\n   Trichomanes), which bears the sporangia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporangia', 'pl. ', 'of Sporangium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporangium', 'n.', 'A spore case in the cryptogamous plants, as in ferns,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spore', 'n.', 'One of the minute grains in flowerless plants, which are\n   analogous to seeds, as serving to reproduce the species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spore', 'n.', 'An embryo sac or embryonal vesicle in the ovules of\n   flowering plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spore', 'n.', 'A minute grain or germ; a small, round or ovoid body, formed\n   in certain organisms, and by germination giving rise to a new organism;\n   as, the reproductive spores of bacteria, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spore', 'n.', 'One of the parts formed by fission in certain Protozoa. See\n   Spore formation, belw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporid', 'n.', 'A sporidium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporidiferous', 'a.', 'Bearing sporidia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporidia', 'pl. ', 'of Sporidium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporidium', 'n.', 'A secondary spore, or a filament produced from a spore,\n   in certain kinds of minute fungi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporidium', 'n.', 'A spore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporiferous', 'a.', 'Bearing or producing spores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporification', 'n.', 'Spore formation. See Spore formation (b), under\n   Spore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporocarp', 'n.', 'A closed body or conceptacle containing one or more\n   masses of spores or sporangia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporocarp', 'n.', 'A sporangium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporocyst', 'n.', 'An asexual zooid, usually forming one of a series of\n   larval forms in the agamic reproduction of various trematodes and other\n   parasitic worms. The sporocyst generally develops from an egg, but in\n   its turn produces other larvae by internal budding, or by the\n   subdivision of a part or all of its contents into a number of minute\n   germs. See Redia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporocyst', 'n.', 'Any protozoan when it becomes encysted produces germs by\n   sporulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporogenesis', 'n.', 'reproduction by spores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporogony', 'n.', 'The growth or development of an animal or a zooid from a\n   nonsexual germ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporophore', 'n.', 'A placenta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporophore', 'n.', 'That alternately produced form of certain cryptogamous\n   plants, as ferns, mosses, and the like, which is nonsexual, but\n   produces spores in countless numbers. In ferns it is the leafy plant,\n   in mosses the capsule. Cf. Oophore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporophoric', 'a.', 'Having the nature of a sporophore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporosac', 'n.', 'A hydrozoan reproductive zooid or gonophore which does\n   not become medusoid in form or structure. See Illust. under Athecata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporosac', 'n.', 'An early or simple larval stage of trematode worms and\n   some other invertebrates, which is capable or reproducing other germs\n   by asexual generation; a nurse; a redia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporozoa', 'n. pl.', 'An extensive division of parasitic Protozoa, which\n   increase by sporulation. It includes the Gregarinida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporozoid', 'n.', 'Same as Zoospore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporran', 'n.', 'A large purse or pouch made of skin with the hair or fur\n   on, worn in front of the kilt by Highlanders when in full dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sport', 'n.', 'That which diverts, and makes mirth; pastime; amusement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sport', 'n.', 'Mock; mockery; contemptuous mirth; derision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sport', 'n.', 'That with which one plays, or which is driven about in play;\n   a toy; a plaything; an object of mockery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sport', 'n.', 'Play; idle jingle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sport', 'n.', 'Diversion of the field, as fowling, hunting, fishing,\n   racing, games, and the like, esp. when money is staked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sport', 'n.', 'A plant or an animal, or part of a plant or animal, which\n   has some peculiarity not usually seen in the species; an abnormal\n   variety or growth. See Sporting plant, under Sporting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sport', 'n.', 'A sportsman; a gambler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sported', 'imp. & p. p.', 'of Sport');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporting', 'p. pr. & vb. n.', 'of Sport');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sport', 'v. i.', 'To play; to frolic; to wanton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sport', 'v. i.', 'To practice the diversions of the field or the turf; to\n   be given to betting, as upon races.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sport', 'v. i.', 'To trifle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sport', 'v. i.', 'To assume suddenly a new and different character from the\n   rest of the plant or from the type of the species; -- said of a bud,\n   shoot, plant, or animal. See Sport, n., 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sport', 'v. t.', 'To divert; to amuse; to make merry; -- used with the\n   reciprocal pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sport', 'v. t.', 'To represent by any knd of play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sport', 'v. t.', 'To exhibit, or bring out, in public; to use or wear; as,\n   to sport a new equipage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sport', 'v. t.', 'To give utterance to in a sportive manner; to throw out\n   in an easy and copious manner; -- with off; as, to sport off epigrams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sportability', 'n.', 'Sportiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sportal', 'a.', 'Of or pertaining to sports; used in sports.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporter', 'n.', 'One who sports; a sportsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sportful', 'a.', 'Full of sport; merry; frolicsome; full of jesting;\n   indulging in mirth or play; playful; wanton; as, a sportful companion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sportful', 'a.', 'Done in jest, or for mere play; sportive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporting', 'a.', 'Of pertaining to, or engaging in, sport or sporrts;\n   exhibiting the character or conduct of one who, or that which, sports.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sportingly', 'adv.', 'In sport; sportively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sportive', 'a.', 'Tending to, engaged in, or provocate of, sport; gay;\n   froliscome; playful; merry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sportless', 'a.', 'Without sport or mirth; joyless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sportling', 'n.', 'A little person or creature engaged in sports or in\n   play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sportsmen', 'pl. ', 'of Sportsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sportsman', 'n.', 'One who pursues the sports of the field; one who hunts,\n   fishes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sportsmanship', 'n.', 'The practice of sportsmen; skill in field sports.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sportulae', 'pl. ', 'of Sportula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sportula', 'n.', 'A gift; a present; a prize; hence, an alms; a largess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sportulary', 'a.', 'Subsisting on alms or charitable contributions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sportule', 'n.', 'A charitable gift or contribution; a gift; an alms; a\n   dole; a largess; a sportula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporulation', 'n.', 'The act or process of forming spores; spore formation.\n   See Illust. of Bacillus, b.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporule', 'n.', 'A small spore; a spore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sporuliferous', 'a.', 'Producing sporules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spot', 'n.', 'A mark on a substance or body made by foreign matter; a blot;\n   a place discolored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spot', 'n.', 'A stain on character or reputation; something that soils\n   purity; disgrace; reproach; fault; blemish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spot', 'n.', 'A small part of a different color from the main part, or from\n   the ground upon which it is; as, the spots of a leopard; the spots on a\n   playing card.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spot', 'n.', 'A small extent of space; a place; any particular place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spot', 'n.', 'A variety of the common domestic pigeon, so called from a\n   spot on its head just above its beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spot', 'n.', 'A sciaenoid food fish (Liostomus xanthurus) of the Atlantic\n   coast of the United States. It has a black spot behind the shoulders\n   and fifteen oblique dark bars on the sides. Called also goody,\n   Lafayette, masooka, and old wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spot', 'n.', 'The southern redfish, or red horse, which has a spot on each\n   side at the base of the tail. See Redfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spot', 'n.', 'Commodities, as merchandise and cotton, sold for immediate\n   delivery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spotted', 'imp. & p. p.', 'of Spot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spotting', 'p. pr. & vb. n.', 'of Spot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spot', 'v. t.', 'To make visible marks upon with some foreign matter; to\n   discolor in or with spots; to stain; to cover with spots or figures;\n   as, to spot a garnment; to spot paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spot', 'v. t.', 'To mark or note so as to insure recognition; to recognize;\n   to detect; as, to spot a criminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spot', 'v. t.', 'To stain; to blemish; to taint; to disgrace; to tarnish,\n   as reputation; to asperse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spot', 'v. i.', 'To become stained with spots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spotless', 'a.', 'Without a spot; especially, free from reproach or\n   impurity; pure; untainted; innocent; as, a spotless mind; spotless\n   behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spotted', 'a.', 'Marked with spots; as, a spotted garment or character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spottedness', 'n.', 'State or quality of being spotted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spotter', 'n.', 'One who spots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spottiness', 'n.', 'The state or quality of being spotty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spotty', 'a.', 'Full of spots; marked with spots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spousage', 'v. t.', 'Espousal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spousal', 'a.', 'Of or pertaining to a spouse or marriage; nuptial;\n   matrimonial; conjugal; bridal; as, spousal rites; spousal ornaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spousal', 'n.', 'Marriage; nuptials; espousal; -- generally used in the\n   plural; as, the spousals of Hippolita.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spouse', 'n.', 'A man or woman engaged or joined in wedlock; a married\n   person, husband or wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spouse', 'n.', 'A married man, in distinct from a spousess or married\n   woman; a bridegroom or husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spouse', 'n.', 'To wed; to espouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spouse-breach', 'n.', 'Adultery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spouseless', 'a.', 'Destitute of a spouse; unmarried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spousess', 'n.', 'A wife or bride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spouted', 'imp. & p. p.', 'of Spout');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spouting', 'p. pr. & vb. n.', 'of Spout');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spout', 'v. t.', 'To throw out forcibly and abudantly, as liquids through\n   an office or a pipe; to eject in a jet; as, an elephant spouts water\n   from his trunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spout', 'v. t.', 'To utter magniloquently; to recite in an oratorical or\n   pompous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spout', 'v. t.', 'To pawn; to pledge; as, spout a watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spout', 'v. i.', 'To issue with with violence, or in a jet, as a liquid\n   through a narrow orifice, or from a spout; as, water spouts from a\n   hole; blood spouts from an artery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spout', 'v. i.', 'To eject water or liquid in a jet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spout', 'v. i.', 'To utter a speech, especially in a pompous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spout', 'v. t.', 'That through which anything spouts; a discharging lip,\n   pipe, or orifice; a tube, pipe, or conductor of any kind through which\n   a liquid is poured, or by which it is conveyed in a stream from one\n   place to another; as, the spout of a teapot; a spout for conducting\n   water from the roof of a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spout', 'v. t.', 'A trough for conducting grain, flour, etc., into a\n   receptacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spout', 'v. t.', 'A discharge or jet of water or other liquid, esp. when\n   rising in a column; also, a waterspout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spouter', 'n.', 'One who, or that which, spouts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoutfish', 'n.', 'A marine animal that spouts water; -- applied especially\n   to certain bivalve mollusks, like the long clams (Mya), which spout, or\n   squirt out, water when retiring into their holes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoutless', 'a.', 'Having no spout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spoutshell', 'n.', 'Any marine gastropod shell of the genus Apporhais\n   having an elongated siphon. See Illust. under Rostrifera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprack', 'a.', 'Quick; lively; alert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprad', '', 'p. p. of Spread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spradde', '', 'imp. of Spread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprag', 'n.', 'A young salmon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprag', 'n.', 'A billet of wood; a piece of timber used as a prop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spragged', 'imp. & p. p.', 'of Sprag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spragging', 'p. pr. & vb. n.', 'of Sprag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprag', 'v. t.', 'To check the motion of, as a carriage on a steep grade,\n   by putting a sprag between the spokes of the wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprag', 'v. t.', 'To prop or sustain with a sprag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprag', 'a.', 'See Sprack, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprained', 'imp. & p. p.', 'of Sprain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spraining', 'p. pr. & vb. n.', 'of Sprain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprain', 'v. t.', 'To weaken, as a joint, ligament, or muscle, by sudden\n   and excessive exertion, as by wrenching; to overstrain, or stretch\n   injuriously, but without luxation; as, to sprain ones ankle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprain', 'n.', 'The act or result of spraining; lameness caused by\n   spraining; as, a bad sprain of the wrist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spraints', 'v. t.', 'The dung of an otter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprang', '', 'imp. of Spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprat', 'n.', 'A small European herring (Clupea sprattus) closely allied to\n   the common herring and the pilchard; -- called also garvie. The name is\n   also applied to small herring of different kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprat', 'n.', 'A California surf-fish (Rhacochilus toxotes); -- called also\n   alfione, and perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprawled', 'imp. & p. p.', 'of Sprawl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprawling', 'p. pr. & vb. n.', 'of Sprawl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprawl', 'v. i.', 'To spread and stretch the body or limbs carelessly in a\n   horizontal position; to lie with the limbs stretched out ungracefully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprawl', 'v. i.', 'To spread irregularly, as vines, plants, or tress; to\n   spread ungracefully, as chirography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprawl', 'v. i.', 'To move, when lying down, with awkward extension and\n   motions of the limbs; to scramble in creeping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Srawls', 'n. pl.', 'Small branches of a tree; twigs; sprays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spray', 'n.', 'A small shoot or branch; a twig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spray', 'n.', 'A collective body of small branches; as, the tree has a\n   beautiful spray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spray', 'n.', 'A side channel or branch of the runner of a flask, made to\n   distribute the metal in all parts of the mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spray', 'n.', 'A group of castings made in the same mold and connected by\n   sprues formed in the runner and its branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spray', 'v. t.', 'Water flying in small drops or particles, as by the force\n   of wind, or the dashing of waves, or from a waterfall, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spray', 'v. t.', 'A jet of fine medicated vapor, used either as an\n   application to a diseased part or to charge the air of a room with a\n   disinfectant or a deodorizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spray', 'v. t.', 'An instrument for applying such a spray; an atomizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spray', 'v. t.', 'To let fall in the form of spray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spray', 'v. t.', 'To throw spray upon; to treat with a liquid in the form\n   of spray; as, to spray a wound, or a surgical instrument, with carbolic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprayboard', 'n.', 'See Dashboard, n., 2 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread', 'imp. & p. p.', 'of Spread');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spreading', 'p. pr. & vb. n.', 'of Spread');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread', 'v. t.', 'To extend in length and breadth, or in breadth only; to\n   stretch or expand to a broad or broader surface or extent; to open; to\n   unfurl; as, to spread a carpet; to spread a tent or a sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread', 'v. t.', 'To extend so as to cover something; to extend to a great\n   or grater extent in every direction; to cause to fill or cover a wide\n   or wider space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread', 'v. t.', 'To divulge; to publish, as news or fame; to cause to be\n   more extensively known; to disseminate; to make known fully; as, to\n   spread a report; -- often acompanied by abroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread', 'v. t.', 'To propagate; to cause to affect great numbers; as, to\n   spread a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread', 'v. t.', 'To diffuse, as emanations or effluvia; to emit; as,\n   odoriferous plants spread their fragrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread', 'v. t.', 'To strew; to scatter over a surface; as, to spread\n   manure; to spread lime on the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread', 'v. t.', 'To prepare; to set and furnish with provisions; as, to\n   spread a table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread', 'v. i.', 'To extend in length and breadth in all directions, or in\n   breadth only; to be extended or stretched; to expand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread', 'v. i.', 'To be extended by drawing or beating; as, some metals\n   spread with difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread', 'v. i.', 'To be made known more extensively, as news.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread', 'v. i.', 'To be propagated from one to another; as, the disease\n   spread into all parts of the city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread', 'n.', 'Extent; compass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread', 'n.', 'Expansion of parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread', 'n.', 'A cloth used as a cover for a table or a bed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread', 'n.', 'A table, as spread or furnished with a meal; hence, an\n   entertainment of food; a feast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread', 'n.', 'A privilege which one person buys of another, of demanding\n   certain shares of stock at a certain price, or of delivering the same\n   shares of stock at another price, within a time agreed upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread', 'n.', 'An unlimited expanse of discontinuous points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread', '', 'imp. & p. p. of Spread, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spread-eagle', 'a.', 'Characterized by a pretentious, boastful, exaggerated\n   style; defiantly or extravagantly bombastic; as, a spread-eagle orator;\n   a spread-eagle speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spreader', 'n.', 'One who, or that which, spreads, expands, or propogates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spreader', 'n.', 'A machine for combining and drawing fibers of flax to\n   form a sliver preparatory to spinning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spreadingly', 'adv.', 'Increasingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprechery', 'n.', 'Movables of an inferior description; especially, such as\n   have been collected by depredation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spree', 'n.', 'A merry frolic; especially, a drinking frolic; a carousal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprenge', 'v. t.', 'To sprinkle; to scatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprengel pump', '', 'A form of air pump in which exhaustion is produced by\n   a stream of mercury running down a narrow tube, in the manner of an\n   aspirator; -- named from the inventor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprent', '', 'p. p. of Sprenge. Sprinkled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprew', 'n.', 'Thrush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spreynd', '', 'p. p. of Sprenge. Sprinkled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprig', 'n.', 'A small shoot or twig of a tree or other plant; a spray; as,\n   a sprig of laurel or of parsley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprig', 'n.', 'A youth; a lad; -- used humorously or in slight\n   disparagement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprig', 'n.', 'A brad, or nail without a head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprig', 'n.', 'A small eyebolt ragged or barbed at the point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprigged', 'imp. & p. p.', 'of Sprig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprigging', 'p. pr. & vb. n.', 'of Sprig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprig', 'v. t.', 'To mark or adorn with the representation of small\n   branches; to work with sprigs; as, to sprig muslin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprigged', 'a.', 'Having sprigs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spriggy', 'a.', 'Full of sprigs or small branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spright', 'n.', 'Spirit; mind; soul; state of mind; mood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spright', 'n.', 'A supernatural being; a spirit; a shade; an apparition; a\n   ghost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spright', 'n.', 'A kind of short arrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spright', 'v. t.', 'To haunt, as a spright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprightful', 'a.', 'Full of spirit or of life; earnest; vivacious; lively;\n   brisk; nimble; gay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprightless', 'a.', 'Destitute of life; dull; sluggish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprightliness', 'n.', 'The quality or state of being sprightly; liveliness;\n   life; briskness; vigor; activity; gayety; vivacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprightly', 'superl.', 'Sprightlike, or spiritlike; lively; brisk;\n   animated; vigorous; airy; gay; as, a sprightly youth; a sprightly air;\n   a sprightly dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprigtail', 'n.', 'The pintail duck; -- called also sprig, and spreet-tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprigtail', 'n.', 'The sharp-tailed grouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprang', 'imp.', 'of Spring');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprung', '', 'of Spring');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprung', 'p. p.', 'of Spring');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springing', 'p. pr. & vb. n.', 'of Spring');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'To leap; to bound; to jump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'To issue with speed and violence; to move with activity;\n   to dart; to shoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'To start or rise suddenly, as from a covert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'To fly back; as, a bow, when bent, springs back by its\n   elastic power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'To bend from a straight direction or plane surface; to\n   become warped; as, a piece of timber, or a plank, sometimes springs in\n   seasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'To shoot up, out, or forth; to come to the light; to\n   begin to appear; to emerge; as a plant from its seed, as streams from\n   their source, and the like; -often followed by up, forth, or out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'To issue or proceed, as from a parent or ancestor; to\n   result, as from a cause, motive, reason, or principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'To grow; to prosper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. t.', 'To cause to spring up; to start or rouse, as game; to\n   cause to rise from the earth, or from a covert; as, to spring a\n   pheasant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. t.', 'To produce or disclose suddenly or unexpectedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. t.', 'To cause to explode; as, to spring a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. t.', 'To crack or split; to bend or strain so as to weaken;\n   as, to spring a mast or a yard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. t.', 'To cause to close suddenly, as the parts of a trap\n   operated by a spring; as, to spring a trap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. t.', 'To bend by force, as something stiff or strong; to force\n   or put by bending, as a beam into its sockets, and allowing it to\n   straighten when in place; -- often with in, out, etc.; as, to spring in\n   a slat or a bar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. t.', 'To pass over by leaping; as, to spring a fence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'A leap; a bound; a jump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'A flying back; the resilience of a body recovering its\n   former state by elasticity; as, the spring of a bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'Elastic power or force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'An elastic body of any kind, as steel, India rubber,\n   tough wood, or compressed air, used for various mechanical purposes, as\n   receiving and imparting power, diminishing concussion, regulating\n   motion, measuring weight or other force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'Any source of supply; especially, the source from which\n   a stream proceeds; as issue of water from the earth; a natural\n   fountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'Any active power; that by which action, or motion, is\n   produced or propagated; cause; origin; motive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'That which springs, or is originated, from a source;');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'A race; lineage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'A youth; a springal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'A shoot; a plant; a young tree; also, a grove of trees;\n   woodland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'That which causes one to spring; specifically, a lively\n   tune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'The season of the year when plants begin to vegetate and\n   grow; the vernal season, usually comprehending the months of March,\n   April, and May, in the middle latitudes north of the equator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'The time of growth and progress; early portion; first\n   stage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'A crack or fissure in a mast or yard, running obliquely\n   or transversely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spring', 'v. i.', 'A line led from a vessels quarter to her cable so that\n   by tightening or slacking it she can be made to lie in any desired\n   position; a line led diagonally from the bow or stern of a vessel to\n   some point upon the wharf to which she is moored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springal', 'a.', 'Alt. of Springall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springald', 'a.', 'Alt. of Springall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springall', 'a.', 'An active, springly young man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springal', 'n.', 'An ancient military engine for casting stones and arrows\n   by means of a spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springboard', 'n.', 'An elastic board, secured at the ends, or at one end,\n   often by elastic supports, used in performing feats of agility or in\n   exercising.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springbok', 'n.', 'Alt. of Springbuck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springbuck', 'n.', 'A South African gazelle (Gazella euchore) noted for its\n   graceful form and swiftness, and for its peculiar habit of springing\n   lighty and suddenly into the air. It has a white dorsal stripe,\n   expanding into a broad patch of white on the rump and tail. Called also\n   springer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springe', 'v. i.', 'A noose fastened to an elastic body, and drawn close\n   with a sudden spring, whereby it catches a bird or other animal; a gin;\n   a snare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springe', 'v. t.', 'To catch in a springe; to insnare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springe', 'v. t.', 'To sprinkle; to scatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springer', 'n.', 'One who, or that which, springs; specifically, one who\n   rouses game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springer', 'n.', 'A young plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springer', 'n.', 'The impost, or point at which an arch rests upon its\n   support, and from which it seems to spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springer', 'n.', 'The bottom stone of an arch, which lies on the impost.\n   The skew back is one form of springer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springer', 'n.', 'The rib of a groined vault, as being the solid abutment\n   for each section of vaulting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springer', 'n.', 'The grampus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springer', 'n.', 'A variety of the field spaniel. See Spaniel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springer', 'n.', 'A species of antelope; the sprinkbok.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springhalt', 'n.', 'A kind of lameness in horse. See Stringhalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springhead', 'n.', 'A fountain or source.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springiness', 'n.', 'The state or quality of being springly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springing', 'n.', 'The act or process of one who, or that which, springs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springing', 'n.', 'Growth; increase; also, that which springs up; a shoot;\n   a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springle', 'n.', 'A springe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springlet', 'n.', 'A little spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springtail', 'n.', 'Any one of numerous species of small apterous insects\n   belonging to the order Thysanura. They have two elastic caudal stylets\n   which can be bent under the abdomen and then suddenly extended like a\n   spring, thus enabling them to leap to a considerable distance. See\n   Collembola, and Podura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springtide', 'n.', 'The time of spring; springtime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springtime', 'n.', 'The season of spring; springtide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springy', 'superl.', 'Resembling, having the qualities of, or pertaining\n   to, a spring; elastic; as, springy steel; a springy step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Springy', 'superl.', 'Abounding with springs or fountains; wet; spongy; as,\n   springy land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprinkled', 'imp. & p. p.', 'of Sprinkle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprinkling', 'p. pr. & vb. n.', 'of Sprinkle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprinkle', 'v. i.', 'To scatter in small drops or particles, as water,\n   seed, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprinkle', 'v. i.', 'To scatter on; to disperse something over in small\n   drops or particles; to besprinkle; as, to sprinkle the earth with\n   water; to sprinkle a floor with sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprinkle', 'v. i.', 'To baptize by the application of a few drops, or a\n   small quantity, of water; hence, to cleanse; to purify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprinkle', 'v. i.', 'To scatter a liquid, or any fine substance, so that it\n   may fall in particles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprinkle', 'v. i.', 'To rain moderately, or with scattered drops falling\n   now and then; as, it sprinkles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprinkle', 'v. i.', 'To fly or be scattered in small drops or particles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprinkle', 'n.', 'A small quantity scattered, or sparsely distributed; a\n   sprinkling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprinkle', 'n.', 'A utensil for sprinkling; a sprinkler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprinkler', 'n.', 'One who sprinkles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprinkler', 'n.', 'An instrument or vessel used in sprinkling;\n   specifically, a watering pot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprinkling', 'n.', 'The act of one who, or that which, sprinkles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprinkling', 'n.', 'A small quantity falling in distinct drops or\n   particles; as, a sprinkling of rain or snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprinkling', 'n.', 'Hence, a moderate number or quantity distributed like\n   drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprinted', 'imp. & p. p.', 'of Sprint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprinting', 'p. pr. & vb. n.', 'of Sprint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprint', 'v. i.', 'To run very rapidly; to run at full speed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprint', 'n.', 'The act of sprinting; a run of a short distance at full\n   speed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprinter', 'n.', 'One who sprints; one who runs in sprint races; as, a\n   champion sprinter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprit', 'v. i.', 'To throw out with force from a narrow orifice; to eject;\n   to spurt out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprit', 'v. t.', 'To sprout; to bud; to germinate, as barley steeped for\n   malt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprit', 'n.', 'A shoot; a sprout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprit', 'v. i.', 'A small boom, pole, or spar, which crosses the sail of a\n   boat diagonally from the mast to the upper aftmost corner, which it is\n   used to extend and elevate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprite', 'n.', 'A spirit; a soul; a shade; also, an apparition. See\n   Spright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprite', 'n.', 'An elf; a fairy; a goblin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprite', 'n.', 'The green woodpecker, or yaffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spriteful', 'a.', 'Alt. of Spritely');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spritefully', 'a.', 'Alt. of Spritely');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spriteliness', 'a.', 'Alt. of Spritely');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spritely', 'a.', 'See Sprightful, Sprightfully, Sprightliness, Sprightly,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spritsail', 'n.', 'A sail extended by a sprit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spritsail', 'n.', 'A sail formerly hung under the bowsprit, from the\n   spritsail yard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprocket wheel', '', 'Same as Chain wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprod', 'n.', 'A salmon in its second year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprong', '', 'imp. of Spring. Sprung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprouted', 'imp. & p. p.', 'of Sprout');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprouting', 'p. pr. & vb. n.', 'of Sprout');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprout', 'v. t.', 'To shoot, as the seed of a plant; to germinate; to push\n   out new shoots; hence, to grow like shoots of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprout', 'v. t.', 'To shoot into ramifications.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprout', 'v. t.', 'To cause to sprout; as, the rain will sprout the seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprout', 'v. t.', 'To deprive of sprouts; as, to sprout potatoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprout', 'v. i.', 'The shoot of a plant; a shoot from the seed, from the\n   stump, or from the root or tuber, of a plant or tree; more rarely, a\n   shoot from the stem of a plant, or the end of a branch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprout', 'v. i.', 'Young coleworts; Brussels sprouts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spruce', 'a.', 'Any coniferous tree of the genus Picea, as the Norway\n   spruce (P. excelsa), and the white and black spruces of America (P.\n   alba and P. nigra), besides several others in the far Northwest. See\n   Picea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spruce', 'a.', 'The wood or timber of the spruce tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spruce', 'a.', 'Prussia leather; pruce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spruce', 'n.', 'Neat, without elegance or dignity; -- formerly applied to\n   things with a serious meaning; now chiefly applied to persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spruce', 'n.', 'Sprightly; dashing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spruced', 'imp. & p. p.', 'of Spruce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprucing', 'p. pr. & vb. n.', 'of Spruce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spruce', 'v. t.', 'To dress with affected neatness; to trim; to make\n   spruce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spruce', 'v. i.', 'To dress ones self with affected neatness; as, to\n   spruce up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprue', 'n.', 'Strictly, the hole through which melted metal is poured into\n   the gate, and thence into the mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprue', 'n.', 'The waste piece of metal cast in this hole; hence, dross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprue', 'n.', 'Same as Sprew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprug', 'v. t.', 'To make smart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprung', '', 'imp. & p. p. of Spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprung', 'a.', 'Said of a spar that has been cracked or strained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprunt', 'v. i.', 'To spring up; to germinate; to spring forward or\n   outward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprunt', 'n.', 'Anything short and stiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprunt', 'n.', 'A leap; a spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprunt', 'n.', 'A steep ascent in a road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sprunt', 'a.', 'Active; lively; vigorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spruntly', 'adv.', 'In a sprunt manner; smartly; vigorously; youthfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spry', 'superl.', 'Having great power of leaping or running; nimble;\n   active.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spud', 'n.', 'A sharp, narrow spade, usually with a long handle, used by\n   farmers for digging up large-rooted weeds; a similarly shaped implement\n   used for various purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spud', 'n.', 'A dagger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spud', 'n.', 'Anything short and thick; specifically, a piece of dough\n   boiled in fat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spue', 'v. t. & i.', 'See Spew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spuilzie', 'n.', 'See Spulzie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spuke', 'n.', 'See Spook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spuller', 'n.', 'One employed to inspect yarn, to see that it is well spun,\n   and fit for the loom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spulzie', 'n.', 'Plunder, or booty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spume', 'n.', 'Frothy matter raised on liquids by boiling, effervescence,\n   or agitation; froth; foam; scum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spumed', 'imp. & p. p.', 'of Spume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spuming', 'p. pr. & vb. n.', 'of Spume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spume', 'v. i.', 'To froth; to foam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spumeous', 'a.', 'Spumous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spumescence', 'n.', 'The state of being foamy; frothiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spumescent', 'a.', 'Resembling froth or foam; foaming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spumid', 'a.', 'Spumous; frothy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spumiferous', 'a.', 'Producing foam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spuminess', 'n.', 'The quality or condition of being spumy; spumescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spumous', 'a.', 'Alt. of Spumy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spumy', 'a.', 'Consisting of, containing, or covered with, froth, scum, or\n   foam; frothy; foamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spun', '', 'imp. & p. p. of Spin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spunge', 'n.', 'A sponge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spunk', 'n.', 'Wood that readily takes fire; touchwood; also, a kind of\n   tinder made from a species of fungus; punk; amadou.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spunk', 'n.', 'An inflammable temper; spirit; mettle; pluck; as, a man of\n   spunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spunky', 'superl.', 'Full of spunk; quick; spirited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'n.', 'A sparrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'n.', 'A tern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'n.', 'An implement secured to the heel, or above the heel, of a\n   horseman, to urge the horse by its pressure. Modern spurs have a small\n   wheel, or rowel, with short points. Spurs were the badge of knighthood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'n.', 'That which goads to action; an incitement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'n.', 'Something that projects; a snag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'n.', 'One of the large or principal roots of a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'n.', 'Any stiff, sharp spine, as on the wings and legs of certain\n   burds, on the legs of insects, etc.; especially, the spine on a cocks\n   leg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'n.', 'A mountain that shoots from any other mountain, or range of\n   mountains, and extends to some distance in a lateral direction, or at\n   right angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'n.', 'A spiked iron worn by seamen upon the bottom of the boot, to\n   enable them to stand upon the carcass of a whale, to strip off the\n   blubber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'n.', 'A brace strengthening a post and some connected part, as a\n   rafter or crossbeam; a strut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'n.', 'The short wooden buttress of a post.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'n.', 'A projection from the round base of a column, occupying the\n   angle of a square plinth upon which the base rests, or bringing the\n   bottom bed of the base to a nearly square form. It is generally carved\n   in leafage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'n.', 'Any projecting appendage of a flower looking like a spur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'n.', 'Ergotized rye or other grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'n.', 'A wall that crosses a part of a rampart and joins to an inner\n   wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'n.', 'A piece of timber fixed on the bilge ways before launching,\n   having the upper ends bolted to the vessels side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'n.', 'A curved piece of timber serving as a half to support the\n   deck where a whole beam can not be placed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurred', 'imp. & p. p.', 'of Spur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurring', 'p. pr. & vb. n.', 'of Spur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'v. t.', 'To prick with spurs; to incite to a more hasty pace; to\n   urge or goad; as, to spur a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'v. t.', 'To urge or encourage to action, or to a more vigorous\n   pursuit of an object; to incite; to stimulate; to instigate; to impel;\n   to drive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'v. t.', 'To put spurs on; as, a spurred boot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur', 'v. i.', 'To spur on one horse; to travel with great expedition; to\n   hasten; hence, to press forward in any pursuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurgall', 'n.', 'A place galled or excoriated by much using of the spur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurgall', 'v. t.', 'To gall or wound with a spur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurge', 'v. t.', 'To emit foam; to froth; -- said of the emission of yeast\n   from beer in course of fermentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurge', 'n.', 'Any plant of the genus Euphorbia. See Euphorbia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurgewort', 'n.', 'Any euphorbiaceous plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurging', 'n.', 'A purging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurious', 'a.', 'Not proceeding from the true source, or from the source\n   pretended; not genuine; false; adulterate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurious', 'a.', 'Not legitimate; bastard; as, spurious issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurless', 'a.', 'Having no spurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurling', 'n.', 'A tern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurling-line', 'n.', 'The line which forms the communication between the\n   steering wheel and the telltale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurned', 'imp. & p. p.', 'of Spurn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurning', 'p. pr. & vb. n.', 'of Spurn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurn', 'v. t.', 'To drive back or away, as with the foot; to kick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurn', 'v. t.', 'To reject with disdain; to scorn to receive or accept; to\n   treat with contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurn', 'v. i.', 'To kick or toss up the heels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurn', 'v. i.', 'To manifest disdain in rejecting anything; to make\n   contemptuous opposition or resistance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurn', 'n.', 'A kick; a blow with the foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurn', 'n.', 'Disdainful rejection; contemptuous tratment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurn', 'n.', 'A body of coal left to sustain an overhanding mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurner', 'n.', 'One who spurns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurn-water', 'n.', 'A channel at the end of a deck to restrain the water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurred', 'a.', 'Wearing spurs; furnished with a spur or spurs; having\n   shoots like spurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurred', 'a.', 'Affected with spur, or ergot; as, spurred rye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurrer', 'n.', 'One who spurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurrey', 'n.', 'See Spurry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurrier', 'n.', 'One whose occupation is to make spurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur-royal', 'n.', 'A gold coin, first made in the reign of Edward IV.,\n   having a star on the reverse resembling the rowel of a spur. In the\n   reigns of Elizabeth and of James I., its value was fifteen shillings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurry', 'n.', 'An annual herb (Spergula arvensis) with whorled filiform\n   leaves, sometimes grown in Europe for fodder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur-shell', 'n.', 'Any one of several species of handsome gastropod shells\n   of the genus Trochus, or Imperator. The shell is conical, with the\n   margin toothed somewhat like the rowel of a spur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurt', 'v. i.', 'To gush or issue suddenly or violently out in a stream,\n   as liquor from a cask; to rush from a confined place in a small stream\n   or jet; to spirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurt', 'v. t.', 'To throw out, as a liquid, in a stream or jet; to drive\n   or force out with violence, as a liquid from a pipe or small orifice;\n   as, to spurt water from the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sourt', 'n.', 'A sudden or violent ejection or gushing of a liquid, as of\n   water from a tube, orifice, or other confined place, or of blood from a\n   wound; a jet; a spirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sourt', 'n.', 'A shoot; a bud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sourt', 'n.', 'Fig.: A sudden outbreak; as, a spurt of jealousy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurt', 'n.', 'A sudden and energetic effort, as in an emergency; an\n   increased exertion for a brief space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurted', 'imp. & p. p.', 'of Spurt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurting', 'p. pr. & vb. n.', 'of Spurt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurt', 'v. i.', 'To make a sudden and violent exertion, as in an\n   emergency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurtle', 'v. t.', 'To spurt or shoot in a scattering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spurway', 'n.', 'A bridle path.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spur-winged', 'a.', 'Having one or more spurs on the bend of the wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sput', 'n.', 'An annular reenforce, to strengthen a place where a hole is\n   made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sputation', 'n.', 'The act of spitting; expectoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sputative', 'a.', 'Inclined to spit; spitting much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spute', 'v. t.', 'To dispute; to discuss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sputtered', 'imp. & p. p.', 'of Sputter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sputtering', 'p. pr. & vb. n.', 'of Sputter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sputter', 'v. i.', 'To spit, or to emit saliva from the mouth in small,\n   scattered portions, as in rapid speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sputter', 'v. i.', 'To utter words hastily and indistinctly; to speak so\n   rapidly as to emit saliva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sputter', 'v. i.', 'To throw out anything, as little jets of steam, with a\n   noise like that made by one sputtering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sputter', 'v. t.', 'To spit out hastily by quick, successive efforts, with\n   a spluttering sound; to utter hastily and confusedly, without control\n   over the organs of speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sputter', 'n.', 'Moist matter thrown out in small detached particles; also,\n   confused and hasty speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sputterer', 'n.', 'One who sputters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sputa', 'pl. ', 'of Sputum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sputum', 'n.', 'That which is expectorated; a salival discharge; spittle;\n   saliva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spied', 'imp. & p. p.', 'of Spy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spying', 'p. pr. & vb. n.', 'of Spy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spy', 'v. t.', 'To gain sight of; to discover at a distance, or in a state\n   of concealment; to espy; to see.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spy', 'v. t.', 'To discover by close search or examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spy', 'v. t.', 'To explore; to view; inspect; and examine secretly, as a\n   country; -- usually with out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spy', 'v. i.', 'To search narrowly; to scrutinize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spies', 'pl. ', 'of Spy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spy', 'n.', 'One who keeps a constant watch of the conduct of others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spy', 'n.', 'A person sent secretly into an enemys camp, territory, or\n   fortifications, to inspect his works, ascertain his strength,\n   movements, or designs, and to communicate such intelligence to the\n   proper officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spyboat', 'n.', 'A boat sent to make discoveries and bring intelligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spyglass', 'n.', 'A small telescope for viewing distant terrestrial\n   objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spyism', 'n.', 'Act or business of spying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spynace', 'n.', 'Alt. of Spyne');
  INSERT INTO words (word, word_type, description) VALUES  (    'Spyne', 'n.', 'See Pinnace, n., 1 (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squab', 'a.', 'Fat; thick; plump; bulky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squab', 'a.', 'Unfledged; unfeathered; as, a squab pigeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squab', 'n.', 'A neatling of a pigeon or other similar bird, esp. when very\n   fat and not fully fledged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squab', 'n.', 'A person of a short, fat figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squab', 'n.', 'A thickly stuffed cushion; especially, one used for the seat\n   of a sofa, couch, or chair; also, a sofa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squab', 'adv.', 'With a heavy fall; plump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squab', 'v. i.', 'To fall plump; to strike at one dash, or with a heavy\n   stroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squabash', 'v. t.', 'To crush; to quash; to squash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squabbish', 'a.', 'Thick; fat; heavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squabbled', 'imp. & p. p.', 'of Squabble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squabbling', 'p. pr. & vb. n.', 'of Squabble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squabble', 'v. i.', 'To contend for superiority in an unseemly maner; to\n   scuffle; to struggle; to wrangle; to quarrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squabble', 'v. i.', 'To debate peevishly; to dispute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squabble', 'v. t.', 'To disarrange, so that the letters or lines stand awry\n   or are mixed and need careful readjustment; -- said of type that has\n   been set up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squabble', 'n.', 'A scuffle; a wrangle; a brawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squabbler', 'n.', 'One who squabbles; a contentious person; a brawler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squabby', 'a.', 'Short and thick; suqabbish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squab-chick', 'n.', 'A young chicken before it is fully fledged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squaccos', 'pl. ', 'of Squacco');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squacco', 'n.', 'A heron (Ardea comata) found in Asia, Northern Africa, and\n   Southern Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squad', 'n.', 'A small party of men assembled for drill, inspection, or\n   other purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squad', 'n.', 'Hence, any small party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squad', 'n.', 'Sloppy mud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squadron', 'n.', 'Primarily, a square; hence, a square body of troops; a\n   body of troops drawn up in a square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squadron', 'n.', 'A body of cavarly comparising two companies or troops,\n   and averging from one hundred and twenty to two hundred men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squadron', 'n.', 'A detachment of vessels employed on any particular\n   service or station, under the command of the senior officer; as, the\n   North Atlantic Squadron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squadroned', 'a.', 'Formed into squadrons, or squares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squail', 'v. i.', 'To throw sticls at cocks; to throw anything about\n   awkwardly or irregularly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squaimous', 'a.', 'Squeamish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squali', 'n. pl.', 'The suborder of elasmobranch fishes which comprises the\n   sharks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squalid', 'a.', 'Dirty through neglect; foul; filthy; extremely dirty.');
