  INSERT INTO words (word, word_type, description) VALUES  (    'Squalidly', 'adv.', 'In a squalid manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squalidness', 'n.', 'Quality or state of being squalid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squall', 'n.', 'A sudden violent gust of wind often attended with rain or\n   snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squalled', 'imp. & p. p.', 'of Squall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squalling', 'p. pr. & vb. n.', 'of Squall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squall', 'v. i.', 'To cry out; to scream or cry violently, as a woman\n   frightened, or a child in anger or distress; as, the infant squalled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squall', 'n.', 'A loud scream; a harsh cry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squaller', 'n.', 'One who squalls; a screamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squally', 'a.', 'Abounding with squalls; disturbed often with sudden and\n   violent gusts of wind; gusty; as, squally weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squally', 'a.', 'Interrupted by unproductive spots; -- said of a flied of\n   turnips or grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squally', 'a.', 'Not equally good throughout; not uniform; uneven; faulty;\n   -- said of cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squalodon', 'n.', 'A genus of fossil whales belonging to the Phocodontia;\n   -- so called because their are serrated, like a sharks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squalodont', 'a.', 'Pertaining to Squalodon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squaloid', 'a.', 'Like or pertaining to a shark or sharks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squalor', 'n.', 'Squalidness; foulness; filthness; squalidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamae', 'pl. ', 'of Squama');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squama', 'n.', 'A scale cast off from the skin; a thin dry shred consisting\n   of epithelium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamaceous', 'a.', 'Squamose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamata', 'n. pl.', 'A division of edentates having the body covered with\n   large, imbricated horny scales. It includes the pangolins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamate', 'a.', 'Alt. of Squamated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamated', 'a.', 'Same as Squamose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamduck', '', 'The American eider duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squame', 'n.', 'A scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squame', 'n.', 'The scale, or exopodite, of an antenna of a crustacean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamellae', 'pl. ', 'of Squamella');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamella', 'n.', 'A diminutive scale or bractlet, such as those found on\n   the receptacle in many composite plants; a palea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamellate', 'a.', 'Furnished or covered with little scales; squamulose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamiform', 'a.', 'Having the shape of a scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamigerous', 'a.', 'Bearing scales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamipennes', 'pl. ', 'of Squamipen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamipen', 'n.', 'Any one of a group of fishes having the dorsal and anal\n   fins partially covered with scales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamoid', 'a.', 'Resembling a scale; also, covered with scales; scaly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamosal', 'a.', 'Scalelike; squamous; as, the squamosal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamosal', 'a.', 'Of or pertaining to the squamosal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamosal', 'n.', 'The squamous part of the temporal bone, or a bone\n   correspondending to it, under Temporal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamose', '', 'Alt. of Squamous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamous', '', 'Covered with, or consisting of, scales; resembling a scale;\n   scaly; as, the squamose cones of the pine; squamous epithelial cells;\n   the squamous portion of the temporal bone, which is so called from a\n   fancied resemblance to a scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamous', '', 'Of or pertaining to the squamosal bone; squamosal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamozygomatic', 'a.', 'Of or pertaining to both the squamosal and\n   zygomatic bones; -- applied to a bone, or a center of ossification, in\n   some fetal skulls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamozygomatic', 'n.', 'A squamozygomatic bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamulae', 'pl. ', 'of Squamula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamula', 'n.', 'One of the little hypogynous scales found in the flowers\n   of grasses; a lodicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamulate', 'a.', 'Same as Squamulose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamule', 'n.', 'Same as Squamula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squamulose', 'a.', 'Having little scales; squamellate; squamulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squandered', 'imp. & p. p.', 'of Squander');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squandering', 'p. pr. & vb. n.', 'of Squander');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squander', 'v. t.', 'To scatter; to disperse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squander', 'v. t.', 'To spend lavishly or profusely; to spend prodigally or\n   wastefully; to use without economy or judgment; to dissipate; as, to\n   squander an estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squander', 'v. i.', 'To spend lavishly; to be wasteful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squander', 'v. i.', 'To wander at random; to scatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squander', 'n.', 'The act of squandering; waste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squanderer', 'n.', 'One who squanders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squanderingly', 'adv.', 'In a squandering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'The corner, or angle, of a figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'A parallelogram having four equal sides and four right\n   angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'Hence, anything which is square, or nearly so');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'A square piece or fragment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'A pane of glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'A certain number of lines, forming a portion of a column,\n   nearly square; -- used chiefly in reckoning the prices of\n   advertisements in newspapers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'One hundred superficial feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'An area of four sides, generally with houses on each side;\n   sometimes, a solid block of houses; also, an open place or area for\n   public use, as at the meeting or intersection of two or more streets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'An instrument having at least one right angle and two or\n   more straight edges, used to lay out or test square work. It is of\n   several forms, as the T square, the carpenters square, the\n   try-square., etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'Hence, a pattern or rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'The product of a number or quantity multiplied by itself;\n   thus, 64 is the square of 8, for 8 / 8 = 64; the square of a + b is a2\n   + 2ab + b2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'Exact proportion; justness of workmanship and conduct;\n   regularity; rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'A body of troops formed in a square, esp. one formed to\n   resist a charge of cavalry; a squadron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'Fig.: The relation of harmony, or exact agreement;\n   equality; level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'The position of planets distant ninety degrees from each\n   other; a quadrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'The act of squaring, or quarreling; a quarrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'The front of a womans dress over the bosom, usually worked\n   or embroidered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'a.', 'Having four equal sides and four right angles; as, a square\n   figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'a.', 'Forming a right angle; as, a square corner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'a.', 'Having a shape broad for the height, with rectilineal and\n   angular rather than curving outlines; as, a man of a square frame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'a.', 'Exactly suitable or correspondent; true; just.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'a.', 'Rendering equal justice; exact; fair; honest, as square\n   dealing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'a.', 'Even; leaving no balance; as, to make or leave the accounts\n   square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'a.', 'Leaving nothing; hearty; vigorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'a.', 'At right angles with the mast or the keel, and parallel to\n   the horizon; -- said of the yards of a square-rigged vessel when they\n   are so braced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squared', 'imp. & p. p.', 'of Square');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squaring', 'p. pr. & vb. n.', 'of Square');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'To form with four sides and four right angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'To form with right angles and straight lines, or flat\n   surfaces; as, to square masons work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'To compare with, or reduce to, any given measure or\n   standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'To adjust; to regulate; to mold; to shape; to fit; as, to\n   square our actions by the opinions of others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'To make even, so as leave no remainder of difference; to\n   balance; as, to square accounts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'To multiply by itself; as, to square a number or a\n   quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'To hold a quartile position respecting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'n.', 'To place at right angles with the keel; as, to square the\n   yards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'v. i.', 'To accord or agree exactly; to be consistent with; to\n   conform or agree; to suit; to fit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'v. i.', 'To go to opposite sides; to take an attitude of offense\n   or defense, or of defiance; to quarrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square', 'v. i.', 'To take a boxing attitude; -- often with up, sometimes\n   with off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squarely', 'adv.', 'In a square form or manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squareness', 'n.', 'The quality of being square; as, an instrument to try\n   the squareness of work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squarer', 'n.', 'One who, or that which, squares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squarer', 'n.', 'One who squares, or quarrels; a hot-headed, contentious\n   fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square-rigged', 'a.', 'Having the sails extended upon yards suspended\n   horizontally by the middle, as distinguished from fore-and-aft sails;\n   thus, a ship and a brig are square-rigged vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square-toed', 'n.', 'Having the toe square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Square-toes', 'n.', 'A precise person; -- used contemptuously or jocularly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squarish', 'a.', 'Nearly square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squarrose', 'a.', 'Ragged or full of lose scales or projecting parts;\n   rough; jagged');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squarrose', 'a.', 'Consisting of scales widely divaricating; having scales,\n   small leaves, or other bodies, spreading widely from the axis on which\n   they are crowded; -- said of a calyx or stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squarrose', 'a.', 'Divided into shreds or jags, raised above the plane of\n   the leaf, and not parallel to it; said of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squarrose', 'a.', 'Having scales spreading every way, or standing upright,\n   or at right angles to the surface; -- said of a shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squarroso-dentate', 'a.', 'Having the teeth bent out of the plane of the\n   lamina; -- said of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squarrous', 'a.', 'Squarrose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squarrulose', 'a.', 'Somewhat squarrose; slightly squarrose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squash', 'n.', 'An American animal allied to the weasel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squash', 'n.', 'A plant and its fruit of the genus Cucurbita, or gourd\n   kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squashed', 'imp. & p. p.', 'of Squash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squashing', 'p. pr. & vb. n.', 'of Squash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squash', 'v. i.', 'To beat or press into pulp or a flat mass; to crush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squash', 'n.', 'Something soft and easily crushed; especially, an unripe\n   pod of pease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squash', 'n.', 'Hence, something unripe or soft; -- used in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squash', 'n.', 'A sudden fall of a heavy, soft body; also, a shock of soft\n   bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squasher', 'n.', 'One who, or that which, squashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squashiness', 'n.', 'The quality or state of being squashy, or soft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squashy', 'a.', 'Easily squashed; soft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squat', 'n.', 'The angel fish (Squatina angelus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squatted', 'imp. & p. p.', 'of Squat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squatting', 'p. pr. & vb. n.', 'of Squat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squat', 'v. t.', 'To sit down upon the hams or heels; as, the savages\n   squatted near the fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squat', 'v. t.', 'To sit close to the ground; to cower; to stoop, or lie\n   close, to escape observation, as a partridge or rabbit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squat', 'v. t.', 'To settle on anothers land without title; also, to\n   settle on common or public lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squat', 'v. t.', 'To bruise or make flat by a fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squat', 'a.', 'Sitting on the hams or heels; sitting close to the ground;\n   cowering; crouching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squat', 'a.', 'Short and thick, like the figure of an animal squatting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squat', 'n.', 'The posture of one that sits on his heels or hams, or close\n   to the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squat', 'n.', 'A sudden or crushing fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squat', 'n.', 'A small vein of ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squat', 'n.', 'A mineral consisting of tin ore and spar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squaterole', 'n.', 'The black-bellied plover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squatter', 'n.', 'One who squats; specifically, one who settles unlawfully\n   upon land without a title. In the United States and Australia the term\n   is sometimes applied also to a person who settles lawfully upon\n   government land under permission and restrictions, before acquiring\n   title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squatter', 'n.', 'See Squat snipe, under Squat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squatty', 'a.', 'Squat; dumpy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squaw', 'n.', 'A female; a woman; -- in the language of Indian tribes of\n   the Algonquin family, correlative of sannup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squawberry', 'n.', 'A local name for the partridge berry; also, for the\n   deerberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squawked', 'imp. & p. p.', 'of Squawk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squawking', 'p. pr. & vb. n.', 'of Squawk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squawk', 'v. i.', 'To utter a shrill, abrupt scream; to squeak harshly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squawk', 'n.', 'Act of squawking; a harsh squeak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squawk', 'n.', 'The American night heron. See under Night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squawl', 'v. i.', 'See Squall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squawroot', 'n.', 'A scaly parasitic plant (Conopholis Americana) found in\n   oak woods in the United States; -- called also cancer root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squawweed', 'n.', 'The golden ragwort. See under Ragwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squaked', 'imp.& p. p.', 'of Squeak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeaking', 'p. pr. & vb. n.', 'of Squeak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeak', 'v. i.', 'To utter a sharp, shrill cry, usually of short duration;\n   to cry with an acute tone, as an animal; or, to make a sharp,\n   disagreeable noise, as a pipe or quill, a wagon wheel, a door; to\n   creak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeak', 'v. i.', 'To break silence or secrecy for fear of pain or\n   punishment; to speak; to confess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeak', 'n.', 'A sharp, shrill, disagreeable sound suddenly utered, either\n   of the human voice or of any animal or instrument, such as is made by\n   carriage wheels when dry, by the soles of leather shoes, or by a pipe\n   or reed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeaker', 'n.', 'One who, or that which, squeaks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeaker', 'n.', 'The Australian gray crow shrile (Strepera anaphonesis);\n   -- so called from its note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeakingly', 'adv.', 'In a squeaking manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squealed', 'imp. & p. p.', 'of Squeal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squealing', 'p. pr. & vb. n.', 'of Squeal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeal', 'v. i.', 'To cry with a sharp, shrill, prolonged sound, as certain\n   animals do, indicating want, displeasure, or pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeal', 'v. i.', 'To turn informer; to betray a secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeal', 'n.', 'A shrill, somewhat prolonged cry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squealer', 'n.', 'One who, or that which, squeals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squealer', 'n.', 'The European swift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squealer', 'n.', 'The harlequin duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squealer', 'n.', 'The American golden plover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeamish', 'a.', 'Having a stomach that is easily or nauseated; hence,\n   nice to excess in taste; fastidious; easily disgusted; apt to be\n   offended at trifling improprieties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeamous', 'a.', 'Squeamish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeasiness', 'n.', 'Queasiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squesy', 'a.', 'Queasy; nice; squeamish; fastidious; scrupulous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeegee', 'n.', 'Same as Squilgee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeezed', 'imp. & p. p.', 'of Squeeze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeezing', 'p. pr. & vb. n.', 'of Squeeze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeeze', 'v. t.', 'To press between two bodies; to press together closely;\n   to compress; often, to compress so as to expel juice, moisture, etc.;\n   as, to squeeze an orange with the fingers; to squeeze the hand in\n   friendship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeeze', 'v. t.', 'Fig.: To oppress with hardships, burdens, or taxes; to\n   harass; to crush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeeze', 'v. t.', 'To force, or cause to pass, by compression; often with\n   out, through, etc.; as, to squeeze water through felt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeeze', 'v. i.', 'To press; to urge ones way, or to pass, by pressing;\n   to crowd; -- often with through, into, etc.; as, to squeeze hard to get\n   through a crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeeze', 'n.', 'The act of one who squeezes; compression between bodies;\n   pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeeze', 'n.', 'A facsimile impression taken in some soft substance, as\n   pulp, from an inscription on stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeezer', 'n.', 'One who, or that which, squeezes; as, a lemon squeezer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeezer', 'n.', 'A machine like a large pair of pliers, for shingling, or\n   squeezing, the balls of metal when puddled; -- used only in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeezer', 'n.', 'A machine of several forms for the same purpose; -- used\n   in the singular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeezing', 'n.', 'The act of pressing; compression; oppression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeezing', 'n.', 'That which is forced out by pressure; dregs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeezing', 'n.', 'Same as Squeeze, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squelched', 'imp. & p. p.', 'of Squelch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squelching', 'p. pr. & vb. n.', 'of Squelch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squelch', 'v. t.', 'To quell; to crush; to silence or put down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squelch', 'n.', 'A heavy fall, as of something flat; hence, also, a\n   crushing reply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squeteague', 'n.', 'An American sciaenoid fish (Cynoscion regalis),\n   abundant on the Atlantic coast of the United States, and much valued as\n   a food fish. It is of a bright silvery color, with iridescent\n   reflections. Called also weakfish, squitee, chickwit, and sea trout.\n   The spotted squeteague (C. nebulosus) of the Southern United States is\n   a similar fish, but the back and upper fins are spotted with black. It\n   is called also spotted weakfish, and, locally, sea trout, and sea\n   salmon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squib', 'a.', 'A little pipe, or hollow cylinder of paper, filled with\n   powder or combustible matter, to be thrown into the air while burning,\n   so as to burst there with a crack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squib', 'a.', 'A kind of slow match or safety fuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squib', 'a.', 'A sarcastic speech or publication; a petty lampoon; a brief,\n   witty essay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squib', 'a.', 'A writer of lampoons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squib', 'a.', 'A paltry fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squibbed', 'imp. & p. p.', 'of Squib');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squibbing', 'p. pr. & vb. n.', 'of Squib');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squib', 'v. i.', 'To throw squibs; to utter sarcatic or severe reflections;\n   to contend in petty dispute; as, to squib a little debate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squid', 'n.', 'Any one of numerous species of ten-armed cephalopods having\n   a long, tapered body, and a caudal fin on each side; especially, any\n   species of Loligo, Ommastrephes, and related genera. See Calamary,\n   Decacerata, Dibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squid', 'n.', 'A fishhook with a piece of bright lead, bone, or other\n   substance, fastened on its shank to imitate a squid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squier', 'n.', 'A square. See 1st Squire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squierie', 'n.', 'Alt. of Squiery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squiery', 'n.', 'A company of squires; the whole body of squires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squiggle', 'v. i.', 'To shake and wash a fluid about in the mouth with the\n   lips closed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squiggle', 'v. i.', 'To move about like an eel; to squirm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squilgee', 'n.', 'Formerly, a small swab for drying a vessels deck; now, a\n   kind of scraper having a blade or edge of rubber or of leather, -- used\n   for removing superfluous, water or other liquids, as from a vessels\n   deck after washing, from window panes, photographers plates, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squill', 'n.', 'A European bulbous liliaceous plant (Urginea, formerly\n   Scilla, maritima), of acrid, expectorant, diuretic, and emetic\n   properties used in medicine. Called also sea onion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squill', 'n.', 'Any bulbous plant of the genus Scilla; as, the bluebell\n   squill (S. mutans).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squill', 'n.', 'A squilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squill', 'n.', 'A mantis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squillas', 'pl. ', 'of Squilla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squillae', 'pl. ', 'of Squilla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squilla', 'n.', 'Any one of numerous stomapod crustaceans of the genus\n   Squilla and allied genera. They make burrows in mud or beneath stones\n   on the seashore. Called also mantis shrimp. See Illust. under\n   Stomapoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squillitic', 'a.', 'Of or pertaining to squills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squinance', 'n.', 'Alt. of Squinancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squinancy', 'n.', 'The quinsy. See Quinsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squinancy', 'n.', 'A European perennial herb (Asperula cynanchica) with\n   narrowly linear whorled leaves; -- formerly thought to cure the quinsy.\n   Also called quincewort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squinch', 'n.', 'A small arch thrown across the corner of a square room to\n   support a superimposed mass, as where an octagonal spire or drum rests\n   upon a square tower; -- called also sconce, and sconcheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squinsy', 'n.', 'See Quinsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squint', 'a.', 'Looking obliquely. Specifically (Med.), not having the\n   optic axes coincident; -- said of the eyes. See Squint, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squint', 'n.', 'Fig.: Looking askance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squinted', 'imp. & p. p.', 'of Squint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squinting', 'p. pr. & vb. n.', 'of Squint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squint', 'v. i.', 'To see or look obliquely, asquint, or awry, or with a\n   furtive glance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squint', 'v. i.', 'To have the axes of the eyes not coincident; -- to be\n   cross-eyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squint', 'v. i.', 'To deviate from a true line; to run obliquely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squint', 'v. t.', 'To turn to an oblique position; to direct obliquely; as,\n   to squint an eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squint', 'v. t.', 'To cause to look with noncoincident optic axes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squint', 'n.', 'The act or habit of squinting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squint', 'n.', 'A want of coincidence of the axes of the eyes; strabismus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squint', 'n.', 'Same as Hagioscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squinter', 'n.', 'One who squints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squint-eye', 'n.', 'An eye that squints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squint-eyed', 'a.', 'Having eyes that quint; having eyes with axes not\n   coincident; cross-eyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squint-eyed', 'a.', 'Looking obliquely, or asquint; malignant; as,\n   squint-eyed praise; squint-eyed jealousy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squintifego', 'a.', 'Squinting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squinting', '', 'a. & n. from Squint, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squiny', 'v. i.', 'To squint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squinzey', 'n.', 'See Quinsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Souir', 'v. t.', 'To throw with a jerk; to throw edge foremost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squiralty', 'n.', 'Same as Squirarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squirarch', 'n.', 'One who belongs to the squirarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squirarchy', 'n.', 'The gentlemen, or gentry, of a country, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squire', 'n.', 'A square; a measure; a rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squire', 'n.', 'A shield-bearer or armor-bearer who attended a knight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squire', 'n.', 'A title of dignity next in degree below knight, and above\n   gentleman. See Esquire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squire', 'n.', 'A male attendant on a great personage; also (Colloq.), a\n   devoted attendant or follower of a lady; a beau.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squire', 'n.', 'A title of office and courtesy. See under Esquire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'squired', 'imp. & p. p.', 'of Squire');
  INSERT INTO words (word, word_type, description) VALUES  (    'squiring', 'p. pr. & vb. n.', 'of Squire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squire', 'v. t.', 'To attend as a squire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squire', 'v. t.', 'To attend as a beau, or gallant, for aid and protection;\n   as, to squire a lady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squireen', 'n.', 'One who is half squire and half farmer; -- used\n   humorously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squirehood', 'n.', 'The rank or state of a squire; squireship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'squireling', 'n.', 'A petty squire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squirely', 'a. & adv.', 'Becoming a squire; like a squire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'squireship', 'n.', 'Squirehood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squirmed', 'imp. & p. p.', 'of Squirm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squirming', 'p. pr. & vb. n.', 'of Squirm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squirm', 'v. i.', 'To twist about briskly with contor/ions like an eel or a\n   worm; to wriggle; to writhe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squirr', 'v. t.', 'See Squir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squirrel', 'v. i.', 'Any one of numerous species of small rodents belonging\n   to the genus Sciurus and several allied genera of the family Sciuridae.\n   Squirrels generally have a bushy tail, large erect ears, and strong\n   hind legs. They are commonly arboreal in their habits, but many species\n   live in burrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squirrel', 'v. i.', 'One of the small rollers of a carding machine which\n   work with the large cylinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squirted', 'imp. & p. p.', 'of Squirt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squirting', 'p. pr. & vb. n.', 'of Squirt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squirt', 'v. t.', 'To drive or eject in a stream out of a narrow pipe or\n   orifice; as, to squirt water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squirt', 'v. i.', 'To be thrown out, or ejected, in a rapid stream, from a\n   narrow orifice; -- said of liquids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squirt', 'v. i.', 'Hence, to throw out or utter words rapidly; to prate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squirt', 'n.', 'An instrument out of which a liquid is ejected in a small\n   stream with force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squirt', 'n.', 'A small, quick stream; a jet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squirter', 'n.', 'One who, or that which, squirts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squiry', 'n.', 'The body of squires, collectively considered; squirarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squitch grass', '', 'Quitch grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Squitee', 'n.', 'The squeteague; -- called also squit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stabbed', 'imp. & p. p.', 'of Stab');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stabbing', 'p. pr. & vb. n.', 'of Stab');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stab', 'v. t.', 'To pierce with a pointed weapon; to wound or kill by the\n   thrust of a pointed instrument; as, to stab a man with a dagger; also,\n   to thrust; as, to stab a dagger into a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stab', 'v. t.', 'Fig.: To injure secretly or by malicious falsehood or\n   slander; as, to stab a persons reputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stab', 'v. i.', 'To give a wound with a pointed weapon; to pierce; to\n   thrust with a pointed weapon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stab', 'v. i.', 'To wound or pain, as if with a pointed weapon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stab', 'n.', 'The thrust of a pointed weapon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stab', 'n.', 'A wound with a sharp-pointed weapon; as, to fall by the stab\n   an assassin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stab', 'n.', 'Fig.: An injury inflicted covertly or suddenly; as, a stab\n   given to character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stabat Mater', '', 'A celebrated Latin hymn, beginning with these words,\n   commemorating the sorrows of the mother of our Lord at the foot of the\n   cross. It is read in the Mass of the Sorrows of the Virgin Mary, and is\n   sung by Catholics when making \"the way of the cross\" (Via Crucis). See\n   Station, 7 (c).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stabber', 'n.', 'One who, or that which, stabs; a privy murderer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stabber', 'n.', 'A small marline spike; a pricker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stabbingly', 'adv.', 'By stabbing; with intent to injure covertly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stabiliment', 'a.', 'The act of making firm; firm support; establishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stabilitate', 'v. t.', 'To make stable; to establish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stability', 'a.', 'The state or quality of being stable, or firm;\n   steadiness; firmness; strength to stand without being moved or\n   overthrown; as, the stability of a structure; the stability of a throne\n   or a constitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stability', 'a.', 'Steadiness or firmness of character, firmness of\n   resolution or purpose; the quality opposite to fickleness,\n   irresolution, or inconstancy; constancy; steadfastness; as, a man of\n   little stability, or of unusual stability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stability', 'a.', 'Fixedness; -- as opposed to fluidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stable', 'v. i.', 'Firmly established; not easily moved, shaken, or\n   overthrown; fixed; as, a stable government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stable', 'v. i.', 'Steady in purpose; constant; firm in resolution; not\n   easily diverted from a purpose; not fickle or wavering; as, a man of\n   stable character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stable', 'v. i.', 'Durable; not subject to overthrow or change; firm; as, a\n   stable foundation; a stable position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stable', 'v. t.', 'To fix; to establish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stable', 'v. i.', 'A house, shed, or building, for beasts to lodge and feed\n   in; esp., a building or apartment with stalls, for horses; as, a horse\n   stable; a cow stable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stabled', 'imp. & p. p.', 'of Stable');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stabling', 'p. pr. & vb. n.', 'of Stable');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stable', 'v. t.', 'To put or keep in a stable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stable', 'v. i.', 'To dwell or lodge in a stable; to dwell in an inclosed\n   place; to kennel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stableboy', 'n.', 'Alt. of Stableman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stableman', 'n.', 'A boy or man who attends in a stable; a groom; a\n   hostler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stableness', 'n.', 'The quality or state of being stable, or firmly\n   established; stability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stabler', 'n.', 'A stable keeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stable stand', '', 'The position of a man who is found at his standing in\n   the forest, with a crossbow or a longbow bent, ready to shoot at a\n   deer, or close by a tree with greyhounds in a leash ready to slip; --\n   one of the four presumptions that a man intends stealing the kings\n   deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stabling', 'n.', 'The act or practice of keeping horses and cattle in a\n   stable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stabling', 'n.', 'A building, shed, or room for horses and cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stablish', 'v. t.', 'To settle permanently in a state; to make firm; to\n   establish; to fix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stablishment', 'n.', 'Establishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stably', 'adv.', 'In a stable manner; firmly; fixedly; steadily; as, a\n   government stably settled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stabulation', 'n.', 'The act of stabling or housing beasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stabulation', 'n.', 'A place for lodging beasts; a stable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staccato', 'a.', 'Disconnected; separated; distinct; -- a direction to\n   perform the notes of a passage in a short, distinct, and pointed\n   manner. It is opposed to legato, and often indicated by heavy accents\n   written over or under the notes, or by dots when the performance is to\n   be less distinct and emphatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staccato', 'a.', 'Expressed in a brief, pointed manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stack', 'a.', 'A large pile of hay, grain, straw, or the like, usually of a\n   nearly conical form, but sometimes rectangular or oblong, contracted at\n   the top to a point or ridge, and sometimes covered with thatch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stack', 'a.', 'A pile of poles or wood, indefinite in quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stack', 'a.', 'A pile of wood containing 108 cubic feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stack', 'a.', 'A number of flues embodied in one structure, rising above\n   the roof. Hence:');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stack', 'a.', 'Any single insulated and prominent structure, or upright\n   pipe, which affords a conduit for smoke; as, the brick smokestack of a\n   factory; the smokestack of a steam vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stack', 'a.', 'A section of memory in a computer used for temporary storage\n   of data, in which the last datum stored is the first retrieved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stack', 'a.', 'A data structure within random-access memory used to\n   simulate a hardware stack; as, a push-down stack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stacked', 'imp. & p. p.', 'of Stack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stacking', 'p. pr. & vb. n.', 'of Stack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stack', 'n.', 'To lay in a conical or other pile; to make into a large\n   pile; as, to stack hay, cornstalks, or grain; to stack or place wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stackage', 'n.', 'Hay, gray, or the like, in stacks; things stacked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stackage', 'n.', 'A tax on things stacked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stacket', 'n.', 'A stockade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stack-guard', 'n.', 'A covering or protection, as a canvas, for a stack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stacking', '', 'a. & n. from Stack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stackstand', 'n.', 'A staging for supporting a stack of hay or grain; a\n   rickstand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stackyard', 'n.', 'A yard or inclosure for stacks of hay or grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stacte', 'n.', 'One of the sweet spices used by the ancient Jews in the\n   preparation of incense. It was perhaps an oil or other form of myrrh or\n   cinnamon, or a kind of storax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staddle', 'v. i.', 'Anything which serves for support; a staff; a prop; a\n   crutch; a cane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staddle', 'v. i.', 'The frame of a stack of hay or grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staddle', 'v. i.', 'A row of dried or drying hay, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staddle', 'v. i.', 'A small tree of any kind, especially a forest tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staddle', 'v. t.', 'To leave the staddles, or saplings, of, as a wood when\n   it is cut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staddle', 'v. t.', 'To form into staddles, as hay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stade', 'n.', 'A stadium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stade', 'n.', 'A landing place or wharf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stadimeter', 'n.', 'A horizontal graduated bar mounted on a staff, used as\n   a stadium, or telemeter, for measuring distances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stadia', 'pl. ', 'of Stadium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stadium', 'n.', 'A Greek measure of length, being the chief one used for\n   itinerary distances, also adopted by the Romans for nautical and\n   astronomical measurements. It was equal to 600 Greek or 625 Roman feet,\n   or 125 Roman paces, or to 606 feet 9 inches English. This was also\n   called the Olympic stadium, as being the exact length of the foot-race\n   course at Olympia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stadium', 'n.', 'Hence, a race course; especially, the Olympic course for\n   foot races.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stadium', 'n.', 'A kind of telemeter for measuring the distance of an\n   object of known dimensions, by observing the angle it subtends;\n   especially (Surveying), a graduated rod used to measure the distance of\n   the place where it stands from an instrument having a telescope, by\n   observing the number of the graduations of the rod that are seen\n   between certain parallel wires (stadia wires) in the field of view of\n   the telescope; -- also called stadia, and stadia rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stadtholder', 'n.', 'Formerly, the chief magistrate of the United Provinces\n   of Holland; also, the governor or lieutenant governor of a province.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stadtholderate', 'n.', 'Alt. of Stadtholdership');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stadtholdership', 'n.', 'The office or position of a stadtholder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stafette', 'n.', 'An estafet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staves', 'pl. ', 'of Staff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staffs', 'pl. ', 'of Staff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staffs', 'pl. ', 'of Staff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staff', 'n.', 'A long piece of wood; a stick; the long handle of an\n   instrument or weapon; a pole or srick, used for many purposes; as, a\n   surveyors staff; the staff of a spear or pike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staff', 'n.', 'A stick carried in the hand for support or defense by a\n   person walking; hence, a support; that which props or upholds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staff', 'n.', 'A pole, stick, or wand borne as an ensign of authority; a\n   badge of office; as, a constables staff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staff', 'n.', 'A pole upon which a flag is supported and displayed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staff', 'n.', 'The round of a ladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staff', 'n.', 'A series of verses so disposed that, when it is concluded,\n   the same order begins again; a stanza; a stave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staff', 'n.', 'The five lines and the spaces on which music is written; --\n   formerly called stave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staff', 'n.', 'An arbor, as of a wheel or a pinion of a watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staff', 'n.', 'The grooved director for the gorget, or knife, used in\n   cutting for stone in the bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staff', 'n.', 'An establishment of officers in various departments attached\n   to an army, to a section of an army, or to the commander of an army.\n   The generals staff consists of those officers about his person who are\n   employed in carrying his commands into execution. See Etat Major.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staff', 'n.', 'Hence: A body of assistants serving to carry into effect the\n   plans of a superintendant or manager; as, the staff of a newspaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staffier', 'n.', 'An attendant bearing a staff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staffish', 'a.', 'Stiff; harsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staffmen', 'pl. ', 'of Staffman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staffman', 'n.', 'A workman employed in silk throwing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stag', 'n.', 'The adult male of the red deer (Cervus elaphus), a large\n   European species closely related to the American elk, or wapiti.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stag', 'n.', 'The male of certain other species of large deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stag', 'n.', 'A colt, or filly; also, a romping girl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stag', 'n.', 'A castrated bull; -- called also bull stag, and bull seg. See\n   the Note under Ox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stag', 'n.', 'An outside irregular dealer in stocks, who is not a member of\n   the exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stag', 'n.', 'One who applies for the allotment of shares in new projects,\n   with a view to sell immediately at a premium, and not to hold the\n   stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stag', 'n.', 'The European wren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stag', 'v. i.', 'To act as a \"stag\", or irregular dealer in stocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stag', 'v. t.', 'To watch; to dog, or keep track of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stage', 'n.', 'A floor or story of a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stage', 'n.', 'An elevated platform on which an orator may speak, a play be\n   performed, an exhibition be presented, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stage', 'n.', 'A floor elevated for the convenience of mechanical work, or\n   the like; a scaffold; a staging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stage', 'n.', 'A platform, often floating, serving as a kind of wharf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stage', 'n.', 'The floor for scenic performances; hence, the theater; the\n   playhouse; hence, also, the profession of representing dramatic\n   compositions; the drama, as acted or exhibited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stage', 'n.', 'A place where anything is publicly exhibited; the scene of\n   any noted action or carrer; the spot where any remarkable affair\n   occurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stage', 'n.', 'The platform of a microscope, upon which an object is placed\n   to be viewed. See Illust. of Microscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stage', 'n.', 'A place of rest on a regularly traveled road; a stage house;\n   a station; a place appointed for a relay of horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stage', 'n.', 'A degree of advancement in a journey; one of several\n   portions into which a road or course is marked off; the distance\n   between two places of rest on a road; as, a stage of ten miles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stage', 'n.', 'A degree of advancement in any pursuit, or of progress\n   toward an end or result.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stage', 'n.', 'A large vehicle running from station to station for the\n   accomodation of the public; a stagecoach; an omnibus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stage', 'n.', 'One of several marked phases or periods in the development\n   and growth of many animals and plants; as, the larval stage; pupa\n   stage; zoea stage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stage', 'v. t.', 'To exhibit upon a stage, or as upon a stage; to display\n   publicly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagecoach', 'n.', 'A coach that runs regularly from one stage, station, or\n   place to another, for the conveyance of passengers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagecoachmen', 'pl. ', 'of Stagecoachman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagecoachman', 'n.', 'One who drives a stagecoach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagehouse', 'n.', 'A house where a stage regularly stops for passengers or\n   a relay of horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagely', 'a.', 'Pertaining to a stage; becoming the theater; theatrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stageplay', 'n.', 'A dramatic or theatrical entertainment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stageplayer', 'n.', 'An actor on the stage; one whose occupation is to\n   represent characters on the stage; as, Garrick was a celebrated\n   stageplayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stager', 'n.', 'A player.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stager', 'n.', 'One who has long acted on the stage of life; a\n   practitioner; a person of experience, or of skill derived from long\n   experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stager', 'n.', 'A horse used in drawing a stage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagery', 'n.', 'Exhibition on the stage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stage-struck', 'a.', 'Fascinated by the stage; seized by a passionate\n   desire to become an actor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stag-evil', 'n.', 'A kind of palsy affecting the jaw of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staggard', 'n.', 'The male red deer when four years old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staggered', 'imp. & p. p.', 'of Stagger');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staggering', 'p. pr. & vb. n.', 'of Stagger');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagger', 'n.', 'To move to one side and the other, as if about to fall, in\n   standing or walking; not to stand or walk with steadiness; to sway; to\n   reel or totter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagger', 'n.', 'To cease to stand firm; to begin to give way; to fail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagger', 'n.', 'To begin to doubt and waver in purposes; to become less\n   confident or determined; to hesitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagger', 'v. t.', 'To cause to reel or totter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagger', 'v. t.', 'To cause to doubt and waver; to make to hesitate; to\n   make less steady or confident; to shock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagger', 'v. t.', 'To arrange (a series of parts) on each side of a median\n   line alternately, as the spokes of a wheel or the rivets of a boiler\n   seam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagger', 'n.', 'An unsteady movement of the body in walking or standing,\n   as if one were about to fall; a reeling motion; vertigo; -- often in\n   the plural; as, the stagger of a drunken man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagger', 'n.', 'A disease of horses and other animals, attended by\n   reeling, unsteady gait or sudden falling; as, parasitic staggers;\n   appopletic or sleepy staggers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagger', 'n.', 'Bewilderment; perplexity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staggerbush', 'n.', 'An American shrub (Andromeda Mariana) having clusters\n   of nodding white flowers. It grows in low, sandy places, and is said to\n   poison lambs and calves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staggeringly', 'adv.', 'In a staggering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staggerwort', 'n.', 'A kind of ragwort (Senecio Jacobaea).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stag-horn coral', '', 'Alt. of Stag-horn fern');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stag-horn fern', '', 'See under Stag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stag-horned', 'a.', 'Having the mandibles large and palmate, or branched\n   somewhat like the antlers of a stag; -- said of certain beetles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staghound', 'n.', 'A large and powerful hound formerly used in hunting the\n   stag, the wolf, and other large animals. The breed is nearly extinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staging', 'n.', 'A structure of posts and boards for supporting workmen,\n   etc., as in building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staging', 'n.', 'The business of running stagecoaches; also, the act of\n   journeying in stagecoaches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagirite', 'n.', 'A native of, or resident in, Stagira, in ancient\n   Macedonia; especially, Aristotle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagnancy', 'n.', 'State of being stagnant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagnant', 'a.', 'That stagnates; not flowing; not running in a current or\n   steam; motionless; hence, impure or foul from want of motion; as, a\n   stagnant lake or pond; stagnant blood in the veins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagnant', 'a.', 'Not active or brisk; dull; as, business in stagnant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagnantly', 'adv.', 'In a stagnant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagnated', 'imp. & p. p.', 'of Stagnate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagnating', 'p. pr. & vb. n.', 'of Stagnate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagnate', 'v. t.', 'To cease to flow; to be motionless; as, blood\n   stagnates in the veins of an animal; hence, to become impure or foul by\n   want of motion; as, air stagnates in a close room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagnate', 'v. t.', 'To cease to be brisk or active; to become dull or\n   inactive; as, commerce stagnates; business stagnates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagnate', 'a.', 'Stagnant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagnation', 'n.', 'The condition of being stagnant; cessation of flowing\n   or circulation, as of a fluid; the state of being motionless; as, the\n   stagnation of the blood; the stagnation of water or air; the stagnation\n   of vapors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagnation', 'n.', 'The cessation of action, or of brisk action; the state\n   of being dull; as, the stagnation of business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stagworm', 'n.', 'The larve of any species of botfly which is parasitic\n   upon the stag, as /strus, or Hypoderma, actaeon, which burrows beneath\n   the skin, and Cephalomyia auribarbis, which lives in the nostrils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stahlian', 'a.', 'Pertaining to, or taught by, Stahl, a German physician\n   and chemist of the 17th century; as, the Stahlian theory of phlogiston.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stahlian', 'n.', 'A believer in, or advocate of, Stahlism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stahlism', 'n.', 'Alt. of Stahlianism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stahlianism', 'n.', 'The Stahlian theoru, that every vital action is\n   function or operation of the soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stail', '', 'imp. & p. p. of Stay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staid', 'a.', 'Sober; grave; steady; sedate; composed; regular; not wild,\n   volatile, or fanciful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staidly', 'adv.', 'In a staid manner, sedately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staidness', 'n.', 'The quality or state of being staid; seriousness;\n   steadiness; sedateness; regularity; -- the opposite of wildness, or\n   levity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stail', 'n.', 'A handle, as of a mop; a stale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stained', 'imp. & p. p.', 'of Stain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staining', 'p. pr. & vb. n.', 'of Stain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stain', 'v. t.', 'To discolor by the application of foreign matter; to make\n   foul; to spot; as, to stain the hand with dye; armor stained with\n   blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stain', 'v. t.', 'To color, as wood, glass, paper, cloth, or the like, by\n   processess affecting, chemically or otherwise, the material itself; to\n   tinge with a color or colors combining with, or penetrating, the\n   substance; to dye; as, to stain wood with acids, colored washes, paint\n   rubbed in, etc.; to stain glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stain', 'v. t.', 'To spot with guilt or infamy; to bring reproach on; to\n   blot; to soil; to tarnish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stain', 'v. t.', 'To cause to seem inferior or soiled by comparison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stain', 'v. i.', 'To give or receive a stain; to grow dim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stain', 'n.', 'A discoloration by foreign matter; a spot; as, a stain on a\n   garment or cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stain', 'n.', 'A natural spot of a color different from the gound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stain', 'n.', 'Taint of guilt; tarnish; disgrace; reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stain', 'n.', 'Cause of reproach; shame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stain', 'n.', 'A tincture; a tinge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stainer', 'n.', 'One who stains or tarnishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stainer', 'n.', 'A workman who stains; as, a stainer of wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stainless', 'a.', 'Free from stain; immaculate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stainlessly', 'adv.', 'In a stainless manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stair', 'n.', 'One step of a series for ascending or descending to a\n   different level; -- commonly applied to those within a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stair', 'n.', 'A series of steps, as for passing from one story of a house\n   to another; -- commonly used in the plural; but originally used in the\n   singular only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staircase', 'n.', 'A flight of stairs with their supporting framework,\n   casing, balusters, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stairhead', 'n.', 'The head or top of a staircase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stairway', 'n.', 'A flight of stairs or steps; a staircase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staith', 'n.', 'A landing place; an elevated staging upon a wharf for\n   discharging coal, etc., as from railway cars, into vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staithman', 'n.', 'A man employed in weighing and shipping at a staith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stake', 'v. t.', 'A piece of wood, usually long and slender, pointed at one\n   end so as to be easily driven into the ground as a support or stay; as,\n   a stake to support vines, fences, hedges, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stake', 'v. t.', 'A stick inserted upright in a lop, eye, or mortise, at\n   the side or end of a cart, a flat car, or the like, to prevent goods\n   from falling off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stake', 'v. t.', 'The piece of timber to which a martyr was affixed to be\n   burned; hence, martyrdom by fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stake', 'v. t.', 'A small anvil usually furnished with a tang to enter a\n   hole in a bench top, -- used by tinsmiths, blacksmiths, etc., for light\n   work, punching upon, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stake', 'v. t.', 'That which is laid down as a wager; that which is staked\n   or hazarded; a pledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staked', 'imp. & p. p.', 'of Stake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staking', 'p. pr. & vb. n.', 'of Stake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stake', 'v. t.', 'To fasten, support, or defend with stakes; as, to stake\n   vines or plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stake', 'v. t.', 'To mark the limits of by stakes; -- with out; as, to\n   stake out land; to stake out a new road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stake', 'v. t.', 'To put at hazard upon the issue of competition, or upon a\n   future contingency; to wager; to pledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stake', 'v. t.', 'To pierce or wound with a stake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stake-driver', 'n.', 'The common American bittern (Botaurus lentiginosus);\n   -- so called because one of its notes resembles the sound made in\n   driving a stake into the mud. Called also meadow hen, and Indian hen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stakehead', 'n.', 'A horizontal bar on a stake, used for supporting the\n   yarns which are kept apart by pins in the bar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stakeholder', 'n.', 'The holder of a stake; one with whom the bets are\n   deposited when a wager is laid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staktometer', 'n.', 'A drop measurer; a glass tube tapering to a small\n   orifice at the point, and having a bulb in the middle, used for finding\n   the number of drops in equal quantities of different liquids. See\n   Pipette.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stal', 'imp.', 'Stole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalactic', 'a.', 'Alt. of Stalactical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalactical', 'a.', 'Stalactic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalactoform', 'a.', 'Like a stalactite; resembling a stalactite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalactites', 'pl. ', 'of Stalactite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalactite', 'n.', 'A pendent cone or cylinder of calcium carbonate\n   resembling an icicle in form and mode of attachment. Stalactites are\n   found depending from the roof or sides of caverns, and are produced by\n   deposition from waters which have percolated through, and partially\n   dissolved, the overlying limestone rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalactite', 'n.', 'In an extended sense, any mineral or rock of similar\n   form and origin; as, a stalactite of lava.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalactites', 'n.', 'A stalactite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalactitic', 'a.', 'Alt. of Stalactitical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalactitical', 'a.', 'Of or pertaining to a stalactite; having the form or\n   characters of a stalactite; stalactic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalactitiform', 'a.', 'Having the form of a stalactite; stalactiform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalagmite', 'n.', 'A deposit more or less resembling an inverted\n   stalactite, formed by calcareous water dropping on the floors of\n   caverns; hence, a similar deposit of other material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalagmitic', 'a.', 'Alt. of Stalagmitical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalagmitical', 'a.', 'Having the form or structure of stalagmites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalder', 'n.', 'A wooden frame to set casks on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stale', 'n.', 'The stock or handle of anything; as, the stale of a rake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stale', 'v. i.', 'Vapid or tasteless from age; having lost its life,\n   spirit, and flavor, from being long kept; as, stale beer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stale', 'v. i.', 'Not new; not freshly made; as, stele bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stale', 'v. i.', 'Having lost the life or graces of youth; worn out;\n   decayed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stale', 'v. i.', 'Worn out by use or familiarity; having lost its novelty\n   and power of pleasing; trite; common.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staled', 'imp. & p. p.', 'of Stale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staling', 'p. pr. & vb. n.', 'of Stale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stale', 'v. t.', 'To make vapid or tasteless; to destroy the life, beauty,\n   or use of; to wear out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stale', 'a.', 'To make water; to discharge urine; -- said especially of\n   horses and cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stale', 'v. i.', 'That which is stale or worn out by long keeping, or by\n   use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stale', 'v. i.', 'A prostitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stale', 'v. i.', 'Urine, esp. that of beasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stale', 'v. t.', 'Something set, or offered to view, as an allurement to\n   draw others to any place or purpose; a decoy; a stool pigeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stale', 'v. t.', 'A stalking-horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stale', 'v. t.', 'A stalemate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stale', 'v. t.', 'A laughingstock; a dupe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalely', 'adv.', 'In a state stale manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalely', 'adv.', 'Of old; long since.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalemate', 'n.', 'The position of the king when he can not move without\n   being placed on check and there is no other piece which can be moved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalemate', 'v. t.', 'To subject to a stalemate; hence, to bring to a\n   stand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staleness', 'n.', 'The quality or state of being stale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalk', 'n.', 'The stem or main axis of a plant; as, a stalk of wheat, rye,\n   or oats; the stalks of maize or hemp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalk', 'n.', 'The petiole, pedicel, or peduncle, of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalk', 'n.', 'That which resembes the stalk of a plant, as the stem of a\n   quill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalk', 'n.', 'An ornament in the Corinthian capital resembling the stalk\n   of a plant, from which the volutes and helices spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalk', 'n.', 'One of the two upright pieces of a ladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalk', 'n.', 'A stem or peduncle, as of certain barnacles and crinoids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalk', 'n.', 'The narrow basal portion of the abdomen of a hymenopterous\n   insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalk', 'n.', 'The peduncle of the eyes of decapod crustaceans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalk', 'n.', 'An iron bar with projections inserted in a core to\n   strengthen it; a core arbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalked', 'imp. & p. p.', 'of Stalk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalking', 'p. pr. & vb. n.', 'of Stalk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalk', 'v. i.', 'To walk slowly and cautiously; to walk in a stealthy,\n   noiseless manner; -- sometimes used with a reflexive pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalk', 'v. i.', 'To walk behind something as a screen, for the purpose of\n   approaching game; to proceed under clover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalk', 'v. i.', 'To walk with high and proud steps; usually implying the\n   affectation of dignity, and indicating dislike. The word is used,\n   however, especially by the poets, to express dignity of step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalk', 'v. t.', 'To approach under cover of a screen, or by stealth, for\n   the purpose of killing, as game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalk', 'n.', 'A high, proud, stately step or walk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalked', 'a.', 'Having a stalk or stem; borne upon a stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalker', 'n.', 'One who stalks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalker', 'n.', 'A kind of fishing net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalk-eyed', 'a.', 'Having the eyes raised on a stalk, or peduncle; --\n   opposed to sessile-eyed. Said especially of podophthalmous crustaceans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalking-horse', 'n.', 'A horse, or a figure resembling a horse, behind\n   which a hunter conceals himself from the game he is aiming to kill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalking-horse', 'n.', 'Fig.: Something used to cover up a secret project;\n   a mask; a pretense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalkless', 'a.', 'Having no stalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalky', 'a.', 'Hard as a stalk; resembling a stalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall', 'v. i.', 'A stand; a station; a fixed spot; hence, the stand or\n   place where a horse or an ox kept and fed; the division of a stable, or\n   the compartment, for one horse, ox, or other animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall', 'v. i.', 'A stable; a place for cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall', 'v. i.', 'A small apartment or shed in which merchandise is exposed\n   for sale; as, a butchers stall; a bookstall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall', 'v. i.', 'A bench or table on which small articles of merchandise\n   are exposed for sale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall', 'v. i.', 'A seat in the choir of a church, for one of the\n   officiating clergy. It is inclosed, either wholly or partially, at the\n   back and sides. The stalls are frequently very rich, with canopies and\n   elaborate carving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall', 'v. i.', 'In the theater, a seat with arms or otherwise partly\n   inclosed, as distinguished from the benches, sofas, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall', 'v. i.', 'The space left by excavation between pillars. See Post\n   and stall, under Post.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalled', 'imp. & p. p.', 'of Stall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalling', 'p. pr. & vb. n.', 'of Stall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall', 'v. t.', 'To put into a stall or stable; to keep in a stall or\n   stalls; as, to stall an ox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall', 'v. t.', 'To fatten; as, to stall cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall', 'v. t.', 'To place in an office with the customary formalities; to\n   install.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall', 'v. t.', 'To plunge into mire or snow so as not to be able to get\n   on; to set; to fix; as, to stall a cart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall', 'v. t.', 'To forestall; to anticipitate. Having');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall', 'v. t.', 'To keep close; to keep secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall', 'v. i.', 'To live in, or as in, a stall; to dwell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall', 'v. i.', 'To kennel, as dogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall', 'v. i.', 'To be set, as in mire or snow; to stick fast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall', 'v. i.', 'To be tired of eating, as cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stallage', 'n.', 'The right of erecting a stalls in fairs; rent paid for a\n   stall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stallage', 'n.', 'Dung of cattle or horses, mixed with straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stallation', 'n.', 'Installation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalled', 'a.', 'Put or kept in a stall; hence, fatted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staller', 'n.', 'A standard bearer. obtaining');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall-fed', 'imp. & p. p.', 'of Stall-feed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall-feeding', 'p. pr. & vb. n.', 'of Stall-feed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stall-feed', 'v. t.', 'To feed and fatten in a stall or on dry fodder; as,\n   to stall-feed an ox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalling', 'n.', 'Stabling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stallion', 'n.', 'A male horse not castrated; a male horse kept for\n   breeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stallmen', 'pl. ', 'of Stallman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stallman', 'n.', 'One who keeps a stall for the sale of merchandise,\n   especially books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stallon', 'n.', 'A slip from a plant; a scion; a cutting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalwart', 'a.', 'Alt. of Stalworth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalworth', 'a.', 'Brave; bold; strong; redoubted; daring; vehement;\n   violent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalwartly', 'adv.', 'In a stalwart manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalwartness', 'n.', 'The quality of being stalwart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalworthhood', 'n.', 'Alt. of Stalworthness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stalworthness', 'n.', 'The quality or state of being stalworth;\n   stalwartness; boldness; daring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamens', 'pl. ', 'of Stamen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamina', 'pl. ', 'of Stamen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamen', 'n.', 'A thread; especially, a warp thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamen', 'n.', 'The male organ of flowers for secreting and furnishing the\n   pollen or fecundating dust. It consists of the anther and filament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamened', 'a.', 'Furnished with stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamin', 'n.', 'A kind of woolen cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamina', 'n. pl.', 'See Stamen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamina', 'n. pl.', 'The fixed, firm part of a body, which supports it or\n   gives it strength and solidity; as, the bones are the stamina of animal\n   bodies; the ligneous parts of trees are the stamina which constitute\n   their strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamina', 'n. pl.', 'Whatever constitutes the principal strength or support\n   of anything; power of endurance; backbone; vigor; as, the stamina of a\n   constitution or of life; the stamina of a State.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staminal', 'a.', 'Of or pertaining to stamens or stamina; consisting in\n   stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staminate', 'a.', 'Furnished with stamens; producing stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staminate', 'a.', 'Having stamens, but lacking pistils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staminate', 'v. t.', 'To indue with stamina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamineal', 'a.', 'Alt. of Stamineous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamineous', 'a.', 'Consisting of stamens or threads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamineous', 'a.', 'Of or pertaining to the stamens; possessing stamens;\n   also, attached to the stamens; as, a stamineous nectary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staminiferous', 'a.', 'Bearing or having stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staminode', 'n.', 'A staminodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staminodia', 'pl. ', 'of Staminodium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staminodium', 'n.', 'An abortive stamen, or any organ modified from an\n   abortive stamen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stammel', 'n.', 'A large, clumsy horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stammel', 'n.', 'A kind of woolen cloth formerly in use. It seems to have\n   been often of a red color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stammel', 'n.', 'A red dye, used in England in the 15th and 16th centuries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stammel', 'a.', 'Of the color of stammel; having a red color, thought\n   inferior to scarlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stammered', 'imp. & p. p.', 'of Stammer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stammering', 'p. pr. & vb. n.', 'of Stammer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stammer', 'v. i.', 'To make involuntary stops in uttering syllables or\n   words; to hesitate or falter in speaking; to speak with stops and\n   diffivulty; to stutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stammer', 'v. t.', 'To utter or pronounce with hesitation or imperfectly;\n   -- sometimes with out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stammer', 'n.', 'Defective utterance, or involuntary interruption of\n   utterance; a stutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stammerer', 'n.', 'One who stammers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stammering', 'a.', 'Apt to stammer; hesitating in speech; stuttering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stammering', 'n.', 'A disturbance in the formation of sounds. It is due\n   essentially to long-continued spasmodic contraction of the diaphragm,\n   by which expiration is preented, and hence it may be considered as a\n   spasmodic inspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamped', 'imp. & p. p.', 'of Stamp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamping', 'p. pr. & vb. n.', 'of Stamp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'v. i.', 'To strike beat, or press forcibly with the bottom of the\n   foot, or by thrusting the foot downward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'v. i.', 'To bring down (the foot) forcibly on the ground or floor;\n   as, he stamped his foot with rage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'v. i.', 'To crush; to pulverize; specifically (Metal.), to crush\n   by the blow of a heavy stamp, as ore in a mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'v. i.', 'To impress with some mark or figure; as, to stamp a plate\n   with arms or initials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'v. i.', 'Fig.: To impress; to imprint; to fix deeply; as, to stamp\n   virtuous principles on the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'v. i.', 'To cut out, bend, or indent, as paper, sheet metal, etc.,\n   into various forms, by a blow or suddenly applied pressure with a stamp\n   or die, etc.; to mint; to coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'v. i.', 'To put a stamp on, as for postage; as, to stamp a letter;\n   to stamp a legal document.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'v. i.', 'To strike; to beat; to crush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'v. i.', 'To strike the foot forcibly downward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'n.', 'The act of stamping, as with the foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'n.', 'The which stamps; any instrument for making impressions on\n   other bodies, as a die.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'n.', 'The mark made by stamping; a mark imprinted; an impression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'n.', 'that which is marked; a thing stamped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'v. t.', 'A picture cut in wood or metal, or made by impression; a\n   cut; a plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'v. t.', 'An offical mark set upon things chargeable with a duty or\n   tax to government, as evidence that the duty or tax is paid; as, the\n   stamp on a bill of exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'v. t.', 'Hence, a stamped or printed device, issued by the\n   government at a fixed price, and required by law to be affixed to, or\n   stamped on, certain papers, as evidence that the government dues are\n   paid; as, a postage stamp; a receipt stamp, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'v. t.', 'An instrument for cutting out, or shaping, materials, as\n   paper, leather, etc., by a downward pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'v. t.', 'A character or reputation, good or bad, fixed on anything\n   as if by an imprinted mark; current value; authority; as, these persons\n   have the stamp of dishonesty; the Scriptures bear the stamp of a divine\n   origin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'v. t.', 'Make; cast; form; character; as, a man of the same stamp,\n   or of a different stamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'v. t.', 'A kind of heavy hammer, or pestle, raised by water or\n   steam power, for beating ores to powder; anything like a pestle, used\n   for pounding or bathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'v. t.', 'A half-penny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamp', 'v. t.', 'Money, esp. paper money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stampede', 'v. t.', 'A wild, headlong scamper, or running away, of a number\n   of animals; usually caused by fright; hence, any sudden flight or\n   dispersion, as of a crowd or an army in consequence of a panic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stampede', 'v. i.', 'To run away in a panic; -- said droves of cattle,\n   horses, etc., also of armies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stampede', 'v. t.', 'To disperse by causing sudden fright, as a herd or\n   drove of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamper', 'n.', 'One who stamps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamper', 'n.', 'An instrument for pounding or stamping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stamping', '', 'a. & n. from Stamp, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stance', 'n.', 'A stanza.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stance', 'n.', 'A station; a position; a site.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanched', 'imp. & p. p.', 'of Stanch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanching', 'p. pr. & vb. n.', 'of Stanch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanch', 'v. t.', 'To stop the flowing of, as blood; to check; also, to\n   stop the flowing of blood from; as, to stanch a wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanch', 'v. t.', 'To extinguish; to quench, as fire or thirst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanch', 'v. i.', 'To cease, as the flowing of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanch', 'n.', 'That which stanches or checks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanch', 'n.', 'A flood gate by which water is accumulated, for floating a\n   boat over a shallow part of a stream by its release.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanch', 'v. t.', 'Strong and tight; sound; firm; as, a stanch ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanch', 'v. t.', 'Firm in principle; constant and zealous; loyal; hearty;\n   steady; steadfast; as, a stanch churchman; a stanch friend or adherent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanch', 'v. t.', 'Close; secret; private.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanch', 'v. t.', 'To prop; to make stanch, or strong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanchel', 'n.', 'A stanchion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stancher', 'n.', 'One who, or that which, stanches, or stops, the flowing,\n   as of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanchion', 'n.', 'A prop or support; a piece of timber in the form of a\n   stake or post, used for a support or stay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanchion', 'n.', 'Any upright post or beam used as a support, as for the\n   deck, the quarter rails, awnings, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanchion', 'n.', 'A vertical bar for confining cattle in a stall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanchless', 'a.', 'Incapable of being stanched, or stopped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanchless', 'a.', 'Unquenchable; insatiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanchly', 'adv.', 'In a stanch manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanchness', 'n.', 'The quality or state of being stanch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stood', 'imp. & p. p.', 'of Stand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standing', 'p. pr. & vb. n.', 'of Stand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'n.', 'To be at rest in an erect position; to be fixed in an\n   upright or firm position');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'n.', 'To be supported on the feet, in an erect or nearly erect\n   position; -- opposed to lie, sit, kneel, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'n.', 'To continue upright in a certain locality, as a tree fixed\n   by the roots, or a building resting on its foundation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'n.', 'To occupy or hold a place; to have a situation; to be\n   situated or located; as, Paris stands on the Seine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'n.', 'To cease from progress; not to proceed; to stop; to pause;\n   to halt; to remain stationary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'n.', 'To remain without ruin or injury; to hold good against\n   tendencies to impair or injure; to be permanent; to endure; to last;\n   hence, to find endurance, strength, or resources.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'n.', 'To maintain ones ground; to be acquitted; not to fail or\n   yield; to be safe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'n.', 'To maintain an invincible or permanent attitude; to be\n   fixed, steady, or firm; to take a position in resistance or opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'n.', 'To adhere to fixed principles; to maintain moral rectitude;\n   to keep from falling into error or vice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'n.', 'To have or maintain a position, order, or rank; to be in a\n   particular relation; as, Christian charity, or love, stands first in\n   the rank of gifts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'n.', 'To be in some particular state; to have essence or being; to\n   be; to consist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'n.', 'To be consistent; to agree; to accord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'n.', 'To hold a course at sea; as, to stand from the shore; to\n   stand for the harbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'n.', 'To offer ones self, or to be offered, as a candidate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'n.', 'To stagnate; not to flow; to be motionless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'n.', 'To measure when erect on the feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'n.', 'To be or remain as it is; to continue in force; to have\n   efficacy or validity; to abide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'n.', 'To appear in court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'v. t.', 'To endure; to sustain; to bear; as, I can not stand the\n   cold or the heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'v. t.', 'To resist, without yielding or receding; to withstand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'v. t.', 'To abide by; to submit to; to suffer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'v. t.', 'To set upright; to cause to stand; as, to stand a book on\n   the shelf; to stand a man on his feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'v. t.', 'To be at the expense of; to pay for; as, to stand a\n   treat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'v. i.', 'The act of standing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'v. i.', 'A halt or stop for the purpose of defense, resistance, or\n   opposition; as, to come to, or to make, a stand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'v. i.', 'A place or post where one stands; a place where one may\n   stand while observing or waiting for something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'v. i.', 'A station in a city or town where carriages or wagons\n   stand for hire; as, a cab stand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'v. i.', 'A raised platform or station where a race or other\n   outdoor spectacle may be viewed; as, the judges or the grand stand at\n   a race course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'v. i.', 'A small table; also, something on or in which anything\n   may be laid, hung, or placed upright; as, a hat stand; an umbrella\n   stand; a music stand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'v. i.', 'A place where a witness stands to testify in court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'v. i.', 'The situation of a shop, store, hotel, etc.; as, a good,\n   bad, or convenient stand for business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'v. i.', 'Rank; post; station; standing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'v. i.', 'A state of perplexity or embarrassment; as, to be at a\n   stand what to do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'v. i.', 'A young tree, usually reserved when other trees are cut;\n   also, a tree growing or standing upon its own root, in distinction from\n   one produced from a scion set in a stock, either of the same or another\n   kind of tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand', 'v. i.', 'A weight of from two hundred and fifty to three hundred\n   pounds, -- used in weighing pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standage', 'n.', 'A reservior in which water accumulates at the bottom of a\n   mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standard', 'n.', 'A flag; colors; a banner; especially, a national or other\n   ensign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standard', 'n.', 'That which is established by authority as a rule for the\n   measure of quantity, extent, value, or quality; esp., the original\n   specimen weight or measure sanctioned by government, as the standard\n   pound, gallon, or yard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standard', 'n.', 'That which is established as a rule or model by\n   authority, custom, or general consent; criterion; test.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standard', 'n.', 'The proportion of weights of fine metal and alloy\n   established by authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standard', 'n.', 'A tree of natural size supported by its own stem, and not\n   dwarfed by grafting on the stock of a smaller species nor trained upon\n   a wall or trellis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standard', 'n.', 'The upper petal or banner of a papilionaceous corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standard', 'n.', 'An upright support, as one of the poles of a scaffold;\n   any upright in framing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standard', 'n.', 'An inverted knee timber placed upon the deck instead of\n   beneath it, with its vertical branch turned upward from that which lies\n   horizontally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standard', 'n.', 'The sheth of a plow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standard', 'n.', 'A large drinking cup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standard', 'a.', 'Being, affording, or according with, a standard for\n   comparison and judgment; as, standard time; standard weights and\n   measures; a standard authority as to nautical terms; standard gold or\n   silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standard', 'a.', 'Hence: Having a recognized and permanent value; as,\n   standard works in history; standard authors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standard', 'a.', 'Not supported by, or fastened to, a wall; as, standard\n   fruit trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standard', 'a.', 'Not of the dwarf kind; as, a standard pear tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standard-bred', 'a.', 'Bred in conformity to a standard. Specif., applied\n   to a registered trotting horse which comes up to the standard adopted\n   by the National Association of Trotting-horse Breeders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standardize', 'v. t.', 'To reduce to a normal standard; to calculate or\n   adjust the strength of, by means of, and for uses in, analysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standard-wing', 'n.', 'A curious paradise bird (Semioptera Wallacii) which\n   has two long special feathers standing erect on each wing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stand-by', 'n.', 'One who, or that which, stands by one in need; something\n   upon which one relies for constant use or in an emergency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standel', 'n.', 'A young tree, especially one reserved when others are cut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stander', 'n.', 'One who stands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stander', 'n.', 'Same as Standel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stander-by', 'n.', 'One who stands near; one who is present; a bystander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standergrass', 'n.', 'A plant (Orchis mascula); -- called also standerwort,\n   and long purple. See Long purple, under Long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standgale', 'n.', 'See Stannel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standing', 'a.', 'Remaining erect; not cut down; as, standing corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standing', 'a.', 'Not flowing; stagnant; as, standing water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standing', 'a.', 'Not transitory; not liable to fade or vanish; lasting;\n   as, a standing color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standing', 'a.', 'Established by law, custom, or the like; settled;\n   continually existing; permanent; not temporary; as, a standing army;\n   legislative bodies have standing rules of proceeding and standing\n   committees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standing', 'a.', 'Not movable; fixed; as, a standing bed (distinguished\n   from a trundle-bed).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standing', 'n.', 'The act of stopping, or coming to a stand; the state of\n   being erect upon the feet; stand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standing', 'n.', 'Maintenance of position; duration; duration or existence\n   in the same place or condition; continuance; as, a custom of long\n   standing; an officer of long standing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standing', 'n.', 'Place to stand in; station; stand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standing', 'n.', 'Condition in society; relative position; reputation;\n   rank; as, a man of good standing, or of high standing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standish', 'n.', 'A stand, or case, for pen and ink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standpipe', 'n.', 'A vertical pipe, open at the top, between a hydrant and\n   a reservoir, to equalize the flow of water; also, a large vertical\n   pipe, near a pumping engine, into which water is forced up, so as to\n   give it sufficient head to rise to the required level at a distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standpipe', 'n.', 'A supply pipe of sufficient elevation to enable the\n   water to flow into the boiler, notwithstanding the pressure of the\n   steam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standpoint', 'n.', 'A fixed point or station; a basis or fundamental\n   principle; a position from which objects or principles are viewed, and\n   according to which they are compared and judged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Standstill', 'n.', 'A standing without moving forward or backward; a stop;\n   a state or rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stane', 'n.', 'A stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stang', '', 'imp. of Sting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stang', 'n.', 'A long bar; a pole; a shaft; a stake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stang', 'n.', 'In land measure, a pole, rod, or perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stang', 'v. i.', 'To shoot with pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanhope', 'n.', 'A light two-wheeled, or sometimes four-wheeled, carriage,\n   without a top; -- so called from Lord Stanhope, for whom it was\n   contrived.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staniel', 'n.', 'See Stannel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanielry', 'n.', 'Hawking with staniels, -- a base kind of falconry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stank', 'a.', 'Weak; worn out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stank', 'v. i.', 'To sigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stank', 'imp.', 'Stunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stank', 'n.', 'Water retained by an embankment; a pool water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stank', 'n.', 'A dam or mound to stop water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stannary', 'a.', 'Of or pertaining to tin mines, or tin works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stannaries', 'pl. ', 'of Stannary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stannary', 'n.', 'A tin mine; tin works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stannate', 'n.', 'A salt of stannic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stannel', 'n.', 'The kestrel; -- called also standgale, standgall,\n   stanchel, stand hawk, stannel hawk, steingale, stonegall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stannic', 'a.', 'Of or pertaining to tin; derived from or containing tin;\n   specifically, designating those compounds in which the element has a\n   higher valence as contrasted with stannous compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanniferous', 'a.', 'Containing or affording tin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stannine', 'n.', 'Alt. of Stannite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stannite', 'n.', 'A mineral of a steel-gray or iron-black color; tin\n   pyrites. It is a sulphide of tin, copper, and iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanno-', '', 'A combining form (also used adjectively) denoting relation\n   to, or connection with, tin, or including tin as an ingredient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stannofluoride', 'n.', 'Any one of a series of double fluorides of tin\n   (stannum) and some other element.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stannoso-', 'a.', 'A combining form (also used adjectively) denoting\n   relation to, or connection with, certain stannnous compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stannotype', 'n.', 'A photograph taken upon a tin plate; a tintype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stannous', 'a.', 'Pertaining to, or containing, tin; specifically,\n   designating those compounds in which the element has a lower valence as\n   contrasted with stannic compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stannum', 'n.', 'The technical name of tin. See Tin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stannyel', 'n.', 'Alt. of Stanyel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanyel', 'n.', 'See Stannel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stant', '3d pers. sing. pres.', 'Alt. of Stont');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stont', '3d pers. sing. pres.', 'Stands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanzas', 'pl. ', 'of Stanza');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanza', 'n.', 'A number of lines or verses forming a division of a song or\n   poem, and agreeing in meter, rhyme, number of lines, etc., with other\n   divisions; a part of a poem, ordinarily containing every variation of\n   measure in that poem; a combination or arrangement of lines usually\n   recurring; whether like or unlike, in measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanza', 'n.', 'An apartment or division in a building; a room or chamber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stanzaic', 'a.', 'Pertaining to, or consisting of, stanzas; as, a couplet\n   in stanzaic form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stapedial', 'a.', 'Of or pertaining to stapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stapelia', 'n.', 'An extensive and curious genus of African plants of the\n   natural order Asclepiadaceae (Milkweed family). They are succulent\n   plants without leaves, frequently covered with dark tubercles giving\n   them a very grotesque appearance. The odor of the blossoms is like that\n   of carrion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stapes', 'n.', 'The innermost of the ossicles of the ear; the stirrup, or\n   stirrup bone; -- so called from its form. See Illust. of Ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staphyline', 'a.', 'Of or pertaining to the uvula or the palate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staphylinid', 'n.', 'Any rove beetle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staphyloma', 'n.', 'A protrusion of any part of the globe of the eye; as, a\n   staphyloma of the cornea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staphylomatous', 'a.', 'Of or pertaining to staphyloma; affected with\n   staphyloma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staphyloplasty', 'n.', 'The operation for restoring or replacing the soft\n   palate when it has been lost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staphyloraphy', 'n.', 'Alt. of Staphylorrhaphy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staphylorrhaphy', 'n.', 'The operation of uniting a cleft palate,\n   consisting in paring and bringing together the edges of the cleft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staphylotomy', 'n.', 'The operation of removing a staphyloma by cutting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staple', 'n.', 'A settled mart; an emporium; a city or town to which\n   merchants brought commodities for sale or exportation in bulk; a place\n   for wholesale traffic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staple', 'n.', 'Hence: Place of supply; source; fountain head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staple', 'n.', 'The principal commodity of traffic in a market; a principal\n   commodity or production of a country or district; as, wheat, maize, and\n   cotton are great staples of the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staple', 'n.', 'The principal constituent in anything; chief item.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staple', 'n.', 'Unmanufactured material; raw material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staple', 'n.', 'The fiber of wool, cotton, flax, or the like; as, a coarse\n   staple; a fine staple; a long or short staple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staple', 'n.', 'A loop of iron, or a bar or wire, bent and formed with two\n   points to be driven into wood, to hold a hook, pin, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staple', 'n.', 'A shaft, smaller and shorter than the principal one,\n   joining different levels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staple', 'n.', 'A small pit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staple', 'n.', 'A district granted to an abbey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staple', 'a.', 'Pertaining to, or being market of staple for, commodities;\n   as, a staple town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staple', 'a.', 'Established in commerce; occupying the markets; settled;\n   as, a staple trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staple', 'a.', 'Fit to be sold; marketable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staple', 'a.', 'Regularly produced or manufactured in large quantities;\n   belonging to wholesale traffic; principal; chief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'stapled', 'imp. & p. p.', 'of Staple');
  INSERT INTO words (word, word_type, description) VALUES  (    'stapling', 'p. pr. & vb. n.', 'of Staple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staple', 'v. t.', 'To sort according to its staple; as, to staple cotton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stapler', 'n.', 'A dealer in staple goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stapler', 'n.', 'One employed to assort wool according to its staple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Star', 'n.', 'One of the innumerable luminous bodies seen in the heavens;\n   any heavenly body other than the sun, moon, comets, and nebulae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Star', 'n.', 'The polestar; the north star.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Star', 'n.', 'A planet supposed to influence ones destiny; (usually pl.) a\n   configuration of the planets, supposed to influence fortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Star', 'n.', 'That which resembles the figure of a star, as an ornament\n   worn on the breast to indicate rank or honor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Star', 'n.', 'Specifically, a radiated mark in writing or printing; an\n   asterisk [thus, *]; -- used as a reference to a note, or to fill a\n   blank where something is omitted, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Star', 'n.', 'A composition of combustible matter used in the heading of\n   rockets, in mines, etc., which, exploding in the air, presents a\n   starlike appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Star', 'n.', 'A person of brilliant and attractive qualities, especially on\n   public occasions, as a distinguished orator, a leading theatrical\n   performer, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starred', 'imp. & p. p.', 'of Star');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starring', 'p. pr. & vb. n.', 'of Star');
  INSERT INTO words (word, word_type, description) VALUES  (    'Star', 'v. t.', 'To set or adorn with stars, or bright, radiating bodies;\n   to bespangle; as, a robe starred with gems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Star', 'v. i.', 'To be bright, or attract attention, as a star; to shine\n   like a star; to be brilliant or prominent; to play a part as a\n   theatrical star.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Star-blind', 'a.', 'Half blind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starboard', 'v. t.', 'That side of a vessel which is on the right hand of a\n   person who stands on board facing the bow; -- opposed to larboard, or\n   port.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starboard', 'a.', 'Pertaining to the right-hand side of a ship; being or\n   lying on the right side; as, the starboard quarter; starboard tack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starboard', 'v. t.', 'To put to the right, or starboard, side of a vessel;\n   as, to starboard the helm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starblowlines', 'n. pl.', 'The men in the starboard watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starch', 'a.', 'Stiff; precise; rigid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starch', 'n.', 'A widely diffused vegetable substance found especially in\n   seeds, bulbs, and tubers, and extracted (as from potatoes, corn, rice,\n   etc.) as a white, glistening, granular or powdery substance, without\n   taste or smell, and giving a very peculiar creaking sound when rubbed\n   between the fingers. It is used as a food, in the production of\n   commercial grape sugar, for stiffening linen in laundries, in making\n   paste, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starch', 'n.', 'Fig.: A stiff, formal manner; formality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starched', 'imp. & p. p.', 'of Starch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starching', 'p. pr. & vb. n.', 'of Starch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starch', 'v. t.', 'To stiffen with starch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Star-chamber', 'n.', 'An ancient high court exercising jurisdiction in\n   certain cases, mainly criminal, which sat without the intervention of a\n   jury. It consisted of the kings council, or of the privy council only\n   with the addition of certain judges. It could proceed on mere rumor or\n   examine witnesses; it could apply torture. It was abolished by the Long\n   Parliament in 1641.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starched', 'a.', 'Stiffened with starch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starched', 'a.', 'Stiff; precise; formal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starchedness', 'n.', 'The quality or state of being starched; stiffness in\n   manners; formality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starcher', 'n.', 'One who starches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starchly', 'adv.', 'In a starched or starch manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starchness', 'n.', 'Of or pertaining to starched or starch; stiffness of\n   manner; preciseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starchwort', 'n.', 'The cuckoopint, the tubers of which yield a fine\n   quality of starch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starchy', 'a.', 'Consisting of starch; resembling starch; stiff; precise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starcraft', 'n.', 'Astrology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Star-crossed', 'a.', 'Not favored by the stars; ill-fated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stare', 'n.', 'The starling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'stared', 'imp. & p. p.', 'of Stare');
  INSERT INTO words (word, word_type, description) VALUES  (    'staring', 'p. pr. & vb. n.', 'of Stare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stare', 'v. i.', 'To look with fixed eyes wide open, as through fear,\n   wonder, surprise, impudence, etc.; to fasten an earnest and prolonged\n   gaze on some object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stare', 'v. i.', 'To be very conspicuous on account of size, prominence,\n   color, or brilliancy; as, staring windows or colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stare', 'v. i.', 'To stand out; to project; to bristle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stare', 'v. t.', 'To look earnestly at; to gaze at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stare', 'n.', 'The act of staring; a fixed look with eyes wide open.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starer', 'n.', 'One who stares, or gazes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starf', 'imp.', 'Starved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starfinch', 'n.', 'The European redstart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starfish', 'n.', 'Any one of numerous species of echinoderms belonging to\n   the class Asterioidea, in which the body is star-shaped and usually has\n   five rays, though the number of rays varies from five to forty or more.\n   The rays are often long, but are sometimes so short as to appear only\n   as angles to the disklike body. Called also sea star, five-finger, and\n   stellerid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starfish', 'n.', 'The dollar fish, or butterfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stargaser', 'n.', 'One who gazes at the stars; an astrologer; sometimes, in\n   derision or contempt, an astronomer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stargaser', 'n.', 'Any one of several species of spiny-rayed marine fishes\n   belonging to Uranoscopus, Astroscopus, and allied genera, of the family\n   Uranoscopidae. The common species of the Eastern United States are\n   Astroscopus anoplus, and A. guttatus. So called from the position of\n   the eyes, which look directly upward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stargasing', 'n.', 'The act or practice of observing the stars with\n   attention; contemplation of the stars as connected with astrology or\n   astronomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stargasing', 'n.', 'Hence, absent-mindedness; abstraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staringly', 'adv.', 'With a staring look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stark', 'n.', 'Stiff; rigid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stark', 'n.', 'Complete; absolute; full; perfect; entire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stark', 'n.', 'Strong; vigorous; powerful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stark', 'n.', 'Severe; violent; fierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stark', 'n.', 'Mere; sheer; gross; entire; downright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stark', 'adv.', 'Wholly; entirely; absolutely; quite; as, stark mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stark', 'v. t.', 'To stiffen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starkly', 'adv.', 'In a stark manner; stiffly; strongly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starkness', 'n.', 'The quality or state of being stark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starless', 'a.', 'Being without stars; having no stars visible; as, a\n   starless night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starlight', 'n.', 'The light given by the stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starlight', 'a.', 'Lighted by the stars, or by the stars only; as, a\n   starlight night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starlike', 'a.', 'Resembling a star; stellated; radiated like a star; as,\n   starlike flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starlike', 'a.', 'Shining; bright; illustrious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starling', 'n.', 'Any passerine bird belonging to Sturnus and allied\n   genera. The European starling (Sturnus vulgaris) is dark brown or\n   greenish black, with a metallic gloss, and spotted with yellowish\n   white. It is a sociable bird, and builds about houses, old towers, etc.\n   Called also stare, and starred. The pied starling of India is\n   Sternopastor contra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starling', 'n.', 'A California fish; the rock trout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starling', 'n.', 'A structure of piles driven round the piers of a bridge\n   for protection and support; -- called also sterling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starlit', 'a.', 'Lighted by the stars; starlight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starmonger', 'n.', 'A fortune teller; an astrologer; -- used in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starn', 'n.', 'The European starling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starnose', 'n.', 'A curious American mole (Condylura cristata) having the\n   nose expanded at the end into a stellate disk; -- called also\n   star-nosed mole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starost', 'n.', 'A nobleman who possessed a starosty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starosty', 'n.', 'A castle and domain conferred on a nobleman for life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starproof', 'a.', 'Impervious to the light of the stars; as, a starproof\n   elm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Star-read', 'n.', 'Doctrine or knowledge of the stars; star lore;\n   astrology; astronomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starred', 'a.', 'Adorned or studded with stars; bespangled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starred', 'a.', 'Influenced in fortune by the stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starriness', 'n.', 'The quality or state of being starry; as, the\n   starriness of the heavens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starry', 'a.', 'Abounding with stars; adorned with stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starry', 'a.', 'Consisting of, or proceeding from, the stars; stellar;\n   stellary; as, starry light; starry flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starry', 'a.', 'Shining like stars; sparkling; as, starry eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starry', 'a.', 'Arranged in rays like those of a star; stellate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starshine', 'n.', 'The light of the stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starshoot', 'n.', 'See Nostoc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Star-spangled', 'a.', 'Spangled or studded with stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starstone', 'n.', 'Asteriated sapphire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'started', 'imp. & p. p.', 'of Start');
  INSERT INTO words (word, word_type, description) VALUES  (    'starting', 'p. pr. & vb. n.', 'of Start');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start', 'v. i.', 'To leap; to jump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start', 'v. i.', 'To move suddenly, as with a spring or leap, from\n   surprise, pain, or other sudden feeling or emotion, or by a voluntary\n   act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start', 'v. i.', 'To set out; to commence a course, as a race or journey;\n   to begin; as, to start business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start', 'v. i.', 'To become somewhat displaced or loosened; as, a rivet or\n   a seam may start under strain or pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start', 'v. t.', 'To cause to move suddenly; to disturb suddenly; to\n   startle; to alarm; to rouse; to cause to flee or fly; as, the hounds\n   started a fox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start', 'v. t.', 'To bring onto being or into view; to originate; to\n   invent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start', 'v. t.', 'To cause to move or act; to set going, running, or\n   flowing; as, to start a railway train; to start a mill; to start a\n   stream of water; to start a rumor; to start a business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start', 'v. t.', 'To move suddenly from its place or position; to displace\n   or loosen; to dislocate; as, to start a bone; the storm started the\n   bolts in the vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start', 'v. t.', 'To pour out; to empty; to tap and begin drawing from; as,\n   to start a water cask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start', 'n.', 'The act of starting; a sudden spring, leap, or motion,\n   caused by surprise, fear, pain, or the like; any sudden motion, or\n   beginning of motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start', 'n.', 'A convulsive motion, twitch, or spasm; a spasmodic effort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start', 'n.', 'A sudden, unexpected movement; a sudden and capricious\n   impulse; a sally; as, starts of fancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start', 'n.', 'The beginning, as of a journey or a course of action; first\n   motion from a place; act of setting out; the outset; -- opposed to\n   finish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start', 'v. i.', 'A tail, or anything projecting like a tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start', 'v. i.', 'The handle, or tail, of a plow; also, any long handle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start', 'v. i.', 'The curved or inclined front and bottom of a water-wheel\n   bucket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start', 'v. i.', 'The arm, or level, of a gin, drawn around by a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starter', 'n.', 'One who, or that which, starts; as, a starter on a\n   journey; the starter of a race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starter', 'n.', 'A dog that rouses game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Startful', 'a.', 'Apt to start; skittish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Startfulness', 'n.', 'Aptness to start.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starthroat', 'n.', 'Any humming bird of the genus Heliomaster. The feathers\n   of the throat have a brilliant metallic luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starting', '', 'a. & n. from Start, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Startingly', 'adv.', 'By sudden fits or starts; spasmodically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Startish', 'a.', 'Apt to start; skittish; shy; -- said especially of a\n   horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Startled', 'imp. & p. p.', 'of Startle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Startling', 'p. pr. & vb. n.', 'of Startle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Startle', 'v. t.', 'To move suddenly, or be excited, on feeling alarm; to\n   start.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Startle', 'v. t.', 'To excite by sudden alarm, surprise, or apprehension;\n   to frighten suddenly and not seriously; to alarm; to surprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Startle', 'v. t.', 'To deter; to cause to deviate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Startle', 'n.', 'A sudden motion or shock caused by an unexpected alarm,\n   surprise, or apprehension of danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Startlingly', 'adv.', 'In a startling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Startlish', 'a.', 'Easily startled; apt to start; startish; skittish; --\n   said especially of a hourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start-up', 'n.', 'One who comes suddenly into notice; an upstart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start-up', 'n.', 'A kind of high rustic shoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Start-up', 'a.', 'Upstart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starvation', 'n.', 'The act of starving, or the state of being starved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starved', 'imp. & p. p.', 'of Starve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starving', 'p. pr. & vb. n.', 'of Starve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starve', 'v. i.', 'To die; to perish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starve', 'v. i.', 'To perish with hunger; to suffer extreme hunger or want;\n   to be very indigent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starve', 'v. i.', 'To perish or die with cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starve', 'v. t.', 'To destroy with cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starve', 'v. t.', 'To kill with hunger; as, maliciously to starve a man is,\n   in law, murder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starve', 'v. t.', 'To distress or subdue by famine; as, to starvea garrison\n   into a surrender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starve', 'v. t.', 'To destroy by want of any kind; as, to starve plans by\n   depriving them of proper light and air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starve', 'v. t.', 'To deprive of force or vigor; to disable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starvedly', 'adv.', 'In the condition of one starved or starving;\n   parsimoniously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starveling', 'n.', 'One who, or that which, pines from lack or food, or\n   nutriment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starveling', 'a.', 'Hungry; lean; pining with want.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starwort', 'n.', 'Any plant of the genus Aster. See Aster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Starwort', 'n.', 'A small plant of the genus Stellaria, having star-shaped\n   flowers; star flower; chickweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stasmia', 'pl. ', 'of Stasimon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stasimon', 'n.', 'In the Greek tragedy, a song of the chorus, continued\n   without the interruption of dialogue or anapaestics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stasis', 'n.', 'A slackening or arrest of the blood current in the vessels,\n   due not to a lessening of the hearts beat, but presumably to some\n   abnormal resistance of the capillary walls. It is one of the phenomena\n   observed in the capillaries in inflammation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statable', 'a.', 'That can be stated; as, a statablegrievance; the question\n   at issue is statable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statal', 'a.', 'Of, pertaining to, or existing with reference to, a State\n   of the American Union, as distinguished from the general government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statant', 'a.', 'In a standing position; as, a lion statant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statarian', 'a.', 'Fixed; settled; steady; statary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statarianly', 'adv.', 'Fixedly; steadly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statary', 'a.', 'Fixed; settled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'State', 'n.', 'The circumstances or condition of a being or thing at any\n   given time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'State', 'n.', 'Rank; condition; quality; as, the state of honor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'State', 'n.', 'Condition of prosperity or grandeur; wealthy or prosperous\n   circumstances; social importance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'State', 'n.', 'Appearance of grandeur or dignity; pomp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'State', 'n.', 'A chair with a canopy above it, often standing on a dais; a\n   seat of dignity; also, the canopy itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'State', 'n.', 'Estate, possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'State', 'n.', 'A person of high rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'State', 'n.', 'Any body of men united by profession, or constituting a\n   community of a particular character; as, the civil and ecclesiastical\n   states, or the lords spiritual and temporal and the commons, in Great\n   Britain. Cf. Estate, n., 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'State', 'n.', 'The principal persons in a government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'State', 'n.', 'The bodies that constitute the legislature of a country; as,\n   the States-general of Holland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'State', 'n.', 'A form of government which is not monarchial, as a republic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'State', 'n.', 'A political body, or body politic; the whole body of people\n   who are united one government, whatever may be the form of the\n   government; a nation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'State', 'n.', 'In the United States, one of the commonwealth, or bodies\n   politic, the people of which make up the body of the nation, and which,\n   under the national constitution, stands in certain specified relations\n   with the national government, and are invested, as commonwealth, with\n   full power in their several spheres over all matters not expressly\n   inhibited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'State', 'n.', 'Highest and stationary condition, as that of maturity\n   between growth and decline, or as that of crisis between the increase\n   and the abating of a disease; height; acme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'State', 'a.', 'Stately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'State', 'a.', 'Belonging to the state, or body politic; public.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stated', 'imp. & p. p.', 'of State');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stating', 'p. pr. & vb. n.', 'of State');
  INSERT INTO words (word, word_type, description) VALUES  (    'State', 'v. t.', 'To set; to settle; to establish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'State', 'v. t.', 'To express the particulars of; to set down in detail or\n   in gross; to represent fully in words; to narrate; to recite; as, to\n   state the facts of a case, ones opinion, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'State', 'n.', 'A statement; also, a document containing a statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statecraft', 'n.', 'The art of conducting state affairs; state management;\n   statesmanship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stated', 'a.', 'Settled; established; fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stated', 'a.', 'Recurring at regular time; not occasional; as, stated\n   preaching; stated business hours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statedly', 'adv.', 'At stated times; regularly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stateful', 'a.', 'Full of state; stately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statehood', 'n.', 'The condition of being a State; as, a territory seeking\n   Statehood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statehouse', 'n.', 'The building in which a State legislature holds its\n   sessions; a State capitol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stateless', 'a.', 'Without state or pomp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statelily', 'adv.', 'In a stately manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stateliness', 'n.', 'The quality or state of being stately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stately', 'superl.', 'Evincing state or dignity; lofty; majestic; grand;\n   as, statelymanners; a stately gait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stately', 'adv.', 'Majestically; loftily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statement', 'n.', 'The act of stating, reciting, or presenting, orally or\n   in paper; as, to interrupt a speaker in the statement of his case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statement', 'n.', 'That which is stated; a formal embodiment in language of\n   facts or opinions; a narrative; a recital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statemonger', 'n.', 'One versed in politics, or one who dabbles in state\n   affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stateprison', '', 'See under State, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stater', 'n.', 'One who states.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stater', 'n.', 'The principal gold coin of ancient Grece. It varied much in\n   value, the stater best known at Athens being worth about 1 2s., or\n   about $5.35. The Attic silver tetradrachm was in later times called\n   stater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stateroom', 'n.', 'A magnificent room in a place or great house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stateroom', 'n.', 'A small apartment for lodging or sleeping in the cabin,\n   or on the deck, of a vessel; also, a somewhat similar apartment in a\n   railway sleeping car.');
  INSERT INTO words (word, word_type, description) VALUES  (    'States-general', 'n.', 'In France, before the Revolution, the assembly of\n   the three orders of the kingdom, namely, the clergy, the nobility, and\n   the third estate, or commonalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'States-general', 'n.', 'In the Netherlands, the legislative body, composed\n   of two chambers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statesmen', 'pl. ', 'of Statesman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statesman', 'n.', 'A man versed in public affairs and in the principles and\n   art of government; especially, one eminent for political abilities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statesman', 'n.', 'One occupied with the affairs of government, and\n   influental in shaping its policy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statesman', 'n.', 'A small landholder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statesmanlike', 'a.', 'Having the manner or wisdom of statesmen; becoming a\n   statesman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statesmanly', 'a.', 'Becoming a statesman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statesmanship', 'n.', 'The qualifications, duties, or employments of a\n   statesman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stateswomen', 'pl. ', 'of Stateswoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stateswoman', 'n.', 'A woman concerned in public affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stathmograph', 'n.', 'A contrivance for recording the speed of a railway\n   train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Static', 'a.', 'Alt. of Statical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statical', 'a.', 'Resting; acting by mere weight without motion; as,\n   statical pressure; static objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statical', 'a.', 'Pertaining to bodies at rest or in equilibrium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statically', 'adv.', 'In a statical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statics', 'n.', 'That branch of mechanics which treats of the equilibrium\n   of forces, or relates to bodies as held at rest by the forces acting on\n   them; -- distinguished from dynamics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stating', 'n.', 'The act of one who states anything; statement; as, the\n   statingof ones opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Station', 'n.', 'The act of standing; also, attitude or pose in standing;\n   posture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Station', 'n.', 'A state of standing or rest; equilibrium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Station', 'n.', 'The spot or place where anything stands, especially where\n   a person or thing habitually stands, or is appointed to remain for a\n   time; as, the station of a sentinel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Station', 'n.', 'A regular stopping place in a stage road or route; a place\n   where railroad trains regularly come to a stand, for the convenience of\n   passengers, taking in fuel, moving freight, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Station', 'n.', 'The headquarters of the police force of any precinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Station', 'n.', 'The place at which an instrument is planted, or\n   observations are made, as in surveying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Station', 'n.', 'The particular place, or kind of situation, in which a\n   species naturally occurs; a habitat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Station', 'n.', 'A place to which ships may resort, and where they may\n   anchor safely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Station', 'n.', 'A place or region to which a government ship or fleet is\n   assigned for duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Station', 'n.', 'A place calculated for the rendezvous of troops, or for\n   the distribution of them; also, a spot well adapted for offensive\n   measures. Wilhelm (Mil. Dict.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Station', 'n.', 'An enlargement in a shaft or galley, used as a landing, or\n   passing place, or for the accomodation of a pump, tank, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Station', 'n.', 'Post assigned; office; the part or department of public\n   duty which a person is appointed to perform; sphere of duty or\n   occupation; employment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Station', 'n.', 'Situation; position; location.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Station', 'n.', 'State; rank; condition of life; social status.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Station', 'n.', 'The fast of the fourth and sixth days of the week,\n   Wednesday and Friday, in memory of the council which condemned Christ,\n   and of his passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Station', 'n.', 'A church in which the procession of the clergy halts on\n   stated days to say stated prayers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Station', 'n.', 'One of the places at which ecclesiastical processions\n   pause for the performance of an act of devotion; formerly, the tomb of\n   a martyr, or some similarly consecrated spot; now, especially, one of\n   those representations of the successive stages of our Lords passion\n   which are often placed round the naves of large churches and by the\n   side of the way leading to sacred edifices or shrines, and which are\n   visited in rotation, stated services being performed at each; -- called\n   also Station of the cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stationed', 'imp. & p. p.', 'of Station');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stationing', 'p. pr. & vb. n.', 'of Station');
  INSERT INTO words (word, word_type, description) VALUES  (    'Station', 'v. t.', 'To place; to set; to appoint or assign to the\n   occupation of a post, place, or office; as, to station troops on the\n   right of an army; to station a sentinel on a rampart; to station ships\n   on the coasts of Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stational', 'a.', 'Of or pertaining to a station.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stationariness', 'n.', 'The quality or state of being stationary; fixity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stationary', 'a.', 'Not moving; not appearing to move; stable; fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stationary', 'a.', 'Not improving or getting worse; not growing wiser,\n   greater, better, more excellent, or the contrary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stationary', 'a.', 'Appearing to be at rest, because moving in the line of\n   vision; not progressive or retrograde, as a planet.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ries', 'pl. ', 'of Stationary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stationary', 'n.', 'One who, or that which, is stationary, as a planet when\n   apparently it has neither progressive nor retrograde motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stationer', 'a.', 'A bookseller or publisher; -- formerly so called from\n   his occupying a stand, or station, in the market place or elsewhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stationer', 'a.', 'One who sells paper, pens, quills, inkstands, pencils,\n   blank books, and other articles used in writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stationery', 'n.', 'The articles usually sold by stationers, as paper,\n   pens, ink, quills, blank books, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stationery', 'a.', 'Belonging to, or sold by, a stationer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statism', 'n.', 'The art of governing a state; statecraft; policy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statist', 'n.', 'A statesman; a politician; one skilled in government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statist', 'n.', 'A statistician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statistic', 'a.', 'Alt. of Statistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statistical', 'a.', 'Of or pertaining to statistics; as, statistical\n   knowledge, statistical tabulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statistically', 'adv.', 'In the way of statistics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statistician', 'n.', 'One versed in statistics; one who collects and\n   classifies facts for statistics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statistics', 'n.', 'The science which has to do with the collection and\n   classification of certain facts respecting the condition of the people\n   in a state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statistics', 'n.', 'Classified facts respecting the condition of the people\n   in a state, their health, their longevity, domestic economy, arts,\n   property, and political strength, their resources, the state of the\n   country, etc., or respecting any particular class or interest;\n   especially, those facts which can be stated in numbers, or in tables of\n   numbers, or in any tabular and classified arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statistics', 'n.', 'The branch of mathematics which studies methods for the\n   calculation of probabilities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statistology', 'n.', 'See Statistics, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stative', 'a.', 'Of or pertaining to a fixed camp, or military posts or\n   quarters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statoblast', 'n.', 'One of a peculiar kind of internal buds, or germs,\n   produced in the interior of certain Bryozoa and sponges, especially in\n   the fresh-water species; -- also called winter buds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statocracy', 'n.', 'Government by the state, or by political power, in\n   distinction from government by ecclesiastical power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statua', 'n.', 'A statue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statuaries', 'pl. ', 'of Statuary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statuary', 'n.', 'One who practices the art of making statues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statuary', 'n.', 'The art of carving statues or images as representatives\n   of real persons or things; a branch of sculpture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statuary', 'n.', 'A collection of statues; statues, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statue', 'n.', 'The likeness of a living being sculptured or modeled in\n   some solid substance, as marble, bronze, or wax; an image; as, a statue\n   of Hercules, or of a lion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statue', 'n.', 'A portrait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statued', 'imp. & p. p.', 'of Statue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statuing', 'p. pr. & vb. n.', 'of Statue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statue', 'v. t.', 'To place, as a statue; to form a statue of; to make into\n   a statue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statued', 'a.', 'Adorned with statues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statueless', 'a.', 'Without a statue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statuelike', 'a.', 'Like a statue; motionless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statuesque', 'a.', 'Partaking of, or exemplifying, the characteristics of a\n   statue; having the symmetry, or other excellence, of a statue\n   artistically made; as, statuesquelimbs; a statuesque attitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statuesquely', 'adv.', 'In a statuesque manner; in a way suggestive of a\n   statue; like a statue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statuette', 'n.', 'A small statue; -- usually applied to a figure much less\n   than life size, especially when of marble or bronze, or of plaster or\n   clay as a preparation for the marble or bronze, as distinguished from a\n   figure in terra cotta or the like. Cf. Figurine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statuminate', 'v. t.', 'To prop or support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stature', 'n.', 'The natural height of an animal body; -- generally used of\n   the human body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statured', 'a.', 'Arrived at full stature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Status', 'n.', 'State; condition; position of affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Status in quo', '', 'Alt. of Status quo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Status quo', '', 'The state in which anything is already. The phrase is\n   also used retrospectively, as when, on a treaty of place, matters\n   return to the status quo ante bellum, or are left in statu quo ante\n   bellum, i.e., the state (or, in the state) before the war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statutable', 'a.', 'Made or introduced by statute; proceeding from an act\n   of the legistature; as, a statutable provision or remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statutable', 'a.', 'Made or being in conformity to statute; standard; as,\n   statutable measures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statutably', 'adv.', 'Conformably to statute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statute', 'n.', 'An act of the legislature of a state or country,\n   declaring, commanding, or prohibiting something; a positive law; the\n   written will of the legislature expressed with all the requisite forms\n   of legislation; -- used in distinction fraom common law. See Common\n   law, under Common, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statute', 'a.', 'An act of a corporation or of its founder, intended as a\n   permanent rule or law; as, the statutes of a university.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statute', 'a.', 'An assemblage of farming servants (held possibly by\n   statute) for the purpose of being hired; -- called also statute fair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Statutory', 'a.', 'Enacted by statute; depending on statute for its\n   authority; as, a statutory provision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staunch', '', 'Alt. of Staunchness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staunchly', '', 'Alt. of Staunchness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staunchness', '', 'See Stanch, Stanchly, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staurolite', 'n.', 'A mineral of a brown to black color occurring in\n   prismatic crystals, often twinned so as to form groups resembling a\n   cross. It is a silicate of aluminia and iron, and is generally found\n   imbedded in mica schist. Called also granatite, and grenatite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staurolitic', 'a.', 'Of or pertaining to staurolite; resembling or\n   containing staurolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stauroscope', 'n.', 'An optical instrument used in determining the position\n   of the planes of light-vibration in sections of crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staurotide', 'n.', 'Staurolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stave', 'n.', 'One of a number of narrow strips of wood, or narrow iron\n   plates, placed edge to edge to form the sides, covering, or lining of a\n   vessel or structure; esp., one of the strips which form the sides of a\n   cask, a pail, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stave', 'n.', 'One of the cylindrical bars of a lantern wheel; one of the\n   bars or rounds of a rack, a ladder, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stave', 'n.', 'A metrical portion; a stanza; a staff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stave', 'n.', 'The five horizontal and parallel lines on and between which\n   musical notes are written or pointed; the staff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staved', 'imp. & p. p.', 'of Stave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stove', '', 'of Stave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staving', 'p. pr. & vb. n.', 'of Stave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stave', 'n.', 'To break in a stave or the staves of; to break a hole in; to\n   burst; -- often with in; as, to stave a cask; to stave in a boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stave', 'n.', 'To push, as with a staff; -- with off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stave', 'n.', 'To delay by force or craft; to drive away; -- usually with\n   off; as, to stave off the execution of a project.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stave', 'n.', 'To suffer, or cause, to be lost by breaking the cask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stave', 'n.', 'To furnish with staves or rundles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stave', 'n.', 'To render impervious or solid by driving with a calking\n   iron; as, to stave lead, or the joints of pipes into which lead has\n   been run.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stave', 'v. i.', 'To burst in pieces by striking against something; to dash\n   into fragments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staves', 'n.', 'pl. of Staff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staves', 'pl.', 'pl. of Stave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stavesacre', 'n.', 'A kind of larkspur (Delphinium Staphysagria), and its\n   seeds, which are violently purgative and emetic. They are used as a\n   parasiticide, and in the East for poisoning fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stavewood', 'n.', 'A tall tree (Simaruba amara) growing in tropical\n   America. It is one of the trees which yields quassia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staving', 'n.', 'A cassing or lining of staves; especially, one encircling\n   a water wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staw', 'v. i.', 'To be fixed or set; to stay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'n.', 'A large, strong rope, employed to support a mast, by being\n   extended from the head of one mast down to some other, or to some part\n   of the vessel. Those which lead forward are called fore-and-aft stays;\n   those which lead to the vessels side are called backstays. See Illust.\n   of Ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stayed', 'imp. & p. p.', 'of Stay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staid', '', 'of Stay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staying', 'p. pr. & vb. n.', 'of Stay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'v. i.', 'To stop from motion or falling; to prop; to fix firmly; to\n   hold up; to support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'v. i.', 'To support from sinking; to sustain with strength; to\n   satisfy in part or for the time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'v. i.', 'To bear up under; to endure; to support; to resist\n   successfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'v. i.', 'To hold from proceeding; to withhold; to restrain; to\n   stop; to hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'v. i.', 'To hinde/; to delay; to detain; to keep back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'v. i.', 'To remain for the purpose of; to wait for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'v. i.', 'To cause to cease; to put an end to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'v. i.', 'To fasten or secure with stays; as, to stay a flat sheet\n   in a steam boiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'v. i.', 'To tack, as a vessel, so that the other side of the vessel\n   shall be presented to the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'v. i.', 'To remain; to continue in a place; to abide fixed for a\n   space of time; to stop; to stand still.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'v. i.', 'To continue in a state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'v. i.', 'To wait; to attend; to forbear to act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'v. i.', 'To dwell; to tarry; to linger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'v. i.', 'To rest; to depend; to rely; to stand; to insist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'v. i.', 'To come to an end; to cease; as, that day the storm\n   stayed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'v. i.', 'To hold out in a race or other contest; as, a horse stays\n   well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'v. i.', 'To change tack; as a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'n.', 'That which serves as a prop; a support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'n.', 'A corset stiffened with whalebone or other material, worn by\n   women, and rarely by men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'n.', 'Continuance in a place; abode for a space of time; sojourn;\n   as, you make a short stay in this city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'n.', 'Cessation of motion or progression; stand; stop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'n.', 'Hindrance; let; check.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'n.', 'Restraint of passion; moderation; caution; steadiness;\n   sobriety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stay', 'n.', 'Strictly, a part in tension to hold the parts together, or\n   stiffen them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stayed', 'a.', 'Staid; fixed; settled; sober; -- now written staid. See\n   Staid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stayedly', 'adv.', 'Staidly. See Staidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stayedness', 'n.', 'Staidness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stayedness', 'n.', 'Solidity; weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stayer', 'n.', 'One who upholds or supports that which props; one who, or\n   that which, stays, stops, or restrains; also, colloquially, a horse,\n   man, etc., that has endurance, an a race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staylace', 'n.', 'A lace for fastening stays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stayless', 'a.', 'Without stop or delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staymaker', 'n.', 'One whose occupation is to make stays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staynil', 'n.', 'The European starling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Staysail', 'n.', 'Any sail extended on a stay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stayship', 'n.', 'A remora, -- fabled to stop ships by attaching itself to\n   them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stead', 'n.', 'Place, or spot, in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stead', 'n.', 'Place or room which another had, has, or might have.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stead', 'n.', 'A frame on which a bed is laid; a bedstead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stead', 'n.', 'A farmhouse and offices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stead', 'v. t.', 'To help; to support; to benefit; to assist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stead', 'v. t.', 'To fill place of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steadfast', 'a.', 'Firmly fixed or established; fast fixed; firm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steadfast', 'a.', 'Not fickle or wavering; constant; firm; resolute;\n   unswerving; steady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steadfastly', 'adv.', 'In a steadfast manner; firmly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steadfastness', 'n.', 'The quality or state of being steadfast; firmness;\n   fixedness; constancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steadily', 'adv.', 'In a steady manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steadiness', 'n.', 'The quality or state of being steady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steading', 'n.', 'The brans, stables, cattle-yards, etc., of a farm; --\n   called also onstead, farmstead, farm offices, or farmery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steady', 'n.', 'Firm in standing or position; not tottering or shaking;\n   fixed; firm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steady', 'n.', 'Constant in feeling, purpose, or pursuit; not fickle,\n   changeable, or wavering; not easily moved or persuaded to alter a\n   purpose; resolute; as, a man steady in his principles, in his purpose,\n   or in the pursuit of an object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steady', 'n.', 'Regular; constant; undeviating; uniform; as, the steady\n   course of the sun; a steady breeze of wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steadied', 'imp. & p. p.', 'of Steady');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steadying', 'p. pr. & vb. n.', 'of Steady');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steady', 'v. t.', 'To make steady; to hold or keep from shaking, reeling,\n   or falling; to make or keep firm; to support; to make constant,\n   regular, or resolute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steady', 'v. i.', 'To become steady; to regain a steady position or state;\n   to move steadily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steak', 'v. t.', 'A slice of beef, broiled, or cut for broiling; -- also\n   extended to the meat of other large animals; as, venison steak; bear\n   steak; pork steak; turtle steak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steal', 'n.', 'A handle; a stale, or stele.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stole', 'imp.', 'of Steal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stolen', 'p. p.', 'of Steal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stealing', 'p. pr. & vb. n.', 'of Steal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steal', 'v. t.', 'To take and carry away, feloniously; to take without\n   right or leave, and with intent to keep wrongfully; as, to steal the\n   personal goods of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steal', 'v. t.', 'To withdraw or convey clandestinely (reflexive); hence,\n   to creep furtively, or to insinuate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steal', 'v. t.', 'To gain by insinuating arts or covert means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steal', 'v. t.', 'To get into ones power gradually and by imperceptible\n   degrees; to take possession of by a gradual and imperceptible\n   appropriation; -- with away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steal', 'v. t.', 'To accomplish in a concealed or unobserved manner; to try\n   to carry out secretly; as, to steal a look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steal', 'v. i.', 'To practice, or be guilty of, theft; to commit larceny or\n   theft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steal', 'v. i.', 'To withdraw, or pass privily; to slip in, along, or away,\n   unperceived; to go or come furtively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stealer', 'n.', 'One who steals; a thief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stealer', 'n.', 'The endmost plank of a strake which stops short of the\n   stem or stern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stealing', 'n.', 'The act of taking feloniously the personal property of\n   another without his consent and knowledge; theft; larceny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stealing', 'n.', 'That which is stolen; stolen property; -- chiefly used in\n   the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stealingly', 'adv.', 'By stealing, or as by stealing, furtively, or by an\n   invisible motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stealth', 'v. t.', 'The act of stealing; theft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stealth', 'v. t.', 'The thing stolen; stolen property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stealth', 'v. t.', 'The bringing to pass anything in a secret or concealed\n   manner; a secret procedure; a clandestine practice or action; -- in\n   either a good or a bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stealthful', 'a.', 'Given to stealth; stealthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stealthily', 'adv.', 'In a stealthy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stealthiness', 'n.', 'The state, quality, or character of being stealthy;\n   stealth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stealthlike', 'a.', 'Stealthy; sly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stealthy', 'superl.', 'Done by stealth; accomplished clandestinely;\n   unperceived; secret; furtive; sly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steam', 'n.', 'The elastic, aeriform fluid into which water is converted\n   when heated to the boiling points; water in the state of vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steam', 'n.', 'The mist formed by condensed vapor; visible vapor; -- so\n   called in popular usage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steam', 'n.', 'Any exhalation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steamed', 'imp. & p. p.', 'of Steam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steaming', 'p. pr. & vb. n.', 'of Steam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steam', 'v. i.', 'To emit steam or vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steam', 'v. i.', 'To rise in vapor; to issue, or pass off, as vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steam', 'v. i.', 'To move or travel by the agency of steam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steam', 'v. i.', 'To generate steam; as, the boiler steams well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steam', 'v. t.', 'To exhale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steam', 'v. t.', 'To expose to the action of steam; to apply steam to for\n   softening, dressing, or preparing; as, to steam wood; to steamcloth; to\n   steam food, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steamboat', 'n.', 'A boat or vessel propelled by steam power; -- generally\n   used of river or coasting craft, as distinguished from ocean steamers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steamboating', 'n.', 'The occupation or business of running a steamboat, or\n   of transporting merchandise, passengers, etc., by steamboats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steamboating', 'n.', 'The shearing of a pile of books which are as yet\n   uncovered, or out of boards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steam engine', '', 'An engine moved by steam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steamer', 'n.', 'A vessel propelled by steam; a steamship or steamboat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steamer', 'n.', 'A steam fire engine. See under Steam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steamer', 'n.', 'A road locomotive for use on common roads, as in\n   agricultural operations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steamer', 'n.', 'A vessel in which articles are subjected to the action of\n   steam, as in washing, in cookery, and in various processes of\n   manufacture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steamer', 'n.', 'The steamer duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steaminess', 'n.', 'The quality or condition of being steamy; vaporousness;\n   mistness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steamship', 'n.', 'A ship or seagoing vessel propelled by the power of\n   steam; a steamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steamy', 'a.', 'Consisting of, or resembling, steam; full of steam;\n   vaporous; misty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stean', 'n. & v.', 'See Steen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steaningp', 'n.', 'See Steening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steapsin', 'n.', 'An unorganized ferment or enzyme present in pancreatic\n   juice. It decomposes neutral fats into glycerin and fatty acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stearate', 'n.', 'A salt of stearic acid; as, ordinary soap consists\n   largely of sodium or potassium stearates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stearic', 'a.', 'Pertaining to, or obtained from, stearin or tallow;\n   resembling tallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stearin', 'n.', 'One of the constituents of animal fats and also of some\n   vegetable fats, as the butter of cacao. It is especially characterized\n   by its solidity, so that when present in considerable quantity it\n   materially increases the hardness, or raises the melting point, of the\n   fat, as in mutton tallow. Chemically, it is a compound of glyceryl with\n   three molecules of stearic acid, and hence is technically called\n   tristearin, or glyceryl tristearate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stearolic', 'a.', 'Of, pertaining to, or designating, an acid of the\n   acetylene series, isologous with stearis acid, and obtained, as a white\n   crystalline substance, from oleic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stearone', 'n.', 'The ketone of stearic acid, obtained as a white\n   crystalline substance, (C17H35)2.CO, by the distillation of calcium\n   stearate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stearoptene', 'n.', 'The more solid ingredient of certain volatile oils; --\n   contrasted with elaeoptene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stearrhea', 'n.', 'seborrhea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stearyl', 'n.', 'The hypothetical radical characteristic of stearic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steatite', 'n.', 'A massive variety of talc, of a grayish green or brown\n   color. It forms extensive beds, and is quarried for fireplaces and for\n   coarse utensils. Called also potstone, lard stone, and soapstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steatitic', 'n.', 'Pertaining to, or of the nature of, steatite; containing\n   or resembling steatite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steatoma', 'n.', 'A cyst containing matter like suet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steatomatous', 'a.', 'Of the nature of steatoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steatopyga', 'n.', 'A remarkable accretion of fat upon the buttocks of\n   Africans of certain tribes, especially of Hottentot women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steatopygous', 'a.', 'Having fat buttocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sted', 'adv.', 'Alt. of Stedfastly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stedfast', 'adv.', 'Alt. of Stedfastly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stedfastly', 'adv.', 'See Stead, Steadfast, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stee', 'n.', 'A ladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steed', 'n.', 'A horse, especially a spirited horse for state of war; --\n   used chiefly in poetry or stately prose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steedless', 'a.', 'Having no steed; without a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steek', 'v. t.', 'Alt. of Steik');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steik', 'v. t.', 'To pierce with a sharp instrument; hence, to stitch; to\n   sew; also, to fix; to fasten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steel', 'n.', 'A variety of iron intermediate in composition and properties\n   between wrought iron and cast iron (containing between one half of one\n   per cent and one and a half per cent of carbon), and consisting of an\n   alloy of iron with an iron carbide. Steel, unlike wrought iron, can be\n   tempered, and retains magnetism. Its malleability decreases, and\n   fusibility increases, with an increase in carbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steel', 'n.', 'An instrument or implement made of steel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steel', 'n.', 'A weapon, as a sword, dagger, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steel', 'n.', 'An instrument of steel (usually a round rod) for sharpening\n   knives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steel', 'n.', 'A piece of steel for striking sparks from flint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steel', 'n.', 'Fig.: Anything of extreme hardness; that which is\n   characterized by sternness or rigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steel', 'n.', 'A chalybeate medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeled', 'imp. & p. p.', 'of Steel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeling', 'p. pr. & vb. n.', 'of Steel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steel', 'n.', 'To overlay, point, or edge with steel; as, to steel a razor;\n   to steel an ax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steel', 'n.', 'To make hard or strong; hence, to make insensible or\n   obdurate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steel', 'n.', 'Fig.: To cause to resemble steel, as in smoothness, polish,\n   or other qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steel', 'n.', 'To cover, as an electrotype plate, with a thin layer of iron\n   by electrolysis. The iron thus deposited is very hard, like steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steelbow goods', '', 'Those goods on a farm, such as corn, cattle,\n   implements husbandry, etc., which may not be carried off by a removing\n   tenant, as being the property of the landlord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeler', 'n.', 'One who points, edges, or covers with steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeler', 'n.', 'Same as Stealer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steelhead', 'n.', 'A North Pacific salmon (Salmo Gairdneri) found from\n   Northern California to Siberia; -- called also hardhead, and preesil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steelhead', 'n.', 'The ruddy duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeliness', 'n.', 'The quality of being steely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeling', 'n.', 'The process of pointing, edging, or overlaying with\n   steel; specifically, acierage. See Steel, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steely', 'a.', 'Made of steel; consisting of steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steely', 'a.', 'Resembling steel; hard; firm; having the color of steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steelyard', 'n.', 'A form of balance in which the body to be weighed is\n   suspended from the shorter arm of a lever, which turns on a fulcrum,\n   and a counterpoise is caused to slide upon the longer arm to produce\n   equilibrium, its place upon this arm (which is notched or graduated)\n   indicating the weight; a Roman balance; -- very commonly used also in\n   the plural form, steelyards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steem', 'n. & v.', 'See Esteem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steem', 'n. & v.', 'See 1st and 2nd Stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steen', 'n.', 'A vessel of clay or stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steen', 'n.', 'A wall of brick, stone, or cement, used as a lining, as of a\n   well, cistern, etc.; a steening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steen', 'v. t.', 'To line, as a well, with brick, stone, or other hard\n   material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steenbok', 'n.', 'Same as Steinbock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steening', 'n.', 'A lining made of brick, stone, or other hard material, as\n   for a well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steenkirk', 'n.', 'Alt. of Steinkirk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steinkirk', 'n.', 'A kind of neckcloth worn in a loose and disorderly\n   fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steep', 'a.', 'Bright; glittering; fiery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeped', 'imp. & p. p.', 'of Steep');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeping', 'p. pr. & vb. n.', 'of Steep');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steep', 'v. t.', 'To soak in a liquid; to macerate; to extract the essence\n   of by soaking; as, to soften seed by steeping it in water. Often used\n   figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steep', 'v. i.', 'To undergo the process of soaking in a liquid; as, the\n   tea is steeping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steep', 'n.', 'Something steeped, or used in steeping; a fertilizing liquid\n   to hasten the germination of seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steep', 'n.', 'A rennet bag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steep', 'v. t.', 'Making a large angle with the plane of the horizon;\n   ascending or descending rapidly with respect to a horizontal line or a\n   level; precipitous; as, a steep hill or mountain; a steep roof; a steep\n   ascent; a steep declivity; a steep barometric gradient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steep', 'v. t.', 'Difficult of access; not easy reached; lofty; elevated;\n   high.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steep', 'v. t.', 'Excessive; as, a steep price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steep', 'n.', 'A precipitous place, hill, mountain, rock, or ascent; any\n   elevated object sloping with a large angle to the plane of the horizon;\n   a precipice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steep-down', 'a.', 'Deep and precipitous, having steep descent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steepened', 'imp. & p. p.', 'of Steepen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steepening', 'p. pr. & vb. n.', 'of Steepen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steepen', 'v. i.', 'To become steep or steeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeper', 'n.', 'A vessel, vat, or cistern, in which things are steeped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steepiness', 'n.', 'Steepness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steepish', 'a.', 'Somewhat steep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeple', 'n.', 'A spire; also, the tower and spire taken together; the\n   whole of a structure if the roof is of spire form. See Spire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeplechasing', 'n.', 'The act of riding steeple chases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeple-crowned', 'a.', 'Bearing a steeple; as, a steeple-crowned building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeple-crowned', 'a.', 'Having a crown shaped like a steeple; as, a\n   steeple-crowned hat; also, wearing a hat with such a crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steepled', 'a.', 'Furnished with, or having the form of, a steeple; adorned\n   with steeples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeply', 'adv.', 'In a steep manner; with steepness; with precipitous\n   declivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steepness', 'n.', 'Quality or state of being steep; precipitous declivity;\n   as, the steepnessof a hill or a roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steepness', 'n.', 'Height; loftiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steep-up', 'a.', 'Lofty and precipitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steepy', 'a.', 'Steep; precipitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steer', 'a.', 'A young male of the ox kind; especially, a common ox; a\n   castrated taurine male from two to four years old. See the Note under\n   Ox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steer', 'v. t.', 'To castrate; -- said of male calves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steered', 'imp. & p. p.', 'of Steer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steering', 'p. pr. & vb. n.', 'of Steer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steer', 'n.', 'To direct the course of; to guide; to govern; -- applied\n   especially to a vessel in the water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steer', 'v. i.', 'To direct a vessel in its course; to direct ones course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steer', 'v. i.', 'To be directed and governed; to take a direction, or\n   course; to obey the helm; as, the boat steers easily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steer', 'v. i.', 'To conduct ones self; to take or pursue a course of\n   action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steer', 'v. t.', 'A rudder or helm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steer', 'n.', 'A helmsman, a pilot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steerable', 'a.', 'Capable of being steered; dirigible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steerage', 'n.', 'The act or practice of steering, or directing; as, the\n   steerage of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steerage', 'n.', 'The effect of the helm on a ship; the manner in which an\n   individual ship is affected by the helm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steerage', 'n.', 'The hinder part of a vessel; the stern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steerage', 'n.', 'Properly, the space in the after part of a vessel, under\n   the cabin, but used generally to indicate any part of a vessel having\n   the poorest accommodations and occupied by passengers paying the lowest\n   rate of fare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steerage', 'n.', 'Direction; regulation; management; guidance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steerage', 'n.', 'That by which a course is directed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steerageway', 'n.', 'A rate of motion through the water sufficient to\n   render a vessel governable by the helm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steerer', 'n.', 'One who steers; as, a boat steerer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steering', '', 'a. & n. from Steer, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steerless', 'a.', 'Having no rudder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steerling', 'n.', 'A young small steer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steersmen', 'pl. ', 'of Steersman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steersman', 'n.', 'One who steers; the helmsman of a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steersmate', 'n.', 'One who steers; steersman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeved', 'imp. & p. p.', 'of Steeve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeving', 'p. pr. & vb. n.', 'of Steeve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeve', 'v. i.', 'To project upward, or make an angle with the horizon or\n   with the line of a vessels keel; -- said of the bowsprit, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeve', 'v. t.', 'To elevate or fix at an angle with the horizon; -- said\n   of the bowsprit, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeve', 'v. t.', 'To stow, as bales in a vessels hold, by means of a\n   steeve. See Steeve, n. (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeve', 'n.', 'The angle which a bowsprit makes with the horizon, or with\n   the line of the vessels keel; -- called also steeving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeve', 'n.', 'A spar, with a block at one end, used in stowing cotton\n   bales, and similar kinds of cargo which need to be packed tightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeving', 'n.', 'The act or practice of one who steeves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steeving', 'n.', 'See Steeve, n. (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steg', 'n.', 'A gander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steganographist', 'n.', 'One skilled in steganography; a cryptographer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steganography', 'n.', 'The art of writing in cipher, or in characters which\n   are not intelligible except to persons who have the key; cryptography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steganophthalmata', 'n. pl.', 'The Discophora, or Phanerocarpae. Called\n   also Steganophthalmia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steganopod', 'n.', 'One of the Steganopodes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steganopodes', 'n. pl.', 'A division of swimming birds in which all four\n   toes are united by a broad web. It includes the pelicans, cormorants,\n   gannets, and others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steganopodous', 'a.', 'Having all four toes webbed together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stegnosis', 'n.', 'Constipation; also, constriction of the vessels or\n   ducts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stegnotic', 'a.', 'Tending to render costive, or to diminish excretions or\n   discharges generally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stegnotic', 'n.', 'A stegnotic medicine; an astringent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stegocephala', 'n. pl.', 'An extinct order of amphibians found fossil in\n   the Mesozoic rocks; called also Stegocephali, and Labyrinthodonta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stegosauria', 'n. pl.', 'An extinct order of herbivorous dinosaurs,\n   including the genera Stegosaurus, Omosaurus, and their allies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stegosaurus', 'n.', 'A genus of large Jurassic dinosaurs remarkable for a\n   powerful dermal armature of plates and spines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steik', 'v. t.', 'See Steek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stein', 'n. & v.', 'See Steen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steinbock', 'n.', 'The European ibex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steinbock', 'n.', 'A small South African antelope (Nanotragus tragulus)\n   which frequents dry, rocky districts; -- called also steenbok.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steingale', 'n.', 'The stannel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steining', 'n.', 'See Steening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steinkirk', 'n.', 'Same as Steenkirk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steinkle', 'n.', 'The wheater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stelae', 'pl. ', 'of Stela');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stela', 'n.', 'A small column or pillar, used as a monument, milestone,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stele', 'n.', 'Same as Stela.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stele', 'n.', 'A stale, or handle; a stalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stelene', 'a.', 'Resembling, or used as, a stela; columnar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stell', 'v. t.', 'To place or fix firmly or permanently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stell', 'v. t.', 'A prop; a support, as for the feet in standing or\n   cilmbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stell', 'v. t.', 'A partial inclosure made by a wall or trees, to serve as\n   a shelter for sheep or cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stellar', 'a.', 'Alt. of Stellary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stellary', 'a.', 'Of or pertaining to stars; astral; as, a stellar figure;\n   stellary orbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stellary', 'a.', 'Full of stars; starry; as, stellar regions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stellate', 'a.', 'Alt. of Stellated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stellated', 'a.', 'Resembling a star; pointed or radiated, like the emblem\n   of a star.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stellated', 'a.', 'Starlike; having similar parts radiating from a common\n   center; as, stellate flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stellation', 'n.', 'Radiation of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stelled', 'a.', 'Firmly placed or fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steller', 'n.', 'The rytina; -- called also stellerine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stellerid', 'n.', 'A starfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stellerida', 'n. pl.', 'An extensive group of echinoderms, comprising the\n   starfishes and ophiurans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stelleridan', 'n.', 'Alt. of Stelleridean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stelleridean', 'n.', 'A starfish, or brittle star.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stelliferous', 'a.', 'Having, or abounding with, stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stelliform', 'a.', 'Like a star; star-shaped; radiated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stellify', 'v. t.', 'To turn into a star; to cause to appear like a star;\n   to place among the stars, or in heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stellion', 'n.', 'A lizard (Stellio vulgaris), common about the Eastern\n   Mediterranean among ruins. In color it is olive-green, shaded with\n   black, with small stellate spots. Called also hardim, and star lizard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stellionate', 'n.', 'Any fraud not distinguished by a more special name; --\n   chiefly applied to sales of the same property to two different persons,\n   or selling that for ones own which belongs to another, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stellular', 'a.', 'Having the shape or appearance of little stars;\n   radiated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stellular', 'a.', 'Marked with starlike spots of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stellulate', 'a.', 'Minutely stellate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stelmatopoda', 'n. pl.', 'Same as Gymnolaemata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stelography', 'n.', 'The art of writing or inscribing characters on\n   pillars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem', 'v. i.', 'Alt. of Steem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steem', 'v. i.', 'To gleam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem', 'n.', 'Alt. of Steem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steem', 'n.', 'A gleam of light; flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem', 'n.', 'The principal body of a tree, shrub, or plant, of any kind;\n   the main stock; the part which supports the branches or the head or\n   top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem', 'n.', 'A little branch which connects a fruit, flower, or leaf with\n   a main branch; a peduncle, pedicel, or petiole; as, the stem of an\n   apple or a cherry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem', 'n.', 'The stock of a family; a race or generation of progenitors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem', 'n.', 'A branch of a family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem', 'n.', 'A curved piece of timber to which the two sides of a ship are\n   united at the fore end. The lower end of it is scarfed to the keel, and\n   the bowsprit rests upon its upper end. Hence, the forward part of a\n   vessel; the bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem', 'n.', 'Fig.: An advanced or leading position; the lookout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem', 'n.', 'Anything resembling a stem or stalk; as, the stem of a\n   tobacco pipe; the stem of a watch case, or that part to which the ring,\n   by which it is suspended, is attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem', 'n.', 'That part of a plant which bears leaves, or rudiments of\n   leaves, whether rising above ground or wholly subterranean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem', 'n.', 'The entire central axis of a feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem', 'n.', 'The basal portion of the body of one of the Pennatulacea, or\n   of a gorgonian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem', 'n.', 'The short perpendicular line added to the body of a note; the\n   tail of a crotchet, quaver, semiquaver, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem', 'n.', 'The part of an inflected word which remains unchanged (except\n   by euphonic variations) throughout a given inflection; theme; base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem', 'v. t.', 'To remove the stem or stems from; as, to stem cherries; to\n   remove the stem and its appendages (ribs and veins) from; as, to stem\n   tobacco leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem', 'v. t.', 'To ram, as clay, into a blasting hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stemmed', 'imp. & p. p.', 'of Stem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stemming', 'p. pr. & vb. n.', 'of Stem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem', 'v. t.', 'To oppose or cut with, or as with, the stem of a vessel;\n   to resist, or make progress against; to stop or check the flow of, as a\n   current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem', 'v. i.', 'To move forward against an obstacle, as a vessel against a\n   current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem-clasping', 'a.', 'Embracing the stem with its base; amplexicaul; as a\n   leaf or petiole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stemless', 'a.', 'Having no stem; (Bot.) acaulescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stemlet', 'n.', 'A small or young stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stemmata', 'pl. ', 'of Stemma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stemma', 'n.', 'One of the ocelli of an insect. See Ocellus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stemma', 'n.', 'One of the facets of a compound eye of any arthropod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stemmer', 'n.', 'One who, or that which, stems (in any of the senses of the\n   verbs).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stemmery', 'n.', 'A large building in which tobacco is stemmed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stemmy', 'a.', 'Abounding in stems, or mixed with stems; -- said of tea,\n   dried currants, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stemple', 'n.', 'A crossbar of wood in a shaft, serving as a step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stemson', 'n.', 'A piece of curved timber bolted to the stem, keelson, and\n   apron in a ships frame near the bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem-winder', 'n.', 'A stem-winding watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stem-winding', 'a.', 'Wound by mechanism connected with the stem; as, a\n   stem-winding watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stench', 'v. t.', 'To stanch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stench', 'v. i.', 'A smell; an odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stench', 'v. i.', 'An ill smell; an offensive odor; a stink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stench', 'n.', 'To cause to emit a disagreeable odor; to cause to stink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stenchy', 'a.', 'Having a stench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stencil', 'n.', 'A thin plate of metal, leather, or other material, used in\n   painting, marking, etc. The pattern is cut out of the plate, which is\n   then laid flat on the surface to be marked, and the color brushed over\n   it. Called also stencil plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stenciled', 'imp. & p. p.', 'of Stencil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stencilled', '', 'of Stencil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stenciling', 'p. pr. & vb. n.', 'of Stencil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stencilling', '', 'of Stencil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stencil', 'v. t.', 'To mark, paint, or color in figures with stencils; to\n   form or print by means of a stencil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stenciler', 'n.', 'One who paints or colors in figures by means of stencil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stenoderm', 'n.', 'Any species of bat belonging to the genus Stenoderma,\n   native of the West Indies and South America. These bats have a short or\n   rudimentary tail and a peculiarly shaped nose membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stenodermine', 'a.', 'Of or pertaining to the genus Stenoderma, which\n   includes several West Indian and South American nose-leaf bats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stenographed', 'imp. & p. p.', 'of Stenograph');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stenographing', 'p. pr. & vb. n.', 'of Stenograph');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stenograph', 'v. t.', 'To write or report in stenographic characters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stenograph', 'n.', 'A production of stenography; anything written in\n   shorthand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stenographer', 'n.', 'One who is skilled in stenography; a writer of\n   shorthand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stenographic', 'a.', 'Alt. of Stenographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stenographical', 'a.', 'Of or pertaining to stenography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stenographist', 'n.', 'A stenographer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stenography', 'n.', 'The art of writing in shorthand, by using\n   abbreviations or characters for whole words; shorthand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stenophyllous', 'a.', 'Having narrow leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stenosis', 'n.', 'A narrowing of the opening or hollow of any passage,\n   tube, or orifice; as, stenosis of the pylorus. It differs from\n   stricture in being applied especially to diffused rather than localized\n   contractions, and in always indicating an origin organic and not\n   spasmodic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stenostome', 'a.', 'Having a small or narrow mouth; -- said of certain\n   small ground snakes (Opoterodonta), which are unable to dilate their\n   jaws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stente', 'Obs. imp.', 'of Stent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stent', 'obs. p. p.', 'of Stent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stent', 'v. t.', 'To keep within limits; to restrain; to cause to stop, or\n   cease; to stint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stent', 'v. i.', 'To stint; to stop; to cease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stent', 'n.', 'An allotted portion; a stint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stenting', 'n.', 'An opening in a wall in a coal mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stentor', 'n.', 'A herald, in the Iliad, who had a very loud voice; hence,\n   any person having a powerful voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stentor', 'n.', 'Any species of ciliated Infusoria belonging to the genus\n   Stentor and allied genera, common in fresh water. The stentors have a\n   bell-shaped, or cornucopia-like, body with a circle of cilia around the\n   spiral terminal disk. See Illust. under Heterotricha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stentor', 'n.', 'A howling monkey, or howler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stentorian', 'a.', 'Of or pertaining to a stentor; extremely loud;\n   powerful; as, a stentorian voice; stentorian lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stentorin', 'n.', 'A blue coloring matter found in some stentors. See\n   Stentor, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stentorious', 'a.', 'Stentorian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stentoronic', 'a.', 'Stentorian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stentorophonic', 'a.', 'Speaking or sounding very loud; stentorian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stepped', 'imp. & p. p.', 'of Step');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stepping', 'p. pr. & vb. n.', 'of Step');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'a.', 'To move the foot in walking; to advance or recede by raising\n   and moving one of the feet to another resting place, or by moving both\n   feet in succession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'a.', 'To walk; to go on foot; esp., to walk a little distance; as,\n   to step to one of the neighbors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'a.', 'To walk slowly, gravely, or resolutely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'a.', 'Fig.: To move mentally; to go in imagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'v. t.', 'To set, as the foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'v. t.', 'To fix the foot of (a mast) in its step; to erect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'v. i.', 'An advance or movement made by one removal of the foot; a\n   pace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'v. i.', 'A rest, or one of a set of rests, for the foot in\n   ascending or descending, as a stair, or a round of a ladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'v. i.', 'The space passed over by one movement of the foot in\n   walking or running; as, one step is generally about three feet, but may\n   be more or less. Used also figuratively of any kind of progress; as, he\n   improved step by step, or by steps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'v. i.', 'A small space or distance; as, it is but a step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'v. i.', 'A print of the foot; a footstep; a footprint; track.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'v. i.', 'Gait; manner of walking; as, the approach of a man is\n   often known by his step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'v. i.', 'Proceeding; measure; action; an act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'v. i.', 'Walk; passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'v. i.', 'A portable framework of stairs, much used indoors in\n   reaching to a high position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'v. i.', 'In general, a framing in wood or iron which is intended to\n   receive an upright shaft; specif., a block of wood, or a solid platform\n   upon the keelson, supporting the heel of the mast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'v. i.', 'One of a series of offsets, or parts, resembling the steps\n   of stairs, as one of the series of parts of a cone pulley on which the\n   belt runs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'v. i.', 'A bearing in which the lower extremity of a spindle or a\n   vertical shaft revolves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'v. i.', 'The intervak between two contiguous degrees of the csale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step', 'v. i.', 'A change of position effected by a motion of translation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Step-', '', 'A prefix used before father, mother, brother, sister, son,\n   daughter, child, etc., to indicate that the person thus spoken of is\n   not a blood relative, but is a relative by the marriage of a parent;\n   as, a stepmother to X is the wife of the father of X, married by him\n   after the death of the mother of X. See Stepchild, Stepdaughter,\n   Stepson, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stepbrother', 'n.', 'A brother by the marriage of ones father with the\n   mother of another, or of ones mother with the father of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stepchild', 'n.', 'A bereaved child; one who has lost father or mother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stepchild', 'n.', 'A son or daughter of ones wife or husband by a former\n   marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stepdame', 'n.', 'A stepmother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stepdaughter', 'n.', 'A daughter of ones wife or husband by a former\n   marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stepfather', 'n.', 'The husband of ones mother by a subsequent marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stephanion', 'n.', 'The point on the side of the skull where the temporal\n   line, or upper edge of the temporal fossa, crosses the coronal suture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stephanite', 'n.', 'A sulphide of antimony and silver of an iron-black\n   color and metallic luster; called also black silver, and brittle silver\n   ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stephanotis', 'n.', 'A genus of climbing asclepiadaceous shrubs, of\n   Madagascar, Malaya, etc. They have fleshy or coriaceous opposite\n   leaves, and large white waxy flowers in cymes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stephanotis', 'n.', 'A perfume said to be prepared from the flowers of\n   Stephanotis floribunda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stepladder', 'n.', 'A portable set of steps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stepmother', 'n.', 'The wife of ones father by a subsequent marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stepparent', 'n.', 'Stepfather or stepmother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steppe', 'n.', 'One of the vast plains in Southeastern Europe and in Asia,\n   generally elevated, and free from wood, analogous to many of the\n   prairies in Western North America. See Savanna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stepped', 'a.', 'Provided with a step or steps; having a series of offsets\n   or parts resembling the steps of stairs; as, a stepped key.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stepper', 'n.', 'One who, or that which, steps; as, a quick stepper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stepping-stone', 'n.', 'A stone to raise the feet above the surface of\n   water or mud in walking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stepping-stone', 'n.', 'Fig.: A means of progress or advancement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stepsister', 'n.', 'A daughter of ones stepfather or stepmother by a\n   former marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stepson', 'n.', 'A son of ones husband or wife by a former marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stepstone', 'n.', 'A stone laid before a door as a stair to rise on in\n   entering the house.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ster', '', 'A suffix denoting the agent (originally a woman), especially a\n   person who does something with skill or as an occupation; as in\n   spinster (originally, a woman who spins), songster, baxter (=\n   bakester), youngster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stercobilin', 'n.', 'A coloring matter found in the faeces, a product of\n   the alteration of the bile pigments in the intestinal canal, --\n   identical with hydrobilirubin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stercolin', 'n.', 'Same as Serolin (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stercoraceous', 'a.', 'Of or pertaining to dung; partaking of the nature\n   of, or containing, dung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stercoranism', 'n.', 'The doctrine or belief of the Stercoranists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stercoranist', 'n.', 'A nickname formerly given to those who held, or were\n   alleged to hold, that the consecrated elements in the eucharist undergo\n   the process of digestion in the body of the recipient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stercorarian', 'n.', 'A Stercoranist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stercorary', 'n.', 'A place, properly secured from the weather, for\n   containing dung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stercorate', 'n.', 'Excrement; dung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stercoration', 'n.', 'Manuring with dung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stercorianism', 'n.', 'The doctrine or belief of the Stercoranists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stercorin', 'n.', 'Same as Serolin (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stercory', 'n.', 'Excrement; dung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterculiaceous', 'a.', 'Of or pertaining to a natural order (Sterculiaceae)\n   of polypetalous exogenous plants, mostly tropical. The cacao (Theobroma\n   Cacao) is the most useful plant of the order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stere', 'n.', 'A unit of cubic measure in the metric system, being a cubic\n   meter, or kiloliter, and equal to 35.3 cubic feet, or nearly 1/ cubic\n   yards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stere', 'v. t. & i.', 'To stir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stere', 'n.', 'A rudder. See 5th Steer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stere', 'n.', 'Helmsman. See 6th Steer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterelmintha', 'n. pl.', 'Same as Platyelminthes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereo-', '', 'A combining form meaning solid, hard, firm, as in\n   stereo-chemistry, stereography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereobate', 'n.', 'The lower part or basement of a building or pedestal;\n   -- used loosely for several different forms of basement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereo-chemic', 'a.', 'Alt. of Stereo-chemical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereo-chemical', 'a.', 'Pertaining to, or illustrating, the hypothetical\n   space relations of atoms in the molecule; as, a stereo-chemic formula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereo-chemistry', 'n.', 'Chemistry considered with reference to the space\n   relations of atoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereochrome', 'n.', 'Stereochromic picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereochromic', 'a.', 'Pertaining to the art of stereochromy; produced by\n   stereochromy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereochromy', 'n.', 'A style of painting on plastered walls or stone, in\n   which the colors are rendered permanent by sprinklings of water, in\n   which is mixed a proportion of soluble glass (a silicate of soda).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereoelectric', 'a.', 'Of or pertaining to the generation of electricity\n   by means of solid bodies alone; as, a stereoelectric current is one\n   obtained by means of solids, without any liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereogram', 'n.', 'A diagram or picture which represents objects in such a\n   way as to give the impression of relief or solidity; also, a\n   stereograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereograph', 'n.', 'Any picture, or pair of pictures, prepared for\n   exhibition in the stereoscope. Stereographs are now commonly made by\n   means of photography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereographic', 'a.', 'Alt. of Stereographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereographical', 'a.', 'Made or done according to the rules of\n   stereography; delineated on a plane; as, a stereographic chart of the\n   earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereographically', 'adv.', 'In a stereographical manner; by delineation on\n   a plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereography', 'n.', 'The art of delineating the forms of solid bodies on a\n   plane; a branch of solid geometry which shows the construction of all\n   solids which are regularly defined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereometer', 'n.', 'An instrument for measuring the solid contents of a\n   body, or the capacity of a vessel; a volumenometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereometer', 'n.', 'An instrument for determining the specific gravity of\n   liquid bodies, porous bodies, and powders, as well as solids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereometric', 'a.', 'Alt. of Stereometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereometrical', 'a.', 'Of or pertaining to stereometry; performed or\n   obtained by stereometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereometry', 'n.', 'The art of measuring and computing the cubical\n   contents of bodies and figures; -- distinguished from planimetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereomonoscope', 'n.', 'An instrument with two lenses, by which an image\n   of a single picture projected upon a screen of ground glass is made to\n   present an appearance of relief, and may be viewed by several persons\n   at once.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereoplasm', 'n.', 'The solid or insoluble portion of the cell protoplasm.\n   See Hygroplasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereopticon', 'n.', 'An instrument, consisting essentially of a magic\n   lantern in which photographic pictures are used, by which the image of\n   a landscape, or any object, may be thrown upon a screen in such a\n   manner as to seem to stand out in relief, so as to form a striking and\n   accurate representation of the object itself; also, a pair of magic\n   lanterns for producing the effect of dissolving views.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereoscope', 'n.', 'An optical instrument for giving to pictures the\n   appearance of solid forms, as seen in nature. It combines in one,\n   through a bending of the rays of light, two pictures, taken for the\n   purpose from points of view a little way apart. It is furnished with\n   two eyeglasses, and by refraction or reflection the pictures are\n   superimposed, so as to appear as one to the observer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereoscopic', 'a.', 'Alt. of Stereoscopical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereoscopical', 'a.', 'Of or pertaining to the stereoscope; characteristic\n   of, or adapted to, the stereoscope; as, a stereoscopic effect; the\n   stereoscopic function of the eyeglasses; stereoscopic views.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereoscopist', 'n.', 'One skilled in the use or construction of\n   stereoscopes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereoscopy', 'n.', 'The art or science of using the stereoscope, or of\n   constructing the instrument or the views used with it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereostatic', 'a.', 'Geostatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereotomic', 'a.', 'Alt. of Stereotomical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereotomical', 'a.', 'Of or pertaining to stereotomy; performed by\n   stereotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereotomy', 'n.', 'The science or art of cutting solids into certain\n   figures or sections, as arches, and the like; especially, the art of\n   stonecutting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereotype', 'n.', 'A plate forming an exact faximile of a page of type or\n   of an engraving, used in printing books, etc.; specifically, a plate\n   with type-metal face, used for printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereotype', 'n.', 'The art or process of making such plates, or of\n   executing work by means of them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereotyped', 'imp. & p. p.', 'of Stereotype');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereotyping', 'p. pr. & vb. n.', 'of Stereotype');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereotype', 'v. t.', 'To prepare for printing in stereotype; to make the\n   stereotype plates of; as, to stereotype the Bible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereotype', 'v. t.', 'Fig.: To make firm or permanent; to fix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereotyped', 'a.', 'Formed into, or printed from, stereotype plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereotyped', 'a.', 'Fig.: Formed in a fixed, unchangeable manner; as,\n   stereotyped opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereotyper', 'n.', 'One who stereotypes; one who makes stereotype plates,\n   or works in a stereotype foundry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereotypery', 'n.', 'The art, process, or employment of making stereotype\n   plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereotypery', 'n.', 'A place where stereotype plates are made; a\n   stereotype foundry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereotypic', 'a.', 'Of or pertaining to stereotype, or stereotype plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereotypist', 'n.', 'A stereotyper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereotypographer', 'n.', 'A stereotype printer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereotypography', 'n.', 'The act or art of printing from stereotype\n   plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stereotypy', 'n.', 'The art or process of making stereotype plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterhydraulic', 'a.', 'Pertaining to, or designating, a kind of hydraulic\n   press; resembling such a press in action or principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterile', 'a.', 'Producing little or no crop; barren; unfruitful;\n   unproductive; not fertile; as, sterile land; a sterile desert; a\n   sterile year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterile', 'a.', 'Incapable of reproduction; unfitted for reproduction of\n   offspring; not able to germinate or bear fruit; unfruitful; as, a\n   sterile flower, which bears only stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterile', 'a.', 'Free from reproductive spores or germs; as, a sterile\n   fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterile', 'a.', 'Fig.: Barren of ideas; destitute of sentiment; as, a\n   sterile production or author.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterility', 'n.', 'The quality or condition of being sterile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterility', 'n.', 'Quality of being sterile; infecundity; also, the state\n   of being free from germs or spores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterilization', 'n.', 'The act or process of sterilizing, or rendering\n   sterile; also, the state of being sterile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterilized', 'imp. & p. p.', 'of Sterilize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterilizing', 'p. pr. & vb. n.', 'of Sterilize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterilize', 'v. t.', 'To make sterile or unproductive; to impoverish, as\n   land; to exhaust of fertility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterilize', 'v. t.', 'To deprive of the power of reproducing; to render\n   incapable of germination or fecundation; to make sterile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterilize', 'v. t.', 'To destroy all spores or germs in (an organic fluid\n   or mixture), as by heat, so as to prevent the development of bacterial\n   or other organisms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterlet', 'n.', 'A small sturgeon (Acipenser ruthenus) found in the Caspian\n   Sea and its rivers, and highly esteemed for its flavor. The finest\n   caviare is made from its roe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterling', 'n.', 'Same as Starling, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterling', 'n.', 'Any English coin of standard value; coined money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterling', 'n.', 'A certain standard of quality or value for money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterling', 'a.', 'Belonging to, or relating to, the standard British money\n   of account, or the British coinage; as, a pound sterling; a shilling\n   sterling; a penny sterling; -- now chiefly applied to the lawful money\n   of England; but sterling cost, sterling value, are used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterling', 'a.', 'Genuine; pure; of excellent quality; conforming to the\n   highest standard; of full value; as, a work of sterling merit; a man of\n   sterling good sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stern', 'n.', 'The black tern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stern', 'superl.', 'Having a certain hardness or severity of nature,\n   manner, or aspect; hard; severe; rigid; rigorous; austere; fixed;\n   unchanging; unrelenting; hence, serious; resolute; harsh; as, a\n   sternresolve; a stern necessity; a stern heart; a stern gaze; a stern\n   decree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stern', 'v. t.', 'The helm or tiller of a vessel or boat; also, the rudder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stern', 'v. t.', 'The after or rear end of a ship or other vessel, or of a\n   boat; the part opposite to the stem, or prow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stern', 'v. t.', 'Fig.: The post of management or direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stern', 'v. t.', 'The hinder part of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stern', 'v. t.', 'The tail of an animal; -- now used only of the tail of a\n   dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stern', 'a.', 'Being in the stern, or being astern; as, the stern davits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternage', 'n.', 'Stern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternal', 'a.', 'Of or pertaining to the sternum; in the region of the\n   sternum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternbergite', 'n.', 'A sulphide of silver and iron, occurring in soft\n   flexible laminae varying in color from brown to black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternebrae', 'pl. ', 'of Sternebra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternebra', 'n.', 'One of the segments of the sternum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterned', 'a.', 'Having a stern of a particular shape; -- used in\n   composition; as, square-sterned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterner', 'n.', 'A director.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternforemost', 'adv.', 'With the stern, instead of the bow, in advance;\n   hence, figuratively, in an awkward, blundering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternite', 'n.', 'The sternum of an arthropod somite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternly', 'adv.', 'In a stern manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternmost', 'a.', 'Farthest in the rear; farthest astern; as, the sternmost\n   ship in a convoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternness', 'n.', 'The quality or state of being stern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterno-', '', 'A combining form used in anatomy to indicate connection\n   with, or relation to, the sternum; as, sternocostal, sternoscapular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternocoracoid', 'a.', 'Of or pertaining to the sternum and the coracoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternocostal', 'a.', 'Of or pertaining to the sternum and the ribs; as, the\n   sternocostal cartilages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternohyoid', 'a.', 'Of or pertaining to the sternum and the hyoid bone or\n   cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternomastoid', 'a.', 'Of or pertaining to the sternum and the mastoid\n   process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternothyroid', 'a.', 'Of or pertaining to the sternum and the thyroid\n   cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternpost', 'n.', 'A straight piece of timber, or an iron bar or beam,\n   erected on the extremity of the keel to support the rudder, and receive\n   the ends of the planks or plates of the vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternsman', 'n.', 'A steersman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternson', 'n.', 'The end of a ships keelson, to which the sternpost is\n   bolted; -- called also stern knee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterna', 'pl. ', 'of Sternum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternums', 'pl. ', 'of Sternum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternum', 'n.', 'A plate of cartilage, or a series of bony or cartilaginous\n   plates or segments, in the median line of the pectoral skeleton of most\n   vertebrates above fishes; the breastbone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternum', 'n.', 'The ventral part of any one of the somites of an\n   arthropod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternutation', 'n.', 'The act of sneezing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternutative', 'a.', 'Having the quality of provoking to sneeze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternutatory', 'a.', 'Sternutative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternutatory', 'n.', 'A sternutatory substance or medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sternway', 'n.', 'The movement of a ship backward, or with her stern\n   foremost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stern-wheel', 'a.', 'Having a paddle wheel at the stern; as, a stern-wheel\n   steamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stern-wheeler', 'n.', 'A steamboat having a stern wheel instead of side\n   wheels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterquilinous', 'a.', 'Pertaining to a dunghill; hence, mean; dirty;\n   paltry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterre', 'n.', 'A star.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterrink', 'n.', 'The crab-eating seal (Lobodon carcinophaga) of the\n   Antarctic Ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterrometal', 'n.', 'Any alloy of copper, zinc, tin, and iron, of which\n   cannon are sometimes made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stert', 'p. p.', 'Started.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterte', '', 'p. p. of Start.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stertorious', 'a.', 'Stertorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stertorous', 'a.', 'Characterized by a deep snoring, which accompaines\n   inspiration in some diseases, especially apoplexy; hence, hoarsely\n   breathing; snoring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sterve', 'v. t. & i.', 'To die, or cause to die; to perish. See Starve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stet', 'subj. 3d pers. sing.', 'Let it stand; -- a word used by proof\n   readers to signify that something once erased, or marked for omission,\n   is to remain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stetted', 'imp. & p. p.', 'of Stet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stetting', 'p. pr. & vb. n.', 'of Stet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stet', 'v. t.', 'To cause or direct to remain after having been marked for\n   omission; to mark with the word stet, or with a series of dots below or\n   beside the matter; as, the proof reader stetted a deled footnote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stethal', 'n.', 'One of the higher alcohols of the methane series,\n   homologous with ethal, and found in small quantities as an ethereal\n   salt of stearic acid in spermaceti.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stethograph', 'n.', 'See Pneumatograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stethometer', 'n.', 'An apparatus for measuring the external movements of a\n   given point of the chest wall, during respiration; -- also called\n   thoracometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stethoscope', 'n.', 'An instrument used in auscultation for examining the\n   organs of the chest, as the heart and lungs, by conveying to the ear of\n   the examiner the sounds produced in the thorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stethoscope', 'v. t.', 'To auscultate, or examine, with a stethoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stethoscopic', 'a.', 'Alt. of Stethoscopical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stethoscopical', 'a.', 'Of or pertaining to a stethoscope; obtained or made\n   by means of a stethoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stethoscopist', 'n.', 'One skilled in the use of the stethoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stethoscopy', 'n.', 'The art or process of examination by the stethoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steve', 'v. t.', 'To pack or stow, as cargo in a ships hold. See Steeve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stevedore', 'n.', 'One whose occupation is to load and unload vessels in\n   port; one who stows a cargo in a hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steven', 'n.', 'Voice; speech; language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steven', 'n.', 'An outcry; a loud call; a clamor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stew', 'n.', 'A small pond or pool where fish are kept for the table; a\n   vivarium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stew', 'n.', 'An artificial bed of oysters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stewed', 'imp. & p. p.', 'of Stew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stewing', 'p. pr. & vb. n.', 'of Stew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stew', 'v. t.', 'To boil slowly, or with the simmering or moderate heat; to\n   seethe; to cook in a little liquid, over a gentle fire, without\n   boiling; as, to stew meat; to stew oysters; to stew apples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stew', 'v. i.', 'To be seethed or cooked in a slow, gentle manner, or in\n   heat and moisture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stew', 'v. t.', 'A place of stewing or seething; a place where hot bathes\n   are furnished; a hothouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stew', 'v. t.', 'A brothel; -- usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stew', 'v. t.', 'A prostitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stew', 'v. t.', 'A dish prepared by stewing; as, a stewof pigeons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stew', 'v. t.', 'A state of agitating excitement; a state of worry;\n   confusion; as, to be in a stew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steward', 'n.', 'A man employed in a large family, or on a large estate, to\n   manage the domestic concerns, supervise other servants, collect the\n   rents or income, keep accounts, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steward', 'n.', 'A person employed in a hotel, or a club, or on board a\n   ship, to provide for the table, superintend the culinary affairs, etc.\n   In naval vessels, the captains steward, wardroom steward, steerage\n   steward, warrant officers steward, etc., are petty officers who provide\n   for the messes under their charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steward', 'n.', 'A fiscal agent of certain bodies; as, a steward in a\n   Methodist church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steward', 'n.', 'In some colleges, an officer who provides food for the\n   students and superintends the kitchen; also, an officer who attends to\n   the accounts of the students.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steward', 'n.', 'In Scotland, a magistrate appointed by the crown to\n   exercise jurisdiction over royal lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Steward', 'v. t.', 'To manage as a steward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stewardess', 'n.', 'A female steward; specifically, a woman employed in\n   passenger vessels to attend to the wants of female passengers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stewardly', 'adv.', 'In a manner, or with the care, of a steward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stewardship', 'n.', 'The office of a steward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stewartry', 'n.', 'An overseer or superintendent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stewartry', 'n.', 'The office of a steward; stewardship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stewartry', 'n.', 'In Scotland, the jurisdiction of a steward; also, the\n   lands under such jurisdiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stewish', 'a.', 'Suiting a stew, or brothel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stewpan', 'n.', 'A pan used for stewing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stewpot', 'n.', 'A pot used for stewing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stey', 'n.', 'See Stee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sthenic', 'a.', 'Strong; active; -- said especially of morbid states\n   attended with excessive action of the heart and blood vessels, and\n   characterized by strength and activity of the muscular and nervous\n   system; as, a sthenic fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiacciato', 'n.', 'The lowest relief, -- often used in Italian sculpture\n   of the 15th and 16th centuries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stian', 'n.', 'A sty on the eye. See Styan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stibborn', 'a.', 'Stubborn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stibial', 'a.', 'Like, or having the qualities of, antimony; antimonial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stibialism', 'n.', 'Antimonial intoxication or poisoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stibiated', 'a.', 'Combined or impregnated with antimony (stibium).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stibic', 'a.', 'Antimonic; -- used with reference to certain compounds of\n   antimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stibiconite', 'n.', 'A native oxide of antimony occurring in masses of a\n   yellow color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stibine', 'n.', 'Antimony hydride, or hydrogen antimonide, a colorless gas\n   produced by the action of nascent hydrogen on antimony. It has a\n   characteristic odor and burns with a characteristic greenish flame.\n   Formerly called also antimoniureted hydrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stibious', 'a.', 'Antimonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stibium', 'n.', 'The technical name of antimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stibium', 'n.', 'Stibnite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stibnite', 'n.', 'A mineral of a lead-gray color and brilliant metallic\n   luster, occurring in prismatic crystals; sulphide of antimony; --\n   called also antimony glance, and gray antimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stibonium', 'n.', 'The hypothetical radical SbH4, analogous to ammonium; --\n   called also antimonium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sticcado', 'n.', 'An instrument consisting of small bars of wood, flat at\n   the bottom and rounded at the top, and resting on the edges of a kind\n   of open box. They are unequal in size, gradually increasing from the\n   smallest to the largest, and are tuned to the diatonic scale. The tones\n   are produced by striking the pieces of wood with hard balls attached to\n   flexible sticks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stich', 'n.', 'A verse, of whatever measure or number of feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stich', 'n.', 'A line in the Scriptures; specifically (Hebrew Scriptures),\n   one of the rhythmic lines in the poetical books and passages of the Old\n   Treatment, as written in the oldest Hebrew manuscripts and in the\n   Revised Version of the English Bible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stich', 'n.', 'A row, line, or rank of trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stichic', 'a.', 'Of or pertaining to stichs, or lines; consisting of\n   stichs, or lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stichida', 'pl. ', 'of Stichidium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stichidium', 'n.', 'A special podlike or fusiform branch containing\n   tetraspores. It is found in certain red algae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stichomancy', 'n.', 'Divination by lines, or passages of books, taken at\n   hazard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stichometrical', 'a.', 'Of or pertaining to stichometry; characterized by\n   stichs, or lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stichometry', 'n.', 'Measurement of books by the number of lines which they\n   contain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stichometry', 'n.', 'Division of the text of a book into lines; especially,\n   the division of the text of books into lines accommodated to the sense,\n   -- a method of writing manuscripts used before punctuation was adopted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stichwort', 'n.', 'A kind of chickweed (Stellaria Holostea).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'v. t.', 'A small shoot, or branch, separated, as by a cutting,\n   from a tree or shrub; also, any stem or branch of a tree, of any size,\n   cut for fuel or timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'v. t.', 'Any long and comparatively slender piece of wood, whether\n   in natural form or shaped with tools; a rod; a wand; a staff; as, the\n   stick of a rocket; a walking stick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'v. t.', 'Anything shaped like a stick; as, a stick of wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'v. t.', 'A derogatory expression for a person; one who is inert or\n   stupid; as, an odd stick; a poor stick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'v. t.', 'A composing stick. See under Composing. It is usually a\n   frame of metal, but for posters, handbills, etc., one made of wood is\n   used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'v. t.', 'A thrust with a pointed instrument; a stab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuck', 'imp. & p. p.', 'of Stick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sticked', '', 'of Stick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sticking', 'p. pr. & vb. n.', 'of Stick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'n.', 'To penetrate with a pointed instrument; to pierce; to stab;\n   hence, to kill by piercing; as, to stick a beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'n.', 'To cause to penetrate; to push, thrust, or drive, so as to\n   pierce; as, to stick a needle into ones finger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'n.', 'To fasten, attach, or cause to remain, by thrusting in;\n   hence, also, to adorn or deck with things fastened on as by piercing;\n   as, to stick a pin on the sleeve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'n.', 'To set; to fix in; as, to stick card teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'n.', 'To set with something pointed; as, to stick cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'n.', 'To fix on a pointed instrument; to impale; as, to stick an\n   apple on a fork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'n.', 'To attach by causing to adhere to the surface; as, to stick\n   on a plaster; to stick a stamp on an envelope; also, to attach in any\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'n.', 'To compose; to set, or arrange, in a composing stick; as, to\n   stick type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'n.', 'To run or plane (moldings) in a machine, in\n   contradistinction to working them by hand. Such moldings are said to be\n   stuck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'n.', 'To cause to stick; to bring to a stand; to pose; to puzzle;\n   as, to stick one with a hard problem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'n.', 'To impose upon; to compel to pay; sometimes, to cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'v. i.', 'To adhere; as, glue sticks to the fingers; paste sticks\n   to the wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'v. i.', 'To remain where placed; to be fixed; to hold fast to any\n   position so as to be moved with difficulty; to cling; to abide; to\n   cleave; to be united closely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'v. i.', 'To be prevented from going farther; to stop by reason of\n   some obstacle; to be stayed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'v. i.', 'To be embarrassed or puzzled; to hesitate; to be\n   deterred, as by scruples; to scruple; -- often with at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick', 'v. i.', 'To cause difficulties, scruples, or hesitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sticked', 'imp.', 'Stuck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sticker', 'n.', 'One who, or that which, sticks; as, a bill sticker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sticker', 'n.', 'That which causes one to stick; that which puzzles or\n   poses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sticker', 'n.', 'In the organ, a small wooden rod which connects (in part)\n   a key and a pallet, so as to communicate motion by pushing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sticker', 'n.', 'Same as Paster, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stickfuls', 'pl. ', 'of Stickful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stickful', 'n.', 'As much set type as fills a composing stick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stickiness', 'n.', 'The quality of being sticky; as, the stickiness of glue\n   or paste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sticking', '', 'a. & n. from Stick, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stickit', 'a.', 'Stuck; spoiled in making.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick-lac', 'n.', 'See the Note under Lac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stickled', 'imp. & p. p.', 'of Stickle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stickling', 'p. pr. & vb. n.', 'of Stickle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stickle', 'v. i.', 'To separate combatants by intervening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stickle', 'v. i.', 'To contend, contest, or altercate, esp. in a\n   pertinacious manner on insufficient grounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stickle', 'v. i.', 'To play fast and loose; to pass from one side to the\n   other; to trim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stickle', 'v. t.', 'To separate, as combatants; hence, to quiet, to\n   appease, as disputants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stickle', 'v. t.', 'To intervene in; to stop, or put an end to, by\n   intervening; hence, to arbitrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stickle', 'v. t. & i.', 'A shallow rapid in a river; also, the current\n   below a waterfall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stickleback', 'v. t.', 'Any one of numerous species of small fishes of the\n   genus Gasterosteus and allied genera. The back is armed with two or\n   more sharp spines. They inhabit both salt and brackish water, and\n   construct curious nests. Called also sticklebag, sharpling, and\n   prickleback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stickler', 'v. t.', 'One who stickles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stickler', 'v. t.', 'One who arbitrates a duel; a sidesman to a fencer; a\n   second; an umpire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stickler', 'v. t.', 'One who pertinaciously contends for some trifling\n   things, as a point of etiquette; an unreasonable, obstinate contender;\n   as, a stickler for ceremony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick-seed', 'n.', 'A plant (Echinospermum Lappula) of the Borage family,\n   with small blue flowers and prickly nutlets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sticktail', 'n.', 'The ruddy duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stick-tight', 'n.', 'Beggars ticks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sticky', 'superl.', 'Having the quality of sticking to a surface; adhesive;\n   gluey; viscous; viscid; glutinous; tenacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiddy', 'n.', 'An anvil; also, a smith shop. See Stithy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiff', 'superl.', 'Not easily bent; not flexible or pliant; not limber or\n   flaccid; rigid; firm; as, stiff wood, paper, joints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiff', 'superl.', 'Not liquid or fluid; thick and tenacious; inspissated;\n   neither soft nor hard; as, the paste is stiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiff', 'superl.', 'Firm; strong; violent; difficult to oppose; as, a stiff\n   gale or breeze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiff', 'superl.', 'Not easily subdued; unyielding; stubborn; obstinate;\n   pertinacious; as, a stiff adversary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiff', 'superl.', 'Not natural and easy; formal; constrained; affected;\n   starched; as, stiff behavior; a stiff style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiff', 'superl.', 'Harsh; disagreeable; severe; hard to bear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiff', 'superl.', 'Bearing a press of canvas without careening much; as, a\n   stiff vessel; -- opposed to crank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiff', 'superl.', 'Very large, strong, or costly; powerful; as, a stiff\n   charge; a stiff price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiff-backed', 'a.', 'Obstinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiffened', 'imp. & p. p.', 'of Stiffen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiffening', 'p. pr. & vb. n.', 'of Stiffen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiffen', 'v. t.', 'To make stiff; to make less pliant or flexible; as, to\n   stiffen cloth with starch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiffen', 'v. t.', 'To inspissate; to make more thick or viscous; as, to\n   stiffen paste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiffen', 'v. t.', 'To make torpid; to benumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiffen', 'v. i.', 'To become stiff or stiffer, in any sense of the\n   adjective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiffener', 'n.', 'One who, or that which, stiffens anything, as a piece of\n   stiff cloth in a cravat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiffening', 'n.', 'Act or process of making stiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiffening', 'n.', 'Something used to make anything stiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiff-hearted', 'a.', 'Obstinate; stubborn; contumacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiffish', 'a.', 'Somewhat stiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiffly', 'adv.', 'In a stiff manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiff-necked', 'a.', 'Stubborn; inflexibly obstinate; contumacious; as,\n   stiff-necked pride; a stiff-necked people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiff-neckedness', 'n.', 'The quality or state of being stiff-necked;\n   stubbornness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiffness', 'n.', 'The quality or state of being stiff; as, the stiffness\n   of cloth or of paste; stiffness of manner; stiffness of character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stifftail', 'n.', 'The ruddy duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiff-tailed', 'a.', 'Having the quill feathers of the tail somewhat rigid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stifle', 'n.', 'The joint next above the hock, and near the flank, in the\n   hind leg of the horse and allied animals; the joint corresponding to\n   the knee in man; -- called also stifle joint. See Illust. under Horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stifled', 'imp. & p. p.', 'of Stifle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stifling', 'p. pr. & vb. n.', 'of Stifle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stifle', 'v. t.', 'To stop the breath of by crowding something into the\n   windpipe, or introducing an irrespirable substance into the lungs; to\n   choke; to suffocate; to cause the death of by such means; as, to stifle\n   one with smoke or dust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stifle', 'v. t.', 'To stop; to extinguish; to deaden; to quench; as, to\n   stifle the breath; to stifle a fire or flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stifle', 'v. t.', 'To suppress the manifestation or report of; to smother;\n   to conceal from public knowledge; as, to stifle a story; to stifle\n   passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stifle', 'v. i.', 'To die by reason of obstruction of the breath, or\n   because some noxious substance prevents respiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stifled', 'a.', 'Stifling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stifler', 'n.', 'One who, or that which, stifles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stifler', 'n.', 'See Camouflet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmas', 'pl. ', 'of Stigma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmata', 'pl. ', 'of Stigma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigma', 'v. t.', 'A mark made with a burning iron; a brand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigma', 'v. t.', 'Any mark of infamy or disgrace; sign of moral blemish;\n   stain or reproach caused by dishonorable conduct; reproachful\n   characterization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigma', 'v. t.', 'That part of a pistil which has no epidermis, and is\n   fitted to receive the pollen. It is usually the terminal portion, and\n   is commonly somewhat glutinous or viscid. See Illust. of Stamen and of\n   Flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigma', 'v. t.', 'A small spot, mark, scar, or a minute hole; -- applied\n   especially to a spot on the outer surface of a Graafian follicle, and\n   to spots of intercellular substance in scaly epithelium, or to minute\n   holes in such spots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigma', 'v. t.', 'A red speck upon the skin, produced either by the\n   extravasation of blood, as in the bloody sweat characteristic of\n   certain varieties of religious ecstasy, or by capillary congestion, as\n   in the case of drunkards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigma', 'v. t.', 'One of the external openings of the tracheae of insects,\n   myriapods, and other arthropods; a spiracle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigma', 'v. t.', 'One of the apertures of the pulmonary sacs of arachnids.\n   See Illust. of Scorpion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigma', 'v. t.', 'One of the apertures of the gill of an ascidian, and of\n   Amphioxus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigma', 'v. t.', 'A point so connected by any law whatever with another\n   point, called an index, that as the index moves in any manner in a\n   plane the first point or stigma moves in a determinate way in the same\n   plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigma', 'v. t.', 'Marks believed to have been supernaturally impressed\n   upon the bodies of certain persons in imitation of the wounds on the\n   crucified body of Christ. See def. 5, above.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmaria', 'n.', 'The fossil root stem of a coal plant of the genus\n   Sigillaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmata', 'n.', 'pl. of Stigma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmatic', 'n.', 'A notorious profligate or criminal who has been branded;\n   one who bears the marks of infamy or punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmatic', 'n.', 'A person who is marked or deformed by nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmatic', 'n.', 'A person bearing the wounds on the hands and feet\n   resembling those of Jesus Christ caused by His crucifixion; -- for true\n   stigmantics the wounds are supposed to have been caused miraculously,\n   as a sign of great holiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmatic', 'a.', 'Alt. of Stigmatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmatical', 'a.', 'Marked with a stigma, or with something reproachful to\n   character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmatical', 'a.', 'Impressing with infamy or reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmatical', 'a.', 'Of or pertaining to a stigma or stigmata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmatically', 'adv.', 'With a stigma, or mark of infamy or deformity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmatist', 'n.', 'One believed to be supernaturally impressed with the\n   marks of Christs wounds. See Stigma, 8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmatization', 'n.', 'The act of stigmatizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmatization', 'n.', 'The production of stigmata upon the body. See\n   Stigma, 8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmatized', 'imp. & p. p.', 'of Stigmatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmatizing', 'p. pr. & vb. n.', 'of Stigmatize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmatize', 'v. t.', 'To mark with a stigma, or brand; as, the ancients\n   stigmatized their slaves and soldiers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmatize', 'v. t.', 'To set a mark of disgrace on; to brand with some\n   mark of reproach or infamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigmatose', 'a.', 'Same as Stigmatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stigonomancy', 'n.', 'Divination by writing on the bark of a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stike', 'n.', 'Stanza.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilar', 'a.', 'Of or pertaining to the style of a dial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilbene', 'n.', 'A hydrocarbon, C14H12, produced artificially in large,\n   fine crystals; -- called also diphenyl ethylene, toluylene, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilbite', 'n.', 'A common mineral of the zeolite family, a hydrous\n   silicate of alumina and lime, usually occurring in sheaflike\n   aggregations of crystals, also in radiated masses. It is of a white or\n   yellowish color, with pearly luster on the cleavage surface. Called\n   also desmine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stile', 'n.', 'A pin set on the face of a dial, to cast a shadow; a style.\n   See Style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stile', 'n.', 'Mode of composition. See Style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stile', 'v. i.', 'A step, or set of steps, for ascending and descending, in\n   passing a fence or wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stile', 'v. i.', 'One of the upright pieces in a frame; one of the primary\n   members of a frame, into which the secondary members are mortised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilet', 'n.', 'A stiletto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilet', 'n.', 'See Stylet, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilettos', 'pl. ', 'of Stiletto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiletto', 'n.', 'A kind of dagger with a slender, rounded, and pointed\n   blade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiletto', 'n.', 'A pointed instrument for making eyelet holes in\n   embroidery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiletto', 'n.', 'A beard trimmed into a pointed form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilettoed', 'imp. & p. p.', 'of Stiletto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilettoing', 'p. pr. & vb. n.', 'of Stiletto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiletto', 'v. t.', 'To stab or kill with a stiletto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'adv.', 'Motionless; at rest; quiet; as, to stand still; to lie or\n   sit still.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'adv.', 'Uttering no sound; silent; as, the audience is still; the\n   animals are still.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'adv.', 'Not disturbed by noise or agitation; quiet; calm; as, a\n   still evening; a still atmosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'adv.', 'Comparatively quiet or silent; soft; gentle; low.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'adv.', 'Constant; continual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'adv.', 'Not effervescing; not sparkling; as, still wines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'n.', 'Freedom from noise; calm; silence; as, the still of\n   midnight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'n.', 'A steep hill or ascent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'a.', 'To this time; until and during the time now present; now no\n   less than before; yet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'a.', 'In the future as now and before.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'a.', 'In continuation by successive or repeated acts; always;\n   ever; constantly; uniformly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'a.', 'In an increasing or additional degree; even more; -- much\n   used with comparatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'a.', 'Notwithstanding what has been said or done; in spite of what\n   has occured; nevertheless; -- sometimes used as a conjunction. See\n   Synonym of But.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'a.', 'After that; after what is stated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilled', 'imp. & p. p.', 'of Still');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilling', 'p. pr. & vb. n.', 'of Still');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'a.', 'To stop, as motion or agitation; to cause to become quiet,\n   or comparatively quiet; to check the agitation of; as, to still the\n   raging sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'a.', 'To stop, as noise; to silence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'a.', 'To appease; to calm; to quiet, as tumult, agitation, or\n   excitement; as, to still the passions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'v.', 'A vessel, boiler, or copper used in the distillation of\n   liquids; specifically, one used for the distillation of alcoholic\n   liquors; a retort. The name is sometimes applied to the whole apparatus\n   used in in vaporization and condensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'v.', 'A house where liquors are distilled; a distillery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'v. t.', 'To cause to fall by drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'v. t.', 'To expel spirit from by heat, or to evaporate and\n   condense in a refrigeratory; to distill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still', 'v. i.', 'To drop, or flow in drops; to distill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stillage', 'n.', 'A low stool to keep the goods from touching the floor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stillatitious', 'a.', 'Falling in drops; drawn by a still.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ries', 'pl. ', 'of Stillatory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stillatory', 'a.', 'An alembic; a vessel for distillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stillatory', 'a.', 'A laboratory; a place or room in which distillation is\n   performed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stillbirth', 'n.', 'The birth of a dead fetus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stillborn', 'a.', 'Dead at the birth; as, a stillborn child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stillborn', 'a.', 'Fig.: Abortive; as, a stillborn poem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still-burn', 'p. pr. & vb. n.', 'To burn in the process of distillation;\n   as, to still-burn brandy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still-closing', 'a.', 'Ever closing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiller', 'n.', 'One who stills, or quiets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stillhouse', 'n.', 'A house in which distillation is carried on; a\n   distillery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Still-hunt', 'n.', 'A hunting for game in a quiet and cautious manner, or\n   under cover; stalking; hence, colloquially, the pursuit of any object\n   quietly and cautiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stillicide', 'n.', 'A continual falling or succession of drops; rain water\n   falling from the eaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stillicidious', 'a.', 'Falling in drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilliform', 'a.', 'Having the form of a drop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilling', 'n.', 'A stillion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stillion', 'n.', 'A stand, as for casks or vats in a brewery, or for\n   pottery while drying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stillness', 'n.', 'The quality or state of being still; quietness; silence;\n   calmness; inactivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stillness', 'n.', 'Habitual silence or quiet; taciturnity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stillroom', 'n.', 'A room for distilling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stillroom', 'n.', 'An apartment in a house where liquors, preserves, and\n   the like, are kept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stillstand', 'n.', 'A standstill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilly', 'a.', 'Still; quiet; calm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilly', 'adv.', 'In a still manner; quietly; silently; softly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilpnomelane', 'n.', 'A black or greenish black mineral occurring in\n   foliated flates, also in velvety bronze-colored incrustations. It is a\n   hydrous silicate of iron and alumina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilt', 'n.', 'A pole, or piece of wood, constructed with a step or loop to\n   raise the foot above the ground in walking. It is sometimes lashed to\n   the leg, and sometimes prolonged upward so as to be steadied by the\n   hand or arm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilt', 'n.', 'A crutch; also, the handle of a plow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilt', 'n.', 'Any species of limicoline birds belonging to Himantopus and\n   allied genera, in which the legs are remarkably long and slender.\n   Called also longshanks, stiltbird, stilt plover, and lawyer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilted', 'imp. & p. p.', 'of Stilt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilting', 'p. pr. & vb. n.', 'of Stilt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilt', 'v. t.', 'To raise on stilts, or as if on stilts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiltbird', 'n.', 'See Stilt, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilted', 'a.', 'Elevated as if on stilts; hence, pompous; bombastic; as, a\n   stilted style; stilted declamation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiltify', 'v. t.', 'To raise upon stilts, or as upon stilts; to stilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stilty', 'a.', 'Unreasonably elevated; pompous; stilted; as, a stilty\n   style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stime', 'n.', 'A slight gleam or glimmer; a glimpse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stimulant', 'a.', 'Serving to stimulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stimulant', 'a.', 'Produced increased vital action in the organism, or in\n   any of its parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stimulant', 'n.', 'That which stimulates, provokes, or excites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stimulant', 'n.', 'An agent which produces a temporary increase of vital\n   activity in the organism, or in any of its parts; -- sometimes used\n   without qualification to signify an alcoholic beverage used as a\n   stimulant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stimulated', 'imp. & p. p.', 'of Stimulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stimulating', 'p. pr. & vb. n.', 'of Stimulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stimulate', 'v. t.', 'To excite as if with a goad; to excite, rouse, or\n   animate, to action or more vigorous exertion by some pungent motive or\n   by persuasion; as, to stimulate one by the hope of reward, or by the\n   prospect of glory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stimulate', 'v. t.', 'To excite; to irritate; especially, to excite the\n   activity of (a nerve or an irritable muscle), as by electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stimulation', 'n.', 'The act of stimulating, or the state of being\n   stimulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stimulation', 'n.', 'The irritating action of various agents (stimuli) on\n   muscles, nerves, or a sensory end organ, by which activity is evoked;\n   especially, the nervous impulse produced by various agents on nerves,\n   or a sensory end organ, by which the part connected with the nerve is\n   thrown into a state of activity; irritation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stimulative', 'a.', 'Having the quality of stimulating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stimulative', 'n.', 'That which stimulates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stimulator', 'n.', 'One who stimulates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stimulatress', 'n.', 'A woman who stimulates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stimulism', 'n.', 'The theory of medical practice which regarded life as\n   dependent upon stimulation, or excitation, and disease as caused by\n   excess or deficiency in the amount of stimulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stimulism', 'n.', 'The practice of treating disease by alcoholic\n   stimulants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stimuli', 'pl. ', 'of Stimulus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stimulus', 'v. t.', 'A goad; hence, something that rouses the mind or\n   spirits; an incentive; as, the hope of gain is a powerful stimulus to\n   labor and action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stimulus', 'v. t.', 'That which excites or produces a temporary increase of\n   vital action, either in the whole organism or in any of its parts;\n   especially (Physiol.), any substance or agent capable of evoking the\n   activity of a nerve or irritable muscle, or capable of producing an\n   impression upon a sensory organ or more particularly upon its specific\n   end organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sting', 'v. t.', 'Any sharp organ of offense and defense, especially when\n   connected with a poison gland, and adapted to inflict a wound by\n   piercing; as the caudal sting of a scorpion. The sting of a bee or wasp\n   is a modified ovipositor. The caudal sting, or spine, of a sting ray is\n   a modified dorsal fin ray. The term is sometimes applied to the fang of\n   a serpent. See Illust. of Scorpion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sting', 'v. t.', 'A sharp-pointed hollow hair seated on a gland which\n   secrets an acrid fluid, as in nettles. The points of these hairs\n   usually break off in the wound, and the acrid fluid is pressed into it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sting', 'v. t.', 'Anything that gives acute pain, bodily or mental; as, the\n   stings of remorse; the stings of reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sting', 'v. t.', 'The thrust of a sting into the flesh; the act of\n   stinging; a wound inflicted by stinging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sting', 'v. t.', 'A goad; incitement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sting', 'v. t.', 'The point of an epigram or other sarcastic saying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stung', 'imp. & p. p.', 'of Sting');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stang', '', 'of Sting');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinging', 'p. pr. & vb. n.', 'of Sting');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sting', 'v. t.', 'To pierce or wound with a sting; as, bees will sting an\n   animal that irritates them; the nettles stung his hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sting', 'v. t.', 'To pain acutely; as, the conscience is stung with\n   remorse; to bite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sting', 'v. t.', 'To goad; to incite, as by taunts or reproaches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stingaree', 'n.', 'Any sting ray. See under 6th Ray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stingbull', 'n.', 'The European greater weever fish (Trachinus draco),\n   which is capable of inflicting severe wounds with the spinous rays of\n   its dorsal fin. See Weever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinger', 'n.', 'One who, or that which, stings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stingfish', 'n.', 'The weever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stingily', 'adv.', 'In a stingy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinginess', 'n.', 'The quality or state of being stingy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinging', 'a.', 'Piercing, or capable of piercing, with a sting;\n   inflicting acute pain as if with a sting, goad, or pointed weapon;\n   pungent; biting; as, stinging cold; a stinging rebuke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stingless', 'a.', 'Having no sting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stingo', 'n.', 'Old beer; sharp or strong liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stingtail', 'n.', 'A sting ray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stingy', 'a.', 'Stinging; able to sting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stingy', 'superl.', 'Extremely close and covetous; meanly avaricious;\n   niggardly; miserly; penurious; as, a stingy churl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stunk', 'imp. & p. p.', 'of Stink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stank', '', 'of Stink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinking', 'p. pr. & vb. n.', 'of Stink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stink', 'v. i.', 'To emit a strong, offensive smell; to send out a\n   disgusting odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stink', 'v. t.', 'To cause to stink; to affect by a stink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stink', 'n.', 'A strong, offensive smell; a disgusting odor; a stench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinkard', 'n.', 'A mean, stinking, paltry fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinkard', 'n.', 'The teledu of the East Indies. It emits a disagreeable\n   odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinkball', 'n.', 'A composition of substances which in combustion emit a\n   suffocating odor; -- used formerly in naval warfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinker', 'n.', 'One who, or that which, stinks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinker', 'n.', 'Any one of the several species of large antarctic petrels\n   which feed on blubber and carrion and have an offensive odor, as the\n   giant fulmar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinkhorn', 'n.', 'A kind of fungus of the genus Phallus, which emits a\n   fetid odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinking', '', 'a. & n. from Stink, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinkingly', 'adv.', 'In a stinking manner; with an offensive smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinkpot', 'n.', 'An earthen jar charged with powder, grenades, and other\n   materials of an offensive and suffocating smell, -- sometimes used in\n   boarding an enemys vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinkpot', 'n.', 'A vessel in which disinfectants are burned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinkpot', 'n.', 'The musk turtle, or musk tortoise. See under Musk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinkstone', 'n.', 'One of the varieties of calcite, barite, and feldspar,\n   which emit a fetid odor on being struck; -- called also swinestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinkweed', 'n.', 'Stramonium. See Jamestown weed, and Datura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinkwood', 'n.', 'A name given to several kinds of wood with an unpleasant\n   smell, as that of the Foetidia Mauritiana of the Mauritius, and that of\n   the South African Ocotea bullata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stint', 'n.', 'Any one of several species of small sandpipers, as the\n   sanderling of Europe and America, the dunlin, the little stint of India\n   (Tringa minuta), etc. Called also pume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stint', 'n.', 'A phalarope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinted', 'imp. & p. p.', 'of Stint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinting', 'p. pr. & vb. n.', 'of Stint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stint', 'v. t.', 'To restrain within certain limits; to bound; to confine;\n   to restrain; to restrict to a scant allowance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stint', 'v. t.', 'To put an end to; to stop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stint', 'v. t.', 'To assign a certain (i. e., limited) task to (a person),\n   upon the performance of which one is excused from further labor for the\n   day or for a certain time; to stent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stint', 'v. t.', 'To serve successfully; to get with foal; -- said of\n   mares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stint', 'v. i.', 'To stop; to cease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stint', 'v. t.', 'Limit; bound; restraint; extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stint', 'v. t.', 'Quantity or task assigned; proportion allotted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stintance', 'n.', 'Restraint; stoppage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stintedness', 'n.', 'The state of being stinted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stinter', 'n.', 'One who, or that which, stints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stintless', 'a.', 'Without stint or restraint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipe', 'n.', 'The stalk or petiole of a frond, as of a fern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipe', 'n.', 'The stalk of a pistil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipe', 'n.', 'The trunk of a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipe', 'n.', 'The stem of a fungus or mushroom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipel', 'n.', 'The stipule of a leaflet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipellate', 'a.', 'Having stipels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipend', 'n.', 'Settled pay or compensation for services, whether paid\n   daily, monthly, or annually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipend', 'v. t.', 'To pay by settled wages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipendiarian', 'a.', 'Acting from mercenary considerations; stipendiary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipendiary', 'a.', 'Receiving wages, or salary; performing services for a\n   stated price or compensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipendiaries', 'pl. ', 'of Stipendiary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipendiary', 'n.', 'One who receives a stipend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipendiate', 'v. t.', 'To provide with a stipend, or salary; to support;\n   to pay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipendless', 'a.', 'Having no stipend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipites', 'pl. ', 'of Stipes');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipes', 'n.', 'The second joint of a maxilla of an insect or a crustacean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipes', 'n.', 'An eyestalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipitate', 'a.', 'Supported by a stipe; elevated on a stipe, as the fronds\n   of most ferns, or the pod of certain cruciferous plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipitiform', 'a.', 'Having the shape of a stalk; stalklike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stippled', 'imp. & p. p.', 'of Stipple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stippling', 'p. pr. & vb. n.', 'of Stipple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipple', 'v. t.', 'To engrave by means of dots, in distinction from\n   engraving in lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipple', 'v. t.', 'To paint, as in water colors, by small, short touches\n   which together produce an even or softly graded surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipple', 'n.', 'Alt. of Stippling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stippling', 'n.', 'A mode of execution which produces the effect by dots or\n   small points instead of lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stippling', 'n.', 'A mode of execution in which a flat or even tint is\n   produced by many small touches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiptic', 'a. & n.', 'See Styptic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipulas', 'pl. ', 'of Stipula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipulae', 'pl. ', 'of Stipula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipula', 'n.', 'A stipule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipula', 'n.', 'A newly sprouted feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipulaceous', 'a.', 'Alt. of Stipular');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipular', 'a.', 'Of or pertaining to stipules; resembling stipules;\n   furnished with stipules; growing on stipules, or close to them;\n   occupying the position of stipules; as, stipular glands and stipular\n   tendrils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipulary', 'a.', 'Of or pertaining to stipules; stipular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipulate', 'a.', 'Furnished with stipules; as, a stipulate leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipulated', 'imp. & p. p.', 'of Stipulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipulating', 'p. pr. & vb. n.', 'of Stipulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipulate', 'v. i.', 'To make an agreement or covenant with any person or\n   company to do or forbear anything; to bargain; to contract; to settle\n   terms; as, certain princes stipulated to assist each other in resisting\n   the armies of France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipulation',    'n.',    'The act of stipulating; a contracting or bargaining;\n   an agreement.');
