INSERT INTO words (word, word_type, description)VALUES  (    'Stipulation',    'n.',    'That which is stipulated, or agreed upon; that which\n   is definitely arranged or contracted; an agreement; a covenant; a\n   contract or bargain; also, any particular article, item, or condition,\n   in a mutual agreement; as, the stipulations of the allied powers to\n   furnish each his contingent of troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipulation', 'n.', 'A material article of an agreement; an undertaking in\n   the nature of bail taken in the admiralty courts; a bargain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipulation', 'n.', 'The situation, arrangement, and structure of the\n   stipules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipulator', 'n.', 'One who stipulates, contracts, or covenants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipule', 'n.', 'An appendage at the base of petioles or leaves, usually\n   somewhat resembling a small leaf in texture and appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stipuled', 'a.', 'Furnished with stipules, or leafy appendages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stirred', 'imp. & p. p.', 'of Stir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stirring', 'p. pr. & vb. n.', 'of Stir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stir', 'v. t.', 'To change the place of in any manner; to move.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stir', 'v. t.', 'To disturb the relative position of the particles of, as\n   of a liquid, by passing something through it; to agitate; as, to stir a\n   pudding with a spoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stir', 'v. t.', 'To bring into debate; to agitate; to moot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stir', 'v. t.', 'To incite to action; to arouse; to instigate; to prompt;\n   to excite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stir', 'v. i.', 'To move; to change ones position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stir', 'v. i.', 'To be in motion; to be active or bustling; to exert or\n   busy ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stir', 'v. i.', 'To become the object of notice; to be on foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stir', 'v. i.', 'To rise, or be up, in the morning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stir', 'n.', 'The act or result of stirring; agitation; tumult; bustle;\n   noise or various movements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stir', 'n.', 'Public disturbance or commotion; tumultuous disorder;\n   seditious uproar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stir', 'n.', 'Agitation of thoughts; conflicting passions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stirabout', 'n.', 'A dish formed of oatmeal boiled in water to a certain\n   consistency and frequently stirred, or of oatmeal and dripping mixed\n   together and stirred about in a pan; a hasty pudding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiriated', 'a.', 'Adorned with pendants like icicles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stirious', 'a.', 'Resembling icicles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stirk', 'n.', 'A young bullock or heifer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stirless', 'a.', 'Without stirring; very quiet; motionless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stirp', 'n.', 'Stock; race; family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stirpiculture', 'n.', 'The breeding of special stocks or races.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stirpes', 'pl. ', 'of Stirps');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stirps', 'n.', 'Stock; race; family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stirps', 'n.', 'A race, or a fixed and permanent variety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stirrage', 'n.', 'The act of stirring; stir; commotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stirrer', 'n.', 'One who, or that which, stirs something; also, one who\n   moves about, especially after sleep; as, an early stirrer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stirring', 'a.', 'Putting in motion, or being in motion; active; active in\n   business; habitually employed in some kind of business; accustomed to a\n   busy life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stirrup', 'v. i.', 'A kind of ring, or bent piece of metal, wood, leather,\n   or the like, horizontal in one part for receiving the foot of a rider,\n   and attached by a strap to the saddle, -- used to assist a person in\n   mounting a horse, and to enable him to sit steadily in riding, as well\n   as to relieve him by supporting a part of the weight of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stirrup', 'v. i.', 'Any piece resembling in shape the stirrup of a saddle,\n   and used as a support, clamp, etc. See Bridle iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stirrup', 'v. i.', 'A rope secured to a yard, with a thimble in its lower\n   end for supporting a footrope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stirt', 'v. i.', 'Started; leaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stirte', '', 'imp. of Start, v. i. & t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stitch', 'v. i.', 'A single pass of a needle in sewing; the loop or turn of\n   the thread thus made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stitch', 'v. i.', 'A single turn of the thread round a needle in knitting;\n   a link, or loop, of yarn; as, to let down, or drop, a stitch; to take\n   up a stitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stitch', 'v. i.', 'A space of work taken up, or gone over, in a single pass\n   of the needle; hence, by extension, any space passed over; distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stitch', 'v. i.', 'A local sharp pain; an acute pain, like the piercing of\n   a needle; as, a stitch in the side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stitch', 'v. i.', 'A contortion, or twist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stitch', 'v. i.', 'Any least part of a fabric or dress; as, to wet every\n   stitch of clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stitch', 'v. i.', 'A furrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stitched', 'imp. & p. p.', 'of Stitch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stitching', 'p. pr. & vb. n.', 'of Stitch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stitch', 'v. t.', 'To form stitches in; especially, to sew in such a manner\n   as to show on the surface a continuous line of stitches; as, to stitch\n   a shirt bosom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stitch', 'v. t.', 'To sew, or unite together by stitches; as, to stitch\n   printed sheets in making a book or a pamphlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stitch', 'v. t.', 'To form land into ridges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stitch', 'v. i.', 'To practice stitching, or needlework.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stitchel', 'n.', 'A kind of hairy wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stitcher', 'n.', 'One who stitches; a seamstress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stitchery', 'n.', 'Needlework; -- in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stitching', 'n.', 'The act of one who stitches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stitching', 'n.', 'Work done by sewing, esp. when a continuous line of\n   stitches is shown on the surface; stitches, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stitchwort', 'n.', 'See Stichwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stith', 'a.', 'Strong; stiff; rigid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stith', 'n.', 'An anvil; a stithy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stithy', 'n.', 'An anvil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stithy', 'n.', 'A smiths shop; a smithy; a smithery; a forge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stithy', 'v. t.', 'To forge on an anvil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stived', 'imp. & p. p.', 'of Stive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiving', 'p. pr. & vb. n.', 'of Stive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stive', 'v. t.', 'To stuff; to crowd; to fill full; hence, to make hot and\n   close; to render stifling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stive', 'v. i.', 'To be stifled or suffocated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stive', 'n.', 'The floating dust in flour mills caused by the operation or\n   grinding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stiver', 'n.', 'A Dutch coin, and money of account, of the value of two\n   cents, or about one penny sterling; hence, figuratively, anything of\n   little worth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stives', 'n. pl.', 'Stews; a brothel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoak', 'v. t.', 'To stop; to choke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoat', 'n.', 'The ermine in its summer pelage, when it is reddish brown,\n   but with a black tip to the tail. The name is sometimes applied also to\n   other brown weasels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stocah', 'n.', 'A menial attendant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoccade', 'n. & v.', 'See Stockade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoccado', 'n.', 'A stab; a thrust with a rapier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stochastic', 'a.', 'Conjectural; able to conjecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'The stem, or main body, of a tree or plant; the fixed,\n   strong, firm part; the trunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'The stem or branch in which a graft is inserted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'A block of wood; something fixed and solid; a pillar; a firm\n   support; a post.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'Hence, a person who is as dull and lifeless as a stock or\n   post; one who has little sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'The principal supporting part; the part in which others are\n   inserted, or to which they are attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'The wood to which the barrel, lock, etc., of a musket or\n   like firearm are secured; also, a long, rectangular piece of wood,\n   which is an important part of several forms of gun carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'The handle or contrivance by which bits are held in boring;\n   a bitstock; a brace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'The block of wood or metal frame which constitutes the body\n   of a plane, and in which the plane iron is fitted; a plane stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'The wooden or iron crosspiece to which the shank of an\n   anchor is attached. See Illust. of Anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'The support of the block in which an anvil is fixed, or of\n   the anvil itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'A handle or wrench forming a holder for the dies for cutting\n   screws; a diestock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'The part of a tally formerly struck in the exchequer, which\n   was delivered to the person who had lent the king money on account, as\n   the evidence of indebtedness. See Counterfoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'The original progenitor; also, the race or line of a family;\n   the progenitor of a family and his direct descendants; lineage; family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'Money or capital which an individual or a firm employs in\n   business; fund; in the United States, the capital of a bank or other\n   company, in the form of transferable shares, each of a certain amount;\n   money funded in government securities, called also the public funds; in\n   the plural, property consisting of shares in joint-stock companies, or\n   in the obligations of a government for its funded debt; -- so in the\n   United States, but in England the latter only are called stocks, and\n   the former shares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'Same as Stock account, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'Supply provided; store; accumulation; especially, a\n   merchants or manufacturers store of goods; as, to lay in a stock of\n   provisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'Domestic animals or beasts collectively, used or raised on a\n   farm; as, a stock of cattle or of sheep, etc.; -- called also live\n   stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'That portion of a pack of cards not distributed to the\n   players at the beginning of certain games, as gleek, etc., but which\n   might be drawn from afterward as occasion required; a bank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'A thrust with a rapier; a stoccado.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'A covering for the leg, or leg and foot; as, upper stocks\n   (breeches); nether stocks (stockings).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'A kind of stiff, wide band or cravat for the neck; as, a\n   silk stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'A frame of timber, with holes in which the feet, or the feet\n   and hands, of criminals were formerly confined by way of punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'The frame or timbers on which a ship rests while building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'Red and gray bricks, used for the exterior of walls and the\n   front of buildings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'Any cruciferous plant of the genus Matthiola; as, common\n   stock (Matthiola incana) (see Gilly-flower); ten-weeks stock (M.\n   annua).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'An irregular metalliferous mass filling a large cavity in a\n   rock formation, as a stock of lead ore deposited in limestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'A race or variety in a species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'In tectology, an aggregate or colony of persons (see\n   Person), as trees, chains of salpae, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'The beater of a fulling mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'n.', 'A liquid or jelly containing the juices and soluble parts of\n   meat, and certain vegetables, etc., extracted by cooking; -- used in\n   making soup, gravy, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stocked', 'imp. & p. p.', 'of Stock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stocking', 'p. pr. & vb. n.', 'of Stock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'v. t.', 'To lay up; to put aside for future use; to store, as\n   merchandise, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'v. t.', 'To provide with material requisites; to store; to fill;\n   to supply; as, to stock a warehouse, that is, to fill it with goods; to\n   stock a farm, that is, to supply it with cattle and tools; to stock\n   land, that is, to occupy it with a permanent growth, especially of\n   grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'v. t.', 'To suffer to retain milk for twenty-four hours or more\n   previous to sale, as cows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'v. t.', 'To put in the stocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock', 'a.', 'Used or employed for constant service or application, as if\n   constituting a portion of a stock or supply; standard; permanent;\n   standing; as, a stock actor; a stock play; a stock sermon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stockade', 'v. t.', 'A line of stout posts or timbers set firmly in the\n   earth in contact with each other (and usually with loopholes) to form a\n   barrier, or defensive fortification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stockade', 'v. t.', 'An inclosure, or pen, made with posts and stakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stockaded', 'imp. & p. p.', 'of Stockade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stockading', 'p. pr. & vb. n.', 'of Stockade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stockade', 'v. t.', 'To surround, fortify, or protect with a stockade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock-blind', 'a.', 'Blind as a stock; wholly blind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stockbroker', 'n.', 'A broker who deals in stocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stockdove', 'n.', 'A common European wild pigeon (Columba aenas), so called\n   because at one time believed to be the stock of the domestic pigeon,\n   or, according to some, from its breeding in the stocks, or trunks, of\n   trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stocker', 'n.', 'One who makes or fits stocks, as of guns or gun carriages,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stockfish', 'n.', 'Salted and dried fish, especially codfish, hake, ling,\n   and torsk; also, codfish dried without being salted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stockfish', 'n.', 'Young fresh cod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stockholder', 'n.', 'One who is a holder or proprietor of stock in the\n   public funds, or in the funds of a bank or other stock company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stockinet', 'n.', 'An elastic textile fabric imitating knitting, of which\n   stockings, under-garments, etc., are made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stocking', 'n.', 'A close-fitting covering for the foot and leg, usually\n   knit or woven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stocking', 'v. t.', 'To dress in GBs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stockinger', 'n.', 'A stocking weaver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stockish', 'a.', 'Like a stock; stupid; blockish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stockjobber', 'n.', 'One who speculates in stocks for gain; one whose\n   occupation is to buy and sell stocks. In England a jobber acts as an\n   intermediary between brokers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stockjobbing', 'n.', 'The act or art of dealing in stocks; the business of\n   a stockjobber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stockmen', 'pl. ', 'of Stockman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stockman', 'n.', 'A herdsman; a ranchman; one owning, or having charge of,\n   herds of live stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stock-still', 'a.', 'Still as a stock, or fixed post; perfectly still.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stockwork', 'n.', 'A system of working in ore, etc., when it lies not in\n   strata or veins, but in solid masses, so as to be worked in chambers or\n   stories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stockwork', 'n.', 'A metalliferous deposit characterized by the\n   impregnation of the mass of rock with many small veins or nests\n   irregularly grouped. This kind of deposit is especially common with tin\n   ore. Such deposits are worked in floors or stories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stocky', 'a.', 'Short and thick; thick rather than tall or corpulent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stocky', 'a.', 'Headstrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stodgy', 'a.', 'Wet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoechiology', 'n.', 'Alt. of Stoechiometry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoechiometry', 'n.', 'See Stoichiology, Stoichiometry, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoic', 'n.', 'A disciple of the philosopher Zeno; one of a Greek sect\n   which held that men should be free from passion, unmoved by joy or\n   grief, and should submit without complaint to unavoidable necessity, by\n   which all things are governed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoic', 'n.', 'Hence, a person not easily excited; an apathetic person; one\n   who is apparently or professedly indifferent to pleasure or pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoic', 'n.', 'Alt. of Stoical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoical', 'n.', 'Of or pertaining to the Stoics; resembling the Stoics or\n   their doctrines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoical', 'n.', 'Not affected by passion; manifesting indifference to\n   pleasure or pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoichiological', 'a.', 'Of or pertaining to stoichiology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoichiology', 'n.', 'That part of the science of physiology which treats\n   of the elements, or principles, composing animal tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoichiology', 'n.', 'The doctrine of the elementary requisites of mere\n   thought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoichiology', 'n.', 'The statement or discussion of the first principles\n   of any science or art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoichiometric', 'a.', 'Alt. of Stoichiometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoichiometrical', 'a.', 'Of or pertaining to stoichiometry; employed in,\n   or obtained by, stoichiometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoichiometry', 'n.', 'The art or process of calculating the atomic\n   proportions, combining weights, and other numerical relations of\n   chemical elements and their compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoicism', 'n.', 'The opinions and maxims of the Stoics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoicism', 'n.', 'A real or pretended indifference to pleasure or pain;\n   insensibility; impassiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoicity', 'n.', 'Stoicism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoke', 'v. t.', 'To stick; to thrust; to stab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoke', 'v. t.', 'To poke or stir up, as a fire; hence, to tend, as the\n   fire of a furnace, boiler, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoke', 'v. i.', 'To poke or stir up a fire; hence, to tend the fires of\n   furnaces, steamers, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stokehole', 'n.', 'The mouth to the grate of a furnace; also, the space in\n   front of the furnace, where the stokers stand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoker', 'v. t.', 'One who is employed to tend a furnace and supply it with\n   fuel, especially the furnace of a locomotive or of a marine steam\n   boiler; also, a machine for feeding fuel to a fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoker', 'v. t.', 'A fire poker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stokey', 'a.', 'Close; sultry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stolae', 'pl. ', 'of Stola');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stola', 'n.', 'A long garment, descending to the ankles, worn by Roman\n   women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stole', '', 'imp. of Steal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stole', 'n.', 'A stolon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stole', 'n.', 'A long, loose garment reaching to the feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stole', 'n.', 'A narrow band of silk or stuff, sometimes enriched with\n   embroidery and jewels, worn on the left shoulder of deacons, and across\n   both shoulders of bishops and priests, pendent on each side nearly to\n   the ground. At Mass, it is worn crossed on the breast by priests. It is\n   used in various sacred functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoled', 'a.', 'Having or wearing a stole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stolen', '', 'p. p. of Steal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stolid', 'a.', 'Hopelessly insensible or stupid; not easily aroused or\n   excited; dull; impassive; foolish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stolidity', 'n.', 'The state or quality of being stolid; dullness of\n   intellect; obtuseness; stupidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stolidness', 'n.', 'Same as Stolidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stolon', 'n.', 'A trailing branch which is disposed to take root at the end\n   or at the joints; a stole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stolon', 'n.', 'An extension of the integument of the body, or of the body\n   wall, from which buds are developed, giving rise to new zooids, and\n   thus forming a compound animal in which the zooids usually remain\n   united by the stolons. Such stolons are often present in Anthozoa,\n   Hydroidea, Bryozoa, and social ascidians. See Illust. under\n   Scyphistoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoloniferous', 'a.', 'Producing stolons; putting forth suckers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomata', 'pl. ', 'of Stoma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoma', 'n.', 'One of the minute apertures between the cells in many serous\n   membranes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoma', 'n.', 'The minute breathing pores of leaves or other organs opening\n   into the intercellular spaces, and usually bordered by two contractile\n   cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoma', 'n.', 'The line of dehiscence of the sporangium of a fern. It is\n   usually marked by two transversely elongated cells. See Illust. of\n   Sporangium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoma', 'n.', 'A stigma. See Stigma, n., 6 (a) & (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomach', 'n.', 'An enlargement, or series of enlargements, in the anterior\n   part of the alimentary canal, in which food is digested; any cavity in\n   which digestion takes place in an animal; a digestive cavity. See\n   Digestion, and Gastric juice, under Gastric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomach', 'n.', 'The desire for food caused by hunger; appetite; as, a good\n   stomach for roast beef.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomach', 'n.', 'Hence appetite in general; inclination; desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomach', 'n.', 'Violence of temper; anger; sullenness; resentment; willful\n   obstinacy; stubbornness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomach', 'n.', 'Pride; haughtiness; arrogance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomached', 'imp. & p. p.', 'of Stomach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomaching', 'p. pr. & vb. n.', 'of Stomach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomach', 'v. t.', 'To resent; to remember with anger; to dislike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomach', 'v. t.', 'To bear without repugnance; to brook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomach', 'v. i.', 'To be angry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomachal', 'a.', 'Of or pertaining to the stomach; gastric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomachal', 'a.', 'Helping the stomach; stomachic; cordial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomachal', 'n.', 'A stomachic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomacher', 'n.', 'One who stomachs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomacher', 'n.', 'An ornamental covering for the breast, worn originally\n   both by men and women. Those worn by women were often richly decorated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomachful', 'a.', 'Willfully obstinate; stubborn; perverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomachic', 'a.', 'Alt. of Stomachical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomachical', 'a.', 'Of or pertaining to the stomach; as, stomachic\n   vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomachical', 'a.', 'Strengthening to the stomach; exciting the action of\n   the stomach; stomachal; cordial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomachic', 'n.', 'A medicine that strengthens the stomach and excites its\n   action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomaching', 'n.', 'Resentment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomachless', 'a.', 'Being without a stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomachless', 'a.', 'Having no appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomachous', 'a.', 'Stout; sullen; obstinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomachy', 'a.', 'Obstinate; sullen; haughty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomapod', 'n.', 'One of the Stomapoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomapoda', 'n. pl.', 'An order of Crustacea including the squillas. The\n   maxillipeds are leglike in form, and the large claws are comblike. They\n   have a large and elongated abdomen, which contains a part of the\n   stomach and heart; the abdominal appendages are large, and bear the\n   gills. Called also Gastrula, Stomatopoda, and Squilloidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomate', 'n.', 'A stoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomatic', 'a.', 'Of or pertaining to a stoma; of the nature of a stoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomatic', 'n.', 'A medicine for diseases of the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomatiferous', 'a.', 'Having or producing stomata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomatitis', 'n.', 'Inflammation of the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomatoda', 'n. pl.', 'A division of Protozoa in which a mouthlike opening\n   exists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomatodaeum', 'n.', 'Same as Stomodaeum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomatode', 'a.', 'Having a mouth; -- applied to certain Protozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomatode', 'n.', 'One of the Stomatoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomatogastric', 'a.', 'Of or pertaining to the mouth and the stomach; as,\n   the stomatogastric ganglion of certain Mollusca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomatoplastic', 'a.', 'Of or pertaining to the operation of forming a\n   mouth where the aperture has been contracted, or in any way deformed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomatopod', 'n.', 'One of the Stomatopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomatopoda', 'n. pl.', 'Same as Stomapoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomatopodous', 'a.', 'Of or pertaining to the Stomatopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomatoscope', 'n.', 'An apparatus for examining the interior of the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomatous', 'a.', 'Having a stoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomodaeum', 'n.', 'A part of the alimentary canal. See under Mesenteron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomodaeum', 'n.', 'The primitive mouth and esophagus of the embryo of\n   annelids and arthropods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stomp', 'v. i.', 'To stamp with the foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stond', 'n.', 'Stop; halt; hindrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stond', 'n.', 'A stand; a post; a station.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stond', 'v. i.', 'To stand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone', 'n.', 'Concreted earthy or mineral matter; also, any particular\n   mass of such matter; as, a house built of stone; the boy threw a stone;\n   pebbles are rounded stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone', 'n.', 'A precious stone; a gem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone', 'n.', 'Something made of stone. Specifically: -');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone', 'n.', 'The glass of a mirror; a mirror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone', 'n.', 'A monument to the dead; a gravestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone', 'n.', 'A calculous concretion, especially one in the kidneys or\n   bladder; the disease arising from a calculus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone', 'n.', 'One of the testes; a testicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone', 'n.', 'The hard endocarp of drupes; as, the stone of a cherry or\n   peach. See Illust. of Endocarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone', 'n.', 'A weight which legally is fourteen pounds, but in practice\n   varies with the article weighed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone', 'n.', 'Fig.: Symbol of hardness and insensibility; torpidness;\n   insensibility; as, a heart of stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone', 'n.', 'A stand or table with a smooth, flat top of stone, commonly\n   marble, on which to arrange the pages of a book, newspaper, etc.,\n   before printing; -- called also imposing stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoned', 'imp. & p. p.', 'of Stone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoning', 'p. pr. & vb. n.', 'of Stone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone', 'n.', 'To pelt, beat, or kill with stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone', 'n.', 'To make like stone; to harden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone', 'n.', 'To free from stones; also, to remove the seeds of; as, to\n   stone a field; to stone cherries; to stone raisins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone', 'n.', 'To wall or face with stones; to line or fortify with stones;\n   as, to stone a well; to stone a cellar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone', 'n.', 'To rub, scour, or sharpen with a stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonebird', 'n.', 'The yellowlegs; -- called also stone snipe. See Tattler,\n   2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone-blind', 'a.', 'As blind as a stone; completely blind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonebow', 'n.', 'A kind of crossbow formerly used for shooting stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonebrash', 'n.', 'A subsoil made up of small stones or finely-broken\n   rock; brash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonebrearer', 'n.', 'A machine for crushing or hammering stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonebuck', 'n.', 'See Steinbock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonechat', 'n.', 'A small, active, and very common European singing bird\n   (Pratincola rubicola); -- called also chickstone, stonechacker,\n   stonechatter, stoneclink, stonesmith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonechat', 'n.', 'The wheatear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonechat', 'n.', 'The blue titmouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone-cold', 'a.', 'Cold as a stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonecray', 'n.', 'A distemper in hawks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonecrop', 'n.', 'A sort of tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonecrop', 'n.', 'Any low succulent plant of the genus Sedum, esp. Sedum\n   acre, which is common on bare rocks in Europe, and is spreading in\n   parts of America. See Orpine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonecutter', 'n.', 'One whose occupation is to cut stone; also, a machine\n   for dressing stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonecutting', 'n.', 'Hewing or dressing stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone-dead', 'a.', 'As dead as a stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone-deaf', 'a.', 'As deaf as a stone; completely deaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonegall', 'n.', 'See Stannel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonehatch', 'n.', 'The ring plover, or dotterel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone-hearted', 'a.', 'Hard-hearted; cruel; pitiless; unfeeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonehenge', 'n.', 'An assemblage of upright stones with others placed\n   horizontally on their tops, on Salisbury Plain, England, -- generally\n   supposed to be the remains of an ancient Druidical temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone-horse', 'n.', 'Stallion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoner', 'n.', 'One who stones; one who makes an assault with stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoner', 'n.', 'One who walls with stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoneroot', 'n.', 'A North American plant (Collinsonia Canadensis) having a\n   very hard root; horse balm. See Horse balm, under Horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonerunner', 'n.', 'The ring plover, or the ringed dotterel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonerunner', 'n.', 'The dotterel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonesmickle', 'n.', 'The stonechat; -- called also stonesmitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stone-still', 'a.', 'As still as a stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoneware', 'n.', 'A species of coarse potters ware, glazed and baked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoneweed', 'n.', 'Any plant of the genus Lithospermum, herbs having a\n   fruit composed of four stony nutlets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonework', 'n.', 'Work or wall consisting of stone; masons work of stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonewort', 'n.', 'Any plant of the genus Chara; -- so called because they\n   are often incrusted with carbonate of lime. See Chara.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonily', 'adv.', 'In a stony manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoniness', 'n.', 'The quality or state of being stony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stonish', 'a.', 'Stony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stont', '', '3d pers. sing. present of Stand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stony', 'superl.', 'Of or pertaining to stone, consisting of, or abounding\n   in, stone or stones; resembling stone; hard; as, a stony tower; a stony\n   cave; stony ground; a stony crust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stony', 'superl.', 'Converting into stone; petrifying; petrific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stony', 'superl.', 'Inflexible; cruel; unrelenting; pitiless; obdurate;\n   perverse; cold; morally hard; appearing as if petrified; as, a stony\n   heart; a stony gaze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stood', '', 'imp. & p. p. of Stand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stook', 'n.', 'A small collection of sheaves set up in the field; a shock;\n   in England, twelve sheaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stooked', 'imp. & p. p.', 'of Stook');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stooking', 'p. pr. & vb. n.', 'of Stook');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stook', 'v. t.', 'To set up, as sheaves of grain, in stooks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stool', 'n.', 'A plant from which layers are propagated by bending its\n   branches into the soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stool', 'v. i.', 'To ramfy; to tiller, as grain; to shoot out suckers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stool', 'n.', 'A single seat with three or four legs and without a back,\n   made in various forms for various uses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stool', 'n.', 'A seat used in evacuating the bowels; hence, an evacuation;\n   a discharge from the bowels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stool', 'n.', 'A stool pigeon, or decoy bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stool', 'n.', 'A small channel on the side of a vessel, for the dead-eyes\n   of the backstays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stool', 'n.', 'A bishops seat or see; a bishop-stool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stool', 'n.', 'A bench or form for resting the feet or the knees; a\n   footstool; as, a kneeling stool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stool', 'n.', 'Material, such as oyster shells, spread on the sea bottom\n   for oyster spat to adhere to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoolball', 'n.', 'A kind of game with balls, formerly common in England,\n   esp. with young women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoom', 'v. t.', 'To stum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoop', 'n.', 'Originally, a covered porch with seats, at a house door; the\n   Dutch stoep as introduced by the Dutch into New York. Afterward, an\n   out-of-door flight of stairs of from seven to fourteen steps, with\n   platform and parapets, leading to an entrance door some distance above\n   the street; the French perron. Hence, any porch, platform, entrance\n   stairway, or small veranda, at a house door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoop', 'n.', 'A vessel of liquor; a flagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoop', 'n.', 'A post fixed in the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stooped', 'imp. & p. p.', 'of Stoop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stooping', 'p. pr. & vb. n.', 'of Stoop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoop', 'v. i.', 'To bend the upper part of the body downward and forward;\n   to bend or lean forward; to incline forward in standing or walking; to\n   assume habitually a bent position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoop', 'v. i.', 'To yield; to submit; to bend, as by compulsion; to assume\n   a position of humility or subjection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoop', 'v. i.', 'To descend from rank or dignity; to condescend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoop', 'v. i.', 'To come down as a hawk does on its prey; to pounce; to\n   souse; to swoop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoop', 'v. i.', 'To sink when on the wing; to alight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoop', 'v. t.', 'To bend forward and downward; to bow down; as, to stoop\n   the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoop', 'v. t.', 'To cause to incline downward; to slant; as, to stoop a\n   cask of liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoop', 'v. t.', 'To cause to submit; to prostrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoop', 'v. t.', 'To degrade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoop', 'n.', 'The act of stooping, or bending the body forward;\n   inclination forward; also, an habitual bend of the back and shoulders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoop', 'n.', 'Descent, as from dignity or superiority; condescension; an\n   act or position of humiliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoop', 'n.', 'The fall of a bird on its prey; a swoop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stooper', 'n.', 'One who stoops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stooping', '', 'a. & n. from Stoop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoor', 'v. i.', 'To rise in clouds, as dust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoor', 'a.', 'Alt. of Stor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stor', 'a.', 'Strong; powerful; hardy; bold; audacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stopped', 'imp. & p. p.', 'of Stop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stopping', 'p. pr. & vb. n.', 'of Stop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'v. t.', 'To close, as an aperture, by filling or by obstructing;\n   as, to stop the ears; hence, to stanch, as a wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'v. t.', 'To obstruct; to render impassable; as, to stop a way,\n   road, or passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'v. t.', 'To arrest the progress of; to hinder; to impede; to shut\n   in; as, to stop a traveler; to stop the course of a stream, or a flow\n   of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'v. t.', 'To hinder from acting or moving; to prevent the effect or\n   efficiency of; to cause to cease; to repress; to restrain; to suppress;\n   to interrupt; to suspend; as, to stop the execution of a decree, the\n   progress of vice, the approaches of old age or infirmity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'v. t.', 'To regulate the sounds of, as musical strings, by pressing\n   them against the finger board with the finger, or by shortening in any\n   way the vibrating part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'v. t.', 'To point, as a composition; to punctuate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'v. t.', 'To make fast; to stopper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'v. i.', 'To cease to go on; to halt, or stand still; to come to a\n   stop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'v. i.', 'To cease from any motion, or course of action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'v. i.', 'To spend a short time; to reside temporarily; to stay; to\n   tarry; as, to stop with a friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'n.', 'The act of stopping, or the state of being stopped; hindrance\n   of progress or of action; cessation; repression; interruption; check;\n   obstruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'n.', 'That which stops, impedes, or obstructs; as obstacle; an\n   impediment; an obstruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'n.', 'A device, or piece, as a pin, block, pawl, etc., for\n   arresting or limiting motion, or for determining the position to which\n   another part shall be brought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'n.', 'The closing of an aperture in the air passage, or pressure of\n   the finger upon the string, of an instrument of music, so as to modify\n   the tone; hence, any contrivance by which the sounds of a musical\n   instrument are regulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'n.', 'In the organ, one of the knobs or handles at each side of the\n   organist, by which he can draw on or shut off any register or row of\n   pipes; the register itself; as, the vox humana stop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'n.', 'A member, plain or molded, formed of a separate piece and\n   fixed to a jamb, against which a door or window shuts. This takes the\n   place, or answers the purpose, of a rebate. Also, a pin or block to\n   prevent a drawer from sliding too far.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'n.', 'A point or mark in writing or printing intended to\n   distinguish the sentences, parts of a sentence, or clauses; a mark of\n   punctuation. See Punctuation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'n.', 'The diaphragm used in optical instruments to cut off the\n   marginal portions of a beam of light passing through lenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'n.', 'The depression in the face of a dog between the skull and the\n   nasal bones. It is conspicuous in the bulldog, pug, and some other\n   breeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop', 'n.', 'Some part of the articulating organs, as the lips, or the\n   tongue and palate, closed (a) so as to cut off the passage of breath or\n   voice through the mouth and the nose (distinguished as a lip-stop, or a\n   front-stop, etc., as in p, t, d, etc.), or (b) so as to obstruct, but\n   not entirely cut off, the passage, as in l, n, etc.; also, any of the\n   consonants so formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stopcock', 'n.', 'A bib, faucet, or short pipe, fitted with a turning\n   stopper or plug for permitting or restraining the flow of a liquid or\n   gas; a cock or valve for checking or regulating the flow of water, gas,\n   etc., through or from a pipe, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stopcock', 'n.', 'The turning plug, stopper, or spigot of a faucet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stope', 'v. i.', 'A horizontal working forming one of a series, the working\n   faces of which present the appearance of a flight of steps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoped', 'imp. & p. p.', 'of Stope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoping', 'p. pr. & vb. n.', 'of Stope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stope', 'v. t.', 'To excavate in the form of stopes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stope', 'v. t.', 'To fill in with rubbish, as a space from which the ore\n   has been worked out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stope', 'p. p.', 'Alt. of Stopen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stopen', 'p. p.', 'Stepped; gone; advanced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop-gap', 'n.', 'That which closes or fills up an opening or gap; hence, a\n   temporary expedient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoping', 'n.', 'The act of excavating in the form of stopes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stopless', 'a.', 'Not to be stopped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stop-over', 'a.', 'Permitting one to stop over; as, a stop-over check or\n   ticket. See To stop over, under Stop, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoppage', 'n.', 'The act of stopping, or arresting progress, motion, or\n   action; also, the state of being stopped; as, the stoppage of the\n   circulation of the blood; the stoppage of commerce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stopped', 'a.', 'Made by complete closure of the mouth organs; shut; --\n   said of certain consonants (p, b, t, d, etc.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stopper', 'n.', 'One who stops, closes, shuts, or hinders; that which stops\n   or obstructs; that which closes or fills a vent or hole in a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stopper', 'n.', 'A short piece of rope having a knot at one or both ends,\n   with a lanyard under the knot, -- used to secure something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stopper', 'n.', 'A name to several trees of the genus Eugenia, found in\n   Florida and the West Indies; as, the red stopper. See Eugenia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoppered', 'imp. & p. p.', 'of Stopper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoppering', 'p. pr. & vb. n.', 'of Stopper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stopper', 'v. t.', 'To close or secure with a stopper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stopping', 'n.', 'Material for filling a cavity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stopping', 'n.', 'A partition or door to direct or prevent a current of\n   air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stopping', 'n.', 'A pad or poultice of dung or other material applied to a\n   horses hoof to keep it moist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stopping-out', 'n.', 'A method adopted in etching, to keep the acid from\n   those parts which are already sufficiently corroded, by applying\n   varnish or other covering matter with a brush, but allowing the acid to\n   act on the other parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stopple', 'v. t.', 'That which stops or closes the mouth of a vessel; a\n   stopper; as, a glass stopple; a cork stopple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoppled', 'imp. & p. p.', 'of Stopple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoppling', 'p. pr. & vb. n.', 'of Stopple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stopple', 'v. t.', 'To close the mouth of anything with a stopple, or as\n   with a stopple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stopship', 'n.', 'A remora. It was fabled to stop ships by attaching itself\n   to them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stor', 'a.', 'See Stoor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storage', 'n.', 'The act of depositing in a store or warehouse for safe\n   keeping; also, the safe keeping of goods in a warehouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storage', 'n.', 'Space for the safe keeping of goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storage', 'n.', 'The price changed for keeping goods in a store.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storax', 'n.', 'Any one of a number of similar complex resins obtained from\n   the bark of several trees and shrubs of the Styrax family. The most\n   common of these is liquid storax, a brown or gray semifluid substance\n   of an agreeable aromatic odor and balsamic taste, sometimes used in\n   perfumery, and in medicine as an expectorant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Store', 'v. t.', 'That which is accumulated, or massed together; a source\n   from which supplies may be drawn; hence, an abundance; a great\n   quantity, or a great number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Store', 'v. t.', 'A place of deposit for goods, esp. for large quantities;\n   a storehouse; a warehouse; a magazine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Store', 'v. t.', 'Any place where goods are sold, whether by wholesale or\n   retail; a shop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Store', 'v. t.', 'Articles, especially of food, accumulated for some\n   specific object; supplies, as of provisions, arms, ammunition, and the\n   like; as, the stores of an army, of a ship, of a family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Store', 'a.', 'Accumulated; hoarded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stored', 'imp. & p. p.', 'of Store');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storing', 'p. pr. & vb. n.', 'of Store');
  INSERT INTO words (word, word_type, description) VALUES  (    'Store', 'v. t.', 'To collect as a reserved supply; to accumulate; to lay\n   away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Store', 'v. t.', 'To furnish; to supply; to replenish; esp., to stock or\n   furnish against a future time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Store', 'v. t.', 'To deposit in a store, warehouse, or other building, for\n   preservation; to warehouse; as, to store goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stored', 'a.', 'Collected or accumulated as a reserve supply; as, stored\n   electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storehouse', 'n.', 'A building for keeping goods of any kind, especially\n   provisions; a magazine; a repository; a warehouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storehouse', 'n.', 'A mass or quality laid up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storekeeper', 'n.', 'A man in charge of stores or goods of any kind; as, a\n   naval storekeeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storekeeper', 'n.', 'One who keeps a \"store;\" a shopkeeper. See 1st Store,\n   3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storer', 'n.', 'One who lays up or forms a store.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storeroom', 'n.', 'Room in a storehouse or repository; a room in which\n   articles are stored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storeship', 'n.', 'A vessel used to carry naval stores for a fleet,\n   garrison, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storey', 'n.', 'See Story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storge', 'n.', 'Parental affection; the instinctive affection which animals\n   have for their young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storial', 'a.', 'Historical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storied', 'a.', 'Told in a story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storied', 'a.', 'Having a history; interesting from the stories which\n   pertain to it; venerable from the associations of the past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storied', 'a.', 'Having (such or so many) stories; -- chiefly in\n   composition; as, a two-storied house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storier', 'n.', 'A relater of stories; an historian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storify', 'v. t.', 'To form or tell stories of; to narrate or describe in a\n   story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stork', 'n.', 'Any one of several species of large wading birds of the\n   family Ciconidae, having long legs and a long, pointed bill. They are\n   found both in the Old World and in America, and belong to Ciconia and\n   several allied genera. The European white stork (Ciconia alba) is the\n   best known. It commonly makes its nests on the top of a building, a\n   chimney, a church spire, or a pillar. The black stork (C. nigra) is\n   native of Asia, Africa, and Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stork-billed', 'a.', 'Having a bill like that of the stork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storm', 'n.', 'A violent disturbance of the atmosphere, attended by wind,\n   rain, snow, hail, or thunder and lightning; hence, often, a heavy fall\n   of rain, snow, or hail, whether accompanied with wind or not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storm', 'n.', 'A violent agitation of human society; a civil, political, or\n   domestic commotion; sedition, insurrection, or war; violent outbreak;\n   clamor; tumult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storm', 'n.', 'A heavy shower or fall, any adverse outburst of tumultuous\n   force; violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storm', 'n.', 'A violent assault on a fortified place; a furious attempt of\n   troops to enter and take a fortified place by scaling the walls,\n   forcing the gates, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stormed', 'imp. & p. p.', 'of Storm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storming', 'p. pr. & vb. n.', 'of Storm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storm', 'v. t.', 'To assault; to attack, and attempt to take, by scaling\n   walls, forcing gates, breaches, or the like; as, to storm a fortified\n   town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storm', 'v. i.', 'To raise a tempest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storm', 'v. i.', 'To blow with violence; also, to rain, hail, snow, or the\n   like, usually in a violent manner, or with high wind; -- used\n   impersonally; as, it storms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storm', 'v. i.', 'To rage; to be in a violent passion; to fume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storm-beat', 'a.', 'Beaten, injured, or impaired by storms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stormcock', 'n.', 'The missel thrush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stormcock', 'n.', 'The fieldfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stormcock', 'n.', 'The green woodpecker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stormfinch', 'n.', 'The storm petrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stormful', 'a.', 'Abounding with storms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stormglass', 'n.', 'A glass vessel, usually cylindrical, filled with a\n   solution which is sensitive to atmospheric changes, indicating by a\n   clouded appearance, rain, snow, etc., and by clearness, fair weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stormily', 'adv.', 'In a stormy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storminess', 'n.', 'The state of being stormy; tempestuousness;\n   biosteruousness; impetuousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storming', '', 'a. & n. from Storm, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stormless', 'a.', 'Without storms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stormwind', 'n.', 'A heavy wind; a wind that brings a storm; the blast of a\n   storm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stormy', 'superl.', 'Characterized by, or proceeding from, a storm; subject\n   to storms; agitated with furious winds; biosterous; tempestous; as, a\n   stormy season; a stormy day or week.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stormy', 'superl.', 'Proceeding from violent agitation or fury; as, a\n   stormy sound; stormy shocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stormy', 'superl.', 'Violent; passionate; rough; as, stormy passions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storthing', 'n.', 'The Parliament of Norway, chosen by indirect election\n   once in three years, but holding annual sessions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storven', '', 'p. p. of Starve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stories', 'pl. ', 'of Story');
  INSERT INTO words (word, word_type, description) VALUES  (    'Story', 'v. t.', 'A set of rooms on the same floor or level; a floor, or\n   the space between two floors. Also, a horizontal division of a\n   buildings exterior considered architecturally, which need not\n   correspond exactly with the stories within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Story', 'n.', 'A narration or recital of that which has occurred; a\n   description of past events; a history; a statement; a record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Story', 'n.', 'The relation of an incident or minor event; a short\n   narrative; a tale; especially, a fictitious narrative less elaborate\n   than a novel; a short romance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Story', 'n.', 'A euphemism or childs word for \"a lie;\" a fib; as, to tell\n   a story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storied', 'imp. & p. p.', 'of Story');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storying', 'p. pr. & vb. n.', 'of Story');
  INSERT INTO words (word, word_type, description) VALUES  (    'Story', 'v. t.', 'To tell in historical relation; to make the subject of a\n   story; to narrate or describe in story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Storybook', 'n.', 'A book containing stories, or short narratives, either\n   true or false.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Story-teller', 'n.', 'One who tells stories; a narrator of\n   anecdotes,incidents, or fictitious tales; as, an amusing story-teller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Story-teller', 'n.', 'An historian; -- in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Story-teller', 'n.', 'A euphemism or childs word for');
  INSERT INTO words (word, word_type, description) VALUES  (    'Story-telling', 'a.', 'Being accustomed to tell stories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Story-telling', 'n.', 'The act or practice of telling stories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Story-writer', 'n.', 'One who writes short stories, as for magazines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Story-writer', 'n.', 'An historian; a chronicler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stot', 'n.', 'A horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stot', 'n.', 'A young bull or ox, especially one three years old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stote', 'n.', 'See Stoat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stound', 'v. i.', 'To be in pain or sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stound', 'v. i.', 'Stunned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stound', 'n.', 'A sudden, severe pain or grief; peril; alarm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stound', 'n.', 'Astonishment; amazement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stound', 'n.', 'Hour; time; season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stound', 'n.', 'A brief space of time; a moment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stound', 'n.', 'A vessel for holding small beer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoup', 'n.', 'A flagon; a vessel or measure for liquids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoup', 'n.', 'A basin at the entrance of Roman Catholic churches for\n   containing the holy water with which those who enter, dipping their\n   fingers in it, cross themselves; -- called also holy-water stoup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stour', 'n.', 'A battle or tumult; encounter; combat; disturbance; passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stour', 'a.', 'Tall; strong; stern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stout', 'superl.', 'Strong; lusty; vigorous; robust; sinewy; muscular;\n   hence, firm; resolute; dauntless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stout', 'superl.', 'Proud; haughty; arrogant; hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stout', 'superl.', 'Firm; tough; materially strong; enduring; as, a stout\n   vessel, stick, string, or cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stout', 'superl.', 'Large; bulky; corpulent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stout', 'n.', 'A strong malt liquor; strong porter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stout-hearted', 'a.', 'Having a brave heart; courageous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoutish', 'a.', 'Somewhat stout; somewhat corpulent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoutly', 'adv.', 'In a stout manner; lustily; boldly; obstinately; as, he\n   stoutly defended himself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoutness', 'n.', 'The state or quality of being stout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stove', '', 'imp. of Stave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stove', 'n.', 'A house or room artificially warmed or heated; a forcing\n   house, or hothouse; a drying room; -- formerly, designating an\n   artificially warmed dwelling or room, a parlor, or a bathroom, but now\n   restricted, in this sense, to heated houses or rooms used for\n   horticultural purposes or in the processes of the arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stove', 'n.', 'An apparatus, consisting essentially of a receptacle for\n   fuel, made of iron, brick, stone, or tiles, and variously constructed,\n   in which fire is made or kept for warming a room or a house, or for\n   culinary or other purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoved', 'imp. & p. p.', 'of Stove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stoving', 'p. pr. & vb. n.', 'of Stove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stove', 'v. t.', 'To keep warm, in a house or room, by artificial heat; as,\n   to stove orange trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stove', 'v. t.', 'To heat or dry, as in a stove; as, to stove feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stovehouse', 'n.', 'A hothouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stovepipe', 'n.', 'Pipe made of sheet iron in length and angular or curved\n   pieces fitting together, -- used to connect a portable stove with a\n   chimney flue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stover', 'n.', 'Fodder for cattle, especially straw or coarse hay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stowed', 'imp. & p. p.', 'of Stow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stowing', 'p. pr. & vb. n.', 'of Stow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stow', 'v. t.', 'To place or arrange in a compact mass; to put in its\n   proper place, or in a suitable place; to pack; as, to stowbags, bales,\n   or casks in a ships hold; to stow hay in a mow; to stow sheaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stow', 'v. t.', 'To put away in some place; to hide; to lodge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stow', 'v. t.', 'To arrange anything compactly in; to fill, by packing\n   closely; as, to stow a box, car, or the hold of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stowage', 'n.', 'The act or method of stowing; as, the stowage of\n   provisions in a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stowage', 'n.', 'Room in which things may be stowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stowage', 'n.', 'The state of being stowed, or put away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stowage', 'n.', 'Things stowed or packed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stowage', 'n.', 'Money paid for stowing goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stowaway', 'n.', 'One who conceals himself board of a vessel about to leave\n   port, or on a railway train, in order to obtain a free passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stowboard', 'n.', 'A place into which rubbish is put.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stowce', 'n.', 'A windlass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stowce', 'n.', 'A wooden landmark, to indicate possession of mining land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stowing', 'n.', 'A method of working in which the waste is packed into the\n   space formed by excavating the vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stowre', 'a.', 'See Stour, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stowre', 'n.', 'See Stour, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strabism', 'n.', 'Strabismus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strabismometer', 'n.', 'An instrument for measuring the amount of\n   strabismus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strabismus', 'n.', 'An affection of one or both eyes, in which the optic\n   axes can not be directed to the same object, -- a defect due either to\n   undue contraction or to undue relaxation of one or more of the muscles\n   which move the eyeball; squinting; cross-eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strabotomy', 'n.', 'The operation for the removal of squinting by the\n   division of such muscles as distort the eyeball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straddled', 'imp. & p. p.', 'of Straddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straddling', 'p. pr. & vb. n.', 'of Straddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straddle', 'v. i.', 'To part the legs wide; to stand or to walk with the\n   legs far apart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straddle', 'v. i.', 'To stand with the ends staggered; -- said of the\n   spokes of a wagon wheel where they join the hub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straddle', 'v. t.', 'To place one leg on one side and the other on the\n   other side of; to stand or sit astride of; as, to straddle a fence or a\n   horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straddle', 'n.', 'The act of standing, sitting, or walking, with the feet\n   far apart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straddle', 'n.', 'The position, or the distance between the feet, of one\n   who straddles; as, a wide straddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straddle', 'n.', 'A stock option giving the holder the double privilege of\n   a \"put\" and a \"call,\" i. e., securing to the buyer of the option the\n   right either to demand of the seller at a certain price, within a\n   certain time, certain securities, or to require him to take at the same\n   price, and within the same time, the same securities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straddling', 'a.', 'Applied to spokes when they are arranged alternately in\n   two circles in the hub. See Straddle, v. i., and Straddle, v. t., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stradometrical', 'a.', 'Of, or relating to, the measuring of streets or\n   roads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straggled', 'imp. & p. p.', 'of Straggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straggling', 'p. pr. & vb. n.', 'of Straggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straggle', 'v. t.', 'To wander from the direct course or way; to rove; to\n   stray; to wander from the line of march or desert the line of battle;\n   as, when troops are on the march, the men should not straggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straggle', 'v. t.', 'To wander at large; to roam idly about; to ramble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straggle', 'v. t.', 'To escape or stretch beyond proper limits, as the\n   branches of a plant; to spread widely apart; to shoot too far or widely\n   in growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straggle', 'v. t.', 'To be dispersed or separated; to occur at intervals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straggle', 'n.', 'The act of straggling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straggler', 'n.', 'One who straggles, or departs from the direct or proper\n   course, or from the company to which he belongs; one who falls behind\n   the rest; one who rambles without any settled direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straggler', 'n.', 'A roving vagabond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straggler', 'n.', 'Something that shoots, or spreads out, beyond the rest,\n   or too far; an exuberant growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straggler', 'n.', 'Something that stands alone or by itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straggling', '', 'a. & n. from Straggle, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stragglingly', 'adv.', 'In a straggling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stragula', 'pl. ', 'of Stragulum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stragulum', 'n.', 'The mantle, or pallium, of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straight', 'a.', 'A variant of Strait, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straight', 'superl.', 'Right, in a mathematical sense; passing from one\n   point to another by the nearest course; direct; not deviating or\n   crooked; as, a straight line or course; a straight piece of timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straight', 'superl.', 'Approximately straight; not much curved; as,\n   straight ribs are such as pass from the base of a leaf to the apex,\n   with a small curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straight', 'superl.', 'Composed of cards which constitute a regular\n   sequence, as the ace, king, queen, jack, and ten-spot; as, a straight\n   hand; a straight flush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straight', 'superl.', 'Conforming to justice and rectitude; not deviating\n   from truth or fairness; upright; as, straight dealing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straight', 'superl.', 'Unmixed; undiluted; as, to take liquor straight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straight', 'superl.', 'Making no exceptions or deviations in ones support\n   of the organization and candidates of a political party; as, a straight\n   Republican; a straight Democrat; also, containing the names of all the\n   regularly nominated candidates of a party and no others; as, a straight\n   ballot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straight', 'adv.', 'In a straight manner; directly; rightly; forthwith;\n   immediately; as, the arrow went straight to the mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straight', 'n.', 'A hand of five cards in consecutive order as to value; a\n   sequence. When they are of one suit, it is calles straight flush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straight', 'v. t.', 'To straighten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straightedge', 'n.', 'A board, or piece of wood or metal, having one edge\n   perfectly straight, -- used to ascertain whether a line is straight or\n   a surface even, and for drawing straight lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straighted', 'imp. & p. p.', 'of Straighten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straighting', 'p. pr. & vb. n.', 'of Straighten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straighten', 'v. t.', 'To make straight; to reduce from a crooked to a\n   straight form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straighten', 'v. t.', 'To make right or correct; to reduce to order; as, to\n   straighten ones affairs; to straighten an account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straighten', 'v. t.', 'A variant of Straiten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straightener', 'n.', 'One who, or that which, straightens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straightforth', 'adv.', 'Straightway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straightforward', 'a.', 'Proceeding in a straight course or manner; not\n   deviating; honest; frank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straightforward', 'adv.', 'In a straightforward manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straighthorn', 'n.', 'An orthoceras.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straight-joint', 'a.', 'Having straight joints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straight-joint', 'a.', 'Applied to a floor the boards of which are so laid\n   that the joints form a continued line transverse to the length of the\n   boards themselves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straight-joint', 'a.', 'In the United States, applied to planking or\n   flooring put together without the tongue and groove, the pieces being\n   laid edge to edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straight-lined', 'a.', 'Having straight lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straightly', 'adv.', 'In a right line; not crookedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straightly', 'adv.', 'A variant of Straitly. See 1st Straight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straightness', 'n.', 'The quality, condition, or state, of being straight;\n   as, the straightness of a path.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straightness', 'n.', 'A variant of Straitness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straight-out', 'a.', 'Acting without concealment, obliquity, or compromise;\n   hence, unqualified; thoroughgoing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straight-pight', 'a.', 'Straight in form or upright in position; erect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straight-spoken', 'a.', 'Speaking with directness; plain-spoken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straightway', 'adv.', 'Immediately; without loss of time; without delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straightways', 'adv.', 'Straightway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straik', 'n.', 'A strake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'n.', 'Race; stock; generation; descent; family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'n.', 'Hereditary character, quality, or disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'n.', 'Rank; a sort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strained', 'imp. & p. p.', 'of Strain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straining', 'p. pr. & vb. n.', 'of Strain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'a.', 'To draw with force; to extend with great effort; to\n   stretch; as, to strain a rope; to strain the shrouds of a ship; to\n   strain the cords of a musical instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'a.', 'To act upon, in any way, so as to cause change of form or\n   volume, as forces on a beam to bend it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'a.', 'To exert to the utmost; to ply vigorously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'a.', 'To stretch beyond its proper limit; to do violence to, in\n   the matter of intent or meaning; as, to strain the law in order to\n   convict an accused person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'a.', 'To injure by drawing, stretching, or the exertion of force;\n   as, the gale strained the timbers of the ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'a.', 'To injure in the muscles or joints by causing to make too\n   strong an effort; to harm by overexertion; to sprain; as, to strain a\n   horse by overloading; to strain the wrist; to strain a muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'a.', 'To squeeze; to press closely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'a.', 'To make uneasy or unnatural; to produce with apparent\n   effort; to force; to constrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'a.', 'To urge with importunity; to press; as, to strain a\n   petition or invitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'a.', 'To press, or cause to pass, through a strainer, as through\n   a screen, a cloth, or some porous substance; to purify, or separate\n   from extraneous or solid matter, by filtration; to filter; as, to\n   strain milk through cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'v. i.', 'To make violent efforts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'v. i.', 'To percolate; to be filtered; as, water straining\n   through a sandy soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'n.', 'The act of straining, or the state of being strained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'n.', 'A violent effort; an excessive and hurtful exertion or\n   tension, as of the muscles; as, he lifted the weight with a strain; the\n   strain upon a ships rigging in a gale; also, the hurt or injury\n   resulting; a sprain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'n.', 'A change of form or dimensions of a solid or liquid mass,\n   produced by a stress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'n.', 'A portion of music divided off by a double bar; a complete\n   musical period or sentence; a movement, or any rounded subdivision of a\n   movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'n.', 'Any sustained note or movement; a song; a distinct portion\n   of an ode or other poem; also, the pervading note, or burden, of a\n   song, poem, oration, book, etc.; theme; motive; manner; style; also, a\n   course of action or conduct; as, he spoke in a noble strain; there was\n   a strain of woe in his story; a strain of trickery appears in his\n   career.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strain', 'n.', 'Turn; tendency; inborn disposition. Cf. 1st Strain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strainable', 'a.', 'Capable of being strained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strainable', 'a.', 'Violent in action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strainably', 'adv.', 'Violently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strained', 'a.', 'Subjected to great or excessive tension; wrenched;\n   weakened; as, strained relations between old friends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strained', 'a.', 'Done or produced with straining or excessive effort; as,\n   his wit was strained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strainer', 'n.', 'One who strains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strainer', 'n.', 'That through which any liquid is passed for purification\n   or to separate it from solid matter; anything, as a screen or a cloth,\n   used to strain a liquid; a device of the character of a sieve or of a\n   filter; specifically, an openwork or perforated screen, as for the end\n   of the suction pipe of a pump, to prevent large solid bodies from\n   entering with a liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straining', '', 'a. & n. from Strain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straint', 'n.', 'Overexertion; excessive tension; strain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strait', 'a.', 'A variant of Straight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strait', 'superl.', 'Narrow; not broad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strait', 'superl.', 'Tight; close; closely fitting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strait', 'superl.', 'Close; intimate; near; familiar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strait', 'superl.', 'Strict; scrupulous; rigorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strait', 'superl.', 'Difficult; distressful; straited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strait', 'superl.', 'Parsimonious; niggargly; mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strait', 'adv.', 'Strictly; rigorously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straits', 'pl. ', 'of Strait');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strait', 'a.', 'A narrow pass or passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strait', 'a.', 'A (comparatively) narrow passageway connecting two large\n   bodies of water; -- often in the plural; as, the strait, or straits, of\n   Gibraltar; the straits of Magellan; the strait, or straits, of\n   Mackinaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strait', 'a.', 'A neck of land; an isthmus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strait', 'a.', 'Fig.: A condition of narrowness or restriction; doubt;\n   distress; difficulty; poverty; perplexity; -- sometimes in the plural;\n   as, reduced to great straits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strait', 'v. t.', 'To put to difficulties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straitened', 'imp. & p. p.', 'of Straiten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straitening', 'p. pr. & vb. n.', 'of Straiten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straiten', 'v. t.', 'To make strait; to make narrow; hence, to contract; to\n   confine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straiten', 'v. t.', 'To make tense, or tight; to tighten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straiten', 'v. t.', 'To restrict; to distress or embarrass in respect of\n   means or conditions of life; -- used chiefly in the past participle; --\n   as, a man straitened in his circumstances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strait-handed', 'a.', 'Parsimonious; sparing; niggardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strait-jacket', 'n.', 'A dress of strong materials for restraining maniacs\n   or those who are violently delirious. It has long sleeves, which are\n   closed at the ends, confining the hands, and may be tied behind the\n   back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strait-laced', 'a.', 'Bound with stays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strait-laced', 'a.', 'Restricted; stiff; constrained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strait-laced', 'a.', 'Rigid in opinion; strict in manners or morals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straitly', 'adv.', 'In a strait manner; narrowly; strictly; rigorously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straitly', 'adv.', 'Closely; intimately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straitness', 'n.', 'The quality or condition of being strait; especially, a\n   pinched condition or situation caused by poverty; as, the straitnessof\n   their circumstances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strait-waistcoat', 'n.', 'Same as Strait-jacket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strake', '', 'imp. of Strike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strake', 'n.', 'A streak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strake', 'n.', 'An iron band by which the fellies of a wheel are secured to\n   each other, being not continuous, as the tire is, but made up of\n   separate pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strake', 'n.', 'One breadth of planks or plates forming a continuous range\n   on the bottom or sides of a vessel, reaching from the stem to the\n   stern; a streak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strake', 'n.', 'A trough for washing broken ore, gravel, or sand; a\n   launder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strale', 'n.', 'Pupil of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stram', 'v. t.', 'To spring or recoil with violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stram', 'v. t.', 'To dash down; to beat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stramash', 'v. t.', 'To strike, beat, or bang; to break; to destroy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stramash', 'n.', 'A turmoil; a broil; a fray; a fight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stramazoun', 'n.', 'A direct descending blow with the edge of a sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stramineous', 'a.', 'Strawy; consisting of straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stramineous', 'a.', 'Chaffy; like straw; straw-colored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stramonium', 'n.', 'A poisonous plant (Datura Stramonium); stinkweed. See\n   Datura, and Jamestown weed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stramony', 'n.', 'Stramonium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strand', 'n.', 'One of the twists, or strings, as of fibers, wires, etc.,\n   of which a rope is composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strand', 'v. t.', 'To break a strand of (a rope).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strand', 'n.', 'The shore, especially the beach of a sea, ocean, or large\n   lake; rarely, the margin of a navigable river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stranded', 'imp. & p. p.', 'of Strand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stranding', 'p. pr. & vb. n.', 'of Strand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strand', 'v. t.', 'To drive on a strand; hence, to run aground; as, to\n   strand a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strand', 'v. i.', 'To drift, or be driven, on shore to run aground; as, the\n   ship stranded at high water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strang', 'a.', 'Strong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strange', 'superl.', 'Belonging to another country; foreign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strange', 'superl.', 'Of or pertaining to others; not ones own; not\n   pertaining to ones self; not domestic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strange', 'superl.', 'Not before known, heard, or seen; new.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strange', 'superl.', 'Not according to the common way; novel; odd; unusual;\n   irregular; extraordinary; unnatural; queer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strange', 'superl.', 'Reserved; distant in deportment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strange', 'superl.', 'Backward; slow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strange', 'superl.', 'Not familiar; unaccustomed; inexperienced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strange', 'adv.', 'Strangely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strange', 'v. t.', 'To alienate; to estrange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strange', 'v. i.', 'To be estranged or alienated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strange', 'v. i.', 'To wonder; to be astonished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangely', 'adv.', 'As something foreign, or not ones own; in a manner\n   adapted to something foreign and strange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangely', 'adv.', 'In the manner of one who does not know another;\n   distantly; reservedly; coldly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangely', 'adv.', 'In a strange manner; in a manner or degree to excite\n   surprise or wonder; wonderfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangeness', 'n.', 'The state or quality of being strange (in any sense of\n   the adjective).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stranger', 'n.', 'One who is strange, foreign, or unknown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stranger', 'n.', 'One who comes from a foreign land; a foreigner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stranger', 'n.', 'One whose home is at a distance from the place where he\n   is, but in the same country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stranger', 'n.', 'One who is unknown or unacquainted; as, the gentleman is\n   a stranger to me; hence, one not admitted to communication, fellowship,\n   or acquaintance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stranger', 'n.', 'One not belonging to the family or household; a guest; a\n   visitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stranger', 'n.', 'One not privy or party an act, contract, or title; a mere\n   intruder or intermeddler; one who interferes without right; as, actual\n   possession of land gives a good title against a stranger having no\n   title; as to strangers, a mortgage is considered merely as a pledge; a\n   mere stranger to the levy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stranger', 'v. t.', 'To estrange; to alienate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangled', 'imp. & p. p.', 'of Strangle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangling', 'p. pr. & vb. n.', 'of Strangle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangle', 'v. t.', 'To compress the windpipe of (a person or animal) until\n   death results from stoppage of respiration; to choke to death by\n   compressing the throat, as with the hand or a rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangle', 'v. t.', 'To stifle, choke, or suffocate in any manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangle', 'v. t.', 'To hinder from appearance; to stifle; to suppress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangle', 'v. i.', 'To be strangled, or suffocated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangleable', 'a.', 'Capable of being strangled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangler', 'n.', 'One who, or that which, strangles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangles', 'n.', 'A disease in horses and swine, in which the upper part\n   of the throat, or groups of lymphatic glands elsewhere, swells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangulate', 'a.', 'Strangulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangulated', 'a.', 'Having the circulation stopped by compression;\n   attended with arrest or obstruction of circulation, caused by\n   constriction or compression; as, a strangulated hernia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangulated', 'a.', 'Contracted at irregular intervals, if tied with a\n   ligature; constricted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangulation', 'n.', 'The act of strangling, or the state of being\n   strangled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangulation', 'n.', 'Inordinate compression or constriction of a tube or\n   part, as of the throat; especially, such as causes a suspension of\n   breathing, of the passage of contents, or of the circulation, as in\n   cases of hernia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangurious', 'a.', 'Of or pertaining to strangury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangury', 'n.', 'A painful discharge of urine, drop by drop, produced by\n   spasmodic muscular contraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strangury', 'n.', 'A swelling or other disease in a plant, occasioned by a\n   ligature fastened tightly about it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strany', 'n.', 'The guillemot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strap', 'n.', 'A long, narrow, pliable strip of leather, cloth, or the\n   like; specifically, a strip of thick leather used in flogging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strap', 'n.', 'Something made of such a strip, or of a part of one, or a\n   combination of two or more for a particular use; as, a boot strap,\n   shawl strap, stirrup strap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strap', 'n.', 'A piece of leather, or strip of wood covered with a suitable\n   material, for sharpening a razor; a strop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strap', 'n.', 'A narrow strip of anything, as of iron or brass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strap', 'n.', 'A band, plate, or loop of metal for clasping and holding\n   timbers or parts of a machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strap', 'n.', 'A piece of rope or metal passing around a block and used for\n   fastening it to anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strap', 'n.', 'The flat part of the corolla in ligulate florets, as those\n   of the white circle in the daisy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strap', 'n.', 'The leaf, exclusive of its sheath, in some grasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strap', 'n.', 'A shoulder strap. See under Shoulder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strapped', 'imp. & p. p.', 'of Strap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strapping', 'p. pr. & vb. n.', 'of Strap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strap', 'v. t.', 'To beat or chastise with a strap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strap', 'v. t.', 'To fasten or bind with a strap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strap', 'v. t.', 'To sharpen by rubbing on a strap, or strop; as, to strap\n   a razor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strappadoes', 'pl. ', 'of Strappado');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strappado', 'n.', 'A military punishment formerly practiced, which\n   consisted in drawing an offender to the top of a beam and letting him\n   fall to the length of the rope, by which means a limb was often\n   dislocated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strappado', 'v. t.', 'To punish or torture by the strappado.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strapper', 'n.', 'One who uses strap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strapper', 'n.', 'A person or thing of uncommon size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strapping', 'a.', 'Tall; strong; lusty; large; as, a strapping fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strapple', 'v. t.', 'To hold or bind with, or as with, a strap; to\n   entangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strap-shaped', 'a.', 'Shaped like a strap; ligulate; as, a strap-shaped\n   corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strapwork', 'n.', 'A kind of ornament consisting of a narrow fillet or band\n   folded, crossed, and interlaced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strass', 'n.', 'A brilliant glass, used in the manufacture of artificial\n   paste gems, which consists essentially of a complex borosilicate of\n   lead and potassium. Cf. Glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strata', 'n.', 'pl. of Stratum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratagem', 'n.', 'An artifice or trick in war for deceiving the enemy;\n   hence, in general, artifice; deceptive device; secret plot; evil\n   machination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratagemical', 'a.', 'Containing stratagem; as, a stratagemical epistle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratarithmetry', 'n.', 'The art of drawing up an army, or any given number\n   of men, in any geometrical figure, or of estimating or expressing the\n   number of men in such a figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strategetic', 'a.', 'Alt. of Strategetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strategetical', 'a.', 'Strategic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strategetics', 'n.', 'Strategy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strategic', 'a.', 'Alt. of Strategical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strategical', 'a.', 'Of or pertaining to strategy; effected by artifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strategics', 'n.', 'Strategy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strategist', 'n.', 'One skilled in strategy, or the science of directing\n   great military movements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strategi', 'pl. ', 'of Strategus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strategus', 'n.', 'The leader or commander of an army; a general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strategy', 'n.', 'The science of military command, or the science of\n   projecting campaigns and directing great military movements;\n   generalship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strategy', 'n.', 'The use of stratagem or artifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strath', 'n.', 'A valley of considerable size, through which a river runs;\n   a valley bottom; -- often used in composition with the name of the\n   river; as, Strath Spey, Strathdon, Strathmore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strathspey', 'n.', 'A lively Scottish dance, resembling the reel, but\n   slower; also, the tune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straticulate', 'a.', 'Characterized by the presence of thin parallel\n   strata, or layers, as in an agate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratification', 'n.', 'The act or process of laying in strata, or the\n   state of being laid in the form of strata, or layers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratification', 'n.', 'The deposition of material in successive layers in\n   the growth of a cell wall, thus giving rise to a stratified appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratified', 'a.', 'Having its substance arranged in strata, or layers; as,\n   stratified rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratiform', 'a.', 'Having the form of strata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratified', 'imp. & p. p.', 'of Stratify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratifying', 'p. pr. & vb. n.', 'of Stratify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratify', 'v. t.', 'To form or deposit in strata, or layers, as substances\n   in the earth; to arrange in strata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratigraphic', 'a.', 'Alt. of Stratigraphical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratigraphical', 'a.', 'Pertaining to, or depended upon, the order or\n   arrangement of strata; as, stratigraphical evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratigraphic', 'a.', 'Alt. of -ical');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ical', 'a.', 'See Stratographic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratigraphy', 'n.', 'That branch of geology which treats of the\n   arrangement and succession of strata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratocracy', 'n.', 'A military government; government by military chiefs\n   and an army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratographic', 'a.', 'Alt. of Stratographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratographical', 'a.', 'Of or pertaining to stratography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratography', 'n.', 'A description of an army, or of what belongs to an\n   army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratonic', 'a.', 'Of or pertaining to an army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratotic', 'a.', 'Warlike; military.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratums', 'pl. ', 'of Stratum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strata', 'pl. ', 'of Stratum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratum', 'n.', 'A bed of earth or rock of one kind, formed by natural\n   causes, and consisting usually of a series of layers, which form a rock\n   as it lies between beds of other kinds. Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratum', 'n.', 'A bed or layer artificially made; a course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stratus', 'n.', 'A form of clouds in which they are arranged in a\n   horizontal band or layer. See Cloud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straught', '', 'imp. & p. p. of Stretch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straught', 'v. t.', 'To stretch; to make straight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straw', 'v. t.', 'To spread or scatter. See Strew, and Strow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straw', 'n.', 'A stalk or stem of certain species of grain, pulse, etc.,\n   especially of wheat, rye, oats, barley, more rarely of buckwheat,\n   beans, and pease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straw', 'n.', 'The gathered and thrashed stalks of certain species of\n   grain, etc.; as, a bundle, or a load, of rye straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straw', 'n.', 'Anything proverbially worthless; the least possible thing; a\n   mere trifle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strawberry', 'n.', 'A fragrant edible berry, of a delicious taste and\n   commonly of a red color, the fruit of a plant of the genus Fragaria, of\n   which there are many varieties. Also, the plant bearing the fruit. The\n   common American strawberry is Fragaria virginiana; the European, F.\n   vesca. There are also other less common species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strawboard', 'n.', 'Pasteboard made of pulp of straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straw-colored', 'a.', 'Being of a straw color. See Straw color, under\n   Straw, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straw-cutter', 'n.', 'An instrument to cut straw for fodder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strawed', '', 'imp. & p. p. of Straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strawworm', 'n.', 'A caddice worm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strawy', 'a.', 'Of or pertaining to straw; made of, or resembling, straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strayed', 'imp. & p. p.', 'of Stray');
  INSERT INTO words (word, word_type, description) VALUES  (    'Straying', 'p. pr. & vb. n.', 'of Stray');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stray', 'a.', 'To wander, as from a direct course; to deviate, or go out of\n   the way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stray', 'a.', 'To wander from company, or from the proper limits; to rove\n   at large; to roam; to go astray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stray', 'a.', 'Figuratively, to wander from the path of duty or rectitude;\n   to err.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stray', 'v. t.', 'To cause to stray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stray', 'v. i.', 'Having gone astray; strayed; wandering; as, a strayhorse\n   or sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stray', 'n.', 'Any domestic animal that has an inclosure, or its proper\n   place and company, and wanders at large, or is lost; an estray. Used\n   also figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stray', 'n.', 'The act of wandering or going astray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strayer', 'n.', 'One who strays; a wanderer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stre', 'n.', 'Straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streak', 'v. t.', 'To stretch; to extend; hence, to lay out, as a dead\n   body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streak', 'n.', 'A line or long mark of a different color from the ground; a\n   stripe; a vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streak', 'n.', 'A strake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streak', 'n.', 'The fine powder or mark yielded by a mineral when scratched\n   or rubbed against a harder surface, the color of which is sometimes a\n   distinguishing character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streak', 'n.', 'The rung or round of a ladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streaked', 'imp. & p. p.', 'of Streak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streaking', 'p. pr. & vb. n.', 'of Streak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streak', 'v. t.', 'To form streaks or stripes in or on; to stripe; to\n   variegate with lines of a different color, or of different colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streak', 'v. t.', 'With it as an object: To run swiftly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streaked', 'a.', 'Marked or variegated with stripes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streaked', 'a.', 'Uncomfortable; out of sorts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streaky', 'a.', 'Same as Streaked, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stream', 'n.', 'A current of water or other fluid; a liquid flowing\n   continuously in a line or course, either on the earth, as a river,\n   brook, etc., or from a vessel, reservoir, or fountain; specifically,\n   any course of running water; as, many streams are blended in the\n   Mississippi; gas and steam came from the earth in streams; a stream of\n   molten lead from a furnace; a stream of lava from a volcano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stream', 'n.', 'A beam or ray of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stream', 'n.', 'Anything issuing or moving with continued succession of\n   parts; as, a stream of words; a stream of sand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stream', 'n.', 'A continued current or course; as, a stream of weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stream', 'n.', 'Current; drift; tendency; series of tending or moving\n   causes; as, the stream of opinions or manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streamed', 'imp. & p. p.', 'of Stream');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streaming', 'p. pr. & vb. n.', 'of Stream');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stream', 'v. i.', 'To issue or flow in a stream; to flow freely or in a\n   current, as a fluid or whatever is likened to fluids; as, tears\n   streamed from her eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stream', 'v. i.', 'To pour out, or emit, a stream or streams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stream', 'v. i.', 'To issue in a stream of light; to radiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stream', 'v. i.', 'To extend; to stretch out with a wavy motion; to float\n   in the wind; as, a flag streams in the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stream', 'v. t.', 'To send forth in a current or stream; to cause to flow;\n   to pour; as, his eyes streamed tears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stream', 'v. t.', 'To mark with colors or embroidery in long tracts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stream', 'v. t.', 'To unfurl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streamer', 'n.', 'An ensign, flag, or pennant, which floats in the wind;\n   specifically, a long, narrow, ribbonlike flag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streamer', 'n.', 'A stream or column of light shooting upward from the\n   horizon, constituting one of the forms of the aurora borealis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streamer', 'n.', 'A searcher for stream tin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streamful', 'a.', 'Abounding in streams, or in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streaminess', 'n.', 'The state of being streamy; a trailing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streaming', 'a.', 'Sending forth streams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streaming', 'n.', 'The act or operation of that which streams; the act of\n   that which sends forth, or which runs in, streams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streaming', 'n.', 'The reduction of stream tin; also, the search for stream\n   tin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streamless', 'a.', 'Destitute of streams, or of a stream, as a region of\n   country, or a dry channel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streamlet', 'n.', 'A small stream; a rivulet; a rill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streamy', 'a.', 'Abounding with streams, or with running water; streamful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streamy', 'a.', 'Resembling a stream; issuing in a stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stree', 'n.', 'Straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streek', 'v. t.', 'To stretch; also, to lay out, as a dead body. See\n   Streak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streel', 'v. i.', 'To trail along; to saunter or be drawn along,\n   carelessly, swaying in a kind of zigzag motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streen', 'n.', 'See Strene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Street', 'a.', 'Originally, a paved way or road; a public highway; now\n   commonly, a thoroughfare in a city or village, bordered by dwellings or\n   business houses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streetwalker', 'n.', 'A common prostitute who walks the streets to find\n   customers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streetward', 'n.', 'An officer, or ward, having the care of the streets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streetward', 'a.', 'Facing toward the street.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streight', 'a., n., & adv.', 'See 2nd Strait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streighten', 'v. t.', 'See Straiten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strein', 'v. t.', 'To strain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streit', 'a.', 'Drawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streit', 'a.', 'Close; narrow; strict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streite', 'adv.', 'Narrowly; strictly; straitly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strelitz', 'n. sing. & pl.', 'A soldier of the ancient Muscovite guard or\n   Russian standing army; also, the guard itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strelitzia', 'n.', 'A genus of plants related to the banana, found at the\n   Cape of Good Hope. They have rigid glaucous distichous leaves, and\n   peculiar richly colored flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strene', 'n.', 'Race; offspring; stock; breed; strain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strenger', '', 'Alt. of Strengest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strengest', '', 'the original compar. & superl. of Strong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strength', 'n.', 'The quality or state of being strong; ability to do or to\n   bear; capacity for exertion or endurance, whether physical,\n   intellectual, or moral; force; vigor; power; as, strength of body or of\n   the arm; strength of mind, of memory, or of judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strength', 'n.', 'Power to resist force; solidity or toughness; the quality\n   of bodies by which they endure the application of force without\n   breaking or yielding; -- in this sense opposed to frangibility; as, the\n   strength of a bone, of a beam, of a wall, a rope, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strength', 'n.', 'Power of resisting attacks; impregnability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strength', 'n.', 'That quality which tends to secure results; effective\n   power in an institution or enactment; security; validity; legal or\n   moral force; logical conclusiveness; as, the strength of social or\n   legal obligations; the strength of law; the strength of public opinion;\n   strength of evidence; strength of argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strength', 'n.', 'One who, or that which, is regarded as embodying or\n   affording force, strength, or firmness; that on which confidence or\n   reliance is based; support; security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strength', 'n.', 'Force as measured; amount, numbers, or power of any body,\n   as of an army, a navy, and the like; as, what is the strength of the\n   enemy by land, or by sea?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strength', 'n.', 'Vigor or style; force of expression; nervous diction; --\n   said of literary work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strength', 'n.', 'Intensity; -- said of light or color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strength', 'n.', 'Intensity or degree of the distinguishing and essential\n   element; spirit; virtue; excellence; -- said of liquors, solutions,\n   etc.; as, the strength of wine or of acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strength', 'n.', 'A strong place; a stronghold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strength', 'v. t.', 'To strengthen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strengthened', 'imp. & p. p.', 'of Strengthen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strengthening', 'p. pr. & vb. n.', 'of Strengthen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strengthen', 'v. t.', 'To make strong or stronger; to add strength to; as,\n   to strengthen a limb, a bridge, an army; to strengthen an obligation;\n   to strengthen authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strengthen', 'v. t.', 'To animate; to encourage; to fix in resolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strengthen', 'v. i.', 'To grow strong or stronger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strengthener', 'n.', 'One who, or that which, gives or adds strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strengthening', 'a.', 'That strengthens; giving or increasing strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strengthful', 'a.', 'Abounding in strength; full of strength; strong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strengthing', 'n.', 'A stronghold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strengthless', 'a.', 'Destitute of strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strengthner', 'n.', 'See Strengthener.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strengthy', 'a.', 'Having strength; strong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strenuity', 'n.', 'Strenuousness; activity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strenuous', 'a.', 'Eagerly pressing or urgent; zealous; ardent; earnest;\n   bold; valiant; intrepid; as, a strenuous advocate for national rights;\n   a strenuous reformer; a strenuous defender of his country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strepent', 'a.', 'Noisy; loud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streperous', 'a.', 'Loud; boisterous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strepitores', 'n. pl.', 'A division of birds, including the clamatorial and\n   picarian birds, which do not have well developed singing organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strepsipter', 'n.', 'Alt. of Strepsipteran');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strepsipteran', 'n.', 'One of the Strepsiptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strepsiptera', 'n. pl.', 'A group of small insects having the anterior\n   wings rudimentary, and in the form of short and slender twisted\n   appendages, while the posterior ones are large and membranous. They are\n   parasitic in the larval state on bees, wasps, and the like; -- called\n   also Rhipiptera. See Illust. under Rhipipter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strepsipterous', 'a.', 'Of or pertaining to Strepsiptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strepsorhina', 'n. pl.', 'Same as Lemuroidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strepsorhine', 'a.', 'Having twisted nostrils; -- said of the lemurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strepsorhine', 'n.', 'One of the Strepsorhina; a lemur. See Illust. under\n   Monkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streptobacteria', 'n. pl.', 'A so-called variety of bacterium, consisting\n   in reality of several bacteria linked together in the form of a chain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streptococci', 'pl. ', 'of Streptococcus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streptococcus', 'n.', 'A long or short chain of micrococci, more or less\n   curved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streptoneura', 'n. pl.', 'An extensive division of gastropod Mollusca in\n   which the loop or visceral nerves is twisted, and the sexes separate.\n   It is nearly to equivalent to Prosobranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Streptothrix', 'n.', 'A genus of bacilli occurring of the form of long,\n   smooth and apparently branched threads, either straight or twisted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stress', 'n.', 'Distress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stress', 'n.', 'Pressure, strain; -- used chiefly of immaterial things;\n   except in mechanics; hence, urgency; importance; weight; significance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stress', 'n.', 'The force, or combination of forces, which produces a\n   strain; force exerted in any direction or manner between contiguous\n   bodies, or parts of bodies, and taking specific names according to its\n   direction, or mode of action, as thrust or pressure, pull or tension,\n   shear or tangential stress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stress', 'n.', 'Force of utterance expended upon words or syllables. Stress\n   is in English the chief element in accent and is one of the most\n   important in emphasis. See Guide to pronunciation, // 31-35.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stress', 'n.', 'Distress; the act of distraining; also, the thing\n   distrained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stress', 'v. t.', 'To press; to urge; to distress; to put to difficulties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stress', 'v. t.', 'To subject to stress, pressure, or strain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stressful', 'a.', 'Having much stress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretched', 'imp. & p. p.', 'of Stretch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretching', 'p. pr. & vb. n.', 'of Stretch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretch', 'v. t.', 'To reach out; to extend; to put forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretch', 'v. t.', 'To draw out to the full length; to cause to extend in a\n   straight line; as, to stretch a cord or rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretch', 'v. t.', 'To cause to extend in breadth; to spread; to expand;\n   as, to stretch cloth; to stretch the wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretch', 'v. t.', 'To make tense; to tighten; to distend forcibly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretch', 'v. t.', 'To draw or pull out to greater length; to strain; as,\n   to stretch a tendon or muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretch', 'v. t.', 'To exaggerate; to extend too far; as, to stretch the\n   truth; to stretch ones credit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretch', 'v. i.', 'To be extended; to be drawn out in length or in\n   breadth, or both; to spread; to reach; as, the iron road stretches\n   across the continent; the lake stretches over fifty square miles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretch', 'v. i.', 'To extend or spread ones self, or ones limbs; as, the\n   lazy man yawns and stretches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretch', 'v. i.', 'To be extended, or to bear extension, without breaking,\n   as elastic or ductile substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretch', 'v. i.', 'To strain the truth; to exaggerate; as, a man apt to\n   stretch in his report of facts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretch', 'v. i.', 'To sail by the wind under press of canvas; as, the ship\n   stretched to the eastward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretch', 'n.', 'Act of stretching, or state of being stretched; reach;\n   effort; struggle; strain; as, a stretch of the limbs; a stretch of the\n   imagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretch', 'n.', 'A continuous line or surface; a continuous space of time;\n   as, grassy stretches of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretch', 'n.', 'The extent to which anything may be stretched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretch', 'n.', 'The reach or extent of a vessels progress on one tack; a\n   tack or board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretch', 'n.', 'Course; direction; as, the stretch of seams of coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretcher', 'n.', 'One who, or that which, stretches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretcher', 'n.', 'A brick or stone laid with its longer dimension in the\n   line of direction of the wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretcher', 'n.', 'A piece of timber used in building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretcher', 'n.', 'A narrow crosspiece of the bottom of a boat against\n   which a rower braces his feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretcher', 'n.', 'A crosspiece placed between the sides of a boat to keep\n   them apart when hoisted up and griped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretcher', 'n.', 'A litter, or frame, for carrying disabled, wounded, or\n   dead persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretcher', 'n.', 'An overstretching of the truth; a lie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretcher', 'n.', 'One of the rods in an umbrella, attached at one end to\n   one of the ribs, and at the other to the tube sliding upon the handle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretcher', 'n.', 'An instrument for stretching boots or gloves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretcher', 'n.', 'The frame upon which canvas is stretched for a painting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretching', '', 'a. & n. from Stretch, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretto', 'n.', 'The crowding of answer upon subject near the end of a\n   fugue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stretto', 'n.', 'In an opera or oratorio, a coda, or winding up, in an\n   accelerated time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strewed', 'imp. & p. p.', 'of Strew');
  INSERT INTO words (word, word_type, description) VALUES  (    'strewn', 'p. p.', 'of Strew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strewing', 'p. pr. & vb. n.', 'of Strew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strew', 'v. t.', 'To scatter; to spread by scattering; to cast or to throw\n   loosely apart; -- used of solids, separated or separable into parts or\n   particles; as, to strew seed in beds; to strew sand on or over a floor;\n   to strew flowers over a grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strew', 'v. t.', 'To cover more or less thickly by scattering something\n   over or upon; to cover, or lie upon, by having been scattered; as, they\n   strewed the ground with leaves; leaves strewed the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strew', 'v. t.', 'To spread abroad; to disseminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strewing', 'n.', 'The act of scattering or spreading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strewing', 'n.', 'Anything that is, or may be, strewed; -- used chiefly in\n   the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strewment', 'n.', 'Anything scattered, as flowers for decoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strewn', '', 'p. p. of Strew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striae', 'pl. ', 'of Stria');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stria', 'n.', 'A minute groove, or channel; a threadlike line, as of color;\n   a narrow structural band or line; a striation; as, the striae, or\n   groovings, produced on a rock by a glacier passing over it; the striae\n   on the surface of a shell; a stria of nervous matter in the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stria', 'n.', 'A fillet between the flutes of columns, pilasters, or the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striated', 'imp. & p. p.', 'of Striate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striating', 'p. pr. & vb. n.', 'of Striate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striate', 'a.', 'To mark with striaae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striate', 'a.', 'Alt. of Striated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striated', 'a.', 'Marked with striaae, or fine grooves, or lines of color;\n   showing narrow structural bands or lines; as, a striated crystal;\n   striated muscular fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striation', 'n.', 'The quality or condition of being striated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striation', 'n.', 'A stria; as, the striations on a shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striatum', 'n.', 'The corpus striatum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striature', 'n.', 'A stria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strich', 'n.', 'An owl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strick', 'n.', 'A bunch of hackled flax prepared for drawing into slivers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stricken', 'p. p. & a.', 'Struck; smitten; wounded; as, the stricken deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stricken', 'n.', 'Worn out; far gone; advanced. See Strike, v. t., 21.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stricken', 'v. t.', 'Whole; entire; -- said of the hour as marked by the\n   striking of a clock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strickle', 'n.', 'An instrument to strike grain to a level with the\n   measure; a strike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strickle', 'n.', 'An instrument for whetting scythes; a rifle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strickle', 'n.', 'An instrument used for smoothing the surface of a core.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strickle', 'n.', 'A templet; a pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strickle', 'n.', 'An instrument used in dressing flax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strickler', 'n.', 'See Strickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strickless', 'n.', 'See Strickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strict', 'a.', 'Strained; drawn close; tight; as, a strict embrace; a\n   strict ligature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strict', 'a.', 'Tense; not relaxed; as, a strict fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strict', 'a.', 'Exact; accurate; precise; rigorously nice; as, to keep\n   strict watch; to pay strict attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strict', 'a.', 'Governed or governing by exact rules; observing exact\n   rules; severe; rigorous; as, very strict in observing the Sabbath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strict', 'a.', 'Rigidly; interpreted; exactly limited; confined;\n   restricted; as, to understand words in a strict sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strict', 'a.', 'Upright, or straight and narrow; -- said of the shape of\n   the plants or their flower clusters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striction', 'n.', 'The act of constricting, or the state of being\n   constricted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strictly', 'adv.', 'In a strict manner; closely; precisely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strictness', 'n.', 'Quality or state of being strict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stricture', 'n.', 'Strictness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stricture', 'n.', 'A stroke; a glance; a touch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stricture', 'n.', 'A touch of adverse criticism; censure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stricture', 'n.', 'A localized morbid contraction of any passage of the\n   body. Cf. Organic stricture, and Spasmodic stricture, under Organic,\n   and Spasmodic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strictured', 'a.', 'Affected with a stricture; as, a strictured duct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strid', 'n.', 'A narrow passage between precipitous rocks or banks, which\n   looks as if it might be crossed at a stride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strode', 'imp.', 'of Stride');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strid', '', 'of Stride');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stridden', 'p. p.', 'of Stride');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strid', '', 'of Stride');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striding', 'p. pr. & vb. n.', 'of Stride');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stride', 'v. t.', 'To walk with long steps, especially in a measured or\n   pompous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stride', 'v. t.', 'To stand with the legs wide apart; to straddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stride', 'v. t.', 'To pass over at a step; to step over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stride', 'v. t.', 'To straddle; to bestride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stride', 'n.', 'The act of stridding; a long step; the space measured by a\n   long step; as, a masculine stride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strident', 'a.', 'Characterized by harshness; grating; shrill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stridor', 'n.', 'A harsh, shrill, or creaking noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stridulate', 'v. t.', 'To make a shrill, creaking noise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stridulate', 'v. t.', 'to make a shrill or musical sound, such as is made\n   by the males of many insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stridulation', 'n.', 'The act of stridulating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stridulation', 'n.', 'The act of making shrill sounds or musical notes by\n   rubbing together certain hard parts, as is done by the males of many\n   insects, especially by Orthoptera, such as crickets, grasshoppers, and\n   locusts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stridulation', 'n.', 'The noise itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stridulator', 'n.', 'That which stridulates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stridulatory', 'a.', 'Stridulous; able to stridulate; used in stridulating;\n   adapted for stridulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stridulous', 'a.', 'Making a shrill, creaking sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strife', 'n.', 'The act of striving; earnest endeavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strife', 'n.', 'Exertion or contention for superiority; contest of\n   emulation, either by intellectual or physical efforts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strife', 'n.', 'Altercation; violent contention; fight; battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strife', 'n.', 'That which is contended against; occasion of contest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strifeful', 'a.', 'Contentious; discordant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strigate', 'a.', 'Having transverse bands of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striges', 'n. pl.', 'The tribe of birds which comprises the owls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strigil', 'n.', 'An instrument of metal, ivory, etc., used for scraping the\n   skin at the bath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strigillose', 'a.', 'Set with stiff, slender bristles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strigine', 'a.', 'Of or pertaining to owls; owl-like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strigment', 'n.', 'Scraping; that which is scraped off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strigose', 'a.', 'Set with stiff, straight bristles; hispid; as, a strigose\n   leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strigous', 'a.', 'Strigose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struck', 'imp.', 'of Strike');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struck', 'p. p.', 'of Strike');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stricken', '', 'of Strike');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroock', '', 'of Strike');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strucken', '', 'of Strike');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striking', 'p. pr. & vb. n.', 'of Strike');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To touch or hit with some force, either with the hand or\n   with an instrument; to smite; to give a blow to, either with the hand\n   or with any instrument or missile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To come in collision with; to strike against; as, a\n   bullet struck him; the wave struck the boat amidships; the ship struck\n   a reef.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To give, as a blow; to impel, as with a blow; to give a\n   force to; to dash; to cast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To stamp or impress with a stroke; to coin; as, to\n   strike coin from metal: to strike dollars at the mint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To thrust in; to cause to enter or penetrate; to set in\n   the earth; as, a tree strikes its roots deep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To punish; to afflict; to smite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To cause to sound by one or more beats; to indicate or\n   notify by audible strokes; as, the clock strikes twelve; the drums\n   strike up a march.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To lower; to let or take down; to remove; as, to strike\n   sail; to strike a flag or an ensign, as in token of surrender; to\n   strike a yard or a topmast in a gale; to strike a tent; to strike the\n   centering of an arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To make a sudden impression upon, as by a blow; to\n   affect sensibly with some strong emotion; as, to strike the mind, with\n   surprise; to strike one with wonder, alarm, dread, or horror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To affect in some particular manner by a sudden\n   impression or impulse; as, the plan proposed strikes me favorably; to\n   strike one dead or blind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To cause or produce by a stroke, or suddenly, as by a\n   stroke; as, to strike a light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To cause to ignite; as, to strike a match.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To make and ratify; as, to strike a bargain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To take forcibly or fraudulently; as, to strike money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To level, as a measure of grain, salt, or the like, by\n   scraping off with a straight instrument what is above the level of the\n   top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To cut off, as a mortar joint, even with the face of the\n   wall, or inward at a slight angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To hit upon, or light upon, suddenly; as, my eye struck\n   a strange word; they soon struck the trail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To borrow money of; to make a demand upon; as, he struck\n   a friend for five dollars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To lade into a cooler, as a liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To stroke or pass lightly; to wave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. t.', 'To advance; to cause to go forward; -- used only in past\n   participle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. i.', 'To move; to advance; to proceed; to take a course; as,\n   to strike into the fields.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. i.', 'To deliver a quick blow or thrust; to give blows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. i.', 'To hit; to collide; to dush; to clash; as, a hammer\n   strikes against the bell of a clock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. i.', 'To sound by percussion, with blows, or as with blows; to\n   be struck; as, the clock strikes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. i.', 'To make an attack; to aim a blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. i.', 'To touch; to act by appulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. i.', 'To run upon a rock or bank; to be stranded; as, the ship\n   struck in the night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. i.', 'To pass with a quick or strong effect; to dart; to\n   penetrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. i.', 'To break forth; to commence suddenly; -- with into; as,\n   to strike into reputation; to strike into a run.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. i.', 'To lower a flag, or colors, in token of respect, or to\n   signify a surrender of a ship to an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. i.', 'To quit work in order to compel an increase, or prevent\n   a reduction, of wages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. i.', 'To become attached to something; -- said of the spat of\n   oysters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'v. i.', 'To steal money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'n.', 'The act of striking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'n.', 'An instrument with a straight edge for leveling a measure\n   of grain, salt, and the like, scraping off what is above the level of\n   the top; a strickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'n.', 'A bushel; four pecks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'n.', 'An old measure of four bushels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'n.', 'Fullness of measure; hence, excellence of quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'n.', 'An iron pale or standard in a gate or fence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'n.', 'The act of quitting work; specifically, such an act by a\n   body of workmen, done as a means of enforcing compliance with demands\n   made on their employer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'n.', 'A puddlers stirrer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'n.', 'The horizontal direction of the outcropping edges of tilted\n   rocks; or, the direction of a horizontal line supposed to be drawn on\n   the surface of a tilted stratum. It is at right angles to the dip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strike', 'n.', 'The extortion of money, or the attempt to extort money, by\n   threat of injury; blackmailing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striker', 'n.', 'One who, or that which, strikes; specifically, a\n   blacksmiths helper who wields the sledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striker', 'n.', 'A harpoon; also, a harpooner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striker', 'n.', 'A wencher; a lewd man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striker', 'n.', 'A workman who is on a strike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striker', 'n.', 'A blackmailer in politics; also, one whose political\n   influence can be bought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striking', '', 'a. & n. from Strike, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striking', 'a.', 'Affecting with strong emotions; surprising; forcible;\n   impressive; very noticeable; as, a striking representation or image; a\n   striking resemblance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strikle', 'n.', 'See Strickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'String', 'n.', 'A small cord, a line, a twine, or a slender strip of\n   leather, or other substance, used for binding together, fastening, or\n   tying things; a cord, larger than a thread and smaller than a rope; as,\n   a shoe string; a bonnet string; a silken string.');
  INSERT INTO words (word, word_type, description) VALUES  (    'String', 'n.', 'A thread or cord on which a number of objects or parts are\n   strung or arranged in close and orderly succession; hence, a line or\n   series of things arranged on a thread, or as if so arranged; a\n   succession; a concatenation; a chain; as, a string of shells or beads;\n   a string of dried apples; a string of houses; a string of arguments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'String', 'n.', 'A strip, as of leather, by which the covers of a book are\n   held together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'String', 'n.', 'The cord of a musical instrument, as of a piano, harp, or\n   violin; specifically (pl.), the stringed instruments of an orchestra,\n   in distinction from the wind instruments; as, the strings took up the\n   theme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'String', 'n.', 'The line or cord of a bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'String', 'n.', 'A fiber, as of a plant; a little, fibrous root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'String', 'n.', 'A nerve or tendon of an animal body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'String', 'n.', 'An inside range of ceiling planks, corresponding to the\n   sheer strake on the outside and bolted to it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'String', 'n.', 'The tough fibrous substance that unites the valves of the\n   pericap of leguminous plants, and which is readily pulled off; as, the\n   strings of beans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'String', 'n.', 'A small, filamentous ramification of a metallic vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'String', 'n.', 'Same as Stringcourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'String', 'n.', 'The points made in a game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strung', 'imp.', 'of String');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strung', 'p. p.', 'of String');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringed', '', 'of String');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringing', 'p. pr. & vb. n.', 'of String');
  INSERT INTO words (word, word_type, description) VALUES  (    'String', 'v. t.', 'To furnish with strings; as, to string a violin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'String', 'v. t.', 'To put in tune the strings of, as a stringed instrument,\n   in order to play upon it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'String', 'v. t.', 'To put on a string; to file; as, to string beads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'String', 'v. t.', 'To make tense; to strengthen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'String', 'v. t.', 'To deprive of strings; to strip the strings from; as, to\n   string beans. See String, n., 9.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringboard', 'n.', 'Same as Stringpiece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringcourse', 'n.', 'A horizontal band in a building, forming a part of\n   the design, whether molded, projecting, or carved, or in any way\n   distinguished from the rest of the work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringed', 'a.', 'Having strings; as, a stringed instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringed', 'a.', 'Produced by strings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringency', 'n.', 'The quality or state of being stringent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringendo', 'a.', 'Urging or hastening the time, as to a climax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringent', 'a.', 'Binding strongly; making strict requirements;\n   restrictive; rigid; severe; as, stringent rules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringer', 'n.', 'One who strings; one who makes or provides strings,\n   especially for bows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringer', 'n.', 'A libertine; a wencher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringer', 'n.', 'A longitudinal sleeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringer', 'n.', 'A streak of planking carried round the inside of a vessel\n   on the under side of the beams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringer', 'n.', 'A long horizontal timber to connect uprights in a frame,\n   or to support a floor or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringhalt', 'n.', 'An habitual sudden twitching of the hinder leg of a\n   horse, or an involuntary or convulsive contraction of the muscles that\n   raise the hock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringiness', 'n.', 'Quality of being stringy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringless', 'a.', 'Having no strings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringpiece', 'n.', 'A long piece of timber, forming a margin or edge of\n   any piece of construction; esp.:');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringpiece', 'n.', 'One of the longitudinal pieces, supporting the treads\n   and rises of a flight or run of stairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringy', 'a.', 'Consisting of strings, or small threads; fibrous;\n   filamentous; as, a stringy root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stringy', 'a.', 'Capable of being drawn into a string, as a glutinous\n   substance; ropy; viscid; gluely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stripped', 'imp. & p. p.', 'of Strip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stripping', 'p. pr. & vb. n.', 'of Strip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strip', 'v. t.', 'To deprive; to bereave; to make destitute; to plunder;\n   especially, to deprive of a covering; to skin; to peel; as, to strip a\n   man of his possession, his rights, his privileges, his reputation; to\n   strip one of his clothes; to strip a beast of his skin; to strip a tree\n   of its bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strip', 'v. t.', 'To divest of clothing; to uncover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strip', 'v. t.', 'To dismantle; as, to strip a ship of rigging, spars, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strip', 'v. t.', 'To pare off the surface of, as land, in strips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strip', 'v. t.', 'To deprive of all milk; to milk dry; to draw the last\n   milk from; hence, to milk with a peculiar movement of the hand on the\n   teats at the last of a milking; as, to strip a cow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strip', 'v. t.', 'To pass; to get clear of; to outstrip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strip', 'v. t.', 'To pull or tear off, as a covering; to remove; to wrest\n   away; as, to strip the skin from a beast; to strip the bark from a\n   tree; to strip the clothes from a mans back; to strip away all\n   disguisses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strip', 'v. t.', 'To tear off (the thread) from a bolt or nut; as, the\n   thread is stripped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strip', 'v. t.', 'To tear off the thread from (a bolt or nut); as, the bolt\n   is stripped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strip', 'v. t.', 'To remove the metal coating from (a plated article), as\n   by acids or electrolytic action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strip', 'v. t.', 'To remove fiber, flock, or lint from; -- said of the\n   teeth of a card when it becomes partly clogged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strip', 'v. t.', 'To pick the cured leaves from the stalks of (tobacco) and\n   tie them into \"hands\"; to remove the midrib from (tobacco leaves).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strip', 'v. i.', 'To take off, or become divested of, clothes or covering;\n   to undress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strip', 'v. i.', 'To fail in the thread; to lose the thread, as a bolt,\n   screw, or nut. See Strip, v. t., 8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strip', 'n.', 'A narrow piece, or one comparatively long; as, a strip of\n   cloth; a strip of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strip', 'n.', 'A trough for washing ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strip', 'n.', 'The issuing of a projectile from a rifled gun without\n   acquiring the spiral motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stripe', 'n.', 'A line, or long, narrow division of anything of a different\n   color or structure from the ground; hence, any linear variation of\n   color or structure; as, a stripe, or streak, of red on a green ground;\n   a raised stripe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stripe', 'n.', 'A pattern produced by arranging the warp threads in sets of\n   alternating colors, or in sets presenting some other contrast of\n   appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stripe', 'n.', 'A strip, or long, narrow piece attached to something of a\n   different color; as, a red or blue stripe sewed upon a garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stripe', 'n.', 'A stroke or blow made with a whip, rod, scourge, or the\n   like, such as usually leaves a mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stripe', 'n.', 'A long, narrow discoloration of the skin made by the blow\n   of a lash, rod, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stripe', 'n.', 'Color indicating a party or faction; hence, distinguishing\n   characteristic; sign; likeness; sort; as, persons of the same political\n   stripe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stripe', 'n.', 'The chevron on the coat of a noncommissioned officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striped', 'imp. & p. p.', 'of Stripe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striping', 'p. pr. & vb. n.', 'of Stripe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stripe', 'v. t.', 'To make stripes upon; to form with lines of different\n   colors or textures; to variegate with stripes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stripe', 'v. t.', 'To strike; to lash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striped', 'a.', 'Having stripes of different colors; streaked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strip-leaf', 'n.', 'Tobacco which has been stripped of its stalks before\n   packing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stripling', 'n.', 'A youth in the state of adolescence, or just passing\n   from boyhood to manhood; a lad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stripper', 'n.', 'One who, or that which, strips; specifically, a machine\n   for stripping cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strippet', 'n.', 'A small stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stripping', 'n.', 'The act of one who strips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stripping', 'n.', 'The last milk drawn from a cow at a milking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strisores', 'n. pl.', 'A division of passerine birds including the humming\n   birds, swifts, and goatsuckers. It is now generally considered an\n   artificial group.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strove', 'imp.', 'of Strive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striven', 'p. p.', 'of Strive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strove', '', 'of Strive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striving', 'p. pr. & vb. n.', 'of Strive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strive', 'v. i.', 'To make efforts; to use exertions; to endeavor with\n   earnestness; to labor hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strive', 'v. i.', 'To struggle in opposition; to be in contention or\n   dispute; to contend; to contest; -- followed by against or with before\n   the person or thing opposed; as, strive against temptation; strive for\n   the truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strive', 'v. i.', 'To vie; to compete; to be a rival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strive', 'n.', 'An effort; a striving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strive', 'n.', 'Strife; contention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strived', 'p. p.', 'Striven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striven', '', 'p. p. of Strive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striver', 'n.', 'One who strives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Striving', '', 'a. & n. from Strive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strix', 'n.', 'One of the flutings of a column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroam', 'v. i.', 'To wander about idly and vacantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroam', 'v. i.', 'To take long strides in walking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strobilae', 'pl. ', 'of Strobila');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strobila', 'n.', 'A form of the larva of certain Discophora in a state of\n   development succeeding the scyphistoma. The body of the strobila\n   becomes elongated, and subdivides transversely into a series of lobate\n   segments which eventually become ephyrae, or young medusae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strobila', 'n.', 'A mature tapeworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strobilaceous', 'a.', 'Of or pertaining to a strobile or cone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strobilaceous', 'a.', 'Producing strobiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strobilation', 'n.', 'The act or phenomenon of spontaneously dividing\n   transversely, as do certain species of annelids and helminths;\n   transverse fission. See Illust. under Syllidian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strobile', 'n.', 'A scaly multiple fruit resulting from the ripening of an\n   ament in certain plants, as the hop or pine; a cone. See Cone, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strobile', 'n.', 'An individual asexually producing sexual individuals\n   differing from itself also in other respects, as the tapeworm, -- one\n   of the forms that occur in metagenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strobile', 'n.', 'Same as Strobila.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strobiliform', 'a.', 'Shaped like a strobile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strobiline', 'a.', 'Of or pertaining to a strobile; strobilaceous;\n   strobiliform; as, strobiline fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroboscope', 'n.', 'An instrument for studying or observing the successive\n   phases of a periodic or varying motion by means of light which is\n   periodically interrupted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroboscope', 'n.', 'An optical toy similar to the phenakistoscope. See\n   Phenakistoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strockle', 'n.', 'A shovel with a turned-up edge, for frit, sand, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strode', 'n.', 'See Strude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strode', '', 'imp. of Stride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'imp.', 'Struck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'The act of striking; a blow; a hit; a knock; esp., a\n   violent or hostile attack made with the arm or hand, or with an\n   instrument or weapon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'The result of effect of a striking; injury or\n   affliction; soreness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'The striking of the clock to tell the hour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'A gentle, caressing touch or movement upon something; a\n   stroking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'A mark or dash in writing or printing; a line; the touch\n   of a pen or pencil; as, an up stroke; a firm stroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'Hence, by extension, an addition or amandment to a\n   written composition; a touch; as, to give some finishing strokes to an\n   essay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'A sudden attack of disease; especially, a fatal attack;\n   a severe disaster; any affliction or calamity, especially a sudden one;\n   as, a stroke of apoplexy; the stroke of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'A throb or beat, as of the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'One of a series of beats or movements against a\n   resisting medium, by means of which movement through or upon it is\n   accomplished; as, the stroke of a birds wing in flying, or an oar in\n   rowing, of a skater, swimmer, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'The rate of succession of stroke; as, a quick stroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'The oar nearest the stern of a boat, by which the other\n   oars are guided; -- called also stroke oar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'The rower who pulls the stroke oar; the strokesman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'A powerful or sudden effort by which something is done,\n   produced, or accomplished; also, something done or accomplished by such\n   an effort; as, a stroke of genius; a stroke of business; a master\n   stroke of policy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'The movement, in either direction, of the piston\n   plunger, piston rod, crosshead, etc., as of a steam engine or a pump,\n   in which these parts have a reciprocating motion; as, the forward\n   stroke of a piston; also, the entire distance passed through, as by a\n   piston, in such a movement; as, the piston is at half stroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'Power; influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'Appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strokeed', 'imp. & p. p.', 'of Stroke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strokeing', 'p. pr. & vb. n.', 'of Stroke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'To strike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'To rib gently in one direction; especially, to pass the\n   hand gently over by way of expressing kindness or tenderness; to\n   caress; to soothe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'To make smooth by rubbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'To give a finely fluted surface to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroke', 'v. t.', 'To row the stroke oar of; as, to stroke a boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroker', 'n.', 'One who strokes; also, one who pretends to cure by\n   stroking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strokesman', 'pl. ', 'of Strokesman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strokesman', 'n.', 'The man who rows the aftermost oar, and whose stroke is\n   to be followed by the rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroking', 'n.', 'The act of rubbing gently with the hand, or of smoothing;\n   a stroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroking', 'n.', 'The act of laying small gathers in cloth in regular\n   order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroking', 'n.', 'See Stripping, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strolled', 'imp. & p. p.', 'of Stroll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strolling', 'p. pr. & vb. n.', 'of Stroll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroll', 'v. i.', 'To wander on foot; to ramble idly or leisurely; to rove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroll', 'n.', 'A wandering on foot; an idle and leisurely walk; a ramble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroller', 'n.', 'One who strolls; a vagrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stromata', 'pl. ', 'of Stroma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroma', 'n.', 'The connective tissue or supporting framework of an organ;\n   as, the stroma of the kidney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroma', 'n.', 'The spongy, colorless framework of a red blood corpuscle or\n   other cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroma', 'n.', 'A layer or mass of cellular tissue, especially that part of\n   the thallus of certain fungi which incloses the perithecia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stromatic', 'a.', 'Miscellaneous; composed of different kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stromatology', 'n.', 'The history of the formation of stratified rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stromb', 'n.', 'Any marine univalve mollusk of the genus Strombus and\n   allied genera. See Conch, and Strombus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strombite', 'n.', 'A fossil shell of the genus Strombus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stromboid', 'a.', 'Of, pertaining to, or like, Strombus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strombuliform', 'a.', 'Formed or shaped like a top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strombuliform', 'a.', 'Coiled into the shape of a screw or a helix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strombus', 'n.', 'A genus of marine gastropods in which the shell has the\n   outer lip dilated into a broad wing. It includes many large and\n   handsome species commonly called conch shells, or conchs. See Conch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stromeyerite', 'n.', 'A steel-gray mineral of metallic luster. It is a\n   sulphide of silver and copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strond', 'n.', 'Strand; beach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Having active physical power, or great physical power\n   to act; having a power of exerting great bodily force; vigorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Having passive physical power; having ability to bear\n   or endure; firm; hale; sound; robust; as, a strong constitution; strong\n   health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Solid; tough; not easily broken or injured; able to\n   withstand violence; able to sustain attacks; not easily subdued or\n   taken; as, a strong beam; a strong rock; a strong fortress or town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Having great military or naval force; powerful; as, a\n   strong army or fleet; a nation strong at sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Having great wealth, means, or resources; as, a strong\n   house, or company of merchants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Reaching a certain degree or limit in respect to\n   strength or numbers; as, an army ten thousand strong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Moving with rapidity or force; violent; forcible;\n   impetuous; as, a strong current of water or wind; the wind was strong\n   from the northeast; a strong tide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Adapted to make a deep or effectual impression on the\n   mind or imagination; striking or superior of the kind; powerful;\n   forcible; cogent; as, a strong argument; strong reasons; strong\n   evidence; a strong example; strong language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Ardent; eager; zealous; earnestly engaged; as, a\n   strong partisan; a strong Whig or Tory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Having virtues of great efficacy; or, having a\n   particular quality in a great degree; as, a strong powder or tincture;\n   a strong decoction; strong tea or coffee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Full of spirit; containing a large proportion of\n   alcohol; intoxicating; as, strong liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Affecting any sense powerfully; as, strong light,\n   colors, etc.; a strong flavor of onions; a strong scent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Solid; nourishing; as, strong meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Well established; firm; not easily overthrown or\n   altered; as, a strong custom; a strong belief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Violent; vehement; earnest; ardent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Having great force, vigor, power, or the like, as the\n   mind, intellect, or any faculty; as, a man of a strong mind, memory,\n   judgment, or imagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Vigorous; effective; forcible; powerful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Tending to higher prices; rising; as, a strong market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Pertaining to, or designating, a verb which forms its\n   preterit (imperfect) by a variation in the root vowel, and the past\n   participle (usually) by the addition of -en (with or without a change\n   of the root vowel); as in the verbs strive, strove, striven; break,\n   broke, broken; drink, drank, drunk. Opposed to weak, or regular. See\n   Weak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong', 'superl.', 'Applied to forms in Anglo-Saxon, etc., which retain\n   the old declensional endings. In the Teutonic languages the vowel stems\n   have held the original endings most firmly, and are called strong; the\n   stems in -n are called weak other constant stems conform, or are\n   irregular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stronghand', 'n.', 'Violence; force; power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stronghold', 'n.', 'A fastness; a fort or fortress; fortfield place; a\n   place of security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strongish', 'a.', 'Somewhat strong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strongly', 'adv.', 'In a strong manner; so as to be strong in action or in\n   resistance; with strength; with great force; forcibly; powerfully;\n   firmly; vehemently; as, a town strongly fortified; he objected\n   strongly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong-minded', 'a.', 'Having a vigorous mind; esp., having or affecting\n   masculine qualities of mind; -- said of women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong-water', 'n.', 'An acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strong-water', 'n.', 'Distilled or ardent spirits; intoxicating liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strongylid', 'a. & n.', 'Strongyloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strongyloid', 'a.', 'Like, or pertaining to, Strongylus, a genus of\n   parasitic nematode worms of which many species infest domestic animals.\n   Some of the species, especially those living in the kidneys, lungs, and\n   bronchial tubes, are often very injurious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strongyloid', 'n.', 'A strongyloid worm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strontia', 'n.', 'An earth of a white color resembling lime in appearance,\n   and baryta in many of its properties. It is an oxide of the metal\n   strontium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strontian', 'n.', 'Strontia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strontianite', 'n.', 'Strontium carbonate, a mineral of a white, greenish,\n   or yellowish color, usually occurring in fibrous massive forms, but\n   sometimes in prismatic crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strontic', 'a.', 'Of or pertaining to strontium; containing, or designating\n   the compounds of, strontium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strontitic', 'a.', 'Strontic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strontium', 'n.', 'A metallic element of the calcium group, always\n   naturally occurring combined, as in the minerals strontianite,\n   celestite, etc. It is isolated as a yellowish metal, somewhat malleable\n   but harder than calcium. It is chiefly employed (as in the nitrate) to\n   color pyrotechnic flames red. Symbol Sr. Atomic weight 87.3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strontium', 'n.', 'A radioactive isotope of strontium produced by certain\n   nuclear reactions, and constituting one of the prominent harmful\n   components of radioactive fallout from nuclear explosions; also called\n   radiostrontium. It has a half-life of 28 years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strook', '', 'imp. of Strike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strook', 'n.', 'A stroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroot', 'v. i.', 'To swell out; to strut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strop', 'n.', 'A strap; specifically, same as Strap, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stropped', 'imp. & p. p.', 'of Strop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stropping', 'p. pr. & vb. n.', 'of Strop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strop', 'v. t.', 'To draw over, or rub upon, a strop with a view to\n   sharpen; as, to strop a razor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strop', 'n.', 'A piece of rope spliced into a circular wreath, and put\n   round a block for hanging it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strophanthus', 'n.', 'A genus of tropical apocynaceous shrubs having\n   singularly twisted flowers. One species (Strophanthus hispidus) is used\n   medicinally as a cardiac sedative and stimulant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strophes', 'pl. ', 'of Strophe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strophe', 'n.', 'In Greek choruses and dances, the movement of the chorus\n   while turning from the right to the left of the orchestra; hence, the\n   strain, or part of the choral ode, sung during this movement. Also\n   sometimes used of a stanza of modern verse. See the Note under\n   Antistrophe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strophic', 'a.', 'Pertaining to, containing, or consisting of, strophes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strophiolate', 'a.', 'Alt. of Strophiolated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strophiolated', 'a.', 'Furnished with a strophiole, or caruncle, or that\n   which resembles it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strophiole', 'n.', 'A crestlike excrescence about the hilum of certain\n   seeds; a caruncle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strophulus', 'n.', 'See Red-gum, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroud', 'n.', 'A kind of coarse blanket or garment used by the North\n   American Indians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strouding', 'n.', 'Material for strouds; a kind of coarse cloth used in\n   trade with the North American Indians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strout', 'v. i.', 'To swell; to puff out; to project.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strout', 'v. t.', 'To cause to project or swell out; to enlarge affectedly;\n   to strut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strove', '', 'imp. of Strive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strowed', 'imp.', 'of Strow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strown', 'p. p.', 'of Strow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strowed', '', 'of Strow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strow', 'v. t.', 'Same as Strew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strowl', 'v. i.', 'To stroll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strown', '', 'p. p. of Strow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stroy', 'v. i.', 'To destroy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struck', '', 'imp. & p. p. of Strike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strucken', '', 'p. p. of Strike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Structural', 'a.', 'Of or pertaining to structure; affecting structure; as,\n   a structural error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Structural', 'a.', 'Of or pertaining to organit structure; as, a structural\n   element or cell; the structural peculiarities of an animal or a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Structure', 'n.', 'The act of building; the practice of erecting buildings;\n   construction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Structure', 'n.', 'Manner of building; form; make; construction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Structure', 'n.', 'Arrangement of parts, of organs, or of constituent\n   particles, in a substance or body; as, the structure of a rock or a\n   mineral; the structure of a sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Structure', 'n.', 'Manner of organization; the arrangement of the different\n   tissues or parts of animal and vegetable organisms; as, organic\n   structure, or the structure of animals and plants; cellular structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Structure', 'n.', 'That which is built; a building; esp., a building of\n   some size or magnificence; an edifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Structured', 'a.', 'Having a definite organic structure; showing\n   differentiation of parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Structureless', 'a.', 'Without a definite structure, or arrangement of\n   parts; without organization; devoid of cells; homogeneous; as, a\n   structureless membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Structurist', 'n.', 'One who forms structures; a builder; a constructor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strude', 'n.', 'A stock of breeding mares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struggled', 'imp. & p. p.', 'of Struggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struggling', 'p. pr. & vb. n.', 'of Struggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struggle', 'v. i.', 'To strive, or to make efforts, with a twisting, or\n   with contortions of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struggle', 'v. i.', 'To use great efforts; to labor hard; to strive; to\n   contend forcibly; as, to struggle to save ones life; to struggle with\n   the waves; to struggle with adversity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struggle', 'v. i.', 'To labor in pain or anguish; to be in agony; to labor\n   in any kind of difficulty or distress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struggle', 'n.', 'A violent effort or efforts with contortions of the body;\n   agony; distress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struggle', 'n.', 'Great labor; forcible effort to obtain an object, or to\n   avert an evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struggle', 'n.', 'Contest; contention; strife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struggler', 'n.', 'One who struggles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strull', 'n.', 'A bar so placed as to resist weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strummed', 'imp. & p. p.', 'of Strum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strumming', 'p. pr. & vb. n.', 'of Strum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strum', 'v. t. & i.', 'To play on an instrument of music, or as on an\n   instrument, in an unskillful or noisy way; to thrum; as, to strum a\n   piano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struma', 'n.', 'Scrofula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struma', 'n.', 'A cushionlike swelling on any organ; especially, that at\n   the base of the capsule in many mosses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strumatic', 'a.', 'Scrofulous; strumous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strumose', 'a.', 'Strumous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strumose', 'a.', 'Having a struma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strumous', 'a.', 'Scrofulous; having struma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strumousness', 'n.', 'The state of being strumous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strumpet', 'n.', 'A prostitute; a harlot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strumpet', 'a.', 'Of or pertaining to a strumpet; characteristic of a\n   strumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strumpet', 'v. t.', 'To debauch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strumpet', 'v. t.', 'To dishonor with the reputation of being a strumpet;\n   hence, to belie; to slander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strumstrum', 'n.', 'A rude musical instrument somewhat like a cittern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strung', '', 'imp. & p. p. of String.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strunt', 'n.', 'Spirituous liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struntian', 'n.', 'A kind of worsted braid, about an inch broad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struse', 'n.', 'A Russian river craft used for transporting freight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strutted', 'imp. & p. p.', 'of Strut');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strutting', 'p. pr. & vb. n.', 'of Strut');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strut', 'v. t.', 'To swell; to bulge out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strut', 'v. t.', 'To walk with a lofty, proud gait, and erect head; to walk\n   with affected dignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strut', 'n.', 'The act of strutting; a pompous step or walk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strut', 'n.', 'In general, any piece of a frame which resists thrust or\n   pressure in the direction of its own length. See Brace, and Illust. of\n   Frame, and Roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strut', 'n.', 'Any part of a machine or structure, of which the principal\n   function is to hold things apart; a brace subjected to compressive\n   stress; -- the opposite of stay, and tie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strut', 'v. t.', 'To hold apart. Cf. Strut, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strut', 'a.', 'Protuberant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struthian', 'a.', 'Struthious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struthiones', 'pl. ', 'of Struthio');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struthio', 'n.', 'A genus of birds including the African ostriches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struthioidea', 'n. pl.', 'Same as Struthiones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struthiones', 'n. pl.', 'A division, or order, of birds, including only the\n   African ostriches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struthiones', 'n. pl.', 'In a wider sense, an extensive group of birds\n   including the ostriches, cassowaries, emus, moas, and allied birds\n   incapable of flight. In this sense it is equivalent to Ratitae, or\n   Dromaeognathae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struthionine', 'a.', 'Struthious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struthious', 'a.', 'Of or pertaining to the Struthiones, or Ostrich tribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strutter', 'n.', 'One who struts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strutting', '', 'a. & n. from Strut, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Struvite', 'n.', 'A crystalline mineral found in guano. It is a hydrous\n   phosphate of magnesia and ammonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strychnia', 'n.', 'Strychnine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strychnic', 'a.', 'Of or pertaining to strychnine; produced by strychnine;\n   as, strychnic compounds; strychnic poisoning');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strychnic', 'a.', 'used to designate an acid, called also igasuric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strychnine', 'n.', 'A very poisonous alkaloid resembling brucine, obtained\n   from various species of plants, especially from species of Loganiaceae,\n   as from the seeds of the St. Ignatius bean (Strychnos Ignatia) and from\n   nux vomica. It is obtained as a white crystalline substance, having a\n   very bitter acrid taste, and is employed in medicine (chiefly in the\n   form of the sulphate) as a powerful neurotic stimulant. Called also\n   strychnia, and formerly strychnina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Strychnos', 'n.', 'A genus of tropical trees and shrubs of the order\n   Loganiaceae. See Nux vomica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stryphnic', 'a.', 'Pertaining to, or designating, a complex nitrogenous\n   acid, obtained by the action of acetic acid and potassium nitrite on\n   uric acid, as a yellow crystalline substance, with a bitter, astringent\n   taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stub', 'n.', 'The stump of a tree; that part of a tree or plant which\n   remains fixed in the earth when the stem is cut down; -- applied\n   especially to the stump of a small tree, or shrub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stub', 'n.', 'A log; a block; a blockhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stub', 'n.', 'The short blunt part of anything after larger part has been\n   broken off or used up; hence, anything short and thick; as, the stub of\n   a pencil, candle, or cigar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stub', 'n.', 'A part of a leaf in a check book, after a check is torn out,\n   on which the number, amount, and destination of the check are usually\n   recorded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stub', 'n.', 'A pen with a short, blunt nib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stub', 'n.', 'A stub nail; an old horseshoe nail; also, stub iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stubbed', 'imp. & p. p.', 'of Stub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stubbing', 'p. pr. & vb. n.', 'of Stub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stub', 'v. t.', 'To grub up by the roots; to extirpate; as, to stub up\n   edible roots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stub', 'v. t.', 'To remove stubs from; as, to stub land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stub', 'v. t.', 'To strike as the toes, against a stub, stone, or other\n   fixed object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stubbed', 'a.', 'Reduced to a stub; short and thick, like something\n   truncated; blunt; obtuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stubbed', 'a.', 'Abounding in stubs; stubby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stubbed', 'a.', 'Not nice or delicate; hardy; rugged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stubbedness', 'n.', 'The quality or state of being stubbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stubbiness', 'n.', 'The state of being stubby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stubble', 'n.', 'The stumps of wheat, rye, barley, oats, or buckwheat, left\n   in the ground; the part of the stalk left by the scythe or sickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stubbled', 'a.', 'Covered with stubble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stubbled', 'a.', 'Stubbed; as, stubbled legs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stubbly', 'a.', 'Covered with stubble; stubbled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stubborn', 'a.', 'Firm as a stub or stump; stiff; unbending; unyielding;\n   persistent; hence, unreasonably obstinate in will or opinion; not\n   yielding to reason or persuasion; refractory; harsh; -- said of persons\n   and things; as, stubborn wills; stubborn ore; a stubborn oak; as\n   stubborn as a mule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stubby', 'a.', 'Abounding with stubs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stubby', 'a.', 'Short and thick; short and strong, as bristles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuccoes', 'pl. ', 'of Stucco');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuccos', 'pl. ', 'of Stucco');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stucco', 'n.', 'Plaster of any kind used as a coating for walls,\n   especially, a fine plaster, composed of lime or gypsum with sand and\n   pounded marble, used for internal decorations and fine work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stucco', 'n.', 'Work made of stucco; stuccowork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuccoed', 'imp. & p. p.', 'of Stucco');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuccoing', 'p. pr. & vb. n.', 'of Stucco');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stucco', 'v. t.', 'To overlay or decorate with stucco, or fine plaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuccoer', 'n.', 'One who stuccoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuccowork', 'n.', 'Work done in stucco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuck', '', 'imp. & p. p. of Stick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuck', 'n.', 'A thrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuckle', 'n.', 'A number of sheaves set together in the field; a stook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuck-up', 'a.', 'Self-important and supercilious, /onceited; vain;\n   arrogant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stud', 'n.', 'A collection of breeding horses and mares, or the place where\n   they are kept; also, a number of horses kept for a racing, riding, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stud', 'n.', 'A stem; a trunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stud', 'n.', 'An upright scanting, esp. one of the small uprights in the\n   framing for lath and plaster partitions, and furring, and upon which\n   the laths are nailed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stud', 'n.', 'A kind of nail with a large head, used chiefly for ornament;\n   an ornamental knob; a boss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stud', 'n.', 'An ornamental button of various forms, worn in a shirt front,\n   collar, wristband, or the like, not sewed in place, but inserted\n   through a buttonhole or eyelet, and transferable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stud', 'n.', 'A short rod or pin, fixed in and projecting from something,\n   and sometimes forming a journal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stud', 'n.', 'A stud bolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stud', 'n.', 'An iron brace across the shorter diameter of the link of a\n   chain cable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studded', 'imp. & p. p.', 'of Stud');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studding', 'p. pr. & vb. n.', 'of Stud');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stud', 'v. t.', 'To adorn with shining studs, or knobs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stud', 'v. t.', 'To set with detached ornaments or prominent objects; to\n   set thickly, as with studs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studbook', 'n.', 'A genealogical register of a particular breed or stud of\n   horses, esp. thoroughbreds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studdery', 'n.', 'A stud, or collection of breeding horses and mares; also,\n   a place for keeping a stud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studding', 'n.', 'Material for studs, or joists; studs, or joists,\n   collectively; studs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studding sail', '', 'A light sail set at the side of a principal or square\n   sail of a vessel in free winds, to increase her speed. Its head is bent\n   to a small spar which is called the studding-sail boom. See Illust. of\n   Sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Student', 'n.', 'A person engaged in study; one who is devoted to learning;\n   a learner; a pupil; a scholar; especially, one who attends a school, or\n   who seeks knowledge from professional teachers or from books; as, the\n   students of an academy, a college, or a university; a medical student;\n   a hard student.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Student', 'n.', 'One who studies or examines in any manner; an attentive\n   and systematic observer; as, a student of human nature, or of physical\n   nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studentry', 'n.', 'A body of students.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studentship', 'n.', 'The state of being a student.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studfish', 'n.', 'Any one of several species of small American minnows of\n   the genus Fundulus, as F. catenatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stud-horse', 'n.', 'A stallion, esp. one kept for breeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studied', 'a.', 'Closely examined; read with diligence and attention; made\n   the subject of study; well considered; as, a studied lesson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studied', 'a.', 'Well versed in any branch of learning; qualified by study;\n   learned; as, a man well studied in geometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studied', 'a.', 'Premeditated; planned; designed; as, a studied insult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studied', 'a.', 'Intent; inclined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studiedly', 'adv.', 'In a studied manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studier', 'n.', 'A student.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studios', 'pl. ', 'of Studio');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studio', 'n.', 'The working room of an artist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studious', 'a.', 'Given to study; devoted to the acquisition of knowledge\n   from books; as, a studious scholar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studious', 'a.', 'Given to thought, or to the examination of subjects by\n   contemplation; contemplative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studious', 'a.', 'Earnest in endeavors; aiming sedulously; attentive;\n   observant; diligent; -- usually followed by an infinitive or by of; as,\n   be studious to please; studious to find new friends and allies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studious', 'a.', 'Planned with study; deliberate; studied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studious', 'a.', 'Favorable to study; suitable for thought and\n   contemplation; as, the studious shade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studies', 'pl. ', 'of Study');
  INSERT INTO words (word, word_type, description) VALUES  (    'Study', 'v. i.', 'A setting of the mind or thoughts upon a subject; hence,\n   application of mind to books, arts, or science, or to any subject, for\n   the purpose of acquiring knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Study', 'v. i.', 'Mental occupation; absorbed or thoughtful attention;\n   meditation; contemplation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Study', 'v. i.', 'Any particular branch of learning that is studied; any\n   object of attentive consideration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Study', 'v. i.', 'A building or apartment devoted to study or to literary\n   work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Study', 'v. i.', 'A representation or rendering of any object or scene\n   intended, not for exhibition as an original work of art, but for the\n   information, instruction, or assistance of the maker; as, a study of\n   heads or of hands for a figure picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Study', 'v. i.', 'A piece for special practice. See Etude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studied', 'imp. & p. p.', 'of Study');
  INSERT INTO words (word, word_type, description) VALUES  (    'Studying', 'p. pr. & vb. n.', 'of Study');
  INSERT INTO words (word, word_type, description) VALUES  (    'Study', 'n.', 'To fix the mind closely upon a subject; to dwell upon\n   anything in thought; to muse; to ponder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Study', 'n.', 'To apply the mind to books or learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Study', 'n.', 'To endeavor diligently; to be zealous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Study', 'v. t.', 'To apply the mind to; to read and examine for the purpose\n   of learning and understanding; as, to study law or theology; to study\n   languages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Study', 'v. t.', 'To consider attentively; to examine closely; as, to study\n   the work of nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Study', 'v. t.', 'To form or arrange by previous thought; to con over, as\n   in committing to memory; as, to study a speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Study', 'v. t.', 'To make an object of study; to aim at sedulously; to\n   devote ones thoughts to; as, to study the welfare of others; to study\n   variety in composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stufa', 'n.', 'A jet of steam issuing from a fissure in the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuff', 'v. t.', 'Material which is to be worked up in any process of\n   manufacture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuff', 'v. t.', 'The fundamental material of which anything is made up;\n   elemental part; essence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuff', 'v. t.', 'Woven material not made into garments; fabric of any\n   kind; specifically, any one of various fabrics of wool or worsted;\n   sometimes, worsted fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuff', 'v. t.', 'Furniture; goods; domestic vessels or utensils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuff', 'v. t.', 'A medicine or mixture; a potion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuff', 'v. t.', 'Refuse or worthless matter; hence, also, foolish or\n   irrational language; nonsense; trash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuff', 'v. t.', 'A melted mass of turpentine, tallow, etc., with which the\n   masts, sides, and bottom of a ship are smeared for lubrication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuff', 'v. t.', 'Paper stock ground ready for use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuffed', 'imp. & p. p.', 'of Stuff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuffing', 'p. pr. & vb. n.', 'of Stuff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuff', 'n.', 'To fill by crowding something into; to cram with something;\n   to load to excess; as, to stuff a bedtick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuff', 'n.', 'To thrust or crowd; to press; to pack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuff', 'n.', 'To fill by being pressed or packed into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuff', 'n.', 'To fill with a seasoning composition of bread, meat,\n   condiments, etc.; as, to stuff a turkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuff', 'n.', 'To obstruct, as any of the organs; to affect with some\n   obstruction in the organs of sense or respiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuff', 'n.', 'To fill the skin of, for the purpose of preserving as a\n   specimen; -- said of birds or other animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuff', 'n.', 'To form or fashion by packing with the necessary material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuff', 'n.', 'To crowd with facts; to cram the mind of; sometimes, to\n   crowd or fill with false or idle tales or fancies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuff', 'n.', 'To put fraudulent votes into (a ballot box).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuff', 'v. i.', 'To feed gluttonously; to cram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuffer', 'n.', 'One who, or that which, stuffs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuffiness', 'n.', 'The quality of being stuffy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuffing', 'n.', 'That which is used for filling anything; as, the stuffing\n   of a saddle or cushion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuffing', 'n.', 'Any seasoning preparation used to stuff meat; especially,\n   a composition of bread, condiments, spices, etc.; forcemeat; dressing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuffing', 'n.', 'A mixture of oil and tallow used in softening and\n   dressing leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuffy', 'a.', 'Stout; mettlesome; resolute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuffy', 'a.', 'Angry and obstinate; sulky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuffy', 'a.', 'Ill-ventilated; close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuke', 'n.', 'Stucco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stull', 'n.', 'A framework of timber covered with boards to support\n   rubbish; also, a framework of boards to protect miners from falling\n   stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stulm', 'n.', 'A shaft or gallery to drain a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stulp', 'n.', 'A short, stout post used for any purpose, a to mark a\n   boundary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stultification', 'n.', 'The act of stultifying, or the state of being\n   stultified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stultifier', 'n.', 'One who stultifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stultified', 'imp. & p. p.', 'of Stultify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stultifying', 'p. pr. & vb. n.', 'of Stultify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stultify', 'v. t.', 'To make foolish; to make a fool of; as, to stultify\n   one by imposition; to stultify ones self by silly reasoning or\n   conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stultify', 'v. t.', 'To regard as a fool, or as foolish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stultify', 'v. t.', 'To allege or prove to be of unsound mind, so that the\n   performance of some act may be avoided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stultiloquence', 'n.', 'Silly talk; babbling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stultiloquent', 'a.', 'Given to, or characterized by, silly talk; babbling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stultiloquy', 'n.', 'Foolish talk; silly discource; babbling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stulty', 'a.', 'Foolish; silly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stum', 'n.', 'Unfermented grape juice or wine, often used to raise\n   fermentation in dead or vapid wines; must.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stum', 'n.', 'Wine revived by new fermentation, reulting from the admixture\n   of must.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stummed', 'imp. & p. p.', 'of Stum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumming', 'p. pr. & vb. n.', 'of Stum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stum', 'v. t.', 'To renew, as wine, by mixing must with it and raising a\n   new fermentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumbled', 'imp. & p. p.', 'of Stumble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumbling', 'p. pr. & vb. n.', 'of Stumble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumble', 'v. i.', 'To trip in walking or in moving in any way with the\n   legs; to strike the foot so as to fall, or to endanger a fall; to\n   stagger because of a false step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumble', 'v. i.', 'To walk in an unsteady or clumsy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumble', 'v. i.', 'To fall into a crime or an error; to err.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumble', 'v. i.', 'To strike or happen (upon a person or thing) without\n   design; to fall or light by chance; -- with on, upon, or against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumble', 'v. t.', 'To cause to stumble or trip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumble', 'v. t.', 'Fig.: To mislead; to confound; to perplex; to cause to\n   err or to fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumble', 'n.', 'A trip in walking or running.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumble', 'n.', 'A blunder; a failure; a fall from rectitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumbler', 'n.', 'One who stumbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumbling-block', 'n.', 'Any cause of stumbling, perplexity, or error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumblingly', 'adv.', 'In a stumbling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumbling-stone', 'n.', 'A stumbling-block.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stump', 'n.', 'The part of a tree or plant remaining in the earth after the\n   stem or trunk is cut off; the stub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stump', 'n.', 'The part of a limb or other body remaining after a part is\n   amputated or destroyed; a fixed or rooted remnant; a stub; as, the\n   stump of a leg, a finger, a tooth, or a broom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stump', 'n.', 'The legs; as, to stir ones stumps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stump', 'n.', 'One of the three pointed rods stuck in the ground to form a\n   wicket and support the bails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stump', 'n.', 'A short, thick roll of leather or paper, cut to a point, or\n   any similar implement, used to rub down the lines of a crayon or pencil\n   drawing, in shading it, or for shading drawings by producing tints and\n   gradations from crayon, etc., in powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stump', 'n.', 'A pin in a tumbler lock which forms an obstruction to\n   throwing the bolt, except when the gates of the tumblers are properly\n   arranged, as by the key; a fence; also, a pin or projection in a lock\n   to form a guide for a movable piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumped', 'imp. & p. p.', 'of Stump');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumping', 'p. pr. & vb. n.', 'of Stump');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stump', 'v. t.', 'To cut off a part of; to reduce to a stump; to lop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stump', 'v. t.', 'To strike, as the toes, against a stone or something\n   fixed; to stub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stump', 'v. t.', 'To challenge; also, to nonplus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stump', 'v. t.', 'To travel over, delivering speeches for electioneering\n   purposes; as, to stump a State, or a district. See To go on the stump,\n   under Stump, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stump', 'n.', 'To put (a batsman) out of play by knocking off the bail, or\n   knocking down the stumps of the wicket he is defending while he is off\n   his allotted ground; -- sometimes with out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stump', 'n.', 'To bowl down the stumps of, as, of a wicket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stump', 'v. i.', 'To walk clumsily, as if on stumps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumpage', 'n.', 'Timber in standing trees, -- often sold without the land\n   at a fixed price per tree or per stump, the stumps being counted when\n   the land is cleared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumpage', 'n.', 'A tax on the amount of timber cut, regulated by the price\n   of lumber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumper', 'n.', 'One who stumps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumper', 'n.', 'A boastful person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumper', 'n.', 'A puzzling or incredible story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumpiness', 'n.', 'The state of being stumpy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stump-tailed', 'a.', 'Having a short, thick tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumpy', 'a.', 'Full of stumps; hard; strong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stumpy', 'a.', 'Short and thick; stubby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stunned', 'imp. & p. p.', 'of Stun');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stunning', 'p. pr. & vb. n.', 'of Stun');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stun', 'v. t.', 'To make senseless or dizzy by violence; to render\n   senseless by a blow, as on the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stun', 'v. t.', 'To dull or deaden the sensibility of; to overcome;\n   especially, to overpower ones sense of hearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stun', 'v. t.', 'To astonish; to overpower; to bewilder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stun', 'n.', 'The condition of being stunned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stung', '', 'imp. & p. p. of Sting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stunk', '', 'imp. & p. p. of Stink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stunner', 'n.', 'One who, or that which, stuns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stunner', 'n.', 'Something striking or amazing in quality; something of\n   extraordinary excellence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stunning', 'a.', 'Overpowering consciousness; overpowering the senses;\n   especially, overpowering the sense of hearing; confounding with noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stunning', 'a.', 'Striking or overpowering with astonishment, especially on\n   account of excellence; as, stunning poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stunsail', 'n.', 'A contraction of Studding sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stunted', 'imp. & p. p.', 'of Stunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stunting', 'p. pr. & vb. n.', 'of Stunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stunt', 'v. t.', 'To hinder from growing to the natural size; to prevent\n   the growth of; to stint, to dwarf; as, to stunt a child; to stunt a\n   plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stunt', 'n.', 'A check in growth; also, that which has been checked in\n   growth; a stunted animal or thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stunt', 'n.', 'Specifically: A whale two years old, which, having been\n   weaned, is lean, and yields but little blubber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stunted', 'a.', 'Dwarfed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuntness', 'n.', 'Stuntedness; brevity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupa', 'n.', 'A mound or monument commemorative of Buddha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupa', 'n.', 'See 1st Stupe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupe', 'v. t.', 'Cloth or flax dipped in warm water or medicaments and\n   applied to a hurt or sore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuped', 'imp. & p. p.', 'of Stupe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuping', 'p. pr. & vb. n.', 'of Stupe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupe', 'v. t.', 'To foment with a stupe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupe', 'n.', 'A stupid person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupefacient', 'a.', 'Producing stupefaction; stupefactive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupefacient', 'n.', 'Anything promoting stupefaction; a narcotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupefaction', 'n.', 'The act of stupefying, or the state of being\n   stupefied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupefactive', 'a. & n.', 'Same as Stupefacient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupefied', 'a.', 'Having been made stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupefiedness', 'n.', 'Quality of being stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupefier', 'n.', 'One who, or that which, stupefies; a stupefying agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupefied', 'imp. & p. p.', 'of Stupefy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupefying', 'p. pr. & vb. n.', 'of Stupefy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupefy', 'v. t.', 'To make stupid; to make dull; to blunt the faculty of\n   perception or understanding in; to deprive of sensibility; to make\n   torpid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupefy', 'v. t.', 'To deprive of material mobility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupendous', 'a.', 'Astonishing; wonderful; amazing; especially,\n   astonishing in magnitude or elevation; as, a stupendous pile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupeous', 'a.', 'Resembling tow; having long, loose scales, or matted\n   filaments, like tow; stupose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupid', 'a.', 'Very dull; insensible; senseless; wanting in understanding;\n   heavy; sluggish; in a state of stupor; -- said of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupid', 'a.', 'Resulting from, or evincing, stupidity; formed without\n   skill or genius; dull; heavy; -- said of things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupidity', 'n.', 'The quality or state of being stupid; extreme dullness\n   of perception or understanding; insensibility; sluggishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupidity', 'n.', 'Stupor; astonishment; stupefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupify', 'v. t.', 'See Stupefy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupor', 'n.', 'Great diminution or suspension of sensibility; suppression\n   of sense or feeling; lethargy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupor', 'n.', 'Intellectual insensibility; moral stupidity; heedlessness\n   or inattention to ones interests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupose', 'a.', 'Composed of, or having, tufted or matted filaments like\n   tow; stupeous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuprate', 'v. t.', 'To ravish; to debauch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stupration', 'n.', 'Violation of chastity by force; rape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuprum', 'n.', 'Stupration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sturb', 'v. t.', 'To disturb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sturdily', 'adv.', 'In a sturdy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sturdiness', 'n.', 'Quality of being sturdy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sturdy', 'superl.', 'Foolishly obstinate or resolute; stubborn;\n   unrelenting; unfeeling; stern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sturdy', 'superl.', 'Resolute, in a good sense; or firm, unyielding\n   quality; as, a man of sturdy piety or patriotism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sturdy', 'superl.', 'Characterized by physical strength or force; strong;\n   lusty; violent; as, a sturdy lout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sturdy', 'superl.', 'Stiff; stout; strong; as, a sturdy oak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sturdy', 'n.', 'A disease in sheep and cattle, marked by great nervousness,\n   or by dullness and stupor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sturgeon', 'n.', 'Any one of numerous species of large cartilaginous ganoid\n   fishes belonging to Acipenser and allied genera of the family\n   Acipenseridae. They run up rivers to spawn, and are common on the\n   coasts and in the large rivers and lakes of North America, Europe, and\n   Asia. Caviare is prepared from the roe, and isinglass from the air\n   bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sturiones', 'n. pl.', 'An order of fishes including the sturgeons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sturionian', 'n.', 'One of the family of fishes of which the sturgeon is\n   the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sturk', 'n.', 'See Stirk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sturnoid', 'a.', 'Like or pertaining to the starlings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sturt', 'v. i.', 'To vex; to annoy; to startle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sturt', 'n.', 'Disturbance; annoyance; care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sturt', 'n.', 'A bargain in tribute mining by which the tributor profits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sturtion', 'n.', 'A corruption of Nasturtion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stut', 'v. i.', 'To stutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuttered', 'imp. & p. p.', 'of Stutter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuttering', 'p. pr. & vb. n.', 'of Stutter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stutter', 'v. t. & i.', 'To hesitate or stumble in uttering words; to speak\n   with spasmodic repetition or pauses; to stammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stutter', 'n.', 'The act of stuttering; a stammer. See Stammer, and\n   Stuttering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stutter', 'n.', 'One who stutters; a stammerer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stutterer', 'n.', 'One who stutters; a stammerer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuttering', 'n.', 'The act of one who stutters; -- restricted by some\n   physiologists to defective speech due to inability to form the proper\n   sounds, the breathing being normal, as distinguished from stammering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stuttering', 'a.', 'Apt to stutter; hesitating; stammering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sties', 'pl. ', 'of Sty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sty', 'v. i.', 'A pen or inclosure for swine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sty', 'v. i.', 'A place of bestial debauchery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stied', 'imp. & p. p.', 'of Sty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stying', 'p. pr. & vb. n.', 'of Sty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sty', 'v. t.', 'To shut up in, or as in, a sty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sty', 'v. i.', 'To soar; to ascend; to mount. See Stirrup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sty', 'v. i.', 'An inflamed swelling or boil on the edge of the eyelid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styan', 'n.', 'See Sty, a boil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styca', 'n.', 'An anglo-Saxon copper coin of the lowest value, being worth\n   half a farthing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stycerin', 'n.', 'A triacid alcohol, related to glycerin, and obtained from\n   certain styryl derivatives as a yellow, gummy, amorphous substance; --\n   called also phenyl glycerin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stye', 'n.', 'See Sty, a boil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stygial', 'a.', 'Stygian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stygian', 'a.', 'Of or pertaining to the river Styx; hence, hellish;\n   infernal. See Styx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylagalmaic', 'a.', 'Performing the office of columns; as, Atlantes and\n   Caryatides are stylagalmaic figures or images.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylar', 'a.', 'See Stilar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylaster', 'n.', 'Any one of numerous species of delicate, usually pink,\n   calcareous hydroid corals of the genus Stylaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Style', 'v. t.', 'An instrument used by the ancients in writing on tablets\n   covered with wax, having one of its ends sharp, and the other blunt,\n   and somewhat expanded, for the purpose of making erasures by smoothing\n   the wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Style', 'v. t.', 'Hence, anything resembling the ancient style in shape or\n   use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Style', 'v. t.', 'A pen; an authors pen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Style', 'v. t.', 'A sharp-pointed tool used in engraving; a graver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Style', 'v. t.', 'A kind of blunt-pointed surgical instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Style', 'v. t.', 'A long, slender, bristlelike process, as the anal styles\n   of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Style', 'v. t.', 'The pin, or gnomon, of a dial, the shadow of which\n   indicates the hour. See Gnomon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Style', 'v. t.', 'The elongated part of a pistil between the ovary and the\n   stigma. See Illust. of Stamen, and of Pistil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Style', 'v. t.', 'Mode of expressing thought in language, whether oral or\n   written; especially, such use of language in the expression of thought\n   as exhibits the spirit and faculty of an artist; choice or arrangement\n   of words in discourse; rhetorical expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Style', 'v. t.', 'Mode of presentation, especially in music or any of the\n   fine arts; a characteristic of peculiar mode of developing in idea or\n   accomplishing a result.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Style', 'v. t.', 'Conformity to a recognized standard; manner which is\n   deemed elegant and appropriate, especially in social demeanor; fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Style', 'v. t.', 'Mode or phrase by which anything is formally designated;\n   the title; the official designation of any important body; mode of\n   address; as, the style of Majesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Style', 'v. t.', 'A mode of reckoning time, with regard to the Julian and\n   Gregorian calendars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styled', 'imp. & p. p.', 'of Style');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styling', 'p. pr. & vb. n.', 'of Style');
  INSERT INTO words (word, word_type, description) VALUES  (    'Style', 'v. t.', 'To entitle; to term, name, or call; to denominate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylet', 'n.', 'A small poniard; a stiletto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylet', 'n.', 'An instrument for examining wounds and fistulas, and for\n   passing setons, and the like; a probe, -- called also specillum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylet', 'n.', 'A stiff wire, inserted in catheters or other tubular\n   instruments to maintain their shape and prevent clogging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylet', 'n.', 'Any small, more or less rigid, bristlelike organ; as, the\n   caudal stylets of certain insects; the ventral stylets of certain\n   Infusoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styliferous', 'a.', 'Bearing one or more styles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styliform', 'a.', 'Having the form of, or resembling, a style, pin, or pen;\n   styloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylish', 'a.', 'Having style or artistic quality; given to, or fond of,\n   the display of style; highly fashionable; modish; as, a stylish dress,\n   house, manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylist', 'n.', 'One who is a master or a model of style, especially in\n   writing or speaking; a critic of style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylistic', 'a.', 'Of or pertaining to style in language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylite', 'n.', 'One of a sect of anchorites in the early church, who lived\n   on the tops of pillars for the exercise of their patience; -- called\n   also pillarist and pillar saint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylo-', '', 'A combining form used in anatomy to indicate connection with,\n   or relation to, the styloid process of the temporal bone; as,\n   stylohyal, stylomastoid, stylomaxillary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylobate', 'n.', 'The uninterrupted and continuous flat band, coping, or\n   pavement upon which the bases of a row of columns are supported. See\n   Sub-base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styloglossal', 'a.', 'Of or pertaining to styloid process and the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylograph', 'n.', 'A stylographic pen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylographic', 'a.', 'Of or pertaining to stylography; used in stylography;\n   as, stylographic tablets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylographic', 'a.', 'Pertaining to, or used in, stylographic pen; as,\n   stylographic ink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylographical', 'a.', 'Same as Stylographic, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylography', 'n.', 'A mode of writing or tracing lines by means of a style\n   on cards or tablets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylohyal', 'n.', 'A segment in the hyoidean arch between the epihyal and\n   tympanohyal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylohyoid', 'a.', 'Of or pertaining to the styloid process and the hyoid\n   bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styloid', 'a.', 'Styliform; as, the styloid process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styloid', 'a.', 'Of or pertaining to the styloid process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylomastoid', 'a.', 'Of or pertaining to the styloid and mastoid processes\n   of the temporal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylomaxillary', 'a.', 'Of or pertaining to the styloid process and the\n   maxilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylometer', 'n.', 'An instrument for measuring columns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylommata', 'n. pl.', 'Same as Stylommatophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylommatophora', 'n. pl.', 'A division of Pulmonata in which the eyes are\n   situated at the tips of the tentacles. It includes the common land\n   snails and slugs. See Illust. under Snail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylommatophorous', 'a.', 'Of or pertaining to Stylommatophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylopodia', 'pl. ', 'of Stylopodium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylopodium', 'n.', 'An expansion at the base of the style, as in\n   umbelliferous plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylops', 'n.', 'A genus of minute insects parasitic, in their larval\n   state, on bees and wasps. It is the typical genus of the group\n   Strepsiptera, formerly considered a distinct order, but now generally\n   referred to the Coleoptera. See Strepsiptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylus', 'n.', 'An instrument for writing. See Style, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylus', 'n.', 'That needle-shaped part at the tip of the playing arm of\n   phonograph which sits in the groove of a phonograph record while it is\n   turning, to detect the undulations in the phonograph groove and convert\n   them into vibrations which are transmitted to a system (since 1920\n   electronic) which converts the signal into sound; also called needle.\n   The stylus is frequently composed of metal or diamond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylus', 'n.', 'The needle-like device used to cut the grooves which record\n   the sound on the original disc during recording of a phonograph record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stylus', 'n.', 'A pen-shaped pointing device used to specify the cursor\n   position on a graphics tablet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styphnate', 'n.', 'A salt of styphnic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styphnic', 'a.', 'Pertaining to, or designating, a yellow crystalline\n   astringent acid, (NO2)3.C6H.(OH)2, obtained by the action of nitric\n   acid on resorcin. Styphnic acid resembles picric acid, but is not\n   bitter. It acts like a strong dibasic acid, having a series of well\n   defined salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styptic', 'a.', 'Producing contraction; stopping bleeding; having the\n   quality of restraining hemorrhage when applied to the bleeding part;\n   astringent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styptic', 'n.', 'A styptic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styptical', 'a.', 'Styptic; astringent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stypticity', 'n.', 'The quality or state of being styptic; astringency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styracin', 'n.', 'A white crystalline tasteless substance extracted from\n   gum storax, and consisting of a salt of cinnamic acid with cinnamic\n   alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styrax', 'n.', 'A genus of shrubs and trees, mostly American or Asiatic,\n   abounding in resinous and aromatic substances. Styrax officinalis\n   yields storax, and S. Benzoin yields benzoin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styrax', 'n.', 'Same as Storax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styrol', 'n.', 'See Styrolene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styrolene', 'n.', 'An unsaturated hydrocarbon, C8H8, obtained by the\n   distillation of storax, by the decomposition of cinnamic acid, and by\n   the condensation of acetylene, as a fragrant, aromatic, mobile liquid;\n   -- called also phenyl ethylene, vinyl benzene, styrol, styrene, and\n   cinnamene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styrone', 'n.', 'A white crystalline substance having a sweet taste and a\n   hyacinthlike odor, obtained by the decomposition of styracin; --\n   properly called cinnamic, / styryl, alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styryl', 'n.', 'A hypothetical radical found in certain derivatives of\n   styrolene and cinnamic acid; -- called also cinnyl, or cinnamyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stythe', 'n.', 'Choke damp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Stythy', 'n. & v.', 'See Stithy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Styx', 'n.', 'The principal river of the lower world, which had to be\n   crossed in passing to the regions of the dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suability', 'n.', 'Liability to be sued; the state of being subjected by\n   law to civil process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suable', 'a.', 'Capable of being sued; subject by law to be called to\n   answer in court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suade', 'v. t.', 'To persuade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suadible', 'a.', 'Suasible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suage', 'v. t.', 'To assuage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suant', 'a.', 'Spread equally over the surface; uniform; even.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suasible', 'a.', 'Capable of being persuaded; easily persuaded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suasion', 'n.', 'The act of persuading; persuasion; as, moral suasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suasive', 'a.', 'Having power to persuade; persuasive; suasory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suasory', 'a.', 'Tending to persuade; suasive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suave', 'a.', 'Sweet; pleasant; delightful; gracious or agreeable in\n   manner; bland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suavified', 'imp. & p. p.', 'of Suavify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suavifying', 'p. pr. & vb. n.', 'of Suavify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suavify', 'v. t.', 'To make affable or suave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suaviloquent', 'a.', 'Sweetly speaking; using agreeable speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suaviloquy', 'n.', 'Sweetness of speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suavity', 'n.', 'Sweetness to the taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suavity', 'n.', 'The quality of being sweet or pleasing to the mind;\n   agreeableness; softness; pleasantness; gentleness; urbanity; as,\n   suavity of manners; suavity of language, conversation, or address.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sub-', '', 'A prefix signifying under, below, beneath, and hence often, in\n   an inferior position or degree, in an imperfect or partial state, as in\n   subscribe, substruct, subserve, subject, subordinate, subacid,\n   subastringent, subgranular, suborn. Sub- in Latin compounds often\n   becomes sum- before m, sur before r, and regularly becomes suc-, suf-,\n   sug-, and sup- before c, f, g, and p respectively. Before c, p, and t\n   it sometimes takes form sus- (by the dropping of b from a collateral\n   form, subs-).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sub-', '', 'A prefix denoting that the ingredient (of a compound) signified\n   by the term to which it is prefixed,is present in only a small\n   proportion, or less than the normal amount; as, subsulphide, suboxide,\n   etc. Prefixed to the name of a salt it is equivalent to basic; as,\n   subacetate or basic acetate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sub', 'n.', 'A subordinate; a subaltern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subacid', 'a.', 'Moderately acid or sour; as, some plants have subacid\n   juices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subacid', 'n.', 'A substance moderately acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subacrid', 'a.', 'Moderalely acrid or harsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subacromial', 'a.', 'Situated beneath the acromial process of the scapula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subact', 'v. t.', 'To reduce; to subdue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subaction', 'n.', 'The act of reducing to any state, as of mixing two\n   bodies combletely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subacute', 'a.', 'Moderalely acute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subaduncate', 'a.', 'Somewhat hooked or curved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subadvocate', 'n.', 'An under or subordinate advocate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subaerial', 'a.', 'Beneath the sky; in the open air; specifically (Geol.),\n   taking place on the earths surface, as opposed to subaqueous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subagency', 'n.', 'A subordinate agency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subagent', 'n.', 'A person employed by an agent to transact the whole, or a\n   part, of the business intrusted to the latter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subagitation', 'n.', 'Unlawful sexual intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subash', 'n.', 'A province; a government, as of a viceroy; also, a\n   subahdar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subashdar', 'n.', 'A viceroy; a governor of a subah; also, a native captain\n   in the British native army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subashdary', 'n.', 'Alt. of Subashship');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subashship', 'n.', 'The office or jurisdiction of a subahdar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subaid', 'v. t.', 'To aid secretly; to assist in a private manner, or\n   indirectly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subalmoner', 'n.', 'An under almoner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subalpine', 'a.', 'Inhabiting the somewhat high slopes and summits of\n   mountains, but considerably below the snow line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subaltern', 'a.', 'Ranked or ranged below; subordinate; inferior;\n   specifically (Mil.), ranking as a junior officer; being below the rank\n   of captain; as, a subaltern officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subaltern', 'a.', 'Asserting only a part of what is asserted in a related\n   proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subaltern', 'n.', 'A person holding a subordinate position; specifically, a\n   commissioned military officer below the rank of captain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subaltern', 'n.', 'A subaltern proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subalternant', 'n.', 'A universal proposition. See Subaltern, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subalternate', 'a.', 'Succeeding by turns; successive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subalternate', 'a.', 'Subordinate; subaltern; inferior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subalternate', 'n.', 'A particular proposition, as opposed to a universal\n   one. See Subaltern, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subalternating', 'a.', 'Subalternate; successive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subalternation', 'n.', 'The state of being subalternate; succession of\n   turns; subordination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subangular', 'a.', 'Slightly angular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subapennine', 'a.', 'Under, or at the foot of, the Apennine mountains; --\n   applied, in geology, to a series of Tertiary strata of the older\n   Pliocene period.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subapical', 'a.', 'Being under the apex; of or pertaining to the part just\n   below the apex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subaquaneous', 'a.', 'Subaqueous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subaquatic', 'a.', 'Alt. of Subaqueous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subaqueous', 'a.', 'Being under water, or beneath the surface of water;\n   adapted for use under water; submarine; as, a subaqueous helmet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subaqueous', 'a.', 'Formed in or under water; as, subaqueous deposits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subarachnoid', 'a.', 'Alt. of Subarachnoidal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subarachnoidal', 'a.', 'Situated under the arachnoid membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subarctic', 'a.', 'Approximately arctic; belonging to a region just without\n   the arctic circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subarcuate', 'a.', 'Alt. of Subarcuated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subarcuated', 'a.', 'Having a figure resembling that of a bow; somewhat\n   curved or arched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subarration', 'n.', 'The ancient custom of betrothing by the bestowal, on\n   the part of the man, of marriage gifts or tokens, as money, rings, or\n   other presents, upon the woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subarytenoid', 'a.', 'Situated under the arytenoid cartilage of the larynx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subastral', 'a.', 'Beneath the stars or heavens; terrestrial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subastringent', 'a.', 'Somewhat astringent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subatom', 'n.', 'A hypothetical component of a chemical atom, on the theory\n   that the elements themselves are complex substances; -- called also\n   atomicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subaud', 'v. t.', 'To understand or supply in an ellipsis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subaudition', 'n.', 'The act of understanding, or supplying, something not\n   expressed; also, that which is so understood or supplied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subaxillary', 'a.', 'Situated under the axilla, or armpit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subaxillary', 'a.', 'Placed under the axil, or angle formed by the branch\n   of a plant with the stem, or a leaf with the branch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subbasal', 'a.', 'Near the base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sub-base', 'n.', 'The lowest member of a base when divided horizontally, or\n   of a baseboard, pedestal, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sub-bass', 'n.', 'The deepest pedal stop, or the lowest tones of an organ;\n   the fundamental or ground bass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subbeadle', 'n.', 'An under beadle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subbrachial', 'a.', 'Of or pertaining to the subbrachians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subbrachiales', 'n. pl.', 'A division of soft-finned fishes in which the\n   ventral fins are situated beneath the pectorial fins, or nearly so.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subbrachian', 'n.', 'One of the Subbrachiales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subbreed', 'n.', 'A race or strain differing in certain characters from the\n   parent breed; an incipient breed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subbronchial', 'a.', 'Situated under, or on the ventral side of, the\n   bronchi; as, the subbronchial air sacs of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcaliber', 'a.', 'Smaller than the caliber of a firearm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcarboniferous', 'a.', 'Of or pertaining to the lowest division of the\n   Carboniferous formations underlying the proper coal measures. It was a\n   marine formation characterized in general by beds of limestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcarboniferous', 'n.', 'The Subcarboniferous period or formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcarbureted', 'a.', 'United with, or containing, carbon in less than the\n   normal proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcartilaginous', 'a.', 'Situated under or beneath a cartilage or\n   cartilages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcartilaginous', 'a.', 'Partially cartilaginous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcaudal', 'a.', 'Situated under, or on the ventral side of, the tail; as,\n   the subcaudal, or chevron, bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcelestial', 'a.', 'Being beneath the heavens; as, subcelestial glories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcentral', 'a.', 'Under the center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcentral', 'a.', 'Nearly central; not quite central.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subchanter', 'n.', 'An underchanter; a precentors deputy in a cathedral; a\n   succentor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcircular', 'a.', 'Nearly circular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subclass', 'n.', 'One of the natural groups, more important than an order,\n   into which some classes are divided; as, the angiospermous subclass of\n   exogens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subclavian', 'a.', 'Situated under the clavicle, or collar bone; as, the\n   subclavian arteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcolumnar', 'a.', 'Having an imperfect or interrupted columnar structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcommittee', 'n.', 'An under committee; a part or division of a\n   committee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcompressed', 'a.', 'Not fully compressed; partially or somewhat\n   compressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subconcave', 'a.', 'Slightly concave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subconformable', 'a.', 'Partially conformable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subconical', 'a.', 'Slightly conical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subconjunctival', 'a.', 'Situated under the conjunctiva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subconscious', 'a.', 'Occurring without the possibility or the fact of an\n   attendant consciousness; -- said of states of the soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subconscious', 'a.', 'Partially conscious; feebly conscious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subconstellation', 'n.', 'A subordinate constellation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcontract', 'n.', 'A contract under, or subordinate to, a previous\n   contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcontracted', 'a.', 'Contracted after a former contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcontracted', 'a.', 'Betrothed for the second time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcontractor', 'n.', 'One who takes a portion of a contract, as for work,\n   from the principal contractor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcontrary', 'a.', 'Contrary in an inferior degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcontrary', 'a.', 'Having, or being in, a contrary order; -- said of a\n   section of an oblique cone having a circular base made by a plane not\n   parallel to the base, but so inclined to the axis that the section is a\n   circle; applied also to two similar triangles when so placed as to have\n   a common angle at the vertex, the opposite sides not being parallel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcontrary', 'a.', 'Denoting the relation of opposition between the\n   particular affirmative and particular negative. Of these both may be\n   true and only one can be false.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcontraries', 'pl. ', 'of Subcontrary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcontrary', 'n.', 'A subcontrary proposition; a proposition inferior or\n   contrary in a lower degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcoracoid', 'a.', 'Situated under the coracoid process of the scapula;\n   as, the subcoracoid dislocation of the humerus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcordate', 'a.', 'Somewhat cordate; somewhat like a heart in shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcorneous', 'a.', 'Situated under a horny part or layer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcorneous', 'a.', 'Partially horny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcostal', 'a.', 'Situated below the costas, or ribs; as, the subcostal\n   muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcostal', 'n.', 'A subcostal muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcostal', 'n.', 'One of the principal nervures of the wings of an insect.\n   It is situated next beneath or behind the costal. See Nervure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcranial', 'a.', 'Situated under, or on the ventral side of, the cranium;\n   facial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcrustaceous', 'a.', 'Occurring beneath a crust or scab; as, a\n   subcrustaceous cicatrization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcrystalline', 'a.', 'Imperfectly crystallized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcultrate', 'a.', 'Alt. of Subcultrated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcultrated', 'a.', 'Having a form resembling that of a colter, or\n   straight on one side and curved on the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcutaneous', 'a.', 'Situated under the skin; hypodermic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcuticular', 'a.', 'Situated under the cuticle, or scarfskin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcylindrical', 'a.', 'Alt. of Subcylindric');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subcylindric', 'a.', 'Imperfectly cylindrical; approximately cylindrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdeacon', 'n.', 'One belonging to an order in the Roman Catholic Church,\n   next interior to the order of deacons; also, a member of a minor order\n   in the Greek Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdeaconry', 'n.', 'Alt. of Subdeaconship');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdeaconship', 'n.', 'The order or office of subdeacon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdean', 'n.', 'An under dean; the deputy or substitute of a dean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdeanery', 'n.', 'Office or rank of subdean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdecanal', 'a.', 'Of or pertaining to a subdean or subdeanery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdecuple', 'a.', 'Containing one part of ten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdelegate', 'n.', 'A subordinate delegate, or one with inferior powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdelegate', 'v. t.', 'To appoint to act as subdelegate, or as a\n   subordinate; to depete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdented', 'a.', 'Indented beneath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdepartment', 'n.', 'A subordinate department; a bureau. See the Note\n   under Bureau.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdeposit', 'n.', 'That which is deposited beneath something else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subderisorious', 'a.', 'Ridiculing with moderation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subderivative', 'n.', 'A word derived from a derivative, and not directly\n   from the root; as, \"friendliness\" is a subderivative, being derived\n   from \"friendly\", which is in turn a derivative from \"friend.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdiaconate', 'a.', 'Of or pertaining to a subdeacon, or to the office or\n   rank of a subdeacon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdiaconate', 'n.', 'The office or rank of a subdeacon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdial', 'a.', 'Of or pertaining to the open air; being under the open\n   sky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdialect', 'n.', 'A subordinate dialect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdichotomy', 'n.', 'A subordinate, or inferior, division into parts; a\n   subdivision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdilated', 'a.', 'Partially dilated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdititious', 'a.', 'Put secretly in the place of something else; foisted\n   in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdiversify', 'v. t.', 'To diversify aggain what is already diversified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdivided', 'imp. & p. p.', 'of Subdivide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdividing', 'p. pr. & vb. n.', 'of Subdivide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdivide', 'v. t.', 'To divide the parts of (anything) into more parts; to\n   part into smaller divisions; to divide again, as what has already been\n   divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdivide', 'v. i.', 'To be, or to become, subdivided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdivine', 'a.', 'Partaking of divinity; divine in a partial or lower\n   degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdivisible', 'a.', 'Susceptible of subdivision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdivision', 'n.', 'The act of subdividing, or separating a part into\n   smaller parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdivision', 'n.', 'A part of a thing made by subdividing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdolous', 'a.', 'Sly; crafty; cunning; artful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdominant', 'n.', 'The fourth tone above, or fifth below, the tonic; --\n   so called as being under the dominant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subduable', 'a.', 'Able to be subdued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdual', 'n.', 'Act of subduing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subduce', 'v. t.', 'Alt. of Subduct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subduct', 'v. t.', 'To withdraw; to take away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subduct', 'v. t.', 'To subtract by arithmetical operation; to deduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subduction', 'n.', 'The act of subducting or taking away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subduction', 'n.', 'Arithmetical subtraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdued', 'imp. & p. p.', 'of Subdue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subduing', 'p. pr. & vb. n.', 'of Subdue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdue', 'v. t.', 'To bring under; to conquer by force or the exertion of\n   superior power, and bring into permanent subjection; to reduce under\n   dominion; to vanquish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdue', 'v. t.', 'To overpower so as to disable from further resistance;\n   to crush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdue', 'v. t.', 'To destroy the force of; to overcome; as, medicines\n   subdue a fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdue', 'v. t.', 'To render submissive; to bring under command; to reduce\n   to mildness or obedience; to tame; as, to subdue a stubborn child; to\n   subdue the temper or passions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdue', 'v. t.', 'To overcome, as by persuasion or other mild means; as,\n   to subdue opposition by argument or entreaties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdue', 'v. t.', 'To reduce to tenderness; to melt; to soften; as, to\n   subdue ferocity by tears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdue', 'v. t.', 'To make mellow; to break, as land; also, to destroy, as\n   weeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdue', 'v. t.', 'To reduce the intensity or degree of; to tone down; to\n   soften; as, to subdue the brilliancy of colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdued', 'a.', 'Conquered; overpowered; crushed; submissive; mild.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdued', 'a.', 'Not glaring in color; soft in tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subduement', 'n.', 'Subdual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subduer', 'n.', 'One who, or that which, subdues; a conqueror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdulcid', 'a.', 'Somewhat sweet; sweetish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subduple', 'a.', 'Indicating one part of two; in the ratio of one to two.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subduplicate', 'a.', 'Expressed by the square root; -- said of ratios.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subdural', 'a.', 'Situated under the dura mater, or between the dura mater\n   and the arachnoid membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subeditor', 'n.', 'An assistant editor, as of a periodical or journal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subelongate', 'a.', 'Not fully elongated; somewhat elongated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subendocardial', 'a.', 'Situated under the endocardium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subendymal', 'a.', 'Situated under the endyma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subepidermal', 'a.', 'Situated immediately below the epidermis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subepiglottic', 'a.', 'Situated under the epiglottis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subepithelial', 'a.', 'Situated under the epithelium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subequal', 'a.', 'Nearly equal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suberate', 'n.', 'A salt of suberic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subereous', 'a.', 'Of or pertaining to cork; of the nature of cork;\n   suberose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suberic', 'a.', 'Of or pertaining to cork; specifically, designating an\n   acid, C6H12.(CO2H)2, homologous with oxalic acid, and obtained from\n   cork and certain fatty oils, as a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suberin', 'n.', 'A material found in the cell walls of cork. It is a\n   modification of lignin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suberite', 'n.', 'Any sponge of the genus Suberites and allied genera.\n   These sponges have a fine and compact texture, and contain minute\n   siliceous spicules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suberone', 'n.', 'The hypothetical ketone of suberic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suberone', 'n.', 'A colorless liquid, analogous suberone proper, having a\n   pleasant peppermint odor. It is obtained by the distillation of calcium\n   suberate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suberose', 'a.', 'Alt. of Suberous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suberous', 'a.', 'Having a corky texture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subesophageal', 'a.', 'Situated beneath the esophagus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subfamily', 'n.', 'One of the subdivisions, of more importance than genus,\n   into which certain families are divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subfibrous', 'a.', 'Somewhat fibrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subfuscous', 'a.', 'Duskish; moderately dark; brownish; tawny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subfusk', 'a.', 'Subfuscous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subgelatinous', 'a.', 'Imperfectly or partially gelatinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subgeneric', 'a.', 'Of or pertaining to a subgenus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subgenera', 'pl. ', 'of Subgenus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subgenus', 'n.', 'A subdivision of a genus, comprising one or more species\n   which differ from other species of the genus in some important\n   character or characters; as, the azaleas now constitute a subgenus of\n   Rhododendron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subglacial', 'a.', 'Pertaining or belonging to the under side of a glacier;\n   being beneath a glacier; as, subglacial streams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subglobose', 'a.', 'Not quite globose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subglobular', 'a.', 'Nearly globular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subglossal', 'a.', 'Situated under the tongue; sublingual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subglottic', 'a.', 'Situated below the glottis; -- applied to that part of\n   the cavity of the larynx below the true vocal cords.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subglumaceous', 'a.', 'Somewhat glumaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subgovernor', 'n.', 'A subordinate or assistant governor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subgranular', 'a.', 'Somewhat granular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subgroup', 'n.', 'A subdivision of a group, as of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subhastation', 'n.', 'A public sale or auction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subhepatic', 'a.', 'Situated under, or on the ventral side of, the liver;\n   -- applied to the interlobular branches of the portal vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subhornblendic', 'a.', 'Containing hornblende in a scattered state; of or\n   relating to rocks containing disseminated hornblende.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subhumerate', 'v. t.', 'To place the shoulders under; to bear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subhyaloid', 'a.', 'Situated under the hyaliod membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subhyoidean', 'a.', 'Situated or performed beneath the hyoid bone; as,\n   subhyoidean laryngotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subimago', 'n.', 'A stage in the development of certain insects, such as\n   the May flies, intermediate between the pupa and imago. In this stage,\n   the insect is able to fly, but subsequently sheds a skin before\n   becoming mature. Called also pseudimago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subincusation', 'n.', 'A slight charge or accusation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subindices', 'pl. ', 'of Subindex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subindex', 'n.', 'A number or mark placed opposite the lower part of a\n   letter or symbol to distinguish the symbol; thus, a0, b1, c2, xn, have\n   0, 1, 2, and n as subindices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subindicate', 'v. t.', 'To indicate by signs or hints; to indicate\n   imperfectly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subindication', 'n.', 'The act of indicating by signs; a slight indication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subindividual', 'n.', 'A division of that which is individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subinduce', 'v. t.', 'To insinuate; to offer indirectly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subibfer', 'v. t. & i.', 'To infer from an inference already made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subinfeudation', 'n.', 'The granting of lands by inferior lords to their\n   dependents, to be held by themselves by feudal tenure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subinfeudation', 'n.', 'Subordinate tenancy; undertenancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subingression', 'n.', 'Secret entrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subintestinal', 'a.', 'Situated under, or on the ventral side of, the\n   intestine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subinvolution', 'n.', 'Partial or incomplete involution; as, subinvolution\n   of the uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subitaneous', 'a.', 'Sudden; hasty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subitany', 'a.', 'Subitaneous; sudden; hasty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subito', 'adv.', 'In haste; quickly; rapidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjacent', 'a.', 'Lying under or below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjacent', 'a.', 'Being in a lower situation, though not directly beneath;\n   as, hills and subjacent valleys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject', 'a.', 'Placed or situated under; lying below, or in a lower\n   situation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject', 'a.', 'Placed under the power of another; specifically\n   (International Law), owing allegiance to a particular sovereign or\n   state; as, Jamaica is subject to Great Britain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject', 'a.', 'Exposed; liable; prone; disposed; as, a country subject to\n   extreme heat; men subject to temptation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject', 'a.', 'Obedient; submissive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject', 'a.', 'That which is placed under the authority, dominion,\n   control, or influence of something else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject', 'a.', 'Specifically: One who is under the authority of a ruler\n   and is governed by his laws; one who owes allegiance to a sovereign or\n   a sovereign state; as, a subject of Queen Victoria; a British subject;\n   a subject of the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject', 'a.', 'That which is subjected, or submitted to, any physical\n   operation or process; specifically (Anat.), a dead body used for the\n   purpose of dissection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject', 'a.', 'That which is brought under thought or examination; that\n   which is taken up for discussion, or concerning which anything is said\n   or done.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject', 'a.', 'The person who is treated of; the hero of a piece; the\n   chief character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject', 'a.', 'That of which anything is affirmed or predicated; the\n   theme of a proposition or discourse; that which is spoken of; as, the\n   nominative case is the subject of the verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject', 'a.', 'That in which any quality, attribute, or relation, whether\n   spiritual or material, inheres, or to which any of these appertain;\n   substance; substratum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject', 'a.', 'Hence, that substance or being which is conscious of its\n   own operations; the mind; the thinking agent or principal; the ego. Cf.\n   Object, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject', 'n.', 'The principal theme, or leading thought or phrase, on\n   which a composition or a movement is based.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject', 'n.', 'The incident, scene, figure, group, etc., which it is the\n   aim of the artist to represent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjected', 'imp. & p. p.', 'of Subject');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjecting', 'p. pr. & vb. n.', 'of Subject');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject', 'v. t.', 'To bring under control, power, or dominion; to make\n   subject; to subordinate; to subdue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject', 'v. t.', 'To expose; to make obnoxious or liable; as, credulity\n   subjects a person to impositions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject', 'v. t.', 'To submit; to make accountable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject', 'v. t.', 'To make subservient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject', 'v. t.', 'To cause to undergo; as, to subject a substance to a\n   white heat; to subject a person to a rigid test.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjected', 'a.', 'Subjacent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjected', 'a.', 'Reduced to subjection; brought under the dominion of\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjected', 'a.', 'Exposed; liable; subject; obnoxious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjection', 'a.', 'The act of subjecting, or of bringing under the\n   dominion of another; the act of subduing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjection', 'a.', 'The state of being subject, or under the power,\n   control, and government of another; a state of obedience or\n   submissiveness; as, the safety of life, liberty, and property depends\n   on our subjection to the laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjectist', 'n.', 'One skilled in subjective philosophy; a subjectivist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjective', 'a.', 'Of or pertaining to a subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjective', 'a.', 'Especially, pertaining to, or derived from, ones own\n   consciousness, in distinction from external observation; ralating to\n   the mind, or intellectual world, in distinction from the outward or\n   material excessively occupied with, or brooding over, ones own\n   internal states.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjective', 'a.', 'Modified by, or making prominent, the individuality of\n   a writer or an artist; as, a subjective drama or painting; a subjective\n   writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjectivism', 'n.', 'Any philosophical doctrine which refers all knowledge\n   to, and founds it upon, any subjective states; egoism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjectivist', 'n.', 'One who holds to subjectivism; an egoist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjectivity', 'n.', 'The quality or state of being subjective; character\n   of the subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjectless', 'a.', 'Having no subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subject-matter', 'n.', 'The matter or thought presented for consideration\n   in some statement or discussion; that which is made the object of\n   thought or study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjectness', 'n.', 'Quality of being subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjicible', 'a.', 'Capable of being subjected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjoined', 'imp. & p. p.', 'of Subjoin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjoining', 'p. pr. & vb. n.', 'of Subjoin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjoin', 'v. t.', 'To add after something else has been said or written;\n   to ANNEX; as, to subjoin an argument or reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjoinder', 'n.', 'An additional remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sub judice', '', 'Before the judge, or court; not yet decided; under\n   judicial consideration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjugated', 'imp. & p. p.', 'of Subjugate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjugating', 'p. pr. & vb. n.', 'of Subjugate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjugate', 'v. t.', 'To subdue, and bring under the yoke of power or\n   dominion; to conquer by force, and compel to submit to the government\n   or absolute control of another; to vanquish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjugation', 'n.', 'The act of subjugating, or the state of being\n   subjugated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjugator', 'n.', 'One who subjugates; a conqueror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjunction', 'n.', 'Act of subjoining, or state of being subjoined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjunction', 'n.', 'Something subjoined; as, a subjunction to a sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjunctive', 'a.', 'Subjoined or added to something before said or\n   written.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subjunctive', 'n.', 'The subjunctive mood; also, a verb in the subjunctive\n   mood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subkingdom', 'n.', 'One of the several primary divisions of either the\n   animal, or vegetable kingdom, as, in zoology, the Vertebrata, Tunicata,\n   Mollusca, Articulata, Molluscoidea, Echinodermata, Coelentera, and the\n   Protozoa; in botany, the Phanerogamia, and the Cryptogamia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublapsarian', 'n. & a.', 'Same as Infralapsarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublapsarianism', 'n.', 'Infralapsarianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublapsary', 'a.', 'Sublapsarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublate', 'v. t.', 'To take or carry away; to remove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublation', 'n.', 'The act of taking or carrying away; removal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublative', 'a.', 'Having power, or tending, to take away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublease', 'n.', 'A lease by a tenant or lessee to another person; an\n   underlease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublessee', 'n.', 'A holder of a sublease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublet', 'imp. & p. p.', 'of Sublet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subletting', 'p. pr. & vb. n.', 'of Sublet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublet', 'v. t.', 'To underlet; to lease, as when a lessee leases to\n   another person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublevation', 'n.', 'The act of raising on high; elevation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublevation', 'n.', 'An uprising; an insurrection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublibrarian', 'n.', 'An under or assistant librarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublieutenant', 'n.', 'An inferior or second lieutenant; in the British\n   service, a commissioned officer of the lowest rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subligation', 'n.', 'The act of binding underneath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimable', 'v. t.', 'Capable of being sublimed or sublimated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimated', 'imp. & p. p.', 'of Sublimate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimating', 'p. pr. & vb. n.', 'of Sublimate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimate', 'v. t.', 'To bring by heat into the state of vapor, which, on\n   cooling, returns again to the solid state; as, to sublimate sulphur or\n   camphor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimate', 'v. t.', 'To refine and exalt; to heighten; to elevate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimate', 'n.', 'A product obtained by sublimation; hence, also, a\n   purified product so obtained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimate', 'a.', 'Brought into a state of vapor by heat, and again\n   condensed as a solid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimated', 'a.', 'Refined by, or as by, sublimation; exalted; purified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimation', 'n.', 'The act or process of subliming, or the state or\n   result of being sublimed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimation', 'n.', 'The act of heightening or improving; exaltation;\n   elevation; purification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimation', 'n.', 'That which is sublimed; the product of a purifying\n   process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimatory', 'a.', 'Used for sublimation; as, sublimatory vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimatory', 'n.', 'A vessel used for sublimation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublime', 'superl.', 'Lifted up; high in place; exalted aloft; uplifted;\n   lofty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublime', 'superl.', 'Distinguished by lofty or noble traits; eminent; --\n   said of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublime', 'superl.', 'Awakening or expressing the emotion of awe,\n   adoration, veneration, heroic resolve, etc.; dignified; grand; solemn;\n   stately; -- said of an impressive object in nature, of an action, of a\n   discourse, of a work of art, of a spectacle, etc.; as, sublime scenery;\n   a sublime deed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublime', 'superl.', 'Elevated by joy; elate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublime', 'superl.', 'Lofty of mien; haughty; proud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublime', 'n.', 'That which is sublime; -- with the definite article');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublime', 'n.', 'A grand or lofty style in speaking or writing; a style\n   that expresses lofty conceptions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublime', 'n.', 'That which is grand in nature or art, as distinguished\n   from the merely beautiful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimed', 'imp. & p. p.', 'of Sublime');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subliming', 'p. pr. & vb. n.', 'of Sublime');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublime', 'v. t.', 'To raise on high.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublime', 'v. t.', 'To subject to the process of sublimation; to heat,\n   volatilize, and condense in crystals or powder; to distill off, and\n   condense in solid form; hence, also, to purify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublime', 'v. t.', 'To exalt; to heighten; to improve; to purify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublime', 'v. t.', 'To dignify; to ennoble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublime', 'v. i.', 'To pass off in vapor, with immediate condensation;\n   specifically, to evaporate or volatilize from the solid state without\n   apparent melting; -- said of those substances, like arsenic, benzoic\n   acid, etc., which do not exhibit a liquid form on heating, except under\n   increased pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimed', 'a.', 'Having been subjected to the process of sublimation;\n   hence, also, purified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimely', 'adv.', 'In a sublime manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimeness', 'n.', 'The quality or state of being sublime; sublimity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimification', 'n.', 'The act of making sublime, or state of being made\n   sublime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimities', 'pl. ', 'of Sublimity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimity', 'n.', 'The quality or state of being sublime (in any sense of\n   the adjective).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublimity', 'n.', 'That which is sublime; as, the sublimities of nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublineation', 'n.', 'A mark of a line or lines under a word in a sentence,\n   or under another line; underlining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublinguae', 'pl. ', 'of Sublingua');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublingua', 'n.', 'A process or fold below the tongue in some animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublingual', 'a.', 'Situated under the tongue; as, the sublingual gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublingual', 'a.', 'Of or pertaining to the sublingual gland; as,\n   sublingual salvia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublition', 'n.', 'The act or process of laying the ground in a painting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublittoral', 'a.', 'Under the shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublobular', 'a.', 'Situated under, or at the bases of, the lobules of the\n   liver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublumbar', 'a.', 'Situated under, or on the ventral side of, the lumbar\n   region of the vertebral column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublunar', 'a.', 'Alt. of Sublunary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublunary', 'a.', 'Situated beneath the moon; hence, of or pertaining to\n   this world; terrestrial; earthly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sublunary', 'n.', 'Any worldly thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subluxation', 'n.', 'An incomplete or partial dislocation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submammary', 'a.', 'Situated under the mammae; as, submammary inflammation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submarine', 'a.', 'Being, acting, or growing, under water in the sea; as,\n   submarine navigators; submarine plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submarine', 'n.', 'A submarine plant or animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submarshal', 'n.', 'An under or deputy marshal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submaxillary', 'a.', 'Situated under the maxilla, or lower jaw;\n   inframaxillary; as, the submaxillary gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submaxillary', 'a.', 'Of or pertaining to submaxillary gland; as,\n   submaxillary salvia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submedial', 'a.', 'Lying under the middle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submedian', 'a.', 'Next to the median (on either side); as, the submedian\n   teeth of mollusks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submediant', 'n.', 'The sixth tone of the scale; the under mediant, or\n   third below the keynote; the superdominant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submental', 'a.', 'Situated under the chin; as, the submental artery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submenta', 'pl. ', 'of Submentum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submentum', 'n.', 'The basal part of the labium of insects. It bears the\n   mentum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submerged', 'imp. & p. p.', 'of Submerge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submerging', 'p. pr. & vb. n.', 'of Submerge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submerge', 'v. t.', 'To put under water; to plunge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submerge', 'v. t.', 'To cover or overflow with water; to inundate; to\n   flood; to drown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submerge', 'v. i.', 'To plunge into water or other fluid; to be buried or\n   covered, as by a fluid; to be merged; hence, to be completely included.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submergence', 'n.', 'The act of submerging, or the state of being\n   submerged; submersion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submerse', 'a.', 'Submersed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submersed', 'a.', 'Being or growing under water, as the leaves of aquatic\n   plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submersion', 'n.', 'The act of submerging, or putting under water or other\n   fluid, or of causing to be overflowed; the act of plunging under water,\n   or of drowning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submersion', 'n.', 'The state of being put under water or other fluid, or\n   of being overflowed or drowned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submetallic', 'a.', 'Imperfectly metallic; as, a submetallic luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subminister', 'v. t.', 'To supply; to afford.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subminister', 'v. i.', 'To be subservient; to be useful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subministrant', 'a.', 'Subordinate; subservient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subministrate', 'v. t.', 'To supply; to afford; to subminister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subministration', 'n.', 'The act of subministering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submiss', 'a.', 'Submissive; humble; obsequious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submiss', 'a.', 'Gentle; soft; calm; as, submiss voices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submission', 'n.', 'The act of submitting; the act of yielding to power or\n   authority; surrender of the person and power to the control or\n   government of another; obedience; compliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submission', 'n.', 'The state of being submissive; acknowledgement of\n   inferiority or dependence; humble or suppliant behavior; meekness;\n   resignation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submission', 'n.', 'Acknowledgement of a fault; confession of error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submission', 'n.', 'An agreement by which parties engage to submit any\n   matter of controversy between them to the decision of arbitrators.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submissive', 'a.', 'Inclined or ready to submit; acknowledging ones\n   inferiority; yielding; obedient; humble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submissive', 'a.', 'Showing a readiness to submit; expressing submission;\n   as, a submissive demeanor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submissly', 'adv.', 'In a submissive manner; with a submission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submissness', 'n.', 'Submissiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submitted', 'imp. & p. p.', 'of Submit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submitting', 'p. pr. & vb. n.', 'of Submit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submit', 'v. t.', 'To let down; to lower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submit', 'v. t.', 'To put or place under.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submit', 'v. t.', 'To yield, resign, or surrender to power, will, or\n   authority; -- often with the reflexive pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submit', 'v. t.', 'To leave or commit to the discretion or judgment of\n   another or others; to refer; as, to submit a controversy to\n   arbitrators; to submit a question to the court; -- often followed by a\n   dependent proposition as the object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submit', 'v. i.', 'To yield ones person to the power of another; to give\n   up resistance; to surrender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submit', 'v. i.', 'To yield ones opinion to the opinion of authority of\n   another; to be subject; to acquiesce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submit', 'v. i.', 'To be submissive or resigned; to yield without\n   murmuring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submitter', 'n.', 'One who submits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submonish', 'v. t.', 'To suggest; to prompt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submonition', 'n.', 'Suggestion; prompting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submucous', 'a.', 'Situated under a mucous membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submultiple', 'n.', 'A number or quality which is contained in another an\n   exact number of times, or is an aliquot part of it; thus, 7 is the\n   submultiple of 56, being contained in it eight times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submultiple', 'a.', 'Of or pertaining to a submultiple; being a\n   submultiple; as, a submultiple number; submultiple ratio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Submuscular', 'a.', 'Situated underneath a muscle or muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subnarcotic', 'a.', 'Moderately narcotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subnasal', 'a.', 'Situated under the nose; as, the subnasal point, or the\n   middle point of the inferior border of the anterior nasal aperture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subnascent', 'a.', 'Growing underneath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subnect', 'v. t.', 'To tie or fasten beneath; to join beneath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subnex', 'v. t.', 'To subjoin; to subnect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subnormal', 'n.', 'That part of the axis of a curved line which is\n   intercepted between the ordinate and the normal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subnotation', 'n.', 'A rescript.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subnotochordal', 'a.', 'Situated on the ventral side of the notochord; as,\n   the subnotochordal rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subnuvolar', 'a.', 'Under the clouds; attended or partly covered or\n   obscured by clouds; somewhat cloudy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subobscurely', 'adv.', 'Somewhat obscurely or darkly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subobtuse', 'a.', 'Partially obtuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suboccipital', 'a.', 'Situated under, or posterior to, the occiput; as, the\n   suboccipital, or first cervical, nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suboctave', 'a.', 'Alt. of Suboctuple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suboctuple', 'a.', 'Containing one part of eight; having the ratio of one\n   to eight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subocular', 'a.', 'Situated under, or on the ventral side of, the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subofficer', 'n.', 'An under or subordinate officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subopercular', 'a.', 'Situated below the operculum; pertaining to the\n   suboperculum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subopercular', 'n.', 'The suboperculum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suboperculum', 'n.', 'The lower opercular bone in fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suborbicular', 'a.', 'Alt. of Suborbiculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suborbiculate', 'a.', 'Almost orbiculate or orbicular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suborbital', 'a.', 'Alt. of Suborbitar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suborbitar', 'a.', 'Situated under or below the orbit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suborder', 'n.', 'A division of an order; a group of genera of a little\n   lower rank than an order and of greater importance than a tribe or\n   family; as, cichoraceous plants form a suborder of Compositae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subordinacy', 'n.', 'The quality or state of being subordinate, or subject\n   to control; subordination, as, to bring the imagination to act in\n   subordinacy to reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subordinance', 'a.', 'Alt. of Subordinancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subordinancy', 'a.', 'Subordinacy; subordination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subordinary', 'n.', 'One of several heraldic bearings somewhat less common\n   than an ordinary. See Ordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subordinate', 'a.', 'Placed in a lower order, class, or rank; holding a\n   lower or inferior position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subordinate', 'a.', 'Inferior in order, nature, dignity, power, importance,\n   or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subordinate', 'n.', 'One who stands in order or rank below another; --\n   distinguished from a principal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subordinated', 'imp. & p. p.', 'of Subordinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subordinating', 'p. pr. & vb. n.', 'of Subordinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subordinate', 'v. t.', 'To place in a lower order or class; to make or\n   consider as of less value or importance; as, to subordinate one\n   creature to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subordinate', 'v. t.', 'To make subject; to subject or subdue; as, to\n   subordinate the passions to reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subordination', 'n.', 'The act of subordinating, placing in a lower order,\n   or subjecting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subordination', 'n.', 'The quality or state of being subordinate or\n   inferior to an other; inferiority of rank or dignity; subjection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subordination', 'n.', 'Place of inferior rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subordinative', 'a.', 'Tending to subordinate; expressing subordination;\n   used to introduce a subordinate sentence; as, a subordinative\n   conjunction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suborned', 'imp. & p. p.', 'of Suborn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suborning', 'p. pr. & vb. n.', 'of Suborn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suborn', 'v. t.', 'To procure or cause to take a false oath amounting to\n   perjury, such oath being actually taken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suborn', 'v. t.', 'To procure privately, or by collusion; to procure by\n   indirect means; to incite secretly; to instigate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subornation', 'n.', 'The act of suborning; the crime of procuring a person\n   to take such a false oath as constitutes perjury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subornation', 'n.', 'The sin or offense of procuring one to do a criminal\n   or bad action, as by bribes or persuasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suborner', 'n.', 'One who suborns or procures another to take, a false\n   oath; one who procures another to do a bad action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suboval', 'a.', 'Somewhat oval; nearly oval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subovate', 'a.', 'Nearly in the form of an egg, or of the section of an\n   egg, but having the inferior extremity broadest; nearly ovate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subovated', 'a.', 'Subovate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suboxide', 'n.', 'An oxide containing a relatively small amount of oxygen,\n   and less than the normal proportion; as, potassium suboxide, K4O.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpeduncular', 'a.', 'Situated beneath the peduncle; as, the subpeduncular\n   lobe of the cerebellum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpedunculate', 'a.', 'Supported on, or growing from, a very short stem;\n   having a short peduncle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpellucid', 'a.', 'Somewhat pellucid; nearly pellucid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpena', 'n. & v. t.', 'See Subpoena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpentangular', 'a.', 'Nearly or approximately pentangular; almost\n   pentangular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpericardial', 'a.', 'Situated under the cardiac pericardium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subperiosteal', 'a.', 'Situated under the periosteum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subperitoneal', 'a.', 'Situated under the peritoneal membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpetiolar', 'a.', 'Concealed within the base of the petiole, as the leaf\n   buds of the plane tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpleural', 'a.', 'Situated under the pleural membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpodophyllous', 'a.', 'Situated under the podophyllous tissue of the\n   horses foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpoena', 'n.', 'A writ commanding the attendance in court, as a witness,\n   of the person on whom it is served, under a penalty; the process by\n   which a defendant in equity is commanded to appear and answer the\n   plaintiffs bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpoenaed', 'imp. & p. p.', 'of Subpoena');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpoenaing', 'p. pr. & vb. n.', 'of Subpoena');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpoena', 'v. t.', 'To serve with a writ of subpoena; to command\n   attendance in court by a legal writ, under a penalty in case of\n   disobedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpoenal', 'a.', 'Required or done under penalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpolar', 'a.', 'Situated below the poles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpolygonal', 'a.', 'Approximately polygonal; somewhat or almost\n   polygonal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subprehensile', 'a.', 'Somewhat prehensile; prehensile in an inferior\n   degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subprior', 'n.', 'The vicegerent of a prior; a claustral officer who\n   assists the prior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpubic', 'a.', 'Situated under, or posterior to, the pubic bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpulmonary', 'a.', 'Situated under, or on the ventral side of, the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpurchaser', 'n.', 'A purchaser who buys from a purchaser; one who buys\n   at second hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subpyriform', 'a.', 'Somewhat pyriform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subquadrate', 'a.', 'Nearly or approximately square; almost square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subquadruple', 'a.', 'Containing one part of four; in the ratio of one to\n   four; as, subquadruple proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subquinquefid', 'a.', 'Almost quinquefid; nearly quinquefid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subquintuple', 'a.', 'Having the ratio of one to five; as, subquintuple\n   proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subreader', 'n.', 'An under reader in the inns of court, who reads the\n   texts of law the reader is to discourse upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subrector', 'n.', 'An assistant restor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subreligion', 'n.', 'A secondary religion; a belief or principle held in a\n   quasi religious veneration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subreption', 'n.', 'The act of obtaining a favor by surprise, or by unfair\n   representation through suppression or fraudulent concealment of facts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subreptitious', 'a.', 'Surreptitious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subreptive', 'a.', 'Surreptitious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subrigid', 'a.', 'Somewhat rigid or stiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subriguous', 'a.', 'Watered or wet beneath; well-watered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subrogate', 'v. t.', 'To put in the place of another; to substitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subrogation', 'n.', 'The act of subrogating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subrogation', 'n.', 'The substitution of one person in the place of another\n   as a creditor, the new creditor succeeding to the rights of the former;\n   the mode by which a third person who pays a creditor succeeds to his\n   rights against the debtor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subrotund', 'a.', 'Somewhat rotund.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsacral', 'a.', 'Situated under, or on the ventral side of, the sacrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsaline', 'a.', 'Moderately saline or salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsalt', 'n.', 'A basic salt. See the Note under Salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsannation', 'n.', 'Derision; mockery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscapular', 'a.', 'Alt. of Subscapulary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscapulary', 'a.', 'Situated beneath the scapula; infrascapular; as, the\n   subscapular muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscribable', 'a.', 'Capable of being subscribed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscribed', 'imp. & p. p.', 'of Subscribe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscribing', 'p. pr. & vb. n.', 'of Subscribe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscribe', 'v. t.', 'To write underneath, as ones name; to sign (ones\n   name) to a document.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscribe', 'v. t.', 'To sign with ones own hand; to give consent to, as\n   something written, or to bind ones self to the terms of, by writing\n   ones name beneath; as, parties subscribe a covenant or contract; a man\n   subscribes a bond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscribe', 'v. t.', 'To attest by writing ones name beneath; as, officers\n   subscribe their official acts, and secretaries and clerks subscribe\n   copies or records.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscribe', 'v. t.', 'To promise to give, by writing ones name with the\n   amount; as, each man subscribed ten dollars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscribe', 'v. t.', 'To sign away; to yield; to surrender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscribe', 'v. t.', 'To declare over ones signature; to publish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscribe', 'v. i.', 'To sign ones name to a letter or other document.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscribe', 'v. i.', 'To give consent to something written, by signing\n   ones name; hence, to assent; to agree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscribe', 'v. i.', 'To become surely; -- with for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscribe', 'v. i.', 'To yield; to admit ones self to be inferior or in\n   the wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscribe', 'v. i.', 'To set ones name to a paper in token of promise to\n   give a certain sum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscribe', 'v. i.', 'To enter ones name for a newspaper, a book, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscriber', 'n.', 'One who subscribes; one who contributes to an\n   undertaking by subscribing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscriber', 'n.', 'One who enters his name for a paper, book, map, or the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscript', 'a.', 'Written below or underneath; as, iota subscript. (See\n   under Iota.) Specifically (Math.), said of marks, figures, or letters\n   (suffixes), written below and usually to the right of other letters to\n   distinguish them; as, a, n, 2, in the symbols Xa, An, Y2. See Suffix,\n   n., 2, and Subindex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscript', 'n.', 'Anything written below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscription', 'n.', 'The act of subscribing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscription', 'n.', 'That which is subscribed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscription', 'n.', 'A paper to which a signature is attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscription', 'n.', 'The signature attached to a paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscription', 'n.', 'Consent or attestation by underwriting the name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscription', 'n.', 'Sum subscribed; amount of sums subscribed; as, an\n   individual subscription to a fund.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscription', 'n.', 'The acceptance of articles, or other tests tending to\n   promote uniformity; esp. (Ch. of Eng.), formal assent to the\n   Thirty-nine Articles and the Book of Common Prayer, required before\n   ordination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscription', 'n.', 'Submission; obedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscription', 'n.', 'That part of a prescription which contains the\n   direction to the apothecary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscription', 'n.', 'A method of purchasing items produced periodically in\n   a series, as newspapers or magazines, in which a certain number of the\n   items are delivered as produced, without need for ordering each item\n   individually; also, the purchase thus executed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subscriptive', 'a.', 'Of or pertaining to a subscription, or signature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsecute', 'v. t.', 'To follow closely, or so as to overtake; to pursue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsecutive', 'a.', 'Following in a train or succession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsellia', 'pl. ', 'of Subsellium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsellium', 'n.', 'One of the stalls of the lower range where there are\n   two ranges. See Illust. of Stall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsemitone', 'n.', 'The sensible or leading note, or sharp seventh, of any\n   key; subtonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsensible', 'a.', 'Deeper than the reach of the senses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subseptuple', 'a.', 'Having the ratio of one to seven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsequence', 'n.', 'Alt. of Subsequency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsequency', 'n.', 'The act or state of following; -- opposed to\n   precedence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsequent', 'a.', 'Following in time; coming or being after something else\n   at any time, indefinitely; as, subsequent events; subsequent ages or\n   years; a period long subsequent to the foundation of Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsequent', 'a.', 'Following in order of place; succeeding; as, a\n   subsequent clause in a treaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsequently', 'adv.', 'At a later time; afterwards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subserous', 'a.', 'Situated under a serous membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subserved', 'imp. & p. p.', 'of Subserve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subserving', 'p. pr. & vb. n.', 'of Subserve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subserve', 'v. t.', 'To serve in subordination or instrumentally; to be\n   subservient to; to help forward; to promote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subserve', 'v. i.', 'To be subservient or subordinate; to serve in an\n   inferior capacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subservience', 'n.', 'Alt. of Subserviency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subserviency', 'n.', 'The quality or state of being subservient;\n   instrumental fitness or use; hence, willingness to serve anothers\n   purposes; in a derogatory sense, servility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subservient', 'a.', 'Fitted or disposed to subserve; useful in an inferior\n   capacity; serving to promote some end; subordinate; hence, servile,\n   truckling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subserviently', 'adv.', 'In a subservient manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsesqui-', '', 'A prefix (also used adjectively) denoting the combination\n   of constituents (especially electro-negative and electro-positive\n   bodies) in the proportion of two to three; as, a subsesqui acetate, i.\n   e., a salt having two equivalents of acetic acid to three of the base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsextuple', 'a.', 'Having the ratio of one to six; as, a subsextuple\n   proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsided', 'imp. & p. p.', 'of Subside');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsiding', 'p. pr. & vb. n.', 'of Subside');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subside', 'v. i.', 'To sink or fall to the bottom; to settle, as lees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subside', 'v. i.', 'To tend downward; to become lower; to descend; to sink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subside', 'v. i.', 'To fall into a state of quiet; to cease to rage; to be\n   calmed; to settle down; to become tranquil; to abate; as, the sea\n   subsides; the tumults of war will subside; the fever has subsided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsidence', 'n.', 'Alt. of Subsidency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsidency', 'n.', 'The act or process of subsiding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsidiarily', 'adv.', 'In a subsidiary manner; so as to assist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsidiary', 'a.', 'Furnishing aid; assisting; auxiliary; helping;\n   tributary; especially, aiding in an inferior position or capacity; as,\n   a subsidiary stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsidiary', 'a.', 'Of or pertaining to a subsidy; constituting a subsidy;\n   being a part of, or of the nature of, a subsidy; as, subsidiary\n   payments to an ally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsidiaries', 'pl. ', 'of Subsidiary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsidiary', 'n.', 'One who, or that which, contributes aid or additional\n   supplies; an assistant; an auxiliary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsidized', 'imp. & p. p.', 'of Subsidize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsidizing', 'p. pr. & vb. n.', 'of Subsidize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsidize', 'v. t.', 'To furnish with a subsidy; to purchase the assistance\n   of by the payment of a subsidy; to aid or promote, as a private\n   enterprise, with public money; as, to subsidize a steamship line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsidies', 'pl. ', 'of Subsidy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsidy', 'n.', 'Support; aid; cooperation; esp., extraordinary aid in\n   money rendered to the sovereign or to a friendly power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsidy', 'n.', 'Specifically: A sum of money paid by one sovereign or\n   nation to another to purchase the cooperation or the neutrality of such\n   sovereign or nation in war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsidy', 'n.', 'A grant from the government, from a municipal corporation,\n   or the like, to a private person or company to assist the establishment\n   or support of an enterprise deemed advantageous to the public; a\n   subvention; as, a subsidy to the owners of a line of ocean steamships.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsigned', 'imp. & p. p.', 'of Subsign');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsigning', 'p. pr. & vb. n.', 'of Subsign');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsign', 'v. t.', 'To sign beneath; to subscribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsinnation', 'n.', 'The act of writing the name under something, as for\n   attestation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsilicate', 'n.', 'A basic silicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsisted', 'imp. & p. p.', 'of Subsist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsisting', 'p. pr. & vb. n.', 'of Subsist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsist', 'v. i.', 'To be; to have existence; to inhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsist', 'v. i.', 'To continue; to retain a certain state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsist', 'v. i.', 'To be maintained with food and clothing; to be\n   supported; to live.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsist', 'v. t.', 'To support with provisions; to feed; to maintain; as,\n   to subsist ones family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsistence', 'n.', 'Real being; existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsistence', 'n.', 'Inherency; as, the subsistence of qualities in bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsistence', 'n.', 'That which furnishes support to animal life; means of\n   support; provisions, or that which produces provisions; livelihood; as,\n   a meager subsistence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsistence', 'n.', 'Same as Hypostasis, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsistency', 'n.', 'Subsistence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsistent', 'a.', 'Having real being; as, a subsistent spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsistent', 'a.', 'Inherent; as, qualities subsistent in matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsizar', 'n.', 'An under sizar; a student of lower rank than a sizar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsoil', 'n.', 'The bed, or stratum, of earth which lies immediately\n   beneath the surface soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsoil', 'v. t.', 'To turn up the subsoil of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsolary', 'a.', 'Being under the sun; hence, terrestrial; earthly;\n   mundane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subspecies', 'n.', 'A group somewhat lessdistinct than speciesusually are,\n   but based on characters more important than those which characterize\n   ordinary varieties; often, a geographical variety or race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsphenoidal', 'a.', 'Situated under, or on the ventral side of, the body\n   of the sphenoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subspherical', 'a.', 'Nearly spherical; having a figure resembling that of\n   a sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subspinous', 'a.', 'Subvertebral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subspinous', 'a.', 'Situated beneath a spinous process, as that of the\n   scapula; as, subspinous dislocation of the humerus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substance', 'n.', 'That which underlies all outward manifestations;\n   substratum; the permanent subject or cause of phenomena, whether\n   material or spiritual; that in which properties inhere; that which is\n   real, in distinction from that which is apparent; the abiding part of\n   any existence, in distinction from any accident; that which constitutes\n   anything what it is; real or existing essence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substance', 'n.', 'The most important element in any existence; the\n   characteristic and essential components of anything; the main part;\n   essential import; purport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substance', 'n.', 'Body; matter; material of which a thing is made; hence,\n   substantiality; solidity; firmness; as, the substance of which a\n   garment is made; some textile fabrics have little substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substance', 'n.', 'Material possessions; estate; property; resources.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substance', 'n.', 'Same as Hypostasis, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substance', 'v. t.', 'To furnish or endow with substance; to supply\n   property to; to make rich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substanceless', 'a.', 'Having no substance; unsubstantial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substant', 'a.', 'Substantial; firm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantial', 'a.', 'Belonging to substance; actually existing; real; as,\n   substantial life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantial', 'a.', 'Not seeming or imaginary; not illusive; real; solid;\n   true; veritable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantial', 'a.', 'Corporeal; material; firm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantial', 'a.', 'Having good substance; strong; stout; solid; firm; as,\n   substantial cloth; a substantial fence or wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantial', 'a.', 'Possessed of goods or an estate; moderately wealthy;\n   responsible; as, a substantial freeholder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantiality', 'n.', 'The quality or state of being substantial;\n   corporiety; materiality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantialized', 'imp. & p. p.', 'of Substantialize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantializing', 'p. pr. & vb. n.', 'of Substantialize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantialize', 'v. t.', 'To make substantial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantially', 'adv.', 'In a substantial manner; in substance;\n   essentially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantialness', 'n.', 'The quality or state of being substantial; as, the\n   substantialness of a wall or column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantials', 'n. pl.', 'Essential parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantiated', 'imp. & p. p.', 'of Substantiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantiating', 'p. pr. & vb. n.', 'of Substantiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantiate', 'v. t.', 'To make to exist; to make real.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantiate', 'v. t.', 'To establish the existence or truth of by proof or\n   competent evidence; to verify; as, to substantiate a charge or\n   allegation; to substantiate a declaration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantiation', 'n.', 'The act of substantiating or proving; evidence;\n   proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantival', 'a.', 'Of or pertaining to a substantive; of the nature of\n   substantive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantive', 'a.', 'Betokening or expressing existence; as, the\n   substantive verb, that is, the verb to be.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantive', 'a.', 'Depending on itself; independent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantive', 'a.', 'Enduring; solid; firm; substantial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantive', 'a.', 'Pertaining to, or constituting, the essential part or\n   principles; as, the law substantive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantive', 'n.', 'A noun or name; the part of speech which designates\n   something that exists, or some object of thought, either material or\n   immaterial; as, the words man, horse, city, goodness, excellence, are\n   substantives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantive', 'v. t.', 'To substantivize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantively', 'adv.', 'In a substantive manner; in substance;\n   essentially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantively', 'adv.', 'As a substantive, name, or noun; as, an adjective\n   may be used substantively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantiveness', 'n.', 'The quality or state of being substantive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substantivize', 'v. t.', 'To convert into a substantive; as, to\n   substantivize an adjective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substile', 'n.', 'See Substyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substituent', 'n.', 'Any atom, group, or radical substituted for another,\n   or entering a molecule in place of some other part which is removed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substitute', 'n.', 'One who, or that which, is substituted or put in the\n   place of another; one who acts for another; that which stands in lieu\n   of something else');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substitute', 'n.', 'a person who enlists for military service in the place\n   of a conscript or drafted man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substituted', 'imp. & p. p.', 'of Substitute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substituting', 'p. pr. & vb. n.', 'of Substitute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substitute', 'n.', 'To put in the place of another person or thing; to\n   exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substituted', 'a.', 'Exchanged; put in the place of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substituted', 'a.', 'Containing substitutions or replacements; having been\n   subjected to the process of substitution, or having some of its parts\n   replaced; as, alcohol is a substituted water; methyl amine is a\n   substituted ammonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substitution', 'n.', 'The act of substituting or putting one person or\n   thing in the place of another; as, the substitution of an agent,\n   attorney, or representative to act for one in his absense; the\n   substitution of bank notes for gold and silver as a circulating medium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substitution', 'n.', 'The state of being substituted for another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substitution', 'n.', 'The office or authority of one acting for another;\n   delegated authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substitution', 'n.', 'The designation of a person in a will to take a\n   devise or legacy, either on failure of a former devisee or legatee by\n   incapacity or unwillingness to accept, or after him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substitution', 'n.', 'The doctrine that Christ suffered vicariously, being\n   substituted for the sinner, and that his sufferings were expiatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substitution', 'n.', 'The act or process of substituting an atom or radical\n   for another atom or radical; metethesis; also, the state of being so\n   substituted. See Metathesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substitutional', 'a.', 'Of or pertaining to substitution; standing in the\n   place of another; substituted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substitutionary', 'a.', 'Of or pertaining to substitution; substitutional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substitutive', 'a.', 'Tending to afford or furnish a substitute; making\n   substitution; capable of being substituted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substract', 'v. t.', 'To subtract; to withdraw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substraction', 'n.', 'Subtraction; deduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substraction', 'n.', 'See Subtraction, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substractor', 'n.', 'One who subtracts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substractor', 'n.', 'A detractor; a slanderer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substrate', 'n.', 'A substratum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substrate', 'a.', 'Having very slight furrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substrate', 'v. t.', 'To strew or lay under anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substrata', 'pl. ', 'of Substratum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substratum', 'n.', 'That which is laid or spread under; that which\n   underlies something, as a layer of earth lying under another;\n   specifically (Agric.), the subsoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substratum', 'n.', 'The permanent subject of qualities or cause of\n   phenomena; substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substruct', 'v. t.', 'To build beneath something; to lay as the foundation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substruction', 'n.', 'Underbuilding; the foundation, or any preliminary\n   structure intended to raise the lower floor or basement of a building\n   above the natural level of the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substructure', 'n.', 'Same as Substruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substructure', 'n.', 'An under structure; a foundation; groundwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substylar', 'a.', 'Pertaining to the substyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Substyle', 'n.', 'A right line on which the style, or gnomon, of a dial is\n   erected; being the common section of the face of the dial and a plane\n   perpendicular to it passing through the style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsulphate', 'n.', 'A sulphate with an excess of the base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsulphide', 'n.', 'A nonacid compound consisting of one equivalent of\n   sulphur and more than one equivalent of some other body, as a metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsultive', 'a.', 'Subsultory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsultory', 'a.', 'Bounding; leaping; moving by sudden leaps or starts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsultus', 'n.', 'A starting, twitching, or convulsive motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsumable', 'a.', 'Capable of being subsumed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsume', 'v. t.', 'To take up into or under, as individual under species,\n   species under genus, or particular under universal; to place (any one\n   cognition) under another as belonging to it; to include under something\n   else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsumption', 'n.', 'The act of subsuming, or of including under another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsumption', 'n.', 'That which is subsumed, as the minor clause or premise\n   of a syllogism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subsumptive', 'a.', 'Relating to, or containing, a subsumption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtangent', 'n.', 'The part of the axis contained between the ordinate and\n   tangent drawn to the same point in a curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtartarean', 'a.', 'Being or living under Tartarus; infernal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtectacle', 'n.', 'A space under a roof; a tabernacle; a dwelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtegulaneous', 'a.', 'Under the roof or eaves; within doors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtenant', 'n.', 'One who rents a tenement, or land, etc., of one who is\n   also a tenant; an undertenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtended', 'imp. & p. p.', 'of Subtend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtending', 'p. pr. & vb. n.', 'of Subtend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtend', 'v. t.', 'To extend under, or be opposed to; as, the line of a\n   triangle which subtends the right angle; the chord subtends an arc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtense', 'a.', 'A line subtending, or stretching across; a chord; as, the\n   subtense of an arc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtepid', 'a.', 'Slightly tepid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subterete', 'a.', 'Somewhat terete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subterfluent', 'a.', 'Alt. of Subterfluous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subterfluous', 'a.', 'Running under or beneath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subterfuge', 'n.', 'That to which one resorts for escape or concealment; an\n   artifice employed to escape censure or the force of an argument, or to\n   justify opinions or conduct; a shift; an evasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subterrane', 'n.', 'A cave or room under ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subterraneal', 'a.', 'Subterranean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subterranean', 'a.', 'Alt. of Subterraneous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subterraneous', 'a.', 'Being or lying under the surface of the earth;\n   situated within the earth, or under ground; as, subterranean springs; a\n   subterraneous passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subterranity', 'n.', 'A place under ground; a subterrany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subterrany', 'a.', 'Subterranean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subterrany', 'n.', 'A subterranean place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subterrene', 'a.', 'Subterraneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subterrestrial', 'a.', 'Subterranean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subthalamic', 'a.', 'Situated under the optic thalamus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtile', 'a.', 'Thin; not dense or gross; rare; as, subtile air; subtile\n   vapor; a subtile medium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtile', 'a.', 'Delicately constituted or constructed; nice; fine;\n   delicate; tenuous; finely woven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtile', 'a.', 'Acute; piercing; searching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtile', 'a.', 'Characterized by nicety of discrimination; discerning;\n   delicate; refined; subtle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtile', 'a.', 'Sly; artful; cunning; crafty; subtle; as, a subtile\n   person; a subtile adversary; a subtile scheme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtiliate', 'v. t.', 'To make thin or rare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtilism', 'n.', 'The quality or state of being subtile; subtility;\n   subtlety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtility', 'n.', 'Subtilty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtilization', 'n.', 'The act of making subtile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtilization', 'n.', 'The operation of making so volatile as to rise in\n   steam or vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtilization', 'n.', 'Refinement; subtlety; extreme attenuation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtilized', 'imp. & p. p.', 'of Subtilize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtilizing', 'p. pr. & vb. n.', 'of Subtilize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtilize', 'v. t.', 'To make thin or fine; to make less gross or coarse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtilize', 'v. t.', 'To refine; to spin into niceties; as, to subtilize\n   arguments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtilize', 'v. i.', 'To refine in argument; to make very nice\n   distinctions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtilizer', 'n.', 'One who subtilizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtilty', 'n.', 'The quality or state of being subtile; thinness;\n   fineness; as, the subtility of air or light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtilty', 'n.', 'Refinement; extreme acuteness; subtlety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtilty', 'n.', 'Cunning; skill; craft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtilty', 'n.', 'Slyness in design; artifice; guile; a cunning design or\n   artifice; a trick; subtlety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtle', 'superl.', 'Sly in design; artful; cunning; insinuating; subtile;\n   -- applied to persons; as, a subtle foe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtle', 'superl.', 'Cunningly devised; crafty; treacherous; as, a subtle\n   stratagem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtle', 'superl.', 'Characterized by refinement and niceness in drawing\n   distinctions; nicely discriminating; -- said of persons; as, a subtle\n   logician; refined; tenuous; sinuous; insinuating; hence, penetrative or\n   pervasive; -- said of the mind; its faculties, or its operations; as, a\n   subtle intellect; a subtle imagination; a subtle process of thought;\n   also, difficult of apprehension; elusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtle', 'superl.', 'Smooth and deceptive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtleness', 'n.', 'The quality or state of being subtle; subtlety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtleties', 'pl. ', 'of Subtlety');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtlety', 'n.', 'The quality or state of being subtle, or sly; cunning;\n   craftiness; artfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtlety', 'n.', 'Nice discernment with delicacy of mental action; nicety\n   of discrimination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtlety', 'n.', 'Something that is sly, crafty, or delusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtly', 'adv.', 'In a subtle manner; slyly; artfully; cunningly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtly', 'adv.', 'Nicely; delicately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtly', 'adv.', 'Deceitfully; delusively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtonic', 'a.', 'Applied to, or distinguishing, a speech element\n   consisting of tone, or proper vocal sound, not pure as in the vowels,\n   but dimmed and otherwise modified by some kind of obstruction in the\n   oral or the nasal passage, and in some cases with a mixture of breath\n   sound; -- a term introduced by Dr. James Rush in 1833. See Guide to\n   Pronunciation, //155, 199-202.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtonic', 'n.', 'A subtonic sound or element; a vocal consonant, as b, d,\n   g, n, etc.; a subvocal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtonic', 'n.', 'The seventh tone of the scale, or that immediately below\n   the tonic; -- called also subsemitone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtorrid', 'a.', 'Nearly torrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtracted', 'imp. & p. p.', 'of Subtract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtracting', 'p. pr. & vb. n.', 'of Subtract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtract', 'v. t.', 'To withdraw, or take away, as a part from the whole;\n   to deduct; as, subtract 5 from 9, and the remainder is 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtracter', 'n.', 'One who subtracts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtracter', 'n.', 'The subtrahend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtraction', 'n.', 'The act or operation of subtracting or taking away a\n   part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtraction', 'n.', 'The taking of a lesser number or quantity from a\n   greater of the same kind or denomination; an operation for finding the\n   difference between two numbers or quantities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtraction', 'n.', 'The withdrawing or withholding from a person of some\n   right to which he is entitled by law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtractive', 'a.', 'Tending, or having power, to subtract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtractive', 'a.', 'Having the negative sign, or sign minus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtrahend', 'n.', 'The sum or number to be subtracted, or taken from\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtranslucent', 'a.', 'Not perfectly translucent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtransparent', 'a.', 'Not perfectly transparent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtreasurer', 'n.', 'The public officer who has charge of a subtreasury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtreasuries', 'pl. ', 'of Subtreasury');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtreasury', 'n.', 'A subordinate treasury, or place of deposit; as, the\n   United States subtreasury at New York.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtriangular', 'a.', 'Nearly, but not perfectly, triangular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtribe', 'n.', 'A division of a tribe; a group of genera of a little\n   lower rank than a tribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtrihedral', 'a.', 'Approaching the form of a three-sided pyramid; as,\n   the subtrihedral crown of a tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtriple', 'a.', 'Containing a third, or one part to three.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtriplicate', 'a.', 'Expressed by the cube root; -- said especially of\n   ratios.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtropical', 'a.', 'Nearly tropical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtruded', 'imp. & p. p.', 'of Subtrude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtruding', 'p. pr. & vb. n.', 'of Subtrude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtrude', 'v. t.', 'To place under; to insert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subturriculate', 'a.', 'Somewhat turriculate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtutor', 'n.', 'An under tutor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subtypical', 'a.', 'Deviating somewhat from the type of a species, genus,\n   or other group; slightly aberrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subulate', 'a.', 'Alt. of Subulated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subulated', 'a.', 'Very narrow, and tapering gradually to a fine point from\n   a broadish base; awl-shaped; linear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subulicornes', 'n. pl.', 'A division of insects having slender or subulate\n   antennae. The dragon flies and May flies are examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subuliform', 'a.', 'Subulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subulipalp', 'n.', 'One of a group of carabid beetles having slender palpi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subumbonal', 'a.', 'Beneath or forward of the umbos of a bivalve shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subumbrella', 'n.', 'The integument of the under surface of the bell, or\n   disk-shaped body, of a jellyfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subundation', 'n.', 'A flood; a deluge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subungual', 'a.', 'Under the nail or hoof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suburb', 'n.', 'An outlying part of a city or town; a smaller place\n   immediately adjacent to a city; in the plural, the region which is on\n   the confines of any city or large town; as, a house stands in the\n   suburbs; a garden situated in the suburbs of Paris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suburb', 'n.', 'Hence, the confines; the outer part; the environment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suburban', 'a.', 'Of or pertaining to suburbs; inhabiting, or being in, the\n   suburbs of a city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suburban', 'n.', 'One who dwells in the suburbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suburbed', 'a.', 'Having a suburb or suburbs on its outer part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suburbial', 'a.', 'Alt. of Suburbian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suburbian', 'a.', 'Suburban.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suburbicarian', 'a.', 'Alt. of Suburbicary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suburbicary', 'a.', 'Being in the suburbs; -- applied to the six dioceses\n   in the suburbs of Rome subject to the pope as bishop of Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suburethral', 'a.', 'Situated under the urethra, or under its orifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subvaginal', 'a.', 'Situated under or inside a sheath or vaginal membrane;\n   as, the subvaginal, or subdural, spaces about the optic nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ties', 'pl. ', 'of Subvariety');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subvariety', 'n.', 'A subordinate variety, or a division of a variety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subvened', 'imp. & p. p.', 'of Subvene');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subvening', 'p. pr. & vb. n.', 'of Subvene');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subvene', 'v. i.', 'To come under, as a support or stay; to happen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subventaneous', 'a.', 'Produced by the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subvention', 'n.', 'The act of coming under.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subvention', 'n.', 'The act of relieving, as of a burden; support; aid;\n   assistance; help.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subvention', 'n.', 'A government aid or bounty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subvention', 'v. t.', 'To subventionize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subventionize', 'v. t.', 'To come to the aid of; to subsidize; to support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subventitious', 'a.', 'Helping; aiding; supporting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subverse', 'v. t.', 'To subvert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subversion', 'n.', 'The act of overturning, or the state of being\n   overturned; entire overthrow; an overthrow from the foundation; utter\n   ruin; destruction; as, the subversion of a government; the subversion\n   of despotic power; the subversion of the constitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subversionary', 'a.', 'Promoting destruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subversive', 'a.', 'Tending to subvert; having a tendency to overthrow and\n   ruin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subverted', 'imp. & p. p.', 'of Subvert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subverting', 'p. pr. & vb. n.', 'of Subvert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subvert', 'v. t.', 'To overturn from the foundation; to overthrow; to ruin\n   utterly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subvert', 'v. t.', 'To pervert, as the mind, and turn it from the truth; to\n   corrupt; to confound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subvert', 'v. i.', 'To overthrow anything from the foundation; to be\n   subversive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subverant', 'a.', 'Reserved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subvertebral', 'a.', 'Situated beneath, or on the ventral side of, the\n   vertebral column; situated beneath, or inside of, the endoskeleton;\n   hypaxial; hyposkeletal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subverter', 'n.', 'One who, or that which, subverts; an overthrower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subvertible', 'a.', 'That may be subverted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subvitalized', 'a.', 'Imperfectly vitalized; having naturally but little\n   vital power or energy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subvocal', 'a. & n.', 'Same as Subtonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subway', 'n.', 'An underground way or gallery; especially, a passage under\n   a street, in which water mains, gas mains, telegraph wires, etc., are\n   conducted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subworker', 'n.', 'A subordinate worker or helper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subzonal', 'a.', 'Situated under a zone, or zona; -- applied to a membrane\n   between the zona radiata and the umbilical vesicle in the mammal\n   embryo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Subzigomatic', 'a.', 'Situated under the zygoma or zygomatic process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succade', 'n.', 'A sweetmeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succade', 'n.', 'Sweetmeats, or preserves in sugar, whether fruit,\n   vegetables, or confections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succedane', 'n.', 'A succedaneum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succedaneous', 'a.', 'Pertaining to, or acting as, a succedaneum; supplying\n   the place of something else; being, or employed as, a substitute for\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succedanea', 'pl. ', 'of Succedaneum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succedaneum', 'n.', 'One who, or that which, succeeds to the place of\n   another; that which is used for something else; a substitute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succedaneum', 'n.', 'a remedy used as a substitute for another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succeeded', 'imp. & p. p.', 'of Succeed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succeeding', 'p. pr. & vb. n.', 'of Succeed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succeed', 'v. t.', 'To follow in order; to come next after; hence, to take\n   the place of; as, the kings eldest son succeeds his father on the\n   throne; autumn succeeds summer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succeed', 'v. t.', 'To fall heir to; to inherit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succeed', 'v. t.', 'To come after; to be subsequent or consequent to; to\n   follow; to pursue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succeed', 'v. t.', 'To support; to prosper; to promote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succeed', 'v. i.', 'To come in the place of another person, thing, or\n   event; to come next in the usual, natural, or prescribed course of\n   things; to follow; hence, to come next in the possession of anything;\n   -- often with to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succeed', 'v. i.', 'Specifically: To ascend the throne after the removal\n   the death of the occupant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succeed', 'v. i.', 'To descend, as an estate or an heirloom, in the same\n   family; to devolve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succeed', 'v. i.', 'To obtain the object desired; to accomplish what is\n   attempted or intended; to have a prosperous issue or termination; to be\n   successful; as, he succeeded in his plans; his plans succeeded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succeed', 'v. i.', 'To go under cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succeedant', 'a.', 'Succeeding one another; following.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succeeder', 'n.', 'A successor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succeeding', 'n.', 'The act of one who, or that which, succeeds; also, that\n   which succeeds, or follows after; consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succentor', 'n.', 'A subchanter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Success', 'n.', 'Act of succeeding; succession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Success', 'n.', 'That which comes after; hence, consequence, issue, or\n   result, of an endeavor or undertaking, whether good or bad; the outcome\n   of effort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Success', 'n.', 'The favorable or prosperous termination of anything\n   attempted; the attainment of a proposed object; prosperous issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Success', 'n.', 'That which meets with, or one who accomplishes, favorable\n   results, as a play or a player.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Successary', 'n.', 'Succession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Successful', 'a.', 'Resulting in success; assuring, or promotive of,\n   success; accomplishing what was proposed; having the desired effect;\n   hence, prosperous; fortunate; happy; as, a successful use of medicine;\n   a successful experiment; a successful enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succession', 'n.', 'The act of succeeding, or following after; a following\n   of things in order of time or place, or a series of things so\n   following; sequence; as, a succession of good crops; a succession of\n   disasters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succession', 'n.', 'A series of persons or things according to some\n   established rule of precedence; as, a succession of kings, or of\n   bishops; a succession of events in chronology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succession', 'n.', 'An order or series of descendants; lineage; race;\n   descent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succession', 'n.', 'The power or right of succeeding to the station or\n   title of a father or other predecessor; the right to enter upon the\n   office, rank, position, etc., held ny another; also, the entrance into\n   the office, station, or rank of a predecessor; specifically, the\n   succeeding, or right of succeeding, to a throne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succession', 'n.', 'The right to enter upon the possession of the property\n   of an ancestor, or one near of kin, or one preceding in an established\n   order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succession', 'n.', 'The person succeeding to rank or office; a successor or\n   heir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Successional', 'a.', 'Of or pertaining to a succession; existing in a\n   regular order; consecutive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Successionist', 'n.', 'A person who insists on the importance of a regular\n   succession of events, offices, etc.; especially (Eccl.), one who\n   insists that apostolic succession alone is valid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Successive', 'a.', 'Following in order or in uninterrupted course; coming\n   after without interruption or interval; following one after another in\n   a line or series; consecutive; as, the successive revolution of years;\n   the successive kings of Egypt; successive strokes of a hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Successive', 'a.', 'Having or giving the right of succeeding to an\n   inheritance; inherited by succession; hereditary; as, a successive\n   title; a successive empire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Successively', 'adv.', 'In a successive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Successiveness', 'n.', 'The quality or state of being successive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Successless', 'a.', 'Having no success.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Successor', 'n.', 'One who succeeds or follows; one who takes the place\n   which another has left, and sustains the like part or character; --\n   correlative to predecessor; as, the successor of a deceased king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succiduous', 'a.', 'Ready to fall; falling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succiferous', 'a.', 'Producing or conveying sap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succinamate', 'n.', 'A salt of succinamic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succinamic', 'a.', 'Of, pertaining to, or designating, an acid amide\n   derivative of succinic acid, obtained as a white crystalline substance,\n   and forming a series of salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succinate', 'n.', 'A salt of succinic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succinct', 'a.', 'Girded or tucked up; bound; drawn tightly together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succinct', 'a.', 'Compressed into a narrow compass; brief; concise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succinic', 'a.', 'Pertaining to, or derived from, amber; specif.,\n   designating a dibasic acid, C/H/.(CO/H)/, first obtained by the dry\n   distillation of amber. It is found in a number of plants, as in lettuce\n   and wormwood, and is also produced artificially as a white crystalline\n   substance having a slightly acid taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succinimide', 'n.', 'A white crystalline nitrogenous substance,\n   C2H4.(CO)2.NH, obtained by treating succinic anhydride with ammonia\n   gas. It is a typical imido acid, and forms a series of salts. See Imido\n   acid, under Imido.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succinite', 'n.', 'Amber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succinite', 'n.', 'A garnet of an amber color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succinous', 'a.', 'Succinic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succinurate', 'n.', 'A salt of succinuric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succinuric', 'a.', 'Pertaining to, or designating, an acid amide, analogous\n   to succinamic acid, which is obtained as a white crystalline substance\n   by heating urea with succinic anhydride. It is known also in its salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succinyl', 'n.', 'A hypothetical radical characteristic of succinic acid\n   and certain of its derivatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succise', 'a.', 'Appearing as if a part were cut off at the extremity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succision', 'n.', 'The act of cutting down, as of trees; the act of cutting\n   off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succored', 'imp. & p. p.', 'of Succor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succoring', 'p. pr. & vb. n.', 'of Succor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succor', 'v. t.', 'To run to, or run to support; hence, to help or relieve\n   when in difficulty, want, or distress; to assist and deliver from\n   suffering; to relieve; as, to succor a besieged city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succor', 'v. t.', 'Aid; help; assistance; esp., assistance that relieves\n   and delivers from difficulty, want, or distress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succor', 'v. t.', 'The person or thing that brings relief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succorable', 'a.', 'Capable of being succored or assisted; admitting of\n   relief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succorer', 'n.', 'One who affords succor; a helper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succorless', 'a.', 'Destitute of succor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succory', 'n.', 'A plant of the genus Cichorium. See Chicory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succotash', 'n.', 'Green maize and beans boiled together. The dish is\n   borrowed from the native Indians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succoteague', 'n.', 'The squeteague.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succubae', 'pl. ', 'of Succuba');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succuba', 'n.', 'A female demon or fiend. See Succubus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succubine', 'a.', 'Of or pertaining to succuba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succubous', 'a.', 'Having the leaves so placed that the upper part of each\n   one is covered by the base of the next higher leaf, as in hepatic\n   mosses of the genus Plagiochila.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succubi', 'pl. ', 'of Succubus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succubus', 'n.', 'A demon or fiend; especially, a lascivious spirit\n   supposed to have sexual intercourse with the men by night; a succuba.\n   Cf. Incubus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succubus', 'n.', 'The nightmare. See Nightmare, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succula', 'n.', 'A bare axis or cylinder with staves or levers in it to\n   turn it round, but without any drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succulence', 'n.', 'Alt. of Succulency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succulency', 'n.', 'The quality or condition of being succulent; juiciness;\n   as, the succulence of a peach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succulent', 'a.', 'Full of juice; juicy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succulently', 'adv.', 'In a succulent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succulous', 'a.', 'Succulent; juicy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succumbed', 'imp. & p. p.', 'of Succumb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succumbing', 'p. pr. & vb. n.', 'of Succumb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succumb', 'v. t.', 'To yield; to submit; to give up unresistingly; as, to\n   succumb under calamities; to succumb to disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succumbent', 'a.', 'Submissive; yielding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succursal', 'v. t.', 'Serving to aid or help; serving as a chapel of ease;\n   tributary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succi', 'pl. ', 'of Succus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succus', 'n.', 'The expressed juice of a plant, for medicinal use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succussation', 'n.', 'A trot or trotting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succussation', 'n.', 'A shaking; succussion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succussion', 'n.', 'The act of shaking; a shake; esp. (Med.), a shaking of\n   the body to ascertain if there be a liquid in the thorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Succussive', 'a.', 'Characterized by a shaking motion, especially an up and\n   down movement, and not merely tremulous oscillation; as, the succussive\n   motion in earthquakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Such', 'a.', 'Of that kind; of the like kind; like; resembling; similar;\n   as, we never saw such a day; -- followed by that or as introducing the\n   word or proposition which defines the similarity, or the standard of\n   comparison; as, the books are not such that I can recommend them, or,\n   not such as I can recommend; these apples are not such as those we saw\n   yesterday; give your children such precepts as tend to make them\n   better.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Such', 'a.', 'Having the particular quality or character specified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Such', 'a.', 'The same that; -- with as; as, this was the state of the\n   kingdom at such time as the enemy landed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Such', 'a.', 'Certain; -- representing the object as already particularized\n   in terms which are not mentioned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suchospondylous', 'a.', 'Having dorsal vertebrae with long and divided\n   transverse processes; -- applied to certain reptiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suchwise', 'adv.', 'In a such a manner; so.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucked', 'imp. & p. p.', 'of Suck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucking', 'p. pr. & vb. n.', 'of Suck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suck', 'v. t.', 'To draw, as a liquid, by the action of the mouth and\n   tongue, which tends to produce a vacuum, and causes the liquid to rush\n   in by atmospheric pressure; to draw, or apply force to, by exhausting\n   the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suck', 'v. t.', 'To draw liquid from by the action of the mouth; as, to\n   suck an orange; specifically, to draw milk from (the mother, the\n   breast, etc.) with the mouth; as, the young of an animal sucks the\n   mother, or dam; an infant sucks the breast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suck', 'v. t.', 'To draw in, or imbibe, by any process resembles sucking;\n   to inhale; to absorb; as, to suck in air; the roots of plants suck\n   water from the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suck', 'v. t.', 'To draw or drain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suck', 'v. t.', 'To draw in, as a whirlpool; to swallow up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suck', 'v. i.', 'To draw, or attempt to draw, something by suction, as with\n   the mouth, or through a tube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suck', 'v. i.', 'To draw milk from the breast or udder; as, a child, or the\n   young of an animal, is first nourished by sucking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suck', 'v. i.', 'To draw in; to imbibe; to partake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suck', 'n.', 'The act of drawing with the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suck', 'n.', 'That which is drawn into the mouth by sucking; specifically,\n   mikl drawn from the breast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suck', 'n.', 'A small draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suck', 'n.', 'Juice; succulence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suckanhock', 'n.', 'A kind of seawan. See Note under Seawan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suckatash', 'n.', 'See Succotash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucken', 'n.', 'The jurisdiction of a mill, or that extent of ground\n   astricted to it, the tenants of which are bound to bring their grain\n   thither to be ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucker', 'n.', 'One who, or that which, sucks; esp., one of the organs by\n   which certain animals, as the octopus and remora, adhere to other\n   bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucker', 'n.', 'A suckling; a sucking animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucker', 'n.', 'The embolus, or bucket, of a pump; also, the valve of a\n   pump basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucker', 'n.', 'A pipe through which anything is drawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucker', 'n.', 'A small piece of leather, usually round, having a string\n   attached to the center, which, when saturated with water and pressed\n   upon a stone or other body having a smooth surface, adheres, by reason\n   of the atmospheric pressure, with such force as to enable a\n   considerable weight to be thus lifted by the string; -- used by\n   children as a plaything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucker', 'n.', 'A shoot from the roots or lower part of the stem of a\n   plant; -- so called, perhaps, from diverting nourishment from the body\n   of the plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucker', 'n.', 'Any one of numerous species of North American fresh-water\n   cyprinoid fishes of the family Catostomidae; so called because the lips\n   are protrusile. The flesh is coarse, and they are of little value as\n   food. The most common species of the Eastern United States are the\n   northern sucker (Catostomus Commersoni), the white sucker (C. teres),\n   the hog sucker (C. nigricans), and the chub, or sweet sucker (Erimyzon\n   sucetta). Some of the large Western species are called buffalo fish,\n   red horse, black horse, and suckerel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucker', 'n.', 'The remora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucker', 'n.', 'The lumpfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucker', 'n.', 'The hagfish, or myxine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucker', 'n.', 'A California food fish (Menticirrus undulatus) closely\n   allied to the kingfish (a); -- called also bagre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucker', 'n.', 'A parasite; a sponger. See def. 6, above.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucker', 'n.', 'A hard drinker; a soaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucker', 'n.', 'A greenhorn; one easily gulled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucker', 'n.', 'A nickname applied to a native of Illinois.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suckered', 'imp. & p. p.', 'of Sucker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suckering', 'p. pr. & vb. n.', 'of Sucker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucker', 'v. t.', 'To strip off the suckers or shoots from; to deprive of\n   suckers; as, to sucker maize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucker', 'v. i.', 'To form suckers; as, corn suckers abundantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucket', 'v. t.', 'A sweetmeat; a dainty morsel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suckfish', 'n.', 'A sucker fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucking', 'a.', 'Drawing milk from the mother or dam; hence, colloquially,\n   young, inexperienced, as, a sucking infant; a sucking calf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suckle', 'n.', 'A teat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suckled', 'imp. & p. p.', 'of Suckle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suckling', 'p. pr. & vb. n.', 'of Suckle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suckle', 'v. t.', 'To give suck to; to nurse at the breast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suckle', 'v. i.', 'To nurse; to suck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suckler', 'n.', 'An animal that suckles its young; a mammal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suckling', 'v. t.', 'A young child or animal nursed at the breast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suckling', 'v. t.', 'A small kind of yellow clover (Trifolium filiforme)\n   common in Southern Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucrate', 'n.', 'A compound of sucrose (or of some related carbohydrate)\n   with some base, after the analogy of a salt; as, sodium sucrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucre', 'n.', 'A silver coin of Ecuador, worth 68 cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sucrose', 'n.', 'A common variety of sugar found in the juices of many\n   plants, as the sugar cane, sorghum, sugar maple, beet root, etc. It is\n   extracted as a sweet, white crystalline substance which is valuable as\n   a food product, and, being antiputrescent, is largely used in the\n   preservation of fruit. Called also saccharose, cane sugar, etc. By\n   extension, any one of the class of isomeric substances (as lactose,\n   maltose, etc.) of which sucrose proper is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suction', 'v. t.', 'The act or process of sucking; the act of drawing, as\n   fluids, by exhausting the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suctoria', 'n. pl.', 'An order of Infusoria having the body armed with\n   somewhat stiff, tubular processes which they use as suckers in\n   obtaining their food. They are usually stalked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suctoria', 'n. pl.', 'Same as Rhizocephala.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suctorial', 'a.', 'Adapted for sucking; living by sucking; as, the humming\n   birds are suctorial birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suctorial', 'a.', 'Capable of adhering by suction; as, the suctorial\n   fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suctorian', 'n.', 'A cartilaginous fish with a mouth adapted for suction,\n   as the lampery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suctorian', 'n.', 'One of the Suctoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suctorious', 'a.', 'Suctorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudamina', 'n. pl', 'Minute vesicles surrounded by an area of reddened\n   skin, produced by excessive sweating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudarium', 'n.', 'The handkerchief upon which the Savior is said to have\n   impressed his own portrait miraculously, when wiping his face with it,\n   as he passed to the crucifixion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudary', 'n.', 'A napkin or handkerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudation', 'n.', 'A sweating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudatoria', 'pl. ', 'of Sudatorium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudatorium', 'n.', 'A sudatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudatory', 'a.', 'Sweating; perspiring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudatories', 'pl. ', 'of Sudatory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudatory', 'n.', 'A bagnio; a sweating bath; a vapor bath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudden', 'a.', 'Happening without previous notice or with very brief\n   notice; coming unexpectedly, or without the common preparation;\n   immediate; instant; speedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudden', 'a.', 'Hastly prepared or employed; quick; rapid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudden', 'a.', 'Hasty; violent; rash; precipitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudden', 'adv.', 'Suddenly; unexpectedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudden', 'n.', 'An unexpected occurrence; a surprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suddenty', 'n.', 'Suddenness; a sudden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudoral', 'a.', 'Of or pertaining to sweat; as, sudoral eruptions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudoriferous', 'a.', 'Producing, or secreting, sweat; sudoriparous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudorific', 'a.', 'Causing sweat; as, sudorific herbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudorific', 'n.', 'A sudorific medicine. Cf. Diaphoretic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudoriparous', 'a.', 'Same as Sudoriferous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudorous', 'a.', 'Consisting of sweat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sudra', 'n.', 'The lowest of the four great castes among the Hindoos. See\n   Caste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suds', 'n. pl.', 'Water impregnated with soap, esp. when worked up into\n   bubbles and froth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sued', 'imp. & p. p.', 'of Sue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suing', 'p. pr. & vb. n.', 'of Sue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sue', 'v. t.', 'To follow up; to chase; to seek after; to endeavor to win;\n   to woo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sue', 'v. t.', 'To seek justice or right from, by legal process; to\n   institute process in law against; to bring an action against; to\n   prosecute judicially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sue', 'v. t.', 'To proceed with, as an action, and follow it up to its\n   proper termination; to gain by legal process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sue', 'v. t.', 'To clean, as the beak; -- said of a hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sue', 'v. t.', 'To leave high and dry on shore; as, to sue a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sue', 'v. i.', 'To seek by request; to make application; to petition; to\n   entreat; to plead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sue', 'v. i.', 'To prosecute; to make legal claim; to seek (for something)\n   in law; as, to sue for damages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sue', 'v. i.', 'To woo; to pay addresses as a lover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sue', 'v. i.', 'To be left high and dry on the shore, as a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suent', 'a.', 'Uniformly or evenly distributed or spread; even; smooth. See\n   Suant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suently', 'adv.', 'Evenly; smoothly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suer', 'n.', 'One who sues; a suitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suet', 'n.', 'The fat and fatty tissues of an animal, especially the harder\n   fat about the kidneys and loins in beef and mutton, which, when melted\n   and freed from the membranes, forms tallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suety', 'a.', 'Consisting of, or resembling, suet; as, a suety substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suf-', '', 'A form of the prefix Sub-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffered', 'imp. & p. p.', 'of Suffer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffering', 'p. pr. & vb. n.', 'of Suffer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffer', 'v. t.', 'To feel, or endure, with pain, annoyance, etc.; to\n   submit to with distress or grief; to undergo; as, to suffer pain of\n   body, or grief of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffer', 'v. t.', 'To endure or undergo without sinking; to support; to\n   sustain; to bear up under.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffer', 'v. t.', 'To undergo; to be affected by; to sustain; to\n   experience; as, most substances suffer a change when long exposed to\n   air and moisture; to suffer loss or damage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffer', 'v. t.', 'To allow; to permit; not to forbid or hinder; to\n   tolerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffer', 'v. i.', 'To feel or undergo pain of body or mind; to bear what is\n   inconvenient; as, we suffer from pain, sickness, or sorrow; we suffer\n   with anxiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffer', 'v. i.', 'To undergo punishment; specifically, to undergo the\n   penalty of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffer', 'v. i.', 'To be injured; to sustain loss or damage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufferable', 'a.', 'Able to suffer or endure; patient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufferable', 'a.', 'That may be suffered, tolerated, or permitted;\n   allowable; tolerable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufferance', 'n.', 'The state of suffering; the bearing of pain; endurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufferance', 'n.', 'Pain endured; misery; suffering; distress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufferance', 'n.', 'Loss; damage; injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufferance', 'n.', 'Submission under difficult or oppressive circumstances;\n   patience; moderation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufferance', 'n.', 'Negative consent by not forbidding or hindering;\n   toleration; permission; allowance; leave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufferance', 'n.', 'A permission granted by the customs authorities for the\n   shipment of goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufferer', 'n.', 'One who suffers; one who endures or undergoes suffering;\n   one who sustains inconvenience or loss; as, sufferers by poverty or\n   sickness; men are sufferers by fire or by losses at sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufferer', 'n.', 'One who permits or allows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffering', 'n.', 'The bearing of pain, inconvenience, or loss; pain\n   endured; distress, loss, or injury incurred; as, sufferings by pain or\n   sorrow; sufferings by want or by wrongs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffering', 'a.', 'Being in pain or grief; having loss, injury, distress,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufficed', 'imp. & p. p.', 'of Suffice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufficing', 'p. pr. & vb. n.', 'of Suffice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffice', 'v. i.', 'To be enough, or sufficient; to meet the need (of\n   anything); to be equal to the end proposed; to be adequate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffice', 'v. t.', 'To satisfy; to content; to be equal to the wants or\n   demands of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffice', 'v. t.', 'To furnish; to supply adequately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufficience', 'n.', 'Sufficiently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufficiency', 'n.', 'The quality or state of being sufficient, or adequate\n   to the end proposed; adequacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufficiency', 'n.', 'Qualification for any purpose; ability; capacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufficiency', 'n.', 'Adequate substance or means; competence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufficiency', 'n.', 'Supply equal to wants; ample stock or fund.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufficiency', 'n.', 'Conceit; self-confidence; self-sufficiency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufficient', 'a.', 'Equal to the end proposed; adequate to wants; enough;\n   ample; competent; as, provision sufficient for the family; an army\n   sufficient to defend the country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufficient', 'a.', 'Possessing adequate talents or accomplishments; of\n   competent power or ability; qualified; fit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufficient', 'a.', 'Capable of meeting obligations; responsible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufficient', 'a.', 'Self-sufficient; self-satisfied; content.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufficiently', 'adv.', 'To a sufficient degree; to a degree that answers\n   the purpose, or gives content; enough; as, we are sufficiently supplied\n   with food; a man sufficiently qualified for the discharge of his\n   official duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufficing', 'a.', 'Affording enough; satisfying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffisance', 'n.', 'Sufficiency; plenty; abundance; contentment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffisant', 'a.', 'Sufficient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffix', 'n.', 'A letter, letters, syllable, or syllables added or appended\n   to the end of a word or a root to modify the meaning; a postfix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffix', 'n.', 'A subscript mark, number, or letter. See Subscript, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffixed', 'imp. & p. p.', 'of Suffix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffixing', 'p. pr. & vb. n.', 'of Suffix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffix', 'v. t.', 'To add or annex to the end, as a letter or syllable to a\n   word; to append.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffixion', 'n.', 'The act of suffixing, or the state of being suffixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffixment', 'n.', 'Suffixion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufflaminate', 'v. t.', 'To retard the motion of, as a carriage, by\n   preventing one or more of its wheels from revolving, either by means of\n   a chain or otherwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufflaminate', 'v. t.', 'Hence, to stop; to impede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufflate', 'v. t.', 'To blow up; to inflate; to inspire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufflation', 'n.', 'The act of blowing up or inflating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffocate', 'a.', 'Suffocated; choked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffocated', 'imp. & p. p.', 'of Suffocate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffocating', 'p. pr. & vb. n.', 'of Suffocate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffocate', 'v. t.', 'To choke or kill by stopping respiration; to stifle;\n   to smother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffocate', 'v. t.', 'To destroy; to extinguish; as, to suffocate fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffocate', 'v. i.', 'To become choked, stifled, or smothered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffocating', '', 'a. & n. from Suffocate, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffocation', 'n.', 'The act of suffocating, or the state of being\n   suffocated; death caused by smothering or choking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffocative', 'a.', 'Tending or able to choke or stifle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffossion', 'n.', 'A digging under; an undermining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffragan', 'a.', 'Assisting; assistant; as, a suffragan bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffragan', 'a.', 'An assistant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffragan', 'a.', 'A bishop considered as an assistant, or as subject, to\n   his metropolitan; an assistant bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffraganship', 'n.', 'The office of a suffragan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffragant', 'a. & n.', 'Suffragan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffragated', 'imp. & p. p.', 'of Suffragate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffragating', 'p. pr. & vb. n.', 'of Suffragate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffragate', 'a.', 'To vote or vote with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffragator', 'n.', 'One who assists or favors by his vote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffrage', 'n.', 'A vote given in deciding a controverted question, or in\n   the choice of a man for an office or trust; the formal expression of an\n   opinion; assent; vote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffrage', 'n.', 'Testimony; attestation; witness; approval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffrage', 'n.', 'A short petition, as those after the creed in matins and\n   evensong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffrage', 'n.', 'A prayer in general, as one offered for the faithful\n   departed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffrage', 'n.', 'Aid; assistance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffrage', 'n.', 'The right to vote; franchise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffrage', 'v. t.', 'To vote for; to elect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufragette.', 'n.', 'A woman who advocates the right to vote for women; a\n   woman suffragist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffraginous', 'a.', 'Of or pertaining to the hock of a beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffragist', 'n.', 'One who possesses or exercises the political right of\n   suffrage; a voter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffragist', 'n.', 'One who has certain opinions or desires about the\n   political right of suffrage; as, a woman suffragist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffrago', 'n.', 'The heel joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffrance', 'n.', 'Sufferance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffrutescent', 'a.', 'Slightly woody at the base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffruticose', 'a.', 'Woody in the lower part of the stem, but with the\n   yearly branches herbaceous, as sage, thyme, hyssop, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffruticous', 'a.', 'Suffruticose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffumigated', 'imp. & p. p.', 'of Suffumigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffumigating', 'p. pr. & vb. n.', 'of Suffumigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffumigate', 'v. t.', 'To apply fumes or smoke to the parts of, as to the\n   body in medicine; to fumigate in part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffumigation', 'n.', 'The operation of suffumigating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffumige', 'n.', 'A medical fume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffused', 'imp. & p. p.', 'of Suffuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffusing', 'p. pr. & vb. n.', 'of Suffuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffuse', 'v. t.', 'To overspread, as with a fluid or tincture; to fill or\n   cover, as with something fluid; as, eyes suffused with tears; cheeks\n   suffused with blushes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffusion', 'n.', 'The act or process of suffusing, or state of being\n   suffused; an overspreading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffusion', 'n.', 'That with which a thing is suffused.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suffusion', 'n.', 'A blending of one color into another; the spreading of\n   one color over another, as on the feathers of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufi', 'n.', 'A title or surname of the king of Persia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufi', 'n.', 'One of a certain order of religious men in Persia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sufism', 'n.', 'A refined mysticism among certain classes of Mohammedans,\n   particularly in Persia, who hold to a kind of pantheism and practice\n   extreme asceticism in their lives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sug', 'n.', 'A kind of worm or larva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sugar', 'n.', 'A sweet white (or brownish yellow) crystalline substance, of\n   a sandy or granular consistency, obtained by crystallizing the\n   evaporated juice of certain plants, as the sugar cane, sorghum, beet\n   root, sugar maple, etc. It is used for seasoning and preserving many\n   kinds of food and drink. Ordinary sugar is essentially sucrose. See the\n   Note below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sugar', 'n.', 'By extension, anything resembling sugar in taste or\n   appearance; as, sugar of lead (lead acetate), a poisonous white\n   crystalline substance having a sweet taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sugar', 'n.', 'Compliment or flattery used to disguise or render acceptable\n   something obnoxious; honeyed or soothing words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sugar', 'v. i.', 'In making maple sugar, to complete the process of boiling\n   down the sirup till it is thick enough to crystallize; to approach or\n   reach the state of granulation; -- with the preposition off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sugared', 'imp. & p. p.', 'of Sugar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sugaring', 'p. pr. & vb. n.', 'of Sugar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sugar', 'v. t.', 'To impregnate, season, cover, or sprinkle with sugar; to\n   mix sugar with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sugar', 'v. t.', 'To cover with soft words; to disguise by flattery; to\n   compliment; to sweeten; as, to sugar reproof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sugared', 'a.', 'Sweetened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sugared', 'a.', 'Also used figuratively; as, sugared kisses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sugar-house', 'n.', 'A building in which sugar is made or refined; a sugar\n   manufactory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sugariness', 'n.', 'The quality or state of being sugary, or sweet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sugaring', 'n.', 'The act of covering or sweetening with sugar; also, the\n   sugar thus used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sugaring', 'n.', 'The act or process of making sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sugarless', 'a.', 'Without sugar; free from sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sugarplum', 'n.', 'A kind of candy or sweetneat made up in small balls or\n   disks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sugary', 'a.', 'Resembling or containing sugar; tasting of sugar; sweet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sugary', 'a.', 'Fond of sugar or sweet things; as, a sugary palate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sugescent', 'a.', 'Of or pertaining to sucking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suggested', 'imp. & p. p.', 'of Suggest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suggesting', 'p. pr. & vb. n.', 'of Suggest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suggest', 'v. t.', 'To introduce indirectly to the thoughts; to cause to be\n   thought of, usually by the agency of other objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suggest', 'v. t.', 'To propose with difference or modesty; to hint; to\n   intimate; as, to suggest a difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suggest', 'v. t.', 'To seduce; to prompt to evil; to tempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suggest', 'v. t.', 'To inform secretly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suggest', 'v. i.', 'To make suggestions; to tempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suggester', 'n.', 'One who suggests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suggestion', 'n.', 'The act of suggesting; presentation of an idea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suggestion', 'n.', 'That which is suggested; an intimation; an insinuation;\n   a hint; a different proposal or mention; also, formerly, a secret\n   incitement; temptation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suggestion', 'n.', 'Charge; complaint; accusation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suggestion', 'n.', 'Information without oath; an entry of a material fact\n   or circumstance on the record for the information of the court, at the\n   death or insolvency of a party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suggestion', 'n.', 'The act or power of originating or recalling ideas or\n   relations, distinguished as original and relative; -- a term much used\n   by Scottish metaphysicians from Hutcherson to Thomas Brown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suggestive', 'a.', 'Containing a suggestion, hint, or intimation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suggestment', 'n.', 'Suggestion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suggestress', 'n.', 'A woman who suggests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suggil', 'v. t.', 'To defame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suggillate', 'v. t.', 'To beat livid, or black and blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suggillation', 'n.', 'A livid, or black and blue, mark; a blow; a bruise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suicidal', 'a.', 'Partaking of, or of the nature of, the crime or suicide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suicide', 'adv.', 'The act of taking ones own life voluntary and\n   intentionally; self-murder; specifically (Law), the felonious killing\n   of ones self; the deliberate and intentional destruction of ones own\n   life by a person of years of discretion and of sound mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suicide', 'adv.', 'One guilty of self-murder; a felo-de-se.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suicide', 'adv.', 'Ruin of ones own interests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suicidical', 'a.', 'Suicidal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suicidism', 'n.', 'The quality or state of being suicidal, or\n   self-murdering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suicism', 'n.', 'Selfishness; egoism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sui generis', '', 'Of his or its own kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suillage', 'n.', 'A drain or collection of filth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suilline', 'a.', 'Of or pertaining to a hog or the Hog family (Suidae).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suine', 'n.', 'A mixture of oleomargarine with lard or other fatty\n   ingredients. It is used as a substitute for butter. See Butterine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suing', 'n.', 'The process of soaking through anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suingly', 'adv.', 'In succession; afterwards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suint', 'n.', 'A peculiar substance obtained from the wool of sheep,\n   consisting largely of potash mixed with fatty and earthy matters. It is\n   used as a source of potash and also for the manufacture of gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suigothus', 'n. pl.', 'The Scandinavian Goths. See the Note under Goths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suist', 'n.', 'One who seeks for things which gratify merely himself; a\n   selfish person; a selfist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suit', 'n.', 'The act of following or pursuing, as game; pursuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suit', 'n.', 'The act of suing; the process by which one endeavors to gain\n   an end or an object; an attempt to attain a certain result; pursuit;\n   endeavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suit', 'n.', 'The act of wooing in love; the solicitation of a woman in\n   marriage; courtship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suit', 'n.', 'The attempt to gain an end by legal process; an action or\n   process for the recovery of a right or claim; legal application to a\n   court for justice; prosecution of right before any tribunal; as, a\n   civil suit; a criminal suit; a suit in chancery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suit', 'n.', 'That which follows as a retinue; a company of attendants or\n   followers; the assembly of persons who attend upon a prince,\n   magistrate, or other person of distinction; -- often written suite, and\n   pronounced sw/t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suit', 'n.', 'Things that follow in a series or succession; the individual\n   objects, collectively considered, which constitute a series, as of\n   rooms, buildings, compositions, etc.; -- often written suite, and\n   pronounced sw/t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suit', 'n.', 'A number of things used together, and generally necessary to\n   be united in order to answer their purpose; a number of things\n   ordinarily classed or used together; a set; as, a suit of curtains; a\n   suit of armor; a suit of clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suit', 'n.', 'One of the four sets of cards which constitute a pack; --\n   each set consisting of thirteen cards bearing a particular emblem, as\n   hearts, spades, cubs, or diamonds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suit', 'n.', 'Regular order; succession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suited', 'imp. & p. p.', 'of Suit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suiting', 'p. pr. & vb. n.', 'of Suit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suit', 'v. t.', 'To fit; to adapt; to make proper or suitable; as, to suit\n   the action to the word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suit', 'v. t.', 'To be fitted to; to accord with; to become; to befit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suit', 'v. t.', 'To dress; to clothe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suit', 'v. t.', 'To please; to make content; as, he is well suited with his\n   place; to suit ones taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suit', 'v. i.', 'To agree; to accord; to be fitted; to correspond; --\n   usually followed by with or to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suitability', 'n.', 'The quality or state of being suitable; suitableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suitable', 'a.', 'Capable of suiting; fitting; accordant; proper; becoming;\n   agreeable; adapted; as, ornaments suitable to ones station; language\n   suitable for the subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suite', 'n.', 'A retinue or company of attendants, as of a distinguished\n   personage; as, the suite of an ambassador. See Suit, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suite', 'n.', 'A connected series or succession of objects; a number of\n   things used or clessed together; a set; as, a suite of rooms; a suite\n   of minerals. See Suit, n., 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suite', 'n.', 'One of the old musical forms, before the time of the more\n   compact sonata, consisting of a string or series of pieces all in the\n   same key, mostly in various dance rhythms, with sometimes an elaborate\n   prelude. Some composers of the present day affect the suite form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suiting', 'n.', 'Among tailors, cloth suitable for making entire suits of\n   clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suitor', 'n.', 'One who sues, petitions, or entreats; a petitioner; an\n   applicant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suitor', 'n.', 'Especially, one who solicits a woman in marriage; a wooer;\n   a lover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suitor', 'n.', 'One who sues or prosecutes a demand in court; a party to a\n   suit, as a plaintiff, petitioner, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suitor', 'n.', 'One who attends a court as plaintiff, defendant,\n   petitioner, appellant, witness, juror, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suitress', 'n.', 'A female supplicant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suji', 'n.', 'Indian wheat, granulated but not pulverized; a kind of\n   semolina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sula', 'n.', 'A genus of sea birds including the booby and the common\n   gannet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulcate', 'a.', 'Alt. of Sulcated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulcated', 'a.', 'Scored with deep and regular furrows; furrowed or\n   grooved; as, a sulcated stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulcation', 'n.', 'A channel or furrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulciform', 'a.', 'Having the form of a sulcus; as, sulciform markings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulci', 'pl. ', 'of Sulcus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulcus', 'n.', 'A furrow; a groove; a fissure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suleah fish', '', 'A coarse fish of India, used in making a breakfast\n   relish called burtah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulk', 'n.', 'A furrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulk', 'v. i.', 'To be silently sullen; to be morose or obstinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulker', 'n.', 'One who sulks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulkily', 'adv.', 'In a sulky manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulkiness', 'n.', 'The quality or state of being sulky; sullenness;\n   moroseness; as, sulkiness of disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulks', 'n. pl.', 'The condition of being sulky; a sulky mood or humor; as,\n   to be in the sulks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulky', 'n.', 'Moodly silent; sullen; sour; obstinate; morose; splenetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulkies', 'pl. ', 'of Sulky');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulky', 'a.', 'A light two-wheeled carriage for a single person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sull', 'n.', 'A plow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sullage', 'n.', 'Drainage of filth; filth collected from the street or\n   highway; sewage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sullage', 'n.', 'That which sullies or defiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sullage', 'n.', 'The scoria on the surface of molten metal in the ladle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sullage', 'n.', 'Silt; mud deposited by water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sullen', 'a.', 'Lonely; solitary; desolate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sullen', 'a.', 'Gloomy; dismal; foreboding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sullen', 'a.', 'Mischievous; malignant; unpropitious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sullen', 'a.', 'Gloomily angry and silent; cross; sour; affected with ill\n   humor; morose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sullen', 'a.', 'Obstinate; intractable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sullen', 'a.', 'Heavy; dull; sluggish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sullen', 'n.', 'One who is solitary, or lives alone; a hermit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sullen', 'n.', 'Sullen feelings or manners; sulks; moroseness; as, to have\n   the sullens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sullen', 'v. t.', 'To make sullen or sluggish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sullevate', 'v. t.', 'To rouse; to excite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulliage', 'v. t.', 'Foulness; filth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sullied', 'imp. & p. p.', 'of Sully');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sullying', 'p. pr. & vb. n.', 'of Sully');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sully', 'v. t.', 'To soil; to dirty; to spot; to tarnish; to stain; to\n   darken; -- used literally and figuratively; as, to sully a sword; to\n   sully a persons reputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sully', 'v. i.', 'To become soiled or tarnished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sullies', 'pl. ', 'of Sully');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sully', 'n.', 'Soil; tarnish; stain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphacid', 'n.', 'An acid in which, to a greater or less extent, sulphur\n   plays a part analogous to that of oxygen in an oxyacid; thus,\n   thiosulphuric and sulpharsenic acids are sulphacids; -- called also\n   sulphoacid. See the Note under Acid, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphamate', 'n.', 'A salt of sulphamic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphamic', 'a.', 'Of or pertaining to a sulphamide; derived from, or\n   related to, a sulphamide; specifically, designating an amido acid\n   derivative, NH2.SO2.OH, of sulphuric acid (analogous to sulphonic acid)\n   which is not known in the free state, but is known in its salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphamide', 'n.', 'Any one of a series of amido compounds obtained by\n   treating sulphuryl chloride with various amines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphanilic', 'a.', 'Of, pertaining to, or designating, an anilene\n   sulphonic acid which is obtained as a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphantimonate', 'n.', 'A salt of sulphantimonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphantimonic', 'a.', 'Of, pertaining to, or designating, a hypothetical\n   sulphacid of antimony (called also thioantimonic acid) analogous to\n   sulpharsenic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphantimonious', 'a.', 'Of, pertaining to, or designating, a hypothetical\n   sulphacid of antimony (called also thioantimonious acid) analogous to\n   sulpharsenious acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphantimonite', 'n.', 'A salt of sulphantimonious acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulpharsenate', 'n.', 'A salt of sulpharsenic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulpharsenic', 'a.', 'Of, pertaining to, or designating, a hypothetical\n   sulphacid (called also thioarsenic acid) analogous to arsenic acid, and\n   known only in its salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulpharsenious', 'a.', 'Of, pertaining to, or designating, a hypothetical\n   sulphacid (called also thioarsenious acid) analogous to arsenious acid,\n   and known only in its salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulpharsenite', 'n.', 'A salt of sulpharsenious acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphate', 'n.', 'A salt of sulphuric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphatic', 'a.', 'Of, pertaining to, resembling, or containing, a sulphate\n   or sulphates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphato-', '', 'A combining form (also used adjectively) denoting a\n   sulphate as an ingredient in certain double salts; as,\n   sulphato-carbonate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphaurate', 'n.', 'A salt of sulphauric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphauric', 'a.', 'Of, pertaining to, or designating, a hypothetical\n   sulphacid of gold (aurum), known only in its salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphide', 'n.', 'A binary compound of sulphur, or one so regarded; --\n   formerly called sulphuret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphinate', 'n.', 'A salt of a sulphinic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphindigotic', 'a.', 'Of, pertaining to, or designating, a sulphonic acid\n   obtained, as a blue solution, by dissolving indigo in sulphuric acid;\n   -- formerly called also cerulic sulphuric acid, but properly called\n   indigo-disulphonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphine', 'n.', 'Any one of a series of basic compounds which consist\n   essentially of sulphur united with hydrocarbon radicals. In general\n   they are oily or crystalline deliquescent substances having a peculiar\n   odor; as, trimethyl sulphine, (CH3)3S.OH. Cf. Sulphonium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphinic', 'a.', 'Of, pertaining to, or designating, any one of a series\n   of acids regarded as acid ethereal salts of hyposulphurous acid; as,\n   methyl sulphinic acid, CH3.SO.OH, a thick unstable liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphinide', 'n.', 'A white or yellowish crystalline substance,\n   C6H4.(SO2.CO).NH, produced artificially by the oxidation of a sulphamic\n   derivative of toluene. It is the sweetest substance known, having over\n   two hundred times the sweetening power of sugar, and is known in\n   commerce under the name of saccharine. It has acid properties and forms\n   salts (which are inaccurately called saccharinates).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphion', 'n.', 'A hypothetical radical, SO4, regarded as forming the acid\n   or negative constituent of sulphuric acid and the sulphates in\n   electrolytic decomposition; -- so called in accordance with the binary\n   theory of salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphionide', 'n.', 'A binary compound of sulphion, or one so regarded;\n   thus, sulphuric acid, H/SO/, is a sulphionide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphite', 'n.', 'A salt of sulphurous acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulpho-', '', 'A prefix (also used adjectively) designating sulphur as an\n   ingredient in certain compounds. Cf. Thio-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphoarsenic', 'a.', 'Of, pertaining to, or containing, sulphur and\n   arsenic; -- said of an acid which is the same as arsenic acid with the\n   substitution of sulphur for oxygen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphocarbonate', 'n.', 'A salt of sulphocarbonic acid; a thiocarbonate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphocarbonic', 'a.', 'Of, pertaining to, or designating, a sulphacid,\n   H2CSO2 (called also thiocarbonic acid), or an acid, H2CS3, analogous to\n   carbonic acid, obtained as a yellow oily liquid of a pungent odor, and\n   forming salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphocyanate', 'n.', 'A salt of sulphocyanic acid; -- also called\n   thiocyanate, and formerly inaccurately sulphocyanide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphocyanic', 'a.', 'Of, pertaining to, derived from, or designating, a\n   sulphacid, HSCN, analogous to cyanic acid, and obtained as a colorless\n   deliquescent crystalline substance, having a bitter saline taste, and\n   not poisonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphocyanide', 'n.', 'See Sulphocyanate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphocyanogen', 'n.', 'See Persulphocyanogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphonal', 'n.', 'A substance employed as a hypnotic, produced by the\n   union of mercaptan and acetone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphonate', 'n.', 'A salt of sulphonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphone', 'n.', 'Any one of a series of compounds analogous to the\n   ketones, and consisting of the sulphuryl group united with two\n   hydrocarbon radicals; as, dimethyl sulphone, (CH/)/.SO/.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphonic', 'a.', 'Pertaining to, or derived from, a sulphone; -- used\n   specifically to designate any one of a series of acids (regarded as\n   acid ethereal salts of sulphurous acid) obtained by the oxidation of\n   the mercaptans, or by treating sulphuric acid with certain aromatic\n   bases (as benzene); as, phenyl sulphonic acid, C6H5.SO2.OH, a stable\n   colorless crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphonium', 'n.', 'A hypothetical radical, SH3, regarded as the type and\n   nucleus of the sulphines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphophosphate', 'n.', 'A salt of sulphophosphoric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphophosphite', 'n.', 'A salt of sulphophosphorous acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphophosphoric', 'a.', 'Of, pertaining to, or designating, a hypothetical\n   sulphacid of phosphorus, analogous to phosphoric acid, and known in its\n   salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphophosphorous', 'a.', 'Of, pertaining to, or designating, a\n   hypothetical acid of phosphorus, analogous to phosphorous acid, and\n   known in its salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphosalt', 'n.', 'A salt of a sulphacid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphostannate', 'n.', 'A salt of sulphostannic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphostannic', 'a.', 'Of, pertaining to, or designating, a sulphacid of\n   tin (more exactly called metasulphostannic acid), which is obtained as\n   a dark brown amorphous substance, H/SnS/, forming a well-known series\n   of salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphotungstate', 'n.', 'A salt of sulphotungstic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphotungstic', 'a.', 'Of, pertaining to, or designating, hypothetical\n   sulphacid of tungsten (called also sulphowolframic acid), analogous to\n   sulphuric acid, and known in its salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphovinic', 'a.', 'Of, pertaining to, and formerly designating,\n   ethylsulphuric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphur', 'n.', 'A nonmetallic element occurring naturally in large\n   quantities, either combined as in the sulphides (as pyrites) and\n   sulphates (as gypsum), or native in volcanic regions, in vast beds\n   mixed with gypsum and various earthy materials, from which it is melted\n   out. Symbol S. Atomic weight 32. The specific gravity of ordinary\n   octohedral sulphur is 2.05; of prismatic sulphur, 1.96.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphur', 'n.', 'Any one of numerous species of yellow or orange\n   butterflies of the subfamily Pierinae; as, the clouded sulphur\n   (Eurymus, / Colias, philodice), which is the common yellow butterfly of\n   the Eastern United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphurate', 'a.', 'Sulphureous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphurated', 'imp. & p. p.', 'of Sulphurate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphurating', 'p. pr. & vb. n.', 'of Sulphurate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphurate', 'v. t.', 'To sulphurize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphuration', 'n.', 'The act or process of combining or impregnating with\n   sulphur or its compounds; also, the state of being so combined or\n   impregnated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphurator', 'n.', 'An apparatus for impregnating with, or exposing to the\n   action of, sulphur; especially, an apparatus for fumigating or\n   bleaching by means of the fumes of burning sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphur-bottom', 'n.', 'A very large whalebone whale of the genus\n   Sibbaldius, having a yellowish belly; especially, S. sulfureus of the\n   North Pacific, and S. borealis of the North Atlantic; -- called also\n   sulphur whale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphureity', 'n.', 'The quality or state of being sulphureous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphureous', 'a.', 'Consisting of sulphur; having the qualities of\n   sulphur, or brimstone; impregnated with sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphuret', 'n.', 'A sulphide; as, a sulphuret of potassium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphureted', 'a.', 'Combined or impregnated with sulphur; sulphurized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphuric', 'a.', 'Of or pertaining to sulphur; as, a sulphuric smell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphuric', 'a.', 'Derived from, or containing, sulphur; specifically,\n   designating those compounds in which the element has a higher valence\n   as contrasted with the sulphurous compounds; as, sulphuric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphurine', 'a.', 'Sulphureous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphuring', 'n.', 'Exposure to the fumes of burning sulphur, as in\n   bleaching; the process of bleaching by exposure to the fumes of\n   sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphurize', 'v. t.', 'To combine or impregnate with sulphur or any of its\n   compounds; as, to sulphurize caoutchouc in vulcanizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphurous', 'a.', 'Of or pertaining to sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphurous', 'a.', 'Derived from, or containing, sulphur; specifically,\n   designating those compounds in which the element has a lower valence as\n   contrasted with the sulphuric compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphurous', 'a.', 'Having the characteristic odor of sulphur dioxide, or\n   of hydrogen sulphide, or of other sulphur compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphurwort', 'n.', 'The hogs fennel. See under Fennel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphury', 'a.', 'Resembling, or partaking of the nature of, sulphur;\n   having the qualities of sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphuryl', 'n.', 'The hypothetical radical SO2; -- called also sulphon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphydrate', 'n.', 'A compound, analogous to a hydrate, regarded as a salt\n   of sulphydric acid, or as a derivative of hydrogen sulphide in which\n   one half of the hydrogen is replaced by a base (as potassium\n   sulphydrate, KSH), or as a hydrate in which the oxygen has been wholly\n   or partially replaced by sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulphydric', 'a.', 'Of, pertaining to, or designating, hydrogen sulphide,\n   which is regarded as an acid, especially when in solution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sulpician', 'n.', 'One of an order of priests established in France in 1642\n   to educate men for the ministry. The order was introduced soon\n   afterwards into Canada, and in 1791 into the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sultan', 'n.', 'A ruler, or sovereign, of a Mohammedan state; specifically,\n   the ruler of the Turks; the Padishah, or Grand Seignior; -- officially\n   so called.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sultana', 'n.', 'The wife of a sultan; a sultaness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sultana', 'n.', 'A kind of seedless raisin produced near Smyrna in Asiatic\n   Turkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sultanate', 'n.', 'The rule or dominion of a sultan; sultanship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sultaness', 'n.', 'A sultana.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sultanic', 'a.', 'Pertaining to a sultan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sultan-red', 'a.', 'Having a deep red color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sultanry', 'n.', 'The dominions of a sultan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sultanship', 'n.', 'The office or dignity of a sultan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sultany', 'n.', 'Sultanry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sultryly', 'adv.', 'In a sultry manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sultriness', 'n.', 'The quality or state of being sultry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sultry', 'superl.', 'Very hot, burning, and oppressive; as, Libyas sultry\n   deserts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sultry', 'superl.', 'Very hot and moist, or hot, close, stagnant, and\n   oppressive, as air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sum', 'n.', 'The aggregate of two or more numbers, magnitudes, quantities,\n   or particulars; the amount or whole of any number of individuals or\n   particulars added together; as, the sum of 5 and 7 is 12.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sum', 'n.', 'A quantity of money or currency; any amount, indefinitely; as,\n   a sum of money; a small sum, or a large sum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sum', 'n.', 'The principal points or thoughts when viewed together; the\n   amount; the substance; compendium; as, this is the sum of all the\n   evidence in the case; this is the sum and substance of his objections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sum', 'n.', 'Height; completion; utmost degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sum', 'n.', 'A problem to be solved, or an example to be wrought out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summed', 'imp. & p. p.', 'of Sum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summing', 'p. pr. & vb. n.', 'of Sum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sum', 'v. t.', 'To bring together into one whole; to collect into one\n   amount; to cast up, as a column of figures; to ascertain the totality\n   of; -- usually with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sum', 'v. t.', 'To bring or collect into a small compass; to comprise in a\n   few words; to condense; -- usually with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sum', 'v. t.', 'To have (the feathers) full grown; to furnish with\n   complete, or full-grown, plumage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumac', 'n.', 'Alt. of Sumach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumach', 'n.', 'Any plant of the genus Rhus, shrubs or small trees with\n   usually compound leaves and clusters of small flowers. Some of the\n   species are used in tanning, some in dyeing, and some in medicine. One,\n   the Japanese Rhus vernicifera, yields the celebrated Japan varnish, or\n   lacquer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumach', 'n.', 'The powdered leaves, peduncles, and young branches of\n   certain species of the sumac plant, used in tanning and dyeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumatran', 'a.', 'Of or pertaining to Sumatra or its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumatran', 'n.', 'A native of Sumatra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumbul', 'n.', 'The musky root of an Asiatic umbelliferous plant, Ferula\n   Sumbul. It is used in medicine as a stimulant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumless', 'a.', 'Not to be summed up or computed; so great that the amount\n   can not be ascertained; incalculable; inestimable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summarily', 'adv.', 'In a summary manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summarist', 'n.', 'One who summarized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summarize', 'v. t.', 'To comprise in, or reduce to, a summary; to present\n   briefly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summary', 'a.', 'Formed into a sum; summed up; reduced into a narrow\n   compass, or into few words; short; brief; concise; compendious; as, a\n   summary statement of facts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summary', 'a.', 'Hence, rapidly performed; quickly executed; as, a summary\n   process; to take summary vengeance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summaries', 'pl. ', 'of Summary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summary', 'a.', 'A general or comprehensive statement; an abridged account;\n   an abstract, abridgment, or compendium, containing the sum or substance\n   of a fuller account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summation', 'v. t.', 'The act of summing, or forming a sum, or total\n   amount; also, an aggregate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summer', 'v.', 'One who sums; one who casts up an account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summer', 'n.', 'A large stone or beam placed horizontally on columns,\n   piers, posts, or the like, serving for various uses. Specifically: (a)\n   The lintel of a door or window. (b) The commencement of a cross vault.\n   (c) A central floor timber, as a girder, or a piece reaching from a\n   wall to a girder. Called also summertree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summer', 'n.', 'The season of the year in which the sun shines most\n   directly upon any region; the warmest period of the year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summered', 'imp. & p. p.', 'of Summer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summering', 'p. pr. & vb. n.', 'of Summer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summer', 'v. i.', 'To pass the summer; to spend the warm season; as, to\n   summer in Switzerland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summer', 'v. t.', 'To keep or carry through the summer; to feed during the\n   summer; as, to summer stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summer-fallow', 'v. t.', 'To plow and work in summer, in order to prepare\n   for wheat or other crop; to plow and let lie fallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summerhouses', 'pl. ', 'of Summerhouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summerhouse', 'n.', 'A rustic house or apartment in a garden or park, to be\n   used as a pleasure resort in summer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summerliness', 'n.', 'The quality or state of being like summer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summersault', 'n.', 'Alt. of Summerset');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summerset', 'n.', 'See Somersault, Somerset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summerstir', 'v. t.', 'To summer-fallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summertide', 'n.', 'Summer time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summertree', 'n.', 'A summer. See 2d Summer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summery', 'a.', 'Of or pertaining to summer; like summer; as, a summery\n   day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summist', 'n.', 'One who sums up; one who forms an abridgment or summary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summit', 'n.', 'The top; the highest point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summit', 'n.', 'The highest degree; the utmost elevation; the acme; as, the\n   summit of human fame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summit', 'n.', 'The most elevated part of a bivalve shell, or the part in\n   which the hinge is situated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summitless', 'a.', 'Having no summit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summity', 'n.', 'The height or top of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summity', 'n.', 'The utmost degree; perfection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summoned', 'imp. & p. p.', 'of Summon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summoning', 'p. pr. & vb. n.', 'of Summon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summon', 'v. t.', 'To call, bid, or cite; to notify to come to appear; --\n   often with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summon', 'v. t.', 'To give notice to, or command to appear, as in court; to\n   cite by authority; as, to summon witnesses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summon', 'v. t.', 'To call upon to surrender, as a fort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summoner', 'v. t.', 'One who summons; one who cites by authority;\n   specifically, a petty officer formerly employed to summon persons to\n   appear in court; an apparitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summonses', 'pl. ', 'of Summons');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summons', 'v.', 'The act of summoning; a call by authority, or by the\n   command of a superior, to appear at a place named, or to attend to some\n   duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summons', 'v.', 'A warning or citation to appear in court; a written\n   notification signed by the proper officer, to be served on a person,\n   warning him to appear in court at a day specified, to answer to the\n   plaintiff, testify as a witness, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summons', 'v.', 'A demand to surrender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Summons', 'v. t.', 'To summon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumner', 'n.', 'A summoner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumoom', 'n.', 'See Simoom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sump', 'n.', 'A round pit of stone, lined with clay, for receiving the\n   metal on its first fusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sump', 'n.', 'The cistern or reservoir made at the lowest point of a mine,\n   from which is pumped the water which accumulates there.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sump', 'n.', 'A pond of water for salt works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sump', 'n.', 'A puddle or dirty pool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumph', 'n.', 'A dunce; a blockhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumpitan', 'n.', 'A kind of blowgun for discharging arrows, -- used by the\n   savages of Borneo and adjacent islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumpter', 'n.', 'The driver of a pack horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumpter', 'n.', 'A pack; a burden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumpter', 'n.', 'An animal, especially a horse, that carries packs or\n   burdens; a baggage horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumpter', 'a.', 'Carrying pack or burdens on the back; as, a sumpter horse;\n   a sumpter mule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumption', 'n.', 'A taking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumption', 'n.', 'The major premise of a syllogism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumptuary', 'a.', 'Relating to expense; regulating expense or expenditure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumptuosity', 'n.', 'Expensiveness; costliness; sumptuousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sumptuous', 'a.', 'Involving large outlay or expense; costly; expensive;\n   hence, luxurious; splendid; magnificient; as, a sumptuous house or\n   table; sumptuous apparel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sun', 'n.', 'See Sunn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sun', 'n.', 'The luminous orb, the light of which constitutes day, and its\n   absence night; the central body round which the earth and planets\n   revolve, by which they are held in their orbits, and from which they\n   receive light and heat. Its mean distance from the earth is about\n   92,500,000 miles, and its diameter about 860,000.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sun', 'n.', 'Any heavenly body which forms the center of a system of orbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sun', 'n.', 'The direct light or warmth of the sun; sunshine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sun', 'n.', 'That which resembles the sun, as in splendor or importance;\n   any source of light, warmth, or animation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunned', 'imp. & p. p.', 'of Sun');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunning', 'p. pr. & vb. n.', 'of Sun');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sun', 'v. t.', 'To expose to the suns rays; to warm or dry in the sun; as,\n   to sun cloth; to sun grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunbeam', 'n.', 'A beam or ray of the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunbird', 'n.', 'Any one of numerous species of small brilliantly colored\n   birds of the family Nectariniidae, native of Africa, Southern Asia, the\n   East Indies, and Australia. In external appearance and habits they\n   somewhat resemble humming birds, but they are true singing birds\n   (Oscines).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunbird', 'n.', 'The sun bittern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunblink', 'n.', 'A glimpse or flash of the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunbonnet', 'n.', 'A bonnet, generally made of some thin or light fabric,\n   projecting beyond the face, and commonly having a cape, -- worn by\n   women as a protection against the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunbow', 'n.', 'A rainbow; an iris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunburned', 'imp. & p. p.', 'of Sunburn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunburnt', '', 'of Sunburn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunburning', 'p. pr. & vb. n.', 'of Sunburn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunburn', 'v. t.', 'To burn or discolor by the sun; to tan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunburn', 'n.', 'The burning or discoloration produced on the skin by the\n   heat of the sun; tan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sun-burner', 'n.', 'A circle or cluster of gas-burners for lighting and\n   ventilating public buildings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunburning', 'n.', 'Sunburn; tan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunburst', 'n.', 'A burst of sunlight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sundart', 'n.', 'Sunbeam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunday', 'n.', 'The first day of the week, -- consecrated among Christians\n   to rest from secular employments, and to religious worship; the\n   Christian Sabbath; the Lords Day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunday', 'a.', 'Belonging to the Christian Sabbath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sundered', 'imp. & p. p.', 'of Sunder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sundering', 'p. pr. & vb. n.', 'of Sunder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunder', 'v. t.', 'To disunite in almost any manner, either by rending,\n   cutting, or breaking; to part; to put or keep apart; to separate; to\n   divide; to sever; as, to sunder a rope; to sunder a limb; to sunder\n   friends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunder', 'v. i.', 'To part; to separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunder', 'v. t.', 'A separation into parts; a division or severance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunder', 'v. t.', 'To expose to the sun and wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sundew', 'n.', 'Any plant of the genus Drosera, low bog plants whose leaves\n   are beset with pediceled glands which secrete a viscid fluid that\n   glitters like dewdrops and attracts and detains insects. After an\n   insect is caught, the glands curve inward like tentacles and the leaf\n   digests it. Called also lustwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sundial', 'n.', 'An instrument to show the time of day by means of the\n   shadow of a gnomon, or style, on a plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sundog', 'n.', 'A luminous spot occasionally seen a few degrees from the\n   sun, supposed to be formed by the intersection of two or more halos, or\n   in a manner similar to that of halos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sundown', 'n.', 'The setting of the sun; sunset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sundown', 'n.', 'A kind of broad-brimmed sun hat worn by women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sun-dried', 'a.', 'Dried by the heat of the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sundries', 'n. pl.', 'Many different or small things; sundry things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sundrily', 'adv.', 'In sundry ways; variously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sundry', 'v. t.', 'Several; divers; more than one or two; various.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sundry', 'v. t.', 'Separate; diverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sundrymen', 'pl. ', 'of Sundryman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sundryman', 'n.', 'One who deals in sundries, or a variety of articles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunfish', 'n.', 'A very large oceanic plectognath fish (Mola mola, Mola\n   rotunda, or Orthagoriscus mola) having a broad body and a truncated\n   tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunfish', 'n.', 'Any one of numerous species of perch-like North American\n   fresh-water fishes of the family Centrachidae. They have a broad,\n   compressed body, and strong dorsal spines. Among the common species of\n   the Eastern United States are Lepomis gibbosus (called also bream,\n   pondfish, pumpkin seed, and sunny), the blue sunfish, or dollardee (L.\n   pallidus), and the long-eared sunfish (L. auritus). Several of the\n   species are called also pondfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunfish', 'n.', 'The moonfish, or bluntnosed shiner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunfish', 'n.', 'The opah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunfish', 'n.', 'The basking, or liver, shark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunfish', 'n.', 'Any large jellyfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunflower', 'n.', 'Any plant of the genus Helianthus; -- so called probably\n   from the form and color of its flower, which is large disk with yellow\n   rays. The commonly cultivated sunflower is Helianthus annuus, a native\n   of America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sung', '', 'imp. & p. p. of Sing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunglasses', 'pl. ', 'of Sunglass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunglass', 'n.', 'A convex lens of glass for producing heat by converging\n   the suns rays into a focus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunglow', 'n.', 'A rosy flush in the sky seen after sunset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunk', '', 'imp. & p. p. of Sink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunken', 'a.', 'Lying on the bottom of a river or other water; sunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunless', 'a.', 'Destitute or deprived of the sun or its rays; shaded;\n   shadowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunlight', 'n.', 'The light of the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunlike', 'a.', 'Like or resembling the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunlit', 'a.', 'Lighted by the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunn', 'n.', 'An East Indian leguminous plant (Crotalaria juncea) and its\n   fiber, which is also called sunn hemp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunna', 'n.', 'A collection of traditions received by the orthodox\n   Mohammedans as of equal authority with the Koran.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunniah', 'n.', 'One of the sect of Sunnites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunniness', 'n.', 'The quality or state of being sunny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunnite', 'n.', 'One of the orthodox Mohammedans who receive the Sunna as\n   of equal importance with the Koran.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunnud', 'n.', 'A charter or warrant; also, a deed of gift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunny', 'superl.', 'Of or pertaining to the sun; proceeding from, or\n   resembling the sun; hence, shining; bright; brilliant; radiant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunny', 'superl.', 'Exposed to the rays of the sun; brightened or warmed by\n   the direct rays of the sun; as, a sunny room; the sunny side of a hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunny', 'superl.', 'Cheerful; genial; as, a sunny disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunny', 'n.', 'See Sunfish (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunproof', 'a.', 'Impervious to the rays of the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunrise', 'n.', 'Alt. of Sunrising');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunrising', 'n.', 'The first appearance of the sun above the horizon in the\n   morning; more generally, the time of such appearance, whether in fair\n   or cloudy weather; as, to begin work at sunrise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunrising', 'n.', 'Hence, the region where the sun rises; the east.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunset', 'n.', 'Alt. of Sunsetting');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunsetting', 'n.', 'The descent of the sun below the horizon; also, the\n   time when the sun sets; evening. Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunsetting', 'n.', 'Hence, the region where the sun sets; the west.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunshade', 'n.', 'Anything used as a protection from the suns rays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunshade', 'n.', 'A small parasol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunshade', 'n.', 'An awning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunshine', 'n.', 'The light of the sun, or the place where it shines; the\n   direct rays of the sun, the place where they fall, or the warmth and\n   light which they give.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunshine', 'n.', 'Anything which has a warming and cheering influence like\n   that of the rays of the sun; warmth; illumination; brightness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunshine', 'a.', 'Sunshiny; bright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunshiny', 'a.', 'Bright with the rays of the sun; clear, warm, or\n   pleasant; as, a sunshiny day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunshiny', 'a.', 'Bright like the sun; resplendent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunshiny', 'a.', 'Beaming with good spirits; cheerful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunsquall', 'n.', 'Any large jellyfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sun star', '', 'See Sun star, under Sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunsted', 'n.', 'Solstice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunstone', 'n.', 'Aventurine feldspar. See under Aventurine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunstroke', 'n.', 'Any affection produced by the action of the sun on some\n   part of the body; especially, a sudden prostration of the physical\n   powers, with symptoms resembling those of apoplexy, occasioned by\n   exposure to excessive heat, and often terminating fatally; coup de\n   soleil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sun-struck', 'a.', 'Overcome by, or affected with, sunstroke; as,\n   sun-struck soldiers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunup', 'n.', 'Sunrise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunward', 'adv.', 'Toward the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sunwise', 'adv.', 'In the direction of the suns apparent motion, or from\n   the east southward and westward, and so around the circle; also, in the\n   same direction as the movement of the hands of a watch lying face\n   upward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supped', 'imp. & p. p.', 'of Sup');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supping', 'p. pr. & vb. n.', 'of Sup');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sup', 'v. t.', 'To take into the mouth with the lips, as a liquid; to take\n   or drink by a little at a time; to sip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sup', 'n.', 'A small mouthful, as of liquor or broth; a little taken with\n   the lips; a sip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sup', 'v. i.', 'To eat the evening meal; to take supper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sup', 'v. t.', 'To treat with supper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supawn', 'n.', 'Boiled Indian meal; hasty pudding; mush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supe', 'n.', 'A super.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Super-', '', 'A prefix signifying above, over, beyond, and hence often\n   denoting in a superior position, in excess, over and above, in\n   addition, exceedingly; as in superimpose, supersede, supernatural,\n   superabundance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Super-', '', 'A prefix formerly much used to denote that the ingredient to\n   the name of which it was prefixed was present in a large, or unusually\n   large, proportion as compared with the other ingredients; as in calcium\n   superphosphate. It has been superseded by per-, bi-, di-, acid, etc.\n   (as peroxide, bicarbonate, disulphide, and acid sulphate), which retain\n   the old meanings of super-, but with sharper definition. Cf. Acid, a.,\n   Bi-, Di-, and Per-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Super', 'n.', 'A contraction of Supernumerary, in sense 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superable', 'a.', 'Capable of being overcome or conquered; surmountable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superabound', 'v. i.', 'To be very abundant or exuberant; to be more than\n   sufficient; as, the country superabounds with corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superabundance', 'n.', 'The quality or state of being superabundant; a\n   superabundant quantity; redundancy; excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superabundant', 'a.', 'Abounding to excess; being more than is sufficient;\n   redundant; as, superabundant zeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superacidulated', 'a.', 'Acidulated to excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superadded', 'imp. & p. p.', 'of Superadd');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superadding', 'p. pr. & vb. n.', 'of Superadd');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superadd', 'v. t.', 'To add over and above; to add to what has been added;\n   to annex, as something extrinsic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superaddition', 'n.', 'The act of adding something in excess or something\n   extraneous; also, something which is added in excess or extraneously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superadvenient', 'a.', 'Coming upon; coming in addition to, or in\n   assistance of, something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superalimentation', 'n.', 'The act of overfeeding, or making one take food\n   in excess of the natural appetite for it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superaltar', 'n.', 'A raised shelf or stand on the back of an altar, on\n   which different objects can be placed; a predella or gradino.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superangelic', 'a.', 'Superior to the angels in nature or rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superannuated', 'imp. & p. p.', 'of Superannuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superannuating', 'p. pr. & vb. n.', 'of Superannuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superannuate', 'v. t.', 'To impair or disquality on account of age or\n   infirmity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superannuate', 'v. t.', 'To give a pension to, on account of old age or\n   other infirmity; to cause to retire from service on a pension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superannuate', 'v. i.', 'To last beyond the year; -- said of annual plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superannuation', 'n.', 'The state of being superannuated, or too old for\n   office or business; the state of being disqualified by old age;\n   decrepitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superb', 'a.', 'Grand; magnificent; august; stately; as, a superb edifice;\n   a superb colonnade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superb', 'a.', 'Rich; elegant; as, superb furniture or decorations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superb', 'a.', 'Showy; excellent; grand; as, a superb exhibition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superbiate', 'v. t.', 'To make (a person) haughty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supercarbonate', 'n.', 'A bicarbonate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supercarbureted', 'a.', 'Bicarbureted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supercargo', 'n.', 'An officer or person in a merchant ship, whose duty is\n   to manage the sales, and superintend the commercial concerns, of the\n   voyage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supercarpal', 'a.', 'Situated above, or in the upper part of, the carpus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supercelestial', 'a.', 'Situated above the firmament, or great vault of\n   heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supercelestial', 'a.', 'Higher than celestial; superangelic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supercharged', 'imp. & p. p.', 'of Supercharge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supercharging', 'p. pr. & vb. n.', 'of Supercharge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supercharge', 'v. t.', 'To charge (a bearing) upon another bearing; as, to\n   supercharge a rose upon a fess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supercharge', 'n.', 'A bearing charged upon another bearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superchemical', 'a.', 'Above or beyond chemistry; inexplicable by chemical\n   laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superchery', 'n.', 'Deceit; fraud; imposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superciliary', 'a.', 'Of or pertaining to the eyebrows; supraorbital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superciliary', 'a.', 'Having a distinct streak of color above the eyes; as,\n   the superciliary woodpecker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supercilious', 'a.', 'Lofty with pride; haughty; dictatorial; overbearing;\n   arrogant; as, a supercilious officer; asupercilious air; supercilious\n   behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supercilium', 'n.', 'The eyebrow, or the region of the eyebrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supercolumniation', 'n.', 'The putting of one order above another; also, an\n   architectural work produced by this method; as, the putting of the\n   Doric order in the ground story, Ionic above it, and Corinthian or\n   Composite above this.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superconception', 'n.', 'Superfetation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superconsequence', 'n.', 'Remote consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supercrescence', 'n.', 'That which grows upon another growing thing; a\n   parasite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supercrescent', 'a.', 'Growing on some other growing thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supercretaceous', 'a.', 'Same as Supracretaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supercurious', 'a.', 'Excessively curious or inquisitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superdominant', 'n.', 'The sixth tone of the scale; that next above the\n   dominant; -- called also submediant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supereminence', 'n.', 'Alt. of Supereminency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supereminency', 'n.', 'The quality or state of being supereminent;\n   distinguished eminence; as, the supereminence of Cicero as an orator,\n   or Lord Chatham as a statesman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supereminent', 'a.', 'Eminent in a superior degree; surpassing others in\n   excellence; as, a supereminent divine; the supereminent glory of\n   Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supererogant', 'a.', 'Supererogatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supererogated', 'imp. & p. p.', 'of Supererogate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supererogating', 'p. pr. & vb. n.', 'of Supererogate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supererogate', 'v. i.', 'To do more than duty requires; to perform works of\n   supererogation; to atone (for a dificiency in another) by means of a\n   surplus action or quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supererogation', 'n.', 'The act of supererogating; performance of more than\n   duty or necessity requires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supererogative', 'a.', 'Supererogatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supererogatory', 'a.', 'Performed to an extent not enjoined, or not\n   required, by duty or necessity; as, supererogatory services.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superessential', 'a.', 'Essential above others, or above the constitution\n   of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superethical', 'a.', 'More than ethical; above ethics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superexalted', 'imp. & p. p.', 'of Superexalt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superexalting', 'p. pr. & vb. n.', 'of Superexalt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superexalt', 'v. t.', 'To exalt to a superior degree; to exalt above\n   others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superexaltation', 'n.', 'Elevation above the common degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superexcellence', 'n.', 'Superior excellence; extraordinary excellence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superexcellent', 'a.', 'Excellent in an uncommon degree; very excellent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superexcination', 'n.', 'Excessive, or more than normal, excitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superexcrescence', 'n.', 'Something growing superfluously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfamily', 'n.', 'A group intermediate between a family and a suborder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfecundation', 'n.', 'Fertilization of two ova, at the same\n   menstruation, by two different acts of coition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfecundity', 'n.', 'Superabundant fecundity or multiplication of the\n   species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfetate', 'v. i.', 'To conceive after a prior conception, but before\n   the birth of the offspring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfetation', 'n.', 'The formation of a fetus at the result of an\n   impregnation occurring after another impregnation but before the birth\n   of the offspring produced by it. This is possible only when there is a\n   double uterus, or where menstruation persists up to the time of the\n   second impregnation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfete', 'v. i.', 'To superfetate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfete', 'v. t.', 'To conceive (another fetus) after a former\n   conception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfice', 'n.', 'A superficies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superficial', 'a.', 'Of or pertaining to the superficies, or surface; lying\n   on the surface; shallow; not deep; as, a superficial color; a\n   superficial covering; superficial measure or contents; superficial\n   tillage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superficial', 'a.', 'Reaching or comprehending only what is obvious or\n   apparent; not deep or profound; shallow; -- said especially in respect\n   to study, learning, and the like; as, a superficial scholar;\n   superficial knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superficialist', 'n.', 'One who attends to anything superficially; a\n   superficial or shallow person; a sciolist; a smatterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superficiality', 'n.', 'The quality or state of being superficial; also,\n   that which is superficial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superficialize', 'v. t.', 'To attend to, or to treat, superficially, or in\n   a shallow or slighting way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superficiary', 'n.', 'One to whom a right of surface occupation is granted;\n   one who pays quitrent for a house built upon another mans ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superficiary', 'a.', 'Of or pertaining to the superficies, or surface;\n   superficial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superficiary', 'a.', 'Situated or built on another mans land, as a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superficies', 'n.', 'The surface; the exterior part, superficial area, or\n   face of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superficies', 'n.', 'Everything on the surface of a piece of ground, or of\n   a building, so closely connected by art or nature as to constitute a\n   part of it, as houses, or other superstructures, fences, trees, vines,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superficies', 'n.', 'A real right consisting of a grant by a landed\n   proprietor of a piece of ground, bearing a strong resemblance to the\n   long building leases granted by landholders in England, in\n   consideration of a rent, and under reservation of the ownership of the\n   soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfine', 'a.', 'Very fine, or most fine; being of surpassing fineness;\n   of extra nice or fine quality; as, superfine cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfine', 'a.', 'Excessively fine; too nice; over particular; as,\n   superfine distinctions; superfine tastes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfineness', 'n.', 'The state of being superfine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfinical', 'a.', 'Extremely finical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfluence', 'n.', 'Superfluity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfluitant', 'a.', 'Floating above or on the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfluities', 'pl. ', 'of Superfluity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfluity', 'n.', 'A greater quantity than is wanted; superabundance; as,\n   a superfluity of water; a superfluity of wealth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfluity', 'n.', 'The state or quality of being superfluous; excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfluity', 'n.', 'Something beyond what is needed; something which\n   serves for show or luxury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfluous', 'a.', 'More than is wanted or is sufficient; rendered\n   unnecessary by superabundance; unnecessary; useless; excessive; as, a\n   superfluous price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superflux', 'n.', 'Superabundance; superfluity; an overflowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfoetation', 'n.', 'Superfetation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfoliation', 'n.', 'Excess of foliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfrontal', 'n.', 'A cloth which is placed over the top of an altar, and\n   often hangs down a few inches over the frontal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superfuse', 'a.', 'To pour (something) over or on something else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superheated', 'imp. & p. p.', 'of Superheat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superheating', 'p. pr. & vb. n.', 'of Superheat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superheat', 'v. t.', 'To heat too much, to overheat; as, to superheat an\n   oven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superheat', 'v. t.', 'To heat, as steam, apart from contact with water,\n   until it resembles a perfect gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superheat', 'n.', 'The increase of temperature communicated to steam by\n   superheating it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superheater', 'n.', 'An apparatus for superheating steam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superhive', 'n.', 'A removable upper part of a hive. The word is sometimes\n   contracted to super.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superhuman', 'a.', 'Above or beyond what is human; sometimes, divine; as,\n   superhuman strength; superhuman wisdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superimposed', 'imp. & p. p.', 'of Superimpose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superimposing', 'p. pr. & vb. n.', 'of Superimpose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superimpose', 'v. t.', 'To lay or impose on something else; as, a stratum\n   of earth superimposed on another stratum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superinpregnation', 'n.', 'The act of impregnating, or the state of being\n   impregnated, in addition to a prior impregnation; superfetation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superincumbence', 'n.', 'Alt. of Superincumbency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superincumbency', 'n.', 'The quality or state of being superincumbent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superincumbent', 'a.', 'Lying or resting on something else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superinduced', 'imp. & p. p.', 'of Superinduce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superinducing', 'p. pr. & vb. n.', 'of Superinduce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superinduce', 'v. t.', 'To bring in, or upon, as an addition to something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superinducement', 'n.', 'Superinduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superinduction', 'n.', 'The act of superinducing, or the state of being\n   superinduced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superinfuse', 'v. t.', 'To infuse over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superinjection', 'n.', 'An injection succeeding another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superinspect', 'v. t.', 'To over see; to superintend by inspection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superinstitution', 'n.', 'One institution upon another, as when A is\n   instituted and admitted to a benefice upon a title, and B instituted\n   and admitted upon the presentation of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superintellectual', 'a.', 'Being above intellect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superintended', 'imp. & p. p.', 'of Superintend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superintending', 'p. pr. & vb. n.', 'of Superintend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superintend', 'v. t.', 'To have or exercise the charge and oversight of; to\n   oversee with the power of direction; to take care of with authority; to\n   supervise; as, an officer superintends the building of a ship or the\n   construction of a fort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superintendence', 'n.', 'The act of superintending; care and oversight for\n   the purpose of direction; supervision.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-cies', 'pl. ', 'of Superintendency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superintendency', 'n.', 'The act of superintending; superintendence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superintendent', 'a.', 'Overseeing; superintending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superintendent', 'n.', 'One who has the oversight and charge of some place,\n   institution, or organization, affairs, etc., with the power of\n   direction; as, the superintendent of an almshouse; the superintendent\n   of public works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superintender', 'n.', 'A superintendent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superinvestiture', 'n.', 'An outer vestment or garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superior', 'a.', 'More elevated in place or position; higher; upper; as,\n   the superior limb of the sun; the superior part of an image.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superior', 'a.', 'Higher in rank or office; more exalted in dignity; as, a\n   superior officer; a superior degree of nobility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superior', 'a.', 'Higher or greater in excellence; surpassing others in the\n   greatness, or value of any quality; greater in quality or degree; as, a\n   man of superior merit; or of superior bravery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superior', 'a.', 'Beyond the power or influence of; too great or firm to be\n   subdued or affected by; -- with to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superior', 'a.', 'More comprehensive; as a term in classification; as, a\n   genus is superior to a species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superior', 'a.', 'Above the ovary; -- said of parts of the flower which,\n   although normally below the ovary, adhere to it, and so appear to\n   originate from its upper part; also of an ovary when the other floral\n   organs are plainly below it in position, and free from it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superior', 'a.', 'Belonging to the part of an axillary flower which is\n   toward the main stem; posterior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superior', 'a.', 'Pointing toward the apex of the fruit; ascending; -- said\n   of the radicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superior', 'n.', 'One who is above, or surpasses, another in rank, station,\n   office, age, ability, or merit; one who surpasses in what is desirable;\n   as, Addison has no superior as a writer of pure English.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superior', 'n.', 'The head of a monastery, convent, abbey, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superioress', 'n.', 'A woman who acts as chief in a convent, abbey, or\n   nunnery; a lady superior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superiority', 'n.', 'The quality, state, or condition of being superior;\n   as, superiority of rank; superiority in merit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superiorly', 'adv.', 'In a superior position or manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superjacent', 'a.', 'Situated immediately above; as, superjacent rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superlation', 'n.', 'Exaltation of anything beyond truth or propriety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superlative', 'a.', 'Lifted up to the highest degree; most eminent;\n   surpassing all other; supreme; as, superlative wisdom or prudence; a\n   woman of superlative beauty; the superlative glory of the divine\n   character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superlative', 'a.', 'Expressing the highest or lowest degree of the\n   quality, manner, etc., denoted by an adjective or an adverb. The\n   superlative degree is formed from the positive by the use of -est,\n   most, or least; as, highest, most pleasant, least bright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superlative', 'n.', 'That which is highest or most eminent; the utmost\n   degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superlative', 'n.', 'The superlative degree of adjectives and adverbs;\n   also, a form or word by which the superlative degree is expressed; as,\n   strongest, wisest, most stormy, least windy, are all superlatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superlucration', 'n.', 'Excessive or extraordinary gain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superlunar', 'a.', 'Alt. of Superlunary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superlunary', 'a.', 'Being above the moon; not belonging to this world; --\n   opposed to sublunary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supermaterial', 'a.', 'Being above, or superior to, matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supermaxilla', 'n.', 'The supermaxilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supermaxillary', 'a.', 'Supermaxillary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supermedial', 'a.', 'Above the middle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supermundane', 'a.', 'Being above the world; -- opposed to inframundane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supermundial', 'a.', 'Supermundane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernacular', 'a.', 'Like supernaculum; first-rate; as, a supernacular\n   wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernaculum', 'adv. & n.', 'A kind of mock Latin term intended to mean,\n   upon the nail; -- used formerly by topers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernaculum', 'adv. & n.', 'Good liquor, of which not enough is left to\n   wet ones nail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernal', 'a.', 'Being in a higher place or region; locally higher; as,\n   the supernal orbs; supernal regions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernal', 'a.', 'Relating or belonging to things above; celestial;\n   heavenly; as, supernal grace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernatant', 'a.', 'Swimming above; floating on the surface; as, oil\n   supernatant on water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernatant', 'n.', 'The liquid remaining after solids suspended in the\n   liquid have been sedimented by gravity or by centrifugation. Contrasted\n   with the solid sediment, or (in centrifugation) the pellet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernatation', 'n.', 'The act of floating on the surface of a fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernatural', 'a.', 'Being beyond, or exceeding, the power or laws of\n   nature; miraculous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernaturalism', 'n.', 'The quality or state of being supernatural;\n   supernaturalness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernaturalism', 'n.', 'The doctrine of a divine and supernatural agency\n   in the production of the miracles and revelations recorded in the\n   Bible, and in the grace which renews and sanctifies men, -- in\n   opposition to the doctrine which denies the agency of any other than\n   physical or natural causes in the case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernaturalist', 'n.', 'One who holds to the principles of\n   supernaturalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernaturalistic', 'a.', 'Of or pertaining to supernaturalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernaturality', 'n.', 'The quality or state of being supernatural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernaturalize', 'v. t.', 'To treat or regard as supernatural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernaturally', 'adv.', 'In a supernatural manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernaturalness', 'n.', 'The quality or state of being supernatural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernumerary', 'a.', 'Exceeding the number stated or prescribed; as, a\n   supernumerary officer in a regiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernumerary', 'a.', 'Exceeding a necessary, usual, or required number or\n   quality; superfluous; as, supernumerary addresses; supernumerary\n   expense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernumeraries', 'pl. ', 'of Supernumerary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernumerary', 'n.', 'A person or thing beyond the number stated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supernumerary', 'n.', 'A person or thing beyond what is necessary or usual;\n   especially, a person employed not for regular service, but only to fill\n   the place of another in case of need; specifically, in theaters, a\n   person who is not a regular actor, but is employed to appear in a stage\n   spectacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superoccipital', 'a.', 'Supraoccipital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superorder', 'n.', 'A group intermediate in importance between an order and\n   a subclass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superordination', 'n.', 'The ordination of a person to fill a station\n   already occupied; especially, the ordination by an ecclesiastical\n   official, during his lifetime, of his successor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superoxide', 'n.', 'See Peroxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superparticular', 'a.', 'Of or pertaining to a ratio when the excess of the\n   greater term over the less is a unit, as the ratio of 1 to 2, or of 3\n   to 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superpartient', 'a.', 'Of or pertaining to a ratio when the excess of the\n   greater term over the less is more than a unit, as that of 3 to 5, or 7\n   to 10.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superphosphate', 'n.', 'An acid phosphate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superphysical', 'a.', 'Above or beyond physics; not explainable by physical\n   laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superplant', 'n.', 'A plant growing on another, as the mistletoe; an\n   epiphyte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superplease', 'v. t.', 'To please exceedingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superplus', 'n.', 'Surplus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superplusage', 'n.', 'Surplusage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superpolitic', 'a.', 'More than politic; above or exceeding policy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superponderate', 'v. t.', 'To wiegh over and above.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superposable', 'a.', 'Capable of being superposed, as one figure upon\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superposed', 'imp. & p. p.', 'of Superpose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superposing', 'p. pr. & vb. n.', 'of Superpose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superpose', 'v. t.', 'To lay upon, as one kind of rock on another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superpose', 'v. t.', 'To lay (a figure) upon another in such a manner that\n   all the parts of the one coincide with the parts of the other; as, to\n   superpose one plane figure on another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superposition', 'n.', 'The act of superposing, or the state of being\n   superposed; as, the superposition of rocks; the superposition of one\n   plane figure on another, in geometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superpraise', 'v. t.', 'To praise to excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superproportion', 'n.', 'Overplus or excess of proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superpurgation', 'n.', 'Excessive purgation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superreflection', 'n.', 'The reflection of a reflected image or sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superregal', 'a.', 'More than regal; worthy of one greater than a king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superreward', 'v. t.', 'To reward to an excessive degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superroyal', 'a.', 'Larger than royal; -- said of a particular size of\n   printing and writing paper. See the Note under Paper, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersacral', 'a.', 'Situated over, or on the dorsal side of, the sacrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersaliency', 'n.', 'The act of leaping on anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersalient', 'a.', 'Leaping upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersalt', 'n.', 'An acid salt. See Acid salt (a), under Salt, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersaturate', 'v. t.', 'To add to beyond saturation; as, to supersaturate\n   a solution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersaturation', 'n.', 'The operation of supersaturating, or the state of\n   being supersaturated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superscribed', 'imp. & p. p.', 'of Superscribe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superscribing', 'p. pr. & vb. n.', 'of Superscribe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superscribe', 'v. t.', 'To write or engrave (a name, address, inscription,\n   or the like) on the top or surface; to write a name, address, or the\n   like, on the outside or cover of (anything); as, to superscribe a\n   letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superscript', 'n.', 'Superscription.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superscription', 'n.', 'The act of superscribing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superscription', 'n.', 'That which is written or engraved on the surface,\n   outside, or above something else; specifically, an address on a letter,\n   envelope, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superscription', 'n.', 'That part of a prescription which contains the\n   Latin word recipe (Take) or the sign /.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersecular', 'a.', 'Being above the world, or secular things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superseded', 'imp. & p. p.', 'of Supersede');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superseding', 'p. pr. & vb. n.', 'of Supersede');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersede', 'v. t.', 'To come, or be placed, in the room of; to replace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersede', 'v. t.', 'To displace, or set aside, and put another in place\n   of; as, to supersede an officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersede', 'v. t.', 'To make void, inefficacious, or useless, by superior\n   power, or by coming in the place of; to set aside; to render\n   unnecessary; to suspend; to stay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersede', 'v. t.', 'To omit; to forbear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersedeas', 'n.', 'A writ of command to suspend the powers of an officer\n   in certain cases, or to stay proceedings under another writ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersedure', 'n.', 'The act of superseding, or setting aside;\n   supersession; as, the supersedure of trial by jury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superseminate', 'v. t.', 'To sow, as seed, over something previously sown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersemination', 'n.', 'The sowing of seed over seed previously sown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersensible', 'a.', 'Beyond the reach of the senses; above the natural\n   powers of perception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersensitive', 'a.', 'Excessively sensitive; morbidly sensitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersensual', 'a.', 'Supersensible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersensuous', 'a.', 'Supersensible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersensuous', 'a.', 'Excessively sensuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superserviceable', 'a.', 'Overofficious; doing more than is required or\n   desired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersession', 'n.', 'The act of superseding, or the state of being\n   superseded; supersedure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersolar', 'a.', 'Above the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersphenoidal', 'a.', 'Situated above, or on the dorsal side of, the body\n   of the sphenoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superspinous', 'a.', 'Supraspinuos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstition', 'n.', 'An excessive reverence for, or fear of, that which is\n   unknown or mysterious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstition', 'n.', 'An ignorant or irrational worship of the Supreme\n   Deity; excessive exactness or rigor in religious opinions or practice;\n   extreme and unnecessary scruples in the observance of religious rites\n   not commanded, or of points of minor importance; also, a rite or\n   practice proceeding from excess of sculptures in religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstition', 'n.', 'The worship of a false god or gods; false religion;\n   religious veneration for objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstition', 'n.', 'Belief in the direct agency of superior powers in\n   certain extraordinary or singular events, or in magic, omens,\n   prognostics, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstition', 'n.', 'Excessive nicety; scrupulous exactness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstitionist', 'n.', 'One addicted to superstition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstitious', 'a.', 'Of or pertaining to superstition; proceeding from,\n   or manifesting, superstition; as, superstitious rites; superstitious\n   observances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstitious', 'a.', 'Evincing superstition; overscrupulous and rigid in\n   religious observances; addicted to superstition; full of idle fancies\n   and scruples in regard to religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstitious', 'a.', 'Overexact; scrupulous beyond need.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstrain', 'v. t.', 'To overstrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstrata', 'pl. ', 'of Superstatum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstatum', 'n.', 'A stratum, or layer, above another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstruct', 'v. t.', 'To build over or upon another structure; to erect\n   upon a foundation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstruction', 'n.', 'The act of superstructing, or building upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstruction', 'n.', 'That which id superstructed, or built upon some\n   foundation; an edifice; a superstructure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstructive', 'a.', 'Built or erected on something else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstructor', 'n.', 'One who builds a superstructure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstructure', 'n.', 'Any material structure or edifice built on\n   something else; that which is raised on a foundation or basis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstructure', 'n.', 'all that part of a building above the basement.\n   Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superstructure', 'n.', 'The sleepers, and fastenings, in distinction from\n   the roadbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersubstantial', 'a.', 'More than substantial; spiritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersubtle', 'a.', 'To subtle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersulphate', 'n.', 'An acid sulphate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersulphureted', 'a.', 'Supersulphurized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supersulphurize', 'v. t.', 'To impregnate or combine with an excess of\n   sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supertemporal', 'n.', 'That which is more than temporal; that which is\n   eternal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superterranean', 'a.', 'Being above ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superterrene', 'a.', 'Being above ground, or above the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Superterrestrial', 'a.', 'Being above the earth, or above what belongs to\n   the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supertonic', 'n.', 'The note next above the keynote; the second of the\n   scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supertragical', 'a.', 'Tragical to excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supertuberation', 'n.', 'The production of young tubers, as potatoes, from\n   the old while still growing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supervacaneous', 'a.', 'Serving no purpose; superfluous; needless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supervened', 'imp. & p. p.', 'of Supervene');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supervening', 'p. pr. & vb. n.', 'of Supervene');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supervene', 'v. i.', 'To come as something additional or extraneous; to\n   occur with reference or relation to something else; to happen upon or\n   after something else; to be added; to take place; to happen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supervenient', 'a.', 'Coming as something additional or extraneous; coming\n   afterwards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supervention', 'n.', 'The act of supervening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supervisal', 'n.', 'Supervision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supervised', 'imp. & p. p.', 'of Supervise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supervising', 'p. pr. & vb. n.', 'of Supervise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supervise', 'v. t.', 'To oversee for direction; to superintend; to inspect\n   with authority; as, to supervise the construction of a steam engine, or\n   the printing of a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supervise', 'v. t.', 'To look over so as to read; to peruse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supervise', 'n.', 'Supervision; inspection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supervision', 'n.', 'The act of overseeing; inspection; superintendence;\n   oversight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supervisive', 'a.', 'Supervisory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supervisor', 'n.', 'One who supervises; an overseer; an inspector; a\n   superintendent; as, a supervisor of schools.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supervisor', 'n.', 'A spectator; a looker-on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supervisory', 'a.', 'Of or pertaining to supervision; as, supervisory\n   powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supervive', 'v. t.', 'To survive; to outlive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supervolute', 'a.', 'Having a plainted and convolute arrangement in the\n   bud, as in the morning-glory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supination', 'n.', 'The act of turning the hand palm upward; also, position\n   of the hand with the palm upward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supination', 'n.', 'The act or state of lying with the face upward. Opposed\n   to pronation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supinator', 'n.', 'A muscle which produces the motion of supination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supine', 'a.', 'Lying on the back, or with the face upward; -- opposed to\n   prone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supine', 'a.', 'Leaning backward, or inclining with exposure to the sun;\n   sloping; inclined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supine', 'a.', 'Negligent; heedless; indolent; listless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supine', 'n.', 'A verbal noun; or (according to C.F.Becker), a case of the\n   infinitive mood ending in -um and -u, that in -um being sometimes\n   called the former supine, and that in -u the latter supine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supinity', 'n.', 'Supineness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppage', 'n.', 'What may be supped; pottage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppalpation', 'n.', 'The act of enticing by soft words; enticement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supparasitation', 'n.', 'The act of flattering to gain favor; servile\n   approbation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supparasite', 'v. t.', 'To flatter; to cajole; to act the parasite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppawn', 'n.', 'See Supawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppedaneous', 'a.', 'Being under the feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppeditate', 'v. t.', 'To supply; to furnish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppeditation', 'n.', 'Supply; aid afforded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supper', 'n.', 'A meal taken at the close of the day; the evening meal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supper', 'v. i.', 'To take supper; to sup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supper', 'v. t.', 'To supply with supper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supperless', 'a.', 'Having no supper; deprived of supper; as, to go\n   supperless to bed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supping', 'n.', 'The act of one who sups; the act of taking supper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supping', 'n.', 'That which is supped; broth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplace', 'v. t.', 'To replace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplanted', 'imp. & p. p.', 'of Supplant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplanting', 'p. pr. & vb. n.', 'of Supplant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplant', 'n.', 'To trip up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplant', 'n.', 'To remove or displace by stratagem; to displace and take\n   the place of; to supersede; as, a rival supplants another in the favor\n   of a mistress or a prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplant', 'n.', 'To overthrow, undermine, or force away, in order to get a\n   substitute in place of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplantation', 'n.', 'The act of supplanting or displacing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplanter', 'n.', 'One who supplants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supple', 'a.', 'Pliant; flexible; easily bent; as, supple joints; supple\n   fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supple', 'a.', 'Yielding compliant; not obstinate; submissive to guidance;\n   as, a supple horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supple', 'a.', 'Bending to the humor of others; flattering; fawning;\n   obsequious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppled', 'imp. & p. p.', 'of Supple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppling', 'p. pr. & vb. n.', 'of Supple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supple', 'v. t.', 'To make soft and pliant; to render flexible; as, to\n   supple leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supple', 'v. t.', 'To make compliant, submissive, or obedient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supple', 'v. i.', 'To become soft and pliant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supple-chapped', 'a.', 'Having a limber tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supple-jack', 'n.', 'A climbing shrub (Berchemia volubilus) of the Southern\n   United States, having a tough and pliable stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supple-jack', 'n.', 'A somewhat similar tropical American plant (Paullinia\n   Curassavica); also, a walking stick made from its stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplely', 'adv.', 'In a supple manner; softly; pliantly; mildly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplement', 'v. t.', 'That which supplies a deficiency, or meets a want; a\n   store; a supply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplement', 'v. t.', 'That which fills up, completes, or makes an addition\n   to, something already organized, arranged, or set apart; specifically,\n   a part added to, or issued as a continuation of, a book or paper, to\n   make good its deficiencies or correct its errors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplement', 'v. t.', 'The number of degrees which, if added to a specified\n   arc, make it 180; the quantity by which an arc or an angle falls short\n   of 180 degrees, or an arc falls short of a semicircle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplemented', 'imp. & p. p.', 'of Supplement');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplementing', 'p. pr. & vb. n.', 'of Supplement');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplement', 'v. t.', 'To fill up or supply by addition; to add something\n   to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplemental', 'a.', 'Alt. of Supplementary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplementary', 'a.', 'Added to supply what is wanted; additional; being,\n   or serving as, a supplement; as, a supplemental law; a supplementary\n   sheet or volume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplementation', 'n.', 'The act of supplementing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppleness', 'n.', 'The quality or state of being supple; flexibility;\n   pliableness; pliancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppletive', 'a.', 'Alt. of Suppletory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppletory', 'a.', 'Supplying deficiencies; supplementary; as, a suppletory\n   oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppletories', 'pl. ', 'of Suppletory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppletory', 'n.', 'That which is to supply what is wanted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplial', 'n.', 'The act of supplying; a supply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppliance', 'n.', 'That which supplies a want; assistance; a\n   gratification; satisfaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppliance', 'n.', 'Supplication; entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppliant', 'a.', 'Asking earnestly and submissively; entreating;\n   beseeching; supplicating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppliant', 'a.', 'Manifesting entreaty; expressive of supplication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppliant', 'n.', 'One who supplicates; a humble petitioner; one who\n   entreats submissively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplicancy', 'n.', 'Supplication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplicant', 'a.', 'Entreating; asking submissively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplicant', 'n.', 'One who supplicates; a suppliant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplicat', 'n.', 'A petition; esp., a written one, with a certificate that\n   the conditions have been complied with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplicated', 'imp. & p. p.', 'of Supplicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplicating', 'p. pr. & vb. n.', 'of Supplicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplicate', 'v. t.', 'To entreat for; to seek by earnest prayer; to ask\n   for earnestly and humbly; as, to supplicate blessings on Christian\n   efforts to spread the gospel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplicate', 'v. t.', 'To address in prayer; to entreat as a supplicant;\n   as, to supplicate the Deity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplicate', 'v. i.', 'To make petition with earnestness and submission; to\n   implore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplicatingly', 'adv.', 'In a supplicating manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplication', 'n.', 'The act of supplicating; humble and earnest prayer,\n   as in worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplication', 'n.', 'A humble petition; an earnest request; an entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplication', 'n.', 'A religious solemnity observed in consequence of some\n   military success, and also, in times of distress and danger, to avert\n   the anger of the gods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplicator', 'n.', 'One who supplicates; a supplicant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplicatory', 'a.', 'Containing supplication; humble; earnest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplier', 'n.', 'One who supplies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplied', 'imp. & p. p.', 'of Supply');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplying', 'p. pr. & vb. n.', 'of Supply');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supply', 'v. t.', 'To fill up, or keep full; to furnish with what is\n   wanted; to afford, or furnish with, a sufficiency; as, rivers are\n   supplied by smaller streams; an aqueduct supplies an artificial lake;\n   -- often followed by with before the thing furnished; as, to supply a\n   furnace with fuel; to supply soldiers with ammunition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supply', 'v. t.', 'To serve instead of; to take the place of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supply', 'v. t.', 'To fill temporarily; to serve as substitute for another\n   in, as a vacant place or office; to occupy; to have possession of; as,\n   to supply a pulpit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supply', 'v. t.', 'To give; to bring or furnish; to provide; as, to supply\n   money for the war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplies', 'pl. ', 'of Supply');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supply', 'n.', 'The act of supplying; supplial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supply', 'n.', 'That which supplies a want; sufficiency of things for use\n   or want.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supply', 'n.', 'Auxiliary troops or reenforcements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supply', 'n.', 'The food, and the like, which meets the daily necessities\n   of an army or other large body of men; store; -- used chiefly in the\n   plural; as, the army was discontented for lack of supplies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supply', 'n.', 'An amount of money provided, as by Parliament or Congress,\n   to meet the annual national expenditures; generally in the plural; as,\n   to vote supplies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supply', 'n.', 'A person who fills a place for a time; one who supplies the\n   place of another; a substitute; esp., a clergyman who supplies a vacant\n   pulpit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supply', 'a.', 'Serving to contain, deliver, or regulate a supply of\n   anything; as, a supply tank or valve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplyant', 'a.', 'Supplying or aiding; auxiliary; suppletory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supplyment', 'n.', 'A supplying or furnishing; supply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supported', 'imp. & p. p.', 'of Support');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supporting', 'p. pr. & vb. n.', 'of Support');
  INSERT INTO words (word, word_type, description) VALUES  (    'Support', 'v. t.', 'To bear by being under; to keep from falling; to\n   uphold; to sustain, in a literal or physical sense; to prop up; to bear\n   the weight of; as, a pillar supports a structure; an abutment supports\n   an arch; the trunk of a tree supports the branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Support', 'v. t.', 'To endure without being overcome, exhausted, or changed\n   in character; to sustain; as, to support pain, distress, or\n   misfortunes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Support', 'v. t.', 'To keep from failing or sinking; to solace under\n   affictive circumstances; to assist; to encourage; to defend; as, to\n   support the courage or spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Support', 'v. t.', 'To assume and carry successfully, as the part of an\n   actor; to represent or act; to sustain; as, to support the character of\n   King Lear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Support', 'v. t.', 'To furnish with the means of sustenance or livelihood;\n   to maintain; to provide for; as, to support a family; to support the\n   ministers of the gospel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Support', 'v. t.', 'To carry on; to enable to continue; to maintain; as, to\n   support a war or a contest; to support an argument or a debate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Support', 'v. t.', 'To verify; to make good; to substantiate; to establish;\n   to sustain; as, the testimony is not sufficient to support the charges;\n   the evidence will not support the statements or allegations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Support', 'v. t.', 'To vindicate; to maintain; to defend successfully; as,\n   to be able to support ones own cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Support', 'v. t.', 'To uphold by aid or countenance; to aid; to help; to\n   back up; as, to support a friend or a party; to support the present\n   administration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Support', 'v. t.', 'A attend as an honorary assistant; as, a chairman\n   supported by a vice chairman; OConnell left the prison, supported by\n   his two sons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Support', 'n.', 'The act, state, or operation of supporting, upholding, or\n   sustaining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Support', 'n.', 'That which upholds, sustains, or keeps from falling, as a\n   prop, a pillar, or a foundation of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Support', 'n.', 'That which maintains or preserves from being overcome,\n   falling, yielding, sinking, giving way, or the like; subsistence;\n   maintenance; assistance; reenforcement; as, he gave his family a good\n   support, the support of national credit; the assaulting column had the\n   support of a battery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supportable', 'a.', 'Capable of being supported, maintained, or endured;\n   endurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supportance', 'n.', 'Support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supportation', 'n.', 'Maintenance; support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supporter', 'n.', 'One who, or that which, supports; as, oxygen is a\n   supporter of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supporter', 'n.', 'Especially, an adherent; one who sustains, advocates,\n   and defends; as, the supporter of a party, faction, or candidate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supporter', 'n.', 'A knee placed under the cathead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supporter', 'n.', 'A figure, sometimes of a man, but commonly of some\n   animal, placed on either side of an escutcheon, and exterior to it.\n   Usually, both supporters of an escutcheon are similar figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supporter', 'n.', 'A broad band or truss for supporting the abdomen or some\n   other part or organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supportful', 'a.', 'Abounding with support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supportless', 'a.', 'Having no support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supportment', 'n.', 'Support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supportress', 'n.', 'A female supporter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supposable', 'a.', 'Capable of being supposed, or imagined to exist; as,\n   that is not a supposable case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supposal', 'n.', 'The act of supposing; also, that which is supposed;\n   supposition; opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supposed', 'imp. & p. p.', 'of Suppose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supposing', 'p. pr. & vb. n.', 'of Suppose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppose', 'v. t.', 'To represent to ones self, or state to another, not as\n   true or real, but as if so, and with a view to some consequence or\n   application which the reality would involve or admit of; to imagine or\n   admit to exist, for the sake of argument or illustration; to assume to\n   be true; as, let us suppose the earth to be the center of the system,\n   what would be the result?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppose', 'v. t.', 'To imagine; to believe; to receive as true.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppose', 'v. t.', 'To require to exist or to be true; to imply by the laws\n   of thought or of nature; as, purpose supposes foresight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppose', 'v. t.', 'To put by fraud in the place of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppose', 'v. i.', 'To make supposition; to think; to be of opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppose', 'n.', 'Supposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supposeer', 'n.', 'One who supposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supposition', 'n.', 'The act of supposing, laying down, imagining, or\n   considering as true or existing, what is known not to be true, or what\n   is not proved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supposition', 'n.', 'That which is supposed; hypothesis; conjecture;\n   surmise; opinion or belief without sufficient evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppositional', 'a.', 'Resting on supposition; hypothetical; conjectural;\n   supposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supposititious', 'a.', 'Fraudulently substituted for something else; not\n   being what is purports to be; not genuine; spurious; counterfeit; as, a\n   supposititious child; a supposititious writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supposititious', 'a.', 'Suppositional; hypothetical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppositive', 'a.', 'Including or implying supposition, or hypothesis;\n   supposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppositive', 'n.', 'A word denoting or implying supposition, as the words\n   if, granting, provided, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppositor', 'n.', 'An apparatus for the introduction of suppositories into\n   the rectum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppositories', 'pl. ', 'of Suppository');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppository', 'n.', 'A pill or bolus for introduction into the rectum;\n   esp., a cylinder or cone of medicated cacao butter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supposure', 'n.', 'Supposition; hypothesis; conjecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppressed', 'imp. & p. p.', 'of Suppress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppressing', 'p. pr. & vb. n.', 'of Suppress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppress', 'v. t.', 'To overpower and crush; to subdue; to put down; to\n   quell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppress', 'v. t.', 'To keep in; to restrain from utterance or vent; as, to\n   suppress the voice; to suppress a smile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppress', 'v. t.', 'To retain without disclosure; to conceal; not to\n   reveal; to prevent publication of; as, to suppress evidence; to\n   suppress a pamphlet; to suppress the truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppress', 'v. t.', 'To stop; to restrain; to arrest the discharges of; as,\n   to suppress a diarrhea, or a hemorrhage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppressible', 'a.', 'That may be suppressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppression', 'n.', 'The act of suppressing, or the state of being\n   suppressed; repression; as, the suppression of a riot, insurrection, or\n   tumult; the suppression of truth, of reports, of evidence, and the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppression', 'n.', 'Complete stoppage of a natural secretion or excretion;\n   as, suppression of urine; -- used in contradiction to retention, which\n   signifies that the secretion or excretion is retained without\n   expulsion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppression', 'n.', 'Omission; as, the suppression of a word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppressive', 'a.', 'Tending to suppress; subduing; concealing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppressor', 'n.', 'One who suppresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supprise', 'v. t.', 'To surprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppurant', 'n.', 'A suppurative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppurated', 'imp. & p. p.', 'of Suppurate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppurating', 'p. pr. & vb. n.', 'of Suppurate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppurate', 'v. i.', 'To generate pus; as, a boil or abscess suppurates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppurate', 'v. t.', 'To cause to generate pus; as, to suppurate a sore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppuration', 'n.', 'The act or process of suppurating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppuration', 'n.', 'The matter produced by suppuration; pus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppurative', 'a.', 'Tending to suppurate; promoting suppuration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppurative', 'n.', 'A suppurative medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supputate', 'v. t.', 'To suppute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supputation', 'n.', 'Reckoning; account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suppute', 'v. t.', 'To reckon; to compute; to suppose; to impute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supra', 'adv.', 'Over; above; before; also, beyond; besides; -- much used\n   as a prefix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supra-acromial', 'a.', 'Situated above the acromial process of the scapula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supra-angular', 'a.', 'See Surangular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supra-auricular', 'a.', 'Situated above the ear coverts, or auriculars; --\n   said of certain feathers of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supra-auricular', 'n.', 'A supra-auricular feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supra-axillary', 'a.', 'Growing above the axil; inserted above the axil, as\n   a peduncle. See Suprafoliaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suprabranchial', 'a.', 'Situated above the branchiae; -- applied especially\n   to the upper division of the gill cavity of bivalve mollusks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suprachoroid', 'a.', 'Alt. of Suprachoroidal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suprachoroidal', 'a.', 'Situated above the choroid; -- applied to the layer\n   of the choroid coat of the eyeball next to the sclerotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supraciliary', 'a.', 'Superciliary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supraclavicle', 'n.', 'A bone which usually connects the clavicle with the\n   post-temporal in the pectorial arch of fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supraclavicular', 'a.', 'Situated above the clavicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supraclavicular', 'a.', 'Of or pertaining to the supraclavicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supracondylar', 'a.', 'Alt. of Supracondyloid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supracondyloid', 'a.', 'Situated above a condyle or condyles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supracostal', 'a.', 'Situated above, or on the outside of, the ribs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supracranial', 'a.', 'Situated above, or in the roof of, the cranium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supracretaceous', 'a.', 'Lying above the chalk; Supercretaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supradecompound', 'a.', 'More than decompound; divided many times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supra-esophagal', 'a.', 'Alt. of Supra-esophageal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supra-esophageal', 'a.', 'Situated above, or on the dorsal side of, the\n   esophagus; as, the supra-esophageal ganglion of Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supra-ethmoid', 'a.', 'Above, or on the dorsal side of, the ethmoid bone or\n   cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suprafoliaceous', 'a.', 'Inserted into the stem above the leaf, petiole, or\n   axil, as a peduncle or flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supraglotic', 'a.', 'Situated above the glottis; -- applied to that part of\n   the cavity of the larynx above the true vocal cords.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suprahepatic', 'a.', 'Situated over, or on the dorsal side of, the liver;\n   -- applied to the branches of the hepatic veins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suprahyoid', 'a.', 'Hyomental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supra-ilium', 'n.', 'The cartilaginous cap at the sacral end of the ilium\n   of some animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supralapsarian', 'n.', 'One of that class of Calvinists who believed that\n   Gods decree of election determined that man should fall, in order that\n   the opportunity might be furnished of securing the redemption of a part\n   of the race, the decree of salvation being conceived of as formed\n   before or beyond, and not after or following, the lapse, or fall. Cf.\n   Infralapsarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supralapsarian', 'a.', 'Of or pertaining to the Supralapsarians, or their\n   doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supralapsarianism', 'n.', 'The doctrine, belief, or principles of the\n   Supralapsarians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supralapsary', 'a.', 'Supralapsarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supralapsary', 'n.', 'A Supralapsarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supraloral', 'a.', 'Situated above the lores; as, the supraloral feathers\n   of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supraloral', 'n.', 'A supraloral feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supralunar', 'a.', 'Alt. of Supralunary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supralunary', 'a.', 'Beyond the moon; hence, very lofty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supramaxillae', 'pl. ', 'of Supramaxilla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supramaxilla', 'n.', 'The upper jaw or maxilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supramaxillary', 'a.', 'Situated over the lower jaw; as, the supramaxillary\n   nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supramaxillary', 'a.', 'Of or pertaining to the upper jaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supramundane', 'a.', 'Being or situated above the world or above our\n   system; celestial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supranaturalism', 'n.', 'The state of being supernatural; belief in\n   supernatural agency or revelation; supernaturalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supranaturalist', 'n.', 'A supernaturalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supranaturalist', 'a.', 'Alt. of Supranaturalistic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supranaturalistic', 'a.', 'Of or pertaining to supernaturalism;\n   supernaturalistic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supraoccipital', 'a.', 'Situated over, or in the upper part of, the\n   occiput; of or pertaining to the supraoccipital bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supraoccipital', 'n.', 'The supraoccipital bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supraocular', 'a.', 'Above the eyes; -- said of certain scales of fishes\n   and reptiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supra-oesophagal', 'a.', 'See Supra-esophagal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supraorbital', 'a.', 'Alt. of Supraorbitar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supraorbitar', 'a.', 'Situated above the orbit of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suprapedal', 'a.', 'Situated above the foot of a mollusk; as, the\n   suprapedal gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supraprotest', 'n.', 'An acceptance of a bill by a third person after\n   protest for nonacceptance by the drawee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suprapubian', 'a.', 'Alt. of Suprapubic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suprapubic', 'a.', 'Situated above, or anterior to, the pubic bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suprarenal', 'a.', 'Situated above, or anterior to, the kidneys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suprarenal', 'n.', 'A suprarenal capsule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suprascalpular', 'a.', 'Alt. of Suprascalpulary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suprascalpulary', 'a.', 'Situated above, or on the anterior side of, the\n   scapula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suprasphenoidal', 'a.', 'Situated above the sphenoidal bone; as, the\n   suprasphenoidal appendage, or pituitary body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supraspinal', 'a.', 'Situated above the vertebral column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supraspinal', 'a.', 'Situated above a spine or spines; supraspinate;\n   supraspinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supraspinate', 'a.', 'Alt. of Supraspinous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supraspinous', 'a.', 'Situated above a spine or spines; especially,\n   situated above, or on the dorsal side of, the neural spines of the\n   vertebral column, or above, or in front of, the spine of the scapula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suprastapedial', 'a.', 'Of, pertaining to, or designating, that part of the\n   columella of the ear which projects above the connection with the\n   stapes, as in many animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suprastapedial', 'n.', 'The suprastapedial part of the columella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suprasternal', 'a.', 'Situated above, or anterior to, the sternum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supratemporal', 'a.', 'Situated above the temporal bone or temporal fossa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supratemporal', 'n.', 'A supratemporal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supratrochlear', 'a.', 'Situated over or above a trochlea or trochlear\n   surface; -- applied esp. to one of the subdivisions of the trigeminal\n   nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supravaginal', 'a.', 'Situated above or outside a sheath or vaginal\n   membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supravision', 'n.', 'Supervision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supravisor', 'n.', 'A supervisor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supravulgar', 'a.', 'Being above the vulgar or common people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supremacy', 'n.', 'The state of being supreme, or in the highest station of\n   power; highest or supreme authority or power; as, the supremacy of a\n   king or a parliament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supreme', 'a.', 'Highest in authority; holding the highest place in\n   authority, government, or power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supreme', 'a.', 'Highest; greatest; most excellent or most extreme; utmost;\n   greatist possible (sometimes in a bad sense); as, supreme love; supreme\n   glory; supreme magnanimity; supreme folly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supreme', 'a.', 'Situated at the highest part or point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supremely', 'adv.', 'In a supreme manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Supremity', 'n.', 'Supremacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sur-', '', 'A prefix signifying over, above, beyond, upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sura', 'n.', 'One of the sections or chapters of the Koran, which are one\n   hundred and fourteen in number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suradanni', 'n.', 'A valuable kind of wood obtained on the shores of the\n   Demerara River in South America, much used for timbers, rails, naves\n   and fellies of wheels, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suraddition', 'n.', 'Something added or appended, as to a name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surah', 'n.', 'A soft twilled silk fabric much used for womens dresses; --\n   called also surah silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sural', 'a.', 'Of or pertaining to the calf of the leg; as, the sural\n   arteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surance', 'n.', 'Assurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surangular', 'a.', 'Above the angular bone; supra-angular; -- applied to a\n   bone of the lower jaw in many reptiles and birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surangular', 'n.', 'The surangular bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surbase', 'n.', 'A cornice, or series of moldings, on the top of the base\n   of a pedestal, podium, etc. See Illust. of Column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surbase', 'n.', 'A board or group of moldings running round a room on a\n   level with the tops of the chair backs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surbased', 'a.', 'Having a surbase, or molding above the base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surbased', 'a.', 'Having the vertical height from springing line to crown\n   less than the half span; -- said of an arch; as, a segmental arch is\n   surbased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surbated', 'imp. & p. p.', 'of Surbate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surbating', 'p. pr. & vb. n.', 'of Surbate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surbate', 'v. t.', 'To make sore or bruise, as the feet by travel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surbate', 'v. t.', 'To harass; to fatigue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surbeat', 'v. t.', 'Same as Surbate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surbedded', 'imp. & p. p.', 'of Surbed');
