  INSERT INTO words (word, word_type, description) VALUES  (    'Surbet', 'v. t.', 'Same as Surbate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surbet', 'a.', 'Surbated; bruised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcease', 'n.', 'Cessation; stop; end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcease', 'v. t.', 'To cause to cease; to end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcease', 'v. i.', 'To cease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surceaseance', 'n.', 'Cessation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcharged', 'imp. & p. p.', 'of Surcharge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcharging', 'p. pr. & vb. n.', 'of Surcharge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcharge', 'v. t.', 'To overload; to overburden; to overmatch; to\n   overcharge; as, to surcharge a beast or a ship; to surcharge a cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcharge', 'v. t.', 'To overstock; especially, to put more cattle into, as\n   a common, than the person has a right to do, or more than the herbage\n   will sustain. Blackstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcharge', 'v. t.', 'To show an omission in (an account) for which credit\n   ought to have been given.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcharge', 'n.', 'An overcharge; an excessive load or burden; a load\n   greater than can well be borne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcharge', 'n.', 'The putting, by a commoner, of more beasts on the common\n   than he has a right to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcharge', 'n.', 'The showing an omission, as in an account, for which\n   credit ought to have been given.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surchargement', 'n.', 'The act of surcharging; also, surcharge, surplus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcharger', 'n.', 'One who surcharges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcingle', 'n.', 'A belt, band, or girth which passes over a saddle, or\n   over anything laid on a horses back, to bind it fast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcingle', 'n.', 'The girdle of a cassock, by which it is fastened round\n   the waist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcingled', 'a.', 'Bound with the surcingle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcle', 'n.', 'A little shoot; a twig; a sucker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcloyed', 'imp. & p. p.', 'of Surcloy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcloying', 'p. pr. & vb. n.', 'of Surcloy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcloy', 'v. t.', 'To surfeit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcoat', 'n.', 'A coat worn over the other garments; especially, the long\n   and flowing garment of knights, worn over the armor, and frequently\n   emblazoned with the arms of the wearer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcoat', 'n.', 'A name given to the outer garment of either sex at\n   different epochs of the Middle Ages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surcrew', 'n.', 'Increase; addition; surplus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surculate', 'v. t.', 'To purne; to trim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surculation', 'n.', 'Act of purning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surculose', 'a.', 'Producing suckers, or shoots resembling suckers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surd', 'a.', 'Net having the sense of hearing; deaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surd', 'a.', 'Unheard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surd', 'a.', 'Involving surds; not capable of being expressed in rational\n   numbers; radical; irrational; as, a surd expression or quantity; a surd\n   number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surd', 'a.', 'Uttered, as an element of speech, without tone, or proper\n   vocal sound; voiceless; unintonated; nonvocal; atonic; whispered;\n   aspirated; sharp; hard, as f, p, s, etc.; -- opposed to sonant. See\n   Guide to Pronunciation, //169, 179, 180.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surd', 'n.', 'A quantity which can not be expressed by rational numbers;\n   thus, 2 is a surd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surd', 'n.', 'A surd element of speech. See Surd, a., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surdal', 'a.', 'Same as Surd, a., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surdiny', 'n.', 'A sardine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surdity', 'n.', 'Deafness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sure', 'superl.', 'Certainly knowing and believing; confident beyond doubt;\n   implicity trusting; unquestioning; positive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sure', 'superl.', 'Certain to find or retain; as, to be sure of game; to be\n   sure of success; to be sure of life or health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sure', 'superl.', 'Fit or worthy to be depended on; certain not to fail or\n   disappoint expectation; unfailing; strong; permanent; enduring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sure', 'superl.', 'Betrothed; engaged to marry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sure', 'superl.', 'Free from danger; safe; secure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sure', 'adv.', 'In a sure manner; safely; certainly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sure-footed', 'a.', 'Not liable to stumble or fall; as, a sure-footed\n   horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surely', 'adv.', 'In a sure or certain manner; certainly; infallibly;\n   undoubtedly; assuredly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surely', 'adv.', 'Without danger; firmly; steadly; securely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surement', 'n.', 'A making sure; surety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sureness', 'n.', 'The state of being sure; certainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suresby', 'n.', 'One to be sure of, or to be relied on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suretiship', 'n.', 'Suretyship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sureties', 'pl. ', 'of Surety');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surety', 'n.', 'The state of being sure; certainty; security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surety', 'n.', 'That which makes sure; that which confirms; ground of\n   confidence or security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surety', 'n.', 'Security against loss or damage; security for payment, or\n   for the performance of some act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surety', 'n.', 'One who is bound with and for another who is primarily\n   liable, and who is called the principal; one who engages to answer for\n   anothers appearance in court, or for his payment of a debt, or for\n   performance of some act; a bondsman; a bail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surety', 'n.', 'Hence, a substitute; a hostage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surety', 'n.', 'Evidence; confirmation; warrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surety', 'v. t.', 'To act as surety for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suretyship', 'n.', 'The state of being surety; the obligation of a person\n   to answer for the debt, default, or miscarriage of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surf', 'n.', 'The swell of the sea which breaks upon the shore, esp. upon a\n   sloping beach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surf', 'n.', 'The bottom of a drain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surface', 'n.', 'The exterior part of anything that has length and breadth;\n   one of the limits that bound a solid, esp. the upper face; superficies;\n   the outside; as, the surface of the earth; the surface of a diamond;\n   the surface of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surface', 'n.', 'Hence, outward or external appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surface', 'n.', 'A magnitude that has length and breadth without thickness;\n   superficies; as, a plane surface; a spherical surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surface', 'n.', 'That part of the side which is terminated by the flank\n   prolonged, and the angle of the nearest bastion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfaced', 'imp. & p. p.', 'of Surface');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfacing', 'p. pr. & vb. n.', 'of Surface');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surface', 'v. t.', 'To give a surface to; especially, to cause to have a\n   smooth or plain surface; to make smooth or plain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surface', 'v. t.', 'To work over the surface or soil of, as ground, in\n   hunting for gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfacer', 'n.', 'A form of machine for dressing the surface of wood,\n   metal, stone, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfboat', 'n.', 'A boat intended for use in heavy surf. It is built with a\n   pronounced sheer, and with a view to resist the shock of waves and of\n   contact with the beach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfeit', 'n.', 'Excess in eating and drinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfeit', 'n.', 'Fullness and oppression of the system, occasioned often by\n   excessive eating and drinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfeit', 'n.', 'Disgust caused by excess; satiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfeit', 'v. i.', 'To load the stomach with food, so that sickness or\n   uneasiness ensues; to eat to excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfeit', 'v. i.', 'To indulge to satiety in any gratification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfeited', 'imp. & p. p.', 'of Surfeit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfeiting', 'p. pr. & vb. n.', 'of Surfeit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfeit', 'v. t.', 'To feed so as to oppress the stomach and derange the\n   function of the system; to overfeed, and produce satiety, sickness, or\n   uneasiness; -- often reflexive; as, to surfeit ones self with sweets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfeit', 'v. t.', 'To fill to satiety and disgust; to cloy; as, he\n   surfeits us with compliments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfeiter', 'n.', 'One who surfeits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfeit-water', 'n.', 'Water for the cure of surfeits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfel', 'v. t.', 'Alt. of Surfle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfle', 'v. t.', 'To wash, as the face, with a cosmetic water, said by\n   some to be prepared from the sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfer', 'n.', 'The surf duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmen', 'pl. ', 'of Surfman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfman', 'n.', 'One who serves in a surfboat in the life-saving service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfoot', 'a.', 'Tired or sore of foot from travel; lamed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surfy', 'a.', 'Consisting of, abounding in, or resembling, surf; as, a\n   surfy shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surge', 'n.', 'A spring; a fountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surge', 'n.', 'A large wave or billow; a great, rolling swell of water,\n   produced generally by a high wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surge', 'n.', 'The motion of, or produced by, a great wave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surge', 'n.', 'The tapered part of a windlass barrel or a capstan, upon\n   which the cable surges, or slips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surge', 'v. i.', 'To swell; to rise hifg and roll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surge', 'v. i.', 'To slip along a windlass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surged', 'imp. & p. p.', 'of Surge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surging', 'p. pr. & vb. n.', 'of Surge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surge', 'n.', 'To let go or slacken suddenly, as a rope; as, to surge a\n   hawser or messenger; also, to slacken the rope about (a capstan).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surgeful', 'a.', 'Abounding in surges; surgy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surgeless', 'a.', 'Free from surges; smooth; calm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surgent', 'a.', 'Rising; swelling, as a flood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surgeon', 'n.', 'One whose profession or occupation is to cure diseases or\n   injuries of the body by manual operation; one whose occupation is to\n   cure local injuries or disorders (such as wounds, dislocations, tumors,\n   etc.), whether by manual operation, or by medication and constitutional\n   treatment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surgeon', 'n.', 'Any one of numerous species of chaetodont fishes of the\n   family Teuthidae, or Acanthuridae, which have one or two sharp\n   lancelike spines on each side of the base of the tail. Called also\n   surgeon fish, doctor fish, lancet fish, and sea surgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surgeoncy', 'n.', 'The office or employment of a surgeon, as in the naval\n   or military service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surgeonry', 'n.', 'Surgery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surgery', 'n.', 'The art of healing by manual operation; that branch of\n   medical science which treats of manual operations for the healing of\n   diseases or injuries of the body; that branch of medical science which\n   has for its object the cure of local injuries or diseases, as wounds or\n   fractures, tumors, etc., whether by manual operation or by medicines\n   and constitutional treatment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surgery', 'n.', 'A surgeons operating room or laboratory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surgical', 'a.', 'Of or pertaining to surgeons or surgery; done by means of\n   surgery; used in surgery; as, a surgical operation; surgical\n   instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surgically', 'adv.', 'By means of surgery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surgy', 'a.', 'Rising in surges or billows; full of surges; resembling\n   surges in motion or appearance; swelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suricat', 'n.', 'Same as Zenick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surinam toad', '', 'A species of toad native of Surinam. See Pipa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surintendant', 'n.', 'Superintendent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surlily', 'adv.', 'In a surly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surliness', 'n.', 'The quality or state of being surly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surling', 'n.', 'A sour, morose fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surloin', 'n.', 'A loin of beef, or the upper part of the loin. See\n   Sirloin, the more usual, but not etymologically preferable,\n   orthography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surly', 'a.', 'Arrogant; haughty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surly', 'a.', 'Gloomily morose; ill-natured, abrupt, and rude; severe;\n   sour; crabbed; rough; sullen; gloomy; as, a surly groom; a surly dog;\n   surly language; a surly look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surly', 'a.', 'Rough; dark; tempestuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmark', 'n.', 'A mark made on the molds of a ship, when building, to show\n   where the angles of the timbers are to be placed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmisable', 'a.', 'Capable of being surmised; as, a surmisable result.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmisal', 'n.', 'Surmise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmise', 'n.', 'A thought, imagination, or conjecture, which is based upon\n   feeble or scanty evidence; suspicion; guess; as, the surmisses of\n   jealousy or of envy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmise', 'n.', 'Reflection; thought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmised', 'imp. & p. p.', 'of Surmise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmising', 'p. pr. & vb. n.', 'of Surmise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmise', 'v. t.', 'To imagine without certain knowledge; to infer on\n   slight grounds; to suppose, conjecture, or suspect; to guess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmiser', 'n.', 'One who surmises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmising', '', 'a. & n. from Surmise, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmounted', 'imp. & p. p.', 'of Surmount');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmounting', 'p. pr. & vb. n.', 'of Surmount');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmount', 'v. i.', 'To rise above; to be higher than; to overtop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmount', 'v. i.', 'To conquer; to overcome; as, to surmount difficulties\n   or obstacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmount', 'v. i.', 'To surpass; to exceed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmountable', 'a.', 'Capable of being surmounted or overcome; superable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmounted', 'a.', 'Having its vertical height greater than the half span;\n   -- said of an arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmounted', 'a.', 'Partly covered by another charge; -- said of an\n   ordinary or other bearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmounter', 'n.', 'One who, or that which, surmounts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmullet', 'a.', 'Any one of various species of mullets of the family\n   Millidae, esp. the European species (Millus surmulletus), which is\n   highly prized as a food fish. See Mullet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surmulot', 'n.', 'The brown, or Norway, rat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surname', 'n.', 'A name or appellation which is added to, or over and\n   above, the baptismal or Christian name, and becomes a family name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surname', 'n.', 'An appellation added to the original name; an agnomen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surnamed', 'imp. & p. p.', 'of Surname');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surnaming', 'p. pr. & vb. n.', 'of Surname');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surname', 'v. t.', 'To name or call by an appellation added to the original\n   name; to give a surname to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surnominal', 'a.', 'Of or pertaining to a surname or surnames.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suroxidate', 'v. t.', 'To combine with oxygen so as to form a suroxide or\n   peroxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suroxide', 'n.', 'A peroxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surpassed', 'imp. & p. p.', 'of Surpass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surpassing', 'p. pr. & vb. n.', 'of Surpass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surpass', 'v. t.', 'To go beyond in anything good or bad; to exceed; to\n   excel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surpassable', 'a.', 'That may be surpassed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surpassing', 'a.', 'Eminently excellent; exceeding others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surphul', 'v. t.', 'To surfel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surplice', 'n.', 'A white garment worn over another dress by the clergy of\n   the Roman Catholic, Episcopal, and certain other churches, in some of\n   their ministrations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surpliced', 'a.', 'Wearing a surplice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surplus', 'n.', 'That which remains when use or need is satisfied, or when\n   a limit is reached; excess; overplus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surplus', 'n.', 'Specifically, an amount in the public treasury at any time\n   greater than is required for the ordinary purposes of the government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surplus', 'a.', 'Being or constituting a surplus; more than sufficient; as,\n   surplus revenues; surplus population; surplus words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surplusage', 'n.', 'Surplus; excess; overplus; as, surplusage of grain or\n   goods beyond what is wanted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surplusage', 'n.', 'Matter in pleading which is not necessary or relevant\n   to the case, and which may be rejected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surplusage', 'n.', 'A greater disbursement than the charge of the\n   accountant amounts to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surprisal', 'n.', 'The act of surprising, or state of being surprised;\n   surprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surprise', 'n.', 'The act of coming upon, or taking, unawares; the act of\n   seizing unexpectedly; surprisal; as, the fort was taken by surprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surprise', 'n.', 'The state of being surprised, or taken unawares, by some\n   act or event which could not reasonably be foreseen; emotion excited by\n   what is sudden and strange; a suddenly excited feeling of wonder or\n   astonishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surprise', 'n.', 'Anything that causes such a state or emotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surprise', 'n.', 'A dish covered with a crust of raised paste, but with no\n   other contents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surprised', 'imp. & p. p.', 'of Surprise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surprising', 'p. pr. & vb. n.', 'of Surprise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surprise', 'n.', 'To come or fall suddenly and unexpectedly; to take\n   unawares; to seize or capture by unexpected attack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surprise', 'n.', 'To strike with wonder, astonishment, or confusion, by\n   something sudden, unexpected, or remarkable; to confound; as, his\n   conduct surprised me.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surprise', 'n.', 'To lead (one) to do suddenly and without forethought; to\n   bring (one) into some unexpected state; -- with into; as, to be\n   surprised into an indiscretion; to be surprised into generosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surprise', 'n.', 'To hold possession of; to hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surprisement', 'n.', 'Surprisal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surpriser', 'n.', 'One who surprises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surprising', 'a.', 'Exciting surprise; extraordinary; of a nature to excite\n   wonder and astonishment; as, surprising bravery; a surprising escape\n   from danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surquedous', 'a.', 'Alt. of Surquedrous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surquedrous', 'a.', 'Having or exhibiting surquedry; arrogant; insolent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surquedry', 'n.', 'Alt. of Surquidry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surquidry', 'n.', 'Overweening pride; arrogance; presumption; insolence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrebound', 'v. i.', 'To give back echoes; to reecho.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrebut', 'v. i.', 'To reply, as a plaintiff to a defendants rebutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrebuter', 'n.', 'The reply of a plaintiff to a defendants rebutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrein', 'v. t.', 'To override; to exhaust by riding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrejoin', 'v. i.', 'To reply, as a plaintiff to a defendants rejoinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrejoinder', 'n.', 'The answer of a plaintiff to a defendants rejoinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrendered', 'imp. & p. p.', 'of Surrender');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrendering', 'p. pr. & vb. n.', 'of Surrender');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrender', 'v. t.', 'To yield to the power of another; to give or deliver\n   up possession of (anything) upon compulsion or demand; as, to surrender\n   ones person to an enemy or to an officer; to surrender a fort or a\n   ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrender', 'v. t.', 'To give up possession of; to yield; to resign; as, to\n   surrender a right, privilege, or advantage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrender', 'v. t.', 'To yield to any influence, emotion, passion, or\n   power; -- used reflexively; as, to surrender ones self to grief, to\n   despair, to indolence, or to sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrender', 'v. t.', 'To yield; to render or deliver up; to give up; as, a\n   principal surrendered by his bail, a fugitive from justice by a foreign\n   state, or a particular estate by the tenant thereof to him in remainder\n   or reversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrender', 'v. i.', 'To give up ones self into the power of another; to\n   yield; as, the enemy, seeing no way of escape, surrendered at the first\n   summons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrender', 'n.', 'The act of surrendering; the act of yielding, or\n   resigning ones person, or the possession of something, into the power\n   of another; as, the surrender of a castle to an enemy; the surrender of\n   a right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrender', 'n.', 'The yielding of a particular estate to him who has an\n   immediate estate in remainder or reversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrender', 'n.', 'The giving up of a principal into lawful custody by his\n   bail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrender', 'n.', 'The delivery up of fugitives from justice by one\n   government to another, as by a foreign state. See Extradition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrenderee', 'n.', 'The person to whom a surrender is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrenderer', 'n.', 'One who surrenders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrenderor', 'n.', 'One who makes a surrender, as of an estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrendry', 'n.', 'Surrender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surreption', 'n.', 'The act or process of getting in a surreptitious\n   manner, or by craft or stealth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surreption', 'n.', 'A coming unperceived or suddenly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surreptitious', 'a.', 'Done or made by stealth, or without proper\n   authority; made or introduced fraudulently; clandestine; stealthy; as,\n   a surreptitious passage in an old manuscript; a surreptitious removal\n   of goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrey', 'n.', 'A four-wheeled pleasure carriage, (commonly two-seated)\n   somewhat like a phaeton, but having a straight bottom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrogate', 'n.', 'A deputy; a delegate; a substitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrogate', 'n.', 'The deputy of an ecclesiastical judge, most commonly of\n   a bishop or his chancellor, especially a deputy who grants marriage\n   licenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrogate', 'n.', 'In some States of the United States, an officer who\n   presides over the probate of wills and testaments and yield the\n   settlement of estates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrogate', 'v. t.', 'To put in the place of another; to substitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrogateship', 'n.', 'The office of a surrogate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrogation', 'n.', 'The act of substituting one person in the place of\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrounded', 'imp. & p. p.', 'of Surround');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrounding', 'p. pr. & vb. n.', 'of Surround');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surround', 'v. t.', 'To inclose on all sides; to encompass; to environ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surround', 'v. t.', 'To lie or be on all sides of; to encircle; as, a wall\n   surrounds the city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surround', 'v. t.', 'To pass around; to travel about; to circumnavigate;\n   as, to surround the world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surround', 'v. t.', 'To inclose, as a body of troops, between hostile\n   forces, so as to cut off means of communication or retreat; to invest,\n   as a city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surround', 'n.', 'A method of hunting some animals, as the buffalo, by\n   surrounding a herd, and driving them over a precipice, into a ravine,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrounding', 'a.', 'Inclosing; encircling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrounding', 'n.', 'An encompassing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surrounding', 'n.', 'The things which surround or environ; external or\n   attending circumstances or conditions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surroyal', 'n.', 'One of the terminal branches or divisions of the beam of\n   the antler of the stag or other large deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sursanure', 'n.', 'A wound healed or healing outwardly only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surseance', 'n.', 'Peace; quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sursolid', 'n.', 'The fifth power of a number; as, a/ is the sursolid of a,\n   or 32 that of 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surstyle', 'v. t.', 'To surname.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surtax', 'n.', 'An additional or extra tax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surtax', 'v. t.', 'To impose an additional tax on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surtout', 'n.', 'A mans coat to be worn over his other garments; an\n   overcoat, especially when long, and fitting closely like a body coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surturbrand', 'n.', 'A fibrous brown coal or bituminous wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surucucu', 'n.', 'See Bush master, under Bush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surveillance', 'n.', 'Oversight; watch; inspection; supervision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surveillants', 'pl. ', 'of Surveillant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surveillant', 'n.', 'One who watches over another; an overseer; a spy; a\n   supervisor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surveillant', 'a.', 'Overseeing; watchful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survened', 'imp. & p. p.', 'of Survene');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survening', 'p. pr. & vb. n.', 'of Survene');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survene', 'v. t.', 'To supervene upon; to come as an addition to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survenue', 'n.', 'A sudden or unexpected coming or stepping on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surveyed', 'imp. & p. p.', 'of Survey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surveying', 'p. pr. & vb. n.', 'of Survey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survey', 'v. t.', 'To inspect, or take a view of; to view with attention,\n   as from a high place; to overlook; as, to stand on a hill, and survey\n   the surrounding country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survey', 'v. t.', 'To view with a scrutinizing eye; to examine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survey', 'v. t.', 'To examine with reference to condition, situation,\n   value, etc.; to examine and ascertain the state of; as, to survey a\n   building in order to determine its value and exposure to loss by fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survey', 'v. t.', 'To determine the form, extent, position, etc., of, as a\n   tract of land, a coast, harbor, or the like, by means of linear and\n   angular measurments, and the application of the principles of geometry\n   and trigonometry; as, to survey land or a coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survey', 'v. t.', 'To examine and ascertain, as the boundaries and\n   royalties of a manor, the tenure of the tenants, and the rent and value\n   of the same.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survey', 'n.', 'The act of surveying; a general view, as from above.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survey', 'n.', 'A particular view; an examination, especially an official\n   examination, of all the parts or particulars of a thing, with a design\n   to ascertain the condition, quantity, or quality; as, a survey of the\n   stores of a ship; a survey of roads and bridges; a survey of buildings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survey', 'n.', 'The operation of finding the contour, dimensions, position,\n   or other particulars of, as any part of the earths surface, whether\n   land or water; also, a measured plan and description of any portion of\n   country, or of a road or line through it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surveyal', 'n.', 'Survey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surveyance', 'n.', 'Survey; inspection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surveying', 'n.', 'That branch of applied mathematics which teaches the art\n   of determining the area of any portion of the earths surface, the\n   length and directions of the bounding lines, the contour of the\n   surface, etc., with an accurate delineation of the whole on paper; the\n   act or occupation of making surveys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surveyor', 'n.', 'One placed to superintend others; an overseer; an\n   inspector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surveyor', 'n.', 'One who views and examines for the purpose of\n   ascertaining the condition, quantity, or quality of anything; as, a\n   surveyor of highways, ordnance, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surveyor', 'n.', 'One who surveys or measures land; one who practices the\n   art of surveying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surveyor', 'n.', 'An officer who ascertains the contents of casks, and the\n   quantity of liquors subject to duty; a gauger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surveyor', 'n.', 'In the United States, an officer whose duties include the\n   various measures to be taken for ascertaining the quantity, condition,\n   and value of merchandise brought into a port.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surveyorship', 'n.', 'The office of a surveyor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surview', 'v. t.', 'To survey; to make a survey of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surview', 'n.', 'A survey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survise', 'v. t.', 'To look over; to supervise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survival', 'n.', 'A living or continuing longer than, or beyond the\n   existence of, another person, thing, or event; an outliving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survival', 'n.', 'Any habit, usage, or belief, remaining from ancient\n   times, the origin of which is often unknown, or imperfectly known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survivance', 'n.', 'Alt. of Survivancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survivancy', 'n.', 'Survivorship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survived', 'imp. & p. p.', 'of Survive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surviving', 'p. pr. & vb. n.', 'of Survive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survive', 'v. t.', 'To live beyond the life or existence of; to live longer\n   than; to outlive; to outlast; as, to survive a person or an event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survive', 'v. i.', 'To remain alive; to continue to live.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survivency', 'n.', 'Survivorship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surviver', 'n.', 'One who survives; a survivor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Surviving', 'a.', 'Remaining alive; yet living or existing; as, surviving\n   friends; surviving customs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survivor', 'n.', 'One who survives or outlives another person, or any time,\n   event, or thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survivor', 'n.', 'The longer liver of two joint tenants, or two persons\n   having a joint interest in anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survivorship', 'n.', 'The state of being a survivor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Survivorship', 'n.', 'The right of a joint tenant, or other person who has\n   a joint interest in an estate, to take the whole estate upon the death\n   of other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Susceptibilities', 'pl. ', 'of Susceptibility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Susceptibility', 'n.', 'The state or quality of being susceptible; the\n   capability of receiving impressions, or of being affected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Susceptibility', 'n.', 'Specifically, capacity for deep feeling or\n   emotional excitement; sensibility, in its broadest acceptation;\n   impressibility; sensitiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Susceptible', 'a.', 'Capable of admitting anything additional, or any\n   change, affection, or influence; readily acted upon; as, a body\n   susceptible of color or of alteration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Susceptible', 'a.', 'Capable of impression; having nice sensibility;\n   impressible; tender; sensitive; as, children are more susceptible than\n   adults; a man of a susceptible heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Susception', 'n.', 'The act of taking; reception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Susceptive', 'a.', 'Susceptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Susceptivity', 'n.', 'Capacity for receiving; susceptibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Susceptor', 'n.', 'One who undertakes anything; specifically, a godfather;\n   a sponsor; a guardian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suscipiency', 'n.', 'Admission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suscipient', 'a.', 'Receiving; admitting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suscipient', 'n.', 'One who takes or admits; one who receives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suscitability', 'n.', 'Capability of being suscitated; excitability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suscitated', 'imp. & p. p.', 'of Suscitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suscitating', 'p. pr. & vb. n.', 'of Suscitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suscitate', 'v. t.', 'To rouse; to excite; to call into life and action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suscitation', 'n.', 'The act of raising or exciting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suslik', 'n.', 'A ground squirrel (Spermophilus citillus) of Europe and\n   Asia. It has large cheek pouches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspect', 'a.', 'Suspicious; inspiring distrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspect', 'a.', 'Suspected; distrusted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspect', 'a.', 'Suspicion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspect', 'a.', 'One who, or that which, is suspected; an object of\n   suspicion; -- formerly applied to persons and things; now, only to\n   persons suspected of crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspected', 'imp. & p. p.', 'of Suspect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspecting', 'p. pr. & vb. n.', 'of Suspect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspect', 'v. t.', 'To imagine to exist; to have a slight or vague opinion\n   of the existence of, without proof, and often upon weak evidence or no\n   evidence; to mistrust; to surmise; -- commonly used regarding something\n   unfavorable, hurtful, or wrong; as, to suspect the presence of disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspect', 'v. t.', 'To imagine to be guilty, upon slight evidence, or\n   without proof; as, to suspect one of equivocation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspect', 'v. t.', 'To hold to be uncertain; to doubt; to mistrust; to\n   distruct; as, to suspect the truth of a story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspect', 'v. t.', 'To look up to; to respect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspect', 'v. i.', 'To imagine guilt; to have a suspicion or suspicions; to\n   be suspicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspectable', 'a.', 'That may be suspected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspected', 'a.', 'Distrusted; doubted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspecter', 'n.', 'One who suspects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspectful', 'a.', 'Apt to suspect or mistrust; full of suspicion;\n   suspicious; as, to be suspectful of the motives of others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspection', 'n.', 'Suspicion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspectiousness', 'n.', 'Suspiciousness; cause for suspicion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspectless', 'a.', 'Not suspecting; having no suspicion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspectless', 'a.', 'Not suspected; not mistrusted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspended', 'imp. & p. p.', 'of Suspend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspending', 'p. pr. & vb. n.', 'of Suspend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspend', 'n.', 'To attach to something above; to hang; as, to suspend a\n   ball by a thread; to suspend a needle by a loadstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspend', 'n.', 'To make to depend; as, God hath suspended the promise of\n   eternal life on the condition of obedience and holiness of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspend', 'n.', 'To cause to cease for a time; to hinder from proceeding;\n   to interrupt; to delay; to stay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspend', 'n.', 'To hold in an undetermined or undecided state; as, to\n   suspend ones judgment or opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspend', 'n.', 'To debar, or cause to withdraw temporarily, from any\n   privilege, from the execution of an office, from the enjoyment of\n   income, etc.; as, to suspend a student from college; to suspend a\n   member of a club.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspend', 'n.', 'To cause to cease for a time from operation or effect; as,\n   to suspend the habeas corpus act; to suspend the rules of a legislative\n   body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspend', 'n.', 'To support in a liquid, as an insoluble powder, by\n   stirring, to facilitate chemical action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspend', 'v. i.', 'To cease from operation or activity; esp., to stop\n   payment, or be unable to meet obligations or engagements (said of a\n   commercial firm or a bank).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspender', 'n.', 'One who, or that which, suspends; esp., one of a pair of\n   straps or braces worn over the shoulders, for holding up the trousers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspensation', 'n.', 'The act of suspending, or the state of being\n   suspended, especially for a short time; temporary suspension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspense', 'a.', 'Held or lifted up; held or prevented from proceeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspense', 'a.', 'Expressing, or proceeding from, suspense or doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspense', 'a.', 'The state of being suspended; specifically, a state of\n   uncertainty and expectation, with anxiety or apprehension;\n   indetermination; indecision; as, the suspense of a person waiting for\n   the verdict of a jury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspense', 'a.', 'Cessation for a time; stop; pause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspense', 'a.', 'A temporary cessation of ones right; suspension, as when\n   the rent or other profits of land cease by unity of possession of land\n   and rent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspensely', 'adv.', 'In suspense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspensibility', 'n.', 'The quality or state of being suspensible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspensible', 'a.', 'Capable of being suspended; capable of being held from\n   sinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspension', 'n.', 'The act of suspending, or the state of being suspended;\n   pendency; as, suspension from a hook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspension', 'n.', 'Especially, temporary delay, interruption, or cessation');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspension', 'n.', 'Of labor, study, pain, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspension', 'n.', 'Of decision, determination, judgment, etc.; as, to ask\n   a suspension of judgment or opinion in view of evidence to be produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspension', 'n.', 'Of the payment of what is due; as, the suspension of a\n   mercantile firm or of a bank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspension', 'n.', 'Of punishment, or sentence of punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspension', 'n.', 'Of a person in respect of the exercise of his office,\n   powers, prerogative, etc.; as, the suspension of a student or of a\n   clergyman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspension', 'n.', 'Of the action or execution of law, etc.; as, the\n   suspension of the habeas corpus act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspension', 'n.', 'A conditional withholding, interruption, or delay; as,\n   the suspension of a payment on the performance of a condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspension', 'n.', 'The state of a solid when its particles are mixed with,\n   but undissolved in, a fluid, and are capable of separation by\n   straining; also, any substance in this state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspension', 'n.', 'A keeping of the hearer in doubt and in attentive\n   expectation of what is to follow, or of what is to be the inference or\n   conclusion from the arguments or observations employed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspension', 'n.', 'A stay or postponement of execution of a sentence\n   condemnatory by means of letters of suspension granted on application\n   to the lord ordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspension', 'n.', 'The prolongation of one or more tones of a chord into\n   the chord which follows, thus producing a momentary discord, suspending\n   the concord which the ear expects. Cf. Retardation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspensive', 'a.', 'Tending to suspend, or to keep in suspense; causing\n   interruption or delay; uncertain; doubtful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspensor', 'n.', 'A suspensory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspensor', 'n.', 'The cord which suspends the embryo; and which is\n   attached to the radicle in the young state; the proembryo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspensoria', 'pl. ', 'of Suspensorium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspensorium', 'n.', 'Anything which suspends or holds up a part:\n   especially, the mandibular suspensorium (a series of bones, or of\n   cartilages representing them) which connects the base of the lower jaw\n   with the skull in most vertebrates below mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspensory', 'a.', 'Suspended; hanging; depending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspensory', 'a.', 'Fitted or serving to suspend; suspending; as, a\n   suspensory muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspensory', 'a.', 'Of or pertaining to a suspensorium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspensory', 'n.', 'That which suspends, or holds up, as a truss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspensory', 'n.', 'a bandage or bag for supporting the scrotum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspicable', 'v. t.', 'Liable to suspicion; suspicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspiciency', 'v. t.', 'Suspiciousness; suspicion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspicion', 'n.', 'The act of suspecting; the imagination or apprehension\n   of the existence of something (esp. something wrong or hurtful) without\n   proof, or upon very slight evidence, or upon no evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspicion', 'n.', 'Slight degree; suggestion; hint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspicion', 'v. t.', 'To view with suspicion; to suspect; to doubt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspicious', 'a.', 'Inclined to suspect; given or prone to suspicion; apt\n   to imagine without proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspicious', 'a.', 'Indicating suspicion, mistrust, or fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspicious', 'a.', 'Liable to suspicion; adapted to raise suspicion; giving\n   reason to imagine ill; questionable; as, an author of suspicious\n   innovations; suspicious circumstances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspiral', 'n.', 'A breathing hole; a vent or ventiduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspiral', 'n.', 'A spring of water passing under ground toward a cistern\n   or conduit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspiration', 'n.', 'The act of sighing, or fetching a long and deep\n   breath; a deep respiration; a sigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspire', 'v. i.', 'To fetch a long, deep breath; to sigh; to breathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspire', 'n.', 'A long, deep breath; a sigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suspired', 'a.', 'Ardently desired or longed for; earnestly coveted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustained', 'imp. & p. p.', 'of Sustain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustaining', 'p. pr. & vb. n.', 'of Sustain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustain', 'v. t.', 'To keep from falling; to bear; to uphold; to support;\n   as, a foundation sustains the superstructure; a beast sustains a load;\n   a rope sustains a weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustain', 'v. t.', 'Hence, to keep from sinking, as in despondence, or the\n   like; to support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustain', 'v. t.', 'To maintain; to keep alive; to support; to subsist; to\n   nourish; as, provisions to sustain an army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustain', 'v. t.', 'To aid, comfort, or relieve; to vindicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustain', 'v. t.', 'To endure without failing or yielding; to bear up\n   under; as, to sustain defeat and disappointment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustain', 'v. t.', 'To suffer; to bear; to undergo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustain', 'v. t.', 'To allow the prosecution of; to admit as valid; to\n   sanction; to continue; not to dismiss or abate; as, the court sustained\n   the action or suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustain', 'v. t.', 'To prove; to establish by evidence; to corroborate or\n   confirm; to be conclusive of; as, to sustain a charge, an accusation,\n   or a proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustain', 'n.', 'One who, or that which, upholds or sustains; a sustainer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustainable', 'a.', 'Capable of being sustained or maintained; as, the\n   action is not sustainable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustained', 'a.', 'Held up to a certain pitch, degree, or level; uniform;\n   as, sustained pasion; a sustained style of writing; a sustained note in\n   music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustainer', 'n.', 'One who, or that which, sustains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustainment', 'n.', 'The act of sustaining; maintenance; support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustaltic', 'a.', 'Mournful; -- said of a species of music among the\n   ancient Greeks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustenance', 'n.', 'The act of sustaining; support; maintenance;\n   subsistence; as, the sustenance of the body; the sustenance of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustenance', 'n.', 'That which supports life; food; victuals; provisions;\n   means of living; as, the city has ample sustenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustentacle', 'n.', 'Sustenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustentacular', 'a.', 'Supporting; sustaining; as, a sustentacular tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustentate', 'v. t.', 'To sustain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustentation', 'n.', 'The act of sustaining, or the state of being\n   sustained; preservation from falling; support; sustenance; maintenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustentation', 'n.', 'The aggregate of the functions by which a living\n   organism is maintained in a normal condition of weight and growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustentative', 'a.', 'Adapted to sustain, strengthen, or corroborate; as,\n   sustentative citations or quotations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustention', 'n.', 'Sustentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Susters', 'pl. ', 'of Sustre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustres', 'pl. ', 'of Sustre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustren', 'pl. ', 'of Sustre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suster', 'n.', 'Alt. of Sustre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sustre', 'n.', 'Sister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Susu', 'n.', 'See Soosoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Susurrant', 'a.', 'Whispering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Susurration', 'n.', 'A whispering; a soft murmur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Susurringly', 'adv.', 'In the manner of a whisper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Susurrous', 'a.', 'Whispering; rustling; full of whispering sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Susurrus', 'n.', 'The act of whispering; a whisper; a murmur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sutile', 'a.', 'Done by stitching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sutler', 'n.', 'A person who follows an army, and sells to the troops\n   provisions, liquors, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sutlership', 'n.', 'The condition or occupation of a sutler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sutling', 'a.', 'Belonging to sutlers; engaged in the occupation of a\n   sutler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sutor', 'n.', 'A kind of sirup made by the Indians of Arizona from the\n   fruit of some cactaceous plant (probably the Cereus giganteus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sutras', 'pl. ', 'of Sutra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sutra', 'n.', 'A precept; an aphorism; a brief rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sutra', 'n.', 'A collection of such aphorisms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sutra', 'n.', 'A body of Hindoo literature containing aphorisms on grammar,\n   meter, law, and philosophy, and forming a connecting link between the\n   Vedic and later Sanscrit literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suttee', 'n.', 'A Hindoo widow who immolates herself, or is immolated, on\n   the funeral pile of her husband; -- so called because this act of\n   self-immolation is regarded as envincing excellence of wifely\n   character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suttee', 'n.', 'The act of burning a widow on the funeral pile of her\n   husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sutteeism', 'n.', 'The practice of self-immolation of widows in Hindostan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suttle', 'n.', 'The weight when the tare has been deducted, and tret is yet\n   to be allowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suttle', 'v. i.', 'To act as sutler; to supply provisions and other\n   articles to troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sutural', 'a.', 'Of or pertaining to a suture, or seam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sutural', 'a.', 'Taking place at a suture; as, a sutural de/iscence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suturally', 'adv.', 'In a sutural manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suturated', 'a.', 'Sewed or knit together; united by a suture; stitched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suture', 'n.', 'The act of sewing; also, the line along which two things or\n   parts are sewed together, or are united so as to form a seam, or that\n   which resembles a seam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suture', 'n.', 'The uniting of the parts of a wound by stitching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suture', 'n.', 'The stitch by which the parts are united.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suture', 'n.', 'The line of union, or seam, in an immovable articulation,\n   like those between the bones of the skull; also, such an articulation\n   itself; synarthrosis. See Harmonic suture, under Harmonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suture', 'n.', 'The line, or seam, formed by the union of two margins in\n   any part of a plant; as, the ventral suture of a legume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suture', 'n.', 'A line resembling a seam; as, the dorsal suture of a\n   legume, which really corresponds to a midrib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suture', 'n.', 'The line at which the elytra of a beetle meet and are\n   sometimes confluent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suture', 'n.', 'A seam, or impressed line, as between the segments of a\n   crustacean, or between the whorls of a univalve shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sutured', 'a.', 'Having a suture or sutures; knit or united together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suwarrow', 'n.', 'The giant cactus (Cereus giganteus); -- so named by the\n   Indians of Arizona. Called also saguaro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suzerain', 'n.', 'A superior lord, to whom fealty is due; a feudal lord; a\n   lord paramount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Suzerainty', 'n.', 'The dominion or authority of a suzerain; paramount\n   authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swa', 'adv.', 'So.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swabbed', 'imp. & p. p.', 'of Swab');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swabbing', 'p. pr. & vb. n.', 'of Swab');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swab', 'n.', 'To clean with a mop or swab; to wipe when very wet, as after\n   washing; as, to swab the desk of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swab', 'n.', 'A kind of mop for cleaning floors, the desks of vessels,\n   etc., esp. one made of rope-yarns or threads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swab', 'n.', 'A bit of sponge, cloth, or the like, fastened to a handle,\n   for cleansing the mouth of a sick person, applying medicaments to\n   deep-seated parts, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swab', 'n.', 'An epaulet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swab', 'n.', 'A cod, or pod, as of beans or pease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swab', 'n.', 'A sponge, or other suitable substance, attached to a long rod\n   or handle, for cleaning the bore of a firearm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swabber', 'v. t.', 'To swab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swabber', 'n.', 'One who swabs a floor or desk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swabber', 'n.', 'Formerly, an interior officer on board of British ships of\n   war, whose business it was to see that the ship was kept clean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swabber', 'n.', 'Same as Swobber, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swad', 'n.', 'A cod, or pod, as of beans or pease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swad', 'n.', 'A clown; a country bumpkin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swad', 'n.', 'A lump of mass; also, a crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swad', 'n.', 'A thin layer of refuse at the bottom of a seam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swaddle', 'n.', 'Anything used to swaddle with, as a cloth or band; a\n   swaddling band.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swaddled', 'imp. & p. p.', 'of Swaddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swaddling', 'p. pr. & vb. n.', 'of Swaddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swaddle', 'v. t.', 'To bind as with a bandage; to bind or warp tightly with\n   clothes; to swathe; -- used esp. of infants; as, to swaddle a baby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swaddle', 'v. t.', 'To beat; to cudgel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swaddlebill', 'n.', 'The shoveler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swaddler', 'n.', 'A term of contempt for an Irish Methodist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swaddling', '', 'a. & n. from Swaddle, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swagged', 'imp. & p. p.', 'of Swag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swagging', 'p. pr. & vb. n.', 'of Swag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swag', 'v. i.', 'To hang or move, as something loose and heavy; to sway; to\n   swing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swag', 'v. i.', 'To sink down by its weight; to sag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swag', 'n.', 'A swaying, irregular motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swag', 'n.', 'A burglars or thiefs booty; boodle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swag-bellied', 'a.', 'Having a prominent, overhanging belly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swagbelly', 'n.', 'A prominent, overhanging belly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swagbelly', 'n.', 'Any large tumor developed in the abdomen, and neither\n   fluctuating nor sonorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swaged', 'imp. & p. p.', 'of Swage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swaging', 'p. pr. & vb. n.', 'of Swage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swage', 'v. t. & i.', 'See Assuage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swage', 'n.', 'A tool, variously shaped or grooved on the end or face, used\n   by blacksmiths and other workers in metals, for shaping their work,\n   whether sheet metal or forging, by holding the swage upon the work, or\n   the work upon the swage, and striking with a sledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swage', 'v. t.', 'To shape by means of a swage; to fashion, as a piece of\n   iron, by forcing it into a groove or mold having the required shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swaggered', 'imp. & p. p.', 'of Swagger');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swaggering', 'p. pr. & vb. n.', 'of Swagger');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swagger', 'v. i.', 'To walk with a swaying motion; hence, to walk and act\n   in a pompous, consequential manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swagger', 'v. i.', 'To boast or brag noisily; to be ostentatiously proud or\n   vainglorious; to bluster; to bully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swagger', 'v. t.', 'To bully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swagger', 'n.', 'The act or manner of a swaggerer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swaggerer', 'n.', 'One who swaggers; a blusterer; a bully; a boastful,\n   noisy fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swaggy', 'a.', 'Inclined to swag; sinking, hanging, or leaning by its\n   weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swain', 'n.', 'A servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swain', 'n.', 'A young man dwelling in the country; a rustic; esp., a\n   cuntry gallant or lover; -- chiefly in poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swainish', 'a.', 'Pertaining to, or resembling, a swain; rustic; ignorant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swainling', 'n.', 'A little swain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swainmote', 'n.', 'A court held before the verders of the forest as judges,\n   by the steward of the court, thrice every year, the swains, or\n   freeholders, within the forest composing the jury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swainship', 'n.', 'The condition of a swain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swaip', 'v. i.', 'To walk proudly; to sweep along.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swal', 'imp.', 'Swelled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swale', 'n.', 'A valley or low place; a tract of low, and usually wet,\n   land; a moor; a fen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swale', 'v. i. & t.', 'To melt and waste away; to singe. See Sweal, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swale', 'n.', 'A gutter in a candle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallet', 'n.', 'Water breaking in upon the miners at their work; -- so\n   called among tin miners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow', 'n.', 'Any one of numerous species of passerine birds of the\n   family Hirundinidae, especially one of those species in which the tail\n   is deeply forked. They have long, pointed wings, and are noted for the\n   swiftness and gracefulness of their flight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow', 'n.', 'Any one of numerous species of swifts which resemble the\n   true swallows in form and habits, as the common American chimney\n   swallow, or swift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow', 'n.', 'The aperture in a block through which the rope reeves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallowed', 'imp. & p. p.', 'of Swallow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallowing', 'p. pr. & vb. n.', 'of Swallow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow', 'v. t.', 'To take into the stomach; to receive through the\n   gullet, or esophagus, into the stomach; as, to swallow food or drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow', 'v. t.', 'To draw into an abyss or gulf; to ingulf; to absorb --\n   usually followed by up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow', 'v. t.', 'To receive or embrace, as opinions or belief, without\n   examination or scruple; to receive implicitly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow', 'v. t.', 'To engross; to appropriate; -- usually with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow', 'v. t.', 'To occupy; to take up; to employ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow', 'v. t.', 'To seize and waste; to exhaust; to consume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow', 'v. t.', 'To retract; to recant; as, to swallow ones opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow', 'v. t.', 'To put up with; to bear patiently or without\n   retaliation; as, to swallow an affront or insult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow', 'v. i.', 'To perform the act of swallowing; as, his cold is so\n   severe he is unable to swallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow', 'n.', 'The act of swallowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow', 'n.', 'The gullet, or esophagus; the throat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow', 'n.', 'Taste; relish; inclination; liking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow', 'n.', 'Capacity for swallowing; voracity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow', 'n.', 'As much as is, or can be, swallowed at once; as, a swallow\n   of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow', 'n.', 'That which ingulfs; a whirlpool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallower', 'n.', 'One who swallows; also, a glutton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallowfish', 'n.', 'The European sapphirine gurnard (Trigla hirundo). It\n   has large pectoral fins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallowtail', 'n.', 'A kind of tenon or tongue used in making joints. See\n   Dovetail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallowtail', 'n.', 'A species of willow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallowtail', 'n.', 'An outwork with converging sides, its head or front\n   forming a reentrant angle; -- so called from its form. Called also\n   priestcap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallowtail', 'n.', 'A swallow-tailed coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallowtail', 'n.', 'An arrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallowtail', 'n.', 'Any one of numerous species of large and handsome\n   butterflies, belonging to Papilio and allied genera, in which the\n   posterior border of each hind wing is prolongated in the form of a long\n   lobe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow-tailed', 'a.', 'Having a tail like that of a swallow; hence, like a\n   swallows tail in form; having narrow and tapering or pointed skirts;\n   as, a swallow-tailed coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallow-tailed', 'a.', 'United by dovetailing; dovetailed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallowwort', 'n.', 'See Celandine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swallowwort', 'n.', 'A poisonous plant (Vincetoxicum officinale) of the\n   Milkweed family, at one time used in medicine; -- also called white\n   swallowwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swam', '', 'imp. of Swim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swamp', 'n.', 'Wet, spongy land; soft, low ground saturated with water, but\n   not usually covered with it; marshy ground away from the seashore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swamped', 'imp. & p. p.', 'of Swamp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swamping', 'p. pr. & vb. n.', 'of Swamp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swamp', 'v. t.', 'To plunge or sink into a swamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swamp', 'v. t.', 'To cause (a boat) to become filled with water; to capsize\n   or sink by whelming with water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swamp', 'v. t.', 'Fig.: To plunge into difficulties and perils; to\n   overwhelm; to ruin; to wreck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swamp', 'v. i.', 'To sink or stick in a swamp; figuratively, to become\n   involved in insuperable difficulties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swamp', 'v. i.', 'To become filled with water, as a boat; to founder; to\n   capsize or sink; figuratively, to be ruined; to be wrecked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swampy', 'a.', 'Consisting of swamp; like a swamp; low, wet, and spongy;\n   as, swampy land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swan', 'n.', 'Any one of numerous species of large aquatic birds belonging\n   to Cygnus, Olor, and allied genera of the subfamily Cygninae. They have\n   a large and strong beak and a long neck, and are noted for their\n   graceful movements when swimming. Most of the northern species are\n   white. In literature the swan was fabled to sing a melodious song,\n   especially at the time of its death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swan', 'n.', 'Fig.: An appellation for a sweet singer, or a poet noted for\n   grace and melody; as Shakespeare is called the swan of Avon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swan', 'n.', 'The constellation Cygnus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swang', '', 'imp. of Swing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swang', 'n.', 'A swamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swanherd', 'n.', 'One who tends or marks swans; as, the royal swanherd of\n   England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swan-hopping', 'n.', 'A corruption of Swan-upping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swanimote', 'n.', 'See Swainmote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swankie', 'n.', 'Alt. of Swanky');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swanky', 'n.', 'An active and clever young fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swanlike', 'a.', 'Resembling a swan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swanmark', 'n.', 'A mark of ownership cut on the bill or swan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swannery', 'n.', 'A place where swans are bred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swanny', 'a.', 'Swanlike; as, a swanny glossiness of the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swanpan', 'n.', 'The Chinese abacus; a schwanpan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swans-down', 'n.', 'Alt. of Swans-down');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swans-down', 'n.', 'The down, or fine, soft feathers, of the swan, used on\n   various articles of dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swans-down', 'n.', 'A fine, soft, thick cloth of wool mixed with silk or\n   cotton; a sort of twilled fustian, like moleskin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swanskin', 'n.', 'The act of a swan with the down or the feathers on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swanskin', 'n.', 'A species of soft flannel, thick and warm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swan-upping', 'n.', 'A yearly expedition on the Thames to take up young\n   swans and mark them, as by Companies of Dyers and Vintners; -- called\n   also swan-hopping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swapped', 'imp. & p. p.', 'of Swap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swapping', 'p. pr. & vb. n.', 'of Swap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swap', 'v. i.', 'To strike; -- with off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swap', 'v. i.', 'To exchange (usually two things of the same kind); to\n   swop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swap', 'v. t.', 'To fall or descend; to rush hastily or violently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swap', 'v. t.', 'To beat the air, or ply the wings, with a sweeping motion\n   or noise; to flap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swap', 'n.', 'A blow; a stroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swap', 'n.', 'An exchange; a barter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swap', 'n.', 'Hastily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swape', 'n.', 'See Sweep, n., 12.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sward', 'n.', 'Skin; covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sward', 'n.', 'The grassy surface of land; that part of the soil which is\n   filled with the roots of grass; turf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarded', 'imp. & p. p.', 'of Sward');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarding', 'p. pr. & vb. n.', 'of Sward');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sward', 'v. t. & i.', 'To produce sward upon; to cover, or be covered, with\n   sward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sward-cutter', 'n.', 'A plow for turning up grass land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sward-cutter', 'n.', 'A lawn mower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarded', 'a.', 'Covered with sward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swardy', 'a.', 'Covered with sward or grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sware', '', 'imp. of Swear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarf', 'v. i.', 'To grow languid; to faint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarf', 'n.', 'The grit worn away from grindstones in grinding cutlery wet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarm', 'v. i.', 'To climb a tree, pole, or the like, by embracing it with\n   the arms and legs alternately. See Shin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarm', 'n.', 'A large number or mass of small animals or insects,\n   especially when in motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarm', 'n.', 'Especially, a great number of honeybees which emigrate from\n   a hive at once, and seek new lodgings under the direction of a queen; a\n   like body of bees settled permanently in a hive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarm', 'n.', 'Hence, any great number or multitude, as of people in\n   motion, or sometimes of inanimate objects; as, a swarm of meteorites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarmed', 'imp. & p. p.', 'of Swarm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarming', 'p. pr. & vb. n.', 'of Swarm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarm', 'v. i.', 'To collect, and depart from a hive by flight in a body;\n   -- said of bees; as, bees swarm in warm, clear days in summer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarm', 'v. i.', 'To appear or collect in a crowd; to throng together; to\n   congregate in a multitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarm', 'v. i.', 'To be crowded; to be thronged with a multitude of beings\n   in motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarm', 'v. i.', 'To abound; to be filled (with).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarm', 'v. i.', 'To breed multitudes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarm', 'v. t.', 'To crowd or throng.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarmspore', 'n.', 'One of innumerable minute, motile, reproductive bodies,\n   produced asexually by certain algae and fungi; a zoospore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarmspore', 'n.', 'One of the minute flagellate germs produced by the\n   sporulation of a protozoan; -- called also zoospore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swart', 'n.', 'Sward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swart', 'a.', 'Of a dark hue; moderately black; swarthy; tawny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swart', 'a.', 'Gloomy; malignant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swart', 'v. t.', 'To make swart or tawny; as, to swart a living part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swartback', 'n.', 'The black-backed gull (Larus marinus); -- called also\n   swarbie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarth', 'a.', 'Swart; swarthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarth', 'n.', 'An apparition of a person about to die; a wraith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarth', 'n.', 'Sward; short grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarth', 'n.', 'See Swath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarthily', 'adv.', 'In a swarthy manner; with a tawny hue; duskily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarthiness', 'n.', 'The quality or state of being swarthy; a dusky or dark\n   complexion; tawniness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarthness', 'n.', 'Swarthiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarthy', 'a.', 'Being of a dark hue or dusky complexion; tawny; swart; as,\n   swarthy faces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarthy', 'v. t.', 'To make swarthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swartiness', 'n.', 'Swarthiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swartish', 'a.', 'Somewhat swart, dark, or tawny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swartness', 'n.', 'The quality or state of being swart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarty', 'a.', 'Swarthy; tawny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarve', 'v. i.', 'To swerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swarve', 'v. i.', 'To climb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swash', 'v. t.', 'An oval figure, whose moldings are oblique to the axis of\n   the work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swash', 'v. t.', 'Soft, like fruit too ripe; swashy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swashed', 'imp. & p. p.', 'of Swash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swashing', 'p. pr. & vb. n.', 'of Swash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swash', 'v. i.', 'To dash or flow noisily, as water; to splash; as, water\n   swashing on a shallow place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swash', 'v. i.', 'To fall violently or noisily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swash', 'v. i.', 'To bluster; to make a great noise; to vapor or brag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swash', 'n.', 'Impulse of water flowing with violence; a dashing or\n   splashing of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swash', 'n.', 'A narrow sound or channel of water lying within a sand bank,\n   or between a sand bank and the shore, or a bar over which the sea\n   washes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swash', 'n.', 'Liquid filth; wash; hog mash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swash', 'n.', 'A blustering noise; a swaggering behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swash', 'n.', 'A swaggering fellow; a swasher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swashbuckler', 'n.', 'A bully or braggadocio; a swaggering, boastful\n   fellow; a swaggerer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swasher', 'n.', 'One who makes a blustering show of valor or force of arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swashing', 'a.', 'Swaggering; hectoring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swashing', 'a.', 'Resounding; crushing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swashway', 'n.', 'Same as 4th Swash, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swashy', 'a.', 'Soft, like fruit that is too ripe; quashy; swash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swat', '', 'imp. of Sweat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swatch', 'n.', 'A swath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swatch', 'n.', 'A piece, pattern, or sample, generally of cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swate', '', 'imp. of Sweat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swath', 'v. t.', 'A line of grass or grain cut and thrown together by the\n   scythe in mowing or cradling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swath', 'v. t.', 'The whole sweep of a scythe, or the whole breadth from\n   which grass or grain is cut by a scythe or a machine, in mowing or\n   cradling; as, to cut a wide swath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swath', 'v. t.', 'A band or fillet; a swathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swathed', 'imp. & p. p.', 'of Swathe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swathing', 'p. pr. & vb. n.', 'of Swathe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swathe', 'n.', 'To bind with a swathe, band, bandage, or rollers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swathe', 'n.', 'A bandage; a band; a swath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swather', 'n.', 'A device attached to a mowing machine for raising the\n   uncut fallen grain and marking the limit of the swath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swatte', '', 'imp. of Sweat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swayed', 'imp. & p. p.', 'of Sway');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swaying', 'p. pr. & vb. n.', 'of Sway');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sway', 'v. i.', 'To move or wield with the hand; to swing; to wield; as, to\n   sway the scepter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sway', 'v. i.', 'To influence or direct by power and authority; by\n   persuasion, or by moral force; to rule; to govern; to guide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sway', 'v. i.', 'To cause to incline or swing to one side, or backward and\n   forward; to bias; to turn; to bend; warp; as, reeds swayed by wind;\n   judgment swayed by passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sway', 'v. i.', 'To hoist; as, to sway up the yards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sway', 'v. i.', 'To be drawn to one side by weight or influence; to lean;\n   to incline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sway', 'v. i.', 'To move or swing from side to side; or backward and\n   forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sway', 'v. i.', 'To have weight or influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sway', 'v. i.', 'To bear sway; to rule; to govern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sway', 'n.', 'The act of swaying; a swaying motion; the swing or sweep of a\n   weapon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sway', 'n.', 'Influence, weight, or authority that inclines to one side;\n   as, the sway of desires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sway', 'n.', 'Preponderance; turn or cast of balance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sway', 'n.', 'Rule; dominion; control.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sway', 'n.', 'A switch or rod used by thatchers to bind their work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sway-backed', 'a.', 'Having the back hollow or sagged, whether naturally or\n   as the result of injury or weakness; -- said of horses and other\n   animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sway-bracing', 'n.', 'The horizontal bracing of a bridge, which prevents\n   its swaying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swayed', 'a.', 'Bent down, and hollow in the back; sway-backed; -- said of\n   a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swayful', 'a.', 'Able to sway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swaying', 'n.', 'An injury caused by violent strains or by overloading; --\n   said of the backs of horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swealed', 'imp. & p. p.', 'of Sweal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swealing', 'p. pr. & vb. n.', 'of Sweal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweal', 'v. i.', 'To melt and run down, as the tallow of a candle; to waste\n   away without feeding the flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweal', 'v. t.', 'To singe; to scorch; to swale; as, to sweal a pig by\n   singeing off the hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swore', 'imp.', 'of Swear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sware', '', 'of Swear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sworn', 'p. p.', 'of Swear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swearing', 'p. pr. & vb. n.', 'of Swear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swear', 'v. i.', 'To affirm or utter a solemn declaration, with an appeal\n   to God for the truth of what is affirmed; to make a promise, threat, or\n   resolve on oath; also, to affirm solemnly by some sacred object, or one\n   regarded as sacred, as the Bible, the Koran, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swear', 'v. i.', 'To give evidence on oath; as, to swear to the truth of a\n   statement; he swore against the prisoner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swear', 'v. i.', 'To make an appeal to God in an irreverant manner; to use\n   the name of God or sacred things profanely; to call upon God in\n   imprecation; to curse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swear', 'v. t.', 'To utter or affirm with a solemn appeal to God for the\n   truth of the declaration; to make (a promise, threat, or resolve) under\n   oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swear', 'v. t.', 'To put to an oath; to cause to take an oath; to\n   administer an oath to; -- ofetn followed by in or into; as, to swear\n   witnesses; to swear a jury; to swear in an officer; he was sworn into\n   office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swear', 'v. t.', 'To declare or charge upon oath; as, he swore treason\n   against his friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swear', 'v. t.', 'To appeal to by an oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swearer', 'n.', 'One who swears; one who calls God to witness for the truth\n   of his declaration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swearer', 'n.', 'A profane person; one who uses profane language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swearing', '', 'a. & n. from Swear, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweat', 'imp. & p. p.', 'of Sweat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweated', '', 'of Sweat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swat', '', 'of Sweat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweating', 'p. pr. & vb. n.', 'of Sweat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweat', 'v. i.', 'To excrete sensible moisture from the pores of the skin;\n   to perspire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweat', 'v. i.', 'Fig.: To perspire in toil; to work hard; to drudge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweat', 'v. i.', 'To emit moisture, as green plants in a heap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweat', 'v. t.', 'To cause to excrete moisture from the skin; to cause to\n   perspire; as, his physicians attempted to sweat him by most powerful\n   sudorifics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweat', 'v. t.', 'To emit or suffer to flow from the pores; to exude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweat', 'v. t.', 'To unite by heating, after the application of soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweat', 'v. t.', 'To get something advantageous, as money, property, or\n   labor from (any one), by exaction or oppression; as, to sweat a\n   spendthrift; to sweat laborers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweat', 'v. i.', 'The fluid which is excreted from the skin of an animal;\n   the fluid secreted by the sudoriferous glands; a transparent,\n   colorless, acid liquid with a peculiar odor, containing some fatty\n   acids and mineral matter; perspiration. See Perspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweat', 'v. i.', 'The act of sweating; or the state of one who sweats;\n   hence, labor; toil; drudgery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweat', 'v. i.', 'Moisture issuing from any substance; as, the sweat of hay\n   or grain in a mow or stack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweat', 'v. i.', 'The sweating sickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweat', 'v. i.', 'A short run by a race horse in exercise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweater', 'n.', 'One who sweats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweater', 'n.', 'One who, or that which, causes to sweat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweater', 'n.', 'A sudorific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweater', 'n.', 'A woolen jacket or jersey worn by athletes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweater', 'n.', 'An employer who oppresses his workmen by paying low wages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweatily', 'adv.', 'In a sweaty manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweatiness', 'n.', 'Quality or state of being sweaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweating', '', 'a. & n. from Sweat, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweaty', 'superl.', 'Moist with sweat; as, a sweaty skin; a sweaty garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweaty', 'superl.', 'Consisting of sweat; of the nature of sweat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweaty', 'superl.', 'Causing sweat; hence, laborious; toilsome; difficult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swede', 'n.', 'A native or inhabitant of Sweden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swede', 'n.', 'A Swedish turnip. See under Turnip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swedenborgian', 'n.', 'One who holds the doctrines of the New Jerusalem\n   church, as taught by Emanuel Swedenborg, a Swedish philosopher and\n   religious writer, who was born a. d. 1688 and died 1772. Swedenborg\n   claimed to have intercourse with the spiritual world, through the\n   opening of his spiritual senses in 1745. He taught that the Lord Jesus\n   Christ, as comprehending in himself all the fullness of the Godhead, is\n   the one only God, and that there is a spiritual sense to the\n   Scriptures, which he (Swedenborg) was able to reveal, because he saw\n   the correspondence between natural and spiritual things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swedenborgian', 'a.', 'Of or pertaining to Swedenborg or his views.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swedenborgianism', 'n.', 'The doctrines of the Swedenborgians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swedish', 'a.', 'Of or pertaining to Sweden or its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swedish', 'n.', 'The language of Swedes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweeny', 'n.', 'An atrophy of the muscles of the shoulder in horses; also,\n   atrophy of any muscle in horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swept', 'imp. & p. p.', 'of Sweep');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweeping', 'p. pr. & vb. n.', 'of Sweep');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'v. i.', 'To pass a broom across (a surface) so as to remove loose\n   dirt, dust, etc.; to brush, or rub over, with a broom for the purpose\n   of cleaning; as, to sweep a floor, the street, or a chimney. Used also\n   figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'v. i.', 'To drive or carry along or off with a broom or a brush,\n   or as if with a broom; to remove by, or as if by, brushing; as, to\n   sweep dirt from a floor; the wind sweeps the snow from the hills; a\n   freshet sweeps away a dam, timber, or rubbish; a pestilence sweeps off\n   multitudes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'v. i.', 'To brush against or over; to rub lightly along.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'v. i.', 'To carry with a long, swinging, or dragging motion;\n   hence, to carry in a stately or proud fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'v. i.', 'To strike with a long stroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'v. i.', 'To draw or drag something over; as, to sweep the bottom\n   of a river with a net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'v. i.', 'To pass over, or traverse, with the eye or with an\n   instrument of observation; as, to sweep the heavens with a telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'v. i.', 'To clean rooms, yards, etc., or to clear away dust, dirt,\n   litter, etc., with a broom, brush, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'v. i.', 'To brush swiftly over the surface of anything; to pass\n   with switness and force, as if brushing the surface of anything; to\n   move in a stately manner; as, the wind sweeps across the plain; a woman\n   sweeps through a drawing-room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'v. i.', 'To pass over anything comprehensively; to range through\n   with rapidity; as, his eye sweeps through space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'n.', 'The act of sweeping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'n.', 'The compass or range of a stroke; as, a long sweep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'n.', 'The compass of any turning body or of any motion; as, the\n   sweep of a door; the sweep of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'n.', 'The compass of anything flowing or brushing; as, the flood\n   carried away everything within its sweep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'n.', 'Violent and general destruction; as, the sweep of an\n   epidemic disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'n.', 'Direction and extent of any motion not rectlinear; as, the\n   sweep of a compass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'n.', 'Direction or departure of a curve, a road, an arch, or the\n   like, away from a rectlinear line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'n.', 'One who sweeps; a sweeper; specifically, a chimney sweeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'n.', 'A movable templet for making molds, in loam molding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'n.', 'The mold of a ship when she begins to curve in at the\n   rungheads; any part of a ship shaped in a segment of a circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'n.', 'A large oar used in small vessels, partly to propel them and\n   partly to steer them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'n.', 'The almond furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'n.', 'A long pole, or piece of timber, moved on a horizontal\n   fulcrum fixed to a tall post and used to raise and lower a bucket in a\n   well for drawing water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'n.', 'In the game of casino, a pairing or combining of all the\n   cards on the board, and so removing them all; in whist, the winning of\n   all the tricks (thirteen) in a hand; a slam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep', 'n.', 'The sweeping of workshops where precious metals are worked,\n   containing filings, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweepage', 'n.', 'The crop of hay got in a meadow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweeper', 'n.', 'One who, or that which, sweeps, or cleans by sweeping; a\n   sweep; as, a carpet sweeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweeping', 'a.', 'Cleaning off surfaces, or cleaning away dust, dirt, or\n   litter, as a broom does; moving with swiftness and force; carrying\n   everything before it; including in its scope many persons or things;\n   as, a sweeping flood; a sweeping majority; a sweeping accusation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweepings', 'n. pl.', 'Things collected by sweeping; rubbish; as, the\n   sweepings of a street.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweep-saw', 'n.', 'A bow-saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweepstake', 'n.', 'A winning of all the stakes or prizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweepstake', 'n.', 'A complete removal or carrying away; a clean sweep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweepstakes', 'n.', 'A winning of all the stakes or prizes; a sweepstake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweepstakes', 'sing. / pl.', 'The whole money or other things staked at a\n   horse race, a given sum being put up for each horse, all of which goes\n   to the winner, or is divided among several, as may be previously\n   agreed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweepstakes', 'sing. / pl.', 'A race for all the sums staked or prizes\n   offered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweepwasher', 'n.', 'One who extracts the residuum of precious metals from\n   the sweepings, potsherds, etc., of refineries of gold and silver, or\n   places where these metals are used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweepy', 'a.', 'Moving with a sweeping motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweet', 'superl.', 'Having an agreeable taste or flavor such as that of\n   sugar; saccharine; -- opposed to sour and bitter; as, a sweet beverage;\n   sweet fruits; sweet oranges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweet', 'superl.', 'Pleasing to the smell; fragrant; redolent; balmy; as, a\n   sweet rose; sweet odor; sweet incense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweet', 'superl.', 'Pleasing to the ear; soft; melodious; harmonious; as,\n   the sweet notes of a flute or an organ; sweet music; a sweet voice; a\n   sweet singer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweet', 'superl.', 'Pleasing to the eye; beautiful; mild and attractive;\n   fair; as, a sweet face; a sweet color or complexion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweet', 'superl.', 'Fresh; not salt or brackish; as, sweet water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweet', 'superl.', 'Not changed from a sound or wholesome state.\n   Specifically: (a) Not sour; as, sweet milk or bread. (b) Not state; not\n   putrescent or putrid; not rancid; as, sweet butter; sweet meat or fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweet', 'superl.', 'Plaesing to the mind; mild; gentle; calm; amiable;\n   winning; presuasive; as, sweet manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweet', 'n.', 'That which is sweet to the taste; -- used chiefly in the\n   plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweet', 'n.', 'Confectionery, sweetmeats, preserves, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweet', 'n.', 'Home-made wines, cordials, metheglin, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweet', 'n.', 'That which is sweet or pleasant in odor; a perfume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweet', 'n.', 'That which is pleasing or grateful to the mind; as, the\n   sweets of domestic life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweet', 'n.', 'One who is dear to another; a darling; -- a term of\n   endearment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweet', 'adv.', 'Sweetly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweet', 'v. t.', 'To sweeten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetbread', 'n.', 'Either the thymus gland or the pancreas, the former\n   being called neck, / throat, sweetbread, the latter belly sweetbread.\n   The sweetbreads of ruminants, esp. of the calf, are highly esteemed as\n   food. See Pancreas, and Thymus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetbread', 'n.', 'The pancreas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweet-breasted', 'a.', 'Having a sweet, musical voice, as the nightingale.\n   Cf. Breast, n., 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetbrier', 'n.', 'A kind of rose (Rosa rubiginosa) with minutely\n   glandular and fragrant foliage. The small-flowered sweetbrier is Rosa\n   micrantha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetened', 'imp. & p. p.', 'of Sweeten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetening', 'p. pr. & vb. n.', 'of Sweeten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweeten', 'a.', 'To make sweet to the taste; as, to sweeten tea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweeten', 'a.', 'To make pleasing or grateful to the mind or feelings; as,\n   to sweeten life; to sweeten friendship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweeten', 'a.', 'To make mild or kind; to soften; as, to sweeten the\n   temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweeten', 'a.', 'To make less painful or laborious; to relieve; as, to\n   sweeten the cares of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweeten', 'a.', 'To soften to the eye; to make delicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweeten', 'a.', 'To make pure and salubrious by destroying noxious matter;\n   as, to sweeten rooms or apartments that have been infected; to sweeten\n   the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweeten', 'a.', 'To make warm and fertile; -- opposed to sour; as, to dry\n   and sweeten soils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweeten', 'a.', 'To restore to purity; to free from taint; as, to sweeten\n   water, butter, or meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweeten', 'v. i.', 'To become sweet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetener', 'n.', 'One who, or that which, sweetens; one who palliates;\n   that which moderates acrimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetening', 'n.', 'The act of making sweet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetening', 'n.', 'That which sweetens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetheart', 'n.', 'A lover of mistress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweethearting', 'n.', 'Making love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweeting', 'n.', 'A sweet apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweeting', 'n.', 'A darling; -- a word of endearment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetish', 'a.', 'Somewhat sweet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetly', 'adv.', 'In a sweet manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetmeat', 'n.', 'Fruit preserved with sugar, as peaches, pears, melons,\n   nuts, orange peel, etc.; -- usually in the plural; a confect; a\n   confection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetmeat', 'n.', 'The paint used in making patent leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetmeat', 'n.', 'A boat shell (Crepidula fornicata) of the American\n   coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetness', 'n.', 'The quality or state of being sweet (in any sense of the\n   adjective); gratefulness to the taste or to the smell; agreeableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetroot', 'n.', 'Licorice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweet-scented', 'a.', 'Having a sweet scent or smell; fragrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweet-sop', 'n.', 'A kind of custard apple (Anona squamosa). See under\n   Custard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetwater', 'n.', 'A variety of white grape, having a sweet watery juice;\n   -- also called white sweetwater, and white muscadine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetweed', 'n.', 'A name for two tropical American weeds (Capraria\n   biflora, and Scoparia dulcis) of the Figwort family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetwood', 'n.', 'The true laurel (Laurus nobilis.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetwood', 'n.', 'The timber of the tree Oreodaphne Leucoxylon, growing in\n   Jamaica. The name is also applied to the timber of several other\n   related trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweetwort', 'n.', 'Any plant of a sweet taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweigh', 'n.', 'Sway; movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweinmote', 'n.', 'See Swainmote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swelled', 'imp.', 'of Swell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swelled', 'p. p.', 'of Swell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swollen', '', 'of Swell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swelling', 'p. pr. & vb. n.', 'of Swell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'v. i.', 'To grow larger; to dilate or extend the exterior surface\n   or dimensions, by matter added within, or by expansion of the inclosed\n   substance; as, the legs swell in dropsy; a bruised part swells; a\n   bladder swells by inflation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'v. i.', 'To increase in size or extent by any addition; to\n   increase in volume or force; as, a river swells, and overflows its\n   banks; sounds swell or diminish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'v. i.', 'To rise or be driven into waves or billows; to heave; as,\n   in tempest, the ocean swells into waves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'v. i.', 'To be puffed up or bloated; as, to swell with pride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'v. i.', 'To be inflated; to belly; as, the sails swell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'v. i.', 'To be turgid, bombastic, or extravagant; as, swelling\n   words; a swelling style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'v. i.', 'To protuberate; to bulge out; as, a cask swells in the\n   middle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'v. i.', 'To be elated; to rise arrogantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'v. i.', 'To grow upon the view; to become larger; to expand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'v. i.', 'To become larger in amount; as, many little debts added,\n   swell to a great amount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'v. i.', 'To act in a pompous, ostentatious, or arrogant manner; to\n   strut; to look big.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'v. t.', 'To increase the size, bulk, or dimensions of; to cause to\n   rise, dilate, or increase; as, rains and dissolving snow swell the\n   rivers in spring; immigration swells the population.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'v. t.', 'To aggravate; to heighten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'v. t.', 'To raise to arrogance; to puff up; to inflate; as, to be\n   swelled with pride or haughtiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'v. t.', 'To augment gradually in force or loudness, as the sound\n   of a note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'n.', 'The act of swelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'n.', 'Gradual increase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'n.', 'Increase or augmentation in bulk; protuberance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'n.', 'Increase in height; elevation; rise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'n.', 'Increase of force, intensity, or volume of sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'n.', 'Increase of power in style, or of rhetorical force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'n.', 'A gradual ascent, or rounded elevation, of land; as, an\n   extensive plain abounding with little swells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'n.', 'A wave, or billow; especially, a succession of large waves;\n   the roll of the sea after a storm; as, a heavy swell sets into the\n   harbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'n.', 'A gradual increase and decrease of the volume of sound; the\n   crescendo and diminuendo combined; -- generally indicated by the sign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'n.', 'A showy, dashing person; a dandy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swell', 'a.', 'Having the characteristics of a person of rank and\n   importance; showy; dandified; distinguished; as, a swell person; a\n   swell neighborhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swelldom', 'n.', 'People of rank and fashion; the class of swells,\n   collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swellfish', 'n.', 'Any plectognath fish that dilates itself, as the bur\n   fish, puffer, or diodon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swelling', 'n.', 'The act of that which swells; as, the swelling of rivers\n   in spring; the swelling of the breast with pride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swelling', 'n.', 'A protuberance; a prominence');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swelling', 'n.', 'an unnatural prominence or protuberance; as, a scrofulous\n   swelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swellish', 'a.', 'Dandified; stylish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swelltoad', 'n.', 'A swellfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swelt', '', 'imp. of Swell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swelt', 'v. i.', 'To die; to perish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swelt', 'v. i.', 'To faint; to swoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swelt', 'v. t.', 'To overpower, as with heat; to cause to faint; to\n   swelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweltered', 'imp. & p. p.', 'of Swelter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweltering', 'p. pr. & vb. n.', 'of Swelter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swelter', 'v. i.', 'To be overcome and faint with heat; to be ready to\n   perish with heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swelter', 'v. i.', 'To welter; to soak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swelter', 'v. t.', 'To oppress with heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swelter', 'v. t.', 'To exude, like sweat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweltry', 'v. i.', 'Suffocating with heat; oppressively hot; sultry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swelve', 'v. t.', 'To swallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swept', '', 'imp. & p. p. of Sweep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swerd', 'n. & v.', 'See Sward, n. & v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swerd', 'n.', 'Sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swerved', 'imp. & p. p.', 'of Swerve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swerving', 'p. pr. & vb. n.', 'of Swerve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swerve', 'v. i.', 'To stray; to wander; to rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swerve', 'v. i.', 'To go out of a straight line; to deflect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swerve', 'v. i.', 'To wander from any line prescribed, or from a rule or\n   duty; to depart from what is established by law, duty, custom, or the\n   like; to deviate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swerve', 'v. i.', 'To bend; to incline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swerve', 'v. i.', 'To climb or move upward by winding or turning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swerve', 'v. t.', 'To turn aside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sweven', 'n.', 'A vision seen in sleep; a dream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swich', 'a.', 'Such.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swietenia', 'n.', 'A genus of meliaceous trees consisting of one species\n   (Sweitenia Mahogoni), the mahogany tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swift', 'v. i.', 'Moving a great distance in a short time; moving with\n   celerity or velocity; fleet; rapid; quick; speedy; prompt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swift', 'v. i.', 'Of short continuance; passing away quickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swift', 'adv.', 'Swiftly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swift', 'n.', 'The current of a stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swift', 'n.', 'Any one of numerous species of small, long-winged,\n   insectivorous birds of the family Micropodidae. In form and habits the\n   swifts resemble swallows, but they are destitute of complex vocal\n   muscles and are not singing birds, but belong to a widely different\n   group allied to the humming birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swift', 'n.', 'Any one of several species of lizards, as the pine lizard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swift', 'n.', 'The ghost moth. See under Ghost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swift', 'n.', 'A reel, or turning instrument, for winding yarn, thread,\n   etc.; -- used chiefly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swift', 'n.', 'The main card cylinder of a flax-carding machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swifter', 'n.', 'A rope used to retain the bars of the capstan in their\n   sockets while men are turning it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swifter', 'n.', 'A rope used to encircle a boat longitudinally, to\n   strengthen and defend her sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swifter', 'n.', 'The forward shroud of a lower mast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swifter', 'v. t.', 'To tighten, as slack standing rigging, by bringing the\n   opposite shrouds nearer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swiftfoot', 'a.', 'Nimble; fleet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swiftfoot', 'n.', 'The courser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swiftlet', 'n.', 'Any one of numerous species of small East Indian and\n   Asiatic swifts of the genus Collocalia. Some of the species are noted\n   for furnishing the edible birds nest. See Illust. under Edible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swiftly', 'adv.', 'In a swift manner; with quick motion or velocity;\n   fleetly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swiftness', 'n.', 'The quality or state of being swift; speed; quickness;\n   celerity; velocity; rapidity; as, the swiftness of a bird; the\n   swiftness of a stream; swiftness of descent in a falling body;\n   swiftness of thought, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swig', 'v. t.', 'To drink in long draughts; to gulp; as, to swig cider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swig', 'v. t.', 'To suck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swig', 'n.', 'A long draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swig', 'n.', 'A tackle with ropes which are not parallel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swig', 'n.', 'A beverage consisting of warm beer flavored with spices,\n   lemon, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swig', 'v. t.', 'To castrate, as a ram, by binding the testicles tightly\n   with a string, so that they mortify and slough off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swig', 'v. t.', 'To pull upon (a tackle) by throwing the weight of the body\n   upon the fall between the block and a cleat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swilled', 'imp. & p. p.', 'of Swill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swilling', 'p. pr. & vb. n.', 'of Swill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swill', 'v. t.', 'To wash; to drench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swill', 'n.', 'To drink in great draughts; to swallow greedily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swill', 'n.', 'To inebriate; to fill with drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swill', 'v. i.', 'To drink greedily or swinishly; to drink to excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swill', 'n.', 'The wash, or mixture of liquid substances, given to swine;\n   hogwash; -- called also swillings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swill', 'n.', 'Large draughts of liquor; drink taken in excessive\n   quantities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swiller', 'n.', 'One who swills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swillings', 'n. pl.', 'See Swill, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swam', 'imp.', 'of Swim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swum', '', 'of Swim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swum', 'p. p.', 'of Swim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swimming', 'p. pr. & vb. n.', 'of Swim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swim', 'v. i.', 'To be supported by water or other fluid; not to sink; to\n   float; as, any substance will swim, whose specific gravity is less than\n   that of the fluid in which it is immersed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swim', 'v. i.', 'To move progressively in water by means of strokes with\n   the hands and feet, or the fins or the tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swim', 'v. i.', 'To be overflowed or drenched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swim', 'v. i.', 'Fig.: To be as if borne or floating in a fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swim', 'v. i.', 'To be filled with swimming animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swim', 'v. t.', 'To pass or move over or on by swimming; as, to swim a\n   stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swim', 'v. t.', 'To cause or compel to swim; to make to float; as, to swim\n   a horse across a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swim', 'v. t.', 'To immerse in water that the lighter parts may float; as,\n   to swim wheat in order to select seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swim', 'n.', 'The act of swimming; a gliding motion, like that of one\n   swimming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swim', 'n.', 'The sound, or air bladder, of a fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swim', 'n.', 'A part of a stream much frequented by fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swim', 'v. i.', 'To be dizzy; to have an unsteady or reeling sensation; as,\n   the head swims.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swimbel', 'n.', 'A moaning or sighing sound or noise; a sough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swimmer', 'n.', 'One who swims.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swimmer', 'n.', 'A protuberance on the leg of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swimmer', 'n.', 'A swimming bird; one of the natatores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swimmeret', 'n.', 'One of a series of flat, fringed, and usually bilobed,\n   appendages, of which several pairs occur on the abdominal somites of\n   many crustaceans. They are used as fins in swimming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swimming', 'a.', 'That swims; capable of swimming; adapted to, or used in,\n   swimming; as, a swimming bird; a swimming motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swimming', 'a.', 'Suffused with moisture; as, swimming eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swimming', 'n.', 'The act of one who swims.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swimming', 'a.', 'Being in a state of vertigo or dizziness; as, a swimming\n   brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swimming', 'n.', 'Vertigo; dizziness; as, a swimming in the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swimmingly', 'adv.', 'In an easy, gliding manner, as if swimming; smoothly;\n   successfully; prosperously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swimmingness', 'n.', 'Act or state of swimming; suffusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinck', 'v. & n.', 'See Swink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swindled', 'imp. & p. p.', 'of Swindle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swindling', 'p. pr. & vb. n.', 'of Swindle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swindle', 'v. t.', 'To cheat defraud grossly, or with deliberate artifice;\n   as, to swindle a man out of his property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swindle', 'n.', 'The act or process of swindling; a cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swindler', 'n.', 'One who swindles, or defrauds grossly; one who makes a\n   practice of defrauding others by imposition or deliberate artifice; a\n   cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swindlery', 'n.', 'Swindling; rougery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swine', 'n.', 'Any animal of the hog kind, especially one of the domestical\n   species. Swine secrete a large amount of subcutaneous fat, which, when\n   extracted, is known as lard. The male is specifically called boar, the\n   female, sow, and the young, pig. See Hog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinebread', 'n.', 'The truffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinecase', 'n.', 'A hogsty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinecote', 'n.', 'A hogsty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinecrue', 'n.', 'A hogsty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinefish', 'n.', 'The wolf fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swineherd', 'n.', 'A keeper of swine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinepipe', 'n.', 'The European redwing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swine-pox', 'n.', 'A variety of the chicken pox, with acuminated vesicles\n   containing a watery fluid; the water pox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swineery', 'n.', 'Same as Piggery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinestone', 'n.', 'See Stinkstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinesty', 'n.', 'A sty, or pen, for swine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swung', 'imp. & p. p.', 'of Swing');
