  INSERT INTO words (word, word_type, description) VALUES  (    'Swinging', 'p. pr. & vb. n.', 'of Swing');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swing', 'v. i.', 'To move to and fro, as a body suspended in the air; to\n   wave; to vibrate; to oscillate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swing', 'v. i.', 'To sway or move from one side or direction to another;\n   as, the door swung open.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swing', 'v. i.', 'To use a swing; as, a boy swings for exercise or\n   pleasure. See Swing, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swing', 'n.', 'To turn round by action of wind or tide when at anchor; as,\n   a ship swings with the tide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swing', 'n.', 'To be hanged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swing', 'v. t.', 'To cause to swing or vibrate; to cause to move backward\n   and forward, or from one side to the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swing', 'v. t.', 'To give a circular movement to; to whirl; to brandish;\n   as, to swing a sword; to swing a club; hence, colloquially, to manage;\n   as, to swing a business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swing', 'v. t.', 'To admit or turn (anything) for the purpose of shaping\n   it; -- said of a lathe; as, the lathe can swing a pulley of 12 inches\n   diameter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swing', 'n.', 'The act of swinging; a waving, oscillating, or vibratory\n   motion of a hanging or pivoted object; oscillation; as, the swing of a\n   pendulum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swing', 'n.', 'Swaying motion from one side or direction to the other; as,\n   some men walk with a swing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swing', 'n.', 'A line, cord, or other thing suspended and hanging loose,\n   upon which anything may swing; especially, an apparatus for recreation\n   by swinging, commonly consisting of a rope, the two ends of which are\n   attached overhead, as to the bough of a tree, a seat being placed in\n   the loop at the bottom; also, any contrivance by which a similar motion\n   is produced for amusement or exercise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swing', 'n.', 'Influence of power of a body put in swaying motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swing', 'n.', 'Capacity of a turning lathe, as determined by the diameter\n   of the largest object that can be turned in it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swing', 'n.', 'Free course; unrestrained liberty or license; tendency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swingdevil', 'n.', 'The European swift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinge', 'v. & n.', 'See Singe.');
