  INSERT INTO words (word, word_type, description) VALUES  (    'Swingeing', 'p. pr. & vb. n.', 'of Swinge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinge', 'v. t.', 'To beat soundly; to whip; to chastise; to punish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinge', 'v. t.', 'To move as a lash; to lash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinge', 'n.', 'The sweep of anything in motion; a swinging blow; a swing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinge', 'n.', 'Power; sway; influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swingebuckler', 'n.', 'A swashbuckler; a bully; a roisterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swingeing', 'a.', 'Huge; very large.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swingel', 'n.', 'The swinging part of a flail which falls on the grain in\n   thrashing; the swiple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinger', 'n.', 'One who swings or whirls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinger', 'n.', 'One who swinges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinger', 'n.', 'Anything very large, forcible, or astonishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinger', 'n.', 'A person who engages frequently in lively and fashionable\n   pursuits, such as attending night clubs or discos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinger', 'n.', 'A person who engages freely in sexual intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swingle', 'v. i.', 'To dangle; to wave hanging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swingle', 'v. i.', 'To swing for pleasure.');
