  INSERT INTO words (word, word_type, description) VALUES  (    'Swingling', 'p. pr. & vb. n.', 'of Swingle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swingle', 'v. t.', 'To clean, as flax, by beating it with a swingle, so as\n   to separate the coarse parts and the woody substance from it; to\n   scutch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swingle', 'v. t.', 'To beat off the tops of without pulling up the roots;\n   -- said of weeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swingle', 'n.', 'A wooden instrument like a large knife, about two feet\n   long, with one thin edge, used for beating and cleaning flax; a\n   scutcher; -- called also swingling knife, swingling staff, and\n   swingling wand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swinglebar', 'n.', 'A swingletree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swingletail', 'n.', 'The thrasher, or fox shark. See Thrasher.');
