  INSERT INTO words (word, word_type, description) VALUES  (    'Swiss', 'a.', 'Of or pertaining to Switzerland, or the people of\n   Switzerland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Switch', 'n.', 'A small, flexible twig or rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Switch', 'n.', 'A movable part of a rail; or of opposite rails, for\n   transferring cars from one track to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Switch', 'n.', 'A separate mass or trees of hair, or of some substance (at\n   jute) made to resemble hair, worn on the head by women.');
