  INSERT INTO words (word, word_type, description) VALUES  (    'Switch', 'v. t.', 'To trim, as, a hedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Switch', 'v. t.', 'To turn from one railway track to another; to transfer\n   by a switch; -- generally with off, from, etc.; as, to switch off a\n   train; to switch a car from one track to another.');
