  INSERT INTO words (word, word_type, description) VALUES  (    'Sword', 'n.', 'An offensive weapon, having a long and usually sharp/pointed\n   blade with a cutting edge or edges. It is the general term, including\n   the small sword, rapier, saber, scimiter, and many other varieties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sword', 'n.', 'Hence, the emblem of judicial vengeance or punishment, or of\n   authority and power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sword', 'n.', 'Destruction by the sword, or in battle; war; dissension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sword', 'n.', 'The military power of a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sword', 'n.', 'One of the end bars by which the lay of a hand loom is\n   suspended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swordbill', 'n.', 'A humming bird (Docimastes ensiferus) having a very\n   long, slender bill, exceeding the length of the body of the bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sworded', 'a.', 'Girded with a sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sworder', 'n.', 'One who uses, or fights with, a sword; a swordsman; a\n   soldier; a cutthroat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swordfish', 'n.', 'A very large oceanic fish (Xiphias gladius), the only\n   representative of the family Xiphiidae. It is highly valued as a food\n   fish. The bones of the upper jaw are consolidated, and form a long,\n   rigid, swordlike beak; the dorsal fin is high and without distinct\n   spines; the ventral fins are absent. The adult is destitute of teeth.\n   It becomes sixteen feet or more long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swordfish', 'n.', 'The gar pike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swordfish', 'n.', 'The cutlass fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Swordfish', 'n.', 'A southern constellation. See Dorado, 1.');
