  INSERT INTO words (word, word_type, description) VALUES  (    'Sychnocarpous', 'a.', 'Having the capacity of bearing several successive\n   crops of fruit without perishing; as, sychnocarpous plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycite', 'n.', 'A nodule of flint, or a pebble, which resembles a fig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycoceric', 'a.', 'Of, pertaining to, or designating, an acid obtained by\n   the oxidation of sycoceryl alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycoceryl', 'n.', 'A radical, of the aromatic series, regarded as an\n   essential ingredient of certain compounds found in the waxy resin of an\n   Australian species of fig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycock', 'n.', 'The missel thrush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycones', 'n. pl.', 'A division of calcareous sponges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syconium', 'n.', 'Alt. of Syconus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syconus', 'n.', 'A collective fleshy fruit, in which the ovaries are hidden\n   within a hollow receptacle, as in the fig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycophancy', 'n.', 'The character or characteristic of a sycophant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycophancy', 'n.', 'False accusation; calumniation; talebearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycophancy', 'n.', 'Obsequious flattery; servility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycophant', 'n.', 'An informer; a talebearer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycophant', 'n.', 'A base parasite; a mean or servile flatterer;\n   especially, a flatterer of princes and great men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycophant', 'v. t.', 'To inform against; hence, to calumniate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycophant', 'v. t.', 'To play the sycophant toward; to flatter\n   obsequiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycophant', 'v. i.', 'To play the sycophant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycophantcy', 'n.', 'Sycophancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycophantic', 'a.', 'Alt. of Sycophantical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycophantical', 'a.', 'Of or pertaining to a sycophant; characteristic of a\n   sycophant; meanly or obsequiously flattering; courting favor by mean\n   adulation; parasitic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycophantish', 'a.', 'Like a sycophant; obsequiously flattering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycophantism', 'n.', 'Sycophancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycophantize', 'v. i.', 'To play the sycophant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycophantry', 'n.', 'Sycophancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sycosis', 'n.', 'A pustular eruption upon the scalp, or the beared part of\n   the face, whether due to ringworm, acne, or impetigo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syderolite', 'n.', 'A kind of Bohemian earthenware resembling the Wedgwood\n   ware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sye', 'imp.', 'Saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syenite', 'n.', 'Orig., a rock composed of quartz, hornblende, and\n   feldspar, anciently quarried at Syene, in Upper Egypt, and now called\n   granite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syenite', 'n.', 'A granular, crystalline, ingeous rock composed of\n   orthoclase and hornblende, the latter often replaced or accompanied by\n   pyroxene or mica. Syenite sometimes contains nephelite (elaeolite) or\n   leucite, and is then called nephelite (elaeolite) syenite or leucite\n   syenite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syenitic', 'a.', 'Relating to Syene; as, Syenitic inscriptions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syenitic', 'a.', 'Relating to, or like, syenite; as, syenitic granite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syke', 'n. & v.', 'See Sike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syker', 'a. & adv.', 'See Sicker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syle', 'n.', 'A young herring (Clupea harengus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabaria', 'pl. ', 'of Syllabarium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabarium', 'n.', 'A syllabary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabary', 'n.', 'A table of syllables; more especially, a table of the\n   indivisible syllabic symbols used in certain languages, as the Japanese\n   and Cherokee, instead of letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabe', 'n.', 'Syllable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabic', 'a.', 'Alt. of Syllabical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabical', 'a.', 'Of or pertaining to a syllable or syllables; as,\n   syllabic accent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabical', 'a.', 'Consisting of a syllable or syllables; as, a syllabic\n   augment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabically', 'adv.', 'In a syllabic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabicated', 'imp. & p. p.', 'of Syllabicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabicating', 'p. pr. & vb. n.', 'of Syllabicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabicate', 'v. t.', 'To form or divide into syllables; to syllabify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabication', 'n.', 'The act of forming syllables; the act or method of\n   dividing words into syllables. See Guide to Pron., /275.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabification', 'n.', 'Same as Syllabication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabified', 'imp. & p. p.', 'of Syllabify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabifying', 'p. pr. & vb. n.', 'of Syllabify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabify', 'v. t.', 'To form or divide into syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabism', 'n.', 'The expressing of the sounds of a language by syllables,\n   rather than by an alphabet or by signs for words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabist', 'n.', 'One who forms or divides words into syllables, or is\n   skilled in doing this.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabize', 'v. t.', 'To syllabify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllable', 'n.', 'An elementary sound, or a combination of elementary\n   sounds, uttered together, or with a single effort or impulse of the\n   voice, and constituting a word or a part of a word. In other terms, it\n   is a vowel or a diphtong, either by itself or flanked by one or more\n   consonants, the whole produced by a single impulse or utterance. One of\n   the liquids, l, m, n, may fill the place of a vowel in a syllable.\n   Adjoining syllables in a word or phrase need not to be marked off by a\n   pause, but only by such an abatement and renewal, or reenforcement, of\n   the stress as to give the feeling of separate impulses. See Guide to\n   Pronunciation, /275.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllable', 'n.', 'In writing and printing, a part of a word, separated from\n   the rest, and capable of being pronounced by a single impulse of the\n   voice. It may or may not correspond to a syllable in the spoken\n   language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllable', 'n.', 'A small part of a sentence or discourse; anything concise\n   or short; a particle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllable', 'v. t.', 'To pronounce the syllables of; to utter; to\n   articulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabub', 'n.', 'Same as Syllabub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabuses', 'pl. ', 'of Syllabus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabi', 'pl. ', 'of Syllabus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllabus', 'n.', 'A compendium containing the heads of a discourse, and the\n   like; an abstract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllepsis', 'n.', 'A figure of speech by which a word is used in a literal\n   and metaphorical sense at the same time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllepsis', 'n.', 'The agreement of a verb or adjective with one, rather\n   than another, of two nouns, with either of which it might agree in\n   gender, number, etc.; as, rex et regina beati.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylleptic', 'a.', 'Alt. of Sylleptical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylleptical', 'a.', 'Of or pertaining to a syllepsis; containing syllepsis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllidian', 'n.', 'Any one of numerous species of marine annelids of the\n   family Syllidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllogism', 'n.', 'The regular logical form of every argument, consisting\n   of three propositions, of which the first two are called the premises,\n   and the last, the conclusion. The conclusion necessarily follows from\n   the premises; so that, if these are true, the conclusion must be true,\n   and the argument amounts to demonstration');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllogistic', 'a.', 'Alt. of Syllogistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllogistical', 'a.', 'Of or pertaining to a syllogism; consisting of a\n   syllogism, or of the form of reasoning by syllogisms; as, syllogistic\n   arguments or reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllogistically', 'adv.', 'In a syllogistic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllogization', 'n.', 'A reasoning by syllogisms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllogized', 'imp. & p. p.', 'of Syllogize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllogizing', 'p. pr. & vb. n.', 'of Syllogize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllogize', 'v. i.', 'To reason by means of syllogisms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syllogizer', 'n.', 'One who syllogizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylph', 'n.', 'An imaginary being inhabiting the air; a fairy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylph', 'n.', 'Fig.: A slender, graceful woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylph', 'n.', 'Any one of several species of very brilliant South American\n   humming birds, having a very long and deeply-forked tail; as, the\n   blue-tailed sylph (Cynanthus cyanurus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylphid', 'n.', 'A little sylph; a young or diminutive sylph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylphine', 'a.', 'Like a sylph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylphish', 'a.', 'Sylphlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylphlike', 'a.', 'Like a sylph; airy; graceful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylvae', 'pl. ', 'of Sylva');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylva', 'n.', 'Same as Silva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylvan', 'a.', 'Of or pertaining to a sylva; forestlike; hence, rural;\n   rustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylvan', 'a.', 'Abounding in forests or in trees; woody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylvan', 'a.', 'A fabled deity of the wood; a satyr; a faun; sometimes, a\n   rustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylvan', 'n.', 'A liquid hydrocarbon obtained together with furfuran\n   (tetrol) by the distillation of pine wood; -- called also methyl\n   tetrol, or methyl furfuran.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylvanite', 'n.', 'A mineral, a telluride of gold and silver, of a\n   steel-gray, silver-white, or brass-yellow color. It often occurs in\n   implanted crystals resembling written characters, and hence is called\n   graphic tellurium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylvanium', 'n.', 'An old name for tellurium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylvate', 'n.', 'A salt of sylvic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylvatic', 'a.', 'Sylvan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylvestrian', 'a.', 'Sylvan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylvic', 'a.', 'Of, pertaining to, or resembling, pine or its products;\n   specifically, designating an acid called also abeitic acid, which is\n   the chief ingredient of common resin (obtained from Pinus sylvestris,\n   and other species).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylvicoline', 'a.', 'Of or pertaining to the family of warblers\n   (Sylvicolidae). See Warbler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylviculture', 'n.', 'The cultivation of forest trees for timber or other\n   purposes; forestry; arboriculture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylviculturist', 'n.', 'One who cultivates forest trees, especially as a\n   business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylvine', 'n.', 'Alt. of Sylvite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sylvite', 'n.', 'Native potassium chloride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sym-', '', 'See Syn-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symar', 'n.', 'Alt. of Symarr');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symarr', 'n.', 'See Simar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbal', 'n.', 'See Cimbal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbol', 'n.', 'A visible sign or representation of an idea; anything which\n   suggests an idea or quality, or another thing, as by resemblance or by\n   convention; an emblem; a representation; a type; a figure; as, the lion\n   is the symbol of courage; the lamb is the symbol of meekness or\n   patience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbol', 'n.', 'Any character used to represent a quantity, an operation, a\n   relation, or an abbreviation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbol', 'n.', 'An abstract or compendium of faith or doctrine; a creed, or\n   a summary of the articles of religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbol', 'n.', 'That which is thrown into a common fund; hence, an\n   appointed or accustomed duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbol', 'n.', 'Share; allotment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbol', 'n.', 'An abbreviation standing for the name of an element and\n   consisting of the initial letter of the Latin or New Latin name, or\n   sometimes of the initial letter with a following one; as, C for carbon,\n   Na for sodium (Natrium), Fe for iron (Ferrum), Sn for tin (Stannum), Sb\n   for antimony (Stibium), etc. See the list of names and symbols under\n   Element.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbol', 'v. t.', 'To symbolize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolic', 'a.', 'See Symbolics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolic', 'a.', 'Alt. of Symbolical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolical', 'a.', 'Of or pertaining to a symbol or symbols; of the nature\n   of a symbol; exhibiting or expressing by resemblance or signs;\n   representative; as, the figure of an eye is symbolic of sight and\n   knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolics', 'n.', 'The study of ancient symbols');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolics', 'n.', 'that branch of historic theology which treats of creeds\n   and confessions of faith; symbolism; -- called also symbolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolism', 'n.', 'The act of symbolizing, or the state of being\n   symbolized; as, symbolism in Christian art is the representation of\n   truth, virtues, vices, etc., by emblematic colors, signs, and forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolism', 'n.', 'A system of symbols or representations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolism', 'n.', 'The practice of using symbols, or the system of notation\n   developed thereby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolism', 'n.', 'A combining together of parts or ingredients.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolism', 'n.', 'The science of creeds; symbolics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolist', 'n.', 'One who employs symbols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolistic', 'a.', 'Alt. of Symbolistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolistical', 'a.', 'Characterized by the use of symbols; as, symbolistic\n   poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolization', 'n.', 'The act of symbolizing; symbolical representation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolized', 'imp. & p. p.', 'of Symbolize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolizing', 'p. pr. & vb. n.', 'of Symbolize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolize', 'v. i.', 'To have a resemblance of qualities or properties; to\n   correspond; to harmonize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolize', 'v. i.', 'To hold the same faith; to agree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolize', 'v. i.', 'To use symbols; to represent ideas symbolically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolize', 'v. t.', 'To make to agree in properties or qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolize', 'v. t.', 'To make representative of something; to regard or\n   treat as symbolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolize', 'v. t.', 'To represent by a symbol or symbols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbolizer', 'n.', 'One who symbolizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbological', 'a.', 'Pertaining to a symbology; versed in, or\n   characterized by, symbology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbologist', 'n.', 'One who practices, or who is versed in, symbology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbology', 'n.', 'The art of expressing by symbols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symbranchii', 'n. pl.', 'An order of slender eel-like fishes having the\n   gill openings confluent beneath the neck. The pectoral arch is\n   generally attached to the skull, and the entire margin of the upper jaw\n   is formed by the premaxillary. Called also Symbranchia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symmetral', 'a.', 'Commensurable; symmetrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symmetrian', 'n.', 'One eminently studious of symmetry of parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symmetric', 'a.', 'Symmetrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symmetrical', 'a.', 'Involving or exhibiting symmetry; proportional in\n   parts; having its parts in due proportion as to dimensions; as, a\n   symmetrical body or building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symmetrical', 'a.', 'Having the organs or parts of one side corresponding\n   with those of the other; having the parts in two or more series of\n   organs the same in number; exhibiting a symmetry. See Symmetry, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symmetrical', 'a.', 'Having an equal number of parts in the successive\n   circles of floral organs; -- said of flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symmetrical', 'a.', 'Having a likeness in the form and size of floral\n   organs of the same kind; regular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symmetrical', 'a.', 'Having a common measure; commensurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symmetrical', 'a.', 'Having corresponding parts or relations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symmetrician', 'n.', 'Same as Symmetrian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symmetrist', 'n.', 'One eminently studious of symmetry of parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symmetrized', 'imp. & p. p.', 'of Symmetrize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symmetrizing', 'p. pr. & vb. n.', 'of Symmetrize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symmetrize', 'v. t.', 'To make proportional in its parts; to reduce to\n   symmetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symmetry', 'n.', 'A due proportion of the several parts of a body to each\n   other; adaptation of the form or dimensions of the several parts of a\n   thing to each other; the union and conformity of the members of a work\n   to the whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symmetry', 'n.', 'The law of likeness; similarity of structure; regularity\n   in form and arrangement; orderly and similar distribution of parts,\n   such that an animal may be divided into parts which are structurally\n   symmetrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symmetry', 'n.', 'Equality in the number of parts of the successive circles\n   in a flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symmetry', 'n.', 'Likeness in the form and size of floral organs of the\n   same kind; regularity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathetic', 'a.', 'Inclined to sympathy; sympathizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathetic', 'a.', 'Produced by, or expressive of, sympathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathetic', 'a.', 'Produced by sympathy; -- applied particularly to\n   symptoms or affections. See Sympathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathetic', 'a.', 'Of or relating to the sympathetic nervous system or\n   some of its branches; produced by stimulation on the sympathetic\n   nervious system or some part of it; as, the sympathetic saliva, a\n   modified form of saliva, produced from some of the salivary glands by\n   stimulation of a sympathetic nerve fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathetical', 'a.', 'Sympathetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathetically', 'adv.', 'In a sympathetic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathist', 'n.', 'One who sympathizes; a sympathizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathized', 'imp. & p. p.', 'of Sympathize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathizing', 'p. pr. & vb. n.', 'of Sympathize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathize', 'v. i.', 'To have a common feeling, as of bodily pleasure or\n   pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathize', 'v. i.', 'To feel in consequence of what another feels; to be\n   affected by feelings similar to those of another, in consequence of\n   knowing the person to be thus affected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathize', 'v. i.', 'To agree; to be in accord; to harmonize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathize', 'v. t.', 'To experience together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathize', 'v. t.', 'To ansew to; to correspond to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathizer', 'n.', 'One who sympathizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathies', 'pl. ', 'of Sympathy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathy', 'n.', 'Feeling corresponding to that which another feels; the\n   quality of being affected by the affection of another, with feelings\n   correspondent in kind, if not in degree; fellow-feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathy', 'n.', 'An agreement of affections or inclinations, or a\n   conformity of natural temperament, which causes persons to be pleased,\n   or in accord, with one another; as, there is perfect sympathy between\n   them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathy', 'n.', 'Kindness of feeling toward one who suffers; pity;\n   commiseration; compassion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathy', 'n.', 'The reciprocal influence exercised by the various organs\n   or parts of the body on one another, as manifested in the transmission\n   of a disease by unknown means from one organ to another quite remote,\n   or in the influence exerted by a diseased condition of one part on\n   another part or organ, as in the vomiting produced by a tumor of the\n   brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathy', 'n.', 'That relation which exists between different persons by\n   which one of them produces in the others a state or condition like that\n   of himself. This is shown in the tendency to yawn which a person often\n   feels on seeing another yawn, or the strong inclination to become\n   hysteric experienced by many women on seeing another person suffering\n   with hysteria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathy', 'n.', 'A tendency of inanimate things to unite, or to act on\n   each other; as, the sympathy between the loadstone and iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympathy', 'n.', 'Similarity of function, use office, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympetalous', 'a.', 'Having the petals united; gamopetalous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphonic', 'a.', 'Symphonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphonic', 'a.', 'Relating to, or in the manner of, symphony; as, the\n   symphonic form or style of composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphonious', 'a.', 'Agreeing in sound; accordant; harmonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphonious', 'a.', 'Symphonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphonist', 'n.', 'A composer of symphonies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphonized', 'imp. & p. p.', 'of Symphonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphonizing', 'p. pr. & vb. n.', 'of Symphonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphonize', 'v. i.', 'To agree; to be in harmony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphonies', 'pl. ', 'of Symphony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphony', 'n.', 'A consonance or harmony of sounds, agreeable to the ear,\n   whether the sounds are vocal or instrumental, or both.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphony', 'n.', 'A stringed instrument formerly in use, somewhat\n   resembling the virginal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphony', 'n.', 'An elaborate instrumental composition for a full\n   orchestra, consisting usually, like the sonata, of three or four\n   contrasted yet inwardly related movements, as the allegro, the adagio,\n   the minuet and trio, or scherzo, and the finale in quick time. The term\n   has recently been applied to large orchestral works in freer form, with\n   arguments or programmes to explain their meaning, such as the\n   \"symphonic poems\" of Liszt. The term was formerly applied to any\n   composition for an orchestra, as overtures, etc., and still earlier, to\n   certain compositions partly vocal, partly instrumental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphony', 'n.', 'An instrumental passage at the beginning or end, or in\n   the course of, a vocal composition; a prelude, interlude, or postude; a\n   ritornello.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphyla', 'n. pl.', 'An order of small apterous insects having an\n   elongated body, with three pairs of thoracic and about nine pairs of\n   abdominal legs. They are, in many respects, intermediate between\n   myriapods and true insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphyseal', 'a.', 'Of or pertaining to to symphysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphyseotomy', 'n.', 'The operation of dividing the symphysis pubis for\n   the purpose of facilitating labor; -- formerly called the Sigualtian\n   section.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphyses', 'pl. ', 'of Symphysis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphysis', 'n.', 'An articulation formed by intervening cartilage; as, the\n   pubic symphysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphysis', 'n.', 'The union or coalescence of bones; also, the place of\n   union or coalescence; as, the symphysis of the lower jaw. Cf.\n   Articulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphysotomy', 'n.', 'Symphyseotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symphytism', 'n.', 'Coalescence; a growing into one with another word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympiesometer', 'n.', 'A sensitive kind of barometer, in which the pressure\n   of the atmosphere, acting upon a liquid, as oil, in the lower portion\n   of the instrument, compresses an elastic gas in the upper part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symplectic', 'a.', 'Plaiting or joining together; -- said of a bone next\n   above the quadrate in the mandibular suspensorium of many fishes, which\n   unites together the other bones of the suspensorium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symplectic', 'n.', 'The symplectic bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symploce', 'n.', 'The repetition of a word or phrase at the beginning and\n   another at the end of successive clauses; as, Justice came down from\n   heaven to view the earth; Justice returned to heaven, and left the\n   earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympode', 'n.', 'A sympodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympodial', 'a.', 'Composed of superposed branches in such a way as to\n   imitate a simple axis; as, a sympodial stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympodia', 'pl. ', 'of Sympodium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sympodium', 'n.', 'An axis or stem produced by dichotomous branching in\n   which one of the branches is regularly developed at the expense of the\n   other, as in the grapevine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symposiac', 'a.', 'Of or pertaining to compotations and merrymaking;\n   happening where company is drinking together; as, symposiac meetings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symposiac', 'n.', 'A conference or conversation of philosophers at a\n   banquet; hence, any similar gathering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symposiarch', 'n.', 'The master of a feast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symposiast', 'n.', 'One engaged with others at a banquet or merrymaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symposion', 'n.', 'A drinking together; a symposium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symposia', 'pl. ', 'of Symposium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symposium', 'n.', 'A drinking together; a merry feast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symposium', 'n.', 'A collection of short essays by different authors on a\n   common topic; -- so called from the appellation given to the\n   philosophical dialogue by the Greeks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symptom', 'n.', 'Any affection which accompanies disease; a perceptible\n   change in the body or its functions, which indicates disease, or the\n   kind or phases of disease; as, the causes of disease often lie beyond\n   our sight, but we learn their nature by the symptoms exhibited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symptom', 'n.', 'A sign or token; that which indicates the existence of\n   something else; as, corruption in elections is a symptom of the decay\n   of public virtue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symptomatic', 'a.', 'Alt. of Symptomatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symptomatical', 'a.', 'Of or pertaining to symptoms; happening in\n   concurrence with something; being a symptom; indicating the existence\n   of something else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symptomatical', 'a.', 'According to symptoms; as, a symptomatical\n   classification of diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Symptomatology', 'n.', 'The doctrine of symptoms; that part of the science\n   of medicine which treats of the symptoms of diseases; semeiology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syn-', '', 'A prefix meaning with, along with, together, at the same time.\n   Syn- becomes sym- before p, b, and m, and syl- before l.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synacme', 'n.', 'Alt. of Synacmy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synacmy', 'n.', 'Same as Synanthesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synaeresis', 'n.', 'Alt. of Syneresis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syneresis', 'n.', 'The union, or drawing together into one syllable, of two\n   vowels that are ordinarily separated in syllabification; synecphonesis;\n   -- the opposite of diaeresis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synagogical', 'a.', 'Of or pertaining to a synagogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synagogue', 'n.', 'A congregation or assembly of Jews met for the purpose\n   of worship, or the performance of religious rites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synagogue', 'n.', 'The building or place appropriated to the religious\n   worship of the Jews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synagogue', 'n.', 'The council of, probably, 120 members among the Jews,\n   first appointed after the return from the Babylonish captivity; --\n   called also the Great Synagogue, and sometimes, though erroneously, the\n   Sanhedrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synagogue', 'n.', 'A congregation in the early Christian church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synagogue', 'n.', 'Any assembly of men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synalepha', 'n.', 'A contraction of syllables by suppressing some vowel or\n   diphthong at the end of a word, before another vowel or diphthong; as,\n   th army, for the army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synallagmatic', 'a.', 'Imposing reciprocal obligations upon the parties;\n   as, a synallagmatic contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synallaxine', 'a.', 'Having the outer and middle toes partially united; --\n   said of certain birds related to the creepers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synaloepha', 'n.', 'Same as Synalepha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synangia', 'pl. ', 'of Synangium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synangium', 'n.', 'The divided part beyond the pylangium in the aortic\n   trunk of the amphibian heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synantherous', 'a.', 'Having the stamens united by their anthers; as,\n   synantherous flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synanthesis', 'n.', 'The simultaneous maturity of the anthers and stigmas\n   of a blossom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synanthous', 'a.', 'Having flowers and leaves which appear at the same\n   time; -- said of certain plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synanthrose', 'n.', 'A variety of sugar, isomeric with sucrose, found in\n   the tubers of the Jerusalem artichoke (Helianthus tuberosus), in the\n   dahlia, and other Compositae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synapta', 'n.', 'A genus of slender, transparent holothurians which have\n   delicate calcareous anchors attached to the dermal plates. See\n   Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synaptase', 'n.', 'A ferment resembling diastase, found in bitter almonds.\n   Cf. Amygdalin, and Emulsin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synapticulae', 'pl. ', 'of Synapticula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synapticula', 'n.', 'One of numerous calcareous processes which extend\n   between, and unite, the adjacent septa of certain corals, especially of\n   the fungian corals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synarchy', 'n.', 'Joint rule or sovereignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synartesis', 'n.', 'A fastening or knitting together; the state of being\n   closely jointed; close union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synarthrodia', 'n.', 'Synarthrosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synarthroses', 'pl. ', 'of Synarthrosis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synarthrosis', 'n.', 'Immovable articulation by close union, as in sutures.\n   It sometimes includes symphysial articulations also. See the Note under\n   Articulation, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synastry', 'n.', 'Concurrence of starry position or influence; hence,\n   similarity of condition, fortune, etc., as prefigured by astrological\n   calculation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synaxis', 'n.', 'A congregation; also, formerly, the Lords Supper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncarp', 'n.', 'A kind of aggregate fruit in which the ovaries cohere in a\n   solid mass, with a slender receptacle, as in the magnolia; also, a\n   similar multiple fruit, as a mulberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncarpia', 'pl. ', 'of Syncarpium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncarpium', 'n.', 'Same as Syncarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncarpous', 'a.', 'Composed of several carpels consolidated into one\n   ovary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncategorematic', 'a.', 'Not capable of being used as a term by itself; --\n   said of words, as an adverb or preposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchondroses', 'pl. ', 'of Synchondrosis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchondrosis', 'n.', 'An immovable articulation in which the union is\n   formed by cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchondrotomy', 'n.', 'Symphyseotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchoresis', 'n.', 'A concession made for the purpose of retorting with\n   greater force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchronal', 'a.', 'Happening at, or belonging to, the same time;\n   synchronous; simultaneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchronal', 'n.', 'A synchronal thing or event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchronical', 'a.', 'Happening at the same time; synchronous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchronism', 'n.', 'The concurrence of events in time; simultaneousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchronism', 'n.', 'The tabular arrangement of historical events and\n   personages, according to their dates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchronism', 'n.', 'A representation, in the same picture, of two or\n   events which occured at different times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchronistic', 'a.', 'Of or pertaining to synchronism; arranged according\n   to correspondence in time; as, synchronistic tables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchronization', 'n.', 'The act of synchronizing; concurrence of events in\n   respect to time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchronized', 'imp. & p. p.', 'of Synchronize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchronizing', 'p. pr. & vb. n.', 'of Synchronize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchronize', 'v. i.', 'To agree in time; to be simultaneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchronize', 'v. t.', 'To assign to the same date or period of time; as,\n   to synchronize two events of Greek and Roman history.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchronize', 'v. t.', 'To cause to agree in time; as, to synchronize the\n   movements of different machines; to synchronize clocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchronology', 'n.', 'Contemporaneous chronology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchronous', 'a.', 'Happening at the same time; simultaneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchrony', 'n.', 'The concurrence of events in time; synchronism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synchysis', 'n.', 'A derangement or confusion of any kind, as of words in a\n   sentence, or of humors in the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synclastic', 'a.', 'Curved toward the same side in all directions; -- said\n   of surfaces which in all directions around any point bend away from a\n   tangent plane toward the same side, as the surface of a sphere; --\n   opposed to anticlastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synclinal', 'a.', 'Inclined downward from opposite directions, so as to\n   meet in a common point or line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synclinal', 'a.', 'Formed by strata dipping toward a common line or plane;\n   as, a synclinal trough or valley; a synclinal fold; -- opposed to\n   anticlinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synclinal', 'n.', 'A synclinal fold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncline', 'n.', 'A synclinal fold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synclinical', 'a.', 'Synclinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synclinoria', 'pl. ', 'of Synclinorium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synclinorium', 'n.', 'A mountain range owing its origin to the progress of\n   a geosynclinal, and ending in a catastrophe of displacement and\n   upturning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncopal', 'a.', 'Of or pertaining to syncope; resembling syncope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncopated', 'imp. & p. p.', 'of Syncopate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncopating', 'p. pr. & vb. n.', 'of Syncopate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncopate', 'v. t.', 'To contract, as a word, by taking one or more letters\n   or syllables from the middle; as, \"Gloster\" is a syncopated form of\n   \"Gloucester.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncopate', 'v. t.', 'To commence, as a tone, on an unaccented part of a\n   measure, and continue it into the following accented part, so that the\n   accent is driven back upon the weak part and the rhythm drags.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncopation', 'n.', 'The act of syncopating; the contraction of a word by\n   taking one or more letters or syllables from the middle; syncope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncopation', 'n.', 'The act of syncopating; a peculiar figure of rhythm,\n   or rhythmical alteration, which consists in welding into one tone the\n   second half of one beat with the first half of the beat which follows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncope', 'n.', 'An elision or retrenchment of one or more letters or\n   syllables from the middle of a word; as, neer for never, evry for\n   every.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncope', 'n.', 'Same as Syncopation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncope', 'n.', 'A fainting, or swooning. See Fainting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncope', 'n.', 'A pause or cessation; suspension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncopist', 'n.', 'One who syncopates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncopize', 'v. t.', 'To syncopate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncotyledonous', 'a.', 'Having united cotyledonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncretic', 'a.', 'Uniting and blending together different systems, as of\n   philosophy, morals, or religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncretism', 'n.', 'Attempted union of principles or parties irreconcilably\n   at variance with each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncretist', 'n.', 'One who attempts to unite principles or parties which\n   are irreconcilably at variance;');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncretist', 'n.', 'an adherent of George Calixtus and other Germans of the\n   seventeenth century, who sought to unite or reconcile the Protestant\n   sects with each other and with the Roman Catholics, and thus occasioned\n   a long and violent controversy in the Lutheran church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncretistic', 'a.', 'Pertaining to, or characterized by, syncretism; as, a\n   syncretistic mixture of the service of Jehovah and the worship of\n   idols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncretistic', 'a.', 'Of or pertaining to Syncretists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncrisis', 'n.', 'A figure of speech in which opposite things or persons\n   are compared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncitia', 'pl. ', 'of Syncytium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncytium', 'n.', 'Tissue in which the cell or partition walls are wholly\n   wanting and the cell bodies fused together, so that the tissue consists\n   of a continuous mass of protoplasm in which nuclei are imbedded, as in\n   ordinary striped muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syncytium', 'n.', 'The ectoderm of a sponge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syndactyle', 'n.', 'Any bird having syndactilous feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syndactylic', 'a.', 'Syndactilous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syndactylous', 'a.', 'Having the toes firmly united together for some\n   distance, and without an intermediate web, as the kingfishers;\n   gressorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syndesmography', 'n.', 'A description of the ligaments; syndesmology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syndesmology', 'n.', 'That part of anatomy which treats of ligaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syndesmoses', 'pl. ', 'of Syndesmosis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syndesmosis', 'n.', 'An articulation formed by means of ligaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syndetic', 'a.', 'Alt. of Syndetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syndetical', 'a.', 'Connecting; conjunctive; as, syndetic words or\n   connectives; syndetic references in a dictionary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syndic', 'n.', 'An officer of government, invested with different powers in\n   different countries; a magistrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syndic', 'n.', 'An agent of a corporation, or of any body of men engaged in\n   a business enterprise; an advocate or patron; an assignee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syndicate', 'n.', 'The office or jurisdiction of a syndic; a council, or\n   body of syndics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syndicate', 'n.', 'An association of persons officially authorized to\n   undertake some duty or to negotiate some business; also, an association\n   of persons who combine to carry out, on their own account, a financial\n   or industrial project; as, a syndicate of bankers formed to take up and\n   dispose of an entire issue of government bonds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syndicate', 'v. t.', 'To judge; to censure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syndrome', 'n.', 'Concurrence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syndrome', 'n.', 'A group of symptoms occurring together that are\n   characteristic and indicative of some underlying cause, such as a\n   disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syndyasmian', 'a.', 'Pertaining to the state of pairing together sexually;\n   -- said of animals during periods of procreation and while rearing\n   their offspring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syne', 'adv.', 'Afterwards; since; ago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syne', 'adv.', 'Late, -- as opposed to soon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syne', 'conj.', 'Since; seeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synecdoche', 'n.', 'A figure or trope by which a part of a thing is put for\n   the whole (as, fifty sail for fifty ships), or the whole for a part\n   (as, the smiling year for spring), the species for the genus (as,\n   cutthroat for assassin), the genus for the species (as, a creature for\n   a man), the name of the material for the thing made, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synecdochical', 'a.', 'Expressed by synecdoche; implying a synecdoche.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synecdochically', 'adv.', 'By synecdoche.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synechia', 'n.', 'A disease of the eye, in which the iris adheres to the\n   cornea or to the capsule of the crystalline lens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synecphonesis', 'n.', 'A contraction of two syllables into one; synizesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synedral', 'a.', 'Growing on the angles of a stem, as the leaves in some\n   species of Selaginella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synentognathi', 'n. pl.', 'An order of fishes, resembling the Physoclisti,\n   without spines in the dorsal, anal, and ventral fins. It includes the\n   true flying fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synepy', 'n.', 'The interjunction, or joining, of words in uttering the\n   clauses of sentences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syneresis', 'n.', 'Same as Synaeresis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synergetic', 'a.', 'Working together; cooperating; as, synergetic muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synergism', 'n.', 'The doctrine or theory, attributed to Melanchthon, that\n   in the regeneration of a human soul there is a cooperation, or joint\n   agency, on the part both of God and of man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synergist', 'n.', 'One who holds the doctrine of synergism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synergist', 'n.', 'A remedy which has an action similar to that of another\n   remedy, and hence increases the efficiency of that remedy when combined\n   with it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synergistic', 'a.', 'Of or pertaining to synergism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synergistic', 'a.', 'Cooperating; synergetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synergy', 'n.', 'Combined action');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synergy', 'n.', 'the combined healthy action of every organ of a particular\n   system; as, the digestive synergy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synergy', 'n.', 'An effect of the interaction of the actions of two agents\n   such that the result of the combined action is greater than expected as\n   a simple additive combination of the two agents acting separately. Also\n   synergism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syngenesia', 'n. pl.', 'A Linnaean class of plants in which the stamens are\n   united by the anthers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syngenesian', 'a.', 'Alt. of Syngenesious');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syngenesious', 'a.', 'Having the stamens united by the anthers; of or\n   pertaining to the Syngenesia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syngenesis', 'n.', 'A theory of generation in which each germ is supposed\n   to contain the germs of all subsequent generations; -- the opposite of\n   epigenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syngnathi', 'n. pl.', 'A suborder of lophobranch fishes which have an\n   elongated snout and lack the ventral and first dorsal fins. The\n   pipefishes and sea horses are examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syngraph', 'n.', 'A writing signed by both or all the parties to a contract\n   or bond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synizesis', 'n.', 'An obliteration of the pupil of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synizesis', 'n.', 'A contraction of two syllables into one; synecphonesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synneuroses', 'pl. ', 'of Synneorosis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synneorosis', 'n.', 'Syndesmosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synocha', 'n.', 'See Synochus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synochal', 'a.', 'Of or pertaining to synocha; like synocha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synochus', 'n.', 'A continuous fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synocil', 'n.', 'A sense organ found in certain sponges. It consists of\n   several filaments, each of which arises from a single cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synod', 'n.', 'An ecclesiastic council or meeting to consult on church\n   matters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synod', 'n.', 'An assembly or council having civil authority; a legislative\n   body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synod', 'n.', 'A conjunction of two or more of the heavenly bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synodal', 'a.', 'Synodical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synodal', 'n.', 'A tribute in money formerly paid to the bishop or\n   archdeacon, at the time of his Easter visitation, by every parish\n   priest, now made to the ecclesiastical commissioners; a procuration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synodal', 'n.', 'A constitution made in a provincial or diocesan synod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synodic', 'a.', 'Alt. of Synodical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synodical', 'a.', 'Of or pertaining to a synod; transacted in, or\n   authorized by, a synod; as, synodical proceedings or forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synodical', 'a.', 'Pertaining to conjunction, especially to the period\n   between two successive conjunctions; extending from one conjunction, as\n   of the moon or a planet with the sun, to the next; as, a synodical\n   month (see Lunar month, under Month); the synodical revolution of the\n   moon or a planet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synodically', 'adv.', 'In a synodical manner; in a synod; by the authority\n   of a synod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synodist', 'n.', 'An adherent to a synod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synoecious', 'a.', 'Having stamens and pistil in the same head, or, in\n   mosses, having antheridia and archegonia on the same receptacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synomocy', 'n.', 'Sworn brotherhood; a society in ancient Greece nearly\n   resembling a modern political club.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synonyms', 'pl. ', 'of Synonym');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synonym', 'n.', 'One of two or more words (commonly words of the same\n   language) which are equivalents of each other; one of two or more words\n   which have very nearly the same signification, and therefore may often\n   be used interchangeably. See under Synonymous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synonyma', 'n. pl.', 'Synonyms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synonymal', 'a.', 'Synonymous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synonymally', 'adv.', 'Synonymously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synonyme', 'n.', 'Same as Synonym.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synonymic', 'n.', 'The science, or the scientific treatment, of synonymous\n   words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synonymic', 'a.', 'Alt. of Synonymical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synonymical', 'a.', 'Of or pertaining to synonyms, or synonymic;\n   synonymous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synonymicon', 'n.', 'A dictionary of synonyms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synonymist', 'n.', 'One who collects or explains synonyms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synonymized', 'imp. & p. p.', 'of Synonymize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synonymizing', 'p. pr. & vb. n.', 'of Synonymize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synonymize', 'v. t.', 'To express by a synonym or synonyms; to give the\n   synonym or synonyms corresponding to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synonymous', 'a.', 'Having the character of a synonym; expressing the same\n   thing; conveying the same, or approximately the same, idea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synonymy', 'n.', 'The quality of being synonymous; sameness of meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synonymy', 'n.', 'A system of synonyms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synonymy', 'n.', 'A figure by which synonymous words are used to amplify a\n   discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synopses', 'pl. ', 'of Synopsis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synopsis', 'n.', 'A general view, or a collection of heads or parts so\n   arranged as to exhibit a general view of the whole; an abstract or\n   summary of a discourse; a syllabus; a conspectus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synoptic', 'a.', 'Alt. of Synoptical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synoptical', 'a.', 'Affording a general view of the whole, or of the\n   principal parts of a thing; as, a synoptic table; a synoptical\n   statement of an argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synoptic', 'n.', 'One of the first three Gospels of the New Testament. See\n   Synoptist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synoptist', 'n.', 'Any one of the authors of the three synoptic Gospels,\n   which give a history of our Lords life and ministry, in distinction\n   from the writer of Johns Gospel, which gives a fuller record of his\n   teachings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synosteology', 'n.', 'That part of anatomy which treats of joints;\n   arthrology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synosteoses', 'pl. ', 'of Synosteosis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synosteosis', 'n.', 'Union by means of bone; the complete closing up and\n   obliteration of sutures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synostosis', 'n.', 'Same as Synosteosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synovia', 'n.', 'A transparent, viscid, lubricating fluid which contains\n   mucin and secreted by synovial membranes; synovial fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synovial', 'a.', 'Of or pertaining to synovia; secreting synovia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synovitis', 'n.', 'Inflammation of the synovial membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synpelmous', 'a.', 'Having the two main flexor tendons of the toes blended\n   together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synsepalous', 'a.', 'Having united sepals; gamosepalous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syntactic', 'a.', 'Alt. of Syntactical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syntactical', 'a.', 'Of or pertaining to syntax; according to the rules of\n   syntax, or construction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syntax', 'n.', 'Connected system or order; union of things; a number of\n   things jointed together; organism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syntax', 'n.', 'That part of grammar which treats of the construction of\n   sentences; the due arrangement of words in sentences in their necessary\n   relations, according to established usage in any language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syntaxis', 'n.', 'Syntax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synteresis', 'n.', 'Prophylaxis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synteresis', 'n.', 'Conscience viewed as the internal repository of the\n   laws of duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synteretic', 'a.', 'Preserving health; prophylactic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synteretics', 'n.', 'That department of medicine which relates to the\n   preservation of health; prophylaxis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synthermal', 'a.', 'Having the same degree of heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syntheses', 'pl. ', 'of Synthesis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synthesis', 'n.', 'Composition, or the putting of two or more things\n   together, as in compounding medicines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synthesis', 'n.', 'The art or process of making a compound by putting the\n   ingredients together, as contrasted with analysis; thus, water is made\n   by synthesis from hydrogen and oxygen; hence, specifically, the\n   building up of complex compounds by special reactions, whereby their\n   component radicals are so grouped that the resulting substances are\n   identical in every respect with the natural articles when such occur;\n   thus, artificial alcohol, urea, indigo blue, alizarin, etc., are made\n   by synthesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synthesis', 'n.', 'The combination of separate elements of thought into a\n   whole, as of simple into complex conceptions, species into genera,\n   individual propositions into systems; -- the opposite of analysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synthesist', 'n.', 'One who employs synthesis, or who follows synthetic\n   methods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synthesize', 'v. t.', 'To combine by synthesis; to unite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synthesize', 'v. t.', 'To produce by synthesis; as, to synthesize albumin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synthetic', 'a.', 'Alt. of Synthetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synthetical', 'a.', 'Of or pertaining to synthesis; consisting in synthesis\n   or composition; as, the synthetic method of reasoning, as opposed to\n   analytical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synthetical', 'a.', 'Artificial. Cf. Synthesis, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synthetical', 'a.', 'Comprising within itself structural or other\n   characters which are usually found only in two or more diverse groups;\n   -- said of species, genera, and higher groups. See the Note under\n   Comprehensive, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synthetically', 'adv.', 'In a synthetic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Synthetize', 'v. t.', 'To combine; to unite in regular structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syntomy', 'n.', 'Brevity; conciseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syntonin', 'n.', 'A proteid substance (acid albumin) formed from the\n   albuminous matter of muscle by the action of dilute acids; -- formerly\n   called musculin. See Acid albumin, under Albumin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syphering', 'n.', 'The lapping of chamfered edges of planks to make a\n   smooth surface, as for a bulkhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syphilide', 'n.', 'A cutaneous eruption due to syphilis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syphilis', 'n.', 'The pox, or venereal disease; a chronic, specific,\n   infectious disease, usually communicated by sexual intercourse or by\n   hereditary transmission, and occurring in three stages known as\n   primary, secondary, and tertiary syphilis. See under Primary,\n   Secondary, and Tertiary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syphilitic', 'a.', 'Of or pertaining to syphilis; of the nature of\n   syphilis; affected with syphilis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syphilitic', 'n.', 'A syphilitic patient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syphilitically', 'adv.', 'In a syphilitic manner; with venereal disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syphilization', 'n.', 'Inoculation with the syphilitic virus, especially\n   when employed as a preventive measure, like vaccination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syphilize', 'v. t.', 'To inoculate with syphilis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syphiloderm', 'n.', 'A cutaneous affection due to syphilis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syphilodermatous', 'a.', 'Of or pertaining to the cutaneous manifestations\n   of syphilis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syphiloid', 'a.', 'Resembling syphilis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syphilologist', 'n.', 'One skilled in syphilology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syphilology', 'n.', 'That branch of medicine which treats of syphilis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syphon', 'n.', 'See Syphon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syracuse', 'n.', 'A red wine of Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syren', 'n.', 'See Siren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syriac', 'a.', 'Of or pertaining to Syria, or its language; as, the Syriac\n   version of the Pentateuch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syriac', 'n.', 'The language of Syria; especially, the ancient language of\n   that country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syriacism', 'n.', 'A Syrian idiom; a Syrianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syrian', 'a.', 'Of or pertaining to Syria; Syriac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syrian', 'n.', 'A native of Syria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syrianism', 'n.', 'A Syrian idiom, or a peculiarity of the Syrian language;\n   a Syriacism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syriasm', 'n.', 'A Syrian idiom; a Syrianism; a Syriacism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syringa', 'n.', 'A genus of plants; the lilac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syringa', 'n.', 'The mock orange; -- popularly so called because its stems\n   were formerly used as pipestems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syringe', 'n.', 'A kind of small hand-pump for throwing a stream of liquid,\n   or for purposes of aspiration. It consists of a small cylindrical\n   barrel and piston, or a bulb of soft elastic material, with or without\n   valves, and with a nozzle which is sometimes at the end of a flexible\n   tube; -- used for injecting animal bodies, cleansing wounds, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syringed', 'imp. & p. p.', 'of Syringe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syringing', 'p. pr. & vb. n.', 'of Syringe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syringe', 'v. t.', 'To inject by means of a syringe; as, to syringe warm\n   water into a vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syringe', 'v. t.', 'To wash and clean by injection from a syringe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syringeal', 'a.', 'Of or pertaining to the syrinx; as, the syringeal\n   muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syringin', 'n.', 'A glucoside found in the bark of the lilac (Syringa) and\n   extracted as a white crystalline substance; -- formerly called also\n   lilacin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syringocoele', 'n.', 'The central canal of the spinal cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syringotome', 'n.', 'A small blunt-pointed bistoury, -- used in\n   syringotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syringotomy', 'n.', 'The operation of cutting for anal fistula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syringes', 'pl. ', 'of Syrinx');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syrinx', 'n.', 'A wind instrument made of reeds tied together; -- called\n   also pandean pipes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syrinx', 'n.', 'The lower larynx in birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syrma', 'n.', 'A long dress, trailing on the floor, worn by tragic actors\n   in Greek and Roman theaters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syrphian', 'a.', 'Of or pertaining to the syrphus flies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syrphian', 'n.', 'A syrphus fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syrphus fly', '', 'Any one of numerous species of dipterous flies of the\n   genus Syrphus and allied genera. They are usually bright-colored, with\n   yellow bands, and hover around plants. The larvae feed upon plant lice,\n   and are, therefore, very beneficial to agriculture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syrt', 'n.', 'A quicksand; a bog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syrtic', 'a.', 'Of or pertaining to a syrt; resembling syrt, or quicksand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syrtes', 'pl. ', 'of Syrtis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syrtis', 'n.', 'A quicksand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syrup', 'a.', 'Alt. of Syrupy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syrupy', 'a.', 'Same as Sirup, Sirupy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syssarcosis', 'n.', 'The junction of bones by intervening muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systaltic', 'a.', 'Capable of, or taking place by, alternate contraction\n   and dilatation; as, the systaltic action of the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systasis', 'n.', 'A political union, confederation, or league.');
  INSERT INTO words (word, word_type, description) VALUES  (    'System', 'n.', 'An assemblage of objects arranged in regular subordination,\n   or after some distinct method, usually logical or scientific; a\n   complete whole of objects related by some common law, principle, or\n   end; a complete exhibition of essential principles or facts, arranged\n   in a rational dependence or connection; a regular union of principles\n   or parts forming one entire thing; as, a system of philosophy; a system\n   of government; a system of divinity; a system of botany or chemistry; a\n   military system; the solar system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'System', 'n.', 'Hence, the whole scheme of created things regarded as\n   forming one complete plan of whole; the universe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'System', 'n.', 'Regular method or order; formal arrangement; plan; as, to\n   have a system in ones business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'System', 'n.', 'The collection of staves which form a full score. See\n   Score, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'System', 'n.', 'An assemblage of parts or organs, either in animal or\n   plant, essential to the performance of some particular function or\n   functions which as a rule are of greater complexity than those\n   manifested by a single organ; as, the capillary system, the muscular\n   system, the digestive system, etc.; hence, the whole body as a\n   functional unity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'System', 'n.', 'One of the stellate or irregular clusters of intimately\n   united zooids which are imbedded in, or scattered over, the surface of\n   the common tissue of many compound ascidians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systematic', 'a.', 'Alt. of Systematical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systematical', 'a.', 'Of or pertaining to system; consisting in system;\n   methodical; formed with regular connection and adaptation or\n   subordination of parts to each other, and to the design of the whole;\n   as, a systematic arrangement of plants or animals; a systematic course\n   of study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systematical', 'a.', 'Proceeding according to system, or regular method;\n   as, a systematic writer; systematic benevolence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systematical', 'a.', 'Pertaining to the system of the world; cosmical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systematical', 'a.', 'Affecting successively the different parts of the\n   system or set of nervous fibres; as, systematic degeneration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systematically', 'adv.', 'In a systematic manner; methodically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systematism', 'n.', 'The reduction of facts or principles to a system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systematist', 'n.', 'One who forms a system, or reduces to system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systematist', 'n.', 'One who adheres to a system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systematization', 'n.', 'The act or operation of systematizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systematized', 'imp. & p. p.', 'of Systematize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systematizing', 'p. pr. & vb. n.', 'of Systematize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systematize', 'v. t.', 'To reduce to system or regular method; to arrange\n   methodically; to methodize; as, to systematize a collection of plants\n   or minerals; to systematize ones work; to systematize ones ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systematizer', 'n.', 'One who systematizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systematology', 'n.', 'The doctrine of, or a treatise upon, systems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systemic', 'a.', 'Of or relating to a system; common to a system; as, the\n   systemic circulation of the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systemic', 'a.', 'Of or pertaining to the general system, or the body as a\n   whole; as, systemic death, in distinction from local death; systemic\n   circulation, in distinction from pulmonic circulation; systemic\n   diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systemization', 'n.', 'The act or process of systematizing;\n   systematization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systemized', 'imp. & p. p.', 'of Systemize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systemizing', 'p. pr. & vb. n.', 'of Systemize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systemize', 'v. t.', 'To reduce to system; to systematize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systemizer', 'n.', 'One who systemizes, or reduces to system; a\n   systematizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systemless', 'a.', 'Being without system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systemless', 'a.', 'Not agreeing with some artificial system of\n   classification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systemless', 'a.', 'Not having any of the distinct systems or types of\n   structure, as the radiate, articulate, etc., characteristic of organic\n   nature; as, all unicellular organisms are systemless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systole', 'n.', 'The shortening of the long syllable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systole', 'n.', 'The contraction of the heart and arteries by which the\n   blood is forced onward and the circulation kept up; -- correlative to\n   diastole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systolic', 'a.', 'Of or pertaining to systole, or contraction; contracting;\n   esp., relating to the systole of the heart; as, systolic murmur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systyle', 'a.', 'Having a space equal to two diameters or four modules\n   between two columns; -- said of a portico or building. See\n   Intercolumniation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Systyle', 'n.', 'A systyle temple or other edifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syth', 'prep., adv., conj. &', 'Alt. of Sythe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sythe', 'prep., adv., conj. &', 'See Sith, Sithe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Sythe', 'n.', 'Scythe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syzygial', 'a.', 'Pertaining to a syzygy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syzygies', 'pl. ', 'of Syzygy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syzygy', 'n.', 'The point of an orbit, as of the moon or a planet, at which\n   it is in conjunction or opposition; -- commonly used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syzygy', 'n.', 'The coupling together of different feet; as, in Greek\n   verse, an iambic syzygy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syzygy', 'n.', 'Any one of the segments of an arm of a crinoid composed of\n   two joints so closely united that the line of union is obliterated on\n   the outer, though visible on the inner, side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Syzygy', 'n.', 'The immovable union of two joints of a crinoidal arm.\n   T () the twentieth letter of the English alphabet, is a nonvocal\n   consonant. With the letter h it forms the digraph th, which has two\n   distinct sounds, as in thin, then. See Guide to Pronunciation,\n   //262-264, and also //153, 156, 169, 172, 176, 178-180.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ta', 'v. t.', 'To take.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taas', 'n.', 'A heap. See Tas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tab', 'n.', 'The flap or latchet of a shoe fastened with a string or a\n   buckle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tab', 'n.', 'A tag. See Tag, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tab', 'n.', 'A loop for pulling or lifting something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tab', 'n.', 'A border of lace or other material, worn on the inner front\n   edge of ladies bonnets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tab', 'n.', 'A loose pendent part of a ladys garment; esp., one of a\n   series of pendent squares forming an edge or border.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabacco', 'n.', 'Tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabanus', 'n.', 'A genus of blood sucking flies, including the horseflies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabard', 'n.', 'A sort of tunic or mantle formerly worn for protection from\n   the weather. When worn over the armor it was commonly emblazoned with\n   the arms of the wearer, and from this the name was given to the garment\n   adopted for heralds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabarder', 'n.', 'One who wears a tabard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabarder', 'n.', 'A scholar on the foundation of Queens College, Oxford,\n   England, whose original dress was a tabard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabaret', 'n.', 'A stout silk having satin stripes, -- used for furniture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabasheer', 'n.', 'A concretion in the joints of the bamboo, which consists\n   largely or chiefly of pure silica. It is highly valued in the East\n   Indies as a medicine for the cure of bilious vomitings, bloody flux,\n   piles, and various other diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabbinet', 'n.', 'A fabric like poplin, with a watered surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabbies', 'pl. ', 'of Tabby');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabby', 'n.', 'A kind of waved silk, usually called watered silk,\n   manufactured like taffeta, but thicker and stronger. The watering is\n   given to it by calendering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabby', 'n.', 'A mixture of lime with shells, gravel, or stones, in equal\n   proportions, with an equal proportion of water. When dry, this becomes\n   as hard as rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabby', 'n.', 'A brindled cat; hence, popularly, any cat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabby', 'n.', 'An old maid or gossip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabby', 'a.', 'Having a wavy or watered appearance; as, a tabby waistcoat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabby', 'a.', 'Brindled; diversified in color; as, a tabby cat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabbied', 'imp. & p. p.', 'of Tabby');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabbying', 'p. pr. & vb. n.', 'of Tabby');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabby', 'v. t.', 'To water; to cause to look wavy, by the process of\n   calendering; to calender; as, to tabby silk, mohair, ribbon, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabefaction', 'n.', 'A wasting away; a gradual losing of flesh by disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabefied', 'imp. & p. p.', 'of Tabefy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabefying', 'p. pr. & vb. n.', 'of Tabefy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabefy', 'v. t.', 'To cause to waste gradually, to emaciate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabellion', 'n.', 'A secretary or notary under the Roman empire; also, a\n   similar officer in France during the old monarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taber', 'v. i.', 'Same as Tabor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taberd', 'n.', 'See Tabard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabernacle', 'n.', 'A slightly built or temporary habitation; especially, a\n   tent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabernacle', 'n.', 'A portable structure of wooden framework covered with\n   curtains, which was carried through the wilderness in the Israelitish\n   exodus, as a place of sacrifice and worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabernacle', 'n.', 'Hence, the Jewish temple; sometimes, any other place\n   for worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabernacle', 'n.', 'Figuratively: The human body, as the temporary abode of\n   the soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabernacle', 'n.', 'Any small cell, or like place, in which some holy or\n   precious things was deposited or kept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabernacle', 'n.', 'The ornamental receptacle for the pyx, or for the\n   consecrated elements, whether a part of a building or movable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabernacle', 'n.', 'A niche for the image of a saint, or for any sacred\n   painting or sculpture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabernacle', 'n.', 'Hence, a work of art of sacred subject, having a\n   partially architectural character, as a solid frame resting on a\n   bracket, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabernacle', 'n.', 'A tryptich for sacred imagery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabernacle', 'n.', 'A seat or stall in a choir, with its canopy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabernacle', 'n.', 'A boxlike step for a mast with the after side open, so\n   that the mast can be lowered to pass under bridges, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabernacled', 'imp. & p. p.', 'of Tabernacle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabernacling', 'p. pr. & vb. n.', 'of Tabernacle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabernacle', 'v. i.', 'To dwell or reside for a time; to be temporary\n   housed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabernacular', 'a.', 'Of or pertaining to a tabernacle, especially the\n   Jewish tabernacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabernacular', 'a.', 'Formed in latticework; latticed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabernacular', 'a.', 'Of or pertaining to huts or booths; hence, common;\n   low.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabes', 'n.', 'Progressive emaciation of the body, accompained with hectic\n   fever, with no well-marked logical symptoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabescent', 'a.', 'Withering, or wasting away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabetic', 'a.', 'Of or pertaining to tabes; of the nature of tabes;\n   affected with tabes; tabid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabetic', 'n.', 'One affected with tabes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabid', 'a.', 'Affected by tabes; tabetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabific', 'a.', 'Alt. of Tabifical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabifical', 'a.', 'Producing tabes; wasting; tabefying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabinet', 'n.', 'See Tabbinet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tablature', 'n.', 'A painting on a wall or ceiling; a single piece\n   comprehended in one view, and formed according to one design; hence, a\n   picture in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tablature', 'n.', 'An ancient mode of indicating musical sounds by letters\n   and other signs instead of by notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tablature', 'n.', 'Division into plates or tables with intervening spaces;\n   as, the tablature of the cranial bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'A smooth, flat surface, like the side of a board; a thin,\n   flat, smooth piece of anything; a slab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'A thin, flat piece of wood, stone, metal, or other material,\n   on which anything is cut, traced, written, or painted; a tablet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'a memorandum book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'Any smooth, flat surface upon which an inscription, a\n   drawing, or the like, may be produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'Hence, in a great variety of applications: A condensed\n   statement which may be comprehended by the eye in a single view; a\n   methodical or systematic synopsis; the presentation of many items or\n   particulars in one group; a scheme; a schedule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'A view of the contents of a work; a statement of the\n   principal topics discussed; an index; a syllabus; a synopsis; as, a\n   table of contents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'A list of substances and their properties; especially, a\n   list of the elementary substances with their atomic weights, densities,\n   symbols, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'Any collection and arrangement in a condensed form of many\n   particulars or values, for ready reference, as of weights, measures,\n   currency, specific gravities, etc.; also, a series of numbers following\n   some law, and expressing particular values corresponding to certain\n   other numbers on which they depend, and by means of which they are\n   taken out for use in computations; as, tables of logarithms, sines,\n   tangents, squares, cubes, etc.; annuity tables; interest tables;\n   astronomical tables, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'The arrangement or disposition of the lines which appear on\n   the inside of the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'An article of furniture, consisting of a flat slab, board,\n   or the like, having a smooth surface, fixed horizontally on legs, and\n   used for a great variety of purposes, as in eating, writing, or\n   working.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'Hence, food placed on a table to be partaken of; fare;\n   entertainment; as, to set a good table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'The company assembled round a table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'One of the two, external and internal, layers of compact\n   bone, separated by diploe, in the walls of the cranium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'A stringcourse which includes an offset; esp., a band of\n   stone, or the like, set where an offset is required, so as to make it\n   decorative. See Water table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'The board on the opposite sides of which backgammon and\n   draughts are played.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'One of the divisions of a backgammon board; as, to play into\n   the right-hand table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'The games of backgammon and of draughts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'A circular plate of crown glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'The upper flat surface of a diamond or other precious stone,\n   the sides of which are cut in angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'A plane surface, supposed to be transparent and\n   perpendicular to the horizon; -- called also perspective plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'n.', 'The part of a machine tool on which the work rests and is\n   fastened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tableed', 'imp. & p. p.', 'of Table');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tableing', 'p. pr. & vb. n.', 'of Table');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'v. t.', 'To form into a table or catalogue; to tabulate; as, to\n   table fines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'v. t.', 'To delineate, as on a table; to represent, as in a\n   picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'v. t.', 'To supply with food; to feed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'v. t.', 'To insert, as one piece of timber into another, by\n   alternate scores or projections from the middle, to prevent slipping;\n   to scarf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'v. t.', 'To lay or place on a table, as money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'v. t.', 'In parliamentary usage, to lay on the table; to postpone,\n   by a formal vote, the consideration of (a bill, motion, or the like)\n   till called for, or indefinitely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'v. t.', 'To enter upon the docket; as, to table charges against\n   some one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'v. t.', 'To make board hems in the skirts and bottoms of (sails)\n   in order to strengthen them in the part attached to the boltrope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table', 'v. i.', 'To live at the table of another; to board; to eat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tableaux', 'pl. ', 'of Tableau');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tableau', 'n.', 'A striking and vivid representation; a picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tableau', 'n.', 'A representation of some scene by means of persons grouped\n   in the proper manner, placed in appropriate postures, and remaining\n   silent and motionless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tableaux vivants', 'pl. ', 'of Tableau vivant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tableau vivant', '', 'Same as Tableau, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tablebook', 'n.', 'A tablet; a notebook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tablecloth', 'n.', 'A cloth for covering a table, especially one with which\n   a table is covered before the dishes, etc., are set on for meals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tables dhote', 'pl. ', 'of Table dhote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table dhote', '', 'A common table for guests at a hotel; an ordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Table-land', 'n.', 'A broad, level, elevated area of land; a plateau.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tablemen', 'pl. ', 'of Tableman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tableman', 'n.', 'A man at draughts; a piece used in playing games at\n   tables. See Table, n., 10.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tablement', 'n.', 'A table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabler', 'n.', 'One who boards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabler', 'n.', 'One who boards others for hire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tablespoon', 'n.', 'A spoon of the largest size commonly used at the table;\n   -- distinguished from teaspoon, dessert spoon, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tablespoonfuls', 'pl. ', 'of Tablespoonful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tablespoonful', 'n.', 'As much as a tablespoon will hold; enough to fill a\n   tablespoon. It is usually reckoned as one half of a fluid ounce, or\n   four fluid drams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tablet', 'n.', 'A small table or flat surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tablet', 'n.', 'A flat piece of any material on which to write, paint,\n   draw, or engrave; also, such a piece containing an inscription or a\n   picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tablet', 'n.', 'Hence, a small picture; a miniature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tablet', 'n.', 'A kind of pocket memorandum book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tablet', 'n.', 'A flattish cake or piece; as, tablets of arsenic were\n   formerly worn as a preservative against the plague.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tablet', 'n.', 'A solid kind of electuary or confection, commonly made of\n   dry ingredients with sugar, and usually formed into little flat\n   squares; -- called also lozenge, and troche, especially when of a round\n   or rounded form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tableware', 'n.', 'Ware, or articles collectively, for table use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabling', 'n.', 'A forming into tables; a setting down in order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabling', 'n.', 'The letting of one timber into another by alternate scores\n   or projections, as in shipbuilding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabling', 'n.', 'A broad hem on the edge of a sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabling', 'n.', 'Board; support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabling', 'n.', 'Act of playing at tables. See Table, n., 10.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taboo', 'n.', 'A total prohibition of intercourse with, use of, or approach\n   to, a given person or thing under pain of death, -- an interdict of\n   religious origin and authority, formerly common in the islands of\n   Polynesia; interdiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabooed', 'imp. & p. p.', 'of Taboo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabooing', 'p. pr. & vb. n.', 'of Taboo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taboo', 'v. t.', 'To put under taboo; to forbid, or to forbid the use of;\n   to interdict approach to, or use of; as, to taboo the ground set apart\n   as a sanctuary for criminals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabor', 'n.', 'A small drum used as an accompaniment to a pipe or fife,\n   both being played by the same person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabored', 'imp. & p. p.', 'of Tabor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taboring', 'p. pr. & vb. n.', 'of Tabor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabor', 'v. i.', 'To play on a tabor, or little drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabor', 'v. i.', 'To strike lightly and frequently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabor', 'v. t.', 'To make (a sound) with a tabor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taborer', 'n.', 'One who plays on the tabor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taboret', 'n.', 'A small tabor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taborine', 'n.', 'A small, shallow drum; a tabor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taborite', 'n.', 'One of certain Bohemian reformers who suffered\n   persecution in the fifteenth century; -- so called from Tabor, a hill\n   or fortress where they encamped during a part of their struggles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabour', 'n. & v.', 'See Tabor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabouret', 'n.', 'Same as Taboret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabouret', 'n.', 'A seat without arms or back, cushioned and stuffed: a\n   high stool; -- so called from its resemblance to a drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabouret', 'n.', 'An embroidery frame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabrere', 'n.', 'A taborer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabret', 'n.', 'A taboret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabu', 'n. & v.', 'See Taboo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabulae', 'pl. ', 'of Tabula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabula', 'n.', 'A table; a tablet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabula', 'n.', 'One of the transverse plants found in the calicles of\n   certain corals and hydroids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabular', 'a.', 'Having the form of, or pertaining to, a table (in any of\n   the uses of the word).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabular', 'a.', 'Having a flat surface; as, a tabular rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabular', 'a.', 'Formed into a succession of flakes; laminated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabular', 'a.', 'Set in squares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabular', 'a.', 'Arranged in a schedule; as, tabular statistics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabular', 'a.', 'Derived from, or computed by, the use of tables; as,\n   tabular right ascension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabularization', 'n.', 'The act of tabularizing, or the state of being\n   tabularized; formation into tables; tabulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabularized', 'imp. & p. p.', 'of Tabularize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabularizing', 'p. pr. & vb. n.', 'of Tabularize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabularize', 'v. t.', 'To tabulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabulata', 'n. pl.', 'An artificial group of stony corals including those\n   which have transverse septa in the calicles. The genera Pocillopora and\n   Favosites are examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabulated', 'imp. & p. p.', 'of Tabulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabulating', 'p. pr. & vb. n.', 'of Tabulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabulate', 'v. t.', 'To form into a table or tables; to reduce to tables or\n   synopses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabulate', 'v. t.', 'To shape with a flat surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tabulation', 'n.', 'The act of forming into a table or tables; as, the\n   tabulation of statistics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tac', 'n.', 'A kind of customary payment by a tenant; -- a word used in old\n   records.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tacamahac', 'n.', 'Alt. of Tacamahaca');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tacamahaca', 'n.', 'A bitter balsamic resin obtained from tropical American\n   trees of the genus Elaphrium (E. tomentosum and E. Tacamahaca), and\n   also from East Indian trees of the genus Calophyllum; also, the\n   resinous exhudation of the balsam poplar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tacamahaca', 'n.', 'Any tree yielding tacamahac resin, especially, in North\n   America, the balsam poplar, or balm of Gilead (Populus balsamifera).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tacaud', 'n.', 'The bib, or whiting pout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tace', 'n.', 'The cross, or church, of St. Antony. See Illust. (6), under\n   Cross, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tace', 'n.', 'See Tasse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tacet', 'v.impers.', 'It is silent; -- a direction for a vocal or\n   instrumental part to be silent during a whole movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tache', 'n.', 'Something used for taking hold or holding; a catch; a loop;\n   a button.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tache', 'n.', 'A spot, stain, or blemish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tachhydrite', 'n.', 'A hydrous chloride of calcium and magnesium occurring\n   in yellowish masses which rapidly deliquesce upon exposure. It is found\n   in the salt mines at Stassfurt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tachinae', 'pl. ', 'of Tachina');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tachina', 'n.', 'Any one of numerous species of Diptera belonging to\n   Tachina and allied genera. Their larvae are external parasites of other\n   insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tachometer', 'n.', 'An instrument for measuring the velocity, or indicating\n   changes in the velocity, of a moving body or substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tachometer', 'n.', 'An instrument for measuring the velocity of running\n   water in a river or canal, consisting of a wheel with inclined vanes,\n   which is turned by the current. The rotations of the wheel are recorded\n   by clockwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tachometer', 'n.', 'An instrument for showing at any moment the speed of a\n   revolving shaft, consisting of a delicate revolving conical pendulum\n   which is driven by the shaft, and the action of which by change of\n   speed moves a pointer which indicates the speed on a graduated dial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tachometer', 'n.', 'An instrument for measuring the velocity of the blood;\n   a haematachometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tachydidaxy', 'n.', 'A short or rapid method of instructing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tachyglossa', 'n. pl.', 'A division of monotremes which comprises the spiny\n   ant-eaters of Australia and New Guinea. See Illust. under Echidna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tachygraphic', 'a.', 'Alt. of Tachygraphical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tachygraphical', 'a.', 'Of or pertaining to tachygraphy; written in\n   shorthand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tachygraphy', 'n.', 'The art or practice of rapid writing; shorthand\n   writing; stenography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tachylyte', 'n.', 'A vitreous form of basalt; -- so called because\n   decomposable by acids and readily fusible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tacit', 'a.', 'Done or made in silence; implied, but not expressed; silent;\n   as, tacit consent is consent by silence, or by not interposing an\n   objection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taciturn', 'a.', 'Habitually silent; not given to converse; not apt to talk\n   or speak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taciturnity', 'n.', 'Habilual silence, or reserve in speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tack', 'n.', 'A stain; a tache.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tack', 'n.', 'A peculiar flavor or taint; as, a musty tack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tack', 'n.', 'A small, short, sharp-pointed nail, usually having a broad,\n   flat head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tack', 'n.', 'That which is attached; a supplement; an appendix. See Tack,\n   v. t., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tack', 'v. t.', 'A rope used to hold in place the foremost lower corners of\n   the courses when the vessel is closehauled (see Illust. of Ship); also,\n   a rope employed to pull the lower corner of a studding sail to the\n   boom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tack', 'v. t.', 'The part of a sail to which the tack is usually fastened;\n   the foremost lower corner of fore-and-aft sails, as of schooners (see\n   Illust. of Sail).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tack', 'v. t.', 'The direction of a vessel in regard to the trim of her\n   sails; as, the starboard tack, or port tack; -- the former when she is\n   closehauled with the wind on her starboard side; hence, the run of a\n   vessel on one tack; also, a change of direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tack', 'v. t.', 'A contract by which the use of a thing is set, or let, for\n   hire; a lease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tack', 'v. t.', 'Confidence; reliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tacked', 'imp. & p. p.', 'of Tack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tacking', 'p. pr. & vb. n.', 'of Tack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tack', 'v. t.', 'To fasten or attach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tack', 'v. t.', 'Especially, to attach or secure in a slight or hasty\n   manner, as by stitching or nailing; as, to tack together the sheets of\n   a book; to tack one piece of cloth to another; to tack on a board or\n   shingle; to tack one piece of metal to another by drops of solder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tack', 'v. t.', 'In parliamentary usage, to add (a supplement) to a bill;\n   to append; -- often with on or to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tack', 'v. t.', 'To change the direction of (a vessel) when sailing\n   closehauled, by putting the helm alee and shifting the tacks and sails\n   so that she will proceed to windward nearly at right angles to her\n   former course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tack', 'v. i.', 'To change the direction of a vessel by shifting the\n   position of the helm and sails; also (as said of a vessel), to have her\n   direction changed through the shifting of the helm and sails. See Tack,\n   v. t., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tacker', 'n.', 'One who tacks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tacket', 'n.', 'A small, broad-headed nail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tackey', 'a. & n.', 'See Tacky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tacking', 'n.', 'A union of securities given at different times, all of\n   which must be redeemed before an intermediate purchaser can interpose\n   his claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tackle', 'n.', 'Apparatus for raising or lowering heavy weights, consisting\n   of a rope and pulley blocks; sometimes, the rope and attachments, as\n   distinct from the block.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tackle', 'n.', 'Any instruments of action; an apparatus by which an object\n   is moved or operated; gear; as, fishing tackle, hunting tackle;\n   formerly, specifically, weapons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tackle', 'n.', 'The rigging and apparatus of a ship; also, any purchase\n   where more than one block is used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tackled', 'imp. & p. p.', 'of Tackle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tackling', 'p. pr. & vb. n.', 'of Tackle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tackle', 'n.', 'To supply with tackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tackle', 'n.', 'To fasten or attach, as with a tackle; to harness; as, to\n   tackle a horse into a coach or wagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tackle', 'n.', 'To seize; to lay hold of; to grapple; as, a wrestler\n   tackles his antagonist; a dog tackles the game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tackle', 'n.', 'To begin to deal with; as, to tackle the problem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tackled', 'a.', 'Made of ropes tacked together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tackling', 'n.', 'Furniture of the masts and yards of a vessel, as cordage,\n   sails, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tackling', 'n.', 'Instruments of action; as, fishing tackling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tackling', 'n.', 'The straps and fixures adjusted to an animal, by which he\n   draws a carriage, or the like; harness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tacksmen', 'pl. ', 'of Tacksman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tacksman', 'n.', 'One who holds a tack or lease from another; a tenant, or\n   lessee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tacky', 'a.', 'Sticky; adhesive; raw; -- said of paint, varnish, etc., when\n   not well dried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taconic', 'a.', 'Designating, or pertaining to, the series of rocks forming\n   the Taconic mountains in Western New England. They were once supposed\n   to be older than the Cambrian, but later proved to belong to the Lower\n   Silurian and Cambrian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tact', 'n.', 'The sense of touch; feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tact', 'n.', 'The stroke in beating time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tact', 'n.', 'Sensitive mental touch; peculiar skill or faculty; nice\n   perception or discernment; ready power of appreciating and doing what\n   is required by circumstances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tactable', 'a.', 'Capable of being touched; tangible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tactic', 'a.', 'Alt. of Tactical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tactical', 'a.', 'Of or pertaining to the art of military and naval\n   tactics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tactic', 'n.', 'See Tactics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tactician', 'n.', 'One versed in tactics; hence, a skillful maneuverer; an\n   adroit manager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tactics', 'n.', 'The science and art of disposing military and naval forces\n   in order for battle, and performing military and naval evolutions. It\n   is divided into grand tactics, or the tactics of battles, and\n   elementary tactics, or the tactics of instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tactics', 'n.', 'Hence, any system or method of procedure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tactile', 'a.', 'Of or pertaining to the organs, or the sense, of touch;\n   perceiving, or perceptible, by the touch; capable of being touched; as,\n   tactile corpuscles; tactile sensations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tactility', 'n.', 'The quality or state of being tactile; perceptibility by\n   touch; tangibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taction', 'n.', 'The act of touching; touch; contact; tangency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tactless', 'a.', 'Destitute of tact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tactual', 'a.', 'Of or pertaining to the sense, or the organs, of touch;\n   derived from touch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tadpole', 'n.', 'The young aquatic larva of any amphibian. In this stage it\n   breathes by means of external or internal gills, is at first destitute\n   of legs, and has a finlike tail. Called also polliwig, polliwog,\n   porwiggle, or purwiggy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tadpole', 'n.', 'The hooded merganser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taedium', 'n.', 'See Tedium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tael', 'n.', 'A denomination of money, in China, worth nearly six shillings\n   sterling, or about a dollar and forty cents; also, a weight of one\n   ounce and a third.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taen', '', 'Alt. of Taen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taen', '', 'p. p. of Ta, to take, or a contraction of Taken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taeniae', 'pl. ', 'of Taenia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taenia', 'n.', 'A genus of intestinal worms which includes the common\n   tapeworms of man. See Tapeworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taenia', 'n.', 'A band; a structural line; -- applied to several bands and\n   lines of nervous matter in the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taenia', 'n.', 'The fillet, or band, at the bottom of a Doric frieze,\n   separating it from the architrave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taeniada', 'n. pl.', 'Same as Taenioidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taeniata', 'n. pl.', 'A division of Ctenophora including those which have a\n   long, ribbonlike body. The Venuss girdle is the most familiar example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taenidia', 'pl. ', 'of Taenidium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taenidium', 'n.', 'The chitinous fiber forming the spiral thread of the\n   tracheae of insects. See Illust. of Trachea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taenioglossa', 'n. pl.', 'An extensive division of gastropod mollusks in\n   which the odontophore is long and narrow, and usually bears seven rows\n   of teeth. It includes a large number of families both marine and\n   fresh-water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taenioglossate', 'a.', 'Of or pertaining to the Taenioglossa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taenioid', 'a.', 'Ribbonlike; shaped like a ribbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taenioid', 'a.', 'Like or pertaining to Taenia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taenioidea', 'n. pl.', 'The division of cestode worms which comprises the\n   tapeworms. See Tapeworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taeniolae', 'pl. ', 'of Taeniola');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taeniola', 'n.', 'One of the radial partitions which separate the internal\n   cavities of certain medusae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taeniosomi', 'n. pl.', 'An order of fishes remarkable for their long and\n   compressed form. The ribbon fishes are examples. See Ribbon fish, under\n   Ribbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tafferer', 'n.', 'See Taffrail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taffeta', 'n.', 'Alt. of Taffety');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taffety', 'n.', 'A fine, smooth stuff of silk, having usually the wavy\n   luster called watering. The term has also been applied to different\n   kinds of silk goods, from the 16th century to modern times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taffrail', 'n.', 'The upper part of a ships stern, which is flat like a\n   table on the top, and sometimes ornamented with carved work; the rail\n   around a ships stern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taffy', 'n.', 'A kind of candy made of molasses or brown sugar boiled down\n   and poured out in shallow pans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taffy', 'n.', 'Flattery; soft phrases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tafia', 'n.', 'A variety of rum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tag', 'n.', 'Any slight appendage, as to an article of dress; something\n   slight hanging loosely; specifically, a direction card, or label.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tag', 'n.', 'A metallic binding, tube, or point, at the end of a string, or\n   lace, to stiffen it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tag', 'n.', 'The end, or catchword, of an actors speech; cue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tag', 'n.', 'Something mean and paltry; the rabble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tag', 'n.', 'A sheep of the first year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tag', 'n.', 'A sale of usually used items (such as furniture, clothing,\n   household items or bric-a-brac), conducted by one or a small group of\n   individuals, at a location which is not a normal retail establishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tagged', 'imp. & p. p.', 'of Tag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tagging', 'p. pr. & vb. n.', 'of Tag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tag', 'v. t.', 'To fit with, or as with, a tag or tags.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tag', 'v. t.', 'To join; to fasten; to attach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tag', 'v. t.', 'To follow closely after; esp., to follow and touch in the\n   game of tag. See Tag, a play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tag', 'v. i.', 'To follow closely, as it were an appendage; -- often with\n   after; as, to tag after a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tag', 'v.', 'A childs play in which one runs after and touches another,\n   and then runs away to avoid being touched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tagbelt', 'n.', 'Same as Tagsore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tagger', 'n.', 'One who, or that which, appends or joins one thing to\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tagger', 'n.', 'That which is pointed like a tag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tagger', 'n.', 'Sheets of tin or other plate which run below the gauge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tagger', 'n.', 'A device for removing taglocks from sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taglet', 'n.', 'A little tag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taglia', 'n.', 'A peculiar combination of pulleys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tagliacotain', 'a.', 'Of or pertaining to Tagliacozzi, a Venetian surgeon;\n   as, the Tagliacotian operation, a method of rhinoplasty described by\n   him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taglioni', 'n.', 'A kind of outer coat, or overcoat; -- said to be so named\n   after a celebrated Italian family of professional dancers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taglock', 'n.', 'An entangled lock, as of hair or wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tagnicate', 'n.', 'The white-lipped peccary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tag-rag', 'n. & a.', 'The lowest class of people; the rabble. Cf. Rag, tag,\n   and bobtail, under Bobtail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tagsore', 'n.', 'Adhesion of the tail of a sheep to the wool from\n   excoriation produced by contact with the feces; -- called also tagbelt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tagtail', 'n.', 'A worm which has its tail conspicuously colored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tagtail', 'n.', 'A person who attaches himself to another against the will\n   of the latter; a hanger-on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taguan', 'n.', 'A large flying squirrel (Pteromys petuarista). Its body\n   becomes two feet long, with a large bushy tail nearly as long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taguicati', 'n.', 'The white-lipped peccary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taha', 'n.', 'The African rufous-necked weaver bird (Hyphantornis texor).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tahaleb', 'n.', 'A fox (Vulpes Niloticus) of Northern Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tahitian', 'a.', 'Of or pertaining to Tahiti, an island in the Pacific\n   Ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tahitian', 'n.', 'A native inhabitant of Tahiti.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tahr', 'n.', 'Same as Thar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'n.', 'Limitation; abridgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'a.', 'Limited; abridged; reduced; curtailed; as, estate tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'n.', 'The terminal, and usually flexible, posterior appendage of an\n   animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'n.', 'Any long, flexible terminal appendage; whatever resembles, in\n   shape or position, the tail of an animal, as a catkin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'n.', 'Hence, the back, last, lower, or inferior part of anything,\n   -- as opposed to the head, or the superior part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'n.', 'A train or company of attendants; a retinue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'n.', 'The side of a coin opposite to that which bears the head,\n   effigy, or date; the reverse; -- rarely used except in the expression\n   \"heads or tails,\" employed when a coin is thrown up for the purpose of\n   deciding some point by its fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'n.', 'The distal tendon of a muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'n.', 'A downy or feathery appendage to certain achenes. It is\n   formed of the permanent elongated style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'n.', 'A portion of an incision, at its beginning or end, which does\n   not go through the whole thickness of the skin, and is more painful\n   than a complete incision; -- called also tailing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'n.', 'One of the strips at the end of a bandage formed by splitting\n   the bandage one or more times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'n.', 'A rope spliced to the strap of a block, by which it may be\n   lashed to anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'n.', 'The part of a note which runs perpendicularly upward or\n   downward from the head; the stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'n.', 'Same as Tailing, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'n.', 'The bottom or lower portion of a member or part, as a slate\n   or tile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'n.', 'See Tailing, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'v. t.', 'To follow or hang to, like a tail; to be attached closely\n   to, as that which can not be evaded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'v. t.', 'To pull or draw by the tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'v. i.', 'To hold by the end; -- said of a timber when it rests upon\n   a wall or other support; -- with in or into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail', 'v. i.', 'To swing with the stern in a certain direction; -- said of\n   a vessel at anchor; as, this vessel tails down stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailage', 'n.', 'See Tallage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail-bay', 'n.', 'One of the joists which rest one end on the wall and the\n   other on a girder; also, the space between a wall and the nearest\n   girder of a floor. Cf. Case-bay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail-bay', 'n.', 'The part of a canal lock below the lower gates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailblock', 'n.', 'A block with a tail. See Tail, 9.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailboard', 'n.', 'The board at the rear end of a cart or wagon, which can\n   be removed or let down, for convenience in loading or unloading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailed', 'a.', 'Having a tail; having (such) a tail or (so many) tails; --\n   chiefly used in composition; as, bobtailed, longtailed, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailing', 'n.', 'The part of a projecting stone or brick inserted in a\n   wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailing', 'n.', 'Same as Tail, n., 8 (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailing', 'n.', 'Sexual intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailing', 'n.', 'The lighter parts of grain separated from the seed\n   threshing and winnowing; chaff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailing', 'n.', 'The refuse part of stamped ore, thrown behind the tail of\n   the buddle or washing apparatus. It is dressed over again to secure\n   whatever metal may exist in it. Called also tails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taille', 'n.', 'A tally; an account scored on a piece of wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taille', 'n.', 'Any imposition levied by the king, or any other lord, upon\n   his subjects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taille', 'n.', 'The French name for the tenor voice or part; also, for the\n   tenor viol or viola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailless', 'a.', 'Having no tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taillie', 'n.', 'Same as Tailzie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailor', 'n.', 'One whose occupation is to cut out and make mens garments;\n   also, one who cuts out and makes ladies outer garments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailor', 'n.', 'The mattowacca; -- called also tailor herring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailor', 'n.', 'The silversides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailor', 'n.', 'The goldfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailored', 'imp. & p. p.', 'of Tailor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailoring', 'p. pr. & vb. n.', 'of Tailor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailor', 'v. i.', 'To practice making mens clothes; to follow the business\n   of a tailor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailoress', 'n.', 'A female tailor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailoring', 'adv.', 'The business or the work of a tailor or a tailoress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailpiece', 'n.', 'A piece at the end; an appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailpiece', 'n.', 'One of the timbers which tail into a header, in floor\n   framing. See Illust. of Header.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailpiece', 'n.', 'An ornament placed at the bottom of a short page to fill\n   up the space, or at the end of a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailpiece', 'n.', 'A piece of ebony or other material attached to the lower\n   end of a violin or similar instrument, to which the strings are\n   fastened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailpin', 'n.', 'The center in the spindle of a turning lathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailrace', 'n.', 'See Race, n., 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailrace', 'n.', 'The channel in which tailings, suspended in water, are\n   conducted away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailstock', 'n.', 'The sliding block or support, in a lathe, which carries\n   the dead spindle, or adjustable center. The headstock supports the live\n   spindle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tail-water', 'n.', 'Water in a tailrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tailzie', 'n.', 'An entailment or deed whereby the legal course of\n   succession is cut off, and an arbitrary one substituted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tain', 'n.', 'Thin tin plate; also, tin foil for mirrors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taint', 'n.', 'A thrust with a lance, which fails of its intended effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taint', 'n.', 'An injury done to a lance in an encounter, without its being\n   broken; also, a breaking of a lance in an encounter in a dishonorable\n   or unscientific manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tainted', 'imp. & p. p.', 'of Taint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tainting', 'p. pr. & vb. n.', 'of Taint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taint', 'v. i.', 'To thrust ineffectually with a lance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taint', 'v. t.', 'To injure, as a lance, without breaking it; also, to\n   break, as a lance, but usually in an unknightly or unscientific manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taint', 'v. t.', 'To hit or touch lightly, in tilting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taint', 'v. t.', 'To imbue or impregnate with something extraneous,\n   especially with something odious, noxious, or poisonous; hence, to\n   corrupt; to infect; to poison; as, putrid substance taint the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taint', 'v. t.', 'Fig.: To stain; to sully; to tarnish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taint', 'v. i.', 'To be infected or corrupted; to be touched with something\n   corrupting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taint', 'v. i.', 'To be affected with incipient putrefaction; as, meat soon\n   taints in warm weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taint', 'n.', 'Tincture; hue; color; tinge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taint', 'n.', 'Infection; corruption; deprivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taint', 'n.', 'A blemish on reputation; stain; spot; disgrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taintless', 'a.', 'Free from taint or infection; pure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taintlessly', 'adv.', 'In a taintless manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tainture', 'n.', 'Taint; tinge; difilement; stain; spot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taintworm', 'n.', 'A destructive parasitic worm or insect larva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taira', 'n.', 'Same as Tayra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tairn', 'n.', 'See Tarn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tait', 'n.', 'A small nocturnal and arboreal Australian marsupial (Tarsipes\n   rostratus) about the size of a mouse. It has a long muzzle, a long\n   tongue, and very few teeth, and feeds upon honey and insects. Called\n   also noolbenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tajacu', 'n.', 'Alt. of Tajassu');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tajassu', 'n.', 'The common, or collared, peccary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'p. p.', 'Taken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Took', 'imp.', 'of Take');
  INSERT INTO words (word, word_type, description) VALUES  (    'Takend', 'p. p.', 'of Take');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taking', 'p. pr. & vb. n.', 'of Take');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. t.', 'In an active sense; To lay hold of; to seize with the\n   hands, or otherwise; to grasp; to get into ones hold or possession; to\n   procure; to seize and carry away; to convey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. t.', 'To obtain possession of by force or artifice; to get the\n   custody or control of; to reduce into subjection to ones power or\n   will; to capture; to seize; to make prisoner; as, to take am army, a\n   city, or a ship; also, to come upon or befall; to fasten on; to attack;\n   to seize; -- said of a disease, misfortune, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. t.', 'To gain or secure the interest or affection of; to\n   captivate; to engage; to interest; to charm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. t.', 'To make selection of; to choose; also, to turn to; to have\n   recourse to; as, to take the road to the right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. t.', 'To employ; to use; to occupy; hence, to demand; to\n   require; as, it takes so much cloth to make a coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. t.', 'To form a likeness of; to copy; to delineate; to picture;\n   as, to take picture of a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. t.', 'To draw; to deduce; to derive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. t.', 'To assume; to adopt; to acquire, as shape; to permit to\n   ones self; to indulge or engage in; to yield to; to have or feel; to\n   enjoy or experience, as rest, revenge, delight, shame; to form and\n   adopt, as a resolution; -- used in general senses, limited by a\n   following complement, in many idiomatic phrases; as, to take a\n   resolution; I take the liberty to say.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. t.', 'To lead; to conduct; as, to take a child to church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. t.', 'To carry; to convey; to deliver to another; to hand over;\n   as, he took the book to the bindery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. t.', 'To remove; to withdraw; to deduct; -- with from; as, to\n   take the breath from one; to take two from four.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. t.', 'In a somewhat passive sense, to receive; to bear; to\n   endure; to acknowledge; to accept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. t.', 'To accept, as something offered; to receive; not to refuse\n   or reject; to admit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. t.', 'To receive as something to be eaten or dronk; to partake\n   of; to swallow; as, to take food or wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. t.', 'Not to refuse or balk at; to undertake readily; to clear;\n   as, to take a hedge or fence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. t.', 'To bear without ill humor or resentment; to submit to; to\n   tolerate; to endure; as, to take a joke; he will take an affront from\n   no man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. t.', 'To admit, as, something presented to the mind; not to\n   dispute; to allow; to accept; to receive in thought; to entertain in\n   opinion; to understand; to interpret; to regard or look upon; to\n   consider; to suppose; as, to take a thing for granted; this I take to\n   be mans motive; to take men for spies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. t.', 'To accept the word or offer of; to receive and accept; to\n   bear; to submit to; to enter into agreement with; -- used in general\n   senses; as, to take a form or shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. i.', 'To take hold; to fix upon anything; to have the natural or\n   intended effect; to accomplish a purpose; as, he was inoculated, but\n   the virus did not take.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. i.', 'To please; to gain reception; to succeed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. i.', 'To move or direct the course; to resort; to betake ones\n   self; to proceed; to go; -- usually with to; as, the fox, being hard\n   pressed, took to the hedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'v. i.', 'To admit of being pictured, as in a photograph; as, his\n   face does not take well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'n.', 'That which is taken; especially, the quantity of fish\n   captured at one haul or catch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take', 'n.', 'The quantity or copy given to a compositor at one time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take-in', 'n.', 'Imposition; fraud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taken', '', 'p. p. of Take.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take-off', 'n.', 'An imitation, especially in the way of caricature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taker', 'n.', 'One who takes or receives; one who catches or apprehends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Take-up', 'n.', 'That which takes up or tightens; specifically, a device in\n   a sewing machine for drawing up the slack thread as the needle rises,\n   in completing a stitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taking', 'a.', 'Apt to take; alluring; attracting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taking', 'a.', 'Infectious; contageous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taking', 'n.', 'The act of gaining possession; a seizing; seizure;\n   apprehension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taking', 'n.', 'Agitation; excitement; distress of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taking', 'n.', 'Malign influence; infection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taking-off', 'n.', 'Removal; murder. See To take off (c), under Take, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talapoin', 'n.', 'A small African monkey (Cercopithecus, / Miopithecus,\n   talapoin) -- called also melarhine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talaria', 'n. pl.', 'Small wings or winged shoes represented as fastened to\n   the ankles, -- chiefly used as an attribute of Mercury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talbot', 'n.', 'A sort of dog, noted for quick scent and eager pursuit of\n   game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talbotype', 'n.', 'Same as Calotype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talc', 'n.', 'A soft mineral of a soapy feel and a greenish, whitish, or\n   grayish color, usually occurring in foliated masses. It is hydrous\n   silicate of magnesia. Steatite, or soapstone, is a compact granular\n   variety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talcose', 'a.', 'Alt. of Talcous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talcous', 'a.', 'Of or pertaining to talc; composed of, or resembling,\n   talc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tale', 'n.', 'See Tael.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tale', 'v. i.', 'That which is told; an oral relation or recital; any\n   rehearsal of what has occured; narrative; discourse; statement;\n   history; story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tale', 'v. i.', 'A number told or counted off; a reckoning by count; an\n   enumeration; a count, in distinction from measure or weight; a number\n   reckoned or stated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tale', 'v. i.', 'A count or declaration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tale', 'v. i.', 'To tell stories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talebearer', 'n.', 'One who officiously tells tales; one who impertinently\n   or maliciously communicates intelligence, scandal, etc., and makes\n   mischief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talebearing', 'a.', 'Telling tales officiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talebearing', 'n.', 'The act of informing officiously; communication of\n   sectrts, scandal, etc., maliciously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taled', 'n.', 'A kind of quadrangular piece of cloth put on by the Jews\n   when repeating prayers in the synagogues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taleful', 'a.', 'Full of stories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talegalla', 'n.', 'A genus of Australian birds which includes the brush\n   turkey. See Brush turkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talent', 'v. t.', 'Among the ancient Greeks, a weight and a denomination of\n   money equal to 60 minae or 6,000 drachmae. The Attic talent, as a\n   weight, was about 57 lbs. avoirdupois; as a denomination of silver\n   money, its value was 243 15s. sterling, or about $1,180.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talent', 'v. t.', 'Among the Hebrews, a weight and denomination of money.\n   For silver it was equivalent to 3,000 shekels, and in weight was equal\n   to about 93/ lbs. avoirdupois; as a denomination of silver, it has been\n   variously estimated at from 340 to 396 sterling, or about $1,645 to\n   $1,916. For gold it was equal to 10,000 gold shekels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talent', 'v. t.', 'Inclination; will; disposition; desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talent', 'v. t.', 'Intellectual ability, natural or acquired; mental\n   endowment or capacity; skill in accomplishing; a special gift,\n   particularly in business, art, or the like; faculty; a use of the word\n   probably originating in the Scripture parable of the talents (Matt.\n   xxv. 14-30).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talented', 'a.', 'Furnished with talents; possessing skill or talent;\n   mentally gifted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tales', 'n.', 'Persons added to a jury, commonly from those in or about the\n   courthouse, to make up any deficiency in the number of jurors regularly\n   summoned, being like, or such as, the latter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tales', 'syntactically sing.', 'The writ by which such persons are\n   summoned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talesmen', 'pl. ', 'of Talesman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talesman', 'n.', 'A person called to make up a deficiency in the number of\n   jurors when a tales is awarded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taleteller', 'n.', 'One who tells tales or stories, especially in a\n   mischievous or officious manner; a talebearer; a telltale; a tattler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talewise', 'adv.', 'In a way of a tale or story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taliacotian', 'a.', 'See Tagliacotian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taliation', 'n.', 'Retaliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talion', 'n.', 'Retaliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talipes', 'n.', 'The deformity called clubfoot. See Clubfoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talipot', 'n.', 'A beautiful tropical palm tree (Corypha umbraculifera), a\n   native of Ceylon and the Malabar coast. It has a trunk sixty or seventy\n   feet high, bearing a crown of gigantic fan-shaped leaves which are used\n   as umbrellas and as fans in ceremonial processions, and, when cut into\n   strips, as a substitute for writing paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talismans', 'pl. ', 'of Talisman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talisman', 'n.', 'A magical figure cut or engraved under certain\n   superstitious observances of the configuration of the heavens, to which\n   wonderful effects are ascribed; the seal, figure, character, or image,\n   of a heavenly sign, constellation, or planet, engraved on a sympathetic\n   stone, or on a metal corresponding to the star, in order to receive its\n   influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talisman', 'n.', 'Hence, something that produces extraordinary effects,\n   esp. in averting or repelling evil; an amulet; a charm; as, a talisman\n   to avert diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talismanic', 'a.', 'Alt. of Talismanical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talismanical', 'a.', 'Of or pertaining to a talisman; having the properties\n   of a talisman, or preservative against evils by occult influence;\n   magical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talked', 'imp. & p. p.', 'of Talk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talking', 'p. pr. & vb. n.', 'of Talk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talk', 'n.', 'To utter words; esp., to converse familiarly; to speak, as in\n   familiar discourse, when two or more persons interchange thoughts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talk', 'n.', 'To confer; to reason; to consult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talk', 'n.', 'To prate; to speak impertinently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talk', 'v. t.', 'To speak freely; to use for conversing or communicating;\n   as, to talk French.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talk', 'v. t.', 'To deliver in talking; to speak; to utter; to make a\n   subject of conversation; as, to talk nonsense; to talk politics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talk', 'v. t.', 'To consume or spend in talking; -- often followed by away;\n   as, to talk away an evening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talk', 'v. t.', 'To cause to be or become by talking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talk', 'n.', 'The act of talking; especially, familiar converse; mutual\n   discourse; that which is uttered, especially in familiar conversation,\n   or the mutual converse of two or more.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talk', 'n.', 'Report; rumor; as, to hear talk of war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talk', 'n.', 'Subject of discourse; as, his achievment is the talk of the\n   town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talkative', 'a.', 'Given to much talking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talker', 'n.', 'One who talks; especially, one who is noted for his power\n   of conversing readily or agreeably; a conversationist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talker', 'n.', 'A loquacious person, male or female; a prattler; a babbler;\n   also, a boaster; a braggart; -- used in contempt or reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talking', 'a.', 'That talks; able to utter words; as, a talking parrot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talking', 'a.', 'Given to talk; loquacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tall', 'superl.', 'High in stature; having a considerable, or an unusual,\n   extension upward; long and comparatively slender; having the diameter\n   or lateral extent small in proportion to the height; as, a tall person,\n   tree, or mast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tall', 'superl.', 'Brave; bold; courageous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tall', 'superl.', 'Fine; splendid; excellent; also, extravagant; excessive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallage', 'n.', 'Alt. of Talliage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talliage', 'n.', 'A certain rate or tax paid by barons, knights, and\n   inferior tenants, toward the public expenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallage', 'v. t.', 'To lay an impost upon; to cause to pay tallage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallier', 'n.', 'One who keeps tally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallness', 'n.', 'The quality or state of being tall; height of stature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallow', 'n.', 'The suet or fat of animals of the sheep and ox kinds,\n   separated from membranous and fibrous matter by melting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallow', 'n.', 'The fat of some other animals, or the fat obtained from\n   certain plants, or from other sources, resembling the fat of animals of\n   the sheep and ox kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallowed', 'imp. & p. p.', 'of Tallow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallowing', 'p. pr. & vb. n.', 'of Tallow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallow', 'v. t.', 'To grease or smear with tallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallow', 'v. t.', 'To cause to have a large quantity of tallow; to fatten;\n   as, tallow sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallower', 'n.', 'An animal which produces tallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallow-face', 'n.', 'One who has a sickly, pale complexion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallow-faced', 'a.', 'Having a sickly complexion; pale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallowing', 'n.', 'The act, or art, of causing animals to produce tallow;\n   also, the property in animals of producing tallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallowish', 'a.', 'Having the qualities of tallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallowy', 'a.', 'Of the nature of tallow; resembling tallow; greasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallwood', 'n.', 'Firewood cut into billets of a certain length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallies', 'pl. ', 'of Tally');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tally', 'n.', 'Originally, a piece of wood on which notches or scores were\n   cut, as the marks of number; later, one of two books, sheets of paper,\n   etc., on which corresponding accounts were kept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tally', 'n.', 'Hence, any account or score kept by notches or marks,\n   whether on wood or paper, or in a book; especially, one kept in\n   duplicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tally', 'n.', 'One thing made to suit another; a match; a mate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tally', 'n.', 'A notch, mark, or score made on or in a tally; as, to make\n   or earn a tally in a game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tally', 'n.', 'A tally shop. See Tally shop, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallied', 'imp. & p. p.', 'of Tally');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallying', 'p. pr. & vb. n.', 'of Tally');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tally', 'n.', 'To score with correspondent notches; hence, to make to\n   correspond; to cause to fit or suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tally', 'n.', 'To check off, as parcels of freight going inboard or\n   outboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tally', 'v. i.', 'To be fitted; to suit; to correspond; to match.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tally', 'v. i.', 'To make a tally; to score; as, to tally in a game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tally', 'a.', 'Stoutly; with spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallyho', 'interj. & n.', 'The huntsmans cry to incite or urge on his\n   hounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallyho', 'interj. & n.', 'A tallyho coach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallymen', 'pl. ', 'of Tallyman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallyman', 'n.', 'One who keeps the tally, or marks the sticks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tallyman', 'n.', 'One who keeps a tally shop, or conducts his business as\n   tally trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talmas', 'pl. ', 'of Talma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talma', 'n.', 'A kind of large cape, or short, full cloak, forming part of\n   the dress of ladies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talma', 'n.', 'A similar garment worn formerly by gentlemen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talmud', 'n.', 'The body of the Jewish civil and canonical law not\n   comprised in the Pentateuch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talmudic', 'a.', 'Alt. of Talmudical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talmudical', 'a.', 'Of or pertaining to the Talmud; contained in the\n   Talmud; as, Talmudic Greek; Talmudical phrases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talmudist', 'n.', 'One versed in the Talmud; one who adheres to the\n   teachings of the Talmud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talmudistic', 'a.', 'Resembling the Talmud; Talmudic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talon', 'n.', 'The claw of a predaceous bird or animal, especially the claw\n   of a bird of prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talon', 'n.', 'One of certain small prominences on the hind part of the\n   face of an elephants tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talon', 'n.', 'A kind of molding, concave at the bottom and convex at the\n   top; -- usually called an ogee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talon', 'n.', 'The shoulder of the bolt of a lock on which the key acts to\n   shoot the bolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talook', 'n.', 'Alt. of Taluk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taluk', 'n.', 'A large estate; esp., one constituting a revenue district or\n   dependency the native proprietor of which is responsible for the\n   collection and payment of the public revenue due from it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talookdar', 'n.', 'Alt. of Talukdar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talukdar', 'n.', 'A proprietor of a talook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talpa', 'n.', 'A genus of small insectivores including the common European\n   mole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tali', 'pl. ', 'of Talus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talus', 'n.', 'The astragalus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talus', 'n.', 'A variety of clubfoot (Talipes calcaneus). See the Note\n   under Talipes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talus', 'n.', 'A slope; the inclination of the face of a work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Talus', 'n.', 'A sloping heap of fragments of rock lying at the foot of a\n   precipice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamability', 'n.', 'The quality or state of being tamable; tamableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamable', 'a.', 'Capable of being tamed, subdued, or reclaimed from\n   wildness or savage ferociousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamandu', 'n.', 'A small ant-eater (Tamandua tetradactyla) native of the\n   tropical parts of South America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamanoir', 'n.', 'The ant-bear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamarack', 'n.', 'The American larch; also, the larch of Oregon and British\n   Columbia (Larix occidentalis). See Hackmatack, and Larch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamarack', 'n.', 'The black pine (Pinus Murrayana) of Alaska, California,\n   etc. It is a small tree with fine-grained wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamaric', 'n.', 'A shrub or tree supposed to be the tamarisk, or perhaps\n   some kind of heath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamarin', 'n.', 'Any one of several species of small squirrel-like South\n   American monkeys of the genus Midas, especially M. ursulus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamarind', 'n.', 'A leguminous tree (Tamarindus Indica) cultivated both the\n   Indies, and the other tropical countries, for the sake of its shade,\n   and for its fruit. The trunk of the tree is lofty and large, with\n   wide-spreading branches; the flowers are in racemes at the ends of the\n   branches. The leaves are small and finely pinnated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamarind', 'n.', 'One of the preserved seed pods of the tamarind, which\n   contain an acid pulp, and are used medicinally and for preparing a\n   pleasant drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamarisk', 'n.', 'Any shrub or tree of the genus Tamarix, the species of\n   which are European and Asiatic. They have minute scalelike leaves, and\n   small flowers in spikes. An Arabian species (T. mannifera) is the\n   source of one kind of manna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tambac', 'n.', 'See Tombac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tambour', 'n.', 'A kind of small flat drum; a tambourine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tambour', 'n.', 'A small frame, commonly circular, and somewhat resembling\n   a tambourine, used for stretching, and firmly holding, a portion of\n   cloth that is to be embroidered; also, the embroidery done upon such a\n   frame; -- called also, in the latter sense, tambour work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tambour', 'n.', 'Same as Drum, n., 2(d).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tambour', 'n.', 'A work usually in the form of a redan, to inclose a space\n   before a door or staircase, or at the gorge of a larger work. It is\n   arranged like a stockade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tambour', 'n.', 'A shallow metallic cup or drum, with a thin elastic\n   membrane supporting a writing lever. Two or more of these are connected\n   by an India rubber tube, and used to transmit and register the\n   movements of the pulse or of any pulsating artery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamboured', 'imp. & p. p.', 'of Tambour');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tambouring', 'p. pr. & vb. n.', 'of Tambour');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tambour', 'v. t.', 'To embroider on a tambour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tambourin', 'n.', 'A tambourine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tambourin', 'n.', 'An old Provencal dance of a lively character, common on\n   the stage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tambourine', 'n.', 'A small drum, especially a shallow drum with only one\n   skin, played on with the hand, and having bells at the sides; a\n   timbrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tambreet', 'n.', 'The duck mole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamburin', 'n.', 'See Tambourine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tame', 'v. t.', 'To broach or enter upon; to taste, as a liquor; to divide;\n   to distribute; to deal out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tame', 'superl.', 'Reduced from a state of native wildness and shyness;\n   accustomed to man; domesticated; domestic; as, a tame deer, a tame\n   bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tame', 'superl.', 'Crushed; subdued; depressed; spiritless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tame', 'superl.', 'Deficient in spirit or animation; spiritless; dull;\n   flat; insipid; as, a tame poem; tame scenery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamed', 'imp. & p. p.', 'of Tame');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taming', 'p. pr. & vb. n.', 'of Tame');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tame', 'a.', 'To reduce from a wild to a domestic state; to make gentle and\n   familiar; to reclaim; to domesticate; as, to tame a wild beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tame', 'a.', 'To subdue; to conquer; to repress; as, to tame the pride or\n   passions of youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tameable', 'a.', 'Tamable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tameless', 'a.', 'Incapable of being tamed; wild; untamed; untamable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamely', 'adv.', 'In a tame manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tameness', 'n.', 'The quality or state of being tame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamer', 'n.', 'One who tames or subdues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamias', 'n.', 'A genus of ground squirrels, including the chipmunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamil', 'a.', 'Of or pertaining to the Tamils, or to their language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamil', 'n.', 'One of a Dravidian race of men native of Northern Ceylon and\n   Southern India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamil', 'n.', 'The Tamil language, the most important of the Dravidian\n   languages. See Dravidian, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamilian', 'a. & n.', 'Tamil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamine', 'n.', 'Alt. of Taminy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taminy', 'n.', 'A kind of woolen cloth; tammy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamis', 'n.', 'A sieve, or strainer, made of a kind of woolen cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamis', 'n.', 'The cloth itself; tammy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamkin', 'n.', 'A tampion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tammies', 'pl. ', 'of Tammy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tammy', 'n.', 'A kind of woolen, or woolen and cotton, cloth, often highly\n   glazed, -- used for curtains, sieves, strainers, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tammy', 'n.', 'A sieve, or strainer, made of this material; a tamis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamped', 'imp. & p. p.', 'of Tamp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamping', 'p. pr. & vb. n.', 'of Tamp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamp', 'v. t.', 'In blasting, to plug up with clay, earth, dry sand, sod,\n   or other material, as a hole bored in a rock, in order to prevent the\n   force of the explosion from being misdirected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamp', 'v. t.', 'To drive in or down by frequent gentle strokes; as, to\n   tamp earth so as to make a smooth place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tampan', 'n.', 'A venomous South African tick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tampeon', 'n.', 'See Tampion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamper', 'n.', 'One who tamps; specifically, one who prepares for blasting,\n   by filling the hole in which the charge is placed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamper', 'n.', 'An instrument used in tamping; a tamping iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tampered', 'imp. & p. p.', 'of Tamper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tampering', 'p. pr. & vb. n.', 'of Tamper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamper', 'v. i.', 'To meddle; to be busy; to try little experiments; as, to\n   tamper with a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamper', 'v. i.', 'To meddle so as to alter, injure, or vitiate a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamper', 'v. i.', 'To deal unfairly; to practice secretly; to use bribery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamperer', 'n.', 'One who tampers; one who deals unfairly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tampico fiber', '', 'Alt. of fibre');
  INSERT INTO words (word, word_type, description) VALUES  (    'fibre', '', 'A tough vegetable fiber used as a substitute for bristles in\n   making brushes. The piassava and the ixtle are both used under this\n   name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamping', 'n.', 'The act of one who tamps; specifically, the act of filling\n   up a hole in a rock, or the branch of a mine, for the purpose of\n   blasting the rock or exploding the mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamping', 'n.', 'The material used in tamping. See Tamp, v. t., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tampion', 'n.', 'A wooden stopper, or plug, as for a cannon or other piece\n   of ordnance, when not in use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tampion', 'n.', 'A plug for upper end of an organ pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tampoe', 'n.', 'The edible fruit of an East Indian tree (Baccaurea\n   Malayana) of the Spurge family. It somewhat resembles an apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tampon', 'n.', 'A plug introduced into a natural or artificial cavity of\n   the body in order to arrest hemorrhage, or for the application of\n   medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tampon', 'v. t.', 'To plug with a tampon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tampoon', 'n.', 'The stopper of a barrel; a bung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tam-tam', 'n.', 'A kind of drum used in the East Indies and other Oriental\n   countries; -- called also tom-tom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tam-tam', 'n.', 'A gong. See Gong, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tamul', 'a. & n.', 'Tamil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tan', 'n.', 'See Picul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tan', 'n.', 'The bark of the oak, and some other trees, bruised and broken\n   by a mill, for tanning hides; -- so called both before and after it has\n   been used. Called also tan bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tan', 'n.', 'A yellowish-brown color, like that of tan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tan', 'n.', 'A brown color imparted to the skin by exposure to the sun; as,\n   hands covered with tan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tan', 'a.', 'Of the color of tan; yellowish-brown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanned', 'imp. & p. p.', 'of Tan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanning', 'p. pr. & vb. n.', 'of Tan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tan', 'n.', 'To convert (the skin of an animal) into leather, as by usual\n   process of steeping it in an infusion of oak or some other bark,\n   whereby it is impregnated with tannin, or tannic acid (which exists in\n   several species of bark), and is thus rendered firm, durable, and in\n   some degree impervious to water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tan', 'n.', 'To make brown; to imbrown, as by exposure to the rays of the\n   sun; as, to tan the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tan', 'v. i.', 'To get or become tanned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tana', 'n.', 'Same as Banxring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanager', 'n.', 'Any one of numerous species of bright-colored singing\n   birds belonging to Tanagra, Piranga, and allied genera. The scarlet\n   tanager (Piranga erythromelas) and the summer redbird (Piranga rubra)\n   are common species of the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanagrine', 'a.', 'Of or pertaining to the tanagers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanagroid', 'a.', 'Tanagrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanate', 'n.', 'An Asiatic wild dog (Canis procyonoides), native of Japan\n   and adjacent countries. It has a short, bushy tail. Called also raccoon\n   dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tandem', 'adv. & a.', 'One after another; -- said especially of horses\n   harnessed and driven one before another, instead of abreast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tandem', 'n.', 'A team of horses harnessed one before the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tang', 'n.', 'A coarse blackish seaweed (Fuscus nodosus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tang', 'n.', 'A strong or offensive taste; especially, a taste of something\n   extraneous to the thing itself; as, wine or cider has a tang of the\n   cask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tang', 'n.', 'Fig.: A sharp, specific flavor or tinge. Cf. Tang a twang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tang', 'n.', 'A projecting part of an object by means of which it is\n   secured to a handle, or to some other part; anything resembling a\n   tongue in form or position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tang', 'n.', 'The part of a knife, fork, file, or other small instrument,\n   which is inserted into the handle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tang', 'n.', 'The projecting part of the breech of a musket barrel, by\n   which the barrel is secured to the stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tang', 'n.', 'The part of a sword blade to which the handle is fastened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tang', 'n.', 'The tongue of a buckle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tang', 'n.', 'A sharp, twanging sound; an unpleasant tone; a twang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanged', 'imp. & p. p.', 'of Tang');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanging', 'p. pr. & vb. n.', 'of Tang');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tang', 'v. t.', 'To cause to ring or sound loudly; to ring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tang', 'v. i.', 'To make a ringing sound; to ring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangalung', 'n.', 'An East Indian civet (Viverra tangalunga).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangence', 'n.', 'Tangency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangency', 'n.', 'The quality or state of being tangent; a contact or\n   touching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangent', 'v. t.', 'A tangent line curve, or surface; specifically, that\n   portion of the straight line tangent to a curve that is between the\n   point of tangency and a given line, the given line being, for example,\n   the axis of abscissas, or a radius of a circle produced. See\n   Trigonometrical function, under Function.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangent', 'a.', 'Touching; touching at a single point');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangent', 'a.', 'meeting a curve or surface at a point and having at that\n   point the same direction as the curve or surface; -- said of a straight\n   line, curve, or surface; as, a line tangent to a curve; a curve tangent\n   to a surface; tangent surfaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangental', 'a.', 'Tangential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangential', 'a.', 'Of or pertaining to a tangent; in the direction of a\n   tangent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangentially', 'adv.', 'In the direction of a tangent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangerine', 'n.', 'A kind of orange, much like the mandarin, but of deeper\n   color and higher flavor. It is said to have been produced in America\n   from the mandarin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangfish', 'n.', 'The common harbor seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanghinia', 'n.', 'The ordeal tree. See under Ordeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangibility', 'n.', 'The quality or state of being tangible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangible', 'a.', 'Perceptible to the touch; tactile; palpable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangible', 'a.', 'Capable of being possessed or realized; readily\n   apprehensible by the mind; real; substantial; evident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangled', 'imp. & p. p.', 'of Tangle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangling', 'p. pr. & vb. n.', 'of Tangle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangle', 'n.', 'To unite or knit together confusedly; to interweave or\n   interlock, as threads, so as to make it difficult to unravel the knot;\n   to entangle; to ravel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangle', 'n.', 'To involve; to insnare; to entrap; as, to be tangled in\n   lies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangle', 'v. i.', 'To be entangled or united confusedly; to get in a\n   tangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangle', 'n.', 'Any large blackish seaweed, especially the Laminaria\n   saccharina. See Kelp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangle', 'v.', 'A knot of threads, or other thing, united confusedly, or so\n   interwoven as not to be easily disengaged; a snarl; as, hair or yarn in\n   tangles; a tangle of vines and briers. Used also figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangle', 'v.', 'An instrument consisting essentially of an iron bar to\n   which are attached swabs, or bundles of frayed rope, or other similar\n   substances, -- used to capture starfishes, sea urchins, and other\n   similar creatures living at the bottom of the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanglefish', 'n.', 'The sea adder, or great pipefish of Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanglingly', 'adv.', 'In a tangling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangly', 'a.', 'Entangled; intricate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangly', 'a.', 'Covered with tangle, or seaweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangram', 'n.', 'A Chinese toy made by cutting a square of thin wood, or\n   other suitable material, into seven pieces, as shown in the cut, these\n   pieces being capable of combination in various ways, so as to form a\n   great number of different figures. It is now often used in primary\n   schools as a means of instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangue', 'n.', 'The tenrec.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangun', 'n.', 'A piebald variety of the horse, native of Thibet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tangwhaup', 'n.', 'The whimbrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanier', 'n.', 'An aroid plant (Caladium sagittaefolium), the leaves of\n   which are boiled and eaten in the West Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanist', 'n.', 'In Ireland, a lord or proprietor of a tract of land or of a\n   castle, elected by a family, under the system of tanistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanistry', 'n.', 'In Ireland, a tenure of family lands by which the\n   proprietor had only a life estate, to which he was admitted by\n   election.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanite', 'n.', 'A firm composition of emery and a certain kind of cement,\n   used for making grinding wheels, slabs, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tank', 'n.', 'A small Indian dry measure, averaging 240 grains in weight;\n   also, a Bombay weight of 72 grains, for pearls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tank', 'n.', 'A large basin or cistern; an artificial receptacle for\n   liquids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanka', 'n.', 'A kind of boat used in Canton. It is about 25 feet long and\n   is often rowed by women. Called also tankia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tankard', 'n.', 'A large drinking vessel, especially one with a cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tankia', 'n.', 'See Tanka.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tankling', 'n.', 'A tinkling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanling', 'n.', 'One tanned by the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tannable', 'a.', 'That may be tanned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tannage', 'n.', 'A tanning; the act, operation, or result of tanning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tannate', 'n.', 'A salt of tannic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanner', 'n.', 'One whose occupation is to tan hides, or convert them into\n   leather by the use of tan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanneries', 'pl. ', 'of Tannery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tannery', 'n.', 'A place where the work of tanning is carried on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tannery', 'n.', 'The art or process of tanning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tannic', 'a.', 'Of or pertaining to tan; derived from, or resembling, tan;\n   as, tannic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tannier', 'n.', 'See Tanier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tannin', 'n.', 'Same as Tannic acid, under Tannic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanning', 'n.', 'The art or process of converting skins into leather. See\n   Tan, v. t., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanrec', 'n.', 'Same as Tenrec.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tansy', 'n.', 'Any plant of the composite genus Tanacetum. The common tansy\n   (T. vulgare) has finely divided leaves, a strong aromatic odor, and a\n   very bitter taste. It is used for medicinal and culinary purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tansy', 'n.', 'A dish common in the seventeenth century, made of eggs,\n   sugar, rose water, cream, and the juice of herbs, baked with butter in\n   a shallow dish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tant', 'n.', 'A small scarlet arachnid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tantalate', 'n.', 'A salt of tantalic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tantalic', 'a.', 'Of or pertaining to tantalum; derived from, or\n   containing, tantalum; specifically, designating any one of a series of\n   acids analogous to nitric acid and the polyacid compounds of\n   phosphorus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tantalism', 'n.', 'A punishment like that of Tantalus; a teasing or\n   tormenting by the hope or near approach of good which is not\n   attainable; tantalization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tantalite', 'n.', 'A heavy mineral of an iron-black color and submetallic\n   luster. It is essentially a tantalate of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tantalization', 'n.', 'The act of tantalizing, or state of being\n   tantalized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tantalized', 'imp. & p. p.', 'of Tantalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tantalizing', 'p. pr. & vb. n.', 'of Tantalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tantalize', 'v. t.', 'To tease or torment by presenting some good to the\n   view and exciting desire, but continually frustrating the expectations\n   by keeping that good out of reach; to tease; to torment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tantalizer', 'n.', 'One who tantalizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tantalizingly', 'adv.', 'In a tantalizing or teasing manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tantalum', 'n.', 'A rare nonmetallic element found in certain minerals, as\n   tantalite, samarskite, and fergusonite, and isolated as a dark powder\n   which becomes steel-gray by burnishing. Symbol Ta. Atomic weight 182.0.\n   Formerly called also tantalium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tantalus', 'n.', 'A Phrygian king who was punished in the lower world by\n   being placed in the midst of a lake whose waters reached to his chin\n   but receded whenever he attempted to allay his thirst, while over his\n   head hung branches laden with choice fruit which likewise receded\n   whenever he stretched out his hand to grasp them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tantalus', 'n.', 'A genus of wading birds comprising the wood ibises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tantamount', 'a.', 'Equivalent in value, signification, or effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tantamount', 'v. i.', 'To be tantamount or equivalent; to amount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tantivy', 'adv.', 'Swiftly; speedily; rapidly; -- a fox-hunting term; as,\n   to ride tantivy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tantivy', 'n.', 'A rapid, violent gallop; an impetuous rush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tantivy', 'v. i.', 'To go away in haste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tantrum', 'n.', 'A whim, or burst of ill-humor; an affected air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanyard', 'n.', 'An inclosure where the tanning of leather is carried on; a\n   tannery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tanystomata', 'n. pl.', 'A division of dipterous insects in which the\n   proboscis is large and contains lancelike mandibles and maxillae. The\n   horseflies and robber flies are examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taoism', 'n.', 'One of the popular religions of China, sanctioned by the\n   state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapped', 'imp. & p. p.', 'of Tap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapping', 'p. pr. & vb. n.', 'of Tap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tap', 'v. t.', 'To strike with a slight or gentle blow; to touch gently; to\n   rap lightly; to pat; as, to tap one with the hand or a cane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tap', 'v. t.', 'To put a new sole or heel on; as, to tap shoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tap', 'n.', 'A gentle or slight blow; a light rap; a pat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tap', 'n.', 'A piece of leather fastened upon the bottom of a boot or shoe\n   in repairing or renewing the sole or heel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tap', 'n.', 'A signal, by drum or trumpet, for extinguishing all lights in\n   soldiers quarters and retiring to bed, -- usually given about a\n   quarter of an hour after tattoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tap', 'v. i.', 'To strike a gentle blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tap', 'n.', 'A hole or pipe through which liquor is drawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tap', 'n.', 'A plug or spile for stopping a hole pierced in a cask, or the\n   like; a faucet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tap', 'n.', 'Liquor drawn through a tap; hence, a certain kind or quality\n   of liquor; as, a liquor of the same tap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tap', 'n.', 'A place where liquor is drawn for drinking; a taproom; a bar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tap', 'n.', 'A tool for forming an internal screw, as in a nut, consisting\n   of a hardened steel male screw grooved longitudinally so as to have\n   cutting edges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tap', 'v. t.', 'To pierce so as to let out, or draw off, a fluid; as, to\n   tap a cask, a tree, a tumor, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tap', 'v. t.', 'Hence, to draw from (anything) in any analogous way; as, to\n   tap telegraph wires for the purpose of intercepting information; to tap\n   the treasury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tap', 'v. t.', 'To draw, or cause to flow, by piercing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tap', 'v. t.', 'To form an internal screw in (anything) by means of a tool\n   called a tap; as, to tap a nut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapa', 'n.', 'A kind of cloth prepared by the Polynesians from the inner\n   bark of the paper mulberry; -- sometimes called also kapa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapayaxin', 'n.', 'A Mexican spinous lizard (Phrynosoma orbiculare) having\n   a head somewhat like that of a toad; -- called also horned toad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tape', 'n.', 'A narrow fillet or band of cotton or linen; a narrow woven\n   fabric used for strings and the like; as, curtains tied with tape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tape', 'n.', 'A tapeline; also, a metallic ribbon so marked as to serve as\n   a tapeline; as, a steel tape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapeline', 'n.', 'A painted tape, marked with linear dimensions, as inches,\n   feet, etc., and often inclosed in a case, -- used for measuring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taper', 'n.', 'A small wax candle; a small lighted wax candle; hence, a\n   small light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taper', 'n.', 'A tapering form; gradual diminution of thickness in an\n   elongated object; as, the taper of a spire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taper', 'a.', 'Regularly narrowed toward the point; becoming small toward\n   one end; conical; pyramidical; as, taper fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapered', 'imp. & p. p.', 'of Taper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapering', 'p. pr. & vb. n.', 'of Taper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taper', 'v. i.', 'To become gradually smaller toward one end; as, a sugar\n   loaf tapers toward one end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taper', 'v. t.', 'To make or cause to taper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapered', 'a.', 'Lighted with a taper or tapers; as, a tapered choir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapering', 'a.', 'Becoming gradually smaller toward one end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taperness', 'n.', 'The quality or state of being taper; tapering form;\n   taper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapestries', 'pl. ', 'of Tapestry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapestry', 'n.', 'A fabric, usually of worsted, worked upon a warp of linen\n   or other thread by hand, the designs being usually more or less\n   pictorial and the stuff employed for wall hangings and the like. The\n   term is also applied to different kinds of embroidery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapestried', 'imp. & p. p.', 'of Tapestry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapestrying', 'p. pr. & vb. n.', 'of Tapestry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapestry', 'v. t.', 'To adorn with tapestry, or as with tapestry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapet', 'n.', 'Worked or figured stuff; tapestry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapetis', 'pl. ', 'of Tapeti');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapeti', 'n.', 'A small South American hare (Lepus Braziliensis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapetum', 'n.', 'An area in the pigmented layer of the choroid coat of the\n   eye in many animals, which has an iridescent or metallic luster and\n   helps to make the eye visible in the dark. Sometimes applied to the\n   whole layer of pigmented epithelium of the choroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapeworm', 'n.', 'Any one of numerous species of cestode worms belonging to\n   Taenia and many allied genera. The body is long, flat, and composed of\n   numerous segments or proglottids varying in shape, those toward the end\n   of the body being much larger and longer than the anterior ones, and\n   containing the fully developed sexual organs. The head is small,\n   destitute of a mouth, but furnished with two or more suckers (which\n   vary greatly in shape in different genera), and sometimes, also, with\n   hooks for adhesion to the walls of the intestines of the animals in\n   which they are parasitic. The larvae (see Cysticercus) live in the\n   flesh of various creatures, and when swallowed by another animal of the\n   right species develop into the mature tapeworm in its intestine. See\n   Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taphouse', 'n.', 'A house where liquors are retailed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taphrenchyma', 'n.', 'Same as Bothrenchyma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapinage', 'n.', 'A lurking or skulking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapioca', 'n.', 'A coarsely granular substance obtained by heating, and\n   thus partly changing, the moistened starch obtained from the roots of\n   the cassava. It is much used in puddings and as a thickening for soups.\n   See Cassava.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapir', 'n.', 'Any one of several species of large odd-toed ungulates\n   belonging to Tapirus, Elasmognathus, and allied genera. They have a\n   long prehensile upper lip, short ears, short and stout legs, a short,\n   thick tail, and short, close hair. They have three toes on the hind\n   feet, and four toes on the fore feet, but the outermost toe is of\n   little use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapiroid', 'a.', 'Allied to the tapir, or the Tapir family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapis', 'n.', 'Tapestry; formerly, the cover of a council table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapis', 'v. t.', 'To cover or work with figures like tapestry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapiser', 'n.', 'A maker of tapestry; an upholsterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapish', 'v. i.', 'To lie close to the ground, so as to be concealed; to\n   squat; to crouch; hence, to hide ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taplash', 'n.', 'Bad small beer; also, the refuse or dregs of liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taplings', 'n. pl.', 'The strong double leathers by which the two parts of\n   a flail are united.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapoa tafa', '', 'A small carnivorous marsupial (Phascogale penicillata)\n   having long, soft fur, and a very long tail with a tuft of long hairs\n   at the end; -- called also brush-tailed phascogale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tappen', 'n.', 'An obstruction, or indigestible mass, found in the\n   intestine of bears and other animals during hibernation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapper', 'n.', 'The lesser spotted woodpecker (Dendrocopus minor); --\n   called also tapperer, tabberer, little wood pie, barred woodpecker,\n   wood tapper, hickwall, and pump borer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tappester', 'n.', 'A female tapster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tappet', 'n.', 'A lever or projection moved by some other piece, as a cam,\n   or intended to tap or touch something else, with a view to produce\n   change or regulate motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tappice', 'v. i.', 'Alt. of Tappis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tappis', 'v. i.', 'See Tapish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tappit hen', '', 'A hen having a tuft of feathers on her head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tappit hen', '', 'A measuring pot holding one quart (according to some,\n   three quarts); -- so called from a knob on the lid, thought to resemble\n   a crested hen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taproom', 'n.', 'A room where liquors are kept on tap; a barroom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taproot', 'n.', 'The root of a plant which penetrates the earth directly\n   downward to a considerable depth without dividing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tapster', 'n.', 'One whose business is to tap or draw ale or other liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taqua-nut', 'n.', 'A Central American name for the ivory nut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tar', 'n.', 'A sailor; a seaman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tar', 'n.', 'A thick, black, viscous liquid obtained by the distillation of\n   wood, coal, etc., and having a varied composition according to the\n   temperature and material employed in obtaining it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarred', 'imp. & p. p.', 'of Tar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarring', 'p. pr. & vb. n.', 'of Tar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tar', 'v. t.', 'To smear with tar, or as with tar; as, to tar ropes; to tar\n   cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taranis', 'n.', 'A Celtic divinity, regarded as the evil principle, but\n   confounded by the Romans with Jupiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarantass', 'n.', 'A low four-wheeled carriage used in Russia. The carriage\n   box rests on two long, springy poles which run from the fore to the\n   hind axletree. When snow falls, the wheels are taken off, and the body\n   is mounted on a sledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarantella', 'n.', 'A rapid and delirious sort of Neapolitan dance in 6-8\n   time, which moves in whirling triplets; -- so called from a popular\n   notion of its being a remedy against the poisonous bite of the\n   tarantula. Some derive its name from Taranto in Apulia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarantella', 'n.', 'Music suited to such a dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarantism', 'n.', 'A nervous affection producing melancholy, stupor, and an\n   uncontrollable desire to dance. It was supposed to be produced by the\n   bite of the tarantula, and considered to be incapable of cure except by\n   protracted dancing to appropriate music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarantulas', 'pl. ', 'of Tarantula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarantulae', 'pl. ', 'of Tarantula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarantula', 'n.', 'Any one of several species of large spiders, popularly\n   supposed to be very venomous, especially the European species\n   (Tarantula apuliae). The tarantulas of Texas and adjacent countries are\n   large species of Mygale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarantulated', 'a.', 'Bitten by a tarantula; affected with tarantism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarbogan', 'n. & v.', 'See Toboggan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarboosh', 'n.', 'A red cap worn by Turks and other Eastern nations,\n   sometimes alone and sometimes swathed with linen or other stuff to make\n   a turban. See Fez.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tardation', 'n.', 'The act of retarding, or delaying; retardation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tardigrada', 'a.', 'A tribe of edentates comprising the sloths. They are\n   noted for the slowness of their movements when on the ground. See\n   Sloth, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tardigrada', 'a.', 'An order of minute aquatic arachnids; -- called also\n   bear animalcules, sloth animalcules, and water bears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tardigrade', 'a.', 'Moving or stepping slowly; slow-paced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tardigrade', 'a.', 'Of or pertaining to the Tardigrada.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tardigrade', 'n.', 'One of the Tardigrada.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tardigradous', 'a.', 'Moving slowly; slow-paced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tardily', 'adv.', 'In a tardy manner; slowly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tardiness', 'n.', 'The quality or state of being tardy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarditation', 'n.', 'Tardiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tardity', 'n.', 'Slowness; tardiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tardo', 'a.', 'Slow; -- a direction to perform a passage slowly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tardo', 'n.', 'A sloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tardy', 'superl.', 'Moving with a slow pace or motion; slow; not swift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tardy', 'superl.', 'Not being inseason; late; dilatory; -- opposed to\n   prompt; as, to be tardy in ones payments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tardy', 'superl.', 'Unwary; unready.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tardy', 'superl.', 'Criminal; guilty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tardy', 'v. t.', 'To make tardy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tare', 'imp.', 'Tore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tare', 'n.', 'A weed that grows among wheat and other grain; -- alleged by\n   modern naturalists to be the Lolium temulentum, or darnel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tare', 'n.', 'A name of several climbing or diffuse leguminous herbs of the\n   genus Vicia; especially, the V. sativa, sometimes grown for fodder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tare', 'n.', 'Deficientcy in the weight or quantity of goods by reason of\n   the weight of the cask, bag, or whatever contains the commodity, and is\n   weighed with it; hence, the allowance or abatement of a certain weight\n   or quantity which the seller makes to the buyer on account of the\n   weight of such cask, bag, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tared', 'imp. & p. p.', 'of Tare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taring', 'p. pr. & vb. n.', 'of Tare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tare', 'v. t.', 'To ascertain or mark the tare of (goods).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tared', 'a.', 'Weighed; determined; reduced to equal or standard weight;\n   as, tared filter papers, used in weighing precipitates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarente', 'n.', 'A harmless lizard of the Gecko family (Platydactylus\n   Mauritianicus) found in Southern Europe and adjacent countries,\n   especially among old walls and ruins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarentism', 'n.', 'See Tarantism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarentula', 'n.', 'See Tarantula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Targe', 'n.', 'A shield or target.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Target', 'n.', 'A kind of small shield or buckler, used as a defensive\n   weapon in war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Target', 'n.', 'A butt or mark to shoot at, as for practice, or to test the\n   accuracy of a firearm, or the force of a projectile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Target', 'n.', 'The pattern or arrangement of a series of hits made by a\n   marksman on a butt or mark; as, he made a good target.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Target', 'n.', 'The sliding crosspiece, or vane, on a leveling staff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Target', 'n.', 'A conspicuous disk attached to a switch lever to show its\n   position, or for use as a signal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Targeted', 'a.', 'Furnished, armed, or protected, with a target.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Targeteer', 'n.', 'One who is armed with a target or shield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Targums', 'pl. ', 'of Targum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Targumim', 'pl. ', 'of Targum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Targum', 'n.', 'A translation or paraphrase of some portion of the Old\n   Testament Scriptures in the Chaldee or Aramaic language or dialect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Targumist', 'n.', 'The writer of a Targum; one versed in the Targums.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tariff', 'n.', 'A schedule, system, or scheme of duties imposed by the\n   government of a country upon goods imported or exported; as, a revenue\n   tariff; a protective tariff; Clays compromise tariff. (U. S. 1833).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tariff', 'n.', 'The duty, or rate of duty, so imposed; as, the tariff on\n   wool; a tariff of two cents a pound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tariff', 'n.', 'Any schedule or system of rates, changes, etc.; as, a\n   tariff of fees, or of railroad fares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tariffed', 'imp. & p. p.', 'of Tariff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tariffing', 'p. pr. & vb. n.', 'of Tariff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tariff', 'v. t.', 'To make a list of duties on, as goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarin', 'n.', 'The siskin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taring', 'n.', 'The common tern; -- called also tarret, and tarrock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarlatan', 'n.', 'A kind of thin, transparent muslin, used for dresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarn', 'n.', 'A mountain lake or pool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarnished', 'imp. & p. p.', 'of Tarnish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarnishing', 'p. pr. & vb. n.', 'of Tarnish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarnish', 'a.', 'To soil, or change the appearance of, especially by an\n   alternation induced by the air, or by dust, or the like; to diminish,\n   dull, or destroy the luster of; to sully; as, to tarnish a metal; to\n   tarnish gilding; to tarnish the purity of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarnish', 'v. i.', 'To lose luster; to become dull; as, gilding will\n   tarnish in a foul air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarnish', 'n.', 'The quality or state of being tarnished; stain; soil;\n   blemish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarnish', 'n.', 'A thin film on the surface of a metal, usually due to a\n   slight alteration of the original color; as, the steel tarnish in\n   columbite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarnisher', 'n.', 'One who, or that which, tarnishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taro', 'n.', 'A name for several aroid plants (Colocasia antiquorum, var.\n   esculenta, Colocasia macrorhiza, etc.), and their rootstocks. They have\n   large ovate-sagittate leaves and large fleshy rootstocks, which are\n   cooked and used for food in tropical countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarot', 'n.', 'A game of cards; -- called also taroc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarpan', 'n.', 'A wild horse found in the region of the Caspian Sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarpaulin', 'n.', 'A piece of canvas covered with tar or a waterproof\n   composition, used for covering the hatches of a ship, hammocks, boats,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarpaulin', 'n.', 'A hat made of, or covered with, painted or tarred cloth,\n   worn by sailors and others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarpaulin', 'n.', 'Hence, a sailor; a seaman; a tar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarpon', 'n.', 'Same as Tarpum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarpum', 'n.', 'A very large marine fish (Megapolis Atlanticus) of the\n   Southern United States and the West Indies. It often becomes six or\n   more feet in length, and has large silvery scales. The scales are a\n   staple article of trade, and are used in fancywork. Called also tarpon,\n   sabalo, savanilla, silverfish, and jewfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarquinish', 'a.', 'Like a Tarquin, a king of ancient Rome; proud; haughty;\n   overbearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarrace', 'n.', 'See Trass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarragon', 'n.', 'A plant of the genus Artemisa (A. dracunculus), much used\n   in France for flavoring vinegar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarras', 'n.', 'See Trass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarre', 'v.', 'To set on, as a dog; to incite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarriance', 'n.', 'The act or time of tarrying; delay; lateness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarrier', 'n.', 'One who, or that which, tarries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarrier', 'n.', 'A kind of dig; a terrier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarrock', 'n.', 'The young of the kittiwake gull before the first molt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarrock', 'n.', 'The common guillemot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarrock', 'n.', 'The common tern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarry', 'n.', 'Consisting of, or covered with, tar; like tar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarried', 'imp. & p. p.', 'of Tarry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarrying', 'p. pr. & vb. n.', 'of Tarry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarry', 'v. i.', 'To stay or remain behind; to wait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarry', 'v. i.', 'To delay; to put off going or coming; to loiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarry', 'v. i.', 'To stay; to abide; to continue; to lodge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarry', 'v. t.', 'To delay; to defer; to put off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarry', 'v. t.', 'To wait for; to stay or stop for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarry', 'n.', 'Stay; stop; delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsal', 'a.', 'Of or pertaining to the tarsus (either of the foot or eye).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsal', 'n.', 'A tarsal bone or cartilage; a tarsale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsal', 'n.', 'Same as Tercel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsalia', 'pl. ', 'of Tarsale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsale', 'n.', 'One of the bones or cartilages of the tarsus; esp., one of\n   the series articulating with the metatarsals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarse', 'n.', 'The male falcon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarse', 'n.', 'tarsus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsectomy', 'n.', 'The operation of excising one or more of the bones of\n   the tarsus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsel', 'n.', 'A male hawk. See Tercel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsi', 'n.', 'pl. of Tarsus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsia', 'n.', 'Alt. of Tarsiatura');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsiatura', 'n.', 'A kind of mosaic in woodwork, much employed in Italy in\n   the fifteenth century and later, in which scrolls and arabesques, and\n   sometimes architectural scenes, landscapes, fruits, flowers, and the\n   like, were produced by inlaying pieces of wood of different colors and\n   shades into panels usually of walnut wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsier', 'n.', 'See Tarsius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsius', 'n.', 'A genus of nocturnal lemurine mammals having very large\n   eyes and ears, a long tail, and very long proximal tarsal bones; --\n   called also malmag, spectral lemur, podji, and tarsier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarso-', '', 'A combining form used in anatomy to indicate connection with,\n   or relation to, the tarsus; as, tarsometatarsus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsometatarsal', 'a.', 'Of or pertaining to both the tarsus and\n   metatarsus; as, the tarsometatarsal articulations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsometatarsal', 'a.', 'Of or pertaining to the tarsometatarsus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsometatarsi', 'pl. ', 'of Tarsometatarsus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsometatarsus', 'n.', 'The large bone next the foot in the leg of a bird.\n   It is formed by the union of the distal part of the tarsus with the\n   metatarsus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsorrhaphy', 'n.', 'An operation to diminish the size of the opening\n   between eyelids when enlarged by surrounding cicatrices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsotomy', 'n.', 'The operation of cutting or removing the tarsal\n   cartilages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsi', 'pl. ', 'of Tarsus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsus', 'n.', 'The ankle; the bones or cartilages of the part of the foot\n   between the metatarsus and the leg, consisting in man of seven short\n   bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsus', 'n.', 'A plate of dense connective tissue or cartilage in the\n   eyelid of man and many animals; -- called also tarsal cartilage, and\n   tarsal plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarsus', 'n.', 'The foot of an insect or a crustacean. It usually consists\n   of form two to five joints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tart', 'v. t.', 'Sharp to the taste; acid; sour; as, a tart apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tart', 'v. t.', 'Fig.: Sharp; keen; severe; as, a tart reply; tart\n   language; a tart rebuke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tart', 'n.', 'A species of small open pie, or piece of pastry, containing\n   jelly or conserve; a sort of fruit pie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartan', 'n.', 'Woolen cloth, checkered or crossbarred with narrow bands of\n   various colors, much worn in the Highlands of Scotland; hence, any\n   pattern of tartan; also, other material of a similar pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartan', 'n.', 'A small coasting vessel, used in the Mediterranean, having\n   one mast carrying large leteen sail, and a bowsprit with staysail or\n   jib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartar', 'n.', 'A reddish crust or sediment in wine casks, consisting\n   essentially of crude cream of tartar, and used in marking pure cream of\n   tartar, tartaric acid, potassium carbonate, black flux, etc., and, in\n   dyeing, as a mordant for woolen goods; -- called also argol, wine\n   stone, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartar', 'n.', 'A correction which often incrusts the teeth, consisting of\n   salivary mucus, animal matter, and phosphate of lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartar', 'n.', 'A native or inhabitant of Tartary in Asia; a member of any\n   one of numerous tribes, chiefly Moslem, of Turkish origin, inhabiting\n   the Russian Europe; -- written also, more correctly but less usually,\n   Tatar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartar', 'n.', 'A person of a keen, irritable temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartar', 'a.', 'Of or pertaining to Tartary in Asia, or the Tartars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartar', 'n.', 'See Tartarus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartarated', 'a.', 'Tartrated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartarean', 'a.', 'Alt. of Tartareous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartareous', 'a.', 'Of or pertaining to Tartarus; hellish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartareous', 'a.', 'Consisting of tartar; of the nature of tartar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartareous', 'a.', 'Having the surface rough and crumbling; as, many\n   lichens are tartareous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartarian', 'a.', 'Alt. of Tartaric');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartaric', 'a.', 'Of or pertaining to Tartary in Asia, or the Tartars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartarian', 'n.', 'The name of some kinds of cherries, as the Black\n   Tartarian, or the White Tartarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartaric', 'a.', 'Of or pertaining to tartar; derived from, or resembling,\n   tartar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartarine', 'n.', 'Potassium carbonate, obtained by the incineration of\n   tartar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartarized', 'imp. & p. p.', 'of Tartarize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartarizing', 'p. pr. & vb. n.', 'of Tartarize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartarize', 'v. t.', 'To impregnate with, or subject to the action of,\n   tartar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartarize', 'v. t.', 'To cause to resemble the Tartars and their\n   civilization, as by conquest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartarous', 'a.', 'Containing tartar; consisting of tartar, or partaking of\n   its qualities; tartareous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartarous', 'a.', 'Resembling, or characteristic of, a Tartar; ill-natured;\n   irritable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartarum', 'n.', 'See 1st Tartar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartarus', 'n.', 'The infernal regions, described in the Iliad as situated\n   as far below Hades as heaven is above the earth, and by later writers\n   as the place of punishment for the spirits of the wicked. By the later\n   poets, also, the name is often used synonymously with Hades, or the\n   Lower World in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartary', 'n.', 'Tartarus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartinis tones', '', 'See the Note under Tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartish', 'a.', 'Somewhat tart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartlet', 'n.', 'A small tart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartly', 'adv.', 'In a tart manner; with acidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartness', 'n.', 'The quality or state of being tart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartralic', 'a.', 'Pertaining to, or designating, an acid obtained as a\n   white amorphous deliquescent substance, C8H10O11; -- called also\n   ditartaric, tartrilic, or tartrylic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartramate', 'n.', 'A salt of tartramic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartramic', 'a.', 'Of, pertaining to, or designating, an acid which is the\n   primary acid amide derivative of tartaric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartramide', 'n.', 'An acid amide derivative of tartaric acid, obtained as\n   a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartrate', 'n.', 'A salt of tartaric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartrated', 'a.', 'Containing, or derived from, tartar; combined with\n   tartaric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartrazine', 'n.', 'An artificial dyestuff obtained as an orange-yellow\n   powder, and regarded as a phenyl hydrazine derivative of tartaric and\n   sulphonic acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartrelic', 'a.', 'Of, pertaining to, or designating, an anhydride, C4H4O5,\n   of tartaric acid, obtained as a white crystalline deliquescent\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartro-', '', 'A combining form (also used adjectively) used in chemistry\n   to denote the presence of tartar or of some of its compounds or\n   derivatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartronate', 'n.', 'A salt of tartronic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartronic', 'a.', 'Of, pertaining to, or designating, an organic acid\n   (called also hydroxy malonic acid) obtained, by reducing mesoxalic\n   acid, as a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartronyl', 'n.', 'A hypothetical radical constituting the characteristic\n   residue of tartronic acid and certain of its derivatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartrovinic', 'a.', 'Of, pertaining to, or designating, a certain acid\n   composed of tartaric acid in combination with ethyl, and now called\n   ethyltartaric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartuffe', 'n.', 'Alt. of Tartufe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartufe', 'n.', 'A hypocritical devotee. See the Dictionary of Noted Names\n   in Fiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartuffish', 'a.', 'Alt. of Tartufish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tartufish', 'a.', 'Like a tartuffe; precise; hypocritical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tarweed', 'n.', 'A name given to several resinous-glandular composite\n   plants of California, esp. to the species of Grindelia, Hemizonia, and\n   Madia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tas', 'n.', 'A heap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tas', 'v. t.', 'To tassel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasco', 'n.', 'A kind of clay for making melting pots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasimer', 'n.', 'An instrument for detecting or measuring minute extension\n   or movements of solid bodies. It consists essentially of a small rod,\n   disk, or button of carbon, forming part of an electrical circuit, the\n   resistance of which, being varied by the changes of pressure produced\n   by the movements of the object to be measured, causes variations in the\n   strength of the current, which variations are indicated by a sensitive\n   galvanometer. It is also used for measuring minute changes of\n   temperature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Task', 'v.', 'Labor or study imposed by another, often in a definite\n   quantity or amount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Task', 'v.', 'Business; employment; undertaking; labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasked', 'imp. & p. p.', 'of Task');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasking', 'p. pr. & vb. n.', 'of Task');
  INSERT INTO words (word, word_type, description) VALUES  (    'Task', 'v. t.', 'To impose a task upon; to assign a definite amount of\n   business, labor, or duty to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Task', 'v. t.', 'To oppress with severe or excessive burdens; to tax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Task', 'v. t.', 'To charge; to tax; as with a fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasker', 'n.', 'One who imposes a task.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasker', 'n.', 'One who performs a task, as a day-laborer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasker', 'n.', 'A laborer who receives his wages in kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taskmaster', 'n.', 'One who imposes a task, or burdens another with labor;\n   one whose duty is to assign tasks; an overseer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taskwork', 'n.', 'Work done as a task; also, work done by the job;\n   piecework.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taslet', 'n.', 'A piece of armor formerly worn to guard the things; a\n   tasse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasmanian', 'a.', 'Of or pertaining to Tasmania, or Van Diemens Land. --\n   n. A native or inhabitant of Tasmania; specifically (Ethnol.), in the\n   plural, the race of men that formerly inhabited Tasmania, but is now\n   extinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasse', 'n.', 'A piece of armor for the thighs, forming an appendage to the\n   ancient corselet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tassel', 'n.', 'A male hawk. See Tercel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tassel', 'n.', 'A kind of bur used in dressing cloth; a teasel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tassel', 'n.', 'A pendent ornament, attached to the corners of cushions, to\n   curtains, and the like, ending in a tuft of loose threads or cords.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tassel', 'n.', 'The flower or head of some plants, esp. when pendent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tassel', 'n.', 'A narrow silk ribbon, or the like, sewed to a book to be\n   put between the leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tassel', 'n.', 'A piece of board that is laid upon a wall as a sort of\n   plate, to give a level surface to the ends of floor timbers; -- rarely\n   used in the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasseled', 'imp. & p. p.', 'of Tassel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasselled', '', 'of Tassel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasseling', 'p. pr. & vb. n.', 'of Tassel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasselling', '', 'of Tassel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tassel', 'v. i.', 'To put forth a tassel or flower; as, maize tassels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tassel', 'v. t.', 'To adorn with tassels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasset', 'n.', 'A defense for the front of the thigh, consisting of one or\n   more iron plates hanging from the belt on the lower edge of the\n   corselet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tastable', 'a.', 'Capable of worthy of being tasted; savory; relishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasted', 'imp. & p. p.', 'of Taste');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasting', 'p. pr. & vb. n.', 'of Taste');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taste', 'v. t.', 'To try by the touch; to handle; as, to taste a bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taste', 'v. t.', 'To try by the touch of the tongue; to perceive the relish\n   or flavor of (anything) by taking a small quantity into a mouth. Also\n   used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taste', 'v. t.', 'To try by eating a little; to eat a small quantity of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taste', 'v. t.', 'To become acquainted with by actual trial; to essay; to\n   experience; to undergo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taste', 'v. t.', 'To partake of; to participate in; -- usually with an\n   implied sense of relish or pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taste', 'v. i.', 'To try food with the mouth; to eat or drink a little\n   only; to try the flavor of anything; as, to taste of each kind of wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taste', 'v. i.', 'To have a smack; to excite a particular sensation, by\n   which the specific quality or flavor is distinguished; to have a\n   particular quality or character; as, this water tastes brackish; the\n   milk tastes of garlic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taste', 'v. i.', 'To take sparingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taste', 'v. i.', 'To have perception, experience, or enjoyment; to partake;\n   as, to taste of natures bounty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taste', 'n.', 'The act of tasting; gustation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taste', 'n.', 'A particular sensation excited by the application of a\n   substance to the tongue; the quality or savor of any substance as\n   perceived by means of the tongue; flavor; as, the taste of an orange or\n   an apple; a bitter taste; an acid taste; a sweet taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taste', 'n.', 'The one of the five senses by which certain properties of\n   bodies (called their taste, savor, flavor) are ascertained by contact\n   with the organs of taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taste', 'n.', 'Intellectual relish; liking; fondness; -- formerly with of,\n   now with for; as, he had no taste for study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taste', 'n.', 'The power of perceiving and relishing excellence in human\n   performances; the faculty of discerning beauty, order, congruity,\n   proportion, symmetry, or whatever constitutes excellence, particularly\n   in the fine arts and belles-letters; critical judgment; discernment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taste', 'n.', 'Manner, with respect to what is pleasing, refined, or in\n   accordance with good usage; style; as, music composed in good taste; an\n   epitaph in bad taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taste', 'n.', 'Essay; trial; experience; experiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taste', 'n.', 'A small portion given as a specimen; a little piece tastted\n   of eaten; a bit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taste', 'n.', 'A kind of narrow and thin silk ribbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasteful', 'a.', 'Having a high relish; savory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasteful', 'a.', 'Having or exhibiting good taste; in accordance with good\n   taste; tasty; as, a tasteful drapery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasteless', 'a.', 'Having no taste; insipid; flat; as, tasteless fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasteless', 'a.', 'Destitute of the sense of taste; or of good taste; as, a\n   tasteless age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasteless', 'a.', 'Not in accordance with good taste; as, a tasteless\n   arrangement of drapery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taster', 'n.', 'One who tastes; especially, one who first tastes food or\n   drink to ascertain its quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taster', 'n.', 'That in which, or by which, anything is tasted, as, a dram\n   cup, a cheese taster, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taster', 'n.', 'One of a peculiar kind of zooids situated on the polyp-stem\n   of certain Siphonophora. They somewhat resemble the feeding zooids, but\n   are destitute of mouths. See Siphonophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tastily', 'adv.', 'In a tasty manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasting', 'n.', 'The act of perceiving or tasting by the organs of taste;\n   the faculty or sense by which we perceive or distinguish savors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasto', 'n.', 'A key or thing touched to produce a tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasty', 'superl.', 'Having a good taste; -- applied to persons; as, a tasty\n   woman. See Taste, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tasty', 'n.', 'Being in conformity to the principles of good taste;\n   elegant; as, tasty furniture; a tasty dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tat', 'n.', 'Gunny cloth made from the fiber of the Corchorus olitorius, or\n   jute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tat', 'n.', 'A pony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tataupa', 'n.', 'A South American tinamou (Crypturus tataupa).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tatch', 'n.', 'A spot or stain; also, a trick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tath', 'obs.', '3d pers. sing. pres. of Ta, to take.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tath', 'n.', 'Dung, or droppings of cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tath', 'n.', 'The luxuriant grass growing about the droppings of cattle in\n   a pasture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tath', 'v. t.', 'To manure (land) by pasturing cattle on it, or causing\n   them to lie upon it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tatou', 'n.', 'The giant armadillo (Priodontes gigas) of tropical South\n   America. It becomes nearly five feet long including the tail. It is\n   noted for its burrowing powers, feeds largely upon dead animals, and\n   sometimes invades human graves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tatouay', 'n.', 'An armadillo (Xenurus unicinctus), native of the tropical\n   parts of South America. It has about thirteen movable bands composed of\n   small, nearly square, scales. The head is long; the tail is round and\n   tapered, and nearly destitute of scales; the claws of the fore feet are\n   very large. Called also tatouary, and broad-banded armadillo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tatouhou', 'n.', 'The peba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tatt', 'v. t. & i.', 'To make (anything) by tatting; to work at tatting;\n   as, tatted edging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tatta', 'n.', 'A bamboo frame or trellis hung at a door or window of a\n   house, over which water is suffered to trickle, in order to moisten and\n   cool the air as it enters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tatter', 'n.', 'One who makes tatting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tatter', 'n.', 'A rag, or a part torn and hanging; -- chiefly used in the\n   plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tattered', 'p. p.', 'of Tatter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tatter', 'v. t.', 'To rend or tear into rags; -- used chiefly in the past\n   participle as an adjective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tatterdemalion', 'n.', 'A ragged fellow; a ragamuffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tatting', 'n.', 'A kind of lace made from common sewing thread, with a\n   peculiar stitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tattled', 'imp. & p. p.', 'of Tattle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tattling', 'p. pr. & vb. n.', 'of Tattle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tattle', 'v. i.', 'To prate; to talk idly; to use many words with little\n   meaning; to chat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tattle', 'v. i.', 'To tell tales; to communicate secrets; to be a\n   talebearer; as, a tattling girl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tattle', 'n.', 'Idle talk or chat; trifling talk; prate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tattler', 'n.', 'One who tattles; an idle talker; one who tells tales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tattler', 'n.', 'Any one of several species of large, long-legged\n   sandpipers belonging to the genus Totanus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tattlery', 'n.', 'Idle talk or chat; tittle-tattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tattling', 'a.', 'Given to idle talk; apt to tell tales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tattoo', 'n.', 'A beat of drum, or sound of a trumpet or bugle, at night,\n   giving notice to soldiers to retreat, or to repair to their quarters in\n   garrison, or to their tents in camp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tattooed', 'imp. & p. p.', 'of Tattoo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tattooing', 'p. pr. & vb. n.', 'of Tattoo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tattoo', 'v. t.', 'To color, as the flesh, by pricking in coloring matter,\n   so as to form marks or figures which can not be washed out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tattoos', 'pl. ', 'of Tattoo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tattoo', 'n.', 'An indelible mark or figure made by puncturing the skin and\n   introducing some pigment into the punctures; -- a mode of ornamentation\n   practiced by various barbarous races, both in ancient and modern times,\n   and also by some among civilized nations, especially by sailors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tatu', 'n.', 'Same as Tatou.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tatusiid', 'n.', 'Any armadillo of the family Tatusiidae, of which the peba\n   and mule armadillo are examples. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tau', 'n.', 'The common American toadfish; -- so called from a marking\n   resembling the Greek letter tau (/).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taught', 'a.', 'See Taut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taught', '', 'imp. & p. p. of Teach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taunt', 'a.', 'Very high or tall; as, a ship with taunt masts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taunted', 'imp. & p. p.', 'of Taunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taunting', 'p. pr. & vb. n.', 'of Taunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taunt', 'v. t.', 'To reproach with severe or insulting words; to revile; to\n   upbraid; to jeer at; to flout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taunt', 'n.', 'Upbraiding language; bitter or sarcastic reproach; insulting\n   invective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taunter', 'n.', 'One who taunts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taunting', '', 'a. & n. from Taunt, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tauntingly', 'adv.', 'In a taunting manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tauntress', 'n.', 'A woman who taunts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taur', 'n.', 'The constellation Taurus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tauricornous', 'a.', 'Having horns like those of a bull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tauridor', 'n.', 'A bullfighter; a toreador.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tauriform', 'a.', 'Having the form of a bull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taurine', 'a.', 'Of or pertaining to the genus Taurus, or cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taurine', 'n.', 'A body occurring in small quantity in the juices of\n   muscle, in the lungs, and elsewhere, but especially in the bile, where\n   it is found as a component part of taurocholic acid, from which it can\n   be prepared by decomposition of the acid. It crystallizes in colorless,\n   regular six-sided prisms, and is especially characterized by containing\n   both nitrogen and sulphur, being chemically amido-isethionic acid,\n   C2H7NSO3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taurocholate', 'n.', 'A salt of taurocholic acid; as, sodium taurocholate,\n   which occurs in human bile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taurocholic', 'a.', 'Pertaining to, or designating, a conjugate acid\n   (called taurocholic acid) composed of taurine and cholic acid, present\n   abundantly in human bile and in that of carnivora. It is exceedingly\n   deliquescent, and hence appears generally as a thick, gummy mass,\n   easily soluble in water and alcohol. It has a bitter taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taurocol', 'n.', 'Alt. of Taurocolla');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taurocolla', 'n.', 'Glue made from a bulls hide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tauromachian', 'a.', 'Of or pertaining to bullfights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tauromachian', 'n.', 'A bullfighter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tauromachy', 'n.', 'Bullfighting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taurus', 'n.', 'The Bull; the second in order of the twelve signs of the\n   zodiac, which the sun enters about the 20th of April; -- marked thus\n   [/] in almanacs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taurus', 'n.', 'A zodiacal constellation, containing the well-known\n   clusters called the Pleiades and the Hyades, in the latter of which is\n   situated the remarkably bright Aldebaran.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taurus', 'n.', 'A genus of ruminants comprising the common domestic cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taurylic', 'a.', 'Pertaining to, or designating, an acid found of a urine\n   of neat cattle, and probably identical with cresol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taut', 'a.', 'Tight; stretched; not slack; -- said esp. of a rope that is\n   tightly strained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taut', 'a.', 'Snug; close; firm; secure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tautegorical', 'a.', 'Expressing the same thing with different words; --\n   opposed to allegorical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tautochrone', 'n.', 'A curved line, such that a heavy body, descending\n   along it by the action of gravity, will always arrive at the lowest\n   point in the same time, wherever in the curve it may begin to fall; as,\n   an inverted cycloid with its base horizontal is a tautochrone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tautochronous', 'a.', 'Occupying the same time; pertaining to, or having\n   the properties of, a tautochrone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tautog', 'n.', 'An edible labroid fish (Haitula onitis, or Tautoga onitis)\n   of the Atlantic coast of the United States. When adult it is nearly\n   black, more or less irregularly barred, with greenish gray. Called also\n   blackfish, oyster fish, salt-water chub, and moll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tautologic', 'a.', 'Tautological.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tautological', 'a.', 'Involving tautology; having the same signification;\n   as, tautological expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tautologist', 'n.', 'One who uses tautological words or phrases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tautologized', 'imp. & p. p.', 'of Tautologize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tautologizing', 'p. pr. & vb. n.', 'of Tautologize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tautologize', 'v. i.', 'To repeat the same thing in different words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tautologous', 'a.', 'Repeating the same thing in different words;\n   tautological.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tautology', 'n.', 'A repetition of the same meaning in different words;\n   needless repetition of an idea in different words or phrases; a\n   representation of anything as the cause, condition, or consequence of\n   itself, as in the following lines: --//The dawn is overcast, the\n   morning lowers,/And heavily in clouds brings on the day. Addison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tautomeric', 'a.', 'Relating to, or characterized by, tautomerism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tautomerism', 'n.', 'The condition, quality, or relation of metameric\n   substances, or their respective derivatives, which are more or less\n   interchangeable, according as one form or the other is the more stable.\n   It is a special case of metamerism; thus, the lactam and the lactim\n   compounds exhibit tautomerism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tautoousian', 'a.', 'Alt. of Tautoousious');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tautoousious', 'a.', 'Having the same essence; being identically of the\n   same nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tautophonical', 'a.', 'Pertaining to, or characterized by, tautophony;\n   repeating the same sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tautophony', 'n.', 'Repetition of the same sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tautozonal', 'a.', 'Belonging to the same zone; as, tautozonal planes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tavern', 'n.', 'A public house where travelers and other transient guests\n   are accomodated with rooms and meals; an inn; a hotel; especially, in\n   modern times, a public house licensed to sell liquor in small\n   quantities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taverner', 'n.', 'One who keeps a tavern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taverning', 'n.', 'A feasting at taverns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tavernmen', 'pl. ', 'of Tavernman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tavernman', 'n.', 'The keeper of a tavern; also, a tippler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taw', 'n.', 'Tow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taw', 'v. t.', 'To push; to tug; to tow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tawed', 'imp. & p. p.', 'of Taw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tawing', 'p. pr. & vb. n.', 'of Taw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taw', 'v. t.', 'To prepare or dress, as hemp, by beating; to tew; hence, to\n   beat; to scourge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taw', 'v. t.', 'To dress and prepare, as the skins of sheep, lambs, goats,\n   and kids, for gloves, and the like, by imbuing them with alum, salt,\n   and other agents, for softening and bleaching them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taw', 'n.', 'A large marble to be played with; also, a game at marbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taw', 'n.', 'A line or mark from which the players begin a game of marbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tawdrily', 'adv.', 'In a tawdry manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tawdriness', 'n.', 'Quality or state of being tawdry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tawdry', 'superl.', 'Bought at the festival of St. Audrey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tawdry', 'superl.', 'Very fine and showy in colors, without taste or\n   elegance; having an excess of showy ornaments without grace; cheap and\n   gaudy; as, a tawdry dress; tawdry feathers; tawdry colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tawdries', 'pl. ', 'of Tawdry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tawdry', 'n.', 'A necklace of a rural fashion, bought at St. Audreys fair;\n   hence, a necklace in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tawer', 'n.', 'One who taws; a dresser of white leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tawery', 'n.', 'A place where skins are tawed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tawniness', 'n.', 'The quality or state of being tawny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tawny', 'n.', 'Of a dull yellowish brown color, like things tanned, or\n   persons who are sunburnt; as, tawny Moor or Spaniard; the tawny lion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taws', 'n.', 'A leather lash, or other instrument of punishment, used by a\n   schoolmaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tax', 'n.', 'A charge, especially a pecuniary burden which is imposed by\n   authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tax', 'n.', 'A charge or burden laid upon persons or property for the\n   support of a government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tax', 'n.', 'Especially, the sum laid upon specific things, as upon polls,\n   lands, houses, income, etc.; as, a land tax; a window tax; a tax on\n   carriages, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tax', 'n.', 'A sum imposed or levied upon the members of a society to\n   defray its expenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tax', 'n.', 'A task exacted from one who is under control; a contribution\n   or service, the rendering of which is imposed upon a subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tax', 'n.', 'A disagreeable or burdensome duty or charge; as, a heavy tax\n   on time or health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tax', 'n.', 'Charge; censure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tax', 'n.', 'A lesson to be learned; a task.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxed', 'imp. & p. p.', 'of Tax');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxing', 'p. pr. & vb. n.', 'of Tax');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tax', 'n.', 'To subject to the payment of a tax or taxes; to impose a tax\n   upon; to lay a burden upon; especially, to exact money from for the\n   support of government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tax', 'n.', 'To assess, fix, or determine judicially, the amount of; as, to\n   tax the cost of an action in court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tax', 'n.', 'To charge; to accuse; also, to censure; -- often followed by\n   with, rarely by of before an indirect object; as, to tax a man with\n   pride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxability', 'n.', 'The quality or state of being taxable; taxableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxable', 'a.', 'Capable of being taxed; liable by law to the assessment of\n   taxes; as, taxable estate; taxable commodities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxable', 'a.', 'That may be legally charged by a court against the\n   plaintiff of defendant in a suit; as, taxable costs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxaspidean', 'a.', 'Having the posterior tarsal scales, or scutella,\n   rectangular and arranged in regular rows; -- said of certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxation', 'n.', 'The act of laying a tax, or of imposing taxes, as on the\n   subjects of a state, by government, or on the members of a corporation\n   or company, by the proper authority; the raising of revenue; also, a\n   system of raising revenue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxation', 'n.', 'The act of taxing, or assessing a bill of cost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxation', 'n.', 'Tax; sum imposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxation', 'n.', 'Charge; accusation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxel', 'n.', 'The American badger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxeopoda', 'n. pl.', 'An order of extinct Mammalia found in the Tertiary\n   formations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxer', 'n.', 'One who taxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxer', 'n.', 'One of two officers chosen yearly to regulate the assize of\n   bread, and to see the true gauge of weights and measures is observed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxgatherer', 'n.', 'One who collects taxes or revenues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxiarch', 'n.', 'An Athenian military officer commanding a certain\n   division of an army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxicorn', 'n.', 'One of a family of beetles (Taxicornes) whose antennae\n   are largest at the tip. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxidermic', 'a.', 'Of or pertaining to the art of preparing and preserving\n   the skins of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxidermist', 'n.', 'A person skilled in taxidermy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxidermy', 'v. t.', 'The art of preparing, preserving, and mounting the\n   skins of animals so as to represent their natural appearance, as for\n   cabinets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxine', 'n.', 'A poisonous alkaloid of bitter taste extracted from the\n   leaves and seeds of the European yew (Taxus baccata). Called also\n   taxia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxis', 'n.', 'Manipulation applied to a hernial tumor, or to an intestinal\n   obstruction, for the purpose of reducing it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxless', 'a.', 'Free from taxation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxology', 'n.', 'Same as Taxonomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxonomic', 'a.', 'Pertaining to, or involving, taxonomy, or the laws and\n   principles of classification; classificatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxonomist', 'n.', 'One skilled in taxonomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxonomy', 'n.', 'That division of the natural sciences which treats of the\n   classification of animals and plants; the laws or principles of\n   classification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxor', 'n.', 'Same as Taxer, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taxpayer', 'n.', 'One who is assessed and pays a tax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tayra', 'n.', 'A South American carnivore (Galera barbara) allied to the\n   grison. The tail is long and thick. The length, including the tail, is\n   about three feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tazel', 'n.', 'The teasel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tazza', 'n.', 'An ornamental cup or vase with a large, flat, shallow bowl,\n   resting on a pedestal and often having handles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tchawytcha', 'n.', 'The quinnat salmon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'T cart', '', 'See under T.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tea', 'n.', 'The prepared leaves of a shrub, or small tree (Thea, /\n   Camellia, Chinensis). The shrub is a native of China, but has been\n   introduced to some extent into some other countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tea', 'n.', 'A decoction or infusion of tea leaves in boiling water; as,\n   tea is a common beverage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tea', 'n.', 'Any infusion or decoction, especially when made of the dried\n   leaves of plants; as, sage tea; chamomile tea; catnip tea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tea', 'n.', 'The evening meal, at which tea is usually served; supper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tea', 'v. i.', 'To take or drink tea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teaberry', 'n.', 'The checkerberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Taught', 'imp. & p. p.', 'of Teach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teaching', 'p. pr. & vb. n.', 'of Teach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teach', 'v. t.', 'To impart the knowledge of; to give intelligence\n   concerning; to impart, as knowledge before unknown, or rules for\n   practice; to inculcate as true or important; to exhibit impressively;\n   as, to teach arithmetic, dancing, music, or the like; to teach morals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teach', 'v. t.', 'To direct, as an instructor; to manage, as a preceptor;\n   to guide the studies of; to instruct; to inform; to conduct through a\n   course of studies; as, to teach a child or a class.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teach', 'v. t.', 'To accustom; to guide; to show; to admonish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teach', 'v. i.', 'To give instruction; to follow the business, or to\n   perform the duties, of a preceptor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teachable', 'a.', 'Capable of being taught; apt to learn; also, willing to\n   receive instruction; docile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teachableness', 'n.', 'Willingness to be taught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teache', 'n.', 'One of the series of boilers in which the cane juice is\n   treated in making sugar; especially, the last boiler of the series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teacher', 'n.', 'One who teaches or instructs; one whose business or\n   occupation is to instruct others; an instructor; a tutor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teacher', 'n.', 'One who instructs others in religion; a preacher; a\n   minister of the gospel; sometimes, one who preaches without regular\n   ordination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teaching', 'n.', 'The act or business of instructing; also, that which is\n   taught; instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teachless', 'a.', 'Not teachable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teacup', 'n.', 'A small cup from which to drink tea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teacupfuls', 'pl. ', 'of Teacupful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teacupful', 'n.', 'As much as a teacup can hold; enough to fill a teacup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tead', 'n.', 'Alt. of Teade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teade', 'n.', 'A torch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teagle', 'n.', 'A hoisting apparatus; an elevator; a crane; a lift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teague', 'n.', 'An Irishman; -- a term used in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teak', 'n.', 'A tree of East Indies (Tectona grandis) which furnishes an\n   extremely strong and durable timber highly valued for shipbuilding and\n   other purposes; also, the timber of the tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teakettle', 'n.', 'A kettle in which water is boiled for making tea,\n   coffee, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teal', 'n.', 'Any one of several species of small fresh-water ducks of the\n   genus Anas and the subgenera Querquedula and Nettion. The male is\n   handsomely colored, and has a bright green or blue speculum on the\n   wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Team', 'n.', 'A group of young animals, especially of young ducks; a brood;\n   a litter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Team', 'n.', 'Hence, a number of animals moving together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Team', 'n.', 'Two or more horses, oxen, or other beasts harnessed to the\n   same vehicle for drawing, as to a coach, wagon, sled, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Team', 'n.', 'A number of persons associated together in any work; a gang;\n   especially, a number of persons selected to contend on one side in a\n   match, or a series of matches, in a cricket, football, rowing, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Team', 'n.', 'A flock of wild ducks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Team', 'n.', 'A royalty or privilege granted by royal charter to a lord of\n   a manor, of having, keeping, and judging in his court, his bondmen,\n   neifes, and villains, and their offspring, or suit, that is, goods and\n   chattels, and appurtenances thereto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Team', 'v. i.', 'To engage in the occupation of driving a team of horses,\n   cattle, or the like, as in conveying or hauling lumber, goods, etc.; to\n   be a teamster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Team', 'v. t.', 'To convey or haul with a team; as, to team lumber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teamed', 'a.', 'Yoked in, or as in, a team.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teaming', 'n.', 'The act or occupation of driving a team, or of hauling or\n   carrying, as logs, goods, or the like, with a team.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teaming', 'n.', 'Contract work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teamster', 'n.', 'One who drives a team.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teamwork', 'n.', 'Work done by a team, as distinguished from that done by\n   personal labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teapot', 'n.', 'A vessel with a spout, in which tea is made, and from which\n   it is poured into teacups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teapoy', 'n.', 'An ornamental stand, usually with three legs, having\n   caddies for holding tea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tear', 'n.', 'A drop of the limpid, saline fluid secreted, normally in\n   small amount, by the lachrymal gland, and diffused between the eye and\n   the eyelids to moisten the parts and facilitate their motion.\n   Ordinarily the secretion passes through the lachrymal duct into the\n   nose, but when it is increased by emotion or other causes, it overflows\n   the lids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tear', 'n.', 'Something in the form of a transparent drop of fluid matter;\n   also, a solid, transparent, tear-shaped drop, as of some balsams or\n   resins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tear', 'n.', 'That which causes or accompanies tears; a lament; a dirge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tore', 'imp.', 'of Tear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tare', '', 'of Tear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torn', 'p. p.', 'of Tear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tearing', 'p. pr. & vb. n.', 'of Tear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tear', 'v. t.', 'To separate by violence; to pull apart by force; to rend;\n   to lacerate; as, to tear cloth; to tear a garment; to tear the skin or\n   flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tear', 'v. t.', 'Hence, to divide by violent measures; to disrupt; to rend;\n   as, a party or government torn by factions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tear', 'v. t.', 'To rend away; to force away; to remove by force; to\n   sunder; as, a child torn from its home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tear', 'v. t.', 'To pull with violence; as, to tear the hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tear', 'v. t.', 'To move violently; to agitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tear', 'v. i.', 'To divide or separate on being pulled; to be rent; as,\n   this cloth tears easily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tear', 'v. i.', 'To move and act with turbulent violence; to rush with\n   violence; hence, to rage; to rave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tear', 'n.', 'The act of tearing, or the state of being torn; a rent; a\n   fissure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tearer', 'n.', 'One who tears or rends anything; also, one who rages or\n   raves with violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tear-falling', 'a.', 'Shedding tears; tender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tearful', 'a.', 'Abounding with tears; weeping; shedding tears; as, tearful\n   eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tearless', 'a.', 'Shedding no tears; free from tears; unfeeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tearpit', 'n.', 'A cavity or pouch beneath the lower eyelid of most deer\n   and antelope; the lachrymal sinus; larmier. It is capable of being\n   opened at pleasure and secretes a waxy substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tear-thumb', 'n.', 'A name given to several species of plants of the genus\n   Polygonum, having angular stems beset with minute reflexed prickles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teary', 'a.', 'Wet with tears; tearful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teary', 'a.', 'Consisting of tears, or drops like tears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tea-saucer', 'n.', 'A small saucer in which a teacup is set.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teased', 'imp. & p. p.', 'of Tease');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teasing', 'p. pr. & vb. n.', 'of Tease');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tease', 'v. t.', 'To comb or card, as wool or flax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tease', 'v. t.', 'To stratch, as cloth, for the purpose of raising a nap;\n   teasel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tease', 'v. t.', 'To tear or separate into minute shreds, as with needles\n   or similar instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tease', 'v. t.', 'To vex with importunity or impertinence; to harass,\n   annoy, disturb, or irritate by petty requests, or by jests and\n   raillery; to plague.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tease', 'n.', 'One who teases or plagues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teasel', 'n.', 'A plant of the genus Dipsacus, of which one species (D.\n   fullonum) bears a large flower head covered with stiff, prickly, hooked\n   bracts. This flower head, when dried, is used for raising a nap on\n   woolen cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teasel', 'n.', 'A bur of this plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teasel', 'n.', 'Any contrivance intended as a substitute for teasels in\n   dressing cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teaseled', 'imp. & p. p.', 'of Teasel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teaselled', '', 'of Teasel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teaseling', 'p. pr. & vb. n.', 'of Teasel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teaselling', '', 'of Teasel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teasel', 'v. t.', 'To subject, as woolen cloth, to the action of teasels,\n   or any substitute for them which has an effect to raise a nap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teaseler', 'n.', 'One who uses teasels for raising a nap on cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teaseling', 'n.', 'The cutting and gathering of teasels; the use of\n   teasels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teaser', 'n.', 'One who teases or vexes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teaser', 'n.', 'A jager gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teasle', 'n. & v. t.', 'See Teasel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teaspoon', 'n.', 'A small spoon used in stirring and sipping tea, coffee,\n   etc., and for other purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teaspoonfuls', 'pl. ', 'of Teaspoonful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teaspoonful', 'n.', 'As much as teaspoon will hold; enough to fill a\n   teaspoon; -- usually reckoned at a fluid dram or one quarter of a\n   tablespoonful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teat', 'n.', 'The protuberance through which milk is drawn from the udder\n   or breast of a mammal; a nipple; a pap; a mammilla; a dug; a tit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teat', 'n.', 'A small protuberance or nozzle resembling the teat of an\n   animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teated', 'a.', 'Having protuberances resembling the teat of an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teathe', 'n. & v.', 'See Tath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teatish', 'a.', 'Peevish; tettish; fretful; -- said of a child. See\n   Tettish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teaze-hole', 'n.', 'The opening in the furnaces through which fuel is\n   introduced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teazel', 'n. & v. t.', 'See Teasel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teazer', 'n.', 'The stoker or fireman of a furnace, as in glass works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teazle', 'n. & v. t.', 'See Teasel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tebeth', 'n.', 'The tenth month of the Jewish ecclesiastical year,\n   answering to a part of December with a part of January.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Techily', 'adv.', 'In a techy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Techiness', 'n.', 'The quality or state of being techy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technic', 'a.', 'Technical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technic', 'a.', 'The method of performance in any art; technical skill;\n   artistic execution; technique.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technic', 'a.', 'Technical terms or objects; things pertaining to the\n   practice of an art or science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technical', 'a.', 'Of or pertaining to the useful or mechanic arts, or to\n   any science, business, or the like; specially appropriate to any art,\n   science, or business; as, the words of an indictment must be technical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technicalities', 'pl. ', 'of Technicality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technicality', 'n.', 'The quality or state of being technical;\n   technicalness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technicality', 'n.', 'That which is technical, or peculiar to any trade,\n   profession, sect, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technically', 'adv.', 'In a technical manner; according to the\n   signification of terms as used in any art, business, or profession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technicalness', 'n.', 'The quality or state of being technical;\n   technicality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technicals', 'n. pl.', 'Those things which pertain to the practical part of\n   an art, science, or profession; technical terms; technics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technicist', 'n.', 'One skilled in technics or in one or more of the\n   practical arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technicological', 'a.', 'Technological; technical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technicology', 'n.', 'Technology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technics', 'n.', 'The doctrine of arts in general; such branches of\n   learning as respect the arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technique', 'n.', 'Same as Technic, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technism', 'n.', 'Technicality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technologic', 'a.', 'Technological.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technological', 'a.', 'Of or pertaining to technology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technologist', 'n.', 'One skilled in technology; one who treats of arts, or\n   of the terms of arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Technology', 'n.', 'Industrial science; the science of systematic knowledge\n   of the industrial arts, especially of the more important manufactures,\n   as spinning, weaving, metallurgy, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Techy', 'a.', 'Peevish; fretful; irritable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tectibranch', 'n.', 'One of the Tectibranchiata. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tectibranchia', 'n. pl.', 'Same as Tectibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tectibranchiata', 'n. pl.', 'An order, or suborder, of gastropod Mollusca\n   in which the gills are usually situated on one side of the back, and\n   protected by a fold of the mantle. When there is a shell, it is usually\n   thin and delicate and often rudimentary. The aplysias and the bubble\n   shells are examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tectibranchiate', 'a.', 'Having the gills covered by the mantle; of or\n   pertaining to the Tectibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tectibranchiate', 'n.', 'A tectibranchiate mollusk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tectly', 'adv.', 'Covertly; privately; secretly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tectology', 'n.', 'A division of morphology created by Haeckel; the science\n   of organic individuality constituting the purely structural portion of\n   morphology, in which the organism is regarded as composed of organic\n   individuals of different orders, each organ being considered an\n   individual. See Promorphology, and Morphon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tectonic', 'a.', 'Of or pertaining to building or construction;\n   architectural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tectonics', 'n.', 'The science, or the art, by which implements, vessels,\n   dwellings, or other edifices, are constructed, both agreeably to the\n   end for which they are designed, and in conformity with artistic\n   sentiments and ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tectorial', 'a.', 'Of or pertaining to covering; -- applied to a membrane\n   immediately over the organ of Corti in the internal ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tectrices', 'n. pl.', 'The wing coverts of a bird. See Covert, and Illust.\n   of Bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tecum', 'n.', 'See Tucum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tedded', 'imp. & p. p.', 'of Ted');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tedding', 'p. pr. & vb. n.', 'of Ted');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ted', 'v. t.', 'To spread, or turn from the swath, and scatter for drying,\n   as new-mowed grass; -- chiefly used in the past participle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tedder', 'n.', 'A machine for stirring and spreading hay, to expedite its\n   drying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tedder', 'n.', 'Same as Tether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teddered', 'imp. & p. p.', 'of Tedder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teddering', 'p. pr. & vb. n.', 'of Tedder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tedder', 'v. t.', 'Same as Tether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Te Deum', '', 'An ancient and celebrated Christian hymn, of uncertain\n   authorship, but often ascribed to St. Ambrose; -- so called from the\n   first words \"Te Deum laudamus.\" It forms part of the daily matins of\n   the Roman Catholic breviary, and is sung on all occasions of\n   thanksgiving. In its English form, commencing with words, \"We praise\n   thee, O God,\" it forms a part of the regular morning service of the\n   Church of England and the Protestant Episcopal Church in America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Te Deum', '', 'A religious service in which the singing of the hymn forms a\n   principal part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tedge', 'n.', 'The gate of a mold, through which the melted metal is\n   poured; runner, geat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tediosity', 'n.', 'Tediousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tedious', 'a.', 'Involving tedium; tiresome from continuance, prolixity,\n   slowness, or the like; wearisome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tedium', 'n.', 'Irksomeness; wearisomeness; tediousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tee', 'n.', 'The mark aimed at in curling and in quoits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tee', 'n.', 'The nodule of earth from which the ball is struck in golf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tee', 'n.', 'A short piece of pipe having a lateral outlet, used to connect\n   a line of pipe with a pipe at a right angle with the line; -- so called\n   because it resembles the letter T in shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tee iron', '', 'See T iron, under T.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teek', 'n.', 'See Teak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teel', 'n.', 'Sesame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teelseed', 'n.', 'The seed of sesame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teem', 'v. t.', 'To pour; -- commonly followed by out; as, to teem out ale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teem', 'v. t.', 'To pour, as steel, from a melting pot; to fill, as a mold,\n   with molten metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teem', 'a.', 'To think fit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teemed', 'imp. & p. p.', 'of Teem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teeming', 'p. pr. & vb. n.', 'of Teem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teem', 'v. i.', 'To bring forth young, as an animal; to produce fruit, as a\n   plant; to bear; to be pregnant; to conceive; to multiply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teem', 'v. i.', 'To be full, or ready to bring forth; to be stocked to\n   overflowing; to be prolific; to abound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teem', 'v. t.', 'To produce; to bring forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teemer', 'n.', 'One who teems, or brings forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teemful', 'a.', 'Pregnant; prolific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teemful', 'a.', 'Brimful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teeming', 'a.', 'Prolific; productive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teemless', 'a.', 'Not fruitful or prolific; barren; as, a teemless earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teen', 'n.', 'Grief; sorrow; affiction; pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teen', 'n.', 'To excite; to provoke; to vex; to affict; to injure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teen', 'v. t.', 'To hedge or fence in; to inclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teenage', 'n.', 'The longer wood for making or mending fences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teend', 'v. t. & i.', 'To kindle; to burn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teenful', 'a.', 'Full of teen; harmful; grievous; grieving; afflicted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teens', 'n. pl.', 'The years of ones age having the termination -teen,\n   beginning with thirteen and ending with nineteen; as, a girl in her\n   teens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teeny', 'a.', 'Very small; tiny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teeny', 'a.', 'Fretful; peevish; pettish; cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teeong', 'n.', 'The mino bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teest', 'n.', 'A tinsmiths stake, or small anvil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teetan', 'n.', 'A pipit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teetee', 'n.', 'Any one of several species of small, soft-furred South\n   American monkeys belonging to Callithrix, Chrysothrix, and allied\n   genera; as, the collared teetee (Callithrix torquatus), and the\n   squirrel teetee (Chrysothrix sciurea). Called also pinche, titi, and\n   saimiri. See Squirrel monkey, under Squirrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teetee', 'n.', 'A diving petrel of Australia (Halodroma wrinatrix).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teetered', 'imp. & p. p.', 'of Teeter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teetering', 'p. pr. & vb. n.', 'of Teeter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teeter', 'v. i. & t.', 'To move up and down on the ends of a balanced\n   plank, or the like, as children do for sport; to seesaw; to titter; to\n   titter-totter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teeter-tail', 'n.', 'The spotted sandpiper. See the Note under Sandpiper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teeth', 'n.', 'pl. of Tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teethed', 'imp. & p. p.', 'of Teeth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teething', 'p. pr. & vb. n.', 'of Teeth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teeth', 'v. i.', 'To breed, or grow, teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teething', 'n.', 'The process of the first growth of teeth, or the\n   phenomena attending their issue through the gums; dentition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teetotal', 'a.', 'Entire; total.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teetotaler', 'n.', 'One pledged to entire abstinence from all intoxicating\n   drinks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teetotalism', 'n.', 'The principle or practice of entire abstinence, esp.\n   from intoxicating drinks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teetotally', 'adv.', 'Entirely; totally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teetotum', 'n.', 'A childs toy, somewhat resembling a top, and twirled by\n   the fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teetuck', 'n.', 'The rock pipit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teeuck', 'n.', 'The lapwing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teewit', 'n.', 'The pewit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teg', 'n.', 'A sheep in its second year; also, a doe in its second year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tegmina', 'pl. ', 'of Tegmen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tegmen', 'n.', 'A tegument or covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tegmen', 'n.', 'The inner layer of the coating of a seed, usually thin and\n   delicate; the endopleura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tegmen', 'n.', 'One of the elytra of an insect, especially of certain\n   Orthoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tegmen', 'n.', 'Same as Tectrices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tegmental', 'a.', 'Of or pertaining to a tegument or tegmentum; as, the\n   tegmental layer of the epiblast; the tegmental cells of the taste buds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tegmenta', 'pl. ', 'of Tegmentum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tegmentum', 'n.', 'A covering; -- applied especially to the bundles of\n   longitudinal fibers in the upper part of the crura of the cerebrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teguexin', 'n.', 'A large South American lizard (Tejus teguexin). It\n   becomes three or four feet long, and is blackish above, marked with\n   yellowish spots of various sizes. It feeds upon fruits, insects,\n   reptiles, young birds, and birds eggs. The closely allied species\n   Tejus rufescens is called red teguexin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tegulae', 'pl. ', 'of Tegula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tegula', 'n.', 'A small appendage situated above the base of the wings of\n   Hymenoptera and attached to the mesonotum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tegular', 'a.', 'Of or pertaining to a tile; resembling a tile, or arranged\n   like tiles; consisting of tiles; as, a tegular pavement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tegulated', 'a.', 'Composed of small plates, as of horn or metal,\n   overlapping like tiles; -- said of a kind of ancient armor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tegument', 'n.', 'A cover or covering; an integument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tegument', 'n.', 'Especially, the covering of a living body, or of some\n   part or organ of such a body; skin; hide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tegumentary', 'a.', 'Of or pertaining to a tegument or teguments;\n   consisting of teguments; serving as a tegument or covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Te-hee', 'n. & interj.', 'A tittering laugh; a titter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Te-hee', 'v. i.', 'To titter; to laugh derisively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teil', 'n.', 'The lime tree, or linden; -- called also teil tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teind', 'n.', 'A tithe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teine', 'n.', 'See Teyne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teinland', 'n.', 'Land granted by the crown to a thane or lord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teinoscope', 'n.', 'An instrument formed by combining prisms so as to\n   correct the chromatic aberration of the light while linear dimensions\n   of objects seen through the prisms are increased or diminished; --\n   called also prism telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teint', 'n.', 'Tint; color; tinge, See Tint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teinture', 'n.', 'Color; tinge; tincture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tek', 'n.', 'A Siberian ibex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telamones', 'n. pl.', 'Same as Atlantes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telangiectasis', 'n.', 'Dilatation of the capillary vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telangiectasy', 'n.', 'Telangiectasis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telarly', 'adv.', 'In a weblike manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telary', 'a.', 'Of or pertaining to a web; hence, spinning webs; retiary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teledu', 'n.', 'An East Indian carnivore (Mydaus meliceps) allied to the\n   badger, and noted for the very offensive odor that it emits, somewhat\n   resembling that of a skunk. It is a native of the high mountains of\n   Java and Sumatra, and has long, silky fur. Called also stinking badger,\n   and stinkard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telegram', 'n.', 'A message sent by telegraph; a telegraphic dispatch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telegrammic', 'a.', 'Pertaining to, or resembling, a telegram; laconic;\n   concise; brief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telegraph', 'n.', 'An apparatus, or a process, for communicating\n   intelligence rapidly between distant points, especially by means of\n   preconcerted visible or audible signals representing words or ideas, or\n   by means of words and signs, transmitted by electrical action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telegraphed', 'imp. & p. p.', 'of Telegraph');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telegraphing', 'p. pr. & vb. n.', 'of Telegraph');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telegraph', 'v. t.', 'To convey or announce by telegraph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telegrapher', 'n.', 'One who sends telegraphic messages; a telegraphic\n   operator; a telegraphist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telegraphic', 'a.', 'Of or pertaining to the telegraph; made or\n   communicated by a telegraph; as, telegraphic signals; telegraphic art;\n   telegraphic intelligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telegraphical', 'a.', 'Telegraphic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telegraphist', 'n.', 'One skilled in telegraphy; a telegrapher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telegraphy', 'n.', 'The science or art of constructing, or of communicating\n   by means of, telegraphs; as, submarine telegraphy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telemeter', 'n.', 'An instrument used for measuring the distance of an\n   object from an observer; as, a telescope with a micrometer for\n   measuring the apparent diameter of an object whose real dimensions are\n   known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teleocephial', 'n. pl.', 'An extensive order of bony fishes including most\n   of the common market species, as bass, salmon, cod, perch, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teleological', 'a.', 'Of or pertaining to teleology, or the doctrine of\n   design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teleologist', 'n.', 'One versed in teleology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teleology', 'n.', 'The doctrine of the final causes of things');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teleology', 'n.', 'the doctrine of design, which assumes that the phenomena\n   of organic life, particularly those of evolution, are explicable only\n   by purposive causes, and that they in no way admit of a mechanical\n   explanation or one based entirely on biological science; the doctrine\n   of adaptation to purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teleophore', 'n.', 'Same as Gonotheca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teleorganic', 'a.', 'Vital; as, teleorganic functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teleosaur', 'n.', 'Any one of several species of fossil suarians belonging\n   to Teleosaurus and allied genera. These reptiles are related to the\n   crocodiles, but have biconcave vertebrae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teleosaurus', 'n.', 'A genus of extinct crocodilian reptiles of the\n   Jurassic period, having a long and slender snout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teleost', 'n.', 'One of the Teleosti. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teleostean', 'a.', 'Of or pertaining to the teleosts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teleostean', 'n.', 'A teleostean fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teleostei', 'n. pl.', 'A subclass of fishes including all the ordinary bony\n   fishes as distinguished from the ganoids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teleostomi', 'n. pl.', 'An extensive division of fishes including the\n   ordinary fishes (Teleostei) and the ganoids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teleozoic', 'a.', 'Having tissued composed of cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teleozoon', 'n.', 'A metazoan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telepathy', 'n.', 'The sympathetic affection of one mind by the thoughts,\n   feelings, or emotions of another at a distance, without communication\n   through the ordinary channels of sensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telepheme', 'n.', 'A message by a telephone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telephone', 'n.', 'An instrument for reproducing sounds, especially\n   articulate speech, at a distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telephone', 'v. t.', 'To convey or announce by telephone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telephonic', 'a.', 'Conveying sound to a great distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telephonic', 'a.', 'Of or pertaining to the telephone; by the telephone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telephonically', 'adv.', 'By telephonic means or processes; by the use of\n   the telephone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telephony', 'n.', 'The art or process of reproducing sounds at a distance,\n   as with the telephone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telepolariscope', 'n.', 'A polariscope arranged to be attached to a\n   telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telerythin', 'n.', 'A red crystalline compound related to, or produced\n   from, erythrin. So called because regarded as the end of the series of\n   erythrin compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telescope', 'n.', 'An optical instrument used in viewing distant objects,\n   as the heavenly bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telescoped', 'imp. & p. p.', 'of Telescope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telescoping', 'p. pr. & vb. n.', 'of Telescope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telescope', 'a.', 'To slide or pass one within another, after the manner of\n   the sections of a small telescope or spyglass; to come into collision,\n   as railway cars, in such a manner that one runs into another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telescope', 'v. t.', 'To cause to come into collision, so as to telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telescopic', 'a.', 'Alt. of Telescopical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telescopical', 'a.', 'Of or pertaining to a telescope; performed by a\n   telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telescopical', 'a.', 'Seen or discoverable only by a telescope; as,\n   telescopic stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telescopical', 'a.', 'Able to discern objects at a distance; farseeing;\n   far-reaching; as, a telescopic eye; telescopic vision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telescopical', 'a.', 'Having the power of extension by joints sliding one\n   within another, like the tube of a small telescope or a spyglass;\n   especially (Mach.), constructed of concentric tubes, either stationary,\n   as in the telescopic boiler, or movable, as in the telescopic chimney\n   of a war vessel, which may be put out of sight by being lowered\n   endwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telescopically', 'adv.', 'In a telescopical manner; by or with the\n   telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telescopist', 'n.', 'One who uses a telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telescopy', 'n.', 'The art or practice of using or making telescopes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telesm', 'n.', 'A kind of amulet or magical charm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telesmatic', 'a.', 'Alt. of Telesmatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telesmatical', 'a.', 'Of or pertaining to telesms; magical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telespectroscope', 'n.', 'A spectroscope arranged to be attached to a\n   telescope for observation of distant objects, as the sun or stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telestereoscope', 'n.', 'A stereoscope adapted to view distant natural\n   objects or landscapes; a telescopic stereoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telestic', 'a.', 'Tending or relating to a purpose or an end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telestich', 'n.', 'A poem in which the final letters of the lines, taken\n   consequently, make a name. Cf. Acrostic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telethermometer', 'n.', 'An apparatus for determining the temperature of a\n   distant point, as by a thermoelectric circuit or otherwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teleutospore', 'n.', 'The thick-celled winter or resting spore of the rusts\n   (order Uredinales), produced in late summer. See Illust. of Uredospore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telic', 'a.', 'Denoting the final end or purpose, as distinguished from\n   ecbatic. See Ecbatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Told', 'imp. & p. p.', 'of Tell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telling', 'p. pr. & vb. n.', 'of Tell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tell', 'v. t.', 'To mention one by one, or piece by piece; to recount; to\n   enumerate; to reckon; to number; to count; as, to tell money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tell', 'v. t.', 'To utter or recite in detail; to give an account of; to\n   narrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tell', 'v. t.', 'To make known; to publish; to disclose; to divulge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tell', 'v. t.', 'To give instruction to; to make report to; to acquaint; to\n   teach; to inform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tell', 'v. t.', 'To order; to request; to command.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tell', 'v. t.', 'To discern so as to report; to ascertain by observing; to\n   find out; to discover; as, I can not tell where one color ends and the\n   other begins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tell', 'v. t.', 'To make account of; to regard; to reckon; to value; to\n   estimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tell', 'v. i.', 'To give an account; to make report.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tell', 'v. i.', 'To take effect; to produce a marked effect; as, every shot\n   tells; every expression tells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tell', 'n.', 'That which is told; tale; account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tell', 'n.', 'A hill or mound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tellable', 'a.', 'Capable of being told.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tellen', 'n.', 'Any species of Tellina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teller', 'n.', 'One who tells, relates, or communicates; an informer,\n   narrator, or describer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teller', 'n.', 'One of four officers of the English Exchequer, formerly\n   appointed to receive moneys due to the king and to pay moneys payable\n   by the king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teller', 'n.', 'An officer of a bank who receives and counts over money\n   paid in, and pays money out on checks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teller', 'n.', 'One who is appointed to count the votes given in a\n   legislative body, public meeting, assembly, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tellership', 'n.', 'The office or employment of a teller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tellina', 'n.', 'A genus of marine bivalve mollusks having thin, delicate,\n   and often handsomely colored shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telling', 'a.', 'Operating with great effect; effective; as, a telling\n   speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telltale', 'a.', 'Telling tales; babbling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telltale', 'n.', 'One who officiously communicates information of the\n   private concerns of others; one who tells that which prudence should\n   suppress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telltale', 'n.', 'A movable piece of ivory, lead, or other material,\n   connected with the bellows of an organ, that gives notice, by its\n   position, when the wind is exhausted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telltale', 'n.', 'A mechanical attachment to the steering wheel, which, in\n   the absence of a tiller, shows the position of the helm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telltale', 'n.', 'A compass in the cabin of a vessel, usually placed where\n   the captain can see it at all hours, and thus inform himself of the\n   vessels course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telltale', 'n.', 'A machine or contrivance for indicating or recording\n   something, particularly for keeping a check upon employees, as factory\n   hands, watchmen, drivers, check takers, and the like, by revealing to\n   their employers what they have done or omitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telltale', 'n.', 'The tattler. See Tattler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tellural', 'a.', 'Of or pertaining to the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tellurate', 'n.', 'A salt of telluric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telluret', 'n.', 'A telluride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tellureted', 'n.', 'Combined or impregnated with tellurium; tellurized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tellurhydric', 'a.', 'Of, pertaining to, or designating, hydrogen\n   telluride, which is regarded as an acid, especially when in solution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tellurian', 'a.', 'Of or pertaining to the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tellurian', 'n.', 'A dweller on the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tellurian', 'n.', 'An instrument for showing the operation of the causes\n   which produce the succession of day and night, and the changes of the\n   seasons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telluric', 'a.', 'Of or pertaining to the earth; proceeding from the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telluric', 'a.', 'Of or pertaining to tellurium; derived from, or\n   resembling, tellurium; specifically, designating those compounds in\n   which the element has a higher valence as contrasted with tellurous\n   compounds; as, telluric acid, which is analogous to sulphuric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telluride', 'n.', 'A compound of tellurium with a more positive element or\n   radical; -- formerly called telluret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tellurism', 'n.', 'An hypothesis of animal magnetism propounded by Dr.\n   Keiser, in Germany, in which the phenomena are ascribed to the agency\n   of a telluric spirit or influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tellurite', 'n.', 'A salt of tellurous acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tellurite', 'n.', 'Oxide of tellurium. It occurs sparingly in tufts of\n   white or yellowish crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tellurium', 'n.', 'A rare nonmetallic element, analogous to sulphur and\n   selenium, occasionally found native as a substance of a silver-white\n   metallic luster, but usually combined with metals, as with gold and\n   silver in the mineral sylvanite, with mercury in Coloradoite, etc.\n   Symbol Te. Atomic weight 125.2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tellurize', 'v. t.', 'To impregnate with, or to subject to the action of,\n   tellurium; -- chiefly used adjectively in the past participle; as,\n   tellurized ores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tellurous', 'a.', 'Of or pertaining to tellurium; derived from, or\n   containing, tellurium; specifically, designating those compounds in\n   which the element has a lower valence as contrasted with telluric\n   compounds; as, tellurous acid, which is analogous to sulphurous acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telodynamic', 'a.', 'Relating to a system for transmitting power to a\n   distance by means of swiftly moving ropes or cables driving grooved\n   pulleys of large diameter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teloogoo', 'n.', 'See Telugu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telotrochae', 'pl. ', 'of Telotrocha');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telotrocha', 'n.', 'An annelid larva having telotrochal bands of cilia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telotrochal', 'a.', 'Alt. of Telotrochous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telotrochous', 'a.', 'Having both a preoral and a posterior band of cilla;\n   -- applied to the larvae of certain annelids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telotype', 'n.', 'An electric telegraph which prints the messages in\n   letters and not in signs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telpher', 'n.', 'A contrivance for the conveyance of vehicles or loads by\n   means of electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telpherage', 'n.', 'The conveyance of vehicles or loads by means of\n   electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telsons', 'pl. ', 'of Telson');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telson', 'n.', 'The terminal joint or movable piece at the end of the\n   abdomen of Crustacea and other articulates. See Thoracostraca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telugu', 'n.', 'A Darvidian language spoken in the northern parts of the\n   Madras presidency. In extent of use it is the next language after\n   Hindustani (in its various forms) and Bengali.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telugu', 'n.', 'One of the people speaking the Telugu language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Telugu', 'a.', 'Of or pertaining to the Telugu language, or the Telugus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temerarious', 'a.', 'Unreasonably adventurous; despising danger; rash;\n   headstrong; audacious; reckless; heedless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temeration', 'n.', 'Temerity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temerity', 'n.', 'Unreasonable contempt of danger; extreme venturesomeness;\n   rashness; as, the temerity of a commander in war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temerous', 'a.', 'Temerarious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempean', 'a.', 'Of or pertaining to Temple, a valley in Thessaly,\n   celebrated by Greek poets on account of its beautiful scenery;\n   resembling Temple; hence, beautiful; delightful; charming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempered', 'imp. & p. p.', 'of Temper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempering', 'p. pr. & vb. n.', 'of Temper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temper', 'v. t.', 'To mingle in due proportion; to prepare by combining; to\n   modify, as by adding some new element; to qualify, as by an ingredient;\n   hence, to soften; to mollify; to assuage; to soothe; to calm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temper', 'v. t.', 'To fit together; to adjust; to accomodate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temper', 'v. t.', 'To bring to a proper degree of hardness; as, to temper\n   iron or steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temper', 'v. t.', 'To govern; to manage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temper', 'v. t.', 'To moisten to a proper consistency and stir thoroughly,\n   as clay for making brick, loam for molding, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temper', 'v. t.', 'To adjust, as the mathematical scale to the actual\n   scale, or to that in actual use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temper', 'n.', 'The state of any compound substance which results from the\n   mixture of various ingredients; due mixture of different qualities;\n   just combination; as, the temper of mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temper', 'n.', 'Constitution of body; temperament; in old writers, the\n   mixture or relative proportion of the four humors, blood, choler,\n   phlegm, and melancholy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temper', 'n.', 'Disposition of mind; the constitution of the mind,\n   particularly with regard to the passions and affections; as, a calm\n   temper; a hasty temper; a fretful temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temper', 'n.', 'Calmness of mind; moderation; equanimity; composure; as, to\n   keep ones temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temper', 'n.', 'Heat of mind or passion; irritation; proneness to anger; --\n   in a reproachful sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temper', 'n.', 'The state of a metal or other substance, especially as to\n   its hardness, produced by some process of heating or cooling; as, the\n   temper of iron or steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temper', 'n.', 'Middle state or course; mean; medium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temper', 'n.', 'Milk of lime, or other substance, employed in the process\n   formerly used to clarify sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temper', 'v. i.', 'To accord; to agree; to act and think in conformity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temper', 'v. i.', 'To have or get a proper or desired state or quality; to\n   grow soft and pliable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempera', 'n.', 'A mode or process of painting; distemper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperable', 'a.', 'Capable of being tempered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperament', 'v. t.', 'Internal constitution; state with respect to the\n   relative proportion of different qualities, or constituent parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperament', 'v. t.', 'Due mixture of qualities; a condition brought about\n   by mutual compromises or concessions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperament', 'v. t.', 'The act of tempering or modifying; adjustment, as\n   of clashing rules, interests, passions, or the like; also, the means by\n   which such adjustment is effected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperament', 'v. t.', 'Condition with regard to heat or cold; temperature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperament', 'v. t.', 'A system of compromises in the tuning of organs,\n   pianofortes, and the like, whereby the tones generated with the\n   vibrations of a ground tone are mutually modified and in part canceled,\n   until their number reduced to the actual practicable scale of twelve\n   tones to the octave. This scale, although in so far artificial, is yet\n   closely suggestive of its origin in nature, and this system of tuning,\n   although not mathematically true, yet satisfies the ear, while it has\n   the convenience that the same twelve fixed tones answer for every key\n   or scale, C/ becoming identical with D/, and so on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperament', 'v. t.', 'The peculiar physical and mental character of an\n   individual, in olden times erroneously supposed to be due to individual\n   variation in the relations and proportions of the constituent parts of\n   the body, especially of the fluids, as the bile, blood, lymph, etc.\n   Hence the phrases, bilious or choleric temperament, sanguine\n   temperament, etc., implying a predominance of one of these fluids and a\n   corresponding influence on the temperament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperamental', 'a.', 'Of or pertaining to temperament; constitutional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperance', 'v. t.', 'Habitual moderation in regard to the indulgence of\n   the natural appetites and passions; restrained or moderate indulgence;\n   moderation; as, temperance in eating and drinking; temperance in the\n   indulgence of joy or mirth; specifically, moderation, and sometimes\n   abstinence, in respect to using intoxicating liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperance', 'v. t.', 'Moderation of passion; patience; calmness;\n   sedateness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperance', 'v. t.', 'State with regard to heat or cold; temperature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperancy', 'n.', 'Temperance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperate', 'v. t.', 'Moderate; not excessive; as, temperate heat; a\n   temperate climate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperate', 'v. t.', 'Not marked with passion; not violent; cool; calm; as,\n   temperate language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperate', 'v. t.', 'Moderate in the indulgence of the natural appetites\n   or passions; as, temperate in eating and drinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperate', 'v. t.', 'Proceeding from temperance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperate', 'v. t.', 'To render temperate; to moderate; to soften; to\n   temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperately', 'adv.', 'In a temperate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperateness', 'n.', 'The quality or state of being temperate;\n   moderateness; temperance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperative', 'a.', 'Having power to temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperature', 'n.', 'Constitution; state; degree of any quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperature', 'n.', 'Freedom from passion; moderation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperature', 'n.', 'Condition with respect to heat or cold, especially as\n   indicated by the sensation produced, or by the thermometer or\n   pyrometer; degree of heat or cold; as, the temperature of the air; high\n   temperature; low temperature; temperature of freezing or of boiling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperature', 'n.', 'Mixture; compound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempered', 'a.', 'Brought to a proper temper; as, tempered steel; having\n   (such) a temper; -- chiefly used in composition; as, a good-tempered or\n   bad-tempered man; a well-tempered sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temperer', 'n.', 'One who, or that which, tempers; specifically, a machine\n   in which lime, cement, stone, etc., are mixed with water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempering', 'n.', 'The process of giving the requisite degree of hardness\n   or softness to a substance, as iron and steel; especially, the process\n   of giving to steel the degree of hardness required for various\n   purposes, consisting usually in first plunging the article, when heated\n   to redness, in cold water or other liquid, to give an excess of\n   hardness, and then reheating it gradually until the hardness is reduced\n   or drawn down to the degree required, as indicated by the color\n   produced on a polished portion, or by the burning of oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempest', 'n.', 'An extensive current of wind, rushing with great velocity\n   and violence, and commonly attended with rain, hail, or snow; a furious\n   storm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempest', 'n.', 'Fig.: Any violent tumult or commotion; as, a political\n   tempest; a tempest of war, or of the passions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempest', 'n.', 'A fashionable assembly; a drum. See the Note under Drum,\n   n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempest', 'v. t.', 'To disturb as by a tempest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempest', 'v. i.', 'To storm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempestive', 'a.', 'Seasonable; timely; as, tempestive showers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempestivily', 'n.', 'The quality, or state, of being tempestive;\n   seasonableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempestuous', 'a.', 'Of or pertaining to a tempest; involving or resembling\n   a tempest; turbulent; violent; stormy; as, tempestuous weather; a\n   tempestuous night; a tempestuous debate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Templar', 'n.', 'One of a religious and military order first established at\n   Jerusalem, in the early part of the 12th century, for the protection of\n   pilgrims and of the Holy Sepulcher. These Knights Templars, or Knights\n   of the Temple, were so named because they occupied an apartment of the\n   palace of Bladwin II. in Jerusalem, near the Temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Templar', 'n.', 'A student of law, so called from having apartments in the\n   Temple at London, the original buildings having belonged to the Knights\n   Templars. See Inner Temple, and Middle Temple, under Temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Templar', 'n.', 'One belonged to a certain order or degree among the\n   Freemasons, called Knights Templars. Also, one of an order among\n   temperance men, styled Good Templars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Templar', 'a.', 'Of or pertaining to a temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Template', 'n.', 'Same as Templet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temple', 'n.', 'A contrivence used in a loom for keeping the web stretched\n   transversely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temple', 'n.', 'The space, on either side of the head, back of the eye and\n   forehead, above the zygomatic arch and in front of the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temple', 'n.', 'One of the side bars of a pair of spectacles, jointed to\n   the bows, and passing one on either side of the head to hold the\n   spectacles in place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temple', 'n.', 'A place or edifice dedicated to the worship of some deity;\n   as, the temple of Jupiter at Athens, or of Juggernaut in India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temple', 'n.', 'The edifice erected at Jerusalem for the worship of\n   Jehovah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temple', 'n.', 'Hence, among Christians, an edifice erected as a place of\n   public worship; a church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temple', 'n.', 'Fig.: Any place in which the divine presence specially\n   resides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temple', 'v. t.', 'To build a temple for; to appropriate a temple to; as,\n   to temple a god.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Templed', 'a.', 'Supplied with a temple or temples, or with churches;\n   inclosed in a temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Templet', 'n.', 'A gauge, pattern, or mold, commonly a thin plate or board,\n   used as a guide to the form of the work to be executed; as, a masons\n   or a wheelwrights templet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Templet', 'n.', 'A short piece of timber, iron, or stone, placed in a wall\n   under a girder or other beam, to distribute the weight or pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempo', 'n.', 'The rate or degree of movement in time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporal', 'a.', 'Of or pertaining to the temple or temples; as, the\n   temporal bone; a temporal artery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporal', 'n.', 'Of or pertaining to time, that is, to the present life,\n   or this world; secular, as distinguished from sacred or eternal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporal', 'n.', 'Civil or political, as distinguished from ecclesiastical;\n   as, temporal power; temporal courts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporal', 'n.', 'Anything temporal or secular; a temporality; -- used\n   chiefly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporalities', 'pl. ', 'of Temporality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporality', 'n.', 'The state or quality of being temporary; -- opposed to\n   perpetuity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporality', 'n.', 'The laity; temporality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporality', 'n.', 'That which pertains to temporal welfare; material\n   interests; especially, the revenue of an ecclesiastic proceeding from\n   lands, tenements, or lay fees, tithes, and the like; -- chiefly used in\n   the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporally', 'adv.', 'In a temporal manner; secularly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporalness', 'n.', 'Worldliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporalty', 'n.', 'The laity; secular people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporalty', 'n.', 'A secular possession; a temporality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporaneous', 'a.', 'Temporarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporarily', 'adv.', 'In a temporary manner; for a time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporariness', 'n.', 'The quality or state of being temporary; -- opposed\n   to perpetuity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporary', 'a.', 'Lasting for a time only; existing or continuing for a\n   limited time; not permanent; as, the patient has obtained temporary\n   relief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporist', 'n.', 'A temporizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporization', 'n.', 'The act of temporizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporized', 'imp. & p. p.', 'of Temporize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporizing', 'p. pr. & vb. n.', 'of Temporize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporize', 'v. t.', 'To comply with the time or occasion; to humor, or\n   yield to, the current of opinion or circumstances; also, to trim, as\n   between two parties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporize', 'v. t.', 'To delay; to procrastinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporize', 'v. t.', 'To comply; to agree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporizer', 'n.', 'One who temporizes; one who yields to the time, or\n   complies with the prevailing opinions, fashions, or occasions; a\n   trimmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporizingly', 'adv.', 'In a temporizing or yielding manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporo-', '', 'A combining form used in anatomy to indicate connection\n   with, or relation to, the temple, or temporal bone; as, temporofacial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporo-auricular', 'a.', 'Of or pertaining to both the temple and the ear;\n   as, the temporo-auricular nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporofacial', 'a.', 'Of or pertaining to both the temple and the face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporomalar', 'a.', 'Of or pertaining to both the temple and the region of\n   the malar bone; as, the temporomalar nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temporomaxillary', 'a.', 'Of or pertaining to both the temple or the\n   temporal bone and the maxilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temps', 'n.', 'Time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempse', 'n.', 'See Temse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempted', 'imp. & p. p.', 'of Tempt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempting', 'p. pr. & vb. n.', 'of Tempt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempt', 'v. t.', 'To put to trial; to prove; to test; to try.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempt', 'v. t.', 'To lead, or endeavor to lead, into evil; to entice to\n   what is wrong; to seduce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempt', 'v. t.', 'To endeavor to persuade; to induce; to invite; to incite;\n   to provoke; to instigate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempt', 'v. t.', 'To endeavor to accomplish or reach; to attempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temptability', 'n.', 'The quality or state of being temptable; lability to\n   temptation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temptable', 'a.', 'Capable of being tempted; liable to be tempted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temptation', 'n.', 'The act of tempting, or enticing to evil; seduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temptation', 'n.', 'The state of being tempted, or enticed to evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temptation', 'n.', 'That which tempts; an inducement; an allurement,\n   especially to something evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temptationless', 'a.', 'Having no temptation or motive; as, a\n   temptationless sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temptatious', 'a.', 'Tempting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempter', 'n.', 'One who tempts or entices; especially, Satan, or the\n   Devil, regarded as the great enticer to evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tempting', 'a.', 'Adapted to entice or allure; attractive; alluring;\n   seductive; enticing; as, tempting pleasures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temptress', 'n.', 'A woman who entices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temse', 'n.', 'A sieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temulence', 'n.', 'Alt. of Temulency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temulency', 'n.', 'Intoxication; inebriation; drunkenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temulent', 'a.', 'Intoxicated; drunken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Temulentive', 'a.', 'Somewhat temulent; addicted to drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ten', 'a.', 'One more than nine; twice five.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ten', 'n.', 'The number greater by one than nine; the sum of five and five;\n   ten units of objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ten', 'n.', 'A symbol representing ten units, as 10, x, or X.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenability', 'n.', 'The quality or state of being tenable; tenableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenable', 'a.', 'Capable of being held, naintained, or defended, as against\n   an assailant or objector, or againts attempts to take or process; as, a\n   tenable fortress, a tenable argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenableness', 'n.', 'Same as Tenability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenace', 'n.', 'The holding by the fourth hand of the best and third best\n   cards of a suit led; also, sometimes, the combination of best with\n   third best card of a suit in any hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenacious', 'a.', 'Holding fast, or inclined to hold fast; inclined to\n   retain what is in possession; as, men tenacious of their just rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenacious', 'a.', 'Apt to retain; retentive; as, a tenacious memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenacious', 'a.', 'Having parts apt to adhere to each other; cohesive;\n   tough; as, steel is a tenacious metal; tar is more tenacious than oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenacious', 'a.', 'Apt to adhere to another substance; glutinous; viscous;\n   sticking; adhesive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenacious', 'a.', 'Niggardly; closefisted; miserly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenacious', 'a.', 'Holding stoutly to ones opinion or purpose; obstinate;\n   stubborn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenacity', 'n.', 'The quality or state of being tenacious; as, tenacity, or\n   retentiveness, of memory; tenacity, or persistency, of purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenacity', 'n.', 'That quality of bodies which keeps them from parting\n   without considerable force; cohesiveness; the effect of attraction; --\n   as distinguished from brittleness, fragility, mobility, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenacity', 'n.', 'That quality of bodies which makes them adhere to other\n   bodies; adhesiveness; viscosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenacity', 'n.', 'The greatest longitudinal stress a substance can bear\n   without tearing asunder, -- usually expressed with reference to a unit\n   area of the cross section of the substance, as the number of pounds per\n   square inch, or kilograms per square centimeter, necessary to produce\n   rupture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenacula', 'pl. ', 'of Tenaculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenaculums', 'pl. ', 'of Tenaculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenaculum', 'n.', 'An instrument consisting of a fine, sharp hook attached\n   to a handle, and used mainly for taking up arteries, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenacy', 'n.', 'Tenaciousness; obstinacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenaille', 'n.', 'An outwork in the main ditch, in front of the curtain,\n   between two bastions. See Illust. of Ravelin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenaillon', 'n.', 'A work constructed on each side of the ravelins, to\n   increase their strength, procure additional ground beyond the ditch, or\n   cover the shoulders of the bastions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenacies', 'pl. ', 'of Tenancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenancy', 'n.', 'A holding, or a mode of holding, an estate; tenure; the\n   temporary possession of what belongs to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenancy', 'n.', 'A house for habitation, or place to live in, held of\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenant', 'n.', 'One who holds or possesses lands, or other real estate, by\n   any kind of right, whether in fee simple, in common, in severalty, for\n   life, for years, or at will; also, one who has the occupation or\n   temporary possession of lands or tenements the title of which is in\n   another; -- correlative to landlord. See Citation from Blackstone,\n   under Tenement, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenant', 'n.', 'One who has possession of any place; a dweller; an\n   occupant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenanted', 'imp. & p. p.', 'of Tenant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenanting', 'p. pr. & vb. n.', 'of Tenant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenant', 'v. t.', 'To hold, occupy, or possess as a tenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenantable', 'a.', 'Fit to be rented; in a condition suitable for a tenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenantless', 'a.', 'Having no tenants; unoccupied; as, a tenantless\n   mansion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenantry', 'n.', 'The body of tenants; as, the tenantry of a manor or a\n   kingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenantry', 'n.', 'Tenancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenant saw', '', 'See Tenon saw, under Tenon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tench', 'n.', 'A European fresh-water fish (Tinca tinca, or T. vulgaris)\n   allied to the carp. It is noted for its tenacity of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tend', 'v. t.', 'To make a tender of; to offer or tender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tended', 'imp. & p. p.', 'of Tend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tending', 'p. pr. & vb. n.', 'of Tend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tend', 'v. t.', 'To accompany as an assistant or protector; to care for the\n   wants of; to look after; to watch; to guard; as, shepherds tend their\n   flocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tend', 'v. t.', 'To be attentive to; to note carefully; to attend to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tend', 'v. i.', 'To wait, as attendants or servants; to serve; to attend;\n   -- with on or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tend', 'v. i.', 'To await; to expect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tend', 'a.', 'To move in a certain direction; -- usually with to or\n   towards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tend', 'a.', 'To be directed, as to any end, object, or purpose; to aim; to\n   have or give a leaning; to exert activity or influence; to serve as a\n   means; to contribute; as, our petitions, if granted, might tend to our\n   destruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendance', 'n.', 'The act of attending or waiting; attendance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendance', 'n.', 'Persons in attendance; attendants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendence', 'n.', 'Tendency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendencies', 'pl. ', 'of Tendency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendency', 'n.', 'Direction or course toward any place, object, effect, or\n   result; drift; causal or efficient influence to bring about an effect\n   or result.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'n.', 'One who tends; one who takes care of any person or thing; a\n   nurse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'n.', 'A vessel employed to attend other vessels, to supply them\n   with provisions and other stores, to convey intelligence, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'n.', 'A car attached to a locomotive, for carrying a supply of\n   fuel and water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendered', 'imp. & p. p.', 'of Tender');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendering', 'p. pr. & vb. n.', 'of Tender');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'v. t.', 'To offer in payment or satisfaction of a demand, in\n   order to save a penalty or forfeiture; as, to tender the amount of rent\n   or debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'v. t.', 'To offer in words; to present for acceptance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'n.', 'An offer, either of money to pay a debt, or of service to\n   be performed, in order to save a penalty or forfeiture, which would be\n   incurred by nonpayment or nonperformance; as, the tender of rent due,\n   or of the amount of a note, with interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'n.', 'Any offer or proposal made for acceptance; as, a tender of\n   a loan, of service, or of friendship; a tender of a bid for a contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'n.', 'The thing offered; especially, money offered in payment of\n   an obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'superl.', 'Easily impressed, broken, bruised, or injured; not\n   firm or hard; delicate; as, tender plants; tender flesh; tender fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'superl.', 'Sensible to impression and pain; easily pained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'superl.', 'Physically weak; not hardly or able to endure\n   hardship; immature; effeminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'superl.', 'Susceptible of the softer passions, as love,\n   compassion, kindness; compassionate; pitiful; anxious for anothers\n   good; easily excited to pity, forgiveness, or favor; sympathetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'superl.', 'Exciting kind concern; dear; precious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'superl.', 'Careful to save inviolate, or not to injure; -- with\n   of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'superl.', 'Unwilling to cause pain; gentle; mild.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'superl.', 'Adapted to excite feeling or sympathy; expressive of\n   the softer passions; pathetic; as, tender expressions; tender\n   expostulations; a tender strain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'superl.', 'Apt to give pain; causing grief or pain; delicate; as,\n   a tender subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'superl.', 'Heeling over too easily when under sail; -- said of a\n   vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'n.', 'Regard; care; kind concern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender', 'v. t.', 'To have a care of; to be tender toward; hence, to\n   regard; to esteem; to value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenderfoot', 'n.', 'A delicate person; one not inured to the hardship and\n   rudeness of pioneer life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender-hearted', 'a.', 'Having great sensibility; susceptible of\n   impressions or influence; affectionate; pitying; sensitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tender-hefted', 'a.', 'Having great tenderness; easily moved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenderling', 'n.', 'One made tender by too much kindness; a fondling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenderling', 'n.', 'One of the first antlers of a deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenderloin', 'n.', 'A strip of tender flesh on either side of the vertebral\n   column under the short ribs, in the hind quarter of beef and pork. It\n   consists of the psoas muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenderly', 'adv.', 'In a tender manner; with tenderness; mildly; gently;\n   softly; in a manner not to injure or give pain; with pity or affection;\n   kindly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenderness', 'n.', 'The quality or state of being tender (in any sense of\n   the adjective).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendinous', 'a.', 'Pertaining to a tendon; of the nature of tendon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendinous', 'a.', 'Full of tendons; sinewy; as, nervous and tendinous parts\n   of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendment', 'n.', 'Attendance; care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendon', 'n.', 'A tough insensible cord, bundle, or band of fibrous\n   connective tissue uniting a muscle with some other part; a sinew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendonous', 'a.', 'Tendinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendosynovitis', 'n.', 'See Tenosynovitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendrac', 'n.', 'Any one of several species of small insectivores of the\n   family Centetidae, belonging to Ericulus, Echinope, and related genera,\n   native of Madagascar. They are more or less spinose and resemble the\n   hedgehog in habits. The rice tendrac (Oryzorictes hora) is very\n   injurious to rice crops. Some of the species are called also tenrec.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendril', 'a.', 'A slender, leafless portion of a plant by which it becomes\n   attached to a supporting body, after which the tendril usually\n   contracts by coiling spirally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendril', 'a.', 'Clasping; climbing as a tendril.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendriled', 'a.', 'Alt. of Tendrilled');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendrilled', 'a.', 'Furnished with tendrils, or with such or so many,\n   tendrils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendron', 'n.', 'A tendril.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tendry', 'n.', 'A tender; an offer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tene', 'n. & v.', 'See 1st and 2d Teen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenebrae', 'n.', 'The matins and lauds for the last three days of Holy\n   Week, commemorating the sufferings and death of Christ, -- usually sung\n   on the afternoon or evening of Wednesday, Thursday, and Friday, instead\n   of on the following days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenebricose', 'a.', 'Tenebrous; dark; gloomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenebrific', 'a.', 'Rendering dark or gloomy; tenebrous; gloomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenebrificous', 'a.', 'Tenebrific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenebrious', 'a.', 'Tenebrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenebrose', 'a.', 'Characterized by darkness or gloom; tenebrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenebrosity', 'n.', 'The quality or state of being tenebrous;\n   tenebrousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenebrous', 'a.', 'Dark; gloomy; dusky; tenebrious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenement', 'n.', 'That which is held of another by service; property which\n   one holds of a lord or proprietor in consideration of some military or\n   pecuniary service; fief; fee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenement', 'n.', 'Any species of permanent property that may be held, so as\n   to create a tenancy, as lands, houses, rents, commons, an office, an\n   advowson, a franchise, a right of common, a peerage, and the like; --\n   called also free / frank tenements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenement', 'n.', 'A dwelling house; a building for a habitation; also, an\n   apartment, or suite of rooms, in a building, used by one family; often,\n   a house erected to be rented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenement', 'n.', 'Fig.: Dwelling; abode; habitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenemental', 'a.', 'Of or pertaining to a tenement; capable of being held\n   by tenants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenementary', 'a.', 'Capable of being leased; held by tenants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenent', 'n.', 'A tenet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teneral', 'a.', 'Of, pertaining to, or designating, a condition assumed by\n   the imago of certain Neuroptera, after exclusion from the pupa. In this\n   state the insect is soft, and has not fully attained its mature\n   coloring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teneriffe', 'n.', 'A white wine resembling Madeira in taste, but more tart,\n   produced in Teneriffe, one of the Canary Islands; -- called also\n   Vidonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenerity', 'a.', 'Tenderness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenesmic', 'a.', 'Of or pertaining to tenesmus; characterized by tenesmus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenesmus', 'n.', 'An urgent and distressing sensation, as if a discharge\n   from the intestines must take place, although none can be effected; --\n   always referred to the lower extremity of the rectum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenet', 'n.', 'Any opinion, principle, dogma, belief, or doctrine, which a\n   person holds or maintains as true; as, the tenets of Plato or of\n   Cicero.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenfold', 'a. & adv.', 'In tens; consisting of ten in one; ten times\n   repeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenia', 'n.', 'See Taenia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenioid', 'a.', 'See Taenoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tennantite', 'n.', 'A blackish lead-gray mineral, closely related to\n   tetrahedrite. It is essentially a sulphide of arsenic and copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenne', 'n.', 'A tincture, rarely employed, which is considered as an\n   orange color or bright brown. It is represented by diagonal lines from\n   sinister to dexter, crossed by vertical lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tennis', 'n.', 'A play in which a ball is driven to and fro, or kept in\n   motion by striking it with a racket or with the open hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tennis', 'v. t.', 'To drive backward and forward, as a ball in playing\n   tennis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tennu', 'n.', 'The tapir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ten-oclock', 'n.', 'A plant, the star-of-Bethlehem. See under Star.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenon', 'n.', 'A projecting member left by cutting away the wood around it,\n   and made to insert into a mortise, and in this way secure together the\n   parts of a frame; especially, such a member when it passes entirely\n   through the thickness of the piece in which the mortise is cut, and\n   shows on the other side. Cf. Tooth, Tusk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenon', 'v. t.', 'To cut or fit for insertion into a mortise, as the end of\n   a piece of timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenonian', 'a.', 'Discovered or described by M. Tenon, a French anatomist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenor', 'n.', 'A state of holding on in a continuous course; manner of\n   continuity; constant mode; general tendency; course; career.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenor', 'n.', 'That course of thought which holds on through a discourse;\n   the general drift or course of thought; purport; intent; meaning;\n   understanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenor', 'n.', 'Stamp; character; nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenor', 'n.', 'An exact copy of a writing, set forth in the words and\n   figures of it. It differs from purport, which is only the substance or\n   general import of the instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenor', 'n.', 'The higher of the two kinds of voices usually belonging to\n   adult males; hence, the part in the harmony adapted to this voice; the\n   second of the four parts in the scale of sounds, reckoning from the\n   base, and originally the air, to which the other parts were auxillary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenor', 'n.', 'A person who sings the tenor, or the instrument that play\n   it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenosynovitis', 'n.', 'Inflammation of the synovial sheath enveloping a\n   tendon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenotome', 'n.', 'A slender knife for use in the operation of tenotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenotomy', 'n.', 'The division of a tendon, or the act of dividing a\n   tendon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenpenny', 'a.', 'Valued or sold at ten pence; as, a tenpenny cake. See 2d\n   Penny, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenpenny', 'a.', 'Denoting a size of nails. See 1st Penny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenpins', 'n.', 'A game resembling ninepins, but played with ten pins. See\n   Ninepins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ten-pounder', 'n.', 'A large oceanic fish (Elops saurus) found in the\n   tropical parts of all the oceans. It is used chiefly for bait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenrec', 'n.', 'A small insectivore (Centetes ecaudatus), native of\n   Madagascar, but introduced also into the islands of Bourbon and\n   Mauritius; -- called also tanrec. The name is applied to other allied\n   genera. See Tendrac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tense', 'n.', 'One of the forms which a verb takes by inflection or by\n   adding auxiliary words, so as to indicate the time of the action or\n   event signified; the modification which verbs undergo for the\n   indication of time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tense', 'a.', 'Stretched tightly; strained to stiffness; rigid; not lax;\n   as, a tense fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tensibility', 'n.', 'The quality or state of being tensible; tensility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tensible', 'a.', 'Capable of being extended or drawn out; ductile;\n   tensible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tensile', 'a.', 'Of or pertaining to extension; as, tensile strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tensile', 'a.', 'Capable of extension; ductile; tensible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tensiled', 'a.', 'Made tensile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tensility', 'n.', 'The quality or state of being tensile, or capable of\n   extension; tensibility; as, the tensility of the muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tension', 'a.', 'The act of stretching or straining; the state of being\n   stretched or strained to stiffness; the state of being bent strained;\n   as, the tension of the muscles, tension of the larynx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tension', 'a.', 'Fig.: Extreme strain of mind or excitement of feeling;\n   intense effort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tension', 'a.', 'The degree of stretching to which a wire, cord, piece of\n   timber, or the like, is strained by drawing it in the direction of its\n   length; strain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tension', 'a.', 'The force by which a part is pulled when forming part of\n   any system in equilibrium or in motion; as, the tension of a srting\n   supporting a weight equals that weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tension', 'a.', 'A device for checking the delivery of the thread in a\n   sewing machine, so as to give the stitch the required degree of\n   tightness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tension', 'a.', 'Expansive force; the force with which the particles of a\n   body, as a gas, tend to recede from each other and occupy a larger\n   space; elastic force; elasticity; as, the tension of vapor; the tension\n   of air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tension', 'a.', 'The quality in consequence of which an electric charge\n   tends to discharge itself, as into the air by a spark, or to pass from\n   a body of greater to one of less electrical potential. It varies as the\n   quantity of electricity upon a given area.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tensioned', 'a.', 'Extended or drawn out; subjected to tension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tensity', 'n.', 'The quality or state of being tense, or strained to\n   stiffness; tension; tenseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tensive', 'a.', 'Giving the sensation of tension, stiffness, or\n   contraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tensor', 'n.', 'A muscle that stretches a part, or renders it tense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tensor', 'n.', 'The ratio of one vector to another in length, no regard\n   being had to the direction of the two vectors; -- so called because\n   considered as a stretching factor in changing one vector into another.\n   See Versor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ten-strike', 'n.', 'A knocking down of all ten pins at one delivery of the\n   ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ten-strike', 'n.', 'Any quick, decisive stroke or act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tensure', 'n.', 'Tension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tent', 'n.', 'A kind of wine of a deep red color, chiefly from Galicia or\n   Malaga in Spain; -- called also tent wine, and tinta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tent', 'n.', 'Attention; regard, care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tent', 'n.', 'Intention; design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tent', 'v. t.', 'To attend to; to heed; hence, to guard; to hinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tent', 'v. t.', 'To probe or to search with a tent; to keep open with a\n   tent; as, to tent a wound. Used also figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tent', 'n.', 'A roll of lint or linen, or a conical or cylindrical piece of\n   sponge or other absorbent, used chiefly to dilate a natural canal, to\n   keep open the orifice of a wound, or to absorb discharges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tent', 'n.', 'A probe for searching a wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tent', 'n.', 'A pavilion or portable lodge consisting of skins, canvas, or\n   some strong cloth, stretched and sustained by poles, -- used for\n   sheltering persons from the weather, especially soldiers in camp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tent', 'n.', 'The representation of a tent used as a bearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tented', 'imp. & p. p.', 'of Tent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenting', 'p. pr. & vb. n.', 'of Tent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tent', 'v. i.', 'To lodge as a tent; to tabernacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentacle', 'n.', 'A more or less elongated process or organ, simple or\n   branched, proceeding from the head or cephalic region of invertebrate\n   animals, being either an organ of sense, prehension, or motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentacled', 'a.', 'Having tentacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentacular', 'a.', 'Of or pertaining to a tentacle or tentacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentaculata', 'n. pl.', 'A division of Ctenophora including those which\n   have two long tentacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentaculate', 'a.', 'Alt. of Tentaculated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentaculated', 'a.', 'Having tentacles, or organs like tentacles;\n   tentacled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentaculifera', 'n. pl.', 'Same as Suctoria, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentaculiferous', 'a.', 'Producing or bearing tentacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentaculiform', 'a.', 'Shaped like a tentacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentaculite', 'n.', 'Any one of numerous species of small, conical fossil\n   shells found in Paleozoic rocks. They are supposed to be pteropods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentaculocyst', 'n.', 'One of the auditory organs of certain medusae; --\n   called also auditory tentacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentacula', 'pl. ', 'of Tentaculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentaculum', 'n.', 'A tentacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentaculum', 'n.', 'One of the stiff hairs situated about the mouth, or on\n   the face, of many animals, and supposed to be tactile organs; a tactile\n   hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentage', 'n.', 'A collection of tents; an encampment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentation', 'n.', 'Trial; temptation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentation', 'n.', 'A mode of adjusting or operating by repeated trials or\n   experiments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentative', 'a.', 'Of or pertaining to a trial or trials; essaying;\n   experimental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentative', 'n.', 'An essay; a trial; an experiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tented', 'a.', 'Covered with tents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenter', 'n.', 'One who takes care of, or tends, machines in a factory; a\n   kind of assistant foreman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenter', 'n.', 'A kind of governor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenter', 'n.', 'A machine or frame for stretching cloth by means of hooks,\n   called tenter-hooks, so that it may dry even and square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentered', 'imp. & p. p.', 'of Tenter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentering', 'p. pr. & vb. n.', 'of Tenter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenter', 'v. i.', 'To admit extension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenter', 'v. t.', 'To hang or stretch on, or as on, tenters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentfuls', 'pl. ', 'of Tentful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentful', 'n.', 'As much, or as many, as a tent will hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenth', 'a.', 'Next in order after the ninth; coming after nine others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenth', 'a.', 'Constituting or being one of ten equal parts into which\n   anything is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenth', 'n.', 'The next in order after the ninth; one coming after nine\n   others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenth', 'n.', 'The quotient of a unit divided by ten; one of ten equal\n   parts into which anything is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenth', 'n.', 'The tenth part of annual produce, income, increase, or the\n   like; a tithe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenth', 'n.', 'The interval between any tone and the tone represented on\n   the tenth degree of the staff above it, as between one of the scale and\n   three of the octave above; the octave of the third.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenth', 'n.', 'A temporary aid issuing out of personal property, and\n   granted to the king by Parliament; formerly, the real tenth part of all\n   the movables belonging to the subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenth', 'n.', 'The tenth part of the annual profit of every living in the\n   kingdom, formerly paid to the pope, but afterward transferred to the\n   crown. It now forms a part of the fund called Queen Annes Bounty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenthly', 'adv.', 'In a tenth manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenthmeter', 'n.', 'Alt. of Tenthmetre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenthmetre', 'n.', 'A unit for the measurement of many small lengths, such\n   that 1010 of these units make one meter; the ten millionth part of a\n   millimeter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenthredinides', 'n. pl.', 'A group of Hymneoptera comprising the sawflies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentif', 'a.', 'Attentive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentifly', 'adv.', 'Attentively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentiginous', 'a.', 'Stiff; stretched; strained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentiginous', 'a.', 'Lustful, or pertaining to lust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentmaker', 'n.', 'One whose occupation it is to make tents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentorium', 'n.', 'A fold of the dura mater which separates the cerebellum\n   from the cerebrum and often incloses a process or plate of the skull\n   called the bony tentorium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentory', 'n.', 'The awning or covering of a tent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tentwort', 'n.', 'A kind of small fern, the wall rue. See under Wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenuated', 'imp. & p. p.', 'of Tenuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenuating', 'p. pr. & vb. n.', 'of Tenuate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenuate', 'v. t.', 'To make thin; to attenuate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenuifolious', 'a.', 'Having thin or narrow leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenuious', 'a.', 'Rare or subtile; tenuous; -- opposed to dense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenuirosters', 'pl. ', 'of Tenuiroster');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenuiroster', 'n.', 'One of the Tenuirostres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenuirostral', 'a.', 'Thin-billed; -- applied to birds with a slender bill,\n   as the humming birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenuirostres', 'n. pl.', 'An artificial group of passerine birds having\n   slender bills, as the humming birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenues', 'pl. ', 'of Tenuis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenuis', 'n.', 'One of the three surd mutes /, /, /; -- so called in\n   relation to their respective middle letters, or medials, /, /, /, and\n   their aspirates, /, /, /. The term is also applied to the corresponding\n   letters and articulate elements in other languages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenuity', 'n.', 'The quality or state of being tenuous; thinness, applied\n   to a broad substance; slenderness, applied to anything that is long;\n   as, the tenuity of a leaf; the tenuity of a hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenuity', 'n.', 'Rarily; rareness; thinness, as of a fluid; as, the tenuity\n   of the air; the tenuity of the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenuity', 'n.', 'Poverty; indigence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenuity', 'n.', 'Refinement; delicacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenuous', 'a.', 'Thin; slender; small; minute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenuous', 'a.', 'Rare; subtile; not dense; -- said of fluids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenuous', 'a.', 'Lacking substance, as a tenuous argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenure', 'n.', 'The act or right of holding, as property, especially real\n   estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenure', 'n.', 'The manner of holding lands and tenements of a superior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenure', 'n.', 'The consideration, condition, or service which the occupier\n   of land gives to his lord or superior for the use of his land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tenure', 'n.', 'Manner of holding, in general; as, in absolute governments,\n   men hold their rights by a precarious tenure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teocallis', 'pl. ', 'of Teocalli');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teocalli', 'n.', 'Literally, Gods house; a temple, usually of pyramidal\n   form, such as were built by the aborigines of Mexico, Yucatan, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teosinte', 'n.', 'A large grass (Euchlaena luxurians) closely related to\n   maize. It is native of Mexico and Central America, but is now\n   cultivated for fodder in the Southern United States and in many warm\n   countries. Called also Guatemala grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tepal', 'n.', 'A division of a perianth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tepee', 'n.', 'An Indian wigwam or tent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tepefaction', 'n.', 'Act of tepefying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tepefied', 'imp. & p. p.', 'of Tepefy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tepefying', 'p. pr. & vb. n.', 'of Tepefy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tepefy', 'v. t. & i.', 'To make or become tepid, or moderately warm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tephramancy', 'n.', 'Divination by the ashes of the altar on which a victim\n   had been consumed in sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tephrite', 'n.', 'An igneous rock consisting essentially of plagioclase and\n   either leucite or nephelite, or both.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tephroite', 'n.', 'A silicate of manganese of an ash-gray color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tephrosia', 'n.', 'A genus of leguminous shrubby plants and herbs, mostly\n   found in tropical countries, a few herbaceous species being North\n   American. The foliage is often ashy-pubescent, whence the name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tepid', 'a.', 'Moderately warm; lukewarm; as, a tepid bath; tepid rays;\n   tepid vapors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tepidity', 'n.', 'The quality or state of being tepid; moderate warmth;\n   lukewarmness; tepidness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tepor', 'n.', 'Gentle heat; moderate warmth; tepidness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tequila', 'n.', 'An intoxicating liquor made from the maguey in the\n   district of Tequila, Mexico.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ter-', '', 'A combining form from L. ter signifying three times, thrice.\n   See Tri-, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teraconic', 'a.', 'Pertaining to, or designating, an acid obtained by the\n   distillation of terebic acid, and homologous with citraconic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teracrylic', 'a.', 'Of, pertaining to, or designating, an acid of the\n   acrylic series, obtained by the distillation of terpenylic acid, as an\n   only substance having a peculiar cheesy odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teraphs', 'pl. ', 'of Teraph');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teraph', 'n.', 'See Teraphim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teraphim', 'n. pl.', 'Images connected with the magical rites used by those\n   Israelites who added corrupt practices to the patriarchal religion.\n   Teraphim were consulted by the Israelites for oracular answers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terapin', 'n.', 'See Terrapin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teratical', 'a.', 'Wonderful; ominous; prodigious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teratogeny', 'n.', 'The formation of monsters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teratoid', 'a.', 'Resembling a monster; abnormal; of a pathological growth,\n   exceedingly complex or highly organized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teratological', 'a.', 'Of or pertaining to teratology; as, teratological\n   changes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teratology', 'n.', 'That branch of biological science which treats of\n   monstrosities, malformations, or deviations from the normal type of\n   structure, either in plants or animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teratology', 'n.', 'Affectation of sublimity; bombast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teratoma', 'n.', 'A tumor, sometimes found in newborn children, which is\n   made up of a heterigenous mixture of tissues, as of bone, cartilage and\n   muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terbic', 'a.', 'Of, pertaining to, or containing, terbium; also,\n   designating certain of its compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terbium', 'n.', 'A rare metallic element, of uncertain identification,\n   supposed to exist in certain minerals, as gadolinite and samarskite,\n   with other rare ytterbium earth. Symbol Tr or Tb. Atomic weight 150.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terce', 'n.', 'See Tierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tercel', 'n.', 'See Tiercel. Called also tarsel, tassel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tercelet', 'n.', 'A male hawk or eagle; a tiercelet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tercellene', 'n.', 'A small male hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tercentenary', 'a.', 'Including, or relating to, an interval of three\n   hundred years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tercentenary', 'n.', 'The three hundredth anniversary of any event; also, a\n   celebration of such an anniversary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tercet', 'n.', 'A triplet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tercet', 'n.', 'A triplet; a group of three lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tercine', 'n.', 'A cellular layer derived from the nucleus of an ovule and\n   surrounding the embryo sac. Cf. Quintine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebate', 'n.', 'A salt of terebic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebene', 'n.', 'A polymeric modification of terpene, obtained as a white\n   crystalline camphorlike substance; -- called also camphene. By\n   extension, any one of a group of related substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebenthene', 'n.', 'Oil of turpentine. See Turpentine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebic', 'a.', 'Pertaining to, or obtained from, terbenthene (oil of\n   turpentine); specifically, designating an acid, C7H10O4, obtained by\n   the oxidation of terbenthene with nitric acid, as a white crystalline\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebilenic', 'a.', 'Of, pertaining to, or designating, a complex acid,\n   C7H8O4, obtained as a white crystalline substance by a modified\n   oxidation of terebic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebinth', 'n.', 'The turpentine tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebinthic', 'a.', 'Of or pertaining to turpentine; resembling turpentine;\n   terbinthine; as, terbinthic qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebinthinate', 'a.', 'Impregnating with the qualities of turpentine;\n   terbinthine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebinthine', 'a.', 'Of or pertaining to turpentine; consisting of\n   turpentine, or partaking of its qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebras', 'pl. ', 'of Terebra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebrae', 'pl. ', 'of Terebra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebra', 'n.', 'A genus of marine gastropods having a long, tapering\n   spire. They belong to the Toxoglossa. Called also auger shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebra', 'n.', 'The boring ovipositor of a hymenopterous insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebrant', 'a.', 'Boring, or adapted for boring; -- said of certain\n   Hymenoptera, as the sawflies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebrantia', 'n. pl.', 'A division of Hymenoptera including those which\n   have an ovipositor adapted for perforating plants. It includes the\n   sawflies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebrate', 'v. t.', 'To perforate; to bore; to pierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebrating', 'a.', 'Boring; perforating; -- applied to molluskas which\n   form holes in rocks, wood, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebrating', 'a.', 'Boring; piercing; -- applied to certain kinds of pain,\n   especially to those of locomotor ataxia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebration', 'n.', 'The act of terebrating, or boring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebratulae', 'pl. ', 'of Terebratula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebratula', 'n.', 'A genus of brachiopods which includes many living and\n   some fossil species. The larger valve has a perforated beak, through\n   which projects a short peduncle for attachment. Called also lamp shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebratulid', 'n.', 'Any species of Terebratula or allied genera. Used\n   also adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terebratuliform', 'a.', 'Having the general form of a terebratula shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teredine', 'n.', 'A borer; the teredo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teredos', 'pl. ', 'of Teredo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teredines', 'pl. ', 'of Teredo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teredo', 'n.', 'A genus of long, slender, wormlike bivalve mollusks which\n   bore into submerged wood, such as the piles of wharves, bottoms of\n   ships, etc.; -- called also shipworm. See Shipworm. See Illust. in App.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terephthalate', 'n.', 'A salt of terephthalic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terephthalic', 'a.', 'Of, pertaining to, or designating, a dibasic acid of\n   the aromatic series, metameric with phthalic acid, and obtained, as a\n   tasteless white crystalline powder, by the oxidation of oil of\n   turpentine; -- called also paraphthalic acid. Cf. Phthalic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teret', 'a.', 'Round; terete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terete', 'a.', 'Cylindrical and slightly tapering; columnar, as some stems\n   of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teretial', 'a.', 'Rounded; as, the teretial tracts in the floor of the\n   fourth ventricle of the brain of some fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teretous', 'a.', 'Terete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tergal', 'a.', 'Of or pertaining to back, or tergum. See Dorsal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tergant', 'a.', 'Showing the back; as, the eagle tergant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tergeminal', 'a.', 'Alt. of Tergeminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tergeminate', 'a.', 'Thrice twin; having three pairs of leaflets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tergeminous', 'a.', 'Threefold; thrice-paired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tergiferous', 'a.', 'Carrying or bearing upon the back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tergite', 'n.', 'The dorsal portion of an arthromere or somite of an\n   articulate animal. See Illust. under Coleoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tergiversate', 'v. i.', 'To shift; to practice evasion; to use subterfuges;\n   to shuffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tergiversation', 'n.', 'The act of tergiversating; a shifting; shift;\n   subterfuge; evasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tergiversation', 'n.', 'Fickleness of conduct; inconstancy; change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tergiversator', 'n.', 'One who tergiversates; one who suffles, or practices\n   evasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terga', 'pl. ', 'of Tergum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tergum', 'n.', 'The back of an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tergum', 'n.', 'The dorsal piece of a somite of an articulate animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tergum', 'n.', 'One of the dorsal plates of the operculum of a cirriped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terin', 'n.', 'A small yellow singing bird, with an ash-colored head; the\n   European siskin. Called also tarin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Term', 'n.', 'That which limits the extent of anything; limit; extremity;\n   bound; boundary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Term', 'n.', 'The time for which anything lasts; any limited time; as, a\n   term of five years; the term of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Term', 'n.', 'In universities, schools, etc., a definite continuous period\n   during which instruction is regularly given to students; as, the school\n   year is divided into three terms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Term', 'n.', 'A point, line, or superficies, that limits; as, a line is the\n   term of a superficies, and a superficies is the term of a solid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Term', 'n.', 'A fixed period of time; a prescribed duration');
  INSERT INTO words (word, word_type, description) VALUES  (    'Term', 'n.', 'The limitation of an estate; or rather, the whole time for\n   which an estate is granted, as for the term of a life or lives, or for\n   a term of years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Term', 'n.', 'A space of time granted to a debtor for discharging his\n   obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Term', 'n.', 'The time in which a court is held or is open for the trial of\n   causes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Term', 'n.', 'The subject or the predicate of a proposition; one of the\n   three component parts of a syllogism, each one of which is used twice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Term', 'n.', 'A word or expression; specifically, one that has a precisely\n   limited meaning in certain relations and uses, or is peculiar to a\n   science, art, profession, or the like; as, a technical term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Term', 'n.', 'A quadrangular pillar, adorned on the top with the figure of\n   a head, as of a man, woman, or satyr; -- called also terminal figure.\n   See Terminus, n., 2 and 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Term', 'n.', 'A member of a compound quantity; as, a or b in a + b; ab or\n   cd in ab - cd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Term', 'n.', 'The menses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Term', 'n.', 'Propositions or promises, as in contracts, which, when\n   assented to or accepted by another, settle the contract and bind the\n   parties; conditions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Term', 'n.', 'In Scotland, the time fixed for the payment of rents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Term', 'n.', 'A piece of carved work placed under each end of the taffrail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termed', 'imp. & p. p.', 'of Term');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terming', 'p. pr. & vb. n.', 'of Term');
  INSERT INTO words (word, word_type, description) VALUES  (    'Term', 'n.', 'To apply a term to; to name; to call; to denominate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terma', 'n.', 'The terminal lamina, or thin ventral part, of the anterior\n   wall of the third ventricle of the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termagancy', 'n.', 'The quality or state of being termagant; turbulence;\n   tumultuousness; as, a violent termagancy of temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termagant', 'n.', 'An imaginary being supposed by the Christians to be a\n   Mohammedan deity or false god. He is represented in the ancient\n   moralities, farces, and puppet shows as extremely vociferous and\n   tumultous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termagant', 'n.', 'A boisterous, brawling, turbulent person; -- formerly\n   applied to both sexes, now only to women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termagant', 'a.', 'Tumultuous; turbulent; boisterous; furious; quarrelsome;\n   scolding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termatarium', 'n.', 'Any nest or dwelling of termes, or white ants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termatary', 'n.', 'Same as Termatarium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termer', 'n.', 'One who resorted to London during the law term only, in\n   order to practice tricks, to carry on intrigues, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termer', 'n.', 'One who has an estate for a term of years or for life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termites', 'pl. ', 'of Termes');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termes', 'n.', 'A genus of Pseudoneuroptera including the white ants, or\n   termites. See Termite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminable', 'a.', 'Capable of being terminated or bounded; limitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminal', 'n.', 'Of or pertaining to the end or extremity; forming the\n   extremity; as, a terminal edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminal', 'n.', 'Growing at the end of a branch or stem; terminating; as,\n   a terminal bud, flower, or spike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminal', 'n.', 'That which terminates or ends; termination; extremity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminal', 'n.', 'Either of the ends of the conducting circuit of an\n   electrical apparatus, as an inductorium, dynamo, or electric motor,\n   usually provided with binding screws for the attachment of wires by\n   which a current may be conveyed into or from the machine; a pole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminalia', 'n. pl.', 'A festival celebrated annually by the Romans on\n   February 23 in honor of Terminus, the god of boundaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminant', 'n.', 'Termination; ending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminated', 'imp. & p. p.', 'of Terminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminating', 'p. pr. & vb. n.', 'of Terminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminate', 'v. t.', 'To set a term or limit to; to form the extreme point\n   or side of; to bound; to limit; as, to terminate a surface by a line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminate', 'v. t.', 'To put an end to; to make to cease; as, to terminate\n   an effort, or a controversy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminate', 'v. t.', 'Hence, to put the finishing touch to; to bring to\n   completion; to perfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminate', 'v. i.', 'To be limited in space by a point, line, or surface;\n   to stop short; to end; to cease; as, the torrid zone terminates at the\n   tropics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminate', 'v. i.', 'To come to a limit in time; to end; to close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termination', 'n.', 'The act of terminating, or of limiting or setting\n   bounds; the act of ending or concluding; as, a voluntary termination of\n   hostilities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termination', 'n.', 'That which ends or bounds; limit in space or extent;\n   bound; end; as, the termination of a line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termination', 'n.', 'End in time or existence; as, the termination of the\n   year, or of life; the termination of happiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termination', 'n.', 'End; conclusion; result.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termination', 'n.', 'Last purpose of design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termination', 'n.', 'A word; a term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termination', 'n.', 'The ending of a word; a final syllable or letter; the\n   part added to a stem in inflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminational', 'a.', 'Of or pertaining to termination; forming a\n   termination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminative', 'a.', 'Tending or serving to terminate; terminating;\n   determining; definitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminator', 'n.', 'One who, or that which, terminates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminator', 'n.', 'The dividing line between the illuminated and the\n   unilluminated part of the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminatory', 'a.', 'Terminative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termine', 'v. t.', 'To terminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminer', 'n.', 'A determining; as, in oyer and terminer. See Oyer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminism', 'n.', 'The doctrine held by the Terminists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminist', 'n.', 'One of a class of theologians who maintain that God has\n   fixed a certain term for the probation of individual persons, during\n   which period, and no longer, they have the offer to grace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminological', 'a.', 'Of or pertaining to terminology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminology', 'n.', 'The doctrine of terms; a theory of terms or\n   appellations; a treatise on terms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminology', 'n.', 'The terms actually used in any business, art, science,\n   or the like; nomenclature; technical terms; as, the terminology of\n   chemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termini', 'pl. ', 'of Terminus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminus', 'n.', 'Literally, a boundary; a border; a limit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminus', 'n.', 'The Roman divinity who presided over boundaries, whose\n   statue was properly a short pillar terminating in the bust of a man,\n   woman, satyr, or the like, but often merely a post or stone stuck in\n   the ground on a boundary line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminus', 'n.', 'Hence, any post or stone marking a boundary; a term. See\n   Term, 8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terminus', 'n.', 'Either end of a railroad line; also, the station house,\n   or the town or city, at that place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termites', 'pl. ', 'of Termite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termite', 'n.', 'Any one of numerous species of pseudoneoropterous insects\n   belonging to Termes and allied genera; -- called also white ant. See\n   Illust. of White ant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termless', 'a.', 'Having no term or end; unlimited; boundless; unending;\n   as, termless time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termless', 'a.', 'Inexpressible; indescribable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termly', 'a.', 'Occurring every term; as, a termly fee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termly', 'adv.', 'Term by term; every term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termonology', 'n.', 'Terminology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Termor', 'n.', 'Same as Termer, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tern', 'n.', 'Any one of numerous species of long-winged aquatic birds,\n   allied to the gulls, and belonging to Sterna and various allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tern', 'a.', 'Threefold; triple; consisting of three; ternate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tern', 'a.', 'That which consists of, or pertains to, three things or\n   numbers together; especially, a prize in a lottery resulting from the\n   favorable combination of three numbers in the drawing; also, the three\n   numbers themselves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ternary', 'a.', 'Proceeding by threes; consisting of three; as, the ternary\n   number was anciently esteemed a symbol of perfection, and held in great\n   veneration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ternary', 'a.', 'Containing, or consisting of, three different parts, as\n   elements, atoms, groups, or radicals, which are regarded as having\n   different functions or relations in the molecule; thus, sodic\n   hydroxide, NaOH, is a ternary compound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ternaries', 'pl. ', 'of Ternary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ternary', 'n.', 'A ternion; the number three; three things taken together;\n   a triad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ternate', 'a.', 'Having the parts arranged by threes; as, ternate branches,\n   leaves, or flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terneplate', 'a.', 'Thin iron sheets coated with an alloy of lead and tin;\n   -- so called because made up of three metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ternion', 'a.', 'The number three; three things together; a ternary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terpene', 'n.', 'Any one of a series of isomeric hydrocarbons of pleasant\n   aromatic odor, occurring especially in coniferous plants and\n   represented by oil of turpentine, but including also certain\n   hydrocarbons found in some essential oils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terpentic', 'a.', 'Terpenylic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terpenylic', 'a.', 'Of, pertaining to, or designating, an acid, C8H12O4\n   (called also terpentic acid), homologous with terebic acid, and\n   obtained as a white crystalline substance by the oxidation of oil of\n   turpentine with chromic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terpilene', 'n.', 'A polymeric form of terpene, resembling terbene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terpin', 'n.', 'A white crystalline substance regarded as a hydrate of oil\n   of turpentine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terpinol', 'n.', 'Any oil substance having a hyacinthine odor, obtained by\n   the action of acids on terpin, and regarded as a related hydrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terpsichore', 'n.', 'The Muse who presided over the choral song and the\n   dance, especially the latter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terpsichorean', 'a.', 'Of or pertaining to Terpsichore; of or pertaining to\n   dancing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terra', 'n.', 'The earth; earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrace', 'v.', 'A raised level space, shelf, or platform of earth,\n   supported on one or more sides by a wall, a bank of tuft, or the like,\n   whether designed for use or pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrace', 'v.', 'A balcony, especially a large and uncovered one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrace', 'v.', 'A flat roof to a house; as, the buildings of the Oriental\n   nations are covered with terraces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrace', 'v.', 'A street, or a row of houses, on a bank or the side of a\n   hill; hence, any street, or row of houses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrace', 'v.', 'A level plain, usually with a steep front, bordering a\n   river, a lake, or sometimes the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terraced', 'imp. & p. p.', 'of Terrace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terracing', 'p. pr. & vb. n.', 'of Terrace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrace', 'v. t.', 'To form into a terrace or terraces; to furnish with a\n   terrace or terraces, as, to terrace a garden, or a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terraculture', 'n.', 'Cultivation on the earth; agriculture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrane', 'n.', 'A group of rocks having a common age or origin; -- nearly\n   equivalent to formation, but used somewhat less comprehensively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrapin', 'n.', 'Any one of numerous species of tortoises living in fresh\n   and brackish waters. Many of them are valued for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terraqueous', 'a.', 'Consisting of land and water; as, the earth is a\n   terraqueous globe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrar', 'n.', 'See 2d Terrier, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terras', 'n.', 'See /rass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terreen', 'n.', 'See Turren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terreity', 'n.', 'Quality of being earthy; earthiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrel', 'n.', 'A spherical magnet so placed that its poles, equator, etc.,\n   correspond to those of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terremote', 'n.', 'An earthquake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrene', 'n.', 'A tureen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrene', 'a.', 'Of or pertaining to the earth; earthy; as, terrene\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrene', 'a.', 'Earthy; terrestrial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrene', 'n.', 'The earths surface; the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrene', 'n.', 'The surface of the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrenity', 'n.', 'Earthiness; worldliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terreous', 'a.', 'Consisting of earth; earthy; as, terreous substances;\n   terreous particles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terreplein', 'n.', 'The top, platform, or horizontal surface, of a rampart,\n   on which the cannon are placed. See Illust. of Casemate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrestre', 'a.', 'Terrestrial; earthly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrestrial', 'a.', 'Of or pertaining to the earth; existing on the earth;\n   earthly; as, terrestrial animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrestrial', 'a.', 'Representing, or consisting of, the earth; as, a\n   terrestrial globe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrestrial', 'a.', 'Of or pertaining to the world, or to the present\n   state; sublunary; mundane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrestrial', 'a.', 'Consisting of land, in distinction from water;\n   belonging to, or inhabiting, the land or ground, in distinction from\n   trees, water, or the like; as, terrestrial serpents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrestrial', 'a.', 'Adapted for the observation of objects on land and on\n   the earth; as, a terrestrial telescope, in distinction from an\n   astronomical telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrestrial', 'n.', 'An inhabitant of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrestrify', 'v. t.', 'To convert or reduce into a condition like that of\n   the earth; to make earthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrestrious', 'a.', 'Terrestrial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terret', 'n.', 'One of the rings on the top of the saddle of a harness,\n   through which the reins pass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terre-tenant', 'n.', 'One who has the actual possession of land; the\n   occupant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terre-verte', 'n.', 'An olive-green earth used as a pigment. See\n   Glauconite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrible', 'a.', 'Adapted or likely to excite terror, awe, or dread;\n   dreadful; formidable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrible', 'a.', 'Excessive; extreme; severe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terricolae', 'n. pl.', 'A division of annelids including the common\n   earthworms and allied species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrienniak', 'n.', 'The arctic fox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrier', 'n.', 'An auger or borer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrier', 'n.', 'One of a breed of small dogs, which includes several\n   distinct subbreeds, some of which, such as the Skye terrier and\n   Yorkshire terrier, have long hair and drooping ears, while others, at\n   the English and the black-and-tan terriers, have short, close, smooth\n   hair and upright ears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrier', 'n.', 'Formerly, a collection of acknowledgments of the vassals\n   or tenants of a lordship, containing the rents and services they owed\n   to the lord, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrier', 'n.', 'In modern usage, a book or roll in which the lands of\n   private persons or corporations are described by their site,\n   boundaries, number of acres, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrific', 'a.', 'Causing terror; adapted to excite great fear or dread;\n   terrible; as, a terrific form; a terrific sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrifical', 'a.', 'Terrific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrifically', 'adv.', 'In a terrific manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrified', 'imp. & p. p.', 'of Terrify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrifying', 'p. pr. & vb. n.', 'of Terrify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrify', 'v. t.', 'To make terrible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrify', 'v. t.', 'To alarm or shock with fear; to frighten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrigenous', 'a.', 'Earthborn; produced by the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Territorial', 'a.', 'Of or pertaining to territory or land; as, territorial\n   limits; territorial jurisdiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Territorial', 'a.', 'Limited to a certain district; as, right may be\n   personal or territorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Territorial', 'a.', 'Of or pertaining to all or any of the Territories of\n   the United States, or to any district similarly organized elsewhere;\n   as, Territorial governments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Territorialized', 'imp. & p. p.', 'of Territorialize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Territorializing', 'p. pr. & vb. n.', 'of Territorialize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Territorialize', 'v. t.', 'To enlarge by extension of territory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Territorialize', 'v. t.', 'To reduce to the condition of a territory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Territorially', 'adv.', 'In regard to territory; by means of territory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Territoried', 'a.', 'Possessed of territory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Territories', 'pl. ', 'of Territory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Territory', 'n.', 'A large extent or tract of land; a region; a country; a\n   district.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Territory', 'n.', 'The extent of land belonging to, or under the dominion\n   of, a prince, state, or other form of government; often, a tract of\n   land lying at a distance from the parent country or from the seat of\n   government; as, the territory of a State; the territories of the East\n   India Company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Territory', 'n.', 'In the United States, a portion of the country not\n   included within the limits of any State, and not yet admitted as a\n   State into the Union, but organized with a separate legislature, under\n   a Territorial governor and other officers appointed by the President\n   and Senate of the United States. In Canada, a similarly organized\n   portion of the country not yet formed into a Province.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terror', 'n.', 'Extreme fear; fear that agitates body and mind; violent\n   dread; fright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terror', 'n.', 'That which excites dread; a cause of extreme fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrorism', 'n.', 'The act of terrorizing, or state of being terrorized; a\n   mode of government by terror or intimidation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrorism', 'n.', 'The practise of coercing governments to accede to\n   political demands by committing violence on civilian targets; any\n   similar use of violence to achieve goals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrorist', 'n.', 'One who governs by terrorism or intimidation;\n   specifically, an agent or partisan of the revolutionary tribunal during\n   the Reign of Terror in France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrorize', 'v. t.', 'To impress with terror; to coerce by intimidation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrorless', 'a.', 'Free from terror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terry', 'n.', 'A kind of heavy colored fabric, either all silk, or silk and\n   worsted, or silk and cotton, often called terry velvet, used for\n   upholstery and trimmings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tersanctus', 'n.', 'An ancient ascription of praise (containing the word\n   \"Holy\" -- in its Latin form, \"Sanctus\" -- thrice repeated), used in the\n   Mass of the Roman Catholic Church and before the prayer of consecration\n   in the communion service of the Church of England and the Protestant\n   Episcopal Church. Cf. Trisagion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terse', 'superl.', 'Appearing as if rubbed or wiped off; rubbed; smooth;\n   polished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terse', 'superl.', 'Refined; accomplished; -- said of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terse', 'superl.', 'Elegantly concise; free of superfluous words; polished\n   to smoothness; as, terse language; a terse style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tersulphide', 'n.', 'A trisulphide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tersulphuret', 'n.', 'A trisulphide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ter-tenant', 'n.', 'See Terre-tenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tertial', 'a. & n.', 'Same as Tertiary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tertian', 'a.', 'Occurring every third day; as, a tertian fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tertian', 'n.', 'A disease, especially an intermittent fever, which returns\n   every third day, reckoning inclusively, or in which the intermission\n   lasts one day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tertian', 'n.', 'A liquid measure formerly used for wine, equal to seventy\n   imperial, or eighty-four wine, gallons, being one third of a tun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tertiary', 'a.', 'Being of the third formation, order, or rank; third; as,\n   a tertiary use of a word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tertiary', 'a.', 'Possessing some quality in the third degree; having been\n   subjected to the substitution of three atoms or radicals; as, a\n   tertiary alcohol, amine, or salt. Cf. Primary, and Secondary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tertiary', 'a.', 'Later than, or subsequent to, the Secondary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tertiary', 'a.', 'Growing on the innermost joint of a birds wing; tertial;\n   -- said of quills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tertiaries', 'pl. ', 'of Tertiary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tertiary', 'n.', 'A member of the Third Order in any monastic system; as,\n   the Franciscan tertiaries; the Dominican tertiaries; the Carmelite\n   tertiaries. See Third Order, under Third.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tertiary', 'n.', 'The Tertiary era, period, or formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tertiary', 'n.', 'One of the quill feathers which are borne upon the basal\n   joint of the wing of a bird. See Illust. of Bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tertiate', 'v. t.', 'To do or perform for the third time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tertiate', 'v. t.', 'To examine, as the thickness of the metal at the\n   muzzle of a gun; or, in general, to examine the thickness of, as\n   ordnance, in order to ascertain its strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terutero', 'n.', 'The South American lapwing (Vanellus Cayennensis). Its\n   wings are furnished with short spurs. Called also Cayenne lapwing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terza rima', '', 'A peculiar and complicated system of versification,\n   borrowed by the early Italian poets from the Troubadours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terzetto', 'n.', 'A composition in three voice parts; a vocal (rarely an\n   instrumental) trio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tesselar', 'a.', 'Formed of tesserae, as a mosaic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tessellata', 'n. pl.', 'A division of Crinoidea including numerous fossil\n   species in which the body is covered with tessellated plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tessellated', 'imp. & p. p.', 'of Tessellate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tessellating', 'p. pr. & vb. n.', 'of Tessellate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tessellate', 'v. t.', 'To form into squares or checkers; to lay with\n   checkered work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tessellate', 'a.', 'Tessellated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tessellated', 'a.', 'Formed of little squares, as mosaic work; checkered;\n   as, a tessellated pavement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tessellated', 'a.', 'Marked like a checkerboard; as, a tessellated leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tessellation', 'n.', 'The act of tessellating; also, the mosaic work so\n   formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tesserae', 'pl. ', 'of Tessera');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tessera', 'n.', 'A small piece of marble, glass, earthenware, or the like,\n   having a square, or nearly square, face, used by the ancients for\n   mosaic, as for making pavements, for ornamenting walls, and like\n   purposes; also, a similar piece of ivory, bone, wood, etc., used as a\n   ticket of admission to theaters, or as a certificate for successful\n   gladiators, and as a token for various other purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tesseraic', 'a.', 'Diversified by squares; done in mosaic; tessellated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tesseral', 'a.', 'Of, pertaining to, or containing, tesserae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tesseral', 'a.', 'Isometric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tessular', 'a.', 'Tesseral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Test', 'n.', 'A cupel or cupelling hearth in which precious metals are\n   melted for trial and refinement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Test', 'n.', 'Examination or trial by the cupel; hence, any critical\n   examination or decisive trial; as, to put a mans assertions to a test.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Test', 'n.', 'Means of trial; as, absence is a test of love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Test', 'n.', 'That with which anything is compared for proof of its\n   genuineness; a touchstone; a standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Test', 'n.', 'Discriminative characteristic; standard of judgment; ground\n   of admission or exclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Test', 'n.', 'Judgment; distinction; discrimination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Test', 'n.', 'A reaction employed to recognize or distinguish any\n   particular substance or constituent of a compound, as the production of\n   some characteristic precipitate; also, the reagent employed to produce\n   such reaction; thus, the ordinary test for sulphuric acid is the\n   production of a white insoluble precipitate of barium sulphate by means\n   of some soluble barium salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tested', 'imp. & p. p.', 'of Test');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testing', 'p. pr. & vb. n.', 'of Test');
  INSERT INTO words (word, word_type, description) VALUES  (    'Test', 'v. t.', 'To refine, as gold or silver, in a test, or cupel; to\n   subject to cupellation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Test', 'v. t.', 'To put to the proof; to prove the truth, genuineness, or\n   quality of by experiment, or by some principle or standard; to try; as,\n   to test the soundness of a principle; to test the validity of an\n   argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Test', 'v. t.', 'To examine or try, as by the use of some reagent; as, to\n   test a solution by litmus paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Test', 'n.', 'A witness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Test', 'v. i.', 'To make a testament, or will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tests', 'pl. ', 'of Testa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testae', 'pl. ', 'of Testa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Test', 'n.', 'Alt. of Testa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testa', 'n.', 'The external hard or firm covering of many invertebrate\n   animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testa', 'n.', 'The outer integument of a seed; the episperm, or spermoderm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testable', 'a.', 'Capable of being tested or proved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testable', 'a.', 'Capable of being devised, or given by will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testacea', 'n. pl.', 'Invertebrate animals covered with shells, especially\n   mollusks; shellfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testacean', 'n.', 'Onr of the Testacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testaceography', 'n.', 'The science which treats of testaceans, or\n   shellfish; the description of shellfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testaceology', 'n.', 'The science of testaceous mollusks; conchology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testaceous', 'a.', 'Of or pertaining to shells; consisted of a hard shell,\n   or having a hard shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testaceous', 'a.', 'Having a dull red brick color or a brownish yellow\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testacy', 'n.', 'The state or circumstance of being testate, or of leaving\n   a valid will, or testament, at death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testament', 'n.', 'A solemn, authentic instrument in writing, by which a\n   person declares his will as to disposal of his estate and effects after\n   his death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testament', 'n.', 'One of the two distinct revelations of Gods purposes\n   toward man; a covenant; also, one of the two general divisions of the\n   canonical books of the sacred Scriptures, in which the covenants are\n   respectively revealed; as, the Old Testament; the New Testament; --\n   often limited, in colloquial language, to the latter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testamental', 'a.', 'Of or pertaining to a testament; testamentary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testamentary', 'a.', 'Of or pertaining to a will, or testament; as, letters\n   testamentary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testamentary', 'a.', 'Bequeathed by will; given by testament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testamentary', 'a.', 'Done, appointed by, or founded on, a testament, or\n   will; as, a testamentary guardian of a minor, who may be appointed by\n   the will of a father to act in that capacity until the child becomes of\n   age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testamentation', 'n.', 'The act or power of giving by testament, or will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testamentize', 'v. i.', 'To make a will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testamur', 'n.', 'A certificate of merit or proficiency; -- so called from\n   the Latin words, Ita testamur, with which it commences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testate', 'a.', 'Having made and left a will; as, a person is said to die\n   testate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testate', 'n.', 'One who leaves a valid will at death; a testate person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testation', 'n.', 'A witnessing or witness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testator', 'n.', 'A man who makes and leaves a will, or testament, at\n   death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testatrix', 'n.', 'A woman who makes and leaves a will at death; a female\n   testator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teste', 'n.', 'A witness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teste', 'n.', 'The witnessing or concluding clause, duty attached; -- said\n   of a writ, deed, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tester', 'n.', 'A headpiece; a helmet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tester', 'n.', 'A flat canopy, as over a pulpit or tomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tester', 'n.', 'A canopy over a bed, supported by the bedposts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tester', 'n.', 'An old French silver coin, originally of the value of about\n   eighteen pence, subsequently reduced to ninepence, and later to\n   sixpence, sterling. Hence, in modern English slang, a sixpence; --\n   often contracted to tizzy. Called also teston.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testern', 'n.', 'A sixpence; a tester.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testern', 'v. t.', 'To present with a tester.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testes', 'n.', 'pl. of Teste, or of Testis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testicardines', 'n. pl.', 'A division of brachiopods including those which\n   have a calcareous shell furnished with a hinge and hinge teeth.\n   Terebratula and Spirifer are examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testicle', 'n.', 'One of the essential male genital glands which secrete\n   the semen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testicond', 'a.', 'Having the testicles naturally concealed, as in the case\n   of the cetaceans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testicular', 'a.', 'Of or pertaining to the testicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testiculate', 'a.', 'Shaped like a testicle, ovate and solid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testiculate', 'a.', 'Having two tubers resembling testicles in form, as\n   some species of orchis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testiere', 'n.', 'A piece of plate armor for the head of a war horse; a\n   tester.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testif', 'a.', 'Testy; headstrong; obstinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testification', 'n.', 'The act of testifying, or giving testimony or\n   evidence; as, a direct testification of our homage to God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testificator', 'n.', 'A testifier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testifier', 'n.', 'One who testifies; one who gives testimony, or bears\n   witness to prove anything; a witness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testified', 'imp. & p. p.', 'of Testify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testifying', 'p. pr. & vb. n.', 'of Testify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testify', 'v. i.', 'To make a solemn declaration, verbal or written, to\n   establish some fact; to give testimony for the purpose of communicating\n   to others a knowledge of something not known to them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testify', 'v. i.', 'To make a solemn declaration under oath or affirmation,\n   for the purpose of establishing, or making proof of, some fact to a\n   court; to give testimony in a cause depending before a tribunal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testify', 'v. i.', 'To declare a charge; to protest; to give information;\n   to bear witness; -- with against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testify', 'v. t.', 'To bear witness to; to support the truth of by\n   testimony; to affirm or declare solemny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testify', 'v. t.', 'To affirm or declare under oath or affirmation before a\n   tribunal, in order to prove some fact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testify', 'adv.', 'In a testy manner; fretfully; peevishly; with petulance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testimonial', 'a.', 'A writing or certificate which bears testimony in\n   favor of ones character, good conduct, ability, etc., or of the value\n   of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testimonial', 'a.', 'Something, as money or plate, presented to a preson as\n   a token of respect, or of obligation for services rendered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testimonial', 'a.', 'Relating to, or containing, testimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testimonies', 'pl. ', 'of Testimony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testimony', 'n.', 'A solemn declaration or affirmation made for the purpose\n   of establishing or proving some fact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testimony', 'n.', 'Affirmation; declaration; as, these doctrines are\n   supported by the uniform testimony of the fathers; the belief of past\n   facts must depend on the evidence of human testimony, or the testimony\n   of historians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testimony', 'n.', 'Open attestation; profession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testimony', 'n.', 'Witness; evidence; proof of some fact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testimony', 'n.', 'The two tables of the law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testimony', 'n.', 'Hence, the whole divine revelation; the sacre/\n   Scriptures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testimony', 'v. t.', 'To witness; to attest; to prove by testimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testiness', 'n.', 'The quality or state of being testy; fretfulness;\n   petulance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testing', 'n.', 'The act of testing or proving; trial; proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testing', 'n.', 'The operation of refining gold or silver in a test, or\n   cupel; cupellation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testes', 'pl. ', 'of Testis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testis', 'n.', 'A testicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teston', 'n.', 'A tester; a sixpence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testone', 'n.', 'A silver coin of Portugal, worth about sixpence sterling,\n   or about eleven cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testoon', 'n.', 'An Italian silver coin. The testoon of Rome is worth 1s.\n   3d. sterling, or about thirty cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testudinal', 'a.', 'Of, pertaining to, or resembling, a tortoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testudinarious', 'a.', 'Of or pertaining to the shell of a tortoise;\n   resembling a tortoise shell; having the color or markings of a tortoise\n   shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testudinata', 'n. pl.', 'An order of reptiles which includes the turtles\n   and tortoises. The body is covered by a shell consisting of an upper or\n   dorsal shell, called the carapace, and a lower or ventral shell, called\n   the plastron, each of which consists of several plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testudinate', 'a.', 'Alt. of Testudinated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testudinated', 'a.', 'Resembling a tortoise shell in appearance or\n   structure; roofed; arched; vaulted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testudineous', 'a.', 'Resembling the shell of a tortoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testudines', 'pl. ', 'of Testudo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testudo', 'n.', 'A genus of tortoises which formerly included a large\n   number of diverse forms, but is now restricted to certain terrestrial\n   species, such as the European land tortoise (Testudo Graeca) and the\n   gopher of the Southern United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testudo', 'n.', 'A cover or screen which a body of troops formed with their\n   shields or targets, by holding them over their heads when standing\n   close to each other. This cover resembled the back of a tortoise, and\n   served to shelter the men from darts, stones, and other missiles. A\n   similar defense was sometimes formed of boards, and moved on wheels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testudo', 'n.', 'A kind of musical instrument. a species of lyre; -- so\n   called in allusion to the lyre of Mercury, fabled to have been made of\n   the shell of a tortoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Testy', 'superl.', 'Fretful; peevish; petulant; easily irritated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetanic', 'a.', 'Of or pertaining to tetanus; having the character of\n   tetanus; as, a tetanic state; tetanic contraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetanic', 'a.', 'Producing, or tending to produce, tetanus, or tonic\n   contraction of the muscles; as, a tetanic remedy. See Tetanic, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetanic', 'n.', 'A substance (notably nux vomica, strychnine, and brucine)\n   which, either as a remedy or a poison, acts primarily on the spinal\n   cord, and which, when taken in comparatively large quantity, produces\n   tetanic spasms or convulsions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetanin', 'n.', 'A poisonous base (ptomaine) formed in meat broth through\n   the agency of a peculiar microbe from the wound of a person who has\n   died of tetanus; -- so called because it produces tetanus as one of its\n   prominent effects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetanization', 'n.', 'The production or condition of tetanus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetanize', 'v. t.', 'To throw, as a muscle, into a state of permanent\n   contraction; to cause tetanus in. See Tetanus, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetanoid', 'a.', 'Resembling tetanus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetanomotor', 'n.', 'An instrument from tetanizing a muscle by irritating\n   its nerve by successive mechanical shocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetanus', 'n.', 'A painful and usually fatal disease, resulting generally\n   from a wound, and having as its principal symptom persistent spasm of\n   the voluntary muscles. When the muscles of the lower jaw are affected,\n   it is called locked-jaw, or lickjaw, and it takes various names from\n   the various incurvations of the body resulting from the spasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetanus', 'n.', 'That condition of a muscle in which it is in a state of\n   continued vibratory contraction, as when stimulated by a series of\n   induction shocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetany', 'n.', 'A morbid condition resembling tetanus, but distinguished\n   from it by being less severe and having intermittent spasms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetard', 'n.', 'A gobioid fish (Eleotris gyrinus) of the Southern United\n   States; -- called also sleeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetartohedral', 'a.', 'Having one fourth the number of planes which are\n   requisite to complete symmetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetartohedrism', 'n.', 'The property of being tetartohedral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetaug', 'n.', 'See Tautog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetchiness', 'n.', 'See Techiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetchy', 'a.', 'See Techy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tete', 'n.', 'A kind of wig; false hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tete-a-tete', 'n.', 'Private conversation; familiar interview or conference\n   of two persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tete-a-tete', 'n.', 'A short sofa intended to accomodate two persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tete-a-tete', 'a.', 'Private; confidential; familiar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tete-a-tete', 'adv.', 'Face to face; privately or confidentially;\n   familiarly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetes-de-pont', 'pl. ', 'of Tete-de-pont');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tete-de-pont', 'n.', 'A work thrown up at the end of a bridge nearest the\n   enemy, for covering the communications across a river; a bridgehead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetel', 'n.', 'A large African antelope (Alcelaphus tora). It has widely\n   divergent, strongly ringed horns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tether', 'n.', 'A long rope or chain by which an animal is fastened, as to\n   a stake, so that it can range or feed only within certain limits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tethered', 'imp. & p. p.', 'of Tether');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tethering', 'p. pr. & vb. n.', 'of Tether');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tether', 'v. t.', 'To confine, as an animal, with a long rope or chain, as\n   for feeding within certain limits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tethydan', 'n.', 'A tunicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tethyodea', 'n. pl.', 'A division of Tunicata including the common attached\n   ascidians, both simple and compound. Called also Tethioidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tethys', 'n.', 'A genus of a large naked mollusks having a very large,\n   broad, fringed cephalic disk, and branched dorsal gills. Some of the\n   species become a foot long and are brilliantly colored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetra-', '', 'A combining form or prefix signifying four, as in tetrabasic,\n   tetrapetalous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetra-', '', 'A combining form (also used adjectively) denoting four\n   proportional or combining parts of the substance or ingredient denoted\n   by the term to which it is prefixed, as in tetra-chloride, tetroxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrabasic', 'a.', 'Capable of neutralizing four molecules of a monacid\n   base; having four hydrogen atoms capable of replacement by bases;\n   quadribasic; -- said of certain acids; thus, normal silicic acid,\n   Si(OH)4, is a tetrabasic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetraboric', 'a.', 'Same as Pyroboric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrabranchiata', 'n. pl.', 'An order of Cephalopoda having four gills.\n   Among living species it includes only the pearly nautilus. Numerous\n   genera and species are found in the fossil state, such as Ammonites,\n   Baculites, Orthoceras, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrabranchiate', 'a.', 'Of or pertaining to the Tetrabranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrabranchiate', 'n.', 'One of the Tetrabranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetracarpel', 'a.', 'Composed of four carpels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrachord', 'n.', 'A scale series of four sounds, of which the extremes,\n   or first and last, constituted a fourth. These extremes were immutable;\n   the two middle sounds were changeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrachotomous', 'a.', 'Having a division by fours; separated into four\n   parts or series, or into series of fours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetracid', 'a.', 'Capable of neutralizing four molecules of a monobasic\n   acid; having four hydrogen atoms capable of replacement ba acids or\n   acid atoms; -- said of certain bases; thus, erythrine, C4H6(OH)4, is a\n   tetracid alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetracoccous', 'a.', 'Having four cocci, or carpels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetracolon', 'n.', 'A stanza or division in lyric poetry, consisting of\n   four verses or lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetracoralla', 'n. pl.', 'Same as Rugosa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetractinellid', 'n.', 'Any species of sponge of the division\n   Tetractinellida. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetractinellida', 'n. pl.', 'A division of Spongiae in which the spicules\n   are siliceous and have four branches diverging at right angles. Called\n   also Tetractinellinae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrad', 'n.', 'The number four; a collection of four things; a quaternion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrad', 'n.', 'A tetravalent or quadrivalent atom or radical; as, carbon\n   is a tetrad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetradactyl', 'a.', 'Alt. of Tetradactyle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetradactyle', 'a.', 'Tetradactylous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetradactylous', 'a.', 'Having, or characterized by, four digits to the\n   foot or hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetradecane', 'n.', 'A light oily hydrocarbon, C14H30, of the marsh-gas\n   series; -- so called from the fourteen carbon atoms in the molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetradecapoda', 'n. pl.', 'Same as Arthrostraca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetradic', 'a.', 'Of or pertaining to a tetrad; possessing or having the\n   characteristics of a tetrad; as, a carbon is a tetradic element.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetradite', 'n.', 'A person in some way remarkable with regard to the\n   number four, as one born on the fourth day of the month, or one who\n   reverenced four persons in the Godhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetradon', 'n.', 'See Tetrodon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetradont', 'a. & n.', 'See Tetrodont.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetradrachm', 'n.', 'Alt. of Tetradrachma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetradrachma', 'n.', 'A silver coin among the ancient Greeks, of the value\n   of four drachms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetradymite', 'n.', 'A telluride of bismuth. It is of a pale steel-gray\n   color and metallic luster, and usually occurs in foliated masses.\n   Called also telluric bismuth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetradynamia', 'n. pl.', 'A Linnaean class of plants having six stamens,\n   four of which are longer than the others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetradynamian', 'n.', 'A plant of the order Tetradynamia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetradynamian', 'a.', 'Alt. of Tetradynamous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetradynamous', 'a.', 'Belonging to the order Tetradynamia; having six\n   stamens, four of which are uniformly longer than the others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetragon', 'n.', 'A plane figure having four sides and angles; a\n   quadrangle, as a square, a rhombus, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetragon', 'n.', 'An aspect of two planets with regard to the earth when\n   they are distant from each other ninety degrees, or the fourth of a\n   circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetragonal', 'a.', 'Of or pertaining to a tetragon; having four angles or\n   sides; thus, the square, the parallelogram, the rhombus, and the\n   trapezium are tetragonal fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetragonal', 'a.', 'Having four prominent longitudinal angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetragonal', 'a.', 'Designating, or belonging to, a certain system of\n   crystallization; dimetric. See Tetragonal system, under\n   Crystallization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetragrammaton', 'n.', 'The mystic number four, which was often symbolized\n   to represent the Deity, whose name was expressed by four letters among\n   some ancient nations; as, the Hebrew JeHoVaH, Greek qeo`s, Latin deus,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetragynia', 'n. pl.', 'A Linnaean order of plants having four styles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetragynian', 'a.', 'Alt. of Tetragynous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetragynous', 'a.', 'Belonging to the order Tetragynia; having four styles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrahedral', 'a.', 'Having, or composed of, four sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrahedral', 'a.', 'Having the form of the regular tetrahedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrahedral', 'a.', 'Pertaining or related to a tetrahedron, or to the\n   system of hemihedral forms to which the tetrahedron belongs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrahedrally', 'adv.', 'In a tetrahedral manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrahedrite', 'n.', 'A sulphide of antimony and copper, with small\n   quantities of other metals. It is a very common ore of copper, and some\n   varieties yield a considerable presentage of silver. Called also gray\n   copper ore, fahlore, and panabase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrahedron', 'n.', 'A solid figure inclosed or bounded by four triangles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrahexahedral', 'a.', 'Pertaining to a tetrahexahedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrahexahedron', 'n.', 'A solid in the isometric system, bounded by\n   twenty-four equal triangular faces, four corresponding to each face of\n   the cube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrakishexahedron', 'n.', 'A tetrahexahedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrakosane', 'n.', 'A hydrocarbon, C24H50, resembling paraffin, and like\n   it belonging to the marsh-gas series; -- so called from having\n   twenty-four atoms of carbon in the molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetralogy', 'n.', 'A group or series of four dramatic pieces, three\n   tragedies and one satyric, or comic, piece (or sometimes four\n   tragedies), represented consequently on the Attic stage at the\n   Dionysiac festival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetramera', 'n. pl.', 'A division of Coleoptera having, apparently, only\n   four tarsal joints, one joint being rudimentary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetramerous', 'a.', 'Having the parts arranged in sets of four; as, a\n   tetramerous flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetramerous', 'a.', 'Having four joints in each of the tarsi; -- said of\n   certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrameter', 'n.', 'A verse or line consisting of four measures, that is,\n   in iambic, trochaic, and anapestic verse, of eight feet; in other kinds\n   of verse, of four feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetramethylene', 'n.', 'A hypothetical hydrocarbon, C4H8, analogous to\n   trimethylene, and regarded as the base of well-known series or\n   derivatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetramethylene', 'n.', 'Sometimes, an isomeric radical used to designate\n   certain compounds which are really related to butylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetramorph', 'n.', 'The union of the four attributes of the Evangelists in\n   one figure, which is represented as winged, and standing on winged\n   fiery wheels, the wings being covered with eyes. The representations of\n   it are evidently suggested by the vision of Ezekiel (ch. i.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrandria', 'n. pl.', 'A Linnaean class of plants having four stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrandrian', 'a.', 'Alt. of Tetrandrous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrandrous', 'a.', 'Belonging to the class Tetrandria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetraonid', 'n.', 'A bird belonging to the tribe of which the genus Tetrao\n   is the type, as the grouse, partridge, quail, and the like. Used also\n   adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrapetalous', 'a.', 'Containing four distinct petals, or flower leaves;\n   as, a tetrapetalous corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrapharmacom', 'n.', 'Alt. of Tetrapharmacum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrapharmacum', 'n.', 'A combination of wax, resin, lard, and pitch,\n   composing an ointment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetraphenol', 'n.', 'Furfuran.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetraphyllous', 'a.', 'Having four leaves; consisting of four distinct\n   leaves or leaflets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrapla', 'sing.', 'A Bible consisting of four different Greek versions\n   arranged in four columns by Origen; hence, any version in four\n   languages or four columns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetraneumona', 'n. pl.', 'A division of Arachnida including those spiders\n   which have four lungs, or pulmonary sacs. It includes the bird spiders\n   (Mygale) and the trapdoor spiders. See Mygale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrapnuemonian', 'n.', 'One of the Tetrapneumona.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrapod', 'n.', 'An insect characterized by having but four perfect legs,\n   as certain of the butterflies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrapody', 'n.', 'A set of four feet; a measure or distance of four feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrapteran', 'n.', 'An insect having four wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrapterous', 'a.', 'Having four wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetraptote', 'n.', 'A noun that has four cases only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrarch', 'a.', 'A Roman governor of the fourth part of a province; hence,\n   any subordinate or dependent prince; also, a petty king or sovereign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrarch', 'a.', 'Four.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrarchate', 'n.', 'A tetrarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrarchical', 'a.', 'Of or pertaining to a tetrarch or tetrarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrarchies', 'pl. ', 'of Tetrarchy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrarchy', 'n.', 'The district under a Roman tetrarch; the office or\n   jurisdiction of a tetrarch; a tetrarchate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetraschistic', 'a.', 'Characterized by division into four parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrasepalous', 'a.', 'Having four sepals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetraspaston', 'n.', 'A machine in which four pulleys act together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetraspermous', 'a.', 'Having four seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetraspore', 'n.', 'A nonsexual spore, one of a group of four regularly\n   occurring in red seaweeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrastich', 'n.', 'A stanza, epigram, or poem, consisting of four verses\n   or lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrastyle', 'a.', 'Having four columns in front; -- said of a temple,\n   portico, or colonnade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrastyle', 'n.', 'A tetrastyle building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrasyllabic', 'a.', 'Alt. of Tetrasyllabical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrasyllabical', 'a.', 'Consisting of, or having, four syllables;\n   quadrisyllabic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Terrasyllable', 'n.', 'A word consisting of four syllables; a\n   quadrisyllable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrathecal', 'a.', 'Having four loculaments, or thecae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrathionate', 'n.', 'A salt of tetrathionic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrathionic', 'a.', 'Of, pertaining to, or designating, a thionic\n   derivative, H/S/O/, of sulphuric acid, obtained as a colorless,\n   odorless liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetratomic', 'a.', 'Consisting of four atoms; having four atoms in the\n   molecule, as phosphorus and arsenic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetratomic', 'a.', 'Having a valence of four; quadrivalent; tetravalent;\n   sometimes, in a specific sense, having four hydroxyl groups, whether\n   acid or basic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetravalence', 'n.', 'The quality or state of being tetravalent;\n   quadrivalence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetravalent', 'a.', 'Having a valence of four; tetratomic; quadrivalent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetraxile', 'a.', 'Having four branches diverging at right angles; -- said\n   of certain spicules of sponges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrazo-', 'a.', 'A combining form (also used adjectively), designating any\n   one of a series of double derivatives of the azo and diazo compounds\n   containing four atoms of nitrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrazone', 'n.', 'Any one of a certain series of basic compounds\n   containing a chain of four nitrogen atoms; for example, ethyl\n   tetrazone, (C2H5)2N.N2.N(C2H5)2, a colorless liquid having an odor of\n   leeks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetric', 'a.', 'Alt. of Tetrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrical', 'a.', 'Forward; perverse; harsh; sour; rugged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetricity', 'n.', 'Crabbedness; perverseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetricous', 'a.', 'Tetric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrinic', 'a.', 'Of, pertaining to, or designating, a complex ketonic\n   acid, C5H6O3, obtained as a white crystalline substance; -- so called\n   because once supposed to contain a peculiar radical of four carbon\n   atoms. Called also acetyl-acrylic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrodon', 'n.', 'Any one of numerous species of plectognath fishes\n   belonging to Tetrodon and allied genera. Each jaw is furnished with two\n   large, thick, beaklike, bony teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrodont', 'a.', 'Of or pertaining to the tetrodons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrodont', 'n.', 'A tetrodon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrol', 'n.', 'A hypothetical hydrocarbon, C4H4, analogous to benzene; --\n   so called from the four carbon atoms in the molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrolic', 'a.', 'Of, pertaining to, or designating, an acid, C3H3.CO2H, of\n   the acetylene series, homologous with propiolic acid, obtained as a\n   white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetroxide', 'n.', 'An oxide having four atoms of oxygen in the molecule; a\n   quadroxide; as, osmium tetroxide, OsO/.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetryl', 'n.', 'Butyl; -- so called from the four carbon atoms in the\n   molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetrylene', 'n.', 'Butylene; -- so called from the four carbon atoms in the\n   molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetter', 'n.', 'A vesicular disease of the skin; herpes. See Herpes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tettered', 'imp. & p. p.', 'of Tetter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tettering', 'p. pr. & vb. n.', 'of Tetter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetter', 'v. t.', 'To affect with tetter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetterous', 'a.', 'Having the character of, or pertaining to, tetter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetter-totter', 'n.', 'A certain game of children; seesaw; -- called also\n   titter-totter, and titter-cum-totter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetterwort', 'n.', 'A plant used as a remedy for tetter, -- in England the\n   calendine, in America the bloodroot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tettigonian', 'n.', 'Any one of numerous species of Hemiptera belonging to\n   Tettigonia and allied genera; a leaf hopper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tettish', 'a.', 'Captious; testy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tettix', 'n.', 'The cicada.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tettix', 'n.', 'A genus of small grasshoppers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tetty', 'a.', 'Testy; irritable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teufit', 'n.', 'The lapwing; -- called also teuchit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teuk', 'n.', 'The redshank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teutons', 'pl. ', 'of Teuton');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teutones', 'pl. ', 'of Teuton');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teuton', 'n.', 'One of an ancient German tribe; later, a name applied to\n   any member of the Germanic race in Europe; now used to designate a\n   German, Dutchman, Scandinavian, etc., in distinction from a Celt or one\n   of a Latin race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teuton', 'n.', 'A member of the Teutonic branch of the Indo-European, or\n   Aryan, family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teutonic', 'a.', 'Of or pertaining to the Teutons, esp. the ancient\n   Teutons; Germanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teutonic', 'a.', 'Of or pertaining to any of the Teutonic languages, or the\n   peoples who speak these languages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teutonic', 'n.', 'The language of the ancient Germans; the Teutonic\n   languages, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teutonicism', 'n.', 'A mode of speech peculiar to the Teutons; a Teutonic\n   idiom, phrase, or expression; a Teutonic mode or custom; a Germanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tewed', 'imp. & p. p.', 'of Tew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tewing', 'p. pr. & vb. n.', 'of Tew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tew', 'v.', 'To prepare by beating or working, as leather or hemp; to taw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tew', 'v.', 'Hence, to beat; to scourge; also, to pull about; to maul; to\n   tease; to vex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tew', 'v. i.', 'To work hard; to strive; to fuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tew', 'v. t.', 'To tow along, as a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tew', 'n.', 'A rope or chain for towing a boat; also, a cord; a string.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tewan', 'n.', 'A tribe of American Indians including many of the Pueblos of\n   New Mexico and adjacent regions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tewed', 'a.', 'Fatigued; worn with labor or hardship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tewel', 'n.', 'A pipe, funnel, or chimney, as for smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tewel', 'n.', 'The tuyere of a furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tewhit', 'n.', 'The lapwing; -- called also teewheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tewtaw', 'v. t.', 'To beat; to break, as flax or hemp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Texas', 'n.', 'A structure on the hurricane deck of a steamer, containing\n   the pilot house, officers cabins, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Text', 'n.', 'A discourse or composition on which a note or commentary is\n   written; the original words of an author, in distinction from a\n   paraphrase, annotation, or commentary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Text', 'n.', 'The four Gospels, by way of distinction or eminence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Text', 'n.', 'A verse or passage of Scripture, especially one chosen as the\n   subject of a sermon, or in proof of a doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Text', 'n.', 'Hence, anything chosen as the subject of an argument,\n   literary composition, or the like; topic; theme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Text', 'n.', 'A style of writing in large characters; text-hand also, a\n   kind of type used in printing; as, German text.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Text', 'v. t.', 'To write in large characters, as in text hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Text-book', 'n.', 'A book with wide spaces between the lines, to give room\n   for notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Text-book', 'n.', 'A volume, as of some classical author, on which a\n   teacher lectures or comments; hence, any manual of instruction; a\n   schoolbook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Text-hand', 'n.', 'A large hand in writing; -- so called because it was the\n   practice to write the text of a book in a large hand and the notes in a\n   smaller hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textile', 'a.', 'Pertaining to weaving or to woven fabrics; as, textile\n   arts; woven, capable of being woven; formed by weaving; as, textile\n   fabrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textile', 'n.', 'That which is, or may be, woven; a fabric made by weaving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textmen', 'pl. ', 'of Textman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textman', 'n.', 'One ready in quoting texts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textorial', 'a.', 'Of or pertaining to weaving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textrine', 'a.', 'Of or pertaining to weaving, textorial; as, the textrine\n   art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textual', 'a.', 'Of, pertaining to, or contained in, the text; as, textual\n   criticism; a textual reading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textual', 'a.', 'Serving for, or depending on, texts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textual', 'a.', 'Familiar with texts or authorities so as to cite them\n   accurately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textualist', 'n.', 'A textman; a textuary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textually', 'adv.', 'In a textual manner; in the text or body of a work; in\n   accordance with the text.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textuarist', 'n.', 'A textuary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textuary', 'a.', 'Contained in the text; textual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textuary', 'a.', 'Serving as a text; authoritative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textuary', 'n.', 'One who is well versed in the Scriptures; a textman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textuary', 'n.', 'One who adheres strictly or rigidly to the text.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textuel', 'a.', 'Textual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textuist', 'n.', 'A textualist; a textman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textural', 'a.', 'Of or pertaining to texture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Texture', 'n.', 'The act or art of weaving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Texture', 'n.', 'That which woven; a woven fabric; a web.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Texture', 'n.', 'The disposition or connection of threads, filaments, or\n   other slender bodies, interwoven; as, the texture of cloth or of a\n   spiders web.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Texture', 'n.', 'The disposition of the several parts of any body in\n   connection with each other, or the manner in which the constituent\n   parts are united; structure; as, the texture of earthy substances or\n   minerals; the texture of a plant or a bone; the texture of paper; a\n   loose or compact texture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Texture', 'n.', 'A tissue. See Tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textured', 'imp. & p. p.', 'of Texture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Texturing', 'p. pr. & vb. n.', 'of Texture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Texture', 'v. t.', 'To form a texture of or with; to interweave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Textury', 'n.', 'The art or process of weaving; texture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teyne', 'n.', 'A thin plate of metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Th', '', 'In Old English, the article the, when the following word began\n   with a vowel, was often written with elision as if a part of the word.\n   Thus in Chaucer, the forms thabsence, tharray, thegle, thend, thingot,\n   etc., are found for the absence, the array, the eagle, the end, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thack', '', 'Alt. of Thacker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thacker', '', 'See Thatch, Thatcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thak', 'v. t.', 'To thwack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalamencephalon', 'n.', 'The segment of the brain next in front of the\n   midbrain, including the thalami, pineal gland, and pituitary body; the\n   diencephalon; the interbrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalamic', 'a.', 'Of or pertaining to a thalamus or to thalami.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalamifloral', 'a.', 'Alt. of Thalamiflorous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalamiflorous', 'a.', 'Bearing the stamens directly on the receptacle; --\n   said of a subclass of polypetalous dicotyledonous plants in the system\n   of De Candolle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalamocoele', 'n.', 'The cavity or ventricle of the thalamencephalon; the\n   third ventricle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalamophora', 'n. pl.', 'Same as Foraminifera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalami', 'pl. ', 'of Thalamus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalamus', 'n.', 'A mass of nervous matter on either side of the third\n   ventricle of the brain; -- called also optic thalamus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalamus', 'n.', 'Same as Thallus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalamus', 'n.', 'The receptacle of a flower; a torus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalassian', 'n.', 'Any sea tortoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalassic', 'a.', 'Of or pertaining to the sea; -- sometimes applied to\n   rocks formed from sediments deposited upon the sea bottom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalassinian', 'n.', 'Any species of Thalaassinidae, a family of burrowing\n   macrurous Crustacea, having a long and soft abdomen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalassography', 'n.', 'The study or science of the life of marine\n   organisms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thaler', 'n.', 'A German silver coin worth about three shillings sterling,\n   or about 73 cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalia', 'n.', 'That one of the nine Muses who presided over comedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalia', 'n.', 'One of the three Graces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalia', 'n.', 'One of the Nereids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thaliacea', 'n. pl.', 'A division of Tunicata comprising the free-swimming\n   species, such as Salpa and Doliolum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalian', 'a.', 'Of or pertaining to Thalia; hence, of or pertaining to\n   comedy; comic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thallate', 'n.', 'A salt of a hypothetical thallic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thallene', 'n.', 'A hydrocarbon obtained from coal-tar residues, and\n   remarkable for its intense yellowish green fluorescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thallic', 'a.', 'Of or pertaining to thallium; derived from, or containing,\n   thallium; specifically, designating those compounds in which the\n   element has a higher valence as contrasted with the thallous compounds;\n   as, thallic oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalline', 'a.', 'Consisting of a thallus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalline', 'n.', 'An artificial alkaloid of the quinoline series, obtained\n   as a white crystalline substance, C10H13NO, whose salts are valuable as\n   antipyretics; -- so called from the green color produced in its\n   solution by certain oxidizing agents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thallious', 'a.', 'See Thallous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thallium', 'n.', 'A rare metallic element of the aluminium group found in\n   some minerals, as certain pyrites, and also in the lead-chamber deposit\n   in the manufacture of sulphuric acid. It is isolated as a heavy, soft,\n   bluish white metal, easily oxidized in moist air, but preserved by\n   keeping under water. Symbol Tl. Atomic weight 203.7.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thallogen', 'n.', 'One of a large class or division of the vegetable\n   kingdom, which includes those flowerless plants, such as fungi, algae,\n   and lichens, that consist of a thallus only, composed of cellular\n   tissue, or of a congeries of cells, or even of separate cells, and\n   never show a distinction into root, stem, and leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalloid', 'a.', 'Resembling, or consisting of, thallus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thallophyte', 'n.', 'Same as Thallogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thallous', 'a.', 'Of or pertaining to thallium; derived from, or\n   containing, thallium; specifically, designating those compounds in\n   which the element has a lower valence as contrasted with the thallic\n   compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thalli', 'pl. ', 'of Thallus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thallus', 'n.', 'A solid mass of cellular tissue, consisting of one or more\n   layers, usually in the form of a flat stratum or expansion, but\n   sometimes erect or pendulous, and elongated and branching, and forming\n   the substance of the thallogens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thammuz', 'n.', 'Alt. of Tammuz');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tammuz', 'n.', 'A deity among the ancient Syrians, in honor of whom the\n   Hebrew idolatresses held an annual lamentation. This deity has been\n   conjectured to be the same with the Phoenician Adon, or Adonis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tammuz', 'n.', 'The fourth month of the Jewish ecclesiastical year, --\n   supposed to correspond nearly with our month of July.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thamnophile', 'n.', 'A bush shrike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thamyn', 'n.', 'An Asiatic deer (Rucervus Eldi) resembling the swamp deer;\n   -- called also Elds deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Than', 'conj.', 'A particle expressing comparison, used after certain\n   adjectives and adverbs which express comparison or diversity, as more,\n   better, other, otherwise, and the like. It is usually followed by the\n   object compared in the nominative case. Sometimes, however, the object\n   compared is placed in the objective case, and than is then considered\n   by some grammarians as a preposition. Sometimes the object is expressed\n   in a sentence, usually introduced by that; as, I would rather suffer\n   than that you should want.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Than', 'adv.', 'Then. See Then.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thanage', 'n.', 'The district in which a thane anciently had jurisdiction;\n   thanedom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thanatoid', 'a.', 'Deathlike; resembling death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thanatology', 'n.', 'A description, or the doctrine, of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thanatopsis', 'n.', 'A view of death; a meditation on the subject of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thane', 'n.', 'A dignitary under the Anglo-Saxons and Danes in England. Of\n   these there were two orders, the kings thanes, who attended the kings\n   in their courts and held lands immediately of them, and the ordinary\n   thanes, who were lords of manors and who had particular jurisdiction\n   within their limits. After the Conquest, this title was disused, and\n   baron took its place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thanedom', 'n.', 'The property or jurisdiction of a thane; thanage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thanehood', 'n.', 'The character or dignity of a thane; also, thanes,\n   collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thaneship', 'n.', 'The state or dignity of a thane; thanehood; also, the\n   seignioralty of a thane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thanks', 'pl. ', 'of Thank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thank', 'n.', 'A expression of gratitude; an acknowledgment expressive of a\n   sense of favor or kindness received; obligation, claim, or desert, or\n   gratitude; -- now generally used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thanked', 'imp. & p. p.', 'of Thank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thanking', 'p. pr. & vb. n.', 'of Thank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thank', 'n.', 'To express gratitude to (anyone) for a favor; to make\n   acknowledgments to (anyone) for kindness bestowed; -- used also\n   ironically for blame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thankful', 'a.', 'Obtaining or deserving thanks; thankworthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thankful', 'a.', 'Impressed with a sense of kindness received, and ready to\n   acknowledge it; grateful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thankless', 'a.', 'Not acknowledging favors; not expressing thankfulness;\n   unthankful; ungrateful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thankless', 'a.', 'Not obtaining or deserving thanks; unacceptable; as, a\n   thankless task.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thankly', 'adv.', 'Thankfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thanksgive', 'v. t.', 'To give or dedicate in token of thanks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thanksgiver', 'n.', 'One who gives thanks, or acknowledges a kindness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thanksgiving', 'n.', 'The act of rending thanks, or expressing gratitude\n   for favors or mercies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thanksgiving', 'n.', 'A public acknowledgment or celebration of divine\n   goodness; also, a day set apart for religious services, specially to\n   acknowledge the goodness of God, either in any remarkable deliverance\n   from calamities or danger, or in the ordinary dispensation of his\n   bounties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thankworthiness', 'n.', 'The quality or state of being thankworthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thankworthy', 'a.', 'Deserving thanks; worthy of gratitude; mreitorious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thar', 'n.', 'A goatlike animal (Capra Jemlaica) native of the Himalayas.\n   It has small, flattened horns, curved directly backward. The hair of\n   the neck, shoulders, and chest of the male is very long, reaching to\n   the knees. Called also serow, and imo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thar', 'v. impersonal, pres.', 'It needs; need.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tharms', 'n. pl.', 'Twisted guts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tharos', 'n.', 'A small American butterfly (Phycoides tharos) having the\n   upper surface of the wings variegated with orange and black, the outer\n   margins black with small white crescents; -- called also pearl\n   crescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'That', 'pron., a., conj., & ', 'As a demonstrative pronoun (pl. Those),\n   that usually points out, or refers to, a person or thing previously\n   mentioned, or supposed to be understood. That, as a demonstrative, may\n   precede the noun to which it refers; as, that which he has said is\n   true; those in the basket are good apples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'That', 'pron., a., conj., & ', 'As an adjective, that has the same\n   demonstrative force as the pronoun, but is followed by a noun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'That', 'pron., a., conj., & ', 'As a relative pronoun, that is\n   equivalent to who or which, serving to point out, and make definite, a\n   person or thing spoken of, or alluded to, before, and may be either\n   singular or plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'That', 'pron., a., conj., & ', 'As a conjunction, that retains much of\n   its force as a demonstrative pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'That', 'pron., a., conj., & ', 'To introduce a clause employed as the\n   object of the preceding verb, or as the subject or predicate nominative\n   of a verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'That', 'pron., a., conj., & ', 'To introduce, a reason or cause; --\n   equivalent to for that, in that, for the reason that, because.');
  INSERT INTO words (word, word_type, description) VALUES  (    'That', 'pron., a., conj., & ', 'To introduce a purpose; -- usually\n   followed by may, or might, and frequently preceded by so, in order, to\n   the end, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'That', 'pron., a., conj., & ', 'To introduce a consequence, result, or\n   effect; -- usually preceded by so or such, sometimes by that.');
  INSERT INTO words (word, word_type, description) VALUES  (    'That', 'pron., a., conj., & ', 'In an elliptical sentence to introduce\n   a dependent sentence expressing a wish, or a cause of surprise,\n   indignation, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'That', 'pron., a., conj., & ', 'As adverb: To such a degree; so; as, he\n   was that frightened he could say nothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thatch', 'n.', 'Straw, rushes, or the like, used for making or covering the\n   roofs of buildings, or of stacks of hay or grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thatch', 'n.', 'A name in the West Indies for several kinds of palm, the\n   leaves of which are used for thatching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thatched', 'imp. & p. p.', 'of Thatch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thatching', 'p. pr. & vb. n.', 'of Thatch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thatch', 'n.', 'To cover with, or with a roof of, straw, reeds, or some\n   similar substance; as, to thatch a roof, a stable, or a stack of grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thatcher', 'n.', 'One who thatches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thatching', 'n.', 'The act or art of covering buildings with thatch; so as\n   to keep out rain, snow, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thatching', 'n.', 'The materials used for this purpose; thatch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thaught', 'n.', 'See Thwart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thaumatolatry', 'n.', 'Worship or undue admiration of wonderful or\n   miraculous things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thaumatrope', 'n.', 'An optical instrument or toy for showing the\n   presistence of an impression upon the eyes after the luminous object is\n   withdrawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thaumaturge', 'n.', 'A magician; a wonder worker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thaumaturgic', 'a.', 'Alt. of Thaumaturgical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thaumaturgical', 'a.', 'Of or pertaining to thaumaturgy; magical;\n   wonderful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thaumaturgics', 'n.', 'Feats of legerdemain, or magical performances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thaumaturgist', 'n.', 'One who deals in wonders, or believes in them; a\n   wonder worker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thaumaturgus', 'n.', 'A miracle worker; -- a title given by the Roman\n   Catholics to some saints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thaumaturgy', 'n.', 'The act or art of performing something wonderful;\n   magic; legerdemain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thave', 'n.', 'Same as Theave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thawed', 'imp. & p. p.', 'of Thaw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thawing', 'p. pr. & vb. n.', 'of Thaw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thaw', 'v. i.', 'To melt, dissolve, or become fluid; to soften; -- said of\n   that which is frozen; as, the ice thaws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thaw', 'v. i.', 'To become so warm as to melt ice and snow; -- said in\n   reference to the weather, and used impersonally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thaw', 'v. i.', 'Fig.: To grow gentle or genial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thaw', 'v. t.', 'To cause (frozen things, as earth, snow, ice) to melt,\n   soften, or dissolve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thaw', 'n.', 'The melting of ice, snow, or other congealed matter; the\n   resolution of ice, or the like, into the state of a fluid; liquefaction\n   by heat of anything congealed by frost; also, a warmth of weather\n   sufficient to melt that which is congealed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thawy', 'a.', 'Liquefying by heat after having been frozen; thawing;\n   melting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'The', 'v. i.', 'See Thee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'The', 'definite article.', 'A word placed before nouns to limit or\n   individualize their meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'The', 'adv.', 'By that; by how much; by so much; on that account; -- used\n   before comparatives; as, the longer we continue in sin, the more\n   difficult it is to reform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thea', 'n.', 'A genus of plants found in China and Japan; the tea plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theandric', 'a.', 'Relating to, or existing by, the union of divine and\n   human operation in Christ, or the joint agency of the divine and human\n   nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theanthropic', 'a.', 'Alt. of Theanthropical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theanthropical', 'a.', 'Partaking of, or combining, both divinity and\n   humanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theanthropism', 'n.', 'A state of being God and man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theanthropism', 'n.', 'The ascription of human atributes to the Deity, or\n   to a polytheistic deity; anthropomorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theanthropist', 'n.', 'One who advocates, or believes in, theanthropism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theanthropy', 'n.', 'Theanthropism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thearchic', 'a.', 'Divinely sovereign or supreme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thearchy', 'n.', 'Government by God; divine sovereignty; theocracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theater', 'n.', 'Alt. of Theatre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theatre', 'n.', 'An edifice in which dramatic performances or spectacles\n   are exhibited for the amusement of spectators; anciently uncovered,\n   except the stage, but in modern times roofed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theatre', 'n.', 'Any room adapted to the exhibition of any performances\n   before an assembly, as public lectures, scholastic exercises,\n   anatomical demonstrations, surgical operations, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theatre', 'n.', 'That which resembles a theater in form, use, or the like;\n   a place rising by steps or gradations, like the seats of a theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theatre', 'n.', 'A sphere or scheme of operation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theatre', 'n.', 'A place or region where great events are enacted; as, the\n   theater of war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theatin', 'n.', 'Alt. of Theatine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theatine', 'n.', 'One of an order of Italian monks, established in 1524,\n   expressly to oppose Reformation, and to raise the tone of piety among\n   Roman Catholics. They hold no property, nor do they beg, but depend on\n   what Providence sends. Their chief employment is preaching and giving\n   religious instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theatine', 'n.', 'One of an order of nuns founded by Ursula Benincasa, who\n   died in 1618.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theatral', 'a.', 'Of or pertaining to a theater; theatrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theatric', 'a.', 'Theatrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theatrical', 'a.', 'Of or pertaining to a theater, or to the scenic\n   representations; resembling the manner of dramatic performers;\n   histrionic; hence, artificial; as, theatrical performances; theatrical\n   gestures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theatricals', 'n. pl.', 'Dramatic performances; especially, those produced\n   by amateurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theave', 'n.', 'A ewe lamb of the first year; also, a sheep three years\n   old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thebaic', 'a.', 'Of or pertaining to Thebes in Egypt; specifically,\n   designating a version of the Bible preserved by the Copts, and esteemed\n   of great value by biblical scholars. This version is also called the\n   Sahidic version.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thebaid', 'n.', 'A Latin epic poem by Statius about Thebes in Boeotia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thebaine', 'n.', 'A poisonous alkaloid, C19H21NO3, found in opium in small\n   quantities, having a sharp, astringent taste, and a tetanic action\n   resembling that of strychnine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theban', 'a.', 'Of or pertaining to Thebes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theban', 'n.', 'A native or inhabitant of Thebes; also, a wise man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thecae', 'pl. ', 'of Theca');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theca', 'n.', 'A sheath; a case; as, the theca, or cell, of an anther; the\n   theca, or spore case, of a fungus; the theca of the spinal cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theca', 'n.', 'The chitinous cup which protects the hydranths of certain\n   hydroids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theca', 'n.', 'The more or less cuplike calicle of a coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theca', 'n.', 'The wall forming a calicle of a coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thecal', 'a.', 'Of or pertaining to a theca; as, a thecal abscess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thecaphore', 'n.', 'A surface or organ bearing a theca, or covered with\n   thecae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thecaphore', 'n.', 'See Basigynium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thecasporous', 'a.', 'Having the spores in thecae, or cases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thecata', 'n. pl.', 'Same as Thecophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thecla', 'n.', 'Any one of many species of small delicately colored\n   butterflies belonging to Thecla and allied genera; -- called also\n   hairstreak, and elfin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thecodactyl', 'n.', 'Any one of a group of lizards of the Gecko tribe,\n   having the toes broad, and furnished with a groove in which the claws\n   can be concealed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thecodont', 'a.', 'Having the teeth inserted in sockets in the alveoli of\n   the jaws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thecodont', 'a.', 'Of or pertaining to the thecodonts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thecodont', 'n.', 'One of the Thecodontia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thecodontia', 'n. pl.', 'A group of fossil saurians having biconcave\n   vertebrae and the teeth implanted in sockets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thecophora', 'n. pl.', 'A division of hydroids comprising those which have\n   the hydranths in thecae and the gonophores in capsules. The\n   campanularians and sertularians are examples. Called also Thecata. See\n   Illust. under Hydroidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thecosomata', 'n. pl.', 'An order of Pteropoda comprising those species\n   which have a shell. See Pteropoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thedom', 'n.', 'Success; fortune; luck; chance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thee', 'a.', 'To thrive; to prosper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thee', 'pron.', 'The objective case of thou. See Thou.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theft', 'n.', 'The act of stealing; specifically, the felonious taking and\n   removing of personal property, with an intent to deprive the rightful\n   owner of the same; larceny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theft', 'n.', 'The thing stolen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theftbote', 'n.', 'The receiving of a mans goods again from a thief, or a\n   compensation for them, by way of composition, with the intent that the\n   thief shall escape punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thegn', 'n.', 'Thane. See Thane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thegnhood', 'n.', 'Thanehood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theiform', 'a.', 'Having the form of tea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theine', 'n.', 'See Caffeine. Called also theina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Their', 'pron. & a.', 'The possessive case of the personal pronoun they;\n   as, their houses; their country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theism', 'n.', 'The belief or acknowledgment of the existence of a God, as\n   opposed to atheism, pantheism, or polytheism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theist', 'n.', 'One who believes in the existence of a God; especially, one\n   who believes in a personal God; -- opposed to atheist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theistic', 'a.', 'Alt. of Theistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theistical', 'a.', 'Of or pertaining to theism, or a theist; according to\n   the doctrine of theists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thelphusian', 'n.', 'One of a tribe of fresh-water crabs which live in or\n   on the banks of rivers in tropical countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thelytokous', 'a.', 'Producing females only; -- said of certain female\n   insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Them', 'pron.', 'The objective case of they. See They.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thematic', 'a.', 'Of or pertaining to the theme of a word. See Theme, n.,\n   4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thematic', 'n.', 'Of or pertaining to a theme, or subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theme', 'n.', 'A subject or topic on which a person writes or speaks; a\n   proposition for discussion or argument; a text.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theme', 'n.', 'Discourse on a certain subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theme', 'n.', 'A composition or essay required of a pupil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theme', 'n.', 'A noun or verb, not modified by inflections; also, that part\n   of a noun or verb which remains unchanged (except by euphonic\n   variations) in declension or conjugation; stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theme', 'n.', 'That by means of which a thing is done; means; instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theme', 'n.', 'The leading subject of a composition or a movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Themis', 'n.', 'The goddess of law and order; the patroness of existing\n   rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Themselves', 'pron.', 'The plural of himself, herself, and itself. See\n   Himself, Herself, Itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Then', 'adv.', 'At that time (referring to a time specified, either past or\n   future).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Then', 'adv.', 'Soon afterward, or immediately; next; afterward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Then', 'adv.', 'At another time; later; again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Then', 'conj.', 'Than.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Then', 'conj.', 'In that case; in consequence; as a consequence; therefore;\n   for this reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thenadays', 'adv.', 'At that time; then; in those days; -- correlative to\n   nowadays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thenal', 'a.', 'Alt. of Thenar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thenar', 'a.', 'Of or pertaining to the thenar; corresponding to thenar;\n   palmar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thenar', 'n.', 'The palm of the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thenar', 'n.', 'The prominence of the palm above the base of the thumb; the\n   thenar eminence; the ball of the thumb. Sometimes applied to the\n   corresponding part of the foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thenardite', 'n.', 'Anhydrous sodium sulphate, a mineral of a white or\n   brown color and vitreous luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thence', 'adv.', 'From that place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thence', 'adv.', 'From that time; thenceforth; thereafter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thence', 'adv.', 'For that reason; therefore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thence', 'adv.', 'Not there; elsewhere; absent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thenceforth', 'adv.', 'From that time; thereafter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thenceforward', 'adv.', 'From that time onward; thenceforth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thencefrom', 'adv.', 'From that place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theobroma', 'n.', 'A genus of small trees. See Cacao.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theobromic', 'a.', 'Of, pertaining to, or designating, an acid extracted\n   from cacao butter (from the Theobroma Cacao), peanut oil (from Arachis\n   hypogaea), etc., as a white waxy crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theobromine', 'n.', 'An alkaloidal ureide, C7H8N4O2, homologous with and\n   resembling caffeine, produced artificially, and also extracted from\n   cacao and chocolate (from Theobroma Cacao) as a bitter white\n   crystalline substance; -- called also dimethyl xanthine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theochristic', 'a.', 'Anointed by God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theocracy', 'n.', 'Government of a state by the immediate direction or\n   administration of God; hence, the exercise of political authority by\n   priests as representing the Deity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theocracy', 'n.', 'The state thus governed, as the Hebrew commonwealth\n   before it became a kingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theocrasy', 'n.', 'A mixture of the worship of different gods, as of\n   Jehovah and idols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theocrasy', 'n.', 'An intimate union of the soul with God in contemplation,\n   -- an ideal of the Neoplatonists and of some Oriental mystics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theocrat', 'n.', 'One who lives under a theocratic form of government; one\n   who in civil affairs conforms to divine law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theocratic', 'a.', 'Alt. of Theocratical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theocratical', 'a.', 'Of or pertaining to a theocracy; administred by the\n   immediate direction of God; as, the theocratical state of the\n   Israelites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theodicy', 'n.', 'A vindication of the justice of God in ordaining or\n   permitting natural and moral evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theodicy', 'n.', 'That department of philosophy which treats of the being,\n   perfections, and government of God, and the immortality of the soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theodolite', 'n.', 'An instrument used, especially in trigonometrical\n   surveying, for the accurate measurement of horizontal angles, and also\n   usually of vertical angles. It is variously constructed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theodolitic', 'a.', 'Of or pertaining to a theodolite; made by means of a\n   theodolite; as, theodolitic observations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theogonic', 'a.', 'Of or relating to theogony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theogonism', 'n.', 'Theogony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theogonist', 'n.', 'A writer on theogony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theogony', 'n.', 'The generation or genealogy of the gods; that branch of\n   heathen theology which deals with the origin and descent of the\n   deities; also, a poem treating of such genealogies; as, the Theogony of\n   Hesiod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theologaster', 'n.', 'A pretender or quack in theology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theologer', 'n.', 'A theologian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theologian', 'n.', 'A person well versed in theology; a professor of\n   theology or divinity; a divine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theologic', 'a.', 'Theological.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theological', 'a.', 'Of or pertaining to theology, or the science of God\n   and of divine things; as, a theological treatise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theologics', 'n.', 'Theology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theologist', 'n.', 'A theologian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theologized', 'imp. & p. p.', 'of Theologize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theologizing', 'p. pr. & vb. n.', 'of Theologize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theologize', 'v. t.', 'To render theological; to apply to divinity; to\n   reduce to a system of theology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theologize', 'v. i.', 'To frame a system of theology; to theorize or\n   speculate upon theological subjects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theologizer', 'n.', 'One who theologizes; a theologian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theologue', 'n.', 'A theologian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theologue', 'n.', 'A student in a theological seminary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theologies', 'pl. ', 'of Theology');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theology', 'n.', 'The science of God or of religion; the science which\n   treats of the existence, character, and attributes of God, his laws and\n   government, the doctrines we are to believe, and the duties we are to\n   practice; divinity; (as more commonly understood) \"the knowledge\n   derivable from the Scriptures, the systematic exhibition of revealed\n   truth, the science of Christian faith and life.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theomachist', 'n.', 'One who fights against the gods; one who resists God\n   of the divine will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theomachy', 'n.', 'A fighting against the gods, as the battle of the gaints\n   with the gods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theomachy', 'n.', 'A battle or strife among the gods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theomachy', 'n.', 'Opposition to God or the divine will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theomancy', 'n.', 'A kind of divination drawn from the responses of oracles\n   among heathen nations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theopathetic', 'a.', 'Alt. of Theopathic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theopathic', 'a.', 'Of or pertaining to a theopathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theopathy', 'n.', 'Capacity for religious affections or worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theophanic', 'a.', 'Of or pertaining to a theopany; appearing to man, as a\n   god.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-nies', 'pl. ', 'of Theophany');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theophany', 'n.', 'A manifestation of God to man by actual appearance,\n   usually as an incarnation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theophilanthropic', 'a.', 'Pertaining to theophilanthropy or the\n   theophilanthropists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theophilanthropism', 'n.', 'The doctrine of the theophilanthropists;\n   theophilanthropy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theophilanthropist', 'n.', 'A member of a deistical society established at\n   Paris during the French revolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theophilanthropy', 'n.', 'Theophilanthropism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theophilosophic', 'a.', 'Combining theism and philosophy, or pertaining to\n   the combination of theism and philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theopneusted', 'a.', 'Divinely inspired; theopneustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theopneustic', 'a.', 'Given by the inspiration of the Spirit of God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theopneusty', 'n.', 'Divine inspiration; the supernatural influence of the\n   Divine Spirit in qualifying men to receive and communicate revealed\n   truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theorbist', 'n.', 'One who plays on a theorbo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theorbo', 'n.', 'An instrument made like large lute, but having two necks,\n   with two sets of pegs, the lower set holding the strings governed by\n   frets, while to the upper set were attached the long bass strings used\n   as open notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theorem', 'n.', 'That which is considered and established as a principle;\n   hence, sometimes, a rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theorem', 'n.', 'A statement of a principle to be demonstrated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theorem', 'v. t.', 'To formulate into a theorem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theorematic', 'a.', 'Alt. of Theorematical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theorematical', 'a.', 'Of or pertaining to a theorem or theorems; comprised\n   in a theorem; consisting of theorems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theorematist', 'n.', 'One who constructs theorems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theoremic', 'a.', 'Theorematic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theoretic', 'a.', 'Alt. of Theoretical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theoretical', 'a.', 'Pertaining to theory; depending on, or confined to,\n   theory or speculation; speculative; terminating in theory or\n   speculation: not practical; as, theoretical learning; theoretic\n   sciences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theoretics', 'n.', 'The speculative part of a science; speculation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theoric', 'a.', 'Of or pertaining to the theorica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theoric', 'a.', 'Relating to, or skilled in, theory; theoretically skilled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theoric', 'n.', 'Speculation; theory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theorica', 'n. pl.', 'Public moneys expended at Athens on festivals,\n   sacrifices, and public entertainments (especially theatrical\n   performances), and in gifts to the people; -- also called theoric fund.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theorical', 'a.', 'Theoretic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theorically', 'adv.', 'In a theoretic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theorist', 'n.', 'One who forms theories; one given to theory and\n   speculation; a speculatist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theorization', 'n.', 'The act or product of theorizing; the formation of a\n   theory or theories; speculation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theorized', 'imp. & p. p.', 'of Theorize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theorizing', 'p. pr. & vb. n.', 'of Theorize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theorize', 'v. i.', 'To form a theory or theories; to form opinions solely\n   by theory; to speculate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theorizer', 'n.', 'One who theorizes or speculates; a theorist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theories', 'pl. ', 'of Theory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theory', 'n.', 'A doctrine, or scheme of things, which terminates in\n   speculation or contemplation, without a view to practice; hypothesis;\n   speculation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theory', 'n.', 'An exposition of the general or abstract principles of any\n   science; as, the theory of music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theory', 'n.', 'The science, as distinguished from the art; as, the theory\n   and practice of medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theory', 'n.', 'The philosophical explanation of phenomena, either physical\n   or moral; as, Lavoisiers theory of combustion; Adam Smiths theory of\n   moral sentiments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theosoph', 'n.', 'Alt. of Theosopher');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theosopher', 'n.', 'A theosophist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theosophic', 'a.', 'Alt. of Theosophical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theosophical', 'a.', 'Of or pertaining to theosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theosophism', 'n.', 'Belief in theosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theosophist', 'n.', 'One addicted to theosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theosophistical', 'a.', 'Of or pertaining to theosophy; theosophical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theosophized', 'imp. & p. p.', 'of Theosophize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theosophizing', 'p. pr. & vb. n.', 'of Theosophize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theosophize', 'v. i.', 'To practice theosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theosophy', 'n.', 'Any system of philosophy or mysticism which proposes to\n   attain intercourse with God and superior spirits, and consequent\n   superhuman knowledge, by physical processes, as by the theurgic\n   operations of some ancient Platonists, or by the chemical processes of\n   the German fire philosophers; also, a direct, as distinguished from a\n   revealed, knowledge of God, supposed to be attained by extraordinary\n   illumination; especially, a direct insight into the processes of the\n   divine mind, and the interior relations of the divine nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therapeutae', 'n. pl.', 'A name given to certain ascetics said to have\n   anciently dwelt in the neighborhood of Alexandria. They are described\n   in a work attributed to Philo, the genuineness and credibility of which\n   are now much discredited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therapeutic', 'a.', 'Alt. of Therapeutical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therapeutical', 'a.', 'Of or pertaining to the healing art; concerned in\n   discovering and applying remedies for diseases; curative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therapeutic', 'n.', 'One of the Therapeutae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therapeutics', 'n.', 'That part of medical science which treats of the\n   discovery and application of remedies for diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therapeutist', 'n.', 'One versed in therapeutics, or the discovery and\n   application of remedies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therapy', 'n.', 'Therapeutics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'There', 'pron.', 'In or at that place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'There', 'pron.', 'In that matter, relation, etc.; at that point, stage,\n   etc., regarded as a distinct place; as, he did not stop there, but\n   continued his speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'There', 'pron.', 'To or into that place; thither.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereabout', 'adv.', 'Alt. of Thereabouts');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereabouts', 'adv.', 'Near that place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereabouts', 'adv.', 'Near that number, degree, or quantity; nearly; as,\n   ten men, or thereabouts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereabouts', 'adv.', 'Concerning that; about that.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereafter', 'adv.', 'After that; afterward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereafter', 'adv.', 'According to that; accordingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereafter', 'adv.', 'Of that sort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereagain', 'adv.', 'In opposition; against ones course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'There-anent', 'adv.', 'Concerning that.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereat', 'adv.', 'At that place; there.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereat', 'adv.', 'At that occurrence or event; on that account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therebefore', 'adv.', 'Alt. of Therebiforn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therebiforn', 'adv.', 'Before that time; beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereby', 'adv.', 'By that; by that means; in consequence of that.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereby', 'adv.', 'Annexed to that.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereby', 'adv.', 'Thereabout; -- said of place, number, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therefor', 'adv.', 'For that, or this; for it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therefore', 'adv.', 'For that or this reason, referring to something\n   previously stated; for that.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therefore', 'adv.', 'Consequently; by consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therefrom', 'adv.', 'From this or that.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therein', 'adv.', 'In that or this place, time, or thing; in that\n   particular or respect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereinto', 'adv.', 'Into that or this, or into that place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereof', 'adv.', 'Of that or this.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereology', 'n.', 'Therapeutios.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereon', 'adv.', 'On that or this.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereout', 'adv.', 'Out of that or this.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereout', 'adv.', 'On the outside; out of doors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereto', 'adv.', 'To that or this.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereto', 'adv.', 'Besides; moreover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theretofore', 'adv.', 'Up to that time; before then; -- correlative with\n   heretofore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereunder', 'adv.', 'Under that or this.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereunto', 'adv.', 'Unto that or this; thereto; besides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereupon', 'adv.', 'Upon that or this; thereon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereupon', 'adv.', 'On account, or in consequence, of that; therefore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thereupon', 'adv.', 'Immediately; at once; without delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therewhile', 'adv.', 'At that time; at the same time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therewith', 'adv.', 'With that or this.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therewith', 'adv.', 'In addition; besides; moreover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therewith', 'adv.', 'At the same time; forthwith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therewithal', 'adv.', 'Over and above; besides; moreover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therewithal', 'adv.', 'With that or this; therewith; at the same time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therf', 'a.', 'Not fermented; unleavened; -- said of bread, loaves, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theriac', 'n.', 'Alt. of Theriaca');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theriaca', 'n.', 'An ancient composition esteemed efficacious against the\n   effects of poison; especially, a certain compound of sixty-four drugs,\n   prepared, pulverized, and reduced by means of honey to an electuary; --\n   called also theriaca Andromachi, and Venice treacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theriaca', 'n.', 'Treacle; molasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theriac', 'a.', 'Alt. of Theriacal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theriacal', 'a.', 'Of or pertaining to theriac; medicinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Therial', 'a.', 'Theriac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theriodont', 'n.', 'One of the Theriodontia. Used also adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theriodonta', 'n. pl.', 'Same as Theriodontia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theriodontia', 'n. pl.', 'An extinct order of reptiles found in the Permian\n   and Triassic formations in South Africa. In some respects they\n   resembled carnivorous mammals. Called also Theromorpha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theriotomy', 'n.', 'Zootomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermae', 'n. pl.', 'Springs or baths of warm or hot water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermal', 'a.', 'Of or pertaining to heat; warm; hot; as, the thermal unit;\n   thermal waters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermally', 'adv.', 'In a thermal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermetograph', 'n.', 'A self-registering thermometer, especially one that\n   registers the maximum and minimum during long periods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermic', 'a.', 'Of or pertaining to heat; due to heat; thermal; as,\n   thermic lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermidor', 'n.', 'The eleventh month of the French republican calendar, --\n   commencing July 19, and ending August 17. See the Note under\n   Vendemiaire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermifugine', 'n.', 'An artificial alkaloid of complex composition,\n   resembling thalline and used as an antipyretic, -- whence its name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermo-', '', 'A combining form from Gr. qe`rmh heat, qermo`s hot, warm; as\n   in thermochemistry, thermodynamic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermobarometer', 'n.', 'An instrument for determining altitudes by the\n   boiling point of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermobattery', 'n.', 'A thermoelectric battery; a thermopile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermocautery', 'n.', 'Cautery by the application of heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermochemic', 'a.', 'Alt. of Thermochemical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermochemical', 'a.', 'Of or pertaining to thermochemistry; obtained by,\n   or employed in, thermochemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermochemistry', 'n.', 'That branch of chemical science which includes the\n   investigation of the various relations existing between chemical action\n   and that manifestation of force termed heat, or the determination of\n   the heat evolved by, or employed in, chemical actions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermochrosy', 'n.', 'The property possessed by heat of being composed,\n   like light, of rays of different degrees of refrangibility, which are\n   unequal in rate or degree of transmission through diathermic\n   substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermocurrent', 'n.', 'A current, as of electricity, developed, or set in\n   motion, by the action of heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermodynamic', 'a.', 'Relating to thermodynamics; caused or operated by\n   force due to the application of heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermodynamics', 'n.', 'The science which treats of the mechanical action\n   or relations of heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermoelectric', 'a.', 'Pertaining to thermoelectricity; as, thermoelectric\n   currents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermoelectricity', 'n.', 'Electricity developed in the action of heat. See\n   the Note under Electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermoelectrometer', 'n.', 'An instrument for measuring the strength of an\n   electric current in the heat which it produces, or for determining the\n   heat developed by such a current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermogen', 'n.', 'Caloric; heat; regarded as a material but imponderable\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermogenic', 'a.', 'Relating to heat, or to the production of heat;\n   producing heat; thermogenous; as, the thermogenic tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermogenous', 'a.', 'Producing heat; thermogenic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermograph', 'n.', 'An instrument for automatically recording indications\n   of the variation of temperature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermology', 'n.', 'A discourse on, or an account of, heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermolysis', 'n.', 'The resolution of a compound into parts by heat;\n   dissociation by heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermolyze', 'v. t.', 'To subject to thermolysis; to dissociate by heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermomagnetism', 'n.', 'Magnetism as affected or caused by the action of\n   heat; the relation of heat to magnetism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermometer', 'n.', 'An instrument for measuring temperature, founded on\n   the principle that changes of temperature in bodies are accompained by\n   proportional changes in their volumes or dimensions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermometric', 'a.', 'Alt. of Thermometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermometrical', 'a.', 'Of or pertaining to a thermometer; as, the\n   thermometrical scale or tube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermometrical', 'a.', 'Made, or ascertained, by means of a thermometer;\n   as, thermometrical observations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermometrically', 'adv.', 'In a thermometrical manner; by means of a\n   thermometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermometrograph', 'n.', 'An instrument for recording graphically the\n   variations of temperature, or the indications of a thermometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermometry', 'n.', 'The estimation of temperature by the use of a\n   thermometric apparatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermomultiplier', 'n.', 'Same as Thermopile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermopile', 'n.', 'An instrument of extreme sensibility, used to determine\n   slight differences and degrees of heat. It is composed of alternate\n   bars of antimony and bismuth, or any two metals having different\n   capacities for the conduction of heat, connected with an astatic\n   galvanometer, which is very sensibly affected by the electric current\n   induced in the system of bars when exposed even to the feeblest degrees\n   of heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermoscope', 'n.', 'An instrument for indicating changes of temperature\n   without indicating the degree of heat by which it is affected;\n   especially, an instrument contrived by Count Rumford which, as modified\n   by Professor Leslie, was afterward called the differential thermometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermoscopic', 'a.', 'Of or pertaining to the thermoscope; made by means of\n   the thermoscope; as, thermoscopic observations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermostat', 'n.', 'A self-acting apparatus for regulating temperature by\n   the unequal expansion of different metals, liquids, or gases by heat,\n   as in opening or closing the damper of a stove, or the like, as the\n   heat becomes greater or less than is desired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermostatic', 'a.', 'Of or pertaining to the thermostat; made or effected\n   by means of the thermostat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermosystaltic', 'a.', 'Influenced in its contraction by heat or cold; --\n   said of a muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermotaxic', 'a.', 'Pertaining to, or connected with, the regulation of\n   temperature in the animal body; as, the thermotaxic nervous system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermotension', 'n.', 'A process of increasing the strength of wrought iron\n   by heating it to a determinate temperature, and giving to it, while in\n   that state, a mechanical strain or tension in the direction in which\n   the strength is afterward to be exerted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermotic', 'a.', 'Alt. of Thermotical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermotical', 'a.', 'Of or pertaining to heat; produced by heat; as,\n   thermotical phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermotics', 'n.', 'The science of heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermotropic', 'a.', 'Manifesting thermotropism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermotropism', 'n.', 'The phenomenon of turning towards a source of\n   warmth, seen in the growing parts of some plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermotype', 'n.', 'A picture (as of a slice of wood) obtained by first\n   wetting the object slightly with hydrochloric or dilute sulphuric acid,\n   then taking an impression with a press, and next strongly heating this\n   impression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermotypy', 'n.', 'The art or process of obtaining thermotypes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thermovoltaic', 'a.', 'Of or relating to heat and electricity; especially,\n   relating to thermal effects produced by voltaic action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theromorpha', 'n. pl.', 'See Theriodonta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theropoda', 'n. pl.', 'An order of carnivorous dinosaurs in which the feet\n   are less birdlike, and hence more like those of an ordinary quadruped,\n   than in the Ornithopoda. It includes the rapacious genera Megalosaurus,\n   Creosaurus, and their allies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thesauri', 'pl. ', 'of Thesaurus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thesaurus', 'n.', 'A treasury or storehouse; hence, a repository,\n   especially of knowledge; -- often applied to a comprehensive work, like\n   a dictionary or cyclopedia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'These', 'pron.', 'The plural of this. See This.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thesicle', 'n.', 'A little or subordinate thesis; a proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theses', 'pl. ', 'of Thesis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thesis', 'n.', 'A position or proposition which a person advances and\n   offers to maintain, or which is actually maintained by argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thesis', 'n.', 'Hence, an essay or dissertation written upon specific or\n   definite theme; especially, an essay presented by a candidate for a\n   diploma or degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thesis', 'n.', 'An affirmation, or distinction from a supposition or\n   hypothesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thesis', 'n.', 'The accented part of the measure, expressed by the downward\n   beat; -- the opposite of arsis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thesis', 'n.', 'The depression of the voice in pronouncing the syllables of\n   a word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thesis', 'n.', 'The part of the foot upon which such a depression falls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thesmothete', 'n.', 'A lawgiver; a legislator; one of the six junior\n   archons at Athens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thespian', 'a.', 'Of or pertaining to Thespis; hence, relating to the\n   drama; dramatic; as, the Thespian art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thespian', 'n.', 'An actor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thessalian', 'a.', 'Of or pertaining to Thessaly in Greece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thessalian', 'n.', 'A native or inhabitant of Thessaly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thessalonian', 'a.', 'Of or pertaining to Thessalonica, a city of\n   Macedonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thessalonian', 'n.', 'A native or inhabitant of Thessalonica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theta', 'n.', 'A letter of the Greek alphabet corresponding to th in\n   English; -- sometimes called the unlucky letter, from being used by the\n   judges on their ballots in passing condemnation on a prisoner, it being\n   the first letter of the Greek qa`natos, death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thetical', 'a.', 'Laid down; absolute or positive, as a law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thetine', 'n.', 'Any one of a series of complex basic sulphur compounds\n   analogous to the sulphines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theurgic', 'a.', 'Alt. of Theurgical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theurgical', 'a.', 'Of or pertaining to theurgy; magical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theurgist', 'n.', 'One who pretends to, or is addicted to, theurgy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theurgy', 'n.', 'A divine work; a miracle; hence, magic; sorcery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theurgy', 'n.', 'A kind of magical science or art developed in Alexandria\n   among the Neoplatonists, and supposed to enable man to influence the\n   will of the gods by means of purification and other sacramental rites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Theurgy', 'n.', 'In later or modern magic, that species of magic in which\n   effects are claimed to be produced by supernatural agency, in\n   distinction from natural magic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thew', 'n.', 'Manner; custom; habit; form of behavior; qualities of mind;\n   disposition; specifically, good qualities; virtues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thew', 'n.', 'Muscle or strength; nerve; brawn; sinew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thewed', 'a.', 'Furnished with thews or muscles; as, a well-thewed limb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thewed', 'a.', 'Accustomed; mannered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thewy', 'a.', 'Having strong or large thews or muscles; muscular; sinewy;\n   strong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'They', 'obj.', 'The plural of he, she, or it. They is never used\n   adjectively, but always as a pronoun proper, and sometimes refers to\n   persons without an antecedent expressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thialdine', 'n.', 'A weak nitrogenous sulphur base, C6H13NS2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thialol', 'n.', 'A colorless oily liquid, (C2H5)2S2, having a strong garlic\n   odor; -- called also ethyl disulphide. By extension, any one of the\n   series of related compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thibetan', 'a.', 'Of or pertaining to Thibet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thibetan', 'n.', 'A native or inhabitant of Thibet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thibet cloth', '', 'A fabric made of coarse goats hair; a kind of camlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thibet cloth', '', 'A kind of fine woolen cloth, used for dresses, cloaks,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thibetian', 'a. & n.', 'Same as Thibetan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thible', 'n.', 'A slice; a skimmer; a spatula; a pudding stick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick', 'superl.', 'Measuring in the third dimension other than length and\n   breadth, or in general dimension other than length; -- said of a solid\n   body; as, a timber seven inches thick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick', 'superl.', 'Having more depth or extent from one surface to its\n   opposite than usual; not thin or slender; as, a thick plank; thick\n   cloth; thick paper; thick neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick', 'superl.', 'Dense; not thin; inspissated; as, thick vapors. Also\n   used figuratively; as, thick darkness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick', 'superl.', 'Not transparent or clear; hence, turbid, muddy, or\n   misty; as, the water of a river is apt to be thick after a rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick', 'superl.', 'Abundant, close, or crowded in space; closely set;\n   following in quick succession; frequently recurring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick', 'superl.', 'Not having due distinction of syllables, or good\n   articulation; indistinct; as, a thick utterance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick', 'superl.', 'Deep; profound; as, thick sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick', 'superl.', 'Dull; not quick; as, thick of fearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick', 'superl.', 'Intimate; very friendly; familiar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick', 'n.', 'The thickest part, or the time when anything is thickest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick', 'n.', 'A thicket; as, gloomy thicks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick', 'adv.', 'Frequently; fast; quick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick', 'adv.', 'Closely; as, a plat of ground thick sown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick', 'adv.', 'To a great depth, or to a greater depth than usual; as,\n   land covered thick with manure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick', 'v. t. & i.', 'To thicken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thickbill', 'n.', 'The bullfinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thickened', 'imp. & p. p.', 'of Thicken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thickening', 'p. pr. & vb. n.', 'of Thicken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thicken', 'v. t.', 'To make thick (in any sense of the word).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thicken', 'v. t.', 'To render dense; to inspissate; as, to thicken paint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thicken', 'v. t.', 'To make close; to fill up interstices in; as, to\n   thicken cloth; to thicken ranks of trees or men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thicken', 'v. t.', 'To strengthen; to confirm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thicken', 'v. t.', 'To make more frequent; as, to thicken blows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thicken', 'v. i.', 'To become thick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thickening', 'n.', 'Something put into a liquid or mass to make it thicker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thicket', 'a.', 'A wood or a collection of trees, shrubs, etc., closely\n   set; as, a ram caught in a thicket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thickhead', 'n.', 'A thick-headed or stupid person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thickhead', 'n.', 'Any one of several species of Australian singing birds\n   of the genus Pachycephala. The males of some of the species are\n   bright-colored. Some of the species are popularly called thrushes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick-headed', 'a.', 'Having a thick skull; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thickish', 'a.', 'Somewhat thick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick-knee', 'n.', 'A stone curlew. See under Stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thickly', 'adv.', 'In a thick manner; deeply; closely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thickness', 'n.', 'The quality or state of being thick (in any of the\n   senses of the adjective).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thickset', 'a.', 'Close planted; as, a thickset wood; a thickset hedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thickset', 'a.', 'Having a short, thick body; stout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thickset', 'n.', 'A close or thick hedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thickset', 'n.', 'A stout, twilled cotton cloth; a fustian corduroy, or\n   velveteen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thickskin', 'n.', 'A coarse, gross person; a person void of sensibility or\n   sinsitiveness; a dullard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick-skinned', 'a.', 'Having a thick skin; hence, not sensitive; dull;\n   obtuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thickskull', 'n.', 'A dullard, or dull person; a blockhead; a numskull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick-skulled', 'a.', 'Having a thick skull; hence, dull; heavy; stupid;\n   slow to learn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick wind', '', 'A defect of respiration in a horse, that is unassociated\n   with noise in breathing or with the signs of emphysema.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thick-winded', 'a.', 'Affected with thick wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thider', 'adv.', 'Thither.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thiderward', 'adv.', 'Thitherward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thieves', 'pl. ', 'of Thief');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thief', 'n.', 'One who steals; one who commits theft or larceny. See Theft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thief', 'n.', 'A waster in the snuff of a candle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thiefly', 'a. & adv.', 'Like a thief; thievish; thievishly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thienone', 'n.', 'A ketone derivative of thiophene obtained as a white\n   crystalline substance, (C4H3S)2.CO, by the action of aluminium chloride\n   and carbonyl chloride on thiophene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thienyl', 'n.', 'The hypothetical radical C4H3S, regarded as the essential\n   residue of thiophene and certain of its derivatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thieved', 'imp. & p. p.', 'of Thieve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thieving', 'p. pr. & vb. n.', 'of Thieve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thieve', 'v. t. & i.', 'To practice theft; to steal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thievery', 'n.', 'The practice of stealing; theft; thievishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thievery', 'n.', 'That which is stolen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thievish', 'a.', 'Given to stealing; addicted to theft; as, a thievish boy,\n   a thievish magpie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thievish', 'a.', 'Like a thief; acting by stealth; sly; secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thievish', 'a.', 'Partaking of the nature of theft; accomplished by\n   stealing; dishonest; as, a thievish practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thigh', 'n.', 'The proximal segment of the hind limb between the knee and\n   the trunk. See Femur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thigh', 'n.', 'The coxa, or femur, of an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thilk', 'pron.', 'That same; this; that.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thill', 'n.', 'One of the two long pieces of wood, extending before a\n   vehicle, between which a horse is hitched; a shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thill', 'n.', 'The floor of a coal mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thiller', 'n.', 'The horse which goes between the thills, or shafts, and\n   supports them; also, the last horse in a team; -- called also thill\n   horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thimble', 'n.', 'A kind of cap or cover, or sometimes a broad ring, for the\n   end of the finger, used in sewing to protect the finger when pushing\n   the needle through the material. It is usually made of metal, and has\n   upon the outer surface numerous small pits to catch the head of the\n   needle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thimble', 'n.', 'Any thimble-shaped appendage or fixure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thimble', 'n.', 'A tubular piece, generally a strut, through which a bolt\n   or pin passes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thimble', 'n.', 'A fixed or movable ring, tube, or lining placed in a hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thimble', 'n.', 'A tubular cone for expanding a flue; -- called ferrule in\n   England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thimble', 'n.', 'A ring of thin metal formed with a grooved circumference\n   so as to fit within an eye-spice, or the like, and protect it from\n   chafing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thimbleberry', 'n.', 'A kind of black raspberry (Rubus occidentalis),\n   common in America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thimbleeye', 'n.', 'The chub mackerel. See under Chub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thimblefuls', 'pl. ', 'of Thimbleful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thimbleful', 'n.', 'As much as a thimble will hold; a very small quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thimblerig', 'n.', 'A sleight-of-hand trick played with three small cups,\n   shaped like thimbles, and a small ball or little pea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thimblerigged', 'imp. & p. p.', 'of Thimblerig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thimblerigging', 'p. pr. & vb. n.', 'of Thimblerig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thimblerig', 'v. t.', 'To swindle by means of small cups or thimbles, and a\n   pea or small ball placed under one of them and quickly shifted to\n   another, the victim laying a wager that he knows under which cup it is;\n   hence, to cheat by any trick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thimblerigger', 'n.', 'One who cheats by thimblerigging, or tricks of\n   legerdemain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thimbleweed', 'n.', 'Any plant of the composite genus Rudbeckia, coarse\n   herbs somewhat resembling the sunflower; -- so called from their\n   conical receptacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thin', 'superl.', 'Having little thickness or extent from one surface to\n   its opposite; as, a thin plate of metal; thin paper; a thin board; a\n   thin covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thin', 'superl.', 'Rare; not dense or thick; -- applied to fluids or soft\n   mixtures; as, thin blood; thin broth; thin air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thin', 'superl.', 'Not close; not crowded; not filling the space; not\n   having the individuals of which the thing is composed in a close or\n   compact state; hence, not abundant; as, the trees of a forest are thin;\n   the corn or grass is thin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thin', 'superl.', 'Not full or well grown; wanting in plumpness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thin', 'superl.', 'Not stout; slim; slender; lean; gaunt; as, a person\n   becomes thin by disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thin', 'superl.', 'Wanting in body or volume; small; feeble; not full.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thin', 'superl.', 'Slight; small; slender; flimsy; wanting substance or\n   depth or force; superficial; inadequate; not sufficient for a covering;\n   as, a thin disguise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thin', 'adv.', 'Not thickly or closely; in a seattered state; as, seed sown\n   thin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thinned', 'imp. & p. p.', 'of Thin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thinning', 'p. pr. & vb. n.', 'of Thin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thin', 'v. t.', 'To make thin (in any of the senses of the adjective).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thin', 'v. i.', 'To grow or become thin; -- used with some adverbs, as out,\n   away, etc.; as, geological strata thin out, i. e., gradually diminish\n   in thickness until they disappear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thine', 'pron. & a.', 'A form of the possessive case of the pronoun thou,\n   now superseded in common discourse by your, the possessive of you, but\n   maintaining a place in solemn discourse, in poetry, and in the usual\n   language of the Friends, or Quakers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thing', 'n.', 'Whatever exists, or is conceived to exist, as a separate\n   entity, whether animate or inanimate; any separable or distinguishable\n   object of thought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thing', 'n.', 'An inanimate object, in distinction from a living being; any\n   lifeless material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thing', 'n.', 'A transaction or occurrence; an event; a deed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thing', 'n.', 'A portion or part; something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thing', 'n.', 'A diminutive or slighted object; any object viewed as merely\n   existing; -- often used in pity or contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thing', 'n.', 'Clothes; furniture; appurtenances; luggage; as, to pack or\n   store ones things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thing', 'n.', 'Whatever may be possessed or owned; a property; --\n   distinguished from person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thing', 'n.', 'In Scandinavian countries, a legislative or judicial\n   assembly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thought', 'imp. & p. p.', 'of Think');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thinking', 'p. pr. & vb. n.', 'of Think');
  INSERT INTO words (word, word_type, description) VALUES  (    'Think', 'v. t.', 'To seem or appear; -- used chiefly in the expressions\n   methinketh or methinks, and methought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Think', 'v. t.', 'To employ any of the intellectual powers except that of\n   simple perception through the senses; to exercise the higher\n   intellectual faculties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Think', 'v. t.', 'To call anything to mind; to remember; as, I would have\n   sent the books, but I did not think of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Think', 'v. t.', 'To reflect upon any subject; to muse; to meditate; to\n   ponder; to consider; to deliberate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Think', 'v. t.', 'To form an opinion by reasoning; to judge; to conclude;\n   to believe; as, I think it will rain to-morrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Think', 'v. t.', 'To purpose; to intend; to design; to mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Think', 'v. t.', 'To presume; to venture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Think', 'v. t.', 'To conceive; to imagine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Think', 'v. t.', 'To plan or design; to plot; to compass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Think', 'v. t.', 'To believe; to consider; to esteem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thinkable', 'a.', 'Capable of being thought or conceived; cogitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thinker', 'n.', 'One who thinks; especially and chiefly, one who thinks in\n   a particular manner; as, a close thinker; a deep thinker; a coherent\n   thinker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thinking', 'a.', 'Having the faculty of thought; cogitative; capable of a\n   regular train of ideas; as, man is a thinking being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thinking', 'n.', 'The act of thinking; mode of thinking; imagination;\n   cogitation; judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thinly', 'a.', 'In a thin manner; in a loose, scattered manner; scantily;\n   not thickly; as, ground thinly planted with trees; a country thinly\n   inhabited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thinner', 'n.', 'One who thins, or makes thinner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thinness', 'n.', 'The quality or state of being thin (in any of the senses\n   of the word).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thinnish', 'a.', 'Somewhat thin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thinolite', 'n.', 'A calcareous tufa, in part crystalline, occurring on a\n   large scale as a shore deposit about the Quaternary lake basins of\n   Nevada.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thin-skinned', 'a.', 'Having a thin skin; hence, sensitive; irritable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thio-', '', 'A combining form (also used adjectively) denoting the presence\n   of sulphur. See Sulpho-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thiocarbonate', 'n.', 'A sulphocarbonate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thiocarbonic', 'a.', 'Same as Sulphocarbonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thiocyanate', 'n.', 'Same as Sulphocyanate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thiocyanic', 'a.', 'Same as Sulphocyanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thionaphthene', 'n.', 'A double benzene and thiophene nucleus, C8H6S,\n   analogous to naphthalene, and like it the base of a large series of\n   derivatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thionic', 'a.', 'Of or pertaining to sulphur; containing or resembling\n   sulphur; specifically, designating certain of the thio compounds; as,\n   the thionic acids. Cf. Dithionic, Trithionic, Tetrathionic, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thionine', 'n.', 'An artificial red or violet dyestuff consisting of a\n   complex sulphur derivative of certain aromatic diamines, and obtained\n   as a dark crystalline powder; -- called also phenylene violet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thionol', 'n.', 'A red or violet dyestuff having a greenish metallic\n   luster. It is produced artificially, by the chemical dehydration of\n   thionine, as a brown amorphous powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thionoline', 'n.', 'A beautiful fluorescent crystalline substance,\n   intermediate in composition between thionol and thionine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thionyl', 'n.', 'The hypothetical radical SO, regarded as an essential\n   constituent of certain sulphurous compounds; as, thionyl chloride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thiophene', 'n.', 'A sulphur hydrocarbon, C4H4S, analogous to furfuran and\n   benzene, and acting as the base of a large number of substances which\n   closely resemble the corresponding aromatic derivatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thiophenic', 'a.', 'Of, pertaining to, or derived from, thiophene;\n   specifically, designating a certain acid analogous to benzoic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thiophenol', 'n.', 'A colorless mobile liquid, C6H5.SH, of an offensive\n   odor, and analogous to phenol; -- called also phenyl sulphydrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thiophthene', 'n.', 'A double thiophene nucleus, C6H4S2, analogous to\n   thionaphthene, and the base of a large series of compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thiosulphate', 'n.', 'A salt of thiosulphuric acid; -- formerly called\n   hyposulphite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thiosulphuric', 'a.', 'Of, pertaining to, or designating, an unstable acid,\n   H2S2O3, analogous to sulphuric acid, and formerly called hyposulphurous\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thiotolene', 'n.', 'A colorless oily liquid, C4H3S.CH3, analogous to, and\n   resembling, toluene; -- called also methyl thiophene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thioxene', 'n.', 'Any one of three possible metameric substances, which are\n   dimethyl derivatives of thiophene, like the xylenes from benzene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Third', 'a.', 'Next after the second; coming after two others; -- the\n   ordinal of three; as, the third hour in the day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Third', 'a.', 'Constituting or being one of three equal parts into which\n   anything is divided; as, the third part of a day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Third', 'n.', 'The quotient of a unit divided by three; one of three equal\n   parts into which anything is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Third', 'n.', 'The sixtieth part of a second of time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Third', 'n.', 'The third tone of the scale; the mediant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Third', 'n.', 'The third part of the estate of a deceased husband, which,\n   by some local laws, the widow is entitled to enjoy during her life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Third-borough', 'n.', 'An under constable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirdings', 'n. pl.', 'The third part of the corn or grain growing on the\n   ground at the tenants death, due to the lord for a heriot, as within\n   the manor of Turfat in Herefordshire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirdly', 'adv.', 'In the third place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Third-penny', 'n.', 'A third part of the profits of fines and penalties\n   imposed at the country court, which was among the perquisites enjoyed\n   by the earl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirled', 'imp. & p. p.', 'of Thirl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirling', 'p. pr. & vb. n.', 'of Thirl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirl', 'v. t.', 'To bore; to drill or thrill. See Thrill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirlage', 'n.', 'The right which the owner of a mill possesses, by\n   contract or law, to compel the tenants of a certain district, or of his\n   sucken, to bring all their grain to his mill for grinding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirst', 'n.', 'A sensation of dryness in the throat associated with a\n   craving for liquids, produced by deprivation of drink, or by some other\n   cause (as fear, excitement, etc.) which arrests the secretion of the\n   pharyngeal mucous membrane; hence, the condition producing this\n   sensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirst', 'n.', 'Fig.: A want and eager desire after anything; a craving or\n   longing; -- usually with for, of, or after; as, the thirst for gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirsted', 'imp. & p. p.', 'of Thirst');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirsting', 'p. pr. & vb. n.', 'of Thirst');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirst', 'n.', 'To feel thirst; to experience a painful or uneasy sensation\n   of the throat or fauces, as for want of drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirst', 'n.', 'To have a vehement desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirst', 'v. t.', 'To have a thirst for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirster', 'n.', 'One who thirsts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirstily', 'adv.', 'In a thirsty manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirstiness', 'n.', 'The state of being thirsty; thirst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirstle', 'n.', 'The throstle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirsty', 'n.', 'Feeling thirst; having a painful or distressing sensation\n   from want of drink; hence, having an eager desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirsty', 'n.', 'Deficient in moisture; dry; parched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirteen', 'a.', 'One more than twelve; ten and three; as, thirteen ounces\n   or pounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirteen', 'n.', 'The number greater by one than twelve; the sum of ten and\n   three; thirteen units or objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirteen', 'n.', 'A symbol representing thirteen units, as 13 or xiii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirteenth', 'a.', 'Next in order after the twelfth; the third after the\n   tenth; -- the ordinal of thirteen; as, the thirteenth day of the month.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirteenth', 'a.', 'Constituting or being one of thirteen equal parts into\n   which anything is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirteenth', 'n.', 'The quotient of a unit divided by thirteen; one of\n   thirteen equal parts into which anything is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirteenth', 'n.', 'The next in order after the twelfth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirteenth', 'n.', 'The interval comprising an octave and a sixth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirtieth', 'a.', 'Next in order after the twenty-ninth; the tenth after\n   the twentieth; -- the ordinal of thirty; as, the thirtieth day of the\n   month.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirtieth', 'a.', 'Constituting or being one of thirty equal parts into\n   which anything is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirtieth', 'n.', 'The quotient of a unit divided by thirty; one of thirty\n   equal parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirty', 'a.', 'Being three times ten; consisting of one more than\n   twenty-nine; twenty and ten; as, the month of June consists of thirty\n   days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirties', 'pl. ', 'of Thirty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirty', 'n.', 'The sum of three tens, or twenty and ten; thirty units or\n   objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirty', 'n.', 'A symbol expressing thirty, as 30, or XXX.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thirty-second', 'a.', 'Being one of thirty-two equal parts into which\n   anything is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'These', 'pl. ', 'of This');
  INSERT INTO words (word, word_type, description) VALUES  (    'This', 'pron. & a.', 'As a demonstrative pronoun, this denotes something\n   that is present or near in place or time, or something just mentioned,\n   or that is just about to be mentioned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'This', 'pron. & a.', 'As an adjective, this has the same demonstrative\n   force as the pronoun, but is followed by a noun; as, this book; this\n   way to town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thistle', 'n.', 'Any one of several prickly composite plants, especially\n   those of the genera Cnicus, Craduus, and Onopordon. The name is often\n   also applied to other prickly plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thistly', 'a.', 'Overgrown with thistles; as, thistly ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thistly', 'a.', 'Fig.: Resembling a thistle or thistles; sharp; pricking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thither', 'adv.', 'To that place; -- opposed to hither.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thither', 'adv.', 'To that point, end, or result; as, the argument tended\n   thither.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thither', 'a.', 'Being on the farther side from the person speaking;\n   farther; -- a correlative of hither; as, on the thither side of the\n   water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thither', 'a.', 'Applied to time: On the thither side of, older than; of\n   more years than. See Hither, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thitherto', 'adv.', 'To that point; so far.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thitherward', 'adv.', 'To ward that place; in that direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thitsee', 'n.', 'The varnish tree of Burmah (Melanorrhoea usitatissima).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thitsee', 'n.', 'A black varnish obtained from the tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thlipsis', 'n.', 'Compression, especially constriction of vessels by an\n   external cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tho', 'def. art.', 'The.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tho', 'pron. pl.', 'Those.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tho', 'adv.', 'Then.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tho', 'conj.', 'Though.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thole', 'n.', 'A wooden or metal pin, set in the gunwale of a boat, to\n   serve as a fulcrum for the oar in rowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thole', 'n.', 'The pin, or handle, of a scythe snath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tholed', 'imp. & p. p.', 'of Thole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tholing', 'p. pr. & vb. n.', 'of Thole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thole', 'v. t.', 'To bear; to endure; to undergo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thole', 'v. i.', 'To wait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thomaean', 'n.', 'Alt. of Thomean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thomean', 'n.', 'A member of the ancient church of Christians established\n   on the Malabar coast of India, which some suppose to have been\n   originally founded by the Apostle Thomas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thomism', 'n.', 'Alt. of Thomaism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thomaism', 'n.', 'The doctrine of Thomas Aquinas, esp. with respect to\n   predestination and grace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thomist', 'n.', 'A follower of Thomas Aquinas. See Scotist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thomite', 'n.', 'A Thomaean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thomsenolite', 'n.', 'A fluoride of aluminium, calcium, and sodium\n   occurring with the cryolite of Greenland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thomsens disease', '', 'An affection apparently congenital, consisting in\n   tonic contraction and stiffness of the voluntary muscles occurring\n   after a period of muscular inaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thomsonian', 'a.', 'Of or pertaining to Thomsonianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thomsonian', 'n.', 'A believer in Thomsonianism; one who practices\n   Thomsonianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thomsonianism', 'n.', 'An empirical system which assumes that the human\n   body is composed of four elements, earth, air, fire, and water, and\n   that vegetable medicines alone should be used; -- from the founder, Dr.\n   Samuel Thomson, of Massachusetts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thomsonite', 'n.', 'A zeolitic mineral, occurring generally in masses of a\n   radiated structure. It is a hydrous silicate of aluminia, lime, and\n   soda. Called also mesole, and comptonite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thong', 'n.', 'A strap of leather; especially, one used for fastening\n   anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thooid', 'a.', 'Of or pertaining to a group of carnivores, including the\n   wovels and the dogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thor', 'n.', 'The god of thunder, and son of Odin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoracentesis', 'n.', 'The operation of puncturing the chest wall so as to\n   let out liquids contained in the cavity of the chest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoracic', 'a.', 'Of or pertaining to the thorax, or chest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoracic', 'n.', 'One of a group of fishes having the ventral fins placed\n   beneath the thorax or beneath the pectorial fins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoracica', 'n. pl.', 'A division of cirripeds including those which have\n   six thoracic segments, usually bearing six pairs of cirri. The common\n   barnacles are examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoracometer', 'n.', 'Same as Stethometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoracoplasty', 'n.', 'A remodeling or reshaping of the thorax; especially,\n   the operation of removing the ribs, so as to obliterate the pleural\n   cavity in cases of empyema.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoracostraca', 'a.', 'An extensive division of Crustacea, having a dorsal\n   shield or carapec/ //niting all, or nearly all, of the thoracic somites\n   to the head. It includes the crabs, lobsters, shrimps, and similar\n   species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoracotomy', 'n.', 'The operation of opening the pleural cavity by\n   incision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoral', 'a.', 'Of or pertaining to a bed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorax', 'n.', 'The part of the trunk between the neck and the abdomen,\n   containing that part of the body cavity the walls of which are\n   supported by the dorsal vertebrae, the ribs, and the sternum, and which\n   the heart and lungs are situated; the chest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorax', 'n.', 'The middle region of the body of an insect, or that region\n   which bears the legs and wings. It is composed of three united somites,\n   each of which is composed of several distinct parts. See Illust. in\n   Appendix. and Illust. of Coleoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorax', 'n.', 'The second, or middle, region of the body of a crustacean,\n   arachnid, or other articulate animal. In the case of decapod Crustacea,\n   some writers include under the term thorax only the three segments\n   bearing the maxillipeds; others include also the five segments bearing\n   the legs. See Illust. in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorax', 'n.', 'A breastplate, cuirass, or corselet; especially, the\n   breastplate worn by the ancient Greeks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoria', 'n.', 'A rare white earthy substance, consisting of the oxide of\n   thorium; -- formerly called also thorina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoric', 'a.', 'Of or pertaining to thorium; designating the compounds of\n   thorium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorite', 'n.', 'A mineral of a brown to black color, or, as in the variety\n   orangite, orange-yellow. It is essentially a silicate of thorium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorium', 'n.', 'A metallic element found in certain rare minerals, as\n   thorite, pyrochlore, monazite, etc., and isolated as an infusible gray\n   metallic powder which burns in the air and forms thoria; -- formerly\n   called also thorinum. Symbol Th. Atomic weight 232.0.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorn', 'n.', 'A hard and sharp-pointed projection from a woody stem;\n   usually, a branch so transformed; a spine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorn', 'n.', 'Any shrub or small tree which bears thorns; especially, any\n   species of the genus Crataegus, as the hawthorn, whitethorn, cockspur\n   thorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorn', 'n.', 'Fig.: That which pricks or annoys as a thorn; anything\n   troublesome; trouble; care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorn', 'n.', 'The name of the Anglo-Saxon letter /, capital form /. It was\n   used to represent both of the sounds of English th, as in thin, then.\n   So called because it was the initial letter of thorn, a spine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorn', 'v. t.', 'To prick, as with a thorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thornback', 'n.', 'A European skate (Raia clavata) having thornlike spines\n   on its back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thornback', 'n.', 'The large European spider crab or king crab (Maia\n   squinado).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thornbill', 'n.', 'Any one of several species of small, brilliantly colored\n   American birds of the genus Rhamphomicron. They have a long, slender,\n   sharp bill, and feed upon honey, insects, and the juice of the sugar\n   cane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thornbird', 'n.', 'A small South American bird (Anumbius anumbii) allied to\n   the ovenbirds of the genus Furnarius). It builds a very large and\n   complex nest of twigs and thorns in a bush or tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thornbut', 'n.', 'The turbot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorn-headed', 'a.', 'Having a head armed with thorns or spines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thornless', 'a.', 'Destitute of, or free from, thorns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thornset', 'a.', 'Set with thorns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorntail', 'n.', 'A beautiful South American humming bird (Gouldia\n   Popelairii), having the six outer tail feathers long, slender, and\n   pointed. The head is ornamented with a long, pointed crest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorny', 'superl.', 'Full of thorns or spines; rough with thorns; spiny;\n   as, a thorny wood; a thorny tree; a thorny crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorny', 'superl.', 'Like a thorn or thorns; hence, figuratively,\n   troublesome; vexatious; harassing; perplexing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoro', 'a.', 'Thorough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorough', 'prep.', 'Through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorough', 'a.', 'Passing through; as, thorough lights in a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorough', 'a.', 'Passing through or to the end; hence, complete; perfect;\n   as, a thorough reformation; thorough work; a thorough translator; a\n   thorough poet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorough', 'adv.', 'Thoroughly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorough', 'adv.', 'Through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorough', 'n.', 'A furrow between two ridges, to drain off the surface\n   water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorough bass', '', 'The representation of chords by figures placed under\n   the base; figured bass; basso continuo; -- sometimes used as synonymous\n   with harmony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorough-brace', 'n.', 'A leather strap supporting the body of a carriage,\n   and attached to springs, or serving as a spring. See Illust. of Chaise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoroughbred', 'a.', 'Bred from the best blood through a long line;\n   pure-blooded; -- said of stock, as horses. Hence, having the\n   characteristics of such breeding; mettlesome; courageous; of elegant\n   form, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoroughbred', 'n.', 'A thoroughbred animal, especially a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoroughfare', 'n.', 'A passage through; a passage from one street or\n   opening to another; an unobstructed way open to the public; a public\n   road; hence, a frequented street.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoroughfare', 'n.', 'A passing or going through; passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoroughgoing', 'a.', 'Going through, or to the end or bottom; very\n   thorough; complete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoroughgoing', 'a.', 'Going all lengths; extreme; thoroughplaced; -- less\n   common in this sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorough-lighted', 'a.', 'Provided with thorough lights or windows at\n   opposite sides, as a room or building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoroughly', 'adv.', 'In a thorough manner; fully; entirely; completely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoroughness', 'n.', 'The quality or state of being thorough; completeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoroughpaced', 'a.', 'Perfect in what is undertaken; complete; going all\n   lengths; as, a thoroughplaced Tory or Whig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoroughpin', 'n.', 'A disease of the hock (sometimes of the knee) of a\n   horse, caused by inflammation of the synovial membrane and a consequent\n   excessive secretion of the synovial fluid; -- probably so called\n   because there is usually an oval swelling on each side of the leg,\n   appearing somewhat as if a pin had been thrust through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoroughsped', 'a.', 'Fully accomplished; thoroughplaced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoroughstitch', 'adv.', 'So as to go the whole length of any business;\n   fully; completely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoroughwax', 'n.', 'An umbelliferous plant (Bupleurum rotundifolium) with\n   perfoliate leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoroughwax', 'n.', 'Thoroughwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoroughwort', 'n.', 'Same as Boneset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorow', 'prep.', 'Through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorow', 'a.', 'Thorough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorp', 'n.', 'Alt. of Thorpe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thorpe', 'n.', 'A group of houses in the country; a small village; a\n   hamlet; a dorp; -- now chiefly occurring in names of places and\n   persons; as, Althorp, Mablethorpe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Those', 'pron.', 'The plural of that. See That.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoth', 'n.', 'The god of eloquence and letters among the ancient\n   Egyptians, and supposed to be the inventor of writing and philosophy.\n   He corresponded to the Mercury of the Romans, and was usually\n   represented as a human figure with the head of an ibis or a lamb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoth', 'n.', 'The Egyptian sacred baboon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thou', 'obj.', 'The second personal pronoun, in the singular number,\n   denoting the person addressed; thyself; the pronoun which is used in\n   addressing persons in the solemn or poetical style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thou', 'v. t.', 'To address as thou, esp. to do so in order to treat with\n   insolent familiarity or contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thou', 'v. i.', 'To use the words thou and thee in discourse after the\n   manner of the Friends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Though', 'conj.', 'Granting, admitting, or supposing that; notwithstanding\n   that; if.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Though', 'adv.', 'However; nevertheless; notwithstanding; -- used in\n   familiar language, and in the middle or at the end of a sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thought', '', 'imp. & p. p. of Think.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thought', 'n.', 'The act of thinking; the exercise of the mind in any of\n   its higher forms; reflection; cogitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thought', 'n.', 'Meditation; serious consideration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thought', 'n.', 'That which is thought; an idea; a mental conception,\n   whether an opinion, judgment, fancy, purpose, or intention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thought', 'n.', 'Solicitude; anxious care; concern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thought', 'n.', 'A small degree or quantity; a trifle; as, a thought\n   longer; a thought better.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoughtful', 'a.', 'Full of thought; employed in meditation; contemplative;\n   as, a man of thoughtful mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoughtful', 'a.', 'Attentive; careful; exercising the judgment; having the\n   mind directed to an object; as, thoughtful of gain; thoughtful in\n   seeking truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoughtful', 'a.', 'Anxious; solicitous; concerned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoughtless', 'adv.', 'Lacking thought; careless; inconsiderate; rash; as,\n   a thoughtless person, or act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoughtless', 'adv.', 'Giddy; gay; dissipated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thoughtless', 'adv.', 'Deficient in reasoning power; stupid; dull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thousand', 'n.', 'The number of ten hundred; a collection or sum consisting\n   of ten times one hundred units or objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thousand', 'n.', 'Hence, indefinitely, a great number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thousand', 'n.', 'A symbol representing one thousand units; as, 1,000, M or\n   CI/.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thousand', 'a.', 'Consisting of ten hundred; being ten times one hundred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thousand', 'a.', 'Hence, consisting of a great number indefinitely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thousandfold', 'a.', 'Multiplied by a thousand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thousand legs', '', 'A millepid, or galleyworm; -- called also\n   thousand-legged worm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thousandth', 'a.', 'Next in order after nine hundred and ninty-nine; coming\n   last of a thousand successive individuals or units; -- the ordinal of\n   thousand; as, the thousandth part of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thousandth', 'a.', 'Constituting, or being one of, a thousand equal parts\n   into which anything is divided; the tenth of a hundredth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thousandth', 'a.', 'Occurring as being one of, or the last one of, a very\n   great number; very small; minute; -- used hyperbolically; as, to do a\n   thing for the thousandth time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thousandth', 'n.', 'The quotient of a unit divided by a thousand; one of a\n   thousand equal parts into which a unit is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thowel', 'n.', 'Alt. of Thowl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thowl', 'n.', 'A thole pin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thowl', 'n.', 'A rowlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thracian', 'a.', 'Of or pertaining to Thrace, or its people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thracian', 'n.', 'A native or inhabitant of Thrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrack', 'v. t.', 'To load or burden; as, to thrack a man with property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrackscat', 'n.', 'Metal still in the mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thraldom', 'n.', 'The condition of a thrall; slavery; bondage; state of\n   servitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrall', 'n.', 'A slave; a bondman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrall', 'n.', 'Slavery; bondage; servitude; thraldom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrall', 'n.', 'A shelf; a stand for barrels, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrall', 'a.', 'Of or pertaining to a thrall; in the condition of a thrall;\n   bond; enslaved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrall', 'v. t.', 'To enslave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thralldom', 'n.', 'Thraldom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrall-less', 'a.', 'Having no thralls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrall-less', 'a.', 'Not enslaved; not subject to bonds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrall-like', 'a.', 'Resembling a thrall, or his condition, feelings, or\n   the like; slavish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thranite', 'n.', 'One of the rowers on the topmost of the three benches in\n   a trireme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrapple', 'n.', 'Windpipe; throttle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrashed', 'imp. & p. p.', 'of Thresh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrashing', 'p. pr. & vb. n.', 'of Thresh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrash', 'v. t.', 'Alt. of Thresh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thresh', 'v. t.', 'To beat out grain from, as straw or husks; to beat the\n   straw or husk of (grain) with a flail; to beat off, as the kernels of\n   grain; as, to thrash wheat, rye, or oats; to thrash over the old straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thresh', 'v. t.', 'To beat soundly, as with a stick or whip; to drub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrash', 'v. t.', 'Alt. of Thresh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thresh', 'v. t.', 'To practice thrashing grain or the like; to perform the\n   business of beating grain from straw; as, a man who thrashes well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thresh', 'v. t.', 'Hence, to labor; to toil; also, to move violently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrashel', 'n.', 'An instrument to thrash with; a flail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrasher', 'n.', 'Alt. of Thresher');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thresher', 'n.', 'One who, or that which, thrashes grain; a thrashing\n   machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thresher', 'n.', 'A large and voracious shark (Alopias vulpes), remarkable\n   for the great length of the upper lobe of its tail, with which it\n   beats, or thrashes, its prey. It is found both upon the American and\n   the European coasts. Called also fox shark, sea ape, sea fox, slasher,\n   swingle-tail, and thrasher shark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thresher', 'n.', 'A name given to the brown thrush and other allied\n   species. See Brown thrush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrashing', '', 'a. & n. from Thrash, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrasonical', 'a.', 'Of or pertaining to Thraso; like, or becoming to,\n   Thraso; bragging; boastful; vainglorious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thraste', 'imp.', 'of Thraste');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrast', 'p. p.', 'of Thraste');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thraste', 'v. t.', 'To thrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrave', 'n.', 'Twenty-four (in some places, twelve) sheaves of wheat; a\n   shock, or stook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrave', 'n.', 'The number of two dozen; also, an indefinite number; a\n   bunch; a company; a throng.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thraw', 'n. & v.', 'See Throse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thread', 'n.', 'A very small twist of flax, wool, cotton, silk, or other\n   fibrous substance, drawn out to considerable length; a compound cord\n   consisting of two or more single yarns doubled, or joined together, and\n   twisted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thread', 'n.', 'A filament, as of a flower, or of any fibrous substance, as\n   of bark; also, a line of gold or silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thread', 'n.', 'The prominent part of the spiral of a screw or nut; the\n   rib. See Screw, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thread', 'n.', 'Fig.: Something continued in a long course or tenor; a,s\n   the thread of life, or of a discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thread', 'n.', 'Fig.: Composition; quality; fineness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threaded', 'imp. & p. p.', 'of Thread');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threading', 'p. pr. & vb. n.', 'of Thread');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thread', 'v. t.', 'To pass a thread through the eye of; as, to thread a\n   needle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thread', 'v. t.', 'To pass or pierce through as a narrow way; also, to\n   effect or make, as ones way, through or between obstacles; to thrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thread', 'v. t.', 'To form a thread, or spiral rib, on or in; as, to thread\n   a screw or nut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threadbare', 'a.', 'Worn to the naked thread; having the nap worn off;\n   threadbare clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threadbare', 'a.', 'Fig.: Worn out; as, a threadbare subject; stale topics\n   and threadbare quotations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threadbareness', 'n.', 'The state of being threadbare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threaden', 'a.', 'Made of thread; as, threaden sails; a threaden fillet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threader', 'n.', 'A device for assisting in threading a needle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threader', 'n.', 'A tool or machine for forming a thread on a screw or in a\n   nut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threadfin', 'n.', 'Any one of several species of fishes belonging to\n   Polynemus and allied genera. They have numerous long pectoral\n   filaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threadfish', 'n.', 'The cutlass fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threadfish', 'n.', 'A carangoid fish (Caranx gallus, or C. crinitus) having\n   the anterior rays of the soft dorsal and anal fins prolonged in the\n   form of long threads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threadiness', 'n.', 'Quality of being thready.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thread-shaped', 'a.', 'Having the form of a thread; filiform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threadworm', 'n.', 'Any long, slender nematode worm, especially the pinworm\n   and filaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thready', 'a.', 'Like thread or filaments; slender; as, the thready roots\n   of a shrub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thready', 'a.', 'Containing, or consisting of, thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threaped', 'imp. & p. p.', 'of Threap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threaping', 'p. pr. & vb. n.', 'of Threap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threap', 'v. t.', 'To call; to name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threap', 'v. t.', 'To maintain obstinately against denial or contradiction;\n   also, to contend or argue against (another) with obstinacy; to chide;\n   as, he threaped me down that it was so.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threap', 'v. t.', 'To beat, or thrash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threap', 'v. t.', 'To cozen, or cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threap', 'v. i.', 'To contend obstinately; to be pertinacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threap', 'n.', 'An obstinate decision or determination; a pertinacious\n   affirmation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threat', 'n.', 'The expression of an intention to inflict evil or injury on\n   another; the declaration of an evil, loss, or pain to come; menace;\n   threatening; denunciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threat', 'n.', 'To threaten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threatened', 'imp. & p. p.', 'of Threaten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threatening', 'p. pr. & vb. n.', 'of Threaten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threaten', 'v. t.', 'To utter threats against; to menace; to inspire with\n   apprehension; to alarm, or attempt to alarm, as with the promise of\n   something evil or disagreeable; to warn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threaten', 'v. t.', 'To exhibit the appearance of (something evil or\n   unpleasant) as approaching; to indicate as impending; to announce the\n   conditional infliction of; as, to threaten war; to threaten death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threaten', 'v. i.', 'To use threats, or menaces; also, to have a\n   threatening appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threatener', 'n.', 'One who threatens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threatening', '', 'a. & n. from Threaten, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threatful', 'a.', 'Full of threats; having a menacing appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threave', 'n.', 'Same as Thrave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three', 'a.', 'One more than two; two and one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three', 'n.', 'The number greater by a unit than two; three units or\n   objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three', 'n.', 'A symbol representing three units, as 3 or iii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-coat', 'a.', 'Having or consisting of three coats; -- applied to\n   plastering which consists of pricking-up, floating, and a finishing\n   coat; or, as called in the United States, a scratch coat, browning, and\n   finishing coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-cornered', 'a.', 'Having three corners, or angles; as, a\n   three-cornered hat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-cornered', 'a.', 'Having three prominent longitudinal angles; as, a\n   three-cornered stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-decker', 'n.', 'A vessel of war carrying guns on three decks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-flowered', 'a.', 'Bearing three flowers together, or only three\n   flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threefold', 'a.', 'Consisting of three, or thrice repeated; triple; as,\n   threefold justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-handed', 'a.', 'Said of games or contests where three persons play\n   against each other, or two against one; as, a three-handed game of\n   cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-leafed', 'a.', 'Alt. of Three-leaved');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-leaved', 'a.', 'Producing three leaves; as, three-leaved nightshade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-leaved', 'a.', 'Consisting of three distinct leaflets; having the\n   leaflets arranged in threes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-lobed', 'a.', 'Having three lobes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-nerved', 'a.', 'Having three nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-parted', 'a.', 'Divided into, or consisting of, three parts;\n   tripartite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threepence', 'n.', 'A small silver coin of three times the value of a\n   penny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threepenny', 'a.', 'Costing or worth three pence; hence, worth but little;\n   poor; mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-pile', 'n.', 'An old name for the finest and most costly kind of\n   velvet, having a fine, thick pile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-piled', 'a.', 'Having the quality of three-pile; best; most costly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-piled', 'a.', 'Fig.: Extravagant; exaggerated; high-flown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-piled', 'a.', 'Accustomed to wearing three-pile; hence, of high rank,\n   or wealth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-ply', 'a.', 'Consisting of three distinct webs inwrought together in\n   weaving, as cloth or carpeting; having three strands; threefold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-pointed', 'a.', 'Having three acute or setigerous points;\n   tricuspidate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-quarter', 'a.', 'Measuring thirty inches by twenty-five; -- said of\n   portraitures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-score', 'a.', 'Thrice twenty; sixty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-sided', 'a.', 'Having three sides, especially three plane sides; as,\n   a three-sided stem, leaf, petiole, peduncle, scape, or pericarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-square', 'a.', 'Having a cross section in the form of an equilateral\n   triangle; -- said especially of a kind of file.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-valved', 'a.', 'Consisting of, or having, three valves; opening with\n   three valves; as, a three-valved pericarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Three-way', 'a.', 'Connected with, or serving to connect, three channels or\n   pipes; as, a three-way cock or valve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threne', 'n.', 'Lamentation; threnody; a dirge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threnetic', 'a.', 'Alt. of Threnetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threnetical', 'a.', 'Pertaining to a threne; sorrowful; mournful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threnode', 'n.', 'A threne, or threnody; a dirge; a funeral song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threnodist', 'n.', 'One who composes, delivers, or utters, a threnode, or\n   threnody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threnody', 'n.', 'A song of lamentation; a threnode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threpe', 'v. t.', 'To call; to term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threpsology', 'n.', 'The doctrine of nutrition; a treatise on nutrition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threshed', 'imp. & p. p.', 'of Thresh');
