  INSERT INTO words (word, word_type, description) VALUES  (    'Thresher', 'n.', 'Same as Thrasher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thresh-fold', 'n.', 'Threshold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threshold', 'n.', 'The plank, stone, or piece of timber, which lies under a\n   door, especially of a dwelling house, church, temple, or the like; the\n   doorsill; hence, entrance; gate; door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threshold', 'n.', 'Fig.: The place or point of entering or beginning,\n   entrance; outset; as, the threshold of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threshwold', 'n.', 'Threshold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threste', 'imp.', 'of Threste');
