  INSERT INTO words (word, word_type, description) VALUES  (    'Thretty', 'a.', 'Thirty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threw', '', 'imp. of Throw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thribble', 'a.', 'Triple; treble; threefold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrice', 'adv.', 'Three times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrice', 'adv.', 'In a threefold manner or degree; repeatedly; very.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thricecock', 'n.', 'The missel thrush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrid', 'a.', 'Third.');
