  INSERT INTO words (word, word_type, description) VALUES  (    'Thridding', 'p. pr. & vb. n.', 'of Thrid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrid', 'v. t.', 'To pass through in the manner of a thread or a needle; to\n   make or find a course through; to thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrid', 'v. t.', 'To make or effect (a way or course) through something;\n   as, to thrid ones way through a wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrid', 'n.', 'Thread; continuous line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrifallow', 'v. t.', 'See Thryfallow, and Trifallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrift', 'n.', 'A thriving state; good husbandry; economical management in\n   regard to property; frugality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrift', 'n.', 'Success and advance in the acquisition of property;\n   increase of worldly goods; gain; prosperity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrift', 'n.', 'Vigorous growth, as of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrift', 'n.', 'One of several species of flowering plants of the genera\n   Statice and Armeria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thriftily', 'adv.', 'In a thrifty manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thriftily', 'adv.', 'Carefully; properly; becomingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thriftiness', 'n.', 'The quality or state of being thrifty; thrift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thriftless', 'a.', 'Without thrift; not prudent or prosperous in money\n   affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrifty', 'superl.', 'Given to, or evincing, thrift; characterized by\n   economy and good menegement of property; sparing; frugal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrifty', 'superl.', 'Thriving by industry and frugality; prosperous in the\n   acquisition of worldly goods; increasing in wealth; as, a thrifty\n   farmer or mechanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrifty', 'superl.', 'Growing rapidly or vigorously; thriving; as, a\n   thrifty plant or colt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrifty', 'superl.', 'Secured by thrift; well husbanded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrifty', 'superl.', 'Well appearing; looking or being in good condition;\n   becoming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrill', 'n.', 'A warbling; a trill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrill', 'v. t.', 'A breathing place or hole; a nostril, as of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrilled', 'imp. & p. p.', 'of Thrill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrilling', 'p. pr. & vb. n.', 'of Thrill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrill', 'v. t.', 'To perforate by a pointed instrument; to bore; to\n   transfix; to drill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrill', 'v. t.', 'Hence, to affect, as if by something that pierces or\n   pricks; to cause to have a shivering, throbbing, tingling, or exquisite\n   sensation; to pierce; to penetrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrill', 'v. t.', 'To hurl; to throw; to cast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrill', 'v. i.', 'To pierce, as something sharp; to penetrate; especially,\n   to cause a tingling sensation that runs through the system with a\n   slight shivering; as, a sharp sound thrills through the whole frame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrill', 'v. i.', 'To feel a sharp, shivering, tingling, or exquisite\n   sensation, running through the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrill', 'n.', 'A drill. See 3d Drill, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrill', 'n.', 'A sensation as of being thrilled; a tremulous excitement;\n   as, a thrill of horror; a thrill of joy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrillant', 'a.', 'Piercing; sharp; thrilling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrilling', 'a.', 'Causing a thrill; causing tremulous excitement; deeply\n   moving; as, a thrilling romance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throng', 'imp.', 'of Thring');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thring', 'v. t. & i.', 'To press, crowd, or throng.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrips', 'n.', 'Any one of numerous small species of Thysanoptera,\n   especially those which attack useful plants, as the grain thrips\n   (Thrips cerealium).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrist', 'n.', 'Thrist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrittene', 'a.', 'Thirteen.');
