  INSERT INTO words (word, word_type, description) VALUES  (    'Thrive', 'v. i.', 'To prosper by industry, economy, and good management of\n   property; to increase in goods and estate; as, a farmer thrives by good\n   husbandry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrive', 'v. i.', 'To prosper in any business; to have increase or success.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrive', 'v. i.', 'To increase in bulk or stature; to grow vigorously or\n   luxuriantly, as a plant; to flourish; as, young cattle thrive in rich\n   pastures; trees thrive in a good soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thriven', '', 'p. p. of Thrive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thriver', 'n.', 'One who thrives, or prospers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrivingly', 'adv.', 'In a thriving manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrivingness', 'n.', 'The quality or condition of one who thrives;\n   prosperity; growth; increase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thro', '', 'A contraction of Through.');
