  INSERT INTO words (word, word_type, description) VALUES  (    'Throat', 'n.', 'A contracted portion of a vessel, or of a passage way; as,\n   the throat of a pitcher or vase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throat', 'n.', 'The part of a chimney between the gathering, or portion of\n   the funnel which contracts in ascending, and the flue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throat', 'n.', 'The upper fore corner of a boom-and-gaff sail, or of a\n   staysail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throat', 'n.', 'That end of a gaff which is next the mast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throat', 'n.', 'The angle where the arm of an anchor is joined to the\n   shank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throat', 'n.', 'The inside of a timber knee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throat', 'n.', 'The orifice of a tubular organ; the outer end of the tube\n   of a monopetalous corolla; the faux, or fauces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throat', 'v. t.', 'To utter in the throat; to mutter; as, to throat\n   threats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throat', 'v. t.', 'To mow, as beans, in a direction against their bending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throatband', 'n.', 'Same as Throatlatch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throatboll', 'n.', 'The Adams apple in the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throating', 'n.', 'A drip, or drip molding.');
