  INSERT INTO words (word, word_type, description) VALUES  (    'Throb', 'v. i.', 'To beat, or pulsate, with more than usual force or\n   rapidity; to beat in consequence of agitation; to palpitate; -- said of\n   the heart, pulse, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throb', 'n.', 'A beat, or strong pulsation, as of the heart and arteries; a\n   violent beating; a papitation:');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throdden', 'v. i.', 'To grow; to thrive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throe', 'n.', 'Extreme pain; violent pang; anguish; agony; especially, one\n   of the pangs of travail in childbirth, or purturition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throe', 'n.', 'A tool for splitting wood into shingles; a frow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throe', 'v. i.', 'To struggle in extreme pain; to be in agony; to agonize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throe', 'v. t.', 'To put in agony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrombosis', 'n.', 'The obstruction of a blood vessel by a clot formed at\n   the site of obstruction; -- distinguished from embolism, which is\n   produced by a clot or foreign body brought from a distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrombi', 'pl. ', 'of Thrombus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrombus', 'n.', 'A clot of blood formed of a passage of a vessel and\n   remaining at the site of coagulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrombus', 'n.', 'A tumor produced by the escape of blood into the\n   subcutaneous cellular tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throne', 'n.', 'A chair of state, commonly a royal seat, but sometimes the\n   seat of a prince, bishop, or other high dignitary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throne', 'n.', 'Hence, sovereign power and dignity; also, the one who\n   occupies a throne, or is invested with sovereign authority; an exalted\n   or dignified personage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throne', 'n.', 'A high order of angels in the celestial hierarchy; -- a\n   meaning given by the schoolmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throned', 'imp. & p. p.', 'of Throne');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throning', 'p. pr. & vb. n.', 'of Throne');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throne', 'v. t.', 'To place on a royal seat; to enthrone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throne', 'v. t.', 'To place in an elevated position; to give sovereignty or\n   dominion to; to exalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throne', 'v. i.', 'To be in, or sit upon, a throne; to be placed as if upon\n   a throne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throneless', 'a.', 'Having no throne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throng', 'n.', 'A multitude of persons or of living beings pressing or\n   pressed into a close body or assemblage; a crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throng', 'n.', 'A great multitude; as, the heavenly throng.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thronged', 'imp. & p. p.', 'of Throng');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thronging', 'p. pr. & vb. n.', 'of Throng');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throng', 'v. i.', 'To crowd together; to press together into a close body,\n   as a multitude of persons; to gather or move in multitudes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throng', 'v. t.', 'To crowd, or press, as persons; to oppress or annoy with\n   a crowd of living beings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throng', 'v. t.', 'To crowd into; to fill closely by crowding or pressing\n   into, as a hall or a street.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throng', 'a.', 'Thronged; crowded; also, much occupied; busy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throngly', 'adv.', 'In throngs or crowds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throp', 'n.', 'A thorp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thropple', 'n.', 'The windpipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thropple', 'v. t.', 'To throttle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throstle', 'n.', 'The song thrush. See under Song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throstle', 'n.', 'A machine for spinning wool, cotton, etc., from the rove,\n   consisting of a set of drawing rollers with bobbins and flyers, and\n   differing from the mule in having the twisting apparatus stationary and\n   the processes continuous; -- so called because it makes a singing\n   noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throstling', 'n.', 'A disease of bovine cattle, consisting of a swelling\n   under the throat, which, unless checked, causes strangulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throttle', 'n.', 'The windpipe, or trachea; the weasand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throttle', 'n.', 'The throttle valve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throttled', 'imp. & p. p.', 'of Throttle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throttling', 'p. pr. & vb. n.', 'of Throttle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throttle', 'v. t.', 'To compress the throat of; to choke; to strangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throttle', 'v. t.', 'To utter with breaks and interruption, in the manner\n   of a person half suffocated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throttle', 'v. t.', 'To shut off, or reduce flow of, as steam to an engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throttle', 'v. i.', 'To have the throat obstructed so as to be in danger of\n   suffocation; to choke; to suffocate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throttle', 'v. i.', 'To breathe hard, as when nearly suffocated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throttler', 'n.', 'One who, or that which, throttles, or chokes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throttler', 'n.', 'See Flasher, 3 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Through', 'prep.', 'From end to end of, or from side to side of; from one\n   surface or limit of, to the opposite; into and out of at the opposite,\n   or at another, point; as, to bore through a piece of timber, or through\n   a board; a ball passes through the side of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Through', 'prep.', 'Between the sides or walls of; within; as, to pass\n   through a door; to go through an avenue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Through', 'prep.', 'By means of; by the agency of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Through', 'prep.', 'Over the whole surface or extent of; as, to ride\n   through the country; to look through an account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Through', 'prep.', 'Among or in the midst of; -- used to denote passage;\n   as, a fish swims through the water; the light glimmers through a\n   thicket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Through', 'prep.', 'From the beginning to the end of; to the end or\n   conclusion of; as, through life; through the year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Through', 'adv.', 'From one end or side to the other; as, to pierce a thing\n   through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Through', 'adv.', 'From beginning to end; as, to read a letter through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Through', 'adv.', 'To the end; to a conclusion; to the ultimate purpose;\n   as, to carry a project through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Through', 'a.', 'Going or extending through; going, extending, or serving\n   from the beginning to the end; thorough; complete; as, a through line;\n   a through ticket; a through train. Also, admitting of passage through;\n   as, a through bridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throughly', 'adv.', 'Thoroughly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throughout', 'prep.', 'Quite through; from one extremity to the other of;\n   also, every part of; as, to search throughout the house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throughout', 'adv.', 'In every part; as, the cloth was of a piece\n   throughout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throve', '', 'imp. of Thrive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'n.', 'Pain; especially, pain of travail; throe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'n.', 'Time; while; space of time; moment; trice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Threw', 'imp.', 'of Throw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrown', 'p. p.', 'of Throw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throwing', 'p. pr. & vb. n.', 'of Throw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'v. t.', 'To fling, cast, or hurl with a certain whirling motion of\n   the arm, to throw a ball; -- distinguished from to toss, or to bowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'v. t.', 'To fling or cast in any manner; to drive to a distance\n   from the hand or from an engine; to propel; to send; as, to throw\n   stones or dust with the hand; a cannon throws a ball; a fire engine\n   throws a stream of water to extinguish flames.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'v. t.', 'To drive by violence; as, a vessel or sailors may be\n   thrown upon a rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'v. t.', 'To cause to take a strategic position; as, he threw a\n   detachment of his army across the river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'v. t.', 'To overturn; to prostrate in wrestling; as, a man throws\n   his antagonist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'v. t.', 'To cast, as dice; to venture at dice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'v. t.', 'To put on hastily; to spread carelessly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'v. t.', 'To divest or strip ones self of; to put off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'v. t.', 'To form or shape roughly on a throwing engine, or\n   potters wheel, as earthen vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'v. t.', 'To give forcible utterance to; to cast; to vent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'v. t.', 'To bring forth; to produce, as young; to bear; -- said\n   especially of rabbits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'v. t.', 'To twist two or more filaments of, as silk, so as to form\n   one thread; to twist together, as singles, in a direction contrary to\n   the twist of the singles themselves; -- sometimes applied to the whole\n   class of operations by which silk is prepared for the weaver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'v. i.', 'To perform the act of throwing or casting; to cast;\n   specifically, to cast dice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'n.', 'The act of hurling or flinging; a driving or propelling from\n   the hand or an engine; a cast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'n.', 'A stroke; a blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'n.', 'The distance which a missile is, or may be, thrown; as, a\n   stones throw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'n.', 'A cast of dice; the manner in which dice fall when cast; as,\n   a good throw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'n.', 'An effort; a violent sally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'n.', 'The extreme movement given to a sliding or vibrating\n   reciprocating piece by a cam, crank, eccentric, or the like; travel;\n   stroke; as, the throw of a slide valve. Also, frequently, the length of\n   the radius of a crank, or the eccentricity of an eccentric; as, the\n   throw of the crank of a steam engine is equal to half the stroke of the\n   piston.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'n.', 'A potters wheel or table; a jigger. See 2d Jigger, 2 (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'n.', 'A turners lathe; a throwe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw', 'n.', 'The amount of vertical displacement produced by a fault; --\n   according to the direction it is designated as an upthrow, or a\n   downthrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw-crook', 'n.', 'An instrument used for twisting ropes out of straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throwe', 'n.', 'A turning lathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrower', 'n.', 'One who throws. Specifically: (a) One who throws or twists\n   silk; a throwster. (b) One who shapes vessels on a throwing engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throwing', '', 'a. & n. from Throw, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrown', '', 'a. & p. p. from Throw, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throw-off', 'n.', 'A start in a hunt or a race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Throwster', 'n.', 'One who throws or twists silk; a thrower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thru', 'prep., adv. & a.', 'Through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrum', 'n.', 'One of the ends of weavers threads; hence, any soft, short\n   threads or tufts resembling these.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrum', 'n.', 'Any coarse yarn; an unraveled strand of rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrum', 'n.', 'A threadlike part of a flower; a stamen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrum', 'n.', 'A shove out of place; a small displacement or fault along a\n   seam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrum', 'n.', 'A mat made of canvas and tufts of yarn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrummed', 'imp. & p. p.', 'of Thrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrumming', 'p. pr. & vb. n.', 'of Thrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrum', 'v. t.', 'To furnish with thrums; to insert tufts in; to fringe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrum', 'v. t.', 'To insert short pieces of rope-yarn or spun yarn in; as,\n   to thrum a piece of canvas, or a mat, thus making a rough or tufted\n   surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrum', 'v. i.', 'To play rudely or monotonously on a stringed instrument\n   with the fingers; to strum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrum', 'v. i.', 'Hence, to make a monotonous drumming noise; as, to thrum\n   on a table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrum', 'v. t.', 'To play, as a stringed instrument, in a rude or\n   monotonous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrum', 'v. t.', 'Hence, to drum on; to strike in a monotonous manner; to\n   thrum the table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrum-eyed', 'a.', 'Having the anthers raised above the stigma, and visible\n   at the throat of the corolla, as in long-stamened primroses; -- the\n   reverse of pin-eyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrummy', 'a.', 'Like thrums; made of, furnished with, or characterized by,\n   thrums.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrumwort', 'n.', 'A kind of amaranth (Amarantus caudatus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thruout', '', 'Throughout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrush', 'n.', 'Any one of numerous species of singing birds belonging to\n   Turdus and allied genera. They are noted for the sweetness of their\n   songs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrush', 'n.', 'Any one of numerous species of singing birds more or less\n   resembling the true thrushes in appearance or habits; as the\n   thunderbird and the American brown thrush (or thrasher). See Brown\n   thrush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrush', 'n.', 'An affection of the mouth, fauces, etc., common in newly\n   born children, characterized by minute ulcers called aphthae. See\n   Aphthae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrush', 'n.', 'An inflammatory and suppurative affection of the feet in\n   certain animals. In the horse it is in the frog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrushel', 'n.', 'The song thrush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrusher', 'n.', 'The song thrush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrust', 'n. & v.', 'Thrist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrust', 'imp. & p. p.', 'of Thrust');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrusting', 'p. pr. & vb. n.', 'of Thrust');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrust', 'v. t.', 'To push or drive with force; to drive, force, or impel;\n   to shove; as, to thrust anything with the hand or foot, or with an\n   instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrust', 'v. t.', 'To stab; to pierce; -- usually with through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrust', 'v. i.', 'To make a push; to attack with a pointed weapon; as, a\n   fencer thrusts at his antagonist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrust', 'v. i.', 'To enter by pushing; to squeeze in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrust', 'v. i.', 'To push forward; to come with force; to press on; to\n   intrude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrust', 'n.', 'A violent push or driving, as with a pointed weapon moved\n   in the direction of its length, or with the hand or foot, or with any\n   instrument; a stab; -- a word much used as a term of fencing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrust', 'n.', 'An attack; an assault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrust', 'n.', 'The force or pressure of one part of a construction against\n   other parts; especially (Arch.), a horizontal or diagonal outward\n   pressure, as of an arch against its abutments, or of rafters against\n   the wall which support them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrust', 'n.', 'The breaking down of the roof of a gallery under its\n   superincumbent weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thruster', 'n.', 'One who thrusts or stabs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrusting', 'n.', 'The act of pushing with force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrusting', 'n.', 'The act of squeezing curd with the hand, to expel the\n   whey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrusting', 'n.', 'The white whey, or that which is last pressed out of the\n   curd by the hand, and of which butter is sometimes made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thrustle', 'n.', 'The throstle, or song thrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thryes', 'a.', 'Thrice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thryfallow', 'v. t.', 'To plow for the third time in summer; to trifallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thud', 'n.', 'A dull sound without resonance, like that produced by\n   striking with, or striking against, some comparatively soft substance;\n   also, the stroke or blow producing such sound; as, the thrud of a\n   cannon ball striking the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thug', 'n.', 'One of an association of robbers and murderers in India who\n   practiced murder by stealthy approaches, and from religious motives.\n   They have been nearly exterminated by the British government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thuggee', 'n.', 'The practice of secret or stealthy murder by Thugs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thuggery', 'n.', 'Alt. of Thuggism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thuggism', 'n.', 'Thuggee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thuja', 'n.', 'A genus of evergreen trees, thickly branched, remarkable for\n   the distichous arrangement of their branches, and having scalelike,\n   closely imbricated, or compressed leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thule', 'n.', 'The name given by ancient geographers to the northernmost\n   part of the habitable world. According to some, this land was Norway,\n   according to others, Iceland, or more probably Mainland, the largest of\n   the Shetland islands; hence, the Latin phrase ultima Thule, farthest\n   Thule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thulia', 'n.', 'Oxide of thulium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thulium', 'n.', 'A rare metallic element of uncertain properties and\n   identity, said to have been found in the mineral gadolinite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thumb', 'n.', 'The short, thick first digit of the human hand, differing\n   from the other fingers in having but two phalanges; the pollex. See\n   Pollex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thumbed', 'imp. & p. p.', 'of Thumb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thumbing', 'p. pr. & vb. n.', 'of Thumb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thumb', 'v. t.', 'To handle awkwardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thumb', 'v. t.', 'To play with the thumbs, or with the thumbs and fingers;\n   as, to thumb over a tune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thumb', 'v. t.', 'To soil or wear with the thumb or the fingers; to soil,\n   or wear out, by frequent handling; also, to cover with the thumb; as,\n   to thumb the touch-hole of a cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thumb', 'v. i.', 'To play with the thumb or thumbs; to play clumsily; to\n   thrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thumbbird', 'n.', 'The goldcrest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thumbed', 'a.', 'Having thumbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thumbed', 'a.', 'Soiled by handling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thumbkin', 'n.', 'An instrument of torture for compressing the thumb; a\n   thumbscrew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thumbless', 'a.', 'Without a thumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thumbscrew', 'n.', 'A screw having a flat-sided or knurled head, so that it\n   may be turned by the thumb and forefinger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thumbscrew', 'n.', 'An old instrument of torture for compressing the thumb\n   by a screw; a thumbkin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thummie', 'n.', 'The chiff-chaff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thummim', 'n. pl.', 'A mysterious part or decoration of the breastplate of\n   the Jewish high priest. See the note under Urim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thump', 'n.', 'The sound made by the sudden fall or blow of a heavy body,\n   as of a hammer, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thump', 'n.', 'A blow or knock, as with something blunt or heavy; a heavy\n   fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thumped', 'imp. & p. p.', 'of Thump');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thumping', 'p. pr. & vb. n.', 'of Thump');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thump', 'v. t.', 'To strike or beat with something thick or heavy, or so as\n   to cause a dull sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thump', 'v. i.', 'To give a thump or thumps; to strike or fall with a heavy\n   blow; to pound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thumper', 'n.', 'One who, or that which, thumps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thumping', 'a.', 'Heavy; large.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunder', 'n.', 'The sound which follows a flash of lightning; the report\n   of a discharge of atmospheric electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunder', 'n.', 'The discharge of electricity; a thunderbolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunder', 'n.', 'Any loud noise; as, the thunder of cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunder', 'n.', 'An alarming or statrling threat or denunciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thundered', 'imp. & p. p.', 'of Thunder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thundering', 'p. pr. & vb. n.', 'of Thunder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunder', 'n.', 'To produce thunder; to sound, rattle, or roar, as a\n   discharge of atmospheric electricity; -- often used impersonally; as,\n   it thundered continuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunder', 'n.', 'Fig.: To make a loud noise; esp. a heavy sound, of some\n   continuance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunder', 'n.', 'To utter violent denunciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunder', 'v. t.', 'To emit with noise and terror; to utter vehemently; to\n   publish, as a threat or denunciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderbird', 'n.', 'An Australian insectivorous singing bird (Pachycephala\n   gutturalis). The male is conspicuously marked with black and yellow,\n   and has a black crescent on the breast. Called also white-throated\n   thickhead, orange-breasted thrust, black-crowned thrush, guttural\n   thrush, and black-breasted flycatcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderbolt', 'n.', 'A shaft of lightning; a brilliant stream of\n   electricity passing from one part of the heavens to another, or from\n   the clouds to the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderbolt', 'n.', 'Something resembling lightning in suddenness and\n   effectiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderbolt', 'n.', 'Vehement threatening or censure; especially,\n   ecclesiastical denunciation; fulmination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderbolt', 'n.', 'A belemnite, or thunderstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderburst', 'n.', 'A burst of thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderclap', 'n.', 'A sharp burst of thunder; a sudden report of a\n   discharge of atmospheric electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thundercloud', 'n.', 'A cloud charged with electricity, and producing\n   lightning and thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderer', 'n.', 'One who thunders; -- used especially as a translation of\n   L. tonans, an epithet applied by the Romans to several of their gods,\n   esp. to Jupiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderfish', 'n.', 'A large European loach (Misgurnus fossilis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderhead', 'n.', 'A rounded mass of cloud, with shining white edges; a\n   cumulus, -- often appearing before a thunderstorm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thundering', 'a.', 'Emitting thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thundering', 'a.', 'Very great; -- often adverbially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thundering', 'n.', 'Thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderless', 'a.', 'Without thunder or noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderous', 'a.', 'Producing thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderous', 'a.', 'Making a noise like thunder; sounding loud and deep;\n   sonorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderproof', 'a.', 'Secure against the effects of thunder or lightning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thundershower', 'n.', 'A shower accompanied with lightning and thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderstone', 'n.', 'A thunderbolt, -- formerly believed to be a stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderstone', 'n.', 'A belemnite. See Belemnite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderstorm', 'n.', 'A storm accompanied with lightning and thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderstruck', 'imp.', 'of Thunderstrike');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderstruck', 'p. p.', 'of Thunderstrike');
  INSERT INTO words (word, word_type, description) VALUES  (    '-strucken', '', 'of Thunderstrike');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderstriking', 'p. pr. & vb. n.', 'of Thunderstrike');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderstrike', 'v. t.', 'To strike, blast, or injure by, or as by,\n   lightning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderstrike', 'v. t.', 'To astonish, or strike dumb, as with something\n   terrible; -- rarely used except in the past participle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunderworm', 'n.', 'A small, footless, burrowing, snakelike lizard\n   (Rhineura Floridana) allied to Amphisbaena, native of Florida; -- so\n   called because it leaves its burrows after a thundershower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thundery', 'a.', 'Accompanied with thunder; thunderous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thundrous', 'a.', 'Thunderous; sonorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thunny', 'n.', 'The tunny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thurgh', 'prep.', 'Through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thurghfare', 'n.', 'Thoroughfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thurible', 'n.', 'A censer of metal, for burning incense, having various\n   forms, held in the hand or suspended by chains; -- used especially at\n   mass, vespers, and other solemn services.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thuriferous', 'a.', 'Producing or bearing frankincense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thurification', 'n.', 'The act of fuming with incense, or the act of\n   burning incense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thuringian', 'a.', 'Of or pertaining to Thuringia, a country in Germany, or\n   its people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thuringian', 'n.', 'A native, or inhabitant of Thuringia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thuringite', 'n.', 'A mineral occurring as an aggregation of minute scales\n   having an olive-green color and pearly luster. It is a hydrous silicate\n   of aluminia and iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thurl', 'n.', 'A hole; an aperture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thurl', 'n.', 'A short communication between adits in a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thurl', 'n.', 'A long adit in a coalpit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thurl', 'v. t.', 'To cut through; to pierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thurl', 'v. t.', 'To cut through, as a partition between one working and\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thurling', 'n.', 'Same as Thurl, n., 2 (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thurrok', 'n.', 'The hold of a ship; a sink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thursday', 'n.', 'The fifth day of the week, following Wednesday and\n   preceding Friday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thurst', 'n.', 'The ruins of the fallen roof resulting from the removal of\n   the pillars and stalls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thus', 'n.', 'The commoner kind of frankincense, or that obtained from the\n   Norway spruce, the long-leaved pine, and other conifers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thus', 'adv.', 'In this or that manner; on this wise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thus', 'adv.', 'To this degree or extent; so far; so; as, thus wise; thus\n   peaceble; thus bold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thussock', 'n.', 'See Tussock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thuya', 'n.', 'Same as Thuja.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thuyin', 'n.', 'A substance extracted from trees of the genus Thuja, or\n   Thuya, and probably identical with quercitrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwacked', 'imp. & p. p.', 'of Thwack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwacking', 'p. pr. & vb. n.', 'of Thwack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwack', 'v. t.', 'To strike with something flat or heavy; to bang, or\n   thrash: to thump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwack', 'v. t.', 'To fill to overflow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwack', 'n.', 'A heavy blow with something flat or heavy; a thump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwaite', 'n.', 'The twaite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwaite', 'n.', 'Forest land cleared, and converted to tillage; an assart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwart', 'a.', 'Situated or placed across something else; transverse;\n   oblique.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwart', 'a.', 'Fig.: Perverse; crossgrained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwart', 'a.', 'Thwartly; obliquely; transversely; athwart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwart', 'prep.', 'Across; athwart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwart', 'n.', 'A seat in an open boat reaching from one side to the other,\n   or athwart the boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwarted', 'imp. & p. p.', 'of Thwart');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwarting', 'p. pr. & vb. n.', 'of Thwart');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwart', 'v. t.', 'To move across or counter to; to cross; as, an arrow\n   thwarts the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwart', 'v. t.', 'To cross, as a purpose; to oppose; to run counter to; to\n   contravene; hence, to frustrate or defeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwart', 'v. i.', 'To move or go in an oblique or crosswise manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwart', 'v. i.', 'Hence, to be in opposition; to clash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwarter', 'n.', 'A disease in sheep, indicated by shaking, trembling, or\n   convulsive motions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwartingly', 'adv.', 'In a thwarting or obstructing manner; so as to\n   thwart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwartly', 'adv.', 'Transversely; obliquely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwartness', 'n.', 'The quality or state of being thwart; obliquity;\n   perverseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwite', 'v. t.', 'To cut or clip with a knife; to whittle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwittle', 'v. t.', 'To cut or whittle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thwittle', 'n.', 'A small knife; a whittle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thy', 'pron.', 'Of thee, or belonging to thee; the more common form of\n   thine, possessive case of thou; -- used always attributively, and\n   chiefly in the solemn or grave style, and in poetry. Thine is used in\n   the predicate; as, the knife is thine. See Thine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thyine wood', '', 'The fragrant and beautiful wood of a North African tree\n   (Callitris quadrivalvis), formerly called Thuja articulata. The tree is\n   of the Cedar family, and furnishes a balsamic resin called sandarach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thylacine', 'n.', 'The zebra wolf. See under Wolf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thymate', 'n.', 'A compound of thymol analogous to a salt; as, sodium\n   thymate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thyme', 'n.', 'Any plant of the labiate genus Thymus. The garden thyme\n   (Thymus vulgaris) is a warm, pungent aromatic, much used to give a\n   relish to seasoning and soups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thymene', 'n.', 'A liquid terpene obtained from oil of thyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thymiatechny', 'n.', 'The art of employing perfumes in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thymic', 'a.', 'Of or pertaining to the thymus gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thymic', 'a.', 'Pertaining to, or derived from, thyme; as, thymic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thymol', 'n.', 'A phenol derivative of cymene, C10H13.OH, isomeric with\n   carvacrol, found in oil of thyme, and extracted as a white crystalline\n   substance of a pleasant aromatic odor and strong antiseptic properties;\n   -- called also hydroxy cymene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thymus', 'a.', 'Of, pertaining to, or designating, the thymus gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thymus', 'n.', 'The thymus gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thymy', 'a.', 'Abounding with thyme; fragrant; as, a thymy vale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thyro-', '', 'A combining form used in anatomy to indicate connection with,\n   or relation to, the thyroid body or the thyroid cartilage; as,\n   thyrohyal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thyroarytenoid', 'a.', 'Of or pertaining to both the thyroid and arytenoid\n   cartilages of the larynx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thyrohyal', 'n.', 'One of the lower segments in the hyoid arch, often\n   consolidated with the body of the hyoid bone and forming one of its\n   great horns, as in man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thyrohyoid', 'a.', 'Of or pertaining to the thyroid cartilage of the larynx\n   and the hyoid arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thyroid', 'a.', 'Shaped like an oblong shield; shield-shaped; as, the\n   thyroid cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thyroid', 'a.', 'Of or pertaining to the thyroid body, thyroid cartilage,\n   or thyroid artery; thyroideal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thyroideal', 'a.', 'Thyroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thyrotomy', 'n.', 'The operation of cutting into the thyroid cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thyrse', 'n.', 'A thyrsus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thyrsoid', 'a.', 'Alt. of Thyrsoidal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thyrsoidal', 'a.', 'Having somewhat the form of a thyrsus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thyrsi', 'pl. ', 'of Thyrsus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thyrsus', 'n.', 'A staff entwined with ivy, and surmounted by a pine cone,\n   or by a bunch of vine or ivy leaves with grapes or berries. It is an\n   attribute of Bacchus, and of the satyrs and others engaging in Bacchic\n   rites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thyrsus', 'n.', 'A species of inflorescence; a dense panicle, as in the\n   lilac and horse-chestnut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thysanopter', 'n.', 'One of the Thysanoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thysanoptera', 'n. pl.', 'A division of insects, considered by some writers\n   a distinct order, but regarded by others as belonging to the Hemiptera.\n   They are all of small size, and have narrow, broadly fringed wings with\n   rudimentary nervures. Most of the species feed upon the juices of\n   plants, and some, as those which attack grain, are very injurious to\n   crops. Called also Physopoda. See Thrips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thysanopteran', 'n.', 'One of the Thysanoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thysanopterous', 'a.', 'Of or pertaining to the Thysanoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thysanura', 'n. pl.', 'An order of wingless hexapod insects which have\n   setiform caudal appendages, either bent beneath the body to form a\n   spring, or projecting as bristles. It comprises the Cinura, or\n   bristletails, and the Collembola, or springtails. Called also\n   Thysanoura. See Lepisma, and Podura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thysanuran', 'n.', 'One of the Thysanura. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thysanurous', 'a.', 'Of or pertaining to the Thysanura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thysbe', 'n.', 'A common clearwing moth (Hemaris thysbe).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Thyself', 'pron.', 'An emphasized form of the personal pronoun of the\n   second person; -- used as a subject commonly with thou; as, thou\n   thyself shalt go; that is, thou shalt go, and no other. It is sometimes\n   used, especially in the predicate, without thou, and in the nominative\n   as well as in the objective case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiar', 'n.', 'A tiara.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiara', 'n.', 'A form of headdress worn by the ancient Persians. According\n   to Xenophon, the royal tiara was encircled with a diadem, and was high\n   and erect, while those of the people were flexible, or had rims turned\n   over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiara', 'n.', 'The popes triple crown. It was at first a round, high cap,\n   but was afterward encompassed with a crown, subsequently with a second,\n   and finally with a third. Fig.: The papal dignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiaraed', 'a.', 'Adorned with, or wearing, a tiara.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tib-cat', 'n.', 'A female cat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tibiae', 'pl. ', 'of Tibia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tibia', 'n.', 'The inner, or preaxial, and usually the larger, of the two\n   bones of the leg or hind limb below the knee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tibia', 'n.', 'The fourth joint of the leg of an insect. See Illust. under\n   Coleoptera, and under Hexapoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tibia', 'n.', 'A musical instrument of the flute kind, originally made of\n   the leg bone of an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tibial', 'a.', 'Of or pertaining to a tibia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tibial', 'a.', 'Of or pertaining to a pipe or flute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tibial', 'n.', 'A tibial bone; a tibiale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tibialia', 'pl. ', 'of Tibiale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tibiale', 'n.', 'The bone or cartilage of the tarsus which articulates with\n   the tibia and corresponds to a part of the astragalus in man and most\n   mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tibicinate', 'v. i.', 'To play on a tibia, or pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tibio-', '', 'A combining form used in anatomy to indicate connection with,\n   or relation to, the tibia; as, tibiotarsus, tibiofibular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tibiotarsal', 'a.', 'Of or pertaining to both to the tibia and the tarsus;\n   as, the tibiotarsal articulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tibiotarsal', 'a.', 'Of or pertaining to the tibiotarsus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tibiotarsi', 'pl. ', 'of Tibiotarsus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tibiotarsus', 'n.', 'The large bone between the femur and tarsometatarsus\n   in the leg of a bird. It is formed by the union of the proximal part of\n   the tarsus with the tibia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tibrie', 'n.', 'The pollack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tic', 'n.', 'A local and habitual convulsive motion of certain muscles;\n   especially, such a motion of some of the muscles of the face;\n   twitching; velication; -- called also spasmodic tic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tical', 'n.', 'A bean-shaped coin of Siam, worth about sixty cents; also, a\n   weight equal to 236 grains troy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tical', 'n.', 'A money of account in China, reckoning at about $1.60; also,\n   a weight of about four ounces avoirdupois.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tice', 'v. t.', 'To entice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tice', 'n.', 'A ball bowled to strike the ground about a bats length in\n   front of the wicket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticement', 'n.', 'Enticement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tichorrhine', 'n.', 'A fossil rhinoceros with a vertical bony medial septum\n   supporting the nose; the hairy rhinoceros.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tick', 'n.', 'Credit; trust; as, to buy on, or upon, tick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tick', 'v. i.', 'To go on trust, or credit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tick', 'v. i.', 'To give tick; to trust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tick', 'n.', 'Any one of numerous species of large parasitic mites which\n   attach themselves to, and suck the blood of, cattle, dogs, and many\n   other animals. When filled with blood they become ovate, much swollen,\n   and usually livid red in color. Some of the species often attach\n   themselves to the human body. The young are active and have at first\n   but six legs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tick', 'n.', 'Any one of several species of dipterous insects having a\n   flattened and usually wingless body, as the bird ticks (see under Bird)\n   and sheep tick (see under Sheep).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tick', 'n.', 'The cover, or case, of a bed, mattress, etc., which contains\n   the straw, feathers, hair, or other filling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tick', 'n.', 'Ticking. See Ticking, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticked', 'imp. & p. p.', 'of Tick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticking', 'p. pr. & vb. n.', 'of Tick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tick', 'v. i.', 'To make a small or repeating noise by beating or\n   otherwise, as a watch does; to beat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tick', 'v. i.', 'To strike gently; to pat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tick', 'n.', 'A quick, audible beat, as of a clock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tick', 'n.', 'Any small mark intended to direct attention to something, or\n   to serve as a check.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tick', 'n.', 'The whinchat; -- so called from its note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tick', 'v. t.', 'To check off by means of a tick or any small mark; to\n   score.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticken', 'n.', 'See Ticking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticker', 'n.', 'One who, or that which, ticks, or produces a ticking sound,\n   as a watch or clock, a telegraphic sounder, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticket', 'v.', 'A small piece of paper, cardboard, or the like, serving as\n   a notice, certificate, or distinguishing token of something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticket', 'v.', 'A little note or notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticket', 'v.', 'A tradesmans bill or account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticket', 'v.', 'A certificate or token of right of admission to a place of\n   assembly, or of passage in a public conveyance; as, a theater ticket; a\n   railroad or steamboat ticket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticket', 'v.', 'A label to show the character or price of goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticket', 'v.', 'A certificate or token of a share in a lottery or other\n   scheme for distributing money, goods, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticket', 'v.', 'A printed list of candidates to be voted for at an\n   election; a set of nominations by one party for election; a ballot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticketed', 'imp. & p. p.', 'of Ticket');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticketing', 'p. pr. & vb. n.', 'of Ticket');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticket', 'v. t.', 'To distinguish by a ticket; to put a ticket on; as, to\n   ticket goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticket', 'v. t.', 'To furnish with a tickets; to book; as, to ticket\n   passengers to California.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticketing', 'n.', 'A periodical sale of ore in the English mining\n   districts; -- so called from the tickets upon which are written the\n   bids of the buyers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticking', 'n.', 'A strong, closely woven linen or cotton fabric, of which\n   ticks for beds are made. It is usually twilled, and woven in stripes of\n   different colors, as white and blue; -- called also ticken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tickled', 'imp. & p. p.', 'of Tickle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tickling', 'p. pr. & vb. n.', 'of Tickle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tickle', 'v. t.', 'To touch lightly, so as to produce a peculiar thrilling\n   sensation, which commonly causes laughter, and a kind of spasm which\n   become dengerous if too long protracted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tickle', 'v. t.', 'To please; to gratify; to make joyous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tickle', 'v. i.', 'To feel titillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tickle', 'v. i.', 'To excite the sensation of titillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tickle', 'a.', 'Ticklish; easily tickled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tickle', 'a.', 'Liable to change; uncertain; inconstant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tickle', 'a.', 'Wavering, or liable to waver and fall at the slightest\n   touch; unstable; easily overthrown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tickle-footed', 'a.', 'Uncertain; inconstant; slippery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticklenburg', 'n.', 'A coarse, mixed linen fabric made to be sold in the\n   West Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tickleness', 'n.', 'Unsteadiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tickler', 'n.', 'One who, or that which, tickles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tickler', 'n.', 'Something puzzling or difficult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tickler', 'n.', 'A book containing a memorandum of notes and debts arranged\n   in the order of their maturity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tickler', 'n.', 'A prong used by coopers to extract bungs from casks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticklish', 'a.', 'Sensible to slight touches; easily tickled; as, the sole\n   of the foot is very ticklish; the hardened palm of the hand is not\n   ticklish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticklish', 'a.', 'Standing so as to be liable to totter and fall at the\n   slightest touch; unfixed; easily affected; unstable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticklish', 'a.', 'Difficult; nice; critical; as, a ticklish business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tickseed', 'n.', 'A seed or fruit resembling in shape an insect, as that of\n   certain plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tickseed', 'n.', 'Same as Coreopsis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tickseed', 'n.', 'Any plant of the genus Corispermum, plants of the\n   Goosefoot family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticktack', 'n.', 'A noise like that made by a clock or a watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticktack', 'n.', 'A kind of backgammon played both with men and pegs;\n   tricktrack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticktack', 'adv.', 'With a ticking noise, like that of a watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ticpolonga', 'n.', 'A very venomous viper (Daboia Russellii), native of\n   Ceylon and India; -- called also cobra monil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tid', 'a.', 'Tender; soft; nice; -- now only used in tidbit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidal', 'a.', 'Of or pertaining to tides; caused by tides; having tides;\n   periodically rising and falling, or following and ebbing; as, tidal\n   waters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidbit', 'n.', 'A delicate or tender piece of anything eatable; a delicious\n   morsel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidde', 'obs.', 'imp. of Tide, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidder', 'v. t.', 'Alt. of Tiddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiddle', 'v. t.', 'To use with tenderness; to fondle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tide', 'prep.', 'Time; period; season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tide', 'prep.', 'The alternate rising and falling of the waters of the\n   ocean, and of bays, rivers, etc., connected therewith. The tide ebbs\n   and flows twice in each lunar day, or the space of a little more than\n   twenty-four hours. It is occasioned by the attraction of the sun and\n   moon (the influence of the latter being three times that of the\n   former), acting unequally on the waters in different parts of the\n   earth, thus disturbing their equilibrium. A high tide upon one side of\n   the earth is accompanied by a high tide upon the opposite side. Hence,\n   when the sun and moon are in conjunction or opposition, as at new moon\n   and full moon, their action is such as to produce a greater than the\n   usual tide, called the spring tide, as represented in the cut. When the\n   moon is in the first or third quarter, the suns attraction in part\n   counteracts the effect of the moons attraction, thus producing under\n   the moon a smaller tide than usual, called the neap tide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tide', 'prep.', 'A stream; current; flood; as, a tide of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tide', 'prep.', 'Tendency or direction of causes, influences, or events;\n   course; current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tide', 'prep.', 'Violent confluence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tide', 'prep.', 'The period of twelve hours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tide', 'v. t.', 'To cause to float with the tide; to drive or carry with\n   the tide or stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tide', 'n.', 'To betide; to happen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tide', 'n.', 'To pour a tide or flood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tide', 'n.', 'To work into or out of a river or harbor by drifting with the\n   tide and anchoring when it becomes adverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tided', 'a.', 'Affected by the tide; having a tide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tideless', 'a.', 'Having no tide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tide-rode', 'a.', 'Swung by the tide when at anchor; -- opposed to\n   wind-rode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidesmen', 'pl. ', 'of Tidesman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidesman', 'n.', 'A customhouse officer who goes on board of a merchant\n   ship to secure payment of the duties; a tidewaiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidewaiter', 'n.', 'A customhouse officer who watches the landing of goods\n   from merchant vessels, in order to secure payment of duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tideway', 'n.', 'Channel in which the tide sets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidife', 'n.', 'The blue titmouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidily', 'adv.', 'In a tidy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidiness', 'n.', 'The quality or state of being tidy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiding', 'n.', 'Tidings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidings', 'n.', 'Account of what has taken place, and was not before known;\n   news.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidley', 'n.', 'The wren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidley', 'n.', 'The goldcrest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidology', 'n.', 'A discourse or treatise upon the tides; that part of\n   science which treats of tides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidy', 'n.', 'The wren; -- called also tiddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidy', 'superl.', 'Being in proper time; timely; seasonable; favorable; as,\n   tidy weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidy', 'superl.', 'Arranged in good order; orderly; appropriate; neat; kept\n   in proper and becoming neatness, or habitually keeping things so; as, a\n   tidy lass; their dress is tidy; the apartments are well furnished and\n   tidy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidies', 'pl. ', 'of Tidy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidy', 'n.', 'A cover, often of tatting, drawn work, or other ornamental\n   work, for the back of a chair, the arms of a sofa, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidy', 'n.', 'A childs pinafore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidied', 'imp. & p. p.', 'of Tidy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidying', 'p. pr. & vb. n.', 'of Tidy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidy', 'v. t.', 'To put in proper order; to make neat; as, to tidy a room;\n   to tidy ones dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidy', 'v. i.', 'To make things tidy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tidytips', 'n.', 'A California composite plant (Layia platyglossa), the\n   flower of which has yellow rays tipped with white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ties', 'pl. ', 'of Tie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tie', 'v. t.', 'A knot; a fastening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tie', 'v. t.', 'A bond; an obligation, moral or legal; as, the sacred ties\n   of friendship or of duty; the ties of allegiance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tie', 'v. t.', 'A knot of hair, as at the back of a wig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tie', 'v. t.', 'An equality in numbers, as of votes, scores, etc., which\n   prevents either party from being victorious; equality in any contest,\n   as a race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tie', 'v. t.', 'A beam or rod for holding two parts together; in railways,\n   one of the transverse timbers which support the track and keep it in\n   place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tie', 'v. t.', 'A line, usually straight, drawn across the stems of notes,\n   or a curved line written over or under the notes, signifying that they\n   are to be slurred, or closely united in the performance, or that two\n   notes of the same pitch are to be sounded as one; a bind; a ligature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tie', 'v. t.', 'Low shoes fastened with lacings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tied', 'imp. & p. p.', 'of Tie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tight', '', 'of Tie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tying', 'p. pr. & vb. n.', 'of Tie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tie', 'v. t.', 'To fasten with a band or cord and knot; to bind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tie', 'v. t.', 'To form, as a knot, by interlacing or complicating a cord;\n   also, to interlace, or form a knot in; as, to tie a cord to a tree; to\n   knit; to knot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tie', 'v. t.', 'To unite firmly; to fasten; to hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tie', 'v. t.', 'To hold or constrain by authority or moral influence, as by\n   knotted cords; to oblige; to constrain; to restrain; to confine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tie', 'v. t.', 'To unite, as notes, by a cross line, or by a curved line,\n   or slur, drawn over or under them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tie', 'v. t.', 'To make an equal score with, in a contest; to be even with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tie', 'v. i.', 'To make a tie; to make an equal score.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiebar', 'n.', 'A flat bar used as a tie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiebeam', 'n.', 'A beam acting as a tie, as at the bottom of a pair of\n   principal rafters, to prevent them from thrusting out the wall. See\n   Illust. of Timbers, under Roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tier', 'n.', 'One who, or that which, ties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tier', 'n.', 'A cholds apron covering the upper part of the body, and tied\n   with tape or cord; a pinafore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tier', 'v. t.', 'A row or rank, especially one of two or more rows placed\n   one above, or higher than, another; as, a tier of seats in a theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tierce', 'n.', 'A cask whose content is one third of a pipe; that is,\n   forty-two wine gallons; also, a liquid measure of forty-two wine, or\n   thirty-five imperial, gallons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tierce', 'n.', 'A cask larger than a barrel, and smaller than a hogshead or\n   a puncheon, in which salt provisions, rice, etc., are packed for\n   shipment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tierce', 'n.', 'The third tone of the scale. See Mediant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tierce', 'n.', 'A sequence of three playing cards of the same suit. Tierce\n   of ace, king, queen, is called tierce-major.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tierce', 'n.', 'A position in thrusting or parrying in which the wrist and\n   nails are turned downward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tierce', 'n.', 'The third hour of the day, or nine a. m,; one of the\n   canonical hours; also, the service appointed for that hour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tierce', 'a.', 'Divided into three equal parts of three different\n   tinctures; -- said of an escutcheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiercel', 'n.', 'Alt. of Tiercelet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiercelet', 'n.', 'The male of various falcons, esp. of the peregrine;\n   also, the male of the goshawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tierce-major', 'n.', 'See Tierce, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiercet', 'n.', 'A triplet; three lines, or three lines rhyming together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tie-rod', 'n.', 'A rod used as a tie. See Tie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiers etat', '', 'The third estate, or commonalty, in France, answering to\n   the commons in Great Britain; -- so called in distinction from, and as\n   inferior to, the nobles and clergy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tietick', 'n.', 'The meadow pipit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiewig', 'n.', 'A wig having a tie or ties, or one having some of the curls\n   tied up; also, a wig tied upon the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiff', 'n.', 'Liquor; especially, a small draught of liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiff', 'n.', 'A fit of anger or peevishness; a slight altercation or\n   contention. See Tift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiffed', 'imp. & p. p.', 'of Tiff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiffing', 'p. pr. & vb. n.', 'of Tiff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiff', 'v. i.', 'To be in a pet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiff', 'v. t.', 'To deck out; to dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiffany', 'n.', 'A species of gause, or very silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiffin', 'n.', 'A lunch, or slight repast between breakfast and dinner; --\n   originally, a Provincial English word, but introduced into India, and\n   brought back to England in a special sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiffish', 'a.', 'Inclined to tiffs; peevish; petulant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tift', 'n.', 'A fit of pettishness, or slight anger; a tiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tig', 'n.', 'A game among children. See Tag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tig', 'n.', 'A capacious, flat-bottomed drinking cup, generally with four\n   handles, formerly used for passing around the table at convivial\n   entertainment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tigella', 'n.', 'That part of an embryo which represents the young stem;\n   the caulicle or radicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tigelle', 'n.', 'Same as Tigella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiger', 'n.', 'A very large and powerful carnivore (Felis tigris) native of\n   Southern Asia and the East Indies. Its back and sides are tawny or\n   rufous yellow, transversely striped with black, the tail is ringed with\n   black, the throat and belly are nearly white. When full grown, it\n   equals or exceeds the lion in size and strength. Called also royal\n   tiger, and Bengal tiger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiger', 'n.', 'Fig.: A ferocious, bloodthirsty person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiger', 'n.', 'A servant in livery, who rides with his master or mistress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiger', 'n.', 'A kind of growl or screech, after cheering; as, three cheers\n   and a tiger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiger', 'n.', 'A pneumatic box or pan used in refining sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiger-eye', 'n.', 'A siliceous stone of a yellow color and chatoyant\n   luster, obtained in South Africa and much used for ornament. It is an\n   altered form of the mineral crocidolite. See Crocidolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiger-foot', 'n.', 'Same as Tigers-foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiger-footed', 'a.', 'Hastening to devour; furious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tigerine', 'a.', 'Tigerish; tigrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tigerish', 'a.', 'Like a tiger; tigrish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tigers-foot', 'n.', 'A name given to some species of morning-glory\n   (Ipomoea) having the leaves lobed in pedate fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tigh', 'n.', 'A close, or inclosure; a croft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tight', '', 'p. p. of Tie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tight', 'superl.', 'Firmly held together; compact; not loose or open; as,\n   tight cloth; a tight knot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tight', 'superl.', 'Close, so as not to admit the passage of a liquid or\n   other fluid; not leaky; as, a tight ship; a tight cask; a tight room;\n   -- often used in this sense as the second member of a compound; as,\n   water-tight; air-tight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tight', 'superl.', 'Fitting close, or too close, to the body; as, a tight\n   coat or other garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tight', 'superl.', 'Not ragged; whole; neat; tidy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tight', 'superl.', 'Close; parsimonious; saving; as, a man tight in his\n   dealings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tight', 'superl.', 'Not slack or loose; firmly stretched; taut; -- applied\n   to a rope, chain, or the like, extended or stretched out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tight', 'superl.', 'Handy; adroit; brisk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tight', 'superl.', 'Somewhat intoxicated; tipsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tight', 'superl.', 'Pressing; stringent; not easy; firmly held; dear; --\n   said of money or the money market. Cf. Easy, 7.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tight', 'v. t.', 'To tighten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tightened', 'imp. & p. p.', 'of Tighten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tightening', 'p. pr. & vb. n.', 'of Tighten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tighten', 'v. t.', 'To draw tighter; to straiten; to make more close in any\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tightener', 'n.', 'That which tightens; specifically (Mach.), a tightening\n   pulley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tighter', 'n.', 'A ribbon or string used to draw clothes closer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tightly', 'adv.', 'In a tight manner; closely; nearly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tightness', 'n.', 'The quality or condition of being tight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tights', 'n. pl.', 'Close-fitting garments, especially for the lower part\n   of the body and the legs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiglic', 'a.', 'Of, pertaining to, or designating, an acid, C4H7CO2H\n   (called also methyl crotonic acid), homologous with crotonic acid, and\n   obtained from croton oil (from Croton Tiglium) as a white crystalline\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tigress', 'n.', 'The female of the tiger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tigrine', 'a.', 'Of or pertaining to a tiger; like a tiger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tigrine', 'a.', 'Resembling the tiger in color; as, the tigrine cat (Felis\n   tigrina) of South America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tigrish', 'a.', 'Resembling a tiger; tigerish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tike', 'n.', 'A tick. See 2d Tick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tike', 'n.', 'A dog; a cur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tike', 'n.', 'A countryman or clown; a boorish person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tikus', 'n.', 'The bulau.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Til', 'prep. & conj.', 'See Till.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilburies', 'pl. ', 'of Tilbury');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilbury', 'n.', 'A kind of gig or two-wheeled carriage, without a top or\n   cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilde', 'n.', 'The accentual mark placed over n, and sometimes over l, in\n   Spanish words [thus, , /], indicating that, in pronunciation, the sound\n   of the following vowel is to be preceded by that of the initial, or\n   consonantal, y.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tile', 'v. t.', 'To protect from the intrusion of the uninitiated; as, to\n   tile a Masonic lodge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tile', 'n.', 'A plate, or thin piece, of baked clay, used for covering the\n   roofs of buildings, for floors, for drains, and often for ornamental\n   mantel works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tile', 'n.', 'A small slab of marble or other material used for flooring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tile', 'n.', 'A plate of metal used for roofing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tile', 'n.', 'A small, flat piece of dried earth or earthenware, used to\n   cover vessels in which metals are fused.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tile', 'n.', 'A draintile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tile', 'n.', 'A stiff hat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiled', 'imp. & p. p.', 'of Tile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiling', 'p. pr. & vb. n.', 'of Tile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tile', 'v. t.', 'To cover with tiles; as, to tile a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tile', 'v. t.', 'Fig.: To cover, as if with tiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tile-drain', 'v. t.', 'To drain by means of tiles; to furnish with a tile\n   drain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilefish', 'n.', 'A large, edible, deep-water food fish (Lopholatilus\n   chamaeleonticeps) more or less thickly covered with large, round,\n   yellow spots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiler', 'n.', 'A man whose occupation is to cover buildings with tiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiler', 'n.', 'A doorkeeper or attendant at a lodge of Freemasons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tileries', 'pl. ', 'of Tilery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilery', 'n.', 'A place where tiles are made or burned; a tile kiln.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilestone', 'n.', 'A kind of laminated shale or sandstone belonging to some\n   of the layers of the Upper Silurian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilestone', 'n.', 'A tile of stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiliaceous', 'a.', 'Of, pertaining to, or resembling, a natural order of\n   plants (Tiliaceae) of which the linden (Tilia) is the type. The order\n   includes many plants which furnish a valuable fiber, as the jute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiling', 'n.', 'A surface covered with tiles, or composed of tiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiling', 'n.', 'Tiles, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Till', 'n.', 'A vetch; a tare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Till', 'n.', 'A drawer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Till', 'n.', 'A tray or drawer in a chest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Till', 'n.', 'A money drawer in a shop or store.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Till', 'n.', 'A deposit of clay, sand, and gravel, without lamination,\n   formed in a glacier valley by means of the waters derived from the\n   melting glaciers; -- sometimes applied to alluvium of an upper river\n   terrace, when not laminated, and appearing as if formed in the same\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Till', 'n.', 'A kind of coarse, obdurate land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Till', 'v. t.', 'To; unto; up to; as far as; until; -- now used only in\n   respect to time, but formerly, also, of place, degree, etc., and still\n   so used in Scotland and in parts of England and Ireland; as, I worked\n   till four oclock; I will wait till next week.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Till', 'conj.', 'As far as; up to the place or degree that; especially, up\n   to the time that; that is, to the time specified in the sentence or\n   clause following; until.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilled', 'imp. & p. p.', 'of Till');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilling', 'p. pr. & vb. n.', 'of Till');
  INSERT INTO words (word, word_type, description) VALUES  (    'Till', 'prep.', 'To plow and prepare for seed, and to sow, dress, raise\n   crops from, etc., to cultivate; as, to till the earth, a field, a farm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Till', 'prep.', 'To prepare; to get.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Till', 'v. i.', 'To cultivate land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tillable', 'a.', 'Capable of being tilled; fit for the plow; arable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tillage', 'n.', 'The operation, practice, or art of tilling or preparing\n   land for seed, and keeping the ground in a proper state for the growth\n   of crops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tillage', 'n.', 'A place tilled or cultivated; cultivated land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tillandsia', 'n.', 'A genus of epiphytic endogenous plants found in the\n   Southern United States and in tropical America. Tillandsia usneoides,\n   called long moss, black moss, Spanish moss, and Florida moss, has a\n   very slender pendulous branching stem, and forms great hanging tufts on\n   the branches of trees. It is often used for stuffing mattresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiller', 'v. t.', 'One who tills; a husbandman; a cultivator; a plowman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiller', 'n.', 'A shoot of a plant, springing from the root or bottom of\n   the original stalk; a sucker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiller', 'n.', 'A sprout or young tree that springs from a root or stump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiller', 'n.', 'A young timber tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tillered', 'imp. & p. p.', 'of Tiller');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tillering', 'p. pr. & vb. n.', 'of Tiller');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiller', 'v. i.', 'To put forth new shoots from the root, or round the\n   bottom of the original stalk; as, wheat or rye tillers; some spread\n   plants by tillering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiller', 'n.', 'A lever of wood or metal fitted to the rudder head and used\n   for turning side to side in steering. In small boats hand power is\n   used; in large vessels, the tiller is moved by means of mechanical\n   appliances. See Illust. of Rudder. Cf. 2d Helm, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiller', 'n.', 'The stalk, or handle, of a crossbow; also, sometimes, the\n   bow itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiller', 'n.', 'The handle of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiller', 'n.', 'A small drawer; a till.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilley', '', 'Alt. of Tilley seed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilley seed', '', 'The seeds of a small tree (Croton Pavana) common in the\n   Malay Archipelago. These seeds furnish croton oil, like those of Croton\n   Tiglium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tillmen', 'pl. ', 'of Tillman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tillman', 'n.', 'A man who tills the earth; a husbandman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tillodont', 'n.', 'One of the Tillodontia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tillodontia', 'n. pl.', 'An extinct group of Mammalia found fossil in the\n   Eocene formation. The species are related to the carnivores, ungulates,\n   and rodents. Called also Tillodonta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tillet', 'n.', 'A bag made of thin glazed muslin, used as a wrapper for\n   dress goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tillow', 'v. i.', 'See 3d Tiller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilly-vally', 'interj., adv., or a.', 'A word of unknown origin and\n   signification, formerly used as expressive of contempt, or when\n   anything said was reject as trifling or impertinent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilmus', 'n.', 'Floccillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilt', 'n.', 'A covering overhead; especially, a tent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilt', 'n.', 'The cloth covering of a cart or a wagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilt', 'n.', 'A cloth cover of a boat; a small canopy or awning extended\n   over the sternsheets of a boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilted', 'imp. & p. p.', 'of Tilt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilting', 'p. pr. & vb. n.', 'of Tilt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilt', 'v. t.', 'To cover with a tilt, or awning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilt', 'v. t.', 'To incline; to tip; to raise one end of for discharging\n   liquor; as, to tilt a barrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilt', 'v. t.', 'To point or thrust, as a lance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilt', 'v. t.', 'To point or thrust a weapon at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilt', 'v. t.', 'To hammer or forge with a tilt hammer; as, to tilt steel\n   in order to render it more ductile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilt', 'v. i.', 'To run or ride, and thrust with a lance; to practice the\n   military game or exercise of thrusting with a lance, as a combatant on\n   horseback; to joust; also, figuratively, to engage in any combat or\n   movement resembling that of horsemen tilting with lances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilt', 'v. i.', 'To lean; to fall partly over; to tip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilt', 'n.', 'A thrust, as with a lance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilt', 'n.', 'A military exercise on horseback, in which the combatants\n   attacked each other with lances; a tournament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilt', 'n.', 'See Tilt hammer, in the Vocabulary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilt', 'n.', 'Inclination forward; as, the tilt of a cask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilter', 'n.', 'One who tilts, or jousts; hence, one who fights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilter', 'n.', 'One who operates a tilt hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilth', 'n.', 'The state of being tilled, or prepared for a crop; culture;\n   as, land is good tilth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilth', 'n.', 'That which is tilled; tillage ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilt hammer', '', 'A tilted hammer; a heavy hammer, used in iron works,\n   which is lifted or tilted by projections or wipers on a revolving\n   shaft; a trip hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilting', 'n.', 'The act of one who tilts; a tilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilting', 'n.', 'The process by which blister steel is rendered ductile by\n   being forged with a tilt hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilt-mill', 'n.', 'A mill where a tilt hammer is used, or where the process\n   of tilting is carried on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Til tree', '', 'See Teil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilt-up', 'n.', 'Same as Tip-up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tilt-yard', 'n.', 'A yard or place for tilting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timal', 'n.', 'The blue titmouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timaline', 'a.', 'Of or pertaining to the genus Timalus or family\n   Timalidae, which includes the babblers thrushes, and bulbuls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timbal', 'n.', 'A kettledrum. See Tymbal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timber', 'n.', 'A certain quantity of fur skins, as of martens, ermines,\n   sables, etc., packed between boards; being in some cases forty skins,\n   in others one hundred and twenty; -- called also timmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timber', 'n.', 'The crest on a coat of arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timber', 'v. t.', 'To surmount as a timber does.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timber', 'n.', 'That sort of wood which is proper for buildings or for\n   tools, utensils, furniture, carriages, fences, ships, and the like; --\n   usually said of felled trees, but sometimes of those standing. Cf.\n   Lumber, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timber', 'n.', 'The body, stem, or trunk of a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timber', 'n.', 'Fig.: Material for any structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timber', 'n.', 'A single piece or squared stick of wood intended for\n   building, or already framed; collectively, the larger pieces or sticks\n   of wood, forming the framework of a house, ship, or other structure, in\n   distinction from the covering or boarding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timber', 'n.', 'Woods or forest; wooden land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timber', 'n.', 'A rib, or a curving piece of wood, branching outward from\n   the keel and bending upward in a vertical direction. One timber is\n   composed of several pieces united.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timbered', 'imp. & p. p.', 'of Timber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timbering', 'p. pr. & vb. n.', 'of Timber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timber', 'v. t.', 'To furnish with timber; -- chiefly used in the past\n   participle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timber', 'v. i.', 'To light on a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timber', 'v. i.', 'To make a nest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timbered', 'a.', 'Furnished with timber; -- often compounded; as, a\n   well-timbered house; a low-timbered house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timbered', 'a.', 'Built; formed; contrived.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timbered', 'a.', 'Massive, like timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timbered', 'a.', 'Covered with growth timber; wooden; as, well-timbered\n   land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timberhead', 'n.', 'The top end of a timber, rising above the gunwale, and\n   serving for belaying ropes, etc.; -- called also kevel head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timbering', 'n.', 'The act of furnishing with timber; also, timbers,\n   collectively; timberwork; timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timberling', 'n.', 'A small tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timbermen', 'pl. ', 'of Timberman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timberman', 'n.', 'A man employed in placing supports of timber in a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timberwork', 'n.', 'Work made of timbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timbre', 'n.', 'See 1st Timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timbre', 'n.', 'The crest on a coat of arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timbre', 'n.', 'The quality or tone distinguishing voices or instruments;\n   tone color; clang tint; as, the timbre of the voice; the timbre of a\n   violin. See Tone, and Partial tones, under Partial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timbrel', 'n.', 'A kind of drum, tabor, or tabret, in use from the highest\n   antiquity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timbreled', 'a.', 'Alt. of Timbrelled');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timbrelled', 'a.', 'Sung to the sound of the timbrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timburine', 'n.', 'A tambourine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Times', 'pl. ', 'of Time');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time', 'n.', 'Duration, considered independently of any system of\n   measurement or any employment of terms which designate limited portions\n   thereof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time', 'n.', 'A particular period or part of duration, whether past,\n   present, or future; a point or portion of duration; as, the time was,\n   or has been; the time is, or will be.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time', 'n.', 'The period at which any definite event occurred, or person\n   lived; age; period; era; as, the Spanish Armada was destroyed in the\n   time of Queen Elizabeth; -- often in the plural; as, ancient times;\n   modern times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time', 'n.', 'The duration of ones life; the hours and days which a person\n   has at his disposal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time', 'n.', 'A proper time; a season; an opportunity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time', 'n.', 'Hour of travail, delivery, or parturition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time', 'n.', 'Performance or occurrence of an action or event, considered\n   with reference to repetition; addition of a number to itself;\n   repetition; as, to double cloth four times; four times four, or\n   sixteen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time', 'n.', 'The present life; existence in this world as contrasted with\n   immortal life; definite, as contrasted with infinite, duration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time', 'n.', 'Tense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time', 'n.', 'The measured duration of sounds; measure; tempo; rate of\n   movement; rhythmical division; as, common or triple time; the musician\n   keeps good time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timed', 'imp. & p. p.', 'of Time');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timing', 'p. pr. & vb. n.', 'of Time');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time', 'v. t.', 'To appoint the time for; to bring, begin, or perform at\n   the proper season or time; as, he timed his appearance rightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time', 'v. t.', 'To regulate as to time; to accompany, or agree with, in\n   time of movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time', 'v. t.', 'To ascertain or record the time, duration, or rate of; as,\n   to time the speed of horses, or hours for workmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time', 'v. t.', 'To measure, as in music or harmony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time', 'v. i.', 'To keep or beat time; to proceed or move in time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time', 'v. i.', 'To pass time; to delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timeful', 'a.', 'Seasonable; timely; sufficiently early.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time-honored', 'a.', 'Honored for a long time; venerable, and worthy of\n   honor, by reason of antiquity, or long continuance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timekeeper', 'n.', 'A clock, watch, or other chronometer; a timepiece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timekeeper', 'n.', 'A person who keeps, marks, regulates, or determines the\n   time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timekeeper', 'n.', 'A person who keeps a record of the time spent by\n   workmen at their work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timekeeper', 'n.', 'One who gives the time for the departure of\n   conveyances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timekeeper', 'n.', 'One who marks the time in musical performances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timekeeper', 'n.', 'One appointed to mark and declare the time of\n   participants in races or other contests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timeless', 'a.', 'Done at an improper time; unseasonable; untimely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timeless', 'a.', 'Done or occurring before the proper time; premature;\n   immature; as, a timeless grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timeless', 'a.', 'Having no end; interminable; unending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timelessly', 'adv.', 'In a timeless manner; unseasonably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timeliness', 'n.', 'The quality or state of being timely; seasonableness;\n   opportuneness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timeling', 'n.', 'A timeserver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timely', 'superl.', 'Being or occurring in good time; sufficiently early;\n   seasonable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timely', 'superl.', 'Keeping time or measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timely', 'adv.', 'Early; soon; in good season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timenoguy', 'n.', 'A rope carried taut between or over obstacles likely to\n   engage or foul the running rigging in working a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timeous', 'a.', 'Timely; seasonable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timepiece', 'n.', 'A clock, watch, or other instrument, to measure or show\n   the progress of time; a chronometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timepleaser', 'n.', 'One who complies with prevailing opinions, whatever\n   they may be; a timeserver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timer', 'n.', 'A timekeeper; especially, a watch by which small intervals\n   of time can be measured; a kind of stop watch. It is used for timing\n   the speed of horses, machinery, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timesaving', 'a.', 'Saving time; as, a timesaving expedient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timeserver', 'n.', 'One who adapts his opinions and manners to the times;\n   one who obsequiously compiles with the ruling power; -- now used only\n   in a bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timeserving', 'a.', 'Obsequiously complying with the spirit of the times,\n   or the humors of those in power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timeserving', 'n.', 'An obsequious compliance with the spirit of the times,\n   or the humors of those in power, which implies a surrender of ones\n   independence, and sometimes of ones integrity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time-table', 'n.', 'A tabular statement of the time at which, or within\n   which, several things are to take place, as the recitations in a\n   school, the departure and arrival of railroad trains or other public\n   conveyances, the rise and fall of the tides, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time-table', 'n.', 'A plane surface divided in one direction with lines\n   representing hours and minutes, and in the other with lines\n   representing miles, and having diagonals (usually movable strings)\n   representing the speed and position of various trains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Time-table', 'n.', 'A table showing the notation, length, or duration of\n   the several notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timid', 'a.', 'Wanting courage to meet danger; easily frightened; timorous;\n   not bold; fearful; shy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timidity', 'n.', 'The quality or state of being timid; timorousness;\n   timidness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timidous', 'a.', 'Timid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timist', 'n.', 'A performer who keeps good time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timist', 'n.', 'A timeserver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timmer', 'n.', 'Same as 1st Timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timocracy', 'n.', 'A state in which the love of honor is the ruling motive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timocracy', 'n.', 'A state in which honors are distributed according to a\n   rating of property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timocratic', 'a.', 'Belonging to, or constituted by, timocracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timoneer', 'n.', 'A helmsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timorous', 'a.', 'Fearful of danger; timid; deficient in courage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timorous', 'a.', 'Indicating, or caused by, fear; as, timorous doubts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timorsome', 'a.', 'Easily frightened; timorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timothy', '', 'Alt. of Timothy grass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timothy grass', '', 'A kind of grass (Phleum pratense) with long\n   cylindrical spikes; -- called also herds grass, in England, cats-tail\n   grass, and meadow cats-tail grass. It is much prized for fodder. See\n   Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timous', 'a.', 'Timely; seasonable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timpani', 'pl. ', 'of Timpano');
  INSERT INTO words (word, word_type, description) VALUES  (    'Timpano', 'n.', 'See Tympano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tim-whiskey', 'n.', 'A kind of carriage. See Whiskey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tin', 'n.', 'An elementary substance found as an oxide in the mineral\n   cassiterite, and reduced as a soft white crystalline metal, malleable\n   at ordinary temperatures, but brittle when heated. It is not easily\n   oxidized in the air, and is used chiefly to coat iron to protect it\n   from rusting, in the form of tin foil with mercury to form the\n   reflective surface of mirrors, and in solder, bronze, speculum metal,\n   and other alloys. Its compounds are designated as stannous, or stannic.\n   Symbol Sn (Stannum). Atomic weight 117.4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tin', 'n.', 'Thin plates of iron covered with tin; tin plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tin', 'n.', 'Money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinned', 'imp. & p. p.', 'of Tin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinning', 'p. pr. & vb. n.', 'of Tin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tin', 'v. t.', 'To cover with tin or tinned iron, or to overlay with tin\n   foil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinamides', 'n. pl.', 'A division of struthious birds, including the\n   tinamous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinamou', 'n.', 'Any one of several species of South American birds\n   belonging to Tinamus and allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tincal', 'n.', 'Crude native borax, formerly imported from Thibet. It was\n   once the chief source of boric compounds. Cf. Borax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinchel', 'n.', 'A circle of sportsmen, who, by surrounding an extensive\n   space and gradually closing in, bring a number of deer and game within\n   a narrow compass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinct', 'a.', 'Tined; tinged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinct', 'n.', 'Color; tinge; tincture; tint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinct', 'v. t.', 'To color or stain; to imblue; to tint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinctorial', 'a.', 'Of or relating to color or colors; imparting a color;\n   as, tinctorial matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tincture', 'n.', 'A tinge or shade of color; a tint; as, a tincture of red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tincture', 'n.', 'One of the metals, colors, or furs used in armory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tincture', 'n.', 'The finer and more volatile parts of a substance,\n   separated by a solvent; an extract of a part of the substance of a body\n   communicated to the solvent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tincture', 'n.', 'A solution (commonly colored) of medicinal substance in\n   alcohol, usually more or less diluted; spirit containing medicinal\n   substances in solution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tincture', 'n.', 'A slight taste superadded to any substance; as, a\n   tincture of orange peel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tincture', 'n.', 'A slight quality added to anything; a tinge; as, a\n   tincture of French manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinctured', 'imp. & p. p.', 'of Tincture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tincturing', 'p. pr. & vb. n.', 'of Tincture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tincture', 'v. t.', 'To communicate a slight foreign color to; to tinge; to\n   impregnate with some extraneous matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tincture', 'v. t.', 'To imbue the mind of; to communicate a portion of\n   anything foreign to; to tinge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tind', 'v. t.', 'To kindle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tindal', 'n.', 'A petty officer among lascars, or native East Indian\n   sailors; a boatswains mate; a cockswain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tindal', 'n.', 'An attendant on an army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinder', 'n.', 'Something very inflammable, used for kindling fire from a\n   spark, as scorched linen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tine', 'n.', 'Trouble; distress; teen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tine', 'v. t.', 'To kindle; to set on fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tine', 'v. i.', 'To kindle; to rage; to smart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tine', 'v. t.', 'To shut in, or inclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tine', 'n.', 'A tooth, or spike, as of a fork; a prong, as of an antler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinea', 'n.', 'A name applied to various skin diseases, but especially to\n   ringworm. See Ringworm, and Sycosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinea', 'n.', 'A genus of small Lepidoptera, including the clothes moths\n   and carpet moths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinean', 'n.', 'Any species of Tinea, or of the family Tineidae, which\n   includes numerous small moths, many of which are injurious to woolen\n   and fur goods and to cultivated plants. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tined', 'a.', 'Furnished with tines; as, a three-tined fork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tineid', 'n.', 'Same as Tinean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinemen', 'pl. ', 'of Tineman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tineman', 'n.', 'An officer of the forest who had the care of vert and\n   venison by night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinet', 'n.', 'Brushwood and thorns for making and repairing hedges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ting', 'n.', 'A sharp sound, as of a bell; a tinkling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ting', 'v. i.', 'To sound or ring, as a bell; to tinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ting', 'n.', 'The apartment in a Chinese temple where the idol is kept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinged', 'imp. & p. p.', 'of Tinge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tingeing', 'p. pr. & vb. n.', 'of Tinge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinge', 'v. t.', 'To imbue or impregnate with something different or\n   foreign; as, to tinge a decoction with a bitter taste; to affect in\n   some degree with the qualities of another substance, either by mixture,\n   or by application to the surface; especially, to color slightly; to\n   stain; as, to tinge a blue color with red; an infusion tinged with a\n   yellow color by saffron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinge', 'n.', 'A degree, usually a slight degree, of some color, taste, or\n   something foreign, infused into another substance or mixture, or added\n   to it; tincture; color; dye; hue; shade; taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tingent', 'a.', 'Having the power to tinge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinger', 'n.', 'One who, or that which, tinges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tingid', 'a.', 'Of or pertaining to the genus Tingis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tingis', 'n.', 'A genus of small hemipterous insects which injure trees by\n   sucking the sap from the leaves. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tingled', 'imp. & p. p.', 'of Tingle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tingling', 'p. pr. & vb. n.', 'of Tingle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tingle', 'v. i.', 'To feel a kind of thrilling sensation, as in hearing a\n   shrill sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tingle', 'v. i.', 'To feel a sharp, thrilling pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tingle', 'v. i.', 'To have, or to cause, a sharp, thrilling sensation, or a\n   slight pricking sensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tink', 'v. i.', 'To make a sharp, shrill noise; to tinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tink', 'n.', 'A sharp, quick sound; a tinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinker', 'n.', 'A mender of brass kettles, pans, and other metal ware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinker', 'n.', 'One skilled in a variety of small mechanical work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinker', 'n.', 'A small mortar on the end of a staff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinker', 'n.', 'A young mackerel about two years old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinker', 'n.', 'The chub mackerel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinker', 'n.', 'The silversides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinker', 'n.', 'A skate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinker', 'n.', 'The razor-billed auk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinkered', 'imp. & p. p.', 'of Tinker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinkering', 'p. pr. & vb. n.', 'of Tinker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinker', 'v. t.', 'To mend or solder, as metal wares; hence, more\n   generally, to mend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinker', 'v. i.', 'To busy ones self in mending old kettles, pans, etc.;\n   to play the tinker; to be occupied with small mechanical works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinkering', 'n.', 'The act or work of a tinker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinkerly', 'a.', 'After the manner of a tinker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinkershire', 'n.', 'Alt. of Tinkle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinkle', 'n.', 'The common guillemot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinkle', 'v. i.', 'To make, or give forth, small, quick, sharp sounds, as a\n   piece of metal does when struck; to clink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinkle', 'v. i.', 'To hear, or resound with, a small, sharp sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinkled', 'imp. & p. p.', 'of Tinkle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinkling', 'p. pr. & vb. n.', 'of Tinkle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinkle', 'v. t.', 'To cause to clonk, or make small, sharp, quick sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinkle', 'n.', 'A small, sharp, quick sound, as that made by striking\n   metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinkler', 'n.', 'A tinker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinkling', 'n.', 'A tinkle, or succession of tinkles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinkling', 'n.', 'A grackle (Quiscalus crassirostris) native of Jamaica. It\n   often associates with domestic cattle, and rids them of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinmen', 'pl. ', 'of Tinman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinman', 'n.', 'A manufacturer of tin vessels; a dealer in tinware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinmouth', 'n.', 'The crappie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinned', 'a.', 'Covered, or plated, with tin; as, a tinned roof; tinned\n   iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinned', 'a.', 'Packed in tin cases; canned; as, tinned meats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinnen', 'a.', 'Made or consisting of tin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinner', 'n.', 'One who works in a tin mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinner', 'n.', 'One who makes, or works in, tinware; a tinman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinnient', 'a.', 'Emitting a clear sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinning', 'n.', 'The act, art, or process of covering or coating anything\n   with melted tin, or with tin foil, as kitchen utensils, locks, and the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinning', 'n.', 'The covering or lining of tin thus put on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinnitus', 'n.', 'A ringing, whistling, or other imaginary noise perceived\n   in the ears; -- called also tinnitus aurium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinnock', 'n.', 'The blue titmouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinny', 'a.', 'Pertaining to, abounding with, or resembling, tin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinsel', 'n.', 'A shining material used for ornamental purposes;\n   especially, a very thin, gauzelike cloth with much gold or silver woven\n   into it; also, very thin metal overlaid with a thin coating of gold or\n   silver, brass foil, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinsel', 'n.', 'Something shining and gaudy; something superficially\n   shining and showy, or having a false luster, and more gay than\n   valuable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinsel', 'a.', 'Showy to excess; gaudy; specious; superficial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinseled', 'imp. & p. p.', 'of Tinsel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinselled', '', 'of Tinsel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinseling', 'p. pr. & vb. n.', 'of Tinsel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinselling', '', 'of Tinsel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinsel', 'v. t.', 'To adorn with tinsel; to deck out with cheap but showy\n   ornaments; to make gaudy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinselly', 'a.', 'Like tinsel; gaudy; showy, but cheap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinselly', 'adv.', 'In a showy and cheap manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinsmith', 'n.', 'One who works in tin; a tinner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinstone', 'n.', 'Cassiterite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tint', 'n.', 'A slight coloring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tint', 'n.', 'A pale or faint tinge of any color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tint', 'n.', 'A color considered with reference to other very similar\n   colors; as, red and blue are different colors, but two shades of\n   scarlet are different tints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tint', 'n.', 'A shaded effect produced by the juxtaposition of many fine\n   parallel lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinted', 'imp. & p. p.', 'of Tint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinting', 'p. pr. & vb. n.', 'of Tint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tint', 'v. t.', 'To give a slight coloring to; to tinge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tintamar', 'n.', 'A hideous or confused noise; an uproar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinternell', 'n.', 'A certain old dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tintle', 'n.', 'The wren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tintinnabular', 'a.', 'Alt. of Tintinnabulary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tintinnabulary', 'a.', 'Having or making the sound of a bell; tinkling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tintinnabulation', 'n.', 'A tinkling sound, as of a bell or bells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tintinnabulous', 'a.', 'Of, pertaining to, or resembling, the tinkling of a\n   bell; having a tinkling sound; tintinnabular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinto', 'n.', 'A red Madeira wine, wanting the high aroma of the white\n   sorts, and, when old, resembling tawny port.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tintype', 'n.', 'Same as Ferrotype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tinware', 'n.', 'Articles made of tinned iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiny', 'superl.', 'Very small; little; puny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tip', 'n.', 'The point or extremity of anything; a pointed or somewhat\n   sharply rounded end; the end; as, the tip of the finger; the tip of a\n   spear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tip', 'n.', 'An end piece or part; a piece, as a cap, nozzle, ferrule, or\n   point, applied to the extreme end of anything; as, a tip for an\n   umbrella, a shoe, a gas burner, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tip', 'n.', 'A piece of stiffened lining pasted on the inside of a hat\n   crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tip', 'n.', 'A thin, boarded brush made of camels hair, used by gilders in\n   lifting gold leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tip', 'n.', 'Rubbish thrown from a quarry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipped', 'imp. & p. p.', 'of Tip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipping', 'p. pr. & vb. n.', 'of Tip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tip', 'v. t.', 'To form a point upon; to cover the tip, top, or end of; as,\n   to tip anything with gold or silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tip', 'v. t.', 'To strike slightly; to tap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tip', 'v. t.', 'To bestow a gift, or douceur, upon; to give a present to;\n   as, to tip a servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tip', 'v. t.', 'To lower one end of, or to throw upon the end; to tilt; as,\n   to tip a cask; to tip a cart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tip', 'v. i.', 'To fall on, or incline to, one side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tip', 'n.', 'A light touch or blow; a tap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tip', 'n.', 'A gift; a douceur; a fee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tip', 'n.', 'A hint, or secret intimation, as to the chances in a horse\n   race, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipcart', 'n.', 'A cart so constructed that the body can be easily tipped,\n   in order to dump the load.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipcat', 'n.', 'A game in which a small piece of wood pointed at both ends,\n   called a cat, is tipped, or struck with a stick or bat, so as to fly\n   into the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipper', 'n.', 'A kind of ale brewed with brackish water obtained from a\n   particular well; -- so called from the first brewer of it, one Thomas\n   Tipper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tippet', 'n.', 'A cape, or scarflike garment for covering the neck, or the\n   neck and shoulders, -- usually made of fur, cloth, or other warm\n   material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tippet', 'n.', 'A length of twisted hair or gut in a fish line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tippet', 'n.', 'A handful of straw bound together at one end, and used for\n   thatching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipping', 'n.', 'A distinct articulation given in playing quick notes on\n   the flute, by striking the tongue against the roof of the mouth;\n   double-tonguing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tippled', 'imp. & p. p.', 'of Tipple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tippling', 'p. pr. & vb. n.', 'of Tipple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipple', 'v. i.', 'To drink spirituous or strong liquors habitually; to\n   indulge in the frequent and improper used of spirituous liquors;\n   especially, to drink frequently in small quantities, but without\n   absolute drunkeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipple', 'v. t.', 'To drink, as strong liquors, frequently or in excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipple', 'v. t.', 'To put up in bundles in order to dry, as hay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipple', 'n.', 'Liquor taken in tippling; drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tippled', 'a.', 'Intoxicated; inebriated; tipsy; drunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tippler', 'n.', 'One who keeps a tippling-house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tippler', 'n.', 'One who habitually indulges in the excessive use of\n   spirituous liquors, whether he becomes intoxicated or not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tippling-house', 'n.', 'A house in which liquors are sold in drams or small\n   quantities, to be drunk on the premises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipsify', 'v. t.', 'To make tipsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipsily', 'adv.', 'In a tipsy manner; like one tipsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipsiness', 'n.', 'The state of being tipsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipstaff', 'pl. ', 'of Tipstaff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipstaff', 'n.', 'A staff tipped with metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipstaff', 'n.', 'An officer who bears a staff tipped with metal; a\n   constable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipsy', 'superl.', 'Being under the influence of strong drink; rendered\n   weak or foolish by liquor, but not absolutely or completely drunk;\n   fuddled; intoxicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipsy', 'superl.', 'Staggering, as if from intoxication; reeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiptoes', 'pl. ', 'of Tiptoe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiptoe', 'n.', 'The end, or tip, of the toe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiptoe', 'a.', 'Being on tiptoe, or as on tiptoe; hence, raised as high as\n   possible; lifted up; exalted; also, alert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiptoe', 'a.', 'Noiseless; stealthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiptoe', 'v. i.', 'To step or walk on tiptoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiptop', 'n.', 'The highest or utmost degree; the best of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiptop', 'a.', 'Very excellent; most excellent; perfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipulae', 'pl. ', 'of Tipula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipulas', 'pl. ', 'of Tipula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipula', 'n.', 'Any one of many species of long-legged dipterous insects\n   belonging to Tipula and allied genera. They have long and slender\n   bodies. See Crane fly, under Crane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tipulary', 'a.', 'Of or pertaining to the tipulas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tip-up', 'n.', 'The spotted sandpiper; -- called also teeter-tail. See\n   under Sandpiper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tirade', 'n.', 'A declamatory strain or flight of censure or abuse; a\n   rambling invective; an oration or harangue abounding in censorious and\n   bitter language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tirailleur', 'n.', 'Formerly, a member of an independent body of marksmen\n   in the French army. They were used sometimes in front of the army to\n   annoy the enemy, sometimes in the rear to check his pursuit. The term\n   is now applied to all troops acting as skirmishers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tire', 'n.', 'A tier, row, or rank. See Tier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tire', 'n.', 'Attire; apparel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tire', 'n.', 'A covering for the head; a headdress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tire', 'n.', 'A childs apron, covering the breast and having no sleeves; a\n   pinafore; a tier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tire', 'n.', 'Furniture; apparatus; equipment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tire', 'n.', 'A hoop or band, as of metal, on the circumference of the\n   wheel of a vehicle, to impart strength and receive the wear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tire', 'v. t.', 'To adorn; to attire; to dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tire', 'v. i.', 'To seize, pull, and tear prey, as a hawk does.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tire', 'v. i.', 'To seize, rend, or tear something as prey; to be fixed\n   upon, or engaged with, anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tired', 'imp. & p. p.', 'of Tire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiring', 'p. pr. & vb. n.', 'of Tire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tire', 'v. i.', 'To become weary; to be fatigued; to have the strength\n   fail; to have the patience exhausted; as, a feeble person soon tires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tire', 'v. t.', 'To exhaust the strength of, as by toil or labor; to\n   exhaust the patience of; to wear out (ones interest, attention, or the\n   like); to weary; to fatigue; to jade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tired', 'a.', 'Weary; fatigued; exhausted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiredness', 'n.', 'The state of being tired, or weary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tireless', 'a.', 'Untiring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tireling', 'a.', 'Tired; fatigued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiresome', 'a.', 'Fitted or tending to tire; exhausted; wearisome;\n   fatiguing; tedious; as, a tiresome journey; a tiresome discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tire-women', 'pl. ', 'of Tire-woman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tire-woman', 'n.', 'A ladys maid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tire-woman', 'n.', 'A dresser in a theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiring-house', 'n.', 'A tiring-room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiring-room', 'n.', 'The room or place where players dress for the stage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tirma', 'n.', 'The oyster catcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiro', 'n.', 'Same as Tyro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'T iron', '', 'See under T.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tironian', 'a.', 'Of or pertaining to Tiro, or a system of shorthand said\n   to have been introduced by him into ancient Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tirralirra', 'n.', 'A verbal imitation of a musical sound, as of the note\n   of a lark or a horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tirrit', 'n.', 'A word from the vocabulary of Mrs. Quickly, the hostess in\n   Shakespeares Henry IV., probably meaning terror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tirwit', 'n.', 'The lapwing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'T is', '', 'A common contraction of it is.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tisane', 'n.', 'See Ptisan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tisar', 'n.', 'The fireplace at the side of an annealing oven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tisic', 'a.', 'Alt. of Tisical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tisical', 'a.', 'Consumptive, phthisical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tisic', 'n.', 'Consumption; phthisis. See Phthisis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tisicky', 'a.', 'Consumptive, phthisical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tisri', 'n.', 'The seventh month of the Jewish ecclesiastical year,\n   answering to a part of September with a part of October.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tissue', 'n.', 'A woven fabric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tissue', 'n.', 'A fine transparent silk stuff, used for veils, etc.;\n   specifically, cloth interwoven with gold or silver threads, or embossed\n   with figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tissue', 'n.', 'One of the elementary materials or fibres, having a uniform\n   structure and a specialized function, of which ordinary animals and\n   plants are composed; a texture; as, epithelial tissue; connective\n   tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tissue', 'n.', 'Fig.: Web; texture; complicated fabrication; connected\n   series; as, a tissue of forgeries, or of falsehood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tissued', 'imp. & p. p.', 'of Tissue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tissuing', 'p. pr. & vb. n.', 'of Tissue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tissue', 'v. t.', 'To form tissue of; to interweave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tissued', 'a.', 'Clothed in, or adorned with, tissue; also, variegated; as,\n   tissued flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tit', 'n.', 'A small horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tit', 'n.', 'A woman; -- used in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tit', 'n.', 'A morsel; a bit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tit', 'n.', 'Any one of numerous species of small singing birds belonging\n   to the families Paridae and Leiotrichidae; a titmouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tit', 'n.', 'The European meadow pipit; a titlark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titan', 'a.', 'Titanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titanate', 'n.', 'A salt of titanic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titanic', 'a.', 'Of or relating to Titans, or fabled giants of ancient\n   mythology; hence, enormous in size or strength; as, Titanic structures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titanic', 'a.', 'Of or pertaining to titanium; derived from, or containing,\n   titanium; specifically, designating those compounds of titanium in\n   which it has a higher valence as contrasted with the titanous\n   compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titaniferous', 'a.', 'Containing or affording titanium; as, titaniferous\n   magnetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titanite', 'n.', 'See Sphene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titanitic', 'a.', 'Pertaining to, or containing, titanium; as, a titanitic\n   mineral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titanium', 'n.', 'An elementary substance found combined in the minerals\n   manaccanite, rutile, sphene, etc., and isolated as an infusible\n   iron-gray amorphous powder, having a metallic luster. It burns when\n   heated in the air. Symbol Ti. Atomic weight 48.1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titano-', '', 'A combining form (also used adjectively) designating certain\n   double compounds of titanium with some other elements; as,\n   titano-cyanide, titano-fluoride, titano-silicate, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titanotherium', 'n.', 'A large American Miocene mammal, allied to the\n   rhinoceros, and more nearly to the extinct Brontotherium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titanous', 'a.', 'Designating certain compounds of titanium in which that\n   element has a lower valence as contrasted with titanic compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titbit', 'n.', 'Same as Tidbit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tith', 'a.', 'Tight; nimble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithable', 'a.', 'Subject to the payment of tithes; as, tithable lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithe', 'n.', 'A tenth; the tenth part of anything; specifically, the\n   tenthpart of the increase arising from the profits of land and stock,\n   allotted to the clergy for their support, as in England, or devoted to\n   religious or charitable uses. Almost all the tithes of England and\n   Wales are commuted by law into rent charges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithe', 'n.', 'Hence, a small part or proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithe', 'a.', 'Tenth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithed', 'imp. & p. p.', 'of Tithe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithing', 'p. pr. & vb. n.', 'of Tithe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithe', 'v. t.', 'To levy a tenth part on; to tax to the amount of a tenth;\n   to pay tithes on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithe', 'v. i.', 'Tp pay tithes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tither', 'n.', 'One who collects tithes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tither', 'n.', 'One who pays tithes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithing', 'n.', 'The act of levying or taking tithes; that which is taken\n   as tithe; a tithe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithing', 'n.', 'A number or company of ten householders who, dwelling near\n   each other, were sureties or frankpledges to the king for the good\n   behavior of each other; a decennary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithingmen', 'pl. ', 'of Tithingman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithingman', 'n.', 'The chief man of a tithing; a headborough; one elected\n   to preside over the tithing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithingman', 'n.', 'A peace officer; an under constable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithingman', 'n.', 'A parish officer elected annually to preserve good\n   order in the church during divine service, to make complaint of any\n   disorderly conduct, and to enforce the observance of the Sabbath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithly', 'a.', 'Tightly; nimbly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithonic', 'a.', 'Of, pertaining to, or denoting, those rays of light which\n   produce chemical effects; actinic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithonicity', 'n.', 'The state or property of being tithonic; actinism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithonographic', 'a.', 'Of, relating to, or produced by, the chemical\n   action of rays of light; photographic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithonometer', 'n.', 'An instrument or apparatus for measuring or detecting\n   tithonicity; an actinometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tithymal', 'n.', 'Any kind of spurge, esp. Euphorbia Cyparissias.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titi', 'n.', 'Same as Teetee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titillated', 'imp. & p. p.', 'of Titillate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titillating', 'p. pr. & vb. n.', 'of Titillate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titillate', 'v. t. & i.', 'To tickle; as, to titillate the nose with a\n   feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titillation', 'n.', 'The act of tickling, or the state of being tickled; a\n   tickling sensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titillation', 'n.', 'Any pleasurable sensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titillative', 'a.', 'Tending or serving to titillate, or tickle; tickling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titlark', 'n.', 'Any one of numerous small spring birds belonging to\n   Anthus, Corydalla, and allied genera, which resemble the true larks in\n   color and in having a very long hind claw; especially, the European\n   meadow pipit (Anthus pratensis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Title', 'n.', 'An inscription put over or upon anything as a name by which\n   it is known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Title', 'n.', 'The inscription in the beginning of a book, usually\n   containing the subject of the work, the authors and publishers names,\n   the date, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Title', 'n.', 'The panel for the name, between the bands of the back of a\n   book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Title', 'n.', 'A section or division of a subject, as of a law, a book,\n   specif. (Roman & Canon Laws), a chapter or division of a law book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Title', 'n.', 'An appellation of dignity, distinction, or preeminence\n   (hereditary or acquired), given to persons, as duke marquis, honorable,\n   esquire, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Title', 'n.', 'A name; an appellation; a designation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Title', 'n.', 'That which constitutes a just cause of exclusive possession;\n   that which is the foundation of ownership of property, real or\n   personal; a right; as, a good title to an estate, or an imperfect\n   title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Title', 'n.', 'The instrument which is evidence of a right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Title', 'n.', 'That by which a beneficiary holds a benefice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Title', 'n.', 'A church to which a priest was ordained, and where he was to\n   reside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titled', 'imp. & p. p.', 'of Title');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titling', 'p. pr. & vb. n.', 'of Title');
  INSERT INTO words (word, word_type, description) VALUES  (    'Title', 'n.', 'To call by a title; to name; to entitle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titled', 'a.', 'Having or bearing a title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titleless', 'a.', 'Not having a title or name; without legitimate title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Title-page', 'n.', 'The page of a book which contains it title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titler', 'n.', 'A large truncated cone of refined sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titling', 'n.', 'The hedge sparrow; -- called also titlene. Its nest often\n   chosen by the cuckoo as a place for depositing its own eggs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titling', 'n.', 'The meadow pipit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titling', 'n.', 'Stockfish; -- formerly so called in customhouses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titmal', 'n.', 'The blue titmouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titmice', 'pl. ', 'of Titmouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titmouse', 'n.', 'Any one of numerous species of small insectivorous\n   singing birds belonging to Parus and allied genera; -- called also tit,\n   and tomtit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titrated', 'imp. & p. p.', 'of Titrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titrating', 'p. pr. & vb. n.', 'of Titrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titrate', 'n.', 'To analyse, or determine the strength of, by means of\n   standard solutions. Cf. Standardized solution, under Solution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titrated', 'a.', 'Standardized; determined or analyzed by titration; as,\n   titrated solutions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titration', 'n.', 'The act or process of titrating; a substance obtained by\n   titrating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tittered', 'imp. & p. p.', 'of Titter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tittering', 'p. pr. & vb. n.', 'of Titter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titter', 'v. t.', 'To laugh with the tongue striking against the root of\n   the upper teeth; to laugh with restraint, or without much noise; to\n   giggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titter', 'n.', 'A restrained laugh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titter', 'v. i.', 'To seesaw. See Teeter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titterel', 'n.', 'The whimbrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titter-totter', 'v. i.', 'See Teeter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tittimouse', 'n.', 'Titmouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tittle', 'n.', 'A particle; a minute part; a jot; an iota.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tittlebat', 'n.', 'The three-spined stickleback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tittle-tattle', 'n.', 'Idle, trifling talk; empty prattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tittle-tattle', 'n.', 'An idle, trifling talker; a gossip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tittle-tattle', 'v. i.', 'To talk idly; to prate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tittle-tattling', 'n.', 'The act or habit of parting idly or gossiping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titty', 'n.', 'A little teat; a nipple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titubate', 'v. i.', 'To stumble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titubate', 'v. i.', 'To rock or roll, as a curved body on a plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titubation', 'n.', 'The act of stumbling, rocking, or rolling; a reeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titular', 'a.', 'Existing in title or name only; nominal; having the title\n   to an office or dignity without discharging its appropriate duties; as,\n   a titular prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titular', 'n.', 'A titulary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titularity', 'n.', 'The quality or state of being titular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titularly', 'adv.', 'In a titular manner; nominally; by title only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titularies', 'pl. ', 'of Titulary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titulary', 'n.', 'A person invested with a title, in virtue of which he\n   holds an office or benefice, whether he performs the duties of it or\n   not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titulary', 'a.', 'Consisting in a title; titular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Titulary', 'a.', 'Of or pertaining to a title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tituled', 'a.', 'Having a title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiver', 'n.', 'A kind of ocher which is used in some parts of England in\n   marking sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiver', 'v. t.', 'To mark with tiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tivy', 'adv.', 'With great speed; -- a huntsmans word or sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tiza', 'n.', 'See Ulexite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tmesis', 'n.', 'The separation of the parts of a compound word by the\n   intervention of one or more words; as, in what place soever, for\n   whatsoever place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To-', 'prep.', 'An obsolete intensive prefix used in the formation of\n   compound verbs; as in to-beat, to-break, to-hew, to-rend, to-tear. See\n   these words in the Vocabulary. See the Note on All to, or All-to, under\n   All, adv.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To', 'prep.', 'The preposition to primarily indicates approach and arrival,\n   motion made in the direction of a place or thing and attaining it,\n   access; and also, motion or tendency without arrival; movement toward;\n   -- opposed to from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To', 'prep.', 'Hence, it indicates motion, course, or tendency toward a\n   time, a state or condition, an aim, or anything capable of being\n   regarded as a limit to a tendency, movement, or action; as, he is going\n   to a trade; he is rising to wealth and honor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To', 'prep.', 'In a very general way, and with innumerable varieties of\n   application, to connects transitive verbs with their remoter or\n   indirect object, and adjectives, nouns, and neuter or passive verbs\n   with a following noun which limits their action. Its sphere verges upon\n   that of for, but it contains less the idea of design or appropriation;\n   as, these remarks were addressed to a large audience; let us keep this\n   seat to ourselves; a substance sweet to the taste; an event painful to\n   the mind; duty to God and to our parents; a dislike to spirituous\n   liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To', 'prep.', 'As sign of the infinitive, to had originally the use of last\n   defined, governing the infinitive as a verbal noun, and connecting it\n   as indirect object with a preceding verb or adjective; thus, ready to\n   go, i.e., ready unto going; good to eat, i.e., good for eating; I do my\n   utmost to lead my life pleasantly. But it has come to be the almost\n   constant prefix to the infinitive, even in situations where it has no\n   prepositional meaning, as where the infinitive is direct object or\n   subject; thus, I love to learn, i.e., I love learning; to die for ones\n   country is noble, i.e., the dying for ones country. Where the\n   infinitive denotes the design or purpose, good usage formerly allowed\n   the prefixing of for to the to; as, what went ye out for see? (Matt.\n   xi. 8).');
  INSERT INTO words (word, word_type, description) VALUES  (    'To', 'prep.', 'In many phrases, and in connection with many other words, to\n   has a pregnant meaning, or is used elliptically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To', 'prep.', 'Extent; limit; degree of comprehension; inclusion as far as;\n   as, they met us to the number of three hundred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To', 'prep.', 'Effect; end; consequence; as, the prince was flattered to\n   his ruin; he engaged in a war to his cost; violent factions exist to\n   the prejudice of the state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To', 'prep.', 'Apposition; connection; antithesis; opposition; as, they\n   engaged hand to hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To', 'prep.', 'Accord; adaptation; as, an occupation to his taste; she has\n   a husband to her mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To', 'prep.', 'Comparison; as, three is to nine as nine is to twenty-seven;\n   it is ten to one that you will offend him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To', 'prep.', 'Addition; union; accumulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To', 'prep.', 'Accompaniment; as, she sang to his guitar; they danced to\n   the music of a piano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To', 'prep.', 'Character; condition of being; purpose subserved or office\n   filled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toad', 'n.', 'Any one of numerous species of batrachians belonging to the\n   genus Bufo and allied genera, especially those of the family Bufonidae.\n   Toads are generally terrestrial in their habits except during the\n   breeding season, when they seek the water. Most of the species burrow\n   beneath the earth in the daytime and come forth to feed on insects at\n   night. Most toads have a rough, warty skin in which are glands that\n   secrete an acrid fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toadeater', 'n.', 'A fawning, obsequious parasite; a mean sycophant; a\n   flatterer; a toady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toadfish', 'n.', 'Any marine fish of the genus Batrachus, having a large,\n   thick head and a wide mouth, and bearing some resemblance to a toad.\n   The American species (Batrachus tau) is very common in shallow water.\n   Called also oyster fish, and sapo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toadfish', 'n.', 'The angler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toadfish', 'n.', 'A swellfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toadflax', 'n.', 'An herb (Linaria vulgaris) of the Figwort family, having\n   narrow leaves and showy orange and yellow flowers; -- called also\n   butter and eggs, flaxweed, and ramsted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toadhead', 'n.', 'The golden plover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toadish', 'a.', 'Like a toad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toadlet', 'n.', 'A small toad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toadstone', 'n.', 'A local name for the igneous rocks of Derbyshire,\n   England; -- said by some to be derived from the German todter stein,\n   meaning dead stone, that is, stone which contains no ores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toadstone', 'n.', 'Bufonite, formerly regarded as a precious stone, and\n   worn as a jewel. See Bufonite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toadstool', 'n.', 'A name given to many umbrella-shaped fungi, mostly of\n   the genus Agaricus. The species are almost numberless. They grow on\n   decaying organic matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toadies', 'pl. ', 'of Toady');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toady', 'n.', 'A mean flatterer; a toadeater; a sycophant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toady', 'n.', 'A coarse, rustic woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toadied', 'imp. & p. p.', 'of Toady');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toadying', 'p. pr. & vb. n.', 'of Toady');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toady', 'v. t.', 'To fawn upon with mean sycophancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toadyism', 'n.', 'The practice of meanly fawning on another; base\n   sycophancy; servile adulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toasted', 'imp. & p. p.', 'of Toast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toasting', 'p. pr. & vb. n.', 'of Toast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toast', 'v. t.', 'To dry and brown by the heat of a fire; as, to toast\n   bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toast', 'v. t.', 'To warm thoroughly; as, to toast the feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toast', 'v. t.', 'To name when a health is proposed to be drunk; to drink\n   to the health, or in honor, of; as, to toast a lady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toast', 'v.', 'Bread dried and browned before a fire, usually in slices;\n   also, a kind of food prepared by putting slices of toasted bread into\n   milk, gravy, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toast', 'v.', 'A lady in honor of whom persons or a company are invited to\n   drink; -- so called because toasts were formerly put into the liquor,\n   as a great delicacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toast', 'v.', 'Hence, any person, especially a person of distinction, in\n   honor of whom a health is drunk; hence, also, anything so commemorated;\n   a sentiment, as \"The land we live in,\" \"The day we celebrate,\" etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toaster', 'n.', 'One who toasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toaster', 'n.', 'A kitchen utensil for toasting bread, cheese, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toasting', '', 'a. & n. from Toast, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toastmaster', 'n.', 'A person who presides at a public dinner or banquet,\n   and announces the toasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toat', 'n.', 'The handle of a joiners plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tobacco', 'n.', 'An American plant (Nicotiana Tabacum) of the Nightshade\n   family, much used for smoking and chewing, and as snuff. As a medicine,\n   it is narcotic, emetic, and cathartic. Tobacco has a strong, peculiar\n   smell, and an acrid taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tobacco', 'n.', 'The leaves of the plant prepared for smoking, chewing,\n   etc., by being dried, cured, and manufactured in various ways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tobacconing', 'n.', 'Smoking tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tobacconist', 'n.', 'A dealer in tobacco; also, a manufacturer of tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tobacconist', 'n.', 'A smoker of tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To-beat', 'v. t.', 'To beat thoroughly or severely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tobias fish', '', 'The lant, or sand eel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tobine', 'n.', 'A stout twilled silk used for dresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tobit', 'n.', 'A book of the Apocrypha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toboggan', 'n.', 'A kind of sledge made of pliable board, turned up at one\n   or both ends, used for coasting down hills or prepared inclined planes;\n   also, a sleigh or sledge, to be drawn by dogs, or by hand, over soft\n   and deep snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tobogganed', 'imp. & p. p.', 'of Toboggan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tobogganing', 'p. pr. & vb. n.', 'of Toboggan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toboggan', 'v. i.', 'To slide down hill over the snow or ice on a toboggan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tobogganer', 'n.', 'Alt. of Tobogganist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tobogganist', 'n.', 'One who practices tobogganing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To-break', 'v. t.', 'To break completely; to break in pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To-brest', 'v. t.', 'To burst or break in pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toccata', 'n.', 'An old form of piece for the organ or harpsichord,\n   somewhat in the free and brilliant style of the prelude, fantasia, or\n   capriccio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tocher', 'n.', 'Dowry brought by a bride to her husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tockay', 'n.', 'A spotted lizard native of India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toco', 'n.', 'A toucan (Ramphastos toco) having a very large beak. See\n   Illust. under Toucan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tocology', 'n.', 'The science of obstetrics, or midwifery; that department\n   of medicine which treats of parturition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tocororo', 'n.', 'A cuban trogon (Priotelus temnurus) having a serrated\n   bill and a tail concave at the end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tocsin', 'n.', 'An alarm bell, or the ringing of a bell for the purpose of\n   alarm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tod', 'n.', 'A bush; a thick shrub; a bushy clump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tod', 'n.', 'An old weight used in weighing wool, being usually\n   twenty-eight pounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tod', 'n.', 'A fox; -- probably so named from its bushy tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tod', 'v. t. & i.', 'To weigh; to yield in tods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To-day', 'prep.', 'On this day; on the present day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To-day', 'n.', 'The present day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toddled', 'imp. & p. p.', 'of Toddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toddling', 'p. pr. & vb. n.', 'of Toddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toddle', 'v. i.', 'To walk with short, tottering steps, as a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toddle', 'n.', 'A toddling walk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toddler', 'n.', 'One who toddles; especially, a young child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toddy', 'n.', 'A juice drawn from various kinds of palms in the East\n   Indies; or, a spirituous liquor procured from it by fermentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toddy', 'n.', 'A mixture of spirit and hot water sweetened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To-do', 'n.', 'Bustle; stir; commotion; ado.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tody', 'n.', 'Any one of several species of small insectivorous West Indian\n   birds of the genus Todus. They are allied to the kingfishers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toe', 'n.', 'One of the terminal members, or digits, of the foot of a man\n   or an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toe', 'n.', 'The fore part of the hoof or foot of an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toe', 'n.', 'Anything, or any part, corresponding to the toe of the foot;\n   as, the toe of a boot; the toe of a skate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toe', 'n.', 'The journal, or pivot, at the lower end of a revolving shaft\n   or spindle, which rests in a step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toe', 'n.', 'A lateral projection at one end, or between the ends, of a\n   piece, as a rod or bolt, by means of which it is moved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toe', 'n.', 'A projection from the periphery of a revolving piece, acting\n   as a cam to lift another piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toed', 'imp. & p. p.', 'of Toe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toeing', 'p. pr. & vb. n.', 'of Toe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toe', 'v. t.', 'To touch or reach with the toes; to come fully up to; as,\n   to toe the mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toe', 'v. i.', 'To hold or carry the toes (in a certain way).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toed', 'a.', 'Having (such or so many) toes; -- chiefly used in\n   composition; as, narrow-toed, four-toed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toed', 'a.', 'Having the end secured by nails driven obliquely, said of a\n   board, plank, or joist serving as a brace, and in general of any part\n   of a frame secured to other parts by diagonal nailing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To-fall', 'n.', 'A lean-to. See Lean-to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toffee', 'n.', 'Alt. of Toffy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toffy', 'n.', 'Taffy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tofore', 'prep.', 'Alt. of Toforn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toforn', 'prep.', 'Before.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toft', 'n.', 'A knoll or hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toft', 'n.', 'A grove of trees; also, a plain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toft', 'n.', 'A place where a messuage has once stood; the site of a burnt\n   or decayed house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toftmen', 'pl. ', 'of Toftman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toftman', 'n.', 'The owner of a toft. See Toft, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tofus', 'n.', 'Tophus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tofus', 'n.', 'Tufa. See under Tufa, and Toph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Togas', 'pl. ', 'of Toga');
  INSERT INTO words (word, word_type, description) VALUES  (    'Togae', 'pl. ', 'of Toga');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toga', 'n.', 'The loose outer garment worn by the ancient Romans,\n   consisting of a single broad piece of woolen cloth of a shape\n   approaching a semicircle. It was of undyed wool, except the border of\n   the toga praetexta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Togated', 'a.', 'Dressed in a toga or gown; wearing a gown; gowned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toged', 'a.', 'Togated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Together', 'prep.', 'In company or association with respect to place or\n   time; as, to live together in one house; to live together in the same\n   age; they walked together to the town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Together', 'prep.', 'In or into union; into junction; as, to sew, knit, or\n   fasten two things together; to mix things together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Together', 'prep.', 'In concert; with mutual cooperation; as, the allies\n   made war upon France together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toggery', 'n.', 'Clothes; garments; dress; as, fishing toggery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toggle', 'n.', 'A wooden pin tapering toward both ends with a groove around\n   its middle, fixed transversely in the eye of a rope to be secured to\n   any other loop or bight or ring; a kind of button or frog capable of\n   being readily engaged and disengaged for temporary purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toggle', 'n.', 'Two rods or plates connected by a toggle joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toght', 'a.', 'Taut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Togider', 'adv.', 'Alt. of Togidres');
  INSERT INTO words (word, word_type, description) VALUES  (    'Togidres', 'adv.', 'Together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Togue', 'n.', 'The namaycush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tohew', 'v. t.', 'To hew in pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toil', 'n.', 'A net or snare; any thread, web, or string spread for taking\n   prey; -- usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toiled', 'imp. & p. p.', 'of Toil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toiling', 'p. pr. & vb. n.', 'of Toil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toil', 'v. i.', 'To exert strength with pain and fatigue of body or mind,\n   especially of the body, with efforts of some continuance or duration;\n   to labor; to work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toil', 'v. t.', 'To weary; to overlabor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toil', 'v. t.', 'To labor; to work; -- often with out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toil', 'v.', 'Labor with pain and fatigue; labor that oppresses the body or\n   mind, esp. the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toiler', 'n.', 'One who toils, or labors painfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toilet', 'n.', 'A covering of linen, silk, or tapestry, spread over a table\n   in a chamber or a dressing room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toilet', 'n.', 'A dressing table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toilet', 'n.', 'Act or mode of dressing, or that which is arranged in\n   dressing; attire; dress; as, her toilet is perfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toilette', 'n.', 'See Toilet, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toilful', 'a.', 'Producing or involving much toil; laborious; toilsome; as,\n   toilful care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toilinette', 'n.', 'A cloth, the weft of which is of woolen yarn, and the\n   warp of cotton and silk, -- used for waistcoats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toilless', 'a.', 'Free from toil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toilsome', 'a.', 'Attended with toil, or fatigue and pain; laborious;\n   wearisome; as, toilsome work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toise', 'a.', 'An old measure of length in France, containing six French\n   feet, or about 6.3946 French feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tokay', 'n.', 'A grape of an oval shape and whitish color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tokay', 'n.', 'A rich Hungarian wine made from Tokay grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Token', 'n.', 'Something intended or supposed to represent or indicate\n   another thing or an event; a sign; a symbol; as, the rainbow is a token\n   of Gods covenant established with Noah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Token', 'n.', 'A memorial of friendship; something by which the friendship\n   of another person is to be kept in mind; a memento; a souvenir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Token', 'n.', 'Something given or shown as a symbol or guarantee of\n   authority or right; a sign of authenticity, of power, good faith, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Token', 'n.', 'A piece of metal intended for currency, and issued by a\n   private party, usually bearing the name of the issuer, and redeemable\n   in lawful money. Also, a coin issued by government, esp. when its use\n   as lawful money is limited and its intrinsic value is much below its\n   nominal value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Token', 'n.', 'A livid spot upon the body, indicating, or supposed to\n   indicate, the approach of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Token', 'n.', 'Ten and a half quires, or, commonly, 250 sheets, of paper\n   printed on both sides; also, in some cases, the same number of sheets\n   printed on one side, or half the number printed on both sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Token', 'n.', 'A piece of metal given beforehand to each person in the\n   congregation who is permitted to partake of the Lords Supper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Token', 'n.', 'A bit of leather having a peculiar mark designating a\n   particular miner. Each hewer sends one of these with each corf or tub\n   he has hewn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tokened', 'imp. & p. p.', 'of Token');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tokening', 'p. pr. & vb. n.', 'of Token');
  INSERT INTO words (word, word_type, description) VALUES  (    'Token', 'n.', 'To betoken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tokened', 'a.', 'Marked by tokens, or spots; as, the tokened pestilence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tokenless', 'a.', 'Without a token.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tokin', 'n.', 'A tocsin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tol', 'v. t.', 'To take away. See Toll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tola', 'n.', 'A weight of British India. The standard tola is equal to 180\n   grains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolane', 'n.', 'A hydrocarbon, C14H10, related both to the acetylene and\n   the aromatic series, and produced artificially as a white crystalline\n   substance; -- called also diphenyl acetylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolbooth', 'n.', 'See Tollbooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Told', '', 'imp. & p. p. of Tell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toled', 'imp. & p. p.', 'of Tole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toling', 'p. pr. & vb. n.', 'of Tole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tole', 'v. t.', 'To draw, or cause to follow, by displaying something\n   pleasing or desirable; to allure by some bait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toledo', 'n.', 'A sword or sword blade made at Toledo in Spain, which city\n   was famous in the 16th and 17th centuries for the excellence of its\n   weapons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolerabolity', 'n.', 'The quality or state of being tolerable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolerable', 'a.', 'Capable of being borne or endured; supportable, either\n   physically or mentally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolerable', 'a.', 'Moderately good or agreeable; not contemptible; not very\n   excellent or pleasing, but such as can be borne or received without\n   disgust, resentment, or opposition; passable; as, a tolerable\n   administration; a tolerable entertainment; a tolerable translation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolerance', 'n.', 'The power or capacity of enduring; the act of enduring;\n   endurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolerance', 'n.', 'The endurance of the presence or actions of\n   objectionable persons, or of the expression of offensive opinions;\n   toleration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolerance', 'n.', 'The power possessed or acquired by some persons of\n   bearing doses of medicine which in ordinary cases would prove injurious\n   or fatal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolerant', 'a.', 'Inclined to tolerate; favoring toleration; forbearing;\n   indulgent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolerated', 'imp. & p. p.', 'of Tolerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolerating', 'p. pr. & vb. n.', 'of Tolerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolerate', 'v. t.', 'To suffer to be, or to be done, without prohibition or\n   hindrance; to allow or permit negatively, by not preventing; not to\n   restrain; to put up with; as, to tolerate doubtful practices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toleration', 'n.', 'The act of tolerating; the allowance of that which is\n   not wholly approved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toleration', 'n.', 'Specifically, the allowance of religious opinions and\n   modes of worship in a state when contrary to, or different from, those\n   of the established church or belief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toleration', 'n.', 'Hence, freedom from bigotry and severity in judgment of\n   the opinions or belief of others, especially in respect to religious\n   matters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toll', 'v. t.', 'To take away; to vacate; to annul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toll', 'v. t.', 'To draw; to entice; to allure. See Tole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toll', 'v. t.', 'To cause to sound, as a bell, with strokes slowly and\n   uniformly repeated; as, to toll the funeral bell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toll', 'v. t.', 'To strike, or to indicate by striking, as the hour; to\n   ring a toll for; as, to toll a departed friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toll', 'v. t.', 'To call, summon, or notify, by tolling or ringing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolled', 'imp. & p. p.', 'of Toll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolling', 'p. pr. & vb. n.', 'of Toll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toll', 'v. i.', 'To sound or ring, as a bell, with strokes uniformly\n   repeated at intervals, as at funerals, or in calling assemblies, or to\n   announce the death of a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toll', 'n.', 'The sound of a bell produced by strokes slowly and uniformly\n   repeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toll', 'n.', 'A tax paid for some liberty or privilege, particularly for\n   the privilege of passing over a bridge or on a highway, or for that of\n   vending goods in a fair, market, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toll', 'n.', 'A liberty to buy and sell within the bounds of a manor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toll', 'n.', 'A portion of grain taken by a miller as a compensation for\n   grinding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toll', 'v. i.', 'To pay toll or tallage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toll', 'v. i.', 'To take toll; to raise a tax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toll', 'v. t.', 'To collect, as a toll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tollable', 'a.', 'Subject to the payment of toll; as, tollable goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tollage', 'n.', 'Payment of toll; also, the amount or quantity paid as\n   toll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tollbooth', 'n.', 'A place where goods are weighed to ascertain the duties\n   or toll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tollbooth', 'n.', 'In Scotland, a burgh jail; hence, any prison, especially\n   a town jail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tollbooth', 'v. t.', 'To imprison in a tollbooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toller', 'n.', 'A toll gatherer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toller', 'n.', 'One who tolls a bell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolletane', 'a.', 'Of or pertaining to Toledo in Spain; made in Toledo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tollgate', 'n.', 'A gate where toll is taken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tollhouses', 'pl. ', 'of Tollhouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tollhouse', 'n.', 'A house occupied by a receiver of tolls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tollmen', 'pl. ', 'of Tollman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tollman', 'n.', 'One who receives or collects toll; a toll gatherer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolmen', 'n.', 'See Dolmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolsester', 'n.', 'A toll or tribute of a sextary of ale, paid to the lords\n   of some manors by their tenants, for liberty to brew and sell ale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolsey', 'n.', 'A tollbooth; also, a merchants meeting place, or exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolt', 'n.', 'A writ by which a cause pending in a court baron was removed\n   into a country court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toltec', 'n.', 'One of a race which formerly occupied Mexico.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolu', 'n.', 'A fragrant balsam said to have been first brought from\n   Santiago de Tolu, in New Granada. See Balsam of Tolu, under Balsam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toluate', 'n.', 'A salt of any one of the toluic acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toluene', 'n.', 'A hydrocarbon, C6H5.CH3, of the aromatic series,\n   homologous with benzene, and obtained as a light mobile colorless\n   liquid, by distilling tolu balsam, coal tar, etc.; -- called also\n   methyl benzene, phenyl methane, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toluenyl', 'n.', 'Tolyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toluic', 'a.', 'Pertaining to, or designating, one of three metameric\n   acids, CH3.C6H4.CO2H, which are related to toluene and analogous to\n   benzoic acids. They are white crystalline substances, and are called\n   respectively orthotoluic acid, metatoluic acid, and paratoluic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toluid', 'n.', 'A complex double tolyl and toluidine derivative of\n   glycocoll, obtained as a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toluidine', 'n.', 'Any one of three metameric amido derivatives of toluene\n   analogous to aniline, and called respectively orthtoluidine,\n   metatoluidine, and paratoluidine; especially, the commonest one, or\n   paratoluidine, which is obtained as a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toluol', 'n.', 'Alt. of Toluole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toluole', 'n.', 'Same as Toluene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toluric', 'a.', 'Of, pertaining to, or designating, any one of three\n   isomeric crystalline acids, C9H10ON.CO2H, which are toluyl derivatives\n   of glycocoll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolutation', 'n.', 'A pacing or ambling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toluyl', 'n.', 'Any one of the three hypothetical radicals corresponding to\n   the three toluic acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toluylene', 'n.', 'Same as Stilbene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toluylene', 'n.', 'Sometimes, but less properly, tolylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolyl', 'n.', 'The hydrocarbon radical, CH3.C6H4, regarded as\n   characteristic of certain compounds of the aromatic series related to\n   toluene; as, tolyl carbinol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolylene', 'n.', 'A hydrocarbon radical, C6H4.(CH2)2, regarded as\n   characteristic of certain toluene derivatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tolypeutine', 'n.', 'The apar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tom', 'n.', 'The knave of trumps at gleek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomahawk', 'n.', 'A kind of war hatchet used by the American Indians. It\n   was originally made of stone, but afterwards of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomahawked', 'imp. & p. p.', 'of Tomahawk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomahawking', 'p. pr. & vb. n.', 'of Tomahawk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomahawk', 'v. t.', 'To cut, strike, or kill, with a tomahawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomaley', 'n.', 'The liver of the lobster, which becomes green when boiled;\n   -- called also tomalline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toman', 'n.', 'A money of account in Persia, whose value varies greatly at\n   different times and places. Its average value may be reckoned at about\n   two and a half dollars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomatoes', 'pl. ', 'of Tomato');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomato', 'n.', 'The fruit of a plant of the Nightshade family (Lycopersicum\n   esculentun); also, the plant itself. The fruit, which is called also\n   love apple, is usually of a rounded, flattened form, but often\n   irregular in shape. It is of a bright red or yellow color, and is eaten\n   either cooked or uncooked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomb', 'n.', 'A pit in which the dead body of a human being is deposited; a\n   grave; a sepulcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomb', 'n.', 'A house or vault, formed wholly or partly in the earth, with\n   walls and a roof, for the reception of the dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomb', 'n.', 'A monument erected to inclose the body and preserve the name\n   and memory of the dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tombed', 'imp. & p. p.', 'of Tomb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tombing', 'p. pr. & vb. n.', 'of Tomb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomb', 'v. t.', 'To place in a tomb; to bury; to inter; to entomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tombac', 'n.', 'An alloy of copper and zinc, resembling brass, and\n   containing about 84 per cent of copper; -- called also German, / Dutch,\n   brass. It is very malleable and ductile, and when beaten into thin\n   leaves is sometimes called Dutch metal. The addition of arsenic makes\n   white tombac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tombester', 'n.', 'A female dancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tombless', 'a.', 'Destitute of a tomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomboy', 'n.', 'A romping girl; a hoiden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tombstone', 'n.', 'A stone erected over a grave, to preserve the memory of\n   the deceased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomcat', 'n.', 'A male cat, especially when full grown or of large size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomcod', 'n.', 'A small edible American fish (Microgadus tomcod) of the\n   Codfish family, very abundant in autumn on the Atlantic coast of the\n   Northen United States; -- called also frostfish. See Illust. under\n   Frostfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomcod', 'n.', 'The kingfish. See Kingfish (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomcod', 'n.', 'The jack. See 2d Jack, 8. (c).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tome', 'n.', 'As many writings as are bound in a volume, forming part of a\n   larger work; a book; -- usually applied to a ponderous volume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomelet', 'n.', 'All small tome, or volume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomentose', 'a.', 'Covered with matted woolly hairs; as, a tomentose leaf;\n   a tomentose leaf; a tomentose membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tometous', 'a.', 'Tomentose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomenta', 'pl. ', 'of Tomentum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomentum', 'n.', 'The closely matted hair or downy nap covering the leaves\n   or stems of some plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomfool', 'n.', 'A great fool; a trifler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomfoolery', 'n.', 'Folly; trifling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomia', 'pl. ', 'of Tomium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomium', 'n.', 'The cutting edge of the bill of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomjohn', 'n.', 'A kind of open sedan used in Ceylon, carried by a single\n   pole on mens shoulders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tommy', 'n.', 'Bread, -- generally a penny roll; the supply of food carried\n   by workmen as their daily allowance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tommy', 'n.', 'A truck, or barter; the exchange of labor for goods, not\n   money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomnoddy', 'n.', 'A sea bird, the puffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomnoddy', 'n.', 'A fool; a dunce; a noddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomopteris', 'n.', 'A genus of transparent marine annelids which swim\n   actively at the surface of the sea. They have deeply divided or forked\n   finlike organs (parapodia). This genus is the type of the order, or\n   suborder, Gymnocopa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomorn', 'adv.', 'To-morrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomorrow', 'adv.', 'On the day after the present day; on the next day; on\n   the morrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomorrow', 'n.', 'The day after the present; the morrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tompion', 'n.', 'A stopper of a cannon or a musket. See Tampion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tompion', 'n.', 'A plug in a flute or an organ pipe, to modulate the tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tompion', 'n.', 'The iron bottom to which grapeshot are fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tompon', 'n.', 'An inking pad used in lithographic printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomrig', 'n.', 'A rude, wild, wanton girl; a hoiden; a tomboy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomtit', 'n.', 'A titmouse, esp. the blue titmouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tomtit', 'n.', 'The wren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tom-tom', 'n.', 'See Tam-tam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ton', '', 'pl. of Toe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ton', 'n.', 'The common tunny, or house mackerel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ton', 'n.', 'The prevailing fashion or mode; vogue; as, things of ton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ton', 'n.', 'A measure of weight or quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ton', 'n.', 'The weight of twenty hundredweight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ton', 'n.', 'Forty cubic feet of space, being the unit of measurement of\n   the burden, or carrying capacity, of a vessel; as a vessel of 300 tons\n   burden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ton', 'n.', 'A certain weight or quantity of merchandise, with reference to\n   transportation as freight; as, six hundred weight of ship bread in\n   casks, seven hundred weight in bags, eight hundred weight in bulk; ten\n   bushels of potatoes; eight sacks, or ten barrels, of flour; forty cubic\n   feet of rough, or fifty cubic feet of hewn, timber, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonality', 'n.', 'The principle of key in music; the character which a\n   composition has by virtue of the key in which it is written, or through\n   the family relationship of all its tones and chords to the keynote, or\n   tonic, of the whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To-name', 'n.', 'A name added, for the sake of distinction, to ones\n   surname, or used instead of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonca bean', '', 'See Tonka bean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tone', 'n.', 'Sound, or the character of a sound, or a sound considered as\n   of this or that character; as, a low, high, loud, grave, acute, sweet,\n   or harsh tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tone', 'n.', 'Accent, or inflection or modulation of the voice, as adapted\n   to express emotion or passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tone', 'n.', 'A whining style of speaking; a kind of mournful or artificial\n   strain of voice; an affected speaking with a measured rhythm ahd a\n   regular rise and fall of the voice; as, children often read with a\n   tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tone', 'n.', 'A sound considered as to pitch; as, the seven tones of the\n   octave; she has good high tones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tone', 'n.', 'The larger kind of interval between contiguous sounds in the\n   diatonic scale, the smaller being called a semitone as, a whole tone\n   too flat; raise it a tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tone', 'n.', 'The peculiar quality of sound in any voice or instrument; as,\n   a rich tone, a reedy tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tone', 'n.', 'A mode or tune or plain chant; as, the Gregorian tones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tone', 'n.', 'That state of a body, or of any of its organs or parts, in\n   which the animal functions are healthy and performed with due vigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tone', 'n.', 'Tonicity; as, arterial tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tone', 'n.', 'State of mind; temper; mood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tone', 'n.', 'Tenor; character; spirit; drift; as, the tone of his remarks\n   was commendatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tone', 'n.', 'General or prevailing character or style, as of morals,\n   manners, or sentiment, in reference to a scale of high and low; as, a\n   low tone of morals; a tone of elevated sentiment; a courtly tone of\n   manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tone', 'n.', 'The general effect of a picture produced by the combination\n   of light and shade, together with color in the case of a painting; --\n   commonly used in a favorable sense; as, this picture has tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toned', 'imp. & p. p.', 'of Tone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toning', 'p. pr. & vb. n.', 'of Tone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tone', 'v. t.', 'To utter with an affected tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tone', 'v. t.', 'To give tone, or a particular tone, to; to tune. See Tune,\n   v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tone', 'v. t.', 'To bring, as a print, to a certain required shade of\n   color, as by chemical treatment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toned', 'a.', 'Having (such) a tone; -- chiefly used in composition; as,\n   high-toned; sweet-toned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toneless', 'a.', 'Having no tone; unmusical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tong', 'n.', 'Alt. of Tonge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonge', 'n.', 'Tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonga', 'n.', 'A drug useful in neuralgia, derived from a Fijian plant\n   supposed to be of the aroid genus Epipremnum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongkang', 'n.', 'A kind of boat or junk used in the seas of the Malay\n   Archipelago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongo', 'n.', 'The mangrove; -- so called in the Pacific Islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongs', 'n. pl.', 'An instrument, usually of metal, consisting of two\n   parts, or long shafts, jointed together at or near one end, or united\n   by an elastic bow, used for handling things, especially hot coals or\n   metals; -- often called a pair of tongs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'n.', 'an organ situated in the floor of the mouth of most\n   vertebrates and connected with the hyoid arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'n.', 'The power of articulate utterance; speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'n.', 'Discourse; fluency of speech or expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'n.', 'Honorable discourse; eulogy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'n.', 'A language; the whole sum of words used by a particular\n   nation; as, the English tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'n.', 'Speech; words or declarations only; -- opposed to thoughts\n   or actions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'n.', 'A people having a distinct language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'n.', 'The lingual ribbon, or odontophore, of a mollusk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'n.', 'The proboscis of a moth or a butterfly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'n.', 'The lingua of an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'n.', 'Any small sole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'n.', 'That which is considered as resembing an animals tongue,\n   in position or form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'n.', 'A projection, or slender appendage or fixture; as, the\n   tongue of a buckle, or of a balance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'n.', 'A projection on the side, as of a board, which fits into a\n   groove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'n.', 'A point, or long, narrow strip of land, projecting from the\n   mainland into a sea or a lake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'n.', 'The pole of a vehicle; especially, the pole of an ox cart,\n   to the end of which the oxen are yoked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'n.', 'The clapper of a bell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'n.', 'A short piece of rope spliced into the upper part of\n   standing backstays, etc.; also. the upper main piece of a mast composed\n   of several pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'n.', 'Same as Reed, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongued', 'imp. & p. p.', 'of Tongue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonguing', 'p. pr. & vb. n.', 'of Tongue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'v. t.', 'To speak; to utter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'v. t.', 'To chide; to scold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'v. t.', 'To modulate or modify with the tongue, as notes, in\n   playing the flute and some other wind instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'v. t.', 'To join means of a tongue and grove; as, to tongue\n   boards together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'v. i.', 'To talk; to prate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue', 'v. i.', 'To use the tongue in forming the notes, as in playing\n   the flute and some other wind instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonguebird', 'n.', 'The wryneck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongued', 'a.', 'Having a tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonguefish', 'n.', 'A flounder (Symphurus plagiusa) native of the southern\n   coast of the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongueless', 'a.', 'Having no tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongueless', 'a.', 'Hence, speechless; mute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongueless', 'a.', 'Unnamed; not spoken of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonguelet', 'n.', 'A little tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue-pad', 'n.', 'A great talker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue-shaped', 'a.', 'Shaped like a tongue; specifically (Bot.), linear or\n   oblong, and fleshy, blunt at the end, and convex beneath; as, a\n   tongue-shaped leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue-shell', 'n.', 'Any species of Lingula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonguester', 'n.', 'One who uses his tongue; a talker; a story-teller; a\n   gossip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue-tie', 'n.', 'Impeded motion of the tongue because of the shortness\n   of the fraenum, or of the adhesion of its margins to the gums.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue-tie', 'v. t.', 'To deprive of speech or the power of speech, or of\n   distinct articulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue-tied', 'a.', 'Destitute of the power of distinct articulation;\n   having an impediment in the speech, esp. when caused by a short\n   fraenum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongue-tied', 'a.', 'Unable to speak freely, from whatever cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tongueworm', 'n.', 'Any species of Linguatulina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonguy', 'a.', 'Ready or voluble in speaking; as, a tonguy speaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonic', 'a.', 'Of or relating to tones or sounds; specifically (Phon.),\n   applied to, or distingshing, a speech sound made with tone unmixed and\n   undimmed by obstruction, such sounds, namely, the vowels and\n   diphthongs, being so called by Dr. James Rush (1833) \" from their\n   forming the purest and most plastic material of intonation.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonic', 'a.', 'Of or pertaining to tension; increasing tension; hence,\n   increasing strength; as, tonic power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonic', 'a.', 'Increasing strength, or the tone of the animal system;\n   obviating the effects of debility, and restoring healthy functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonic', 'n.', 'A tonic element or letter; a vowel or a diphthong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonic', 'n.', 'The key tone, or first tone of any scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonic', 'n.', 'A medicine that increases the strength, and gives vigor of\n   action to the system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonical', 'a.', 'Tonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonicity', 'n.', 'The state of healthy tension or partial contraction of\n   muscle fibers while at rest; tone; tonus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonight', 'adv.', 'On this present or coming night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonight', 'adv.', 'On the last night past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonight', 'n.', 'The present or the coming night; the night after the\n   present day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonite', 'n.', 'An explosive compound; a preparation of gun cotton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonka bean', '', 'The seed of a leguminous tree (Dipteryx odorata), native\n   of Guiana. It has a peculiarly agreeable smell, and is employed in the\n   scenting of snuff. Called also tonquin bean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonnage', 'n.', 'The weight of goods carried in a boat or a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonnage', 'n.', 'The cubical content or burden of a vessel, or vessels, in\n   tons; or, the amount of weight which one or several vessels may carry.\n   See Ton, n. (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonnage', 'n.', 'A duty or impost on vessels, estimated per ton, or, a\n   duty, toll, or rate payable on goods per ton transported on canals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonnage', 'n.', 'The whole amount of shipping estimated by tons; as, the\n   tonnage of the United States. See Ton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonne', 'n.', 'A tun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonnihood', 'n.', 'The female of the bullfinch; -- called also tonyhoop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonnish', 'a.', 'In the ton; fashionable; modish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonometer', 'n.', 'An instrument for determining the rate of vibrations in\n   tones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonometer', 'n.', 'An apparatus for studying and registering the action of\n   various fluids and drugs on the excised heart of lower animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonometer', 'n.', 'An instrument for measuring tension, esp. that of the\n   eyeball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonometry', 'n.', 'The act of measuring with a tonometer;');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonometry', 'n.', 'measurement of tension, esp. the tension of the eyeball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonophant', 'n.', 'A modification of the kaleidophon, for showing\n   composition of acoustic vibrations. It consists of two thin slips of\n   steel welded together, their length being adjystable by a screw socket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonous', 'a.', 'Abounding in tone or sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonquin bean', '', 'See Tonka bean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonsil', 'n.', 'One of the two glandular organs situated in the throat at\n   the sides of the fauces. The tonsils are sometimes called the almonds,\n   from their shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonsilar', 'a.', 'Of or pertaining to the tonsils; tonsilitic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonsile', 'a.', 'Capable of being clipped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonsilitic', 'a.', 'Tonsilar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonsilitis', 'n.', 'Inflammation of the tonsil; quinsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonsilotome', 'n.', 'An instrument for removing the tonsils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonsilotomy', 'n.', 'The operation of removing the tonsil, or a portion\n   thereof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonsor', 'n.', 'A barber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonsorial', 'a.', 'Of or pertaining to a barber, or shaving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonsure', 'n.', 'The act of clipping the hair, or of shaving the crown of\n   the head; also, the state of being shorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonsure', 'n.', 'The first ceremony used for devoting a person to the\n   service of God and the church; the first degree of the clericate, given\n   by a bishop, abbot, or cardinal priest, consisting in cutting off the\n   hair from a circular space at the back of the head, with prayers and\n   benedictions; hence, entrance or admission into minor orders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonsure', 'n.', 'The shaven corona, or crown, which priests wear as a mark\n   of their order and of their rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonsured', 'a.', 'Having the tonsure; shaven; shorn; clipped; hence, bald.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tontine', 'n.', 'An annuity, with the benefit of survivorship, or a loan\n   raised on life annuities with the benefit of survivorship. Thus, an\n   annuity is shared among a number, on the principle that the share of\n   each, at his death, is enjoyed by the survivors, until at last the\n   whole goes to the last survivor, or to the last two or three, according\n   to the terms on which the money is advanced. Used also adjectively; as,\n   tontine insurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonus', 'n.', 'Tonicity, or tone; as, muscular tonus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tonies', 'pl. ', 'of Tony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tony', 'n.', 'A simpleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Too', 'adv.', 'Over; more than enough; -- noting excess; as, a thing is too\n   long, too short, or too wide; too high; too many; too much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Too', 'adv.', 'Likewise; also; in addition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Took', '', 'imp. of Take.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tool', 'n.', 'An instrument such as a hammer, saw, plane, file, and the\n   like, used in the manual arts, to facilitate mechanical operations; any\n   instrument used by a craftsman or laborer at his work; an implement;\n   as, the tools of a joiner, smith, shoe-maker, etc.; also, a cutter,\n   chisel, or other part of an instrument or machine that dresses work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tool', 'n.', 'A machine for cutting or shaping materials; -- also called\n   machine tool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tool', 'n.', 'Hence, any instrument of use or service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tool', 'n.', 'A weapon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tool', 'n.', 'A person used as an instrument by another person; -- a word\n   of reproach; as, men of intrigue have their tools, by whose agency they\n   accomplish their purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'tooled', 'imp. & p. p.', 'of Tool');
  INSERT INTO words (word, word_type, description) VALUES  (    'tooling', 'p. pr. & vb. n.', 'of Tool');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tool', 'v. t.', 'To shape, form, or finish with a tool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tool', 'v. t.', 'To drive, as a coach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tooling', 'n.', 'Work performed with a tool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tool-post', 'n.', 'Alt. of Tool-stock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tool-stock', 'n.', 'The part of a tool-rest in which a cutting tool is\n   clamped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tool-rest', 'n.', 'the part that supports a tool-post or a tool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toom', 'a.', 'Empty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toom', 'v. t.', 'To empty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toon', '', 'pl. of Toe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toon', 'n.', 'The reddish brown wood of an East Indian tree (Cedrela Toona)\n   closely resembling the Spanish cedar; also. the tree itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toonwood', 'n.', 'Same as Toon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toot', 'v. i.', 'To stand out, or be prominent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toot', 'v. i.', 'To peep; to look narrowly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toot', 'v. t.', 'To see; to spy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tooted', 'imp. & p. p.', 'of Toot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tooting', 'p. pr. & vb. n.', 'of Toot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toot', 'v. i.', 'To blow or sound a horn; to make similar noise by contact\n   of the tongue with the root of the upper teeth at the beginning and end\n   of the sound; also, to give forth such a sound, as a horn when blown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toot', 'v. t.', 'To cause to sound, as a horn, the note being modified at\n   the beginning and end as if by pronouncing the letter t; to blow; to\n   sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tooter', 'n.', 'One who toots; one who plays upon a pipe or horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Teeth', 'pl. ', 'of Tooth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tooth', 'n.', 'One of the hard, bony appendages which are borne on the\n   jaws, or on other bones in the walls of the mouth or pharynx of most\n   vertebrates, and which usually aid in the prehension and mastication of\n   food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tooth', 'n.', 'Fig.: Taste; palate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tooth', 'n.', 'Any projection corresponding to the tooth of an animal, in\n   shape, position, or office; as, the teeth, or cogs, of a cogwheel; a\n   tooth, prong, or tine, of a fork; a tooth, or the teeth, of a rake, a\n   saw, a file, a card.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tooth', 'n.', 'A projecting member resembling a tenon, but fitting into a\n   mortise that is only sunk, not pierced through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tooth', 'n.', 'One of several steps, or offsets, in a tusk. See Tusk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tooth', 'n.', 'An angular or prominence on any edge; as, a tooth on the\n   scale of a fish, or on a leaf of a plant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tooth', 'n.', 'one of the appendages at the mouth of the capsule of a moss.\n   See Peristome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tooth', 'n.', 'Any hard calcareous or chitinous organ found in the mouth of\n   various invertebrates and used in feeding or procuring food; as, the\n   teeth of a mollusk or a starfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothed', 'imp. & p. p.', 'of Tooth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothing', 'p. pr. & vb. n.', 'of Tooth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tooth', 'v. t.', 'To furnish with teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tooth', 'v. t.', 'To indent; to jag; as, to tooth a saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tooth', 'v. t.', 'To lock into each other. See Tooth, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothache', 'n.', 'Pain in a tooth or in the teeth; odontalgia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothback', 'n.', 'Any notodontian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothbill', 'n.', 'A peculiar fruit-eating ground pigeon (Didunculus\n   strigiostris) native of the Samoan Islands, and noted for its\n   resemblance, in several characteristics, to the extinct dodo. Its beak\n   is stout and strongly hooked, and the mandible has two or three strong\n   teeth toward the end. Its color is chocolate red. Called also\n   toothbilled pigeon, and manu-mea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothbrush', 'n.', 'A brush for cleaning the teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothdrawer', 'n.', 'One whose business it is to extract teeth with\n   instruments; a dentist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothed', 'a.', 'Having teeth; furnished with teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothed', 'a.', 'Having marginal projecting points; dentate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothful', 'a.', 'Toothsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothing', 'n.', 'The act or process of indenting or furnishing with teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothing', 'n.', 'Bricks alternately projecting at the end of a wall, in\n   order that they may be bonded into a continuation of it when the\n   remainder is carried up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothless', 'a.', 'Having no teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothlet', 'n.', 'A little tooth, or like projection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothleted', 'a.', 'Having a toothlet or toothlets; as, a toothleted leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothpick', 'n.', 'A pointed instument for clearing the teeth of substances\n   lodged between them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothpicker', 'n.', 'A toothpick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothshell', 'n.', 'Any species of Dentalium and allied genera having a\n   tooth-shaped shell. See Dentalium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothsome', 'a.', 'Grateful to the taste; palatable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothwort', 'n.', 'A plant whose roots are fancied to resemble teeth, as\n   certain plants of the genus Lathraea, and various species of Dentaria.\n   See Coralwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toothy', 'a.', 'Toothed; with teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toozoo', 'n.', 'The ringdove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'n.', 'A childs toy, commonly in the form of a conoid or pear, made\n   to spin on its point, usually by drawing off a string wound round its\n   surface or stem, the motion being sometimes continued by means of a\n   whip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'n.', 'A plug, or conical block of wood, with longitudital grooves on\n   its surface, in which the strands of the rope slide in the process of\n   twisting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'n.', 'The highest part of anything; the upper end, edge, or\n   extremity; the upper side or surface; summit; apex; vertex; cover; lid;\n   as, the top of a spire; the top of a house; the top of a mountain; the\n   top of the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'n.', 'The utmost degree; the acme; the summit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'n.', 'The highest rank; the most honorable position; the utmost\n   attainable place; as, to be at the top of ones class, or at the top of\n   the school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'n.', 'The chief person; the most prominent one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'n.', 'The crown of the head, or the hair upon it; the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'n.', 'The head, or upper part, of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'n.', 'A platform surrounding the head of the lower mast and\n   projecting on all sudes. It serves to spead the topmast rigging, thus\n   strengheningthe mast, and also furnishes a convenient standing place\n   for the men aloft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'n.', 'A bundle or ball of slivers of comkbed wool, from which the\n   noils, or dust, have been taken out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'n.', 'Eve; verge; point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'n.', 'The part of a cut gem between the girdle, or circumference,\n   and the table, or flat upper surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'n.', 'Top-boots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topped', 'imp. & p. p.', 'of Top');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topping', 'p. pr. & vb. n.', 'of Top');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'v. i.', 'To rise aloft; to be eminent; to tower; as, lofty ridges\n   and topping mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'v. i.', 'To predominate; as, topping passions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'v. i.', 'To excel; to rise above others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'v. t.', 'To cover on the top; to tip; to cap; -- chiefly used in the\n   past participle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'v. t.', 'To rise above; to excel; to outgo; to surpass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'v. t.', 'To rise to the top of; to go over the top of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'v. t.', 'To take off the or upper part of; to crop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'v. t.', 'To perform eminently, or better than before.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top', 'v. t.', 'To raise one end of, as a yard, so that that end becomes\n   higher than the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toparch', 'n.', 'The ruler or principal man in a place or country; the\n   governor of a toparchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toparchies', 'pl. ', 'of Toparchy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toparchy', 'n.', 'A small state, consisting of a few cities or towns; a\n   petty country governed by a toparch; as, Judea was formerly divided\n   into ten toparchies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-armor', 'n.', 'A top railing supported by stanchions and equipped with\n   netting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topau', 'n.', 'The rhinocerous bird (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topaz', 'n.', 'A mineral occurring in rhombic prisms, generally yellowish\n   and pellucid, also colorless, and of greenesh, bluish, or brownish\n   shades. It sometimes occurs massive and opaque. It is a fluosilicate of\n   alumina, and is used as a gem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topaz', 'n.', 'Either one of two species of large, brilliantly colored\n   humming birds of the Topaza, of South America and the West Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topazolite', 'n.', 'A topaz-yellow variety of garnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-block', 'n.', 'A large ironbound block strapped with a hook, and, when\n   used, hung to an eyebolt in the cap, -- used in swaying and lowering\n   the topmast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-boots', 'n. pl.', 'High boots, having generally a band of some kind of\n   light-colored leather around the upper part of the leg; riding boots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-chain', 'n.', 'A chain for slinging the lower yards, in time of action,\n   to prevent their falling, if the ropes by which they are hung are shot\n   away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-cloth', 'n.', 'A piece of canvas used to cover the hammocks which are\n   lashed to the top in action to protect the topmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topcoat', 'n.', 'An outer coat; an overcoat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-drain', 'v. t.', 'To drain the surface of, as land; as, to top-drain a\n   field or farm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-draining', 'n.', 'The act or practice of drining the surface of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-dressed', 'imp. & p. p.', 'of Top-dress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-dressing', 'p. pr. & vb. n.', 'of Top-dress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-dress', 'v. t.', 'To apply a surface dressing of manureto,as land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-dressing', 'n.', 'The act of applying a dressing of manure to the\n   surface of land; also, manure so applied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tope', 'n.', 'A moundlike Buddhist sepulcher, or memorial monument, often\n   erected over a Buddhist relic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tope', 'n.', 'A grove or clump of trees; as, a toddy tope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tope', 'n.', 'A small shark or dogfish (Galeorhinus, / Galeus, galeus),\n   native of Europe, but found also on the coasts of California and\n   Tasmania; -- called also toper, oil shark, millers dog, and penny dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tope', 'n.', 'The wren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toped', 'imp. & p. p.', 'of Tope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toping', 'p. pr. & vb. n.', 'of Tope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tope', 'v. i.', 'To drink hard or frequently; to drink strong or spiritous\n   liquors to excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toper', 'n.', 'One who topes, or drinks frequently or to excess; a\n   drunkard; a sot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topet', 'n.', 'The European crested titmouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topful', 'a.', 'Full to the top, ore brim; brimfull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topgallant', 'a.', 'Situated above the topmast and below the royal mast;\n   designatb, or pertaining to, the third spars in order from the deck;\n   as, the topgallant mast, yards, braces, and the like. See Illustration\n   of Ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topgallant', 'a.', 'Fig.: Highest; elevated; splendid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topgallant', 'n.', 'A topgallant mast or sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topgallant', 'n.', 'Fig.: Anything elevated or splendid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toph', 'n.', 'kind of sandstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tophaceous', 'a.', 'Gritty; sandy; rough; stony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-hamper', 'n.', 'The upper rigging, spars, etc., of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-heavy', 'a.', 'Having the top or upper part too heavy for the lower\n   part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tophet', 'n.', 'A place lying east or southeast of Jerusalem, in the valley\n   of Hinnom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tophin', 'n.', 'Same as Toph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tophi', 'pl. ', 'of Tophus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tophus', 'n.', 'One of the mineral concretions about the joints, and in\n   other situations, occurring chiefly in gouty persons. They consist\n   usually of urate of sodium; when occurring in the internal organs they\n   are also composed of phosphate of calcium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tophus', 'n.', 'Calcareous tufa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topiarian', 'a.', 'Of or pertaining to the ornamental cutting and trimming\n   of trees, hedges, etc.; practicing ornamental gardening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topiary', 'a.', 'Of or pertaining to ornamental gardening; produced by\n   cutting, trimming, etc.; topiarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topic', 'n.', 'One of the various general forms of argument employed in\n   probable as distinguished from demonstrative reasoning, -- denominated\n   by Aristotle to`poi (literally, places), as being the places or sources\n   from which arguments may be derived, or to which they may be referred;\n   also, a prepared form of argument, applicable to a great variety of\n   cases, with a supply of which the ancient rhetoricians and orators\n   provided themselves; a commonplace of argument or oratory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topic', 'n.', 'A treatise on forms of argument; a system or scheme of forms\n   or commonplaces of argument or oratory; as, the Topics of Aristotle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topic', 'n.', 'An argument or reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topic', 'n.', 'The subject of any distinct portion of a discourse, or\n   argument, or literary composition; also, the general or main subject of\n   the whole; a matter treated of; a subject, as of conversation or of\n   thought; a matter; a point; a head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topic', 'n.', 'An external local application or remedy, as a plaster, a\n   blister, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topic', 'a.', 'Topical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topical', 'n.', 'Of or pertaining to a place; limited; logical application;\n   as, a topical remedy; a topical claim or privilege.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topical', 'n.', 'Pertaining to, or consisting of, a topic or topics;\n   according to topics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topical', 'n.', 'Resembling a topic, or general maxim; hence, not\n   demonstrative, but merely probable, as an argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topically', 'adv.', 'In a topical manner; with application to, or\n   limitation of, a particular place or topic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topknot', 'n.', 'A crest or knot of feathers upon the head or top, as of a\n   bird; also, an orgamental knot worn on top of the head, as by women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topknot', 'n.', 'A small Europen flounder (Rhoumbus punctatus). The name is\n   also applied to allied species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topless', 'a.', 'Having no top, or no visble fop; hence, fig.: very lofty;\n   supreme; unequaled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-light', 'n.', 'A lantern or light on the top of a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topmem', 'pl. ', 'of Topman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topman', 'n.', 'See Topsman, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topman', 'n.', 'A man stationed in the top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topmast', 'n.', 'The second mast, or that which is next above the lower\n   mast, and below the topgallant mast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topmost', 'a.', 'Highest; uppermost; as, the topmost cliff; the topmost\n   branch of a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topographer', 'n.', 'One who is skilled in the science of topography; one\n   who describes a particular place, town, city, or tract of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topographic', '', 'Alt. of Topographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topographical', '', 'Of or pertaining to topography; descriptive of a\n   place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topographist', 'n.', 'A topographer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topography', 'n.', 'The description of a particular place, town, manor,\n   parish, or tract of land; especially, the exact and scientific\n   delineation and description in minute detail of any place or region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topology', 'n.', 'The art of, or method for, assisting the memory by\n   associating the thing or subject to be remembered with some place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toponomy', 'n.', 'The designation of position and direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toppiece', 'n.', 'A small wig for the top of the head; a toupee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topping', 'a.', 'Rising above; surpassing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topping', 'a.', 'Hence, assuming superiority; proud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topping', 'a.', 'Fine; gallant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topping', 'n.', 'The act of one who tops; the act of cutting off the top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topping', 'n.', 'The act of raising one extremity of a spar higher than the\n   other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topping', 'n.', 'That which comes from hemp in the process of hatcheling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toppingly', 'adv.', 'In a topping or proud manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toppingly', 'a.', 'Same as Topping, a., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toppled', 'imp. & p. p.', 'of Topple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toppling', 'p. pr. & vb. n.', 'of Topple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topple', 'v. i.', 'To fall forward; to pitch or tumble down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topple', 'v. t.', 'To throw down; to overturn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-proud', 'a.', 'Proud to the highest degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-rope', 'n.', 'A rope used for hoisting and lowering a topmast, and for\n   other purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topsail', 'n.', 'In a square-rigged vessel, the sail next above the\n   lowermost sail on a mast. This sail is the one most frequently reefed\n   or furled in working the ship. In a fore-and-aft rigged vessel, the\n   sail set upon and above the gaff. See Cutter, Schooner, Sail, and Ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tops-and-bottoms', 'n. pl.', 'Small rolls of dough, baked, cut in halves,\n   and then browned in an oven, -- used as food for infants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-shaped', 'a.', 'Having the shape of a top; (Bot.) cone-shaped, with the\n   apex downward; turbinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-shell', 'n.', 'Any one of numerous species of marine top-shaped shells\n   of the genus Trochus, or family Trochidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topsmen', 'pl. ', 'of Topsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topsman', 'n.', 'The chief drover of those who drive a herd of cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topsman', 'n.', 'The uppermost sawyer in a saw pit; a topman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topsoil', 'n.', 'The upper layer of soil; surface soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topsoiling', 'n.', 'The act or art of taking off the top soil of land\n   before an excavation or embankment is begun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topstone', 'n.', 'A stone that is placed on the top, or which forms the\n   top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Topsy-turvy', 'adv.', 'In an inverted posture; with the top or head\n   downward; upside down; as, to turn a carriage topsy-turvy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-tackle', 'n.', 'A tackle used in hoisting and lowering the topmast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-timbers', 'n.', 'The highest timbers on the side of a vessel, being\n   those above the futtocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Top-tool', 'n.', 'A tool applied to the top of the work, in distinction\n   from a tool inserted in the anvil and on which the work is placed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toque', 'n.', 'A kind of cap worn in the 16th century, and copied in modern\n   fashions; -- called also toquet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toque', 'n.', 'A variety of the bonnet monkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toquet', 'n.', 'See Toque, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tor', 'n.', 'A tower; a turret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tor', 'n.', 'High-pointed hill; a rocky pinnacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torace', 'v. t.', 'Alt. of Torase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torase', 'v. t.', 'To scratch to pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torbernite', 'n.', 'A mineral occurring in emerald-green tabular crystals\n   having a micaceous structure. It is a hydrous phosphate of uranium and\n   copper. Called also copper uranite, and chalcolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torc', 'n.', 'Same as Torque, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torch', 'n.', 'A light or luminary formed of some combustible substance, as\n   of resinous wood; a large candle or flambeau, or a lamp giving a large,\n   flaring flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torch', 'n.', 'A flashlight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torchbearer', 'n.', 'One whose office it is to carry a torch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torcher', 'n.', 'One who gives light with a torch, or as if with a torch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torchlight', 'n.', 'The light of a torch, or of torches. Also adjectively;\n   as, a torchlight procession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torchon lace', '', 'a simple thread lace worked upon a pillow with coarse\n   thread; also, a similar lace made by machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torchwood', 'n.', 'The inflammable wood of certain trees (Amyris\n   balsamifera, A. Floridana, etc.); also, the trees themselves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torchwort', 'n.', 'The common mullein, the stalks of which, dipped in suet,\n   anciently served for torches. Called also torch, and hig-taper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tore', '', 'imp. of Tear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tore', 'n.', 'The dead grass that remains on mowing land in winter and\n   spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tore', 'n.', 'Same as Torus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tore', 'n.', 'The surface described by the circumference of a circle\n   revolving about a straight line in its own plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tore', 'n.', 'The solid inclosed by such a surface; -- sometimes called an\n   anchor ring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toreador', 'n.', 'A bullfighter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'To-rent', 'imp. & p. p.', 'of To-rend');
  INSERT INTO words (word, word_type, description) VALUES  (    'To-rend', 'v. t.', 'To rend in pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toret', 'n.', 'A Turret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toret', 'n.', 'A ring for fastening a hawks leash to the jesses; also, a\n   ring affixed to the collar of a dog, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toreumatography', 'n.', 'A description of sculpture such as bas-relief in\n   metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toreumatology', 'n.', 'The art or the description of scupture such as\n   bas-relief in metal; toreumatography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toreutic', 'a.', 'In relief; pertaining to sculpture in relief, especially\n   of metal; also, pertaining to chasing such as surface ornamentation in\n   metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torgoch', 'n.', 'The saibling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torilto', 'n.', 'A species of Turnix (Turnix sylvatica) native of Spain and\n   Northen Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torinese', 'a.', 'Of or pertaining to Turin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torinese', 'n. sing. & pl.', 'A native or inhabitant of Turin;\n   collectively, the people of Turin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torment', 'n.', 'An engine for casting stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torment', 'n.', 'Extreme pain; anguish; torture; the utmost degree of\n   misery, either of body or mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torment', 'n.', 'That which gives pain, vexation, or misery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'tormented', 'imp. & p. p.', 'of Torment');
  INSERT INTO words (word, word_type, description) VALUES  (    'tormenting', 'p. pr. & vb. n.', 'of Torment');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torment', 'v. t.', 'To put to extreme pain or anguish; to inflict\n   excruciating misery upon, either of body or mind; to torture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torment', 'v. t.', 'To pain; to distress; to afflict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torment', 'v. t.', 'To tease; to vex; to harass; as, to be tormented with\n   importunities, or with petty annoyances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torment', 'v. t.', 'To put into great agitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tormenter', 'n.', 'One who, or that which, torments; a tormentor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tormenter', 'n.', 'An executioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tormentful', 'a.', 'Full of torment; causing, or accompainied by, torment;\n   excruciating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tormentil', 'n.', 'A rosaceous herb (Potentilla Tormentilla), the root of\n   which is used as a powerful astringent, and for alleviating gripes, or\n   tormina, in diarrhea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tormenting', 'a.', 'Causing torment; as, a tormenting dream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tormentise', 'n.', 'Torture; torment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tormentor', 'n.', 'One who, or that which, torments; one who inflicts penal\n   anguish or tortures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tormentor', 'n.', 'An implement for reducing a stiff soil, resembling a\n   harrow, but running upon wheels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tormentress', 'n.', 'A woman who torments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tormentry', 'n.', 'Anything producing torment, annoyance, or pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tormina', 'n. pl.', 'acute, colicky pains; gripes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torminous', 'a.', 'Affected with tormina; griping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torn', '', 'p. p. of Tear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tornadoes', 'pl. ', 'of Tornado');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tornado', 'n.', 'A violent whirling wind; specifically (Meteorol.), a\n   tempest distinguished by a rapid whirling and slow progressive motion,\n   usually accompaned with severe thunder, lightning, and torrents of\n   rain, and commonly of short duration and small breadth; a small\n   cyclone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tornariae', 'pl. ', 'of Tornaria');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tornaria', 'n.', 'The peculiar free swimming larva of Balanoglossus. See\n   Illust. in Append.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torose', 'a.', 'Cylindrical with alternate swellings and contractions;\n   having the surface covered with rounded prominences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torosity', 'n.', 'The quality or state of being torose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torous', 'a.', 'Torose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpedinous', 'a.', 'Of or pertaining to a torpedo; resembling a torpedo;\n   exerting a benumbing influence; stupefying; dull; torpid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpedoes', 'pl. ', 'of Torpedo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpedo', 'n.', 'Any one of numerous species of elasmobranch fishes\n   belonging to Torpedo and allied genera. They are related to the rays,\n   but have the power of giving electrical shocks. Called also crampfish,\n   and numbfish. See Electrical fish, under Electrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpedo', 'n.', 'An engine or machine for destroying ships by blowing them\n   up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpedo', 'n.', 'A quantity of explosives anchored in a channel, beneath\n   the water, or set adrift in a current, and so arranged that they will\n   be exploded when touched by a vessel, or when an electric circuit is\n   closed by an operator on shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpedo', 'n.', 'A kind of small submarine boat carrying an explosive\n   charge, and projected from a ship against another ship at a distance,\n   or made self-propelling, and otherwise automatic in its action against\n   a distant ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpedo', 'n.', 'A kind of shell or cartridge buried in earth, to be\n   exploded by electricity or by stepping on it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpedo', 'n.', 'A kind of detonating cartridge or shell placed on a rail,\n   and exploded when crushed under the locomotive wheels, -- used as an\n   alarm signal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpedo', 'n.', 'An explosive cartridge or shell lowered or dropped into a\n   bored oil well, and there exploded, to clear the well of obstructions\n   or to open communication with a source of supply of oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpedo', 'n.', 'A kind of firework in the form of a small ball, or pellet,\n   which explodes when thrown upon a hard object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpedo', 'v. t.', 'to destroy by, or subject to the action of, a torpedo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpent', 'a.', 'Having no motion or activity; incapable of motion;\n   benumbed; torpid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpescence', 'n.', 'The quality or state or being torpescent; torpidness;\n   numbness; stupidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toppescent', 'a.', 'Becoming torpid or numb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpid', 'a.', 'Having lost motion, or the power of exertion and feeling;\n   numb; benumbed; as, a torpid limb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpid', 'a.', 'Dull; stupid; sluggish; inactive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpidity', 'n.', 'Same as Torpidness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpidly', 'adv.', 'In a torpid manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpidness', 'n.', 'The qualityy or state of being torpid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpified', 'imp. & p. p.', 'of Torpify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpifying', 'p. pr. & vb. n.', 'of Torpify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpify', 'v. t.', 'To make torpid; to numb, or benumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpitude', 'n.', 'Torpidness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpor', 'n.', 'Loss of motion, or of the motion; a state of inactivity\n   with partial or total insensibility; numbness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torpor', 'n.', 'Dullness; sluggishness; inactivity; as, a torpor of the\n   mental faculties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torporific', 'a.', 'Tending to produce torpor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torquate', 'a.', 'Collared; having a torques, or distinct colored ring\n   around the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'torquated', 'a.', 'Having or wearing a torque, or neck chain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torque', 'n.', 'A collar or neck chain, usually twisted, especially as worn\n   by ancient barbaric nations, as the Gauls, Germans, and Britons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torque', 'n.', 'That which tends to produce torsion; a couple of forces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torque', 'n.', 'A turning or twisting; tendency to turn, or cause to turn,\n   about an axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torqued', 'a.', 'Wreathed; twisted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torqued', 'a.', 'Twisted; bent; -- said of a dolphin haurient, which forms\n   a figure like the letter S.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torques', 'n.', 'A cervical ring of hair or feathers, distinguished by its\n   color or structure; a collar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torrefaction', 'n.', 'The act or process of torrefying, or the state of\n   being torrefied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torrefied', 'imp. & p. p.', 'of Torrefy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torrefying', 'p. pr. & vb. n.', 'of Torrefy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torrefy', 'v. t.', 'To dry by a fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torrefy', 'v. t.', 'To subject to scorching heat, so as to drive off\n   volatile ingredients; to roast, as ores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torrefy', 'v. t.', 'To dry or parch, as drugs, on a metallic plate till\n   they are friable, or are reduced to the state desired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torrent', 'n.', 'A violent stream, as of water, lava, or the like; a stream\n   suddenly raised and running rapidly, as down a precipice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torrent', 'n.', 'Fig.: A violent or rapid flow; a strong current; a flood;\n   as, a torrent of vices; a torrent of eloquence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torrent', 'n.', 'Rolling or rushing in a rapid stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torrential', 'a.', 'Alt. of Torrentine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torrentine', 'a.', 'Of or pertaining to a torrent; having the character of\n   a torrent; caused by a torrent .');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torricellian', 'a.', 'Of or pertaining to Torricelli, an Italian\n   philosopher and mathematician, who, in 1643, discovered that the rise\n   of a liquid in a tube, as in the barometer, is due to atmospheric\n   pressure. See Barometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torrid', 'a.', 'Parched; dried with heat; as, a torrid plain or desert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torrid', 'a.', 'Violenty hot; drying or scorching with heat; burning;\n   parching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torridity', 'n.', 'Torridness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torridness', 'n.', 'The quality or state of being torrid or parched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torril', 'n.', 'A worthless woman; also, a worthless horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torrock', 'n.', 'A gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torsal', 'n.', 'A torsel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torse', 'n.', 'A wreath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torse', 'n.', 'A developable surface. See under Developable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torsel', 'n.', 'A plate of timber for the end of a beam or joist to rest\n   on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torsibillty', 'n.', 'The tendency, as of a rope, to untwist after being\n   twisted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torsion', 'n.', 'The act of turning or twisting, or the state of being\n   twisted; the twisting or wrenching of a body by the exertion of a\n   lateral force tending to turn one end or part of it about a\n   longitudinal axis, while the other is held fast or turned in the\n   opposite direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torsion', 'n.', 'That force with which a thread, wire, or rod of any\n   material, returns, or tends to return, to a state of rest after it has\n   been twisted; torsibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torsional', 'a.', 'Of or pertaining to torsion; resulting from torsion, or\n   the force with which a thread or wire returns to a state of rest after\n   having been twisted round its axis; as, torsional force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torsk', 'n.', 'The cusk. See Cusk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torsk', 'n.', 'The codfish. Called also tusk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torsos', 'pl. ', 'of Torso');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torsi', 'pl. ', 'of Torso');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torso', 'n.', 'The human body, as distinguished from the head and limbs; in\n   sculpture, the trunk of a statue, mutilated of head and limbs; as, the\n   torso of Hercules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tort', 'n.', 'Mischief; injury; calamity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tort', 'n.', 'Any civil wrong or injury; a wrongful act (not involving a\n   breach of contract) for which an action will lie; a form of action, in\n   some parts of the United States, for a wrong or injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tort', 'a.', 'Stretched tight; taut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torta', 'n.', 'a flat heap of moist, crushed silver ore, prepared for the\n   patio process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torteaus', 'pl. ', 'of Torteau');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torteau', 'n.', 'A roundel of a red color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torticollis', 'n.', 'See Wryneck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortile', 'a.', 'Twisted; wreathed; coiled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortility', 'n.', 'The quality or state of being tortile, twisted, or\n   wreathed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortilla', 'n.', 'An unleavened cake, as of maize flour, baked on a heated\n   iron or stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortion', 'n.', 'Torment; pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortious', 'a.', 'Injurious; wrongful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortious', 'a.', 'Imploying tort, or privat injury for which the law gives\n   damages; involing tort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortiously', 'adv.', 'In a tortous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortive', 'a.', 'Twisted; wreathed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortoise', 'n.', 'Any one of numerous species of reptiles of the order\n   Testudinata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortoise', 'n.', 'Same as Testudo, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortoise', 'n.', 'having a color like that of a tortoises shell, black\n   with white and orange spots; -- used mostly to describe cats of that\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortoise', 'n.', 'a tortoise-shell cat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortricid', 'a.', 'Of or pertaining to Tortix, or the family Tortricidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortrix', 'n.', 'Any one of numerous species of small moths of the family\n   Tortricidae, the larvae of which usually roll up the leaves of plants\n   on which they live; -- also called leaf roller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortrix', 'n.', 'A genus of tropical short-tailed snakes, which are not\n   venomous. One species (Tortrix scytalae) is handsomely banded with\n   black, and is sometimes worn alive by the natives of Brazil for a\n   necklace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortulous', 'a.', 'Swelled out at intervals like a knotted cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortuose', 'a.', 'Wreathed; twisted; winding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortuoslty', 'n.', 'the quality or state of being tortuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortuous', 'a.', 'Bent in different directions; wreathed; twisted; winding;\n   as, a tortuous train; a tortuous train; a tortuous leaf or corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortuous', 'a.', 'Fig.: Deviating from rectitude; indirect; erroneous;\n   deceitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortuous', 'a.', 'Injurious: tortious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortuous', 'a.', 'Oblique; -- applied to the six signs of the zodiac (from\n   Capricorn to Gemini) which ascend most rapidly and obliquely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torturable', 'a.', 'Capable of being tortured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torture', 'n.', 'Extreme pain; anguish of body or mind; pang; agony;\n   torment; as, torture of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torture', 'n.', 'Especially, severe pain inflicted judicially, either as\n   punishment for a crime, or for the purpose of extorting a confession\n   from an accused person, as by water or fire, by the boot or thumbkin,\n   or by the rack or wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torture', 'n.', 'The act or process of torturing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tortured', 'imp. & p. p.', 'of Torture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torturing.', 'p. pr. & vb. n.', 'of Torture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torture', 'v. t.', 'To put to torture; to pain extremely; to harass; to\n   vex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torture', 'v. t.', 'To punish with torture; to put to the rack; as, to\n   torture an accused person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torture', 'v. t.', 'To wrest from the proper meaning; to distort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torture', 'v. t.', 'To keep on the stretch, as a bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torturer', 'n.', 'One who tortures; a tormentor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torturingly', 'adv.', 'So as to torture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torturous', 'a.', 'Involving, or pertaining to, torture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torulae', 'pl. ', 'of Torula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torula', 'n.', 'A chain of special bacteria. (b) A genus of budding fungi.\n   Same as Saccharomyces. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torulaform', 'a.', 'Having the appearance of a torula; in the form of a\n   little chain; as, a torulaform string of micrococci.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torulose', 'a.', 'Same as Torose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torulous', 'a.', 'Same as Torose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tori', 'pl. ', 'of Torus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torus', 'n.', 'A lage molding used in the bases of columns. Its profile is\n   semicircular. See Illust. of Molding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torus', 'n.', 'One of the ventral parapodia of tubicolous annelids. It\n   usually has the form of an oblong thickening or elevation of the\n   integument with rows of uncini or hooks along the center. See Illust.\n   under Tubicolae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torus', 'n.', 'The receptacle, or part of the flower on which the carpels\n   stand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torus', 'n.', 'See 3d Tore, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torved', 'a.', 'Stern; grim. See Torvous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torvity', 'a.', 'Sourness or severity of countenance; sterness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Torvous', 'a.', 'Sour of aspect; of a severe countenance; stern; grim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tories', 'pl. ', 'of Tory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tory', 'n.', 'A member of the conservative party, as opposed to the\n   progressive party which was formerly called the Whig, and is now called\n   the Liberal, party; an earnest supporter of exsisting royal and\n   ecclesiastical authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tory', 'n.', 'One who, in the time of the Revolution, favored submitting\n   tothe claims of Great Britain against the colonies; an adherent tothe\n   crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tory', 'a.', 'Of ro pertaining to the Tories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toryism', 'n.', 'The principles of the Tories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toscatter', 'v. t.', 'To scatter in pieces; to divide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tose', 'v. t.', 'To tease, or comb, as wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tosh', 'a.', 'Neat; trim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toshred', 'v. t.', 'To cut into shreads or pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tossed', 'imp. & p. p.', 'of Toss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tost', '', 'of Toss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tossing', 'p. pr. & vb. n.', 'of Toss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toss', 'v. t.', 'To throw with the hand; especially, to throw with the palm\n   of the hand upward, or to throw upward; as, to toss a ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toss', 'v. t.', 'To lift or throw up with a sudden or violent motion; as,\n   to toss the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toss', 'v. t.', 'To cause to rise and fall; as, a ship tossed on the waves\n   in a storm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toss', 'v. t.', 'To agitate; to make restless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toss', 'v. t.', 'Hence, to try; to harass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toss', 'v. t.', 'To keep in play; to tumble over; as, to spend four years\n   in tossing the rules of grammar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toss', 'v. i.', 'To roll and tumble; to be in violent commotion; to write;\n   to fling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toss', 'v. i.', 'To be tossed, as a fleet on the ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toss', 'n.', 'A throwing upward, or with a jerk; the act of tossing; as,\n   the toss of a ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toss', 'n.', 'A throwing up of the head; a particular manner of raising the\n   head with a jerk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tossel', 'n.', 'See Tassel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tosser', 'n.', 'Ohe who tosser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tossily', 'adv.', 'In a tossy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tossing', 'n.', 'The act of throwing upward; a rising and falling suddenly;\n   a rolling and tumbling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tossing', 'n.', 'A process which consists in washing ores by violent\n   agitation in water, in order to separate the lighter or earhy\n   particles; -- called also tozing, and treloobing, in Cornwall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tossing', 'n.', 'A process for refining tin by dropping it through the air\n   while melted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tosspot', 'n.', 'A toper; one habitually given to strong drink; a drunkard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tossy', 'a.', 'Tossing the head, as in scorn or pride; hence, proud;\n   contemptuous; scornful; affectedly indifferent; as, a tossy\n   commonplace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tost', '', 'imp. & p. p. of Toss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tosto', 'a.', 'Quick; rapid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toswink', 'v. i.', 'To labor excessively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tot', 'n.', 'Anything small; -- frequently applied as a term of endearment\n   to a little child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tot', 'n.', 'A drinking cup of small size, holding about half a pint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tot', 'n.', 'A foolish fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tota', 'n.', 'The grivet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Total', 'a.', 'Whole; not divided; entire; full; complete; absolute; as, a\n   total departure from the evidence; a total loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Total', 'n.', 'The whole; the whole sum or amount; as, these sums added\n   make the grand total of five millions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totality', 'n.', 'The quality or state of being total; as, the totality of\n   an eclipse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totality', 'n.', 'The whole sum; the whole quantity or amount; the\n   entirety; as, the totalityof human knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totalize', 'v. t.', 'To make total, or complete;to reduce to completeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totally', 'adv.', 'In a total manner; wholly; entirely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totalness', 'n.', 'The quality or state of being total; entireness;\n   totality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toted', 'imp. & p. p.', 'of Tote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toting', 'p. pr. & vb. n.', 'of Tote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tote', 'v. t.', 'To carry or bear; as, to tote a child over a stream; -- a\n   colloquial word of the Southern States, and used esp. by negroes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tote', 'n.', 'The entire body, or all; as, the whole tote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totear', 'v. t.', 'To tear or rend in pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totem', 'n.', 'A rude picture, as of a bird, beast, or the like, used by\n   the North American Indians as a symbolic designation, as of a family or\n   a clan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totemic', 'a.', 'Of or pertaining to a totem, or totemism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totemism', 'n.', 'The system of distinguishing families, clans, etc., in a\n   tribe by the totem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totemism', 'n.', 'Superstitious regard for a totem; the worship of any real\n   or imaginary object; nature worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totemist', 'n.', 'One belonging to a clan or tribe having a totem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toter', 'n.', 'The stone roller. See Stone roller (a), under Stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tother', '', 'A colloquial contraction of the other, and formerly a\n   contraction for that other. See the Note under That, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totipalmate', 'a.', 'Having all four toes united by a web; -- said of\n   certain sea birds, as the pelican and the gannet. See Illust. under\n   Aves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totipalmi', 'n.pl.', 'A division of swimming birds including those that\n   have totipalmate feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totipresence', 'n.', 'Omnipresence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totipresent', 'a.', 'Omnipresence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tottered', 'imp. & p. p.', 'of Totter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tottering', 'p. pr. & vb. n.', 'of Totter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totter', 'v. i.', 'To shake so as to threaten a fall; to vacillate; to be\n   unsteady; to stagger; as,an old man totters with age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totter', 'v. i.', 'To shake; to reel; to lean; to waver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totterer', 'n.', 'One who totters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totteringly', 'adv.', 'In a tottering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tottery', 'a.', 'Trembling or vaccilating, as if about to fall; unsteady;\n   shaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tottled', 'imp. & p. p.', 'of Totly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tottling', 'p. pr. & vb. n.', 'of Totly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totly', 'v. i.', 'To walk in a wavering, unsteady manner; to toddle; to\n   topple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tottlish', 'a.', 'Trembling or tottering, as if about to fall; un steady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Totty', 'a.', 'Unsteady; dizzy; tottery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toty', 'a.', 'Totty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toty', 'n.', 'A sailor or fisherman; -- so called in some parts of the\n   Pacific.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toucan', 'n.', 'Any one of numerous species of fruit-eating birds of\n   tropical America belonging to Ramphastos, Pteroglossus, and allied\n   genera of the family Ramphastidae. They have a very large, but light\n   and thin, beak, often nearly as long as the body itself. Most of the\n   species are brilliantly colored with red, yellow, white, and black in\n   striking contrast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toucan', 'n.', 'A modern constellation of the southern hemisphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toncanet', 'n.', 'A small toucan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touched', 'imp. & p. p.', 'of Touch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touching', 'p. pr. & vb. n.', 'of Touch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. t.', 'To come in contact with; to hit or strike lightly\n   against; to extend the hand, foot, or the like, so as to reach or rest\n   on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. t.', 'To perceive by the sense of feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. t.', 'To come to; to reach; to attain to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. t.', 'To try; to prove, as with a touchstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. t.', 'To relate to; to concern; to affect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. t.', 'To handle, speak of, or deal with; to treat of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. t.', 'To meddle or interfere with; as, I have not touched the\n   books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. t.', 'To affect the senses or the sensibility of; to move; to\n   melt; to soften.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. t.', 'To mark or delineate with touches; to add a slight stroke\n   to with the pencil or brush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. t.', 'To infect; to affect slightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. t.', 'To make an impression on; to have effect upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. t.', 'To strike; to manipulate; to play on; as, to touch an\n   instrument of music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. t.', 'To perform, as a tune; to play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. t.', 'To influence by impulse; to impel forcibly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. t.', 'To harm, afflict, or distress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. t.', 'To affect with insanity, especially in a slight degree;\n   to make partially insane; -- rarely used except in the past participle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. t.', 'To be tangent to. See Tangent, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'a.', 'To lay a hand upon for curing disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. i.', 'To be in contact; to be in a state of junction, so that\n   no space is between; as, two spheres touch only at points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. i.', 'To fasten; to take effect; to make impression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. i.', 'To treat anything in discourse, especially in a slight or\n   casual manner; -- often with on or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v. i.', 'To be brought, as a sail, so close to the wind that its\n   weather leech shakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v.', 'The act of touching, or the state of being touched; contact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v.', 'The sense by which pressure or traction exerted on the skin\n   is recognized; the sense by which the properties of bodies are\n   determined by contact; the tactile sense. See Tactile sense, under\n   Tactile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v.', 'Act or power of exciting emotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v.', 'An emotion or affection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v.', 'Personal reference or application.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v.', 'A stroke; as, a touch of raillery; a satiric touch; hence,\n   animadversion; censure; reproof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v.', 'A single stroke on a drawing or a picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v.', 'Feature; lineament; trait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v.', 'The act of the hand on a musical instrument; bence, in the\n   plural, musical notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v.', 'A small quantity intermixed; a little; a dash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v.', 'A hint; a suggestion; slight notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v.', 'A slight and brief essay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v.', 'A touchstone; hence, stone of the sort used for touchstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v.', 'Hence, examination or trial by some decisive standard; test;\n   proof; tried quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v.', 'The particular or characteristic mode of action, or the\n   resistance of the keys of an instrument to the fingers; as, a heavy\n   touch, or a light touch; also, the manner of touching, striking, or\n   pressing the keys of a piano; as, a legato touch; a staccato touch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'v.', 'The broadest part of a plank worked top and but (see Top and\n   but, under Top, n.), or of one worked anchor-stock fashion (that is,\n   tapered from the middle to both ends); also, the angles of the stern\n   timbers at the counters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'n.', 'That part of the field which is beyond the line of flags on\n   either side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch', 'n.', 'A boys game; tag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touchable', 'a.', 'Capable of being touched; tangible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touchback', 'n.', 'The act of touching the football down by a player behind\n   his own goal line when it received its last impulse from an opponent;\n   -- distinguished from safety touchdown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch-box', 'n.', 'A box containing lighted tinder, formerly carried by\n   soldiers who used matchlocks, to kindle the match.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touchdown', 'n.', 'The act of touching the football down behind the\n   opponents goal .');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touchhole', 'n.', 'The vent of a cannot or other firearm, by which fire is\n   communicateed to the powder of the charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touchily', 'adv.', 'In a touchy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touchiness', 'n.', 'The quality or state of being touchy peevishness;\n   irritability; irascibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touching', 'a.', 'Affecting; moving; pathetic; as, a touching tale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touching', 'prep.', 'Concerning; with respect to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touching', 'n.', 'The sense or act of feeling; touch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch-me-not', 'n.', 'See Impatiens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch-me-not', 'n.', 'Squirting cucumber. See under Cucumber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch-needle', 'n.', 'A small bar of gold and silver, either pure, or\n   alloyed in some known proportion with copper, for trying the purity of\n   articles of gold or silver by comparison of the streaks made by the\n   article and the bar on a touchstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touch-paper', 'n.', 'Paper steeped in saltpeter, which burns slowly, and is\n   used as a match for firing gunpowder, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touchstone', 'n.', 'Lydian stone; basanite; -- so called because used to\n   test the purity of gold and silver by the streak which is left upon the\n   stone when it is rubbed by the metal. See Basanite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touchstone', 'n.', 'Any test or criterion by which the qualities of a thing\n   are tried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touchwood', 'n.', 'Wood so decayed as to serve for tinder; spunk, or punk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touchwood', 'n.', 'Dried fungi used as tinder; especially, the Polyporus\n   igniarius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touchy', 'a.', 'Peevish; irritable; irascible; techy; apt to take fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tough', 'superl.', 'Having the quality of flexibility without brittleness;\n   yielding to force without breaking; capable of resisting great strain;\n   as, the ligaments of animals are remarkably tough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tough', 'superl.', 'Not easily broken; able to endure hardship; firm;\n   strong; as, tough sinews.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tough', 'superl.', 'Not easily separated; viscous; clammy; tenacious; as,\n   tough phlegm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tough', 'superl.', 'Stiff; rigid; not flexible; stubborn; as, a tough bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tough', 'superl.', 'Severe; violent; as, a tough storm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tough-cake', 'n.', 'See Tough-pitch (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toughened', 'imp. & p. p.', 'of Toughen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toughening', 'p. pr. & vb. n.', 'of Toughen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toughen', 'v. i. & t.', 'To grow or make tough, or tougher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tough-head', 'n.', 'The ruddy duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toughish', 'a.', 'Tough in a slight degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toughly', 'adv.', 'In a tough manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toughness', 'n.', 'The quality or state of being tough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tough-pitch', 'n.', 'The exact state or quality of texture and consistency\n   of well reduced and refined copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tough-pitch', 'n.', 'Copper so reduced; -- called also tough-cake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touite', 'n.', 'The wood warbler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toupee', 'n.', 'Alt. of Toupet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toupet', 'n.', 'A little tuft; a curl or artificial lock of hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toupet', 'n.', 'A small wig, or a toppiece of a wig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toupettit', 'n.', 'The crested titmouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tour', 'n.', 'A tower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tour', 'v. t.', 'A going round; a circuit; hence, a journey in a circuit; a\n   prolonged circuitous journey; a comprehensive excursion; as, the tour\n   of Europe; the tour of France or England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tour', 'v. t.', 'A turn; a revolution; as, the tours of the heavenly\n   bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tour', 'v. t.', 'anything done successively, or by regular order; a turn;\n   as, a tour of duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toured', 'imp. & p. p.', 'of Tour');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touring', 'p. pr. & vb. n.', 'of Tour');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tour', 'v. i.', 'To make a tourm; as, to tour throught a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touraco', 'n.', 'Same as Turacou.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tourbillion', 'n.', 'An ornamental firework which turns round, when in the\n   air, so as to form a scroll of fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tourist', 'n.', 'One who makes a tour, or performs a journey in a circuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tourmaline', 'n.', 'A mineral occurring usually in three-sided or six-sided\n   prisms terminated by rhombohedral or scalenohedral planes. Black\n   tourmaline (schorl) is the most common variety, but there are also\n   other varieties, as the blue (indicolite), red (rubellite), also green,\n   brown, and white. The red and green varieties when transparent are\n   valued as jewels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tourn', 'n.', 'A spinning wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tourn', 'n.', 'The sheriffs turn, or court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tournament', 'n.', 'A mock fight, or warlike game, formerly in great favor,\n   in which a number of combatants were engaged, as an exhibition of their\n   address and bravery; hence, figuratively, a real battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tournament', 'n.', 'Any contest of skill in which there are many\n   contestents for championship; as, a chess tournament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tournery', 'n.', 'Work turned on a lathe; turnery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tourney', 'v. t.', 'A tournament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tourney', 'n.', 'To perform in tournaments; to tilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tourniquet', 'n.', 'An instrument for arresting hemorrhage. It consists\n   essentially of a pad or compress upon which pressure is made by a band\n   which is tightened by a screw or other means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tournois', 'n.', 'A former French money of account worth 20 sous, or a\n   franc. It was thus called in distinction from the Paris livre, which\n   contained 25 sous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tournure', 'n.', 'Turn; contour; figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tournure', 'n.', 'Any device used by women to expand the skirt of a dress\n   below the waist; a bustle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toused', 'imp. & p. p.', 'of Touze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tousing', 'p. pr. & vb. n.', 'of Touze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touse', 'v. t. & i.', 'Alt. of Touze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touze', 'v. t. & i.', 'To pull; to haul; to tear; to worry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touse', 'n.', 'A pulling; a disturbance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tousel', 'v. t.', 'Same as Tousle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touser', 'n.', 'One who touses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tousle', 'v. t.', 'To put into disorder; to tumble; to touse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tous-les-mois', 'n.', 'A kind of starch with very large, oval, flattened\n   grains, often sold as arrowroot, and extensively used for adulterating\n   cocoa. It is made from the rootstocks of a species of Canna, probably\n   C. edulis, the tubers of which are edible every month in the year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tout', 'v. i.', 'To act as a tout. See 2d Tout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tout', 'v. i.', 'To ply or seek for customers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tout', 'n.', 'One who secretly watches race horses which are in course of\n   training, to get information about their capabilities, for use in\n   betting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tout', 'v. i.', 'To toot a horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tout', 'n.', 'The anus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tout-ensemble', 'n.', 'All together; hence, in costume, the fine arts,\n   etc., the general effect of a work as a whole, without regard to the\n   execution of the separate perts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touter', 'n.', 'One who seeks customers, as for an inn, a public\n   conveyance, shops, and the like: hence, an obtrusive candidate for\n   office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Touze', 'v.t & i.', 'See Touse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tow', 'n.', 'The coarse and broken part of flax or hemp, separated from the\n   finer part by the hatchel or swingle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towed', 'imp. & p. p.', 'of Tow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towing', 'p. pr. & vb. n.', 'of Tow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tow', 'v. t.', 'To draw or pull through the water, as a vessel of any kind,\n   by means of a rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tow', 'v. t.', 'A rope by which anything is towed; a towline, or towrope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tow', 'v. t.', 'The act of towing, or the state of being towed; --chiefly\n   used in the phrase, to take in tow, that is to tow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tow', 'v. t.', 'That which is towed, or drawn by a towline, as a barge,\n   raft, collection of boats, ect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towage', 'v.', 'The act of towing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towage', 'v.', 'The price paid for towing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towall', 'n.', 'A towel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toward', 'prep.', 'Alt. of Towards');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towards', 'prep.', 'In the direction of; to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towards', 'prep.', 'With direction to, in a moral sense; with respect or\n   reference to; regarding; concerning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towards', 'prep.', 'Tending to; in the direction of; in behalf of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towards', 'prep.', 'Near; about; approaching to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toward', 'adv.', 'Alt. of Towards');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towards', 'adv.', 'Near; at hand; in state of preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toward', 'prep.', 'Approaching; coming near.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toward', 'prep.', 'Readly to do or learn; compliant with duty; not froward;\n   apt; docile; tractable; as, a toward youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toward', 'prep.', 'Ready to act; forward; bold; valiant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towardliness', 'n.', 'The quality or state of being towardly; docility;\n   tractableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towardly', 'a.', 'Same as Toward, a., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towardness', 'n.', 'Quality or state of being toward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towards', 'prep. & adv.', 'See Toward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towboat', 'n.', 'A vessel constructed for being towed, as a canal boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towboat', 'n.', 'A steamer used for towing other vessels; a tug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towel', 'n.', 'A cloth used for wiping, especially one used for drying\n   anything wet, as the person after a bath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towel', 'v. t.', 'To beat with a stick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toweling', 'n.', 'Cloth for towels, especially such as is woven in long\n   pieces to be cut at will, as distinguished from that woven in towel\n   lengths with borders, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tower', 'n.', 'A mass of building standing alone and insulated, usually\n   higher than its diameter, but when of great size not always of that\n   proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tower', 'n.', 'A projection from a line of wall, as a fortification, for\n   purposes of defense, as a flanker, either or the same height as the\n   curtain wall or higher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tower', 'n.', 'A structure appended to a larger edifice for a special\n   purpose, as for a belfry, and then usually high in proportion to its\n   width and to the height of the rest of the edifice; as, a church tower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tower', 'n.', 'A citadel; a fortress; hence, a defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tower', 'n.', 'A headdress of a high or towerlike form, fashionable about\n   the end of the seventeenth century and until 1715; also, any high\n   headdress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tower', 'n.', 'High flight; elevation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'towered', 'imp. & p. p.', 'of Tower');
  INSERT INTO words (word, word_type, description) VALUES  (    'towering', 'p. pr. & vb. n.', 'of Tower');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tower', 'v. i.', 'To rise and overtop other objects; to be lofty or very\n   high; hence, to soar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tower', 'v. t.', 'To soar into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towered', 'a.', 'Adorned or defended by towers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towering', 'a.', 'Very high; elevated; rising aloft; as, a towering height.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towering', 'a.', 'Hence, extreme; violent; surpassing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towery', 'a.', 'Having towers; adorned or defended by towers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tow-head', 'n.', 'An urchin who has soft, whitish hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tow-head', 'n.', 'The hooded merganser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towhee', 'n.', 'The chewink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towilly', 'n.', 'The sanderling; -- so called from its cry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towline', 'v. t.', 'A line used to tow vessels; a towrope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Town', 'adv. & prep.', 'Formerly: (a) An inclosure which surrounded the\n   mere homestead or dwelling of the lord of the manor. [Obs.] (b) The\n   whole of the land which constituted the domain. [Obs.] (c) A collection\n   of houses inclosed by fences or walls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Town', 'adv. & prep.', 'Any number or collection of houses to which belongs\n   a regular market, and which is not a city or the see of a bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Town', 'adv. & prep.', 'Any collection of houses larger than a village, and\n   not incorporated as a city; also, loosely, any large, closely populated\n   place, whether incorporated or not, in distinction from the country, or\n   from rural communities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Town', 'adv. & prep.', 'The body of inhabitants resident in a town; as, the\n   town voted to send two representatives to the legislature; the town\n   voted to lay a tax for repairing the highways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Town', 'adv. & prep.', 'A township; the whole territory within certain\n   limits, less than those of a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Town', 'adv. & prep.', 'The court end of London;-- commonly with the.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Town', 'adv. & prep.', 'The metropolis or its inhabitants; as, in winter\n   the gentleman lives in town; in summer, in the country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Town', 'adv. & prep.', 'A farm or farmstead; also, a court or farmyard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Town-crier', 'n.', 'A town officer who makes proclamations to the people;\n   the public crier of a town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towned', 'a.', 'Having towns; containing many towns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Townhall', 'n.', 'A public hall or building, belonging to a town, where the\n   public offices are established, the town council meets, the people\n   assemble in town meeting, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Townhouse', 'n.', 'A building devoted to the public used of a town; a\n   townhall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Townish', 'a.', 'Of or pertaining to the inhabitants of a town; like the\n   town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Townless', 'a.', 'Having no town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Townlet', 'n.', 'A small town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Townsfolk', 'n.', 'The people of a town; especially, the inhabitants of a\n   city, in distinction from country people; townspeople.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Township', 'n.', 'The district or territory of a town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Township', 'n.', 'In surveys of the public land of the United States, a\n   division of territory six miles square, containing 36 sections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Township', 'n.', 'In Canada, one of the subdivisions of a county.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Townsmen', 'pl. ', 'of Townsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Townsman', 'n.', 'An inhabitant of a town; one of the same town with\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Townsman', 'n.', 'A selectman, in New England. See Selectman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Townpeople', 'n.', 'The inhabitants of a town or city, especially in\n   distinction from country people; townsfolk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Townward', 'adv.', 'Alt. of Townwards');
  INSERT INTO words (word, word_type, description) VALUES  (    'Townwards', 'adv.', 'Toward a town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towpath', 'n.', 'A path traveled by men or animals in towing boats; --\n   called also towing path.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towrope', 'n.', 'A rope used in towing vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towser', 'n.', 'A familiar name for a dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Towy', 'a.', 'Composed of, or like, tow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tox/mia', 'a.', 'Blood poisoning. See under Blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toxic', 'a.', 'Alt. of Toxical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toxical', 'a.', 'Of or pertaining to poison; poisonous; as, toxic\n   medicines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toxicant', 'n.', 'A poisonous agent or drug, as opium; an intoxicant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toxicological', 'a.', 'Of or pertaining to toxicology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toxicologist', 'n.', 'One versed in toxicology; the writer of a treatise on\n   poisons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toxicology', 'n.', 'The science which treats of poisons, their effects,\n   antidotes, and recognition; also, a discourse or treatise on the\n   science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toxicomania', 'n.', 'Toxiphobia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toxicomania', 'n.', 'An insane desire for intoxicating or poisonous drugs,\n   as alcohol or opium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toxifera', 'n.pl.', 'Same as Toxoglossa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toxin', 'n.', 'Alt. of Toxine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toxine', 'n.', 'A poisonous product formed by pathogenic bacteria, as a\n   toxic proteid or poisonous ptomaine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toxiphobia', 'n.', 'An insane or greatly exaggerated dread of poisons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toxodon', 'n.', 'A gigantic extinct herbivorous mammal from South America,\n   having teeth bent like a bow. It is the type of the order Toxodonta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toxodonta', 'n.pl.', 'An extinct order of Mammalia found in the South\n   American Tertiary formation. The incisor teeth were long and curved and\n   provided with a persistent pulp. They are supposed to be related both\n   to the rodents and ungulates. Called also Toxodontia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toxoglossa', 'n.pl.', 'A division of marine gastropod mollusks in which the\n   radula are converted into poison fangs. The cone shells (Conus),\n   Pleurotoma, and Terebra, are examples. See Illust. of Cone, n., 4,\n   Pleurotoma, and Terebra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toxophilite', 'n.', 'A lover of archery; one devoted to archery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toxotes', 'n.', 'A genus of fishes comprising the archer fishes. See Archer\n   fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toy', 'v. t.', 'A plaything for children; a bawble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toy', 'v. t.', 'A thing for amusement, but of no real value; an article of\n   trade of little value; a trifle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toy', 'v. t.', 'A wild fancy; an odd conceit; idle sport; folly; trifling\n   opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toy', 'v. t.', 'Amorous dalliance; play; sport; pastime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toy', 'v. t.', 'An old story; a silly tale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toy', 'v. t.', 'A headdress of linen or woolen, that hangs down over the\n   shoulders, worn by old women of the lower classes; -- called also toy\n   mutch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'toyed', 'imp. & p. p.', 'of Toy');
  INSERT INTO words (word, word_type, description) VALUES  (    'toying', 'p. pr. & vb. n.', 'of Toy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toy', 'v. i.', 'To dally amorously; to trifle; to play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toy', 'v. t.', 'To treat foolishly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toyear', 'adv.', 'This year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toyer', 'n.', 'One who toys; one who is full of trifling tricks; a trifler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toyful', 'a.', 'Full of trifling play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toyhouse', 'n.', 'A house for children to play in or to play with; a\n   playhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toyingly', 'adv.', 'In a toying manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toyish', 'a.', 'Sportive; trifling; wanton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toyish', 'a.', 'Resembling a toy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toyman', 'n.', 'One who deals in toys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toyshop', 'n.', 'A shop where toys are sold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toysome', 'a.', 'Disposed to toy; trifling; wanton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Toze', 'v. t.', 'To pull violently; to touse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tozy', 'a.', 'Soft, like wool that has been teased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trabeae', 'pl. ', 'of Trabea');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trabea', 'n.', 'A toga of purple, or ornamented with purple horizontal\n   stripes. -- worn by kings, consuls, and augurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trabeated', 'a.', 'Furnished with an entablature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trabeation', 'n.', 'Same as Entablature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trabeculae', 'pl. ', 'of Trabecula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trabecula', 'n.', 'A small bar, rod, bundle of fibers, or septal membrane,\n   in the framework of an organ part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trabecular', 'a.', 'Of or pertaining to a trabecula or trabeculae; composed\n   of trabeculae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trabeculate', 'a.', 'Crossbarred, as the ducts in a banana stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trabu', 'n.', 'Same as Trubu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trace', 'n.', 'One of two straps, chains, or ropes of a harness, extending\n   from the collar or breastplate to a whiffletree attached to a vehicle\n   or thing to be drawn; a tug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trace', 'v. t.', 'A mark left by anything passing; a track; a path; a\n   course; a footprint; a vestige; as, the trace of a carriage or sled;\n   the trace of a deer; a sinuous trace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trace', 'v. t.', 'A very small quantity of an element or compound in a\n   given substance, especially when so small that the amount is not\n   quantitatively determined in an analysis; -- hence, in stating an\n   analysis, often contracted to tr.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trace', 'v. t.', 'A mark, impression, or visible appearance of anything\n   left when the thing itself no longer exists; remains; token; vestige.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trace', 'v. t.', 'The intersection of a plane of projection, or an original\n   plane, with a coordinate plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trace', 'v. t.', 'The ground plan of a work or works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'traced', 'imp. & p. p.', 'of Trace');
  INSERT INTO words (word, word_type, description) VALUES  (    'tracing', 'p. pr. & vb. n.', 'of Trace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trace', 'v. t.', 'To mark out; to draw or delineate with marks; especially,\n   to copy, as a drawing or engraving, by following the lines and marking\n   them on a sheet superimposed, through which they appear; as, to trace a\n   figure or an outline; a traced drawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trace',    'v. t.',    'To follow by some mark that has been left by a person or\n   thing which has preceded; to follow by footsteps, tracks, or tokens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trace',    'v. t.',    'Hence, to follow the trace or track of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trace',    'v. t.',    'To copy; to imitate.');
