INSERT INTO words (word, word_type, description)VALUES  (    'Trace',    'v. t.',    'To walk over; to pass through; to traverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trace', 'v. i.', 'To walk; to go; to travel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traceable', 'a.', 'Capable of being traced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracer', 'n.', 'One who, or that which, traces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traceries', 'pl. ', 'of Tracer/y');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracer/y', 'n.', 'Ornamental work with rambled lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracer/y', 'n.', 'The decorative head of a Gothic window.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracer/y', 'n.', 'A similar decoration in some styles of vaulting, the ribs\n   of the vault giving off the minor bars of which the tracery is\n   composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracheae', 'pl. ', 'of Trachea');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachea', 'n.', 'The windpipe. See Illust. of Lung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachea', 'n.', 'One of the respiratory tubes of insects and arachnids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachea', 'n.', 'One of the large cells in woody tissue which have spiral,\n   annular, or other markings, and are connected longitudinally so as to\n   form continuous ducts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracheal', 'a.', 'Of or pertaining to the trachea; like a trachea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachearia', 'n.pl.', 'A division of Arachnida including those that breathe\n   only by means of tracheae. It includes the mites, ticks, false\n   scorpions, and harvestmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracheary', 'a.', 'Tracheal; breathing by means of tracheae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracheary', 'n.', 'One of the Trachearia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracheata', 'n.pl.', 'An extensive division of arthropods comprising all\n   those which breathe by tracheae, as distinguished from Crustacea, which\n   breathe by means of branchiae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracheate', 'a.', 'Breathing by means of tracheae; of or pertaining to the\n   Tracheata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracheate', 'n.', 'Any arthropod having tracheae; one of the Tracheata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracheid', 'n.', 'A wood cell with spiral or other markings and closed\n   throughout, as in pine wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracheitis', 'n.', 'Inflammation of the trachea, or windpipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachelidan', 'n.', 'Any one of a tribe of beetles (Trachelides) which have\n   the head supported on a pedicel. The oil beetles and the Cantharides\n   are examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachelipod', 'n.', 'One of the Trachelipoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachelipoda', 'n.pl.', 'An extensive artificial group of gastropods\n   comprising all those which have a spiral shell and the foot attached to\n   the base of the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachelipodous', 'a.', 'Having the foot united with the neck; of or\n   pertainingto the Trachelipoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachelobranchiate', 'a.', 'Having the gills situated upon the neck; --\n   said of certain mollusks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachelorrhaphy', 'n.', 'The operation of sewing up a laceration of the\n   neck of the uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachenchyma', 'n.', 'A vegetable tissue consisting of tracheae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracheobranchlae', 'pl. ', 'of Tracheobranchia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracheobranchia', 'n.', 'One of the gill-like breathing organs of certain\n   aquatic insect larvae. They contain tracheal tubes somewhat similar to\n   those of other insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracheobronchial', 'a.', 'Pertaining both to the tracheal and bronchial\n   tubes, or to their junction; -- said of the syrinx of certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracheocele', 'n.', 'Goiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracheocele', 'n.', 'A tumor containing air and communicating with the\n   trachea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracheophonae', 'n. pl.', 'A group of passerine birds having the syrinx at\n   the lower end of the trachea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracheoscopy', 'n.', 'Examination of the interior of the trachea by means\n   of a mirror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracheotomy', 'n.', 'The operation of making an opening into the windpipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachinoid', 'a.', 'Of, pertaining to, or like, Trachinus, a genus of\n   fishes which includes the weevers. See Weever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachitis', 'n.', 'Tracheitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachycarpous', 'a.', 'Rough-fruited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachymedusae', 'n. pl.', 'A division of acalephs in which the development\n   is direct from the eggs, without a hydroid stage. Some of the species\n   are parasitic on other medusae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachyspermous', 'a.', 'Rough-seeded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachystomata', 'n. pl.', 'An order of tailed aquatic amphibians, including\n   Siren and Pseudobranchus. They have anterior legs only, are eel-like in\n   form, and have no teeth except a small patch on the palate. The\n   external gills are persistent through life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachyte', 'n.', 'An igneous rock, usually light gray in color and breaking\n   with a rough surface. It consists chiefly of orthoclase feldspar with\n   sometimes hornblende and mica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachytic', 'a.', 'Of, pertaining to, or resembling, trachyte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trachytoid', 'a.', 'Resembling trachyte; -- used to define the structure of\n   certain rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracing', 'n.', 'The act of one who traces; especially, the act of copying\n   by marking on thin paper, or other transparent substance, the lines of\n   a pattern placed beneath; also, the copy thus producted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracing', 'n.', 'A regular path or track; a course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Track', 'n.', 'A mark left by something that has passed along; as, the\n   track, or wake, of a ship; the track of a meteor; the track of a sled\n   or a wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Track', 'n.', 'A mark or impression left by the foot, either of man or\n   beast; trace; vestige; footprint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Track', 'n.', 'The entire lower surface of the foot; -- said of birds, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Track', 'n.', 'A road; a beaten path.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Track', 'n.', 'Course; way; as, the track of a comet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Track', 'n.', 'A path or course laid out for a race, for exercise, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Track', 'n.', 'The permanent way; the rails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Track', 'n.', 'A tract or area, as of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'tracked', 'imp. & p. p.', 'of Track');
  INSERT INTO words (word, word_type, description) VALUES  (    'tracking', 'p. pr. & vb. n.', 'of Track');
  INSERT INTO words (word, word_type, description) VALUES  (    'Track', 'v. t.', 'To follow the tracks or traces of; to pursue by following\n   the marks of the feet; to trace; to trail; as, to track a deer in the\n   snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Track', 'v. t.', 'To draw along continuously, as a vessel, by a line, men\n   or animals on shore being the motive power; to tow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trackage', 'n.', 'The act of tracking, or towing, as a boat; towage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracker', 'n.', 'One who, or that which, tracks or pursues, as a man or dog\n   that follows game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tracker', 'n.', 'In the organ, a light strip of wood connecting (in path) a\n   key and a pallet, to communicate motion by pulling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trackless', 'a.', 'Having no track; marked by no footsteps; untrodden; as,\n   a trackless desert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trackmaster', 'n.', 'One who has charge of the track; -- called also\n   roadmaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Track-road', 'n.', 'A towing path.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trackscout', 'n.', 'See Trackschuyt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tract', 'n.', 'A written discourse or dissertation, generally of short\n   extent; a short treatise, especially on practical religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tract', 'v.', 'Something drawn out or extended; expanse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tract', 'v.', 'A region or quantity of land or water, of indefinite extent;\n   an area; as, an unexplored tract of sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tract', 'v.', 'Traits; features; lineaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tract', 'v.', 'The footprint of a wild beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tract', 'v.', 'Track; trace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tract', 'v.', 'Treatment; exposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tract', 'v.', 'Continuity or extension of anything; as, the tract of\n   speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tract', 'v.', 'Continued or protracted duration; length; extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tract', 'v.', 'Verses of Scripture sung at Mass, instead of the Alleluia,\n   from Septuagesima Sunday till the Saturday befor Easter; -- so called\n   because sung tractim, or without a break, by one voice, instead of by\n   many as in the antiphons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tract', 'v. t.', 'To trace out; to track; also, to draw out; to protact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tractability', 'n.', 'The quality or state of being tractable or docile;\n   docility; tractableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tractable', 'v. t.', 'Capable of being easily led, taught, or managed;\n   docile; manageable; governable; as, tractable children; a tractable\n   learner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tractable', 'v. t.', 'Capable of being handled; palpable; practicable;\n   feasible; as, tractable measures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tractarian', 'n.', 'One of the writers of the Oxford tracts, called \"Tracts\n   for the Times,\" issued during the period 1833-1841, in which series of\n   papers the sacramental system and authority of the Church, and the\n   value of tradition, were brought into prominence. Also, a member of the\n   High Church party, holding generally the principles of the Tractarian\n   writers; a Puseyite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tractarian', 'a.', 'Of or pertaining to the Tractarians, or their\n   principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tractarianism', 'n.', 'The principles of the Tractarians, or of those\n   persons accepting the teachings of the \"Tracts for the Times.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tractate', 'n.', 'A treatise; a tract; an essay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tractation', 'n.', 'Treatment or handling of a subject; discussion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tractator', 'n.', 'One who writes tracts; specif., a Tractarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tractile', 'a.', 'Capable of being drawn out in length; ductile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tractility', 'n.', 'The quality of being tractile; ductility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traction', 'n.', 'The act of drawing, or the state of being drawn; as, the\n   traction of a muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traction', 'n.', 'Specifically, the act of drawing a body along a plane by\n   motive power, as the drawing of a carriage by men or horses, the towing\n   of a boat by a tug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traction', 'n.', 'Attraction; a drawing toward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traction', 'n.', 'The adhesive friction of a wheel on a rail, a rope on a\n   pulley, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tractite', 'n.', 'A Tractarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tractitious', 'a.', 'Treating of; handling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tractive', 'a.', 'Serving to draw; pulling; attracting; as, tractive power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tractor', 'n.', 'That which draws, or is used for drawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tractor', 'n.', 'Two small, pointed rods of metal, formerly used in the\n   treatment called Perkinism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tractoration', 'n.', 'See Perkinism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tractory', 'n.', 'A tractrix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tractrix', 'n.', 'A curve such that the part of the tangent between the\n   point of tangency and a given straight line is constant; -- so called\n   because it was conceived as described by the motion of one end of a\n   tangent line as the other end was drawn along the given line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trad', '', 'imp. of Tread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trade', 'v.', 'A track; a trail; a way; a path; also, passage; travel;\n   resort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trade', 'v.', 'Course; custom; practice; occupation; employment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trade', 'v.', 'Business of any kind; matter of mutual consideration;\n   affair; dealing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trade', 'v.', 'Specifically: The act or business of exchanging commodities\n   by barter, or by buying and selling for money; commerce; traffic;\n   barter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trade', 'v.', 'The business which a person has learned, and which he\n   engages in, for procuring subsistence, or for profit; occupation;\n   especially, mechanical employment as distinguished from the liberal\n   arts, the learned professions, and agriculture; as, we speak of the\n   trade of a smith, of a carpenter, or mason, but not now of the trade of\n   a farmer, or a lawyer, or a physician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trade', 'v.', 'Instruments of any occupation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trade', 'v.', 'A company of men engaged in the same occupation; thus,\n   booksellers and publishers speak of the customs of the trade, and are\n   collectively designated as the trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trade', 'v.', 'The trade winds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trade', 'v.', 'Refuse or rubbish from a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traded', 'imp. & p. p.', 'of Trade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trading', 'p. pr. & vb. n.', 'of Trade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trade', 'v. i.', 'To barter, or to buy and sell; to be engaged in the\n   exchange, purchase, or sale of goods, wares, merchandise, or anything\n   else; to traffic; to bargain; to carry on commerce as a business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trade', 'v. i.', 'To buy and sell or exchange property in a single\n   instance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trade', 'v. i.', 'To have dealings; to be concerned or associated; --\n   usually followed by with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trade', 'v. t.', 'To sell or exchange in commerce; to barter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trade', '', 'imp. of Tread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traded', 'a.', 'Professional; practiced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tradeful', 'a.', 'Full of trade; busy in traffic; commercial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tradeless', 'a.', 'Having no trade or traffic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trade-mark', 'n.', 'A peculiar distinguishing mark or device affixed by a\n   manufacturer or a merchant to his goods, the exclusive right of using\n   which is recognized by law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trader', 'n.', 'One engaged in trade or commerce; one who makes a business\n   of buying and selling or of barter; a merchant; a trafficker; as, a\n   trader to the East Indies; a country trader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trader', 'n.', 'A vessel engaged in the coasting or foreign trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tradescantia', 'n.', 'A genus including spiderwort and Wandering Jew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tradesfolk', 'n.', 'People employed in trade; tradesmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tradesmen', 'pl. ', 'of Tradesman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tradesman', 'n.', 'One who trades; a shopkeeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tradesman', 'n.', 'A mechanic or artificer; esp., one whose livelihood\n   depends upon the labor of his hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tradespeople', 'n.', 'People engaged in trade; shopkeepers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'trades union', '', 'Alt. of Trade union');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trade union', '', 'An organized combination among workmen for the purpose\n   of maintaining their rights, privileges, and interests with respect to\n   wages, hours of labor, customs, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trades-unionist', 'n.', 'Alt. of Trade-unionist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trade-unionist', 'n.', 'A member of a trades union, or a supporter of\n   trades unions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tradeswomen', 'pl. ', 'of Tradeswoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tradeswoman', 'n.', 'A woman who trades, or is skilled in trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trading', 'a.', 'Carrying on trade or commerce; engaged in trade; as, a\n   trading company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trading', 'a.', 'Frequented by traders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trading', 'a.', 'Venal; corrupt; jobbing; as, a trading politician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tradition', 'n.', 'The act of delivering into the hands of another;\n   delivery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tradition', 'n.', 'The unwritten or oral delivery of information, opinions,\n   doctrines, practices, rites, and customs, from father to son, or from\n   ancestors to posterity; the transmission of any knowledge, opinions, or\n   practice, from forefathers to descendants by oral communication,\n   without written memorials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tradition', 'n.', 'Hence, that which is transmitted orally from father to\n   son, or from ancestors to posterity; knowledge or belief transmitted\n   without the aid of written memorials; custom or practice long observed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tradition', 'n.', 'An unwritten code of law represented to have been given\n   by God to Moses on Sinai.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tradition', 'n.', 'That body of doctrine and discipline, or any article\n   thereof, supposed to have been put forth by Christ or his apostles, and\n   not committed to writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tradition', 'v. t.', 'To transmit by way of tradition; to hand down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traditional', 'a.', 'Of or pertaining to tradition; derived from tradition;\n   communicated from ancestors to descendants by word only; transmitted\n   from age to age without writing; as, traditional opinions; traditional\n   customs; traditional expositions of the Scriptures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traditional', 'a.', 'Observant of tradition; attached to old customs;\n   old-fashioned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traditionlism', 'n.', 'A system of faith founded on tradition; esp., the\n   doctrine that all religious faith is to be based solely upon what is\n   delivered from competent authority, exclusive of rational processes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traditionalist', 'n.', 'An advocate of, or believer in, traditionalism; a\n   traditionist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traditionally', 'adv.', 'In a traditional manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traditionarily', 'adv.', 'By tradition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traditionary', 'a.', 'Traditional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traditionaries', 'pl. ', 'of Traditionary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traditionary', 'n.', 'One, among the Jews, who acknowledges the authority\n   of traditions, and explains the Scriptures by them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traditioner', 'n.', 'Alt. of Traditionist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traditionist', 'n.', 'One who adheres to tradition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traditive', 'a.', 'Transmitted or transmissible from father to son, or from\n   age, by oral communication; traditional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traditor', 'n.', 'A deliverer; -- a name of infamy given to Christians who\n   delivered the Scriptures, or the goods of the church, to their\n   persecutors to save their lives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traduced', 'imp. & p. p.', 'of Traduce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traducing', 'p. pr. & vb. n.', 'of Traduce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traduce', 'v. t.', 'To transfer; to transmit; to hand down; as, to traduce\n   mental qualities to ones descendants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traduce', 'v. t.', 'To translate from one language to another; as, to\n   traduce and compose works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traduce', 'v. t.', 'To increase or distribute by propagation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traduce', 'v. t.', 'To draw away; to seduce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traduce', 'v. t.', 'To represent; to exhibit; to display; to expose; to\n   make an example of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traduce', 'v. t.', 'To expose to contempt or shame; to represent as\n   blamable; to calumniate; to vilify; to defame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traducement', 'n.', 'The act of traducing; misrepresentation; ill-founded\n   censure; defamation; calumny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traducent', 'a.', 'Slanderous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traducer', 'n.', 'One who traduces; a slanderer; a calumniator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traducer', 'n.', 'One who derives or deduces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traducian', 'n.', 'A believer in traducianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traducianism', 'n.', 'The doctrine that human souls are produced by the act\n   of generation; -- opposed to creationism, and infusionism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traducible', 'a.', 'Capable of being derived or propagated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traducible', 'a.', 'Capable of being traduced or calumniated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traducingly', 'adv.', 'In a traducing manner; by traduction; slanderously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traduct', 'v. t.', 'To derive or deduce; also, to transmit; to transfer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traduct', 'n.', 'That which is traducted; that which is transferred; a\n   translation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traduction', 'n.', 'Transmission from one to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traduction', 'n.', 'Translation from one language to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traduction', 'n.', 'Derivation by descent; propagation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traduction', 'n.', 'The act of transferring; conveyance; transportation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traduction', 'n.', 'Transition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traduction', 'n.', 'A process of reasoning in which each conclusion applies\n   to just such an object as each of the premises applies to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traductive', 'a.', 'Capable of being deduced; derivable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trafficked', 'imp. & p. p.', 'of Traffic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trafficking', 'p. pr. & vb. n.', 'of Traffic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traffic', 'v. i.', 'To pass goods and commodities from one person to\n   another for an equivalent in goods or money; to buy or sell goods; to\n   barter; to trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traffic', 'v. i.', 'To trade meanly or mercenarily; to bargain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traffic', 'v. t.', 'To exchange in traffic; to effect by a bargain or for a\n   consideration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traffic', 'v.', 'Commerce, either by barter or by buying and selling;\n   interchange of goods and commodities; trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traffic', 'v.', 'Commodities of the market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traffic', 'v.', 'The business done upon a railway, steamboat line, etc.,\n   with reference to the number of passengers or the amount of freight\n   carried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trafficable', 'a.', 'Capable of being disposed of in traffic; marketable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trafficker', 'n.', 'One who traffics, or carries on commerce; a trader; a\n   merchant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trafficless', 'a.', 'Destitute of traffic, or trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragacanth', 'n.', 'A kind of gum procured from a spiny leguminous shrub\n   (Astragalus gummifer) of Western Asia, and other species of Astragalus.\n   It comes in hard whitish or yellowish flakes or filaments, and is\n   nearly insoluble in water, but slowly swells into a mucilaginous mass,\n   which is used as a substitute for gum arabic in medicine and the arts.\n   Called also gum tragacanth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragedian', 'n.', 'A writer of tragedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragedian', 'n.', 'An actor or player in tragedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragedienne', 'n.', 'A woman who plays in tragedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragedious', 'a.', 'Like tragedy; tragical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragedies', 'pl. ', 'of Tragedy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragedy', 'n.', 'A dramatic poem, composed in elevated style, representing\n   a signal action performed by some person or persons, and having a fatal\n   issue; that species of drama which represents the sad or terrible\n   phases of character and life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragedy', 'n.', 'A fatal and mournful event; any event in which human lives\n   are lost by human violence, more especially by unauthorized violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragic', 'a.', 'Alt. of Tragical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragical', 'a.', 'Of or pertaining to tragedy; of the nature or character\n   of tragedy; as, a tragic poem; a tragic play or representation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragical', 'a.', 'Fatal to life; mournful; terrible; calamitous; as, the\n   tragic scenes of the French revolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragical', 'a.', 'Mournful; expressive of tragedy, the loss of life, or of\n   sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragic', 'n.', 'A writer of tragedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragic', 'n.', 'A tragedy; a tragic drama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragi-comedy', 'n.', 'A kind of drama representing some action in which\n   serious and comic scenes are blended; a composition partaking of the\n   nature both of tragedy and comedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragi-comic', 'a.', 'Alt. of Tragi-comical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragi-comical', 'a.', 'Of or pertaining to tragi-comedy; partaking of grave\n   and comic scenes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragi-comi-pastoral', 'a.', 'Partaking of the nature of, or combining,\n   tragedy, comedy, and pastoral poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragopan', 'n.', 'Any one of several species of Asiatic pheasants of the\n   genus Ceriornis. They are brilliantly colored with a variety of tints,\n   the back and breast are usually covered with white or buff ocelli, and\n   the head is ornamented with two bright-colored, fleshy wattles. The\n   crimson tragopan, or horned pheasant (C. satyra), of India is one of\n   the best-known species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tragus', 'n.', 'The prominence in front of the external opening of the ear.\n   See Illust. under Ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'T rail', '', 'See under T.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trailed', 'imp. & p. p.', 'of Trail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trailing', 'p. pr. & vb. n.', 'of Trail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trail', 'v. t.', 'To hunt by the track; to track.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trail', 'v. t.', 'To draw or drag, as along the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trail', 'v. t.', 'To carry, as a firearm, with the breech near the ground\n   and the upper part inclined forward, the piece being held by the right\n   hand near the middle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trail', 'v. t.', 'To tread down, as grass, by walking through it; to lay\n   flat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trail', 'v. t.', 'To take advantage of the ignorance of; to impose upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trail', 'v. i.', 'To be drawn out in length; to follow after.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trail', 'v. i.', 'To grow to great length, especially when slender and\n   creeping upon the ground, as a plant; to run or climb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trail', 'n.', 'A track left by man or beast; a track followed by the\n   hunter; a scent on the ground by the animal pursued; as, a deer trail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trail', 'n.', 'A footpath or road track through a wilderness or wild\n   region; as, an Indian trail over the plains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trail', 'n.', 'Anything drawn out to a length; as, the trail of a meteor; a\n   trail of smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trail', 'n.', 'Anything drawn behind in long undulations; a train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trail', 'n.', 'Anything drawn along, as a vehicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trail', 'n.', 'A frame for trailing plants; a trellis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trail', 'n.', 'The entrails of a fowl, especially of game, as the woodcock,\n   and the like; -- applied also, sometimes, to the entrails of sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trail', 'n.', 'That part of the stock of a gun carriage which rests on the\n   ground when the piece is unlimbered. See Illust. of Gun carriage, under\n   Gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trail', 'n.', 'The act of taking advantage of the ignorance of a person; an\n   imposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trailer', 'n.', 'One who, or that which, trails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trailer', 'n.', 'A part of an object which extends some distance beyond the\n   main body of the object; as, the trailer of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trailing', '', 'a. & vb. n. from Trail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trained', 'imp. & p. p.', 'of Train');
  INSERT INTO words (word, word_type, description) VALUES  (    'Training', 'p. pr. & vb. n.', 'of Train');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v. t.', 'To draw along; to trail; to drag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v. t.', 'To draw by persuasion, artifice, or the like; to attract\n   by stratagem; to entice; to allure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v. t.', 'To teach and form by practice; to educate; to exercise;\n   to discipline; as, to train the militia to the manual exercise; to\n   train soldiers to the use of arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v. t.', 'To break, tame, and accustom to draw, as oxen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v. t.', 'To lead or direct, and form to a wall or espalier; to\n   form to a proper shape, by bending, lopping, or pruning; as, to train\n   young trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v. t.', 'To trace, as a lode or any mineral appearance, to its\n   head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v. i.', 'To be drilled in military exercises; to do duty in a\n   military company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v. i.', 'To prepare by exercise, diet, instruction, etc., for any\n   physical contest; as, to train for a boat race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v.', 'That which draws along; especially, persuasion, artifice, or\n   enticement; allurement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v.', 'Hence, something tied to a lure to entice a hawk; also, a\n   trap for an animal; a snare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v.', 'That which is drawn along in the rear of, or after,\n   something; that which is in the hinder part or rear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v.', 'That part of a gown which trails behind the wearer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v.', 'The after part of a gun carriage; the trail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v.', 'The tail of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v.', 'A number of followers; a body of attendants; a retinue; a\n   suite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v.', 'A consecution or succession of connected things; a series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v.', 'Regular method; process; course; order; as, things now in a\n   train for settlement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v.', 'The number of beats of a watch in any certain time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v.', 'A line of gunpowder laid to lead fire to a charge, mine, or\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v.', 'A connected line of cars or carriages on a railroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v.', 'A heavy, long sleigh used in Canada for the transportation\n   of merchandise, wood, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train', 'v.', 'A roll train; as, a 12-inch train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trainable', 'a.', 'Capable of being trained or educated; as, boys trainable\n   to virtue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trainbands', 'pl. ', 'of Trainband');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trainband', 'n.', 'A band or company of an organized military force\n   instituted by James I. and dissolved by Charles II.; -- afterwards\n   applied to the London militia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trainbearer', 'n.', 'One who holds up a train, as of a robe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trainel', 'n.', 'A dragnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trainer', 'n.', 'One who trains; an instructor; especially, one who trains\n   or prepares men, horses, etc., for exercises requiring physical agility\n   and strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trainer', 'n.', 'A militiaman when called out for exercise or discipline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Training', 'n.', 'The act of one who trains; the act or process of\n   exercising, disciplining, etc.; education.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Train oil', '', 'Oil procured from the blubber or fat of whales, by\n   boiling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trainy', 'a.', 'Belonging to train oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traipse', 'v. i.', 'To walk or run about in a slatternly, careless, or\n   thoughtless manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trais', 'n. pl.', 'Alt. of Trays');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trays', 'n. pl.', 'Traces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trait', 'v.', 'A stroke; a touch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trait', 'v.', 'A distinguishing or marked feature; a peculiarity; as, a\n   trait of character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traiteur', 'n.', 'The keeper of an eating house, or restaurant; a\n   restaurateur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traitor', 'n.', 'One who violates his allegiance and betrays his country;\n   one guilty of treason; one who, in breach of trust, delivers his\n   country to an enemy, or yields up any fort or place intrusted to his\n   defense, or surrenders an army or body of troops to the enemy, unless\n   when vanquished; also, one who takes arms and levies war against his\n   country; or one who aids an enemy in conquering his country. See\n   Treason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traitor', 'n.', 'Hence, one who betrays any confidence or trust; a\n   betrayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traitor', 'a.', 'Traitorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traitor', 'v. t.', 'To act the traitor toward; to betray; to deceive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traitoress', 'n.', 'A traitress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traitorly', 'a.', 'Like a traitor; treacherous; traitorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traitorous', 'a.', 'Guilty of treason; treacherous; perfidious; faithless;\n   as, a traitorous officer or subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traitorous', 'a.', 'Consisting in treason; partaking of treason; implying\n   breach of allegiance; as, a traitorous scheme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traitory', 'n.', 'Treachery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traitress', 'n.', 'A woman who betrays her country or any trust; a\n   traitoress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trajected', 'imp. & p. p.', 'of Traject');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trajecting', 'p. pr. & vb. n.', 'of Traject');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traject', 'v. t.', 'To throw or cast through, over, or across; as, to\n   traject the suns light through three or more cross prisms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traject', 'v. t.', 'A place for passing across; a passage; a ferry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traject', 'v. t.', 'The act of trajecting; trajection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traject', 'v. t.', 'A trajectory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trajection', 'n.', 'The act of trajecting; a throwing or casting through or\n   across; also, emission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trajection', 'n.', 'Transposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trajectories', 'pl. ', 'of Trajectory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trajectory', 'n.', 'The curve which a body describes in space, as a planet\n   or comet in its orbit, or stone thrown upward obliquely in the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trajet', 'n.', 'Alt. of Trajetry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trajetour', 'n.', 'Alt. of Trajetry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trajetry', 'n.', 'See Treget, Tregetour, and Tregetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tralation', 'n.', 'The use of a word in a figurative or extended sense;\n   ametaphor; a trope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tralatition', 'n.', 'A change, as in the use of words; a metaphor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tralatitious', 'a.', 'Passed along; handed down; transmitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tralatitious', 'a.', 'Metaphorical; figurative; not literal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tralatitiously', 'adv.', 'In a tralatitious manner; metephorically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tralineate', 'v. i.', 'To deviate; to stray; to wander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tralucency', 'n.', 'Translucency; as, the tralucency of a gem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tralucent', 'a.', 'Translucent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tram', 'n.', 'A four-wheeled truck running on rails, and used in a mine, as\n   for carrying coal or ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tram', 'n.', 'The shaft of a cart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tram', 'n.', 'One of the rails of a tramway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tram', 'n.', 'A car on a horse railroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tram', 'n.', 'A silk thread formed of two or more threads twisted together,\n   used especially for the weft, or cross threads, of the best quality of\n   velvets and silk goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tramble', 'v. t.', 'To wash, as tin ore, with a shovel in a frame fitted\n   for the purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trammel', 'n.', 'A kind of net for catching birds, fishes, or other prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trammel', 'n.', 'A net for confining a womans hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trammel', 'n.', 'A kind of shackle used for regulating the motions of a\n   horse and making him amble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trammel', 'n.', 'Fig.: Whatever impedes activity, progress, or freedom, as\n   a net or shackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trammel', 'n.', 'An iron hook of various forms and sizes, used for handing\n   kettles and other vessels over the fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trammel', 'n.', 'An instrument for drawing ellipses, one part of which\n   consists of a cross with two grooves at right angles to each other, the\n   other being a beam carrying two pins (which slide in those grooves),\n   and also the describing pencil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trammel', 'n.', 'A beam compass. See under Beam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trammeled', 'imp. & p. p.', 'of Trammel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trammelled', '', 'of Trammel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trammeling', 'p. pr. & vb. n.', 'of Trammel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trammelling', '', 'of Trammel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trammel', 'v. t.', 'To entangle, as in a net; to catch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trammel', 'v. t.', 'To confine; to hamper; to shackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trammeled', 'a.', 'Having blazes, or white marks, on the fore and hind foot\n   of one side, as if marked by trammels; -- said of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trammeler', 'n.', 'One who uses a trammel net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trammeler', 'n.', 'One who, or that which, trammels or restrains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tramming', 'n.', 'The act or process of forming trams. See 2d Tram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tramontane', 'a.', 'Lying or being beyond the mountains; coming from the\n   other side of the mountains; hence, foreign; barbarous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tramontane', 'n.', 'One living beyond the mountains; hence, a foreigner; a\n   stranger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tramped', 'imp. & p. p.', 'of Tramp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tramping', 'p. pr. & vb. n.', 'of Tramp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tramp', 'v. i.', 'To tread upon forcibly and repeatedly; to trample.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tramp', 'v. i.', 'To travel or wander through; as, to tramp the country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tramp', 'v. i.', 'To cleanse, as clothes, by treading upon them in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tramp', 'v. i.', 'To travel; to wander; to stroll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tramp', 'n.', 'A foot journey or excursion; as, to go on a tramp; a long\n   tramp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tramp', 'n.', 'A foot traveler; a tramper; often used in a bad sense for a\n   vagrant or wandering vagabond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tramp', 'n.', 'The sound of the foot, or of feet, on the earth, as in\n   marching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tramp', 'n.', 'A tool for trimming hedges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tramp', 'n.', 'A plate of iron worn to protect the sole of the foot, or the\n   shoe, when digging with a spade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tramper', 'n.', 'One who tramps; a stroller; a vagrant or vagabond; a\n   tramp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trampled', 'imp. & p. p.', 'of Trample');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trampling', 'p. pr. & vb. n.', 'of Trample');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trample', 'v. t.', 'To tread under foot; to tread down; to prostrate by\n   treading; as, to trample grass or flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trample', 'v. t.', 'Fig.: To treat with contempt and insult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trample', 'v. i.', 'To tread with force and rapidity; to stamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trample', 'v. i.', 'To tread in contempt; -- with on or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trample', 'n.', 'The act of treading under foot; also, the sound produced\n   by trampling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trampler', 'n.', 'One who tramples; one who treads down; as, a trampler on\n   natures law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trampoose', 'v. i.', 'To walk with labor, or heavily; to tramp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tramroad', 'n.', 'A road prepared for easy transit of trams or wagons, by\n   forming the wheel tracks of smooth beams of wood, blocks of stone, or\n   plates of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tramway', 'n.', 'Same as Tramroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tramway', 'n.', 'A railway laid in the streets of a town or city, on which\n   cars for passengers or for freight are drawn by horses; a horse\n   railroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranation', 'n.', 'The act of swimming over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trance', 'n.', 'A tedious journey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trance', 'n.', 'A state in which the soul seems to have passed out of the\n   body into another state of being, or to be rapt into visions; an\n   ecstasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trance', 'n.', 'A condition, often simulating death, in which there is a\n   total suspension of the power of voluntary movement, with abolition of\n   all evidences of mental activity and the reduction to a minimum of all\n   the vital functions so that the patient lies still and apparently\n   unconscious of surrounding objects, while the pulsation of the heart\n   and the breathing, although still present, are almost or altogether\n   imperceptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranced', 'imp. & p. p.', 'of Trance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trancing', 'p. pr. & vb. n.', 'of Trance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trance', 'v. t.', 'To entrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trance', 'v. t.', 'To pass over or across; to traverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trance', 'v. i.', 'To pass; to travel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranect', 'n.', 'A ferry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trangram', 'n.', 'Something intricately contrived; a contrived; a puzzle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trannel', 'n.', 'A treenail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranquil', 'a.', 'Quiet; calm; undisturbed; peaceful; not agitated; as, the\n   atmosphere is tranquil; the condition of the country is tranquil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranquilization', 'n.', 'Alt. of Tranquillization');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranquillization', 'n.', 'The act of tranquilizing, or the state of being\n   tranquilized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranquilized', 'imp. & p. p.', 'of Tranquillize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranquilliized', '', 'of Tranquillize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranquilizing', 'p. pr. & vb. n.', 'of Tranquillize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranquillizing', '', 'of Tranquillize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranquilize', 'v. t.', 'Alt. of Tranquillize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranquillize', 'v. t.', 'To render tranquil; to allay when agitated; to\n   compose; to make calm and peaceful; as, to tranquilize a state\n   disturbed by factions or civil commotions; to tranquilize the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranquilizer', 'n.', 'Alt. of Tranquillizer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranquillizer', 'n.', 'One who, or that which, tranquilizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranquilizing', 'a.', 'Alt. of Tranquillizing');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranquillizing', 'a.', 'Making tranquil; calming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranquillity', 'n.', 'The quality or state of being tranquil; calmness;\n   composure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranquilly', 'adv.', 'In a tranquil manner; calmly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranquilness', 'n.', 'Quality or state of being tranquil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trans-', '', 'A prefix, signifying over, beyond, through and through, on\n   the other side, as in transalpine, beyond the Alps; transform, to form\n   through and through, that is, anew, transfigure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transacted', 'imp. & p. p.', 'of Transact');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transacting', 'p. pr. & vb. n.', 'of Transact');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transact', 'v. t.', 'To carry through; to do; perform; to manage; as, to\n   transact commercial business; to transact business by an agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transact', 'v. i.', 'To conduct matters; to manage affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transaction', 'n.', 'The doing or performing of any business; management of\n   any affair; performance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transaction', 'n.', 'That which is done; an affair; as, the transactions on\n   the exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transaction', 'n.', 'An adjustment of a dispute between parties by mutual\n   agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transactor', 'n.', 'One who transacts, performs, or conducts any business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transalpine', 'a.', 'Being on the farther side of the Alps in regard to\n   Rome, that is, on the north or west side of the Alps; of or pertaining\n   to the region or the people beyond the Alps; as, transalpine Gaul; --\n   opposed to cisalpine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transalpine', 'n.', 'A native or inhabitant of a country beyond the Alps,\n   that is, out of Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transanimated', 'imp. & p. p.', 'of Transanimate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transanimating', 'p. pr. & vb. n.', 'of Transanimate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transanimate', 'v. t.', 'To animate with a soul conveyed from another body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transanimation', 'n.', 'The conveyance of a soul from one body to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transatlantic', 'a.', 'Lying or being beyond the Atlantic Ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transatlantic', 'a.', 'Crossing the Atlantic Ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transaudient', 'a.', 'Permitting the passage of sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcalency', 'n.', 'The quality or state of being transcalent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcalent', 'a.', 'Pervious to, or permitting the passage of, heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcended', 'imp. & p. p.', 'of Transcend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcending', 'p. pr. & vb. n.', 'of Transcend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcend', 'v. t.', 'To rise above; to surmount; as, lights in the heavens\n   transcending the region of the clouds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcend', 'v. t.', 'To pass over; to go beyond; to exceed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcend', 'v. t.', 'To surpass; to outgo; to excel; to exceed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcend', 'v. i.', 'To climb; to mount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcend', 'v. i.', 'To be transcendent; to excel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcendence', '', 'Alt. of Transcendency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcendency', '', 'The quality or state of being transcendent; superior\n   excellence; supereminence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcendency', '', 'Elevation above truth; exaggeration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcendent', 'a.', 'Very excellent; superior or supreme in excellence;\n   surpassing others; as, transcendent worth; transcendent valor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcendent', 'a.', 'Transcending, or reaching beyond, the limits of human\n   knowledge; -- applied to affirmations and speculations concerning what\n   lies beyond the reach of the human intellect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trancscendent', 'n.', 'That which surpasses or is supereminent; that which\n   is very excellent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trancscendental', 'a.', 'Supereminent; surpassing others; as,\n   transcendental being or qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trancscendental', 'a.', 'In the Kantian system, of or pertaining to that\n   which can be determined a priori in regard to the fundamental\n   principles of all human knowledge. What is transcendental, therefore,\n   transcends empiricism; but is does not transcend all human knowledge,\n   or become transcendent. It simply signifies the a priori or necessary\n   conditions of experience which, though affording the conditions of\n   experience, transcend the sphere of that contingent knowledge which is\n   acquired by experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trancscendental', 'a.', 'Vaguely and ambitiously extravagant in\n   speculation, imagery, or diction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcendental', 'n.', 'A transcendentalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcendentalism', 'n.', 'The transcending, or going beyond, empiricism,\n   and ascertaining a priori the fundamental principles of human\n   knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcendentalism', 'n.', 'Ambitious and imaginative vagueness in thought,\n   imagery, or diction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcendentalist', 'n.', 'One who believes in transcendentalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcendentality', 'n.', 'The quality or state of being transcendental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcendentally', 'adv.', 'In a transcendental manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcendently', 'adv.', 'In a transcendent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcendentness', 'n.', 'Same as Transcendence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcension', 'n.', 'The act of transcending, or surpassing; also, passage\n   over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcolated', 'imp. & p. p.', 'of Transcolate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcolating', 'p. pr. & vb. n.', 'of Transcolate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcolate', 'v. t.', 'To cause to pass through a sieve or colander; to\n   strain, as through a sieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcolation', 'n.', 'Act of transcolating, or state of being\n   transcolated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcontinental', 'a.', 'Extending or going across a continent; as, a\n   transcontinental railroad or journey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcorporate', 'v. i.', 'To transmigrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcribbler', 'n.', 'A transcriber; -- used in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcribed', 'imp. & p. p.', 'of Transcribe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcribing', 'p. pr. & vb. n.', 'of Transcribe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcribe', 'v. t.', 'To write over again, or in the same words; to copy;\n   as, to transcribe Livy or Tacitus; to transcribe a letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcriber', 'n.', 'One who transcribes, or writes from a copy; a copier;\n   a copyist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcript', 'n.', 'That which has been transcribed; a writing or\n   composition consisting of the same words as the original; a written\n   copy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcript', 'n.', 'A copy of any kind; an imitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcript', 'n.', 'A written version of what was said orally; as, a\n   transcript of a trial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcription', 'n.', 'The act or process of transcribing, or copying; as,\n   corruptions creep into books by repeated transcriptions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcription', 'n.', 'A copy; a transcript.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcription', 'n.', 'An arrangement of a composition for some other\n   instrument or voice than that for which it was originally written, as\n   the translating of a song, a vocal or instrumental quartet, or even an\n   orchestral work, into a piece for the piano; an adaptation; an\n   arrangement; -- a name applied by modern composers for the piano to a\n   more or less fanciful and ornate reproduction on their own instrument\n   of a song or other piece not originally intended for it; as, Liszts\n   transcriptions of songs by Schubert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcriptive', 'a.', 'Done as from a copy; having the style or appearance\n   of a transcription.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcur', 'v. i.', 'To run or rove to and fro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcurrence', 'n.', 'A roving hither and thither.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transcursion', 'n.', 'A rambling or ramble; a passage over bounds; an\n   excursion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transdialect', 'v. t.', 'To change or translate from one dialect into\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transduction', 'n.', 'The act of conveying over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transe', 'n.', 'See Trance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transelement', 'v. t.', 'Alt. of Transelementate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transelementate', 'v. t.', 'To change or transpose the elements of; to\n   transubstantiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transelementation', 'n.', 'Transubstantiation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transenne', 'n.', 'A transom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transept', 'n.', 'The transversal part of a church, which crosses at right\n   angles to the greatest length, and between the nave and choir. In the\n   basilicas, this had often no projection at its two ends. In Gothic\n   churches these project these project greatly, and should be called the\n   arms of the transept. It is common, however, to speak of the arms\n   themselves as the transepts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transexion', 'n.', 'Change of sex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfeminate', 'v. t.', 'To change into a woman, as a man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transferred', 'imp. & p. p.', 'of Transfer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transferring', 'p. pr. & vb. n.', 'of Transfer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfer', 'v. t.', 'To convey from one place or person another; to\n   transport, remove, or cause to pass, to another place or person; as, to\n   transfer the laws of one country to another; to transfer suspicion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfer', 'v. t.', 'To make over the possession or control of; to pass; to\n   convey, as a right, from one person to another; to give; as, the title\n   to land is transferred by deed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfer', 'v. t.', 'To remove from one substance or surface to another;\n   as, to transfer drawings or engravings to a lithographic stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfer', 'n.', 'The act of transferring, or the state of being\n   transferred; the removal or conveyance of a thing from one place or\n   person to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfer', 'n.', 'The conveyance of right, title, or property, either real\n   or personal, from one person to another, whether by sale, by gift, or\n   otherwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfer', 'n.', 'That which is transferred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfer', 'n.', 'A picture, or the like, removed from one body or ground\n   to another, as from wood to canvas, or from one piece of canvas to\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfer', 'n.', 'A drawing or writing printed off from one surface on\n   another, as in ceramics and in many decorative arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfer', 'n.', 'A soldier removed from one troop, or body of troops, and\n   placed in another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfer', 'n.', 'A pathological process by virtue of which a unilateral\n   morbid condition on being abolished on one side of the body makes its\n   appearance in the corresponding region upon the other side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transferability', 'n.', 'The quality or state of being transferable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transferable', 'a.', 'Capable of being transferred or conveyed from one\n   place or person to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transferable', 'a.', 'Negotiable, as a note, bill of exchange, or other\n   evidence of property, that may be conveyed from one person to another\n   by indorsement or other writing; capable of being transferred with no\n   loss of value; as, the stocks of most public companies are\n   transferable; some tickets are not transferable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transferee', 'n.', 'The person to whom a transfer in made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transference', 'n.', 'The act of transferring; conveyance; passage;\n   transfer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transferography', 'n.', 'The act or process of copying inscriptions, or the\n   like, by making transfers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transferrence', 'n.', 'See Transference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transferrer', 'n.', 'One who makes a transfer or conveyance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transferrible', 'a.', 'Capable of being transferred; transferable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfigurate', 'v. t.', 'To transfigure; to transform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfiguratien', 'n.', 'A change of form or appearance; especially, the\n   supernatural change in the personal appearance of our Savior on the\n   mount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfiguratien', 'n.', 'A feast held by some branches of the Christian\n   church on the 6th of August, in commemoration of the miraculous change\n   above mentioned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfigured', 'imp. & p. p.', 'of Transfigure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfiguring', 'p. pr. & vb. n.', 'of Transfigure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfigure', 'v. t.', 'To change the outward form or appearance of; to\n   metamorphose; to transform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfigure', 'v. t.', 'Especially, to change to something exalted and\n   glorious; to give an ideal form to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfixed', 'imp. & p. p.', 'of Transfix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfixing', 'p. pr. & vb. n.', 'of Transfix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfix', 'v. t.', 'To pierce through, as with a pointed weapon; to\n   impale; as, to transfix one with a dart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfixion', 'n.', 'The act of transfixing, or the state of being\n   transfixed, or pierced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfluent', 'a.', 'Flowing or running across or through; as, a\n   transfluent stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfluent', 'a.', 'Passing or flowing through a bridge; -- said of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transflux', 'n.', 'A flowing through, across, or beyond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transforate', 'v. t.', 'To bore through; to perforate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transformed', 'imp. & p. p.', 'of Transform');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transforming', 'p. pr. & vb. n.', 'of Transform');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transform', 'v. t.', 'To change the form of; to change in shape or\n   appearance; to metamorphose; as, a caterpillar is ultimately\n   transformed into a butterfly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transform', 'v. t.', 'To change into another substance; to transmute; as,\n   the alchemists sought to transform lead into gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transform', 'v. t.', 'To change in nature, disposition, heart, character,\n   or the like; to convert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transform', 'v. t.', 'To change, as an algebraic expression or geometrical\n   figure, into another from without altering its value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transform', 'v. i.', 'To be changed in form; to be metamorphosed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transformable', 'a.', 'Capable of being transformed or changed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transformation', 'n.', 'The act of transforming, or the state of being\n   transformed; change of form or condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transformation', 'n.', 'Any change in an organism which alters its general\n   character and mode of life, as in the development of the germ into the\n   embryo, the egg into the animal, the larva into the insect\n   (metamorphosis), etc.; also, the change which the histological units of\n   a tissue are prone to undergo. See Metamorphosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transformation', 'n.', 'Change of one from of material into another, as in\n   assimilation; metabolism; metamorphosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transformation', 'n.', 'The imagined possible or actual change of one metal\n   into another; transmutation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transformation', 'n.', 'A change in disposition, heart, character, or the\n   like; conversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transformation', 'n.', 'The change, as of an equation or quantity, into\n   another form without altering the value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transformative', 'a.', 'Having power, or a tendency, to transform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transformer', 'n.', 'One who, or that which, transforms. Specif. (Elec.),\n   an apparatus for producing from a given electrical current another\n   current of different voltage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transformism', 'n.', 'The hypothesis, or doctrine, that living beings have\n   originated by the modification of some other previously existing forms\n   of living matter; -- opposed to abiogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfreight', 'v. i.', 'To transfrete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfretation', 'n.', 'The act of passing over a strait or narrow sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfrete', 'v. i.', 'To pass over a strait or narrow sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfuge', 'n.', 'Alt. of Transfugitive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfugitive', 'n.', 'One who flees from one side to another; hence, a\n   deserter; a turncoat; an apostate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfund', 'v. t.', 'To pour from one vessel into another; to transfuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfused', 'imp. & p. p.', 'of Transfuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfusing', 'p. pr. & vb. n.', 'of Transfuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfuse', 'v. t.', 'To pour, as liquid, out of one vessel into another;\n   to transfer by pouring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfuse', 'v. t.', 'To transfer, as blood, from the veins or arteries of\n   one man or animal to those of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfuse', 'v. t.', 'To cause to pass from to another; to cause to be\n   instilled or imbibed; as, to transfuse a spirit of patriotism into a\n   man; to transfuse a love of letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfusible', 'a.', 'Capable of being transfused; transferable by\n   transfusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfusion', 'n.', 'The act of transfusing, or pouring, as liquor, out of\n   one vessel into another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfusion', 'n.', 'The act or operation of transferring the blood of one\n   man or animal into the vascular system of another; also, the\n   introduction of any fluid into the blood vessels, or into a cavity of\n   the body from which it can readily be adsorbed into the vessels;\n   intrafusion; as, the peritoneal transfusion of milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transfusive', 'a.', 'Tending to transfuse; having power to transfuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transgressed', 'imp. & p. p.', 'of Transgress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transgressing', 'p. pr. & vb. n.', 'of Transgress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transgress', 'v. t.', 'To pass over or beyond; to surpass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transgress', 'v. t.', 'Hence, to overpass, as any prescribed as the /imit\n   of duty; to break or violate, as a law, civil or moral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transgress', 'v. t.', 'To offend against; to vex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transgress', 'v. i.', 'To offend against the law; to sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transgression', 'n.', 'The act of transgressing, or of passing over or\n   beyond any law, civil or moral; the violation of a law or known\n   principle of rectitude; breach of command; fault; offense; crime; sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transgressional', 'a.', 'Of pertaining to transgression; involving a\n   transgression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transgressive', 'a.', 'Disposed or tending to transgress; faulty; culpable.\n   -');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transgressor', 'n.', 'One who transgresses; one who breaks a law, or\n   violates a command; one who violates any known rule or principle of\n   rectitude; a sinner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transhape', 'v. t.', 'To transshape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranship', 'v. t.', 'Same as Transship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transhipment', 'n.', 'Same as Transshipment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transhuman', 'a.', 'More than human; superhuman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transhumanize', 'v. t.', 'To make more than human; to purity; to elevate\n   above humanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transience', 'n.', 'Alt. of Transiency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transiency', 'n.', 'The quality of being transient; transientness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transient', 'a.', 'Passing before the sight or perception, or, as it were,\n   moving over or across a space or scene viewed, and then disappearing;\n   hence, of short duration; not permanent; not lasting or durable; not\n   stationary; passing; fleeting; brief; transitory; as, transient\n   pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transient', 'a.', 'Hasty; momentary; imperfect; brief; as, a transient view\n   of a landscape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transient', 'a.', 'Staying for a short time; not regular or permanent; as,\n   a transient guest; transient boarders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transient', 'n.', 'That which remains but for a brief time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transilience', 'n.', 'Alt. of Transiliency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transiliency', 'n.', 'A leap across or from one thing to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transire', 'n.', 'A customhouse clearance for a coasting vessel; a permit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transit', 'n.', 'The act of passing; passage through or over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transit', 'n.', 'The act or process of causing to pass; conveyance; as, the\n   transit of goods through a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transit', 'n.', 'A line or route of passage or conveyance; as, the\n   Nicaragua transit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transit', 'n.', 'The passage of a heavenly body over the meridian of a\n   place, or through the field of a telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transit', 'n.', 'The passage of a smaller body across the disk of a larger,\n   as of Venus across the suns disk, or of a satellite or its shadow\n   across the disk of its primary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transit', 'n.', 'An instrument resembling a theodolite, used by surveyors\n   and engineers; -- called also transit compass, and surveyors transit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transit', 'v. t.', 'To pass over the disk of (a heavenly body).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transition', 'n.', 'Passage from one place or state to another; charge; as,\n   the transition of the weather from hot to cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transition', 'n.', 'A direct or indirect passing from one key to another; a\n   modulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transition', 'n.', 'A passing from one subject to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transition', 'n.', 'Change from one form to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transitional', 'a.', 'Of or pertaining to transition; involving or denoting\n   transition; as, transitional changes; transitional stage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transitionary', 'a.', 'Transitional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transitive', 'a.', 'Having the power of making a transit, or passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transitive', 'a.', 'Effected by transference of signification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transitive', 'a.', 'Passing over to an object; expressing an action which\n   is not limited to the agent or subject, but which requires an object to\n   complete the sense; as, a transitive verb, for example, he holds the\n   book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transitorily', 'adv.', 'In a transitory manner; with brief continuance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transitoriness', 'n.', 'The quality or state of being transitory; speedy\n   passage or departure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transitory', 'a.', 'Continuing only for a short time; not enduring;\n   fleeting; evanescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translatable', 'a.', 'Capable of being translated, or rendered into another\n   language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translated', 'imp. & p. p.', 'of Translate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translating', 'p. pr. & vb. n.', 'of Translate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translate', 'v. t.', 'To bear, carry, or remove, from one place to another;\n   to transfer; as, to translate a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translate', 'v. t.', 'To change to another condition, position, place, or\n   office; to transfer; hence, to remove as by death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translate', 'v. t.', 'To remove to heaven without a natural death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translate', 'v. t.', 'To remove, as a bishop, from one see to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translate', 'v. t.', 'To render into another language; to express the sense\n   of in the words of another language; to interpret; hence, to explain or\n   recapitulate in other words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translate', 'v. t.', 'To change into another form; to transform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translate', 'v. t.', 'To cause to remove from one part of the body to\n   another; as, to translate a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translate', 'v. t.', 'To cause to lose senses or recollection; to entrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translate', 'v. i.', 'To make a translation; to be engaged in translation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translation', 'n.', 'The act of translating, removing, or transferring;\n   removal; also, the state of being translated or removed; as, the\n   translation of Enoch; the translation of a bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translation', 'n.', 'The act of rendering into another language;\n   interpretation; as, the translation of idioms is difficult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translation', 'n.', 'That which is obtained by translating something a\n   version; as, a translation of the Scriptures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translation', 'n.', 'A transfer of meaning in a word or phrase, a metaphor;\n   a tralation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translation', 'n.', 'Transfer of meaning by association; association of\n   ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translation', 'n.', 'Motion in which all the points of the moving body have\n   at any instant the same velocity and direction of motion; -- opposed to\n   rotation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translatitious', 'a.', 'Metaphorical; tralatitious; also, foreign; exotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translative', 'a.', 'tropical; figurative; as, a translative sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translator', 'n.', 'One who translates; esp., one who renders into another\n   language; one who expresses the sense of words in one language by\n   equivalent words in another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translator', 'n.', 'A repeating instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translatorship', 'n.', 'The office or dignity of a translator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translatory', 'a.', 'Serving to translate; transferring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translatress', 'n.', 'A woman who translates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translavation', 'n.', 'A laving or lading from one vessel to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transliterate', 'v. t.', 'To express or represent in the characters of\n   another alphabet; as, to transliterate Sanskrit words by means of\n   English letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transliteration', 'n.', 'The act or product of transliterating, or of\n   expressing words of a language by means of the characters of another\n   alphabet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translocation', 'n.', 'removal of things from one place to another;\n   substitution of one thing for another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translucence', 'n.', 'Alt. of Translucency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translucency', 'n.', 'The quality or state of being translucent; clearness;\n   partial transparency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translucent', 'a.', 'Transmitting rays of light without permitting objects\n   to be distinctly seen; partially transparent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translucent', 'a.', 'Transparent; clear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translucently', 'adv.', 'In a translucent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translucid', 'a.', 'Translucent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Translunary', 'a.', 'Being or lying beyond the moon; hence, ethereal; --\n   opposed to sublunary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmarine', 'a.', 'Lying or being beyond the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmeable', 'a.', 'Alt. of Transmeatable');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmeatable', 'a.', 'Capable of being passed over or traversed; passable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmeate', 'v. t.', 'To pass over or beyond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmeation', 'n.', 'The act of transmeating; a passing through or beyond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmew', 'v. t. & i.', 'To transmute; to transform; to metamorphose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmigrant', 'a.', 'Migrating or passing from one place or state to\n   another; passing from one residence to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmigrant', 'n.', 'One who transmigrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmigrated', 'imp. & p. p.', 'of Transmigrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmigrating', 'p. pr. & vb. n.', 'of Transmigrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmigrate', 'v. i.', 'To pass from one country or jurisdiction to\n   another for the purpose of residence, as men or families; to migrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmigrate', 'v. i.', 'To pass from one body or condition into another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmigration', 'n.', 'The act of passing from one country to another;\n   migration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmigration', 'n.', 'The passing of the soul at death into another\n   mortal body; metempsychosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmigrator', 'n.', 'One who transmigrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmigratory', 'a.', 'Passing from one body or state to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmissibility', 'n.', 'The quality of being transmissible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmissible', 'a.', 'Capable of being transmitted from one to another;\n   capable of being passed through any body or substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmission', 'n.', 'The act of transmitting, or the state of being\n   transmitted; as, the transmission of letters, writings, papers, news,\n   and the like, from one country to another; the transmission of rights,\n   titles, or privileges, from father to son, or from one generation to\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmission', 'n.', 'The right possessed by an heir or legatee of\n   transmitting to his successor or successors any inheritance, legacy,\n   right, or privilege, to which he is entitled, even if he should die\n   without enjoying or exercising it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmissive', 'a.', 'Capable of being transmitted; derived, or handed\n   down, from one to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmitted', 'imp. & p. p.', 'of Transmit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmitting', 'p. pr. & vb. n.', 'of Transmit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmit', 'v. t.', 'To cause to pass over or through; to communicate by\n   sending; to send from one person or place to another; to pass on or\n   down as by inheritance; as, to transmit a memorial; to transmit\n   dispatches; to transmit money, or bills of exchange, from one country\n   to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmit', 'v. t.', 'To suffer to pass through; as, glass transmits light;\n   metals transmit, or conduct, electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmittal', 'n.', 'Transmission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmittance', 'n.', 'Transmission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmitter', 'n.', 'One who, or that which, transmits; specifically, that\n   portion of a telegraphic or telephonic instrument by means of which a\n   message is sent; -- opposed to receiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmittible', 'a.', 'Capable of being transmitted; transmissible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmogrification', 'n.', 'The act of transmogrifying, or the state of\n   being transmogrified; transformation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmogrify', 'v. t.', 'To change into a different shape; to transform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmove', 'v. t.', 'To move or change from one state into another; to\n   transform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmutability', 'n.', 'The quality of being transmutable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmutable', 'a.', 'Capable of being transmuted or changed into a\n   different substance, or into into something of a different form a\n   nature; transformable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmutation', 'n.', 'The act of transmuting, or the state of being\n   transmuted; as, the transmutation of metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmutation', 'n.', 'The change or reduction of one figure or body into\n   another of the same area or solidity, but of a different form, as of a\n   triangle into a square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmutation', 'n.', 'The change of one species into another, which is\n   assumed to take place in any development theory of life; transformism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmutationist', 'n.', 'One who believes in the transmutation of metals\n   or of species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmuted', 'imp. & p. p.', 'of Transmute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmuting', 'p. pr. & vb. n.', 'of Transmute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmute', 'v. t.', 'To change from one nature, form, or substance, into\n   another; to transform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmuter', 'n.', 'One who transmutes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transmutual', 'a.', 'Reciprocal; commutual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transnatation', 'n.', 'The act of swimming across, as a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transnature', 'v. t.', 'To transfer or transform the nature of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transom', 'n.', 'A horizontal crossbar in a window, over a door, or between\n   a door and a window above it. Transom is the horizontal, as mullion is\n   the vertical, bar across an opening. See Illust. of Mullion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transom', 'n.', 'One of the principal transverse timbers of the stern,\n   bolted to the sternpost and giving shape to the stern structure; --\n   called also transsummer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transom', 'n.', 'The piece of wood or iron connecting the cheeks of some\n   gun carriages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transom', 'n.', 'The vane of a cross-staff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transom', 'n.', 'One of the crossbeams connecting the side frames of a\n   truck with each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpadane', 'a.', 'Lying or being on the further side of the river Po\n   with reference to Rome, that is, on the north side; -- opposed to\n   cispadane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpalatine', 'a.', 'Situated beyond or outside the palatine bone; --\n   said of a bone in the skull of some reptiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpare', 'v. t. & i.', 'To be, or cause to be, transparent; to appear,\n   or cause to appear, or be seen, through something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transparence', 'n.', 'The quality or state of being transparent;\n   transparency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transparencies', 'pl. ', 'of Transparency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transparency', 'n.', 'The quality or condition of being transparent;\n   transparence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transparency', 'n.', 'That which is transparent; especially, a picture\n   painted on thin cloth or glass, or impressed on porcelain, or the like,\n   to be viewed by natural or artificial light, which shines through it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transparent', 'a.', 'Having the property of transmitting rays of light, so\n   that bodies can be distinctly seen through; pervious to light;\n   diaphanous; pellucid; as, transparent glass; a transparent diamond; --\n   opposed to opaque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transparent', 'a.', 'Admitting the passage of light; open; porous; as, a\n   transparent veil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpass', 'v. t.', 'To pass over; as, Alexander transpassed the river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpass', 'v. i.', 'To pass by; to pass away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpassable', 'a.', 'Capable of being transpassed, or crossed over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpatronize', 'v. t.', 'To transfer the patronage of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpeciate', 'v. t.', 'To change from one species to another; to\n   transform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpicuous', 'a.', 'Transparent; pervious to the sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpierced', 'imp. & p. p.', 'of Transpierce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpiercing', 'p. pr. & vb. n.', 'of Transpierce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpierce', 'v. t.', 'To pierce through; to penetrate; to permeate; to\n   pass through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpirable', 'a.', 'Capable of being transpired, or of transpiring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpiration', 'n.', 'The act or process of transpiring or excreting in\n   the form of vapor; exhalation, as through the skin or other membranes\n   of the body; as, pulmonary transpiration, or the excretion of aqueous\n   vapor from the lungs. Perspiration is a form of transpiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpiration', 'n.', 'The evaporation of water, or exhalation of aqueous\n   vapor, from cells and masses of tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpiration', 'n.', 'The passing of gases through fine tubes, porous\n   substances, or the like; as, transpiration through membranes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpiratory', 'a.', 'Of or relating to transpiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpired', 'imp. & p. p.', 'of Transpire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpiring', 'p. pr. & vb. n.', 'of Transpire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpire', 'v. i.', 'To pass off in the form of vapor or insensible\n   perspiration; to exhale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpire', 'v. i.', 'To evaporate from living cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpire', 'v. i.', 'To escape from secrecy; to become public; as, the\n   proceedings of the council soon transpired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpire', 'v. i.', 'To happen or come to pass; to occur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpire', 'v. t.', 'To excrete through the skin; to give off in the form\n   of vapor; to exhale; to perspire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpire', 'v. t.', 'To evaporate (moisture) from living cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transplaced', 'imp. & p. p.', 'of Transplace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transplacing', 'p. pr. & vb. n.', 'of Transplace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transplace', 'v. t.', 'To remove across some space; to put in an opposite\n   or another place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transplanted', 'imp. & p. p.', 'of Transplant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transplanting', 'p. pr. & vb. n.', 'of Transplant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transplant', 'v. t.', 'To remove, and plant in another place; as, to\n   transplant trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transplant', 'v. t.', 'To remove, and settle or establish for residence in\n   another place; as, to transplant inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transplantation', 'n.', 'The act of transplanting, or the state of being\n   transplanted; also, removal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transplantation', 'n.', 'The removal of tissues from a healthy part, and\n   the insertion of them in another place where there is a lesion; as, the\n   transplantation of tissues in autoplasty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transplantation', 'n.', 'The removal of a bodily organ or of tissues from\n   one person, and the insertion of them into another person to replace a\n   damaged organ or tissue; as, the transplantation of a heart, kidney, or\n   liver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transplanter', 'n.', 'One who transplants; also, a machine for\n   transplanting trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transplendency', 'n.', 'Quality or state of being transplendent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transplendent', 'a.', 'Resplendent in the highest degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transported', 'imp. & p. p.', 'of Transport');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transporting', 'p. pr. & vb. n.', 'of Transport');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transport', 'v. t.', 'To carry or bear from one place to another; to\n   remove; to convey; as, to transport goods; to transport troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transport', 'v. t.', 'To carry, or cause to be carried, into banishment, as\n   a criminal; to banish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transport', 'v. t.', 'To carry away with vehement emotion, as joy, sorrow,\n   complacency, anger, etc.; to ravish with pleasure or ecstasy; as, music\n   transports the soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transport', 'v.', 'Transportation; carriage; conveyance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transport', 'v.', 'A vessel employed for transporting, especially for\n   carrying soldiers, warlike stores, or provisions, from one place to\n   another, or to convey convicts to their destination; -- called also\n   transport ship, transport vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transport', 'v.', 'Vehement emotion; passion; ecstasy; rapture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transport', 'v.', 'A convict transported, or sentenced to exile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transportability', 'n.', 'The quality or state of being transportable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transportable', 'a.', 'Capable of being transported.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transportable', 'a.', 'Incurring, or subject to, the punishment of\n   transportation; as, a transportable offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transportal', 'n.', 'Transportation; the act of removing from one locality\n   to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transportance', 'n.', 'Transportation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transportant', 'a.', 'Transporting; /avishing; as, transportant love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transportation', 'n.', 'The act of transporting, or the state of being\n   transported; carriage from one place to another; removal; conveyance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transportation', 'n.', 'Transport; ecstasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transported', 'a.', 'Conveyed from one place to another; figuratively,\n   carried away with passion or pleasure; entranced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transporter', 'n.', 'One who transports.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transporting', 'a.', 'That transports; fig., ravishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transportingly', 'adv.', 'So as to transport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transportment', 'n.', 'The act of transporting, or the state of being\n   transported; transportation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transposable', 'a.', 'That may transposed; as, a transposable phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transposal', 'n.', 'The act of transposing, or the state of being\n   transposed; transposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transposed', 'imp. & p. p.', 'of Transpose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transposing', 'p. pr. & vb. n.', 'of Transpose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpose', 'v. t.', 'To change the place or order of; to substitute one\n   for the other of; to exchange, in respect of position; as, to transpose\n   letters, words, or propositions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpose', 'v. t.', 'To change; to transform; to invert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpose', 'v. t.', 'To bring, as any term of an equation, from one side\n   over to the other, without destroying the equation; thus, if a + b = c,\n   and we make a = c - b, then b is said to be transposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpose', 'v. t.', 'To change the natural order of, as words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpose', 'v. t.', 'To change the key of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transposer', 'n.', 'One who transposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transposition', 'n.', 'The act of transposing, or the state of being\n   transposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transposition', 'n.', 'The bringing of any term of an equation from one\n   side over to the other without destroying the equation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transposition', 'n.', 'A change of the natural order of words in a\n   sentence; as, the Latin and Greek languages admit transposition,\n   without inconvenience, to a much greater extent than the English.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transposition', 'n.', 'A change of a composition into another key.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpositional', 'a.', 'Of or pertaining to transposition; involving\n   transposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transpositive', 'a.', 'Made by transposing; consisting in transposition;\n   transposable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transprint', 'v. t.', 'To transfer to the wrong place in printing; to print\n   out of place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transprose', 'v. t.', 'To change from prose into verse; to versify; also,\n   to change from verse into prose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transregionate', 'a.', 'Foreign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transshaped', 'imp. & p. p.', 'of Transshape');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transshaping', 'p. pr. & vb. n.', 'of Transshape');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transshape', 'v. t.', 'To change into another shape or form; to transform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transship', 'v. t.', 'To transfer from one ship or conveyance to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transshipment', 'n.', 'The act of transshipping, or transferring, as goods,\n   from one ship or conveyance to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transubstantiate', 'v. t.', 'To change into another substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transubstantiate', 'v. t.', 'To change, as the sacramental elements, bread\n   and wine, into the flesh and blood of Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transubstantiation', 'n.', 'A change into another substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transubstantiation', 'n.', 'The doctrine held by Roman Catholics, that the\n   bread and wine in the Mass is converted into the body and blood of\n   Christ; -- distinguished from consubstantiation, and impanation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transubstantiator', 'n.', 'One who maintains the doctrine of\n   transubstantiation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transudation', 'n.', 'The act or process of transuding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transudation', 'n.', 'Same as Exosmose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transudatory', 'a.', 'Of or pertaining to transudation; passing by\n   transudation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transuded', 'imp. & p. p.', 'of Transude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transuding', 'p. pr. & vb. n.', 'of Transude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transude', 'v. i.', 'To pass, as perspirable matter does, through the pores\n   or interstices of textures; as, liquor may transude through leather or\n   wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transume', 'v. t.', 'To change; to convert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transsummer', 'n.', 'See Transom, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transumpt', 'n.', 'A copy or exemplification of a record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transumption', 'n.', 'Act of taking from one place to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transumptive', 'a.', 'Taking from one to another; metaphorical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transvasate', 'v. t.', 'To pour out of one vessel into another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transvasation', 'n.', 'The act or process of pouring out of one vessel into\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transvection', 'n.', 'The act of conveying or carrying over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transverberate', 'v. t.', 'To beat or strike through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transversal', 'a.', 'Running or lying across; transverse; as, a transversal\n   line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transversal', 'n.', 'A straight line which traverses or intersects any\n   system of other lines, as a line intersecting the three sides of a\n   triangle or the sides produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transverse', 'a.', 'Lying or being across, or in a crosswise direction;\n   athwart; -- often opposed to longitudinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transverse', 'n.', 'Anything that is transverse or athwart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transverse', 'n.', 'The longer, or transverse, axis of an ellipse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transversed', 'imp. & p. p.', 'of Transverse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transversing', 'p. pr. & vb. n.', 'of Transverse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transverse', 'v. t.', 'To overturn; to change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transverse', 'v. t.', 'To change from prose into verse, or from verse into\n   prose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transversely', 'adv.', 'In a transverse manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transversion', 'n.', 'The act of changing from prose into verse, or from\n   verse into prose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transvert', 'v. t.', 'To cause to turn across; to transverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transvertible', 'a.', 'Capable of being transverted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Transvolation', 'n.', 'The act of flying beyond or across.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trant', 'v. i.', 'To traffic in an itinerary manner; to peddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tranter', 'n.', 'One who trants; a peddler; a carrier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapped', 'imp. & p. p.', 'of Trap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapping', 'p. pr. & vb. n.', 'of Trap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trap', 'v. t.', 'To dress with ornaments; to adorn; -- said especially of\n   horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trap', 'n.', 'An old term rather loosely used to designate various\n   dark-colored, heavy igneous rocks, including especially the\n   feldspathic-augitic rocks, basalt, dolerite, amygdaloid, etc., but\n   including also some kinds of diorite. Called also trap rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trap', 'a.', 'Of or pertaining to trap rock; as, a trap dike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trap', 'n.', 'A machine or contrivance that shuts suddenly, as with a\n   spring, used for taking game or other animals; as, a trap for foxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trap', 'n.', 'Fig.: A snare; an ambush; a stratagem; any device by which\n   one may be caught unawares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trap', 'n.', 'A wooden instrument shaped somewhat like a shoe, used in the\n   game of trapball. It consists of a pivoted arm on one end of which is\n   placed the ball to be thrown into the air by striking the other end.\n   Also, a machine for throwing into the air glass balls, clay pigeons,\n   etc., to be shot at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trap', 'n.', 'The game of trapball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trap', 'n.', 'A bend, sag, or partitioned chamber, in a drain, soil pipe,\n   sewer, etc., arranged so that the liquid contents form a seal which\n   prevents passage of air or gas, but permits the flow of liquids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trap', 'n.', 'A place in a water pipe, pump, etc., where air accumulates\n   for want of an outlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trap', 'n.', 'A wagon, or other vehicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trap', 'n.', 'A kind of movable stepladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trap', 'v. t.', 'To catch in a trap or traps; as, to trap foxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trap', 'v. t.', 'Fig.: To insnare; to take by stratagem; to entrap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trap', 'v. t.', 'To provide with a trap; as, to trap a drain; to trap a\n   sewer pipe. See 4th Trap, 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trap', 'v. i.', 'To set traps for game; to make a business of trapping\n   game; as, to trap for beaver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapan', 'n.', 'A snare; a stratagem; a trepan. See 3d Trepan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapanned', 'imp. & p. p.', 'of Trapan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapanning', 'p. pr. & vb. n.', 'of Trapan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapan', 'v. t.', 'To insnare; to catch by stratagem; to entrap; to trepan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapanner', 'n.', 'One who trapans, or insnares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapball', 'n.', 'An old game of ball played with a trap. See 4th Trap, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapdoor', 'n.', 'A lifting or sliding door covering an opening in a roof\n   or floor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapdoor', 'n.', 'A door in a level for regulating the ventilating current;\n   -- called also weather door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trape', 'v. i.', 'To walk or run about in an idle or slatternly manner; to\n   traipse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapes', 'n.', 'A slattern; an idle, sluttish, or untidy woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapes', 'v. i.', 'To go about in an idle or slatternly fashion; to trape;\n   to traipse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapezate', 'a.', 'Having the form of a trapezium; trapeziform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapeze', 'n.', 'A trapezium. See Trapezium, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapeze', 'n.', 'A swinging horizontal bar, suspended at each end by a\n   rope; -- used by gymnasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapeziform', 'a.', 'Having the form of a trapezium; trapezoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapeziums', 'pl. ', 'of Trapezium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapezia', 'pl. ', 'of Trapezium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapezium', 'n.', 'A plane figure bounded by four right lines, of which no\n   two are parallel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapezium', 'n.', 'A bone of the carpus at the base of the first\n   metacarpal, or thumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapezium', 'n.', 'A region on the ventral side of the brain, either just\n   back of the pons Varolii, or, as in man, covered by the posterior\n   extension of its transverse fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapezohedral', 'a.', 'Of, pertaining to, or resembling, a trapezohedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapezohedron', 'n.', 'A solid bounded by twenty-four equal and similar\n   trapeziums; a tetragonal trisoctahedron. See the Note under\n   Trisoctahedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapezohedron', 'n.', 'A tetartohedral solid of the hexagonal system,\n   bounded by six trapezoidal planes. The faces of this form are common on\n   quartz crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapezoid', 'n.', 'A plane four-sided figure, having two sides parallel to\n   each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapezoid', 'n.', 'A bone of the carpus at the base of the second\n   metacarpal, or index finger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapezoid', 'a.', 'Having the form of a trapezoid; trapezoidal; as, the\n   trapezoid ligament which connects the coracoid process and the\n   clavicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapezoid', 'a.', 'Of or pertaining to the trapezoid ligament; as, the\n   trapezoid line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapezoidal', 'a.', 'Having the form of a trapezoid; trapezoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapezoidal', 'a.', 'Trapezohedral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traphole', 'n.', 'See Trou-de-loup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trappean', 'a.', 'Of or pertaining to trap; being of the nature of trap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapper', 'n.', 'One who traps animals; one who makes a business of\n   trapping animals for their furs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapper', 'n.', 'A boy who opens and shuts a trapdoor in a gallery or\n   level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trappings', 'n. pl.', 'That which serves to trap or adorn; ornaments;\n   dress; superficial decorations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trappings', 'n. pl.', 'Specifically, ornaments to be put on horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trappist', 'n.', 'A monk belonging to a branch of the Cistercian Order,\n   which was established by Armand de Rance in 1660 at the monastery of La\n   Trappe in Normandy. Extreme austerity characterizes their discipline.\n   They were introduced permanently into the United States in 1848, and\n   have monasteries in Iowa and Kentucky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trappous', 'n.', 'Of or performance to trap; resembling trap, or partaking\n   of its form or qualities; trappy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trappures', 'n. pl.', 'Trappings for a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trappy', 'a.', 'Same as Trappous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traps', 'n. pl.', 'Small or portable articles for dress, furniture, or use;\n   goods; luggage; things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trapstick', 'n.', 'A stick used in playing the game of trapball; hence,\n   fig., a slender leg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trash', 'n.', 'That which is worthless or useless; rubbish; refuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trash', 'n.', 'Especially, loppings and leaves of trees, bruised sugar\n   cane, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trash', 'n.', 'A worthless person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trash', 'n.', 'A collar, leash, or halter used to restrain a dog in\n   pursuing game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trashed', 'imp. & p. p.', 'of Trash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trashing', 'p. pr. & vb. n.', 'of Trash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trash', 'v. t.', 'To free from trash, or worthless matter; hence, to lop;\n   to crop, as to trash the rattoons of sugar cane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trash', 'v. t.', 'To treat as trash, or worthless matter; hence, to spurn,\n   humiliate, or crush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trash', 'v. t.', 'To hold back by a trash or leash, as a dog in pursuing\n   game; hence, to retard, encumber, or restrain; to clog; to hinder\n   vexatiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trash', 'v. i.', 'To follow with violence and trampling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trashily', 'adv.', 'In a trashy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trashiness', 'n.', 'The quality or state of being trashy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trashy', 'superl.', 'Like trash; containing much trash; waste; rejected;\n   worthless; useless; as, a trashy novel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trass', 'n.', 'A white to gray volcanic tufa, formed of decomposed\n   trachytic cinders; -- sometimes used as a cement. Hence, a coarse sort\n   of plaster or mortar, durable in water, and used to line cisterns and\n   other reservoirs of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traulism', 'n.', 'A stammering or stuttering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traumatic', 'a.', 'Of or pertaining to wounds; applied to wounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traumatic', 'a.', 'Adapted to the cure of wounds; vulnerary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traumatic', 'a.', 'Produced by wounds; as, traumatic tetanus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traumatic', 'n.', 'A traumatic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traumatism', 'n.', 'A wound or injury directly produced by causes external\n   to the body; also, violence producing a wound or injury; as, rupture of\n   the stomach caused by traumatism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traunce', 'n. & v.', 'See Trance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traunt', 'v. i.', 'Same as Trant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traunter', 'n.', 'Same as Tranter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travail', 'n.', 'Labor with pain; severe toil or exertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travail', 'n.', 'Parturition; labor; as, an easy travail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travailed', 'imp. & p. p.', 'of Travail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travailing', 'p. pr. & vb. n.', 'of Travail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travail', 'n.', 'To labor with pain; to toil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travail', 'n.', 'To suffer the pangs of childbirth; to be in labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travail', 'v. t.', 'To harass; to tire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travailous', 'a.', 'Causing travail; laborious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trave', 'n.', 'A crossbeam; a lay of joists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trave', 'n.', 'A wooden frame to confine an unruly horse or ox while\n   shoeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traveled', 'imp. & p. p.', 'of Travel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travelled', '', 'of Travel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traveling', 'p. pr. & vb. n.', 'of Travel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travelling', '', 'of Travel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travel', 'v. i.', 'To labor; to travail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travel', 'v. i.', 'To go or march on foot; to walk; as, to travel over the\n   city, or through the streets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travel', 'v. i.', 'To pass by riding, or in any manner, to a distant place,\n   or to many places; to journey; as, a man travels for his health; he is\n   traveling in California.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travel', 'v. i.', 'To pass; to go; to move.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travel', 'v. t.', 'To journey over; to traverse; as, to travel the\n   continent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travel', 'v. t.', 'To force to journey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travel', 'n.', 'The act of traveling, or journeying from place to place; a\n   journey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travel', 'n.', 'An account, by a traveler, of occurrences and observations\n   during a journey; as, a book of travels; -- often used as the title of\n   a book; as, Travels in Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travel', 'n.', 'The length of stroke of a reciprocating piece; as, the\n   travel of a slide valve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travel', 'n.', 'Labor; parturition; travail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traveled', 'a.', 'Having made journeys; having gained knowledge or\n   experience by traveling; hence, knowing; experienced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traveler', 'n.', 'One who travels; one who has traveled much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traveler', 'n.', 'A commercial agent who travels for the purpose of\n   receiving orders for merchants, making collections, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traveler', 'n.', 'A traveling crane. See under Crane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traveler', 'n.', 'The metal loop which travels around the ring surrounding\n   the bobbin, in a ring spinner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traveler', 'n.', 'An iron encircling a rope, bar, spar, or the like, and\n   sliding thereon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travel-tainted', 'a.', 'Harassed; fatigued with travel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travers', 'a.', 'Across; athwart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traversable', 'a.', 'Capable of being traversed, or passed over; as, a\n   traversable region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traversable', 'a.', 'Deniable; specifically (Law), liable to legal\n   objection; as, a traversable presentment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'a.', 'Lying across; being in a direction across something else;\n   as, paths cut with traverse trenches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'adv.', 'Athwart; across; crosswise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'a.', 'Anything that traverses, or crosses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'a.', 'Something that thwarts, crosses, or obstructs; a cross\n   accident; as, he would have succeeded, had it not been for unlucky\n   traverses not under his control.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'a.', 'A barrier, sliding door, movable screen, curtain, or the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'a.', 'A gallery or loft of communication from side to side of a\n   church or other large building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'a.', 'A work thrown up to intercept an enfilade, or reverse\n   fire, along exposed passage, or line of work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'a.', 'A formal denial of some matter of fact alleged by the\n   opposite party in any stage of the pleadings. The technical words\n   introducing a traverse are absque hoc, without this; that is, without\n   this which follows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'a.', 'The zigzag course or courses made by a ship in passing\n   from one place to another; a compound course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'a.', 'A line lying across a figure or other lines; a\n   transversal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'a.', 'A line surveyed across a plot of ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'a.', 'The turning of a gun so as to make it point in any\n   desired direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'a.', 'A turning; a trick; a subterfuge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traversed', 'imp. & p. p.', 'of Traverse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traversing', 'p. pr. & vb. n.', 'of Traverse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'a.', 'To lay in a cross direction; to cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'a.', 'To cross by way of opposition; to thwart with obstacles;\n   to obstruct; to bring to naught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'a.', 'To wander over; to cross in traveling; as, to traverse\n   the habitable globe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'a.', 'To pass over and view; to survey carefully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'a.', 'To turn to the one side or the other, in order to point\n   in any direction; as, to traverse a cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'a.', 'To plane in a direction across the grain of the wood; as,\n   to traverse a board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'a.', 'To deny formally, as what the opposite party has alleged.\n   When the plaintiff or defendant advances new matter, he avers it to be\n   true, and traverses what the other party has affirmed. To traverse an\n   indictment or an office is to deny it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'v. i.', 'To use the posture or motions of opposition or\n   counteraction, as in fencing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'v. i.', 'To turn, as on a pivot; to move round; to swivel; as,\n   the needle of a compass traverses; if it does not traverse well, it is\n   an unsafe guide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverse', 'v. i.', 'To tread or move crosswise, as a horse that throws his\n   croup to one side and his head to the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverser', 'n.', 'One who, or that which, traverses, or moves, as an index\n   on a scale, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverser', 'n.', 'One who traverses, or denies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traverser', 'n.', 'A traverse table. See under Traverse, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Traversing', 'a.', 'Adjustable laterally; having a lateral motion, or a\n   swinging motion; adapted for giving lateral motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travertine', 'n.', 'A white concretionary form of calcium carbonate,\n   usually hard and semicrystalline. It is deposited from the water of\n   springs or streams holding lime in solution. Extensive deposits exist\n   at Tivoli, near Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travesty', 'a.', 'Disguised by dress so as to be ridiculous; travestied; --\n   applied to a book or shorter composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travesties', 'pl. ', 'of Travesty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travesty', 'n.', 'A burlesque translation or imitation of a work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travestied', 'imp. & p. p.', 'of Travesty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travesting', 'p. pr. & vb. n.', 'of Travesty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Travesty', 'v. t.', 'To translate, imitate, or represent, so as to render\n   ridiculous or ludicrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trawl', 'v. t.', 'To take fish, or other marine animals, with a trawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trawl', 'n.', 'A fishing line, often extending a mile or more, having many\n   short lines bearing hooks attached to it. It is used for catching cod,\n   halibut, etc.; a boulter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trawl', 'n.', 'A large bag net attached to a beam with iron frames at its\n   ends, and dragged at the bottom of the sea, -- used in fishing, and in\n   gathering forms of marine life from the sea bottom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trawlboat', 'n.', 'A boat used in fishing with trawls or trawlnets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trawler', 'n.', 'One who, or that which, trawls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trawler', 'n.', 'A fishing vessel which trails a net behind it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trawlermen', 'pl. ', 'of Trawlerman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trawlerman', 'n.', 'A fisherman who used unlawful arts and engines to catch\n   fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trawlnet', 'n.', 'Same as Trawl, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trawlwarp', 'n.', 'A rope passing through a block, used in managing or\n   dragging a trawlnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tray', 'v. t.', 'To betray; to deceive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trays', 'pl. ', 'of Tray');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tray', 'n.', 'A small trough or wooden vessel, sometimes scooped out of a\n   block of wood, for various domestic uses, as in making bread, chopping\n   meat, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tray', 'n.', 'A flat, broad vessel on which dishes, glasses, etc., are\n   carried; a waiter; a salver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tray', 'n.', 'A shallow box, generally without a top, often used within a\n   chest, trunk, box, etc., as a removable receptacle for small or light\n   articles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trayfuls', 'pl. ', 'of Trayful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trayful', 'n.', 'As much as a tray will hold; enough to fill a tray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trays', 'n. pl.', 'See Trais.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tray-trip', 'n.', 'An old game played with dice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treacher', 'n.', 'A traitor; a cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treacherous', 'a.', 'Like a traitor; involving treachery; violating\n   allegiance or faith pledged; traitorous to the state or sovereign;\n   perfidious in private life; betraying a trust; faithless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treachery', 'n.', 'Violation of allegiance or of faith and confidence;\n   treasonable or perfidious conduct; perfidy; treason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treachetour', 'n.', 'Alt. of Treachour');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treachour', 'n.', 'A traitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treacle', 'n.', 'A remedy against poison. See Theriac, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treacle', 'n.', 'A sovereign remedy; a cure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treacle', 'n.', 'Molasses; sometimes, specifically, the molasses which\n   drains from the sugar-refining molds, and which is also called\n   sugarhouse molasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treacle', 'n.', 'A saccharine fluid, consisting of the inspissated juices\n   or decoctions of certain vegetables, as the sap of the birch, sycamore,\n   and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treacly', 'a.', 'Like, or composed of, treacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trod', 'imp.', 'of Tread');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trodden', 'p. p.', 'of Tread');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trod', '', 'of Tread');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treading', 'p. pr. & vb. n.', 'of Tread');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tread', 'v. i.', 'To set the foot; to step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tread', 'v. i.', 'To walk or go; especially, to walk with a stately or a\n   cautious step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tread', 'v. i.', 'To copulate; said of birds, esp. the males.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tread', 'v. t.', 'To step or walk on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tread', 'v. t.', 'To beat or press with the feet; as, to tread a path; to\n   tread land when too light; a well-trodden path.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tread', 'v. t.', 'To go through or accomplish by walking, dancing, or the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tread', 'v. t.', 'To crush under the foot; to trample in contempt or\n   hatred; to subdue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tread', 'v. t.', 'To copulate with; to feather; to cover; -- said of the\n   male bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tread', 'n.', 'A step or stepping; pressure with the foot; a footstep; as,\n   a nimble tread; a cautious tread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tread', 'n.', 'Manner or style of stepping; action; gait; as, the horse has\n   a good tread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tread', 'n.', 'Way; track; path.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tread', 'n.', 'The act of copulation in birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tread', 'n.', 'The upper horizontal part of a step, on which the foot is\n   placed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tread', 'n.', 'The top of the banquette, on which soldiers stand to fire\n   over the parapet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tread', 'n.', 'The part of a wheel that bears upon the road or rail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tread', 'n.', 'The part of a rail upon which car wheels bear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tread', 'n.', 'The chalaza of a birds egg; the treadle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tread', 'n.', 'A bruise or abrasion produced on the foot or ankle of a\n   horse that interferes. See Interfere, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treadboard', 'n.', 'See Tread, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treader', 'n.', 'One who treads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treadfowl', 'n.', 'A cock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treadle', 'n.', 'The part of a foot lathe, or other machine, which is\n   pressed or moved by the foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treadle', 'n.', 'The chalaza of a birds egg; the tread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treadmill', 'n.', 'A mill worked by persons treading upon steps on the\n   periphery of a wide wheel having a horizontal axis. It is used\n   principally as a means of prison discipline. Also, a mill worked by\n   horses, dogs, etc., treading an endless belt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tread-softly', 'n.', 'Spurge nettle. See under Nettle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treadwheel', 'n.', 'A wheel turned by persons or animals, by treading,\n   climbing, or pushing with the feet, upon its periphery or face. See\n   Treadmill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treague', 'n.', 'A truce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treason', 'n.', 'The offense of attempting to overthrow the government of\n   the state to which the offender owes allegiance, or of betraying the\n   state into the hands of a foreign power; disloyalty; treachery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treason', 'n.', 'Loosely, the betrayal of any trust or confidence;\n   treachery; perfidy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treasonable', 'a.', 'Pertaining to treason; consisting of treason;\n   involving the crime of treason, or partaking of its guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treasonous', 'a.', 'Treasonable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treasure', 'n.', 'Wealth accumulated; especially, a stock, or store of\n   money in reserve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treasure', 'n.', 'A great quantity of anything collected for future use;\n   abundance; plenty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treasure', 'n.', 'That which is very much valued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treasured', 'imp. & p. p.', 'of Treasure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treasuring', 'p. pr. & vb. n.', 'of Treasure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treasure', 'v. t.', 'To collect and deposit, as money or other valuable\n   things, for future use; to lay up; to hoard; usually with up; as, to\n   treasure up gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treasure-house', 'n.', 'A house or building where treasures and stores are\n   kept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treasurer', 'n.', 'One who has the care of a treasure or treasure or\n   treasury; an officer who receives the public money arising from taxes\n   and duties, or other sources of revenue, takes charge of the same, and\n   disburses it upon orders made by the proper authority; one who has\n   charge of collected funds; as, the treasurer of a society or\n   corporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treasurership', 'n.', 'The office of treasurer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treasuress', 'n.', 'A woman who is a treasurer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treasure-trove', 'n.', 'Any money, bullion, or the like, found in the\n   earth, or otherwise hidden, the owner of which is not known. In England\n   such treasure belongs to the crown; whereas similar treasure found in\n   the sea, or upon the surface of the land, belongs to the finder if no\n   owner appears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treasuries', 'pl. ', 'of Treasury');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treasury', 'n.', 'A place or building in which stores of wealth are\n   deposited; especially, a place where public revenues are deposited and\n   kept, and where money is disbursed to defray the expenses of\n   government; hence, also, the place of deposit and disbursement of any\n   collected funds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treasury', 'n.', 'That department of a government which has charge of the\n   finances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treasury', 'n.', 'A repository of abundance; a storehouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treasury', 'n.', 'Hence, a book or work containing much valuable knowledge,\n   wisdom, wit, or the like; a thesaurus; as, \" Maunders Treasury of\n   Botany.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treasury', 'n.', 'A treasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treated', 'imp. & p. p.', 'of Treat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treating', 'p. pr. & vb. n.', 'of Treat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treat', 'v. t.', 'To handle; to manage; to use; to bear ones self toward;\n   as, to treat prisoners cruelly; to treat children kindly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treat', 'v. t.', 'To discourse on; to handle in a particular manner, in\n   writing or speaking; as, to treat a subject diffusely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treat', 'v. t.', 'To entertain with food or drink, especially the latter,\n   as a compliment, or as an expression of friendship or regard; as, to\n   treat the whole company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treat', 'v. t.', 'To negotiate; to settle; to make terms for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treat', 'v. t.', 'To care for medicinally or surgically; to manage in the\n   use of remedies or appliances; as, to treat a disease, a wound, or a\n   patient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treat', 'v. t.', 'To subject to some action; to apply something to; as, to\n   treat a substance with sulphuric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treat', 'v. t.', 'To entreat; to beseech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treat', 'v. i.', 'To discourse; to handle a subject in writing or speaking;\n   to make discussion; -- usually with of; as, Cicero treats of old age\n   and of duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treat', 'v. i.', 'To negotiate; to come to terms of accommodation; -- often\n   followed by with; as, envoys were appointed to treat with France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treat', 'v. i.', 'To give a gratuitous entertainment, esp. of food or\n   drink, as a compliment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treat', 'n.', 'A parley; a conference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treat', 'n.', 'An entertainment given as an expression of regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treat', 'n.', 'That which affords entertainment; a gratification; a\n   satisfaction; as, the concert was a rich treat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treatable', 'a.', 'Manageable; tractable; hence, moderate; not violent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treatably', 'adv.', 'In a treatable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treater', 'n.', 'One who treats; one who handles, or discourses on, a\n   subject; also, one who entertains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treatise', 'n.', 'A written composition on a particular subject, in which\n   its principles are discussed or explained; a tract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treatise', 'n.', 'Story; discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treatiser', 'n.', 'One who writes a treatise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treatment', 'n.', 'The act or manner of treating; management; manipulation;\n   handling; usage; as, unkind treatment; medical treatment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treatment', 'n.', 'Entertainment; treat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treature', 'n.', 'Treatment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treaties', 'pl. ', 'of Treaty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treaty', 'n.', 'The act of treating for the adjustment of differences, as\n   for forming an agreement; negotiation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treaty', 'n.', 'An agreement so made; specifically, an agreement, league,\n   or contract between two or more nations or sovereigns, formally signed\n   by commissioners properly authorized, and solemnly ratified by the\n   several sovereigns, or the supreme power of each state; an agreement\n   between two or more independent states; as, a treaty of peace; a treaty\n   of alliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treaty', 'n.', 'A proposal tending to an agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treaty', 'n.', 'A treatise; a tract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treble', 'a.', 'Threefold; triple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treble', 'a.', 'Acute; sharp; as, a treble sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treble', 'a.', 'Playing or singing the highest part or most acute sounds;\n   playing or singing the treble; as, a treble violin or voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treble', 'adv.', 'Trebly; triply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treble', 'n.', 'The highest of the four principal parts in music; the part\n   usually sung by boys or women; soprano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trebled', 'imp. & p. p.', 'of Treble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trebling', 'p. pr. & vb. n.', 'of Treble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treble', 'v. t.', 'To make thrice as much; to make threefold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treble', 'v. t.', 'To utter in a treble key; to whine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treble', 'v. i.', 'To become threefold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trebleness', 'n.', 'The quality or state of being treble; as, the\n   trebleness of tones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treblet', 'n.', 'Same as Triblet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trebly', 'adv.', 'In a treble manner; with a threefold number or quantity;\n   triply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trebuchet', 'n.', 'Alt. of Trebucket');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trebucket', 'n.', 'A cucking stool; a tumbrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trebucket', 'n.', 'A military engine used in the Middle Ages for throwing\n   stones, etc. It acted by means of a great weight fastened to the short\n   arm of a lever, which, being let fall, raised the end of the long arm\n   with great velocity, hurling stones with much force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trebucket', 'n.', 'A kind of balance for weighing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trechometer', 'n.', 'An odometer for vehicles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treckschuyt', 'n.', 'A covered boat for goods and passengers, used on the\n   Dutch and Flemish canals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treddle', 'n.', 'See Treadle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treddle', 'n.', 'A prostitute; a strumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treddle', 'n.', 'The dung of sheep or hares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tredille', 'n.', 'A game at cards for three.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tree', 'n.', 'Any perennial woody plant of considerable size (usually over\n   twenty feet high) and growing with a single trunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tree', 'n.', 'Something constructed in the form of, or considered as\n   resembling, a tree, consisting of a stem, or stock, and branches; as, a\n   genealogical tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tree', 'n.', 'A piece of timber, or something commonly made of timber; --\n   used in composition, as in axletree, boottree, chesstree, crosstree,\n   whiffletree, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tree', 'n.', 'A cross or gallows; as Tyburn tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tree', 'n.', 'Wood; timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tree', 'n.', 'A mass of crystals, aggregated in arborescent forms, obtained\n   by precipitation of a metal from solution. See Lead tree, under Lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treed', 'imp. & p. p.', 'of Tree');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treeing', 'p. pr. & vb. n.', 'of Tree');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tree', 'v. t.', 'To drive to a tree; to cause to ascend a tree; as, a dog\n   trees a squirrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tree', 'v. t.', 'To place upon a tree; to fit with a tree; to stretch upon\n   a tree; as, to tree a boot. See Tree, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treebeard', 'n.', 'A pendulous branching lichen (Usnea barbata); -- so\n   called from its resemblance to hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treefuls', 'pl. ', 'of Treeful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treeful', 'n.', 'The quantity or number which fills a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treeless', 'a.', 'Destitute of trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treen', 'a.', 'Made of wood; wooden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treen', 'a.', 'Relating to, or drawn from, trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treen', '', 'pl. of Tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treenail', 'n.', 'A long wooden pin used in fastening the planks of a\n   vessel to the timbers or to each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trefle', 'n.', 'A species of time; -- so called from its resemblance in\n   form to a trefoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trefle', 'a.', 'Having a three-lobed extremity or extremities, as a cross;\n   also, more rarely, ornamented with trefoils projecting from the edges,\n   as a bearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trefoil', 'n.', 'Any plant of the genus Trifolium, which includes the white\n   clover, red clover, etc.; -- less properly, applied also to the\n   nonesuch, or black medic. See Clover, and Medic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trefoil', 'n.', 'An ornamental foliation consisting of three divisions, or\n   foils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trefoil', 'n.', 'A charge representing the clover leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trefoiled', 'a.', 'Same as Trefle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treget', 'n.', 'Guile; trickery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tregetour', 'n.', 'A juggler who produces illusions by the use of elaborate\n   machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tregetry', 'n.', 'Trickery; also, a trick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trehala', 'n.', 'An amorphous variety of manna obtained from the nests and\n   cocoons of a Syrian coleopterous insect (Larinus maculatus, L.\n   nidificans, etc.) which feeds on the foliage of a variety of thistle.\n   It is used as an article of food, and is called also nest sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trehalose', 'n.', 'Mycose; -- so called because sometimes obtained from\n   trehala.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Treillage', 'n.', 'Latticework for supporting vines, etc.; an espalier; a\n   trellis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trellis', 'n.', 'A structure or frame of crossbarred work, or latticework,\n   used for various purposes, as for screens or for supporting plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trellised', 'a.', 'Having a trellis or trellises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tremando', 'a.', 'Trembling; -- used as a direction to perform a passage\n   with a general shaking of the whole chord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trematode', 'n.', 'One of the Trematodea. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trematodea', 'n. pl.', 'An extensive order of parasitic worms. They are\n   found in the internal cavities of animals belonging to all classes.\n   Many species are found, also, on the gills and skin of fishes. A few\n   species are parasitic on man, and some, of which the fluke is the most\n   important, are injurious parasites of domestic animals. The trematodes\n   usually have a flattened body covered with a chitinous skin, and are\n   furnished with two or more suckers for adhesion. Most of the species\n   are hermaphrodite. Called also Trematoda, and Trematoidea. See Fluke,\n   Tristoma, and Cercaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trematoid', 'a.', 'Of or pertaining to the Trematodea. See Illustration in\n   Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trembled', 'imp. & p. p.', 'of Tremble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trembling', 'p. pr. & vb. n.', 'of Tremble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tremble', 'v. i.', 'To shake involuntarily, as with fear, cold, or\n   weakness; to quake; to quiver; to shiver; to shudder; -- said of a\n   person or an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tremble', 'v. i.', 'To totter; to shake; -- said of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tremble', 'v. i.', 'To quaver or shake, as sound; to be tremulous; as the\n   voice trembles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tremble', 'n.', 'An involuntary shaking or quivering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trembler', 'n.', 'One who trembles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trembling', 'a.', 'Shaking; tottering; quivering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tremella', 'n.', 'A genus of gelatinous fungi found in moist grounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tremendous', 'a.', 'Fitted to excite fear or terror; such as may astonish\n   or terrify by its magnitude, force, or violence; terrible; dreadful;\n   as, a tremendous wind; a tremendous shower; a tremendous shock or fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tremex', 'n.', 'A genus of large hymenopterous insects allied to the\n   sawflies. The female lays her eggs in holes which she bores in the\n   trunks of trees with her large and long ovipositor, and the larva bores\n   in the wood. See Illust. of Horntail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tremolando', 'a.', 'Same as Tremando.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tremolite', 'n.', 'A white variety of amphibole, or hornblende, occurring\n   in long, bladelike crystals, and coarsely fibrous masses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tremolo', 'n.', 'The rapid reiteration of tones without any apparent\n   cessation, so as to produce a tremulous effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tremolo', 'n.', 'A certain contrivance in an organ, which causes the notes\n   to sound with rapid pulses or beats, producing a tremulous effect; --\n   called also tremolant, and tremulant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tremor', 'v.', 'A trembling; a shivering or shaking; a quivering or\n   vibratory motion; as, the tremor of a person who is weak, infirm, or\n   old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tremulant', 'a.', 'Alt. of Tremulent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tremulent', 'a.', 'Tremulous; trembling; shaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tremulous', 'a.', 'Shaking; shivering; quivering; as, a tremulous limb; a\n   tremulous motion of the hand or the lips; the tremulous leaf of the\n   poplar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tremulous', 'a.', 'Affected with fear or timidity; trembling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tren', 'n.', 'A fish spear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trenail', 'n.', 'Same as Treenail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trenched', 'imp. & p. p.', 'of Trench');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trenching', 'p. pr. & vb. n.', 'of Trench');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trench', 'v. t.', 'To cut; to form or shape by cutting; to make by\n   incision, hewing, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trench', 'v. t.', 'To fortify by cutting a ditch, and raising a rampart or\n   breastwork with the earth thrown out of the ditch; to intrench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trench', 'v. t.', 'To cut furrows or ditches in; as, to trench land for the\n   purpose of draining it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trench', 'v. t.', 'To dig or cultivate very deeply, usually by digging\n   parallel contiguous trenches in succession, filling each from the next;\n   as, to trench a garden for certain crops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trench', 'v. i.', 'To encroach; to intrench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trench', 'v. i.', 'To have direction; to aim or tend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trench', 'v. t.', 'A long, narrow cut in the earth; a ditch; as, a trench\n   for draining land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trench', 'v. t.', 'An alley; a narrow path or walk cut through woods,\n   shrubbery, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trench', 'v. t.', 'An excavation made during a siege, for the purpose of\n   covering the troops as they advance toward the besieged place. The term\n   includes the parallels and the approaches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trenchand', 'a.', 'Trenchant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trenchant', 'v. t.', 'Fitted to trench or cut; gutting; sharp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trenchant', 'v. t.', 'Fig.: Keen; biting; severe; as, trenchant wit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trenchantly', 'adv.', 'In a trenchant, or sharp, manner; sharply; severely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trencher', 'v. t.', 'One who trenches; esp., one who cuts or digs ditches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trencher', 'v. t.', 'A large wooden plate or platter, as for table use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trencher', 'v. t.', 'The table; hence, the pleasures of the table; food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trencher-men', 'pl. ', 'of Trencher-man');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trencher-man', 'n.', 'A feeder; a great eater; a gormandizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trencher-man', 'n.', 'A cook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trencher-man', 'n.', 'A table companion; a trencher mate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trenchmore', 'n.', 'A kind of lively dance of a rude, boisterous character.\n   Also, music in triple time appropriate to the dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trenchmore', 'v. i.', 'To dance the trenchmore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trench-plow', 'v. t.', 'Alt. of Trench-plough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trench-plough', 'v. t.', 'To plow with deep furrows, for the purpose of\n   loosening the land to a greater depth than usual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trended', 'imp. & p. p.', 'of Trend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trending', 'p. pr. & vb. n.', 'of Trend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trend', 'v. i.', 'To have a particular direction; to run; to stretch; to\n   tend; as, the shore of the sea trends to the southwest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trend', 'v. t.', 'To cause to turn; to bend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trend', 'n.', 'Inclination in a particular direction; tendency; general\n   direction; as, the trend of a coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trend', 'v. t.', 'To cleanse, as wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trend', 'n.', 'Clean wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trender', 'n.', 'One whose business is to free wool from its filth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trendle', 'v. i.', 'A wheel, spindle, or the like; a trundle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trental', 'n.', 'An office and mass for the dead on the thirtieth day after\n   death or burial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trental', 'n.', 'Hence, a dirge; an elegy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trenton period', '', 'A subdivision in the lower Silurian system of\n   America; -- so named from Trenton Falls, in New York. The rocks are\n   mostly limestones, and the period is divided into the Trenton, Utica,\n   and Cincinnati epochs. See the Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trepan', 'n.', 'A crown-saw or cylindrical saw for perforating the skull,\n   turned, when used, like a bit or gimlet. See Trephine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trepan', 'n.', 'A kind of broad chisel for sinking shafts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trepanned', 'imp. & p. p.', 'of Trepan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trepanning', 'p. pr. & vb. n.', 'of Trepan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trepan', 'v. t. & i.', 'To perforate (the skull) with a trepan, so as to\n   remove a portion of the bone, and thus relieve the brain from pressure\n   or irritation; to perform an operation with the trepan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trepan', 'n.', 'A snare; a trapan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trepan', 'n.', 'a deceiver; a cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trepan', 'v. t.', 'To insnare; to trap; to trapan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trepang', 'n.', 'Any one of several species of large holothurians, some of\n   which are dried and extensively used as food in China; -- called also\n   beche de mer, sea cucumber, and sea slug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trepanize', 'v. t.', 'To trepan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trepanner', 'n.', 'One who trepans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trepeget', 'n.', 'A trebuchet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trephine', 'n.', 'An instrument for trepanning, being an improvement on the\n   trepan. It is a circular or cylindrical saw, with a handle like that of\n   a gimlet, and a little sharp perforator called the center pin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trephined', 'imp. & p. p.', 'of Trephine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trephining', 'p. pr. & vb. n.', 'of Trephine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trephine', 'v. t.', 'To perforate with a trephine; to trepan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trepid', 'a.', 'Trembling; quaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trepidation', 'n.', 'An involuntary trembling, sometimes an effect of\n   paralysis, but usually caused by terror or fear; quaking; quivering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trepidation', 'n.', 'Hence, a state of terror or alarm; fear; confusion;\n   fright; as, the men were in great trepidation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trepidation', 'n.', 'A libration of the starry sphere in the Ptolemaic\n   system; a motion ascribed to the firmament, to account for certain\n   small changes in the position of the ecliptic and of the stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trepidity', 'n.', 'Trepidation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tresayle', 'n.', 'A grandfathers grandfather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tresor', 'n.', 'Treasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trespassed', 'imp. & p. p.', 'of Trespass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trespassing', 'p. pr. & vb. n.', 'of Trespass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trespass', 'v. i.', 'To pass beyond a limit or boundary; hence, to depart;\n   to go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trespass', 'v. i.', 'To commit a trespass; esp., to enter unlawfully upon\n   the land of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trespass', 'v. i.', 'To go too far; to put any one to inconvenience by\n   demand or importunity; to intrude; as, to trespass upon the time or\n   patience of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trespass', 'v. i.', 'To commit any offense, or to do any act that injures\n   or annoys another; to violate any rule of rectitude, to the injury of\n   another; hence, in a moral sense, to transgress voluntarily any divine\n   law or command; to violate any known rule of duty; to sin; -- often\n   followed by against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trespass', 'v.', 'Any injury or offence done to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trespass', 'v.', 'Any voluntary transgression of the moral law; any\n   violation of a known rule of duty; sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trespass', 'v.', 'An unlawful act committed with force and violence (vi et\n   armis) on the person, property, or relative rights of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trespass', 'v.', 'An action for injuries accompanied with force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trespasser', 'n.', 'One who commits a trespass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trespasser', 'n.', 'One who enters upon anothers land, or violates his\n   rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trespasser', 'n.', 'A transgressor of the moral law; an offender; a sinner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tress', 'n.', 'A braid, knot, or curl, of hair; a ringlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tress', 'n.', 'Fig.: A knot or festoon, as of flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tressed', 'a.', 'Having tresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tressed', 'a.', 'Formed into ringlets or braided; braided; curled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tressel', 'n.', 'A trestle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tressful', 'a.', 'Tressy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tressure', 'n.', 'A kind of border similar to the orle, but of only half\n   the breadth of the latter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tressured', 'a.', 'Provided or bound with a tressure; arranged in the form\n   of a tressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tressy', 'a.', 'Abounding in tresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trestle', 'n.', 'A movable frame or support for anything, as scaffolding,\n   consisting of three or four legs secured to a top piece, and forming a\n   sort of stool or horse, used by carpenters, masons, and other workmen;\n   also, a kind of framework of strong posts or piles, and crossbeams, for\n   supporting a bridge, the track of a railway, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trestle', 'n.', 'The frame of a table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trestletree', 'n.', 'One of two strong bars of timber, fixed horizontally\n   on the opposite sides of the masthead, to support the crosstrees and\n   the frame of the top; -- generally used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trestlework', 'n.', 'A viaduct, pier, scaffold, or the like, resting on\n   trestles connected together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tres-tyne', 'n.', 'In the antler of a stag, the third tyne above the base.\n   This tyne appears in the third year. In those deer in which the brow\n   tyne does not divide, the tres-tyne is the second tyne above the base.\n   See Illust. under Rucervine, and under Rusine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tret', '', '3d pers. sing. pres. of Tread, for treadeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tret', 'n.', 'An allowance to purchasers, for waste or refuse matter, of\n   four pounds on every 104 pounds of suttle weight, or weight after the\n   tare deducted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tretable', 'a.', 'Tractable; moderate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trething', 'n.', 'A tax; an impost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tretis', 'n.', 'Alt. of Tretys');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tretys', 'n.', 'A treatise; also, a treaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tretis', 'a.', 'Alt. of Tretys');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tretys', 'a.', 'Long and well-proportioned; nicely made; pretty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trevat', 'n.', 'A weavers cutting instrument; for severing the loops of\n   the pile threads of velvet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trevet', 'n.', 'A stool or other thing supported by three legs; a trivet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trew', 'a.', 'Alt. of Trewe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trewe', 'a.', 'True.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trews', 'n. pl.', 'Trowsers; especially, those of the Scotch Highlanders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trewth', 'n.', 'Truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trey', 'n.', 'Three, at cards, dice, or dominoes; a card, die, or domino of\n   three spots or pips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tri-', '', 'A prefix meaning three, thrice, threefold; as in tricolored,\n   tridentate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tri-', '', 'A prefix (also used adjectively) denoting three proportional or\n   combining part, or the third degree of that to the name of which it is\n   prefixed; as in trisulphide, trioxide, trichloride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triable', 'a.', 'Fit or possible to be tried; liable to be subjected to\n   trial or test.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triable', 'a.', 'Liable to undergo a judicial examination; properly coming\n   under the cognizance of a court; as, a cause may be triable before one\n   court which is not triable in another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triableness', 'n.', 'Quality or state of being triable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triacid', 'a.', 'Capable of neutralizing three molecules of a monobasic\n   acid or the equivalent; having three hydrogen atoms which may be acid\n   radicals; -- said of certain bases; thus, glycerin is a triacid base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triacle', 'n.', 'See Treacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triacontahedral', 'a.', 'Having thirty sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triaconter', 'n.', 'A vessel with thirty banks of oars, or, as some say,\n   thirty ranks of rowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triad', 'n.', 'A union of three; three objects treated as one; a ternary; a\n   trinity; as, a triad of deities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triad', 'n.', 'A chord of three notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triad', 'n.', 'The common chord, consisting of a tone with its third and\n   fifth, with or without the octave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triad', 'n.', 'An element or radical whose valence is three.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triadelphous', 'a.', 'Having stamens joined by filaments into three\n   bundles. See Illust. under Adelphous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triadic', 'a.', 'Having the characteristics of a triad; as, boron is\n   triadic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triakisoctahedron', 'n.', 'A trigonal trisoctahedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trial', 'n.', 'The act of trying or testing in any manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trial', 'n.', 'Any effort or exertion of strength for the purpose of\n   ascertaining what can be done or effected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trial', 'n.', 'The act of testing by experience; proof; test.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trial', 'n.', 'Examination by a test; experiment, as in chemistry,\n   metallurgy, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trial', 'n.', 'The state of being tried or tempted; exposure to suffering\n   that tests strength, patience, faith, or the like; affliction or\n   temptation that exercises and proves the graces or virtues of men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trial', 'n.', 'That which tries or afflicts; that which harasses; that\n   which tries the character or principles; that which tempts to evil; as,\n   his childs conduct was a sore trial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trial', 'n.', 'The formal examination of the matter in issue in a cause\n   before a competent tribunal; the mode of determining a question of fact\n   in a court of law; the examination, in legal form, of the facts in\n   issue in a cause pending before a competent tribunal, for the purpose\n   of determining such issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triality', 'n.', 'Three united; state of being three.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trialogue', 'n.', 'A discourse or colloquy by three persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triamide', 'n.', 'An amide containing three amido groups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triamine', 'n.', 'An amine containing three amido groups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triander', 'n.', 'Any one of the Triandria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triandria', 'n. pl.', 'A Linnaean class of plants having three distinct and\n   equal stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triandrian', 'a.', 'Alt. of Triandrous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triandrous', 'a.', 'Of or pertaining to the Triandria; having three\n   distinct and equal stamens in the same flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triangle', 'n.', 'A figure bounded by three lines, and containing three\n   angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triangle', 'n.', 'An instrument of percussion, usually made of a rod of\n   steel, bent into the form of a triangle, open at one angle, and sounded\n   by being struck with a small metallic rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triangle', 'n.', 'A draughtsmans square in the form of a right-angled\n   triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triangle', 'n.', 'A kind of frame formed of three poles stuck in the ground\n   and united at the top, to which soldiers were bound when undergoing\n   corporal punishment, -- now disused.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triangle', 'n.', 'A small constellation situated between Aries and\n   Andromeda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triangle', 'n.', 'A small constellation near the South Pole, containing\n   three bright stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triangled', 'a.', 'Having three angles; triangular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triangular', 'a.', 'Having three angles; having the form of a triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triangular', 'a.', 'Oblong or elongated, and having three lateral angles;\n   as, a triangular seed, leaf, or stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triangulares', 'n. pl.', 'The triangular, or maioid, crabs. See Illust.\n   under Maioid, and Illust. of Spider crab, under Spider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triangularity', 'n.', 'The quality or state of being triangular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triangularly', 'adv.', 'In a triangular manner; in the form of a triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triangulated', 'imp. & p. p.', 'of Triangulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triangulating', 'p. pr. & vb. n.', 'of Triangulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triangulate', 'v. t.', 'To divide into triangles; specifically, to survey\n   by means of a series of triangles properly laid down and measured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triangulate', 'v. t.', 'To make triangular, or three-cornered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triangulation', 'n.', 'The series or network of triangles into which the\n   face of a country, or any portion of it, is divided in a\n   trigonometrical survey; the operation of measuring the elements\n   necessary to determine the triangles into which the country to be\n   surveyed is supposed to be divided, and thus to fix the positions and\n   distances of the several points connected by them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triarchies', 'pl. ', 'of Triarchy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triarchy', 'n.', 'Government by three persons; a triumvirate; also, a\n   country under three rulers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triarian', 'a.', 'Occupying the third post or rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triarticulate', 'a.', 'Having three joints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trias', 'n.', 'The formation situated between the Permian and Lias, and so\n   named by the Germans, because consisting of three series of strata,\n   which are called in German the Bunter sandstein, Muschelkalk, and\n   Keuper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triassic', 'a.', 'Of the age of, or pertaining to, the Trias.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triassic', 'n.', 'The Triassic formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triatic', 'a.', 'A term used in the phrase triatic stay. See under Stay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triatomic', 'a.', 'Having three atoms; -- said of certain elements or\n   radicals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triatomic', 'a.', 'Having a valence of three; trivalent; sometimes, in a\n   specific sense, having three hydroxyl groups, whether acid or basic;\n   thus, glycerin, glyceric acid, and tartronic acid are each triatomic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribal', 'a.', 'Of or pertaining to a tribe or tribes; as, a tribal\n   scepter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribalism', 'n.', 'The state of existing in tribes; also, tribal feeling;\n   tribal prejudice or exclusiveness; tribal peculiarities or\n   characteristics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribasic', 'a.', 'Capable of neutralizing three molecules of a monacid\n   base, or their equivalent; having three hydrogen atoms capable of\n   replacement by basic elements on radicals; -- said of certain acids;\n   thus, citric acid is a tribasic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribble', 'n.', 'A frame on which paper is dried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribe', 'n.', 'A family, race, or series of generations, descending from\n   the same progenitor, and kept distinct, as in the case of the twelve\n   tribes of Israel, descended from the twelve sons of Jacob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribe', 'n.', 'A number of species or genera having certain structural\n   characteristics in common; as, a tribe of plants; a tribe of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribe', 'n.', 'A nation of savages or uncivilized people; a body of rude\n   people united under one leader or government; as, the tribes of the Six\n   Nations; the Seneca tribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribe', 'n.', 'A division, class, or distinct portion of a people, from\n   whatever cause that distinction may have originated; as, the city of\n   Athens was divided into ten tribes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribe', 'n.', 'A family of animals descended from some particular female\n   progenitor, through the female line; as, the Duchess tribe of\n   shorthorns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribe', 'v. t.', 'To distribute into tribes or classes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triblet', 'n.', 'Alt. of Tribolet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribolet', 'n.', 'A goldsmiths tool used in making rings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribolet', 'n.', 'A steel cylinder round which metal is drawn in the\n   process of forming tubes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribolet', 'n.', 'A tapering mandrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribometer', 'n.', 'An instrument to ascertain the degree of friction in\n   rubbing surfaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribrach', 'n.', 'A poetic foot of three short syllables, as, meblius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribracteate', 'a.', 'Having three bracts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribual', 'a.', 'Alt. of Tribular');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribular', 'a.', 'Of or relating to a tribe; tribal; as, a tribual\n   characteristic; tribular worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribulation', 'n.', 'That which occasions distress, trouble, or vexation;\n   severe affliction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribunal', 'n.', 'The seat of a judge; the bench on which a judge and his\n   associates sit for administering justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribunal', 'n.', 'Hence, a court or forum; as, the House of Lords, in\n   England, is the highest tribunal in the kingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribunary', 'a.', 'Of or pertaining to tribunes; as, tribunary powers or\n   authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribunate', 'n.', 'The state or office of a tribune; tribuneship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribune', 'n.', 'An officer or magistrate chosen by the people, to protect\n   them from the oppression of the patricians, or nobles, and to defend\n   their liberties against any attempts that might be made upon them by\n   the senate and consuls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribune', 'n.', 'Anciently, a bench or elevated place, from which speeches\n   were delivered; in France, a kind of pulpit in the hall of the\n   legislative assembly, where a member stands while making an address;\n   any place occupied by a public orator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribuneship', 'n.', 'The office or power of a tribune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribunician', 'a.', 'Alt. of Tribunitian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribunitial', 'a.', 'Alt. of Tribunitian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribunitian', 'a.', 'Of or pertaining to tribunes; befitting a tribune; as,\n   tribunitial power or authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribunitious', 'a.', 'Tribunician; tribunitial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trubutarily', 'adv.', 'In a tributary manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tributariness', 'n.', 'The quality or state of being tributary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tributary', 'a.', 'Paying tribute to another, either from compulsion, as an\n   acknowledgment of submission, or to secure protection, or for the\n   purpose of purchasing peace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tributary', 'a.', 'Hence, subject; subordinate; inferior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tributary', 'a.', 'Paid in tribute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tributary', 'a.', 'Yielding supplies of any kind; serving to form or make\n   up, a greater object of the same kind, as a part, branch, etc.;\n   contributing; as, the Ohio has many tributary streams, and is itself\n   tributary to the Mississippi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tributaries', 'pl. ', 'of Tributary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tributary', 'n.', 'A ruler or state that pays tribute, or a stated sum, to\n   a conquering power, for the purpose of securing peace and protection,\n   or as an acknowledgment of submission, or for the purchase of security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tributary', 'n.', 'A stream or river flowing into a larger river or into a\n   lake; an affluent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribute', 'n.', 'An annual or stated sum of money or other valuable thing,\n   paid by one ruler or nation to another, either as an acknowledgment of\n   submission, or as the price of peace and protection, or by virtue of\n   some treaty; as, the Romans made their conquered countries pay tribute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribute', 'n.', 'A personal contribution, as of money, praise, service,\n   etc., made in token of services rendered, or as that which is due or\n   deserved; as, a tribute of affection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribute', 'n.', 'A certain proportion of the ore raised, or of its value,\n   given to the miner as his recompense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tributed', 'imp. & p. p.', 'of Tribute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tributing', 'p. pr. & vb. n.', 'of Tribute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tribute', 'v. i.', 'To pay as tribute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tributer', 'n.', 'One who works for a certain portion of the ore, or its\n   value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricae', 'pl. ', 'of Trica');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trica', 'n.', 'An apothecium in certain lichens, having a spherical surface\n   marked with spiral or concentric ridges and furrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricarballylic', 'a.', 'Of, pertaining to, or designating, a complex\n   tribasic organic acid, C3H5.(CO2H)3 occurring naturally in unripe beet\n   roots, and produced artificially from glycerin as a white crystalline\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricarbimide', 'n.', 'See under Cyanuric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trice', 'v. t.', 'To pull; to haul; to drag; to pull away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trice', 'v. t.', 'To haul and tie up by means of a rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trice', 'n.', 'A very short time; an instant; a moment; -- now used only in\n   the phrase in a trice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricennarious', 'a.', 'Of or pertaining to thirty years; tricennial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricennial', 'a.', 'Of or pertaining to thirty years; consisting of thirty\n   years; occurring once in every thirty years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricentenary', 'a.', 'Including, or relating to, the interval of three\n   hundred years; tercentenary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricentenary', 'n.', 'A period of three centuries, or three hundred years,\n   also, the three-hundredth anniversary of any event; a tercentenary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triceps', 'n.', 'A muscle having three heads; specif., the great extensor\n   of the forearm, arising by three heads and inserted into the olecranon\n   at the elbow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichiasis', 'n.', 'A disease of the eye, in which the eyelashes, being\n   turned in upon the eyeball, produce constant irritation by the motion\n   of the lids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichinae', 'pl. ', 'of Trichina');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichina', 'n.', 'A small, slender nematoid worm (Trichina spiralis) which,\n   in the larval state, is parasitic, often in immense numbers, in the\n   voluntary muscles of man, the hog, and many other animals. When\n   insufficiently cooked meat containing the larvae is swallowed by man,\n   they are liberated and rapidly become adult, pair, and the\n   ovoviviparous females produce in a short time large numbers of young\n   which find their way into the muscles, either directly, or indirectly\n   by means of the blood. Their presence in the muscles and the intestines\n   in large numbers produces trichinosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichiniasis', 'n.', 'Trichinosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichinize', 'v. t.', 'To render trichinous; to affect with trichinae; --\n   chiefly used in the past participle; as, trichinized pork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichinoscope', 'n.', 'An apparatus for the detection of trichinae in the\n   flesh of animals, as of swine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichinosis', 'n.', 'The disease produced by the presence of trichinae in\n   the muscles and intestinal track. It is marked by fever, muscular\n   pains, and symptoms resembling those of typhoid fever, and is\n   frequently fatal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichinous', 'a.', 'Of or pertaining to trichinae or trichinosis; affected\n   with, or containing, trichinae; as, trichinous meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichite', 'n.', 'A kind of crystallite resembling a bunch of hairs, common\n   in obsidian. See Illust. of Crystallite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichite', 'n.', 'A delicate, hairlike siliceous spicule, found in certain\n   sponges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichiuriform', 'a.', 'Like or pertaining to the genus Trichiurus or family\n   Trichiuridae, comprising the scabbard fishes and hairtails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichiuroid', 'a.', 'Of, like, or pertaining to, Trichiurus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichiurus', 'n.', 'A genus of fishes comprising the hairtails. See\n   Hairtail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichloride', 'n.', 'A chloride having three atoms of chlorine in the\n   molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichobranchia', 'n.', 'The gill of a crustacean in which the branchial\n   filaments are slender and cylindrical, as in the crawfishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichocyst', 'n.', 'A lasso cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichogyne', 'n.', 'The slender, hairlike cell which receives the\n   fertilizing particles, or antherozoids, in red seaweeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichomanes', 'n.', 'Any fern of the genus Trichomanes. The fronds are very\n   delicate and often translucent, and the sporangia are borne on\n   threadlike receptacles rising from the middle of cup-shaped marginal\n   involucres. Several species are common in conservatories; two are\n   native in the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichomatose', 'a.', 'Affected with a disease which causes agglutination\n   and matting together; -- said of the hair when affected with plica. See\n   Plica, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichome', 'n.', 'A hair on the surface of leaf or stem, or any\n   modification of a hair, as a minute scale, or star, or gland. The\n   sporangia of ferns are believed to be of the nature of trichomes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichophore', 'n.', 'The special cell in red algae which produces or bears\n   a trichogyne. See Illust. of Trichogyne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichophore', 'n.', 'One of the saclike organs from which the setae of\n   annelids arise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichopter', 'n.', 'One of the Trichoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichoptera', 'n. pl.', 'A suborder of Neuroptera usually having the wings\n   covered with minute hairs. It comprises the caddice flies, and is\n   considered by some to be a distinct order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichopteran', '', 'One of the Trichoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichopterous', 'a.', 'Of, pertaining to, or characterizing, the\n   Trichoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichord', 'n.', 'An instrument, as a lyre or harp, having three strings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichoscolices', 'n. pl.', 'An extensive group of wormlike animals\n   characterized by being more or less covered with cilia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichotomous', 'a.', 'Divided into three parts, or into threes;\n   three-forked; as, a trichotomous stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichotomy', 'n.', 'Division into three parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichroic', 'a.', 'Exhibiting trichroism; pleochroic; pleochroism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichroism', 'n.', 'The quality possessed by some crystals of presenting\n   different colors in three different directions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichromatic', 'a.', 'Having or existing in three different phases of\n   color; having three distinct color varieties; -- said of certain birds\n   and insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichromatism', 'n.', 'The quality, state, or phenomenon of being\n   trichromatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichromic', 'a.', 'Of, pertaining to, or consisting of, three colors or\n   color sensations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trichromic', 'a.', 'Containing three atoms of chromium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricipital', 'a.', 'Having three heads, or three origins; as, a tricipital\n   muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trick', 'a.', 'An artifice or stratagem; a cunning contrivance; a sly\n   procedure, usually with a dishonest intent; as, a trick in trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trick', 'a.', 'A sly, dexterous, or ingenious procedure fitted to puzzle or\n   amuse; as, a bears tricks; a jugglers tricks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trick', 'a.', 'Mischievous or annoying behavior; a prank; as, the tricks of\n   boys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trick', 'a.', 'A particular habit or manner; a peculiarity; a trait; as, a\n   trick of drumming with the fingers; a trick of frowning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trick', 'a.', 'A knot, braid, or plait of hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trick', 'a.', 'The whole number of cards played in one round, and\n   consisting of as many cards as there are players.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trick', 'a.', 'A turn; specifically, the spell of a sailor at the helm, --\n   usually two hours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trick', 'a.', 'A toy; a trifle; a plaything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricked', 'imp. & p. p.', 'of Trick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricking', 'p. pr. & vb. n.', 'of Trick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trick', 'v. t.', 'To deceive by cunning or artifice; to impose on; to\n   defraud; to cheat; as, to trick another in the sale of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trick', 'v. t.', 'To dress; to decorate; to set off; to adorn\n   fantastically; -- often followed by up, off, or out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trick', 'v. t.', 'To draw in outline, as with a pen; to delineate or\n   distinguish without color, as arms, etc., in heraldry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricker', 'n.', 'One who tricks; a trickster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricker', 'n.', 'A trigger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trickery', 'n.', 'The art of dressing up; artifice; stratagem; fraud;\n   imposture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trickiness', 'n.', 'The quality of being tricky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricking', 'a.', 'Given to tricks; tricky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricking', 'n.', 'Dress; ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trickish', 'a.', 'Given to tricks; artful in making bargains; given to\n   deception and cheating; knavish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trickled', 'imp. & p. p.', 'of Trickle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trickling', 'p. pr. & vb. n.', 'of Trickle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trickle', 'v. t.', 'To flow in a small, gentle stream; to run in drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trickment', 'n.', 'Decoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricksiness', 'n.', 'The quality or state of being tricksy; trickiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trickster', 'n.', 'One who tricks; a deceiver; a tricker; a cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricksy', 'a.', 'Exhibiting artfulness; trickish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricktrack', 'n.', 'An old game resembling backgammon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricky', 'a.', 'Given to tricks; practicing deception; trickish; knavish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triclinate', 'a.', 'Triclinic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricliniary', 'a.', 'Of or pertaining to a triclinium, or to the ancient\n   mode of reclining at table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triclinic', 'a.', 'Having, or characterized by, three unequal axes\n   intersecting at oblique angles. See the Note under crystallization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triclinia', 'pl. ', 'of Triclinium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triclinium', 'n.', 'A couch for reclining at meals, extending round three\n   sides of a table, and usually in three parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triclinium', 'n.', 'A dining room furnished with such a triple couch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricoccous', 'a.', 'Having three cocci, or roundish carpels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricolor', 'n.', 'The national French banner, of three colors, blue, white,\n   and red, adopted at the first revolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricolor', 'n.', 'Hence, any three-colored flag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricolored', 'a.', 'Having three colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricornigerous', 'a.', 'Having three horns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricorporal', 'a.', 'Alt. of Tricorporate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricorporate', 'a.', 'Represented with three bodies conjoined to one head,\n   as a lion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricostate', 'a.', 'Three-ribbed; having three ribs from the base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricot', 'n.', 'A fabric of woolen, silk, or cotton knitted, or women to\n   resemble knitted work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricrotic', 'a.', 'Of or pertaining to tricrotism; characterized by\n   tricrotism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricrotism', 'n.', 'That condition of the arterial pulse in which there is\n   a triple beat. The pulse curve obtained in the sphygmographic tracing\n   characteristic of tricrotism shows two secondary crests in addition to\n   the primary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricrotous', 'a.', 'Tricrotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricurvate', 'a.', 'Curved in three directions; as, a tricurvate spicule\n   (see Illust. of Spicule).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricuspid', 'a.', 'Having three cusps, or points; tricuspidate; as, a\n   tricuspid molar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricuspid', 'a.', 'Of or pertaining to the tricuspid valves; as, tricuspid\n   obstruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricuspidate', 'a.', 'Three-pointed; ending in three points; as, a\n   tricuspidate leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tricycle', 'n.', 'A three-wheeled velocipede. See Illust. under Velocipede.\n   Cf. Bicycle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tridacna', 'n.', 'A genus of very large marine bivalve shells found on the\n   coral reefs of the Indian and Pacific Oceans. One species (T. gigas)\n   often weighs four or five hundred pounds, and is sometimes used for\n   baptismal fonts. Called also paw shell, and fountain shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tridactyl', 'a.', 'Alt. of Tridactyle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tridactyle', 'a.', 'Having three fingers or toes, or composed of three\n   movable parts attached to a common base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tridactylous', 'a.', 'Tridactyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triddler', 'n.', 'The jacksnipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tride', 'a.', 'Short and ready; fleet; as, a tride pace; -- a term used by\n   sportsmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tridecane', 'n.', 'A hydrocarbon, C13H28, of the methane series, which is a\n   probable ingredient both of crude petroleum and of kerosene, and is\n   produced artificially as a light colorless liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tridecatoic', 'a.', 'Of, pertaining to, or designating, that acid of the\n   fatty acids heterologous with tridecane. It is a white crystalline\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tridecatylene', 'n.', 'A hydrocarbon, C13H26, of the ethylene series,\n   corresponding to tridecane, and obtained from Burmah petroleum as a\n   light colorless liquid; -- called also tridecylene, and tridecene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trident', 'n.', 'A kind of scepter or spear with three prongs, -- the\n   common attribute of Neptune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trident', 'n.', 'A three-pronged spear or goad, used for urging horses;\n   also, the weapon used by one class of gladiators.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trident', 'n.', 'A three-pronged fish spear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trident', 'n.', 'A curve of third order, having three infinite branches in\n   one direction and a fourth infinite branch in the opposite direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trident', 'a.', 'Having three teeth or prongs; tridentate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tridentate', 'a.', 'Alt. of Tridentated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tridentated', 'a.', 'Having three teeth; three-toothed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tridented', 'a.', 'Having three prongs; trident; tridentate; as, a\n   tridented mace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tridentiferous', 'a.', 'Bearing a trident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tridentine', 'a.', 'Of or pertaining to Trent, or the general church\n   council held in that city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tridiapason', 'n.', 'A triple octave, or twenty-second.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tridimensional', 'a.', 'Having three dimensions; extended in three\n   different directions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triding', 'n.', 'A riding. See Trithing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triduan', 'a.', 'Lasting three lays; also, happening every third day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tridymite', 'n.', 'Pure silica, like quartz, but crystallizing in hexagonal\n   tables. It is found in trachyte and similar rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tried', '', 'imp. & p. p. of Try.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tried', 'adj.', 'Proved; tested; faithful; trustworthy; as, a tried friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triedral', 'a.', 'See Trihedral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triennial', 'a.', 'Continuing three years; as, triennial parliaments; a\n   triennial reign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triennial', 'a.', 'Happening, coming about, or appearing once in every\n   three years; as, triennial elections; a triennial catalogue; a\n   triennial visitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triennial', 'n.', 'Something which takes place or appears once in three\n   years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triennially', 'adv.', 'Once in three years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triens', 'n.', 'A Roman copper coin, equal to one third of the as. See 3d\n   As, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trier', 'n.', 'One who tries; one who makes experiments; one who examines\n   anything by a test or standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trier', 'n.', 'One who tries judicially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trier', 'n.', 'A person appointed according to law to try challenges of\n   jurors; a trior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trier', 'n.', 'That which tries or approves; a test.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trierarch', 'n.', 'The commander of a trireme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trierarch', 'n.', 'At Athens, one who (singly, or jointly with other\n   citizens) had to fit out a trireme for the public service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trierarchises', 'pl. ', 'of Trierarchy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trierarchy', 'n.', 'The office duty of a trierarch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trieterical', 'a.', 'Kept or occurring once in three years; triennial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trieterics', 'n. pl.', 'Festival games celebrated once in three years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triethylamine', 'n.', 'A tertiary amine analogous to trimethylamine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifacial', 'a.', 'See Trigeminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifallowed', 'imp. & p. p.', 'of Trifallow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifallowing', 'p. pr. & vb. n.', 'of Trifallow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifallow', 'v. t.', 'To plow the third time before sowing, as land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifarious', 'a.', 'Facing three ways; arranged in three vertical ranks, as\n   the leaves of veratrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifasciated', 'a.', 'Having, or surrounded by, three fasciae, or bands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifid', 'a.', 'Cleft to the middle, or slightly beyond the middle, into\n   three parts; three-cleft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifistulary', 'a.', 'Having three pipes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifle', 'n.', 'A thing of very little value or importance; a paltry, or\n   trivial, affair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifle', 'n.', 'A dish composed of sweetmeats, fruits, cake, wine, etc.,\n   with syllabub poured over it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifled', 'imp. & p. p.', 'of Trifle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifling', 'p. pr. & vb. n.', 'of Trifle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifle', 'n.', 'To act or talk without seriousness, gravity, weight, or\n   dignity; to act or talk with levity; to indulge in light or trivial\n   amusements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifle', 'v. t.', 'To make of no importance; to treat as a trifle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifle', 'v. t.', 'To spend in vanity; to fritter away; to waste; as, to\n   trifle away money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifler', 'n.', 'One who trifles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifling', 'a.', 'Being of small value or importance; trivial; paltry; as,\n   a trifling debt; a trifling affair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifloral', 'a.', 'Alt. of Triflorous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triflorous', 'a.', 'Three-flowered; having or bearing three flowers; as, a\n   triflorous peduncle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifluctuation', 'n.', 'A concurrence of three waves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifoliate', 'a.', 'Alt. of Trifoliated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifoliated', 'a.', 'Having three leaves or leaflets, as clover. See\n   Illust. of Shamrock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifoliolate', 'a.', 'Having three leaflets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifolium', 'n.', 'A genus of leguminous herbs with densely spiked flowers\n   and usually trifoliate leaves; trefoil. There are many species, all of\n   which are called clover. See Clover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifoly', 'n.', 'Sweet trefoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triforium', 'n.', 'The gallery or open space between the vaulting and the\n   roof of the aisles of a church, often forming a rich arcade in the\n   interior of the church, above the nave arches and below the clearstory\n   windows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triform', 'a.', 'Having a triple form or character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triformity', 'n.', 'The state of being triform, or of having a threefold\n   shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifurcate', 'a.', 'Alt. of Trifurcated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trifurcated', 'a.', 'Having three branches or forks; trichotomous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trig', 'v. t.', 'To fill; to stuff; to cram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trig', 'a.', 'Full; also, trim; neat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trig', 'v. t.', 'To stop, as a wheel, by placing something under it; to\n   scotch; to skid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trig', 'n.', 'A stone, block of wood, or anything else, placed under a\n   wheel or barrel to prevent motion; a scotch; a skid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigamist', 'n.', 'One who has been married three times; also, one who has\n   three husbands or three wives at the same time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigamous', 'a.', 'Having three sorts of flowers in the same head, -- male,\n   female, and hermaphrodite, or perfect, flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigamy', 'n.', 'The act of marrying, or the state of being married, three\n   times; also, the offense of having three husbands or three wives at the\n   same time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigastric', 'a.', 'Having three bellies; -- said of a muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigeminal', 'a.', 'Of, pertaining to, or designating, the fifth pair of\n   cranial nerves, which divide on each side of the head into three main\n   branches distributed to the orbits, jaws, and parts of the mouth;\n   trifacial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigeminous', 'a.', 'Born three together; being one of three born at the\n   same birth; also, threefold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigenic', 'a.', 'Of, pertaining to, or designating, an acid, C4H7N3O2,\n   obtained, by the action of the vapor of cyanic acid on cold aldehyde,\n   as a white crystalline substance having a slightly acid taste and faint\n   smell; -- called also ethidene- / ethylidene-biuret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigesimo-secundo', 'a.', 'Having thirty-two leaves to a sheet; as, a\n   trigesimo-secundo form, book, leaf, size, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigesimo-secundo', 'n.', 'A book composed of sheets so folded that each\n   one makes thirty-two leaves; hence, indicating, more or less\n   definitely, a size of book; -- usually written 32mo, or 32, and called\n   thirty-twomo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigger', 'n.', 'A catch to hold the wheel of a carriage on a declivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigger', 'n.', 'A piece, as a lever, which is connected with a catch or\n   detent as a means of releasing it; especially (Firearms), the part of a\n   lock which is moved by the finger to release the cock and discharge the\n   piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigintal', 'n.', 'A trental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triglyceride', 'n.', 'A glyceride formed by the replacement of three\n   hydrogen atoms in glycerin by acid radicals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triglyph', 'n.', 'An ornament in the frieze of the Doric order, repeated at\n   equal intervals. Each triglyph consists of a rectangular tablet,\n   slightly projecting, and divided nearly to the top by two parallel and\n   perpendicular gutters, or channels, called glyphs, into three parts, or\n   spaces, called femora. A half channel, or glyph, is also cut upon each\n   of the perpendicular edges of the tablet. See Illust. of Entablature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triglyphic', 'a.', 'Alt. of Triglyphical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triglyphical', 'a.', 'Consisting of, or pertaining to, triglyphs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triglyphical', 'a.', 'Containing three sets of characters or sculptures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigness', 'n.', 'The quality or state of being trig; smartness; neatness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigon', 'n.', 'A figure having three angles; a triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigon', 'n.', 'A division consisting of three signs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigon', 'n.', 'Trine, an aspect of two planets distant 120 degrees from\n   each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigon', 'n.', 'A kind of triangular lyre or harp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigon', 'n.', 'A kind of game at ball played by three persons standing at\n   the angular points of a triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigonal', 'a.', 'Having three angles, or corners; triangular; as, a\n   trigonal stem, one having tree prominent longitudinal angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigone', 'n.', 'A smooth triangular area on the inner surface of the\n   bladder, limited by the apertures of the ureters and urethra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigonia', 'n.', 'A genus of pearly bivalve shells, numerous extinct\n   species of which are characteristic of the Mesozoic rocks. A few living\n   species exist on the coast of Australia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigonocerous', 'a.', 'Having horns with three angles, like those of some\n   species of goats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigonometric', '', 'Alt. of Trigonometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigonometrical', '', 'Of or pertaining to trigonometry; performed by the\n   rules of trigonometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-tries', 'pl. ', 'of Trigonometry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigonometry', 'n.', 'That branch of mathematics which treats of the\n   relations of the sides and angles of triangles, which the methods of\n   deducing from certain given parts other required parts, and also of the\n   general relations which exist between the trigonometrical functions of\n   arcs or angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigonometry', 'n.', 'A treatise in this science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigonous', 'a.', 'Same as Trigonal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigram', 'n.', 'Same as Trigraph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigrammatic', 'a.', 'Containing three letters or characters, or three sets\n   of letters or characters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigrammic', 'a.', 'Same as Trigrammatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigraph', 'n.', 'Three letters united in pronunciation so as to have but\n   one sound, or to form but one syllable, as -ieu in adieu; a triphthong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigyn', 'n.', 'Any one of the Trigynia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigynia', 'n. pl.', 'A Linnaean order of plants having three pistils or\n   styles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigynian', 'a.', 'Alt. of Trigynous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trigynous', 'a.', 'Having three pistils or styles; of or pertaining to the\n   Trigynia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trihedral', 'a.', 'Having three sides or faces; thus, a trihedral angle is\n   a solid angle bounded by three plane angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trihedron', 'n.', 'A figure having three sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trihoral', 'a.', 'Occurring once in every three hours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trijugate', 'a.', 'In three pairs; as, a trijugate leaf, or a pinnate leaf\n   with three pairs of leaflets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trijugous', 'a.', 'Same as Trijugate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trikosane', 'n.', 'A hydrocarbon, C23H48, of the methane series, resembling\n   paraffin; -- so called because it has twenty-three atoms of carbon in\n   the molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilateral', 'a.', 'Having three sides; being three-sided; as, a trilateral\n   triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilemma', 'n.', 'A syllogism with three conditional propositions, the\n   major premises of which are disjunctively affirmed in the minor. See\n   Dilemma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilemma', 'n.', 'A state of things in which it is difficult to determine\n   which one of three courses to pursue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilinear', 'a.', 'Of, pertaining to, or included by, three lines; as,\n   trilinear coordinates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilingual', 'a.', 'Containing, or consisting of, three languages;\n   expressed in three languages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilinguar', 'a.', 'See Trilingual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triliteral', 'a.', 'Consisting of three letters; trigrammic; as, a\n   triliteral root or word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triliteral', 'n.', 'A triliteral word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triliteralism', 'n.', 'Same as Triliterality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triliterality', 'n.', 'Alt. of Triliteralness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triliteralness', 'n.', 'The quality of being triliteral; as, the\n   triliterality of Hebrew roots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilith', 'n.', 'Same as Trilithon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilithic', 'a.', 'Pertaining to a trilith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilithons', 'pl. ', 'of Trilithon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilithon', 'n.', 'A monument consisting of three stones; especially, such\n   a monument forming a kind of doorway, as among the ancient Celts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trill', 'v. i.', 'To flow in a small stream, or in drops rapidly succeeding\n   each other; to trickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trill', 'v. t.', 'To turn round; to twirl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilled', 'imp. & p. p.', 'of Trill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilling', 'p. pr. & vb. n.', 'of Trill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trill', 'v. t.', 'To impart the quality of a trill to; to utter as, or\n   with, a trill; as, to trill the r; to trill a note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trill', 'v. i.', 'To utter trills or a trill; to play or sing in tremulous\n   vibrations of sound; to have a trembling sound; to quaver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trill', 'n.', 'A sound, of consonantal character, made with a rapid\n   succession of partial or entire intermissions, by the vibration of some\n   one part of the organs in the mouth -- tongue, uvula, epiglottis, or\n   lip -- against another part; as, the r is a trill in most languages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trill', 'n.', 'The action of the organs in producing such sounds; as, to\n   give a trill to the tongue. d');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trill', 'n.', 'A shake or quaver of the voice in singing, or of the sound\n   of an instrument, produced by the rapid alternation of two contiguous\n   tones of the scale; as, to give a trill on the high C. See Shake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trillachan', 'n.', 'The oyster catcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilling', 'n.', 'One of tree children born at the same birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilling', 'n.', 'A compound crystal, consisting of three individuals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trillion', 'n.', 'According to the French notation, which is used upon the\n   Continent generally and in the United States, the number expressed by a\n   unit with twelve ciphers annexed; a million millions; according to the\n   English notation, the number produced by involving a million to the\n   third power, or the number represented by a unit with eighteen ciphers\n   annexed. See the Note under Numeration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trillium', 'n.', 'A genus of liliaceous plants; the three-leaved\n   nightshade; -- so called because all the parts of the plant are in\n   threes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trillo', 'n.', 'A trill or shake. See Trill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilobate', 'a.', 'Having three lobes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilobation', 'n.', 'The state of being trilobate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilobed', 'a.', 'Same as Trilobate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilobita', 'n. pl.', 'An extinct order of arthropods comprising the\n   trilobites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilobite', 'n.', 'Any one of numerous species of extinct arthropods\n   belonging to the order Trilobita. Trilobites were very common in the\n   Silurian and Devonian periods, but became extinct at the close of the\n   Paleozoic. So named from the three lobes usually seen on each segment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilobitic', 'a.', 'Of, pertaining to or containing, trilobites; as,\n   trilobitic rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilocular', 'a.', 'Having three cells or cavities; as, a trilocular\n   capsule; a trilocular heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trilogy', 'n.', 'A series of three dramas which, although each of them is\n   in one sense complete, have a close mutual relation, and form one\n   historical and poetical picture. Shakespeares \" Henry VI.\" is an\n   example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triluminar', 'a.', 'Alt. of Triluminous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triluminous', 'a.', 'Having three lights');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimmed', 'imp. & p. p.', 'of Trim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimming', 'p. pr. & vb. n.', 'of Trim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trim', 'v. t.', 'To make trim; to put in due order for any purpose; to make\n   right, neat, or pleasing; to adjust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trim', 'v. t.', 'To dress; to decorate; to adorn; to invest; to embellish;\n   as, to trim a hat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trim', 'v. t.', 'To make ready or right by cutting or shortening; to clip\n   or lop; to curtail; as, to trim the hair; to trim a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trim', 'v. t.', 'To dress, as timber; to make smooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trim', 'v. t.', 'To adjust, as a ship, by arranging the cargo, or disposing\n   the weight of persons or goods, so equally on each side of the center\n   and at each end, that she shall sit well on the water and sail well;\n   as, to trim a ship, or a boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trim', 'v. t.', 'To arrange in due order for sailing; as, to trim the\n   sails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trim', 'v. t.', 'To rebuke; to reprove; also, to beat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trim', 'v. i.', 'To balance; to fluctuate between parties, so as to appear\n   to favor each.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trim', 'n.', 'Dress; gear; ornaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trim', 'n.', 'Order; disposition; condition; as, to be in good trim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trim', 'n.', 'The state of a ship or her cargo, ballast, masts, etc., by\n   which she is well prepared for sailing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trim', 'n.', 'The lighter woodwork in the interior of a building;\n   especially, that used around openings, generally in the form of a\n   molded architrave, to protect the plastering at those points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trim', 'v. t.', 'Fitly adjusted; being in good order., or made ready for\n   service or use; firm; compact; snug; neat; fair; as, the ship is trim,\n   or trim built; everything about the man is trim; a person is trim when\n   his body is well shaped and firm; his dress is trim when it fits\n   closely to his body, and appears tight and snug; a man or a soldier is\n   trim when he stands erect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimaculated', 'a.', 'Marked with three spots, or maculae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimellic', 'a.', 'Of, pertaining to, or designating, a certain tribasic\n   acid (called also trimellitic acid) metameric with trimesitic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimembral', 'a.', 'Having, or consisting of, three members.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimera', 'n. pl.', 'A division of Coleoptera including those which have\n   but three joints in the tarsi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimeran', 'n.', 'One of the Trimera. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimerous', 'a.', 'Having the parts in threes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimesitic', 'a.', 'Of, pertaining to, or designating, a tribasic acid,\n   C6H3.(CO2)3, of the aromatic series, obtained, by the oxidation of\n   mesitylene, as a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimester', 'n.', 'A term or period of three months.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimestral', 'a.', 'Trimestrial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimestrial', 'a.', 'Of or pertaining to a trimester, or period of three\n   months; occurring once in every three months; quarterly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimeter', 'a.', 'Consisting of three poetical measures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimeter', 'n.', 'A poetical division of verse, consisting of three\n   measures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimethyl', '', 'A prefix or combining form (also used adjectively)\n   indicating the presence of three methyl groups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimethylamine', 'n.', 'A colorless volatile alkaline liquid, N.(CH3)3,\n   obtained from herring brine, beet roots, etc., with a characteristic\n   herringlike odor. It is regarded as a substituted ammonia containing\n   three methyl groups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimethylene', 'n.', 'A gaseous hydrocarbon, C3H6, isomeric with propylene\n   and obtained from it indirectly. It is the base of a series of\n   compounds analogous to the aromatic hydrocarbons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimetric', 'a.', 'Same as Orthorhombic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimetrical', 'a.', 'Same as Trimeter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimly', 'adv.', 'In a trim manner; nicely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimmer', 'n.', 'One who trims, arranges, fits, or ornaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimmer', 'n.', 'One who does not adopt extreme opinions in politics, or\n   the like; one who fluctuates between parties, so as to appear to favor\n   each; a timeserver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimmer', 'n.', 'An instrument with which trimming is done.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimmer', 'n.', 'A beam, into which are framed the ends of headers in floor\n   framing, as when a hole is to be left for stairs, or to avoid bringing\n   joists near chimneys, and the like. See Illust. of Header.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimming', '', 'a. from Trim, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimming', 'n.', 'The act of one who trims.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimming', 'n.', 'That which serves to trim, make right or fitting, adjust,\n   ornament, or the like; especially, the necessary or the ornamental\n   appendages, as of a garment; hence, sometimes, the concomitants of a\n   dish; a relish; -- usually in the pluraltrimmings. --.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimming', 'n.', 'The act of reprimanding or chastisting; as, to give a boy\n   a trimming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimmingly', 'adv.', 'In a trimming manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimness', 'n.', 'The quality or state of being trim; orderliness;\n   compactness; snugness; neatness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimorph', 'n.', 'A substance which crystallizes in three distinct forms,\n   or which has three distinct physical states; also, any one of these\n   distinct forms. See Trimorphism, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimorphic', 'a.', 'Alt. of Trimorphous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimorphous', 'a.', 'Of, pertaining to, or characterized by, trimorphism;\n   -- contrasted with monomorphic, dimorphic, and polymorphic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimorphism', 'n.', 'The property of crystallizing in three forms\n   fundamentally distinct, as is the case with titanium dioxide, which\n   crystallizes in the forms of rutile, octahedrite, and brookite. See\n   Pleomorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimorphism', 'n.', 'The coexistence among individuals of the same species\n   of three distinct forms, not connected, as a rule, by intermediate\n   gradations; the condition among individuals of the same species of\n   having three different shapes or proportions of corresponding parts; --\n   contrasted with polymorphism, and dimorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimurti', 'n.', 'The triad, or trinity, of Hindu gods, consisting of\n   Brahma, the Creator, Vishnu, the Preserver, and Siva, the Destroyer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trimyarian', 'n.', 'A lamellibranch which has three muscular scars on each\n   valve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinal', 'a.', 'Threefold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trindle', 'v. t. & n.', 'See Trundle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trine', 'a.', 'Threefold; triple; as, trine dimensions, or length, breadth,\n   and thickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trine', 'n.', 'The aspect of planets distant from each other 120 degrees,\n   or one third of the zodiac; trigon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trine', 'n.', 'A triad; trinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trine', 'v. t.', 'To put in the aspect of a trine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinervate', 'a.', 'Having three ribs or nerves extending unbranched from\n   the base to the apex; -- said of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinerve', 'a.', 'Alt. of Trinerved');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinerved', 'a.', 'Same as Trinervate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tringa', 'n.', 'A genus of limicoline birds including many species of\n   sandpipers. See Dunlin, Knot, and Sandpiper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tringle', 'n.', 'A curtain rod for a bedstead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tringoid', 'a.', 'Of or pertaining to Tringa, or the Sandpiper family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinitarian', 'a.', 'Of or pertaining to the Trinity, the doctrine of the\n   Trinity, or believers in that doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinitarian', 'n.', 'One who believes in the doctrine of the Trinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinitarian', 'n.', 'One of a monastic order founded in Rome in 1198 by St.\n   John of Matha, and an old French hermit, Felix of Valois, for the\n   purpose of redeeming Christian captives from the Mohammedans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinitarianism', 'n.', 'The doctrine of the Trinity; the doctrine that\n   there are three distinct persons in the Godhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinitrocellulose', 'n.', 'Gun cotton; -- so called because regarded as\n   containing three nitro groups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinitrophenol', 'n.', 'Picric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinity', 'n.', 'The union of three persons (the Father, the Son, and the\n   Holy Ghost) in one Godhead, so that all the three are one God as to\n   substance, but three persons as to individuality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinity', 'n.', 'Any union of three in one; three units treated as one; a\n   triad, as the Hindu trinity, or Trimurti.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinity', 'n.', 'Any symbol of the Trinity employed in Christian art,\n   especially the triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triniunity', 'n.', 'Triunity; trinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trink', 'n.', 'A kind of fishing net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinket', 'n.', 'A three-cornered sail formerly carried on a ships\n   foremast, probably on a lateen yard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinket', 'v. t.', 'A knife; a cutting tool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinket', 'v. t.', 'A small ornament, as a jewel, ring, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinket', 'v. t.', 'A thing of little value; a trifle; a toy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinket', 'v. i.', 'To give trinkets; hence, to court favor; to intrigue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinketer', 'n.', 'One who trinkets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinketry', 'n.', 'Ornaments of dress; trinkets, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinkle', 'v. i.', 'To act secretly, or in an underhand way; to tamper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinoctial', 'a.', 'Lasting during three nights; comprising three nights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinodal', 'a.', 'Having three knots or nodes; having three points from\n   which a leaf may shoot; as, a trinodal stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinodal', 'a.', 'Having three nodal points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinomial', 'n.', 'A quantity consisting of three terms, connected by the\n   sign + or -; as, x + y + z, or ax + 2b - c2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinomial', 'a.', 'Consisting of three terms; of or pertaining to\n   trinomials; as, a trinomial root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinominal', 'n. & a.', 'Trinomial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trinucleus', 'n.', 'A genus of Lower Silurian trilobites in which the\n   glabella and cheeks form three rounded elevations on the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trio', 'n.', 'Three, considered collectively; three in company or acting\n   together; a set of three; three united.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trio', 'n.', 'A composition for three parts or three instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trio', 'n.', 'The secondary, or episodical, movement of a minuet or\n   scherzo, as in a sonata or symphony, or of a march, or of various dance\n   forms; -- not limited to three parts or instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triobolar', 'a.', 'Alt. of Triobolary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triobolary', 'a.', 'Of the value of three oboli; hence, mean; worthless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trioctile', 'n.', 'An aspect of two planets with regard to the earth when\n   they are three octants, or three eighths of a circle, that is, 135\n   degrees, distant from each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tri/cia', 'n. pl.', 'The third order of the Linnaean class Polygamia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tri/cious', 'a.', 'Having three sorts of flowers on the same or on\n   different plants, some of the flowers being staminate, others\n   pistillate, and others both staminate and pistillate; belonging to the\n   order Tri/cia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triole', 'n.', 'Same as Triplet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triolein', 'n.', 'See Olein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triolet', 'n.', 'A short poem or stanza of eight lines, in which the first\n   line is repeated as the fourth and again as the seventh line, the\n   second being, repeated as the eighth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trionychoidea', 'n. pl.', 'A division of chelonians which comprises Trionyx\n   and allied genera; -- called also Trionychoides, and Trionychina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trionyx', 'n.', 'A genus of fresh-water or river turtles which have the\n   shell imperfectly developed and covered with a soft leathery skin. They\n   are noted for their agility and rapacity. Called also soft tortoise,\n   soft-shell tortoise, and mud turtle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trior', 'n.', 'Same as Trier, 2 and 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trioxide', 'n.', 'An oxide containing three atoms of oxygen; as, sulphur\n   trioxide, SO3; -- formerly called tritoxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripped', 'imp. & p. p.', 'of Trip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripping', 'p. pr. & vb. n.', 'of Trip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip', 'n. i.', 'To move with light, quick steps; to walk or move lightly;\n   to skip; to move the feet nimbly; -- sometimes followed by it. See It,\n   5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip', 'n. i.', 'To make a brief journey or pleasure excursion; as, to trip\n   to Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip', 'n. i.', 'To take a quick step, as when in danger of losing ones\n   balance; hence, to make a false; to catch the foot; to lose footing; to\n   stumble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip', 'n. i.', 'Fig.: To be guilty of a misstep; to commit an offense\n   against morality, propriety, or rule; to err; to mistake; to fail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip', 'v. t.', 'To cause to stumble, or take a false step; to cause to\n   lose the footing, by striking the feet from under; to cause to fall; to\n   throw off the balance; to supplant; -- often followed by up; as, to\n   trip up a man in wrestling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip', 'v. t.', 'Fig.: To overthrow by depriving of support; to put an\n   obstacle in the way of; to obstruct; to cause to fail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip', 'v. t.', 'To detect in a misstep; to catch; to convict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip', 'v. t.', 'To raise (an anchor) from the bottom, by its cable or buoy\n   rope, so that it hangs free.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip', 'v. t.', 'To pull (a yard) into a perpendicular position for\n   lowering it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip', 'v. t.', 'To release, let fall, or see free, as a weight or\n   compressed spring, as by removing a latch or detent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip', 'n.', 'A quick, light step; a lively movement of the feet; a skip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip', 'n.', 'A brief or rapid journey; an excursion or jaunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip', 'n.', 'A false step; a stumble; a misstep; a loss of footing or\n   balance. Fig.: An error; a failure; a mistake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip', 'n.', 'A small piece; a morsel; a bit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip', 'n.', 'A stroke, or catch, by which a wrestler causes his antagonist\n   to lose footing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip', 'n.', 'A single board, or tack, in plying, or beating, to windward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip', 'n.', 'A herd or flock, as of sheep, goats, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip', 'n.', 'A troop of men; a host.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip', 'n.', 'A flock of widgeons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripalmitate', 'n.', 'A palmitate derived from three molecules of palmitic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripalmitin', 'n.', 'See Palmitin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripang', 'n.', 'See Trepang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triparted', 'a.', 'Parted into three piece; having three parts or pieces;\n   -- said of the field or of a bearing; as, a cross triparted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triparted', 'a.', 'Divided nearly to the base into three segments or lobes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripartible', 'a.', 'Divisible into three parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripartient', 'a.', 'Dividing into three parts; -- said of a number which\n   exactly divides another into three parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripartite', 'v. i.', 'Divided into three parts; triparted; as, a\n   tripartite leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripartite', 'v. i.', 'Having three corresponding parts or copies; as, to\n   make indentures tripartite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripartite', 'v. i.', 'Made between three parties; as, a tripartite treaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripartitely', 'adv.', 'In a tripartite manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripartition', 'n.', 'A division by threes, or into three parts; the taking\n   of a third part of any number or quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripaschal', 'a.', 'Including three passovers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripe', 'n.', 'The large stomach of ruminating animals, when prepared for\n   food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripe', 'n.', 'The entrails; hence, humorously or in contempt, the belly;\n   -- generally used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripedal', 'a.', 'Having three feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripe-de-roche', 'n.', 'Same as Rock tripe, under Rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripel', 'n.', 'Same as Tripoli.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-men', 'pl. ', 'of Tripeman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripeman', 'n.', 'A man who prepares or sells tripe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripennate', 'a.', 'Same as Tripinnate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripersonal', 'a.', 'Consisting of three persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripersonalist', 'n.', 'A Trinitarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripersonality', 'n.', 'The state of existing as three persons in one\n   Godhead; trinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripery', 'n.', 'A place where tripe is prepared or sold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripestone', 'n.', 'A variety of anhydrite composed of contorted plates\n   fancied to resemble pieces of tripe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripetaloid', 'a.', 'Having the form or appearance of three petals;\n   appearing as if furnished with three petals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripetalous', 'a.', 'Having three petals, or flower leaves; three-petaled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trip hammer', '', 'A tilt hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triphane', 'n.', 'Spodumene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triphthong', 'n.', 'A combination of three vowel sounds in a single\n   syllable, forming a simple or compound sound; also, a union of three\n   vowel characters, representing together a single sound; a trigraph; as,\n   eye, -ieu in adieu, -eau in beau, are examples of triphthongs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triphthongal', 'a.', 'Of or pertaining to a triphthong; consisting of three\n   vowel sounds pronounced together in a single syllable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triphyline', 'n.', 'Triphylite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triphylite', 'n.', 'A mineral of a grayish-green or bluish color,\n   consisting of the phosphates of iron, manganese, and lithia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triphyllous', 'a.', 'Having three leaves; three-leaved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripinnate', 'a.', 'Having bipinnate leaflets arranged on each side of a\n   rhachis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripinnatifid', 'a.', 'Thrice pinnately cleft; -- said of a pinnatifid leaf\n   when its segments are pinnatifid, and the subdivisions of these also\n   are pinnatifid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triplasian', 'a.', 'Three-fold; triple; treble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triple', 'a.', 'Consisting of three united; multiplied by three; threefold;\n   as, a triple knot; a triple tie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triple', 'a.', 'Three times repeated; treble. See Treble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triple', 'a.', 'One of three; third.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripled', 'imp. & p. p.', 'of Triple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripling', 'p. pr. & vb. n.', 'of Triple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triple', 'a.', 'To make threefold, or thrice as much or as many; to treble;\n   as, to triple the tax on coffee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triple-crowned', 'a.', 'Having three crowns; wearing the triple crown, as\n   the pope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triple-headed', 'a.', 'Having three heads; three-headed; as, the\n   triple-headed dog Cerberus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triplet', 'n.', 'A collection or combination of three of a kind; three\n   united.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triplet', 'n.', 'Three verses rhyming together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triplet', 'n.', 'A group of three notes sung or played in the tree of two.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triplet', 'n.', 'Three children or offspring born at one birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triple-tail', 'n.', 'An edible fish (Lobotes Surinamensis) found in the\n   warmer parts of all the oceans, and common on the southern and middle\n   coasts of the United States. When living it is silvery gray, and\n   becomes brown or blackish when dead. Its dorsal and anal fins are long,\n   and extend back on each side of the tail. It has large silvery scales\n   which are used in the manufacture of fancy work. Called also, locally,\n   black perch, grouper, and flasher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triplicate', 'v. t.', 'Made thrice as much; threefold; tripled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triplicate', 'n.', 'A third thing corresponding to two others of the same\n   kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triplicate-ternate', 'a.', 'Triternate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triplication', 'n.', 'The act of tripling, or making threefold, or adding\n   three together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triplication', 'n.', 'Same as Surrejoinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triplicity', 'a.', 'The quality or state of being triple, or threefold;\n   trebleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triplicostate', 'a.', 'Three-ribbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triplite', 'n.', 'A mineral of a dark brown color, generally with a\n   fibrous, massive structure. It is a fluophosphate of iron and\n   manganese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triploblastic', 'a.', 'Of, pertaining to, or designating, that condition of\n   the ovum in which there are three primary germinal layers, or in which\n   the blastoderm splits into three layers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triploidite', 'n.', 'A manganese phosphate near triplite, but containing\n   hydroxyl instead of fluorine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triply', 'adv.', 'In a triple manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripmadam', 'n.', 'Same as Prickmadam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripod', 'n.', 'Any utensil or vessel, as a stool, table, altar, caldron,\n   etc., supported on three feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripod', 'n.', 'A three-legged frame or stand, usually jointed at top, for\n   supporting a theodolite, compass, telescope, camera, or other\n   instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripodian', 'n.', 'An ancient stringed instrument; -- so called because, in\n   form, it resembled the Delphic tripod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripody', 'n.', 'Three metrical feet taken together, or included in one\n   measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripoli', 'n.', 'An earthy substance originally brought from Tripoli, used\n   in polishing stones and metals. It consists almost wholly of the\n   siliceous shells of diatoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripoline', 'a.', 'Of or pertaining to Tripoli or its inhabitants;\n   Tripolitan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripoline', 'a.', 'Of or pertaining to tripoli, the mineral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripolitan', 'a.', 'Of or pertaining to Tripoli or its inhabitants;\n   Tripoline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripolitan', 'n.', 'A native or inhabitant of Tripoli.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triposes', 'pl. ', 'of Tripos');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripos', 'n.', 'A tripod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripos', 'n.', 'A university examination of questionists, for honors; also,\n   a tripos paper; one who prepares a tripos paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trippant', 'a.', 'See Tripping, a., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripper', 'n.', 'One who trips or supplants; also, one who walks or trips\n   nimbly; a dancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripper', 'n.', 'An excursionist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trippet', 'n.', 'A cam, wiper, or projecting piece which strikes another\n   piece repeatedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripping', 'a.', 'Quick; nimble; stepping lightly and quickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripping', 'a.', 'Having the right forefoot lifted, the others remaining on\n   the ground, as if he were trotting; trippant; -- said of an animal, as\n   a hart, buck, and the like, used as a bearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripping', 'n.', 'Act of one who, or that which, trips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripping', 'n.', 'A light dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripping', 'n.', 'The loosing of an anchor from the ground by means of its\n   cable or buoy rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trippingly', 'adv.', 'In a tripping manner; with a light, nimble, quick\n   step; with agility; nimbly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripsis', 'n.', 'Trituration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripsis', 'n.', 'Shampoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triptote', 'n.', 'A noun having three cases only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triptych', 'n.', 'Anything in three parts or leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triptych', 'n.', 'A writing tablet in three parts, two of which fold over\n   on the middle part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triptych', 'n.', 'A picture or altarpiece in three compartments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripudiary', 'a.', 'Of or pertaining to dancing; performed by dancing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripudiate', 'v. i.', 'To dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tripudiation', 'n.', 'The act of dancing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triquadrantal', 'a.', 'Having three quadrants; thus, a triquadrantal\n   triangle is one whose three sides are quadrants, and whose three angles\n   are consequently right angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triquetral', 'a.', 'Triquetrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triquetrous', 'a.', 'Three sided, the sides being plane or concave; having\n   three salient angles or edges; trigonal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triquetra', 'pl. ', 'of Triquetrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triquetrum', 'n.', 'One of the bones of the carpus; the cuneiform. See\n   Cuneiform (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triradiate', 'a.', 'Alt. of Triradiated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triradiated', 'a.', 'Having three rays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trirectangular', 'a.', 'Having three right angles. See Triquadrantal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trireme', 'n.', 'An ancient galley or vessel with tree banks, or tiers, of\n   oars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trirhomboidal', 'a.', 'Having three rhombic faces or sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trisacramentarian', 'n.', 'One who recognizes three sacraments, and no\n   more; -- namely, baptism, the Lords Supper, and penance. See\n   Sacrament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trisagion', 'n.', 'An ancient anthem, -- usually known by its Latin name\n   tersanctus.See Tersanctus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trisected', 'imp. & p. p.', 'of Trisect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trisecting', 'p. pr. & vb. n.', 'of Trisect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trisect', 'v. t.', 'To cut or divide into three parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trisect', 'v. t.', 'To cut or divide into three equal parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trisected', 'a.', 'Divided into three parts or segments by incisions\n   extending to the midrib or to the base; -- said of leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trisection', 'n.', 'The division of a thing into three parts, Specifically:\n   (Geom.) the division of an angle into three equal parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triseralous', 'a.', 'Having three sepals, or calyx leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triserial', 'a.', 'Alt. of Triseriate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triseriate', 'a.', 'Arranged in three vertical or spiral rows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trismus', 'n.', 'The lockjaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trisnitrate', 'n.', 'A nitrate formed from three molecules of nitric acid;\n   also, less properly, applied to certain basic nitrates; as, trisnitrate\n   of bismuth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trisoctahedron', 'n.', 'A solid of the isometric system bounded by\n   twenty-four equal faces, three corresponding to each face of an\n   octahedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trispast', 'n.', 'Alt. of Trispaston');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trispaston', 'n.', 'A machine with three pulleys which act together for\n   raising great weights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trispermous', 'a.', 'Containing three seeds; three-seeded; as, a\n   trispermous capsule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trisplanchnic', 'a.', 'Of or pertaining to the three great splanchnic\n   cavities, namely, that of the head, the chest, and the abdomen; --\n   applied to the sympathetic nervous system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triste', 'imp.', 'of Trist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trist', 'v. t. & i.', 'To trust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trist', 'n.', 'Trust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trist', 'n.', 'A post, or station, in hunting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trist', 'n.', 'A secret meeting, or the place of such meeting; a tryst. See\n   Tryst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trist', 'a.', 'Sad; sorrowful; gloomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triste', 'n.', 'A cattle fair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tristearate', 'n.', 'Tristearin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tristearin', 'n.', 'See Stearin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tristtul', 'a.', 'Sad; sorrowful; gloomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tristfully', 'adv.', 'In a tristful manner; sadly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tristichous', 'a.', 'Arranged in three vertical rows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tristigmatic', 'a.', 'Alt. of Tristigmatose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tristigmatose', 'a.', 'Having, or consisting of, three stigmas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tristitiate', 'v. t.', 'To make sad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tristoma', 'n.', 'Any one of numerous species of trematode worms belonging\n   to Tristoma and allied genera having a large posterior sucker and two\n   small anterior ones. They usually have broad, thin, and disklike\n   bodies, and are parasite on the gills and skin of fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tristy', 'a.', 'See Trist, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trisuls', 'n.', 'Something having three forks or prongs, as a trident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trisulcate', 'a.', 'Having three furrows, forks, or prongs; having three\n   grooves or sulci; three-grooved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trisulphide', 'n.', 'A sulphide containing three atoms of sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trisyllabic', 'a.', 'Alt. of Trisyllabical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trisyllabical', 'a.', 'Of or pertaining to a trisyllable; consisting of\n   three syllables; as, \"syllable\" is a trisyllabic word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trisyllable', 'n.', 'A word consisting of three syllables only; as,\n   a-ven-ger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trite', 'a.', 'Worn out; common; used until so common as to have lost\n   novelty and interest; hackneyed; stale; as, a trite remark; a trite\n   subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triternate', 'a.', 'Three times ternate; -- applied to a leaf whose petiole\n   separates into three branches, each of which divides into three parts\n   which each bear three leafiets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tritheism', 'n.', 'The opinion or doctrine that the Father, Son, and Holy\n   Spirit are three distinct Gods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tritheist', 'n.', 'One who believes in tritheism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tritheistic', 'a.', 'Alt. of Tritheistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tritheistical', 'a.', 'Of or pertaining to tritheism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tritheite', 'n.', 'A tritheist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trithing', 'n.', 'One of three ancient divisions of a county in England; --\n   now called riding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trithionate', 'n.', 'A salt of trithionic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trithionic', 'a.', 'Of or pertaining to, or designating, a certain thionic\n   acid, H2S3O6 which is obtained as a colorless, odorless liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tritical', 'a.', 'Trite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triticin', 'n.', 'A carbohydrate isomeric with dextrin, obtained from\n   quitch grass (Agropyrum, formerly Triticum, repens) as a white\n   amorphous substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triticum', 'n.', 'A genus of grasses including the various species of\n   wheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triton', 'n.', 'A fabled sea demigod, the son of Neptune and Amphitrite,\n   and the trumpeter of Neptune. He is represented by poets and painters\n   as having the upper part of his body like that of a man, and the lower\n   part like that of a fish. He often has a trumpet made of a shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triton', 'n.', 'Any one of many species of marine gastropods belonging to\n   Triton and allied genera, having a stout spiral shell, often handsomely\n   colored and ornamented with prominent varices. Some of the species are\n   among the largest of all gastropods. Called also trumpet shell, and sea\n   trumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triton', 'n.', 'Any one of numerous species of aquatic salamanders. The\n   common European species are Hemisalamandra cristata, Molge palmata, and\n   M. alpestris, a red-bellied species common in Switzerland. The most\n   common species of the United States is Diemyctylus viridescens. See\n   Illust. under Salamander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tritone', 'n.', 'A superfluous or augmented fourth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tritorium', 'n.', 'Same as Triturium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tritova', 'pl. ', 'of Tritovum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tritovum', 'n.', 'An embryonic insect which has twice cast its skin\n   previous to hatching from the egg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tritozooid', 'n.', 'A zooid of the third generation in asexual\n   reproduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triturable', 'a.', 'Capable of being triturated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triturated', 'imp. & p. p.', 'of Triturate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triturating', 'p. pr. & vb. n.', 'of Triturate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triturate', 'v. t.', 'To rub, grind, bruise, or thrash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triturate', 'v. t.', 'To rub or grind to a very fine or impalpable powder;\n   to pulverize and comminute thoroughly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trituration', 'n.', 'The act of triturating, or reducing to a fine or\n   impalpable powder by grinding, rubbing, bruising, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triture', 'n.', 'A rubbing or grinding; trituration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triturium', 'n.', 'A vessel for separating liquids of different densities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trityl', 'n.', 'Propyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tritylene', 'n.', 'Propylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumph', 'n.', 'A magnificent and imposing ceremonial performed in honor\n   of a general who had gained a decisive victory over a foreign enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumph', 'n.', 'Hence, any triumphal procession; a pompous exhibition; a\n   stately show or pageant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumph', 'n.', 'A state of joy or exultation for success.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumph', 'n.', 'Success causing exultation; victory; conquest; as, the\n   triumph of knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumph', 'n.', 'A trump card; also, an old game at cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumphed', 'imp. & p. p.', 'of Triumph');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumphing', 'p. pr. & vb. n.', 'of Triumph');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumph', 'n.', 'To celebrate victory with pomp; to rejoice over success;\n   to exult in an advantage gained; to exhibit exultation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumph', 'n.', 'To obtain victory; to be successful; to prevail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumph', 'n.', 'To be prosperous; to flourish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumph', 'n.', 'To play a trump card.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumph', 'v. t.', 'To obtain a victory over; to prevail over; to conquer.\n   Also, to cause to triumph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumphal', 'a.', 'Of or pertaining to triumph; used in a triumph;\n   indicating, or in honor of, a triumph or victory; as, a triumphal\n   crown; a triumphal arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumphal', 'n.', 'A token of victory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumplant', 'v. i.', 'Rejoicing for victory; triumphing; exultant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumplant', 'v. i.', 'Celebrating victory; expressive of joy for success;\n   as, a triumphant song or ode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumplant', 'v. i.', 'Graced with conquest; victorious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumplant', 'v. i.', 'Of or pertaining to triumph; triumphal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumphantly', 'adv.', 'In a triumphant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumpher', 'n.', 'One who was honored with a triumph; a victor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumpher', 'n.', 'One who triumphs or rejoices for victory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumphing', 'a.', 'Having or celebrating a triumph; victorious;\n   triumphant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumviri', 'pl. ', 'of Triumvir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumvirs', 'pl. ', 'of Triumvir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumvir', 'n.', 'One of tree men united in public office or authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumvirate', 'n.', 'Government by three in coalition or association; the\n   term of such a government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumvirate', 'n.', 'A coalition or association of three in office or\n   authority; especially, the union of three men who obtained the\n   government of the Roman empire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triumviry', 'n.', 'A triumvirate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triune', 'a.', 'Being three in one; -- an epithet used to express the unity\n   of a trinity of persons in the Godhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triunguli', 'pl. ', 'of Triungulus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triungulus', 'n.', 'The active young larva of any oil beetle. It has feet\n   armed with three claws, and is parasitic on bees. See Illust. of Oil\n   beetle, under Oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triunity', 'n.', 'The quality or state of being triune; trinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trivalence', 'n.', 'The quality or state of being trivalent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trivalent', 'a.', 'Having a valence of three; capable of being combined\n   with, substituted for, or compared with, three atoms of hydrogen; --\n   said of triad atoms or radicals; thus, nitrogen is trivalent in\n   ammonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trivalve', 'n.', 'Anything having three valves, especially a shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trivalvular', 'a.', 'Having three valves; three-valved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trivant', 'n.', 'A truant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triverbial', 'a.', 'Pertaining to, or designating, certain days allowed to\n   the pretor for hearing causes, when be might speak the three\n   characteristic words of his office, do, dico, addico. They were called\n   dies fasti.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trivet', 'n.', 'A tree-legged stool, table, or other support; especially, a\n   stand to hold a kettle or similar vessel near the fire; a tripod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trivet', 'n.', 'A weavers knife. See Trevat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trivial', 'a.', 'Found anywhere; common.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trivial', 'a.', 'Ordinary; commonplace; trifling; vulgar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trivial', 'a.', 'Of little worth or importance; inconsiderable; trifling;\n   petty; paltry; as, a trivial subject or affair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trivial', 'a.', 'Of or pertaining to the trivium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trivial', 'n.', 'One of the three liberal arts forming the trivium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trivialism', 'n.', 'A trivial matter or method; a triviality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trivialities', 'pl. ', 'of Triviality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triviality', 'n.', 'The quality or state of being trivial; trivialness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triviality', 'n.', 'That which is trivial; a trifle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trivially', 'adv.', 'In a trivial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trivialness', 'n.', 'Quality or state of being trivial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trivium', 'n.', 'The three \" liberal\" arts, grammar, logic, and rhetoric;\n   -- being a triple way, as it were, to eloquence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trivium', 'n.', 'The three anterior ambulacra of echinoderms, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triweekly', 'a.', 'Occurring or appearing three times a week; thriceweekly;\n   as, a triweekly newspaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triweekly', 'adv.', 'Three times a week.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Triweekly', 'n.', 'A triweekly publication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troad', 'n.', 'See Trode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troat', 'v. i.', 'To cry, as a buck in rutting time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troat', 'n.', 'The cry of a buck in rutting time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trocar', 'n.', 'A stylet, usually with a triangular point, used for\n   exploring tissues or for inserting drainage tubes, as in dropsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochaic', 'n.', 'A trochaic verse or measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochaic', 'a.', 'Alt. of Trochaical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochaical', 'a.', 'Of or pertaining to trochees; consisting of trochees;\n   as, trochaic measure or verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochal', 'a.', 'Resembling a wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochanter', 'n.', 'One of two processes near the head of the femur, the\n   outer being called the great trochanter, and the inner the small\n   trochanter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochanter', 'n.', 'The third joint of the leg of an insect, or the second\n   when the trochantine is united with the coxa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochanteric', 'a.', 'Of or pertaining to one or both of the trochanters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochantine', 'n.', 'The second joint of the leg of an insect, -- often\n   united with the coxa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochar', 'n.', 'See Trocar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troche', 'n.', 'A medicinal tablet or lozenge; strictly, one of circular\n   form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochee', 'n.', 'A foot of two syllables, the first long and the second\n   short, as in the Latin word ante, or the first accented and the second\n   unaccented, as in the English word motion; a choreus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochil', 'n.', 'The crocodile bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochilic', 'a.', 'OF or pertaining to rotary motion; having power to draw\n   out or turn round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochilics', 'n.', 'The science of rotary motion, or of wheel work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochili', 'n. pl.', 'A division of birds comprising the humming birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochilidist', 'n.', 'One who studies, or is versed in, the nature and\n   habits of humming birds, or the Trochilidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochilos', 'n.', 'The crocodile bird, or trochil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochili', 'pl. ', 'of Trochilus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochilus', 'n.', 'A genus of humming birds. It Formerly included all the\n   known species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochilus', 'n.', 'Any one of several species of wrens and kinglets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochilus', 'n.', 'The crocodile bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochilus', 'n.', 'An annular molding whose section is concave, like the\n   edge of a pulley; -- called also scotia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troching', 'n.', 'One of the small branches of a stags antler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochisci', 'pl. ', 'of Trochiscus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochiscus', 'n.', 'A kind of tablet or lozenge; a troche.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochisk', 'n.', 'See Trochiscus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochite', 'n.', 'A wheel-like joint of the stem of a fossil crinoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochlea', 'n.', 'A pulley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochlea', 'n.', 'A pulley, or a structure resembling a pulley; as, the\n   trochlea, or pulleylike end, of the humerus, which articulates with the\n   ulna; or the trochlea, or fibrous ring, in the upper part of the orbit,\n   through which the superior oblique, or trochlear, muscle of the eye\n   passes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochlear', 'n.', 'Shaped like, or resembling, a pulley; pertaining to, or\n   connected with, a trochlea; as, a trochlear articular surface; the\n   trochlear muscle of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochleary', 'a.', 'Pertaining to, or connected with, a trochlea;\n   trochlear; as, the trochleary, or trochlear, nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochoid', 'n.', 'The curve described by any point in a wheel rolling on a\n   line; a cycloid; a roulette; in general, the curve described by any\n   point fixedly connected with a moving curve while the moving curve\n   rolls without slipping on a second fixed curve, the curves all being in\n   one plane. Cycloids, epicycloids, hypocycloids, cardioids, etc., are\n   all trochoids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochoid', 'a.', 'Admitting of rotation on an axis; -- sometimes applied to\n   a pivot joint like that between the atlas and axis in the vertebral\n   column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochoid', 'a.', 'Top-shaped; having a flat base and conical spire; -- said\n   of certain shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochoid', 'a.', 'Of or pertaining to the genus Trochus or family\n   Trochidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochoidal', 'a.', 'Of or pertaining to a trochoid; having the properties\n   of a trochoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochoidal', 'a.', 'See Trochoid, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochometer', 'n.', 'A contrivance for computing the revolutions of a\n   wheel; an odometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochosphere', 'n.', 'A young larval form of many annelids, mollusks, and\n   bryozoans, in which a circle of cilia is developed around the anterior\n   end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochi', 'pl. ', 'of Trochus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trochus', 'n.', 'Any one of numerous species of marine univalve shells\n   belonging to Trochus and many allied genera of the family Trochidae.\n   Some of the species are called also topshells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troco', 'n.', 'An old English game; -- called also lawn billiards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trod', '', 'imp. & p. p. of Tread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trodden', '', 'p. p. of Tread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trode', '', 'imp. of Tread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trode', 'n.', 'Tread; footing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troglodyte', 'n.', 'One of any savage race that dwells in caves, instead of\n   constructing dwellings; a cave dweller. Most of the primitive races of\n   man were troglodytes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troglodyte', 'n.', 'An anthropoid ape, as the chimpanzee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troglodyte', 'n.', 'The wren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troglodytes', 'n.', 'A genus of apes including the chimpanzee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troglodytes', 'n.', 'A genus of singing birds including the common wrens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troglodytic', 'a.', 'Alt. of Troglodytical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troglodytical', 'a.', 'Of or pertaining to a troglodyte, or dweller in\n   caves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trogon', 'n.', 'Any one of numerous species of beautiful tropical birds\n   belonging to the family Trogonidae. They are noted for the brilliant\n   colors and the resplendent luster of their plumage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trogonoid', 'a.', 'Like or pertaining to the trogons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trogue', 'n.', 'A wooden trough, forming a drain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troic', 'a.', 'Pertaining to Troy; Trojan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troilite', 'n.', 'Native iron protosulphide, FeS. It is known only in\n   meteoric irons, and is usually in imbedded nodular masses of a bronze\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troili', 'pl. ', 'of Troilus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troiluses', 'pl. ', 'of Troilus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troilus', 'n.', 'A large, handsome American butterfly (Euph/ades, /\n   Papilio, troilus). It is black, with yellow marginal spots on the front\n   wings, and blue spots on the rear wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trojan', 'a.', 'Of or pertaining to ancient Troy or its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trojan', 'n.', 'A native or inhabitant of Troy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troll', 'n.', 'A supernatural being, often represented as of diminutive\n   size, but sometimes as a giant, and fabled to inhabit caves, hills, and\n   like places; a witch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trolled', 'imp. & p. p.', 'of Troll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trolling', 'p. pr. & vb. n.', 'of Troll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troll', 'v. t.', 'To move circularly or volubly; to roll; to turn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troll', 'v. t.', 'To send about; to circulate, as a vessel in drinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troll', 'v. t.', 'To sing the parts of in succession, as of a round, a\n   catch, and the like; also, to sing loudly or freely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troll', 'v. t.', 'To angle for with a trolling line, or with a book drawn\n   along the surface of the water; hence, to allure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troll', 'v. t.', 'To fish in; to seek to catch fish from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troll', 'v. i.', 'To roll; to run about; to move around; as, to troll in a\n   coach and six.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troll', 'v. i.', 'To move rapidly; to wag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troll', 'v. i.', 'To take part in trolling a song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troll', 'v. i.', 'To fish with a rod whose line runs on a reel; also, to\n   fish by drawing the hook through the water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troll', 'n.', 'The act of moving round; routine; repetition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troll', 'n.', 'A song the parts of which are sung in succession; a catch; a\n   round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troll', 'n.', 'A trolley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troller', 'n.', 'One who trolls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trolley', 'n.', 'Alt. of Trolly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trolly', 'n.', 'A form of truck which can be tilted, for carrying railroad\n   materials, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trolly', 'n.', 'A narrow cart that is pushed by hand or drawn by an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trolly', 'n.', 'A truck from which the load is suspended in some kinds of\n   cranes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trolly', 'n.', 'A truck which travels along the fixed conductors, and forms\n   a means of connection between them and a railway car.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trollmydames', 'n.', 'The game of nineholes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trollop', 'n.', 'A stroller; a loiterer; esp., an idle, untidy woman; a\n   slattern; a slut; a whore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trollopee', 'n.', 'A kind of loose dress for women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trombone', 'n.', 'A powerful brass instrument of the trumpet kind, thought\n   by some to be the ancient sackbut, consisting of a tube in three parts,\n   bent twice upon itself and ending in a bell. The middle part, bent\n   double, slips into the outer parts, as in a telescope, so that by\n   change of the vibrating length any tone within the compass of the\n   instrument (which may be bass or tenor or alto or even, in rare\n   instances, soprano) is commanded. It is the only member of the family\n   of wind instruments whose scale, both diatonic and chromatic, is\n   complete without the aid of keys or pistons, and which can slide from\n   note to note as smoothly as the human voice or a violin. Softly blown,\n   it has a rich and mellow sound, which becomes harsh and blatant when\n   the tones are forced; used with discretion, its effect is often solemn\n   and majestic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trombone', 'n.', 'The common European bittern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trommel', 'n.', 'A revolving buddle or sieve for separating, or sizing,\n   ores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tromp', 'n.', 'A blowing apparatus, in which air, drawn into the upper part\n   of a vertical tube through side holes by a stream of water within, is\n   carried down with the water into a box or chamber below which it is led\n   to a furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tromp', 'n.', 'Alt. of Trompe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trompe', 'n.', 'A trumpet; a trump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trompil', 'n.', 'An aperture in a tromp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tron', 'n.', 'See 3d Trone, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trona', 'n.', 'A native double salt, consisting of a combination of neutral\n   and acid sodium carbonate, Na2CO3.2HNaCO3.2H2O, occurring as a white\n   crystalline fibrous deposit from certain soda brine springs and lakes;\n   -- called also urao, and by the ancients nitrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tronage', 'n.', 'A toll or duty paid for weighing wool; also, the act of\n   weighing wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tronator', 'n.', 'An officer in London whose duty was to weigh wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trone', 'n.', 'A throne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trone', 'n.', 'A small drain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trone', 'n.', 'Alt. of Trones');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trones', 'n.', 'A steelyard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trones', 'n.', 'A form of weighing machine for heavy wares, consisting of\n   two horizontal bars crossing each other, beaked at the extremities, and\n   supported by a wooden pillar. It is now mostly disused.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troop', 'n.', 'A collection of people; a company; a number; a multitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troop', 'n.', 'Soldiers, collectively; an army; -- now generally used in\n   the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troop', 'n.', 'Specifically, a small body of cavalry, light horse, or\n   dragoons, consisting usually of about sixty men, commanded by a\n   captain; the unit of formation of cavalry, corresponding to the company\n   in infantry. Formerly, also, a company of horse artillery; a battery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troop', 'n.', 'A company of stageplayers; a troupe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troop', 'n.', 'A particular roll of the drum; a quick march.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trooped', 'imp. & p. p.', 'of Troop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trooping', 'p. pr. & vb. n.', 'of Troop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troop', 'v. i.', 'To move in numbers; to come or gather in crowds or\n   troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troop', 'v. i.', 'To march on; to go forward in haste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troopbird', 'n.', 'Any troupial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trooper', 'n.', 'A soldier in a body of cavalry; a cavalryman; also, the\n   horse of a cavalryman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troopfowl', 'n.', 'The American scaup duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troopial', 'n.', 'Same as Troupial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troopmeal', 'adv.', 'By troops; in crowds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troopship', 'n.', 'A vessel built or fitted for the conveyance of troops; a\n   transport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troostite', 'n.', 'Willemite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tropaeolin', 'n.', 'A name given to any one of a series of orange-red\n   dyestuffs produced artificially from certain complex sulphonic acid\n   derivatives of azo and diazo hydrocarbons of the aromatic series; -- so\n   called because of the general resemblance to the shades of nasturtium\n   (Tropaeolum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trope', 'n.', 'The use of a word or expression in a different sense from\n   that which properly belongs to it; the use of a word or expression as\n   changed from the original signification to another, for the sake of\n   giving life or emphasis to an idea; a figure of speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trope', 'n.', 'The word or expression so used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tropeine', 'n.', 'Any one of a series of artificial ethereal salts derived\n   from the alkaloidal base tropine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trophi', 'n. pl.', 'The mouth parts of an insect, collectively, including\n   the labrum, labium, maxillae, mandibles, and lingua, with their\n   appendages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trophic', 'a.', 'Of or connected with nutrition; nitritional; nourishing;\n   as, the so-called trophic nerves, which have a direct influence on\n   nutrition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trophied', 'a.', 'Adorned with trophies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trophonian', 'a.', 'Of or pertaining to Trophonius, his architecture, or\n   his cave and oracle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trophosome', 'n.', 'The nutritive zooids of a hydroid, collectively, as\n   distinguished from the gonosome, or reproductive zooids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trophosperm', 'n.', 'The placenta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trophies', 'pl. ', 'of Trophy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trophy', 'n.', 'A sign or memorial of a victory raised on the field of\n   battle, or, in case of a naval victory, on the nearest land. Sometimes\n   trophies were erected in the chief city of the conquered people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trophy', 'n.', 'The representation of such a memorial, as on a medal; esp.\n   (Arch.), an ornament representing a group of arms and military weapons,\n   offensive and defensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trophy', 'n.', 'Anything taken from an enemy and preserved as a memorial of\n   victory, as arms, flags, standards, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trophy', 'n.', 'Any evidence or memorial of victory or conquest; as, every\n   redeemed soul is a trophy of grace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tropic', 'a.', 'Of, pertaining to, or designating, an acid obtained from\n   atropine and certain other alkaloids, as a white crystalline substance\n   slightly soluble in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tropic', 'n.', 'One of the two small circles of the celestial sphere,\n   situated on each side of the equator, at a distance of 23 28/, and\n   parallel to it, which the sun just reaches at its greatest declination\n   north or south, and from which it turns again toward the equator, the\n   northern circle being called the Tropic of Cancer, and the southern the\n   Tropic of Capricorn, from the names of the two signs at which they\n   touch the ecliptic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tropic', 'n.', 'One of the two parallels of terrestrial latitude\n   corresponding to the celestial tropics, and called by the same names.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tropic', 'n.', 'The region lying between these parallels of latitude, or\n   near them on either side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tropic', 'a.', 'Of or pertaining to the tropics; tropical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tropical', 'n.', 'Of or pertaining to the tropics; characteristic of, or\n   incident to, the tropics; being within the tropics; as, tropical\n   climate; tropical latitudes; tropical heat; tropical diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tropical', 'n.', 'Rhetorically changed from its exact original sense; being\n   of the nature of a trope; figurative; metaphorical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tropically', 'adv.', 'In a tropical manner; figuratively; metaphorically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tropidine', 'n.', 'An alkaloid, C8H13N, obtained by the chemical\n   dehydration of tropine, as an oily liquid having a coninelike odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tropilidene', 'n.', 'A liquid hydrocarbon obtained by the dry distillation\n   of tropine with quicklime. It is regarded as being homologous with\n   dipropargyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tropine', 'n.', 'A white crystalline alkaloid, C8H15NO, produced by\n   decomposing atropine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tropist', 'n.', 'One who deals in tropes; specifically, one who avoids the\n   literal sense of the language of Scripture by explaining it as mere\n   tropes and figures of speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tropologic', 'a.', 'Alt. of Tropological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tropological', 'a.', 'Characterized by tropes; varied by tropes; tropical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tropologize', 'v. t.', 'To use in a tropological sense, as a word; to make\n   a trope of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tropology', 'n.', 'A rhetorical mode of speech, including tropes, or\n   changes from the original import of the word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trossers', 'n. pl.', 'Trousers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trotted', 'imp. & p. p.', 'of Trot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trotting', 'p. pr. & vb. n.', 'of Trot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trot', 'v. i.', 'To proceed by a certain gait peculiar to quadrupeds; to\n   ride or drive at a trot. See Trot, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trot', 'n.', 'Fig.: To run; to jog; to hurry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trot', 'v. t.', 'To cause to move, as a horse or other animal, in the pace\n   called a trot; to cause to run without galloping or cantering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trot', 'v. i.', 'The pace of a horse or other quadruped, more rapid than a\n   walk, but of various degrees of swiftness, in which one fore foot and\n   the hind foot of the opposite side are lifted at the same time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trot', 'v. i.', 'Fig.: A jogging pace, as of a person hurrying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trot', 'v. i.', 'One who trots; a child; a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troth', 'n.', 'Belief; faith; fidelity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troth', 'n.', 'Truth; verity; veracity; as, by my troth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troth', 'n.', 'Betrothal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trothless', 'a.', 'Faitless; false; treacherous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trothplight', 'v. t.', 'To betroth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trothplight', 'a.', 'Betrothed; espoused; affianced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trothplight', 'n.', 'The act of betrothing, or plighting faith; betrothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trothplighted', 'a.', 'Having fidelity pledged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trotter', 'n.', 'One that trots; especially, a horse trained to be driven\n   in trotting matches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trotter', 'n.', 'The foot of an animal, especially that of a sheep; also,\n   humorously, the human foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trottoir', 'n.', 'Footpath; pavement; sidewalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troubadour', 'n.', 'One of a school of poets who flourished from the\n   eleventh to the thirteenth century, principally in Provence, in the\n   south of France, and also in the north of Italy. They invented, and\n   especially cultivated, a kind of lyrical poetry characterized by\n   intricacy of meter and rhyme, and usually of a romantic, amatory\n   strain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troublable', 'a.', 'Causing trouble; troublesome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troubled', 'imp. & p. p.', 'of Trouble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troubling', 'p. pr. & vb. n.', 'of Trouble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trouble', 'v. t.', 'To put into confused motion; to disturb; to agitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trouble', 'v. t.', 'To disturb; to perplex; to afflict; to distress; to\n   grieve; to fret; to annoy; to vex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trouble', 'v. t.', 'To give occasion for labor to; -- used in polite\n   phraseology; as, I will not trouble you to deliver the letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trouble', 'a.', 'Troubled; dark; gloomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trouble', 'v. t.', 'The state of being troubled; disturbance; agitation;\n   uneasiness; vexation; calamity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trouble', 'v. t.', 'That which gives disturbance, annoyance, or vexation;\n   that which afflicts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trouble', 'v. t.', 'A fault or interruption in a stratum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troubler', 'n.', 'One who troubles or disturbs; one who afflicts or\n   molests; a disturber; as, a troubler of the peace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troublesome', 'a.', 'Giving trouble or anxiety; vexatious; burdensome;\n   wearisome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troublous', 'a.', 'Full of trouble; causing trouble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trous-de-loup', 'pl. ', 'of Trou-de-loup');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trou-de-loup', 'n.', 'A pit in the form of an inverted cone or pyramid,\n   constructed as an obstacle to the approach of an enemy, and having a\n   pointed stake in the middle. The pits are called also trapholes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trough', 'n.', 'A long, hollow vessel, generally for holding water or other\n   liquid, especially one formed by excavating a log longitudinally on one\n   side; a long tray; also, a wooden channel for conveying water, as to a\n   mill wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trough', 'n.', 'Any channel, receptacle, or depression, of a long and\n   narrow shape; as, trough between two ridges, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trough-shell', 'n.', 'Any bivalve shell of the genus Mactra. See Mactra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troul', 'v. t. & i.', 'See Troll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trounced', 'imp. & p. p.', 'of Trounce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trouncing', 'p. pr. & vb. n.', 'of Trounce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trounce', 'v. t.', 'To punish or beat severely; to whip smartly; to flog;\n   to castigate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troupe', 'n.', 'A company or troop, especially the company pf performers in\n   a play or an opera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troupial', 'n.', 'Any one of numerous species of bright-colored American\n   birds belonging to Icterus and allied genera, especially Icterus\n   icterus, a native of the West Indies and South America. Many of the\n   species are called orioles in America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trouse', 'n.', 'Trousers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trousering', 'n.', 'Cloth or material for making trousers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trousers', 'n. pl.', 'A garment worn by men and boys, extending from the\n   waist to the knee or to the ankle, and covering each leg separately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trousseau', 'n.', 'The collective lighter equipments or outfit of a bride,\n   including clothes, jewelry, and the like; especially, that which is\n   provided for her by her family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trout', 'n.', 'Any one of numerous species of fishes belonging to Salmo,\n   Salvelinus, and allied genera of the family Salmonidae. They are highly\n   esteemed as game fishes and for the quality of their flesh. All the\n   species breed in fresh water, but after spawning many of them descend\n   to the sea if they have an opportunity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trout', 'n.', 'Any one of several species of marine fishes more or less\n   resembling a trout in appearance or habits, but not belonging to the\n   same family, especially the California rock trouts, the common\n   squeteague, and the southern, or spotted, squeteague; -- called also\n   salt-water trout, sea trout, shad trout, and gray trout. See\n   Squeteague, and Rock trout under Rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troutbird', 'n.', 'The American golden plover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trout-colored', 'a.', 'White, with spots of black, bay, or sorrel; as, a\n   trout-colored horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troutlet', 'n.', 'A little trout; a troutling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troutling', 'n.', 'A little trout; a troutlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trouvere', 'n.', 'Alt. of Trouveur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trouveur', 'n.', 'One of a school of poets who flourished in Northern\n   France from the eleventh to the fourteenth century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trover', 'n.', 'The gaining possession of any goods, whether by finding or\n   by other means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trover', 'n.', 'An action to recover damages against one who found goods,\n   and would not deliver them to the owner on demand; an action which lies\n   in any case to recover the value of goods wrongfully converted by\n   another to his own use. In this case the finding, though alleged, is an\n   immaterial fact; the injury lies in the conversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trow', 'n.', 'A boat with an open well amidships. It is used in spearing\n   fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trow', 'v. i. & t.', 'To believe; to trust; to think or suppose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trowel', 'n.', 'A masons tool, used in spreading and dressing mortar, and\n   breaking bricks to shape them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trowel', 'n.', 'A gardeners tool, somewhat like a scoop, used in taking up\n   plants, stirring the earth, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trowel', 'n.', 'A tool used for smoothing a mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troweled', '', 'Formed with a trowel; smoothed with a trowel; as, troweled\n   stucco, that is, stucco laid on and ready for the reception of paint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trowelfuls', 'pl. ', 'of Trowelful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trowelful', 'n.', 'As much as a trowel will hold; enough to fill a trowel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trowl', 'n.', 'See Troll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trowsed', 'a.', 'Wearing trousers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trowsers', 'n. pl.', 'Same as Trousers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troy', 'n.', 'Troy weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Troyounce', 'n.', 'See Troy ounce, under Troy weight, above, and under\n   Ounce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truage', 'n.', 'A pledge of truth or peace made on payment of a tax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truage', 'n.', 'A tax or impost; tribute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truancy', 'n.', 'The act of playing truant, or the state of being truant;\n   as, addicted to truancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truand', 'n. & a.', 'See Truant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truant', 'n.', 'One who stays away from business or any duty; especially,\n   one who stays out of school without leave; an idler; a loiterer; a\n   shirk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truant', 'a.', 'Wandering from business or duty; loitering; idle, and\n   shirking duty; as, a truant boy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truant', 'v. i.', 'To idle away time; to loiter, or wander; to play the\n   truant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truant', 'v. t.', 'To idle away; to waste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truantly', 'adv.', 'Like a truant; in idleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truantship', 'n.', 'The conduct of a truant; neglect of employment;\n   idleness; truancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trub', 'n.', 'A truffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trubtall', 'n.', 'A short, squat woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trubu', 'n.', 'An East India herring (Clupea toli) which is extensively\n   caught for the sake of its roe and for its flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truce', 'n.', 'A suspension of arms by agreement of the commanders of\n   opposing forces; a temporary cessation of hostilities, for negotiation\n   or other purpose; an armistice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truce', 'n.', 'Hence, intermission of action, pain, or contest; temporary\n   cessation; short quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trucebreaker', 'n.', 'One who violates a truce, covenant, or engagement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truceless', 'a.', 'Without a truce; unforbearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truchman', 'n.', 'An interpreter. See Dragoman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trucidation', 'n.', 'The act of killing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truck', 'v. i.', 'A small wheel, as of a vehicle; specifically (Ord.), a\n   small strong wheel, as of wood or iron, for a gun carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truck', 'v. i.', 'A low, wheeled vehicle or barrow for carrying goods,\n   stone, and other heavy articles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truck', 'v. i.', 'A swiveling carriage, consisting of a frame with one or\n   more pairs of wheels and the necessary boxes, springs, etc., to carry\n   and guide one end of a locomotive or a car; -- sometimes called bogie\n   in England. Trucks usually have four or six wheels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truck', 'v. i.', 'A small wooden cap at the summit of a flagstaff or a\n   masthead, having holes in it for reeving halyards through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truck', 'v. i.', 'A small piece of wood, usually cylindrical or\n   disk-shaped, used for various purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truck', 'v. i.', 'A freight car.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truck', 'v. i.', 'A frame on low wheels or rollers; -- used for various\n   purposes, as for a movable support for heavy bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truck', 'v. t.', 'To transport on a truck or trucks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trucked', 'imp. & p. p.', 'of Truck');
  INSERT INTO words (word, word_type, description) VALUES  (    'trucking', 'p. pr. & vb. n.', 'of Truck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truck', 'v. t.', 'To exchange; to give in exchange; to barter; as, to truck\n   knives for gold dust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truck', 'v. i.', 'To exchange commodities; to barter; to trade; to deal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truck', 'n.', 'Exchange of commodities; barter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truck', 'n.', 'Commodities appropriate for barter, or for small trade;\n   small commodities; esp., in the United States, garden vegetables raised\n   for the market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truck', 'n.', 'The practice of paying wages in goods instead of money; --\n   called also truck system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truckage', 'n.', 'The practice of bartering goods; exchange; barter; truck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truckage', 'n.', 'Money paid for the conveyance of goods on a truck;\n   freight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trucker', 'n.', 'One who trucks; a trafficker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trucking', 'n.', 'The business of conveying goods on trucks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truckle', 'n.', 'A small wheel or caster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truckle', 'v. i.', 'To yield or bend obsequiously to the will of another;\n   to submit; to creep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truckled', 'imp. & p. p.', 'of Truckle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truckling', 'p. pr. & vb. n.', 'of Truckle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truckle', 'v. t.', 'To roll or move upon truckles, or casters; to trundle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truckle-bed', 'n.', 'A low bed on wheels, that may be pushed under another\n   bed; a trundle-bed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truckler', 'n.', 'One who truckles, or yields servilely to the will of\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truckmen', 'pl. ', 'of Truckman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truckman', 'n.', 'One who does business in the way of barter or exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truckman', 'n.', 'One who drives a truck, or whose business is the\n   conveyance of goods on trucks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truculence', 'n.', 'Alt. of Truculency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truculency', 'n.', 'The quality or state of being truculent; savageness of\n   manners; ferociousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truculent', 'a.', 'Fierce; savage; ferocious; barbarous; as, the truculent\n   inhabitants of Scythia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truculent', 'a.', 'Cruel; destructive; ruthless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truculently', 'adv.', 'In a truculent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trudged', 'imp. & p. p.', 'of Trudge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trudging', 'p. pr. & vb. n.', 'of Trudge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trudge', 'v. i.', 'To walk or march with labor; to jog along; to move\n   wearily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trudgeman', 'n.', 'A truchman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'True', 'n.', 'Conformable to fact; in accordance with the actual state of\n   things; correct; not false, erroneous, inaccurate, or the like; as, a\n   true relation or narration; a true history; a declaration is true when\n   it states the facts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'True', 'n.', 'Right to precision; conformable to a rule or pattern; exact;\n   accurate; as, a true copy; a true likeness of the original.');
  INSERT INTO words (word, word_type, description) VALUES  (    'True', 'n.', 'Steady in adhering to friends, to promises, to a prince, or\n   the like; unwavering; faithful; loyal; not false, fickle, or\n   perfidious; as, a true friend; a wife true to her husband; an officer\n   true to his charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'True', 'n.', 'Actual; not counterfeit, adulterated, or pretended; genuine;\n   pure; real; as, true balsam; true love of country; a true Christian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'True', 'adv.', 'In accordance with truth; truly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'True-blue', 'a.', 'Of inflexible honesty and fidelity; -- a term derived\n   from the true, or Coventry, blue, formerly celebrated for its\n   unchanging color. See True blue, under Blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'True-blue', 'n.', 'A person of inflexible integrity or fidelity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'True-born', 'a.', 'Of genuine birth; having a right by birth to any title;\n   as, a true-born Englishman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'True-bred', 'a.', 'Of a genuine or right breed; as, a true-bred beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'True-bred', 'a.', 'Being of real breeding or education; as, a true-bred\n   gentleman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'True-hearted', 'a.', 'Of a faithful heart; honest; sincere; not faithless\n   or deceitful; as, a truhearted friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truelove', 'n.', 'One really beloved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truelove', 'n.', 'A plant. See Paris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truelove', 'n.', 'An unexplained word occurring in Chaucer, meaning,\n   perhaps, an aromatic sweetmeat for sweetening the breath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trueness', 'n.', 'The quality of being true; reality; genuineness;\n   faithfulness; sincerity; exactness; truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'True-penny', 'n.', 'An honest fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truffle', 'n.', 'Any one of several kinds of roundish, subterranean fungi,\n   usually of a blackish color. The French truffle (Tuber melanosporum)\n   and the English truffle (T. aestivum) are much esteemed as articles of\n   food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truffled', 'a.', 'Provided or cooked with truffles; stuffed with truffles;\n   as, a truffled turkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trug', 'n.', 'A trough, or tray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trug', 'n.', 'A hod for mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trug', 'n.', 'An old measure of wheat equal to two thirds of a bushel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trug', 'n.', 'A concubine; a harlot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trugging-house', 'n.', 'A brothel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truism', 'n.', 'An undoubted or self-evident truth; a statement which is\n   pliantly true; a proposition needing no proof or argument; -- opposed\n   to falsism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truismatic', 'a.', 'Of or pertaining to truisms; consisting of truisms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trull', 'n.', 'A drab; a strumpet; a harlot; a trollop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trull', 'n.', 'A girl; a wench; a lass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trullization', 'n.', 'The act of laying on coats of plaster with a trowel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truly', 'adv.', 'In a true manner; according to truth; in agreement with\n   fact; as, to state things truly; the facts are truly represented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truly', 'adv.', 'Exactly; justly; precisely; accurately; as, to estimate\n   truly the weight of evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truly', 'adv.', 'Sincerely; honestly; really; faithfully; as, to be truly\n   attached to a lover; the citizens are truly loyal to their prince or\n   their country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truly', 'adv.', 'Conformably to law; legally; legitimately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truly', 'adv.', 'In fact; in deed; in reality; in truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trump', 'n.', 'A wind instrument of music; a trumpet, or sound of a\n   trumpet; -- used chiefly in Scripture and poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trump', 'v. i.', 'To blow a trumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trump', 'n.', 'A winning card; one of a particular suit (usually determined\n   by chance for each deal) any card of which takes any card of the other\n   suits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trump', 'n.', 'An old game with cards, nearly the same as whist; -- called\n   also ruff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trump', 'n.', 'A good fellow; an excellent person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumped', 'imp. & p. p.', 'of Trump');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumping', 'p. pr. & vb. n.', 'of Trump');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trump', 'v. i.', 'To play a trump card when one of another suit has been\n   led.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trump', 'v. t.', 'To play a trump card upon; to take with a trump card; as,\n   she trumped the first trick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trump', 'v. t.', 'To trick, or impose on; to deceive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trump', 'v. t.', 'To impose unfairly; to palm off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpery', 'n.', 'Deceit; fraud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpery', 'n.', 'Something serving to deceive by false show or pretense;\n   falsehood; deceit; worthless but showy matter; hence, things worn out\n   and of no value; rubbish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpery', 'a.', 'Worthless or deceptive in character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpet', 'n.', 'A wind instrument of great antiquity, much used in war and\n   military exercises, and of great value in the orchestra. In consists of\n   a long metallic tube, curved (once or twice) into a convenient shape,\n   and ending in a bell. Its scale in the lower octaves is limited to the\n   first natural harmonics; but there are modern trumpets capable, by\n   means of valves or pistons, of producing every tone within their\n   compass, although at the expense of the true ringing quality of tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpet', 'n.', 'A trumpeter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpet', 'n.', 'One who praises, or propagates praise, or is the\n   instrument of propagating it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpet', 'n.', 'A funnel, or short, fiaring pipe, used as a guide or\n   conductor, as for yarn in a knitting machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpeted', 'imp. & p. p.', 'of Trumpet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpeting', 'p. pr. & vb. n.', 'of Trumpet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpet', 'v. t.', 'To publish by, or as by, sound of trumpet; to noise\n   abroad; to proclaim; as, to trumpet good tidings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpet', 'v. i.', 'To sound loudly, or with a tone like a trumpet; to\n   utter a trumplike cry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpeter', 'n.', 'One who sounds a trumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpeter', 'n.', 'One who proclaims, publishes, or denounces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpeter', 'n.', 'Any one of several species of long-legged South American\n   birds of the genus Psophia, especially P. crepitans, which is abundant,\n   and often domesticated and kept with other poultry by the natives. They\n   are allied to the cranes. So called from their loud cry. Called also\n   agami, and yakamik.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpeter', 'n.', 'A variety of the domestic pigeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpeter', 'n.', 'An American swan (Olor buccinator) which has a very loud\n   note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpeter', 'n.', 'A large edible fish (Latris hecateia) of the family\n   Cirrhitidae, native of Tasmania and New Zealand. It sometimes weighs as\n   much as fifty or sixty pounds, and is highly esteemed as a food fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpeting', 'n.', 'A channel cut behind the brick lining of a shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpets', 'n. pl.', 'A plant (Sarracenia flava) with long, hollow leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpet-shaped', 'a.', 'Tubular with one end dilated, as the flower of the\n   trumpet creeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpet-tongued', 'a.', 'Having a powerful, far-reaching voice or speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpetweed', 'n.', 'An herbaceous composite plant (Eupatorium purpureum),\n   often having hollow stems, and bearing purplish flowers in small\n   corymbed heads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpetweed', 'n.', 'The sea trumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpetwood', 'n.', 'A tropical American tree (Cecropia peltata) of the\n   Breadfruit family, having hollow stems, which are used for wind\n   instruments; -- called also snakewood, and trumpet tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumpie', 'n.', 'The Richardsons skua (Stercorarius parasiticus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trumplike', 'a.', 'Resembling a trumpet, esp. in sound; as, a trumplike\n   voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truncal', 'a.', 'Of or pertaining to the trunk, or body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truncated', 'imp. & p. p.', 'of Truncate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truncating', 'p. pr. & vb. n.', 'of Truncate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truncate', 'v. t.', 'To cut off; to lop; to maim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truncate', 'a.', 'Appearing as if cut off at the tip; as, a truncate leaf\n   or feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truncated', 'a.', 'Cut off; cut short; maimed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truncated', 'a.', 'Replaced, or cut off, by a plane, especially when\n   equally inclined to the adjoining faces; as, a truncated edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truncated', 'a.', 'Lacking the apex; -- said of certain spiral shells in\n   which the apex naturally drops off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truncation', 'n.', 'The act of truncating, lopping, or cutting off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truncation', 'n.', 'The state of being truncated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truncation', 'n.', 'The replacement of an edge or solid angle by a plane,\n   especially when the plane is equally inclined to the adjoining faces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunch', 'n.', 'A stake; a small post.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truncheon', 'n.', 'A short staff, a club; a cudgel; a shaft of a spear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truncheon', 'n.', 'A baton, or military staff of command.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truncheon', 'n.', 'A stout stem, as of a tree, with the branches lopped\n   off, to produce rapid growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truncheon', 'v. t.', 'To beat with a truncheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truncheoned', 'a.', 'Having a truncheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truncheoneer', 'n.', 'A person armed with a truncheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truncus', 'n.', 'The thorax of an insect. See Trunk, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trundle', 'v. i.', 'A round body; a little wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trundle', 'v. i.', 'A lind of low-wheeled cart; a truck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trundle', 'v. i.', 'A motion as of something moving upon little wheels or\n   rollers; a rolling motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trundle', 'v. i.', 'A lantern wheel. See under Lantern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trundle', 'v. i.', 'One of the bars of a lantern wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trundled', 'imp. & p. p.', 'of Trundle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trundling', 'p. pr. & vb. n.', 'of Trundle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trundle', 'v. t.', 'To roll (a thing) on little wheels; as, to trundle a\n   bed or a gun carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trundle', 'v. t.', 'To cause to roll or revolve; to roll along; as, to\n   trundle a hoop or a ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trundle', 'v. i.', 'To go or move on small wheels; as, a bed trundles under\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trundle', 'v. i.', 'To roll, or go by revolving, as a hoop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trundle-bed', 'n.', 'A low bed that is moved on trundles, or little wheels,\n   so that it can be pushed under a higher bed; a truckle-bed; also,\n   sometimes, a simiral bed without wheels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trundlehead', 'n.', 'One of the disks forming the ends of a lantern wheel\n   or pinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trundlehead', 'n.', 'The drumhead of a capstan; especially, the drumhead of\n   the lower of two capstans on the sane axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trundletail', 'n.', 'A round or curled-up tail; also, a dog with such a\n   tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunk', 'n.', 'The stem, or body, of a tree, apart from its limbs and\n   roots; the main stem, without the branches; stock; stalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunk', 'n.', 'The body of an animal, apart from the head and limbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunk', 'n.', 'The main body of anything; as, the trunk of a vein or of an\n   artery, as distinct from the branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunk', 'n.', 'That part of a pilaster which is between the base and the\n   capital, corresponding to the shaft of a column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunk', 'n.', 'That segment of the body of an insect which is between the\n   head and abdomen, and bears the wings and legs; the thorax; the\n   truncus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunk', 'n.', 'The proboscis of an elephant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunk', 'n.', 'The proboscis of an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunk', 'n.', 'A long tube through which pellets of clay, p/as, etc., are\n   driven by the force of the breath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunk', 'n.', 'A box or chest usually covered with leather, metal, or\n   cloth, or sometimes made of leather, hide, or metal, for containing\n   clothes or other goods; especially, one used to convey the effects of a\n   traveler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunk', 'n.', 'A flume or sluice in which ores are separated from the\n   slimes in which they are contained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunk', 'n.', 'A large pipe forming the piston rod of a steam engine, of\n   sufficient diameter to allow one end of the connecting rod to be\n   attached to the crank, and the other end to pass within the pipe\n   directly to the piston, thus making the engine more compact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunk', 'n.', 'A long, large box, pipe, or conductor, made of plank or\n   metal plates, for various uses, as for conveying air to a mine or to a\n   furnace, water to a mill, grain to an elevator, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunk', 'v. t.', 'To lop off; to curtail; to truncate; to maim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunk', 'v. t.', 'To extract (ores) from the slimes in which they are\n   contained, by means of a trunk. See Trunk, n., 9.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunkback', 'n.', 'The leatherback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunked', 'a.', 'Having (such) a trunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunkfish', 'n.', 'Any one of several species of plectognath fishes,\n   belonging to the genus Ostracion, or the family Ostraciontidae, having\n   an angular body covered with a rigid integument consisting of bony\n   scales. Some of the species are called also coffer fish, and boxfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunkfuls', 'pl. ', 'of Trunkful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunkful', 'n.', 'As much as a trunk will hold; enough to fill a trunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunkwork', 'n.', 'Work or devices suitable to be concealed; a secret\n   stratagem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunnel', 'n.', 'A trundle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunnel', 'n.', 'See Treenail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunnion', 'n.', 'A cylindrical projection on each side of a piece, whether\n   gun, mortar, or howitzer, serving to support it on the cheeks of the\n   carriage. See Illust. of Cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunnion', 'n.', 'A gudgeon on each side of an oscillating steam cylinder,\n   to support it. It is usually tubular, to convey steam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trunnioned', 'a.', 'Provided with trunnions; as, the trunnioned cylinder of\n   an oscillating steam engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trusion', 'n.', 'The act of pushing or thrusting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truss', 'n.', 'A bundle; a package; as, a truss of grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truss', 'n.', 'A padded jacket or dress worn under armor, to protect the\n   body from the effects of friction; also, a part of a womans dress; a\n   stomacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truss', 'n.', 'A bandage or apparatus used in cases of hernia, to keep up\n   the reduced parts and hinder further protrusion, and for other\n   purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truss', 'n.', 'A tuft of flowers formed at the top of the main stalk, or\n   stem, of certain plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truss', 'n.', 'The rope or iron used to keep the center of a yard to the\n   mast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truss', 'n.', 'An assemblage of members of wood or metal, supported at two\n   points, and arranged to transmit pressure vertically to those points,\n   with the least possible strain across the length of any member.\n   Architectural trusses when left visible, as in open timber roofs, often\n   contain members not needed for construction, or are built with greater\n   massiveness than is requisite, or are composed in unscientific ways in\n   accordance with the exigencies of style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trussed', 'imp. & p. p.', 'of Truss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trussing', 'p. pr. & vb. n.', 'of Truss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truss', 'n.', 'To bind or pack close; to make into a truss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truss', 'n.', 'To take fast hold of; to seize and hold firmly; to pounce\n   upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truss', 'n.', 'To strengthen or stiffen, as a beam or girder, by means of a\n   brace or braces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truss', 'n.', 'To skewer; to make fast, as the wings of a fowl to the body\n   in cooking it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truss', 'n.', 'To execute by hanging; to hang; -- usually with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trussing', 'n.', 'The timbers, etc., which form a truss, taken\n   collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trussing', 'n.', 'The art of stiffening or bracing a set of timbers, or the\n   like, by putting in struts, ties, etc., till it has something of the\n   character of a truss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trussing', 'n.', 'The act of a hawk, or other bird of prey, in seizing its\n   quarry, and soaring with it into air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trust', 'n.', 'Assured resting of the mind on the integrity, veracity,\n   justice, friendship, or other sound principle, of another person;\n   confidence; reliance; reliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trust', 'n.', 'Credit given; especially, delivery of property or\n   merchandise in reliance upon future payment; exchange without immediate\n   receipt of an equivalent; as, to sell or buy goods on trust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trust', 'n.', 'Assured anticipation; dependence upon something future or\n   contingent, as if present or actual; hope; belief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trust', 'n.', 'That which is committed or intrusted to one; something\n   received in confidence; charge; deposit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trust', 'n.', 'The condition or obligation of one to whom anything is\n   confided; responsible charge or office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trust', 'n.', 'That upon which confidence is reposed; ground of reliance;\n   hope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trust', 'n.', 'An estate devised or granted in confidence that the devisee\n   or grantee shall convey it, or dispose of the profits, at the will, or\n   for the benefit, of another; an estate held for the use of another; a\n   confidence respecting property reposed in one person, who is termed the\n   trustee, for the benefit of another, who is called the cestui que\n   trust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trust', 'n.', 'An organization formed mainly for the purpose of regulating\n   the supply and price of commodities, etc.; as, a sugar trust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trust', 'a.', 'Held in trust; as, trust property; trustmoney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trusted', 'imp. & p. p.', 'of Trust');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trusting', 'p. pr. & vb. n.', 'of Trust');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trust', 'n.', 'To place confidence in; to rely on, to confide, or repose\n   faith, in; as, we can not trust those who have deceived us.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trust', 'n.', 'To give credence to; to believe; to credit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trust', 'n.', 'To hope confidently; to believe; -- usually with a phrase or\n   infinitive clause as the object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trust', 'n.', 'to show confidence in a person by intrusting (him) with\n   something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trust', 'n.', 'To commit, as to ones care; to intrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trust', 'n.', 'To give credit to; to sell to upon credit, or in confidence\n   of future payment; as, merchants and manufacturers trust their\n   customers annually with goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trust', 'n.', 'To risk; to venture confidently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trust', 'v. i.', 'To have trust; to be credulous; to be won to confidence;\n   to confide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trust', 'v. i.', 'To be confident, as of something future; to hope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trust', 'v. i.', 'To sell or deliver anything in reliance upon a promise of\n   payment; to give credit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trustee', 'n.', 'A person to whom property is legally committed in trust,\n   to be applied either for the benefit of specified individuals, or for\n   public uses; one who is intrusted with property for the benefit of\n   another; also, a person in whose hands the effects of another are\n   attached in a trustee process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trustee', 'v. t.', 'To commit (property) to the care of a trustee; as, to\n   trustee an estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trustee', 'v. t.', 'To attach (a debtors wages, credits, or property in\n   the hands of a third person) in the interest of the creditor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trusteeship', 'n.', 'The office or duty of a trustee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truster', 'n.', 'One who trusts, or credits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truster', 'n.', 'One who makes a trust; -- the correlative of trustee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trustful', 'a.', 'Full of trust; trusting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trustful', 'a.', 'Worthy of trust; faithful; trusty; trustworthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trustily', 'adv.', 'In a trusty manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trustiness', 'n.', 'The quality or state of being trusty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trusting', 'a.', 'Having or exercising trust; confiding; unsuspecting;\n   trustful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trustless', 'a.', 'That may not be trusted; not worthy of trust;\n   unfaithful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trustworthy', 'a.', 'Worthy of trust or confidence; trusty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trusty', 'superl.', 'Admitting of being safely trusted; justly deserving\n   confidence; fit to be confided in; trustworthy; reliable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trusty', 'superl.', 'Hence, not liable to fail; strong; firm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trusty', 'superl.', 'Involving trust; as, a trusty business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truths', 'pl. ', 'of Truth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truth', 'n.', 'The quality or being true; as: -- (a) Conformity to fact or\n   reality; exact accordance with that which is, or has been; or shall be.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truth', 'n.', 'Conformity to rule; exactness; close correspondence with an\n   example, mood, object of imitation, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truth', 'n.', 'Fidelity; constancy; steadfastness; faithfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truth', 'n.', 'The practice of speaking what is true; freedom from\n   falsehood; veracity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truth', 'n.', 'That which is true or certain concerning any matter or\n   subject, or generally on all subjects; real state of things; fact;\n   verity; reality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truth', 'n.', 'A true thing; a verified fact; a true statement or\n   proposition; an established principle, fixed law, or the like; as, the\n   great truths of morals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truth', 'n.', 'Righteousness; true religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truth', 'v. t.', 'To assert as true; to declare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truthful', 'a.', 'Full of truth; veracious; reliable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truthless', 'a.', 'Devoid of truth; dishonest; dishonest; spurious;\n   faithless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truth-lover', 'n.', 'One who loves the truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truthness', 'n.', 'Truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truth-teller', 'n.', 'One who tells the truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truthy', 'a.', 'Truthful; likely; probable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trutination', 'n.', 'The act of weighing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Truttaceous', 'a.', 'Of, pertaining to, or resembling, a trout; as, fish of\n   the truttaceous kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'tried', 'imp. & p. p.', 'of Try');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trying', 'p. pr. & vb. n.', 'of Try');
  INSERT INTO words (word, word_type, description) VALUES  (    'Try', 'v. t.', 'To divide or separate, as one sort from another; to winnow;\n   to sift; to pick out; -- frequently followed by out; as, to try out the\n   wild corn from the good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Try', 'v. t.', 'To purify or refine, as metals; to melt out, and procure in\n   a pure state, as oil, tallow, lard, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Try', 'v. t.', 'To prove by experiment; to apply a test to, for the purpose\n   of determining the quality; to examine; to prove; to test; as, to try\n   weights or measures by a standard; to try a mans opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Try', 'v. t.', 'To subject to severe trial; to put to the test; to cause\n   suffering or trouble to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Try', 'v. t.', 'To experiment with; to test by use; as, to try a remedy for\n   disease; to try a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Try', 'v. t.', 'To strain; to subject to excessive tests; as, the light\n   tries his eyes; repeated disappointments try ones patience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Try', 'v. t.', 'To examine or investigate judicially; to examine by\n   witnesses or other judicial evidence and the principles of law; as, to\n   try a cause, or a criminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Try', 'v. t.', 'To settle; to decide; to determine; specifically, to decide\n   by an appeal to arms; as, to try rival claims by a duel; to try\n   conclusions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Try', 'v. t.', 'To experience; to have or gain knowledge of by experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Try', 'v. t.', 'To essay; to attempt; to endeavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Try', 'v. i.', 'To exert strength; to endeavor; to make an effort or an\n   attempt; as, you must try hard if you wish to learn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Try', 'v. i.', 'To do; to fare; as, how do you try!');
  INSERT INTO words (word, word_type, description) VALUES  (    'Try', 'n.', 'A screen, or sieve, for grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Try', 'n.', 'Act of trying; attempt; experiment; trial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Try', 'v. t.', 'Refined; select; excellent; choice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trygon', 'n.', 'Any one of several species of large sting rays belonging to\n   Trygon and allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trying', 'a.', 'Adapted to try, or put to severe trial; severe; afflictive;\n   as, a trying occasion or position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trypsin', 'n.', 'A proteolytic ferment, or enzyme, present in the\n   pancreatic juice. Unlike the pepsin of the gastric juice, it acts in a\n   neutral or alkaline fluid, and not only converts the albuminous matter\n   of the food into soluble peptones, but also, in part, into leucin and\n   tyrosin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trypsinogen', 'n.', 'The antecedent of trypsin, a substance which is\n   contained in the cells of the pancreas and gives rise to the trypsin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tryptic', 'a.', 'Relating to trypsin or to its action; produced by trypsin;\n   as, trypsin digestion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tryptone', 'n.', 'The peptone formed by pancreatic digestion; -- so called\n   because it is formed through the agency of the ferment trypsin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trysail', 'n.', 'A fore-and-aft sail, bent to a gaff, and hoisted on a\n   lower mast or on a small mast, called the trysail mast, close abaft a\n   lower mast; -- used chiefly as a storm sail. Called also spencer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Try-square', 'n.', 'An instrument used by carpenters, joiners, etc., for\n   laying off right angles off right angles, and testing whether work is\n   square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tryst', 'n.', 'Trust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tryst', 'n.', 'An appointment to meet; also, an appointed place or time of\n   meeting; as, to keep tryst; to break tryst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tryst', 'n.', 'To trust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tryst', 'n.', 'To agree with to meet at a certain place; to make an\n   appointment with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tryst', 'v. i.', 'To mutually agree to meet at a certain place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tryster', 'n.', 'One who makes an appointment, or tryst; one who meets with\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Trysting', 'n.', 'An appointment; a tryst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tsar', 'n.', 'The title of the emperor of Russia. See Czar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tsarina', 'n.', 'Alt. of Tsaritsa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tsaritsa', 'n.', 'The title of the empress of Russia. See Czarina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tschakmeck', 'n.', 'The chameck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tschego', 'n.', 'A West African anthropoid ape allied to the gorilla and\n   chimpanzee, and by some considered only a variety of the chimpanzee. It\n   is noted for building large, umbrella-shaped nests in trees. Called\n   also tscheigo, tschiego, nschego, nscheigo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tsebe', 'n.', 'The springbok.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tsetse', 'n.', 'A venomous two-winged African fly (Glossina morsitans)\n   whose bite is very poisonous, and even fatal, to horses and cattle, but\n   harmless to men. It renders extensive districts in which it abounds\n   uninhabitable during certain seasons of the year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'T square', '', 'See under T.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuatera', 'n.', 'See Hatteria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tub', 'n.', 'An open wooden vessel formed with staves, bottom, and hoops; a\n   kind of short cask, half barrel, or firkin, usually with but one head,\n   -- used for various purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tub', 'n.', 'The amount which a tub contains, as a measure of quantity; as,\n   a tub of butter; a tub of camphor, which is about 1 cwt., etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tub', 'n.', 'Any structure shaped like a tub: as, a certain old form of\n   pulpit; a short, broad boat, etc., -- often used jocosely or\n   opprobriously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tub', 'n.', 'A sweating in a tub; a tub fast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tub', 'n.', 'A small cask; as, a tub of gin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tub', 'n.', 'A box or bucket in which coal or ore is sent up a shaft; -- so\n   called by miners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubbed', 'imp. & p. p.', 'of Tub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubbing', 'p. pr. & vb. n.', 'of Tub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tub', 'v. t.', 'To plant or set in a tub; as, to tub a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tub', 'i.', 'To make use of a bathing tub; to lie or be in a bath; to\n   bathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuba', 'n.', 'An ancient trumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuba', 'n.', 'A sax-tuba. See Sax-tuba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubal', 'a.', 'Of or pertaining to a tube; specifically, of or pertaining\n   to one of the Fallopian tubes; as, tubal pregnancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubbing', 'n.', 'The forming of a tub; also, collectively, materials for\n   tubs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubbing', 'n.', 'A lining of timber or metal around the shaft of a mine;\n   especially, a series of cast-iron cylinders bolted together, used to\n   enable those who sink a shaft to penetrate quicksand, water, etc., with\n   safety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubby', 'a.', 'Resembling a tub; specifically sounding dull and without\n   resonance, like a tub; wanting elasticity or freedom of sound; as, a\n   tubby violin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tube', 'n.', 'A hollow cylinder, of any material, used for the conveyance\n   of fluids, and for various other purposes; a pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tube', 'n.', 'A telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tube', 'n.', 'A vessel in animal bodies or plants, which conveys a fluid or\n   other substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tube', 'n.', 'The narrow, hollow part of a gamopetalous corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tube', 'n.', 'A priming tube, or friction primer. See under Priming, and\n   Friction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tube', 'n.', 'A small pipe forming part of the boiler, containing water and\n   surrounded by flame or hot gases, or else surrounded by water and\n   forming a flue for the gases to pass through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tube', 'n.', 'A more or less cylindrical, and often spiral, case secreted\n   or constructed by many annelids, crustaceans, insects, and other\n   animals, for protection or concealment. See Illust. of Tubeworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tube', 'n.', 'One of the siphons of a bivalve mollusk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubed', 'imp. & p. p.', 'of Tube');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubing', 'p. pr. & vb. n.', 'of Tube');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tube', 'v. t.', 'To furnish with a tube; as, to tube a well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubeform', 'a.', 'In the form of a tube; tubular; tubiform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tube-nosed', 'a.', 'Having the nostrils prolonged in the form of horny\n   tubes along the sides of the beak; -- said of certain sea birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tube-nosed', 'a.', 'Belonging to the Tubinares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuber', 'n.', 'A fleshy, rounded stem or root, usually containing starchy\n   matter, as the potato or arrowroot; a thickened root-stock. See Illust.\n   of Tuberous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuber', 'n.', 'A genus of fungi. See Truffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuber', 'n.', 'A tuberosity; a tubercle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubercle', 'n.', 'A small knoblike prominence or excrescence, whether\n   natural or morbid; as, a tubercle on a plant; a tubercle on a bone; the\n   tubercles appearing on the body in leprosy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubercle', 'n.', 'A small mass or aggregation of morbid matter; especially,\n   the deposit which accompanies scrofula or phthisis. This is composed of\n   a hard, grayish, or yellowish, translucent or opaque matter, which\n   gradually softens, and excites suppuration in its vicinity. It is most\n   frequently found in the lungs, causing consumption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubercled', 'a.', 'Having tubercles; affected with, tubercles; tuberculate;\n   as, a tubercled lung or stalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubercular', 'a.', 'Having tubercles; affected with tubercles; tubercled;\n   tuberculate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubercular', 'a.', 'Like a tubercle; as, a tubercular excrescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubercular', 'a.', 'Characterized by the development of tubercles; as,\n   tubercular diathesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuberculate', 'a.', 'Alt. of Tuberculated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuberculated', 'a.', 'Tubercled; tubercular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuberculin', 'n.', 'A fluid containing the products formed by the growth of\n   the tubercle bacillus in a suitable culture medium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuberculization', 'n.', 'The development of tubercles; the condition of one\n   who is affected with tubercles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuberculose', 'a.', 'Alt. of Tuberculous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuberculous', 'a.', 'Having tubercles; affected with, or characterized by,\n   tubercles; tubercular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuberculosis', 'n.', 'A constitutional disease characterized by the\n   production of tubercles in the internal organs, and especially in the\n   lungs, where it constitutes the most common variety of pulmonary\n   consumption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubercula', 'pl. ', 'of Tuberculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuberculum', 'n.', 'A tubercle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuberiferous', 'a.', 'Producing or bearing tubers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuberose', 'n.', 'A plant (Polianthes tuberosa) with a tuberous root and a\n   liliaceous flower. It is much cultivated for its beautiful and fragrant\n   white blossoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuberose', 'a.', 'Tuberous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuberosities', 'pl. ', 'of Tuberosity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuberosity', 'n.', 'The state of being tuberous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuberosity', 'n.', 'An obtuse or knoblike prominence; a protuberance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuberous', 'a.', 'Covered with knobby or wartlike prominences; knobbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuberous', 'a.', 'Consisting of, or bearing, tubers; resembling a tuber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tube-shell', 'n.', 'Any bivalve mollusk which secretes a shelly tube around\n   its siphon, as the watering-shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubeworm', 'n.', 'Any annelid which constructs a tube; one of the\n   Tubicolae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubfish', 'n.', 'The sapphirine gurnard (Trigla hirundo). See Illust. under\n   Gurnard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubfuls', 'pl. ', 'of Tubful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubful', 'n.', 'As much as a tub will hold; enough to fill a tub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubicinate', 'v. i.', 'To blow a trumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubicolae', 'n. pl.', 'A division of annelids including those which\n   construct, and habitually live in, tubes. The head or anterior segments\n   usually bear gills and cirri. Called also Sedentaria, and\n   Capitibranchiata. See Serpula, and Sabella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubicolar', 'a.', 'Tubicolous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubicole', 'n.', 'One of the Tubicolae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubicolous', 'a.', 'Inhabiting a tube; as, tubicolous worms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubicorn', 'n.', 'Any ruminant having horns composed of a bony axis covered\n   with a horny sheath; a hollow-horned ruminant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubicornous', 'a.', 'Having hollow horns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubiform', 'a.', 'Having the form of a tube; tubeform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubinares', 'n. pl.', 'A tribe of sea birds comprising the petrels,\n   shearwaters, albatrosses, hagdons, and allied birds having tubular\n   horny nostrils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubing', 'n.', 'The act of making tubes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubing', 'n.', 'A series of tubes; tubes, collectively; a length or piece\n   of a tube; material for tubes; as, leather tubing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubipora', 'n.', 'A genus of halcyonoids in which the skeleton, or coral\n   (called organ-pipe coral), consists of a mass of parallel cylindrical\n   tubes united at intervals by transverse plates. These corals are\n   usually red or purple and form large masses. They are natives of the\n   tropical parts of the Indian and Pacific Oceans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubipore', 'n.', 'Any species of the genus Tubipora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubiporite', 'n.', 'Any fossil coral of the genus Syringopora consisting of\n   a cluster of upright tubes united together by small transverse tubules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubivalve', 'n.', 'A shell or tube formed by an annelid, as a serpula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubmen', 'pl. ', 'of Tubman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubman', 'n.', 'One of the two most experienced barristers in the Court of\n   Exchequer. Cf. Postman, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubular', 'a.', 'Having the form of a tube, or pipe; consisting of a pipe;\n   fistular; as, a tubular snout; a tubular calyx. Also, containing, or\n   provided with, tubes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubularia', 'n.', 'A genus of hydroids having large, naked, flowerlike\n   hydranths at the summits of long, slender, usually simple, stems. The\n   gonophores are small, and form clusters at the bases of the outer\n   tentacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubulariae', 'n. pl.', 'See Tubularida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubularian', 'n.', 'Any hydroid belonging to the suborder Tubularida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubularian', 'a.', 'Of or pertaining to the tubularians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubularida', 'n. pl.', 'An extensive division of Hydroidea; the\n   tubularians; -- called also Athecata, Gymnoblastea, and Tubulariae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubulate', 'a.', 'Tubular; tubulated; tubulous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubulated', 'a.', 'Made in the form of a small tube; provided with a tube,\n   or elongated opening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubulation', 'n.', 'The act of shaping or making a tube, or of providing\n   with a tube; also, a tube or tubulure; as, the tubulation of a retort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubulature', 'n.', 'A tubulure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubule', 'n.', 'A small pipe or fistular body; a little tube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubule', 'n.', 'A minute tube lined with glandular epithelium; as, the\n   uriniferous tubules of the kidney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubulibranchian', 'n.', 'One of the Tubulibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubulibranchiata', 'n. pl.', 'A group of gastropod mollusks having a\n   tubular shell. Vermetus is an example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubulicole', 'n.', 'Any hydroid which has tubular chitinous stems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubulidentate', 'a.', 'Having teeth traversed by canals; -- said of certain\n   edentates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubuliform', 'a.', 'Having the form of a small tube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubulipore', 'n.', 'Any one of numerous species of Bryozoa belonging to\n   Tubulipora and allied genera, having tubular calcareous calicles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubulose', 'a.', 'Alt. of Tubulous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubulous', 'a.', 'Resembling, or in the form of, a tube; longitudinally\n   hollow; specifically (Bot.), having a hollow cylindrical corolla, often\n   expanded or toothed at the border; as, a tubulose flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubulous', 'a.', 'Containing, or consisting of, small tubes; specifically\n   (Bot.), composed wholly of tubulous florets; as, a tubulous compound\n   flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tubulure', 'n.', 'A short tubular opening at the top of a retort, or at the\n   top or side of a bottle; a tubulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tucan', 'n.', 'The Mexican pocket gopher (Geomys Mexicanus). It resembles\n   the common pocket gopher of the Western United States, but is larger.\n   Called also tugan, and tuza.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tucet', 'n.', 'See Tucket, a steak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuch', 'n.', 'A dark-colored kind of marble; touchstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuck', 'n.', 'A long, narrow sword; a rapier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuck', 'n.', 'The beat of a drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tucked', 'imp. & p. p.', 'of Tuck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tucking', 'p. pr. & vb. n.', 'of Tuck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuck', 'v. t.', 'To draw up; to shorten; to fold under; to press into a\n   narrower compass; as, to tuck the bedclothes in; to tuck up ones\n   sleeves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuck', 'v. t.', 'To make a tuck or tucks in; as, to tuck a dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuck', 'v. t.', 'To inclose; to put within; to press into a close place;\n   as, to tuck a child into a bed; to tuck a book under ones arm, or into\n   a pocket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuck', 'v. t.', 'To full, as cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuck', 'v. i.', 'To contract; to draw together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuck', 'n.', 'A horizontal sewed fold, such as is made in a garment, to\n   shorten it; a plait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuck', 'n.', 'A small net used for taking fish from a larger one; -- called\n   also tuck-net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuck', 'n.', 'A pull; a lugging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuck', 'n.', 'The part of a vessel where the ends of the bottom planks meet\n   under the stern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuck', 'n.', 'Food; pastry; sweetmeats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuckahoe', 'n.', 'A curious vegetable production of the Southern Atlantic\n   United States, growing under ground like a truffle and often attaining\n   immense size. The real nature is unknown. Called also Indian bread, and\n   Indian loaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tucker', 'n.', 'One who, or that which, tucks; specifically, an instrument\n   with which tuck are made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tucker', 'n.', 'A narrow piece of linen or the like, folded across the\n   breast, or attached to the gown at the neck, forming a part of a\n   womans dress in the 17th century and later.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tucker', 'v. t.', 'A fuller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tucker', 'v. t.', 'To tire; to weary; -- usually with out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tucket', 'n.', 'A slight flourish on a trumpet; a fanfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tucket', 'n.', 'A steak; a collop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuck-net', 'n.', 'See Tuck, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tucum', 'n.', 'A fine, strong fiber obtained from the young leaves of a\n   Brazilian palm (Astrocaryum vulgare), used for cordage, bowstrings,\n   etc.; also, the plant yielding this fiber. Called also tecum, and tecum\n   fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tucuma', 'n.', 'A Brazilian palm (Astrocaryum Tucuma) which furnishes an\n   edible fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tudor', 'a.', 'Of or pertaining to a royal line of England, descended from\n   Owen Tudor of Wales, who married the widowed queen of Henry V. The\n   first reigning Tudor was Henry VII.; the last, Elizabeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tue', 'n.', 'The parson bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuefall', 'n.', 'See To-fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tue-iron', 'n.', 'See Tuyere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tue-irons', 'n. pl.', 'A pair of blacksmiths tongs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuesday', 'n.', 'The third day of the week, following Monday and preceding\n   Wednesday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuet', 'n.', 'The lapwing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tufa', '', 'A soft or porous stone formed by depositions from water,\n   usually calcareous; -- called also calcareous tufa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tufa', '', 'A friable volcanic rock or conglomerate, formed of consolidated\n   cinders, or scoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tufaceous', 'a.', 'Pertaining to tufa; consisting of, or resembling, tufa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuff', 'n.', 'Same as Tufa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuffoon', 'n.', 'See Typhoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuft', 'n.', 'A collection of small, flexible, or soft things in a knot or\n   bunch; a waving or bending and spreading cluster; as, a tuft of flowers\n   or feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuft', 'n.', 'A cluster; a clump; as, a tuft of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuft', 'n.', 'A nobleman, or person of quality, especially in the English\n   universities; -- so called from the tuft, or gold tassel, on the cap\n   worn by them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tufted', 'imp. & p. p.', 'of Tuft');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tufting', 'p. pr. & vb. n.', 'of Tuft');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuft', 'v. t.', 'To separate into tufts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuft', 'v. t.', 'To adorn with tufts or with a tuft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuft', 'v. i.', 'To grow in, or form, a tuft or tufts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuf-taffeta', 'n.', 'A silk fabric formerly in use, having a nap or pile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tufted', 'a.', 'Adorned with a tuft; as, the tufted duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tufted', 'a.', 'Growing in tufts or clusters; tufty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tufthunter', 'n.', 'A hanger-on to noblemen, or persons of quality,\n   especially in English universities; a toady. See 1st Tuft, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tufthunting', 'n.', 'The practice of seeking after, and hanging on,\n   noblemen, or persons of quality, especially in English universities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tufty', 'a.', 'Abounding with tufts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tufty', 'a.', 'Growing in tufts or clusters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tugged', 'imp. & p. p.', 'of Tug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tugging', 'p. pr. & vb. n.', 'of Tug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tug', 'v. t.', 'To pull or draw with great effort; to draw along with\n   continued exertion; to haul along; to tow; as, to tug a loaded cart; to\n   tug a ship into port.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tug', 'v. t.', 'To pull; to pluck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tug', 'v. i.', 'To pull with great effort; to strain in labor; as, to tug\n   at the oar; to tug against the stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tug', 'v. i.', 'To labor; to strive; to struggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tug', 'n.', 'A pull with the utmost effort, as in the athletic contest\n   called tug of war; a supreme effort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tug', 'n.', 'A sort of vehicle, used for conveying timber and heavy\n   articles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tug', 'n.', 'A small, powerful steamboat used to tow vessels; -- called\n   also steam tug, tugboat, and towboat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tug', 'n.', 'A trace, or drawing strap, of a harness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tug', 'n.', 'An iron hook of a hoisting tub, to which a tackle is affixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tugan', 'n.', 'Same as Tucan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tugboat', 'n.', 'See Tug, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tugger', 'n.', 'One who tugs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuggingly', 'adv.', 'In a tugging manner; with laborious pulling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tulle', 'n.', 'In plate armor, a suspended plate in from of the thigh. See\n   Illust. of Tasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuition', 'n.', 'Superintending care over a young person; the particular\n   watch and care of a tutor or guardian over his pupil or ward;\n   guardianship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuition', 'n.', 'Especially, the act, art, or business of teaching;\n   instruction; as, children are sent to school for tuition; his tuition\n   was thorough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuition', 'n.', 'The money paid for instruction; the price or payment for\n   instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuitionary', 'a.', 'Of or pertaining to tuition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuko-tuko', 'n.', 'A burrowing South American rodent (Ctenomys\n   Braziliensis). It has small eyes and ears and a short tail. It\n   resembles the pocket gopher in size, form, and habits, but is more\n   nearly allied to the porcupines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tula metal', '', 'An alloy of silver, copper, and lead made at Tula in\n   Russia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tule', 'n.', 'A large bulrush (Scirpus lacustris, and S. Tatora) growing\n   abundantly on overflowed land in California and elsewhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tulip', 'n.', 'Any plant of the liliaceous genus Tulipa. Many varieties are\n   cultivated for their beautiful, often variegated flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tulip-eared', 'a.', 'Having erect, pointed ears; prick-eared; -- said of\n   certain dogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tulipist', 'n.', 'A person who is especially devoted to the cultivation of\n   tulips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tulipomania', 'n.', 'A violent passion for the acquisition or cultivation\n   of tulips; -- a word said by Beckman to have been coined by Menage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tulipomaniac', 'n.', 'One who is affected with tulipomania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tulip-shell', 'n.', 'A large, handsomely colored, marine univalve shell\n   (Fasciolaria tulipa) native of the Southern United States. The name is\n   sometimes applied also to other species of Fasciolaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tulipwood', 'n.', 'The beautiful rose-colored striped wood of a Brazilian\n   tree (Physocalymna floribunda), much used by cabinetmakers for\n   inlaying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tull', 'v. t.', 'To allure; to tole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tulle', 'n.', 'A kind of silk lace or light netting, used for veils, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tullian', 'a.', 'Belonging to, or in the style of, Tully (Marcus Tullius\n   Cicero).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tullibee', 'n.', 'A whitefish (Coregonus tullibee) found in the Great Lakes\n   of North America; -- called also mongrel whitefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumbled', 'imp. & p. p.', 'of Tumble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumbling', 'p. pr. & vb. n.', 'of Tumble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumble', 'v. i.', 'To roll over, or to and fro; to throw ones self about;\n   as, a person on pain tumbles and tosses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumble', 'v. i.', 'To roll down; to fall suddenly and violently; to be\n   precipitated; as, to tumble from a scaffold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumble', 'v. i.', 'To play tricks by various movements and contortions of\n   the body; to perform the feats of an acrobat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumble', 'v. t.', 'To turn over; to turn or throw about, as for examination\n   or search; to roll or move in a rough, coarse, or unceremonious manner;\n   to throw down or headlong; to precipitate; -- sometimes with over,\n   about, etc.; as, to tumble books or papers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumble', 'v. t.', 'To disturb; to rumple; as, to tumble a bed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumble', 'n.', 'Act of tumbling, or rolling over; a fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumblebug', 'n.', 'See Tumbledung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumble-down', 'a.', 'Ready to fall; dilapidated; ruinous; as, a tumble-down\n   house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumbledung', 'n.', 'Any one of numerous species of scaraboid beetles\n   belonging to Scarabaeus, Copris, Phanaeus, and allied genera. The\n   female lays her eggs in a globular mass of dung which she rolls by\n   means of her hind legs to a burrow excavated in the earth in which she\n   buries it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumbler', 'n.', 'One who tumbles; one who plays tricks by various motions\n   of the body; an acrobat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumbler', 'n.', 'A movable obstruction in a lock, consisting of a lever,\n   latch, wheel, slide, or the like, which must be adjusted to a\n   particular position by a key or other means before the bolt can be\n   thrown in locking or unlocking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumbler', 'n.', 'A piece attached to, or forming part of, the hammer of a\n   gunlock, upon which the mainspring acts and in which are the notches\n   for sear point to enter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumbler', 'n.', 'A drinking glass, without a foot or stem; -- so called\n   because originally it had a pointed or convex base, and could not be\n   set down with any liquor in it, thus compelling the drinker to finish\n   his measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumbler', 'n.', 'A variety of the domestic pigeon remarkable for its habit\n   of tumbling, or turning somersaults, during its flight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumbler', 'n.', 'A breed of dogs that tumble when pursuing game. They were\n   formerly used in hunting rabbits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumbler', 'n.', 'A kind of cart; a tumbrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumblerfuls', 'pl. ', 'of Tumblerful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumblerful', 'n.', 'As much as a tumbler will hold; enough to fill a\n   tumbler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumbleweed', 'n.', 'Any plant which habitually breaks away from its roots\n   in the autumn, and is driven by the wind, as a light, rolling mass,\n   over the fields and prairies; as witch grass, wild indigo, Amarantus\n   albus, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumbling', '', 'a. & vb. n. from Tumble, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumbrel', 'n.', 'Alt. of Tumbril');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumbril', 'n.', 'A cucking stool for the punishment of scolds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumbril', 'n.', 'A rough cart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumbril', 'n.', 'A cart or carriage with two wheels, which accompanies\n   troops or artillery, to convey the tools of pioneers, cartridges, and\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumbril', 'n.', 'A kind of basket or cage of osiers, willows, or the like,\n   to hold hay and other food for sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumefaction', 'n.', 'The act or process of tumefying, swelling, or rising\n   into a tumor; a swelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumefied', 'imp. & p. p.', 'of Tumefy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumefying', 'p. pr. & vb. n.', 'of Tumefy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumefy', 'v. t.', 'To swell; to cause to swell, or puff up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumefy', 'v. i.', 'To rise in a tumor; to swell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumid', 'a.', 'Swelled, enlarged, or distended; as, a tumid leg; tumid\n   flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumid', 'a.', 'Rising above the level; protuberant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumid', 'a.', 'Swelling in sound or sense; pompous; puffy; inflated;\n   bombastic; falsely sublime; turgid; as, a tumid expression; a tumid\n   style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumidity', 'n.', 'The quality or state of being tumid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tummals', 'n.', 'A great quantity or heap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumor', 'n.', 'A morbid swelling, prominence, or growth, on any part of the\n   body; especially, a growth produced by deposition of new tissue; a\n   neoplasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumor', 'n.', 'Affected pomp; bombast; swelling words or expressions; false\n   magnificence or sublimity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumored', 'a.', 'Distended; swelled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumorous', 'a.', 'Swelling; protuberant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumorous', 'a.', 'Inflated; bombastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tump', 'n.', 'A little hillock; a knoll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumped', 'imp. & p. p.', 'of Tump');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumping', 'p. pr. & vb. n.', 'of Tump');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tump', 'v. t.', 'To form a mass of earth or a hillock about; as, to tump\n   teasel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tump', 'v. t.', 'To draw or drag, as a deer or other animal after it has\n   been killed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumpline', 'n.', 'A strap placed across a mans forehead to assist him in\n   carrying a pack on his back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tum-tum', 'n.', 'A dish made in the West Indies by beating boiled plantain\n   quite soft in a wooden mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumular', 'a.', 'Consisting in a heap; formed or being in a heap or\n   hillock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumulate', 'v. t.', 'To cover, as a corpse, with a mound or tomb; to bury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumulate', 'v. i.', 'To swell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumulose', 'a.', 'Tumulous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumulosity', 'n.', 'The quality or state of being tumulous; hilliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumulous', 'a.', 'Full of small hills or mounds; hilly; tumulose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumult', 'n.', 'The commotion or agitation of a multitude, usually\n   accompanied with great noise, uproar, and confusion of voices;\n   hurly-burly; noisy confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumult', 'n.', 'Violent commotion or agitation, with confusion of sounds;\n   as, the tumult of the elements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumult', 'n.', 'Irregular or confused motion; agitation; high excitement;\n   as, the tumult of the spirits or passions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumult', 'v. i.', 'To make a tumult; to be in great commotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumulter', 'n.', 'A maker of tumults.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumultuarily', 'adv.', 'In a tumultuary manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumultuariness', 'n.', 'The quality or state of being tumultuary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumultuary', 'a.', 'Attended by, or producing, a tumult; disorderly;\n   promiscuous; confused; tumultuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumultuary', 'a.', 'Restless; agitated; unquiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumultuate', 'v. i.', 'To make a tumult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumultuation', 'n.', 'Irregular or disorderly movement; commotion; as, the\n   tumultuation of the parts of a fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumultuous', 'a.', 'Full of tumult; characterized by tumult; disorderly;\n   turbulent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumultuous', 'a.', 'Conducted with disorder; noisy; confused; boisterous;\n   disorderly; as, a tumultuous assembly or meeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumultuous', 'a.', 'Agitated, as with conflicting passions; disturbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumultuous', 'a.', 'Turbulent; violent; as, a tumultuous speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumuli', 'pl. ', 'of Tumulus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tumulus', 'n.', 'An artificial hillock, especially one raised over a grave,\n   particularly over the graves of persons buried in ancient times; a\n   barrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tun', 'n.', 'A large cask; an oblong vessel bulging in the middle, like a\n   pipe or puncheon, and girt with hoops; a wine cask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tun', 'n.', 'A fermenting vat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tun', 'n.', 'A certain measure for liquids, as for wine, equal to two\n   pipes, four hogsheads, or 252 gallons. In different countries, the tun\n   differs in quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tun', 'n.', 'A weight of 2,240 pounds. See Ton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tun', 'n.', 'An indefinite large quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tun', 'n.', 'A drunkard; -- so called humorously, or in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tun', 'n.', 'Any shell belonging to Dolium and allied genera; -- called\n   also tun-shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunned', 'imp. & p. p.', 'of Tun');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunning', 'p. pr. & vb. n.', 'of Tun');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tun', 'v. i.', 'To put into tuns, or casks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuna', 'n.', 'The Opuntia Tuna. See Prickly pear, under Prickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuna', 'n.', 'The tunny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuna', 'n.', 'The bonito, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunable', 'a.', 'Capable of being tuned, or made harmonious; hence,\n   harmonious; musical; tuneful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tun-bellied', 'a.', 'Having a large, protuberant belly, or one shaped like\n   a tun; pot-bellied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tun-dish', 'n.', 'A tunnel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tundra', 'n.', 'A rolling, marshy, mossy plain of Northern Siberia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tune', 'n.', 'A sound; a note; a tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tune', 'n.', 'A rhythmical, melodious, symmetrical series of tones for one\n   voice or instrument, or for any number of voices or instruments in\n   unison, or two or more such series forming parts in harmony; a melody;\n   an air; as, a merry tune; a mournful tune; a slow tune; a psalm tune.\n   See Air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tune', 'n.', 'The state of giving the proper, sound or sounds; just\n   intonation; harmonious accordance; pitch of the voice or an instrument;\n   adjustment of the parts of an instrument so as to harmonize with itself\n   or with others; as, the piano, or the organ, is not in tune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tune', 'n.', 'Order; harmony; concord; fit disposition, temper, or humor;\n   right mood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuned', 'imp. & p. p.', 'of Tune');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuning', 'p. pr. & vb. n.', 'of Tune');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tune', 'v. t.', 'To put into a state adapted to produce the proper sounds;\n   to harmonize, to cause to be in tune; to correct the tone of; as, to\n   tune a piano or a violin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tune', 'v. t.', 'To give tone to; to attune; to adapt in style of music; to\n   make harmonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tune', 'v. t.', 'To sing with melody or harmony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tune', 'v. t.', 'To put into a proper state or disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tune', 'v. i.', 'To form one sound to another; to form accordant musical\n   sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tune', 'v. i.', 'To utter inarticulate harmony with the voice; to sing\n   without pronouncing words; to hum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuneful', 'a.', 'Harmonious; melodious; musical; as, tuneful notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuneless', 'a.', 'Without tune; inharmonious; unmusical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuneless', 'a.', 'Not employed in making music; as, tuneless harps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuneless', 'a.', 'Not expressed in music or poetry; unsung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuner', 'n.', 'One who tunes; especially, one whose occupation is to tune\n   musical instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tun-great', 'a.', 'Having the circumference of a tun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tungstate', 'n.', 'A salt of tungstic acid; a wolframate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tungsten', 'n.', 'A rare element of the chromium group found in certain\n   minerals, as wolfram and scheelite, and isolated as a heavy steel-gray\n   metal which is very hard and infusible. It has both acid and basic\n   properties. When alloyed in small quantities with steel, it greatly\n   increases its hardness. Symbol W (Wolframium). Atomic weight, 183.6.\n   Specific gravity, 18.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tungsten', 'n.', 'Scheelite, or calcium tungstate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tungstenic', 'a.', 'Of or pertaining to tungsten; containing tungsten; as,\n   tungstenic ores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tungstic', 'a.', 'Of or pertaining to tungsten; derived from, or\n   resembling, tungsten; wolframic; as, tungstic oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tungstite', 'n.', 'The oxide of tungsten, a yellow mineral occurring in a\n   pulverulent form. It is often associated with wolfram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunguses', 'n. pl.', 'A group of roving Turanian tribes occupying Eastern\n   Siberia and the Amoor valley. They resemble the Mongols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tungusic', 'a.', 'Of or pertaining to the Tunguses; as, the Tungusic\n   dialects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunhoof', 'n.', 'Ground ivy; alehoof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunic', 'n.', 'An under-garment worn by the ancient Romans of both sexes.\n   It was made with or without sleeves, reached to or below the knees, and\n   was confined at the waist by a girdle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunic', 'n.', 'Any similar garment worm by ancient or Oriental peoples;\n   also, a common name for various styles of loose-fitting under-garments\n   and over-garments worn in modern times by Europeans and others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunic', 'n.', 'Same as Tunicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunic', 'n.', 'A membrane, or layer of tissue, especially when enveloping\n   an organ or part, as the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunic', 'n.', 'A natural covering; an integument; as, the tunic of a seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunic', 'n.', 'See Mantle, n., 3 (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunicaries', 'pl. ', 'of Tunicary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunicary', 'n.', 'One of the Tunicata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunicata', 'n. pl.', 'A grand division of the animal kingdom, intermediate,\n   in some respects, between the invertebrates and vertebrates, and by\n   some writers united with the latter. They were formerly classed with\n   acephalous mollusks. The body is usually covered with a firm external\n   tunic, consisting in part of cellulose, and having two openings, one\n   for the entrance and one for the exit of water. The pharynx is usually\n   dilated in the form of a sac, pierced by several series of ciliated\n   slits, and serves as a gill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunicate', 'a.', 'Alt. of Tunicated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunicated', 'a.', 'Covered with a tunic; covered or coated with layers; as,\n   a tunicated bulb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunicated', 'a.', 'Having a tunic, or mantle; of or pertaining to the\n   Tunicata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunicated', 'a.', 'Having each joint buried in the preceding funnel-shaped\n   one, as in certain antennae of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunicate', 'n.', 'One of the Tunicata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunicin', 'n.', 'Animal cellulose; a substance present in the mantle, or\n   tunic, of the Tunicates, which resembles, or is identical with, the\n   cellulose of the vegetable kingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunicle', 'n.', 'A slight natural covering; an integument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunicle', 'n.', 'A short, close-fitting vestment worn by bishops under the\n   dalmatic, and by subdeacons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuning', '', 'a. & n. from Tune, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunk', 'n.', 'A sharp blow; a thump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunker', 'n.', 'Same as Dunker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunnage', 'n.', 'See Tonnage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunnel', 'n. .', 'A vessel with a broad mouth at one end, a pipe or tube at\n   the other, for conveying liquor, fluids, etc., into casks, bottles, or\n   other vessels; a funnel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunnel', 'n. .', 'The opening of a chimney for the passage of smoke; a\n   flue; a funnel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunnel', 'n. .', 'An artificial passage or archway for conducting canals or\n   railroads under elevated ground, for the formation of roads under\n   rivers or canals, and the construction of sewers, drains, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunnel', 'n. .', 'A level passage driven across the measures, or at right\n   angles to veins which it is desired to reach; -- distinguished from the\n   drift, or gangway, which is led along the vein when reached by the\n   tunnel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunneled', 'imp. & p. p.', 'of Tunnel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunnelled', '', 'of Tunnel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunneling', 'p. pr. & vb. n.', 'of Tunnel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunnelling', '', 'of Tunnel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunnel', 'v. t.', 'To form into a tunnel, or funnel, or to form like a\n   tunnel; as, to tunnel fibrous plants into nests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunnel', 'v. t.', 'To catch in a tunnel net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunnel', 'v. t.', 'To make an opening, or a passageway, through or under;\n   as, to tunnel a mountain; to tunnel a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunnies', 'pl. ', 'of Tunny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tunny', 'n.', 'Any one of several species of large oceanic fishes belonging\n   to the Mackerel family, especially the common or great tunny (Orcynus /\n   Albacora thynnus) native of the Mediterranean Sea and the Atlantic\n   Ocean. It sometimes weighs a thousand pounds or more, and is\n   extensively caught in the Mediterranean. On the American coast it is\n   called horse mackerel. See Illust. of Horse mackerel, under Horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tup', 'v. t. & i.', 'To butt, as a ram does.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tup', 'v. t. & i.', 'To cover; -- said of a ram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tup', 'n.', 'A ram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tupal', 'n.', 'Any one of the tupaiids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tupaiid', 'n.', 'Any one of several species of East Indian and Asiatic\n   insectivores of the family Tupaiidae, somewhat resembling squirrels in\n   size and arboreal habits. The nose is long and pointed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tupelo', 'n.', 'A North American tree (Nyssa multiflora) of the Dogwood\n   family, having brilliant, glossy foliage and acid red berries. The wood\n   is crossgrained and very difficult to split. Called also black gum,\n   sour gum, and pepperidge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tupmen', 'pl. ', 'of Tupman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tupman', 'n.', 'A man who breeds, or deals in tups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tur', 'n.', 'The urus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turacin', 'n.', 'A red or crimson pigment obtained from certain feathers of\n   several species of turacou; whence the name. It contains nearly six per\n   cent of copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turacou', 'n.', 'Any one of several species of plantain eaters of the genus\n   Turacus, native of Africa. They are remarkable for the peculiar green\n   and red pigments found in their feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turacoverdin', 'n.', 'A green pigment found in the feathers of the turacou.\n   See Turacin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turanian', 'a.', 'Of, pertaining to, or designating, an extensive family of\n   languages of simple structure and low grade (called also Altaic,\n   Ural-Altaic, and Scythian), spoken in the northern parts of Europe and\n   Asia and Central Asia; of pertaining to, or designating, the people who\n   speak these languages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turanian', 'n.', 'One of the Turanians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turanians', 'n. pl.', 'An extensive division of mankind including the\n   Mongols and allied races of Asia, together with the Malays and\n   Polynesians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turanians', 'n. pl.', 'A group of races or tribes inhabiting Asia and\n   closely related to the Mongols.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turatt', 'n.', 'The hare kangaroo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turban', 'n.', 'A headdress worn by men in the Levant and by most\n   Mohammedans of the male sex, consisting of a cap, and a sash, scarf, or\n   shawl, usually of cotton or linen, wound about the cap, and sometimes\n   hanging down the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turban', 'n.', 'A kind of headdress worn by women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turban', 'n.', 'The whole set of whorls of a spiral shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turband', 'n.', 'A turban.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbaned', 'a.', 'Wearing a turban.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turban-shell', 'n.', 'A sea urchin when deprived of its spines; --\n   popularly so called from a fancied resemblance to a turban.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbant', 'n.', 'A turban.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turban-top', 'n.', 'A kind of fungus with an irregularly wrinkled, somewhat\n   globular pileus (Helvella, / Gyromitra, esculenta.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbaries', 'pl. ', 'of Turbary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbary', 'n.', 'A right of digging turf on another mans land; also, the\n   ground where turf is dug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbellaria', 'n. pl.', 'An extensive group of worms which have the body\n   covered externally with vibrating cilia. It includes the Rhabdoc/la and\n   Dendroc/la. Formerly, the nemerteans were also included in this group.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbellarian', 'n.', 'One of the Turbellaria. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbeth', 'n.', 'See Turpeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbid', 'a.', 'Having the lees or sediment disturbed; roiled; muddy;\n   thick; not clear; -- used of liquids of any kind; as, turbid water;\n   turbid wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbid', 'a.', 'Disturbed; confused; disordered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbidity', 'n.', 'Turbidness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbidly', 'adv.', 'In a turbid manner; with muddiness or confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbidly', 'adv.', 'Proudly; haughtily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbidness', 'n.', 'The quality or state of being turbid; muddiness;\n   foulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbillion', 'n.', 'A whirl; a vortex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbinaceous', 'a.', 'Of or pertaining to peat, or turf; of the nature of\n   peat, or turf; peaty; turfy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbinal', 'a.', 'Rolled in a spiral; scroll-like; turbinate; -- applied to\n   the thin, plicated, bony or cartilaginous plates which support the\n   olfactory and mucous membranes of the nasal chambers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbinal', 'n.', 'A turbinal bone or cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbinate', 'v. i.', 'To revolve or spin like a top; to whirl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbinate', 'a.', 'Alt. of Turbinated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbinated', 'a.', 'Whirling in the manner of a top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbinated', 'a.', 'Shaped like a top, or inverted cone; narrow at the\n   base, and broad at the apex; as, a turbinated ovary, pericarp, or root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbinated', 'a.', 'Turbinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbinated', 'a.', 'Spiral with the whorls decreasing rapidly from a large\n   base to a pointed apex; -- said of certain shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbination', 'n.', 'The act of spinning or whirling, as a top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbine', 'n.', 'A water wheel, commonly horizontal, variously constructed,\n   but usually having a series of curved floats or buckets, against which\n   the water acts by its impulse or reaction in flowing either outward\n   from a central chamber, inward from an external casing, or from above\n   downward, etc.; -- also called turbine wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbinella', 'n.', 'A genus of large marine gastropods having a thick heavy\n   shell with conspicuous folds on the columella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbinite', 'n.', 'A petrified shell resembling the genus Turbo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbinoid', 'a.', 'Like or pertaining to Turbo or the family Turbinidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbit', 'n.', 'The turbot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbit', 'n.', 'A variety of the domestic pigeon, remarkable for its short\n   beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbite', 'n.', 'A fossil turbo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbith', 'n.', 'See Turpeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbo', 'n.', 'Any one of numerous marine gastropods of the genus Turbo or\n   family Turbinidae, usually having a turbinate shell, pearly on the\n   inside, and a calcareous operculum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbot', 'n.', 'A large European flounder (Rhombus maximus) highly esteemed\n   as a food fish. It often weighs from thirty to forty pounds. Its color\n   on the upper side is brownish with small roundish tubercles scattered\n   over the surface. The lower, or blind, side is white. Called also\n   bannock fluke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbot', 'n.', 'Any one of numerous species of flounders more or less\n   related to the true turbots, as the American plaice, or summer flounder\n   (see Flounder), the halibut, and the diamond flounder (Hypsopsetta\n   guttulata) of California.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbot', 'n.', 'The filefish; -- so called in Bermuda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbot', 'n.', 'The trigger fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbulence', 'n.', 'The quality or state of being turbulent; a disturbed\n   state; tumult; disorder; agitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbulency', 'n.', 'Turbulence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbulent', 'a.', 'Disturbed; agitated; tumultuous; roused to violent\n   commotion; as, the turbulent ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbulent', 'a.', 'Disposed to insubordination and disorder; restless;\n   unquiet; refractory; as, turbulent spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbulent', 'a.', 'Producing commotion; disturbing; exciting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turbulently', 'adv.', 'In a turbulent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turcism', 'n.', 'A mode of speech peculiar to the Turks; a Turkish idiom or\n   expression; also, in general, a Turkish mode or custom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turcomans', 'pl. ', 'of Turcoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turcoman', 'n.', 'A member of a tribe of Turanians inhabiting a region east\n   of the Caspian Sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turcoman', 'n.', 'A Turcoman carpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turdiformes', 'n. pl.', 'A division of singing birds including the thrushes\n   and allied kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turdus', 'n.', 'A genus of singing birds including the true thrushes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tureen', 'n.', 'A large, deep vessel for holding soup, or other liquid\n   food, at the table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tureenfuls', 'pl. ', 'of Tureenful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tureenful', 'n.', 'As much as a tureen can hold; enough to fill a tureen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turfs', 'pl. ', 'of Turf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turves', 'pl. ', 'of Turf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turf', 'n.', 'That upper stratum of earth and vegetable mold which is\n   filled with the roots of grass and other small plants, so as to adhere\n   and form a kind of mat; sward; sod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turf', 'n.', 'Peat, especially when prepared for fuel. See Peat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turf', 'n.', 'Race course; horse racing; -- preceded by the.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turfed', 'imp. & p. p.', 'of Turf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turfing', 'p. pr. & vb. n.', 'of Turf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turf', 'v. t.', 'To cover with turf or sod; as, to turf a bank, of the\n   border of a terrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turfen', 'a.', 'Made of turf; covered with turf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turfiness', 'n.', 'Quality or state of being turfy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turfing', 'n.', 'The act or process of providing or covering with turf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turfite', 'n.', 'A votary of the turf, or race course; hence, sometimes, a\n   blackleg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turfless', 'a.', 'Destitute of turf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turfmen', 'pl. ', 'of Turfman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turfman', 'n.', 'A turfite; a votary of the turf, or race course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turfy', 'superl.', 'Abounding with turf; made of, or covered with, turf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turfy', 'superl.', 'Having the nature or appearance of turf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turfy', 'superl.', 'Of or pertaining to the turf, or horse racing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turgent', 'a.', 'Rising into a tumor, or a puffy state; swelling; tumid;\n   as, turgent humors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turgent', 'a.', 'Inflated; bombastic; turgid; pompous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turgesced', 'imp. & p. p.', 'of Turgesce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turgescing', 'p. pr. & vb. n.', 'of Turgesce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turgesce', 'v. i.', 'To become turgid; to swell or be inflated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turgescence', 'n.', 'Alt. of Turgescency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turgescency', 'n.', 'The act of swelling, or the state of being swollen, or\n   turgescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turgescency', 'n.', 'Empty magnificence or pompousness; inflation; bombast;\n   turgidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turgescent', 'a.', 'Becoming turgid or inflated; swelling; growing big.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turgid', 'a.', 'Distended beyond the natural state by some internal agent\n   or expansive force; swelled; swollen; bloated; inflated; tumid; --\n   especially applied to an enlarged part of the body; as, a turgid limb;\n   turgid fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turgid', 'a.', 'Swelling in style or language; vainly ostentatious;\n   bombastic; pompous; as, a turgid style of speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turgidity', 'n.', 'The quality or state of being turgid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turgidous', 'a.', 'Turgid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turiones', 'pl. ', 'of Turio');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turio', 'n.', 'A shoot or sprout from the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turiole', 'n.', 'The golden oriole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turion', 'n.', 'Same as Turio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turioniferous', 'a.', 'Producing shoots, as asparagus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turk', 'n.', 'A member of any of numerous Tartar tribes of Central Asia,\n   etc.; esp., one of the dominant race in Turkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turk', 'n.', 'A native or inhabitant of Turkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turk', 'n.', 'A Mohammedan; esp., one living in Turkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turk', 'n.', 'The plum weevil. See Curculio, and Plum weevil, under Plum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turkeis', 'a.', 'Turkish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turkey', 'n.', 'An empire in the southeast of Europe and southwest of Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turkeys', 'pl. ', 'of Turkey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turkey', 'n.', 'Any large American gallinaceous bird belonging to the genus\n   Meleagris, especially the North American wild turkey (Meleagris\n   gallopavo), and the domestic turkey, which was probably derived from\n   the Mexican wild turkey, but had been domesticated by the Indians long\n   before the discovery of America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turkeys', 'a.', 'Turkish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turkic', 'a.', 'Turkish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turkis', 'n.', 'Turquois.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turkish', 'a.', 'Of or pertaining to Turkey or the Turks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turkish', 'n.', 'The language spoken by Turks, esp. that of the people of\n   Turkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turkism', 'n.', 'Same as Turcism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turkle', 'n.', 'A turtle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turkos', 'pl. ', 'of Turko');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turko', 'n.', 'One of a body of native Algerian tirailleurs in the French\n   army, dressed as a Turk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turkois', 'n. & a.', 'Turquoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turkomans', 'pl. ', 'of Turkoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turkoman', 'n.', 'Same as Turcoman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turlupin', 'n.', 'One of the precursors of the Reformation; -- a nickname\n   corresponding to Lollard, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turm', 'n.', 'A troop; a company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turmaline', 'n.', 'See Tourmaline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turmeric', 'n.', 'An East Indian plant of the genus Curcuma, of the Ginger\n   family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turmeric', 'n.', 'The root or rootstock of the Curcuma longa. It is\n   externally grayish, but internally of a deep, lively yellow or saffron\n   color, and has a slight aromatic smell, and a bitterish, slightly acrid\n   taste. It is used for a dye, a medicine, a condiment, and a chemical\n   test.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turmeric', 'a.', 'Of or pertaining to turmeric; resembling, or obtained\n   from, turmeric; specif., designating an acid obtained by the oxidation\n   of turmerol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turmerol', 'n.', 'Turmeric oil, a brownish yellow, oily substance extracted\n   from turmeric by ligroin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turmoil', 'n.', 'Harassing labor; trouble; molestation by tumult;\n   disturbance; worrying confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turmoiled', 'imp. & p. p.', 'of Turmoil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turmoiling', 'p. pr. & vb. n.', 'of Turmoil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turmoil', 'v. t.', 'To harass with commotion; to disquiet; to worry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turmoil', 'v. i.', 'To be disquieted or confused; to be in commotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turned', 'imp. & p. p.', 'of Turn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turning', 'p. pr. & vb. n.', 'of Turn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. t.', 'To cause to move upon a center, or as if upon a center; to\n   give circular motion to; to cause to revolve; to cause to move round,\n   either partially, wholly, or repeatedly; to make to change position so\n   as to present other sides in given directions; to make to face\n   otherwise; as, to turn a wheel or a spindle; to turn the body or the\n   head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. t.', 'To cause to present a different side uppermost or outmost;\n   to make the upper side the lower, or the inside to be the outside of;\n   to reverse the position of; as, to turn a box or a board; to turn a\n   coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. t.', 'To give another direction, tendency, or inclination to; to\n   direct otherwise; to deflect; to incline differently; -- used both\n   literally and figuratively; as, to turn the eyes to the heavens; to\n   turn a horse from the road, or a ship from her course; to turn the\n   attention to or from something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. t.', 'To change from a given use or office; to divert, as to\n   another purpose or end; to transfer; to use or employ; to apply; to\n   devote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. t.', 'To change the form, quality, aspect, or effect of; to\n   alter; to metamorphose; to convert; to transform; -- often with to or\n   into before the word denoting the effect or product of the change; as,\n   to turn a worm into a winged insect; to turn green to blue; to turn\n   prose into verse; to turn a Whig to a Tory, or a Hindu to a Christian;\n   to turn good to evil, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. t.', 'To form in a lathe; to shape or fashion (anything) by\n   applying a cutting tool to it while revolving; as, to turn the legs of\n   stools or tables; to turn ivory or metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. t.', 'Hence, to give form to; to shape; to mold; to put in\n   proper condition; to adapt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. t.', 'To translate; to construe; as, to turn the Iliad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. t.', 'To make acid or sour; to ferment; to curdle, etc.: as, to\n   turn cider or wine; electricity turns milk quickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. t.', 'To sicken; to nauseate; as, an emetic turns ones stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. i.', 'To move round; to have a circular motion; to revolve\n   entirely, repeatedly, or partially; to change position, so as to face\n   differently; to whirl or wheel round; as, a wheel turns on its axis; a\n   spindle turns on a pivot; a man turns on his heel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. i.', 'Hence, to revolve as if upon a point of support; to hinge;\n   to depend; as, the decision turns on a single fact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. i.', 'To result or terminate; to come about; to eventuate; to\n   issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. i.', 'To be deflected; to take a different direction or\n   tendency; to be directed otherwise; to be differently applied; to be\n   transferred; as, to turn from the road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. i.', 'To be changed, altered, or transformed; to become\n   transmuted; also, to become by a change or changes; to grow; as, wood\n   turns to stone; water turns to ice; one color turns to another; to turn\n   Mohammedan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. i.', 'To undergo the process of turning on a lathe; as, ivory\n   turns well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. i.', 'To become acid; to sour; -- said of milk, ale, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. i.', 'To become giddy; -- said of the head or brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. i.', 'To be nauseated; -- said of the stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. i.', 'To become inclined in the other direction; -- said of\n   scales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. i.', 'To change from ebb to flow, or from flow to ebb; -- said\n   of the tide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. i.', 'To bring down the feet of a child in the womb, in order to\n   facilitate delivery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'v. i.', 'To invert a type of the same thickness, as temporary\n   substitute for any sort which is exhausted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'n.', 'The act of turning; movement or motion about, or as if about,\n   a center or axis; revolution; as, the turn of a wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'n.', 'Change of direction, course, or tendency; different order,\n   position, or aspect of affairs; alteration; vicissitude; as, the turn\n   of the tide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'n.', 'One of the successive portions of a course, or of a series of\n   occurrences, reckoning from change to change; hence, a winding; a bend;\n   a meander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'n.', 'A circuitous walk, or a walk to and fro, ending where it\n   began; a short walk; a stroll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'n.', 'Successive course; opportunity enjoyed by alternation with\n   another or with others, or in due order; due chance; alternate or\n   incidental occasion; appropriate time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'n.', 'Incidental or opportune deed or office; occasional act of\n   kindness or malice; as, to do one an ill turn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'n.', 'Convenience; occasion; purpose; exigence; as, this will not\n   serve his turn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'n.', 'Form; cast; shape; manner; fashion; -- used in a literal or\n   figurative sense; hence, form of expression; mode of signifying; as,\n   the turn of thought; a man of a sprightly turn in conversation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'n.', 'A change of condition; especially, a sudden or recurring\n   symptom of illness, as a nervous shock, or fainting spell; as, a bad\n   turn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'n.', 'A fall off the ladder at the gallows; a hanging; -- so called\n   from the practice of causing the criminal to stand on a ladder which\n   was turned over, so throwing him off, when the signal was given.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'n.', 'A round of a rope or cord in order to secure it, as about a\n   pin or a cleat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'n.', 'A pit sunk in some part of a drift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'n.', 'A court of record, held by the sheriff twice a year in every\n   hundred within his county.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'n.', 'Monthly courses; menses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn', 'n.', 'An embellishment or grace (marked thus, /), commonly\n   consisting of the principal note, or that on which the turn is made,\n   with the note above, and the semitone below, the note above being\n   sounded first, the principal note next, and the semitone below last,\n   the three being performed quickly, as a triplet preceding the marked\n   note. The turn may be inverted so as to begin with the lower note, in\n   which case the sign is either placed on end thus /, or drawn thus /.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnbroach', 'n.', 'A turnspit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn-buckle', 'n.', 'A loop or sleeve with a screw thread at one end and a\n   swivel at the other, -- used for tightening a rod, stay, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn-buckle', 'n.', 'A gravitating catch, as for fastening a shutter, the\n   end of a chain, or a hasp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnbulls blue', '', 'The double cyanide of ferrous and ferric iron, a\n   dark blue amorphous substance having a coppery luster, used in dyeing,\n   calico printing, etc. Cf. Prussian blue, under Prussian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turncoat', 'n.', 'One who forsakes his party or his principles; a renegade;\n   an apostate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnep', 'n.', 'See Turnip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turner', 'n.', 'One who turns; especially, one whose occupation is to form\n   articles with a lathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turner', 'n.', 'A variety of pigeon; a tumbler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turner', 'n.', 'A person who practices athletic or gymnastic exercises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnerite', 'n.', 'A variety of monazite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnery', 'n.', 'The art of fashioning solid bodies into cylindrical or\n   other forms by means of a lathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnery', 'n.', 'Things or forms made by a turner, or in the lathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turney', 'n. & v.', 'Tourney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnhalle', 'n.', 'A building used as a school of gymnastics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnicimorphae', 'n. pl.', 'A division of birds including Turnix and allied\n   genera, resembling quails in appearance but differing from them\n   anatomically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turning', 'n.', 'The act of one who, or that which, turns; also, a winding;\n   a bending course; a fiexure; a meander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turning', 'n.', 'The place of a turn; an angle or corner, as of a road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turning', 'n.', 'Deviation from the way or proper course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turning', 'n.', 'Turnery, or the shaping of solid substances into various\n   by means of a lathe and cutting tools.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turning', 'n.', 'The pieces, or chips, detached in the process of turning\n   from the material turned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turning', 'n.', 'A maneuver by which an enemy or a position is turned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turningness', 'n.', 'The quality of turning; instability; tergiversation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnip', 'v. t.', 'The edible, fleshy, roundish, or somewhat conical, root\n   of a cruciferous plant (Brassica campestris, var. Napus); also, the\n   plant itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnip-shell', 'n.', 'Any one of several large, thick, spiral marine shells\n   belonging to Rapa and allied genera, somewhat turnip-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnix', 'n.', 'Any one of numerous species of birds belonging to Turnix or\n   Hemipodius and allied genera of the family Turnicidae. These birds\n   resemble quails and partridges in general appearance and in some of\n   their habits, but differ in important anatomical characteristics. The\n   hind toe is usually lacking. They are found in Asia, Africa, Southern\n   Europe, the East Indian Islands, and esp. in Australia and adjacent\n   islands, where they are called quails (see Quail, n., 3.). See\n   Turnicimorphae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnkeys', 'pl. ', 'of Turnkey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnkey', 'n.', 'A person who has charge of the keys of a prison, for\n   opening and fastening the doors; a warder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnkey', 'n.', 'An instrument with a hinged claw, -- used for extracting\n   teeth with a twist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn-outs', 'pl. ', 'of Turn-out');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn-out', 'n.', 'The act of coming forth; a leaving of houses, shops,\n   etc.; esp., a quitting of employment for the purpose of forcing\n   increase of wages; a strike; -- opposed to lockout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn-out', 'n.', 'A short side track on a railroad, which may be occupied\n   by one train while another is passing on a main track; a shunt; a\n   siding; a switch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn-out', 'n.', 'That which is prominently brought forward or exhibited;\n   hence, an equipage; as, a man with a showy carriage and horses is said\n   to have a fine turn-out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn-out', 'n.', 'The aggregate number of persons who have come out, as\n   from their houses, for a special purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn-out', 'n.', 'Net quantity of produce yielded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnover', 'n.', 'The act or result of turning over; an upset; as, a bad\n   turnover in a carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnover', 'n.', 'A semicircular pie or tart made by turning one half of a\n   circular crust over the other, inclosing the fruit or other materials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnover', 'n.', 'An apprentice, in any trade, who is handed over from one\n   master to another to complete his time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnover', 'a.', 'Admitting of being turned over; made to be turned over;\n   as, a turnover collar, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnpike', 'n.', 'A frame consisting of two bars crossing each other at\n   right angles and turning on a post or pin, to hinder the passage of\n   beasts, but admitting a person to pass between the arms; a turnstile.\n   See Turnstile, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnpike', 'n.', 'A gate or bar set across a road to stop carriages,\n   animals, and sometimes people, till toll is paid for keeping the road\n   in repair; a tollgate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnpike', 'n.', 'A turnpike road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnpike', 'n.', 'A winding stairway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnpike', 'n.', 'A beam filled with spikes to obstruct passage; a\n   cheval-de-frise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnpiked', 'imp. & p. p.', 'of Turnpike');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnpiking', 'p. pr. & vb. n.', 'of Turnpike');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnpike', 'v. t.', 'To form, as a road, in the manner of a turnpike road;\n   into a rounded form, as the path of a road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnplate', 'n.', 'A turntable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn-sick', 'a.', 'Giddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turn-sick', 'n.', 'A disease with which sheep are sometimes affected; gid;\n   sturdy. See Gid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnsole', 'a.', 'A plant of the genus Heliotropium; heliotrope; -- so\n   named because its flowers are supposed to turn toward the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnsole', 'a.', 'The sunflower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnsole', 'a.', 'A kind of spurge (Euphorbia Helioscopia).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnsole', 'a.', 'The euphorbiaceous plant Chrozophora tinctoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnsole', 'a.', 'Litmus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnsole', 'a.', 'A purple dye obtained from the plant turnsole. See def. 1\n   (d).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnspit', 'n.', 'One who turns a spit; hence, a person engaged in some\n   menial office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnspit', 'n.', 'A small breed of dogs having a long body and short\n   crooked legs. These dogs were formerly much used for turning a spit on\n   which meat was roasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnstile', 'n.', 'A revolving frame in a footpath, preventing the passage\n   of horses or cattle, but admitting that of persons; a turnpike. See\n   Turnpike, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnstile', 'n.', 'A similar arrangement for registering the number of\n   persons passing through a gateway, doorway, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnstone', 'n.', 'Any species of limicoline birds of the genera Strepsilas\n   and Arenaria, allied to the plovers, especially the common American and\n   European species (Strepsilas interpres). They are so called from their\n   habit of turning up small stones in search of mollusks and other\n   aquatic animals. Called also brant bird, sand runner, sea quail, sea\n   lark, sparkback, and skirlcrake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turntable', 'n.', 'A large revolving platform, for turning railroad cars,\n   locomotives, etc., in a different direction; -- called also turnplate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnus', 'n.', 'A common, large, handsome, American swallowtail butterfly,\n   now regarded as one of the forms of Papilio, / Jasoniades, glaucus. The\n   wings are yellow, margined and barred with black, and with an\n   orange-red spot near the posterior angle of the hind wings. Called also\n   tiger swallowtail. See Illust. under Swallowtail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnverein', 'n.', 'A company or association of gymnasts and athletes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnwrest', 'n.', 'Designating a cumbersome style of plow used in England,\n   esp. in Kent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turnwrest', 'n.', 'designating a kind of hillside plow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turonian', 'n.', 'One of the subdivisions into which the Upper Cretaceous\n   formation of Europe is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turpentine', 'n.', 'A semifluid or fluid oleoresin, primarily the exudation\n   of the terebinth, or turpentine, tree (Pistacia Terebinthus), a native\n   of the Mediterranean region. It is also obtained from many coniferous\n   trees, especially species of pine, larch, and fir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turpeth', 'n.', 'The root of Ipom/a Turpethum, a plant of Ceylon, Malabar,\n   and Australia, formerly used in medicine as a purgative; -- sometimes\n   called vegetable turpeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turpeth', 'n.', 'A heavy yellow powder, Hg3O2SO4, which consists of a basic\n   mercuric sulphate; -- called also turpeth mineral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turpin', 'n.', 'A land tortoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turpitude', 'n.', 'Inherent baseness or vileness of principle, words, or\n   actions; shameful wickedness; depravity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turquoise', 'n.', 'Alt. of Turquois');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turquois', 'n.', 'A hydrous phosphate of alumina containing a little\n   copper; calaite. It has a blue, or bluish green, color, and usually\n   occurs in reniform masses with a botryoidal surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turquoise', 'a.', 'Having a fine light blue color, like that of choice\n   mineral turquoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turrel', 'n.', 'A certain tool used by coopers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turret', 'n.', 'A little tower, frequently a merely ornamental structure at\n   one of the angles of a larger structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turret', 'n.', 'A movable building, of a square form, consisting of ten or\n   even twenty stories and sometimes one hundred and twenty cubits high,\n   usually moved on wheels, and employed in approaching a fortified place,\n   for carrying soldiers, engines, ladders, casting bridges, and other\n   necessaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turret', 'n.', 'A revolving tower constructed of thick iron plates, within\n   which cannon are mounted. Turrets are used on vessels of war and on\n   land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turret', 'n.', 'The elevated central portion of the roof of a passenger\n   car. Its sides are pierced for light and ventilation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turreted', 'a.', 'Furnished with a turret or turrets; specifically (Zool.),\n   having the whorls somewhat flattened on the upper side and often\n   ornamented by spines or tubercles; -- said of certain spiral shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turreted', 'a.', 'Formed like a tower; as, a turreted lamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turribant', 'n.', 'A turban.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turrical', 'a.', 'Of or pertaining to a turret, or tower; resembling a\n   tower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turriculate', 'a.', 'Alt. of Turriculated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turriculated', 'a.', 'Furnished with, or formed like, a small turret or\n   turrets; somewhat turreted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turrilite', 'n.', 'Any fossil ammonite of the genus Turrilites. The shell\n   forms an open spiral with the later whorls separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turritella', 'n.', 'Any spiral marine gastropod belonging to Turritella and\n   allied genera. These mollusks have an elongated, turreted shell,\n   composed of many whorls. They have a rounded aperture, and a horny\n   multispiral operculum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turritelloid', 'a.', 'Of, pertaining to, or resembling, the turritellas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turtle', 'n.', 'The turtledove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turtle', 'n.', 'Any one of the numerous species of Testudinata, especially\n   a sea turtle, or chelonian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turtle', 'n.', 'The curved plate in which the form is held in a\n   type-revolving cylinder press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turtledove', 'n.', 'Any one of numerous species of pigeons belonging to\n   Turtur and allied genera, native of various parts of the Old World;\n   especially, the common European species (Turtur vulgaris), which is\n   noted for its plaintive note, affectionate disposition, and devotion to\n   its mate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turtledove', 'n.', 'Any one of several species of pigeons more or less\n   resembling the true turtledoves, as the American mourning dove (see\n   under Dove), and the Australian turtledove (Stictopelia cuneata).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turtle-footed', 'a.', 'Slow-footed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turtlehead', 'n.', 'An American perennial herb (Chelone glabra) having\n   white flowers shaped like the head of a turtle. Called also snakehead,\n   shell flower, and balmony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turtler', 'n.', 'One who catches turtles or tortoises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turtle-shell', 'n.', 'The turtle cowrie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turtling', 'n.', 'The act, practice, or art of catching turtles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Turves', '', 'pl. of Turf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuscan', 'a.', 'Of or pertaining to Tuscany in Italy; -- specifically\n   designating one of the five orders of architecture recognized and\n   described by the Italian writers of the 16th century, or characteristic\n   of the order. The original of this order was not used by the Greeks,\n   but by the Romans under the Empire. See Order, and Illust. of Capital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuscan', 'n.', 'A native or inhabitant of Tuscany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuscaroras', 'n. pl.', 'A tribe of North American Indians formerly living\n   on the Neuse and Tar rivers in North Carolina. They were conquered in\n   1713, after which the remnant of the tribe joined the Five Nations,\n   thus forming the Six Nations. See Six Nations, under Six.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tuscor', 'n.', 'A tush of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tush', 'interj.', 'An exclamation indicating check, rebuke, or contempt;\n   as, tush, tush! do not speak of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tush', 'n.', 'A long, pointed tooth; a tusk; -- applied especially to\n   certain teeth of horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tusk', 'n.', 'Same as Torsk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tusk', 'n.', 'One of the elongated incisor or canine teeth of the wild\n   boar, elephant, etc.; hence, any long, protruding tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tusk', 'n.', 'A toothshell, or Dentalium; -- called also tusk-shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tusk', 'n.', 'A projecting member like a tenon, and serving the same or a\n   similar purpose, but composed of several steps, or offsets. Thus, in\n   the illustration, a is the tusk, and each of the several parts, or\n   offsets, is called a tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tusk', 'v. i.', 'To bare or gnash the teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tusked', 'a.', 'Furnished with tusks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tusker', 'n.', 'An elephant having large tusks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tusk-shell', 'n.', 'See 2d Tusk, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tusky', 'a.', 'Having tusks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tussac grass', '', 'Tussock grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tussah silk', '', 'A silk cloth made from the cocoons of a caterpillar\n   other than the common silkworm, much used in Bengal and China.');
