  INSERT INTO words (word, word_type, description) VALUES  (    'Twin', 'v. t.', 'To cause to be twins, or like twins in any way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twin', 'v. t.', 'To separate into two parts; to part; to divide; hence, to\n   remove; also, to strip; to rob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twin', 'v. i.', 'To depart from a place or thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinborn', 'a.', 'Born at the same birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twine', 'n.', 'A twist; a convolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twine', 'n.', 'A strong thread composed of two or three smaller threads or\n   strands twisted together, and used for various purposes, as for binding\n   small parcels, making nets, and the like; a small cord or string.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twine', 'n.', 'The act of twining or winding round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twined', 'imp. & p. p.', 'of Twine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twining', 'p. pr. & vb. n.', 'of Twine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twine', 'n.', 'To twist together; to form by twisting or winding of\n   threads; to wreathe; as, fine twined linen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twine', 'n.', 'To wind, as one thread around another, or as any flexible\n   substance around another body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twine', 'n.', 'To wind about; to embrace; to entwine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twine', 'n.', 'To change the direction of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twine', 'n.', 'To mingle; to mix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twine', 'v. i.', 'To mutually twist together; to become mutually involved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twine', 'v. i.', 'To wind; to bend; to make turns; to meander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twine', 'v. i.', 'To turn round; to revolve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twine', 'v. i.', 'To ascend in spiral lines about a support; to climb\n   spirally; as, many plants twine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twiner', 'n.', 'Any plant which twines about a support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinged', 'imp. & p. p.', 'of Twinge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinging', 'p. pr. & vb. n.', 'of Twinge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinge', 'v. i.', 'To pull with a twitch; to pinch; to tweak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinge', 'v. i.', 'To affect with a sharp, sudden pain; to torment with\n   pinching or sharp pains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinge', 'v. i.', 'To have a sudden, sharp, local pain, like a twitch; to\n   suffer a keen, darting, or shooting pain; as, the side twinges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinge', 'n.', 'A pinch; a tweak; a twitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinge', 'n.', 'A sudden sharp pain; a darting local pain of momentary\n   continuance; as, a twinge in the arm or side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twining', 'a.', 'Winding around something; twisting; embracing; climbing by\n   winding about a support; as, the hop is a twinning plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twining', 'a.', 'The act of one who, or that which, twines; (Bot.) the act\n   of climbing spirally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twink', 'v. i.', 'To twinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twink', 'n.', 'A wink; a twinkling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twink', 'n.', 'The chaffinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinkled', 'imp. & p. p.', 'of Twinkle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinkling', 'p. pr. & vb. n.', 'of Twinkle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinkle', 'v. i.', 'To open and shut the eye rapidly; to blink; to wink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinkle', 'v. i.', 'To shine with an intermitted or a broken, quavering\n   light; to flash at intervals; to sparkle; to scintillate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinkle', 'n.', 'A closing or opening, or a quick motion, of the eye; a\n   wink or sparkle of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinkle', 'n.', 'A brief flash or gleam, esp. when rapidly repeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinkle', 'n.', 'The time of a wink; a twinkling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinkler', 'n.', 'One who, or that which, twinkles, or winks; a winker; an\n   eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinkling', 'n.', 'The act of one who, or of that which, twinkles; a quick\n   movement of the eye; a wink; a twinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinkling', 'n.', 'A shining with intermitted light; a scintillation; a\n   sparkling; as, the twinkling of the stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinkling', 'n.', 'The time of a wink; a moment; an instant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinleaf', 'n.', 'See Jeffersonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinlike', 'a.', 'Closely resembling; being a counterpart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinling', 'n.', 'A young or little twin, especially a twin lamb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinned', 'a.', 'Composed of parts united according to a law of twinning.\n   See Twin, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinner', 'n.', 'One who gives birth to twins; a breeder of twins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinning', 'n.', 'The assemblage of two or more crystals, or parts of\n   crystals, in reversed position with reference to each other in\n   accordance with some definite law; also, rarely, in artificial twinning\n   (accomplished for example by pressure), the process by which this\n   reversal is brought about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twinter', 'n.', 'A domestic animal two winters old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twire', 'n.', 'A twisted filament; a thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twire', 'v. i.', 'To peep; to glance obliquely; to leer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twire', 'v. i.', 'To twinkle; to glance; to gleam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twire', 'v. i.', 'To sing, or twitter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twire-pipe', 'n.', 'A vagabond musician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twirled', 'imp. & p. p.', 'of Twirl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twirling', 'p. pr. & vb. n.', 'of Twirl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twirl', 'v. t.', 'To move or turn round rapidly; to whirl round; to move\n   and turn rapidly with the fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twirl', 'v. i.', 'To revolve with velocity; to be whirled round rapidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twirl', 'n.', 'The act of twirling; a rapid circular motion; a whirl or\n   whirling; quick rotation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twirl', 'n.', 'A twist; a convolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twisted', 'imp. & p. p.', 'of Twist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twisting', 'p. pr. & vb. n.', 'of Twist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'v. t.', 'To contort; to writhe; to complicate; to crook spirally;\n   to convolve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'v. t.', 'Hence, to turn from the true form or meaning; to pervert;\n   as, to twist a passage cited from an author.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'v. t.', 'To distort, as a solid body, by turning one part\n   relatively to another about an axis passing through both; to subject to\n   torsion; as, to twist a shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'v. t.', 'To wreathe; to wind; to encircle; to unite by\n   intertexture of parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'v. t.', 'To wind into; to insinuate; -- used reflexively; as,\n   avarice twists itself into all human concerns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'v. t.', 'To unite by winding one thread, strand, or other flexible\n   substance, round another; to form by convolution, or winding separate\n   things round each other; as, to twist yarn or thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'v. t.', 'Hence, to form as if by winding one part around another;\n   to wreathe; to make up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'v. t.', 'To form into a thread from many fine filaments; as, to\n   twist wool or cotton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'v. i.', 'To be contorted; to writhe; to be distorted by torsion;\n   to be united by winding round each other; to be or become twisted; as,\n   some strands will twist more easily than others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'v. i.', 'To follow a helical or spiral course; to be in the form\n   of a helix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'n.', 'The act of twisting; a contortion; a flexure; a convolution;\n   a bending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'n.', 'The form given in twisting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'n.', 'That which is formed by twisting, convoluting, or uniting\n   parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'n.', 'A cord, thread, or anything flexible, formed by winding\n   strands or separate things round each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'n.', 'A kind of closely twisted, strong sewing silk, used by\n   tailors, saddlers, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'n.', 'A kind of cotton yarn, of several varieties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'n.', 'A roll of twisted dough, baked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'n.', 'A little twisted roll of tobacco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'n.', 'One of the threads of a warp, -- usually more tightly\n   twisted than the filling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'n.', 'A material for gun barrels, consisting of iron and steel\n   twisted and welded together; as, Damascus twist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'n.', 'The spiral course of the rifling of a gun barrel or a\n   cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'n.', 'A beverage made of brandy and gin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twist', 'v. t.', 'A twig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twiste', '', 'imp. of Twist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twisted', 'a.', 'Contorted; crooked spirally; subjected to torsion; hence,\n   perverted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twister', 'n.', 'One who twists; specifically, the person whose occupation\n   is to twist or join the threads of one warp to those of another, in\n   weaving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twister', 'n.', 'The instrument used in twisting, or making twists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twister', 'n.', 'A girder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twister', 'n.', 'The inner part of the thigh, the proper place to rest upon\n   when on horseback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twistical', 'a.', 'Crooked; tortuous; hence, perverse; unfair; dishonest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twisting', '', 'a. & n. from Twist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twitted', 'imp. & p. p.', 'of Twit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twitting', 'p. pr. & vb. n.', 'of Twit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twit', 'v. t.', 'To vex by bringing to notice, or reminding of, a fault,\n   defect, misfortune, or the like; to revile; to reproach; to upbraid; to\n   taunt; as, he twitted his friend of falsehood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twitched', 'imp. & p. p.', 'of Twitch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twitching', 'p. pr. & vb. n.', 'of Twitch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twitch', 'v. t.', 'To pull with a sudden jerk; to pluck with a short, quick\n   motion; to snatch; as, to twitch one by the sleeve; to twitch a thing\n   out of anothers hand; to twitch off clusters of grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twitch', 'n.', 'The act of twitching; a pull with a jerk; a short, sudden,\n   quick pull; as, a twitch by the sleeve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twitch', 'n.', 'A short, spastic contraction of the fibers or muscles; a\n   simple muscular contraction; as, convulsive twitches; a twitch in the\n   side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twitch', 'n.', 'A stick with a hole in one end through which passes a loop,\n   which can be drawn tightly over the upper lip or an ear of a horse. By\n   twisting the stick the compression is made sufficiently painful to keep\n   the animal quiet during a slight surgical operation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twitcher', 'n.', 'One who, or that which, twitches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twitch grass', '', 'See Quitch grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twite', 'n.', 'The European tree sparrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twite', 'n.', 'The mountain linnet (Linota flavirostris).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twitlark', 'n.', 'The meadow pipit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twitter', 'n.', 'One who twits, or reproaches; an upbraider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twittered', 'imp. & p. p.', 'of Twitter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twittering', 'p. pr. & vb. n.', 'of Twitter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twitter', 'v. i.', 'To make a succession of small, tremulous, intermitted\n   noises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twitter', 'v. i.', 'To make the sound of a half-suppressed laugh; to\n   titter; to giggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twitter', 'v. i.', 'To have a slight trembling of the nerves; to be excited\n   or agitated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twitter', 'v. t.', 'To utter with a twitter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twitter', 'n.', 'The act of twittering; a small, tremulous, intermitted\n   noise, as that made by a swallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twitter', 'n.', 'A half-suppressed laugh; a fit of laughter partially\n   restrained; a titter; a giggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twitter', 'n.', 'A slight trembling or agitation of the nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twittering', 'n.', 'The act of one who, or that which, twitters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twittering', 'n.', 'A slight nervous excitement or agitation, such as is\n   caused by desire, expectation, or suspense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twittingly', 'adv.', 'In a twitting manner; with upbraiding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twittle-twattle', 'n.', 'Tattle; gabble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twixt', '', 'An abbreviation of Betwixt, used in poetry, or in colloquial\n   language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twixt-brain', 'n.', 'The thalamen/cephalon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two', 'n.', 'One and one; twice one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two', 'n.', 'The sum of one and one; the number next greater than one, and\n   next less than three; two units or objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two', 'n.', 'A symbol representing two units, as 2, II., or ii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two-capsuled', 'a.', 'Having two distinct capsules; bicapsular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two-cleft', 'a.', 'Divided about half way from the border to the base into\n   two segments; bifid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two-decker', 'n.', 'A vessel of war carrying guns on two decks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two-edged', 'a.', 'Having two edges, or edges on both sides; as, a\n   two-edged sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twofold', 'a.', 'Double; duplicate; multiplied by two; as, a twofold\n   nature; a twofold sense; a twofold argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twofold', 'adv.', 'In a double degree; doubly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two-foot', 'a.', 'Measuring two feet; two feet long, thick, or wide; as, a\n   two-foot rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two-forked', 'a.', 'Divided into two parts, somewhat after the manner of a\n   fork; dichotomous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two-hand', 'a.', 'Employing two hands; as, the two-hand alphabet. See\n   Dactylology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two-handed', 'a.', 'Having two hands; -- often used as an epithet\n   equivalent to large, stout, strong, or powerful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two-handed', 'a.', 'Used with both hands; as, a two-handed sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two-handed', 'a.', 'Using either hand equally well; ambidextrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two-lipped', 'a.', 'Having two lips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two-lipped', 'a.', 'Divided in such a manner as to resemble the two lips\n   when the mouth is more or less open; bilabiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two-parted', 'a.', 'Divided from the border to the base into two distinct\n   parts; bipartite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twopence', 'n.', 'A small coin, and money of account, in England,\n   equivalent to two pennies, -- minted to a fixed annual amount, for\n   almsgiving by the sovereign on Maundy Thursday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twopenny', 'a.', 'Of the value of twopence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two-ply', 'a.', 'Consisting of two thicknesses, as cloth; double.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two-ply', 'a.', 'Woven double, as cloth or carpeting, by incorporating two\n   sets of warp thread and two of weft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two-ranked', 'a.', 'Alternately disposed on exactly opposite sides of the\n   stem so as to from two ranks; distichous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two-sided', 'a.', 'Having two sides only; hence, double-faced;\n   hypocritical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two-sided', 'a.', 'Symmetrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Two-tongued', 'a.', 'Double-tongued; deceitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Twyblade', 'n.', 'See Twayblade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ty-all', 'n.', 'Something serving to tie or secure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyburn ticket', '', 'A certificate given to one who prosecutes a felon to\n   conviction, exempting him from certain parish and ward offices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tychonic', 'a.', 'Of or pertaining to Tycho Brahe, or his system of\n   astronomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tycoon', 'n.', 'The title by which the shogun, or former commander in chief\n   of the Japanese army, was known to foreigners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tydy', 'n.', 'Same as Tidy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tye', 'n.', 'A knot; a tie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tye', 'n.', 'A chain or rope, one end of which passes through the mast, and\n   is made fast to the center of a yard; the other end is attached to a\n   tackle, by means of which the yard is hoisted or lowered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tye', 'n.', 'A trough for washing ores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tye', 'v. t.', 'See Tie, the proper orthography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyer', 'n.', 'One who ties, or unites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyfoon', 'n.', 'See Typhoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyger', 'n.', 'A tiger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tying', '', 'p. pr. of Tie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tying', 'n.', 'The act or process of washing ores in a buddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyke', 'n.', 'See 2d Tike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tylari', 'pl. ', 'of Tylarus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tylarus', 'n.', 'One of the pads on the under surface of the toes of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyler', 'n.', 'See 2d Tiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tylopoda', 'n. pl.', 'A tribe of ungulates comprising the camels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyloses', 'pl. ', 'of Tylosis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tylosis', 'n.', 'An intrusion of one vegetable cell into the cavity of\n   another, sometimes forming there an irregular mass of cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tymbal', 'n.', 'A kind of kettledrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tymp', 'n.', 'A hollow water-cooled iron casting in the upper part of the\n   archway in which the dam stands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympan', 'n.', 'A drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympan', 'n.', 'A panel; a tympanum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympan', 'n.', 'A frame covered with parchment or cloth, on which the blank\n   sheets are put, in order to be laid on the form to be impressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanal', 'n.', 'Tympanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanic', 'a.', 'Like a tympanum or drum; acting like a drumhead; as, a\n   tympanic membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanic', 'a.', 'Of or pertaining to the tympanum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanic', 'n.', 'The tympanic bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanist', 'n.', 'One who beats a drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanites', 'n.', 'A flatulent distention of the belly; tympany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanitic', 'a.', 'Of, pertaining to, or affected with, tympanites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanitis', 'n.', 'Inflammation of the lining membrane of the middle ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanize', 'v. i.', 'To drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanized', 'imp. & p. p.', 'of Tympanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanizing', 'p. pr. & vb. n.', 'of Tympanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanize', 'v. t.', 'To stretch, as a skin over the head of a drum; to\n   make into a drum or drumhead, or cause to act or sound like a drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympani', 'pl. ', 'of Tympano');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympano', 'n.', 'A kettledrum; -- chiefly used in the plural to denote the\n   kettledrums of an orchestra. See Kettledrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympano-', '', 'A combining form used in anatomy to indicate connection\n   with, or relation to, the tympanum; as in tympanohyal,\n   tympano-Eustachian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanohyal', 'a.', 'Of or pertaining to the tympanum and the hyoidean\n   arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanohyal', 'n.', 'The proximal segment in the hyoidean arch, becoming a\n   part of the styloid process of the temporal bone in adult man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanums', 'pl. ', 'of Tympanum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympana', 'pl. ', 'of Tympanum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanum', 'n.', 'The ear drum, or middle ear. Sometimes applied\n   incorrectly to the tympanic membrane. See Ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanum', 'n.', 'A chamber in the anterior part of the syrinx of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanum', 'n.', 'One of the naked, inflatable air sacs on the neck of the\n   prairie chicken and other species of grouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanum', 'n.', 'The recessed face of a pediment within the frame made by\n   the upper and lower cornices, being usually a triangular space or\n   table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanum', 'n.', 'The space within an arch, and above a lintel or a\n   subordinate arch, spanning the opening below the arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympanum', 'n.', 'A drum-shaped wheel with spirally curved partitions by\n   which water is raised to the axis when the wheel revolves with the\n   lower part of the circumference submerged, -- used for raising water,\n   as for irrigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympany', 'n.', 'A flatulent distention of the belly; tympanites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tympany', 'n.', 'Hence, inflation; conceit; bombast; turgidness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tynd', 'v. t.', 'To shut; to close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyne', 'v. t.', 'To lose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyne', 'v. i.', 'To become lost; to perish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyne', 'n.', 'A prong or point of an antler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyne', 'n.', 'Anxiety; tine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyny', 'a.', 'Small; tiny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typal', 'a.', 'Relating to a type or types; belonging to types; serving as\n   a type; typical.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-type', 'n.', 'A combining form signifying impressed form; stamp; print;\n   type; typical form; representative; as in stereotype phototype,\n   ferrotype, monotype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Type', 'n.', 'The mark or impression of something; stamp; impressed sign;\n   emblem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Type', 'n.', 'Form or character impressed; style; semblance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Type', 'n.', 'A figure or representation of something to come; a token; a\n   sign; a symbol; -- correlative to antitype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Type', 'n.', 'That which possesses or exemplifies characteristic qualities;\n   the representative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Type', 'n.', 'A general form or structure common to a number of\n   individuals; hence, the ideal representation of a species, genus, or\n   other group, combining the essential characteristics; an animal or\n   plant possessing or exemplifying the essential characteristics of a\n   species, genus, or other group. Also, a group or division of animals\n   having a certain typical or characteristic structure of body maintained\n   within the group.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Type', 'n.', 'The original object, or class of objects, scene, face, or\n   conception, which becomes the subject of a copy; esp., the design on\n   the face of a medal or a coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Type', 'n.', 'A simple compound, used as a mode or pattern to which other\n   compounds are conveniently regarded as being related, and from which\n   they may be actually or theoretically derived.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Type', 'n.', 'A raised letter, figure, accent, or other character, cast in\n   metal or cut in wood, used in printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Type', 'n.', 'Such letters or characters, in general, or the whole quantity\n   of them used in printing, spoken of collectively; any number or mass of\n   such letters or characters, however disposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typed', 'imp. & p. p.', 'of Type');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typing', 'p. pr. & vb. n.', 'of Type');
  INSERT INTO words (word, word_type, description) VALUES  (    'Type', 'v. t.', 'To represent by a type, model, or symbol beforehand; to\n   prefigure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Type', 'v. t.', 'To furnish an expression or copy of; to represent; to\n   typify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typesetter', 'n.', 'One who, or that which, sets type; a compositor; a\n   machine for setting type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typesetting', 'n.', 'The act or art of setting type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typewrite', 'v. t. & i.', 'To write with a typewriter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typewriter', 'n.', 'An instrument for writing by means of type, a\n   typewheel, or the like, in which the operator makes use of a sort of\n   keyboard, in order to obtain printed impressions of the characters upon\n   paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typewriter', 'n.', 'One who uses such an instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typewriting', 'n.', 'The act or art of using a typewriter; also, a print\n   made with a typewriter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typhlitis', 'n.', 'Inflammation of the caecum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typhlosole', 'n.', 'A fold of the wall which projects into the cavity of\n   the intestine in bivalve mollusks, certain annelids, starfishes, and\n   some other animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typhoean', 'a.', 'Of or pertaining to Typhoeus (t/*f/\"/s), the fabled giant\n   of Greek mythology, having a hundred heads; resembling Typhoeus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typhoid', 'a.', 'Of or pertaining to typhus; resembling typhus; of a low\n   grade like typhus; as, typhoid symptoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typhomalarial', 'a.', 'Pertaining to typhoid fever and malaria; as,\n   typhomalarial fever, a form of fever having symptoms both of malarial\n   and typhoid fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typhomania', 'n.', 'A low delirium common in typhus fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typhon', 'n.', 'According to Hesiod, the son of Typhoeus, and father of the\n   winds, but later identified with him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typhon', 'n.', 'A violent whirlwind; a typhoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typhoon', 'n.', 'A violent whirlwind; specifically, a violent whirlwind\n   occurring in the Chinese seas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typhos', 'n.', 'Typhus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typhotoxin', 'n.', 'A basic substance, C7H17NO2, formed from the growth of\n   the typhoid bacillus on meat pulp. It induces in small animals\n   lethargic conditions with liquid dejecta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typhous', 'a.', 'Of or pertaining to typhus; of the nature of typhus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typhus', 'n.', 'A contagious continued fever lasting from two to three\n   weeks, attended with great prostration and cerebral disorder, and\n   marked by a copious eruption of red spots upon the body. Also called\n   jail fever, famine fever, putrid fever, spottled fever, etc. See Jail\n   fever, under Jail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typic', 'a.', 'Typical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typical', 'a.', 'Of the nature of a type; representing something by a form,\n   model, or resemblance; emblematic; prefigurative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typical', 'a.', 'Combining or exhibiting the essential characteristics of a\n   group; as, a typical genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typification', 'n.', 'The act of typifying, or representing by a figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typifier', 'n.', 'One who, or that which, typifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typified', 'imp. & p. p.', 'of Typify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typifying', 'p. pr. & vb. n.', 'of Typify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typify', 'v. t.', 'To represent by an image, form, model, or resemblance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typo', 'n.', 'A compositor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typocosmy', 'n.', 'A representation of the world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typographer', 'n.', 'A printer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typographic', 'a.', 'Alt. of Typographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typographical', 'a.', 'Of or pertaining to the act or act of representing\n   by types or symbols; emblematic; figurative; typical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typographical', 'a.', 'Of or pertaining to typography or printing; as, the\n   typographic art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typography', 'n.', 'The act or art of expressing by means of types or\n   symbols; emblematical or hieroglyphic representation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typography', 'n.', 'The art of printing with types; the use of types to\n   produce impressions on paper, vellum, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typolite', 'n.', 'A stone or fossil which has on it impressions or figures\n   of plants and animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typology', 'n.', 'A discourse or treatise on types.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typology', 'n.', 'The doctrine of types.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Typothetae', 'n. pl.', 'Printers; -- used in the name of an association of\n   the master printers of the United States and Canada, called The United\n   Typothetae of America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyran', 'n.', 'A tyrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyranness', 'n.', 'A female tyrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrannic', 'a.', 'Alt. of Tyrannical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrannical', 'a.', 'Of or pertaining to a tyrant; suiting a tyrant;\n   unjustly severe in government; absolute; imperious; despotic; cruel;\n   arbitrary; as, a tyrannical prince; a tyrannical master; tyrannical\n   government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrannicidal', 'a.', 'Of or pertaining to tyrannicide, or the murder of a\n   tyrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrannicide', 'n.', 'The act of killing a tyrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrannicide', 'n.', 'One who kills a tyrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrannish', 'a.', 'Like a tyrant; tyrannical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrannized', 'imp. & p. p.', 'of Tyrannize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrannizing', 'p. pr. & vb. n.', 'of Tyrannize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrannize', 'v. i.', 'To act the tyrant; to exercise arbitrary power; to\n   rule with unjust and oppressive severity; to exercise power others not\n   permitted by law or required by justice, or with a severity not\n   necessary to the ends of justice and government; as, a prince will\n   often tyrannize over his subjects; masters sometimes tyrannize over\n   their servants or apprentices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrannize', 'v. t.', 'To subject to arbitrary, oppressive, or tyrannical\n   treatment; to oppress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrannous', 'a.', 'Tyrannical; arbitrary; unjustly severe; despotic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyranny', 'n.', 'The government or authority of a tyrant; a country\n   governed by an absolute ruler; hence, arbitrary or despotic exercise of\n   power; exercise of power over subjects and others with a rigor not\n   authorized by law or justice, or not requisite for the purposes of\n   government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyranny', 'n.', 'Cruel government or discipline; as, the tyranny of a\n   schoolmaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyranny', 'n.', 'Severity; rigor; inclemency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrant', 'n.', 'An absolute ruler; a sovereign unrestrained by law or\n   constitution; a usurper of sovereignty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrant', 'n.', 'Specifically, a monarch, or other ruler or master, who uses\n   power to oppress his subjects; a person who exercises unlawful\n   authority, or lawful authority in an unlawful manner; one who by\n   taxation, injustice, or cruel punishment, or the demand of unreasonable\n   services, imposes burdens and hardships on those under his control,\n   which law and humanity do not authorize, or which the purposes of\n   government do not require; a cruel master; an oppressor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrant', 'n.', 'Any one of numerous species of American clamatorial birds\n   belonging to the family Tyrannidae; -- called also tyrant bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrant', 'v. i.', 'To act like a tyrant; to play the tyrant; to tyrannical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyre', '', 'Curdled milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyre', 'n. & v.', 'Attire. See 2d and 3d Tire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyre', 'v. i.', 'To prey. See 4th Tire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrian', 'a.', 'Of or pertaining to Tyre or its people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrian', 'a.', 'Being of the color called Tyrian purple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrian', 'n.', 'A native of Tyre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyros', 'pl. ', 'of Tyro');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyro', 'n.', 'A beginner in learning; one who is in the rudiments of any\n   branch of study; a person imperfectly acquainted with a subject; a\n   novice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrociny', 'n.', 'The state of being a tyro, or beginner; apprenticeship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrolite', 'n.', 'A translucent mineral of a green color and pearly or\n   vitreous luster. It is a hydrous arseniate of copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyronism', 'n.', 'The state of being a tyro, or beginner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrosin', 'n.', 'A white crystalline nitrogenous substance present in small\n   amount in the pancreas and spleen, and formed in large quantity from\n   the decomposition of proteid matter by various means, -- as by\n   pancreatic digestion, by putrefaction as of cheese, by the action of\n   boiling acids, etc. Chemically, it consists of oxyphenol and\n   amidopropionic acid, and by decomposition yields oxybenzoic acid, or\n   some other benzol derivative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrotoxicon', 'n.', 'A ptomaine discovered by Vaughan in putrid cheese and\n   other dairy products, and producing symptoms similar to cholera\n   infantum. Chemically, it appears to be related to, or identical with,\n   diazobenzol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tyrotoxine', 'n.', 'Same as Tyrotoxicon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tysonite', 'n.', 'A fluoride of the cerium metals occurring in hexagonal\n   crystals of a pale yellow color. Cf. Fluocerite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tystie', 'n.', 'The black guillemot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tythe', 'n.', 'See Tithe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tything', 'n.', 'See Tithing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tzar', 'n.', 'The emperor of Russia. See Czar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tzarina', 'n.', 'Alt. of Tzaritza');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tzaritza', 'n.', 'The empress of Russia. See Czarina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Tzetze', 'n.', 'Same as Tsetse.\n   U () the twenty-first letter of the English alphabet, is a cursive form\n   of the letter V, with which it was formerly used interchangeably, both\n   letters being then used both as vowels and consonants. U and V are now,\n   however, differentiated, U being used only as a vowel or semivowel, and\n   V only as a consonant. The true primary vowel sound of U, in\n   Anglo-Saxon, was the sound which it still retains in most of the\n   languages of Europe, that of long oo, as in tool, and short oo, as in\n   wood, answering to the French ou in tour. Etymologically U is most\n   closely related to o, y (vowel), w, and v; as in two, duet, dyad,\n   twice; top, tuft; sop, sup; auspice, aviary. See V, also O and Y.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uakari', 'n.', 'Same as Ouakari.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uberous', 'a.', 'Fruitful; copious; abundant; plentiful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uberty', 'n.', 'Fruitfulness; copiousness; abundance; plenty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ubication', 'n.', 'Alt. of Ubiety');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ubiety', 'n.', 'The quality or state of being in a place; local relation;\n   position or location; whereness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ubiquarian', 'a.', 'Ubiquitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ubiquitist', 'n.', 'Alt. of Ubiquitarian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ubiquitarian', 'n.', 'One of a school of Lutheran divines which held that\n   the body of Christ is present everywhere, and especially in the\n   eucharist, in virtue of his omnipresence. Called also ubiquitist, and\n   ubiquitary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ubiquitariness', 'n.', 'Quality or state of being ubiquitary, or\n   ubiquitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ubiquitary', 'a.', 'Ubiquitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ubiquitaries', 'pl. ', 'of Ubiquitary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ubiquitary', 'n.', 'One who exists everywhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ubiquitary', 'n.', 'A ubiquist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ubiquitist', 'n.', 'Same as Ubiquist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ubiquitous', 'a.', 'Existing or being everywhere, or in all places, at the\n   same time; omnipresent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ubiquity', 'n.', 'Existence everywhere, or in places, at the same time;\n   omnipresence; as, the ubiquity of God is not disputed by those who\n   admit his existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ubiquity', 'n.', 'The doctrine, as formulated by Luther, that Christs\n   glorified body is omnipresent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uchees', 'n. pl.', 'A tribe of North American Indians belonging to the\n   Creek confederation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uckewallist', 'n.', 'One of a sect of rigid Anabaptists, which originated\n   in 1637, and whose tenets were essentially the same as those of the\n   Mennonists. In addition, however, they held that Judas and the\n   murderers of Christ were saved. So called from the founder of the sect,\n   Ucke Wallis, a native of Friesland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Udal', 'n.', 'In Shetland and Orkney, a freehold; property held by udal, or\n   allodial, right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Udal', 'a.', 'Allodial; -- a term used in Finland, Shetland, and Orkney.\n   See Allodial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Udaler', 'n.', 'Alt. of Udalman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Udalman', 'n.', 'In the Shetland and Orkney Islands, one who holds property\n   by udal, or allodial, right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Udder', 'n.', 'The glandular organ in which milk is secreted and stored; --\n   popularly called the bag in cows and other quadrupeds. See Mamma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Udder', 'n.', 'One of the breasts of a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uddered', 'a.', 'Having an udder or udders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Udderless', 'a.', 'Destitute or deprived of an udder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Udderless', 'a.', 'Hence, without mothers milk; motherless; as, udderless\n   lambs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Udometer', 'n.', 'A rain gauge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ugh', 'interj.', 'An exclamation expressive of disgust, horror, or recoil.\n   Its utterance is usually accompanied by a shudder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uglesome', 'a.', 'Ugly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uglify', 'v. t.', 'To disfigure; to make ugly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uglily', 'adv.', 'In an ugly manner; with deformity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ugliness', 'n.', 'The quality or state of being ugly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ugly', 'superl.', 'Offensive to the sight; contrary to beauty; being of\n   disagreeable or loathsome aspect; unsightly; repulsive; deformed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ugly', 'superl.', 'Ill-natured; crossgrained; quarrelsome; as, an ugly\n   temper; to feel ugly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ugly', 'superl.', 'Unpleasant; disagreeable; likely to cause trouble or\n   loss; as, an ugly rumor; an ugly customer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ugly', 'n.', 'A shade for the face, projecting from the bonnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ugly', 'v. t.', 'To make ugly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ugrian', 'n. pl.', 'A Mongolian race, ancestors of the Finns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ugsome', 'a.', 'Ugly; offensive; loathsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uhlan', 'n.', 'One of a certain description of militia among the Tartars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uhlan', 'n.', 'One of a kind of light cavalry of Tartaric origin, first\n   introduced into European armies in Poland. They are armed with lances,\n   pistols, and sabers, and are employed chiefly as skirmishers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uintatherium', 'n.', 'An extinct genus of large Eocene ungulates allied to\n   Dinoceras. This name is sometimes used for nearly all the known species\n   of the group. See Dinoceras.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ukase', 'n.', 'In Russia, a published proclamation or imperial order,\n   having the force of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulan', 'n.', 'See Uhlan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ularburong', 'n.', 'A large East Indian nocturnal tree snake (Dipsas\n   dendrophila). It is not venomous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulcer', 'n.', 'A solution of continuity in any of the soft parts of the\n   body, discharging purulent matter, found on a surface, especially one\n   of the natural surfaces of the body, and originating generally in a\n   constitutional disorder; a sore discharging pus. It is distinguished\n   from an abscess, which has its beginning, at least, in the depth of the\n   tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulcer', 'n.', 'Fig.: Anything that festers and corrupts like an open sore;\n   a vice in character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulcer', 'v. t.', 'To ulcerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulcerable', 'a.', 'Capable of ulcerating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulcerated', 'imp. & p. p.', 'of Ulcerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulcerating', 'p. pr. & vb. n.', 'of Ulcerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulcerate', 'v. i.', 'To be formed into an ulcer; to become ulcerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulcerate', 'v. t.', 'To affect with, or as with, an ulcer or ulcers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulcerated', 'a.', 'Affected with, or as with, an ulcer or ulcers; as, an\n   ulcerated sore throat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulceration', 'n.', 'The process of forming an ulcer, or of becoming\n   ulcerous; the state of being ulcerated; also, an ulcer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulcerative', 'a.', 'Of or pertaining to ulcers; as, an ulcerative process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulcered', 'a.', 'Ulcerous; ulcerated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulcerous', 'a.', 'Having the nature or character of an ulcer; discharging\n   purulent or other matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulcerous', 'a.', 'Affected with an ulcer or ulcers; ulcerated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulcuscle', 'n.', 'Alt. of Ulcuscule');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulcuscule', 'n.', 'A little ulcer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ule', 'n.', 'A Mexican and Central American tree (Castilloa elastica and C.\n   Markhamiana) related to the breadfruit tree. Its milky juice contains\n   caoutchouc. Called also ule tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulema', 'n.', 'A college or corporation in Turkey composed of the\n   hierarchy, namely, the imams, or ministers of religion, the muftis, or\n   doctors of law, and the cadis, or administrators of justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulexite', 'n.', 'A mineral occurring in white rounded crystalline masses.\n   It is a hydrous borate of lime and soda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uliginose', 'a.', 'Alt. of Uliginous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uliginous', 'a.', 'Muddy; oozy; slimy; also, growing in muddy places.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ullage', 'n.', 'The amount which a vessel, as a cask, of liquor lacks of\n   being full; wantage; deficiency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ullet', 'n.', 'A European owl (Syrnium aluco) of a tawny color; -- called\n   also uluia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ullmannite', 'n.', 'A brittle mineral of a steel-gray color and metallic\n   luster, containing antimony, arsenic, sulphur, and nickel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulluco', 'n.', 'See Melluc/o.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulmaceous', 'a.', 'Of or pertaining to a suborder of urticaceous plants, of\n   which the elm is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulmate', 'n.', 'A salt of ulmic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulmic', 'a.', 'Pertaining to ulmin; designating an acid obtained from\n   ulmin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulmin', 'n.', 'A brown amorphous substance found in decaying vegetation.\n   Cf. Humin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulmus', 'n.', 'A genus of trees including the elm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulna', 'n.', 'The postaxial bone of the forearm, or branchium,\n   corresponding to the fibula of the hind limb. See Radius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulna', 'n.', 'An ell; also, a yard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulnage', 'n.', 'Measurement by the ell; alnage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulnar', 'a.', 'Of or pertaining to the ulna, or the elbow; as, the ulnar\n   nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulnaria', 'pl. ', 'of Ulnare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulnare', 'n.', 'One of the bones or cartilages of the carpus, which\n   articulates with the ulna and corresponds to the cuneiform in man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulodendron', 'n.', 'A genus of fossil trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulonata', 'n. pl.', 'A division of insects nearly equivalent to the true\n   Orthoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulotrichan', 'a.', 'Of or pertaining to the Ulotrichi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulotrichan', 'n.', 'One of the Ulotrichi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulotrichi', 'n. pl.', 'The division of mankind which embraces the races\n   having woolly or crispy hair. Cf. Leiotrichi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulotrichous', 'a.', 'Having woolly or crispy hair; -- opposed to\n   leiotrichous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulster', 'n.', 'A long, loose overcoat, worn by men and women, originally\n   made of frieze from Ulster, Ireland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulterior', 'a.', 'Situated beyond, or on the farther side; thither; --\n   correlative with hither.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulterior', 'a.', 'Further; remoter; more distant; succeeding; as, ulterior\n   demands or propositions; ulterior views; what ulterior measures will be\n   adopted is uncertain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulterior', 'n.', 'Ulterior side or part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulteriorly', 'adv.', 'More distantly or remotely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultima', 'a.', 'Most remote; furthest; final; last.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultima', 'n.', 'The last syllable of a word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultimate', 'a.', 'Farthest; most remote in space or time; extreme; last;\n   final.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultimate', 'a.', 'Last in a train of progression or consequences; tended\n   toward by all that precedes; arrived at, as the last result; final.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultimate', 'a.', 'Incapable of further analysis; incapable of further\n   division or separation; constituent; elemental; as, an ultimate\n   constituent of matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultimated', 'imp. & p. p.', 'of Ultimate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultimating', 'p. pr. & vb. n.', 'of Ultimate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultimate', 'v. t. & i.', 'To come or bring to an end; to eventuate; to end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultimate', 'v. t. & i.', 'To come or bring into use or practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultimately', 'adv.', 'As a final consequence; at last; in the end; as,\n   afflictions often tend to correct immoral habits, and ultimately prove\n   blessings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultimation', 'n.', 'State of being ultimate; that which is ultimate, or\n   final; ultimatum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultimatums', 'pl. ', 'of Ultimatum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultimata', 'pl. ', 'of Ultimatum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultimatum', 'n.', 'A final proposition, concession, or condition;\n   especially, the final propositions, conditions, or terms, offered by\n   either of the parties in a diplomatic negotiation; the most favorable\n   terms a negotiator can offer, the rejection of which usually puts an\n   end to the hesitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultime', 'a.', 'Ultimate; final.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultimity', 'n.', 'The last stage or consequence; finality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultimo', '', 'In the month immediately preceding the present; as, on the\n   1st ultimo; -- usually abbreviated to ult. Cf. Proximo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultion', 'n.', 'The act of taking vengeance; revenge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultra-', 'a.', 'A prefix from the Latin ultra beyond (see Ulterior), having\n   in composition the signification beyond, on the other side, chiefly\n   when joined with words expressing relations of place; as, ultramarine,\n   ultramontane, ultramundane, ultratropical, etc. In other relations it\n   has the sense of excessively, exceedingly, beyond what is common,\n   natural, right, or proper; as, ultraconservative; ultrademocratic,\n   ultradespotic, ultraliberal, ultraradical, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultra', 'a.', 'Going beyond others, or beyond due limit; extreme;\n   fanatical; uncompromising; as, an ultra reformer; ultra measures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultra', 'n.', 'One who advocates extreme measures; an ultraist; an\n   extremist; a radical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultrage', 'n.', 'Outrage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultraism', 'n.', 'The principles of those who advocate extreme measures, as\n   radical reform, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultraist', 'n.', 'One who pushes a principle or measure to extremes; an\n   extremist; a radical; an ultra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultramarine', 'a.', 'Situated or being beyond the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultramarine', 'n.', 'A blue pigment formerly obtained by powdering lapis\n   lazuli, but now produced in large quantities by fusing together silica,\n   alumina, soda, and sulphur, thus forming a glass, colored blue by the\n   sodium polysulphides made in the fusion. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultramontane', '', 'Being beyond the mountains; specifically, being beyond\n   the Alps, in respect to the one who speaks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultramontane', 'n.', 'One who resides beyond the mountains, especially\n   beyond the Alps; a foreigner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultramontane', 'n.', 'One who maintains extreme views favoring the popes\n   supremacy. See Ultramontanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultramontanism', 'n.', 'The principles of those within the Roman Catholic\n   Church who maintain extreme views favoring the popes supremacy; -- so\n   used by those living north of the Alps in reference to the Italians; --\n   rarely used in an opposite sense, as referring to the views of those\n   living north of the Alps and opposed to the papal claims. Cf.\n   Gallicanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultramontanist', 'n.', 'One who upholds ultramontanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultramundane', 'a.', 'Being beyond the world, or beyond the limits of our\n   system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultrared', 'a.', 'Situated beyond or below the red rays; as, the ultrated\n   rays of the spectrum, which are less refrangible than the red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultratropical', 'a.', 'Situated beyond, or outside of, the tropics;\n   extratropical; also, having an excessively tropical temperature; warmer\n   than the tropics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultraviolet', 'a.', 'Lying outside the visible spectrum at its violet end;\n   -- said of rays more refrangible than the extreme violet rays of the\n   spectrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultra vires', '', 'Beyond power; transcending authority; -- a phrase used\n   frequently in relation to acts or enactments by corporations in excess\n   of their chartered or statutory rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultrazodiacal', 'a.', 'Outside the zodiac; being in that part of the\n   heavens that is more than eight degrees from the ecliptic; as,\n   ultrazodiacal planets, that is, those planets which in part of their\n   orbits go beyond the zodiac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ultroneous', 'a.', 'Spontaneous; voluntary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulula', 'n.', 'A genus of owls including the great gray owl (Ulula cinerea)\n   of Arctic America, and other similar species. See Illust. of Owl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ululant', 'a.', 'Howling; wailing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ululated', 'imp. & p. p.', 'of Ululate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ululating', 'p. pr. & vb. n.', 'of Ululate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ululate', 'v. i.', 'To howl, as a dog or a wolf; to wail; as, ululating\n   jackals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ululation', 'n.', 'A howling, as of a dog or wolf; a wailing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ulva', 'n.', 'A genus of thin papery bright green seaweeds including the\n   kinds called sea lettuce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbe', 'prep.', 'About.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbecast', 'v. i.', 'To cast about; to consider; to ponder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbel', 'n.', 'A kind of flower cluster in which the flower stalks radiate\n   from a common point, as in the carrot and milkweed. It is simple or\n   compound; in the latter case, each peduncle bears another little umbel,\n   called umbellet, or umbellule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbellar', 'a.', 'Of or pertaining to an umbel; having the form of an\n   umbel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbellate', 'a.', 'Alt. of Umbellated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbellated', 'a.', 'Bearing umbels; pertaining to an umbel; umbel-like; as,\n   umbellate plants or flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbellet', 'n.', 'A small or partial umbel; an umbellule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbellic', 'a.', 'Pertaining to, or obtained from, certain umbelliferous\n   plants; as, umbellic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbellifer', 'n.', 'A plant producing an umbel or umbels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbelliferone', 'n.', 'A tasteless white crystalline substance, C9H6O3,\n   found in the bark of a certain plant (Daphne Mezereum), and also\n   obtained by the distillation of certain gums from the Umbelliferae, as\n   galbanum, asafetida, etc. It is analogous to coumarin. Called also\n   hydroxy-coumarin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbelliferous', 'a.', 'Producing umbels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbelliferous', 'a.', 'Of or pertaining to a natural order (Umbelliferae)\n   of plants, of which the parsley, carrot, parsnip, and fennel are\n   well-known examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbellularia', 'n.', 'A genus of deep-sea alcyonaria consisting of a\n   cluster of large flowerlike polyps situated at the summit of a long,\n   slender stem which stands upright in the mud, supported by a bulbous\n   base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbellule', 'n.', 'An umbellet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umber', 'n.', 'A brown or reddish pigment used in both oil and water\n   colors, obtained from certain natural clays variously colored by the\n   oxides of iron and manganese. It is commonly heated or burned before\n   being used, and is then called burnt umber; when not heated, it is\n   called raw umber. See Burnt umber, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umber', 'n.', 'An umbrere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umber', 'n.', 'See Grayling, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umber', 'n.', 'An African wading bird (Scopus umbretta) allied to the\n   storks and herons. It is dull dusky brown, and has a large occipital\n   crest. Called also umbrette, umbre, and umber bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umber', 'a.', 'Of or pertaining to umber; resembling umber; olive-brown;\n   dark brown; dark; dusky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umber', 'v. t.', 'To color with umber; to shade or darken; as, to umber\n   over ones face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbery', 'a.', 'Of or pertaining to umber; like umber; as, umbery gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbilic', 'n.', 'The navel; the center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbilic', 'n.', 'An umbilicus. See Umbilicus, 5 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbilic', 'a.', 'See Umbilical, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbilical', 'n.', 'Of or pertaining to an umbilicus, or umbilical cord;\n   umbilic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbilical', 'n.', 'Pertaining to the center; central.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbilicate', 'a.', 'Alt. of Umbilicated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbilicated', 'a.', 'Depressed in the middle, like a navel, as a flower,\n   fruit, or leaf; navel-shaped; having an umbilicus; as, an umbilicated\n   smallpox vesicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbilicated', 'a.', 'Supported by a stalk at the central point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbilication', 'n.', 'A slight, navel-like depression, or dimpling, of the\n   center of a rounded body; as, the umbilication of a smallpox vesicle;\n   also, the condition of being umbilicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbilicus', 'n.', 'The depression, or mark, in the median line of the\n   abdomen, which indicates the point where the umbilical cord separated\n   from the fetus; the navel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbilicus', 'n.', 'An ornamented or painted ball or boss fastened at each\n   end of the stick on which manuscripts were rolled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbilicus', 'n.', 'The hilum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbilicus', 'n.', 'A depression or opening in the center of the base of\n   many spiral shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbilicus', 'n.', 'Either one of the two apertures in the calamus of a\n   feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbilicus', 'n.', 'One of foci of an ellipse, or other curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbilicus', 'n.', 'A point of a surface at which the curvatures of the\n   normal sections are all equal to each other. A sphere may be osculatory\n   to the surface in every direction at an umbilicus. Called also umbilic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umble pie', '', 'A pie made of umbles. See To eat humble pie, under Humble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbles', 'n. pl.', 'The entrails and coarser parts of a deer; hence,\n   sometimes, entrails, in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbones', 'pl. ', 'of Umbo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbos', 'pl. ', 'of Umbo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbo', 'n.', 'The boss of a shield, at or near the middle, and usually\n   projecting, sometimes in a sharp spike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbo', 'n.', 'A boss, or rounded elevation, or a corresponding depression,\n   in a palate, disk, or membrane; as, the umbo in the integument of the\n   larvae of echinoderms or in the tympanic membrane of the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbo', 'n.', 'One of the lateral prominence just above the hinge of a\n   bivalve shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbonate', 'a.', 'Alt. of Umbonated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbonated', 'a.', 'Having a conical or rounded projection or protuberance,\n   like a boss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbrae', 'pl. ', 'of Umbra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbra', 'n.', 'The conical shadow projected from a planet or satellite, on\n   the side opposite to the sun, within which a spectator could see no\n   portion of the suns disk; -- used in contradistinction from penumbra.\n   See Penumbra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbra', 'n.', 'The central dark portion, or nucleus, of a sun spot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbra', 'n.', 'The fainter part of a sun spot; -- now more commonly called\n   penumbra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbra', 'n.', 'Any one of several species of sciaenoid food fishes of the\n   genus Umbrina, especially the Mediterranean species (U. cirrhosa),\n   which is highly esteemed as a market fish; -- called also ombre, and\n   umbrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbraculiferous', 'a.', 'Bearing something like an open umbrella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbraculiform', 'a.', 'Having the form of anything that serves to shade, as\n   a tree top, an umbrella, and the like; specifically (Bot.), having the\n   form of an umbrella; umbrella-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbrage', 'n.', 'Shade; shadow; obscurity; hence, that which affords a\n   shade, as a screen of trees or foliage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbrage', 'n.', 'Shadowy resemblance; shadow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbrage', 'n.', 'The feeling of being overshadowed; jealousy of another, as\n   standing in ones light or way; hence, suspicion of injury or wrong;\n   offense; resentment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbrageous', 'a.', 'Forming or affording a shade; shady; shaded; as,\n   umbrageous trees or foliage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbrageous', 'a.', 'Not easily perceived, as if from being darkened or\n   shaded; obscure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbrageous', 'a.', 'Feeling jealousy or umbrage; taking, or disposed to\n   take, umbrage; suspicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbrate', 'v. t.', 'To shade; to shadow; to foreshadow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbratic', 'a.', 'Alt. of Umbratical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbratical', 'a.', 'Of or pertaining to the shade or darkness; shadowy;\n   unreal; secluded; retired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbratile', 'a.', 'Umbratic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbratious', 'a.', 'Suspicious; captious; disposed to take umbrage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbre', 'n.', 'See Umber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbrel', 'n.', 'An umbrella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbrella', 'n.', 'A shade, screen, or guard, carried in the hand for\n   sheltering the person from the rays of the sun, or from rain or snow.\n   It is formed of silk, cotton, or other fabric, extended on strips of\n   whalebone, steel, or other elastic material, inserted, or fastened to,\n   a rod or stick by means of pivots or hinges, in such a way as to allow\n   of being opened and closed with ease. See Parasol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbrella', 'n.', 'The umbrellalike disk, or swimming bell, of a jellyfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbrella', 'n.', 'Any marine tectibranchiate gastropod of the genus\n   Umbrella, having an umbrella-shaped shell; -- called also umbrella\n   shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbrere', 'n.', 'Alt. of Umbriere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbriere', 'n.', 'In ancient armor, a visor, or projection like the peak of\n   a cap, to which a face guard was sometimes attached. This was sometimes\n   fixed, and sometimes moved freely upon the helmet and could be raised\n   like the beaver. Called also umber, and umbril.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbrette', 'n.', 'See Umber, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbriferous', 'a.', 'Casting or making a shade; umbrageous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbril', 'n.', 'A umbrere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbrine', 'n.', 'See Umbra, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbrose', 'a.', 'Shady; umbrageous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umbrosity', 'n.', 'The quality or state of being umbrose; shadiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umhofo', 'n.', 'An African two-horned rhinoceros (Atelodus, / Rhinoceros,\n   simus); -- called also chukuru, and white rhinoceros.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umlaut', 'n.', 'The euphonic modification of a root vowel sound by the\n   influence of a, u, or especially i, in the syllable which formerly\n   followed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umlauted', 'a.', 'Having the umlaut; as, umlauted vowels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umpirage', 'n.', 'The office of an umpire; the power, right, or authority\n   of an umpire to decide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umpirage', 'n.', 'The act of umpiring; arbitrament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umpire', 'n.', 'A person to whose sole decision a controversy or question\n   between parties is referred; especially, one chosen to see that the\n   rules of a game, as cricket, baseball, or the like, are strictly\n   observed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umpire', 'n.', 'A third person, who is to decide a controversy or question\n   submitted to arbitrators in case of their disagreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umpired', 'imp. & p. p.', 'of Umpire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umpiring', 'p. pr. & vb. n.', 'of Umpire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umpire', 'v. t.', 'To decide as umpire; to arbitrate; to settle, as a\n   dispute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umpire', 'v. t.', 'To perform the duties of umpire in or for; as, to umpire\n   a game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umpire', 'v. i.', 'To act as umpire or arbitrator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umpireship', 'n.', 'Umpirage; arbitrament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umpress', 'n.', 'Female umpire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umquhile', 'adv.', 'Some time ago; formerly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Umquhile', 'a.', 'Former.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Un-', '', 'An inseparable verbal prefix or particle. It is prefixed: (a) To\n   verbs to express the contrary, and not the simple negative, of the\n   action of the verb to which it is prefixed; as in uncoil, undo, unfold.\n   (b) To nouns to form verbs expressing privation of the thing, quality,\n   or state expressed by the noun, or separation from it; as in unchild,\n   unsex. Sometimes particles and participial adjectives formed with this\n   prefix coincide in form with compounds of the negative prefix un- (see\n   2d Un-); as in undone (from undo), meaning unfastened, ruined; and\n   undone (from 2d un- and done) meaning not done, not finished. Un- is\n   sometimes used with an intensive force merely; as in unloose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Un-', 'adv.', 'An inseparable prefix, or particle, signifying not; in-;\n   non-. In- is prefixed mostly to words of Latin origin, or else to words\n   formed by Latin suffixes; un- is of much wider application, and is\n   attached at will to almost any adjective, or participle used\n   adjectively, or adverb, from which it may be desired to form a\n   corresponding negative adjective or adverb, and is also, but less\n   freely, prefixed to nouns. Un- sometimes has merely an intensive force;\n   as in unmerciless, unremorseless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Un-', 'adv.', 'Un- is prefixed to adjectives, or to words used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Un-', 'adv.', 'To adjectives, to denote the absence of the quality\n   designated by the adjective');
  INSERT INTO words (word, word_type, description) VALUES  (    'Un-', 'adv.', 'To past particles, or to adjectives formed after the analogy\n   of past particles, to indicate the absence of the condition or state\n   expressed by them');
  INSERT INTO words (word, word_type, description) VALUES  (    'Un-', 'adv.', 'To present particles which come from intransitive verbs, or\n   are themselves employed as adjectives, to mark the absence of the\n   activity, disposition, or condition implied by the participle; as, -\n   ---- and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Un-', 'adv.', 'Those which have acquired an opposed or contrary, instead of\n   a merely negative, meaning; as, unfriendly, ungraceful, unpalatable,\n   unquiet, and the like; or else an intensive sense more than a prefixed\n   not would express; as, unending, unparalleled, undisciplined,\n   undoubted, unsafe, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Un-', 'adv.', 'Those which have the value of independent words, inasmuch as\n   the simple words are either not used at all, or are rarely, or at least\n   much less frequently, used; as, unavoidable, unconscionable,\n   undeniable, unspeakable, unprecedented, unruly, and the like; or\n   inasmuch as they are used in a different sense from the usual meaning\n   of the primitive, or especially in one of the significations of the\n   latter; as, unaccountable, unalloyed, unbelieving, unpretending,\n   unreserved, and the like; or inasmuch as they are so frequently and\n   familiarly used that they are hardly felt to be of negative origin; as,\n   uncertain, uneven, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Un-', 'adv.', 'Those which are anomalous, provincial, or, for some other\n   reason, not desirable to be used, and are so indicated; as, unpure for\n   impure, unsatisfaction for dissatisfaction, unexpressible for\n   inexpressible, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Un-', 'adv.', 'Un- is prefixed to nouns to express the absence of, or the\n   contrary of, that which the noun signifies; as, unbelief, unfaith,\n   unhealth, unrest, untruth, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unability', 'n.', 'Inability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unable', 'a.', 'Not able; not having sufficient strength, means, knowledge,\n   skill, or the like; impotent weak; helpless; incapable; -- now usually\n   followed by an infinitive or an adverbial phrase; as, unable for work;\n   unable to bear fatigue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unabled', 'a.', 'Disabled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unableness', 'n.', 'Inability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Una boat', '', 'The English name for a catboat; -- so called because Una\n   was the name of the first boat of this kind taken to England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unabridged', 'a.', 'Not abridged, or shortened; full; complete; entire;\n   whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unabsorbable', 'a.', 'Not absorbable; specifically (Physiol.), not capable\n   of absorption; unable to pass by osmosis into the circulating blood;\n   as, the unabsorbable portion of food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unacceptability', 'n.', 'The quality of being unacceptable;\n   unacceptableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unacceptable', 'a.', 'Not acceptable; not pleasing; not welcome;\n   unpleasant; disagreeable; displeasing; offensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unaccessible', 'a.', 'Inaccessible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unaccomplished', 'a.', 'Not accomplished or performed; unfinished; also,\n   deficient in accomplishment; unrefined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unaccomplishment', 'n.', 'The state of being unaccomplished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unaccountability', 'n.', 'The quality or state of being unaccountable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unaccountable', 'a.', 'Not accountable or responsible; free from control.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unaccountable', 'a.', 'Not to be accounted for; inexplicable; not consonant\n   with reason or rule; strange; mysterious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unaccurate', 'a.', 'Inaccurate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unaccurateness', 'n.', 'Inaccuracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unaccustomed', 'a.', 'Not used; not habituated; unfamiliar; unused; --\n   which to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unaccustomed', 'a.', 'Not usual; uncommon; strange; new.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unacquaintance', 'n.', 'The quality or state of being unacquainted; want of\n   acquaintance; ignorance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unacquainted', 'a.', 'Not acquainted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unacquainted', 'a.', 'Not usual; unfamiliar; strange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unacquaintedness', 'n.', 'Unacquaintance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unactive', 'a.', 'Inactive; listless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unactive', 'v. t.', 'To render inactive or listless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unactiveness', 'n.', 'Inactivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unadmissible', 'a.', 'Alt. of Unadmittable');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unadmittable', 'a.', 'Inadmissible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unadulterate', 'a.', 'Alt. of Unadulterated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unadulterated', 'a.', 'Not adulterated; pure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unadvisable', 'a.', 'Not advisable; inadvisable; inexpedient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unadvised', 'a.', 'Not prudent; not discreet; ill advised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unadvised', 'a.', 'Done without due consideration; wanton; rash;\n   inconsiderate; as, an unadvised proceeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unaffected', 'a.', 'Not affected or moved; destitute of affection or\n   emotion; uninfluenced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unaffected', 'a.', 'Free from affectation; plain; simple; natural; real;\n   sincere; genuine; as, unaffected sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unafiled', 'a.', 'Undefiled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unagreeable', 'a.', 'Disagreeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unagreeable', 'a.', 'Not agreeing or consistent; unsuitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unaidable', 'a.', 'Incapable of being aided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unalienable', 'a.', 'Inalienable; as, unalienable rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unalist', 'n.', 'An ecclesiastical who holds but one benefice; --\n   distinguished from pluralist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unallied', 'a.', 'Not allied; having no ally; having no connection or\n   relation; as, unallied species or genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unalloyed', 'a.', 'Not alloyed; not reduced by foreign admixture; unmixed;\n   unqualified; pure; as, unalloyed metals; unalloyed happiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unalmsed', 'a.', 'Not having received alms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unambiguity', 'n.', 'Absence of ambiguity; clearness; perspicuity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unambition', 'n.', 'The absence of ambition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unamiability', 'n.', 'The quality or state of being unamiable; moroseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unamiable', 'a.', 'Not amiable; morose; ill-natured; repulsive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unanchor', 'v. t.', 'To loose from the anchor, as a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unaneled', 'a.', 'Not aneled; not having received extreme unction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unanimate', 'a.', 'Unanimous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unanimity', 'n.', 'The quality or state of being unanimous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unanimous', 'a.', 'Being of one mind; agreeing in opinion, design, or\n   determination; consentient; not discordant or dissentient; harmonious;\n   as, the assembly was unanimous; the members of the council were\n   unanimous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unanimous', 'a.', 'Formed with unanimity; indicating unanimity; having the\n   agreement and consent of all; agreed upon without the opposition or\n   contradiction of any; as, a unanimous opinion; a unanimous vote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unanswerability', 'n.', 'The quality of being unanswerable;\n   unanswerableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unanswerable', 'a.', 'Not answerable; irrefutable; conclusive; decisive;\n   as, he have an unanswerable argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unanswered', 'a.', 'Not answered; not replied; as, an unanswered letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unanswered', 'a.', 'Not refuted; as, an unanswered argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unanswered', 'a.', 'Not responded to in kind; unrequited; as, unanswered\n   affection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unappalled', 'a.', 'Not appalled; not frightened; dauntless; undaunted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unapparel', 'v. t.', 'To divest of clothing; to strip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unappealable', 'a.', 'Not appealable; that can not be carried to a higher\n   tribunal by appeal; as, an unappealable suit or action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unappealable', 'a.', 'Not to be appealed from; -- said of a judge or a\n   judgment that can not be overruled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unappliable', 'a.', 'Inapplicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unapplicable', 'a.', 'Inapplicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unappropriate', 'a.', 'Inappropriate; unsuitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unappropriate', 'a.', 'Not appropriated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unappropriate', 'v. t.', 'To take from private possession; to restore to\n   the possession or right of all; as, to unappropriate a monopoly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unappropriated', 'a.', 'Not specially appropriate; having not special\n   application.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unappropriated', 'a.', 'Not granted to any person, corporation, or the\n   like, to the exclusion of others; as, unappropriated lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unappropriated', 'a.', 'Not granted for, or applied to, any specific\n   purpose; as, the unappropriated moneys in the treasury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unapproved', 'a.', 'Not approved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unapproved', 'a.', 'Not proved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unapt', 'a.', 'Inapt; slow; dull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unapt', 'a.', 'Unsuitable; unfit; inappropriate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unapt', 'a.', 'Not accustomed and not likely; not disposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unaquit', 'a.', 'Unrequited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unargued', 'a.', 'Not argued or debated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unargued', 'a.', 'Not argued against; undisputed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unargued', 'a.', 'Not censured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unarm', 'v. t.', 'To disarm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unarm', 'v. i.', 'To puff off, or lay down, ones arms or armor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unarmed', 'a.', 'Not armed or armored; having no arms or weapons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unarmed', 'a.', 'Having no hard and sharp projections, as spines, prickles,\n   spurs, claws, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unarted', 'a.', 'Ignorant of the arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unarted', 'a.', 'Not artificial; plain; simple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unartful', 'a.', 'Lacking art or skill; artless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unartistic', 'a.', 'Inartistic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unascried', 'a.', 'Not descried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unaserved', 'a.', 'Not served.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unassuming', 'a.', 'Not assuming; not bold or forward; not arrogant or\n   presuming; humble; modest; retiring; as, an unassuming youth;\n   unassuming manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unassured', 'a.', 'Not assured; not bold or confident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unassured', 'a.', 'Not to be trusted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unassured', 'a.', 'Not insured against loss; as, unassured goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unatonable', 'a.', 'Not capable of being brought into harmony;\n   irreconcilable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unatonable', 'a.', 'Incapable of being atoned for; inexpiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unattached', 'a.', 'Not attached; not adhering; having no engagement; free.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unattached', 'a.', 'Not assigned to any company or regiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unattached', 'a.', 'Not taken or arrested.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unattentive', 'a.', 'Inattentive; careless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unattire', 'v. t.', 'To divest of attire; to undress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unau', 'n.', 'The two-toed sloth (Cholopus didactylus), native of South\n   America. It is about two feet long. Its color is a uniform grayish\n   brown, sometimes with a reddish tint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unaudienced', 'a.', 'Not given an audience; not received or heard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unauspicious', 'a.', 'Inauspicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unauthorize', 'v. t.', 'To disown the authority of; to repudiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unavoidable', 'a.', 'Not avoidable; incapable of being shunned or\n   prevented; inevitable; necessary; as, unavoidable troubles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unavoidable', 'a.', 'Not voidable; incapable of being made null or void.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unavoided', 'a.', 'Not avoided or shunned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unavoided', 'a.', 'Unavoidable; inevitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unaware', 'a.', 'Not aware; not noticing; giving no heed; thoughtless;\n   inattentive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unaware', 'adv.', 'Unawares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unawares', 'adv.', 'Without design or preparation; suddenly; without\n   premeditation, unexpectedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unbacked', 'a.', 'Never mounted by a rider; unbroken.');
