  INSERT INTO words (word, word_type, description) VALUES  (    'Uncovenable', 'a.', 'Not covenable; inconvenient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncovenanted', 'a.', 'Not covenanted; not granted or entered into under a\n   covenant, agreement, or contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncovenanted', 'a.', 'Not having joined in a league, or assented to a\n   covenant or agreement, as to the Solemn League and Covenant of the\n   Scottish people in the times of the Stuarts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncovenanted', 'a.', 'Not having entered into relationship with God through\n   the appointed means of grace; also, not promised or assured by the\n   divine promises or conditions; as, uncovenanted mercies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncovered', 'imp. & p. p.', 'of Uncover');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncovering', 'p. pr. & vb. n.', 'of Uncover');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncover', 'v. t.', 'To take the cover from; to divest of covering; as, to\n   uncover a box, bed, house, or the like; to uncover ones body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncover', 'v. t.', 'To show openly; to disclose; to reveal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncover', 'v. t.', 'To divest of the hat or cap; to bare the head of; as,\n   to uncover ones head; to uncover ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncover', 'v. i.', 'To take off the hat or cap; to bare the head in token\n   of respect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncover', 'v. i.', 'To remove the covers from dishes, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncowl', 'v. t.', 'To divest or deprive of a cowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncreate', 'v. t.', 'To deprive of existence; to annihilate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncreate', 'a.', 'Uncreated; self-existent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncreated', 'a.', 'Deprived of existence; annihilated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncreated', 'a.', 'Not yet created; as, misery uncreated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncreated', 'a.', 'Not existing by creation; self-existent; eternal; as,\n   God is an uncreated being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncreatedness', 'n.', 'The quality or state of being uncreated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncredible', 'a.', 'Incredible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncredit', 'v. t.', 'To cause to be disbelieved; to discredit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncreditable', 'a.', 'Discreditable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncrown', 'v. t.', 'To deprive of a crown; to take the crown from; hence,\n   to discrown; to dethrone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncrudded', 'a.', 'Not cruddled, or curdled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unction', 'n.', 'The act of anointing, smearing, or rubbing with an\n   unguent, oil, or ointment, especially for medical purposes, or as a\n   symbol of consecration; as, mercurial unction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unction', 'n.', 'That which is used for anointing; an unguent; an ointment;\n   hence, anything soothing or lenitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unction', 'n.', 'Divine or sanctifying grace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unction', 'n.', 'That quality in language, address, or the like, which\n   excites emotion; especially, strong devotion; religious fervor and\n   tenderness; sometimes, a simulated, factitious, or unnatural fervor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unctious', 'a.', 'Unctuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unctuosity', 'n.', 'Quality or state of being unctuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unctuous', 'a.', 'Of the nature or quality of an unguent or ointment;\n   fatty; oily; greasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unctuous', 'a.', 'Having a smooth, greasy feel, as certain minerals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unctuous', 'a.', 'Bland; suave; also, tender; fervid; as, an unctuous\n   speech; sometimes, insincerely suave or fervid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unculpable', 'a.', 'Inculpable; not blameworthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncult', 'a.', 'Not cultivated; rude; illiterate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unculture', 'n.', 'Want of culture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncunning', 'a.', 'Ignorant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncunningly', 'adv.', 'Ignorantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncunningness', 'n.', 'Ignorance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncurable', 'a.', 'Incurable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncurably', 'adv.', 'In an uncurable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncurbable', 'a.', 'Not capable of being curbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncurl', 'v. t.', 'To loose from curls, or ringlets; to straighten out, as\n   anything curled or curly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncurl', 'v. i.', 'To become uncurled, or straight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncurrent', 'a.', 'Not current. Specifically: Not passing in common\n   payment; not receivable at par or full value; as, uncurrent notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncurse', 'v. t.', 'To free from a curse or an execration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncurtain', 'v. t.', 'To remove a curtain from; to reveal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unci', 'pl. ', 'of Uncus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncus', 'n.', 'A hook or claw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncustomable', 'a.', 'Not customable, or subject to custom duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncustomed', 'a.', 'Uncustomable; also, not having paid duty or customs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncut', 'a.', 'Not cut; not separated or divided by cutting or otherwise;\n   -- said especially of books, periodicals, and the like, when the leaves\n   have not been separated by trimming in binding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncut', 'a.', 'Not ground, or otherwise cut, into a certain shape; as, an\n   uncut diamond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncuth', 'a.', 'Unknown; strange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncuth', 'n.', 'A stranger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uncypher', 'v. t.', 'See Uncipher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undam', 'v. t.', 'To free from a dam, mound, or other obstruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undampned', 'a.', 'Uncondemned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undated', 'a.', 'Rising and falling in waves toward the margin, as a leaf;\n   waved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undated', 'a.', 'Not dated; having no date; of unknown age; as, an undated\n   letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undauntable', 'a.', 'Incapable of being daunted; intrepid; fearless;\n   indomitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undaunted', 'a.', 'Not daunted; not subdued or depressed by fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unde', 'a.', 'Waving or wavy; -- applied to ordinaries, or division lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undeadly', 'a.', 'Not subject to death; immortal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undeaf', 'v. t.', 'To free from deafness; to cause to hear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undecagon', 'n.', 'A figure having eleven angles and eleven sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undecane', 'n.', 'A liquid hydrocarbon, C11H24, of the methane series,\n   found in petroleum; -- so called from its containing eleven carbon\n   atoms in the molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undeceive', 'v. t.', 'To cause to be no longer deceived; to free from\n   deception, fraud, fallacy, or mistake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undecency', 'n.', 'Indecency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undecennary', 'a.', 'Occurring once in every period of eleven years;\n   undecennial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undecennial', 'a.', 'Occurring or observed every eleventh year; belonging\n   to, or continuing, a period of eleven years; undecennary; as, an\n   undecennial festival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undecent', 'a.', 'Indecent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undecide', 'v. t.', 'To reverse or recant, as a previous decision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undecisive', 'a.', 'Indecisive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undeck', 'v. t.', 'To divest of ornaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undecked', 'a.', 'Not decked; unadorned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undecked', 'a.', 'Not having a deck; as, an undecked vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undecolic', 'a.', 'Pertaining to, or designating, an acid, C11H18O2, of the\n   propiolic acid series, obtained indirectly from undecylenic acid as a\n   white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undecreed', 'a.', 'Not decreed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undecreed', 'a.', 'Reversed or nullified by decree, as something previously\n   decreed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undecyl', 'n.', 'The radical regarded as characteristic of undecylic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undecylenic', 'a.', 'Pertaining to, or designating, an acid C11H20O2,\n   homologous with acrylic acid, and obtained as a white crystalline\n   substance by the distillation of castor oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undecylic', 'a.', 'Related to, derived from, or containing, undecyl;\n   specifically, designating that member of the fatty acids which\n   corresponds to undecane, and is obtained as a white crystalline\n   substance, C11H22O2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undeeded', 'a.', 'Not deeded or transferred by deed; as, undeeded land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undeeded', 'a.', 'Not made famous by any great action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undefatigable', 'a.', 'Indefatigable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undefeasible', 'a.', 'Indefeasible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undefine', 'v. t.', 'To make indefinite; to obliterate or confuse the\n   definition or limitations of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undeify', 'v. t.', 'To degrade from the state of deity; to deprive of the\n   character or qualities of a god; to deprive of the reverence due to a\n   god.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undeniable', 'a.', 'Not deniable; incapable of denial; palpably true;\n   indisputable; obvious; as, undeniable evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undeniable', 'a.', 'Unobjectionable; unquestionably excellent; as, a person\n   of undeniable connections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undeniably', 'adv.', 'In an undeniable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undepartable', 'a.', 'Incapable of being parted; inseparable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Under', 'prep.', 'Below or lower, in place or position, with the idea of\n   being covered; lower than; beneath; -- opposed to over; as, he stood\n   under a tree; the carriage is under cover; a cellar extends under the\n   whole house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Under', 'prep.', 'Denoting relation to some thing or person that is\n   superior, weighs upon, oppresses, bows down, governs, directs,\n   influences powerfully, or the like, in a relation of subjection,\n   subordination, obligation, liability, or the like; as, to travel under\n   a heavy load; to live under extreme oppression; to have fortitude under\n   the evils of life; to have patience under pain, or under misfortunes;\n   to behave like a Christian under reproaches and injuries; under the\n   pains and penalties of the law; the condition under which one enters\n   upon an office; under the necessity of obeying the laws; under vows of\n   chastity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Under', 'prep.', 'Denoting relation to something that exceeds in rank or\n   degree, in number, size, weight, age, or the like; in a relation of the\n   less to the greater, of inferiority, or of falling short.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Under', 'prep.', 'Denoting relation to something that comprehends or\n   includes, that represents or designates, that furnishes a cover,\n   pretext, pretense, or the like; as, he betrayed him under the guise of\n   friendship; Morpheus is represented under the figure of a boy asleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Under', 'prep.', 'Less specifically, denoting the relation of being\n   subject, of undergoing regard, treatment, or the like; as, a bill under\n   discussion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Under', 'adv.', 'In a lower, subject, or subordinate condition; in\n   subjection; -- used chiefly in a few idiomatic phrases; as, to bring\n   under, to reduce to subjection; to subdue; to keep under, to keep in\n   subjection; to control; to go under, to be unsuccessful; to fail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Under', 'a.', 'Lower in position, intensity, rank, or degree; subject;\n   subordinate; -- generally in composition with a noun, and written with\n   or without the hyphen; as, an undercurrent; undertone; underdose;\n   under-garment; underofficer; undersheriff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underact', 'v. t.', 'To perform inefficiently, as a play; to act feebly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underaction', 'n.', 'Subordinate action; a minor action incidental or\n   subsidiary to the main story; an episode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underactor', 'n.', 'A subordinate actor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Under-age', 'a.', 'Not having arrived at adult age, or at years of\n   discretion; hence, raw; green; immature; boyish; childish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underagent', 'n.', 'A subordinate agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underaid', 'v. t.', 'To aid clandestinely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Under-arm', 'a.', 'Done (as bowling) with the arm not raised above the\n   elbow, that is, not swung far out from the body; underhand. Cf.\n   Over-arm and Round-Arm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underback', 'n.', 'A vessel which receives the wort as it flows from the\n   mashing tub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underbear', 'v. t.', 'To support; to endure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underbear', 'v. t.', 'To line; to guard; to face; as, cloth of gold\n   underborne with blue tinsel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underbearer', 'n.', 'One who supports or sustains; especially, at a\n   funeral, one of those who bear the copse, as distinguished from a\n   bearer, or pallbearer, who helps to hold up the pall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underbid', 'v. t.', 'To bid less than, as when a contract or service is\n   offered to the lowest bidder; to offer to contract, sell, or do for a\n   less price than.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underbind', 'v. t.', 'To bind beneath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underboard', 'adv.', 'Under the board, or table; hence, secretly; unfairly;\n   underhand. See the Note under Aboveboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underbrace', 'v. t.', 'To brace, fasten, or bind underneath or below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underbranch', 'n.', 'A lower branch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underbranch', 'n.', 'A twig or branchlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underbred', 'a.', 'Not thoroughly bred; ill-bred; as, an underbred fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underbrush', 'n.', 'Shrubs, small trees, and the like, in a wood or forest,\n   growing beneath large trees; undergrowth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underbuilder', 'n.', 'A subordinate or assistant builder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underbuilding', 'n.', 'Same as Substruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underbuy', 'v. t.', 'To buy at less than the real value or worth; to buy\n   cheaper than.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undercast', 'v. t.', 'To cast under or beneath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underchamberlain', 'n.', 'A deputy chamberlain of the exchequer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underchanter', 'n.', 'Same as Subchanter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underchaps', 'n. pl.', 'The lower chaps or jaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undercharge', 'v. t.', 'To charge below or under; to charge less than is\n   usual or suitable fro; as, to undercharge goods or services.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undercharge', 'v. t.', 'To put too small a charge into; as, to undercharge\n   a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undercharge', 'n.', 'A charge that is less than is usual or suitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underclay', 'n.', 'A stratum of clay lying beneath a coal bed, often\n   containing the roots of coal plants, especially the Stigmaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undercliff', 'n.', 'A subordinate cliff on a shore, consisting of material\n   that has fallen from the higher cliff above.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underclothes', 'n. pl.', 'Clothes worn under others, especially those worn\n   next the skin for warmth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underclothing', 'n.', 'Same as Underclothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undercoat', 'n.', 'A coat worn under another; a light coat, as\n   distinguished from an overcoat, or a greatcoat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undercoat', 'n.', 'A growth of short hair or fur partially concealed by a\n   longer growth; as, a dogs undercoat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underconduct', 'n.', 'A lower conduit; a subterranean conduit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underconsumption', 'n.', 'Consumption of less than is produced; consumption\n   of less than the usual amount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undercraft', 'n.', 'A sly trick or device; as, an undercraft of authors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undercreep', 'v. i.', 'To creep secretly or privily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undercrest', 'v. t.', 'To support as a crest; to bear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undercroft', 'n.', 'A subterranean room of any kind; esp., one under a\n   church (see Crypt), or one used as a chapel or for any sacred purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undercry', 'v. i.', 'To cry aloud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undercurrent', 'n.', 'A current below the surface of water, sometimes\n   flowing in a contrary direction to that on the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undercurrent', 'n.', 'Hence, figuratively, a tendency of feeling, opinion,\n   or the like, in a direction contrary to what is publicly shown; an\n   unseen influence or tendency; as, a strong undercurrent of sentiment in\n   favor of a prisoner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undercurrent', 'a.', 'Running beneath the surface; hidden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undercut', 'n.', 'The lower or under side of a sirloin of beef; the fillet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undercut', 'v. t.', 'To cut away, as the side of an object, so as to leave\n   an overhanging portion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underdealing', 'n.', 'Crafty, unfair, or underhand dealing; unfair\n   practice; trickery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underdelve', 'v. t.', 'To delve under.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underdig', 'v. t.', 'To dig under or beneath; to undermine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underditch', 'v. t.', 'To dig an underground ditches in, so as to drain the\n   surface; to underdrain; as, to underditch a field or a farm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underdo', 'v. i.', 'To do less than is requisite or proper; -- opposed to\n   overdo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underdo', 'v. t.', 'To do less thoroughly than is requisite; specifically,\n   to cook insufficiently; as, to underdo the meat; -- opposed to overdo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underdoer', 'n.', 'One who underdoes; a shirk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underdolven', '', 'p. p. of Underdelve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underdose', 'n.', 'A dose which is less than required; a small or\n   insufficient dose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underdose', 'v. t. & i.', 'To give an underdose or underdoses to; to\n   practice giving insufficient doses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underdrain', 'n.', 'An underground drain or trench with openings through\n   which the water may percolate from the soil or ground above.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underdrain', 'v. t.', 'To drain by forming an underdrain or underdrains in;\n   as, to underdrain land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underdressed', 'a.', 'Not dresses enough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underestimate', 'v. t.', 'To set to/ low a value on; to estimate below the\n   truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underestimate', 'n.', 'The act of underestimating; too low an estimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underfaction', 'n.', 'A subordinate party or faction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underfaculty', 'n.', 'An inferior or subordinate faculty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underfarmer', 'n.', 'An assistant farmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underfeed', 'v. t.', 'To feed with too little food; to supply with an\n   insufficient quantity of food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underfellow', 'n.', 'An underling // mean, low fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underfilling', 'n.', 'The filling below or beneath; the under part of a\n   building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underfollow', 'v. t.', 'To follow closely or immediately after.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underfong', 'v. t.', 'To undertake; to take in hand; to receive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underfong', 'v. t.', 'To insnare; to circumvent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Underfong', 'v. t.', 'To sustain; to support; to guard.');
