  INSERT INTO words (word, word_type, description) VALUES  (    'Undulate', 'v. i.', 'To move in, or have, undulations or waves; to vibrate;\n   to wave; as, undulating air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undulated', 'a.', 'Resembling, or in the nature of, waves; having a wavy\n   surface; undulatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undulated', 'a.', 'Waved obtusely up and down, near the margin, as a leaf\n   or corolla; wavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undulated', 'a.', 'Formed with elevations and depressions resembling waves;\n   having wavelike color markings; as, an undulated shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undulating', 'a.', 'Rising and falling like waves; resembling wave form or\n   motion; undulatory; rolling; wavy; as, an undulating medium; undulating\n   ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undulation', 'n.', 'The act of undulating; a waving motion or vibration;\n   as, the undulations of a fluid, of water, or of air; the undulations of\n   sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undulation', 'n.', 'A wavy appearance or outline; waviness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undulation', 'n.', 'The tremulous tone produced by a peculiar pressure of\n   the finger on a string, as of a violin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undulation', 'n.', 'The pulsation caused by the vibrating together of two\n   tones not quite in unison; -- called also beat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undulation', 'n.', 'A motion to and fro, up and down, or from side to side,\n   in any fluid or elastic medium, propagated continuously among its\n   particles, but with no translation of the particles themselves in the\n   direction of the propagation of the wave; a wave motion; a vibration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undulationist', 'n.', 'One who advocates the undulatory theory of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undulative', 'a.', 'Consisting in, or accompanied by, undulations;\n   undulatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undulatory', 'a.', 'Moving in the manner of undulations, or waves;\n   resembling the motion of waves, which successively rise or swell rise\n   or swell and fall; pertaining to a propagated alternating motion,\n   similar to that of waves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undull', 'v. t.', 'To remove the dullness of; to clear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undulous', 'a.', 'Undulating; undulatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unduly', 'adv.', 'In an undue manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undumpish', 'v. t.', 'To relieve from the dumps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undust', 'v. t.', 'To free from dust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undwellable', 'a.', 'Uninhabitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undwelt', 'a.', 'Not lived (in); -- with in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Undying', 'a.', 'Not dying; imperishable; unending; immortal; as, the\n   undying souls of men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uneared', 'a.', 'Not eared, or plowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unearned', 'a.', 'Not earned; not gained by labor or service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unearthed', 'imp. & p. p.', 'of Unearth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unearthing', 'p. pr. & vb. n.', 'of Unearth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unearth', 'v. t.', 'To drive or draw from the earth; hence, to uncover; to\n   bring out from concealment; to bring to light; to disclose; as, to\n   unearth a secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unearthly', 'a.', 'Not terrestrial; supernatural; preternatural; hence,\n   weird; appalling; terrific; as, an unearthly sight or sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unease', 'n.', 'Want of ease; uneasiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uneasity', 'adv.', 'In an easy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uneasiness', 'n.', 'The quality or state of being uneasy; restlessness;\n   disquietude; anxiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uneasiness', 'n.', 'The quality of making uneasy; discomfort; as, the\n   uneasiness of the road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uneasy', 'a.', 'Not easy; difficult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uneasy', 'a.', 'Restless; disturbed by pain, anxiety, or the like;\n   disquieted; perturbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uneasy', 'a.', 'Not easy in manner; constrained; stiff; awkward; not\n   graceful; as, an uneasy deportment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uneasy', 'a.', 'Occasioning want of ease; constraining; cramping;\n   disagreeable; unpleasing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uneath', 'a.', 'Not easy; difficult; hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uneath', 'adv.', 'Not easily; hardly; scarcely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unedge', 'v. t.', 'To deprive of the edge; to blunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unefectual', 'a.', 'Ineffectual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unelastic', 'a.', 'Not elastic; inelastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unelasticity', 'n.', 'Inelasticity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unelegant', 'a.', 'Inelegant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uneligible', 'a.', 'Ineligible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unembarrassed', 'a.', 'Not embarrassed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unembarrassed', 'a.', 'Not perplexed in mind; not confused; as, the speaker\n   appeared unembarrassed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unembarrassed', 'a.', 'Free from pecuniary difficulties or encumbrances;\n   as, he and his property are unembarrassed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unembarrassed', 'a.', 'Free from perplexing connection; as, the question\n   comes into court unembarrassed with irrelevant matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unembarrassment', 'n.', 'Freedom from embarrassment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unembodied', 'a.', 'Free from a corporeal body; disembodied; as, unembodied\n   spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unembodied', 'a.', 'Not embodied; not collected into a body; not yet\n   organized; as, unembodied militia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unempirically', 'adv.', 'Not empirically; without experiment or experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unemployed', 'a.', 'Not employed in manual or other labor; having no\n   regular work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unemployed', 'a.', 'Not invested or used; as, unemployed capital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unencumber', 'v. t.', 'To free from incumbrance; to disencumber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unendly', 'a.', 'Unending; endless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unentangle', 'v. t.', 'To disentangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unequal', 'a.', 'Not equal; not matched; not of the same size, length,\n   breadth, quantity, strength, talents, acquirements, age, station, or\n   the like; as, the fingers are of unequal length; peers and commoners\n   are unequal in rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unequal', 'a.', 'Ill balanced or matched; disproportioned; hence, not\n   equitable; partial; unjust; unfair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unequal', 'a.', 'Not uniform; not equable; irregular; uneven; as, unequal\n   pulsations; an unequal poem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unequal', 'a.', 'Not adequate or sufficient; inferior; as, the man was\n   unequal to the emergency; the timber was unequal to the sudden strain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unequal', 'a.', 'Not having the two sides or the parts symmetrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unequalable', 'a.', 'Not capable of being equaled or paralleled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unequaled', 'a.', 'Not equaled; unmatched; unparalleled; unrivaled;\n   exceeding; surpassing; -- in a good or bad sense; as, unequaled\n   excellence; unequaled ingratitude or baseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unequally', 'adv.', 'In an unequal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unequalness', 'n.', 'The quality or state of being unequal; inequality;\n   unevenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unequitable', 'a.', 'Inequitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unequity', 'n.', 'Want of equity or uprightness; injustice; wickedness;\n   iniquity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unequivocal', 'a.', 'Not equivocal; not doubtful; not ambiguous; evident;\n   sincere; plain; as, unequivocal evidence; unequivocal words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unerring', 'a.', 'Committing no mistake; incapable or error or failure\n   certain; sure; unfailing; as, the unerring wisdom of God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unerringly', 'adv.', 'In an unerring manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unessential', 'a.', 'Not essential; not of prime importance; not\n   indispensable; unimportant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unessential', 'a.', 'Void of essence, or real being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unessential', 'n.', 'Something not constituting essence, or something which\n   is not of absolute necessity; as, forms are among the unessentials of\n   religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unessentially', 'adv.', 'In an unessential manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unestablish', 'v. t.', 'To disestablish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ubeth', 'adv.', 'Alt. of Unethes');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unethes', 'adv.', 'With difficulty; scarcely. See Uneath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uneven', 'a.', 'Not even; not level; not uniform; rough; as, an uneven road\n   or way; uneven ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uneven', 'a.', 'Not equal; not of equal length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uneven', 'a.', 'Not divisible by two without a remainder; odd; -- said of\n   numbers; as, 3, 7, and 11 are uneven numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unevitable', 'a.', 'Inevitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unexact', 'a.', 'Not exact; inexact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unexampled', 'a.', 'Having no example or similar case; being without\n   precedent; unprecedented; unparalleled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unexceptionable', 'a.', 'Not liable to any exception or objection;\n   unobjectionable; faultless; good; excellent; as, a man of most\n   unexceptionable character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unexceptive', 'a.', 'Not exceptive; not including, admitting, or being, an\n   exception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unexcusable', 'a.', 'Inexcusable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unexhaustible', 'a.', 'Inexhaustible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unexpectation', 'n.', 'Absence of expectation; want of foresight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unexpected', 'a.', 'Not expected; coming without warning; sudden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unexpedient', 'a.', 'Inexpedient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unexpensive', 'a.', 'Inexpensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unexperience', 'n.', 'Inexperience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unexperienced', 'a.', 'Not experienced; being without experience;\n   inexperienced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unexperienced', 'a.', 'Untried; -- applied to things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unexperient', 'a.', 'Inexperienced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unexpert', 'a.', 'Not expert; inexpert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unexpertly', 'adv.', 'In an unexpert manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unexpressible', 'a.', 'Inexpressible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unexpressive', 'a.', 'Not expressive; not having the power of utterance;\n   inexpressive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unexpressive', 'a.', 'Incapable of being expressed; inexpressible;\n   unutterable; ineffable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unextinguishable', 'a.', 'Inextinguishable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unextricable', 'a.', 'Not extricable; inextricable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unface', 'v. t.', 'To remove the face or cover from; to unmask; to expose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfailable', 'a.', 'Infallible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfailing', 'a.', 'Not failing; not liable to fail; inexhaustible; certain;\n   sure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfair', 'v. t.', 'To deprive of fairness or beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfair', 'a.', 'Not fair; not honest; not impartial; disingenuous; using or\n   involving trick or artifice; dishonest; unjust; unequal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfaith', 'n.', 'Absence or want of faith; faithlessness; distrust;\n   unbelief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfaithful', 'a.', 'Not faithful; not observant of promises, vows,\n   allegiance, or duty; violating trust or confidence; treacherous;\n   perfidious; as, an unfaithful subject; an unfaithful agent or servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfaithful', 'a.', 'Not possessing faith; infidel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfalcated', 'a.', 'Not falcated, or hooked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfalcated', 'a.', 'Having no deductions; not curtailed, or shortened;\n   undiminished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfallible', 'a.', 'Infallible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfasten', 'v. t.', 'To loose; to unfix; to unbind; to untie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfathered', 'a.', 'Having no father; fatherless; hence, born contrary to\n   nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfathered', 'a.', 'Having no acknowledged father; hence, illegitimate;\n   spurious; bastard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfavorable', 'a.', 'Not favorable; not propitious; adverse; contrary;\n   discouraging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfeather', 'v. t.', 'To deprive of feathers; to strip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfeatured', 'a.', 'Wanting regular features; deformed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfeaty', 'a.', 'Not feat; not dexterous; unskillful; clumsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfeeling', 'a.', 'Destitute of feeling; void of sensibility; insensible;\n   insensate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfeeling', 'a.', 'Without kind feelings; cruel; hard-hearted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfeigned', 'a.', 'Not feigned; not counterfeit; not hypocritical; real;\n   sincere; genuine; as, unfeigned piety; unfeigned love to man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfellow', 'v. t.', 'To prevent from being a fellow or companion; to\n   separate from ones fellows; to dissever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfellowed', 'a.', 'Being without a fellow; unmatched; unmated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfence', 'v. t.', 'To strip of a fence; to remove a fence from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfertile', 'a.', 'Not fertile; infertile; barren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfestlich', 'a.', 'Unfit for a feast; hence, jaded; worn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfetter', 'v. t.', 'To loose from fetters or from restraint; to unchain;\n   to unshackle; to liberate; as, to unfetter the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfeudalize', 'v. t.', 'To free from feudal customs or character; to make\n   not feudal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfile', 'v. t.', 'To remove from a file or record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfiled', 'a.', 'Not defiled; pure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfilial', 'a.', 'Unsuitable to a son or a daughter; undutiful; not\n   becoming a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfinished', 'a.', 'Not finished, not brought to an end; imperfect;\n   incomplete; left in the rough; wanting the last hand or touch; as, an\n   unfinished house; an unfinished picture; an unfinished iron casting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfirm', 'a.', 'Infirm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfirmness', 'n.', 'Infirmness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfit', 'v. t.', 'To make unsuitable or incompetent; to deprive of the\n   strength, skill, or proper qualities for anything; to disable; to\n   incapacitate; to disqualify; as, sickness unfits a man for labor; sin\n   unfits us for the society of holy beings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfit', 'a.', 'Not fit; unsuitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfix', 'v. t.', 'To loosen from a fastening; to detach from anything that\n   holds; to unsettle; as, to unfix a bayonet; to unfix the mind or\n   affections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfix', 'v. t.', 'To make fluid; to dissolve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfledged', 'a.', 'Not fledged; not feathered; hence, not fully developed;\n   immature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unflesh', 'v. t.', 'To deprive of flesh; to reduce a skeleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfleshly', 'a.', 'Not pertaining to the flesh; spiritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unflexible', 'a.', 'Inflexible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unflinching', 'a.', 'Not flinching or shrinking; unyielding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unflower', 'v. t.', 'To strip of flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfold', 'v. t.', 'To open the folds of; to expand; to spread out; as, to\n   unfold a tablecloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfold', 'v. t.', 'To open, as anything covered or close; to lay open to\n   view or contemplation; to bring out in all the details, or by\n   successive development; to display; to disclose; to reveal; to\n   elucidate; to explain; as, to unfold ones designs; to unfold the\n   principles of a science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfold', 'v. t.', 'To release from a fold or pen; as, to unfold sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfold', 'v. i.', 'To open; to expand; to become disclosed or developed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfolder', 'n.', 'One who, or that which, unfolds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfoldment', 'n.', 'The acct of unfolding, or the state of being unfolded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfool', 'v. t.', 'To restore from folly, or from being a fool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unforesee', 'v. t.', 'To fail to foresee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unforeseeable', 'a.', 'Incapable of being foreseen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unforeskinned', 'a.', 'Deprived of the foreskin; circumcised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unforgettable', 'a.', 'Not forgettable; enduring in memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unform', 'v. t.', 'To decompose, or resolve into parts; to destroy the form\n   of; to unmake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unformed', 'a.', 'Decomposed, or resolved into parts; having the form\n   destroyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unformed', 'a.', 'Not formed; not arranged into regular shape, order, or\n   relations; shapeless; amorphous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unformed', 'a.', 'Unorganized; without definite shape or structure; as, an\n   unformed, or unorganized, ferment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfortunate', 'a.', 'Not fortunate; unsuccessful; not prosperous; unlucky;\n   attended with misfortune; unhappy; as, an unfortunate adventure; an\n   unfortunate man; an unfortunate commander; unfortunate business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfortunate', 'n.', 'An unfortunate person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfounded', 'a.', 'Not founded; not built or established.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfounded', 'a.', 'Having no foundation; baseless; vain; idle; as,\n   unfounded expectations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unframe', 'v. t.', 'To take apart, or destroy the frame of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfrangible', 'a.', 'Infrangible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfrankable', 'a.', 'Not frankable; incapable of being sent free by public\n   conveyance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfraught', 'a.', 'Not fraught; not burdened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfraught', 'a.', 'Removed, as a burden; unloaded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfree', 'a.', 'Not free; held in bondage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfreeze', 'v. t.', 'To thaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfrequency', 'n.', 'Infrequency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfrequent', 'a.', 'Infrequent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfrequent', 'v. t.', 'To cease to frequent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfrequented', 'a.', 'Rarely visited; seldom or never resorted to by human\n   beings; as, an unfrequented place or forest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfret', 'v. t.', 'To smooth after being fretted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfriend', 'n.', 'One not a friend; an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfriended', 'a.', 'Wanting friends; not befriended; not countenanced or\n   supported.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfriendly', 'a.', 'Not friendly; not kind or benevolent; hostile; as, an\n   unfriendly neighbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfriendly', 'a.', 'Not favorable; not adapted to promote or support any\n   object; as, weather unfriendly to health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfriendship', 'n.', 'The state or quality of being unfriendly;\n   unfriendliness; enmity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfrock', 'v. t.', 'To deprive or divest or a frock; specifically, to\n   deprive of priestly character or privilege; as, to unfrock a priest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfruitful', 'a.', 'Not producing fruit or offspring; unproductive;\n   infertile; barren; sterile; as, an unfruitful tree or animal;\n   unfruitful soil; an unfruitful life or effort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfumed', 'a.', 'Not exposed to fumes; not fumigated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfurl', 'v. t. & i.', 'To loose from a furled state; to unfold; to expand;\n   to open or spread; as, to unfurl sails; to unfurl a flag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfurnish', 'v. t.', 'To strip of furniture; to divest; to strip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unfusible', 'a.', 'Infusible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungain', 'a.', 'Ungainly; clumsy; awkward; also, troublesome; inconvenient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungainliness', 'n.', 'The state or quality of being ungainly; awkwardness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungainly', 'a.', 'Not gainly; not expert or dexterous; clumsy; awkward;\n   uncouth; as, an ungainly strut in walking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungainly', 'a.', 'Unsuitable; unprofitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungainly', 'adv.', 'In an ungainly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungear', 'v. t.', 'To strip of gear; to unharness; to throw out of gear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungeld', 'n.', 'A person so far out of the protection of the law, that if\n   he were murdered, no geld, or fine, should be paid, or composition made\n   by him that killed him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungenerous', 'a.', 'Not generous; illiberal; ignoble; unkind; dishonorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungenerously', 'adv.', 'In an ungenerous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungenitured', 'a.', 'Destitute of genitals; impotent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungentle', 'a.', 'Not gentle; lacking good breeding or delicacy; harsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unget', 'v. t.', 'To cause to be unbegotten or unborn, or as if unbegotten\n   or unborn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungifted', 'a.', 'Being without gifts, especially native gifts or\n   endowments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungird', 'v. t.', 'To loose the girdle or band of; to unbind; to unload.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungive', 'v. t. & i.', 'To yield; to relax; to give way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungka', 'n.', 'The siamang; -- called also ungka ape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungka-puti', 'n.', 'The agile gibbon; -- called also ungka-pati, and\n   ungka-etam. See Gibbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unglaze', 'v. t.', 'To strip of glass; to remove the glazing, or glass,\n   from, as a window.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unglorify', 'v. t.', 'To deprive of glory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unglorious', 'a.', 'Inglorious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unglove', 'v. t.', 'To take off the glove or gloves of; as, to unglove the\n   hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unglue', 'v. t.', 'To separate, part, or open, as anything fastened with\n   glue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungod', 'v. t.', 'To deprive of divinity; to undeify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungod', 'v. t.', 'To cause to recognize no god; to deprive of a god; to\n   make atheistical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungodly', 'a.', 'Not godly; not having regard for God; disobedient to God;\n   wicked; impious; sinful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungodly', 'a.', 'Polluted by sin or wickedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungored', 'a.', 'Not stained with gore; not bloodied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungored', 'a.', 'Not gored or pierced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungot', 'a.', 'Alt. of Ungotten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungotten', 'a.', 'Not gotten; not acquired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungotten', 'a.', 'Not begotten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungovernable', 'a.', 'Not governable; not capable of being governed, ruled,\n   or restrained; licentious; wild; unbridled; as, ungovernable passions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungown', 'v. t.', 'To strip of a gown; to unfrock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungowned', 'a.', 'Stripped of a gown; unfrocked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungowned', 'a.', 'Not having, or not wearing, a gown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungraceful', 'a.', 'Not graceful; not marked with ease and dignity;\n   deficient in beauty and elegance; inelegant; awkward; as, ungraceful\n   manners; ungraceful speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungracious', 'a.', 'Not gracious; showing no grace or kindness; being\n   without good will; unfeeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungracious', 'a.', 'Having no grace; graceless; wicked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungracious', 'a.', 'Not well received; offensive; unpleasing; unacceptable;\n   not favored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungrate', 'a.', 'Displeasing; ungrateful; ingrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungrateful', 'a.', 'Not grateful; not thankful for favors; making no\n   returns, or making ill return for kindness, attention, etc.;\n   ingrateful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungrateful', 'a.', 'Unpleasing; unacceptable; disagreeable; as, harsh\n   sounds are ungrateful to the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungrave', 'v. t.', 'To raise or remove from the grave; to disinter; to\n   untomb; to exhume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungual', 'a.', 'Of or pertaining to a nail, claw, talon, or hoof, or\n   resembling one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungual', 'a.', 'Having a nail, claw, or hoof attached; -- said of certain\n   bones of the feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguard', 'v. t.', 'To deprive of a guard; to leave unprotected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungueal', 'a.', 'Ungual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguent', 'n.', 'A lubricant or salve for sores, burns, or the like; an\n   ointment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguentary', 'a.', 'Like an unguent, or partaking of its qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguentous', 'a.', 'Unguentary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguestlike', 'adv.', 'In a manner not becoming to a guest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguical', 'a.', 'Ungual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguicular', 'a.', 'Of or pertaining to a claw or a nail; ungual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguiculata', 'n. pl.', 'An extensive division of Mammalia including those\n   having claws or nails, as distinguished from the hoofed animals\n   (Ungulata).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguiculate', 'n.', 'One of the Unguiculata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguiculate', 'a.', 'Alt. of Unguiculated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguiculated', 'a.', 'Furnished with nails, claws, or hooks; clawed. See\n   the Note under Nail, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguiculated', 'n.', 'Furnished with a claw, or a narrow stalklike base, as\n   the petals of a carnation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguiferous', 'a.', 'Producing, having, or supporting nails or claws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguiform', 'a.', 'Having the form of a claw or claws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguinous', 'a.', 'Consisting of, or resembling, fat or oil; oily;\n   unctuous; oleaginous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungues', 'pl. ', 'of Unguis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguis', 'n.', 'The nail, claw, talon, or hoof of a finger, toe, or other\n   appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguis', 'n.', 'One of the terminal hooks on the foot of an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguis', 'n.', 'The slender base of a petal in some flowers; a claw; called\n   also ungula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungulae', 'pl. ', 'of Ungula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungula', 'n.', 'A hoof, claw, or talon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungula', 'n.', 'A section or part of a cylinder, cone, or other solid of\n   revolution, cut off by a plane oblique to the base; -- so called from\n   its resemblance to the hoof of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungula', 'n.', 'Same as Unguis, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungular', 'a.', 'Of or pertaining to a hoof, claw, or talon; ungual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungulata', 'n. pl.', 'An extensive group of mammals including all those\n   that have hoofs. It comprises the Artiodactyla and Perissodactyla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungulate', 'a.', 'Shaped like a hoof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungulate', 'a.', 'Furnished with hoofs. See the Note under Nail, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungulate', 'n.', 'Any hoofed quadruped; one of the Ungulata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguled', 'a.', 'Hoofed, or bearing hoofs; -- used only when these are of a\n   tincture different from the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unguligrade', 'a.', 'Having, or walking on, hoofs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ungulous', 'a.', 'Same as Ungulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhair', 'v. t.', 'To deprive of hair, or of hairs; as, to unhair hides for\n   leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhallow', 'v. t.', 'To profane; to desecrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhallowed', 'a.', 'Not consecrated; hence, profane; unholy; impious;\n   wicked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhand', 'v. t.', 'To loose from the hand; to let go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhandsome', 'a.', 'Not handsome; not beautiful; ungraceful; not comely or\n   pleasing; plain; homely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhandsome', 'a.', 'Wanting noble or amiable qualities; dishonorable;\n   illiberal; low; disingenuous; mean; indecorous; as, unhandsome conduct,\n   treatment, or imputations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhandsome', 'a.', 'Unhandy; clumsy; awkward; inconvenient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhandy', 'a.', 'Clumsy; awkward; as, an Unhandy man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhang', 'v. t.', 'To divest or strip of hangings; to remove the hangings,\n   as a room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhang', 'v. t.', 'To remove (something hanging or swinging) from that\n   which supports it; as, to unhang a gate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhap', 'n.', 'Ill luck; misfortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhappied', 'a.', 'Made unhappy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhappy', 'a.', 'Not happy or fortunate; unfortunate; unlucky; as, affairs\n   have taken an unhappy turn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhappy', 'a.', 'In a degree miserable or wretched; not happy; sad;\n   sorrowful; as, children render their parents unhappy by misconduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhappy', 'a.', 'Marked by infelicity; evil; calamitous; as, an unhappy\n   day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhappy', 'a.', 'Mischievous; wanton; wicked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unharbor', 'v. t.', 'To drive from harbor or shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unharbored', 'a.', 'Having no harbor or shelter; unprotected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unharbored', 'a.', 'Affording no harbor or shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unharmonious', 'a.', 'Inharmonious; unsymmetrical; also, unmusical;\n   discordant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unharness', 'v. t.', 'To strip of harness; to loose from harness or gear;\n   as, to unharness horses or oxen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unharness', 'v. t.', 'To disarm; to divest of armor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhasp', 'v. t.', 'To unloose the hasp of; to unclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhat', 'v. t. & i.', 'To take off the hat of; to remove ones hat,\n   especially as a mark of respect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhead', 'v. t.', 'To take out the head of; as, to unhead a cask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhead', 'v. t.', 'To decapitate; to behead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unheal', 'n.', 'Misfortune; calamity; sickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unheal', 'v. t.', 'To uncover. See Unhele.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhealth', 'n.', 'Unsoundness; disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unheard', 'a.', 'Not heard; not perceived by the ear; as, words unheard by\n   those present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unheard', 'a.', 'Not granted an audience or a hearing; not allowed to\n   speak; not having made a defense, or stated ones side of a question;\n   disregarded; unheeded; as, to condem/ a man unheard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unheard', 'a.', 'Not known to fame; not illustrious or celebrated; obscure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unheard-of', 'a.', 'New; unprecedented; unparalleled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unheart', 'v. t.', 'To cause to lose heart; to dishearten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unheedy', 'a.', 'Incautious; precipitate; heedless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unheired', 'a.', 'Destitute of an heir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhele', 'n.', 'Same as Unheal, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhele', 'v. t.', 'To uncover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhelm', 'v. t.', 'To deprive of the helm or helmet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhelmed', 'a.', 'Divested or deprived of the helm or helmet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhelmed', 'a.', 'Not wearing a helmet; without a helmet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhelmet', 'v. t.', 'To deprive of the helmet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhide', 'v. t.', 'To bring out from concealment; to discover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhinge', 'v. t.', 'To take from the hinges; as, to unhinge a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhinge', 'v. t.', 'To displace; to unfix by violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhinge', 'v. t.', 'To render unstable or wavering; to unsettle; as, to\n   unhinge ones mind or opinions; to unhinge the nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhingement', 'n.', 'The act unhinging, or the state of being unhinged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhitch', 'v. t.', 'To free from being hitched, or as if from being\n   hitched; to unfasten; to loose; as, to unhitch a horse, or a trace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhive', 'v. t. v. t.', 'To drive or remove from a hive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhive', 'v. t. v. t.', 'To deprive of habitation or shelter, as a crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhoard', 'v. t.', 'To take or steal from a hoard; to pilfer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhold', 'v. t.', 'To cease to hold; to unhand; to release.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unholy', 'a.', 'Not holy; unhallowed; not consecrated; hence, profane;\n   wicked; impious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhonest', 'a.', 'Dishonest; dishonorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhood', 'v. t.', 'To remove a hood or disguise from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhook', 'v. t.', 'To loose from a hook; to undo or open by loosening or\n   unfastening the hooks of; as, to unhook a fish; to unhook a dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhoop', 'v. t.', 'To strip or deprive of hoops; to take away the hoops of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhoped', 'a.', 'Not hoped or expected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhoped-for', 'a.', 'Unhoped; unexpected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhorse', 'v. t.', 'To throw from a horse; to cause to dismount; also, to\n   take a horse or horses from; as, to unhorse a rider; to unhorse a\n   carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhosed', 'a.', 'Without hose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhospitable', 'a.', 'Inhospitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhouse', 'v. t.', 'To drive from a house or habitation; to dislodge;\n   hence, to deprive of shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhoused', 'a.', 'Driven from a house; deprived of shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhoused', 'a.', 'Not provided with a house or shelter; houseless;\n   homeless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhouseled', 'a.', 'Not having received the sacrament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhuman', 'a.', 'Not human; inhuman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhumanize', 'v. t.', 'To render inhuman or barbarous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhusked', 'a.', 'Not husked; having the husk on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unhusked', 'a.', 'Having the husk removed; without husk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uni-', '', 'A prefix signifying one, once; as in uniaxial, unicellular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniat', 'n.', 'Alt. of Uniate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniate', 'n.', 'A member of the Greek Church, who nevertheless acknowledges\n   the supremacy of the Pope of Rome; one of the United Greeks. Also used\n   adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniaxal', 'a.', 'Uniaxial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniaxial', 'a.', 'Having but one optic axis, or line of no double\n   refraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniaxial', 'a.', 'Having only one axis; developing along a single line or\n   plane; -- opposed to multiaxial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniaxially', 'adv.', 'In a uniaxial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unibranchiate', 'a.', 'Having but one gill, as certain molluscs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unicameral', 'a.', 'Having, or consisting of, a single chamber; -- said of\n   a legislative assembly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unicapsular', '', 'Having but one capsule to each flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unicarinated', 'a.', 'Having one ridge or keel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unicelled', 'a.', 'Unicellular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unicellular', 'a.', 'Having, or consisting of, but a single cell; as, a\n   unicellular organism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unicentral', 'a.', 'Having a single center of growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unicity', 'n.', 'The condition of being united; quality of the unique;\n   unification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniclinal', 'a.', 'See Nonoclinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unicolorous', 'a.', 'Having the surface of a uniform color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unicorn', 'n.', 'A fabulous animal with one horn; the monoceros; -- often\n   represented in heraldry as a supporter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unicorn', 'n.', 'A two-horned animal of some unknown kind, so called in the\n   Authorized Version of the Scriptures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unicorn', 'n.', 'Any large beetle having a hornlike prominence on the head\n   or prothorax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unicorn', 'n.', 'The larva of a unicorn moth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unicorn', 'n.', 'The kamichi; -- called also unicorn bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unicorn', 'n.', 'A howitzer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unicornous', 'a.', 'Having but a single horn; -- said of certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unicostate', 'a.', 'Having a single rib or strong nerve running upward from\n   the base; -- said of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unicursal', 'a.', 'That can be passed over in a single course; -- said of a\n   curve when the coordinates of the point on the curve can be expressed\n   as rational algebraic functions of a single parameter /.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unideaed', 'a.', 'Having no ideas; senseless; frivolous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unideal', 'a.', 'Not ideal; real; unimaginative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unideal', 'a.', 'Unideaed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unidimensional', 'a.', 'Having but one dimension. See Dimension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unifacial', 'a.', 'Having but one front surface; as, some foliaceous corals\n   are unifacial, the polyp mouths being confined to one surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unific', 'a.', 'Making one or unity; unifying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unification', 'n.', 'The act of unifying, or the state of being unified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unifier', 'n.', 'One who, or that which, unifies; as, a natural law is a\n   unifier of phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unifilar', 'a.', 'Having only one thread; involving the use of only one\n   thread, wire, fiber, or the like; as, unifilar suspension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniflagellate', 'a.', 'Having but one flagellum; as, uniflagellate\n   organisms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniflorous', 'a.', 'Bearing one flower only; as, a uniflorous peduncle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unifolliate', 'a.', 'Having only one leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unifollilate', 'a.', 'Having only one leaflet, as the leaves of the orange\n   tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniform', 'a.', 'Having always the same form, manner, or degree; not\n   varying or variable; unchanging; consistent; equable; homogenous; as,\n   the dress of the Asiatics has been uniform from early ages; the\n   temperature is uniform; a stratum of uniform clay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniform', 'a.', 'Of the same form with others; agreeing with each other;\n   conforming to one rule or mode; consonant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniform', 'a.', 'A dress of a particular style or fashion worn by persons\n   in the same service or order by means of which they have a distinctive\n   appearance; as, the uniform of the artillery, of the police, of the\n   Freemasons, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniform', 'v. t.', 'To clothe with a uniform; as, to uniform a company of\n   soldiers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniform', 'v. t.', 'To make conformable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniformal', 'a.', 'Uniform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniformism', 'n.', 'The doctrine of uniformity in the geological history of\n   the earth; -- in part equivalent to uniformitarianism, but also used,\n   more broadly, as opposed to catastrophism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniformitarian', 'a.', 'Of, pertaining to, or designating, the view or\n   doctrine that existing causes, acting in the same manner and with\n   essentially the same intensity as at the present time, are sufficient\n   to account for all geological changes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniformitarian', 'n.', 'One who accepts uniformitarianism, or the\n   uniformitarian doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniformitarianism', 'n.', 'The uniformitarian doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniformity', 'n.', 'The quality or state of being uniform; freedom from\n   variation or difference; resemblance to itself at all times; sameness\n   of action, effect, etc., under like conditions; even tenor; as, the\n   uniformity of design in a poem; the uniformity of nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniformity', 'n.', 'Consistency; sameness; as, the uniformity of a mans\n   opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniformity', 'n.', 'Similitude between the parts of a whole; as, the\n   uniformity of sides in a regular figure; beauty is said to consist in\n   uniformity with variety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniformity', 'n.', 'Continued or unvaried sameness or likeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniformity', 'n.', 'Conformity to a pattern or rule; resemblance,\n   consonance, or agreement; as, the uniformity of different churches in\n   ceremonies or rites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniformly', 'adv.', 'In a uniform manner; without variation or diversity;\n   by a regular, constant, or common ratio of change; with even tenor; as,\n   a temper uniformly mild.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unifromness', 'n.', 'The quality or state of being uniform; uniformity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unified', 'imp. & p. p.', 'of Unify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unifying', 'p. pr. & vb. n.', 'of Unify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unify', 'v. t.', 'To cause to be one; to make into a unit; to unite; to\n   view as one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unigeniture', 'n.', 'The state of being the only begotten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unigenous', 'a.', 'Being of one kind; being of the same genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unijugate', 'a.', 'Having but one pair of leaflets; -- said of a pinnate\n   leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unilabiate', 'a.', 'Having one lip only; as, a unilabiate corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unilateral', 'a.', 'Being on one side only; affecting but one side;\n   one-sided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unilateral', 'a.', 'Pertaining to one side; one-sided; as, a unilateral\n   raceme, in which the flowers grow only on one side of a common axis, or\n   are all turned to one side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniliteral', 'a.', 'Consisting of one letter only; as, a uniliteral word or\n   sign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unilobar', 'a.', 'Consisting of a single lobe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unilocular', 'a.', 'Having one cell or cavity only; as, a unilocular\n   capsule or shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unimitable', 'a.', 'Inimitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unimpairable', 'a.', 'That can not be impaired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unimpeachable', 'a.', 'Not impeachable; not to be called in question;\n   exempt from liability to accusation; free from stain, guilt, or fault;\n   irreproachable; blameless; as, an unimpeachable reputation;\n   unimpeachable testimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unimplicate', 'a.', 'Not implicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unimportance', 'n.', 'Want of importance; triviality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unimproved', 'a.', 'Not improved; not made better or wiser; not advanced in\n   knowledge, manners, or excellence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unimproved', 'a.', 'Not used; not employed; especially, not used or\n   employed for a valuable purpose; as, unimproved opportunities;\n   unimproved blessings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unimproved', 'a.', 'Not tilled, cultivated, or built upon; yielding no\n   revenue; as, unimproved land or soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unimuscular', 'a.', 'Having only one adductor muscle, and one muscular\n   impression on each valve, as the oyster; monomyarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unincumbered', 'a.', 'Not incumbered; not burdened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unincumbered', 'a.', 'Free from any temporary estate or interest, or from\n   mortgage, or other charge or debt; as, an estate unincumbered with\n   dower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uninfringible', 'a.', 'That may not be infringed; as, an uninfringible\n   monopoly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unintelligence', 'n.', 'Absence or lack of intelligence; unwisdom;\n   ignorance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uninteressed', 'a.', 'Uninterested; unaffected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uninterested', 'a.', 'Not interested; not having any interest or property\n   in; having nothing at stake; as, to be uninterested in any business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uninterested', 'a.', 'Not having the mind or the passions engaged; as,\n   uninterested in a discourse or narration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unintermission', 'n.', 'Want or failure of intermission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uninucleated', 'a.', 'Possessed of but a single nucleus; as, a uninucleated\n   cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unio', 'n.', 'Any one of numerous species of fresh-water mussels belonging\n   to Unio and many allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniocular', 'a.', 'Of, pertaining to, or seated in, one eye; monocular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Union', 'n.', 'The act of uniting or joining two or more things into one,\n   or the state of being united or joined; junction; coalition;\n   combination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Union', 'n.', 'Agreement and conjunction of mind, spirit, will, affections,\n   or the like; harmony; concord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Union', 'n.', 'That which is united, or made one; something formed by a\n   combination or coalition of parts or members; a confederation; a\n   consolidated body; a league; as, the weavers have formed a union;\n   trades unions have become very numerous; the United States of America\n   are often called the Union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Union', 'n.', 'A textile fabric composed of two or more materials, as\n   cotton, silk, wool, etc., woven together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Union', 'n.', 'A large, fine pearl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Union', 'n.', 'A device emblematic of union, used on a national flag or\n   ensign, sometimes, as in the military standard of Great Britain,\n   covering the whole field; sometimes, as in the flag of the United\n   States, and the English naval and marine flag, occupying the upper\n   inner corner, the rest of the flag being called the fly. Also, a flag\n   having such a device; especially, the flag of Great Britain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Union', 'n.', 'A joint or other connection uniting parts of machinery, or\n   the like, as the elastic pipe of a tender connecting it with the feed\n   pipe of a locomotive engine; especially, a pipe fitting for connecting\n   pipes, or pipes and fittings, in such a way as to facilitate\n   disconnection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Union', 'n.', 'A cask suspended on trunnions, in which fermentation is\n   carried on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unionism', 'n.', 'The sentiment of attachment to a federal union,\n   especially to the federal union of the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unionism', 'n.', 'The principles, or the system, of combination among\n   workmen engaged in the same occupation or trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unionist', 'n.', 'One who advocates or promotes union; especially a loyal\n   supporter of a federal union, as that of the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unionist', 'n.', 'A member or supporter of a trades union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unionistic', 'a.', 'Of or pertaining to union or unionists; tending to\n   promote or preserve union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniovulate', 'a.', 'Containing but one ovule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unipara', 'n.', 'A woman who has borne one child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniparous', 'a.', 'Producing but one egg or young at a time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniparous', 'a.', 'Producing but one axis of inflorescence; -- said of the\n   scorpioid cyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniped', 'a.', 'Having only one foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unipersonal', 'a.', 'Existing as one, and only one, person; as, a\n   unipersonal God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unipersonal', 'a.', 'Used in only one person, especially only in the third\n   person, as some verbs; impersonal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unipersonalist', 'n.', 'One who believes that the Deity is unipersonal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniphonous', 'a.', 'Having but one sound, as the drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniplicate', 'a.', 'Having, or consisting of, but one fold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unipolar', 'a.', 'Having, or acting by means of, one pole only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unipolar', 'a.', 'Having but one pole or process; -- applied to those\n   ganglionic nerve cells which have but one radiating process; -- opposed\n   to multipolar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unique', 'a.', 'Being without a like or equal; unmatched; unequaled;\n   unparalleled; single in kind or excellence; sole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unique', 'n.', 'A thing without a like; something unequaled or\n   unparalleled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniquity', 'n.', 'The quality or state of being unique; uniqueness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniradiated', 'a.', 'Having but one ray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniramous', 'a.', 'Having but one branch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniseptate', 'a.', 'Having but one septum, or partition; -- said of\n   two-celled fruits, such as the silicles of cruciferous plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniserial', 'a.', 'Having only one row or series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniseriate', 'a.', 'Having one line or series; uniserial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unisexual', 'a.', 'Having one sex only, as plants which have the male and\n   female flowers on separate individuals, or animals in which the sexes\n   are in separate individuals; di/cious; -- distinguished from bisexual,\n   or hermaphrodite. See Di/cious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unisilicate', 'n.', 'A salt of orthosilicic acid, H4SiO4; -- so called\n   because the ratio of the oxygen atoms united to the basic metals and\n   silicon respectively is 1:1; for example, Mg2SiO4 or 2MgO.SiO2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unison', 'n.', 'Harmony; agreement; concord; union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unison', 'n.', 'Identity in pitch; coincidence of sounds proceeding from an\n   equality in the number of vibrations made in a given time by two or\n   more sonorous bodies. Parts played or sung in octaves are also said to\n   be in unison, or in octaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unison', 'n.', 'A single, unvaried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unison', 'n.', 'Sounding alone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unison', 'n.', 'Sounded alike in pitch; unisonant; unisonous; as, unison\n   passages, in which two or more parts unite in coincident sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unisonal', 'a.', 'Being in unison; unisonant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unisonance', 'n.', 'Accordance of sounds; unison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unisonant', 'a.', 'Being in unison; having the same degree of gravity or\n   acuteness; sounded alike in pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unisonous', 'a.', 'Being in unison; unisonant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unit', 'n.', 'A single thing or person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unit', 'n.', 'The least whole number; one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unit', 'n.', 'A gold coin of the reign of James I., of the value of twenty\n   shillings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unit', 'n.', 'Any determinate amount or quantity (as of length, time, heat,\n   value) adopted as a standard of measurement for other amounts or\n   quantities of the same kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unit', 'n.', 'A single thing, as a magnitude or number, regarded as an\n   undivided whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unitable', 'a.', 'Capable of union by growth or otherwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unitarian', 'n.', 'One who denies the doctrine of the Trinity, believing\n   that God exists only in one person; a unipersonalist; also, one of a\n   denomination of Christians holding this belief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unitarian', 'n.', 'One who rejects the principle of dualism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unitarian', 'n.', 'A monotheist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unitarian', 'a.', 'Of or pertaining to Unitarians, or their doctrines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unitarianism', 'n.', 'The doctrines of Unitarians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unitarianized', 'imp. & p. p.', 'of Unitarianize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unitarianizing', 'p. pr. & vb. n.', 'of Unitarianize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unitarianize', 'v. t. & i.', 'To change or turn to Unitarian views.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unitary', 'a.', 'Of or pertaining to a unit or units; relating to unity;\n   as, the unitary method in arithmetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unitary', 'a.', 'Of the nature of a unit; not divided; united.');
  INSERT INTO words (word, word_type, description) VALUES  (    'United', 'imp. & p. p.', 'of Unite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniting', 'p. pr. & vb. n.', 'of Unite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unite', 'v. t.', 'To put together so as to make one; to join, as two or\n   more constituents, to form a whole; to combine; to connect; to join; to\n   cause to adhere; as, to unite bricks by mortar; to unite iron bars by\n   welding; to unite two armies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unite', 'v. t.', 'Hence, to join by a legal or moral bond, as families by\n   marriage, nations by treaty, men by opinions; to join in interest,\n   affection, fellowship, or the like; to cause to agree; to harmonize; to\n   associate; to attach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unite', 'v. i.', 'To become one; to be cemented or consolidated; to\n   combine, as by adhesion or mixture; to coalesce; to grow together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unite', 'v. i.', 'To join in an act; to concur; to act in concert; as, all\n   parties united in signing the petition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unite', 'v. t.', 'United; joint; as, unite consent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'United', 'a.', 'Combined; joined; made one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unitedly', 'adv.', 'In an united manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniter', 'n.', 'One who, or that which, unites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uniterable', 'a.', 'Not iterable; incapable of being repeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unition', 'v. t.', 'The act of uniting, or the state of being united;\n   junction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unitive', 'a.', 'Having the power of uniting; causing, or tending to\n   produce, union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unitively', 'adv.', 'In a unitive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unitized', 'imp. & p. p.', 'of Unitize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unitizing', 'p. pr. & vb. n.', 'of Unitize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unitize', 'v. t.', 'To reduce to a unit, or one whole; to form into a unit;\n   to unify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unitude', 'n.', 'Unity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unities', 'pl. ', 'of Unity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unity', 'n.', 'The state of being one; oneness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unity', 'n.', 'Concord; harmony; conjunction; agreement; uniformity; as, a\n   unity of proofs; unity of doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unity', 'n.', 'Any definite quantity, or aggregate of quantities or\n   magnitudes taken as one, or for which 1 is made to stand in\n   calculation; thus, in a table of natural sines, the radius of the\n   circle is regarded as unity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unity', 'n.', 'In dramatic composition, one of the principles by which a\n   uniform tenor of story and propriety of representation are preserved;\n   conformity in a composition to these; in oratory, discourse, etc., the\n   due subordination and reference of every part to the development of the\n   leading idea or the eastablishment of the main proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unity', 'n.', 'Such a combination of parts as to constitute a whole, or a\n   kind of symmetry of style and character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unity', 'n.', 'The peculiar characteristics of an estate held by several in\n   joint tenancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Univalence', 'n.', 'The quality or state of being univalent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Univalent', 'a.', 'Having a valence of one; capable of combining with, or\n   of being substituted for, one atom of hydrogen; monovalent; -- said of\n   certain atoms and radicals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Univalve', 'n.', 'A shell consisting of one valve only; a mollusk whose\n   shell is composed of a single piece, as the snails and conchs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Univalve', 'a.', 'Alt. of Univalved');
  INSERT INTO words (word, word_type, description) VALUES  (    'Univalved', 'a.', 'Having one valve; as, a univalve shell or pericarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Univalvia', 'n. pl.', 'Same as Gastropoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Univalvular', 'a.', 'Same as Univalve, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universal', 'a.', 'Of or pertaining to the universe; extending to,\n   including, or affecting, the whole number, quantity, or space;\n   unlimited; general; all-reaching; all-pervading; as, universal ruin;\n   universal good; universal benevolence or benefice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universal', 'a.', 'Constituting or considered as a whole; total; entire;\n   whole; as, the universal world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universal', 'a.', 'Adapted or adaptable to all or to various uses, shapes,\n   sizes, etc.; as, a universal milling machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universal', 'a.', 'Forming the whole of a genus; relatively unlimited in\n   extension; affirmed or denied of the whole of a subject; as, a\n   universal proposition; -- opposed to particular; e. g. (universal\n   affirmative) All men are animals; (universal negative) No men are\n   omniscient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universal', 'n.', 'The whole; the general system of the universe; the\n   universe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universal', 'n.', 'A general abstract conception, so called from being\n   universally applicable to, or predicable of, each individual or species\n   contained under it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universal', 'n.', 'A universal proposition. See Universal, a., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universalian', 'a.', 'Of or pertaining to Universalism; Universalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universalism', 'n.', 'The doctrine or belief that all men will be saved, or\n   made happy, in the future state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universalist', 'n.', 'One who believes in Universalism; one of a\n   denomination of Christians holding this faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universalist', 'n.', 'One who affects to understand all the particulars in\n   statements or propositions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universalist', 'a.', 'Of or pertaining to Unversalists of their doctrines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universalistic', 'a.', 'Of or pertaining to the whole; universal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universalties', 'pl. ', 'of Universality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universality', 'n.', 'The quality or state of being universal; unlimited\n   extension or application; generality; -- distinguished from\n   particularity; as, the unversality of a proposition; the unversality of\n   sin; the unversality of the Deluge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universalized', 'imp. & p. p.', 'of Universalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universalizing', 'p. pr. & vb. n.', 'of Universalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universalize', 'v. t.', 'To make universal; to generalize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universally', 'adv.', 'In a universal manner; without exception; as, Gods\n   laws are universally binding on his creatures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universalness', 'n.', 'The quality or state of being universal;\n   universality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universe', 'n.', 'All created things viewed as constituting one system or\n   whole; the whole body of things, or of phenomena; the / / of the\n   Greeks, the mundus of the Latins; the world; creation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universities', 'pl. ', 'of University');
  INSERT INTO words (word, word_type, description) VALUES  (    'University', 'n.', 'The universe; the whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'University', 'n.', 'An association, society, guild, or corporation, esp.\n   one capable of having and acquiring property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'University', 'n.', 'An institution organized and incorporated for the\n   purpose of imparting instruction, examining students, and otherwise\n   promoting education in the higher branches of literature, science, art,\n   etc., empowered to confer degrees in the several arts and faculties, as\n   in theology, law, medicine, music, etc. A university may exist without\n   having any college connected with it, or it may consist of but one\n   college, or it may comprise an assemblage of colleges established in\n   any place, with professors for instructing students in the sciences and\n   other branches of learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universological', 'a.', 'Of or pertaining to universology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universologist', 'n.', 'One who is versed in universology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Universology', 'n.', 'The science of the universe, and the relations which\n   it involves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Univocacy', 'n.', 'The quality or state of being univocal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Univocal', 'a.', 'Having one meaning only; -- contrasted with equivocal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Univocal', 'a.', 'Having unison of sound, as the octave in music. See\n   Unison, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Univocal', 'n.', 'Having always the same drift or tenor; uniform; certain;\n   regular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Univocal', 'n.', 'Unequivocal; indubitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Univocal', 'n.', 'A generic term, or a term applicable in the same sense to\n   all the species it embraces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Univocal', 'n.', 'A word having but one meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Univocally', 'adv.', 'In a univocal manner; in one term; in one sense; not\n   equivocally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Univocation', 'n.', 'Agreement of name and meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unjoin', 'v. t.', 'To disjoin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unjoint', 'v. t.', 'To disjoint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unjointed', 'a.', 'Disjointed; unconnected; hence, incoherent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unjointed', 'a.', 'Having no joint or articulation; as, an unjointed stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unjust', 'a.', 'Acting contrary to the standard of right; not animated or\n   controlled by justice; false; dishonest; as, an unjust man or judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unjust', 'a.', 'Contrary to justice and right; prompted by a spirit of\n   injustice; wrongful; as, an unjust sentence; an unjust demand; an\n   unjust accusation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unjustice', 'n.', 'Want of justice; injustice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unkard', 'a.', 'See Unked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unke', 'n.', 'A European aquatic toad (Bombinator igneus). Its back is\n   dark; its belly is marked with crimson. Called also feuerkrote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unked', 'a.', 'Odd; strange; ugly; old; uncouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unked', 'a.', 'Lonely; dreary; unkard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unkemmed', 'a.', 'Unkempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unkempt', 'a.', 'Not combed; disheveled; as, an urchin with unkempt hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unkempt', 'a.', 'Fig.; Not smoothed; unpolished; rough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unkennel', 'v. t.', 'To drive from a kennel or hole; as, to unkennel a fox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unkennel', 'v. t.', 'Fig.: To discover; to disclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unkent', 'a.', 'Unknown; strange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unketh', 'a.', 'Uncouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unkind', 'a.', 'Having no race or kindred; childless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unkind', 'a.', 'Not kind; contrary to nature, or the law of kind or\n   kindred; unnatural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unkind', 'a.', 'Wanting in kindness, sympathy, benevolence, gratitude, or\n   the like; cruel; harsh; unjust; ungrateful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unkindliness', 'n.', 'Unkindness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unkindly', 'a.', 'Not kindly; unkind; ungracious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unkindly', 'a.', 'Unnatural; contrary to nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unkindly', 'a.', 'Unfavorable; annoying; malignant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unkindred', 'a.', 'Not kindred; not of the same kin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unking', 'v. t.', 'To cause to cease to be a king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unkingship', 'n.', 'The quality or condition of being unkinged; abolition\n   of monarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unkiss', 'v. t.', 'To cancel or annul what was done or sealed by a kiss; to\n   cancel by a kiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unkle', 'n.', 'See Uncle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unknight', 'v. t.', 'To deprive of knighthood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unknit', 'v. t.', 'To undo or unravel what is knitted together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unknot', 'v. t.', 'To free from knots; to untie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unknow', 'v. t.', 'To cease to know; to lose the knowledge of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unknow', 'v. t.', 'To fail of knowing; to be ignorant of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unknow', 'a.', 'Unknown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unknowledged', 'a.', 'Not acknowledged or recognized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unknown', 'a.', 'Not known; not apprehended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlabored', 'a.', 'Not produced by labor or toil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlabored', 'a.', 'Not cultivated; untitled; as, an unlabored field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlabored', 'a.', 'Not laboriously produced, or not evincing labor; as, an\n   unlabored style or work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlace', 'v. t.', 'To loose by undoing a lacing; as, to unlace a shoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlace', 'v. t.', 'To loose the dress of; to undress; hence, to expose; to\n   disgrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlace', 'v. t.', 'To loose, and take off, as a bonnet from a sail, or to\n   cast off, as any lacing in any part of the rigging of a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlade', 'v. t.', 'To take the load from; to take out the cargo of; as, to\n   unlade a ship or a wagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlade', 'v. t.', 'To unload; to remove, or to have removed, as a load or a\n   burden; to discharge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlaid', 'a.', 'Not laid or placed; not fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlaid', 'a.', 'Not allayed; not pacified; not laid finally to rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlaid', 'a.', 'Not laid out, as a corpse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unland', 'v. t.', 'To deprive of lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlap', 'v. t.', 'To unfold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlash', 'v. t.', 'To loose, as that which is lashed or tied down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlatched', 'imp. & p. p.', 'of Unlatch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlatching', 'p. pr. & vb. n.', 'of Unlatch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlatch', 'v. i.', 'To open or loose by lifting the latch; as, to unlatch a\n   door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlaugh', 'v. t.', 'To recall, as former laughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlaw', 'v. t.', 'To deprive of the authority or character of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlaw', 'v. t.', 'To put beyond protection of law; to outlaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlaw', 'v. t.', 'To impose a fine upon; to fine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlaw', 'n.', 'Any transgression or offense against the law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlaw', 'n.', 'A fine imposed as a penalty for violation of the law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlawed', 'a.', 'Not having the claws and balls of the forefeet cut off; --\n   said of dogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlawful', 'a.', 'Not lawful; contrary to law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlawlike', 'a.', 'Not according to law; being or done in violation of law;\n   unlawful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlay', 'v. t.', 'To untwist; as, to unlay a rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlearn', 'v. t.', 'To forget, as what has been learned; to lose from\n   memory; also, to learn the contrary of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlearn', 'v. t.', 'To fail to learn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlearned', 'a.', 'Not learned; untaught; uneducated; ignorant; illiterate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlearned', 'a.', 'Not gained by study; not known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlearned', 'a.', 'Not exhibiting learning; as, unlearned verses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unleash', 'v. t.', 'To free from a leash, or as from a leash; to let go; to\n   release; as, to unleash dogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unleavened', 'a.', 'Not leavened; containing no leaven; as, unleavened\n   bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unless', 'conj.', 'Upon any less condition than (the fact or thing stated\n   in the sentence or clause which follows); if not; supposing that not;\n   if it be not; were it not that; except; as, we shall fail unless we are\n   industrious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlicked', 'a.', 'Not licked; hence, not properly formed; ungainly. Cf. To\n   lick into shape, under Lick, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlike', 'a.', 'Not like; dissimilar; diverse; having no resemblance; as,\n   the cases are unlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlike', 'a.', 'Not likely; improbable; unlikely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlikelihood', 'n.', 'Absence of likelihood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlikeliness', 'n.', 'The quality or state of being unlikely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlikely', 'a.', 'Not likely; improbable; not to be reasonably expected;\n   as, an unlikely event; the thing you mention is very unlikely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlikely', 'a.', 'Not holding out a prospect of success; likely to fail;\n   unpromising; as, unlikely means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlikely', 'a.', 'Not such as to inspire liking; unattractive;\n   disagreeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlikely', 'adv.', 'In an unlikely manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unliken', 'v. t.', 'To make unlike; to dissimilate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlikeness', 'n.', 'The quality or state of being unlike; want of\n   resemblance; dissimilarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlimber', 'v. t.', 'To detach the limber from; as, to unlimber a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlimitable', 'a.', 'Illimitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlimited', 'a.', 'Not limited; having no bounds; boundless; as, an\n   unlimited expanse of ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlimited', 'a.', 'Undefined; indefinite; not bounded by proper exceptions;\n   as, unlimited terms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlimited', 'a.', 'Unconfined; not restrained; unrestricted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unline', 'v. t.', 'To take the lining out of; hence, to empty; as, to\n   unline ones purse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlink', 'v. t.', 'To separate or undo, as links; to uncoil; to unfasten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unliquidated', 'a.', 'Not liquidated; not exactly ascertained; not adjusted\n   or settled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unliquored', 'a.', 'Not moistened or wet with liquor; dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unliquored', 'a.', 'Not in liquor; not intoxicated; sober.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlive', 'v. t.', 'To //ve in a contrary manner, as a life; to live in a\n   manner contrary to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlived', 'a.', 'Bereft or deprived of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unload', 'v. t.', 'To take the load from; to discharge of a load or cargo;\n   to disburden; as, to unload a ship; to unload a beast.');
