  INSERT INTO words (word, word_type, description) VALUES  (    'Unload', 'v. t.', 'To discharge or remove, as a load or a burden; as, to\n   unload the cargo of a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unload', 'v. t.', 'To draw the charge from; as, to unload a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unload', 'v. t.', 'To sell in large quantities, as stock; to get rid of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unload', 'v. i.', 'To perform the act of unloading anything; as, let unload\n   now.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unloader', 'n.', 'One who, or that which, unloads; a device for unloading,\n   as hay from a wagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlocated', 'a.', 'Not located or placed; not fixed in a place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlocated', 'a.', 'Not surveyed, or designated by marks, limits, or\n   boundaries, as appropriated to some individual, company, or\n   corporation; as, unlocated lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlock', 'v. t.', 'To unfasten, as what is locked; as, to unlock a door or\n   a chest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlock', 'v. t.', 'To open, in general; to lay open; to undo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlodge', 'v. t.', 'To dislodge; to deprive of lodgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlook', 'v. t.', 'To recall or retract, as a look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlooked', 'a.', 'Not observed or foreseen; unexpected; -- generally with\n   for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlooked-for', 'a.', 'Not looked for; unexpected; as, an unlooked-for\n   event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unloose', 'v. t.', 'To make loose; to loosen; to set free.');
