  INSERT INTO words (word, word_type, description) VALUES  (    'Unlorded', 'a.', 'Not raised to the rank of a lord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlove', 'v. t.', 'To cease to love; to hate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlovely', 'a.', 'Not lovely; not amiable; possessing qualities that excite\n   dislike; disagreeable; displeasing; unpleasant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unluckily', 'adv.', 'In an unlucky manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unluckiness', 'n.', 'Quality or state of being unlucky.');
