  INSERT INTO words (word, word_type, description) VALUES  (    'Unlucky', 'a.', 'Bringing bad luck; ill-omened; inauspicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlucky', 'a.', 'Mischievous; as, an unlucky wag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlust', 'n.', 'Listlessness; disinclination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unlute', 'v. t.', 'To separate, as things cemented or luted; to take the\n   lute or the clay from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unmade', 'a.', 'Not yet made or formed; as, an unmade grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unmade', 'a.', 'Deprived of form, character, etc.; disunited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unmagistrate', 'v. t.', 'To divest of the office or authority of a\n   magistrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unmaiden', 'v. t.', 'To ravish; to deflower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unmake', 'v. t.', 'To destroy the form and qualities of; to deprive of\n   being; to uncreate.');
