  INSERT INTO words (word, word_type, description) VALUES  (    'Unman', 'v. t.', 'To emasculate; to deprive of virility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unman', 'v. t.', 'To deprive of the courage and fortitude of a man; to\n   break or subdue the manly spirit in; to cause to despond; to\n   dishearten; to make womanish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unman', 'v. t.', 'To deprive of men; as, to unman a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unmanacle', 'v. t.', 'To free from manacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unmanhood', 'n.', 'Absence or lack of manhood.');
