  INSERT INTO words (word, word_type, description) VALUES  (    'Unmanned', 'a.', 'Not tamed; not made familiar with, or subject to, man; --\n   also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unmanned', 'a.', 'Not furnished with men; as, an unmanned ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unmannerly', 'a.', 'Not mannerly; ill-bred; rude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unmannerly', 'adv.', 'Uncivilly; rudely.');
