  INSERT INTO words (word, word_type, description) VALUES  (    'Unyoked', 'a.', 'Freed or loosed from a yoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unyoked', 'a.', 'Licentious; unrestrained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unyolden', 'a.', 'Not yielded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Unzoned', 'a.', 'Not zoned; not bound with a girdle; as, an unzoned bosom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Up', 'adv.', 'Aloft; on high; in a direction contrary to that of gravity;\n   toward or in a higher place or position; above; -- the opposite of\n   down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Up', 'adv.', 'From a lower to a higher position, literally or figuratively;\n   as, from a recumbent or sitting position; from the mouth, toward the\n   source, of a river; from a dependent or inferior condition; from\n   concealment; from younger age; from a quiet state, or the like; -- used\n   with verbs of motion expressed or implied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Up', 'adv.', 'In a higher place or position, literally or figuratively; in\n   the state of having arisen; in an upright, or nearly upright, position;\n   standing; mounted on a horse; in a condition of elevation, prominence,\n   advance, proficiency, excitement, insurrection, or the like; -- used\n   with verbs of rest, situation, condition, and the like; as, to be up on\n   a hill; the lid of the box was up; prices are up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Up', 'adv.', 'To or in a position of equal advance or equality; not short\n   of, back of, less advanced than, away from, or the like; -- usually\n   followed by to or with; as, to be up to the chin in water; to come up\n   with ones companions; to come up with the enemy; to live up to\n   engagements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Up', 'adv.', 'To or in a state of completion; completely; wholly; quite;\n   as, in the phrases to eat up; to drink up; to burn up; to sum up; etc.;\n   to shut up the eyes or the mouth; to sew up a rent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Up', 'adv.', 'Aside, so as not to be in use; as, to lay up riches; put up\n   your weapons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Up', 'prep.', 'From a lower to a higher place on, upon, or along; at a\n   higher situation upon; at the top of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Up', 'prep.', 'From the coast towards the interior of, as a country; from\n   the mouth towards the source of, as a stream; as, to journey up the\n   country; to sail up the Hudson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Up', 'prep.', 'Upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Up', 'n.', 'The state of being up or above; a state of elevation,\n   prosperity, or the like; -- rarely occurring except in the phrase ups\n   and downs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Up', 'a.', 'Inclining up; tending or going up; upward; as, an up look; an\n   up grade; the up train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upas', 'n.', 'A tree (Antiaris toxicaria) of the Breadfruit family, common\n   in the forests of Java and the neighboring islands. Its secretions are\n   poisonous, and it has been fabulously reported that the atmosphere\n   about it is deleterious. Called also bohun upas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upas', 'n.', 'A virulent poison used in Java and the adjacent islands for\n   poisoning arrows. One kind, upas antiar, is, derived from upas tree\n   (Antiaris toxicaria). Upas tieute is prepared from a climbing plant\n   (Strychnos Tieute).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upbar', 'v. t.', 'To fasten with a bar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upbar', 'v. t.', 'To remove the bar or bards of, as a gate; to under.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upbear', 'v. t.', 'To bear up; to raise aloft; to support in an elevated\n   situation; to sustain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upbind', 'v. t.', 'To bind up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upblow', 'v. t.', 'To inflate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upblow', 'v. i.', 'To blow up; as, the wind upblows from the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upbraided', 'imp. & p. p.', 'of Upbraid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upbraiding', 'p. pr. & vb. n.', 'of Upbraid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upbraid', 'v. t.', 'To charge with something wrong or disgraceful; to\n   reproach; to cast something in the teeth of; -- followed by with or\n   for, and formerly of, before the thing imputed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upbraid', 'v. t.', 'To reprove severely; to rebuke; to chide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upbraid', 'v. t.', 'To treat with contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upbraid', 'v. t.', 'To object or urge as a matter of reproach; to cast up;\n   -- with to before the person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upbraid', 'v. i.', 'To utter upbraidings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upbraid', 'n.', 'The act of reproaching; contumely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upbreak', 'v. i.', 'To break upwards; to force away or passage to the\n   surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upbreak', 'n.', 'A breaking upward or bursting forth; an upburst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upbreathe', 'v. r.', 'To breathe up or out; to exhale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upbreed', 'v. t.', 'To rear, or bring up; to nurse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upbrought', 'a.', 'Brought up; educated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upbuoyance', 'n.', 'The act of buoying up; uplifting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upburst', 'n.', 'The act of bursting upwards; a breaking through to the\n   surface; an upbreak or uprush; as, an upburst of molten matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upcast', 'a.', 'Cast up; thrown upward; as, with upcast eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upcast', 'n.', 'A cast; a throw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upcast', 'n.', 'The ventilating shaft of a mine out of which the air passes\n   after having circulated through the mine; -- distinguished from the\n   downcast. Called also upcast pit, and upcast shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upcast', 'n.', 'An upset, as from a carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upcast', 'n.', 'A taunt; a reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upcast', 'v. t.', 'To cast or throw up; to turn upward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upcast', 'v. t.', 'To taunt; to reproach; to upbraid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upcaught', 'a.', 'Seized or caught up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upcheer', 'v. t.', 'To cheer up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upclimb', 'v. t. & i.', 'To climb up; to ascend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upcoil', 'v. t. & i.', 'To coil up; to make into a coil, or to be made into\n   a coil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upcountry', 'adv.', 'In an upcountry direction; as, to live upcountry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upcountry', 'a.', 'Living or situated remote from the seacoast; as, an\n   upcountry residence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upcountry', 'n.', 'The interior of the country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upcurl', 'v. t.', 'To curl up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Updive', 'v. i.', 'To spring upward; to rise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Updraw', 'v. t.', 'To draw up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upend', 'v. t.', 'To end up; to set on end, as a cask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upeygan', 'n.', 'The borele.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upfill', 'v. t.', 'To fill up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upflow', 'v. i.', 'To flow or stream up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upflung', 'a.', 'Flung or thrown up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upgather', 'v. t.', 'To gather up; to contract; to draw together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upgaze', 'v. i.', 'To gaze upward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upgive', 'v. t.', 'To give up or out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upgrow', 'v. i.', 'To grow up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upgrowth', 'n.', 'The process or result of growing up; progress;\n   development.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upgush', 'n.', 'A gushing upward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upgush', 'v. i.', 'To gush upward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uphaf', '', 'imp. of Upheave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uphand', 'a.', 'Lifted by the hand, or by both hands; as, the uphand\n   sledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uphang', 'v. t.', 'To hang up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uphasp', 'v. t.', 'To hasp or faster up; to close; as, sleep uphasps the\n   eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upheaped', 'a.', 'Piled up; accumulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upheaval', 'n.', 'The act of upheaving, or the state of being upheaved;\n   esp., an elevation of a portion of the earths crust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upheave', 'v. t.', 'To heave or lift up from beneath; to raise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upheld', '', 'imp. & p. p. of Uphold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upher', 'n.', 'A fir pole of from four to seven inches diameter, and twenty\n   to forty feet long, sometimes roughly hewn, used for scaffoldings, and\n   sometimes for slight and common roofs, for which use it is split.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uphill', 'adv.', 'Upwards on, or as on, a hillside; as, to walk uphill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uphill', 'a.', 'Ascending; going up; as, an uphill road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uphill', 'a.', 'Attended with labor; difficult; as, uphill work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uphilt', 'v. t.', 'To thrust in up to the hilt; as, to uphilt ones sword\n   into an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uphoard', 'v. t.', 'To hoard up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uphold', 'v. t.', 'To hold up; to lift on high; to elevate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uphold', 'v. t.', 'To keep erect; to support; to sustain; to keep from\n   falling; to maintain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uphold', 'v. t.', 'To aid by approval or encouragement; to countenance; as,\n   to uphold a person in wrongdoing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upholder', 'n.', 'A broker or auctioneer; a tradesman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upholder', 'n.', 'An undertaker, or provider for funerals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upholder', 'n.', 'An upholsterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upholder', 'n.', 'One who, or that which, upholds; a supporter; a defender;\n   a sustainer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upholster', 'v. t.', 'To furnish (rooms, carriages, bedsteads, chairs,\n   etc.) with hangings, coverings, cushions, etc.; to adorn with\n   furnishings in cloth, velvet, silk, etc.; as, to upholster a couch; to\n   upholster a room with curtains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upholster', 'n.', 'A broker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upholster', 'n.', 'An upholsterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upholsterer', 'n.', 'One who provides hangings, coverings, cushions,\n   curtains, and the like; one who upholsters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upholstery', 'n.', 'The articles or goods supplied by upholsterers; the\n   business or work of an upholsterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uphroe', 'n.', 'Same as Euphroe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upland', 'n.', 'High land; ground elevated above the meadows and intervals\n   which lie on the banks of rivers, near the sea, or between hills; land\n   which is generally dry; -- opposed to lowland, meadow, marsh, swamp,\n   interval, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upland', 'n.', 'The country, as distinguished from the neighborhood of\n   towns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upland', 'a.', 'Of or pertaining to uplands; being on upland; high in\n   situation; as, upland inhabitants; upland pasturage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upland', 'a.', 'Pertaining to the country, as distinguished from the\n   neighborhood of towns; rustic; rude; unpolished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uplander', 'n.', 'One dwelling in the upland; hence, a countryman; a\n   rustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uplander', 'n.', 'The upland sandpiper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uplandish', 'a.', 'Of or pertaining to uplands; dwelling on high lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uplandish', 'a.', 'Rude; rustic; unpolished; uncivilized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uplay', 'v. t.', 'To hoard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uplead', 'v. t.', 'To lead upward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uplean', 'v. i.', 'To lean or incline upon anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uplifting', 'imp. & p. p.', 'of Uplift');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uplift', 'v. t.', 'To lift or raise aloft; to raise; to elevate; as, to\n   uplift the arm; to uplift a rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uplift', 'n.', 'A raising or upheaval of strata so as to disturb their\n   regularity and uniformity, and to occasion folds, dislocations, and the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Up-line', 'n.', 'A line or track leading from the provinces toward the\n   metropolis or a principal terminus; the track upon which up-trains run.\n   See Up-train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uplock', 'v. t.', 'To lock up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uplook', 'v. i.', 'To look or gaze up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upmost', 'a.', 'Highest; topmost; uppermost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upokororo', 'n.', 'An edible fresh-water New Zealand fish (Prototroctes\n   oxyrhynchus) of the family Haplochitonidae. In general appearance and\n   habits, it resembles the northern lake whitefishes and trout. Called\n   also grayling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upon', 'prep.', 'On; -- used in all the senses of that word, with which it\n   is interchangeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uppent', 'a.', 'A Pent up; confined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upper', 'comp.', 'Being further up, literally or figuratively; higher in\n   place, position, rank, dignity, or the like; superior; as, the upper\n   lip; the upper side of a thing; the upper house of a legislature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upper', 'n.', 'The upper leather for a shoe; a vamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uppermost', 'a.', 'Highest in place, position, rank, power, or the like;\n   upmost; supreme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uppertendom', 'n.', 'The highest class in society; the upper ten. See Upper\n   ten, under Upper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uppile', 'v. t.', 'To pile, or heap, up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uppish', 'a.', 'Proud; arrogant; assuming; putting on airs of superiority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upplight', '', 'imp. & p. p. of Uppluck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uppluck', 'v. t.', 'To pull or pluck up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uppricked', 'a.', 'Upraised; erect; -- said of the ears of an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upprop', 'v. t.', 'To prop up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upraise', 'v. t.', 'To raise; to lift up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uprear', 'v. t.', 'To raise; to erect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upridged', 'a.', 'Raised up in a ridge or ridges; as, a billow upridged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upright', 'a.', 'In an erect position or posture; perpendicular; vertical,\n   or nearly vertical; pointing upward; as, an upright tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upright', 'a.', 'Morally erect; having rectitude; honest; just; as, a man\n   upright in all his ways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upright', 'a.', 'Conformable to moral rectitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upright', 'a.', 'Stretched out face upward; flat on the back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upright', 'n.', 'Something standing upright, as a piece of timber in a\n   building. See Illust. of Frame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uprighteously', 'adv.', 'In an upright or just manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uprightly', 'adv.', 'In an upright manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uprightness', 'n.', 'the quality or state of being upright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uprise', 'v. i.', 'To rise; to get up; to appear from below the horizon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uprise', 'v. i.', 'To have an upward direction or inclination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uprise', 'n.', 'The act of rising; appearance above the horizon; rising.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uprising', 'n.', 'Act of rising; also, a steep place; an ascent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uprising', 'n.', 'An insurrection; a popular revolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uprist', 'n.', 'Uprising.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uprist', '', 'imp. of Uprise. Uprose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uproar', 'n.', 'Great tumult; violent disturbance and noise; noisy\n   confusion; bustle and clamor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uproar', 'v. t.', 'To throw into uproar or confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uproar', 'v. i.', 'To make an uproar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uproarious', 'a.', 'Making, or accompanied by, uproar, or noise and tumult;\n   as, uproarious merriment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uproll', 'v. t.', 'To roll up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uproot', 'v. t.', 'To root up; to tear up by the roots, or as if by the\n   roots; to remove utterly; to eradicate; to extirpate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uprouse', 'v. t.', 'To rouse up; to rouse from sleep; to awake; to arouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uprun', 'v. i.', 'To run up; to ascend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uprush', 'v. i.', 'To rush upward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uprush', 'n.', 'Act of rushing upward; an upbreak or upburst; as, an uprush\n   of lava.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upsarokas', 'n. pl.', 'See Crows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upseek', 'v. i.', 'To seek or strain upward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upsend', 'v. t.', 'To send, cast, or throw up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upset', 'v. t.', 'To set up; to put upright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upset', 'v. t.', 'To thicken and shorten, as a heated piece of iron, by\n   hammering on the end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upset', 'v. t.', 'To shorten (a tire) in the process of resetting,\n   originally by cutting it and hammering on the ends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upset', 'v. t.', 'To overturn, overthrow, or overset; as, to upset a\n   carriage; to upset an argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upset', 'v. t.', 'To disturb the self-possession of; to disorder the nerves\n   of; to make ill; as, the fright upset her.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upset', 'v. i.', 'To become upset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upset', 'a.', 'Set up; fixed; determined; -- used chiefly or only in the\n   phrase upset price; that is, the price fixed upon as the minimum for\n   property offered in a public sale, or, in an auction, the price at\n   which property is set up or started by the auctioneer, and the lowest\n   price at which it will be sold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upset', 'n.', 'The act of upsetting, or the state of being upset; an\n   overturn; as, the wagon had an upset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upsetting', 'a.', 'Conceited; assuming; as, an upsetting fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upshoot', 'v. i.', 'To shoot upward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upshot', 'n.', 'Final issue; conclusion; the sum and substance; the end;\n   the result; the consummation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upside', 'n.', 'The upper side; the part that is uppermost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upsidown', 'adv.', 'See Upsodown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upsitting', 'n.', 'A sitting up of a woman after her confinement, to\n   receive and entertain her friends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upskip', 'n.', 'An upstart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upsnatch', 'v. t.', 'To snatch up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upsoar', 'v. i.', 'To soar or mount up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upsodown', 'adv.', 'Upside down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upspear', 'v. i.', 'To grow or shoot up like a spear; as, upspearing grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upspring', 'v. i.', 'To spring up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upspring', 'n.', 'An upstart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upspring', 'n.', 'A spring or leap into the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upspurner', 'n.', 'A spurner or contemner; a despiser; a scoffer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upstairs', 'adv.', 'Up the stairs; in or toward an upper story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upstairs', 'a.', 'Being above stairs; as, an upstairs room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upstand', 'v. i.', 'To stand up; to be erected; to rise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upstare', 'v. i.', 'To stare or stand upward; hence, to be uplifted or\n   conspicuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upstart', 'v. i.', 'To start or spring up suddenly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upstart', 'n.', 'One who has risen suddenly, as from low life to wealth,\n   power, or honor; a parvenu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upstart', 'n.', 'The meadow saffron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upstart', 'a.', 'Suddenly raised to prominence or consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upstay', 'v. t.', 'To sustain; to support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upsterte', '', 'imp. & p. p. of Upstart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upstir', 'n.', 'Insurrection; commotion; disturbance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upstream', 'adv.', 'Toward the higher part of a stream; against the\n   current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upstreet', 'adv.', 'Toward the higher part of a street; as, to walk\n   upstreet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upstroke', 'n.', 'An upward stroke, especially the stroke, or line, made by\n   a writing instrument when moving upward, or from the body of the\n   writer, or a line corresponding to the part of a letter thus made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upsun', 'n.', 'The time during which the sun is up, or above the horizon;\n   the time between sunrise and sunset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upswarm', 'v. i. & i.', 'To rise, or cause to rise, in a swarm or swarms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upsway', 'v. t.', 'To sway or swing aloft; as, to upsway a club.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upswell', 'v. i.', 'To swell or rise up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upsyturvy', 'adv.', 'Upside down; topsy-turvy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uptails all', '', 'An old game at cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uptails all', '', 'Revelers; roysterers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uptails all', '', 'Revelry; confusion; frolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uptake', 'v. t.', 'To take into the hand; to take up; to help.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uptake', 'n.', 'The pipe leading upward from the smoke box of a steam\n   boiler to the chimney, or smokestack; a flue leading upward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uptake', 'n.', 'Understanding; apprehension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uptear', 'v. t.', 'To tear up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upthrow', 'v. t.', 'To throw up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upthrow', 'n.', 'See Throw, n., 9.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upthunder', 'v. i.', 'To send up a noise like thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uptie', 'v. t.', 'To tie up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uptill', 'prep.', 'To; against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uptown', 'adv.', 'To or in the upper part of a town; as, to go uptown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uptown', 'a.', 'Situated in, or belonging to, the upper part of a town or\n   city; as, a uptown street, shop, etc.; uptown society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uptrace', 'v. t.', 'To trace up or out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uptrain', 'v. t.', 'To train up; to educate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Up-train', '', 'A train going in the direction of the metropolis or the\n   main terminus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Up-train', '', 'A train going in the direction conventionally called up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upturn', 'v. t.', 'To turn up; to direct upward; to throw up; as, to upturn\n   the ground in plowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upupa', 'n.', 'A genus of birds which includes the common hoopoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upwaft', 'v. t.', 'To waft upward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upward', 'adv.', 'Alt. of Upwards');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upwards', 'adv.', 'In a direction from lower to higher; toward a higher\n   place; in a course toward the source or origin; -- opposed to downward;\n   as, to tend or roll upward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upwards', 'adv.', 'In the upper parts; above.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upwards', 'adv.', 'Yet more; indefinitely more; above; over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upward', 'a.', 'Directed toward a higher place; as, with upward eye; with\n   upward course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upward', 'n.', 'The upper part; the top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upwhirl', 'v. t. & i.', 'To rise upward in a whirl; to raise upward with a\n   whirling motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upwind', 'v. t.', 'To wind up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upwreath', 'v. i.', 'To rise with a curling motion; to curl upward, as\n   smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Upyat', '', 'imp. of Upgive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ur', 'n.', 'Alt. of Ure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ure', 'n.', 'The urus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urachus', 'n.', 'A cord or band of fibrous tissue extending from the\n   bladder to the umbilicus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uraemia', 'n.', 'Accumulation in the blood of the principles of the urine,\n   producing dangerous disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uraemic', 'a.', 'Of or pertaining to uraemia; as, uraemic convulsions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uraeum', 'n.', 'The posterior half of an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ural', 'a.', 'Pertaining to, or designating, the Urals, a mountain range\n   between Europe and Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ural-Altaic', 'a.', 'Of or pertaining to the Urals and the Altai; as the\n   Ural-Altaic, or Turanian, languages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urali', 'n.', 'See Curare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uralian', 'a.', 'Alt. of Uralic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uralic', 'a.', 'Of or relating to the Ural Mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uralite', 'n.', 'Amphibole resulting from the alternation of pyroxene by\n   paramorphism. It is not uncommon in massive eruptive rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uralitization', 'n.', 'The change of pyroxene to amphibole by paramorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uramil', 'n.', 'Murexan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranate', 'n.', 'A salt of uranic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urania', 'n.', 'One of the nine Muses, daughter of Zeus by Mnemosyne, and\n   patron of astronomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urania', 'n.', 'A genus of large, brilliantly colored moths native of the\n   West Indies and South America. Their bright colored and tailed hind\n   wings and their diurnal flight cause them to closely resemble\n   butterflies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranian', 'a.', 'Of or pertaining to the planet Uranus; as, the Uranian\n   year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranic', 'a.', 'Of or pertaining to the heavens; celestial; astronomical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranic', 'a.', 'Pertaining to, resembling, or containing uranium;\n   specifically, designating those compounds in which uranium has a\n   valence relatively higher than in uranous compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranin', 'n.', 'An alkaline salt of fluorescein, obtained as a brownish red\n   substance, which is used as a dye; -- so called from the peculiar\n   yellowish green fluorescence (resembling that of uranium glass) of its\n   solutions. See Fluorescein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uraninite', 'n.', 'A mineral consisting chiefly of uranium oxide with some\n   lead, thorium, etc., occurring in black octahedrons, also in masses\n   with a pitchlike luster; pitchblende.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uraniscoplasty', 'n.', 'The process of forming an artificial palate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uraniscoraphy', 'n.', 'Alt. of Uraniscorrhaphy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uraniscorrhaphy', 'n.', 'Suture of the palate. See Staphyloraphy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranite', 'n.', 'A general term for the uranium phosphates, autunite, or\n   lime uranite, and torbernite, or copper uranite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranitic', 'a.', 'Of or pertaining to uranium; containing uranium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranium', 'n.', 'An element of the chromium group, found in certain rare\n   minerals, as pitchblende, uranite, etc., and reduced as a heavy, hard,\n   nickel-white metal which is quite permanent. Its yellow oxide is used\n   to impart to glass a delicate greenish-yellow tint which is accompanied\n   by a strong fluorescence, and its black oxide is used as a pigment in\n   porcelain painting. Symbol U. Atomic weight 239.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uran-ocher', 'n.', 'Alt. of Uran-ochre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uran-ochre', 'n.', 'A yellow, earthy incrustation, consisting essentially\n   of the oxide of uranium, but more or less impure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranographic', 'a.', 'Alt. of Uranographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranographical', 'a.', 'Of or pertaining to uranography; as, an\n   uranographic treatise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranographist', 'n.', 'One practiced in uranography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranography', 'n.', 'A description or plan of the heavens and the heavenly\n   bodies; the construction of celestial maps, globes, etc.; uranology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranolite', 'n.', 'A meteorite or aerolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranology', 'n.', 'A discourse or treatise on the heavens and the heavenly\n   bodies; the study of the heavens; uranography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranometria', 'n.', 'A uranometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranometry', 'n.', 'A chart or catalogue of fixed stars, especially of\n   stars visible to the naked eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranoplasty', 'n.', 'The plastic operation for closing a fissure in the\n   hard palate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranoscopy', 'n.', 'Observation of the heavens or heavenly bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranoso-', 'a.', 'A combining form (also used adjectively) from uranium; --\n   used in naming certain complex compounds; as in uranoso-uranic oxide,\n   uranoso-uranic sulphate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranous', 'a.', 'Pertaining to, or containing, uranium; designating those\n   compounds in which uranium has a lower valence as contrasted with the\n   uranic compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranus', 'n.', 'The son or husband of Gaia (Earth), and father of Chronos\n   (Time) and the Titans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranus', 'n.', 'One of the primary planets. It is about 1,800,000,000 miles\n   from the sun, about 36,000 miles in diameter, and its period of\n   revolution round the sun is nearly 84 of our years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uran-utan', '', 'The orang-utang');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uranyl', 'n.', 'The radical UO2, conveniently regarded as a residue of many\n   uranium compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urao', 'n.', 'See Trona.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urare', 'n.', 'Alt. of Urari');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urari', 'n.', 'See Curare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urate', 'n.', 'A salt of uric acid; as, sodium urate; ammonium urate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uratic', '', 'Of or containing urates; as, uratic calculi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urban', 'a.', 'Of or belonging to a city or town; as, an urban population.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urban', 'a.', 'Belonging to, or suiting, those living in a city;\n   cultivated; polite; urbane; as, urban manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urbane', 'a.', 'Courteous in manners; polite; refined; elegant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urbaniste', 'n.', 'A large and delicious pear or Flemish origin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urbanity', 'n.', 'The quality or state of being urbane; civility or\n   courtesy of manners; politeness; refinement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urbanity', 'n.', 'Polite wit; facetiousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urbanize', 'v. t.', 'To render urban, or urbane; to refine; to polish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urbicolae', 'n. pl.', 'An extensive family of butterflies, including those\n   known as skippers (Hesperiadae).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urbicolous', 'a.', 'Of or pertaining to a city; urban.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urceolar', 'a.', 'Urceolate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urcelate', 'a.', 'Shaped like a pitcher or urn; swelling below, and\n   contrasted at the orifice, as a calyx or corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urceole', 'n.', 'A vessel for water for washing the hands; also, one to\n   hold wine or water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urceoli', 'pl. ', 'of Urceolus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urceolus', 'n.', 'Any urn-shaped organ of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urchin', 'n.', 'A hedgehog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urchin', 'n.', 'A sea urchin. See Sea urchin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urchin', 'n.', 'A mischievous elf supposed sometimes to take the form a\n   hedgehog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urchin', 'n.', 'A pert or roguish child; -- now commonly used only of a\n   boy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urchin', 'n.', 'One of a pair in a series of small card cylinders, arranged\n   around a carding drum; -- so called from its fancied resemblance to the\n   hedgehog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urchin', 'a.', 'Rough; pricking; piercing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urchon', 'n.', 'The urchin, or hedgehog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urdu', 'n.', 'The language more generally called Hindustanee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ure', 'n.', 'Use; practice; exercise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ure', 'v. t.', 'To use; to exercise; to inure; to accustom by practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urea', 'a.', 'A very soluble crystalline body which is the chief\n   constituent of the urine in mammals and some other animals. It is also\n   present in small quantity in blood, serous fluids, lymph, the liver,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ureal', 'a.', 'Of or pertaining to urea; containing, or consisting of,\n   urea; as, ureal deposits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ureameter', 'n.', 'An apparatus for the determination of the amount of urea\n   in urine, in which the nitrogen evolved by the action of certain\n   reagents, on a given volume of urine, is collected and measured, and\n   the urea calculated accordingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urechitin', 'n.', 'A glucoside extracted from the leaves of a certain plant\n   (Urechitis suberecta) as a bitter white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urechitoxin', 'n.', 'A poisonous glucoside found accompanying urechitin,\n   and extracted as a bitter white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uredo', 'n.', 'One of the stages in the life history of certain rusts\n   (Uredinales), regarded at one time as a distinct genus. It is a summer\n   stage preceding the teleutospore, or winter stage. See Uredinales, in\n   the Supplement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uredo', 'n.', 'Nettle rash. See Urticaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uredospore', 'n.', 'The thin-walled summer spore which is produced during\n   the so-called Uredo stage of certain rusts. See (in the Supplement)\n   Uredinales, Heter/cious, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ureide', 'n.', 'Any one of the many complex derivatives of urea; thus,\n   hydantoin, and, in an extended dense, guanidine, caffeine, et., are\n   ureides.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-uret', '', 'A suffix with the same meaning as -ide. See -ide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ureter', 'n.', 'The duct which conveys the urine from the kidney to the\n   bladder or cloaca. There are two ureters, one for each kidney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ureteritis', 'n.', 'Inflammation of the ureter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urethane', 'n.', 'A white crystalline substance, NH2.CO.OC2H5, produced by\n   the action of ammonia on ethyl carbonate. It is used somewhat in\n   medicine as a hypnotic. By extension, any one of the series of related\n   substances of which urethane proper is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urethra', 'n.', 'The canal by which the urine is conducted from the bladder\n   and discharged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urethral', 'a.', 'Of or pertaining to the urethra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urethritis', 'n.', 'Inflammation of the urethra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urethroplasty', 'n.', 'An operation for the repair of an injury or a defect\n   in the walls of the urethra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urethroscope', 'n.', 'An instrument for viewing the interior of the\n   urethra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urethroscopy', 'n.', 'Examination of the urethra by means of the\n   urethroscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urethrotome', 'n.', 'An instrument for cutting a urethral stricture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urethrotomy', 'n.', 'An incision of the urethra, esp. incision for relief\n   of urethral stricture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uretic', 'a.', 'Of or pertaining to the urine; diuretic; urinary; as,\n   uretic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urged', 'imp. & p. p.', 'of Urge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urging', 'p. pr. & vb. n.', 'of Urge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urge', 'v. t.', 'To press; to push; to drive; to impel; to force onward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urge', 'v. t.', 'To press the mind or will of; to ply with motives,\n   arguments, persuasion, or importunity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urge', 'v. t.', 'To provoke; to exasperate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urge', 'v. t.', 'To press hard upon; to follow closely');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urge', 'v. t.', 'To present in an urgent manner; to press upon attention;\n   to insist upon; as, to urge an argument; to urge the necessity of a\n   case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urge', 'v. t.', 'To treat with forcible means; to take severe or violent\n   measures with; as, to urge an ore with intense heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urge', 'v. i.', 'To press onward or forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urge', 'v. i.', 'To be pressing in argument; to insist; to persist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urgence', 'n.', 'Urgency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urgency', 'n.', 'The quality or condition of being urgent; insistence;\n   pressure; as, the urgency of a demand or an occasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urgent', 'a.', 'Urging; pressing; besetting; plying, with importunity;\n   calling for immediate attention; instantly important.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urgently', 'adv.', 'In an urgent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urger', 'n.', 'One who urges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uric', 'a.', 'Of or pertaining to urine; obtained from urine; as, uric\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urim', 'n.', 'A part or decoration of the breastplate of the high priest\n   among the ancient Jews, by which Jehovah revealed his will on certain\n   occasions. Its nature has been the subject of conflicting conjectures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urinal', 'n.', 'A vessel for holding urine; especially, a bottle or tube\n   for holding urine for inspection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urinal', 'n.', 'A place or convenience for urinating purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urinarium', 'n.', 'A reservoir for urine, etc., for manure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urinary', 'a.', 'Of or pertaining to the urine; as, the urinary bladder;\n   urinary excretions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urinary', 'a.', 'Resembling, or being of the nature of, urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urinary', 'n.', 'A urinarium; also, a urinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urinate', 'v. i.', 'To discharge urine; to make water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urination', 'n.', 'The act or process of voiding urine; micturition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urinative', 'a.', 'Provoking the flow of urine; uretic; diuretic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urinator', 'n.', 'One who dives under water in search of something, as for\n   pearls; a diver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urine', 'n.', 'In mammals, a fluid excretion from the kidneys; in birds and\n   reptiles, a solid or semisolid excretion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urine', 'v. i.', 'To urinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uriniferous', 'a.', 'Bearing or conveying urine; as, uriniferous tubules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uriniparous', 'a.', 'Producing or preparing urine; as, the uriniparous\n   tubes in the cortical portion of the kidney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urinogenital', 'a.', 'Pertaining to the urinary and genital organs;\n   genitourinary; urogenital; as, the urinogenital canal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urinometer', 'n.', 'A small hydrometer for determining the specific gravity\n   of urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urinometry', 'n.', 'The estimation of the specific gravity of urine by the\n   urinometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urinose', 'a.', 'Alt. of Urinous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urinous', 'a.', 'Of or pertaining to urine, or partaking of its qualities;\n   having the character or odor of urine; similar to urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urite', 'n.', 'One of the segments of the abdomen or post-abdomen of\n   arthropods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urith', 'n.', 'The bindings of a hedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urn', 'n.', 'A vessel of various forms, usually a vase furnished with a\n   foot or pedestal, employed for different purposes, as for holding\n   liquids, for ornamental uses, for preserving the ashes of the dead\n   after cremation, and anciently for holding lots to be drawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urn', 'n.', 'Fig.: Any place of burial; the grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urn', 'n.', 'A measure of capacity for liquids, containing about three\n   gallons and a haft, wine measure. It was haft the amphora, and four\n   times the congius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urn', 'n.', 'A hollow body shaped like an urn, in which the spores of\n   mosses are contained; a spore case; a theca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urn', 'n.', 'A tea urn. See under Tea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urn', 'v. t.', 'To inclose in, or as in, an urn; to inurn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urnal', 'a.', 'Of or pertaining to an urn; effected by an urn or urns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urnfuls', 'pl. ', 'of Urnful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urnful', 'n.', 'As much as an urn will hold; enough to fill an urn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urn-shaped', 'a.', 'Having the shape of an urn; as, the urn-shaped capsules\n   of some mosses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uro-', '', 'A combining form fr. Gr. oy^ron, urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uro-', '', 'A combining form from Gr. oyra`, the tail, the caudal\n   extremity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urobilin', 'n.', 'A yellow pigment identical with hydrobilirubin, abundant\n   in the highly colored urine of fever, and also present in normal urine.\n   See Urochrome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urocele', 'n.', 'A morbid swelling of the scrotum due to extravasation of\n   urine into it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urocerata', 'n. pl.', 'A division of boring Hymenoptera, including Tremex\n   and allied genera. See Illust. of Horntail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urochord', 'n.', 'The central axis or cord in the tail of larval ascidians\n   and of certain adult tunicates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urochorda', 'n. pl.', 'Same as Tunicata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urochordal', 'a.', 'Of or pertaining to the Urochorda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urochrome', 'n.', 'A yellow urinary pigment, considered by Thudichum as the\n   only pigment present in normal urine. It is regarded by Maly as\n   identical with urobilin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urochs', 'n.', 'See Aurochs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urocord', 'n.', 'See Urochord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urocyst', 'n.', 'The urinary bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urodela', 'n. pl.', 'An order of amphibians having the tail well developed\n   and often long. It comprises the salamanders, tritons, and allied\n   animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urodele', 'n.', 'One of the Urodela.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urodelian', 'a.', 'Of or pertaining to the Urodela.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urodelian', 'n.', 'One of the Urodela.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uroerythrin', 'n.', 'A reddish urinary pigment, considered as the substance\n   which gives to the urine of rheumatism its characteristic color. It\n   also causes the red color often seen in deposits of urates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urogastric', 'a.', 'Behind the stomach; -- said of two lobes of the\n   carapace of certain crustaceans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urogenital', 'a.', 'Same as Urinogenital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uroglaucin', 'n.', 'A body identical with indigo blue, occasionally found\n   in the urine in degeneration of the kidneys. It is readily formed by\n   oxidation or decomposition of indican.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urohaematin', 'n.', 'Urinary haematin; -- applied to the normal coloring\n   matter of the urine, on the supposition that it is formed either\n   directly or indirectly (through bilirubin) from the haematin of the\n   blood. See Urochrome, and Urobilin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urohyal', 'a.', 'Of or pertaining to one or more median and posterior\n   elements in the hyoidean arch of fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urohyal', 'n.', 'A urohyal bone or cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urology', 'n.', 'See Uronology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uromere', 'n.', 'Any one of the abdominal segments of an arthropod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uronology', 'n.', 'That part of medicine which treats of urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uropod', 'n.', 'Any one of the abdominal appendages of a crustacean,\n   especially one of the posterior ones, which are often larger than the\n   rest, and different in structure, and are used chiefly in locomotion.\n   See Illust. of Crustacea, and Stomapoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uropodal', 'a.', 'Of or pertaining to a uropod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uropoetic', 'a.', 'Producing, or favoring the production of, urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uropoetic', 'a.', 'Of, pertaining to, or designating, a system of organs\n   which eliminate nitrogenous waste matter from the blood of certain\n   invertebrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uropygial', 'a.', 'Of or pertaining to the uropygium, or prominence at the\n   base of the tail feathers, in birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uropygium', 'n.', 'The prominence at the posterior extremity of a birds\n   body, which supports the feathers of the tail; the rump; -- sometimes\n   called popes nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urosacral', 'a.', 'Of or pertaining to both the caudal and sacral parts of\n   the vertebral column; as, the urosacral vertebrae of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uroscopy', 'n.', 'The diagnosis of diseases by inspection of urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urosome', 'n.', 'The abdomen, or post-abdomen, of arthropods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urostege', 'n.', 'One of the plates on the under side of the tail of a\n   serpent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urostea', 'pl. ', 'of Urosteon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urosteons', 'pl. ', 'of Urosteon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urosteon', 'n.', 'A median ossification back of the lophosteon in the\n   sternum of some birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urosternite', 'n.', 'The sternal, or under piece, of any one of the\n   uromeres of insects and other arthropods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urostyle', 'n.', 'A styliform process forming the posterior extremity of\n   the vertebral column in some fishes and amphibians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urox', 'n.', 'The aurochs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uroxanate', 'n.', 'A salt of uroxanic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uroxanic', 'a.', 'Pertaining to, or designating, an acid, C5H8N4O6, which\n   is obtained, as a white crystalline substance, by the slow oxidation of\n   uric acid in alkaline solution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uroxanthin', 'n.', 'Same as Indican.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urrhodin', 'n.', 'Indigo red, a product of the decomposition, or oxidation,\n   of indican. It is sometimes found in the sediment of pathological\n   urines. It is soluble in ether or alcohol, giving the solution a\n   beautiful red color. Also called indigrubin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urry', 'n.', 'A sort of blue or black clay lying near a vein of coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ursa', 'n.', 'Either one of the Bears. See the Phrases below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ursal', 'n.', 'The ursine seal. See the Note under 1st Seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ursiform', 'a.', 'Having the shape of a bear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ursine', 'a.', 'Of or pertaining to a bear; resembling a bear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urson', 'n.', 'The Canada porcupine. See Porcupine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ursuk', 'n.', 'The bearded seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ursula', 'n.', 'A beautiful North American butterfly (Basilarchia, /\n   Limenitis, astyanax). Its wings are nearly black with red and blue\n   spots and blotches. Called also red-spotted purple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ursuline', 'n.', 'One of an order of nuns founded by St. Angela Merici, at\n   Brescia, in Italy, about the year 1537, and so called from St. Ursula,\n   under whose protection it was placed. The order was introduced into\n   Canada as early as 1639, and into the United States in 1727. The\n   members are devoted entirely to education.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ursuline', 'a.', 'Of or pertaining to St. Ursula, or the order of\n   Ursulines; as, the Ursuline nuns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ursus', 'n.', 'A genus of Carnivora including the common bears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urtica', 'n.', 'A genus of plants including the common nettles. See Nettle,\n   n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urticaceous', 'a.', 'Of or pertaining to a natural order (Urticaceae) of\n   plants, of which the nettle is the type. The order includes also the\n   hop, the elm, the mulberry, the fig, and many other plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urtical', 'a.', 'Resembling nettles; -- said of several natural orders\n   allied to urticaceous plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urticaria', 'n.', 'The nettle rash, a disease characterized by a transient\n   eruption of red pimples and of wheals, accompanied with a burning or\n   stinging sensation and with itching; uredo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urticated', 'imp. & p. p.', 'of Urticate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urticating', 'p. pr. & vb. n.', 'of Urticate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urticate', 'v. t. & i.', 'To sting with, or as with, nettles; to irritate;\n   to annoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urtication', 'n.', 'The act or process of whipping or stinging with\n   nettles; -- sometimes used in the treatment of paralysis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urubu', 'n.', 'The black vulture (Catharista atrata). It ranges from the\n   Southern United States to South America. See Vulture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urus', 'n.', 'A very large, powerful, and savage extinct bovine animal (Bos\n   urus / primigenius) anciently abundant in Europe. It appears to have\n   still existed in the time of Julius Caesar. It had very large horns,\n   and was hardly capable of domestication. Called also, ur, ure, and tur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Urva', 'n.', 'The crab-eating ichneumon (Herpestes urva), native of India.\n   The fur is black, annulated with white at the tip of each hair, and a\n   white streak extends from the mouth to the shoulder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Us', 'pron.', 'The persons speaking, regarded as an object; ourselves; --\n   the objective case of we. See We.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usable', 'a.', 'Capable of being used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usage', 'n.', 'The act of using; mode of using or treating; treatment;\n   conduct with respect to a person or a thing; as, good usage; ill usage;\n   hard usage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usage', 'n.', 'Manners; conduct; behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usage', 'n.', 'Long-continued practice; customary mode of procedure;\n   custom; habitual use; method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usage', 'n.', 'Customary use or employment, as of a word or phrase in a\n   particular sense or signification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usage', 'n.', 'Experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usager', 'n.', 'One who has the use of anything in trust for another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usance', 'v. t.', 'Use; usage; employment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usance', 'v. t.', 'Custom; practice; usage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usance', 'v. t.', 'Interest paid for money; usury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usance', 'v. t.', 'The time, fixed variously by the usage between different\n   countries, when a bill of exchange is payable; as, a bill drawn on\n   London at one usance, or at double usance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usant', 'a.', 'Using; accustomed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usbegs', 'n. pl.', 'Alt. of Usbeks');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usbeks', 'n. pl.', 'A Turkish tribe which about the close of the 15th\n   century conquered, and settled in, that part of Asia now called\n   Turkestan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Use', 'v. t.', 'The act of employing anything, or of applying it to ones\n   service; the state of being so employed or applied; application;\n   employment; conversion to some purpose; as, the use of a pen in\n   writing; his machines are in general use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Use', 'v. t.', 'Occasion or need to employ; necessity; as, to have no\n   further use for a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Use', 'v. t.', 'Yielding of service; advantage derived; capability of being\n   used; usefulness; utility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Use', 'v. t.', 'Continued or repeated practice; customary employment;\n   usage; custom; manner; habit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Use', 'v. t.', 'Common occurrence; ordinary experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Use', 'v. t.', 'The special form of ritual adopted for use in any diocese;\n   as, the Sarum, or Canterbury, use; the Hereford use; the York use; the\n   Roman use; etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Use', 'v. t.', 'The premium paid for the possession and employment of\n   borrowed money; interest; usury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Use', 'v. t.', 'The benefit or profit of lands and tenements. Use imports a\n   trust and confidence reposed in a man for the holding of lands. He to\n   whose use or benefit the trust is intended shall enjoy the profits. An\n   estate is granted and limited to A for the use of B.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Use', 'v. t.', 'A stab of iron welded to the side of a forging, as a shaft,\n   near the end, and afterward drawn down, by hammering, so as to lengthen\n   the forging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Used', 'imp. & p. p.', 'of Use');
  INSERT INTO words (word, word_type, description) VALUES  (    'Using', 'p. pr. & vb. n.', 'of Use');
  INSERT INTO words (word, word_type, description) VALUES  (    'Use', 'v. t.', 'To make use of; to convert to ones service; to avail ones\n   self of; to employ; to put a purpose; as, to use a plow; to use a\n   chair; to use time; to use flour for food; to use water for irrigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Use', 'v. t.', 'To behave toward; to act with regard to; to treat; as, to\n   use a beast cruelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Use', 'v. t.', 'To practice customarily; to make a practice of; as, to use\n   diligence in business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Use', 'v. t.', 'To accustom; to habituate; to render familiar by practice;\n   to inure; -- employed chiefly in the passive participle; as, men used\n   to cold and hunger; soldiers used to hardships and danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Use', 'v. i.', 'To be wont or accustomed; to be in the habit or practice;\n   as, he used to ride daily; -- now disused in the present tense, perhaps\n   because of the similarity in sound, between \"use to,\" and \"used to.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Use', 'v. i.', 'To be accustomed to go; to frequent; to inhabit; to dwell;\n   -- sometimes followed by of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Useful', 'a.', 'Full of use, advantage, or profit; producing, or having\n   power to produce, good; serviceable for any end or object; helpful\n   toward advancing any purpose; beneficial; profitable; advantageous; as,\n   vessels and instruments useful in a family; books useful for\n   improvement; useful knowledge; useful arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usefully', 'adv.', 'In a useful manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usefulness', 'n.', 'The quality or state of being useful; utility;\n   serviceableness; advantage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Useless', 'a.', 'Having, or being of, no use; unserviceable; producing no\n   good end; answering no valuable purpose; not advancing the end\n   proposed; unprofitable; ineffectual; as, a useless garment; useless\n   pity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'User', 'n.', 'One who uses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'User', 'n.', 'Enjoyment of property; use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usher', 'n.', 'An officer or servant who has the care of the door of a\n   court, hall, chamber, or the like; hence, an officer whose business it\n   is to introduce strangers, or to walk before a person of rank. Also,\n   one who escorts persons to seats in a church, theater, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usher', 'n.', 'An under teacher, or assistant master, in a school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ushered', 'imp. & p. p.', 'of Usher');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ushering', 'p. pr. & vb. n.', 'of Usher');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usher', 'v. t.', 'To introduce or escort, as an usher, forerunner, or\n   harbinger; to forerun; -- sometimes followed by in or forth; as, to\n   usher in a stranger; to usher forth the guests; to usher a visitor into\n   the room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usherance', 'n.', 'The act of ushering, or the state of being ushered in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usherdom', 'n.', 'The office or position of an usher; ushership; also,\n   ushers, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usherless', 'a.', 'Destitute of an usher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ushership', 'n.', 'The office of an usher; usherdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usitative', 'a.', 'Denoting usual or customary action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usnea', 'n.', 'A genus of lichens, most of the species of which have long,\n   gray, pendulous, and finely branched fronds. Usnea barbata is the\n   common bearded lichen which grows on branches of trees in northern\n   forests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usnic', 'a.', 'Pertaining to, or designating, a complex acid obtained, as a\n   yellow crystalline substance, from certain genera of lichens (Usnea,\n   Parmelia, etc.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usquebaugh', 'a.', 'A compound distilled spirit made in Ireland and\n   Scotland; whisky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usquebaugh', 'a.', 'A liquor compounded of brandy, or other strong spirit,\n   raisins, cinnamon and other spices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usself', 'n. pl.', 'Ourselves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ustion', 'n.', 'The act of burning, or the state of being burned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ustorious', 'a.', 'Having the quality of burning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ustulate', 'a.', 'Blackened as if burned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ustulation', 'n.', 'The act of burning or searing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ustulation', 'n.', 'The operation of expelling one substance from another\n   by heat, as sulphur or arsenic from ores, in a muffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ustulation', 'n.', 'The roasting or drying of moist substances so as\n   prepare them for pulverizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ustulation', 'n.', 'The burning of wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ustulation', 'n.', 'Lascivious passion; concupiscence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usual', 'n.', 'Such as is in common use; such as occurs in ordinary\n   practice, or in the ordinary course of events; customary; ordinary;\n   habitual; common.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usucaption', 'n.', 'The acquisition of the title or right to property by\n   the uninterrupted possession of it for a certain term prescribed by\n   law; -- the same as prescription in common law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usufruct', 'n.', 'The right of using and enjoying the profits of an estate\n   or other thing belonging to another, without impairing the substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usufructuary', 'n.', 'A person who has the use of property and reaps the\n   profits of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usufructuary', 'a.', 'Of or pertaining to a usufruct; having the nature of\n   a usufruct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usurarious', 'a.', 'Alt. of Usurary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usurary', 'a.', 'Usurious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usured', 'imp. & p. p.', 'of Usure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usuring', 'p. pr. & vb. n.', 'of Usure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usure', 'v. i.', 'To practice usury; to charge unlawful interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usure', 'n.', 'Usury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usurer', 'n.', 'One who lends money and takes interest for it; a money\n   lender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usurer', 'n.', 'One who lends money at a rate of interest beyond that\n   established by law; one who exacts an exorbitant rate of interest for\n   the use of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usurious', 'a.', 'Practicing usury; taking illegal or exorbitant interest\n   for the use of money; as, a usurious person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usurious', 'a.', 'Partaking of usury; containing or involving usury; as, a\n   usurious contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usurped', 'imp. & p. p.', 'of Usurp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usurping', 'p. pr. & vb. n.', 'of Usurp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usurp', 'v. t.', 'To seize, and hold in possession, by force, or without\n   right; as, to usurp a throne; to usurp the prerogatives of the crown;\n   to usurp power; to usurp the right of a patron is to oust or dispossess\n   him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usurp', 'v. i.', 'To commit forcible seizure of place, power, functions, or\n   the like, without right; to commit unjust encroachments; to be, or act\n   as, a usurper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usurpant', 'a.', 'Usurping; encroaching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usurpation', 'n.', 'The act of usurping, or of seizing and enjoying; an\n   authorized, arbitrary assumption and exercise of power, especially an\n   infringing on the rights of others; specifically, the illegal seizure\n   of sovereign power; -- commonly used with of, also used with on or\n   upon; as, the usurpation of a throne; the usurpation of the supreme\n   power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usurpation', 'n.', 'Use; usage; custom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usurpatory', 'a.', 'Marked by usurpation; usurping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usurpature', 'n.', 'Usurpation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usurper', 'n.', 'One who usurps; especially, one who seizes illegally on\n   sovereign power; as, the usurper of a throne, of power, or of the\n   rights of a patron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usurpingly', 'adv.', 'In a usurping manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usury', 'v. t.', 'A premium or increase paid, or stipulated to be paid, for\n   a loan, as of money; interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usury', 'v. t.', 'The practice of taking interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Usury', 'v. t.', 'Interest in excess of a legal rate charged to a borrower\n   for the use of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ut', 'n.', 'The first note in Guidos musical scale, now usually superseded\n   by do. See Solmization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utas', 'n.', 'The eighth day after any term or feast; the octave; as, the\n   utas of St. Michael.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utas', 'n.', 'Hence, festivity; merriment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utensil', 'v. t.', 'That which is used; an instrument; an implement;\n   especially, an instrument or vessel used in a kitchen, or in domestic\n   and farming business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uterine', 'a.', 'Of or instrument to the uterus, or womb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uterine', 'a.', 'Born of the same mother, but by a different father.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uterogestation', 'n.', 'Gestation in the womb from conception to birth;\n   pregnancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uterovaginal', 'n.', 'Pertaining to both the uterus and the vagina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uterus', 'n.', 'The organ of a female mammal in which the young are\n   developed previous to birth; the womb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uterus', 'n.', 'A receptacle, or pouch, connected with the oviducts of many\n   invertebrates in which the eggs are retained until they hatch or until\n   the embryos develop more or less. See Illust. of Hermaphrodite in\n   Append.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utes', 'n. pl.', 'An extensive tribe of North American Indians of the\n   Shoshone stock, inhabiting Colorado, Utah, New Mexico, Arizona, and\n   adjacent regions. They are subdivided into several subordinate tribes,\n   some of which are among the most degraded of North American Indians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utia', 'n.', 'Any species of large West Indian rodents of the genus\n   Capromys, or Utia. In general appearance and habits they resemble rats,\n   but they are as large as rabbits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utica', 'a.', 'Of, pertaining to, or designating, a subdivision of the\n   Trenton Period of the Lower Silurian, characterized in the State of New\n   York by beds of shale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utile', 'v. t.', 'Profitable; useful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utilitarian', 'a.', 'Of or pertaining to utility; consisting in utility;\n   /iming at utility as distinguished from beauty, ornament, etc.;\n   sometimes, reproachfully, evincing, or characterized by, a regard for\n   utility of a lower kind, or marked by a sordid spirit; as, utilitarian\n   narrowness; a utilitarian indifference to art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utilitarian', 'a.', 'Of or pertaining to utilitarianism; supporting\n   utilitarianism; as, the utilitarian view of morality; the Utilitarian\n   Society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utilitarian', 'n.', 'One who holds the doctrine of utilitarianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utilitarianism', 'n.', 'The doctrine that the greatest happiness of the\n   greatest number should be the end and aim of all social and political\n   institutions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utilitarianism', 'n.', 'The doctrine that virtue is founded in utility, or\n   that virtue is defined and enforced by its tendency to promote the\n   highest happiness of the universe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utilitarianism', 'n.', 'The doctrine that utility is the sole standard of\n   morality, so that the rectitude of an action is determined by its\n   usefulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utility', 'n.', 'The quality or state of being useful; usefulness;\n   production of good; profitableness to some valuable end; as, the\n   utility of manure upon land; the utility of the sciences; the utility\n   of medicines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utility', 'n.', 'Adaptation to satisfy the desires or wants; intrinsic\n   value. See Note under Value, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utility', 'n.', 'Happiness; the greatest good, or happiness, of the\n   greatest number, -- the foundation of utilitarianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utilizable', 'a.', 'Capable of being utilized; as, the utilizable products\n   of the gas works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utilization', 'n.', 'The act of utilizing, or the state of being utilized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utilized', 'imp. & p. p.', 'of Utilize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utilizing', 'p. pr. & vb. n.', 'of Utilize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utilize', 'v. t.', 'To make useful; to turn to profitable account or use;\n   to make use of; as, to utilize the whole power of a machine; to utilize\n   ones opportunities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uti possidetis', '', 'The basis or principle of a treaty which leaves\n   belligerents mutually in possession of what they have acquired by their\n   arms during the war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uti possidetis', '', 'A species of interdict granted to one who was in\n   possession of an immovable thing, in order that he might be declared\n   the legal possessor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utis', 'n.', 'See Utas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utlary', 'n.', 'Outlawry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utmost', 'a.', 'Situated at the farthest point or extremity; farthest out;\n   most distant; extreme; as, the utmost limits of the land; the utmost\n   extent of human knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utmost', 'a.', 'Being in the greatest or highest degree, quantity, number,\n   or the like; greatest; as, the utmost assiduity; the utmost harmony;\n   the utmost misery or happiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utmost', 'n.', 'The most that can be; the farthest limit; the greatest\n   power, degree, or effort; as, he has done his utmost; try your utmost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utopia', 'n.', 'An imaginary island, represented by Sir Thomas More, in a\n   work called Utopia, as enjoying the greatest perfection in politics,\n   laws, and the like. See Utopia, in the Dictionary of Noted Names in\n   Fiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utopia', 'n.', 'Hence, any place or state of ideal perfection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utopian', 'a.', 'Of or pertaining to Utopia; resembling Utopia; hence,\n   ideal; chimerical; fanciful; founded upon, or involving, imaginary\n   perfections; as, Utopian projects; Utopian happiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utopian', 'n.', 'An inhabitant of Utopia; hence, one who believes in the\n   perfectibility of human society; a visionary; an idealist; an optimist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utopianism', 'n.', 'The ideas, views, aims, etc., of a Utopian;\n   impracticable schemes of human perfection; optimism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utopianist', 'n.', 'An Utopian; an optimist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utopical', 'a.', 'Utopian; ideal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utopist', 'n.', 'A Utopian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utraquist', 'n.', 'One who receives the eucharist in both kinds; esp., one\n   of a body of Hussites who in the 15th century fought for the right to\n   do this. Called also Calixtines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utricle', 'n.', 'A little sac or vesicle, as the air cell of fucus, or\n   seaweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utricle', 'n.', 'A microscopic cell in the structure of an egg, animal, or\n   plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utricle', 'n.', 'A small, thin-walled, one-seeded fruit, as of goosefoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utricle', 'n.', 'A utriculus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utricular', 'a.', 'Of or pertaining to a utricle, or utriculus; containing,\n   or furnished with, a utricle or utricles; utriculate; as, a utricular\n   plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utricular', 'a.', 'Resembling a utricle or bag, whether large or minute; --\n   said especially with reference to the condition of certain substances,\n   as sulphur, selenium, etc., when condensed from the vaporous state and\n   deposited upon cold bodies, in which case they assume the form of small\n   globules filled with liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utricularia', 'n.', 'A genus of aquatic flowering plants, in which the\n   submersed leaves bear many little utricles, or ascidia. See Ascidium,');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utriculate', 'a.', 'Resembling a bladder; swollen like a bladder; inflated;\n   utricular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utriculoid', 'a.', 'Resembling a bladder; utricular; utriculate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utriculus', 'n.', 'A little sac, or bag; a utricle; especially, a part of\n   the membranous labyrinth of the ear. See the Note under Ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utro', '', '- (/). A combining form used in anatomy to indicate connection\n   with, or relation to, the uterus; as in utro-ovarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utter', 'a.', 'Outer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utter', 'a.', 'Situated on the outside, or extreme limit; remote from the\n   center; outer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utter', 'a.', 'Complete; perfect; total; entire; absolute; as, utter ruin;\n   utter darkness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utter', 'a.', 'Peremptory; unconditional; unqualified; final; as, an utter\n   refusal or denial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uttered', 'imp. & p. p.', 'of Utter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uttering', 'p. pr. & vb. n.', 'of Utter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utter', 'a.', 'To put forth or out; to reach out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utter', 'a.', 'To dispose of in trade; to sell or vend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utter', 'a.', 'hence, to put in circulation, as money; to put off, as\n   currency; to cause to pass in trade; -- often used, specifically, of\n   the issue of counterfeit notes or coins, forged or fraudulent\n   documents, and the like; as, to utter coin or bank notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utter', 'a.', 'To give public expression to; to disclose; to publish; to\n   speak; to pronounce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utterable', 'a.', 'Capable of being uttered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utterance', 'n.', 'The act of uttering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utterance', 'n.', 'Sale by offering to the public.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utterance', 'n.', 'Putting in circulation; as, the utterance of false coin,\n   or of forged notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utterance', 'n.', 'Vocal expression; articulation; speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utterance', 'n.', 'Power or style of speaking; as, a good utterance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utterance', 'n.', 'The last extremity; the end; death; outrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utterer', 'n.', 'One who utters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utterest', 'superl.', 'Uttermost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utterless', 'a.', 'Incapable of being uttered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utterly', 'adv.', 'In an utter manner; to the full extent; fully; totally;\n   as, utterly ruined; it is utterly vain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uttermore', 'a.', 'Further; outer; utter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uttermost', 'a.', 'Extreme; utmost; being; in the farthest, greatest, or\n   highest degree; as, the uttermost extent or end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uttermost', 'n.', 'The utmost; the highest or greatest degree; the farthest\n   extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Utterness', 'n.', 'The quality or state of being utter, or extreme;\n   extremity; utmost; uttermost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uva', 'n.', 'A small pulpy or juicy fruit containing several seeds and\n   having a thin skin, as a grape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uvate', 'n.', 'A conserve made of grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uva-ursi', 'n.', 'The bearberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uvea', 'n.', 'The posterior pigmented layer of the iris; -- sometimes\n   applied to the whole iris together with the choroid coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uveous', 'a.', 'Resembling a grape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uvic', 'a.', 'Pertaining to, or obtained from, grapes; specifically,\n   designating an organic acid, C7H8O3 (also called pyrotritartaric acid),\n   obtained as a white crystalline substance by the decomposition of\n   tartaric and pyrotartaric acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uvitic', 'a.', 'Pertaining to, or designating, an acid, CH3C6H3(CO2H)2,\n   obtained as a white crystalline substance by the partial oxidation of\n   mesitylene; -- called also mesitic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uvitonic', 'a.', 'Pertaining to, or designating, an acid which is obtained\n   as a white crystalline substance by the action of ammonia on\n   pyrotartaric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uvrou', 'n.', 'See Euphroe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uvula', 'n.', 'The pendent fleshy lobe in the middle of the posterior\n   border of the soft palate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uvular', 'a.', 'Of or pertaining to a uvula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uvulatome', 'n.', 'An instrument for removing the uvula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uvulatomy', 'n.', 'The operation of removing the uvula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uwarowite', 'n.', 'Ouvarovite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uxorial', 'a.', 'Dotingly fond of, or servilely submissive to, a wife;\n   uxorious; also, becoming a wife; pertaining to a wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uxoricidal', 'a.', 'Of or pertaining to uxoricide; tending to uxoricide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uxoricide', 'n.', 'The murder of a wife by her husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uxoricide', 'n.', 'One who murders his wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uxorious', 'a.', 'Excessively fond of, or submissive to, a wife; being a\n   dependent husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Uzema', 'n.', 'A Burman measure of twelve miles.\n   V () V, the twenty-second letter of the English alphabet, is a vocal\n   consonant. V and U are only varieties of the same character, U being\n   the cursive form, while V is better adapted for engraving, as in stone.\n   The two letters were formerly used indiscriminately, and till a\n   comparatively recent date words containing them were often classed\n   together in dictionaries and other books of reference (see U). The\n   letter V is from the Latin alphabet, where it was used both as a\n   consonant (about like English w) and as a vowel. The Latin derives it\n   from it from a form (V) of the Greek vowel / (see Y), this Greek letter\n   being either from the same Semitic letter as the digamma F (see F), or\n   else added by the Greeks to the alphabet which they took from the\n   Semitic. Etymologically v is most nearly related to u, w, f, b, p; as\n   in vine, wine; avoirdupois, habit, have; safe, save; trover,\n   troubadour, trope. See U, F, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'V', '', 'As a numeral, V stands for five, in English and Latin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaagmer', 'n.', 'The dealfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacancies', 'pl. ', 'of Vacancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacancy', 'n.', 'The quality or state of being vacant; emptiness; hence,\n   freedom from employment; intermission; leisure; idleness; listlessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacancy', 'n.', 'That which is vacant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacancy', 'n.', 'Empty space; vacuity; vacuum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacancy', 'n.', 'An open or unoccupied space between bodies or things; an\n   interruption of continuity; chasm; gap; as, a vacancy between\n   buildings; a vacancy between sentences or thoughts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacancy', 'n.', 'Unemployed time; interval of leisure; time of\n   intermission; vacation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacancy', 'n.', 'A place or post unfilled; an unoccupied office; as, a\n   vacancy in the senate, in a school, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacant', 'a.', 'Deprived of contents; not filled; empty; as, a vacant room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacant', 'a.', 'Unengaged with business or care; unemployed; unoccupied;\n   disengaged; free; as, vacant hours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacant', 'a.', 'Not filled or occupied by an incumbent, possessor, or\n   officer; as, a vacant throne; a vacant parish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacant', 'a.', 'Empty of thought; thoughtless; not occupied with study or\n   reflection; as, a vacant mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacant', 'a.', 'Abandoned; having no heir, possessor, claimant, or\n   occupier; as, a vacant estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacantly', 'adv.', 'In a vacant manner; inanely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacated', 'imp. & p. p.', 'of Vacate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacating', 'p. pr. & vb. n.', 'of Vacate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacate', 'v. t.', 'To make vacant; to leave empty; to cease from filling or\n   occupying; as, it was resolved by Parliament that James had vacated the\n   throne of England; the tenant vacated the house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacate', 'v. t.', 'To annul; to make void; to deprive of force; to make of\n   no authority or validity; as, to vacate a commission or a charter; to\n   vacate proceedings in a cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacate', 'v. t.', 'To defeat; to put an end to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacation', 'n.', 'The act of vacating; a making void or of no force; as,\n   the vacation of an office or a charter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacation', 'n.', 'Intermission of a stated employment, procedure, or\n   office; a period of intermission; rest; leisure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacation', 'n.', 'Intermission of judicial proceedings; the space of time\n   between the end of one term and the beginning of the next; nonterm;\n   recess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacation', 'n.', 'The intermission of the regular studies and exercises of\n   an educational institution between terms; holidays; as, the spring\n   vacation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacation', 'n.', 'The time when an office is vacant; esp. (Eccl.), the time\n   when a see, or other spiritual dignity, is vacant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaccary', 'n.', 'A cow house, dairy house, or cow pasture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaccina', 'n.', 'Vaccinia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaccinal', 'a.', 'Of or pertaining to vaccinia or vaccination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaccinated', 'imp. & p. p.', 'of Vaccinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaccinating', 'p. pr. & vb. n.', 'of Vaccinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaccinate', 'v. t.', 'To inoculate with the cowpox by means of a virus,\n   called vaccine, taken either directly or indirectly from cows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaccination', 'n.', 'The act, art, or practice of vaccinating, or\n   inoculating with the cowpox, in order to prevent or mitigate an attack\n   of smallpox. Cf. Inoculation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaccinator', 'n.', 'One who, or that which, vaccinates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaccine', 'a.', 'Of or pertaining to cows; pertaining to, derived from, or\n   caused by, vaccinia; as, vaccine virus; the vaccine disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaccine', 'n.', 'The virus of vaccinia used in vaccination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaccine', 'n.', 'any preparation used to render an organism immune to some\n   disease, by inducing or increasing the natural immunity mechanisms.\n   Prior to 1995, such preparations usually contained killed organisms of\n   the type for which immunity was desired, and sometimes used live\n   organisms having attenuated virulence. since that date, preparations\n   containing only specific antigenic portions of the pathogenic organism\n   are also used, some of which are prepared by genetic engineering\n   techniques.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaccinia', 'n.', 'Cowpox; vaccina. See Cowpox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaccinist', 'n.', 'A vaccinator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaccinium', 'n.', 'A genus of ericaceous shrubs including the various kinds\n   of blueberries and the true cranberries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacher', 'n.', 'A keeper of stock or cattle; a herdsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vachery', 'n.', 'An inclosure for cows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vachery', 'n.', 'A dairy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacillancy', 'n.', 'The quality or state of being vacillant, or wavering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacillant', 'a.', 'Vacillating; wavering; fluctuating; irresolute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacillated', 'imp. & p. p.', 'of Vacillate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacillating', 'p. pr. & vb. n.', 'of Vacillate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacillate', 'v. t.', 'To move one way and the other; to reel or stagger; to\n   waver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacillate', 'v. t.', 'To fluctuate in mind or opinion; to be unsteady or\n   inconstant; to waver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacillating', 'a.', 'Inclined to fluctuate; wavering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacillation', 'n.', 'The act of vacillating; a moving one way and the\n   other; a wavering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacillatory', 'a.', 'Inclined to vacillate; wavering; irresolute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacuate', 'v. t.', 'To make void, or empty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacuation', 'n.', 'The act of emptying; evacuation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacuist', 'n.', 'One who holds the doctrine that the space between the\n   bodies of the universe, or the molecules and atoms of matter., is a\n   vacuum; -- opposed to plenist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacuity', 'n.', 'The quality or state of being vacuous, or not filled;\n   emptiness; vacancy; as, vacuity of mind; vacuity of countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacuity', 'n.', 'Space unfilled or unoccupied, or occupied with an\n   invisible fluid only; emptiness; void; vacuum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacuity', 'n.', 'Want of reality; inanity; nihility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacuna', 'n.', 'The goddess of rural leisure, to whom the husbandmen\n   sacrificed at the close of the harvest. She was especially honored by\n   the Sabines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacuolated', 'a.', 'Full of vacuoles, or small air cavities; as, vacuolated\n   cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacuolation', 'n.', 'Formation into, or multiplication of, vacuoles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacuole', 'n.', 'A small air cell, or globular space, in the interior of\n   organic cells, either containing air, or a pellucid watery liquid, or\n   some special chemical secretions of the cell protoplasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacuous', 'a.', 'Empty; unfilled; void; vacant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacuousness', 'n.', 'The quality or state of being vacuous; emptiness;\n   vacuity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacuums', 'pl. ', 'of Vacuum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacua', 'pl. ', 'of Vacuum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacuum', 'n.', 'A space entirely devoid of matter (called also, by way of\n   distinction, absolute vacuum); hence, in a more general sense, a space,\n   as the interior of a closed vessel, which has been exhausted to a high\n   or the highest degree by an air pump or other artificial means; as,\n   water boils at a reduced temperature in a vacuum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vacuum', 'n.', 'The condition of rarefaction, or reduction of pressure\n   below that of the atmosphere, in a vessel, as the condenser of a steam\n   engine, which is nearly exhausted of air or steam, etc.; as, a vacuum\n   of 26 inches of mercury, or 13 pounds per square inch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vadantes', 'n. pl.', 'An extensive artificial group of birds including the\n   wading, swimming, and cursorial birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vade', 'v. i.', 'To fade; hence, to vanish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vade mecum', '', 'A book or other thing that a person carries with him as a\n   constant companion; a manual; a handbook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vadimony', 'n.', 'A bond or pledge for appearance before a judge on a\n   certain day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vadium', 'n.', 'Pledge; security; bail. See Mortgage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vae', 'n.', 'See Voe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vafrous', 'a.', 'Crafty; cunning; sly; as, vafrous tricks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagabond', 'a.', 'Moving from place to place without a settled habitation;\n   wandering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagabond', 'a.', 'Floating about without any certain direction; driven to\n   and fro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagabond', 'a.', 'Being a vagabond; strolling and idle or vicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagabond', 'n.', 'One who wanders from place to place, having no fixed\n   dwelling, or not abiding in it, and usually without the means of honest\n   livelihood; a vagrant; a tramp; hence, a worthless person; a rascal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagabond', 'v. i.', 'To play the vagabond; to wander like a vagabond; to\n   stroll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagabondage', 'n.', 'The condition of a vagabond; a state or habit of\n   wandering about in idleness; vagrancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagabondism', 'n.', 'Vagabondage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagabondize', 'v. i.', 'To play the vagabond; to wander about in idleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagabondry', 'n.', 'Vagabondage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagal', 'a.', 'Of or pertaining to the vagus, or pneumogastric nerves;\n   pneumogastric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagancy', 'n.', 'A wandering; vagrancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagantes', 'p. pl.', 'A tribe of spiders, comprising some of those which\n   take their prey in a web, but which also frequently run with agility,\n   and chase and seize their prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagarious', 'a.', 'Given to, or characterized by, vagaries; capricious;\n   whimsical; crochety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagaries', 'pl. ', 'of Vagary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagary', 'n.', 'A wandering or strolling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagary', 'n.', 'Hence, a wandering of the thoughts; a wild or fanciful\n   freak; a whim; a whimsical purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagient', 'a.', 'Crying like a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaginae', 'pl. ', 'of Vagina');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagina', 'n.', 'A sheath; a theca; as, the vagina of the portal vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagina', 'n.', 'Specifically, the canal which leads from the uterus to the\n   external orifice if the genital canal, or to the cloaca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagina', 'n.', 'The terminal part of the oviduct in insects and various\n   other invertebrates. See Illust., of Spermatheca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagina', 'n.', 'The basal expansion of certain leaves, which inwraps the\n   stem; a sheath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagina', 'n.', 'The shaft of a terminus, from which the bust of figure\n   seems to issue or arise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaginal', 'a.', 'Of or pertaining to a vagina; resembling a vagina, or\n   sheath; thecal; as, a vaginal synovial membrane; the vaginal process of\n   the temporal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaginal', 'a.', 'Of or pertaining to the vagina of the genital canal; as,\n   the vaginal artery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaginant', 'a.', 'Serving to in invest, or sheathe; sheathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaginate', 'a.', 'Alt. of Vaginated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaginated', 'a.', 'Invested with, or as if with, a sheath; as, a vaginate\n   stem, or one invested by the tubular base of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaginati', 'n. pl.', 'A tribe of birds comprising the sheathbills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaginervose', 'a.', 'Having the nerves, or veins, placed in apparent\n   disorder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaginicola', 'n.', 'A genus of Infusoria which form minute vaselike or\n   tubular cases in which they dwell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaginismus', 'n.', 'A painful spasmodic contraction of the vagina, often\n   rendering copulation impossible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaginitis', 'n.', 'Inflammation of the vagina, or the genital canal,\n   usually of its mucous living membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaginopennous', 'a.', 'Having elytra; sheath-winged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaginula', 'n.', 'A little sheath, as that about the base of the pedicel of\n   most mosses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaginula', 'n.', 'One of the tubular florets in composite flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaginule', 'n.', 'A vaginula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagissate', 'v. i.', 'To caper or frolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagous', 'a.', 'Wandering; unsettled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagrancy', 'n.', 'The quality or state of being a vagrant; a wandering\n   without a settled home; an unsettled condition; vagabondism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagrant', 'a.', 'Moving without certain direction; wandering; erratic;\n   unsettled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagrant', 'a.', 'Wandering from place to place without any settled\n   habitation; as, a vagrant beggar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagrant', 'n.', 'One who strolls from place to place; one who has no\n   settled habitation; an idle wanderer; a sturdy beggar; an incorrigible\n   rogue; a vagabond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagrantly', 'adv.', 'In a vagrant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagrantness', 'n.', 'State of being vagrant; vagrancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vague', 'v. i.', 'Wandering; vagrant; vagabond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vague', 'v. i.', 'Unsettled; unfixed; undetermined; indefinite; ambiguous;\n   as, a vague idea; a vague proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vague', 'v. i.', 'Proceeding from no known authority; unauthenticated;\n   uncertain; flying; as, a vague report.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vague', 'n.', 'An indefinite expanse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vague', 'v. i.', 'To wander; to roam; to stray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vague', 'n.', 'A wandering; a vagary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaguely', 'adv.', 'In a vague manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagueness', 'n.', 'The quality or state of being vague.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagus', 'a.', 'Wandering; -- applied especially to the pneumogastric nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vagus', 'n.', 'The vagus, ore pneumogastric, nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vail', 'n. & v. t.', 'Same as Veil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vail', 'n.', 'Avails; profit; return; proceeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vail', 'n.', 'An unexpected gain or acquisition; a casual advantage or\n   benefit; a windfall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vail', 'n.', 'Money given to servants by visitors; a gratuity; -- usually\n   in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vail', 'v. t.', 'To let fail; to allow or cause to sink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vail', 'v. t.', 'To lower, or take off, in token of inferiority, reverence,\n   submission, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vail', 'v. i.', 'To yield or recede; to give place; to show respect by\n   yielding, uncovering, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vail', 'n.', 'Submission; decline; descent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vailer', 'n.', 'One who vails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaimure', 'n.', 'An outer, or exterior. wall. See Vauntmure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vain', 'superl.', 'Having no real substance, value, or importance; empty;\n   void; worthless; unsatisfying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vain', 'superl.', 'Destitute of forge or efficacy; effecting no purpose;\n   fruitless; ineffectual; as, vain toil; a vain attempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vain', 'superl.', 'Proud of petty things, or of trifling attainments;\n   having a high opinion of ones own accomplishments with slight reason;\n   conceited; puffed up; inflated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vain', 'superl.', 'Showy; ostentatious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vain', 'n.', 'Vanity; emptiness; -- now used only in the phrase in vain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vainglorious', 'a.', 'Feeling or indicating vainglory; elated by vanity;\n   boastful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vainglory', 'n.', 'Excessive vanity excited by ones own performances;\n   empty pride; undue elation of mind; vain show; boastfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vainly', 'adv.', 'In a vain manner; in vain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vainness', 'n.', 'The quality or state of being vain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vair', 'n.', 'The skin of the squirrel, much used in the fourteenth century\n   as fur for garments, and frequently mentioned by writers of that period\n   in describing the costly dresses of kings, nobles, and prelates. It is\n   represented in heraldry by a series of small shields placed close\n   together, and alternately white and blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vairy', 'n.', 'Charged with vair; variegated with shield-shaped figures.\n   See Vair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaishnava', 'n.', 'A worshiper of the god Vishnu in any of his\n   incarnations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaishnavism', 'n.', 'The worship of Vishnu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaisya', 'n.', 'The third of the four great original castes among the\n   Hindus, now either extinct or partially represented by the mercantile\n   class of Banyas. See the Note under Caste, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaivode', 'n.', 'See Waywode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vakeel', 'n.', 'A native attorney or agent; also, an ambassador.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valance', 'n.', 'Hanging drapery for a bed, couch, window, or the like,\n   especially that which hangs around a bedstead, from the bed to the\n   floor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valance', 'n.', 'The drooping edging of the lid of a trunk. which covers\n   the joint when the lid is closed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valanced', 'imp. & p. p.', 'of Valance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valancing', 'p. pr. & vb. n.', 'of Valance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valance', 'v. t.', 'To furnish with a valance; to decorate with hangings or\n   drapery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vale', 'n.', 'A tract of low ground, or of land between hills; a valley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vale', 'n.', 'See 2d Vail, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valediction', 'n.', 'A farewell; a bidding farewell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valedictorian', 'n.', 'One who pronounces a valedictory address;\n   especially, in American colleges, the student who pronounces the\n   valedictory of the graduating class at the annual commencement, usually\n   the student who ranks first in scholarship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valedictory', 'a.', 'Bidding farewell; suitable or designed for an occasion\n   of leave-taking; as, a valedictory oration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valedictories', 'pl. ', 'of Valedictory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valedictory', 'n.', 'A valedictory oration or address spoken at\n   commencement in American colleges or seminaries by one of the\n   graduating class, usually by the leading scholar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valence', 'n.', 'The degree of combining power of an atom (or radical) as\n   shown by the number of atoms of hydrogen (or of other monads, as\n   chlorine, sodium, etc.) with which it will combine, or for which it can\n   be substituted, or with which it can be compared; thus, an atom of\n   hydrogen is a monad, and has a valence of one; the atoms of oxygen,\n   nitrogen, and carbon are respectively dyads, triads, and tetrads, and\n   have a valence respectively of two, three, and four.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valencia', 'n.', 'A kind of woven fabric for waistcoats, having the weft of\n   wool and the warp of silk or cotton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valenciennes lace', '', 'A rich kind of lace made at Valenciennes, in\n   France. Each piece is made throughout, ground and pattern, by the same\n   person and with the same thread, the pattern being worked in the net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valencies', 'pl. ', 'of Valency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valency', 'n.', 'See Valence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valency', 'n.', 'A unit of combining power; a so-called bond of affinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valentia', 'n.', 'See Valencia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valentine', 'n.', 'A sweetheart chosen on St. Valentines Day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valentine', 'n.', 'A letter containing professions of love, or a missive of\n   a sentimental, comic, or burlesque character, sent on St. Valentines\n   Day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valentinian', 'n.', 'One of a school of Judaizing Gnostics in the second\n   century; -- so called from Valentinus, the founder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valeramide', 'n.', 'The acid amide derivative of valeric acid, obtained as\n   a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valerate', 'n.', 'A salt of valeric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valerian', 'n.', 'Any plant of the genus Valeriana. The root of the\n   officinal valerian (V. officinalis) has a strong smell, and is much\n   used in medicine as an antispasmodic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valerianaceous', 'a.', 'Of, pertaining to, or resembling, plants of a\n   natural order (Valerianaccae) of which the valerian is the type. The\n   order includes also the corn salads and the oriental spikenard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valerianate', 'n.', 'A valerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valerianic', 'a.', 'Performance to, or obtained from, valerian root;\n   specifically, designating an acid which is usually called valeric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valeric', 'a.', 'Valerianic; specifically, designating any one of three\n   metameric acids, of which the typical one (called also inactive valeric\n   acid), C4H9CO2H, is obtained from valerian root and other sources, as a\n   corrosive, mobile, oily liquid, having a strong acid taste, and an odor\n   of old cheese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valeridine', 'n.', 'A base, C10H19N, produced by heating valeric aldehyde\n   with ammonia. It is probably related to the conine alkaloids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valerin', 'n.', 'A salt of valeric acid with glycerin, occurring in butter,\n   dolphin oil., and forming an forming an oily liquid with a slightly\n   unpleasant odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valeritrine', 'n.', 'A base, C15H27N, produced together with valeridine,\n   which it resembles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valero-', '', 'A combining form (also used adjectively) indicating\n   derivation from, or relation to, valerian or some of its products, as\n   valeric acid; as in valerolactone, a colorless oily liquid produced as\n   the anhydride of an hydroxy valeric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valerone', 'n.', 'A ketone of valeric acid obtained as an oily liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valeryl', 'n.', 'The hypothetical radical C5H9O, regarded as the essential\n   nucleus of certain valeric acid derivatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valerylene', 'n.', 'A liquid hydrocarbon, C5H8; -- called also pentine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valet', 'n.', 'A male waiting servant; a servant who attends on gentlemans\n   person; a body servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valet', 'n.', 'A kind of goad or stick with a point of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valetudinarian', 'a.', 'Of infirm health; seeking to recover health;\n   sickly; weakly; infirm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valetudinarian', 'n.', 'A person of a weak or sickly constitution; one who\n   is seeking to recover health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valetudinarianism', 'n.', 'The condition of a valetudinarian; a state of\n   feeble health; infirmity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valetudinary', 'a.', 'Infirm; sickly; valetudinarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valetudinary', 'n.', 'A valetudinarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valetudinous', 'a.', 'Valetudinarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valhalla', 'n.', 'The palace of immortality, inhabited by the souls of\n   heroes slain in battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valhalla', 'n.', 'Fig.: A hall or temple adorned with statues and memorials\n   of a nations heroes; specifically, the Pantheon near Ratisbon, in\n   Bavaria, consecrated to the illustrious dead of all Germany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valiance', 'n.', 'Alt. of Valiancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valiancy', 'n.', 'The quality or state of being valiant; bravery; valor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valiant', 'a.', 'Vigorous in body; strong; powerful; as, a valiant fencer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valiant', 'a.', 'Intrepid in danger; courageous; brave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valiant', 'a.', 'Performed with valor or bravery; heroic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valid', 'a.', 'Strong; powerful; efficient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valid', 'a.', 'Having sufficient strength or force; founded in truth;\n   capable of being justified, defended, or supported; not weak or\n   defective; sound; good; efficacious; as, a valid argument; a valid\n   objection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valid', 'a.', 'Having legal strength or force; executed with the proper\n   formalities; incapable of being rightfully overthrown or set aside; as,\n   a valid deed; a valid covenant; a valid instrument of any kind; a valid\n   claim or title; a valid marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Validate', 'v. t.', 'To confirm; to render valid; to give legal force to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Validation', 'n.', 'The act of giving validity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Validity', 'n.', 'The quality or state of being valid; strength; force;\n   especially, power to convince; justness; soundness; as, the validity of\n   an argument or proof; the validity of an objection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Validity', 'n.', 'Legal strength, force, or authority; that quality of a\n   thing which renders it supportable in law, or equity; as, the validity\n   of a will; the validity of a contract, claim, or title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Validity', 'n.', 'Value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Validly', 'adv.', 'In a valid manner; so as to be valid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Validness', 'n.', 'The quality or state of being valid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valinch', 'n.', 'A tube for drawing liquors from a cask by the bunghole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valise', 'n.', 'A small sack or case, usually of leather, but sometimes of\n   other material, for containing the clothes, toilet articles, etc., of a\n   traveler; a traveling bag; a portmanteau.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valkyria', 'n.', 'One of the maidens of Odin, represented as awful and\n   beautiful, who presided over battle and marked out those who were to be\n   slain, and who also ministered at the feasts of heroes in Valhalla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valkyrian', 'a.', 'Of or pertaining to the Valkyrias; hence, relating to\n   battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vallancy', 'n.', 'A large wig that shades the face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vallar', 'a.', 'Of or pertaining to a rampart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vallar', 'n.', 'A vallar crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vallary', 'a.', 'Same as Vallar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vallation', 'n.', 'A rampart or intrenchment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vallatory', 'a.', 'Of or pertaining to a vallation; used for a vallation;\n   as, vallatory reads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valleculae', 'pl. ', 'of Vallecula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vallecula', 'n.', 'A groove; a fossa; as, the vallecula, or fossa, which\n   separates the hemispheres of the cerebellum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vallecula', 'n.', 'One of the grooves, or hollows, between the ribs of the\n   fruit of umbelliferous plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vallets pills', '', 'Pills containing sulphate of iron and carbonate of\n   sodium, mixed with saccharine matter; -- called also Vallets mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valleys', 'pl. ', 'of Valley');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valley', 'n.', 'The space inclosed between ranges of hills or mountains;\n   the strip of land at the bottom of the depressions intersecting a\n   country, including usually the bed of a stream, with frequently broad\n   alluvial plains on one or both sides of the stream. Also used\n   figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valley', 'n.', 'The place of meeting of two slopes of a roof, which have\n   their plates running in different directions, and form on the plan a\n   reentrant angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valley', 'n.', 'The depression formed by the meeting of two slopes on a\n   flat roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valla', 'pl. ', 'of Vallum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vallums', 'pl. ', 'of Vallum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vallum', 'n.', 'A rampart; a wall, as in a fortification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valonia', 'n.', 'The acorn cup of two kinds of oak (Quercus macrolepis, and\n   Q. vallonea) found in Eastern Europe. It contains abundance of tannin,\n   and is much used by tanners and dyers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valonia', 'n.', 'A genus of marine green algae, in which the whole frond\n   consists of a single oval or cylindrical cell, often an inch in length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valor', 'n.', 'Value; worth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valor', 'n.', 'Strength of mind in regard to danger; that quality which\n   enables a man to encounter danger with firmness; personal bravery;\n   courage; prowess; intrepidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valor', 'n.', 'A brave man; a man of valor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valorous', 'a.', 'Possessing or exhibiting valor; brave; courageous;\n   valiant; intrepid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valsalvian', 'a.', 'Of or pertaining to Valsalva, an Italian anatomist of\n   the 17th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valuable', 'a.', 'Having value or worth; possessing qualities which are\n   useful and esteemed; precious; costly; as, a valuable horse; valuable\n   land; a valuable cargo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valuable', 'a.', 'Worthy; estimable; deserving esteem; as, a valuable\n   friend; a valuable companion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valuable', 'n.', 'A precious possession; a thing of value, especially a\n   small thing, as an article of jewelry; -- used mostly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valuableness', 'n.', 'The quality of being valuable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valuably', 'adv.', 'So as to be of value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valuation', 'n.', 'The act of valuing, or of estimating value or worth; the\n   act of setting a price; estimation; appraisement; as, a valuation of\n   lands for the purpose of taxation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valuation', 'n.', 'Value set upon a thing; estimated value or worth; as,\n   the goods sold for more than their valuation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valuator', 'n.', 'One who assesses, or sets a value on, anything; an\n   appraiser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Value', 'n.', 'The property or aggregate properties of a thing by which it\n   is rendered useful or desirable, or the degree of such property or sum\n   of properties; worth; excellence; utility; importance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Value', 'n.', 'Worth estimated by any standard of purchasing power,\n   especially by the market price, or the amount of money agreed upon as\n   an equivalent to the utility and cost of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Value', 'n.', 'Precise signification; import; as, the value of a word; the\n   value of a legal instrument');
  INSERT INTO words (word, word_type, description) VALUES  (    'Value', 'n.', 'Esteem; regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Value', 'n.', 'The relative length or duration of a tone or note, answering\n   to quantity in prosody; thus, a quarter note [/] has the value of two\n   eighth notes [/].');
  INSERT INTO words (word, word_type, description) VALUES  (    'Value', 'n.', 'In an artistical composition, the character of any one part\n   in its relation to other parts and to the whole; -- often used in the\n   plural; as, the values are well given, or well maintained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Value', 'n.', 'Valor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valued', 'imp. & p. p.', 'of Value');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valuing', 'p. pr. & vb. n.', 'of Value');
  INSERT INTO words (word, word_type, description) VALUES  (    'Value', 'v. t.', 'To estimate the value, or worth, of; to rate at a certain\n   price; to appraise; to reckon with respect to number, power,\n   importance, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Value', 'v. t.', 'To rate highly; to have in high esteem; to hold in\n   respect and estimation; to appreciate; to prize; as, to value one for\n   his works or his virtues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Value', 'v. t.', 'To raise to estimation; to cause to have value, either\n   real or apparent; to enhance in value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Value', 'v. t.', 'To be worth; to be equal to in value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valued', 'a.', 'Highly regarded; esteemed; prized; as, a valued\n   contributor; a valued friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valueless', 'a.', 'Being of no value; having no worth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valuer', 'n.', 'One who values; an appraiser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valure', 'n.', 'Value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valvasor', 'n.', 'See Vavasor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valvata', 'n.', 'A genus of small spiral fresh-water gastropods having an\n   operculum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valvate', 'a.', 'Resembling, or serving as, a valve; consisting of, or\n   opening by, a valve or valves; valvular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valvate', 'a.', 'Meeting at the edges without overlapping; -- said of the\n   sepals or the petals of flowers in aestivation, and of leaves in\n   vernation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valvate', 'a.', 'Opening as if by doors or valves, as most kinds of\n   capsules and some anthers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valve', 'n.', 'A door; especially, one of a pair of folding doors, or one\n   of the leaves of such a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valve', 'n.', 'A lid, plug, or cover, applied to an aperture so that by its\n   movement, as by swinging, lifting and falling, sliding, turning, or the\n   like, it will open or close the aperture to permit or prevent passage,\n   as of a fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valve', 'n.', 'One or more membranous partitions, flaps, or folds, which\n   permit the passage of the contents of a vessel or cavity in one\n   direction, but stop or retard the flow in the opposite direction; as,\n   the ileocolic, mitral, and semilunar valves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valve', 'n.', 'One of the pieces into which a capsule naturally separates\n   when it bursts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valve', 'n.', 'One of the two similar portions of the shell of a diatom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valve', 'n.', 'A small portion of certain anthers, which opens like a\n   trapdoor to allow the pollen to escape, as in the barberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valve', 'n.', 'One of the pieces or divisions of bivalve or multivalve\n   shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valved', 'a.', 'Having a valve or valve; valvate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valvelet', 'n.', 'A little valve; a valvule; especially, one of the pieces\n   which compose the outer covering of a pericarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valve-shell', 'n.', 'Any fresh-water gastropod of the genus Valvata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valvulae', 'pl. ', 'of Valvula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valvula', 'n.', 'A little valve or fold; a valvelet; a valvule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valvular', 'a.', 'Of or pertaining to a valve or valves; specifically\n   (Med.), of or pertaining to the valves of the heart; as, valvular\n   disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valvular', 'a.', 'Containing valves; serving as a valve; opening by valves;\n   valvate; as, a valvular capsule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valvule', 'n.', 'A little valve; a valvelet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valvule', 'n.', 'A small valvelike process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Valylene', 'n.', 'A volatile liquid hydrocarbon, C5H6, related to ethylene\n   and acetylene, but possessing the property of unsaturation in the third\n   degree. It is the only known member of a distinct series of compounds.\n   It has a garlic odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vambrace', 'n.', 'The piece designed to protect the arm from the elbow to\n   the wrist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vamose', 'v. i. & t.', 'To depart quickly; to depart from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vamp', 'v. i.', 'To advance; to travel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vamp', 'n.', 'The part of a boot or shoe above the sole and welt, and in\n   front of the ankle seam; an upper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vamp', 'n.', 'Any piece added to an old thing to give it a new appearance.\n   See Vamp, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vamped', 'imp. & p. p.', 'of Vamp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vamping', 'p. pr. & vb. n.', 'of Vamp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vamp', 'v. t.', 'To provide, as a shoe, with new upper leather; hence, to\n   piece, as any old thing, with a new part; to repair; to patch; -- often\n   followed by up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vamper', 'n.', 'One who vamps; one who pieces an old thing with something\n   new; a cobbler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vamper', 'v. i.', 'To swagger; to make an ostentatious show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vampire', 'n.', 'A blood-sucking ghost; a soul of a dead person\n   superstitiously believed to come from the grave and wander about by\n   night sucking the blood of persons asleep, thus causing their death.\n   This superstition is now prevalent in parts of Eastern Europe, and was\n   especially current in Hungary about the year 1730.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vampire', 'n.', 'Fig.: One who lives by preying on others; an extortioner;\n   a bloodsucker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vampire', 'n.', 'Either one of two or more species of South American\n   blood-sucking bats belonging to the genera Desmodus and Diphylla. These\n   bats are destitute of molar teeth, but have strong, sharp cutting\n   incisors with which they make punctured wounds from which they suck the\n   blood of horses, cattle, and other animals, as well as man, chiefly\n   during sleep. They have a caecal appendage to the stomach, in which the\n   blood with which they gorge themselves is stored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vampire', 'n.', 'Any one of several species of harmless tropical American\n   bats of the genus Vampyrus, especially V. spectrum. These bats feed\n   upon insects and fruit, but were formerly erroneously supposed to suck\n   the blood of man and animals. Called also false vampire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vampirism', 'n.', 'Belief in the existence of vampires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vampirism', 'n.', 'The actions of a vampire; the practice of bloodsucking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vampirism', 'n.', 'Fig.: The practice of extortion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vamplate', 'n.', 'A round of iron on the shaft of a tilting spear, to\n   protect the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vamure', 'n.', 'See Vauntmure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Van', 'n.', 'The front of an army; the first line or leading column; also,\n   the front line or foremost division of a fleet, either in sailing or in\n   battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Van', 'n.', 'A shovel used in cleansing ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Van', 'v. t.', 'To wash or cleanse, as a small portion of ore, on a shovel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Van', 'n.', 'A light wagon, either covered or open, used by tradesmen and\n   others fore the transportation of goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Van', 'n.', 'A large covered wagon for moving furniture, etc., also for\n   conveying wild beasts, etc., for exhibition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Van', 'n.', 'A close railway car for baggage. See the Note under Car, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Van', 'n.', 'A fan or other contrivance, as a sieve, for winnowing grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Van', 'n.', 'A wing with which the air is beaten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Van', 'v. t.', 'To fan, or to cleanse by fanning; to winnow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanadate', 'n.', 'A salt of vanadic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanadic', 'a.', 'Pertaining to, or obtained from, vanadium; containing\n   vanadium; specifically distinguished those compounds in which vanadium\n   has a relatively higher valence as contrasted with the vanadious\n   compounds; as, vanadic oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanadinite', 'n.', 'A mineral occurring in yellowish, and ruby-red\n   hexagonal crystals. It consist of lead vanadate with a small proportion\n   of lead chloride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanadious', 'a.', 'Pertaining to, or containing, vanadium; specifically,\n   designating those compounds in which vanadium has a lower valence as\n   contrasted with the vanadic compounds; as, vanadious acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanadite', 'n.', 'A salt of vanadious acid, analogous to a nitrite or a\n   phosphite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanadium', 'n.', 'A rare element of the nitrogen-phosphorus group, found\n   combined, in vanadates, in certain minerals, and reduced as an\n   infusible, grayish-white metallic powder. It is intermediate between\n   the metals and the non-metals, having both basic and acid properties.\n   Symbol V (or Vd, rarely). Atomic weight 51.2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanadous', 'a.', 'Of or pertaining to vanadium; obtained from vanadium; --\n   said of an acid containing one equivalent of vanadium and two of\n   oxygen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanadyl', 'n.', 'The hypothetical radical VO, regarded as a characterized\n   residue of certain vanadium compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Van-courier', 'n.', 'One sent in advance; an avant-courier; a precursor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vandal', 'n.', 'One of a Teutonic race, formerly dwelling on the south\n   shore of the Baltic, the most barbarous and fierce of the northern\n   nations that plundered Rome in the 5th century, notorious for\n   destroying the monuments of art and literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vandal', 'n.', 'Hence, one who willfully destroys or defaces any work of\n   art or literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vandal', 'a.', 'Alt. of Vandalic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vandalic', 'a.', 'Of or pertaining to the Vandals; resembling the Vandals\n   in barbarism and destructiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vandalism', 'n.', 'The spirit or conduct of the Vandals; ferocious cruelty;\n   hostility to the arts and literature, or willful destruction or\n   defacement of their monuments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vandyke', 'a.', 'Of or pertaining to the style of Vandyke the painter; used\n   or represented by Vandyke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vandyke', 'n.', 'A picture by Vandyke. Also, a Vandyke collar, or a Vandyke\n   edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vandyke', 'v. t.', 'fit or furnish with a Vandyke; to form with points or\n   scallops like a Vandyke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vane', 'n.', 'A contrivance attached to some elevated object for the\n   purpose of showing which way the wind blows; a weathercock. It is\n   usually a plate or strip of metal, or slip of wood, often cut into some\n   fanciful form, and placed upon a perpendicular axis around which it\n   moves freely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vane', 'n.', 'Any flat, extended surface attached to an axis and moved by\n   the wind; as, the vane of a windmill; hence, a similar fixture of any\n   form moved in or by water, air, or other fluid; as, the vane of a screw\n   propeller, a fan blower, an anemometer, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vane', 'n.', 'The rhachis and web of a feather taken together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vane', 'n.', 'One of the sights of a compass, quadrant, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanessa', 'n.', 'Any one of numerous species of handsomely colored\n   butterflies belonging to Vanessa and allied genera. Many of these\n   species have the edges of the wings irregularly scalloped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanessian', 'n.', 'A vanessa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanfess', 'n.', 'A ditch on the outside of the counterscarp, usually full\n   of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vang', 'n.', 'A rope to steady the peak of a gaff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanglo', 'n.', 'Benne (Sesamum orientale); also, its seeds; -- so called in\n   the West Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanguard', 'n.', 'The troops who march in front of an army; the advance\n   guard; the van.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanilla', 'n.', 'A genus of climbing orchidaceous plants, natives of\n   tropical America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanilla', 'n.', 'The long podlike capsules of Vanilla planifolia, and V.\n   claviculata, remarkable for their delicate and agreeable odor, for the\n   volatile, odoriferous oil extracted from them; also, the flavoring\n   extract made from the capsules, extensively used in confectionery,\n   perfumery, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanillate', 'n.', 'A salt of vanillic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanillic', 'a.', 'Pertaining to, or derived from, vanilla or vanillin;\n   resembling vanillin; specifically, designating an alcohol and an acid\n   respectively, vanillin being the intermediate aldehyde.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanillin', 'n.', 'A white crystalline aldehyde having a burning taste and\n   characteristic odor of vanilla. It is extracted from vanilla pods, and\n   is also obtained by the decomposition of coniferin, and by the\n   oxidation of eugenol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanilloes', 'n. pl.', 'An inferior kind of vanilla, the pods of Vanilla\n   Pompona.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanillyl', 'n.', 'The hypothetical radical characteristic of vanillic\n   alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaniloquence', 'n.', 'Vain or foolish talk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanished', 'imp. & p. p.', 'of Vanish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanishing', 'p. pr. & vb. n.', 'of Vanish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanish', 'v. i.', 'To pass from a visible to an invisible state; to go out\n   of sight; to disappear; to fade; as, vapor vanishes from the sight by\n   being dissipated; a ship vanishes from the sight of spectators on land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanish', 'v. i.', 'To be annihilated or lost; to pass away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanish', 'n.', 'The brief terminal part of vowel or vocal element,\n   differing more or less in quality from the main part; as, a as in ale\n   ordinarily ends with a vanish of i as in ill, o as in old with a vanish\n   of oo as in foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanishing', '', 'a. & n. from Vanish, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanishment', 'n.', 'A vanishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanities', 'pl. ', 'of Vanity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanity', 'n.', 'The quality or state of being vain; want of substance to\n   satisfy desire; emptiness; unsubstantialness; unrealness; falsity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanity', 'n.', 'An inflation of mind upon slight grounds; empty pride\n   inspired by an overweening conceit of ones personal attainments or\n   decorations; an excessive desire for notice or approval; pride;\n   ostentation; conceit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanity', 'n.', 'That which is vain; anything empty, visionary, unreal, or\n   unsubstantial; fruitless desire or effort; trifling labor productive of\n   no good; empty pleasure; vain pursuit; idle show; unsubstantial\n   enjoyment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanity', 'n.', 'One of the established characters in the old moralities and\n   puppet shows. See Morality, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanjas', 'n.', 'The Australian pied crow shrike (Strepera graculina). It is\n   glossy bluish black, with the under tail coverts and the tips and bases\n   of the tail feathers white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanner', 'n.', 'A machine for concentrating ore. See Frue vanner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanner hawk', '', 'The kestrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanning', 'n.', 'A process by which ores are washed on a shovel, or in a\n   vanner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanquished', 'imp. & p. p.', 'of Vanquish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanquishing', 'p. pr. & vb. n.', 'of Vanquish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanquish', 'v. t.', 'To conquer, overcome, or subdue in battle, as an\n   enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanquish', 'v. t.', 'Hence, to defeat in any contest; to get the better of;\n   to put down; to refute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanquish', 'n.', 'A disease in sheep, in which they pine away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanquishable', 'a.', 'That may be vanquished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanquisher', 'n.', 'One who, or that which, vanquishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanquishment', 'n.', 'The act of vanquishing, or the state of being\n   vanquished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vansire', 'n.', 'An ichneumon (Herpestes galera) native of Southern Africa\n   and Madagascar. It is reddish brown or dark brown, grizzled with white.\n   Called also vondsira, and marsh ichneumon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vant', 'v. i.', 'See Vaunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vantage', 'n.', 'superior or more favorable situation or opportunity; gain;\n   profit; advantage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vantage', 'n.', 'The first point after deuce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vantage', 'v. t.', 'To profit; to aid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vantbrace', 'n.', 'Alt. of Vantbrass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vantbrass', 'n.', 'Armor for the arm; vambrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vant-courier', 'n.', 'An avant-courier. See Van-courier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vanward', 'a.', 'Being on, or towards, the van, or front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vap', 'n.', 'That which is vapid, insipid, or lifeless; especially, the\n   lifeless part of liquor or wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vapid', 'a.', 'Having lost its life and spirit; dead; spiritless; insipid;\n   flat; dull; unanimated; as, vapid beer; a vapid speech; a vapid state\n   of the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vapidity', 'n.', 'The quality or state of being vapid; vapidness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vapor', 'n.', 'Any substance in the gaseous, or aeriform, state, the\n   condition of which is ordinarily that of a liquid or solid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vapor', 'n.', 'In a loose and popular sense, any visible diffused substance\n   floating in the atmosphere and impairing its transparency, as smoke,\n   fog, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vapor', 'n.', 'Wind; flatulence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vapor', 'n.', 'Something unsubstantial, fleeting, or transitory; unreal\n   fancy; vain imagination; idle talk; boasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vapor', 'n.', 'An old name for hypochondria, or melancholy; the blues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vapor', 'n.', 'A medicinal agent designed for administration in the form of\n   inhaled vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vapored', 'imp. & p. p.', 'of Vapor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporing', 'p. pr. & vb. n.', 'of Vapor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vapor', 'n.', 'To pass off in fumes, or as a moist, floating substance,\n   whether visible or invisible, to steam; to be exhaled; to evaporate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vapor', 'n.', 'To emit vapor or fumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vapor', 'n.', 'To talk idly; to boast or vaunt; to brag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vapor', 'v. t.', 'To send off in vapor, or as if in vapor; as, to vapor\n   away a heated fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporability', 'n.', 'The quality or state of being vaporable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporable', 'a.', 'Capable of being converted into vapor by the agency of\n   heat; vaporizable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporate', 'v. i.', 'To emit vapor; to evaporate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporation', 'n.', 'The act or process of converting into vapor, or of\n   passing off in vapor; evaporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vapored', 'a.', 'Wet with vapors; moist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vapored', 'a.', 'Affected with the vapors. See Vapor, n., 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporer', 'n.', 'One who vapors; a braggart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporiferous', 'a.', 'Conveying or producing vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporific', 'a.', 'Producing vapor; tending to pass, or to cause to pass,\n   into vapor; thus, volatile fluids are vaporific; heat is a vaporific\n   agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporiform', 'a.', 'Existing in a vaporous form or state; as, steam is a\n   vaporiform substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporimeter', 'n.', 'An instrument for measuring the volume or the tension\n   of any vapor; specifically, an instrument of this sort used as an\n   alcoholometer in testing spirituous liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporing', 'a.', 'Talking idly; boasting; vaunting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporish', 'a.', 'Full of vapors; vaporous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporish', 'a.', 'Hypochondriacal; affected by hysterics; splenetic;\n   peevish; humorsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporizable', 'a.', 'Capable of being vaporized into vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporization', 'n.', 'The act or process of vaporizing, or the state of\n   being converted into vapor; the artificial formation of vapor;\n   specifically, the conversion of water into steam, as in a steam boiler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporized', 'imp. & p. p.', 'of Vaporize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporizing', 'p. pr. & vb. n.', 'of Vaporize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporize', 'v. t.', 'To convert into vapor, as by the application of heat,\n   whether naturally or artificially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporize', 'v. i.', 'To pass off in vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporizer', 'n.', 'One who, or that which, vaporizes, or converts into\n   vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporose', 'a.', 'Full of vapor; vaporous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporous', 'a.', 'Having the form or nature of vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporous', 'a.', 'Full of vapors or exhalations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporous', 'a.', 'Producing vapors; hence, windy; flatulent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporous', 'a.', 'Unreal; unsubstantial; vain; whimsical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaporousness', 'n.', 'The quality of being vaporous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vapory', 'a.', 'Full of vapors; vaporous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vapory', 'a.', 'Hypochondriacal; splenetic; peevish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vapulation', 'n.', 'The act of beating or whipping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaquero', 'n.', 'One who has charge of cattle, horses, etc.; a herdsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vara', 'n.', 'A Spanish measure of length equal to about one yard. The vara\n   now in use equals 33.385 inches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varan', 'n.', 'The monitor. See Monitor, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varangian', 'n.', 'One of the Northmen who founded a dynasty in Russia in\n   the 9th century; also, one of the Northmen composing, at a later date,\n   the imperial bodyguard at Constantinople.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varanus', 'n.', 'A genus of very large lizards native of Asia and Africa.\n   It includes the monitors. See Monitor, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vare', 'n.', 'A wand or staff of authority or justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vare', 'n.', 'A weasel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varec', 'n.', 'The calcined ashes of any coarse seaweed used for the\n   manufacture of soda and iodine; also, the seaweed itself; fucus; wrack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vari', 'n.', 'The ringtailed lemur (Lemur catta) of Madagascar. Its long\n   tail is annulated with black and white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variability', 'n.', 'The quality or state of being variable; variableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variability', 'n.', 'The power possessed by living organisms, both animal\n   and vegetable, of adapting themselves to modifications or changes in\n   their environment, thus possibly giving rise to ultimate variation of\n   structure or function.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variable', 'a.', 'Having the capacity of varying or changing; capable of\n   alternation in any manner; changeable; as, variable winds or seasons; a\n   variable quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variable', 'a.', 'Liable to vary; too susceptible of change; mutable;\n   fickle; unsteady; inconstant; as, the affections of men are variable;\n   passions are variable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variable', 'n.', 'That which is variable; that which varies, or is subject\n   to change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variable', 'n.', 'A quantity which may increase or decrease; a quantity\n   which admits of an infinite number of values in the same expression; a\n   variable quantity; as, in the equation x2 - y2 = R2, x and y are\n   variables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variable', 'n.', 'A shifting wind, or one that varies in force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variable', 'n.', 'Those parts of the sea where a steady wind is not\n   expected, especially the parts between the trade-wind belts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variableness', 'n.', 'The quality or state of being variable; variability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variably', 'adv.', 'In a variable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variance', 'n.', 'The quality or state of being variant; change of\n   condition; variation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variance', 'n.', 'Difference that produce dispute or controversy;\n   disagreement; dissension; discord; dispute; quarrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variance', 'n.', 'A disagreement or difference between two parts of the\n   same legal proceeding, which, to be effectual, ought to agree, -- as\n   between the writ and the declaration, or between the allegation and the\n   proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variant', 'a.', 'Varying in from, character, or the like; variable;\n   different; diverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variant', 'a.', 'Changeable; changing; fickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variant', 'n.', 'Something which differs in form from another thing, though\n   really the same; as, a variant from a type in natural history; a\n   variant of a story or a word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variate', 'v. t. & i.', 'To alter; to make different; to vary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variation', 'n.', 'The act of varying; a partial change in the form,\n   position, state, or qualities of a thing; modification; alternation;\n   mutation; diversity; deviation; as, a variation of color in different\n   lights; a variation in size; variation of language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variation', 'n.', 'Extent to which a thing varies; amount of departure from\n   a position or state; amount or rate of change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variation', 'n.', 'Change of termination of words, as in declension,\n   conjugation, derivation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variation', 'n.', 'Repetition of a theme or melody with fanciful\n   embellishments or modifications, in time, tune, or harmony, or\n   sometimes change of key; the presentation of a musical thought in new\n   and varied aspects, yet so that the essential features of the original\n   shall still preserve their identity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variation', 'n.', 'One of the different arrangements which can be made of\n   any number of quantities taking a certain number of them together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varicella', 'n.', 'Chicken pox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varices', 'n. pl.', 'See Varix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variciform', 'a.', 'Resembling a varix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varicocele', 'n.', 'A varicose enlargement of the veins of the spermatic\n   cord; also, a like enlargement of the veins of the scrotum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varicose', 'a.', 'Irregularly swollen or enlarged; affected with, or\n   containing, varices, or varicosities; of or pertaining to varices, or\n   varicosities; as, a varicose nerve fiber; a varicose vein; varicose\n   ulcers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varicose', 'a.', 'Intended for the treatment of varicose veins; -- said of\n   elastic stockings, bandages. and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varicosity', 'n.', 'The quality or state of being varicose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varicosity', 'n.', 'An enlargement or swelling in a vessel, fiber, or the\n   like; a varix; as, the varicosities of nerve fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varicous', 'a.', 'Varicose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varied', 'a.', 'Changed; altered; various; diversified; as, a varied\n   experience; varied interests; varied scenery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variegated', 'imp. & p. p.', 'of Variegate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variegating', 'p. pr. & vb. n.', 'of Variegate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variegate', 'v. t.', 'To diversify in external appearance; to mark with\n   different colors; to dapple; to streak; as, to variegate a floor with\n   marble of different colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variegated', 'a.', 'Having marks or patches of different colors; as,\n   variegated leaves, or flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variegation', 'n.', 'The act of variegating or diversifying, or the state\n   of being diversified, by different colors; diversity of colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varier', 'n.', 'A wanderer; one who strays in search of variety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varietal', 'a.', 'Of or pertaining to a variety; characterizing a variety;\n   constituting a variety, in distinction from an individual or species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varietas', 'n.', 'A variety; -- used in giving scientific names, and often\n   abbreviated to var.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varieties', 'pl. ', 'of Variety');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variety', 'n.', 'The quality or state of being various; intermixture or\n   succession of different things; diversity; multifariousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variety', 'n.', 'That which is various.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variety', 'n.', 'A number or collection of different things; a varied\n   assortment; as, a variety of cottons and silks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variety', 'n.', 'Something varying or differing from others of the same\n   general kind; one of a number of things that are akin; a sort; as,\n   varieties of wood, land, rocks, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variety', 'n.', 'An individual, or group of individuals, of a species\n   differing from the rest in some one or more of the characteristics\n   typical of the species, and capable either of perpetuating itself for a\n   period, or of being perpetuated by artificial means; hence, a\n   subdivision, or peculiar form, of a species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variety', 'n.', 'In inorganic nature, one of those forms in which a species\n   may occur, which differ in minor characteristics of structure, color,\n   purity of composition, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variform', 'a.', 'Having different shapes or forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variformed', 'a.', 'Formed with different shapes; having various forms;\n   variform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varify', 'v. t.', 'To make different; to vary; to variegate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variola', 'n.', 'The smallpox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variolar', 'a.', 'Variolous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variolation', 'n.', 'Inoculation with smallpox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variolic', 'a.', 'Variolous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variolite', 'n.', 'A kind of diorite or diabase containing imbedded whitish\n   spherules, which give the rock a spotted appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variolitic', 'a.', 'Thickly marked with small, round specks; spotted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variolitic', 'a.', 'Of, pertaining to, or resembling, variolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varioloid', 'a.', 'Resembling smallpox; pertaining to the disease called\n   varioloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varioloid', 'a.', 'The smallpox as modified by previous inoculation or\n   vaccination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variolous', 'a.', 'Of or pertaining to the smallpox; having pits, or sunken\n   impressions, like those of the smallpox; variolar; variolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variorum', 'a.', 'Containing notes by different persons; -- applied to a\n   publication; as, a variorum edition of a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Various', 'a.', 'Different; diverse; several; manifold; as, men of various\n   names; various occupations; various colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Various', 'a.', 'Changeable; uncertain; inconstant; variable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Various', 'a.', 'Variegated; diversified; not monotonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variously', 'adv.', 'In various or different ways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Variscite', 'n.', 'An apple-green mineral occurring in reniform masses. It\n   is a hydrous phosphate of alumina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varisse', 'n.', 'An imperfection on the inside of the hind leg in horses,\n   different from a curb, but at the same height, and frequently injuring\n   the sale of the animal by growing to an unsightly size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varices', 'pl. ', 'of Varix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varix', 'n.', 'A uneven, permanent dilatation of a vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varix', 'n.', 'One of the prominent ridges or ribs extending across each of\n   the whorls of certain univalve shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vark', 'n.', 'The bush hog, or boshvark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varlet', 'n.', 'A servant, especially to a knight; an attendant; a valet; a\n   footman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varlet', 'n.', 'Hence, a low fellow; a scoundrel; a rascal; as, an impudent\n   varlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varlet', 'n.', 'In a pack of playing cards, the court card now called the\n   knave, or jack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varletry', 'n.', 'The rabble; the crowd; the mob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varnish', 'n.', 'A viscid liquid, consisting of a solution of resinous\n   matter in an oil or a volatile liquid, laid on work with a brush, or\n   otherwise. When applied the varnish soon dries, either by evaporation\n   or chemical action, and the resinous part forms thus a smooth, hard\n   surface, with a beautiful gloss, capable of resisting, to a greater or\n   less degree, the influences of air and moisture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varnish', 'n.', 'That which resembles varnish, either naturally or\n   artificially; a glossy appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varnish', 'n.', 'An artificial covering to give a fair appearance to any\n   act or conduct; outside show; gloss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varnished', 'imp. & p. p.', 'of Varnish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varnishing', 'p. pr. & vb. n.', 'of Varnish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varnish', 'n.', 'To lay varnish on; to cover with a liquid which produces,\n   when dry, a hard, glossy surface; as, to varnish a table; to varnish a\n   painting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varnish', 'n.', 'To cover or conceal with something that gives a fair\n   appearance; to give a fair coloring to by words; to gloss over; to\n   palliate; as, to varnish guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varnisher', 'n.', 'One who varnishes; one whose occupation is to varnish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varnisher', 'n.', 'One who disguises or palliates; one who gives a fair\n   external appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varnishing', 'n.', 'The act of laying on varnish; also, materials for\n   varnish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vartabed', 'n.', 'A doctor or teacher in the Armenian church. Members of\n   this order of ecclesiastics frequently have charge of dioceses, with\n   episcopal functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varuna', 'n.', 'The god of the waters; the Indian Neptune. He is regarded\n   as regent of the west, and lord of punishment, and is represented as\n   riding on a sea monster, holding in his hand a snaky cord or noose with\n   which to bind offenders, under water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varvel', 'n.', 'In falconry, one of the rings secured to the ends of the\n   jesses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varveled', 'a.', 'Having varvels, or rings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varied', 'imp. & p. p.', 'of Vary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varying', 'p. pr. & vb. n.', 'of Vary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vary', 'v. t.', 'To change the aspect of; to alter in form, appearance,\n   substance, position, or the like; to make different by a partial\n   change; to modify; as, to vary the properties, proportions, or nature\n   of a thing; to vary a posture or an attitude; to vary ones dress or\n   opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vary', 'v. t.', 'To change to something else; to transmute; to exchange; to\n   alternate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vary', 'v. t.', 'To make of different kinds; to make different from one\n   another; to diversity; to variegate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vary', 'v. t.', 'To embellish; to change fancifully; to present under new\n   aspects, as of form, key, measure, etc. See Variation, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vary', 'v. i.', 'To alter, or be altered, in any manner; to suffer a\n   partial change; to become different; to be modified; as, colors vary in\n   different lights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vary', 'v. i.', 'To differ, or be different; to be unlike or diverse; as,\n   the laws of France vary from those of England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vary', 'v. i.', 'To alter or change in succession; to alternate; as, one\n   mathematical quantity varies inversely as another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vary', 'v. i.', 'To deviate; to depart; to swerve; -- followed by from; as,\n   to vary from the law, or from reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vary', 'v. i.', 'To disagree; to be at variance or in dissension; as, men\n   vary in opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vary', 'n.', 'Alteration; change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Varying', '', 'a. & n. from Vary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vasa', 'pl. ', 'of Vas');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vas', 'n.', 'A vessel; a duct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vasa deferentia', 'pl. ', 'of Vas');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vascular', 'a.', 'Consisting of, or containing, vessels as an essential\n   part of a structure; full of vessels; specifically (Bot.), pertaining\n   to, or containing, special ducts, or tubes, for the circulation of sap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vascular', 'a.', 'Operating by means of, or made up of an arrangement of,\n   vessels; as, the vascular system in animals, including the arteries,\n   veins, capillaries, lacteals, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vascular', 'a.', 'Of or pertaining to the vessels of animal and vegetable\n   bodies; as, the vascular functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vascular', 'a.', 'Of or pertaining to the higher division of plants, that\n   is, the phaenogamous plants, all of which are vascular, in distinction\n   from the cryptogams, which to a large extent are cellular only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vascularities', 'pl. ', 'of Vascularity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vascularity', 'n.', 'The quality or state of being vascular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vasculose', 'n.', 'One of the substances of which vegetable tissue is\n   composed, differing from cellulose in its solubility in certain media.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vascula', 'pl. ', 'of Vasculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vasculum', 'n.', 'Same as Ascidium, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vasculum', 'n.', 'A tin box, commonly cylindrical or flattened, used in\n   collecting plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vase', 'n.', 'A vessel adapted for various domestic purposes, and anciently\n   for sacrificial uses; especially, a vessel of antique or elegant\n   pattern used for ornament; as, a porcelain vase; a gold vase; a Grecian\n   vase. See Illust. of Portland vase, under Portland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vase', 'n.', 'A vessel similar to that described in the first definition\n   above, or the representation of one in a solid block of stone, or the\n   like, used for an ornament, as on a terrace or in a garden. See Illust.\n   of Niche.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vase', 'n.', 'The body, or naked ground, of the Corinthian and Composite\n   capital; -- called also tambour, and drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vase', 'n.', 'The calyx of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaseline', 'n.', 'A yellowish translucent substance, almost odorless and\n   tasteless, obtained as a residue in the purification of crude\n   petroleum, and consisting essentially of a mixture of several of the\n   higher members of the paraffin series. It is used as an unguent, and\n   for various purposes in the arts. See the Note under Petrolatum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vase-shaped', 'a.', 'Formed like a vase, or like a common flowerpot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vasiform', 'a.', 'Having the form of a vessel, or duct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vasoconstrictor', 'a.', 'Causing constriction of the blood vessels; as, the\n   vasoconstrictor nerves, stimulation of which causes constriction of the\n   blood vessels to which they go. These nerves are also called\n   vasohypertonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vasoconstrictor', 'n.', 'A substance which causes constriction of the blood\n   vessels. Such substances are used in medicine to raise blood pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vasodentine', 'n.', 'A modified form of dentine, which is permeated by\n   blood capillaries; vascular dentine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vasodilator', 'a.', 'Causing dilation or relaxation of the blood vessels;\n   as, the vasodilator nerves, stimulation of which causes dilation of the\n   blood vessels to which they go. These nerves are also called\n   vaso-inhibitory, and vasohypotonic nerves, since their stimulation\n   causes relaxation and rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vasoformative', 'a.', 'Concerned in the development and formation of blood\n   vessels and blood corpuscles; as, the vasoformative cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaso-inhibitory', 'a.', 'See Vasodilator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vasomotor', 'a.', 'Causing movement in the walls of vessels; as, the\n   vasomotor mechanisms; the vasomotor nerves, a system of nerves\n   distributed over the muscular coats of the blood vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vassal', 'n.', 'The grantee of a fief, feud, or fee; one who holds land of\n   superior, and who vows fidelity and homage to him; a feudatory; a\n   feudal tenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vassal', 'n.', 'A subject; a dependent; a servant; a slave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vassal', 'a.', 'Resembling a vassal; slavish; servile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vassal', 'v. t.', 'To treat as a vassal; to subject to control; to enslave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vassalage', 'n.', 'The state of being a vassal, or feudatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vassalage', 'n.', 'Political servitude; dependence; subjection; slavery;\n   as, the Greeks were held in vassalage by the Turks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vassalage', 'n.', 'A territory held in vassalage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vassalage', 'n.', 'Vassals, collectively; vassalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vassalage', 'n.', 'Valorous service, such as that performed by a vassal;\n   valor; prowess; courage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vassaless', 'n.', 'A female vassal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vassalry', 'n.', 'The body of vassals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vast', 'superl.', 'Waste; desert; desolate; lonely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vast', 'superl.', 'Of great extent; very spacious or large; also, huge in\n   bulk; immense; enormous; as, the vast ocean; vast mountains; the vast\n   empire of Russia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vast', 'superl.', 'Very great in numbers, quantity, or amount; as, a vast\n   army; a vast sum of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vast', 'superl.', 'Very great in importance; as, a subject of vast concern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vast', 'n.', 'A waste region; boundless space; immensity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vastation', 'n.', 'A laying waste; waste; depopulation; devastation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vastel', 'n.', 'See Wastel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vastidity', 'n.', 'Vastness; immensity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vastitude', 'n.', 'Vastness; immense extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vastitude', 'n.', 'Destruction; vastation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vastity', 'n.', 'Vastness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vastly', 'adv.', 'To a vast extent or degree; very greatly; immensely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vastness', 'n.', 'The quality or state of being vast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vasty', 'a.', 'Vast; immense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vasum', 'n.', 'A genus including several species of large marine gastropods\n   having massive pyriform shells, with conspicuous folds on the\n   columella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vat', 'n.', 'A large vessel, cistern, or tub, especially one used for\n   holding in an immature state, chemical preparations for dyeing, or for\n   tanning, or for tanning leather, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vat', 'n.', 'A measure for liquids, and also a dry measure; especially, a\n   liquid measure in Belgium and Holland, corresponding to the hectoliter\n   of the metric system, which contains 22.01 imperial gallons, or 26.4\n   standard gallons in the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vat', 'n.', 'A wooden tub for washing ores and mineral substances in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vat', 'n.', 'A square, hollow place on the back of a calcining furnace,\n   where tin ore is laid to dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vat', 'n.', 'A vessel for holding holy water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vatted', 'imp. & p. p.', 'of Vat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vatting', 'p. pr. & vb. n.', 'of Vat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vat', 'v. t.', 'To put or transfer into a vat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vatfuls', 'pl. ', 'of Vatful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vatful', 'n.', 'As much as a vat will hold; enough to fill a vat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vatical', 'a.', 'Of or pertaining to a prophet; prophetical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vatican', 'n.', 'A magnificent assemblage of buildings at Rome, near the\n   church of St. Peter, including the popes palace, a museum, a library,\n   a famous chapel, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaticanism', 'n.', 'The doctrine of papal supremacy; extreme views in\n   support of the authority of the pope; ultramontanism; -- a term used\n   only by persons who are not Roman Catholics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaticanist', 'n.', 'One who strongly adheres to the papal authority; an\n   ultramontanist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaticide', 'n.', 'The murder, or the murderer, of a prophet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaticinal', 'a.', 'Of or pertaining to prophecy; prophetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaticinate', 'v. i. & t.', 'To prophesy; to foretell; to practice\n   prediction; to utter prophecies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaticination', 'n.', 'Prediction; prophecy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaticinator', 'n.', 'One who vaticinates; a prophet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaticine', 'n.', 'A prediction; a vaticination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaudeville', 'n.', 'A kind of song of a lively character, frequently\n   embodying a satire on some person or event, sung to a familiar air in\n   couplets with a refrain; a street song; a topical song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaudeville', 'n.', 'A theatrical piece, usually a comedy, the dialogue of\n   which is intermingled with light or satirical songs, set to familiar\n   airs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaudois', 'n. sing. & pl.', 'An inhabitant, or the inhabitants, of the\n   Swiss canton of Vaud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaudois', 'n. sing. & pl.', 'A modern name of the Waldenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaudoux', 'n. & a.', 'See Voodoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vault', 'n.', 'An arched structure of masonry, forming a ceiling or canopy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vault', 'n.', 'An arched apartment; especially, a subterranean room, use\n   for storing articles, for a prison, for interment, or the like; a cell;\n   a cellar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vault', 'n.', 'The canopy of heaven; the sky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vault', 'n.', 'A leap or bound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vault', 'n.', 'The bound or leap of a horse; a curvet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vault', 'n.', 'A leap by aid of the hands, or of a pole, springboard, or\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaulted', 'imp. & p. p.', 'of Vault');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaulting', 'p. pr. & vb. n.', 'of Vault');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vault', 'v. t.', 'To form with a vault, or to cover with a vault; to give\n   the shape of an arch to; to arch; as, vault a roof; to vault a passage\n   to a court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vault', 'v. i.', 'To leap over; esp., to leap over by aid of the hands or a\n   pole; as, to vault a fence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vault', 'n.', 'To leap; to bound; to jump; to spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vault', 'n.', 'To exhibit feats of tumbling or leaping; to tumble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaultage', 'n.', 'Vaulted work; also, a vaulted place; an arched cellar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaulted', 'a.', 'Arched; concave; as, a vaulted roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaulted', 'a.', 'Covered with an arch, or vault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaulted', 'a.', 'Arched like the roof of the mouth, as the upper lip of\n   many ringent flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaulter', 'n.', 'One who vaults; a leaper; a tumbler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaulting', 'n.', 'The act of constructing vaults; a vaulted construction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaulting', 'n.', 'Act of one who vaults or leaps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaulty', 'a.', 'Arched; concave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaunce', 'v. i.', 'To advance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaunted', 'imp. & p. p.', 'of Vaunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaunting', 'p. pr. & vb. n.', 'of Vaunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaunt', 'v. i.', 'To boast; to make a vain display of ones own worth,\n   attainments, decorations, or the like; to talk ostentatiously; to brag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaunt', 'v. t.', 'To boast of; to make a vain display of; to display with\n   ostentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaunt', 'n.', 'A vain display of what one is, or has, or has done;\n   ostentation from vanity; a boast; a brag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaunt', 'n.', 'The first part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaunt', 'v. t.', 'To put forward; to display.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaunt-courier', 'n.', 'See Van-courier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaunter', 'n.', 'One who vaunts; a boaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vauntful', 'a.', 'Given to vaunting or boasting; vainly ostentatious;\n   boastful; vainglorious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vauntingly', 'adv.', 'In a vaunting manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vauntmure', 'n.', 'A false wall; a work raised in front of the main wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vauquelinite', 'n.', 'Chromate of copper and lead, of various shades of\n   green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaut', 'v. i.', 'To vault; to leap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaut', 'n.', 'A vault; a leap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vauty', 'a.', 'Vaulted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vavasor', 'n.', 'The vassal or tenant of a baron; one who held under a\n   baron, and who also had tenants under him; one in dignity next to a\n   baron; a title of dignity next to a baron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vavasory', 'n.', 'The quality or tenure of the fee held by a vavasor; also,\n   the lands held by a vavasor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaward', 'n.', 'The fore part; van.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vaza parrot', '', 'Any one of several species of parrots of the genus\n   Coracopsis, native of Madagascar; -- called also vasa parrot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veadar', 'n.', 'The thirteenth, or intercalary, month of the Jewish\n   ecclesiastical calendar, which is added about every third year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veal', 'n.', 'The flesh of a calf when killed and used for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vection', 'n.', 'Vectitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vectitation', 'n.', 'The act of carrying, or state of being carried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vector', 'n.', 'Same as Radius vector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vector', 'n.', 'A directed quantity, as a straight line, a force, or a\n   velocity. Vectors are said to be equal when their directions are the\n   same their magnitudes equal. Cf. Scalar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vecture', 'n.', 'The act of carrying; conveyance; carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veda', 'n.', 'The ancient sacred literature of the Hindus; also, one of the\n   four collections, called Rig-Veda, Yajur-Veda, Sama-Veda, and\n   Atharva-Veda, constituting the most ancient portions of that\n   literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vedanta', 'n.', 'A system of philosophy among the Hindus, founded on\n   scattered texts of the Vedas, and thence termed the \"Anta,\" or end or\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vedantic', 'a.', 'Of or pertaining to the Vedas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vedantist', 'n.', 'One versed in the doctrines of the Vedantas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vedette', 'n.', 'A sentinel, usually on horseback, stationed on the outpost\n   of an army, to watch an enemy and give notice of danger; a vidette.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vedro', 'n.', 'A Russian liquid measure, equal to 3.249 gallons of U. S.\n   standard measure, or 2.706 imperial gallons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veered', 'imp. & p. p.', 'of Veer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veering', 'p. pr. & vb. n.', 'of Veer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veer', 'v. i.', 'To change direction; to turn; to shift; as, wind veers to\n   the west or north.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veer', 'v. t.', 'To direct to a different course; to turn; to wear; as, to\n   veer, or wear, a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veering', 'a.', 'Shifting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veery', 'n.', 'An American thrush (Turdus fuscescens) common in the\n   Northern United States and Canada. It is light tawny brown above. The\n   breast is pale buff, thickly spotted with brown. Called also Wilsons\n   thrush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vega', 'n.', 'A brilliant star of the first magnitude, the brightest of\n   those constituting the constellation Lyra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetability', 'n.', 'The quality or state of being vegetable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetable', 'v.', 'Of or pertaining to plants; having the nature of, or\n   produced by, plants; as, a vegetable nature; vegetable growths, juices,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetable', 'v.', 'Consisting of, or comprising, plants; as, the vegetable\n   kingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetable', 'v.', 'Plants having distinct flowers and true seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetable', 'v.', 'Plants without true flowers, and reproduced by minute\n   spores of various kinds, or by simple cell division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetable', 'n.', 'A plant. See Plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetable', 'n.', 'A plant used or cultivated for food for man or domestic\n   animals, as the cabbage, turnip, potato, bean, dandelion, etc.; also,\n   the edible part of such a plant, as prepared for market or the table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetal', 'a.', 'Of or pertaining to vegetables, or the vegetable kingdom;\n   of the nature of a vegetable; vegetable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetal', 'a.', 'Of, pertaining to, or designating, that class of vital\n   phenomena, such as digestion, absorption, assimilation, secretion,\n   excretion, circulation, generation, etc., which are common to plants\n   and animals, in distinction from sensation and volition, which are\n   peculiar to animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetal', 'n.', 'A vegetable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetality', 'n.', 'The quality or state of being vegetal, or vegetable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetality', 'n.', 'The quality or state of being vegetal, or exhibiting\n   those physiological phenomena which are common to plants and animals.\n   See Vegetal, a., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetarian', 'n.', 'One who holds that vegetables and fruits are the only\n   proper food for man. Strict vegetarians eat no meat, eggs, or milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetarian', 'a.', 'Of or pertaining to vegetarianism; as, a vegetarian\n   diet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetarianism', 'n.', 'The theory or practice of living upon vegetables and\n   fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetated', 'imp. & p. p.', 'of Vegetate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetating', 'p. pr. & vb. n.', 'of Vegetate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetate', 'v. i.', 'To grow, as plants, by nutriment imbibed by means of\n   roots and leaves; to start into growth; to sprout; to germinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetate', 'v. i.', 'Fig.: To lead a live too low for an animate creature;\n   to do nothing but eat and grow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetate', 'v. i.', 'To grow exuberantly; to produce fleshy or warty\n   outgrowths; as, a vegetating papule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetation', 'n.', 'The act or process of vegetating, or growing as a plant\n   does; vegetable growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetation', 'n.', 'The sum of vegetable life; vegetables or plants in\n   general; as, luxuriant vegetation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetation', 'n.', 'An exuberant morbid outgrowth upon any part, especially\n   upon the valves of the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetative', 'a.', 'Growing, or having the power of growing, as plants;\n   capable of vegetating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetative', 'a.', 'Having the power to produce growth in plants; as, the\n   vegetative properties of soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetative', 'a.', 'Having relation to growth or nutrition; partaking of\n   simple growth and enlargement of the systems of nutrition, apart from\n   the sensorial or distinctively animal functions; vegetal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegete', 'a.', 'Lively; active; sprightly; vigorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetive', 'a.', 'Having the nature of a plant; vegetable; as, vegetive\n   life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetive', 'n.', 'A vegetable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegeto-animal', 'a.', 'Partaking of the nature both of vegetable and animal\n   matter; -- a term sometimes applied to vegetable albumen and gluten,\n   from their resemblance to similar animal products.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vegetous', 'a.', 'Vigorous; lively; active; vegete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vehemence', 'n.', 'The quality pr state of being vehement; impetuous force;\n   impetuosity; violence; fury; as, the vehemence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vehemence', 'n.', 'Violent ardor; great heat; animated fervor; as, the\n   vehemence of love, anger, or other passions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vehemency', 'n.', 'Vehemence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vehement', 'a.', 'Acting with great force; furious; violent; impetuous;\n   forcible; mighty; as, vehement wind; a vehement torrent; a vehement\n   fire or heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vehement', 'a.', 'Very ardent; very eager or urgent; very fervent;\n   passionate; as, a vehement affection or passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vehemently', 'adv.', 'In a vehement manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vehicle', 'n.', 'That in or on which any person or thing is, or may be,\n   carried, as a coach, carriage, wagon, cart, car, sleigh, bicycle, etc.;\n   a means of conveyance; specifically, a means of conveyance upon land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vehicle', 'n.', 'That which is used as the instrument of conveyance or\n   communication; as, matter is the vehicle of energy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vehicle', 'n.', 'A substance in which medicine is taken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vehicle', 'n.', 'Any liquid with which a pigment is applied, including\n   whatever gum, wax, or glutinous or adhesive substance is combined with\n   it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vehicled', 'a.', 'Conveyed in a vehicle; furnished with a vehicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vehicular', 'a.', 'Of or pertaining to a vehicle; serving as a vehicle; as,\n   a vehicular contrivance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vehiculary', 'a.', 'Vehicular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vehiculate', 'v. t. & i.', 'To convey by means of a vehicle; to ride in a\n   vehicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vehiculation', 'n.', 'Movement of vehicles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vehiculatory', 'a.', 'Vehicular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vehmic', 'a.', 'Of, pertaining to, or designating, certain secret tribunals\n   which flourished in Germany from the end of the 12th century to the\n   middle of the 16th, usurping many of the functions of the government\n   which were too weak to maintain law and order, and inspiring dread in\n   all who came within their jurisdiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veil', 'n.', 'Something hung up, or spread out, to intercept the view, and\n   hide an object; a cover; a curtain; esp., a screen, usually of gauze,\n   crape, or similar diaphnous material, to hide or protect the face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veil', 'n.', 'A cover; disguise; a mask; a pretense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veil', 'n.', 'The calyptra of mosses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veil', 'n.', 'A membrane connecting the margin of the pileus of a mushroom\n   with the stalk; -- called also velum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veil', 'n.', 'A covering for a person or thing; as, a nuns veil; a paten\n   veil; an altar veil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veil', 'n.', 'Same as Velum, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veiled', 'imp. & p. p.', 'of Veil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veiling', 'p. pr. & vb. n.', 'of Veil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veil', 'n.', 'To throw a veil over; to cover with a veil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veil', 'n.', 'Fig.: To invest; to cover; to hide; to conceal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veiled', 'a.', 'Covered by, or as by, a veil; hidden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veiling', 'n.', 'A veil; a thin covering; also, material for making veils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veilless', 'a.', 'Having no veil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vein', 'n.', 'One of the vessels which carry blood, either venous or\n   arterial, to the heart. See Artery, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vein', 'n.', 'One of the similar branches of the framework of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vein', 'n.', 'One of the ribs or nervures of the wings of insects. See\n   Venation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vein', 'n.', 'A narrow mass of rock intersecting other rocks, and filling\n   inclined or vertical fissures not corresponding with the\n   stratification; a lode; a dike; -- often limited, in the language of\n   miners, to a mineral vein or lode, that is, to a vein which contains\n   useful minerals or ores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vein', 'n.', 'A fissure, cleft, or cavity, as in the earth or other\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vein', 'n.', 'A streak or wave of different color, appearing in wood, and\n   in marble and other stones; variegation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vein', 'n.', 'A train of association, thoughts, emotions, or the like; a\n   current; a course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vein', 'n.', 'Peculiar temper or temperament; tendency or turn of mind; a\n   particular disposition or cast of genius; humor; strain; quality; also,\n   manner of speech or action; as, a rich vein of humor; a satirical vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veined', 'imp. & p. p.', 'of Vein');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veining', 'p. pr. & vb. n.', 'of Vein');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vein', 'v. t.', 'To form or mark with veins; to fill or cover with veins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veinal', 'a.', 'Pertaining to veins; venous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veined', 'a.', 'Full of veins; streaked; variegated; as, veined marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veined', 'a.', 'Having fibrovascular threads extending throughout the\n   lamina; as, a veined leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veinless', 'a.', 'Having no veins; as, a veinless leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veinlet', 'n.', 'A small vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veinous', 'a.', 'Marked with veins; veined; veiny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veinstone', 'n.', 'The nonmetalliferous mineral or rock material which\n   accompanies the ores in a vein, as quartz, calcite, barite, fluor spar,\n   etc.; -- called also veinstuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veiny', 'a.', 'Full of veins; veinous; veined; as, veiny marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velar', 'a.', 'Of or pertaining to a velum; esp. (Anat.) of or pertaining\n   to the soft palate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velar', 'a.', 'Having the place of articulation on the soft palate;\n   guttural; as, the velar consonants, such as k and hard q.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velaria', 'pl. ', 'of Velarium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velarium', 'n.', 'The marginal membrane of certain medusae belonging to the\n   Discophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velate', 'a.', 'Having a veil; veiled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vele', 'n.', 'A veil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velella', 'n.', 'Any species of oceanic Siphonophora belonging to the genus\n   Velella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veliferous', 'a.', 'Carrying or bearing sails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veliger', 'n.', 'Any larval gastropod or bivalve mollusk in the state when\n   it is furnished with one or two ciliated membranes for swimming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velitation', 'n.', 'A dispute or contest; a slight contest; a skirmish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velivolant', 'a.', 'Flying with sails; passing under full sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vell', 'n.', 'The salted stomach of a calf, used in making cheese; a rennet\n   bag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vell', 'n.', 'To cut the turf from, as for burning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velleity', 'n.', 'The lowest degree of desire; imperfect or incomplete\n   volition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vellet', 'n.', 'Velvet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vellicated', 'imp. & p. p.', 'of Vellicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vellicating', 'p. pr. & vb. n.', 'of Vellicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vellicate', 'v. t.', 'To twitch; to cause to twitch convulsively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vellicate', 'v. i.', 'To move spasmodically; to twitch; as, a nerve\n   vellicates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vellication', '', 'The act of twitching, or of causing to twitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vellication', '', 'A local twitching, or convulsive motion, of a muscular\n   fiber, especially of the face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vellicative', 'a.', 'Having the power of vellicating, plucking, or\n   twitching; causing vellication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vellon', 'n.', 'A word occurring in the phrase real vellon. See the Note\n   under Its Real.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vellum', 'n.', 'A fine kind of parchment, usually made from calfskin, and\n   rendered clear and white, -- used as for writing upon, and for binding\n   books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vellumy', 'a.', 'Resembling vellum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velocimeter', 'n.', 'An apparatus for measuring speed, as of machinery or\n   vessels, but especially of projectiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velocipede', 'n.', 'A light road carriage propelled by the feet of the\n   rider. Originally it was propelled by striking the tips of the toes on\n   the roadway, but commonly now by the action of the feet on a pedal or\n   pedals connected with the axle of one or more of the wheels, and\n   causing their revolution. They are made in many forms, with two, three,\n   or four wheels. See Bicycle, and Tricycle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velecipedist', 'n.', 'One who rides on a velocipede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velocities', 'pl. ', 'of Velocity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velocity', 'n.', 'Quickness of motion; swiftness; speed; celerity;\n   rapidity; as, the velocity of wind; the velocity of a planet or comet\n   in its orbit or course; the velocity of a cannon ball; the velocity of\n   light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velocity', 'n.', 'Rate of motion; the relation of motion to time, measured\n   by the number of units of space passed over by a moving body or point\n   in a unit of time, usually the number of feet passed over in a second.\n   See the Note under Speed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velours', 'n.', 'One of many textile fabrics having a pile like that of\n   velvet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veltfare', 'n.', 'The fieldfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vela', 'pl. ', 'of Velum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velum', 'n.', 'Curtain or covering; -- applied to various membranous\n   partitions, especially to the soft palate. See under Palate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velum', 'n.', 'See Veil, n., 3 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velum', 'n.', 'A thin membrane surrounding the sporocarps of quillworts\n   Isoetes).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velum', 'n.', 'A veil-like organ or part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velum', 'n.', 'The circular membrane that partially incloses the space\n   beneath the umbrella of hydroid medusae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velum', 'n.', 'A delicate funnel-like membrane around the flagellum of\n   certain Infusoria. See Illust. a of Protozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velure', 'n.', 'Velvet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velutina', 'n.', 'Any one of several species of marine gastropods belonging\n   to Velutina and allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velutinous', 'a.', 'Having the surface covered with a fine and dense silky\n   pubescence; velvety; as, a velutinous leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velverd', 'n.', 'The veltfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velveret', 'n.', 'A kind of velvet having cotton back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velvet', 'n.', 'A silk fabric, having a short, close nap of erect threads.\n   Inferior qualities are made with a silk pile on a cotton or linen back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velvet', 'n.', 'The soft and highly vascular deciduous skin which envelops\n   and nourishes the antlers of deer during their rapid growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velvet', 'a.', 'Made of velvet; soft and delicate, like velvet; velvety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velvet', 'v. i.', 'To pain velvet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velvet', 'v. t.', 'To make like, or cover with, velvet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velvetbreast', 'n.', 'The goosander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velveteen', 'n.', 'A kind of cloth, usually cotton, made in imitation of\n   velvet; cotton velvet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velveting', 'n.', 'The fine shag or nap of velvet; a piece of velvet;\n   velvet goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velvetleaf', 'n.', 'A name given to several plants which have soft, velvety\n   leaves, as the Abutilon Avicennae, the Cissampelos Pareira, and the\n   Lavatera arborea, and even the common mullein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Velvety', 'a.', 'Made of velvet, or like velvet; soft; smooth; delicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venae', 'pl. ', 'of Vena');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vena', 'n.', 'A vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venae cavae', 'pl. ', 'of Vena');
  INSERT INTO words (word, word_type, description) VALUES  (    'VenAe portae', 'pl. ', 'of Vena');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venada', 'N.', 'The pudu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venal', 'a.', 'Of or pertaining to veins; venous; as, venal blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venal', 'a.', 'Capable of being bought or obtained for money or other\n   valuable consideration; made matter of trade or barter; held for sale;\n   salable; mercenary; purchasable; hireling; as, venal services.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venality', 'n.', 'The quality or state of being venal, or purchasable;\n   mercenariness; prostitution of talents, offices, or services, for money\n   or reward; as, the venality of a corrupt court; the venality of an\n   official.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venally', 'adv.', 'In a venal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venantes', 'n. pl.', 'The hunting spiders, which run after, or leap upon,\n   their prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venary', 'a.', 'Of or, pertaining to hunting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venatic', 'a.', 'Alt. of Venatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venatical', 'a.', 'Of or pertaining to hunting; used in hunting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venatica', 'n.', 'See Vinatico.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venation', 'n.', 'The arrangement or system of veins, as in the wing of an\n   insect, or in the leaves of a plant. See Illust. in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venation', 'n.', 'The act or art of hunting, or the state of being hunted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venatorial', 'a.', 'Or or pertaining to hunting; venatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vended', 'imp. & p. p.', 'of Vend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vending', 'p. pr. & vb. n.', 'of Vend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vend', 'v. t.', 'To transfer to another person for a pecuniary equivalent;\n   to make an object of trade; to dispose of by sale; to sell; as, to vend\n   goods; to vend vegetables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vend', 'n.', 'The act of vending or selling; a sale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vend', 'n.', 'The total sales of coal from a colliery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vendace', 'n.', 'A European lake whitefish (Coregonus Willughbii, or C.\n   Vandesius) native of certain lakes in Scotland and England. It is\n   regarded as a delicate food fish. Called also vendis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vendee', 'n.', 'The person to whom a thing is vended, or sold; -- the\n   correlative of vendor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vendemiaire', 'n.', 'The first month of the French republican calendar,\n   dating from September 22, 1792.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vender', 'n.', 'One who vends; one who transfers the exclusive right of\n   possessing a thing, either his own, or that of another as his agent,\n   for a price or pecuniary equivalent; a seller; a vendor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vendetta', 'n.', 'A blood feud; private revenge for the murder of a\n   kinsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vendibility', 'n.', 'The quality or state of being vendible, or salable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vendible', 'a.', 'Capable of being vended, or sold; that may be sold;\n   salable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vendible', 'n.', 'Something to be sold, or offered for sale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venditate', 'v. t.', 'To cry up. as if for sale; to blazon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venditation', 'n.', 'The act of setting forth ostentatiously; a boastful\n   display.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vendition', 'n.', 'The act of vending, or selling; sale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vendor', 'n.', 'A vender; a seller; the correlative of vendee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vends', 'n. pl.', 'See Wends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vendue', 'n.', 'A public sale of anything, by outcry, to the highest\n   bidder; an auction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veneered', 'imp. & p. p.', 'of Veneer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veneering', 'p. pr. & vb. n.', 'of Veneer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veneer', 'v. t.', 'To overlay or plate with a thin layer of wood or other\n   material for outer finish or decoration; as, to veneer a piece of\n   furniture with mahogany. Used also figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veneer', 'v. t.', 'A thin leaf or layer of a more valuable or beautiful\n   material for overlaying an inferior one, especially such a thin leaf of\n   wood to be glued to a cheaper wood; hence, external show; gloss; false\n   pretense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veneering', 'n.', 'The act or art of one who veneers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veneering', 'n.', 'Thin wood or other material used as a veneer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venefical', 'a.', 'Veneficial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venefice', 'n.', 'The act or practice of poisoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veneficial', 'a.', 'Alt. of Veneficious');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veneficious', 'a.', 'Acting by poison; used in poisoning or in sorcery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venemous', 'a.', 'Venomous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venenate', 'v. t.', 'To poison; to infect with poison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venenate', 'a.', 'Poisoned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vennation', 'n.', 'The act of poisoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vennation', 'n.', 'Poison; venom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venene', 'a.', 'Poisonous; venomous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venenose', 'a.', 'Poisonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venerability', 'n.', 'The quality or state of being venerable;\n   venerableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venerable', 'a.', 'Capable of being venerated; worthy of veneration or\n   reverence; deserving of honor and respect; -- generally implying an\n   advanced age; as, a venerable magistrate; a venerable parent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venerable', 'a.', 'Rendered sacred by religious or other associations; that\n   should be regarded with awe and treated with reverence; as, the\n   venerable walls of a temple or a church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veneracea', 'n. pl.', 'An extensive tribe of bivalve mollusks of which the\n   genus Venus is the type. The shells are usually oval, or somewhat\n   heartshaped, with a conspicuous lunule. See Venus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venerated', 'imp. & p. p.', 'of Venerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venerating', 'p. pr. & vb. n.', 'of Venerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venerate', 'v. t.', 'To regard with reverential respect; to honor with\n   mingled respect and awe; to reverence; to revere; as, we venerate\n   parents and elders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veneration', 'n.', 'The act of venerating, or the state of being venerated;\n   the highest degree of respect and reverence; respect mingled with awe;\n   a feeling or sentimental excited by the dignity, wisdom, or superiority\n   of a person, by sacredness of character, by consecration to sacred\n   services, or by hallowed associations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venerator', 'n.', 'One who venerates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venereal', 'a.', 'Of or pertaining to venery, or sexual love; relating to\n   sexual intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venereal', 'a.', 'Arising from sexual intercourse; as, a venereal disease;\n   venereal virus or poison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venereal', 'a.', 'Adapted to the cure of venereal diseases; as, venereal\n   medicines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venereal', 'a.', 'Adapted to excite venereal desire; aphrodisiac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venereal', 'a.', 'Consisting of, or pertaining to, copper, formerly called\n   by chemists Venus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venereal', 'n.', 'The venereal disease; syphilis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venerean', 'a.', 'Devoted to the offices of Venus, or love; venereal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venereous', 'a.', 'Venereal; exciting lust; aphrodisiac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venereous', 'a.', 'Lustful; lascivious; libidinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venerous', 'a.', 'Venereous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venery', 'n.', 'Sexual love; sexual intercourse; coition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venery', 'n.', 'The art, act, or practice of hunting; the sports of the\n   chase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venesection', 'n.', 'The act or operation of opening a vein for letting\n   blood; bloodletting; phlebotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venetian', 'a.', 'Of or pertaining to Venice in Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venetian', 'n.', 'A native or inhabitant of Venice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venew', 'n.', 'A bout, or turn, as at fencing; a thrust; a hit; a veney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veney', 'n.', 'A bout; a thrust; a venew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venge', 'v. t.', 'To avenge; to punish; to revenge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vengeable', 'a.', 'Revengeful; deserving revenge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vengeance', 'n.', 'Punishment inflicted in return for an injury or an\n   offense; retribution; -- often, in a bad sense, passionate or\n   unrestrained revenge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vengeance', 'n.', 'Harm; mischief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vengeancely', 'adv.', 'Extremely; excessively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vengeful', 'a.', 'Vindictive; retributive; revengeful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vengement', 'n.', 'Avengement; penal retribution; vengeance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venger', 'n.', 'An avenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veniable', 'a.', 'Venial; pardonable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venial', 'a.', 'Capable of being forgiven; not heinous; excusable;\n   pardonable; as, a venial fault or transgression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venial', 'a.', 'Allowed; permitted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veniality', 'n.', 'The quality or state of being venial; venialness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venire facias', '', 'A judicial writ or precept directed to the sheriff,\n   requiring him to cause a certain number of qualified persons to appear\n   in court at a specified time, to serve as jurors in said court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venire facias', '', 'A writ in the nature of a summons to cause the party\n   indicted on a penal statute to appear. Called also venire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venison', 'n.', 'Beasts of the chase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venison', 'n.', 'Formerly, the flesh of any of the edible beasts of the\n   chase, also of game birds; now, the flesh of animals of the deer kind\n   exclusively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venite', 'n.', 'The 95th Psalm, which is said or sung regularly in the\n   public worship of many churches. Also, a musical composition adapted to\n   this Psalm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venom', 'n.', 'Matter fatal or injurious to life; poison; particularly, the\n   poisonous, the poisonous matter which certain animals, such as\n   serpents, scorpions, bees, etc., secrete in a state of health, and\n   communicate by thing or stinging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venom', 'n.', 'Spite; malice; malignity; evil quality. Chaucer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venom', 'n.', 'To infect with venom; to envenom; to poison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venomous', 'a.', 'Full of venom; noxious to animal life; poisonous; as, the\n   bite of a serpent may be venomous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venomous', 'a.', 'Having a poison gland or glands for the secretion of\n   venom, as certain serpents and insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venomous', 'a.', 'Noxious; mischievous; malignant; spiteful; as, a venomous\n   progeny; a venomous writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venose', 'a.', 'Having numerous or conspicuous veins; veiny; as, a venose\n   frond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venosity', 'n.', 'The quality or state of being venous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venosity', 'n.', 'A condition in which the circulation is retarded, and the\n   entire mass of blood is less oxygenated than it normally is.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venous', 'a.', 'Of or pertaining to a vein or veins; as, the venous\n   circulation of the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venous', 'a.', 'Contained in the veins, or having the same qualities as if\n   contained in the veins, that is, having a dark bluish color and\n   containing an insufficient amount of oxygen so as no longer to be fit\n   for oxygenating the tissues; -- said of the blood, and opposed to\n   arterial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venous', 'a.', 'Marked with veins; veined; as, a venous leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vent', 'n.', 'Sale; opportunity to sell; market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vent', 'v. t.', 'To sell; to vend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vent', 'n.', 'A baiting place; an inn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vent', 'v. i.', 'To snuff; to breathe or puff out; to snort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vent', 'n.', 'A small aperture; a hole or passage for air or any fluid to\n   escape; as, the vent of a cask; the vent of a mold; a volcanic vent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vent', 'n.', 'The anal opening of certain invertebrates and fishes; also,\n   the external cloacal opening of reptiles, birds, amphibians, and many\n   fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vent', 'n.', 'The opening at the breech of a firearm, through which fire is\n   communicated to the powder of the charge; touchhole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vent', 'n.', 'Sectional area of the passage for gases divided by the length\n   of the same passage in feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vent', 'n.', 'Fig.: Opportunity of escape or passage from confinement or\n   privacy; outlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vent', 'n.', 'Emission; escape; passage to notice or expression;\n   publication; utterance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vented', 'imp. & p. p.', 'of Vent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venting', 'p. pr. & vb. n.', 'of Vent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vent', 'v. t.', 'To let out at a vent, or small aperture; to give passage\n   or outlet to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vent', 'v. t.', 'To suffer to escape from confinement; to let out; to\n   utter; to pour forth; as, to vent passion or complaint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vent', 'v. t.', 'To utter; to report; to publish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vent', 'v. t.', 'To scent, as a hound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vent', 'v. t.', 'To furnish with a vent; to make a vent in; as, to vent. a\n   mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventage', 'n.', 'A small hole, as the stop in a flute; a vent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventail', 'n.', 'That part of a helmet which is intended for the admission\n   of air, -- sometimes in the visor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venter', 'n.', 'One who vents; one who utters, reports, or publishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venter', 'n.', 'The belly; the abdomen; -- sometimes applied to any large\n   cavity containing viscera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venter', 'n.', 'The uterus, or womb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venter', 'n.', 'A belly, or protuberant part; a broad surface; as, the\n   venter of a muscle; the venter, or anterior surface, of the scapula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venter', 'n.', 'The lower part of the abdomen in insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venter', 'n.', 'A pregnant woman; a mother; as, A has a son B by one\n   venter, and a daughter C by another venter; children by different\n   venters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venthole', 'n.', 'A touchhole; a vent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventiduct', 'n.', 'A passage for wind or air; a passage or pipe for\n   ventilating apartments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventilated', 'imp. & p. p.', 'of Ventilate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventilating', 'p. pr. & vb. n.', 'of Ventilate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventilate', 'v. t.', 'To open and expose to the free passage of air; to\n   supply with fresh air, and remove impure air from; to air; as, to\n   ventilate a room; to ventilate a cellar; to ventilate a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventilate', 'v. t.', 'To provide with a vent, or escape, for air, gas,\n   etc.; as, to ventilate a mold, or a water-wheel bucket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventilate', 'v. t.', 'To change or renew, as the air of a room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventilate', 'v. t.', 'To winnow; to fan; as, to ventilate wheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventilate', 'v. t.', 'To sift and examine; to bring out, and subject to\n   penetrating scrutiny; to expose to examination and discussion; as, to\n   ventilate questions of policy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventilate', 'v. t.', 'To give vent; to utter; to make public.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventilation', 'n.', 'The act of ventilating, or the state of being\n   ventilated; the art or process of replacing foul air by that which is\n   pure, in any inclosed place, as a house, a church, a mine, etc.; free\n   exposure to air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventilation', 'n.', 'The act of refrigerating, or cooling; refrigeration;\n   as, ventilation of the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventilation', 'n.', 'The act of fanning, or winnowing, for the purpose of\n   separating chaff and dust from the grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventilation', 'n.', 'The act of sifting, and bringing out to view or\n   examination; free discussion; public exposure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventilation', 'n.', 'The act of giving vent or expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventilative', 'a.', 'Of or pertaining to ventilation; adapted to secure\n   ventilation; ventilating; as, ventilative apparatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventilator', 'n.', 'A contrivance for effecting ventilation; especially, a\n   contrivance or machine for drawing off or expelling foul or stagnant\n   air from any place or apartment, or for introducing that which is fresh\n   and pure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventose', 'n.', 'A ventouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventose', 'a.', 'Windy; flatulent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventose', 'a.', 'The sixth month of the calendar adopted by the first\n   French republic. It began February 19, and ended March 20. See\n   Vend/miaire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventosity', 'n.', 'Quality or state of being ventose; windiness; hence,\n   vainglory; pride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventouse', 'n.', 'A cupping glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventouse', 'v. t. & i.', 'To cup; to use a cupping glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventrad', 'adv.', 'Toward the ventral side; on the ventral side; ventrally;\n   -- opposed to dorsad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventral', 'a.', 'Of, pertaining to, or situated near, the belly, or ventral\n   side, of an animal or of one of its parts; hemal; abdominal; as, the\n   ventral fin of a fish; the ventral root of a spinal nerve; -- opposed\n   to dorsal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventral', 'a.', 'Of or pertaining to that surface of a carpel, petal, etc.,\n   which faces toward the center of a flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventral', 'a.', 'Of or pertaining to the lower side or surface of a\n   creeping moss or other low flowerless plant. Opposed to dorsal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventricle', 'n.', 'A cavity, or one of the cavities, of an organ, as of the\n   larynx or the brain; specifically, the posterior chamber, or one of the\n   two posterior chambers, of the heart, which receives the blood from the\n   auricle and forces it out from the heart. See Heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventricle', 'n.', 'The stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventricle', 'n.', 'Fig.: Any cavity, or hollow place, in which any function\n   may be conceived of as operating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventricose', 'a.', 'Alt. of Ventricous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventricous', 'a.', 'Swelling out on one side or unequally; bellied;\n   ventricular; as, a ventricose corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventricular', 'a.', 'Of or pertaining to a ventricle; bellied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventriculite', 'n.', 'Any one of numerous species of siliceous fossil\n   sponges belonging to Ventriculites and allied genera, characteristic of\n   the Cretaceous period.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventriculous', 'a.', 'Somewhat distended in the middle; ventricular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventriculi', 'pl. ', 'of Ventriculus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventriculus', 'n.', 'One of the stomachs of certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventriculus', 'n.', 'The body cavity of a sponge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventrilocution', 'n.', 'Ventriloquism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventriloquial', 'a.', 'Ventriloquous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventriloquism', 'n.', 'The act, art, or practice of speaking in such a\n   manner that the voice appears to come, not from the person speaking,\n   but from some other source, as from the opposite side of the room, from\n   the cellar, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventriloquist', 'n.', 'One who practices, or is skilled in, ventriloquism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventriloquized', 'imp. & p. p.', 'of Ventriloquize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventriloquizing', 'p. pr. & vb. n.', 'of Ventriloquize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventriloquize', 'v. i.', 'To practice ventriloquism; to speak like a\n   ventriloquist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventriloquous', 'a.', 'Of or pertaining to a ventriloquist or\n   ventriloquism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventriloquy', 'n.', 'Same as Ventriloquism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventrimeson', 'n.', 'See Meson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventro-', '', 'A combining form used in anatomy to indicate connection\n   with, or relation to, the abdomen; also, connection with, relation to,\n   or direction toward, the ventral side; as, ventrolateral;\n   ventro-inguinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventro-inguinal', 'a.', 'Pertaining both to the abdomen and groin, or to\n   the abdomen and inguinal canal; as, ventro-inguinal hernia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venture', 'n.', 'An undertaking of chance or danger; the risking of\n   something upon an event which can not be foreseen with certainty; a\n   hazard; a risk; a speculation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venture', 'n.', 'An event that is not, or can not be, foreseen; an\n   accident; chance; hap; contingency; luck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venture', 'n.', 'The thing put to hazard; a stake; a risk; especially,\n   something sent to sea in trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventured', 'imp. & p. p.', 'of Venture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venturing', 'p. pr. & vb. n.', 'of Venture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venture', 'v. i.', 'To hazard ones self; to have the courage or\n   presumption to do, undertake, or say something; to dare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venture', 'v. i.', 'To make a venture; to run a hazard or risk; to take the\n   chances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venture', 'v. t.', 'To expose to hazard; to risk; to hazard; as, to venture\n   ones person in a balloon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venture', 'v. t.', 'To put or send on a venture or chance; as, to venture a\n   horse to the West Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venture', 'v. t.', 'To confide in; to rely on; to trust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venturer', 'n.', 'One who ventures, or puts to hazard; an adventurer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venturer', 'n.', 'A strumpet; a prostitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venturesome', 'a.', 'Inclined to venture; not loth to run risk or danger;\n   venturous; bold; daring; adventurous; as, a venturesome boy or act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venturine', 'n.', 'Gold powder for covering varnished surfaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venturous', 'n.', 'Daring; bold; hardy; fearless; venturesome; adveturous;\n   as, a venturous soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ventuse', 'v. t. & i.', 'See Ventouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venue', 'n.', 'A neighborhood or near place; the place or county in which\n   anything is alleged to have happened; also, the place where an action\n   is laid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venue', 'n.', 'A bout; a hit; a turn. See Venew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venule', 'n.', 'A small vein; a veinlet; specifically (Zool.), one of the\n   small branches of the veins of the wings in insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venulose', 'a.', 'Full of venules, or small veins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venus', 'n.', 'The goddess of beauty and love, that is, beauty or love\n   deified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venus', 'n.', 'One of the planets, the second in order from the sun, its\n   orbit lying between that of Mercury and that of the Earth, at a mean\n   distance from the sun of about 67,000,000 miles. Its diameter is 7,700\n   miles, and its sidereal period 224.7 days. As the morning star, it was\n   called by the ancients Lucifer; as the evening star, Hesperus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venus', 'n.', 'The metal copper; -- probably so designated from the ancient\n   use of the metal in making mirrors, a mirror being still the\n   astronomical symbol of the planet Venus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venus', 'n.', 'Any one of numerous species of marine bivalve shells of the\n   genus Venus or family Veneridae. Many of these shells are large, and\n   ornamented with beautiful frills; others are smooth, glossy, and\n   handsomely colored. Some of the larger species, as the round clam, or\n   quahog, are valued for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Venust', 'a.', 'Beautiful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veracious', 'a.', 'Observant of truth; habitually speaking truth; truthful;\n   as, veracious historian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veracious', 'a.', 'Characterized by truth; not false; as, a veracious\n   account or narrative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veraciously', 'adv.', 'In a veracious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veracity', 'n.', 'The quality or state of being veracious; habitual\n   observance of truth; truthfulness; truth; as, a man of veracity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veranda', 'n.', 'An open, roofed gallery or portico, adjoining a dwelling\n   house, forming an out-of-door sitting room. See Loggia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veratralbine', 'n.', 'A yellowish amorphous alkaloid extracted from the\n   rootstock of Veratrum album.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veratrate', 'n.', 'A salt of veratric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veratria', 'n.', 'Veratrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veratric', 'a.', 'Pertaining to, or derived from, plants of the genus\n   Veratrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veratrina', 'n.', 'Same as Veratrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veratrine', 'n.', 'A poisonous alkaloid obtained from the root hellebore\n   (Veratrum) and from sabadilla seeds as a white crystalline powder,\n   having an acrid, burning taste. It is sometimes used externally, as in\n   ointments, in the local treatment of neuralgia and rheumatism. Called\n   also veratria, and veratrina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veratrol', 'n.', 'A liquid hydrocarbon obtained by the decomposition of\n   veratric acid, and constituting the dimethyl ether of pyrocatechin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veratrum', 'n.', 'A genus of coarse liliaceous herbs having very poisonous\n   qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verb', 'n.', 'A word; a vocable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verb', 'n.', 'A word which affirms or predicates something of some person\n   or thing; a part of speech expressing being, action, or the suffering\n   of action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbal', 'a.', 'Expressed in words, whether spoken or written, but commonly\n   in spoken words; hence, spoken; oral; not written; as, a verbal\n   contract; verbal testimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbal', 'a.', 'Consisting in, or having to do with, words only; dealing\n   with words rather than with the ideas intended to be conveyed; as, a\n   verbal critic; a verbal change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbal', 'a.', 'Having word answering to word; word for word; literal; as,\n   a verbal translation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbal', 'a.', 'Abounding with words; verbose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbal', 'a.', 'Of or pertaining to a verb; as, a verbal group; derived\n   directly from a verb; as, a verbal noun; used in forming verbs; as, a\n   verbal prefix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbal', 'n.', 'A noun derived from a verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbalism', 'n.', 'Something expressed verbally; a verbal remark or\n   expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbalist', 'n.', 'A literal adherent to, or a minute critic of, words; a\n   literalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbality', 'n.', 'The quality or state of being verbal; mere words; bare\n   literal expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbalization', 'n.', 'The act of verbalizing, or the state of being\n   verbalized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbalized', 'imp. & p. p.', 'of Verbalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbalizing', 'p. pr. & vb. n.', 'of Verbalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbalize', 'v. t.', 'To convert into a verb; to verbify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbalize', 'v. i.', 'To be verbose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbally', 'adv.', 'In a verbal manner; orally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbally', 'adv.', 'Word for word; verbatim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbarian', 'a.', 'Of or pertaining to words; verbal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbarian', 'n.', 'One who coins words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbarium', 'n.', 'A game in word making. See Logomachy, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbatim', 'adv.', 'Word for word; in the same words; verbally; as, to tell\n   a story verbatim as another has related it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbena', 'n.', 'A genus of herbaceous plants of which several species are\n   extensively cultivated for the great beauty of their flowers; vervain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbenaceous', 'a.', 'Of or pertaining to a natural order (Verbenaceae) of\n   gamopetalous plants of which Verbena is the type. The order includes\n   also the black and white mangroves, and many plants noted for medicinal\n   use or for beauty of bloom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbenated', 'imp. & p. p.', 'of Verbenate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbenating', 'p. pr. & vb. n.', 'of Verbenate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbenate', 'v. t.', 'To strew with verbena, or vervain, as in ancient\n   sacrifices and rites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verberate', 'v. t.', 'To beat; to strike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verberation', 'n.', 'The act of verberating; a beating or striking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verberation', 'n.', 'The impulse of a body; which causes sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbiage', 'n.', 'The use of many words without necessity, or with little\n   sense; a superabundance of words; verbosity; wordiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verify', 'v. t.', 'To make into a verb; to use as a verb; to verbalize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbose', 'a.', 'Abounding in words; using or containing more words than\n   are necessary; tedious by a multiplicity of words; prolix; wordy; as, a\n   verbose speaker; a verbose argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbosities', 'pl. ', 'of Verbosity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verbosity', 'n.', 'The quality or state of being verbose; the use of more\n   words than are necessary; prolixity; wordiness; verbiage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verd', 'n.', 'The privilege of cutting green wood within a forest for fuel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verd', 'n.', 'The right of pasturing animals in a forest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verd', 'n.', 'Greenness; freshness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verdancy', 'n.', 'The quality or state of being verdant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verdant', 'a.', 'Covered with growing plants or grass; green; fresh;\n   flourishing; as, verdant fields; a verdant lawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verdant', 'a.', 'Unripe in knowledge or judgment; unsophisticated; raw;\n   green; as, a verdant youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verd antique', '', 'A mottled-green serpentine marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verd antique', '', 'A green porphyry called oriental verd antique.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verdantly', 'adv.', 'In a verdant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verderer', 'n.', 'Alt. of Verderor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verderor', 'n.', 'An officer who has the charge of the kings forest, to\n   preserve the vert and venison, keep the assizes, view, receive, and\n   enroll attachments and presentments of all manner of trespasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verdict', 'n.', 'The answer of a jury given to the court concerning any\n   matter of fact in any cause, civil or criminal, committed to their\n   examination and determination; the finding or decision of a jury on the\n   matter legally submitted to them in the course of the trial of a cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verdict', 'n.', 'Decision; judgment; opinion pronounced; as, to be\n   condemned by the verdict of the public.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verdigris', 'n.', 'A green poisonous substance used as a pigment and drug,\n   obtained by the action of acetic acid on copper, and consisting\n   essentially of a complex mixture of several basic copper acetates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verdigris', 'n.', 'The green rust formed on copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verdigris', 'v. t.', 'To cover, or coat, with verdigris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verdin', 'n.', 'A small yellow-headed bird (Auriparus flaviceps) of Lower\n   California, allied to the titmice; -- called also goldtit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verdine', 'n.', 'A commercial name for green aniline dye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verdingale', 'n.', 'See Farthingale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verdit', 'n.', 'Verdict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verditer', 'n.', 'Verdigris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verditer', 'n.', 'Either one of two pigments (called blue verditer, and\n   green verditer) which are made by treating copper nitrate with calcium\n   carbonate (in the form of lime, whiting, chalk, etc.) They consist of\n   hydrated copper carbonates analogous to the minerals azurite and\n   malachite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verditure', 'n.', 'The faintest and palest green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verdoy', 'a.', 'Charged with leaves, fruits, flowers, etc.; -- said of a\n   border.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verdure', 'n.', 'Green; greenness; freshness of vegetation; as, the verdure\n   of the meadows in June.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verdured', 'a.', 'Covered with verdure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verdureless', 'a.', 'Destitute of verdure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verdurous', 'a.', 'Covered with verdure; clothed with the fresh green of\n   vegetation; verdured; verdant; as, verdurous pastures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verecund', 'a.', 'Rashful; modest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verecundious', 'a.', 'Verecund.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verecundity', 'n.', 'The quality or state of being verecund; modesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veretillum', 'n.', 'Any one of numerous species of club-shaped, compound\n   Alcyonaria belonging to Veretillum and allied genera, of the tribe\n   Pennatulacea. The whole colony can move about as if it were a simple\n   animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vergalien', 'n.', 'Alt. of Vergaloo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vergaloo', 'n.', 'See Virgalieu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verge', 'n.', 'A rod or staff, carried as an emblem of authority; as, the\n   verge, carried before a dean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verge', 'n.', 'The stick or wand with which persons were formerly admitted\n   tenants, they holding it in the hand, and swearing fealty to the lord.\n   Such tenants were called tenants by the verge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verge', 'n.', 'The compass of the court of Marshalsea and the Palace court,\n   within which the lord steward and the marshal of the kings household\n   had special jurisdiction; -- so called from the verge, or staff, which\n   the marshal bore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verge', 'n.', 'A virgate; a yardland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verge', 'n.', 'A border, limit, or boundary of a space; an edge, margin, or\n   brink of something definite in extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verge', 'n.', 'A circumference; a circle; a ring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verge', 'n.', 'The shaft of a column, or a small ornamental shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verge', 'n.', 'The edge of the tiling projecting over the gable of a roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verge', 'n.', 'The spindle of a watch balance, especially one with pallets,\n   as in the old vertical escapement. See under Escapement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verge', 'n.', 'The edge or outside of a bed or border.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verge', 'n.', 'A slip of grass adjoining gravel walks, and dividing them\n   from the borders in a parterre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verge', 'n.', 'The penis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verge', 'n.', 'The external male organ of certain mollusks, worms, etc. See\n   Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verged', 'imp. & p. p.', 'of Verge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verging', 'p. pr. & vb. n.', 'of Verge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verge', 'v. i.', 'To border upon; to tend; to incline; to come near; to\n   approach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verge', 'v. i.', 'To tend downward; to bend; to slope; as, a hill verges to\n   the north.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vergeboard', 'n.', 'The ornament of woodwork upon the gable of a house,\n   used extensively in the 15th century. It was generally suspended from\n   the edge of the projecting roof (see Verge, n., 4), and in position\n   parallel to the gable wall. Called also bargeboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vergency', 'n.', 'The act of verging or approaching; tendency; approach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vergency', 'n.', 'The reciprocal of the focal distance of a lens, used as\n   measure of the divergence or convergence of a pencil of rays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verger', 'n.', 'One who carries a verge, or emblem of office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verger', 'n.', 'An attendant upon a dignitary, as on a bishop, a dean, a\n   justice, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verger', 'n.', 'The official who takes care of the interior of a church\n   building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verger', 'n.', 'A garden or orchard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vergette', 'a.', 'Divided by pallets, or pales; paly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vergette', 'n.', 'A small pale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veridical', 'a.', 'Truth-telling; truthful; veracious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verifiable', 'a.', 'Capable of being verified; confirmable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verfication', 'n.', 'The act of verifying, or the state of being verified;\n   confirmation; authentication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verfication', 'n.', 'Confirmation by evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verfication', 'n.', 'A formal phrase used in concluding a plea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verificative', 'a.', 'Serving to verify; verifying; authenciating;\n   confirming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verifier', 'n.', 'One who, or that which, verifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verified', 'imp. & p. p.', 'of Verify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verifying', 'p. pr. & vb. n.', 'of Verify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verify', 'v. t.', 'To prove to be true or correct; to establish the truth\n   of; to confirm; to substantiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verify', 'v. t.', 'To confirm or establish the authenticity of by\n   examination or competent evidence; to authenciate; as, to verify a\n   written statement; to verify an account, a pleading, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verify', 'v. t.', 'To maintain; to affirm; to support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veriloquent', 'a.', 'Speaking truth; truthful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verily', 'adv.', 'In very truth; beyond doubt or question; in fact;\n   certainly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verine', 'n.', 'An alkaloid obtained as a yellow amorphous substance by the\n   decomposition of veratrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verisimilar', 'a.', 'Having the appearance of truth; probable; likely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verisimilitude', 'n.', 'The quality or state of being verisimilar; the\n   appearance of truth; probability; likelihood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verisimility', 'n.', 'Verisimilitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versimilous', 'a.', 'Verisimilar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veritable', 'a.', 'Agreeable to truth or to fact; actual; real; true;\n   genuine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veritas', 'n.', 'The Bureau Veritas. See under Bureau.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verities', 'pl. ', 'of Verity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verity', 'n.', 'The quality or state of being true, or real; consonance of\n   a statement, proposition, or other thing, with fact; truth; reality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verity', 'n.', 'That which is true; a true assertion or tenet; a truth; a\n   reality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verjuice', 'n.', 'The sour juice of crab apples, of green or unripe grapes,\n   apples, etc.; also, an acid liquor made from such juice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verjuice', 'n.', 'Tartness; sourness, as of disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermeil', 'n.', 'Vermilion; also, the color of vermilion, a bright,\n   beautiful red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermeil', 'n.', 'Silver gilt or gilt bronze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermeil', 'n.', 'A liquid composition applied to a gilded surface to give\n   luster to the gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermeologist', 'n.', 'One who treats of vermes, or worms; a\n   helminthologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermeology', 'n.', 'A discourse or treatise on worms; that part of zoology\n   which treats of worms; helminthology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermes', 'n. pl.', 'An extensive artificial division of the animal kingdom,\n   including the parasitic worms, or helminths, together with the\n   nemerteans, annelids, and allied groups. By some writers the\n   branchiopods, the bryzoans, and the tunicates are also included. The\n   name was used in a still wider sense by Linnaeus and his followers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermes', 'n. pl.', 'A more restricted group, comprising only the helminths\n   and closely allied orders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermetid', 'n.', 'Any species of vermetus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermetus', 'n.', 'Any one of many species of marine gastropods belonging to\n   Vermetus and allied genera, of the family Vermetidae. Their shells are\n   regularly spiral when young, but later in life the whorls become\n   separate, and the shell is often irregularly bent and contorted like a\n   worm tube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermicelli', 'n.', 'The flour of a hard and small-grained wheat made into\n   dough, and forced through small cylinders or pipes till it takes a\n   slender, wormlike form, whence the Italian name. When the paste is made\n   in larger tubes, it is called macaroni.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermicide', 'n.', 'A medicine which destroys intestinal worms; a worm\n   killer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermicious', 'a.', 'Of or pertaining to worms; wormy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermicular', 'a.', 'Of or pertaining to a worm or worms; resembling a worm;\n   shaped like a worm; especially, resembling the motion or track of a\n   worm; as, the vermicular, or peristaltic, motion of the intestines. See\n   Peristaltic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermiculated', 'imp. & p. p.', 'of Vermiculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermiculating', 'p. pr. & vb. n.', 'of Vermiculate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermiculate', 'v. t.', 'To form or work, as by inlaying, with irregular\n   lines or impressions resembling the tracks of worms, or appearing as if\n   formed by the motion of worms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermiculate', 'a.', 'Wormlike in shape; covered with wormlike elevations;\n   marked with irregular fine lines of color, or with irregular wavy\n   impressed lines like worm tracks; as, a vermiculate nut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermiculate', 'a.', 'Crawling or creeping like a worm; hence, insinuating;\n   sophistical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermiculated', 'a.', 'Made or marked with irregular wavy lines or\n   impressions; vermiculate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermiculation', 'n.', 'The act or operation of moving in the manner of a\n   worm; continuation of motion from one part to another; as, the\n   vermiculation, or peristaltic motion, of the intestines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermiculation', 'n.', 'The act of vermiculating, or forming or inlaying so\n   as to resemble the motion, track, or work of a worm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermiculation', 'n.', 'Penetration by worms; the state of being wormeaten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermiculation', 'n.', 'A very fine wavy crosswise color marking, or a patch\n   of such markings, as on the feathers of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermicule', 'n.', 'A small worm or insect larva; also, a wormlike body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermiculite', 'n.', 'A group of minerals having, a micaceous structure.\n   They are hydrous silicates, derived generally from the alteration of\n   some kind of mica. So called because the scales, when heated, open out\n   into wormlike forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermiculose', 'a.', 'Alt. of Vermiculous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermiculous', 'a.', 'Containing, or full of, worms; resembling worms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermiform', 'a.', 'Resembling a worm in form or motions; vermicular; as,\n   the vermiform process of the cerebellum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermiformia', 'n. pl.', 'A tribe of worms including Phoronis. See Phoronis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermifugal', 'a.', 'Tending to prevent, destroy, or expel, worms or vermin;\n   anthelmintic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermifuge', 'n.', 'A medicine or substance that expels worms from animal\n   bodies; an anthelmintic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermil', 'n.', 'See Vermeil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermilinguia', 'n. pl.', 'A tribe of edentates comprising the South\n   American ant-eaters. The tongue is long, slender, exsertile, and very\n   flexible, whence the name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermilinguia', 'n. pl.', 'A tribe of Old World lizards which comprises the\n   chameleon. They have long, flexible tongues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermilion', 'n.', 'A bright red pigment consisting of mercuric sulphide,\n   obtained either from the mineral cinnabar or artificially. It has a\n   fine red color, and is much used in coloring sealing wax, in printing,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermilion', 'n.', 'Hence, a red color like the pigment; a lively and\n   brilliant red; as, cheeks of vermilion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermilion', 'v. t.', 'To color with vermilion, or as if with vermilion; to\n   dye red; to cover with a delicate red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermily', 'n.', 'Vermeil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermin', 'n. sing. & pl.', 'An animal, in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermin', 'n. sing. & pl.', 'A noxious or mischievous animal; especially,\n   noxious little animals or insects, collectively, as squirrels, rats,\n   mice, flies, lice, bugs, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermin', 'n. sing. & pl.', 'Hence, in contempt, noxious human beings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verminate', 'v. i.', 'To breed vermin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermination', 'n.', 'The generation or breeding of vermin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermination', 'n.', 'A griping of the bowels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verminly', 'a. & adv.', 'Resembling vermin; in the manner of vermin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verminous', 'a.', 'Tending to breed vermin; infested by vermin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verminous', 'a.', 'Caused by, or arising from the presence of, vermin; as,\n   verminous disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verminously', 'adv.', 'In a verminous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermiparous', 'a.', 'Producing or breeding worms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermivorous', 'a.', 'Devouring worms; feeding on worms; as, vermivorous\n   birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vermuth', 'n.', 'A liqueur made of white wine, absinthe, and various\n   aromatic drugs, used to excite the appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernacle', 'n.', 'See Veronica, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernacular', 'a.', 'Belonging to the country of ones birth; ones own by\n   birth or nature; native; indigenous; -- now used chiefly of language;\n   as, English is our vernacular language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernacular', 'n.', 'The vernacular language; ones mother tongue; often,\n   the common forms of expression in a particular locality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernacularism', 'n.', 'A vernacular idiom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernacularization', 'n.', 'The act or process of making vernacular, or the\n   state of being made vernacular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernacularly', 'adv.', 'In a vernacular manner; in the vernacular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernaculous', 'a.', 'Vernacular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernaculous', 'a.', 'Scoffing; scurrilous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernage', 'n.', 'A kind of sweet wine from Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernal', 'a.', 'Of or pertaining to the spring; appearing in the spring;\n   as, vernal bloom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernal', 'a.', 'Fig.: Belonging to youth, the spring of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernant', 'a.', 'Flourishing, as in spring; vernal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernate', 'v. i.', 'To become young again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernation', 'n.', 'The arrangement of the leaves within the leaf bud, as\n   regards their folding, coiling, rolling, etc.; prefoliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernicle', 'n.', 'A Veronica. See Veronica, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernicose', 'a.', 'Having a brilliantly polished surface, as some leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernier', 'n.', 'A short scale made to slide along the divisions of a\n   graduated instrument, as the limb of a sextant, or the scale of a\n   barometer, for indicating parts of divisions. It is so graduated that a\n   certain convenient number of its divisions are just equal to a certain\n   number, either one less or one more, of the divisions of the\n   instrument, so that parts of a division are determined by observing\n   what line on the vernier coincides with a line on the instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernile', 'a.', 'Suiting a salve; servile; obsequious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernility', 'n.', 'Fawning or obsequious behavior; servility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernine', 'n.', 'An alkaloid extracted from the shoots of the vetch, red\n   clover, etc., as a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernish', 'n. & v.', 'Varnish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vernonin', 'n.', 'A glucoside extracted from the root of a South African\n   plant of the genus Vernonia, as a deliquescent powder, and used as a\n   mild heart tonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veronese', 'a.', 'Of or pertaining to Verona, in Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veronese', 'n. sing. & pl.', 'A native of Verona; collectively, the people\n   of Verona.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veronica', 'n.', 'A portrait or representation of the face of our Savior on\n   the alleged handkerchief of Saint Veronica, preserved at Rome; hence, a\n   representation of this portrait, or any similar representation of the\n   face of the Savior. Formerly called also Vernacle, and Vernicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veronica', 'n.', 'A genus scrophulariaceous plants; the speedwell. See\n   Speedwell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verray', 'a.', 'Very; true.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verrayment', 'adv.', 'Verily; truly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verrel', 'n.', 'See Ferrule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verriculate', 'a.', 'Having thickset tufts of parallel hairs, bristles, or\n   branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verruciform', 'a.', 'Shaped like a wart or warts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verrucose', 'a.', 'Covered with wartlike elevations; tuberculate; warty;\n   verrucous; as, a verrucose capsule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verrucous', 'a.', 'Verrucose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verruculose', 'a.', 'Minutely verrucose; as, a verruculose leaf or stalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vers', 'n. sing. & pl.', 'A verse or verses. See Verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versability', 'n.', 'The quality or state of being versable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versable', 'a.', 'Capable of being turned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versableness', 'n.', 'Versability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versal', 'a.', 'Universal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versant', 'a.', 'Familiar; conversant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versant', 'n.', 'The slope of a side of a mountain chain; hence, the\n   general slope of a country; aspect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versatile', 'a.', 'Capable of being turned round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versatile', 'a.', 'Liable to be turned in opinion; changeable; variable;\n   unsteady; inconstant; as versatile disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versatile', 'a.', 'Turning with ease from one thing to another; readily\n   applied to a new task, or to various subjects; many-sided; as,\n   versatile genius; a versatile politician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versatile', 'a.', 'Capable of turning; freely movable; as, a versatile\n   anther, which is fixed at one point to the filament, and hence is very\n   easily turned around; a versatile toe of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versatility', 'n.', 'The quality or state of being versatile;\n   versatileness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vers de societe', '', 'See Society verses, under Society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verse', 'n.', 'A line consisting of a certain number of metrical feet (see\n   Foot, n., 9) disposed according to metrical rules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verse', 'n.', 'Metrical arrangement and language; that which is composed in\n   metrical form; versification; poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verse', 'n.', 'A short division of any composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verse', 'n.', 'A stanza; a stave; as, a hymn of four verses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verse', 'n.', 'One of the short divisions of the chapters in the Old and\n   New Testaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verse', 'n.', 'A portion of an anthem to be performed by a single voice to\n   each part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verse', 'n.', 'A piece of poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versed', 'imp. & p. p.', 'of Verse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versing', 'p. pr. & vb. n.', 'of Verse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verse', 'v. t.', 'To tell in verse, or poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verse', 'v. i.', 'To make verses; to versify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versed', 'a.', 'Acquainted or familiar, as the result of experience, study,\n   practice, etc.; skilled; practiced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versed', 'a.', 'Turned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verseman', 'n.', 'Same as Versemonger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versemonger', 'n.', 'A writer of verses; especially, a writer of\n   commonplace poetry; a poetaster; a rhymer; -- used humorously or in\n   contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verser', 'n.', 'A versifier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verset', 'n.', 'A verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versicle', 'n.', 'A little verse; especially, a short verse or text said or\n   sung in public worship by the priest or minister, and followed by a\n   response from the people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versicolor', 'a.', 'Alt. of Versicolored');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versicolored', 'a.', 'Having various colors; changeable in color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versicular', 'a.', 'Of or pertaining to verses; designating distinct\n   divisions of a writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versification', 'n.', 'The act, art, or practice, of versifying, or making\n   verses; the construction of poetry; metrical composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versificator', 'n.', 'A versifier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versifier', 'n.', 'One who versifies, or makes verses; as, not every\n   versifier is a poet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versifier', 'n.', 'One who converts into verse; one who expresses in verse\n   the ideas of another written in prose; as, Dr. Watts was a versifier of\n   the Psalms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versified', 'imp. & p. p.', 'of Versify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versifying', 'p. pr. & vb. n.', 'of Versify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versify', 'v. i.', 'To make verses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versify', 'v. t.', 'To relate or describe in verse; to compose in verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versify', 'v. t.', 'To turn into verse; to render into metrical form; as,\n   to versify the Psalms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Version', 'n.', 'A change of form, direction, or the like; transformation;\n   conversion; turning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Version', 'n.', 'A condition of the uterus in which its axis is deflected\n   from its normal position without being bent upon itself. See\n   Anteversion, and Retroversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Version', 'n.', 'The act of translating, or rendering, from one language\n   into another language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Version', 'n.', 'A translation; that which is rendered from another\n   language; as, the Common, or Authorized, Version of the Scriptures (see\n   under Authorized); the Septuagint Version of the Old Testament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Version', 'n.', 'An account or description from a particular point of view,\n   especially as contrasted with another account; as, he gave another\n   version of the affair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versionist', 'n.', 'One who makes or favors a version; a translator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verso', 'n.', 'The reverse, or left-hand, page of a book or a folded sheet\n   of paper; -- opposed to recto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versor', 'n.', 'The turning factor of a quaternion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verst', 'n.', 'A Russian measure of length containing 3,500 English feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versual', 'a.', 'Of or pertaining to a verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versus', 'prep.', 'Against; as, John Doe versus Richard Roe; -- chiefly\n   used in legal language, and abbreviated to v. or vs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Versute', 'a.', 'Crafty; wily; cunning; artful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vert', 'n.', 'Everything that grows, and bears a green leaf, within the\n   forest; as, to preserve vert and venison is the duty of the verderer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vert', 'n.', 'The right or privilege of cutting growing wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vert', 'n.', 'The color green, represented in a drawing or engraving by\n   parallel lines sloping downward toward the right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verteber', 'n.', 'A vertebra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertebrae', 'pl. ', 'of Vertebra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertebra', 'n.', 'One of the serial segments of the spinal column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertebra', 'n.', 'One of the central ossicles in each joint of the arms of\n   an ophiuran.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertebral', 'a.', 'Of or pertaining to a vertebrae, or the vertebral\n   column; spinal; rachidian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertebral', 'a.', 'Vertebrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertebral', 'n.', 'A vertebrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertebrally', 'adv.', 'At or within a vertebra or vertebrae; --\n   distinguished from interverterbrally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertebrarterial', 'a.', 'Of or pertaining to a vertebrae and an artery; --\n   said of the foramina in the transverse processes of cervical vertebrae\n   and of the canal which they form for the vertebral artery and vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertebrata', 'n. pl.', 'One of the grand divisions of the animal kingdom,\n   comprising all animals that have a backbone composed of bony or\n   cartilaginous vertebrae, together with Amphioxus in which the backbone\n   is represented by a simple undivided notochord. The Vertebrata always\n   have a dorsal, or neural, cavity above the notochord or backbone, and a\n   ventral, or visceral, cavity below it. The subdivisions or classes of\n   Vertebrata are Mammalia, Aves, Reptilia, Amphibia, Pisces,\n   Marsipobranchia, and Leptocardia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertebrate', 'n.', 'One of the Vertebrata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertebrate', 'a.', 'Alt. of Vertebrated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertebrated', 'a.', 'Having a backbone, or vertebral column, containing the\n   spinal marrow, as man, quadrupeds, birds, amphibia, and fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertebrated', 'a.', 'Contracted at intervals, so as to resemble the spine\n   in animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertebrated', 'a.', 'Having movable joints resembling vertebrae; -- said of\n   the arms ophiurans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertebrated', 'a.', 'Of or pertaining to the Vertebrata; -- used only in\n   the form vertebrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertebre', 'n.', 'A vertebra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertebro-', '', 'A combining form used in anatomy to indicate connection\n   with, or relation to, a vertebra, vertebrae, or vertebral column; as in\n   vertebrocostal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertebro-iliac', 'a.', 'Iliolumbar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertexes', 'pl. ', 'of Vertex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertices', 'pl. ', 'of Vertex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertex', 'n.', 'A turning point; the principal or highest point; top;\n   summit; crown; apex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertex', 'n.', 'The top, or crown, of the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertex', 'n.', 'The zenith, or the point of the heavens directly overhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertex', 'n.', 'The point in any figure opposite to, and farthest from, the\n   base; the terminating point of some particular line or lines in a\n   figure or a curve; the top, or the point opposite the base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertical', 'a.', 'Of or pertaining to the vertex; situated at the vertex,\n   or highest point; directly overhead, or in the zenith; perpendicularly\n   above one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertical', 'a.', 'Perpendicular to the plane of the horizon; upright;\n   plumb; as, a vertical line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertical', 'n.', 'Vertical position; zenith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertical', 'n.', 'A vertical line, plane, or circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verticality', 'n.', 'The quality or state of being vertical; verticalness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertically', 'adv.', 'In a vertical manner, position, or direction;\n   perpendicularly; as, to look down vertically; to raise a thing\n   vertically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verticalness', 'n.', 'Quality or state of being vertical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verticil', 'n.', 'A circle either of leaves or flowers about a stem at the\n   same node; a whorl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verticillaster', 'a.', 'A whorl of flowers apparently of one cluster, but\n   composed of two opposite axillary cymes, as in mint. See Illust. of\n   Whorl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verticillate', 'a.', 'Alt. of Verticillated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verticillated', 'a.', 'Arranged in a transverse whorl or whorls like the\n   rays of a wheel; as, verticillate leaves of a plant; a verticillate\n   shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verticillus', 'n.', 'A whorl; a verticil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verticity', 'n.', 'The quality or power of turning; revolution; rotation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verticle', 'n.', 'An axis; hinge; a turning point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertiginate', 'a.', 'Turned round; giddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertiginous', 'a.', 'Turning round; whirling; rotary; revolving; as,\n   vertiginous motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertiginous', 'a.', 'Affected with vertigo; giddy; dizzy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertigoes', 'pl. ', 'of Vertigo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertigines', 'pl. ', 'of Vertigo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertigo', 'n.', 'Dizziness or swimming of the head; an affection of the\n   head in which objects, though stationary, appear to move in various\n   directions, and the person affected finds it difficult to maintain an\n   erect posture; giddiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertigo', 'n.', 'Any one of numerous species of small land snails belonging\n   to the genus Vertigo, having an elongated or conical spiral shell and\n   usually teeth in the aperture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertilinear', 'a.', 'Straight; rectilinear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertu', 'n.', 'Virtue; power. See Virtue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertu', 'n.', 'See Virtu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vertuous', 'a.', 'Virtuous; powerful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verumontanum', 'n.', 'An elevation, or crest, in the wall of the urethra\n   where the seminal ducts enter it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vervain', 'n.', 'Any plant of the genus Verbena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Verve', 'n.', 'Excitement of imagination such as animates a poet, artist,\n   or musician, in composing or performing; rapture; enthusiasm; spirit;\n   energy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vervel', 'n.', 'See Varvel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vervet', 'n.', 'A South African monkey (Cercopithecus pygerythrus, /\n   Lelandii). The upper parts are grayish green, finely specked with\n   black. The cheeks and belly are reddish white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Very', 'v. t.', 'True; real; actual; veritable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Very', 'adv.', 'In a high degree; to no small extent; exceedingly;\n   excessively; extremely; as, a very great mountain; a very bright sum; a\n   very cold day; the river flows very rapidly; he was very much hurt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesbium', 'n.', 'A rare metallic element of which little is known. It is\n   said by Scacchi to have been extracted from a yellowish incrustation\n   from the cracks of a Vesuvian lava erupted in 1631.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vese', 'n.', 'Onset; rush; violent draught or wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesica', 'n.', 'A bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesical', 'a.', 'Of or pertaining to the bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicant', 'n.', 'A vesicatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicated', 'imp. & p. p.', 'of Vesicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicating', 'p. pr. & vb. n.', 'of Vesicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicate', 'v. t.', 'To raise little bladders or blisters upon; to inflame\n   and separate the cuticle of; to blister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesication', 'n.', 'The process of vesicating, or of raising blisters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicatory', 'a.', 'Tending, or having power, to raise a blister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicatory', 'n.', 'A blistering application or plaster; a vesicant; an\n   epispastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicle', 'n.', 'A bladderlike vessel; a membranous cavity; a cyst; a cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicle', 'n.', 'A small bladderlike body in the substance of vegetable, or\n   upon the surface of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicle', 'n.', 'A small, and more or less circular, elevation of the\n   cuticle, containing a clear watery fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicle', 'n.', 'A cavity or sac, especially one filled with fluid; as, the\n   umbilical vesicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicle', 'n.', 'A small convex hollow prominence on the surface of a shell\n   or a coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicle', 'n.', 'A small cavity, nearly spherical in form, and usually of\n   the size of a pea or smaller, such as are common in some volcanic\n   rocks. They are produced by the liberation of watery vapor in the\n   molten mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesico-', '', 'A combining form used in anatomy to indicate connection\n   with, or relation to, the bladder; as in vesicoprostatic,\n   vesicovaginal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicoprostatic', 'a.', 'Of a pertaining to the bladder and the prostrate\n   gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicouterine', 'a.', 'Of or pertaining to the bladder and the uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicovaginal', 'a.', 'Of or pertaining to the bladder and the vagina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesiculae', 'pl. ', 'of Vesicula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicula', 'n.', 'A vesicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicular', 'a.', 'Of or pertaining to vesicles; esp., of or pertaining to\n   the air vesicles, or air cells, of the lungs; as, vesicular breathing,\n   or normal breathing, in which the air enters freely the air vesicles of\n   the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicular', 'a.', 'Containing, or composed of, vesicles or vesiclelike\n   structures; covered with vesicles or bladders; vesiculate; as,\n   vesicular coral; vesicular lava; a vesicular leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicular', 'a.', 'Having the form or structure of a vesicle; as, a\n   vesicular body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesicularia', 'n.', 'Any one of numerous species of marine Bryozoa\n   belonging to Vesicularia and allied genera. They have delicate tubular\n   cells attached in clusters to slender flexible stems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesiculata', 'n. pl.', 'The campanularian medusae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesiculate', 'a.', 'Bladdery; full of, or covered with, bladders;\n   vesicular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesiculate', 'v. t.', 'To form vesicles in, as lava.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesiculation', 'n.', 'The state of containing vesicles, or the process by\n   which vesicles are formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesiculose', 'a.', 'Alt. of Vesiculous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesiculous', 'a.', 'Bladdery; vesicular; vesiculate; composed of vesicles;\n   covered with vesicles; as, a vesiculose shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vespa', 'n.', 'A genus of Hymenoptera including the common wasps and\n   hornets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesper', 'n.', 'The evening star; Hesper; Venus, when seen after sunset;\n   hence, the evening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesper', 'a.', 'Of or pertaining to the evening, or to the service of\n   vespers; as, a vesper hymn; vesper bells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesperal', 'a.', 'Vesper; evening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vespers', 'n.', 'One of the little hours of the Breviary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vespers', 'n.', 'The evening song or service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vespertilio', 'n.', 'A genus of bats including some of the common small\n   insectivorous species of North America and Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vespertiliones', 'n. pl.', 'A tribe of bats including the common\n   insectivorous bats of America and Europe, belonging to Vespertilio and\n   allied genera. They lack a nose membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vespertilionine', 'a.', 'Of or pertaining to the Vespertiliones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vespertinal', 'a.', 'Vespertine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vespertine', 'a.', 'Of or pertaining to the evening; happening or being in\n   the evening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vespertine', 'a.', 'Blossoming in the evening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vespiary', 'n.', 'A nest, or habitation, of insects of the wasp kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vespilloes', 'pl. ', 'of Vespillo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vespillo', 'n.', 'One who carried out the dead bodies of the poor at night\n   for burial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vessel', 'n.', 'A hollow or concave utensil for holding anything; a hollow\n   receptacle of any kind, as a hogshead, a barrel, a firkin, a bottle, a\n   kettle, a cup, a bowl, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vessel', 'n.', 'A general name for any hollow structure made to float upon\n   the water for purposes of navigation; especially, one that is larger\n   than a common rowboat; as, a war vessel; a passenger vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vessel', 'n.', 'Fig.: A person regarded as receiving or containing\n   something; esp. (Script.), one into whom something is conceived as\n   poured, or in whom something is stored for use; as, vessels of wrath or\n   mercy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vessel', 'n.', 'Any tube or canal in which the blood or other fluids are\n   contained, secreted, or circulated, as the arteries, veins, lymphatics,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vessel', 'n.', 'A continuous tube formed from superposed large cylindrical\n   or prismatic cells (tracheae), which have lost their intervening\n   partitions, and are usually marked with dots, pits, rings, or spirals\n   by internal deposition of secondary membranes; a duct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vessel', 'v. t.', 'To put into a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesselfuls', 'pl. ', 'of Vesselful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesselful', 'n.', 'As much as a vessel will hold; enough to fill a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesses', 'n.', 'Alt. of Vessets');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vessets', 'n.', 'A kind of worsted; also, a worsted cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vessicnon', 'n.', 'Alt. of Vessignon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vessignon', 'n.', 'A soft swelling on a horses leg; a windgall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vest', 'n.', 'An article of clothing covering the person; an outer garment;\n   a vestment; a dress; a vesture; a robe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vest', 'n.', 'Any outer covering; array; garb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vest', 'n.', 'Specifically, a waistcoat, or sleeveless body garment, for\n   men, worn under the coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vested', 'imp. & p. p.', 'of Vest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesting', 'p. pr. & vb. n.', 'of Vest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vest', 'n.', 'To clothe with, or as with, a vestment, or garment; to dress;\n   to robe; to cover, surround, or encompass closely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vest', 'n.', 'To clothe with authority, power, or the like; to put in\n   possession; to invest; to furnish; to endow; -- followed by with before\n   the thing conferred; as, to vest a court with power to try cases of\n   life and death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vest', 'n.', 'To place or give into the possession or discretion of some\n   person or authority; to commit to another; -- with in before the\n   possessor; as, the power of life and death is vested in the king, or in\n   the courts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vest', 'n.', 'To invest; to put; as, to vest money in goods, land, or\n   houses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vest', 'n.', 'To clothe with possession; as, to vest a person with an\n   estate; also, to give a person an immediate fixed right of present or\n   future enjoyment of; as, an estate is vested in possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vest', 'v. i.', 'To come or descend; to be fixed; to take effect, as a\n   title or right; -- followed by in; as, upon the death of the ancestor,\n   the estate, or the right to the estate, vests in the heir at law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesta', 'n.', 'One of the great divinities of the ancient Romans, identical\n   with the Greek Hestia. She was a virgin, and the goddess of the hearth;\n   hence, also, of the fire on it, and the family round it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesta', 'n.', 'An asteroid, or minor planet, discovered by Olbers in 1807.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesta', 'n.', 'A wax friction match.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestal', 'a.', 'Of or pertaining to Vesta, the virgin goddess of the\n   hearth; hence, pure; chaste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestal', 'a.', 'A virgin consecrated to Vesta, and to the service of\n   watching the sacred fire, which was to be perpetually kept burning upon\n   her altar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestal', 'a.', 'A virgin; a woman pure and chaste; also, a nun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestales', 'n. pl.', 'A group of butterflies including those known as\n   virgins, or gossamer-winged butterflies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vested', 'a.', 'Clothed; robed; wearing vestments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vested', 'a.', 'Not in a state of contingency or suspension; fixed; as,\n   vested rights; vested interests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestiarian', 'a.', 'Of or pertaining to a vestiary or vestments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestiary', 'n.', 'A wardrobe; a robing room; a vestry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestiary', 'a.', 'Pertaining to clothes, or vestments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestibular', 'a.', 'Of or pertaining to a vestibule; like a vestibule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestibule', 'n.', 'The porch or entrance into a house; a hall or\n   antechamber next the entrance; a lobby; a porch; a hall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestibula', 'pl. ', 'of Vestibulum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestibulum', 'n.', 'A cavity into which, in certain bryozoans, the\n   esophagus and anus open.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestigate', 'v. t.', 'To investigate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestige', 'n.', 'The mark of the foot left on the earth; a track or\n   footstep; a trace; a sign; hence, a faint mark or visible sign left by\n   something which is lost, or has perished, or is no longer present;\n   remains; as, the vestiges of ancient magnificence in Palmyra; vestiges\n   of former population.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestigial', 'a.', 'Of or pertaining to a vestige or remnant; like a\n   vestige.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesting', 'n.', 'Cloth for vests; a vest pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestiture', 'n.', 'In vestiture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestlet', 'n.', 'Any one of several species of actinians belonging to the\n   genus Cerianthus. These animals have a long, smooth body tapering to\n   the base, and two separate circles of tentacles around the mouth. They\n   form a tough, flexible, feltlike tube with a smooth internal lining, in\n   which they dwell, whence the name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestment', 'n.', 'A covering or garment; some part of clothing or dress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestment', 'n.', 'any priestly garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestries', 'pl. ', 'of Vestry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestry', 'n.', 'A room appendant to a church, in which sacerdotal vestments\n   and sacred utensils are sometimes kept, and where meetings for worship\n   or parish business are held; a sacristy; -- formerly called revestiary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestry', 'n.', 'A parochial assembly; an assembly of persons who manage\n   parochial affairs; -- so called because usually held in a vestry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestry', 'n.', 'A body, composed of wardens and vestrymen, chosen annually\n   by a parish to manage its temporal concerns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestrymen', 'pl. ', 'of Vestryman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestryman', 'n.', 'A member of a vestry; especially (Prot. Epis. Ch.), a\n   member other than a warden. See Vestry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesture', 'v. t.', 'A garment or garments; a robe; clothing; dress;\n   apparel; vestment; covering; envelope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesture', 'v. t.', 'The corn, grass, underwood, stubble, etc., with which\n   land was covered; as, the vesture of an acre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesture', 'v. t.', 'Seizin; possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vestured', 'a.', 'Covered with vesture or garments; clothed; enveloped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesuvian', 'a.', 'Of or pertaining to Vesuvius, a volcano near Naples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesuvian', 'a.', 'Vesuvianite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesuvianite', 'n.', 'A mineral occurring in tetragonal crystals, and also\n   massive, of a brown to green color, rarely sulphur yellow and blue. It\n   is a silicate of alumina and lime with some iron magnesia, and is\n   common at Vesuvius. Also called idocrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vesuvine', 'n.', 'A trade name for a brown dyestuff obtained from certain\n   basic azo compounds of benzene; -- called also Bismarck brown,\n   Manchester brown, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vetch', 'n.', 'Any leguminous plant of the genus Vicia, some species of\n   which are valuable for fodder. The common species is V. sativa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vetchling', 'n.', 'Any small leguminous plant of the genus Lathyrus,\n   especially L. Nissolia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vetchy', 'a.', 'Consisting of vetches or of pea straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vetchy', 'a.', 'Abounding with vetches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veteran', 'a.', 'Long exercised in anything, especially in military life\n   and the duties of a soldier; long practiced or experienced; as, a\n   veteran officer or soldier; veteran skill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veteran', 'n.', 'One who has been long exercised in any service or art,\n   particularly in war; one who has had.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veteranize', 'v. i.', 'To reenlist for service as a soldier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veterinarian', 'n.', 'One skilled in the diseases of cattle or domestic\n   animals; a veterinary surgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veterinary', 'a.', 'Of or pertaining to the art of healing or treating the\n   diseases of domestic animals, as oxen, horses, sheep, etc.; as, a\n   veterinary writer or school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vetiver', 'n.', 'An East Indian grass (Andropogon muricatus); also, its\n   fragrant roots which are much used for making mats and screens. Also\n   called kuskus, and khuskhus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vetoes', 'pl. ', 'of Veto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veto', 'n.', 'An authoritative prohibition or negative; a forbidding; an\n   interdiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veto', 'n.', 'A power or right possessed by one department of government to\n   forbid or prohibit the carrying out of projects attempted by another\n   department; especially, in a constitutional government, a power vested\n   in the chief executive to prevent the enactment of measures passed by\n   the legislature. Such a power may be absolute, as in the case of the\n   Tribunes of the People in ancient Rome, or limited, as in the case of\n   the President of the United States. Called also the veto power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veto', 'n.', 'The exercise of such authority; an act of prohibition or\n   prevention; as, a veto is probable if the bill passes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veto', 'n.', 'A document or message communicating the reasons of the\n   executive for not officially approving a proposed law; -- called also\n   veto message.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vetoed', 'imp. & p. p.', 'of Veto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vetoing', 'p. pr. & vb. n.', 'of Veto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Veto', 'v. t.', 'To prohibit; to negative; also, to refuse assent to, as a\n   legislative bill, and thus prevent its enactment; as, to veto an\n   appropriation bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vetoist', 'n.', 'One who uses, or sustains the use of, the veto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vetture', 'pl. ', 'of Vettura');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vettura', 'n.', 'An Italian four-wheeled carriage, esp. one let for hire; a\n   hackney coach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vetturini', 'pl. ', 'of Vetturino');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vetturino', 'n.', 'One who lets or drives a vettura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vetturino', 'n.', 'A vettura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vetust', 'a.', 'Venerable from antiquity; ancient; old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexed', 'imp. & p. p.', 'of Vex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexing', 'p. pr. & vb. n.', 'of Vex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vex', 'v. t.', 'To to/s back and forth; to agitate; to disquiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vex', 'v. t.', 'To make angry or annoyed by little provocations; to\n   irritate; to plague; to torment; to harass; to afflict; to trouble; to\n   tease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vex', 'v. t.', 'To twist; to weave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vex', 'v. i.', 'To be irritated; to fret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexation', 'n.', 'The act of vexing, or the state of being vexed;\n   agitation; disquiet; trouble; irritation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexation', 'n.', 'The cause of trouble or disquiet; affliction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexation', 'n.', 'A harassing by process of law; a vexing or troubling, as\n   by a malicious suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexatious', 'a.', 'Causing vexation; agitating; afflictive; annoying; as, a\n   vexatious controversy; a vexatious neighbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexatious', 'a.', 'Full or vexation, trouble, or disquiet; disturbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexed', 'a.', 'Annoyed; harassed; troubled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexed', 'a.', 'Much debated or contested; causing discussion; as, a vexed\n   question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexer', 'n.', 'One who vexes or troubles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexil', 'n.', 'A vexillum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexillar', '', 'Alt. of Vexillary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexillary', '', 'Of or pertaining to an ensign or standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexillary', '', 'Of or pertaining to the vexillum, or upper petal of\n   papilionaceous flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexillary', 'n.', 'A standard bearer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexillation', 'n.', 'A company of troops under one vexillum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexilla', 'pl. ', 'of Vexillum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexillum', 'n.', 'A flag or standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexillum', 'n.', 'A company of troops serving under one standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexillum', 'n.', 'A banner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexillum', 'n.', 'The sign of the cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexillum', 'n.', 'The upper petal of a papilionaceous flower; the standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexillum', 'n.', 'The rhachis and web of a feather taken together; the\n   vane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vexingly', 'adv.', 'In a vexing manner; so as to vex, tease, or irritate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'V hook', '', 'A gab at the end of an eccentric rod, with long jaws, shaped\n   like the letter V.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Via', 'n.', 'A road way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Via', 'prep.', 'By the way of; as, to send a letter via Queenstown to\n   London.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viability', 'n.', 'The quality or state of being viable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viability', 'n.', 'The capacity of living after birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viability', 'n.', 'The capacity of living, or being distributed, over wide\n   geographical limits; as, the viability of a species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viable', 'a.', 'Capable of living; born alive and with such form and\n   development of organs as to be capable of living; -- said of a newborn,\n   or a prematurely born, infant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viaduct', 'n.', 'A structure of considerable magnitude, usually with arches\n   or supported on trestles, for carrying a road, as a railroad, high\n   above the ground or water; a bridge; especially, one for crossing a\n   valley or a gorge. Cf. Trestlework.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viage', 'n.', 'A voyage; a journey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vial', 'n.', 'A small bottle, usually of glass; a little glass vessel with\n   a narrow aperture intended to be closed with a stopper; as, a vial of\n   medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vialed', 'imp. & p. p.', 'of Vial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vialled', '', 'of Vial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vialing', 'p. pr. & vb. n.', 'of Vial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vialling', '', 'of Vial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vial', 'v. t.', 'To put in a vial or vials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viameter', 'n.', 'An odometer; -- called also viatometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viand', 'n.', 'An article of food; provisions; food; victuals; -- used\n   chiefly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viander', 'n.', 'A feeder; an eater; also, one who provides viands, or\n   food; a host.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vi-apple', 'n.', 'See Otaheite apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viary', 'a.', 'Of or pertaining to roads; happening on roads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viatecture', 'n.', 'The art of making roads or ways for traveling,\n   including the construction of bridges, canals, viaducts, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viatic', 'a.', 'Of or pertaining to a journey or traveling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viaticum', 'n.', 'An allowance for traveling expenses made to those who\n   were sent into the provinces to exercise any office or perform any\n   service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viaticum', 'n.', 'Provisions for a journey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viaticum', 'n.', 'The communion, or eucharist, when given to persons in\n   danger of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viatometer', 'n.', 'A viameter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibices', 'n. pl.', 'More or less extensive patches of subcutaneous\n   extravasation of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibracula', 'pl. ', 'of Vibraculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibraculum', 'n.', 'One of the movable, slender, spinelike organs or parts\n   with which certain bryozoans are furnished. They are regarded as\n   specially modified zooids, of nearly the same nature as Avicularia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibrancy', 'n.', 'The state of being vibrant; resonance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibrant', 'a.', 'Vibrating; tremulous; resonant; as, vibrant drums.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibrate', 'imp. & p. p.', 'of Vibrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibrating', 'p. pr. & vb. n.', 'of Vibrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibrate', 'v. t.', 'To brandish; to move to and fro; to swing; as, to\n   vibrate a sword or a staff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibrate', 'v. t.', 'To mark or measure by moving to and fro; as, a pendulum\n   vibrating seconds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibrate', 'v. t.', 'To affect with vibratory motion; to set in vibration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibrate', 'v. i.', 'To move to and fro, or from side to side, as a\n   pendulum, an elastic rod, or a stretched string, when disturbed from\n   its position of rest; to swing; to oscillate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibrate', 'v. i.', 'To have the constituent particles move to and fro, with\n   alternate compression and dilation of parts, as the air, or any elastic\n   body; to quiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibrate', 'v. i.', 'To produce an oscillating or quivering effect of sound;\n   as, a whisper vibrates on the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibrate', 'v. i.', 'To pass from one state to another; to waver; to\n   fluctuate; as, a man vibrates between two opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibratile', 'a.', 'Adapted to, or used in, vibratory motion; having the\n   power of vibrating; vibratory; as, the vibratile organs of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibratility', 'n.', 'The quality or state of being vibratile; disposition\n   to vibration or oscillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibration', 'n.', 'The act of vibrating, or the state of being vibrated, or\n   in vibratory motion; quick motion to and fro; oscillation, as of a\n   pendulum or musical string.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibration', 'n.', 'A limited reciprocating motion of a particle of an\n   elastic body or medium in alternately opposite directions from its\n   position of equilibrium, when that equilibrium has been disturbed, as\n   when a stretched cord or other body produces musical notes, or\n   particles of air transmit sounds to the ear. The path of the particle\n   may be in a straight line, in a circular arc, or in any curve whatever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibratiuncle', 'a.', 'A small vibration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibrative', 'a. Vibrating', '; vibratory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibratory', 'a.', 'Consisting in, or causing, vibration, or oscillation;\n   vibrating; as, a vibratory motion; a vibratory power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibrios', 'pl. ', 'of Vibrio');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibriones', 'pl. ', 'of Vibrio');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibrio', 'n.', 'A genus of motile bacteria characterized by short, slightly\n   sinuous filaments and an undulatory motion; also, an individual of this\n   genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibrissae', 'pl. ', 'of Vibrissa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibrissa', 'n.', 'One of the specialized or tactile hairs which grow about\n   the nostrils, or on other parts of the face, in many animals, as the\n   so-called whiskers of the cat, and the hairs of the nostrils of man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibrissa', 'n.', 'The bristlelike feathers near the mouth of many birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibroscope', 'n.', 'An instrument for observing or tracing vibrations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vibroscope', 'n.', 'An instrument resembling the phenakistoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viburnum', 'n.', 'A genus of shrubs having opposite, petiolate leaves and\n   cymose flowers, several species of which are cultivated as ornamental,\n   as the laurestine and the guelder-rose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicar', 'n.', 'One deputed or authorized to perform the functions of\n   another; a substitute in office; a deputy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicar', 'n.', 'The incumbent of an appropriated benefice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicarage', 'n.', 'The benefice of a vicar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicarage', 'n.', 'The house or residence of a vicar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicarial', 'a.', 'Of or pertaining to a vicar; as, vicarial tithes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicarial', 'a.', 'Delegated; vicarious; as, vicarial power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicarian', 'n.', 'A vicar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicariate', 'a.', 'Having delegated power, as a vicar; vicarious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicariate', 'n.', 'Delegated office or power; vicarship; the office or\n   oversight of a vicar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicarious', 'prep.', 'Of or pertaining to a vicar, substitute, or deputy;\n   deputed; delegated; as, vicarious power or authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicarious', 'prep.', 'Acting of suffering for another; as, a vicarious\n   agent or officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicarious', 'prep.', 'Performed of suffered in the place of another;\n   substituted; as, a vicarious sacrifice; vicarious punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicarious', 'prep.', 'Acting as a substitute; -- said of abnormal action\n   which replaces a suppressed normal function; as, vicarious hemorrhage\n   replacing menstruation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicariously', 'adv.', 'In a vicarious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicarship', 'n.', 'The office or dignity of a vicar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicary', 'n.', 'A vicar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vice', 'n.', 'A defect; a fault; an error; a blemish; an imperfection; as,\n   the vices of a political constitution; the vices of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vice', 'n.', 'A moral fault or failing; especially, immoral conduct or\n   habit, as in the indulgence of degrading appetites; customary deviation\n   in a single respect, or in general, from a right standard, implying a\n   defect of natural character, or the result of training and habits; a\n   harmful custom; immorality; depravity; wickedness; as, a life of vice;\n   the vice of intemperance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vice', 'n.', 'The buffoon of the old English moralities, or moral dramas,\n   having the name sometimes of one vice, sometimes of another, or of Vice\n   itself; -- called also Iniquity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vice', 'n.', 'A kind of instrument for holding work, as in filing. Same as\n   Vise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vice', 'n.', 'A tool for drawing lead into cames, or flat grooved rods, for\n   casements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vice', 'n.', 'A gripe or grasp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viced', 'imp. & p. p.', 'of Vice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicing', 'p. pr. & vb. n.', 'of Vice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vice', 'v. t.', 'To hold or squeeze with a vice, or as if with a vice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vice', 'prep.', 'In the place of; in the stead; as, A. B. was appointed\n   postmaster vice C. D. resigned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vice', 'prep.', 'Denoting one who in certain cases may assume the office or\n   duties of a superior; designating an officer or an office that is\n   second in rank or authority; as, vice president; vice agent; vice\n   consul, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viced', 'a.', 'Vicious; corrupt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicegerency', 'n.', 'The office of a vicegerent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicegerent', 'a.', 'Having or exercising delegated power; acting by\n   substitution, or in the place of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicegerent', 'a.', 'Having or exercising delegated power; acting by\n   substitution, or in the place of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicegerent', 'n.', 'An officer who is deputed by a superior, or by proper\n   authority, to exercise the powers of another; a lieutenant; a vicar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicemen', 'pl. ', 'of Viceman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viceman', 'n.', 'A smith who works at the vice instead of at the anvil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicenary', 'a.', 'Of or pertaining to twenty; consisting of twenty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicennial', 'a.', 'Lasting or comprising twenty years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicennial', 'a.', 'Happening once in twenty years; as, a vicennial\n   celebration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vice-regal', 'a.', 'Of or pertaining to a viceroy or viceroyalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viceroy', 'prep.', 'The governor of a country or province who rules in the\n   name of the sovereign with regal authority, as the kings substitute;\n   as, the viceroy of India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viceroy', 'prep.', 'A large and handsome American butterfly (Basilarchia, /\n   Limenitis, archippus). Its wings are orange-red, with black lines along\n   the nervures and a row of white spots along the outer margins. The\n   larvae feed on willow, poplar, and apple trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viceroyalty', 'n.', 'The dignity, office, or jurisdiction of a viceroy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viceroyship', 'n.', 'Viceroyalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicety', 'n.', 'Fault; defect; coarseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vichy water', '', 'A mineral water found at Vichy, France. It is\n   essentially an effervescent solution of sodium, calcium, and magnetism\n   carbonates, with sodium and potassium chlorides; also, by extension,\n   any artificial or natural water resembling in composition the Vichy\n   water proper. Called also, colloquially, Vichy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viciate', 'v. t.', 'See Vitiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viinage', 'n.', 'The place or places adjoining or near; neighborhood;\n   vicinity; as, a jury must be of the vicinage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicinal', 'a.', 'Near; vicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicine', 'a.', 'Near; neighboring; vicinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicine', 'n.', 'An alkaloid ex tracted from the seeds of the vetch (Vicia\n   sativa) as a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicinity', 'n.', 'The quality or state of being near, or not remote;\n   nearness; propinquity; proximity; as, the value of the estate was\n   increased by the vicinity of two country seats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicinity', 'n.', 'That which is near, or not remote; that which is adjacent\n   to anything; adjoining space or country; neighborhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viciosity', 'n.', 'Vitiosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicious', 'a.', 'Characterized by vice or defects; defective; faulty;\n   imperfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicious', 'a.', 'Addicted to vice; corrupt in principles or conduct;\n   depraved; wicked; as, vicious children; vicious examples; vicious\n   conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicious', 'a.', 'Wanting purity; foul; bad; noxious; as, vicious air,\n   water, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicious', 'a.', 'Not correct or pure; corrupt; as, vicious language;\n   vicious idioms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicious', 'a.', 'Not well tamed or broken; given to bad tricks; unruly;\n   refractory; as, a vicious horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicious', 'a.', 'Bitter; spiteful; malignant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicissitude', 'n.', 'Regular change or succession from one thing to\n   another; alternation; mutual succession; interchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicissitude', 'n.', 'Irregular change; revolution; mutation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicissitudinary', 'a.', 'Subject to vicissitudes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicissitudinous', 'a.', 'Full of, or subject to, changes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicissy duck', '', 'A West Indian duck, sometimes domesticated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicontiel', 'a.', 'Of or pertaining to the viscount or sheriff of a\n   country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicontiels', 'n. pl.', 'Things belonging to the sheriff; especially, farms\n   (called also vicontiel rents) for which the sheriff used to pay rent to\n   the king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicount', 'n.', 'See Viscount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victim', 'n.', 'A living being sacrificed to some deity, or in the\n   performance of a religious rite; a creature immolated, or made an\n   offering of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victim', 'n.', 'A person or thing destroyed or sacrificed in the pursuit of\n   an object, or in gratification of a passion; as, a victim to jealousy,\n   lust, or ambition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victim', 'n.', 'A person or living creature destroyed by, or suffering\n   grievous injury from, another, from fortune or from accident; as, the\n   victim of a defaulter; the victim of a railroad accident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victim', 'n.', 'Hence, one who is duped, or cheated; a dupe; a gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victimate', 'v. t.', 'To make a victim of; to sacrifice; to immolate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victimized', 'imp. & p. p.', 'of Victimize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victimizing', 'p. pr. & vb. n.', 'of Victimize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victimize', 'v. t.', 'To make a victim of, esp. by deception; to dupe; to\n   cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victor', 'n.', 'The winner in a contest; one who gets the better of another\n   in any struggle; esp., one who defeats an enemy in battle; a\n   vanquisher; a conqueror; -- often followed by art, rarely by of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victor', 'n.', 'A destroyer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victor', 'a.', 'Victorious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victoress', 'n.', 'A victress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victoria', 'n.', 'A genus of aquatic plants named in honor of Queen\n   Victoria. The Victoria regia is a native of Guiana and Brazil. Its\n   large, spreading leaves are often over five feet in diameter, and have\n   a rim from three to five inches high; its immense rose-white flowers\n   sometimes attain a diameter of nearly two feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victoria', 'n.', 'A kind of low four-wheeled pleasure carriage, with a\n   calash top, designed for two persons and the driver who occupies a high\n   seat in front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victoria', 'n.', 'An asteroid discovered by Hind in 1850; -- called also\n   Clio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victorian', 'a.', 'Of or pertaining to the reign of Queen Victoria of\n   England; as, the Victorian poets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victorine', 'n.', 'A womans fur tippet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victorious', 'a.', 'Of or pertaining to victory, or a victor being a\n   victor; bringing or causing a victory; conquering; winning; triumphant;\n   as, a victorious general; victorious troops; a victorious day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victories', 'pl. ', 'of Victory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victory', 'n.', 'The defeat of an enemy in battle, or of an antagonist in\n   any contest; a gaining of the superiority in any struggle or\n   competition; conquest; triumph; -- the opposite of defeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victress', 'n.', 'A woman who wins a victory; a female victor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victrice', 'n.', 'A victress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victrix', 'n.', 'Victress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victual', 'n.', 'Food; -- now used chiefly in the plural. See Victuals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victual', 'n.', 'Grain of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victualed', 'imp. & p. p.', 'of Victual');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victualled', '', 'of Victual');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victualing', 'p. pr. & vb. n.', 'of Victual');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victualling', '', 'of Victual');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victual', 'v. t.', 'To supply with provisions for subsistence; to provide\n   with food; to store with sustenance; as, to victual an army; to victual\n   a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victualage', 'n.', 'Victuals; food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victualer', 'n.', 'One who furnishes victuals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victualer', 'n.', 'One who keeps a house of entertainment; a tavern keeper;\n   an innkeeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victualer', 'n.', 'A vessel employed to carry provisions, usually for\n   military or naval use; a provision use; a provision ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victualer', 'n.', 'One who deals in grain; a corn factor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victualing', 'a.', 'Of or pertaining to victuals, or provisions; supplying\n   provisions; as, a victualing ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victuals', 'n. pl.', 'Food for human beings, esp. when it is cooked or\n   prepared for the table; that which supports human life; provisions;\n   sustenance; meat; viands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Victus', 'n.', 'Food; diet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicua', 'n.', 'Alt. of Vicugna');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vicugna', 'n.', 'A South American mammal (Auchenia vicunna) native of the\n   elevated plains of the Andes, allied to the llama but smaller. It has a\n   thick coat of very fine reddish brown wool, and long, pendent white\n   hair on the breast and belly. It is hunted for its wool and flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vida finch', '', 'The whidah bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vidame', 'n.', 'One of a class of temporal officers who originally\n   represented the bishops, but later erected their offices into fiefs,\n   and became feudal nobles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vide', '', 'imperative sing. of L. videre, to see; -- used to direct\n   attention to something; as, vide supra, see above.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Videlicet', 'adv.', 'To wit; namely; -- often abbreviated to viz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vidette', 'n.', 'Same Vedette.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vidonia', 'n.', 'A dry white wine, of a tart flavor, produced in Teneriffe;\n   -- called also Teneriffe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viduage', 'n.', 'The state of widows or of widowhood; also, widows,\n   collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vidual', 'a.', 'Of or pertaining to the state of a widow; widowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viduation', 'n.', 'The state of being widowed or bereaved; loss;\n   bereavement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viduity', 'n.', 'Widowhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vied', 'imp. & p. p.', 'of Vie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vying', 'p. pr. & vb. n.', 'of Vie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vie', 'v. i.', 'To stake a sum upon a hand of cards, as in the old game of\n   gleek. See Revie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vie', 'v. i.', 'To strive for superiority; to contend; to use emulous\n   effort, as in a race, contest, or competition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vie', 'v. t.', 'To stake; to wager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vie', 'v. t.', 'To do or produce in emulation, competition, or rivalry; to\n   put in competition; to bandy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vie', 'n.', 'A contest for superiority; competition; rivalry; strife; also,\n   a challenge; a wager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vielle', 'n.', 'An old stringed instrument played upon with a wheel; a\n   hurdy-gurdy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vienna paste', '', 'A caustic application made up of equal parts of caustic\n   potash and quicklime; -- called also Vienna caustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viennese', 'a.', 'Of or pertaining to Vienna, or people of Vienna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viennese', 'n. sing. & pl.', 'An inhabitant, or the inhabitants, of Vienna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'View', 'n.', 'The act of seeing or beholding; sight; look; survey;\n   examination by the eye; inspection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'View', 'n.', 'Mental survey; intellectual perception or examination; as, a\n   just view of the arguments or facts in a case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'View', 'n.', 'Power of seeing, either physically or mentally; reach or\n   range of sight; extent of prospect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'View', 'n.', 'That which is seen or beheld; sight presented to the natural\n   or intellectual eye; scene; prospect; as, the view from a window.');
  INSERT INTO words (word, word_type, description) VALUES  (    'View', 'n.', 'The pictorial representation of a scene; a sketch, /ither\n   drawn or painted; as, a fine view of Lake George.');
  INSERT INTO words (word, word_type, description) VALUES  (    'View', 'n.', 'Mode of looking at anything; manner of apprehension;\n   conception; opinion; judgment; as, to state ones views of the policy\n   which ought to be pursued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'View', 'n.', 'That which is looked towards, or kept in sight, as object,\n   aim, intention, purpose, design; as, he did it with a view of escaping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'View', 'n.', 'Appearance; show; aspect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viewed', 'imp. & p. p.', 'of View');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viewing', 'p. pr. & vb. n.', 'of View');
  INSERT INTO words (word, word_type, description) VALUES  (    'View', 'v. t.', 'To see; to behold; especially, to look at with attention,\n   or for the purpose of examining; to examine with the eye; to inspect;\n   to explore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'View', 'v. t.', 'To survey or examine mentally; to consider; as, to view\n   the subject in all its aspects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viewer', 'n.', 'One who views or examines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viewer', 'n.', 'A person appointed to inspect highways, fences, or the\n   like, and to report upon the same.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viewer', 'n.', 'The superintendent of a coal mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viewiness', 'n.', 'The quality or state of being viewy, or of having\n   unpractical views.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viewless', 'a.', 'Not perceivable by the eye; invisible; unseen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viewly', 'a.', 'Alt. of Viewsome');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viewsome', 'a.', 'Pleasing to the sight; sightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viewy', 'a.', 'Having peculiar views; fanciful; visionary; unpractical; as,\n   a viewy person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viewy', 'a.', 'Spectacular; pleasing to the eye or the imagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vifda', 'n.', 'In the Orkney and Shetland Islands, beef and mutton hung and\n   dried, but not salted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigesimal', 'a.', 'Twentieth; divided into, or consisting of, twenties or\n   twenty parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigesimation', 'n.', 'The act of putting to death every twentieth man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigesimo-quarto', 'a.', 'Having twenty-four leaves to a sheet; as, a\n   vigesimo-quarto form, book, leaf, size, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-tos', 'pl. ', 'of Vigesimo-quarto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigesimo-quarto', 'n.', 'A book composed of sheets each of which is folded\n   into twenty-four leaves; hence, indicating more or less definitely a\n   size of book so made; -- usually written 24mo, or 24.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigil', 'v. i.', 'Abstinence from sleep, whether at a time when sleep is\n   customary or not; the act of keeping awake, or the state of being\n   awake, or the state of being awake; sleeplessness; wakefulness; watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigil', 'v. i.', 'Hence, devotional watching; waking for prayer, or other\n   religious exercises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigil', 'v. i.', 'Originally, the watch kept on the night before a feast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigil', 'v. i.', 'Later, the day and the night preceding a feast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigil', 'v. i.', 'A religious service performed in the evening preceding a\n   feast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigilance', 'n.', 'The quality or state of being vigilant; forbearance of\n   sleep; wakefulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigilance', 'n.', 'Watchfulness in respect of danger; care; caution;\n   circumspection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigilance', 'n.', 'Guard; watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigilancy', 'n.', 'Vigilance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigilant', 'a.', 'Attentive to discover and avoid danger, or to provide for\n   safety; wakeful; watchful; circumspect; wary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigilantly', 'adv.', 'In a vigilant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigily', 'n.', 'A vigil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigintivirate', 'n.', 'The office of the vigintiviri, a body of officers of\n   government consisting of twenty men; also, the vigintiviri.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vignette', 'n.', 'A running ornament consisting of leaves and tendrils,\n   used in Gothic architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vignette', 'n.', 'A decorative design, originally representing vine\n   branches or tendrils, at the head of a chapter, of a manuscript or\n   printed book, or in a similar position; hence, by extension, any small\n   picture in a book; hence, also, as such pictures are often without a\n   definite bounding line, any picture, as an engraving, a photograph, or\n   the like, which vanishes gradually at the edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vignette', 'v. t.', 'To make, as an engraving or a photograph, with a\n   border or edge insensibly fading away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigonia', 'a.', 'Of or pertaining to the vicu/a; characterizing the vicu/a;\n   -- said of the wool of that animal, used in felting hats, and for other\n   purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigor', 'n.', 'Active strength or force of body or mind; capacity for\n   exertion, physically, intellectually, or morally; force; energy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigor', 'n.', 'Strength or force in animal or force in animal or vegetable\n   nature or action; as, a plant grows with vigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigor', 'n.', 'Strength; efficacy; potency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigor', 'v. t.', 'To invigorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigorite', 'n.', 'An explosive containing nitroglycerin. It is used in\n   blasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigoroso', 'a. & adv.', 'Vigorous; energetic; with energy; -- a direction\n   to perform a passage with energy and force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigorous', 'a.', 'Possessing vigor; full of physical or mental strength or\n   active force; strong; lusty; robust; as, a vigorous youth; a vigorous\n   plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vigorous', 'a.', 'Exhibiting strength, either of body or mind; powerful;\n   strong; forcible; energetic; as, vigorous exertions; a vigorous\n   prosecution of a war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viking', 'n.', 'One belonging to the pirate crews from among the Northmen,\n   who plundered the coasts of Europe in the eighth, ninth, and tenth\n   centuries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vilany', 'n.', 'Villainy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vilayet', 'n.', 'One of the chief administrative divisions or provinces of\n   the Ottoman Empire; -- formerly called eyalet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vild', 'a.', 'Vile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vile', 'superl.', 'Low; base; worthless; mean; despicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vile', 'superl.', 'Morally base or impure; depraved by sin; hateful; in the\n   sight of God and men; sinful; wicked; bad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viled', 'a.', 'Abusive; scurrilous; defamatory; vile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vileyns', 'a.', 'Villainous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vilification', 'n.', 'The act of vilifying or defaming; abuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vilifier', 'n.', 'One who vilifies or defames.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vilified', 'imp. & p. p.', 'of Vilify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vilifying', 'p. pr. & vb. n.', 'of Vilify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vilify', 'v. t.', 'To make vile; to debase; to degrade; to disgrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vilify', 'v. t.', 'To degrade or debase by report; to defame; to traduce;\n   to calumniate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vilify', 'v. t.', 'To treat as vile; to despise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vilipend', 'v. t.', 'To value lightly; to depreciate; to slight; to\n   despise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vilipendency', 'n.', 'Disesteem; slight; disparagement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vility', 'n.', 'Vileness; baseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vill', 'n.', 'A small collection of houses; a village.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villas', 'pl. ', 'of Villa');
