  INSERT INTO words (word, word_type, description) VALUES  (    'Village', 'n.', 'A small assemblage of houses in the country, less than a\n   town or city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villager', 'n.', 'An inhabitant of a village.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villagery', 'n.', 'Villages; a district of villages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villain', 'n.', 'One who holds lands by a base, or servile, tenure, or in\n   villenage; a feudal tenant of the lowest class, a bondman or servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villain', 'n.', 'A baseborn or clownish person; a boor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villain', 'n.', 'A vile, wicked person; a man extremely depraved, and\n   capable or guilty of great crimes; a deliberate scoundrel; a knave; a\n   rascal; a scamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villain', 'a.', 'Villainous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villain', 'v. t.', 'To debase; to degrade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villainous', 'a.', 'Base; vile; mean; depraved; as, a villainous person or\n   wretch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villainous', 'a.', 'Proceeding from, or showing, extreme depravity; suited\n   to a villain; as, a villainous action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villainous', 'a.', 'Sorry; mean; mischievous; -- in a familiar sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villainies', 'pl. ', 'of Villainy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villainy', 'n.', 'The quality or state of being a villain, or villainous;\n   extreme depravity; atrocious wickedness; as, the villainy of the\n   seducer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villainy', 'n.', 'Abusive, reproachful language; discourteous speech; foul\n   talk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villainy', 'n.', 'The act of a villain; a deed of deep depravity; a crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villakin', 'n.', 'A little villa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villan', 'n.', 'A villain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villanage', 'n.', 'The state of a villain, or serf; base servitude; tenure\n   on condition of doing the meanest services for the lord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villanage', 'n.', 'Baseness; infamy; villainy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villanel', 'n.', 'A ballad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villanelle', 'pl. ', 'of Villanella');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villanella', 'n.', 'An old rustic dance, accompanied with singing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villanelle', 'n.', 'A poem written in tercets with but two rhymes, the\n   first and third verse of the first stanza alternating as the third\n   verse in each successive stanza and forming a couplet at the close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villanette', 'n.', 'A small villa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villanized', 'imp. & p. p.', 'of Villanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villanizing', 'p. pr. & vb. n.', 'of Villanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villanize', 'v. t.', 'To make vile; to debase; to degrade; to revile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villanizer', 'n.', 'One who villanizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villanous', 'n.', 'Alt. of Villanousness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villanously', 'n.', 'Alt. of Villanousness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villanousness', 'n.', 'See Villainous, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villany', 'n.', 'See Villainy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villatic', 'a.', 'Of or pertaining to a farm or a village; rural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villein', 'n.', 'See Villain, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villenage', 'n.', 'Villanage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villenous', 'a.', 'Of or pertaining to a villein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villi', 'n.', 'pl. of Villus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villiform', 'a.', 'Having the form or appearance of villi; like close-set\n   fibers, either hard or soft; as, the teeth of perch are villiform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villose', 'a.', 'See Villous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villosity', 'n.', 'State of being villous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villosity', 'n.', 'A coating of long, slender hairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villosity', 'n.', 'A villus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villous', 'a.', 'Abounding in, or covered with, fine hairs, or a woolly\n   substance; shaggy with soft hairs; nappy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villous', 'a.', 'Furnished or clothed with villi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villi', 'pl. ', 'of Villus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villus', 'n.', 'One of the minute papillary processes on certain vascular\n   membranes; a villosity; as, villi cover the lining of the small\n   intestines of many animals and serve to increase the absorbing surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Villus', 'n.', 'Fine hairs on plants, resembling the pile of velvet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vim', 'n.', 'Power; force; energy; spirit; activity; vigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vimen', 'n.', 'A long, slender, flexible shoot or branch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viminal', 'a.', 'Of or pertaining to twigs; consisting of twigs; producing\n   twigs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vimineous', 'a.', 'Of or pertaining to twigs; made of pliant twigs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vimineous', 'a.', 'Producing long, slender twigs or shoots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinaceous', 'a.', 'Belonging to, or like, wine or grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinaceous', 'a.', 'Of the color of wine, especially of red wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinaigrette', 'n.', 'A sauce, made of vinegar, oil, and other ingredients,\n   -- used esp. for cold meats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinaigrette', 'n.', 'A small perforated box for holding aromatic vinegar\n   contained in a sponge, or a smelling bottle for smelling salts; --\n   called also vinegarette.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinaigrette', 'n.', 'A small, two-wheeled vehicle, like a Bath chair, to be\n   drawn or pushed by a boy or man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinagrous', 'a.', 'Resembling vinegar; sour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinagrous', 'a.', 'Fig.: Unamiable; morose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinasse', 'n.', 'The waste liquor remaining in the process of making beet\n   sugar, -- used in the manufacture of potassium carbonate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinatico', 'n.', 'Madeira mahogany; the coarse, dark-colored wood of the\n   Persea Indica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vincentian', 'a.', 'Of or pertaining to Saint Vincent de Paul, or founded\n   by him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vincentian', 'n.', 'Same as Lazarist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vincentian', 'n.', 'A member of certain charitable sisterhoods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vincetoxin', 'n.', 'A glucoside extracted from the root of the white\n   swallowwort (Vincetoxicum officinale, a plant of the Asclepias family)\n   as a bitter yellow amorphous substance; -- called also asclepiadin, and\n   cynanchin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vincibility', 'n.', 'The quality or state of being vincible, vincibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vincible', 'a.', 'Capable of being overcome or subdued; conquerable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vincibleness', 'n.', 'The quality or state of being vincible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vincture', 'n.', 'A binding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vincula', 'pl. ', 'of Vinculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinculums', 'pl. ', 'of Vinculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinculum', 'n.', 'A bond of union; a tie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinculum', 'n.', 'A straight, horizontal mark placed over two or more\n   members of a compound quantity, which are to be subjected to the same\n   operation, as in the expression x2 + y2 - x + y.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinculum', 'n.', 'A band or bundle of fibers; a fraenum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinculum', 'n.', 'A commissure uniting the two main tendons in the foot of\n   certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindemial', 'a.', 'Of or pertaining to a vintage, or grape harvest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindemiate', 'v. i.', 'To gather the vintage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindemiation', 'n.', 'The operation of gathering grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindicable', 'a.', 'Capable of being vindicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindicated', 'imp. & p. p.', 'of Vindicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindicating', 'p. pr. & vb. n.', 'of Vindicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindicate', 'v. t.', 'To lay claim to; to assert a right to; to claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindicate', 'v. t.', 'To maintain or defend with success; to prove to be\n   valid; to assert convincingly; to sustain against assault; as, to\n   vindicate a right, claim, or title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindicate', 'v. t.', 'To support or maintain as true or correct, against\n   denial, censure, or objections; to defend; to justify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindicate', 'v. t.', 'To maintain, as a law or a cause, by overthrowing\n   enemies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindicate', 'v. t.', 'To liberate; to set free; to deliver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindicate', 'v. t.', 'To avenge; to punish; as, a war to vindicate or\n   punish infidelity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindication', 'n.', 'The act of vindicating, or the state of being\n   vindicated; defense; justification against denial or censure; as, the\n   vindication of opinions; his vindication is complete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindication', 'n.', 'The claiming a thing as ones own; the asserting of a\n   right or title in, or to, a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindicative', 'a.', 'Tending to vindicate; vindicating; as, a vindicative\n   policy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindicative', 'a.', 'Revengeful; vindictive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindicator', 'n.', 'One who vindicates; one who justifies or maintains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindicatory', 'a.', 'Tending or serving to vindicate or justify;\n   justificatory; vindicative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindicatory', 'a.', 'Inflicting punishment; avenging; punitory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindictive', 'a.', 'Disposed to revenge; prompted or characterized by\n   revenge; revengeful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vindictive', 'a.', 'Punitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vine', 'n.', 'Any woody climbing plant which bears grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vine', 'n.', 'Hence, a climbing or trailing plant; the long, slender stem\n   of any plant that trails on the ground, or climbs by winding round a\n   fixed object, or by seizing anything with its tendrils, or claspers; a\n   creeper; as, the hop vine; the bean vine; the vines of melons,\n   squashes, pumpkins, and other cucurbitaceous plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vineal', 'a.', 'Of or pertaining to vines; containing vines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vine-clad', 'a.', 'Covered with vines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vined', 'a.', 'Having leaves like those of the vine; ornamented with vine\n   leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinedresser', 'n.', 'One who cultivates, prunes, or cares for, grapevines;\n   a laborer in a vineyard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinegar', 'a.', 'A sour liquid used as a condiment, or as a preservative,\n   and obtained by the spontaneous (acetous) fermentation, or by the\n   artificial oxidation, of wine, cider, beer, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinegar', 'a.', 'Hence, anything sour; -- used also metaphorically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinegar', 'v. t.', 'To convert into vinegar; to make like vinegar; to\n   render sour or sharp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinegarette', 'n.', 'See Vinaigrette, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinegary', 'a.', 'Having the nature of vinegar; sour; unamiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viner', 'n.', 'A vinedresser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinery', 'n.', 'A vineyard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinery', 'n.', 'A structure, usually inclosed with glass, for rearing and\n   protecting vines; a grapery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinette', 'n.', 'A sprig or branch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinewed', 'a.', 'Same as Vinnewed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vineyard', 'n.', 'An inclosure or yard for grapevines; a plantation of\n   vines producing grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vineyardist', 'n.', 'One who cultivates a vineyard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vingt et un', '', 'A game at cards, played by two or more persons. The\n   fortune of each player depends upon obtaining from the dealer such\n   cards that the sum of their pips, or spots, is twenty-one, or a number\n   near to it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vingtun', 'n.', 'Contraction for Vingt et un.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinic', 'a.', 'Of or pertaining to wine; as, vinic alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viniculture', 'n.', 'The cultivation of the vine, esp. for making wine;\n   viticulture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinnewed', 'a.', 'Moldy; musty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinny', 'a.', 'Vinnewed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinolency', 'n.', 'Drunkennes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinolent', 'a.', 'Given to wine; drunken; intemperate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinometer', 'n.', 'An instrument for determining the strength or purity of\n   wine by measuring its density.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vin ordinaire', '', 'A cheap claret, used as a table wine in France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinose', 'a.', 'Vinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinosity', 'n.', 'The quality or state of being vinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinous', 'a.', 'Of or pertaining to wine; having the qualities of wine; as,\n   a vinous taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinquish', 'n.', 'See Vanquish, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vintage', 'n.', 'The produce of the vine for one season, in grapes or in\n   wine; as, the vintage is abundant; the vintage of 1840.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vintage', 'n.', 'The act or time of gathering the crop of grapes, or making\n   the wine for a season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vintager', 'n.', 'One who gathers the vintage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vintaging', 'n.', 'The act of gathering the vintage, or crop of grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vintner', 'n.', 'One who deals in wine; a wine seller, or wine merchant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vintry', 'n.', 'A place where wine is sold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viny', 'a.', 'Of or pertaining to vines; producing, or abounding in, vines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vinyl', 'n.', 'The hypothetical radical C2H3, regarded as the\n   characteristic residue of ethylene and that related series of\n   unsaturated hydrocarbons with which the allyl compounds are homologous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viol', 'n.', 'A stringed musical instrument formerly in use, of the same\n   form as the violin, but larger, and having six strings, to be struck\n   with a bow, and the neck furnished with frets for stopping the strings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viol', 'n.', 'A large rope sometimes used in weighing anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viola', 'n.', 'A genus of polypetalous herbaceous plants, including all\n   kinds of violets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viola', 'n.', 'An instrument in form and use resembling the violin, but\n   larger, and a fifth lower in compass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violable', 'a.', 'Capable of being violated, broken, or injured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violaceous', 'a.', 'Resembling violets in color; bluish purple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violaceous', 'a.', 'Of or pertaining to a natural order of plants, of which\n   the violet is the type. It contains about twenty genera and two hundred\n   and fifty species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violaniline', 'n.', 'A dyestuff of the induline group, made from aniline,\n   and used as a substitute for indigo in dyeing wool and silk a\n   violet-blue or a gray-blue color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violantin', 'n.', 'A complex nitrogenous substance, produced as a yellow\n   crystalline substance, and regarded as a complex derivative of\n   barbituric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violaquercitrin', 'n.', 'A yellow crystalline glucoside obtained from the\n   pansy (Viola tricolor), and decomposing into glucose and quercitrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violascent', 'a.', 'Violescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violates', 'imp. & p. p.', 'of Violate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violating', 'p. pr. & vb. n.', 'of Violate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violate', 'v. t.', 'To treat in a violent manner; to abuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violate', 'v. t.', 'To do violence to, as to anything that should be held\n   sacred or respected; to profane; to desecrate; to break forcibly; to\n   trench upon; to infringe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violate', 'v. t.', 'To disturb; to interrupt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violate', 'v. t.', 'To commit rape on; to ravish; to outrage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violation', 'n.', 'The act of violating, treating with violence, or\n   injuring; the state of being violated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violation', 'n.', 'Infringement; transgression; nonobservance; as, the\n   violation of law or positive command, of covenants, promises, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violation', 'n.', 'An act of irreverence or desecration; profanation or\n   contemptuous treatment of sacred things; as, the violation of a church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violation', 'n.', 'Interruption, as of sleep or peace; disturbance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violation', 'n.', 'Ravishment; rape; outrage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violative', 'a.', 'Violating, or tending to violate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violator', 'n.', 'One who violates; an infringer; a profaner; a ravisher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viole', 'n.', 'A vial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violence', 'n.', 'The quality or state of being violent; highly excited\n   action, whether physical or moral; vehemence; impetuosity; force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violence', 'n.', 'Injury done to that which is entitled to respect,\n   reverence, or observance; profanation; infringement; unjust force;\n   outrage; assault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violence', 'n.', 'Ravishment; rape; constupration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violence', 'v. t.', 'To assault; to injure; also, to bring by violence; to\n   compel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violent', 'a.', 'Moving or acting with physical strength; urged or impelled\n   with force; excited by strong feeling or passion; forcible; vehement;\n   impetuous; fierce; furious; severe; as, a violent blow; the violent\n   attack of a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violent', 'a.', 'Acting, characterized, or produced by unjust or improper\n   force; outrageous; unauthorized; as, a violent attack on the right of\n   free speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violent', 'a.', 'Produced or effected by force; not spontaneous; unnatural;\n   abnormal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violent', 'n.', 'An assailant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violent', 'v. t.', 'To urge with violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violent', 'v. i.', 'To be violent; to act violently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violently', 'adv.', 'In a violent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violescent', 'a.', 'Tending to a violet color; violascent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violet', 'n.', 'Any plant or flower of the genus Viola, of many species.\n   The violets are generally low, herbaceous plants, and the flowers of\n   many of the species are blue, while others are white or yellow, or of\n   several colors, as the pansy (Viola tricolor).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violet', 'n.', 'The color of a violet, or that part of the spectrum\n   farthest from red. It is the most refrangible part of the spectrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violet', 'n.', 'In art, a color produced by a combination of red and blue\n   in equal proportions; a bluish purple color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violet', 'n.', 'Any one of numerous species of small violet-colored\n   butterflies belonging to Lycaena, or Rusticus, and allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violet', 'n.', 'Dark blue, inclining to red; bluish purple; having a color\n   produced by red and blue combined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violet-tip', 'n.', 'A very handsome American butterfly (Polygonia\n   interrogationis). Its wings are mottled with various shades of red and\n   brown and have violet tips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violin', 'n.', 'A small instrument with four strings, played with a bow; a\n   fiddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violine', 'n.', 'A pale yellow amorphous substance of alkaloidal nature and\n   emetic properties, said to have been extracted from the root and\n   foliage of the violet (Viola).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violine', 'n.', 'Mauve aniline. See under Mauve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violinist', 'n.', 'A player on the violin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violist', 'n.', 'A player on the viol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violoncellist', 'n.', 'A player on the violoncello.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violoncello', 'n.', 'A stringed instrument of music; a bass viol of four\n   strings, or a bass violin with long, large strings, giving sounds an\n   octave lower than the viola, or tenor or alto violin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violone', 'n.', 'The largest instrument of the bass-viol kind, having\n   strings tuned an octave below those of the violoncello; the\n   contrabasso; -- called also double bass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violous', 'a.', 'Violent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Violuric', 'a.', 'Of, pertaining to, or designating, a complex nitroso\n   derivative of barbituric acid. It is obtained as a white or yellow\n   crystalline substance, and forms characteristic yellow, blue, and\n   violet salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viper', 'a.', 'Any one of numerous species of Old World venomous makes\n   belonging to Vipera, Clotho, Daboia, and other genera of the family\n   Viperidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viper', 'a.', 'A dangerous, treacherous, or malignant person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viperina', 'n. pl.', 'See Viperoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viperine', 'a.', 'Of or pertaining to a viper or vipers; resembling a\n   viper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viperish', 'a.', 'Somewhat like a viper; viperous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viperoidea', 'n. pl.', 'Alt. of Viperoides');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viperoides', 'n. pl.', 'A division of serpents which includes the true\n   vipers of the Old World and the rattlesnakes and moccasin snakes of\n   America; -- called also Viperina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viperous', 'a.', 'Having the qualities of a viper; malignant; venomous; as,\n   a viperous tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viraginian', 'a.', 'Of or pertaining to a virago; having the qualities of a\n   virago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viraginity', 'n.', 'The qualities or characteristics of a virago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viragoes', 'pl. ', 'of Virago');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virago', 'n.', 'A woman of extraordinary stature, strength, and courage; a\n   woman who has the robust body and masculine mind of a man; a female\n   warrior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virago', 'n.', 'Hence, a mannish woman; a bold, turbulent woman; a\n   termagant; a vixen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vire', 'n.', 'An arrow, having a rotary motion, formerly used with the\n   crossbow. Cf. Vireton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virelay', 'n.', 'An ancient French song, or short poem, wholly in two\n   rhymes, and composed in short lines, with a refrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virent', 'a.', 'Green; not withered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vireo', 'n.', 'Any one of numerous species of American singing birds\n   belonging to Vireo and allied genera of the family Vireonidae. In many\n   of the species the back is greenish, or olive-colored. Called also\n   greenlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virescence', '', 'The act or state of becoming green through the formation\n   of chlorophyll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virescent', 'a.', 'Beginning to be green; slightly green; greenish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vireton', 'n.', 'An arrow or bolt for a crossbow having feathers or brass\n   placed at an angle with the shaft to make it spin in flying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgalieu', 'n.', 'A valuable kind of pear, of an obovate shape and with\n   melting flesh of delicious flavor; -- more properly called White\n   Doyenne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgate', 'a.', 'Having the form of a straight rod; wand-shaped; straight\n   and slender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgate', 'n.', 'A yardland, or measure of land varying from fifteen to\n   forty acres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgated', 'a.', 'Striped; streaked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virge', 'n.', 'A wand. See Verge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virger', 'n.', 'See Verger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgilian', 'a.', 'Of or pertaining to Virgil, the Roman poet; resembling\n   the style of Virgil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgin', 'n.', 'A woman who has had no carnal knowledge of man; a maid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgin', 'n.', 'A person of the male sex who has not known sexual\n   indulgence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgin', 'n.', 'See Virgo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgin', 'n.', 'Any one of several species of gossamer-winged butterflies\n   of the family Lycaenidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgin', 'n.', 'A female insect producing eggs from which young are\n   hatched, though there has been no fecundation by a male; a\n   parthenogenetic insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgin', 'a.', 'Being a virgin; chaste; of or pertaining to a virgin;\n   becoming a virgin; maidenly; modest; indicating modesty; as, a virgin\n   blush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgin', 'a.', 'Pure; undefiled; unmixed; fresh; new; as, virgin soil;\n   virgin gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgin', 'a.', 'Not yet pregnant; impregnant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgin', 'v. i.', 'To act the virgin; to be or keep chaste; -- followed by\n   it. See It, 5.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virginal', 'a.', 'Of or pertaining to a virgin; becoming a virgin;\n   maidenly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virginal', 'n.', 'An instrument somewhat resembling the spinet, but having\n   a rectangular form, like the small piano. It had strings and keys, but\n   only one wire to a note. The instrument was used in the sixteenth\n   century, but is now wholly obsolete. It was sometimes called a pair of\n   virginals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virginal', 'v. i.', 'To play with the fingers, as if on a virginal; to tap\n   or pat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virginhood', 'n.', 'Virginity; maidenhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virginia', 'n.', 'One of the States of the United States of America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virginia', 'a.', 'Of or pertaining to the State of Virginia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virginity', 'n.', 'The quality or state of being a virgin; undefiled purity\n   or chastity; maidenhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virginity', 'n.', 'The unmarried life; celibacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgo', 'n.', 'A sign of the zodiac which the sun enters about the 21st of\n   August, marked thus [/] in almanacs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgo', 'n.', 'A constellation of the zodiac, now occupying chiefly the\n   sign Libra, and containing the bright star Spica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgouleuse', 'n.', 'An old French variety of pear, of little value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgularian', 'n.', 'Any one of numerous species of long, slender\n   Alcyonaria belonging to Virgularia and allied genera of the family\n   Virgularidae. These corals are allied to the sea-pens, but have a long\n   rodlike rhachis inclosing a slender, round or square, calcareous axis.\n   The polyps are arranged in transverse rows or clusters along each side\n   of the rhachis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgulate', 'a.', 'Shaped like a little twig or rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virgule', 'n.', 'A comma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virial', 'n.', 'A certain function relating to a system of forces and their\n   points of application, -- first used by Clausius in the investigation\n   of problems in molecular physics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virid', 'a.', 'Green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viridescence', 'n.', 'Quality or state of being viridescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viridescent', 'a.', 'Slightly green; greenish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viridine', 'n.', 'A greenish, oily, nitrogenous hydrocarbon, C12H19N7,\n   obtained from coal tar, and probably consisting of a mixture of several\n   metameric compounds which are higher derivatives of the base pyridine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viridite', 'n.', 'A greenish chloritic mineral common in certain igneous\n   rocks, as diabase, as a result of alternation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viridity', 'n.', 'Greenness; verdure; the color of grass and foliage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viridity', 'n.', 'Freshness; soundness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viridness', 'n.', 'Viridity; greenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virile', 'a.', 'Having the nature, properties, or qualities, of an adult\n   man; characteristic of developed manhood; hence, masterful; forceful;\n   specifically, capable of begetting; -- opposed to womanly, feminine,\n   and puerile; as, virile age, virile power, virile organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virility', 'n.', 'The quality or state of being virile; developed manhood;\n   manliness; specif., the power of procreation; as, exhaustion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viripotent', 'a.', 'Developed in manhood; hence, able to beget;\n   marriageable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virmilion', 'n.', 'See Vermilion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virole', 'n.', 'A ring surrounding a bugle or hunting horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viroled', 'a.', 'Furnished with a virole or viroles; -- said of a horn or a\n   bugle when the rings are of different tincture from the rest of the\n   horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virose', 'a.', 'Having a nauseous odor; fetid; poisonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtu', 'n.', 'A love of the fine arts; a taste for curiosities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtual', 'a.', 'Having the power of acting or of invisible efficacy\n   without the agency of the material or sensible part; potential;\n   energizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtual', 'a.', 'Being in essence or effect, not in fact; as, the virtual\n   presence of a man in his agent or substitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtuality', 'n.', 'The quality or state of being virtual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtuality', 'n.', 'Potentiality; efficacy; potential existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtually', 'adv.', 'In a virtual manner; in efficacy or effect only, and\n   not actually; to all intents and purposes; practically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtuate', 'v. t.', 'To make efficacious; to give virtue of efficacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtue', 'n.', 'Manly strength or courage; bravery; daring; spirit; valor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtue', 'n.', 'Active quality or power; capacity or power adequate to the\n   production of a given effect; energy; strength; potency; efficacy; as,\n   the virtue of a medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtue', 'n.', 'Energy or influence operating without contact of the\n   material or sensible substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtue', 'n.', 'Excellence; value; merit; meritoriousness; worth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtue', 'n.', 'Specifically, moral excellence; integrity of character;\n   purity of soul; performance of duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtue', 'n.', 'A particular moral excellence; as, the virtue of\n   temperance, of charity, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtue', 'n.', 'Specifically: Chastity; purity; especially, the chastity of\n   women; virginity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtue', 'n.', 'One of the orders of the celestial hierarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtueless', 'a.', 'Destitute of virtue; without efficacy or operating\n   qualities; powerless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtuosity', 'n.', 'The quality or state of being a virtuoso; in a bad\n   sense, the character of one in whom mere artistic feeling or aesthetic\n   cultivation takes the place of religious character; sentimentalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtuosity', 'n.', 'Virtuosos, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtuosity', 'n.', 'An art or study affected by virtuosos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtuosos', 'pl. ', 'of Virtuoso');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtuosi', 'pl. ', 'of Virtuoso');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtuoso', 'n.', 'One devoted to virtu; one skilled in the fine arts, in\n   antiquities, and the like; a collector or ardent admirer of\n   curiosities, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtuoso', 'n.', 'A performer on some instrument, as the violin or the\n   piano, who excels in the technical part of his art; a brilliant concert\n   player.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtuosoship', 'n.', 'The condition, pursuits, or occupation of a virtuoso.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtuous', 'a.', 'Possessing or exhibiting virtue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtuous', 'a.', 'Exhibiting manly courage and strength; valorous; valiant;\n   brave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtuous', 'a.', 'Having power or efficacy; powerfully operative;\n   efficacious; potent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtuous', 'a.', 'Having moral excellence; characterized by morality;\n   upright; righteous; pure; as, a virtuous action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virtuous', 'a.', 'Chaste; pure; -- applied especially to women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virulence', 'n.', 'Alt. of Virulency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virulency', 'n.', 'The quality or state of being virulent or venomous;\n   poisonousness; malignancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virulency', 'n.', 'Extreme bitterness or malignity of disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virulent', 'a.', 'Extremely poisonous or venomous; very active in doing\n   injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virulent', 'a.', 'Very bitter in enmity; actuated by a desire to injure;\n   malignant; as, a virulent invective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virulented', 'a.', 'Made virulent; poisoned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virulently', 'adv.', 'In a virulent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virus', 'v. i.', 'Contagious or poisonous matter, as of specific ulcers,\n   the bite of snakes, etc.; -- applied to organic poisons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virus', 'v. i.', 'The special contagion, inappreciable to the senses and\n   acting in exceedingly minute quantities, by which a disease is\n   introduced into the organism and maintained there.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Virus', 'v. i.', 'Fig.: Any morbid corrupting quality in intellectual or\n   moral conditions; something that poisons the mind or the soul; as, the\n   virus of obscene books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vis', 'n.', 'Force; power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vis', 'n.', 'Physical force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vis', 'n.', 'Moral power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visa', 'n.', 'See Vis/.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visaed', 'imp. & p. p.', 'of Visa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visaing', 'p. pr. & vb. n.', 'of Visa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visa', 'v. t.', 'To indorse, after examination, with the word vise, as a\n   passport; to vise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visage', 'n.', 'The face, countenance, or look of a person or an animal; --\n   chiefly applied to the human face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visage', 'v. t.', 'To face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visaged', 'a.', 'Having a visage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visard', 'n.', 'A mask. See Visor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visard', 'v. t.', 'To mask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vis-a-vis', 'n.', 'One who, or that which, is face to face with another;\n   esp., one who faces another in dancing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vis-a-vis', 'n.', 'A carriage in which two persons sit face to face. Also,\n   a form of sofa with seats for two persons, so arranged that the\n   occupants are face to face while sitting on opposite sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vis-a-vis', 'adv.', 'Face to face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscacha', 'n.', 'Alt. of Viz-cacha');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viz-cacha', 'n.', 'A large burrowing South American rodent (Lagostomus\n   trichodactylus) allied to the chinchillas, but much larger. Its fur is\n   soft and rather long, mottled gray above, white or yellowish white\n   beneath. There is a white band across the muzzle, and a dark band on\n   each cheek. It inhabits grassy plains, and is noted for its extensive\n   burrows and for heaping up miscellaneous articles at the mouth of its\n   burrows. Called also biscacha, bizcacha, vischacha, vishatscha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscera', 'n.', 'pl. of Viscus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visceral', 'a.', 'Of or pertaining to the viscera; splanchnic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visceral', 'a.', 'Fig.: Having deep sensibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscerated', 'imp. & p. p.', 'of Viscerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscerating', 'p. pr. & vb. n.', 'of Viscerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscerate', 'v. t.', 'To deprive of the viscera, or entrails; to\n   eviscerate; to disembowel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visceroskeletal', 'a.', 'Of or pertaining to the framework, or skeleton, or\n   skeleton, of the viscera; as, the visceroskeletal system of muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscid', 'a.', 'Sticking or adhering, and having a ropy or glutinous\n   consistency; viscous; glutinous; sticky; tenacious; clammy; as,\n   turpentine, tar, gums, etc., are more or less viscid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscidity', 'n.', 'The quality or state of being viscid; also, that which\n   is viscid; glutinous concretion; stickiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscin', 'n.', 'A clear, viscous, tasteless substance extracted from the\n   mucilaginous sap of the mistletoe (Viscum album), holly, etc., and\n   constituting an essential ingredient of birdlime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscoidal', 'a.', 'Somewhat viscous. Cf. Mobile, a., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscosimeter', 'n.', 'An instrument for measuring the degree of viscosity\n   of liquids, as solutions of gum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscosity', 'n.', 'The quality or state of being viscous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscosity', 'n.', 'A quality analogous to that of a viscous fluid, supposed\n   to be caused by internal friction, especially in the case of gases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscount', 'a.', 'An officer who formerly supplied the place of the count,\n   or earl; the sheriff of the county.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscount', 'a.', 'A nobleman of the fourth rank, next in order below an\n   earl and next above a baron; also, his degree or title of nobility. See\n   Peer, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscountcy', 'n.', 'The dignity or jurisdiction of a viscount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscountess', 'n.', 'The wife of a viscount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscountship', 'n.', 'Alt. of Viscounty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscounty', 'n.', 'The quality, rank, or office of a viscount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscous', 'a.', 'Adhesive or sticky, and having a ropy or glutinous\n   consistency; viscid; glutinous; clammy; tenacious; as, a viscous juice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscum', 'n.', 'A genus of parasitic shrubs, including the mistletoe of\n   Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscum', 'n.', 'Birdlime, which is often made from the berries of the\n   European mistletoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscera', 'pl. ', 'of Viscus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viscus', 'n.', 'One of the organs, as the brain, heart, or stomach, in the\n   great cavities of the body of an animal; -- especially used in the\n   plural, and applied to the organs contained in the abdomen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vise', 'n.', 'An instrument consisting of two jaws, closing by a screw,\n   lever, cam, or the like, for holding work, as in filing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vise', 'n.', 'An indorsement made on a passport by the proper authorities\n   of certain countries on the continent of Europe, denoting that it has\n   been examined, and that the person who bears it is permitted to proceed\n   on his journey; a visa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viseed', 'imp. & p. p.', 'of Vise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viseing', 'p. pr. & vb. n.', 'of Vise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vise', 'v. t.', 'To examine and indorse, as a passport; to visa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vishnu', 'n.', 'A divinity of the modern Hindu trimurti, or trinity. He is\n   regarded as the preserver, while Brahma is the creator, and Siva the\n   destroyer of the creation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visibility', 'n.', 'The quality or state of being visible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visible', 'a.', 'Perceivable by the eye; capable of being seen;\n   perceptible; in view; as, a visible star; the least spot is visible on\n   white paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visible', 'a.', 'Noticeable; apparent; open; conspicuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visigoth', 'n.', 'One of the West Goths. See the Note under Goth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vision', 'v.', 'The act of seeing external objects; actual sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vision', 'v.', 'The faculty of seeing; sight; one of the five senses, by\n   which colors and the physical qualities of external objects are\n   appreciated as a result of the stimulating action of light on the\n   sensitive retina, an expansion of the optic nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vision', 'v.', 'That which is seen; an object of sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vision', 'v.', 'Especially, that which is seen otherwise than by the\n   ordinary sight, or the rational eye; a supernatural, prophetic, or\n   imaginary sight; an apparition; a phantom; a specter; as, the visions\n   of Isaiah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vision', 'v.', 'Hence, something unreal or imaginary; a creation of fancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visioned', 'imp. & p. p.', 'of Vision');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visioning', 'p. pr. & vb. n.', 'of Vision');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vision', 'v. t.', 'To see in a vision; to dream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visional', 'a.', 'Of or pertaining to a vision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visionariness', 'n.', 'The quality or state of being visionary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visionary', 'a.', 'Of or pertaining to a visions or visions; characterized\n   by, appropriate to, or favorable for, visions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visionary', 'a.', 'Affected by phantoms; disposed to receive impressions on\n   the imagination; given to reverie; apt to receive, and act upon,\n   fancies as if they were realities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visionary', 'a.', 'Existing in imagination only; not real; fanciful;\n   imaginary; having no solid foundation; as, visionary prospect; a\n   visionary scheme or project.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visionaries', 'pl. ', 'of Visionary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visionary', 'n.', 'One whose imagination is disturbed; one who sees visions\n   or phantoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visionary', 'n.', 'One whose imagination overpowers his reason and controls\n   his judgment; an unpractical schemer; one who builds castles in the\n   air; a daydreamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visioned', 'a.', 'Having the power of seeing visions; inspired; also, seen\n   in visions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visionist', 'n.', 'A visionary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visionless', 'a.', 'Destitute of vision; sightless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visited', 'imp. & p. p.', 'of Visit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visiting', 'p. pr. & vb. n.', 'of Visit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visit', 'v. t.', 'To go or come to see, as for the purpose of friendship,\n   business, curiosity, etc.; to attend; to call upon; as, the physician\n   visits his patient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visit', 'v. t.', 'To go or come to see for inspection, examination,\n   correction of abuses, etc.; to examine, to inspect; as, a bishop visits\n   his diocese; a superintendent visits persons or works under his charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visit', 'v. t.', 'To come to for the purpose of chastising, rewarding,\n   comforting; to come upon with reward or retribution; to appear before\n   or judge; as, to visit in mercy; to visit one in wrath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visit', 'v. i.', 'To make a visit or visits; to maintain visiting\n   relations; to practice calling on others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visit', 'v. t.', 'The act of visiting, or going to see a person or thing; a\n   brief stay of business, friendship, ceremony, curiosity, or the like,\n   usually longer than a call; as, a visit of civility or respect; a visit\n   to Saratoga; the visit of a physician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visit', 'v. t.', 'The act of going to view or inspect; an official or\n   formal inspection; examination; visitation; as, the visit of a trustee\n   or inspector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visitable', 'a.', 'Liable or subject to be visited or inspected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visitant', 'n.', 'One who visits; a guest; a visitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visitant', 'a.', 'Visiting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visitation', 'n.', 'The act of visiting, or the state of being visited;\n   access for inspection or examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visitation', 'n.', 'Specifically: The act of a superior or superintending\n   officer who, in the discharge of his office, visits a corporation,\n   college, etc., to examine into the manner in which it is conducted, and\n   see that its laws and regulations are duly observed and executed; as,\n   the visitation of a diocese by a bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visitation', 'n.', 'The object of a visit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visitation', 'n.', 'The act of a naval commander who visits, or enters on\n   board, a vessel belonging to another nation, for the purpose of\n   ascertaining her character and object, but without claiming or\n   exercising a right of searching the vessel. It is, however, usually\n   coupled with the right of search (see under Search), visitation being\n   used for the purpose of search.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visitation', 'n.', 'Special dispensation; communication of divine favor and\n   goodness, or, more usually, of divine wrath and vengeance; retributive\n   calamity; retribution; judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visitation', 'n.', 'A festival in honor of the visit of the Virgin Mary to\n   Elisabeth, mother of John the Baptist, celebrated on the second of\n   July.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visitatorial', 'a.', 'Of or pertaining to visitation, or a judicial visitor\n   or superintendent; visitorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visite', 'n.', 'A light cape or short cloak of silk or lace worn by women\n   in summer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visiter', 'n.', 'A visitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visiting', '', 'a. & vb. n. from Visit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visitor', '', 'One who visits; one who comes or goes to see another, as in\n   civility or friendship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visitor', '', 'A superior, or a person lawfully appointed for the purpose,\n   who makes formal visits of inspection to a corporation or an\n   institution. See Visit, v. t., 2, and Visitation, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visitorial', 'a.', 'Same as Visitatorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visive', 'a.', 'Of or pertaining to the sight; visual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visne', 'n.', 'Neighborhood; vicinity; venue. See Venue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visnomy', 'n.', 'Face; countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vison', 'n.', 'The mink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visor', 'n.', 'A part of a helmet, arranged so as to lift or open, and so\n   show the face. The openings for seeing and breathing are generally in\n   it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visor', 'n.', 'A mask used to disfigure or disguise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visor', 'n.', 'The fore piece of a cap, projecting over, and protecting the\n   eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visored', 'a.', 'Wearing a visor; masked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vistas', 'pl. ', 'of Vista');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vista', 'n.', 'A view; especially, a view through or between intervening\n   objects, as trees; a view or prospect through an avenue, or the like;\n   hence, the trees or other objects that form the avenue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visto', 'n.', 'A vista; a prospect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visual', 'a.', 'Of or pertaining to sight; used in sight; serving as the\n   instrument of seeing; as, the visual nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visual', 'a.', 'That can be seen; visible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Visualize', 'v. t.', 'To make visual, or visible; to see in fancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitaille', 'n.', 'Food; victuals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vital', 'a.', 'Belonging or relating to life, either animal or vegetable;\n   as, vital energies; vital functions; vital actions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vital', 'a.', 'Contributing to life; necessary to, or supporting, life; as,\n   vital blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vital', 'a.', 'Containing life; living.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vital', 'a.', 'Being the seat of life; being that on which life depends;\n   mortal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vital', 'a.', 'Very necessary; highly important; essential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vital', 'a.', 'Capable of living; in a state to live; viable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vital', 'n.', 'A vital part; one of the vitals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitalic', 'a.', 'Pertaining to life; vital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitalism', 'n.', 'The doctrine that all the functions of a living organism\n   are due to an unknown vital principle distinct from all chemical and\n   physical forces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitalist', 'n.', 'A believer in the theory of vitalism; -- opposed to\n   physicist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitalistic', 'a.', 'Pertaining to, or involving, vitalism, or the theory of\n   a special vital principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitality', 'n.', 'The quality or state of being vital; the principle of\n   life; vital force; animation; as, the vitality of eggs or vegetable\n   seeds; the vitality of an enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitalization', 'n.', 'The act or process of vitalizing, or infusing the\n   vital principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitalized', 'imp. & p. p.', 'of Vitalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitalizing', 'p. pr. & vb. n.', 'of Vitalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitalize', 'v. t.', 'To endow with life, or vitality; to give life to; to\n   make alive; as, vitalized blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitally', 'adv.', 'In a vital manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitals', 'n. pl.', 'Organs that are necessary for life; more especially,\n   the heart, lungs, and brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitals', 'n. pl.', 'Fig.: The part essential to the life or health of\n   anything; as, the vitals of a state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitellary', 'a.', 'Vitelline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitelligenous', 'a.', 'Producing yolk, or vitelline substance; -- applied\n   to certain cells (also called nutritive, or yolk, cells) formed in the\n   ovaries of many insects, and supposed to supply nutriment to the\n   developing ova.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitellin', 'n.', 'An albuminous body, belonging to the class of globulins,\n   obtained from yolk of egg, of which it is the chief proteid\n   constituent, and from the seeds of many plants. From the latter it can\n   be separated in crystalline form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitelline', 'a.', 'Of or pertaining to the yolk of eggs; as, the vitelline\n   membrane, a smooth, transparent membrane surrounding the vitellus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitellogene', 'n.', 'A gland secreting the yolk of the eggs in trematodes,\n   turbellarians, and some other helminths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitellus', 'n.', 'The contents or substance of the ovum; egg yolk. See\n   Illust. of Ovum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitellus', 'n.', 'Perisperm in an early condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitiated', 'imp. & p. p.', 'of Vitiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitiating', 'p. pr. & vb. n.', 'of Vitiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitiate', 'v. t.', 'To make vicious, faulty, or imperfect; to render\n   defective; to injure the substance or qualities of; to impair; to\n   contaminate; to spoil; as, exaggeration vitiates a style of writing;\n   sewer gas vitiates the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitiate', 'v. t.', 'To cause to fail of effect, either wholly or in part;\n   to make void; to destroy, as the validity or binding force of an\n   instrument or transaction; to annul; as, any undue influence exerted on\n   a jury vitiates their verdict; fraud vitiates a contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitiation', 'n.', 'The act of vitiating, or the state of being vitiated;\n   depravation; corruption; invalidation; as, the vitiation of the blood;\n   the vitiation of a contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viticulose', 'a.', 'Having long and slender trailing stems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viticultural', 'a.', 'Of or pertaining to viticulture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viticulture', 'n.', 'The cultivation of the vine; grape growing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viticulturist', 'n.', 'One engaged in viticulture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitiligo', 'n.', 'A rare skin disease consisting in the development of\n   smooth, milk-white spots upon various parts of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitilitigate', 'v. i.', 'To contend in law litigiously or cavilously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitilitigation', 'n.', 'Cavilous litigation; cavillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitiosity', 'n.', 'Viciousness; depravity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitious', 'n.', 'Alt. of Vitiousness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitiously', 'n.', 'Alt. of Vitiousness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitiousness', 'n.', 'See Vicious, Viciously, Viciousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitis', 'n.', 'A genus of plants including all true grapevines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitoe', 'a.', 'See Durukuli.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitrella', 'n.', 'One of the transparent lenslike cells in the ocelli of\n   certain arthropods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitre-o-electic', 'a.', 'Containing or exhibiting positive, or vitreous,\n   electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitreous', 'a.', 'Consisting of, or resembling, glass; glassy; as, vitreous\n   rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitreous', 'a.', 'Of or pertaining to glass; derived from glass; as,\n   vitreous electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitreousness', 'n.', 'The quality or state of being vitreous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitrescence', 'n.', 'The quality or state of being vitreous; glassiness, or\n   the quality of being vitrescent; capability of conversion into glass;\n   susceptibility of being formed into glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitrescent', 'a.', 'Capable of being formed into glass; tending to become\n   glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitrescible', 'a.', 'That may be vitrified; vitrifiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitric', 'a.', 'Having the nature and qualities of glass; glasslike; --\n   distinguished from ceramic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitrifaction', 'n.', 'The act, art, or process of vitrifying; also, the\n   state of being vitrified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitrifacture', 'n.', 'The manufacture of glass and glassware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitrifiable', 'a.', 'Capable of being vitrified, or converted into glass by\n   heat and fusion; as, flint and alkalies are vitrifiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitrificable', 'a.', 'Vitrifiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitrificate', 'v. t.', 'To convert into glass; to vitrify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitrification', 'n.', 'Same as Vitrifaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitrified', 'a.', 'Converted into glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitriform', 'a.', 'Having the form or appearance of glass; resembling\n   glass; glasslike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitrified', 'imp. & p. p.', 'of Vitrify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitrifying', 'p. pr. & vb. n.', 'of Vitrify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitrify', 'v. t.', 'To convert into, or cause to resemble, glass or a\n   glassy substance, by heat and fusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitrify', 'v. t.', 'To become glass; to be converted into glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitrina', 'n.', 'A genus of terrestrial gastropods, having transparent,\n   very thin, and delicate shells, -- whence the name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitriol', 'n.', 'A sulphate of any one of certain metals, as copper, iron,\n   zinc, cobalt. So called on account of the glassy appearance or luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitriol', 'n.', 'Sulphuric acid; -- called also oil of vitriol. So called\n   because first made by the distillation of green vitriol. See Sulphuric\n   acid, under Sulphuric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitriolated', 'imp. & p. p.', 'of Vitriolate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitriolating', 'p. pr. & vb. n.', 'of Vitriolate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitriolate', 'v. t.', 'To convert into, or change to, a vitriol; to make\n   into sulphuric acid or a sulphate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitriolate', 'v. t.', 'To subject to the action of, or impregnate with,\n   vitriol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitriolate', 'a.', 'Vitriolated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitriolate', 'n.', 'A sulphate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitriolated', 'a.', 'Changed into a vitriol or a sulphate, or subjected to\n   the action of sulphuric acid or of a sulphate; as, vitriolated potash,\n   i. e., potassium sulphate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitriolation', 'n.', 'The act, process, or result of vitriolating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitriolic', 'a.', 'Of or pertaining to vitriol; derived from, or\n   resembling, vitriol; vitriolous; as, a vitriolic taste. Cf. Vitriol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitriolizable', 'a.', 'Capable of being converted into a vitriol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitriolization', 'n.', 'The act of vitriolizing, or the state of being\n   vitriolized; vitriolation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitriolize', 'v. t.', 'To convert into a vitriol; to vitriolate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitriolous', 'a.', 'See Vitriolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitrite', 'n.', 'A kind of glass which is very hard and difficult to fuse,\n   used as an insulator in electrical lamps and other apparatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitruvian', 'a.', 'Of or pertaining to Vitruvius, an ancient Roman\n   architect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vittae', 'pl. ', 'of Vitta');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitta', 'n.', 'One of the oil tubes in the fruit of umbelliferous plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vitta', 'n.', 'A band, or stripe, of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vittate', 'a.', 'Bearing or containing vittae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vittate', 'a.', 'Striped longitudinally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vituline', 'a.', 'Of or pertaining to a calf or veal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vituperable', 'a.', 'Liable to, or deserving, vituperation, or severe\n   censure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vituperate', 'v. t.', 'To find fault with; to scold; to overwhelm with\n   wordy abuse; to censure severely or abusively; to rate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vituperation', 'n.', 'The act of vituperating; abuse; severe censure;\n   blame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vituperative', 'a.', 'Uttering or writing censure; containing, or\n   characterized by, abuse; scolding; abusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vituperator', 'n.', 'One who vituperates, or censures abusively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vituperrious', 'a.', 'Worthy of vituperation; shameful; disgraceful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivace', 'a. & adv.', 'Brisk; vivacious; with spirit; -- a direction to\n   perform a passage in a brisk and lively manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivacious', 'a.', 'Having vigorous powers of life; tenacious of life;\n   long-lived.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivacious', 'a.', 'Sprightly in temper or conduct; lively; merry; as, a\n   vivacious poet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivacious', 'a.', 'Living through the winter, or from year to year;\n   perennial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivacity', 'n.', 'The quality or state of being vivacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivacity', 'n.', 'Tenacity of life; vital force; natural vigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivacity', 'n.', 'Life; animation; spiritedness; liveliness; sprightliness;\n   as, the vivacity of a discourse; a lady of great vivacity; vivacity of\n   countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivandiere', 'n.', 'In Continental armies, especially in the French army, a\n   woman accompanying a regiment, who sells provisions and liquor to the\n   soldiers; a female sutler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivariums', 'pl. ', 'of Vivarium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivaria', 'pl. ', 'of Vivarium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivarium', 'n.', 'A place artificially arranged for keeping or raising\n   living animals, as a park, a pond, an aquarium, a warren, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivaries', 'pl. ', 'of Vivary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivary', 'n.', 'A vivarium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viva voce', '', 'By word of mouth; orally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivda', 'n.', 'See Vifda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vive', '', 'Long live, that is, success to; as, vive le roi, long live the\n   king; vive la bagatelle, success to trifles or sport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vive', 'a.', 'Lively; animated; forcible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vively', 'adv.', 'In a lively manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivency', 'n.', 'Manner of supporting or continuing life or vegetation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viverra', 'n.', 'A genus of carnivores which comprises the civets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viverrine', 'a.', 'Of or pertaining to the Viverridae, or Civet family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivers', 'n. pl.', 'Provisions; victuals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vives', 'n.', 'A disease of brute animals, especially of horses, seated in\n   the glands under the ear, where a tumor is formed which sometimes ends\n   in suppuration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivianite', 'n.', 'A hydrous phosphate of iron of a blue to green color,\n   growing darker on exposure. It occurs in monoclinic crystals, also\n   fibrous, massive, and earthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivid', 'a.', 'True to the life; exhibiting the appearance of life or\n   freshness; animated; spirited; bright; strong; intense; as, vivid\n   colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivid', 'a.', 'Forming brilliant images, or painting in lively colors;\n   lively; sprightly; as, a vivid imagination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vividity', 'n.', 'The quality or state of being vivid; vividness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivific', 'a.', 'Alt. of Vivifical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivifical', 'a.', 'Giving life; reviving; enlivening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivificate', 'v. t.', 'To give life to; to animate; to revive; to vivify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivificate', 'v. t.', 'To bring back a metal to the metallic form, as from\n   an oxide or solution; to reduce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivification', 'n.', 'The act of vivifying, or the state of being vivified;\n   restoration of life; revival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivification', 'n.', 'One of the changes of assimilation, in which proteid\n   matter which has been transformed, and made a part of the tissue or\n   tissue cells, is endowed with life, and thus enabled to manifest the\n   phenomena of irritability, contractility, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivification', 'n.', 'The act or process of vivificating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivificative', 'a.', 'Able or tending to vivify, animate, or give life;\n   vivifying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivified', 'imp. & p. p.', 'of Vivify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivifying', 'p. pr. & vb. n.', 'of Vivify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivify', 'v. t.', 'To endue with life; to make to be living; to quicken; to\n   animate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivipara', 'n. pl.', 'An artificial division of vertebrates including those\n   that produce their young alive; -- opposed to Ovipara.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viviparity', 'n.', 'The quality or condition of being viviparous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viviparous', 'a.', 'Producing young in a living state, as most mammals, or\n   as those plants the offspring of which are produced alive, either by\n   bulbs instead of seeds, or by the seeds themselves germinating on the\n   plant, instead of falling, as they usually do; -- opposed to oviparous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viviparously', 'adv.', 'In a viviparous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viviparousness', 'n.', 'The quality of being viviparous; viviparity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivisect', 'v. t.', 'To perform vivisection upon; to dissect alive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivisection', 'n.', 'The dissection of an animal while alive, for the\n   purpose of making physiological investigations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivisectional', 'a.', 'Of or pertaining to vivisection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivisectionist', 'n.', 'One who practices or advocates vivisection; a\n   vivisector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vivisector', 'n.', 'A vivisectionist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vixen', 'n.', 'A female fox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vixen', 'n.', 'A cross, ill-tempered person; -- formerly used of either\n   sex, now only of a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vixenish', 'a.', 'Of or pertaining to a vixen; resembling a vixen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vixenly', 'a.', 'Like a vixen; vixenish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Viz', 'adv.', 'To wit; that is; namely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vizard', 'n.', 'A mask; a visor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vizarded', 'a.', 'Wearing a vizard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vizcacha', 'n.', 'Same as Viscacha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vizier', 'n.', 'A councilor of state; a high executive officer in Turkey\n   and other Oriental countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vizierate', 'n.', 'The office, dignity, or authority of a vizier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vizier-azem', 'n.', 'A grand vizier. See under Vizier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vizierial', 'a.', 'Of, pertaining to, or issued by, a vizier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vizir', 'n.', 'See Vizier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vizor', 'n.', 'See Visor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vlissmaki', 'n.', 'The diadem indris. See Indris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'V moth', '', 'A common gray European moth (Halia vauaria) having a V-shaped\n   spot of dark brown on each of the fore wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocable', 'n.', 'A word; a term; a name; specifically, a word considered as\n   composed of certain sounds or letters, without regard to its meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocabularies', 'pl. ', 'of Vocabulary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocabulary', 'n.', 'A list or collection of words arranged in alphabetical\n   order and explained; a dictionary or lexicon, either of a whole\n   language, a single work or author, a branch of science, or the like; a\n   word-book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocabulary', 'n.', 'A sum or stock of words employed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocabulist', 'n.', 'The writer or maker of a vocabulary; a lexicographer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocal', 'a.', 'Of or pertaining to the voice or speech; having voice;\n   endowed with utterance; full of voice, or voices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocal', 'a.', 'Uttered or modulated by the voice; oral; as, vocal melody;\n   vocal prayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocal', 'a.', 'Of or pertaining to a vowel or voice sound; also, /poken\n   with tone, intonation, and resonance; sonant; sonorous; -- said of\n   certain articulate sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocal', 'a.', 'Consisting of, or characterized by, voice, or tone produced\n   in the larynx, which may be modified, either by resonance, as in the\n   case of the vowels, or by obstructive action, as in certain consonants,\n   such as v, l, etc., or by both, as in the nasals m, n, ng; sonant;\n   intonated; voiced. See Voice, and Vowel, also Guide to Pronunciation,\n   // 199-202.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocal', 'a.', 'Of or pertaining to a vowel; having the character of a\n   vowel; vowel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocal', 'n.', 'A vocal sound; specifically, a purely vocal element of\n   speech, unmodified except by resonance; a vowel or a diphthong; a tonic\n   element; a tonic; -- distinguished from a subvocal, and a nonvocal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocal', 'n.', 'A man who has a right to vote in certain elections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocalic', 'a.', 'Of or pertaining to vowel sounds; consisting of the vowel\n   sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocalism', 'n.', 'The exercise of the vocal organs; vocalization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocalism', 'n.', 'A vocalic sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocalist', 'n.', 'A singer, or vocal musician, as opposed to an\n   instrumentalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocality', 'n.', 'The quality or state of being vocal; utterableness;\n   resonance; as, the vocality of the letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocality', 'n.', 'The quality of being a vowel; vocalic character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocalization', 'n.', 'The act of vocalizing, or the state of being\n   vocalized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocalization', 'n.', 'The formation and utterance of vocal sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocalized', 'imp. & p. p.', 'of Vocalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocalizing', 'p. pr. & vb. n.', 'of Vocalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocalize', 'v. t.', 'To form into voice; to make vocal or sonant; to give\n   intonation or resonance to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocalize', 'v. t.', 'To practice singing on the vowel sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocally', 'adv.', 'In a vocal manner; with voice; orally; with audible\n   sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocally', 'adv.', 'In words; verbally; as, to express desires vocally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocalness', 'n.', 'The quality of being vocal; vocality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocation', 'n.', 'A call; a summons; a citation; especially, a designation\n   or appointment to a particular state, business, or profession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocation', 'n.', 'Destined or appropriate employment; calling; occupation;\n   trade; business; profession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocation', 'n.', 'A calling by the will of God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocation', 'n.', 'The bestowment of Gods distinguishing grace upon a\n   person or nation, by which that person or nation is put in the way of\n   salvation; as, the vocation of the Jews under the old dispensation, and\n   of the Gentiles under the gospel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocation', 'n.', 'A call to special religious work, as to the ministry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocative', 'a.', 'Of or pertaining to calling; used in calling;\n   specifically (Gram.), used in address; appellative; -- said of that\n   case or form of the noun, pronoun, or adjective, in which a person or\n   thing is addressed; as, Domine, O Lord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocative', 'n.', 'The vocative case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vociferance', 'n.', 'Vociferation; noise; clamor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vociferant', 'a.', 'Noisy; clamorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vociferate', 'v. i.', 'To cry out with vehemence; to exclaim; to bawl; to\n   clamor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vociferated', 'imp. & p. p.', 'of Vociferate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vociferating', 'p. pr. & vb. n.', 'of Vociferate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vociferate', 'v. t.', 'To utter with a loud voice; to shout out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vociferation', 'n.', 'The act of vociferating; violent outcry; vehement\n   utterance of the voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vociferator', 'n.', 'One who vociferates, or is clamorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vociferous', 'a.', 'Making a loud outcry; clamorous; noisy; as, vociferous\n   heralds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vocule', 'n.', 'A short or weak utterance; a faint or feeble sound, as that\n   heard on separating the lips in pronouncing p or b.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vodanium', 'n.', 'A supposed element, afterward found to be a mixture of\n   several metals, as copper, iron, lead, nickel, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vodka', 'n.', 'A Russian drink distilled from rye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voe', 'n.', 'An inlet, bay, or creek; -- so called in the Orkney and\n   Shetland Islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vogle', 'n.', 'Same as Vugg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vogue', 'n.', 'The way or fashion of people at any particular time;\n   temporary mode, custom, or practice; popular reception for the time; --\n   used now generally in the phrase in vogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vogue', 'n.', 'Influence; power; sway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voice', 'n.', 'Sound uttered by the mouth, especially that uttered by human\n   beings in speech or song; sound thus uttered considered as possessing\n   some special quality or character; as, the human voice; a pleasant\n   voice; a low voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voice', 'n.', 'Sound of the kind or quality heard in speech or song in the\n   consonants b, v, d, etc., and in the vowels; sonant, or intonated,\n   utterance; tone; -- distinguished from mere breath sound as heard in f,\n   s, sh, etc., and also whisper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voice', 'n.', 'The tone or sound emitted by anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voice', 'n.', 'The faculty or power of utterance; as, to cultivate the\n   voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voice', 'n.', 'Language; words; speech; expression; signification of\n   feeling or opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voice', 'n.', 'Opinion or choice expressed; judgment; a vote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voice', 'n.', 'Command; precept; -- now chiefly used in scriptural\n   language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voice', 'n.', 'One who speaks; a speaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voice', 'n.', 'A particular mode of inflecting or conjugating verbs, or a\n   particular form of a verb, by means of which is indicated the relation\n   of the subject of the verb to the action which the verb expresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voiced', 'imp. & p. p.', 'of Voice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voicing', 'p. pr. & vb. n.', 'of Voice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voice', 'v. t.', 'To give utterance or expression to; to utter; to publish;\n   to announce; to divulge; as, to voice the sentiments of the nation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voice', 'v. t.', 'To utter with sonant or vocal tone; to pronounce with a\n   narrowed glottis and rapid vibrations of the vocal cords; to speak\n   above a whisper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voice', 'v. t.', 'To fit for producing the proper sounds; to regulate the\n   tone of; as, to voice the pipes of an organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voice', 'v. t.', 'To vote; to elect; to appoint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voice', 'v. i.', 'To clamor; to cry out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voiced', 'a.', 'Furnished with a voice; expressed by the voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voiced', 'a.', 'Uttered with voice; pronounced with vibrations of the vocal\n   cords; sonant; -- said of a sound uttered with the glottis narrowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voiceful', 'a.', 'Having a voice or vocal quality; having a loud voice or\n   many voices; vocal; sounding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voiceless', 'a.', 'Having no voice, utterance, or vote; silent; mute; dumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voiceless', 'a.', 'Not sounded with voice; as, a voiceless consonant; surd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Void', 'a.', 'Containing nothing; empty; vacant; not occupied; not filled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Void', 'a.', 'Having no incumbent; unoccupied; -- said of offices and the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Void', 'a.', 'Being without; destitute; free; wanting; devoid; as, void of\n   learning, or of common use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Void', 'a.', 'Not producing any effect; ineffectual; vain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Void', 'a.', 'Containing no immaterial quality; destitute of mind or soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Void', 'a.', 'Of no legal force or effect, incapable of confirmation or\n   ratification; null. Cf. Voidable, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Void', 'n.', 'An empty space; a vacuum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voided', 'imp. & p. p.', 'of Void');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voiding', 'p. pr. & vb. n.', 'of Void');
  INSERT INTO words (word, word_type, description) VALUES  (    'Void', 'a.', 'To remove the contents of; to make or leave vacant or empty;\n   to quit; to leave; as, to void a table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Void', 'a.', 'To throw or send out; to evacuate; to emit; to discharge; as,\n   to void excrements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Void', 'a.', 'To render void; to make to be of no validity or effect; to\n   vacate; to annul; to nullify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Void', 'v. i.', 'To be emitted or evacuated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voidable', 'a.', 'Capable of being voided, or evacuated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voidable', 'a.', 'Capable of being avoided, or of being adjudged void,\n   invalid, and of no force; capable of being either avoided or confirmed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voidance', 'n.', 'The act of voiding, emptying, ejecting, or evacuating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voidance', 'n.', 'A ejection from a benefice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voidance', 'n.', 'The state of being void; vacancy, as of a benefice which\n   is without an incumbent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voidance', 'n.', 'Evasion; subterfuge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voided', 'a.', 'Emptied; evacuated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voided', 'a.', 'Annulled; invalidated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voided', 'a.', 'Having the inner part cut away, or left vacant, a narrow\n   border being left at the sides, the tincture of the field being seen in\n   the vacant space; -- said of a charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voider', 'n.', 'One who, or that which, voids, /mpties, vacates, or annuls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voider', 'n.', 'A tray, or basket, formerly used to receive or convey that\n   which is voided or cleared away from a given place; especially, one for\n   carrying off the remains of a meal, as fragments of food; sometimes, a\n   basket for containing household articles, as clothes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voider', 'n.', 'A servant whose business is to void, or clear away, a table\n   after a meal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voider', 'n.', 'One of the ordinaries, much like the flanch, but less\n   rounded and therefore smaller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voiding', 'n.', 'The act of one who, or that which, v/ids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voiding', 'n.', 'That which is voided; that which is ejected or evacuated;\n   a remnant; a fragment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voiding', 'a.', 'Receiving what is ejected or voided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voidness', 'n.', 'The quality or state of being void; /mptiness; vacuity;\n   nullity; want of substantiality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voir dire', '', 'An oath administered to a witness, usually before being\n   sworn in chief, requiring him to speak the truth, or make true answers\n   in reference to matters inquired of, to ascertain his competency to\n   give evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voiture', 'n.', 'A carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voivode', 'n.', 'See Waywode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volacious', 'a.', 'Apt or fit to fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volador', 'n.', 'A flying fish of California (Exoc/tus Californicus): --\n   called also volator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volador', 'n.', 'The Atlantic flying gurnard. See under Flying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volage', 'a.', 'Light; giddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volant', 'a.', 'Passing through the air upon wings, or as if upon wings;\n   flying; hence, passing from place to place; current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volant', 'a.', 'Nimble; light and quick; active; rapid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volant', 'a.', 'Represented as flying, or having the wings spread; as, an\n   eagle volant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volante', 'n.', 'A cumbrous two-wheeled pleasure carriage used in Cuba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volapuk', 'n.', 'Literally, worlds speech; the name of an artificial\n   language invented by Johan Martin Schleyer, of Constance, Switzerland,\n   about 1879.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volapukist', 'n.', 'One who is conversant with, or who favors adoption of,\n   Volapuk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volar', 'a.', 'Of or pertaining to the palm of the hand or the sole of the\n   foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volary', 'n.', 'See Volery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volatile', 'a.', 'Passing through the air on wings, or by the buoyant force\n   of the atmosphere; flying; having the power to fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volatile', 'a.', 'Capable of wasting away, or of easily passing into the\n   aeriform state; subject to evaporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volatile', 'a.', 'Fig.: Light-hearted; easily affected by circumstances;\n   airy; lively; hence, changeable; fickle; as, a volatile temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volatile', 'n.', 'A winged animal; wild fowl; game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volatileness', 'n.', 'Alt. of Volatility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volatility', 'n.', 'Quality or state of being volatile; disposition to\n   evaporate; changeableness; fickleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volatilizable', 'a.', 'Capable of being volatilized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volatilization', 'n.', 'The act or process of volatilizing, or rendering\n   volatile; the state of being volatilized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volatilized', 'imp. & p. p.', 'of Volatilize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volatilizing', 'p. pr. & vb. n.', 'of Volatilize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volatilize', 'v. t.', 'To render volatile; to cause to exhale or evaporate;\n   to cause to pass off in vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volator', 'n.', 'Same as Volador, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vol-au-vent', 'n.', 'A light puff paste, with a raised border, filled,\n   after baking, usually with a ragout of fowl, game, or fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volborthite', 'n.', 'A mineral occurring in small six-sided tabular\n   crystals of a green or yellow color. It is a hydrous vanadate of copper\n   and lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volcanian', 'a.', 'Volcanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volcanic', 'a.', 'Of or pertaining to a volcano or volcanoes; as, volcanic\n   heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volcanic', 'a.', 'Produced by a volcano, or, more generally, by igneous\n   agencies; as, volcanic tufa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volcanic', 'a.', 'Changed or affected by the heat of a volcano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volcanically', 'adv.', 'Like a volcano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volcanicity', 'n.', 'Quality or state of being volcanic; volcanic power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volcanism', 'n.', 'Volcanic power or action; volcanicity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volcanist', 'n.', 'One versed in the history and phenomena of volcanoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volcanist', 'n.', 'One who believes in the igneous, as opposed to the\n   aqueous, origin of the rocks of the earths crust; a vulcanist. Cf.\n   Neptunist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volcanity', 'n.', 'The quality or state of being volcanic, or volcanic\n   origin; volcanicity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volcanization', 'n.', 'The act of volcanizing, or the state of being\n   volcanized; the process of undergoing volcanic heat, and being affected\n   by it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volcanized', 'imp. & p. p.', 'of Volcanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volcanizing', 'p. pr. & vb. n.', 'of Volcanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volcanize', 'v. t.', 'To subject to, or cause to undergo, volcanic heat,\n   and to be affected by its action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volcanoes', 'pl. ', 'of Volcano');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volcano', 'n.', 'A mountain or hill, usually more or less conical in form,\n   from which lava, cinders, steam, sulphur gases, and the like, are\n   ejected; -- often popularly called a burning mountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vole', 'n.', 'A deal at cards that draws all the tricks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vole', 'v. i.', 'To win all the tricks by a vole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vole', 'n.', 'Any one of numerous species of micelike rodents belonging to\n   Arvicola and allied genera of the subfamily Arvicolinae. They have a\n   thick head, short ears, and a short hairy tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volery', 'n.', 'A flight of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volery', 'n.', 'A large bird cage; an aviary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volge', 'n.', 'The common sort of people; the crowd; the mob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volitable', 'a.', 'Volatilizable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volitation', 'n.', 'The act of flying; flight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volitient', 'a.', 'Exercising the will; acting from choice; willing, or\n   having power to will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volition', 'n.', 'The act of willing or choosing; the act of forming a\n   purpose; the exercise of the will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volition', 'n.', 'The result of an act or exercise of choosing or willing;\n   a state of choice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volition', 'n.', 'The power of willing or determining; will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volitional', 'a.', 'Belonging or relating to volition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volitive', 'a.', 'Of or pertaining to the will; originating in the will;\n   having the power to will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volitive', 'a.', 'Used in expressing a wish or permission as, volitive\n   proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volkslieder', 'pl. ', 'of Volkslied');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volkslied', 'n.', 'A popular song, or national air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volleys', 'pl. ', 'of Volley');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volley', 'n.', 'A flight of missiles, as arrows, bullets, or the like; the\n   simultaneous discharge of a number of small arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volley', 'n.', 'A burst or emission of many things at once; as, a volley of\n   words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volley', 'n.', 'A return of the ball before it touches the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volley', 'n.', 'A sending of the ball full to the top of the wicket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volleyed', 'imp. & p. p.', 'of Volley');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volleying', 'p. pr. & vb. n.', 'of Volley');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volley', 'v. t.', 'To discharge with, or as with, a volley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volley', 'v. i.', 'To be thrown out, or discharged, at once; to be\n   discharged in a volley, or as if in a volley; to make a volley or\n   volleys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volley', 'v. i.', 'To return the ball before it touches the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volley', 'v. i.', 'To send the ball full to the top of the wicket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volleyed', 'a.', 'Discharged with a sudden burst, or as if in a volley; as,\n   volleyed thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volow', 'v. t.', 'To baptize; -- used in contempt by the Reformers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volt', 'n.', 'A circular tread; a gait by which a horse going sideways\n   round a center makes two concentric tracks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volt', 'n.', 'A sudden movement to avoid a thrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volt', 'n.', 'The unit of electro-motive force; -- defined by the\n   International Electrical Congress in 1893 and by United States Statute\n   as, that electro-motive force which steadily applied to a conductor\n   whose resistance is one ohm will produce a current of one ampere. It is\n   practically equivalent to / the electro-motive force of a standard\n   Clarks cell at a temperature of 15 C.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volte', 'pl. ', 'of Volta');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volta', 'n.', 'A turning; a time; -- chiefly used in phrases signifying\n   that the part is to be repeated one, two, or more times; as, una volta,\n   once. Seconda volta, second time, points to certain modifications in\n   the close of a repeated strain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volta-electric', 'a.', 'Of or pertaining to voltaic electricity, or\n   voltaism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volta-electrometer', 'n.', 'An instrument for the exact measurement of\n   electric currents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voltage', 'n.', 'Electric potential or potential difference, expressed in\n   volts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voltagraphy', 'n.', 'In electrotypy, the act or art of copying, in metals\n   deposited by electrolytic action, a form or pattern which is made the\n   negative electrode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voltaic', 'a.', 'Of or pertaining to Alessandro Volta, who first devised\n   apparatus for developing electric currents by chemical action, and\n   established this branch of electric science; discovered by Volta; as,\n   voltaic electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voltaic', 'a.', 'Of or pertaining to voltaism, or voltaic electricity; as,\n   voltaic induction; the voltaic arc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voltairean', 'a.', 'Of or relating to Voltaire, the French author.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voltairism', 'n.', 'The theories or practice of Voltaire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voltaism', 'n.', 'That form of electricity which is developed by the\n   chemical action between metals and different liquids; voltaic\n   electricity; also, the science which treats of this form of\n   electricity; -- called also galvanism, from Galvani, on account of his\n   experiments showing the remarkable influence of this agent on animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voltameter', 'n.', 'An instrument for measuring the voltaic electricity\n   passing through it, by its effect in decomposing water or some other\n   chemical compound acting as an electrolyte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voltaplast', 'n.', 'A form of voltaic, or galvanic, battery suitable for\n   use electrotyping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voltatype', 'n.', 'An electrotype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volti', 'imperative.', 'Turn, that is, turn over the leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voltigeur', 'n.', 'A tumbler; a leaper or vaulter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voltigeur', 'n.', 'One of a picked company of irregular riflemen in each\n   regiment of the French infantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voltmeter', 'n.', 'An instrument for measuring in volts the differences of\n   potential between different points of an electrical circuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voltzite', 'n.', 'An oxysulphide of lead occurring in implanted spherical\n   globules of a yellowish or brownish color; -- called also voltzine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volubilate', 'a.', 'Alt. of Volubile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volubile', 'a.', 'Turning, or whirling; winding; twining; voluble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volubility', 'n.', 'The quality or state of being voluble (in any of the\n   senses of the adjective).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluble', 'a.', 'Easily rolling or turning; easily set in motion; apt to\n   roll; rotating; as, voluble particles of matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluble', 'a.', 'Moving with ease and smoothness in uttering words; of\n   rapid speech; nimble in speaking; glib; as, a flippant, voluble,\n   tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluble', 'a.', 'Changeable; unstable; fickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluble', 'a.', 'Having the power or habit of turning or twining; as, the\n   voluble stem of hop plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volume', 'n.', 'A roll; a scroll; a written document rolled up for keeping\n   or for use, after the manner of the ancients.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volume', 'n.', 'Hence, a collection of printed sheets bound together,\n   whether containing a single work, or a part of a work, or more than one\n   work; a book; a tome; especially, that part of an extended work which\n   is bound up together in one cover; as, a work in four volumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volume', 'n.', 'Anything of a rounded or swelling form resembling a roll; a\n   turn; a convolution; a coil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volume', 'n.', 'Dimensions; compass; space occupied, as measured by cubic\n   units, that is, cubic inches, feet, yards, etc.; mass; bulk; as, the\n   volume of an elephants body; a volume of gas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volume', 'n.', 'Amount, fullness, quantity, or caliber of voice or tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volumed', 'a.', 'Having the form of a volume, or roil; as, volumed mist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volumed', 'a.', 'Having volume, or bulk; massive; great.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volumenometer', 'n.', 'An instrument for measuring the volume of a body,\n   especially a solid, by means of the difference in tension caused by its\n   presence and absence in a confined portion of air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volumenometry', 'n.', 'The method or process of measuring volumes by means\n   of the volumenometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volumescope', 'n.', 'An instrument consisting essentially of a glass tube\n   provided with a graduated scale, for exhibiting to the eye the changes\n   of volume of a gas or gaseous mixture resulting from chemical action,\n   and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volumeter', 'n.', 'An instrument for measuring the volumes of gases or\n   liquids by introducing them into a vessel of known capacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volumetric', 'a.', 'Of or pertaining to the measurement of volume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volumetrical', 'a.', 'Volumetric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluminous', 'a.', 'Of or pertaining to volume or volumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluminous', 'a.', 'Consisting of many folds, coils, or convolutions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluminous', 'a.', 'Of great volume, or bulk; large.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluminous', 'a.', 'Having written much, or produced many volumes; copious;\n   diffuse; as, a voluminous writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volumist', 'n.', 'One who writes a volume; an author.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluntarily', 'adv.', 'In a voluntary manner; of ones own will;\n   spontaneously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluntariness', 'n.', 'The quality or state of being voluntary;\n   spontaneousness; specifically, the quality or state of being free in\n   the exercise of ones will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluntary', 'v. t.', 'Proceeding from the will; produced in or by an act of\n   choice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluntary', 'v. t.', 'Unconstrained by the interference of another;\n   unimpelled by the influence of another; not prompted or persuaded by\n   another; done of his or its own accord; spontaneous; acting of ones\n   self, or of itself; free.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluntary', 'v. t.', 'Done by design or intention; intentional; purposed;\n   intended; not accidental; as, if a man kills another by lopping a tree,\n   it is not voluntary manslaughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluntary', 'v. t.', 'Of or pertaining to the will; subject to, or\n   regulated by, the will; as, the voluntary motions of an animal, such as\n   the movements of the leg or arm (in distinction from involuntary\n   motions, such as the movements of the heart); the voluntary muscle\n   fibers, which are the agents in voluntary motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluntary', 'v. t.', 'Endowed with the power of willing; as, man is a\n   voluntary agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluntary', 'v. t.', 'Free; without compulsion; according to the will,\n   consent, or agreement, of a party; without consideration; gratuitous;\n   without valuable consideration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluntary', 'v. t.', 'Of or pertaining to voluntaryism; as, a voluntary\n   church, in distinction from an established or state church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluntaries', 'pl. ', 'of Voluntary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluntary', 'n.', 'One who engages in any affair of his own free will; a\n   volunteer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluntary', 'n.', 'A piece played by a musician, often extemporarily,\n   according to his fancy; specifically, an organ solo played before,\n   during, or after divine service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluntary', 'n.', 'One who advocates voluntaryism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluntaryism', 'n.', 'The principle of supporting a religious system and\n   its institutions by voluntary association and effort, rather than by\n   the aid or patronage of the state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volunteer', 'a.', 'One who enters into, or offers for, any service of his\n   own free will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volunteer', 'a.', 'One who enters into service voluntarily, but who, when\n   in service, is subject to discipline and regulations like other\n   soldiers; -- opposed to conscript; specifically, a voluntary member of\n   the organized militia of a country as distinguished from the standing\n   army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volunteer', 'a.', 'A grantee in a voluntary conveyance; one to whom a\n   conveyance is made without valuable consideration; a party, other than\n   a wife or child of the grantor, to whom, or for whose benefit, a\n   voluntary conveyance is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volunteer', 'a.', 'Of or pertaining to a volunteer or volunteers;\n   consisting of volunteers; voluntary; as, volunteer companies; volunteer\n   advice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volunteered', 'imp. & p. p.', 'of Volunteer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volunteering', 'p. pr. & vb. n.', 'of Volunteer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volunteer', 'v. t.', 'To offer or bestow voluntarily, or without\n   solicitation or compulsion; as, to volunteer ones services.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volunteer', 'v. i.', 'To enter into, or offer for, any service of ones own\n   free will, without solicitation or compulsion; as, he volunteered in\n   that undertaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volupere', 'n.', 'A womans cap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluptuaries', 'pl. ', 'of Voluptuary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluptuary', 'n.', 'A voluptuous person; one who makes his physical\n   enjoyment his chief care; one addicted to luxury, and the gratification\n   of sensual appetites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluptuary', 'a.', 'Voluptuous; luxurious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluptuous', 'a.', 'Full of delight or pleasure, especially that of the\n   senses; ministering to sensuous or sensual gratification; exciting\n   sensual desires; luxurious; sensual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluptuous', 'a.', 'Given to the enjoyments of luxury and pleasure;\n   indulging to excess in sensual gratifications.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volupty', 'n.', 'Voluptuousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volutas', 'pl. ', 'of Voluta');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volutae', 'pl. ', 'of Voluta');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluta', 'n.', 'Any one of numerous species of large, handsome marine\n   gastropods belonging to Voluta and allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volutation', 'n.', 'A rolling of a body; a wallowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volute', 'n.', 'A spiral scroll which forms the chief feature of the Ionic\n   capital, and which, on a much smaller scale, is a feature in the\n   Corinthian and Composite capitals. See Illust. of Capital, also Helix,\n   and Stale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volute', 'n.', 'A spiral turn, as in certain shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volute', 'n.', 'Any voluta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voluted', 'a.', 'Having a volute, or spiral scroll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volution', 'n.', 'A spiral turn or wreath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volution', 'n.', 'A whorl of a spiral shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volva', 'n.', 'A saclike envelope of certain fungi, which bursts open as\n   the plant develops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volvox', 'n.', 'A genus of minute, pale-green, globular, organisms, about\n   one fiftieth of an inch in diameter, found rolling through water, the\n   motion being produced by minute colorless cilia. It has been considered\n   as belonging to the flagellate Infusoria, but is now referred to the\n   vegetable kingdom, and each globule is considered a colony of many\n   individuals. The commonest species is Volvox globator, often called\n   globe animalcule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volvulus', 'n.', 'The spasmodic contraction of the intestines which causes\n   colic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volvulus', 'n.', 'Any twisting or displacement of the intestines causing\n   obstruction; ileus. See Ileus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Volyer', 'n.', 'A lurcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomer', 'n.', 'A bone, or one of a pair of bones, beneath the ethmoid\n   region of the skull, forming a part a part of the partition between the\n   nostrils in man and other mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomer', 'n.', 'The pygostyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomerine', 'a.', 'Of or pertaining to the vomer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomica', 'n.', 'An abscess cavity in the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomica', 'n.', 'An abscess in any other parenchymatous organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomicine', 'n.', 'See Brucine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomic nut', '', 'Same as Nux vomica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomited', 'imp. & p. p.', 'of Vomit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomiting', 'p. pr. & vb. n.', 'of Vomit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomit', 'n.', 'To eject the contents of the stomach by the mouth; to puke;\n   to spew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomit', 'v. t.', 'To throw up; to eject from the stomach through the mouth;\n   to disgorge; to puke; to spew out; -- often followed by up or out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomit', 'v. t.', 'Hence, to eject from any hollow place; to belch forth; to\n   emit; to throw forth; as, volcanoes vomit flame, stones, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomit', 'n.', 'Matter that is vomited; esp., matter ejected from the\n   stomach through the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomit', 'n.', 'That which excites vomiting; an emetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomiting', 'n.', 'The spasmodic ejection of matter from the stomach through\n   the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomition', 'n.', 'The act or power of vomiting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomitive', 'a.', 'Causing the ejection of matter from the stomach; emetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomito', 'n.', 'The yellow fever in its worst form, when it is usually\n   attended with black vomit. See Black vomit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomitory', 'a.', 'Causing vomiting; emetic; vomitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomitories', 'pl. ', 'of Vomitory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomitory', 'n.', 'An emetic; a vomit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomitory', 'n.', 'A principal door of a large ancient building, as of an\n   amphitheater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomiturition', 'n.', 'An ineffectual attempt to vomit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vomiturition', 'n.', 'The vomiting of but little matter; also, that\n   vomiting which is effected with little effort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vondsira', 'n.', 'Same as Vansire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voodoo', 'n.', 'See Voodooism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voodoo', 'n.', 'One who practices voodooism; a negro sorcerer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voodoo', 'a.', 'Of or pertaining to voodooism, or a voodoo; as, voodoo\n   incantations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voodooism', 'n.', 'A degraded form of superstition and sorcery, said to\n   include human sacrifices and cannibalism in some of its rites. It is\n   prevalent among the negroes of Hayti, and to some extent in the United\n   States, and is regarded as a relic of African barbarism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voracious', 'a.', 'Greedy in eating; very hungry; eager to devour or\n   swallow; ravenous; gluttonous; edacious; rapacious; as, a voracious man\n   or appetite; a voracious gulf or whirlpool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voracity', 'n.', 'The quality of being voracious; voraciousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voraginous', 'a.', 'Pertaining to a gulf; full of gulfs; hence, devouring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vortexes', 'pl. ', 'of Vortex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vortices', 'pl. ', 'of Vortex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vortex', 'n.', 'A mass of fluid, especially of a liquid, having a whirling\n   or circular motion tending to form a cavity or vacuum in the center of\n   the circle, and to draw in towards the center bodies subject to its\n   action; the form assumed by a fluid in such motion; a whirlpool; an\n   eddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vortex', 'n.', 'A supposed collection of particles of very subtile matter,\n   endowed with a rapid rotary motion around an axis which was also the\n   axis of a sun or a planet. Descartes attempted to account for the\n   formation of the universe, and the movements of the bodies composing\n   it, by a theory of vortices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vortex', 'n.', 'Any one of numerous species of small Turbellaria belonging\n   to Vortex and allied genera. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vortical', 'a.', 'Of or pertaining to a vortex or vortexes; resembling a\n   vortex in form or motion; whirling; as, a vortical motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vorticel', 'n.', 'A vorticella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vorticellas', 'pl. ', 'of Vorticella');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vorticellae', 'pl. ', 'of Vorticella');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vorticella', 'n.', 'Any one of numerous species of ciliated Infusoria\n   belonging to Vorticella and many other genera of the family\n   Vorticellidae. They have a more or less bell-shaped body with a circle\n   of vibrating cilia around the oral disk. Most of the species have\n   slender, contractile stems, either simple or branched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vorticose', 'a.', 'Vortical; whirling; as, a vorticose motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vortiginous', 'a.', 'Moving rapidly round a center; vortical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Votaress', 'n.', 'A woman who is a votary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Votarist', 'n.', 'A votary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Votary', 'a.', 'Consecrated by a vow or promise; consequent on a vow;\n   devoted; promised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Votaries', 'pl. ', 'of Votary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Votary', 'n.', 'One devoted, consecrated, or engaged by a vow or promise;\n   hence, especially, one devoted, given, or addicted, to some particular\n   service, worship, study, or state of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vote', 'n.', 'An ardent wish or desire; a vow; a prayer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vote', 'n.', 'A wish, choice, or opinion, of a person or a body of persons,\n   expressed in some received and authorized way; the expression of a\n   wish, desire, will, preference, or choice, in regard to any measure\n   proposed, in which the person voting has an interest in common with\n   others, either in electing a person to office, or in passing laws,\n   rules, regulations, etc.; suffrage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vote', 'n.', 'That by means of which will or preference is expressed in\n   elections, or in deciding propositions; voice; a ballot; a ticket; as,\n   a written vote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vote', 'n.', 'Expression of judgment or will by a majority; legal decision\n   by some expression of the minds of a number; as, the vote was\n   unanimous; a vote of confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vote', 'n.', 'Votes, collectively; as, the Tory vote; the labor vote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voted', 'imp. & p. p.', 'of Vote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voting', 'p. pr. & vb. n.', 'of Vote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vote', 'v. i.', 'To express or signify the mind, will, or preference,\n   either viva voce, or by ballot, or by other authorized means, as in\n   electing persons to office, in passing laws, regulations, etc., or in\n   deciding on any proposition in which one has an interest with others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vote', 'v. t.', 'To choose by suffrage; to elec/; as, to vote a candidate\n   into office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vote', 'v. t.', 'To enact, establish, grant, determine, etc., by a formal\n   vote; as, the legislature voted the resolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vote', 'v. t.', 'To declare by general opinion or common consent, as if by\n   a vote; as, he was voted a bore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vote', 'v. t.', 'To condemn; to devote; to doom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voter', 'n.', 'One who votes; one who has a legal right to vote, or give\n   his suffrage; an elector; a suffragist; as, an independent voter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voting', '', 'a. & n. from Vote, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Votist', 'n.', 'One who makes a vow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Votive', 'a.', 'Given by vow, or in fulfillment of a vow; consecrated by a\n   vow; devoted; as, votive offerings; a votive tablet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Votress', 'n.', 'A votaress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vouched', 'imp. & p. p.', 'of Vouch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vouching', 'p. pr. & vb. n.', 'of Vouch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vouch', 'v. t.', 'To call; to summon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vouch', 'v. t.', 'To call upon to witness; to obtest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vouch', 'v. t.', 'To warrant; to maintain by affirmations; to attest; to\n   affirm; to avouch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vouch', 'v. t.', 'To back; to support; to confirm; to establish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vouch', 'v. t.', 'To call into court to warrant and defend, or to make good\n   a warranty of title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vouch', 'v. i.', 'To bear witness; to give testimony or full attestation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vouch', 'v. i.', 'To assert; to aver; to declare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vouch', 'n.', 'Warrant; attestation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vouchee', 'n.', 'The person who is vouched, or called into court to support\n   or make good his warranty of title in the process of common recovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voucher', 'n.', 'One who vouches, or gives witness or full attestation, to\n   anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voucher', 'n.', 'A book, paper, or document which serves to vouch the truth\n   of accounts, or to confirm and establish facts of any kind; also, any\n   acquittance or receipt showing the payment of a debt; as, the\n   merchants books are his vouchers for the correctness of his accounts;\n   notes, bonds, receipts, and other writings, are used as vouchers in\n   proving facts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voucher', 'n.', 'The act of calling in a person to make good his warranty\n   of title in the old form of action for the recovery of lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voucher', 'n.', 'The tenant in a writ of right; one who calls in another to\n   establish his warranty of title. In common recoveries, there may be a\n   single voucher or double vouchers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vouchment', 'n.', 'A solemn assertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vouch/or', 'n.', 'Same as Voucher, 3 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vouchsafed', 'imp. & p. p.', 'of Vouchsafe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vouchsafing', 'p. pr. & vb. n.', 'of Vouchsafe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vouchsafe', 'v. t.', 'To condescend to grant; to concede; to bestow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vouchsafe', 'v. t.', 'To receive or accept in condescension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vouchsafe', 'v. i.', 'To condescend; to deign; to yield; to descend or\n   stoop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vouchsafement', 'n.', 'The act of vouchsafing, or that which is vouchsafed;\n   a gift or grant in condescension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voussoir', 'n.', 'One of the wedgelike stones of which an arch is composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vow', 'n.', 'A solemn promise made to God, or to some deity; an act by\n   which one consecrates or devotes himself, absolutely or conditionally,\n   wholly or in part, for a longer or shorter time, to some act, service,\n   or condition; a devotion of ones possessions; as, a baptismal vow; a\n   vow of poverty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vow', 'n.', 'Specifically, a promise of fidelity; a pledge of love or\n   affection; as, the marriage vow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vowed', 'imp. & p. p.', 'of Vow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vowing', 'p. pr. & vb. n.', 'of Vow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vow', 'n.', 'To give, consecrate, or dedicate to God, or to some deity, by\n   a solemn promise; to devote; to promise solemnly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vow', 'n.', 'To assert solemnly; to asseverate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vow', 'v. i.', 'To make a vow, or solemn promise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vowel', 'n.', 'A vocal, or sometimes a whispered, sound modified by\n   resonance in the oral passage, the peculiar resonance in each case\n   giving to each several vowel its distinctive character or quality as a\n   sound of speech; -- distinguished from a consonant in that the latter,\n   whether made with or without vocality, derives its character in every\n   case from some kind of obstructive action by the mouth organs. Also, a\n   letter or character which represents such a sound. See Guide to\n   Pronunciation, // 5, 146-149.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vowel', 'a.', 'Of or pertaining to a vowel; vocal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voweled', 'a.', 'Furnished with vowels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vowelish', 'a.', 'Of the nature of a vowel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vowelism', 'n.', 'The use of vowels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vowelize', 'v. t.', 'To give the quality, sound, or office of a vowel to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vower', 'n.', 'One who makes a vow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vow-fellow', 'n.', 'One bound by the same vow as another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vox', 'n.', 'A voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voyage', 'n.', 'Formerly, a passage either by sea or land; a journey, in\n   general; but not chiefly limited to a passing by sea or water from one\n   place, port, or country, to another; especially, a passing or journey\n   by water to a distant place or country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voyage', 'n.', 'The act or practice of traveling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voyage', 'n.', 'Course; way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voyaged', 'imp. & p. p.', 'of Voyage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voyaging', 'p. pr. & vb. n.', 'of Voyage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voyage', 'v. i.', 'To take a voyage; especially, to sail or pass by water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voyage', 'v. t.', 'To travel; to pass over; to traverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voyageable', 'a.', 'That may be sailed over, as water or air; navigable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voyager', 'n.', 'One who voyages; one who sails or passes by sea or water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voyageur', 'n.', 'A traveler; -- applied in Canada to a man employed by the\n   fur companies in transporting goods by the rivers and across the land,\n   to and from the remote stations in the Northwest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voyol', 'n.', 'See Viol, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Voyol', 'n.', 'The block through which a messenger passes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vraisemblance', 'n.', 'The appearance of truth; verisimilitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vugg', 'n.', 'Alt. of Vugh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vugh', 'n.', 'A cavity in a lode; -- called also vogle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulcan', 'n.', 'The god of fire, who presided over the working of metals;\n   -- answering to the Greek Hephaestus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulcanian', 'a.', 'Of or pertaining to Vulcan; made by Vulcan; hence, of or\n   pertaining to works in iron or other metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulcanian', 'a.', 'Volcanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulcanic', 'a.', 'Of or pertaining to Vulcan; made by Vulcan; Vulcanian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulcanic', 'a.', 'Of or pertaining to volcanoes; specifically, relating to\n   the geological theory of the Vulcanists, or Plutonists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulcanicity', 'n.', 'Volcanicity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulcanism', 'n.', 'Volcanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulcanist', 'n.', 'A volcanist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulcanite', 'n.', 'Hard rubber produced by vulcanizing with a large\n   proportion of sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulcanization', 'n.', 'The act or process of imparting to caoutchouc,\n   gutta-percha, or the like, greater elasticity, durability, or hardness\n   by heating with sulphur under pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulcanized', 'imp. & p. p.', 'of Vulcanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulcanizing', 'p. pr. & vb. n.', 'of Vulcanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulcanize', 'v. t.', 'To change the properties of, as caoutchouc, or India\n   rubber, by the process of vulcanization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulcanizer', 'n.', 'One who, or that which, vulcanizes; esp., an apparatus\n   for vulcanizing caoutchouc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulcano', 'n.', 'A volcano.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulcanology', 'n.', 'The science which treats of phenomena due to plutonic\n   action, as in volcanoes, hot springs, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulgar', 'a.', 'Of or pertaining to the mass, or multitude, of people;\n   common; general; ordinary; public; hence, in general use; vernacular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulgar', 'a.', 'Belonging or relating to the common people, as\n   distinguished from the cultivated or educated; pertaining to common\n   life; plebeian; not select or distinguished; hence, sometimes, of\n   little or no value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulgar', 'a.', 'Hence, lacking cultivation or refinement; rustic; boorish;\n   also, offensive to good taste or refined feelings; low; coarse; mean;\n   base; as, vulgar men, minds, language, or manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulgar', 'n.', 'One of the common people; a vulgar person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulgar', 'n.', 'The vernacular, or common language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulgarian', 'n.', 'A vulgar person; one who has vulgar ideas. Used also\n   adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulgarism', 'n.', 'Grossness; rudeness; vulgarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulgarism', 'n.', 'A vulgar phrase or expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulgarity', 'n.', 'The quality or state of being vulgar; mean condition of\n   life; the state of the lower classes of society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulgarity', 'n.', 'Grossness or clownishness of manners of language;\n   absence of refinement; coarseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulgarization', 'n.', 'The act or process of making vulgar, or common.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulgarized', 'imp. & p. p.', 'of Vulgarize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulgarizing', 'p. pr. & vb. n.', 'of Vulgarize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulgarize', 'v. t. & i.', 'To make vulgar, or common.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulgarly', 'adv.', 'In a vulgar manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulgarness', 'n.', 'The quality of being vulgar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulgate', 'a.', 'An ancient Latin version of the Scripture, and the only\n   version which the Roman Church admits to be authentic; -- so called\n   from its common use in the Latin Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulgate', 'a.', 'Of or pertaining to the Vulgate, or the old Latin version\n   of the Scriptures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulnerability', 'n.', 'The quality or state of being vulnerable;\n   vulnerableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulnerable', 'a.', 'Capable of being wounded; susceptible of wounds or\n   external injuries; as, a vulnerable body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulnerable', 'a.', 'Liable to injury; subject to be affected injuriously;\n   assailable; as, a vulnerable reputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulnerableness', 'n.', 'The quality or state of being vulnerable;\n   vulnerability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulnerary', 'a.', 'Useful in healing wounds; adapted to the cure of\n   external injuries; as, vulnerary plants or potions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulnerary', 'n.', 'A vulnerary remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulnerate', 'v. t.', 'To wound; to hurt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulneration', 'n.', 'The act of wounding, or the state of being wounded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulnerose', 'a.', 'Full of wounds; wounded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulnific', 'a.', 'Alt. of Vulnifical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulnifical', 'a.', 'Causing wounds; inflicting wounds; wounding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulnose', 'a.', 'Having wounds; vulnerose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulpes', 'n.', 'A genus of Carnivora including the foxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulpic', 'a.', 'Pertaining to, derived from, or designating, an acid\n   obtained from a lichen (Cetraria vulpina) as a yellow or red\n   crystalline substance which on decomposition yields pulvinic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulpicide', 'n.', 'One who kills a fox, except in hunting; also, the act of\n   so killing a fox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulpine', 'a.', 'Of or pertaining to the fox; resembling the fox; foxy;\n   cunning; crafty; artful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulpinic', 'a.', 'Same as Vulpic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulpinism', 'n.', 'The quality of being cunning like the fox; craft;\n   artfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulpinite', 'n.', 'A scaly granular variety of anhydrite of a grayish white\n   color, used for ornamental purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vultern', 'n.', 'The brush turkey (Talegallus Lathami) of Australia. See\n   Brush turkey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulture', 'n.', 'Any one of numerous species of rapacious birds belonging\n   to Vultur, Cathartes, Catharista, and various other genera of the\n   family Vulturidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulturine', 'a.', 'Of or pertaining to a vulture; resembling a vulture in\n   qualities or looks; as, the vulturine sea eagle (Gypohierax\n   Angolensis); vulturine rapacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulturish', 'a.', 'Vulturous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulturism', 'n.', 'The quality or state of being like a vulture;\n   rapaciousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulturous', 'a.', 'Like a vulture; rapacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulva', 'n.', 'The external parts of the female genital organs; sometimes,\n   the opening between the projecting parts of the external organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulva', 'n.', 'The orifice of the oviduct of an insect or other\n   invertebrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulviform', 'a.', 'Like a cleft with projecting edges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulvitis', 'n.', 'Inflammation of the vulva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulvo-uterine', 'a.', 'Pertaining both to the vulva and the uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vulvovaginal', 'a.', 'Pertaining both to the vulva and the vagina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vyce', 'n.', 'A kind of clamp with gimlet points for holding a barrel head\n   while the staves are being closed around it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Vying', '', 'a. & n. from Vie.\n   W () the twenty-third letter of the English alphabet, is usually a\n   consonant, but sometimes it is a vowel, forming the second element of\n   certain diphthongs, as in few, how. It takes its written form and its\n   name from the repetition of a V, this being the original form of the\n   Roman capital letter which we call U. Etymologically it is most related\n   to v and u. See V, and U. Some of the uneducated classes in England,\n   especially in London, confuse w and v, substituting the one for the\n   other, as weal for veal, and veal for weal; wine for vine, and vine for\n   wine, etc. See Guide to Pronunciation, // 266-268.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waag', 'n.', 'The grivet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waahoo', 'n.', 'The burning bush; -- said to be called after a quack\n   medicine made from it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wabble', 'v. i.', 'To move staggeringly or unsteadily from one side to the\n   other; to vacillate; to move the manner of a rotating disk when the\n   axis of rotation is inclined to that of the disk; -- said of a turning\n   or whirling body; as, a top wabbles; a buzz saw wabbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wabble', 'n.', 'A hobbling, unequal motion, as of a wheel unevenly hung; a\n   staggering to and fro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wabbly', 'a.', 'Inclined to wabble; wabbling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wacke', 'n.', 'Alt. of Wacky');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wacky', 'n.', 'A soft, earthy, dark-colored rock or clay derived from the\n   alteration of basalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wad', 'n.', 'Woad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wad', 'n.', 'A little mass, tuft, or bundle, as of hay or tow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wad', 'n.', 'Specifically: A little mass of some soft or flexible material,\n   such as hay, straw, tow, paper, or old rope yarn, used for retaining a\n   charge of powder in a gun, or for keeping the powder and shot close;\n   also, to diminish or avoid the effects of windage. Also, by extension,\n   a dusk of felt, pasteboard, etc., serving a similar purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wad', 'n.', 'A soft mass, especially of some loose, fibrous substance, used\n   for various purposes, as for stopping an aperture, padding a garment,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waded', 'imp. & p. p.', 'of Wad');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wadding', 'p. pr. & vb. n.', 'of Wad');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wad', 'v. t.', 'To form into a mass, or wad, or into wadding; as, to wad\n   tow or cotton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wad', 'v. t.', 'To insert or crowd a wad into; as, to wad a gun; also, to\n   stuff or line with some soft substance, or wadding, like cotton; as, to\n   wad a cloak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wad', 'n.', 'Alt. of Wadd');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wadd', 'n.', 'An earthy oxide of manganese, or mixture of different oxides\n   and water, with some oxide of iron, and often silica, alumina, lime, or\n   baryta; black ocher. There are several varieties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wadd', 'n.', 'Plumbago, or black lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wadding', 'n.', 'A wad, or the materials for wads; any pliable substance of\n   which wads may be made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wadding', 'n.', 'Any soft stuff of loose texture, used for stuffing or\n   padding garments; esp., sheets of carded cotton prepared for the\n   purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waddled', 'imp. & p. p.', 'of Waddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waddling', 'p. pr. & vb. n.', 'of Waddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waddle', 'v. i.', 'To walk with short steps, swaying the body from one side\n   to the other, like a duck or very fat person; to move clumsily and\n   totteringly along; to toddle; to stumble; as, a child waddles when he\n   begins to walk; a goose waddles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waddle', 'v. t.', 'To trample or tread down, as high grass, by walking\n   through it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waddler', 'n.', 'One who, or that which, waddles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waddlingly', 'adv.', 'In a waddling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wade', 'n.', 'Woad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waded', 'imp. & p. p.', 'of Wade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wading', 'p. pr. & vb. n.', 'of Wade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wade', 'v. i.', 'To go; to move forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wade', 'v. i.', 'To walk in a substance that yields to the feet; to move,\n   sinking at each step, as in water, mud, sand, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wade', 'v. i.', 'Hence, to move with difficulty or labor; to proceed /lowly\n   among objects or circumstances that constantly /inder or embarrass; as,\n   to wade through a dull book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wade', 'v. t.', 'To pass or cross by wading; as, he waded /he rivers and\n   swamps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wade', 'n.', 'The act of wading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wader', 'n.', 'One who, or that which, wades.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wader', 'n.', 'Any long-legged bird that wades in the water in search of\n   food, especially any species of limicoline or grallatorial birds; --\n   called also wading bird. See Illust. g, under Aves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wading', '', 'a. & n. from Wade, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wadmol', 'n.', 'A coarse, hairy, woolen cloth, formerly used for garments\n   by the poor, and for various other purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wadset', 'n.', 'A kind of pledge or mortgage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wadsetter', 'n.', 'One who holds by a wadset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wadies', 'pl. ', 'of Wady');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wady', 'n.', 'A ravine through which a brook flows; the channel of a water\n   course, which is dry except in the rainy season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wae', 'n.', 'A wave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waeg', 'n.', 'The kittiwake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wafer', 'n.', 'A thin cake made of flour and other ingredients.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wafer', 'n.', 'A thin cake or piece of bread (commonly unleavened,\n   circular, and stamped with a crucifix or with the sacred monogram) used\n   in the Eucharist, as in the Roman Catholic Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wafer', 'n.', 'An adhesive disk of dried paste, made of flour, gelatin,\n   isinglass, or the like, and coloring matter, -- used in sealing letters\n   and other documents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wafered', 'imp. & p. p.', 'of Wafer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wafering', 'p. pr. & vb. n.', 'of Wafer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wafer', 'v. t.', 'To seal or close with a wafer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waferer', 'n.', 'A dealer in the cakes called wafers; a confectioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waffle', 'n.', 'A thin cake baked and then rolled; a wafer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waffle', 'n.', 'A soft indented cake cooked in a waffle iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wafted', 'imp. & p. p.', 'of Waft');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wafting', 'p. pr. & vb. n.', 'of Waft');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waft', 'v. t.', 'To give notice to by waving something; to wave the hand\n   to; to beckon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waft', 'v. t.', 'To cause to move or go in a wavy manner, or by the impulse\n   of waves, as of water or air; to bear along on a buoyant medium; as, a\n   balloon was wafted over the channel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waft', 'v. t.', 'To cause to float; to keep from sinking; to buoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waft', 'v. i.', 'To be moved, or to pass, on a buoyant medium; to float.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waft', 'n.', 'A wave or current of wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waft', 'n.', 'A signal made by waving something, as a flag, in the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waft', 'n.', 'An unpleasant flavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waft', 'n.', 'A knot, or stop, in the middle of a flag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waftage', 'n.', 'Conveyance on a buoyant medium, as air or water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wafter', 'n.', 'One who, or that which, wafts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wafter', 'n.', 'A boat for passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wafture', 'n.', 'The act of waving; a wavelike motion; a waft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagged', 'imp. & p. p.', 'of Wag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagging', 'p. pr. & vb. n.', 'of Wag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wag', 'v. t.', 'To move one way and the other with quick turns; to shake to\n   and fro; to move vibratingly; to cause to vibrate, as a part of the\n   body; as, to wag the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wag', 'v. i.', 'To move one way and the other; to be shaken to and fro; to\n   vibrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wag', 'v. i.', 'To be in action or motion; to move; to get along; to\n   progress; to stir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wag', 'v. i.', 'To go; to depart; to pack oft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wag', 'v.', 'The act of wagging; a shake; as, a wag of the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wag', 'v.', 'A man full of sport and humor; a ludicrous fellow; a humorist;\n   a wit; a joker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagati', 'n.', 'A small East Indian wild cat (Felis wagati), regarded by\n   some as a variety of the leopard cat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waged', 'imp. & p. p.', 'of Wage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waging', 'p. pr. & vb. n.', 'of Wage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wage', 'v. t.', 'To pledge; to hazard on the event of a contest; to stake;\n   to bet, to lay; to wager; as, to wage a dollar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wage', 'v. t.', 'To expose ones self to, as a risk; to incur, as a danger;\n   to venture; to hazard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wage', 'v. t.', 'To engage in, as a contest, as if by previous gage or\n   pledge; to carry on, as a war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wage', 'v. t.', 'To adventure, or lay out, for hire or reward; to hire out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wage', 'v. t.', 'To put upon wages; to hire; to employ; to pay wages to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wage', 'v. t.', 'To give security for the performance of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wage', 'v. i.', 'To bind ones self; to engage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wage', 'v. t.', 'That which is staked or ventured; that for which one\n   incurs risk or danger; prize; gage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wage', 'v. t.', 'That for which one labors; meed; reward; stipulated\n   payment for service performed; hire; pay; compensation; -- at present\n   generally used in the plural. See Wages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagel', 'n.', 'See Waggel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagenboom', 'n.', 'A south African proteaceous tree (Protea grandiflora);\n   also, its tough wood, used for making wagon wheels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wager', 'v. t.', 'Something deposited, laid, or hazarded on the event of a\n   contest or an unsettled question; a bet; a stake; a pledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wager', 'v. t.', 'A contract by which two parties or more agree that a\n   certain sum of money, or other thing, shall be paid or delivered to one\n   of them, on the happening or not happening of an uncertain event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wager', 'v. t.', 'That on which bets are laid; the subject of a bet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagered', 'imp. & p. p.', 'of Wager');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagering', 'p. pr. & vb. n.', 'of Wager');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wager', 'v. t.', 'To hazard on the issue of a contest, or on some question\n   that is to be decided, or on some casualty; to lay; to stake; to bet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wager', 'v. i.', 'To make a bet; to lay a wager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagerer', 'n.', 'One who wagers, or lays a bet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagering', 'a.', 'Hazarding; pertaining to the act of one who wagers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wages', 'n.', 'A compensation given to a hired person for services; price\n   paid for labor; recompense; hire. See Wage, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waggel', 'n.', 'The young of the great black-backed gull (Larus marinus),\n   formerly considered a distinct species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waggeries', 'pl. ', 'of Waggery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waggery', 'n.', 'The manner or action of a wag; mischievous merriment;\n   sportive trick or gayety; good-humored sarcasm; pleasantry; jocularity;\n   as, the waggery of a schoolboy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waggie', 'n.', 'The pied wagtail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waggish', 'a.', 'Like a wag; mischievous in sport; roguish in merriment or\n   good humor; frolicsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waggish', 'a.', 'Done, made, or laid in waggery or for sport; sportive;\n   humorous; as, a waggish trick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waggle', 'v. i.', 'To reel, sway, or move from side to side; to move with a\n   wagging motion; to waddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waggled', 'imp. & p. p.', 'of Waggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waggling', 'p. pr. & vb. n.', 'of Waggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waggle', 'v. t.', 'To move frequently one way and the other; to wag; as, a\n   bird waggles his tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wag-halter', 'n.', 'One who moves or wears a halter; one likely to be\n   hanged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagnerite', 'n.', 'A fluophosphate of magnesia, occurring in yellowish\n   crystals, and also in massive forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagon', 'n.', 'A wheeled carriage; a vehicle on four wheels, and usually\n   drawn by horses; especially, one used for carrying freight or\n   merchandise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagon', 'n.', 'A freight car on a railway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagon', 'n.', 'A chariot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagon', 'n.', 'The Dipper, or Charless Wain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagoned', 'imp. & p. p.', 'of Wagon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagoning', 'p. pr. & vb. n.', 'of Wagon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagon', 'v. t.', 'To transport in a wagon or wagons; as, goods are wagoned\n   from city to city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagon', 'v. i.', 'To wagon goods as a business; as, the man wagons between\n   Philadelphia and its suburbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagonage', 'n.', 'Money paid for carriage or conveyance in wagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagonage', 'n.', 'A collection of wagons; wagons, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagoner', 'n.', 'One who conducts a wagon; one whose business it is to\n   drive a wagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagoner', 'n.', 'The constellation Charless Wain, or Ursa Major. See Ursa\n   major, under Ursa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagonette', 'n.', 'A kind of pleasure wagon, uncovered and with seats\n   extended along the sides, designed to carry six or eight persons\n   besides the driver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagonfuls', 'pl. ', 'of Wagonful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagonful', 'n.', 'As much as a wagon will hold; enough to fill a wagon; a\n   wagonload.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagon-headed', 'a.', 'Having a top, or head, shaped like the top of a\n   covered wagon, or resembling in section or outline an inverted U, thus\n   /; as, a wagonheaded ceiling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagonload', 'n.', 'Same as Wagonful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagon-roofed', 'a.', 'Having a roof, or top, shaped like an inverted U;\n   wagon-headed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagonry', 'n.', 'Conveyance by means of a wagon or wagons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagonwright', 'n.', 'One who makes wagons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wagtail', 'n.', 'Any one of many species of Old World singing birds\n   belonging to Motacilla and several allied genera of the family\n   Motacillidae. They have the habit of constantly jerking their long\n   tails up and down, whence the name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wah', 'n.', 'The panda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wahabee', 'n.', 'A follower of Abdel Wahab (b. 1691; d. 1787), a reformer\n   of Mohammedanism. His doctrines prevail particularly among the\n   Bedouins, and the sect, though checked in its influence, extends to\n   most parts of Arabia, and also into India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waid', 'a.', 'Oppressed with weight; crushed; weighed down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waif', 'n.', 'Goods found of which the owner is not known; originally, such\n   goods as a pursued thief threw away to prevent being apprehended, which\n   belonged to the king unless the owner made pursuit of the felon, took\n   him, and brought him to justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waif', 'n.', 'Hence, anything found, or without an owner; that which comes\n   along, as it were, by chance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waif', 'n.', 'A wanderer; a castaway; a stray; a homeless child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waift', 'n.', 'A waif.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wail', 'v. t.', 'To choose; to select.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wailed', 'imp. & p. p.', 'of Wail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wailing', 'p. pr. & vb. n.', 'of Wail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wail', 'v. t.', 'To lament; to bewail; to grieve over; as, to wail ones\n   death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wail', 'v. i.', 'To express sorrow audibly; to make mournful outcry; to\n   weep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wail', 'n.', 'Loud weeping; violent lamentation; wailing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wailer', 'n.', 'One who wails or laments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waileress', 'n.', 'A woman who wails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wailful', 'a.', 'Sorrowful; mournful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wailingly', 'adv.', 'In a wailing manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wailment', 'n.', 'Lamentation; loud weeping; wailing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waiment', 'v. & n.', 'See Wayment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wain', 'n.', 'A four-wheeled vehicle for the transportation of goods,\n   produce, etc.; a wagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wain', 'n.', 'A chariot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wainable', 'a.', 'Capable of being plowed or cultivated; arable; tillable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wainage', 'n.', 'A finding of carriages, carts, etc., for the\n   transportation of goods, produce, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wainage', 'n.', 'See Gainage, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wainbote', 'n.', 'See Cartbote. See also the Note under Bote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wainscot', 'n.', 'Oaken timber or boarding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wainscot', 'n.', 'A wooden lining or boarding of the walls of apartments,\n   usually made in panels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wainscot', 'n.', 'Any one of numerous species of European moths of the\n   family Leucanidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wainscoted', 'imp. & p. p.', 'of Wainscot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wainscoting', 'p. pr. & vb. n.', 'of Wainscot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wainscot', 'v. t.', 'To line with boards or panelwork, or as if with\n   panelwork; as, to wainscot a hall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wainscoting', 'n.', 'The act or occupation of covering or lining with\n   boards in panel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wainscoting', 'n.', 'The material used to wainscot a house, or the wainscot\n   as a whole; panelwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wainwright', 'n.', 'Same as Wagonwright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wair', 'n.', 'A piece of plank two yard/ long and a foot broad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waist', 'n.', 'That part of the human body which is immediately below the\n   ribs or thorax; the small part of the body between the thorax and hips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waist', 'n.', 'Hence, the middle part of other bodies; especially (Naut.),\n   that part of a vessels deck, bulwarks, etc., which is between the\n   quarter-deck and the forecastle; the middle part of the ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waist', 'n.', 'A garment, or part of a garment, which covers the body from\n   the neck or shoulders to the waist line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waist', 'n.', 'A girdle or belt for the waist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waistband', 'n.', 'The band which encompasses the waist; esp., one on the\n   upper part of breeches, trousers, pantaloons, skirts, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waistband', 'n.', 'A sash worn by women around the waist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waistcloth', 'n.', 'A cloth or wrapper worn about the waist; by extension,\n   such a garment worn about the hips and passing between the thighs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waistcloth', 'n.', 'A covering of canvas or tarpaulin for the hammocks,\n   stowed on the nettings, between the quarterdeck and the forecastle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waistcoat', 'n.', 'A short, sleeveless coat or garment for men, worn under\n   the coat, extending no lower than the hips, and covering the waist; a\n   vest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waistcoat', 'n.', 'A garment occasionally worn by women as a part of\n   fashionable costume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waistcoateer', 'n.', 'One wearing a waistcoat; esp., a woman wearing one\n   uncovered, or thought fit for such a habit; hence, a loose woman;\n   strumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waistcoating', 'n.', 'A fabric designed for waistcoats; esp., one in which\n   there is a pattern, differently colored yarns being used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waister', 'n.', 'A seaman, usually a green hand or a broken-down man,\n   stationed in the waist of a vessel of war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waited', 'imp. & p. p.', 'of Wait');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waiting', 'p. pr. & vb. n.', 'of Wait');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wait', 'v. i.', 'To watch; to observe; to take notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wait', 'v. i.', 'To stay or rest in expectation; to stop or remain\n   stationary till the arrival of some person or event; to rest in\n   patience; to stay; not to depart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wait', 'v. t.', 'To stay for; to rest or remain stationary in expectation\n   of; to await; as, to wait orders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wait', 'v. t.', 'To attend as a consequence; to follow upon; to accompany;\n   to await.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wait', 'v. t.', 'To attend on; to accompany; especially, to attend with\n   ceremony or respect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wait', 'v. t.', 'To cause to wait; to defer; to postpone; -- said of a\n   meal; as, to wait dinner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wait', 'v. i.', 'The act of waiting; a delay; a halt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wait', 'v. i.', 'Ambush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wait', 'v. i.', 'One who watches; a watchman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wait', 'v. i.', 'Hautboys, or oboes, played by town musicians; not used in\n   the singular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wait', 'v. i.', 'Musicians who sing or play at night or in the early\n   morning, especially at Christmas time; serenaders; musical watchmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waiter', 'n.', 'One who, or that which, waits; an attendant; a servant in\n   attendance, esp. at table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waiter', 'n.', 'A vessel or tray on which something is carried, as dishes,\n   etc.; a salver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waiting', '', 'a. & n. from Wait, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waitingly', 'adv.', 'By waiting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waitress', 'n.', 'A female waiter or attendant; a waiting maid or waiting\n   woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waive', 'v. t.', 'A waif; a castaway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waive', 'v. t.', 'A woman put out of the protection of the law. See Waive,\n   v. t., 3 (b), and the Note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waived', 'imp. & p. p.', 'of Waive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waiving', 'p. pr. & vb. n.', 'of Waive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waive', 'v. t.', 'To relinquish; to give up claim to; not to insist on or\n   claim; to refuse; to forego.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waive', 'v. t.', 'To throw away; to cast off; to reject; to desert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waive', 'v. t.', 'To throw away; to relinquish voluntarily, as a right\n   which one may enforce if he chooses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waive', 'v. t.', 'To desert; to abandon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waive', 'v. i.', 'To turn aside; to recede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waiver', 'n.', 'The act of waiving, or not insisting on, some right, claim,\n   or privilege.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waivure', 'n.', 'See Waiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waiwode', 'n.', 'See Waywode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wake', 'n.', 'The track left by a vessel in the water; by extension, any\n   track; as, the wake of an army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waked', 'imp. & p. p.', 'of Wake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woke', '', 'of Wake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waking', 'p. pr. & vb. n.', 'of Wake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wake', 'v. i.', 'To be or to continue awake; to watch; not to sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wake', 'v. i.', 'To sit up late festive purposes; to hold a night revel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wake', 'v. i.', 'To be excited or roused from sleep; to awake; to be\n   awakened; to cease to sleep; -- often with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wake', 'v. i.', 'To be exited or roused up; to be stirred up from a\n   dormant, torpid, or inactive state; to be active.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wake', 'v. t.', 'To rouse from sleep; to awake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wake', 'v. t.', 'To put in motion or action; to arouse; to excite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wake', 'v. t.', 'To bring to life again, as if from the sleep of death; to\n   reanimate; to revive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wake', 'v. t.', 'To watch, or sit up with, at night, as a dead body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wake', 'n.', 'The act of waking, or being awaked; also, the state of being\n   awake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wake', 'n.', 'The state of forbearing sleep, especially for solemn or\n   festive purposes; a vigil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wake', 'n.', 'An annual parish festival formerly held in commemoration of\n   the dedication of a church. Originally, prayers were said on the\n   evening preceding, and hymns were sung during the night, in the church;\n   subsequently, these vigils were discontinued, and the day itself, often\n   with succeeding days, was occupied in rural pastimes and exercises,\n   attended by eating and drinking, often to excess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wake', 'n.', 'The sitting up of persons with a dead body, often attended\n   with a degree of festivity, chiefly among the Irish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wakeful', 'a.', 'Not sleeping; indisposed to sleep; watchful; vigilant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wakened', 'imp. & p. pr.', 'of Waken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wakening', 'p. pr. & vb. n.', 'of Waken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waken', 'v. i.', 'To wake; to cease to sleep; to be awakened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waken', 'v. t.', 'To excite or rouse from sleep; to wake; to awake; to\n   awaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waken', 'v. t.', 'To excite; to rouse; to move to action; to awaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wakener', 'n.', 'One who wakens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wakening', 'n.', 'The act of one who wakens; esp., the act of ceasing to\n   sleep; an awakening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wakening', 'n.', 'The revival of an action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waker', 'n.', 'One who wakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wake-robin', 'n.', 'Any plant of the genus Arum, especially, in England,\n   the cuckoopint (Arum maculatum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waketime', 'n.', 'Time during which one is awake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waking', 'n.', 'The act of waking, or the state or period of being awake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waking', 'n.', 'A watch; a watching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walaway', 'interj.', 'See Welaway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wald', 'n.', 'A forest; -- used as a termination of names. See Weald.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waldenses', 'n. pl.', 'A sect of dissenters from the ecclesiastical system\n   of the Roman Catholic Church, who in the 13th century were driven by\n   persecution to the valleys of Piedmont, where the sect survives. They\n   profess substantially Protestant principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waldensian', 'a.', 'Of or pertaining to the Waldenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waldensian', 'n.', 'One Holding the Waldensian doctrines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waldgrave', 'n.', 'In the old German empire, the head forest keeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waldheimia', 'n.', 'A genus of brachiopods of which many species are found\n   in the fossil state. A few still exist in the deep sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wale', 'n.', 'A streak or mark made on the skin by a rod or whip; a stripe;\n   a wheal. See Wheal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wale', 'n.', 'A ridge or streak rising above the surface, as of cloth;\n   hence, the texture of cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wale', 'n.', 'A timber bolted to a row of piles to secure them together and\n   in position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wale', 'n.', 'Certain sets or strakes of the outside planking of a vessel;\n   as, the main wales, or the strakes of planking under the port sills of\n   the gun deck; channel wales, or those along the spar deck, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wale', 'n.', 'A wale knot, or wall knot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wale', 'v. t.', 'To mark with wales, or stripes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wale', 'v. t.', 'To choose; to select; specifically (Mining), to pick out\n   the refuse of (coal) by hand, in order to clean it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walhalla', 'n.', 'See Valhalla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waling', 'n.', 'Same as Wale, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walked', 'imp. & p. p.', 'of Walk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walking', 'p. pr. & vb. n.', 'of Walk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walk', 'v. i.', 'To move along on foot; to advance by steps; to go on at a\n   moderate pace; specifically, of two-legged creatures, to proceed at a\n   slower or faster rate, but without running, or lifting one foot\n   entirely before the other touches the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walk', 'v. i.', 'To move or go on the feet for exercise or amusement; to\n   take ones exercise; to ramble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walk', 'v. i.', 'To be stirring; to be abroad; to go restlessly about; --\n   said of things or persons expected to remain quiet, as a sleeping\n   person, or the spirit of a dead person; to go about as a somnambulist\n   or a specter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walk', 'v. i.', 'To be in motion; to act; to move; to wag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walk', 'v. i.', 'To behave; to pursue a course of life; to conduct ones\n   self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walk', 'v. i.', 'To move off; to depart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walk', 'v. t.', 'To pass through, over, or upon; to traverse; to\n   perambulate; as, to walk the streets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walk', 'v. t.', 'To cause to walk; to lead, drive, or ride with a slow\n   pace; as to walk ones horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walk', 'v. t.', 'To subject, as cloth or yarn, to the fulling process; to\n   full.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walk', 'n.', 'The act of walking, or moving on the feet with a slow pace;\n   advance without running or leaping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walk', 'n.', 'The act of walking for recreation or exercise; as, a morning\n   walk; an evening walk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walk', 'n.', 'Manner of walking; gait; step; as, we often know a person at\n   a distance by his walk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walk', 'n.', 'That in or through which one walks; place or distance walked\n   over; a place for walking; a path or avenue prepared for foot\n   passengers, or for taking air and exercise; way; road; hence, a place\n   or region in which animals may graze; place of wandering; range; as, a\n   sheep walk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walk', 'n.', 'A frequented track; habitual place of action; sphere; as, the\n   walk of the historian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walk', 'n.', 'Conduct; course of action; behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walk', 'n.', 'The route or district regularly served by a vender; as, a\n   milkmans walk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walkable', 'a.', 'Fit to be walked on; capable of being walked on or over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walker', 'n.', 'One who walks; a pedestrian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walker', 'n.', 'That with which one walks; a foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walker', 'n.', 'A forest officer appointed to walk over a certain space for\n   inspection; a forester.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walker', 'v. t.', 'A fuller of cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walker', 'v. t.', 'Any ambulatorial orthopterous insect, as a stick insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walking', '', 'a. & n. from Walk, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walk-mill', 'n.', 'A fulling mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walk-over', 'n.', 'In racing, the going over a course by a horse which has\n   no competitor for the prize; hence, colloquially, a one-sided contest;\n   an uncontested, or an easy, victory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walkyr', 'n.', 'See Valkyria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wall', 'n.', 'A kind of knot often used at the end of a rope; a wall knot;\n   a wale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wall', 'n.', 'A work or structure of stone, brick, or other materials,\n   raised to some height, and intended for defense or security, solid and\n   permanent inclosing fence, as around a field, a park, a town, etc.,\n   also, one of the upright inclosing parts of a building or a room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wall', 'n.', 'A defense; a rampart; a means of protection; in the plural,\n   fortifications, in general; works for defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wall', 'n.', 'An inclosing part of a receptacle or vessel; as, the walls of\n   a steam-engine cylinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wall', 'n.', 'The side of a level or drift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wall', 'n.', 'The country rock bounding a vein laterally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walled', 'imp. & p. p.', 'of Wall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walling', 'p. pr. & vb. n.', 'of Wall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wall', 'v. t.', 'To inclose with a wall, or as with a wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wall', 'v. t.', 'To defend by walls, or as if by walls; to fortify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wall', 'v. t.', 'To close or fill with a wall, as a doorway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallaba', 'n.', 'A leguminous tree (Eperua falcata) of Demerara, with\n   pinnate leaves and clusters of red flowers. The reddish brown wood is\n   used for palings and shingles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallabies', 'pl. ', 'of Wallaby');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallaby', 'n.', 'Any one of numerous species of kangaroos belonging to the\n   genus Halmaturus, native of Australia and Tasmania, especially the\n   smaller species, as the brush kangaroo (H. Bennettii) and the pademelon\n   (H. thetidis). The wallabies chiefly inhabit the wooded district and\n   bushy plains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallah', 'n.', 'A black variety of the jaguar; -- called also tapir tiger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallaroo', 'n.', 'Any one of several species of kangaroos of the genus\n   Macropus, especially M. robustus, sometimes called the great wallaroo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallbird', 'n.', 'The spotted flycatcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waller', 'n.', 'One who builds walls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waller', 'n.', 'The wels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallerian degeneration', '', 'A form of degeneration occurring in nerve\n   fibers as a result of their division; -- so called from Dr. Waller, who\n   published an account of it in 1850.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallet', 'n.', 'A bag or sack for carrying about the person, as a bag for\n   carrying the necessaries for a journey; a knapsack; a beggars\n   receptacle for charity; a peddlers pack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallet', 'n.', 'A pocketbook for keeping money about the person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallet', 'n.', 'Anything protuberant and swagging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walleteer', 'n.', 'One who carries a wallet; a foot traveler; a tramping\n   beggar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wall-eye', 'n.', 'An eye in which the iris is of a very light gray or\n   whitish color; -- said usually of horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wall-eye', 'n.', 'An American fresh-water food fish (Stizostedion vitreum)\n   having large and prominent eyes; -- called also glasseye, pike perch,\n   yellow pike, and wall-eyed perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wall-eye', 'n.', 'A California surf fish (Holconotus argenteus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wall-eye', 'n.', 'The alewife; -- called also wall-eyed herring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wall-eyed', 'a.', 'Having an eye of a very light gray or whitish color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallflower', 'n.', 'A perennial, cruciferous plant (Cheiranthus Cheiri),\n   with sweet-scented flowers varying in color from yellow to orange and\n   deep red. In Europe it very common on old walls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallflower', 'n.', 'A lady at a ball, who, either from choice, or because\n   not asked to dance, remains a spectator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallhick', 'n.', 'The lesser spotted woodpecker (Dryobates minor).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walling', 'n.', 'The act of making a wall or walls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walling', 'n.', 'Walls, in general; material for walls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walloons', 'n. pl.', 'A Romanic people inhabiting that part of Belgium\n   which comprises the provinces of Hainaut, Namur, Liege, and Luxembourg,\n   and about one third of Brabant; also, the language spoken by this\n   people. Used also adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallop', 'v. i.', 'To move quickly, but with great effort; to gallop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallop', 'n.', 'A quick, rolling movement; a gallop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walloped', 'imp. & p. p.', 'of Wallop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walloping', 'p. pr. & vb. n.', 'of Wallop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallop', 'v. i.', 'To boil with a continued bubbling or heaving and\n   rolling, with noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallop', 'v. i.', 'To move in a rolling, cumbersome manner; to waddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallop', 'v. i.', 'To be slatternly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallop', 'v. t.', 'To beat soundly; to flog; to whip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallop', 'v. t.', 'To wrap up temporarily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallop', 'v. t.', 'To throw or tumble over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallop', 'n.', 'A thick piece of fat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallop', 'n.', 'A blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallowed', 'imp. & p. p.', 'of Wallow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallowing', 'p. pr. & vb. n.', 'of Wallow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallow', 'n.', 'To roll ones self about, as in mire; to tumble and roll\n   about; to move lazily or heavily in any medium; to flounder; as, swine\n   wallow in the mire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallow', 'n.', 'To live in filth or gross vice; to disport ones self in a\n   beastly and unworthy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallow', 'n.', 'To wither; to fade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallow', 'v. t.', 'To roll; esp., to roll in anything defiling or unclean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallow', 'n.', 'A kind of rolling walk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallower', 'n.', 'One who, or that which, wallows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallower', 'n.', 'A lantern wheel; a trundle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallowish', 'a.', 'Flat; insipid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wall-plat', 'n.', 'The spotted flycatcher. It builds its nest on walls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wall-sided', 'a.', 'Having sides nearly perpendicular; -- said of certain\n   vessels to distinguish them from those having flaring sides, or sides\n   tumbling home (see under Tumble, v. i.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wallwort', 'n.', 'The dwarf elder, or danewort (Sambucus Ebulus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walm', 'v. i.', 'To roll; to spout; to boil up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walnut', 'n.', 'The fruit or nut of any tree of the genus Juglans; also,\n   the tree, and its timber. The seven or eight known species are all\n   natives of the north temperate zone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walrus', 'n.', 'A very large marine mammal (Trichecus rosmarus) of the Seal\n   family, native of the Arctic Ocean. The male has long and powerful\n   tusks descending from the upper jaw. It uses these in procuring food\n   and in fighting. It is hunted for its oil, ivory, and skin. It feeds\n   largely on mollusks. Called also morse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walter', 'v. i.', 'To roll or wallow; to welter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waltron', 'n.', 'A walrus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walty', 'a.', 'Liable to roll over; crank; as, a walty ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waltz', 'n.', 'A dance performed by two persons in circular figures with a\n   whirling motion; also, a piece of music composed in triple measure for\n   this kind of dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waltzed', 'imp. & p. p.', 'of Waltz');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waltzing', 'p. pr. & vb. n.', 'of Waltz');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waltz', 'v. i.', 'To dance a waltz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waltzer', 'n.', 'A person who waltzes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Walwe', 'v.', 'To wallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waly', 'interj.', 'An exclamation of grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wamble', 'v. i.', 'To heave; to be disturbed by nausea; -- said of the\n   stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wamble', 'v. i.', 'To move irregularly to and fro; to roll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wamble', 'n.', 'Disturbance of the stomach; a feeling of nausea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wamble-cropped', 'a.', 'Sick at the stomach; also, crestfallen; dejected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wammel', 'v. i.', 'To move irregularly or awkwardly; to wamble, or wabble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wamp', 'n.', 'The common American eider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wampee', 'n.', 'A tree (Cookia punctata) of the Orange family, growing in\n   China and the East Indies; also, its fruit, which is about the size of\n   a large grape, and has a hard rind and a peculiar flavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wampee', 'n.', 'The pickerel weed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wampum', 'n.', 'Beads made of shells, used by the North American Indians as\n   money, and also wrought into belts, etc., as an ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wan', 'imp.', 'Won.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wan', 'a.', 'Having a pale or sickly hue; languid of look; pale; pallid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wan', 'n.', 'The quality of being wan; wanness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wan', 'v. i.', 'To grow wan; to become pale or sickly in looks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wand', 'n.', 'A small stick; a rod; a verge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wand', 'n.', 'A staff of authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wand', 'n.', 'A rod used by conjurers, diviners, magicians, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wandered', 'imp. & p. p.', 'of Wander');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wandering', 'p. pr. & vb. n.', 'of Wander');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wander', 'v. i.', 'To ramble here and there without any certain course or\n   with no definite object in view; to range about; to stroll; to rove;\n   as, to wander over the fields.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wander', 'v. i.', 'To go away; to depart; to stray off; to deviate; to go\n   astray; as, a writer wanders from his subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wander', 'v. i.', 'To be delirious; not to be under the guidance of reason;\n   to rave; as, the mind wanders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wander', 'v. t.', 'To travel over without a certain course; to traverse; to\n   stroll through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanderer', 'n.', 'One who wanders; a rambler; one who roves; hence, one who\n   deviates from duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wandering', '', 'a. & n. from Wander, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanderingly', 'adv.', 'In a wandering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanderment', 'n.', 'The act of wandering, or roaming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanderoo', 'n.', 'A large monkey (Macacus silenus) native of Malabar. It is\n   black, or nearly so, but has a long white or gray beard encircling the\n   face. Called also maha, silenus, neelbhunder, lion-tailed baboon, and\n   great wanderoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wandy', 'a.', 'Long and flexible, like a wand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waned', 'imp. & p. p.', 'of Wane');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waning', 'p. pr. & vb. n.', 'of Wane');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wane', 'v. i.', 'To be diminished; to decrease; -- contrasted with wax, and\n   especially applied to the illuminated part of the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wane', 'v. i.', 'To decline; to fail; to sink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wane', 'v. t.', 'To cause to decrease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wane', 'n.', 'The decrease of the illuminated part of the moon to the eye\n   of a spectator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wane', 'n.', 'Decline; failure; diminution; decrease; declension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wane', 'n.', 'An inequality in a board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waney', 'n.', 'A sharp or uneven edge on a board that is cut from a log not\n   perfectly squared, or that is made in the process of squaring. See\n   Wany, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wang', 'n.', 'The jaw, jawbone, or cheek bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wang', 'n.', 'A slap; a blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wang', 'n.', 'See Whang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wangan', 'n.', 'A boat for conveying provisions, tools, etc.; -- so called\n   by Maine lumbermen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanger', 'n.', 'A pillow for the cheek; a pillow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanghee', 'n.', 'The Chinese name of one or two species of bamboo, or\n   jointed cane, of the genus Phyllostachys. The slender stems are much\n   used for walking sticks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wango', 'n.', 'A boomerang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanhope', 'n.', 'Want of hope; despair; also, faint or delusive hope;\n   delusion. [Obs.] Piers Plowman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanhorn', 'n.', 'An East Indian plant (Kaempferia Galanga) of the Ginger\n   family. See Galanga.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waniand', 'n.', 'The wane of the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waning', 'n.', 'The act or process of waning, or decreasing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanion', 'n.', 'A word of uncertain signification, used only in the phrase\n   with a wanion, apparently equivalent to with a vengeance, with a\n   plague, or with misfortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wankle', 'a.', 'Not to be depended on; weak; unstable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanly', 'adv.', 'In a wan, or pale, manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanned', 'a.', 'Made wan, or pale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanness', 'n.', 'The quality or state of being wan; a sallow, dead, pale\n   color; paleness; pallor; as, the wanness of the cheeks after a fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wannish', 'a.', 'Somewhat wan; of a pale hue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Want', 'v. i.', 'The state of not having; the condition of being without\n   anything; absence or scarcity of what is needed or desired; deficiency;\n   lack; as, a want of power or knowledge for any purpose; want of food\n   and clothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Want', 'v. i.', 'Specifically, absence or lack of necessaries; destitution;\n   poverty; penury; indigence; need.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Want', 'v. i.', 'That which is needed or desired; a thing of which the loss\n   is felt; what is not possessed, and is necessary for use or pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Want', 'v. i.', 'A depression in coal strata, hollowed out before the\n   subsequent deposition took place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanted', 'imp. & p. p.', 'of Want');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanting', 'p. pr. & vb. n.', 'of Want');
  INSERT INTO words (word, word_type, description) VALUES  (    'Want', 'v. t.', 'To be without; to be destitute of, or deficient in; not to\n   have; to lack; as, to want knowledge; to want judgment; to want\n   learning; to want food and clothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Want', 'v. t.', 'To have occasion for, as useful, proper, or requisite; to\n   require; to need; as, in winter we want a fire; in summer we want\n   cooling breezes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Want', 'v. t.', 'To feel need of; to wish or long for; to desire; to crave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Want', 'v. i.', 'To be absent; to be deficient or lacking; to fail; not to\n   be sufficient; to fall or come short; to lack; -- often used\n   impersonally with of; as, it wants ten minutes of four.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Want', 'v. i.', 'To be in a state of destitution; to be needy; to lack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Want', '', 'A colloquial contraction of was not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wantage', 'n.', 'That which is wanting; deficiency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanting', 'a.', 'Absent; lacking; missing; also, deficient; destitute;\n   needy; as, one of the twelve is wanting; I shall not be wanting in\n   exertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wantless', 'a.', 'Having no want; abundant; fruitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanton', 'v. t.', 'Untrained; undisciplined; unrestrained; hence, loose;\n   free; luxuriant; roving; sportive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanton', 'v. t.', 'Wandering from moral rectitude; perverse; dissolute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanton', 'v. t.', 'Specifically: Deviating from the rules of chastity;\n   lewd; lustful; lascivious; libidinous; lecherous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanton', 'v. t.', 'Reckless; heedless; as, wanton mischief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanton', 'n.', 'A roving, frolicsome thing; a trifler; -- used rarely as a\n   term of endearment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanton', 'n.', 'One brought up without restraint; a pampered pet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanton', 'n.', 'A lewd person; a lascivious man or woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wantoned', 'imp. & p. p.', 'of Wanton');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wantoning', 'p. pr. & vb. n.', 'of Wanton');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanton', 'v. i.', 'To rove and ramble without restraint, rule, or limit; to\n   revel; to play loosely; to frolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanton', 'v. i.', 'To sport in lewdness; to play the wanton; to play\n   lasciviously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanton', 'v. t.', 'To cause to become wanton; also, to waste in wantonness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wantonize', 'v. i.', 'To behave wantonly; to frolic; to wanton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wantonly', 'adv.', 'In a wanton manner; without regularity or restraint;\n   loosely; sportively; gayly; playfully; recklessly; lasciviously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wantonly', 'adv.', 'Unintentionally; accidentally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wantonness', 'n.', 'The quality or state of being wanton; negligence of\n   restraint; sportiveness; recklessness; lasciviousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wantrust', 'n.', 'Failing or diminishing trust; want of trust or\n   confidence; distrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wantwit', 'n.', 'One destitute of wit or sense; a blockhead; a fool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanty', 'n.', 'A surcingle, or strap of leather, used for binding a load\n   upon the back of a beast; also, a leather tie; a short wagon rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wany', 'v. i.', 'To wane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wany', 'a.', 'Waning or diminished in some parts; not of uniform size\n   throughout; -- said especially of sawed boards or timber when tapering\n   or uneven, from being cut too near the outside of the log.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wany', 'a.', 'Spoiled by wet; -- said of timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wanze', 'v. i.', 'To wane; to wither.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wap', 'v. t. & i.', 'To beat; to whap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wap', 'n.', 'A blow or beating; a whap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wapacut', 'n.', 'The American hawk owl. See under Hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wapatoo', 'n.', 'The edible tuber of a species of arrowhead (Sagittaria\n   variabilis); -- so called by the Indians of Oregon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Waped', 'a.', 'Cast down; crushed by misery; dejected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wapentake', 'n.', 'In some northern counties of England, a division, or\n   district, answering to the hundred in other counties. Yorkshire,\n   Lincolnshire, and Nottinghamshire are divided into wapentakes, instead\n   of hundreds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wapinschaw', 'n.', 'An exhibition of arms. according to the rank of the\n   individual, by all persons bearing arms; -- formerly made at certain\n   seasons in each district.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wapiti', 'n.', 'The American elk (Cervus Canadensis). It is closely related\n   to the European red deer, which it somewhat exceeds in size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wapp', 'n.', 'A fair-leader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wapp', 'n.', 'A rope with wall knots in it with which the shrouds are set\n   taut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wappato', 'n.', 'See Wapatoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wappened', 'a.', 'A word of doubtful meaning used once by Shakespeare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wapper', 'v. t. & i.', 'To cause to shake; to tremble; to move tremulously,\n   as from weakness; to totter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wapper', 'n.', 'A gudgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wappet', 'n.', 'A small yelping cur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wapping', 'n.', 'Yelping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'War', 'a.', 'Ware; aware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'War', 'n.', 'A contest between nations or states, carried on by force,\n   whether for defence, for revenging insults and redressing wrongs, for\n   the extension of commerce, for the acquisition of territory, for\n   obtaining and establishing the superiority and dominion of one over the\n   other, or for any other purpose; armed conflict of sovereign powers;\n   declared and open hostilities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'War', 'n.', 'A condition of belligerency to be maintained by physical\n   force. In this sense, levying war against the sovereign authority is\n   treason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'War', 'n.', 'Instruments of war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'War', 'n.', 'Forces; army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'War', 'n.', 'The profession of arms; the art of war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'War', 'n.', 'a state of opposition or contest; an act of opposition; an\n   inimical contest, act, or action; enmity; hostility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warred', 'imp. & p. p.', 'of War');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warring', 'p. pr. & vb. n.', 'of War');
  INSERT INTO words (word, word_type, description) VALUES  (    'War', 'v. i.', 'To make war; to invade or attack a state or nation with\n   force of arms; to carry on hostilities; to be in a state by violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'War', 'v. i.', 'To contend; to strive violently; to fight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'War', 'v. t.', 'To make war upon; to fight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'War', 'v. t.', 'To carry on, as a contest; to wage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'War-beaten', 'a.', 'Warworn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warble', 'n.', 'A small, hard tumor which is produced on the back of a\n   horse by the heat or pressure of the saddle in traveling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warble', 'n.', 'A small tumor produced by the larvae of the gadfly in the\n   backs of horses, cattle, etc. Called also warblet, warbeetle, warnles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warble', 'n.', 'See Wormil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warbled', 'imp. & p. p.', 'of Warble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warbling', 'p. pr. & vb. n.', 'of Warble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warble', 'v. t.', 'To sing in a trilling, quavering, or vibratory manner;\n   to modulate with turns or variations; to trill; as, certain birds are\n   remarkable for warbling their songs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warble', 'v. t.', 'To utter musically; to modulate; to carol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warble', 'v. t.', 'To cause to quaver or vibrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warble', 'v. i.', 'To be quavered or modulated; to be uttered melodiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warble', 'v. i.', 'To sing in a trilling manner, or with many turns and\n   variations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warble', 'v. i.', 'To sing with sudden changes from chest to head tones; to\n   yodel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warble', 'n.', 'A quavering modulation of the voice; a musical trill; a\n   song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warbler', 'n.', 'One who, or that which, warbles; a singer; a songster; --\n   applied chiefly to birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warbler', 'n.', 'Any one of numerous species of small Old World singing\n   birds belonging to the family Sylviidae, many of which are noted\n   songsters. The bluethroat, blackcap, reed warbler (see under Reed), and\n   sedge warbler (see under Sedge) are well-known species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warbler', 'n.', 'Any one of numerous species of small, often bright\n   colored, American singing birds of the family or subfamily\n   Mniotiltidae, or Sylvicolinae. They are allied to the Old World\n   warblers, but most of them are not particularly musical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warblingly', 'adv.', 'In a warbling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warburgs tincture', '', 'A preparation containing quinine and many other\n   ingredients, often used in the treatment of malarial affections. It was\n   invented by Dr. Warburg of London.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ward', 'v. i.', 'Alt. of -wards');
  INSERT INTO words (word, word_type, description) VALUES  (    '-wards', 'v. i.', 'Suffixes denoting course or direction to; motion or\n   tendency toward; as in backward, or backwards; toward, or towards, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ward', 'a.', 'The act of guarding; watch; guard; guardianship;\n   specifically, a guarding during the day. See the Note under Watch, n.,\n   1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ward', 'n.', 'One who, or that which, guards; garrison; defender;\n   protector; means of guarding; defense; protection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ward', 'n.', 'The state of being under guard or guardianship; confinement\n   under guard; the condition of a child under a guardian; custody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ward', 'n.', 'A guarding or defensive motion or position, as in fencing;\n   guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ward', 'n.', 'One who, or that which, is guarded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ward', 'n.', 'A minor or person under the care of a guardian; as, a ward in\n   chancery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ward', 'n.', 'A division of a county.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ward', 'n.', 'A division, district, or quarter of a town or city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ward', 'n.', 'A division of a forest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ward', 'n.', 'A division of a hospital; as, a fever ward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ward', 'n.', 'A projecting ridge of metal in the interior of a lock, to\n   prevent the use of any key which has not a corresponding notch for\n   passing it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ward', 'n.', 'A notch or slit in a key corresponding to a ridge in the lock\n   which it fits; a ward notch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warded', 'imp. & p. p.', 'of Ward');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warding', 'p. pr. & vb. n.', 'of Ward');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ward', 'n.', 'To keep in safety; to watch; to guard; formerly, in a\n   specific sense, to guard during the day time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ward', 'n.', 'To defend; to protect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ward', 'n.', 'To defend by walls, fortifications, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ward', 'n.', 'To fend off; to repel; to turn aside, as anything mischievous\n   that approaches; -- usually followed by off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ward', 'v. i.', 'To be vigilant; to keep guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ward', 'v. i.', 'To act on the defensive with a weapon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ward-corn', 'n.', 'The duty of keeping watch and ward (see the Note under\n   Watch, n., 1) with a horn to be blown upon any occasion of surprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wardcorps', 'n.', 'Guardian; one set to watch over another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warden', 'n.', 'A keeper; a guardian; a watchman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warden', 'n.', 'An officer who keeps or guards; a keeper; as, the warden of\n   a prison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warden', 'n.', 'A head official; as, the warden of a college; specifically\n   (Eccl.), a churchwarden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warden', 'n.', 'A large, hard pear, chiefly used for baking and roasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wardenry', 'n.', 'Alt. of Wardenship');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wardenship', 'n.', 'The office or jurisdiction of a warden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warder', 'n.', 'One who wards or keeps; a keeper; a guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warder', 'n.', 'A truncheon or staff carried by a king or a commander in\n   chief, and used in signaling his will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wardian', 'a.', 'Designating, or pertaining to, a kind of glass inclosure\n   for keeping ferns, mosses, etc., or for transporting growing plants\n   from a distance; as, a Wardian case of plants; -- so named from the\n   inventor, Nathaniel B. Ward, an Englishman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wardmote', 'n.', 'Anciently, a meeting of the inhabitants of a ward; also,\n   a court formerly held in each ward of London for trying defaults in\n   matters relating to the watch, police, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wardrobe', 'v. t.', 'A room or apartment where clothes are kept, or wearing\n   apparel is stored; a portable closet for hanging up clothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wardrobe', 'v. t.', 'Wearing apparel, in general; articles of dress or\n   personal decoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wardrobe', 'v. t.', 'A privy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wardroom', 'n.', 'A room occupied as a messroom by the commissioned\n   officers of a war vessel. See Gunroom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wardroom', 'n.', 'A room used by the citizens of a city ward, for meetings,\n   political caucuses, elections, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-wards', '', 'See -ward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wardship', 'n.', 'The office of a ward or keeper; care and protection of a\n   ward; guardianship; right of guardianship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wardship', 'n.', 'The state of begin under a guardian; pupilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wardsmen', 'pl. ', 'of Wardsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wardsman', 'n.', 'A man who keeps ward; a guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ware', 'imp.', 'Wore.');
