  INSERT INTO words (word, word_type, description) VALUES  (    'Ware', 'n.', 'Seaweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ware', 'a.', 'Articles of merchandise; the sum of articles of a particular\n   kind or class; style or class of manufactures; especially, in the\n   plural, goods; commodities; merchandise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ware', 'a.', 'A ware; taking notice; hence, wary; cautious; on ones guard.\n   See Beware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ware', 'n.', 'The state of being ware or aware; heed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ware', 'v. t.', 'To make ware; to warn; to take heed of; to beware of; to\n   guard against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wareful', 'a.', 'Wary; watchful; cautious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warefulness', 'n.', 'Wariness; cautiousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warega fly', '', 'A Brazilian fly whose larvae live in the skin of man and\n   animals, producing painful sores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warehouses', 'pl. ', 'of Warehouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warehouse', 'n.', 'A storehouse for wares, or goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warehoused', 'imp. & p. p.', 'of Warehouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warehousing', 'p. pr. & vb. n.', 'of Warehouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warehouse', 'v. t.', 'To deposit or secure in a warehouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warehouse', 'v. t.', 'To place in the warehouse of the government or\n   customhouse stores, to be kept until duties are paid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warehousemen', 'pl. ', 'of Warehouseman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warehouseman', 'n.', 'One who keeps a warehouse; the owner or keeper of a\n   dock warehouse or wharf store.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warehouseman', 'n.', 'One who keeps a wholesale shop or store for\n   Manchester or woolen goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warehousing', 'n.', 'The act of placing goods in a warehouse, or in a\n   customhouse store.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wareless', 'n.', 'Unwary; incautious; unheeding; careless; unaware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warely', 'adv.', 'Cautiously; warily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warence', 'n.', 'Madder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wareroom', 'n.', 'A room in which goods are stored or exhibited for sale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wares', 'n. pl.', 'See 4th Ware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warfare', 'n.', 'Military service; military life; contest carried on by\n   enemies; hostilities; war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warfare', 'n.', 'Contest; struggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warfare', 'v. i.', 'To lead a military life; to carry on continual wars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warfarer', 'n.', 'One engaged in warfare; a military man; a soldier; a\n   warrior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warhable', 'a.', 'Fit for war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wariangle', 'n.', 'The red-backed shrike (Lanius collurio); -- called also\n   wurger, worrier, and throttler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warily', 'adv.', 'In a wary manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wariment', 'n.', 'Wariness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warine', 'n.', 'A South American monkey, one of the sapajous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wariness', 'n.', 'The quality or state of being wary; care to foresee and\n   guard against evil; cautiousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warish', 'v. t.', 'To protect from the effects of; hence, to cure; to heal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warish', 'v. i.', 'To be cured; to recover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warison', 'v. t.', 'Preparation; protection; provision; supply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warison', 'v. t.', 'Reward; requital; guerdon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wark', 'n.', 'Work; a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warkloom', 'n.', 'A tool; an implement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warlike', 'a.', 'Fit for war; disposed for war; as, a warlike state; a\n   warlike disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warlike', 'a.', 'Belonging or relating to war; military; martial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warlikeness', 'n.', 'Quality of being warlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warling', 'n.', 'One often quarreled with; -- / word coined, perhaps, to\n   rhyme with darling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warlock', 'n.', 'A male witch; a wizard; a sprite; an imp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warlock', 'a.', 'Of or pertaining to a warlock or warlock; impish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warlockry', 'n.', 'Impishness; magic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warly', 'a.', 'Warlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warm', 'superl.', 'Having heat in a moderate degree; not cold as, warm\n   milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warm', 'superl.', 'Having a sensation of heat, esp. of gentle heat;\n   glowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warm', 'superl.', 'Subject to heat; having prevalence of heat, or little or\n   no cold weather; as, the warm climate of Egypt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warm', 'superl.', 'Fig.: Not cool, indifferent, lukewarm, or the like, in\n   spirit or temper; zealous; ardent; fervent; excited; sprightly;\n   irritable; excitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warm', 'superl.', 'Violent; vehement; furious; excited; passionate; as, a\n   warm contest; a warm debate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warm', 'superl.', 'Being well off as to property, or in good circumstances;\n   forehanded; rich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warm', 'superl.', 'In childrens games, being near the object sought for;\n   hence, being close to the discovery of some person, thing, or fact\n   concealed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warm', 'superl.', 'Having yellow or red for a basis, or in their\n   composition; -- said of colors, and opposed to cold which is of blue\n   and its compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warmed', 'imp. & p. p.', 'of Warm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warming', 'p. pr. & vb. n.', 'of Warm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warm', 'a.', 'To communicate a moderate degree of heat to; to render warm;\n   to supply or furnish heat to; as, a stove warms an apartment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warm', 'a.', 'To make engaged or earnest; to interest; to engage; to excite\n   ardor or zeal; to enliven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warm', 'v. i.', 'To become warm, or moderately heated; as, the earth soon\n   warms in a clear day summer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warm', 'v. i.', 'To become ardent or animated; as, the speake/ warms as he\n   proceeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warm', 'n.', 'The act of warming, or the state of being warmed; a warming;\n   a heating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warm-blooded', 'a.', 'Having warm blood; -- applied especially to those\n   animals, as birds and mammals, which have warm blood, or, more\n   properly, the power of maintaining a nearly uniform temperature\n   whatever the temperature of the surrounding air. See Homoiothermal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warmer', 'n.', 'One who, or that which, warms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warmful', 'a.', 'Abounding in capacity to warm; giving warmth; as, a\n   warmful garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warm-hearted', 'a.', 'Having strong affection; cordial; sincere; hearty;\n   sympathetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warming', '', 'a. & n. from Warm, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warmly', 'adv.', 'In a warm manner; ardently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warmness', 'n.', 'Warmth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warmonger', 'n.', 'One who makes ar a trade or business; a mercenary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warmouth', 'n.', 'An American freshwater bream, or sunfish (Chaenobryttus\n   gulosus); -- called also red-eyed bream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warmth', 'n.', 'The quality or state of being warm; gentle heat; as, the\n   warmth of the sun; the warmth of the blood; vital warmth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warmth', 'n.', 'A state of lively and excited interest; zeal; ardor;\n   fervor; passion; enthusiasm; earnestness; as, the warmth of love or\n   piety; he replied with much warmth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warmth', 'n.', 'The glowing effect which arises from the use of warm\n   colors; hence, any similar appearance or effect in a painting, or work\n   of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warmthless', 'a.', 'Being without warmth; not communicating warmth; cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warn', 'v. t.', 'To refuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warned', 'imp. & p. p.', 'of Warn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warning', 'p. pr. & vb. n.', 'of Warn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warn', 'v. t.', 'To make ware or aware; to give previous information to; to\n   give notice to; to notify; to admonish; hence, to notify or summon by\n   authority; as, to warn a town meeting; to warn a tenant to quit a\n   house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warn', 'v. t.', 'To give notice to, of approaching or probable danger or\n   evil; to caution against anything that may prove injurious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warn', 'v. t.', 'To ward off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warner', 'n.', 'One who warns; an admonisher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warner', 'n.', 'A warrener.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warning', 'a.', 'Giving previous notice; cautioning; admonishing; as, a\n   warning voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warning', 'n.', 'Previous notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warning', 'n.', 'Caution against danger, or against faults or evil\n   practices which incur danger; admonition; monition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warningly', 'adv.', 'In a warning manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warnstore', 'v. t.', 'To furnish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warped', 'imp. & p. p.', 'of Warp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warping', 'p. pr. & vb. n.', 'of Warp');
