  INSERT INTO words (word, word_type, description) VALUES  (    'Warp', 'v. t.', 'To turn or twist out of shape; esp., to twist or bend out\n   of a flat plane by contraction or otherwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warp', 'v. t.', 'To turn aside from the true direction; to cause to bend or\n   incline; to pervert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warp', 'v. t.', 'To weave; to fabricate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warp', 'v. t.', 'To tow or move, as a vessel, with a line, or warp,\n   attached to a buoy, anchor, or other fixed object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warp', 'v. t.', 'To cast prematurely, as young; -- said of cattle, sheep,\n   etc.');
