  INSERT INTO words (word, word_type, description) VALUES  (    'Warp', 'v. i.', 'To turn, twist, or be twisted out of shape; esp., to be\n   twisted or bent out of a flat plane; as, a board warps in seasoning or\n   shrinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warp', 'v. i.', 'to turn or incline from a straight, true, or proper\n   course; to deviate; to swerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warp', 'v. i.', 'To fly with a bending or waving motion; to turn and wave,\n   like a flock of birds or insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warp', 'v. i.', 'To cast the young prematurely; to slink; -- said of\n   cattle, sheep, etc.');
