  INSERT INTO words (word, word_type, description) VALUES  (    'Warp', 'v.', 'The threads which are extended lengthwise in the loom, and\n   crossed by the woof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warp', 'v.', 'A rope used in hauling or moving a vessel, usually with one\n   end attached to an anchor, a post, or other fixed object; a towing\n   line; a warping hawser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warp', 'v.', 'A slimy substance deposited on land by tides, etc., by which\n   a rich alluvial soil is formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warp', 'v.', 'A premature casting of young; -- said of cattle, sheep, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warp', 'v.', 'Four; esp., four herrings; a cast. See Cast, n., 17.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warp', 'v.', 'The state of being warped or twisted; as, the warp of a\n   board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Warpage', 'n.', 'The act of warping; also, a charge per ton made on\n   shipping in some harbors.');
