INSERT INTO words (word, word_type, description) VALUES  (    'Wearisome',    'a.',    'Causing weariness; tiresome; tedious; weariful; as, a\n   wearisome march; a wearisome days work; a wearisome book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weary', 'superl.', 'Having the strength exhausted by toil or exertion; worn\n   out in respect to strength, endurance, etc.; tired; fatigued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weary', 'superl.', 'Causing weariness; tiresome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weary', 'superl.', 'Having ones patience, relish, or contentment\n   exhausted; tired; sick; -- with of before the cause; as, weary of\n   marching, or of confinement; weary of study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wearied', 'imp. & p. p.', 'of Weary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wearying', 'p. pr. & vb. n.', 'of Weary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weary', 'v. t.', 'To reduce or exhaust the physical strength or endurance\n   of; to tire; to fatigue; as, to weary ones self with labor or\n   traveling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weary', 'v. t.', 'To make weary of anything; to exhaust the patience of, as\n   by continuance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weary', 'v. t.', 'To harass by anything irksome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weary', 'v. i.', 'To grow tired; to become exhausted or impatient; as, to\n   weary of an undertaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weasand', 'n.', 'The windpipe; -- called also, formerly, wesil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weasel', 'n.', 'Any one of various species of small carnivores belonging to\n   the genus Putorius, as the ermine and ferret. They have a slender,\n   elongated body, and are noted for the quickness of their movements and\n   for their bloodthirsty habit in destroying poultry, rats, etc. The\n   ermine and some other species are brown in summer, and turn white in\n   winter; others are brown at all seasons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weasel-faced', 'a.', 'Having a thin, sharp face, like a weasel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weaser', 'n.', 'The American merganser; -- called also weaser sheldrake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weasiness', 'n.', 'Quality or state of being weasy; full feeding; sensual\n   indulgence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weasy', 'a.', 'Given to sensual indulgence; gluttonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weather', 'n.', 'The state of the air or atmosphere with respect to heat or\n   cold, wetness or dryness, calm or storm, clearness or cloudiness, or\n   any other meteorological phenomena; meteorological condition of the\n   atmosphere; as, warm weather; cold weather; wet weather; dry weather,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weather', 'n.', 'Vicissitude of season; meteorological change; alternation\n   of the state of the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weather', 'n.', 'Storm; tempest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weather', 'n.', 'A light rain; a shower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weathered', 'imp. & p. p.', 'of Weather');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weathering', 'p. pr. & vb. n.', 'of Weather');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weather', 'v. t.', 'To expose to the air; to air; to season by exposure to\n   air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weather', 'v. t.', 'Hence, to sustain the trying effect of; to bear up\n   against and overcome; to sustain; to endure; to resist; as, to weather\n   the storm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weather', 'v. t.', 'To sail or pass to the windward of; as, to weather a\n   cape; to weather another ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weather', 'v. t.', 'To place (a hawk) unhooded in the open air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weather', 'v. i.', 'To undergo or endure the action of the atmosphere; to\n   suffer meteorological influences; sometimes, to wear away, or alter,\n   under atmospheric influences; to suffer waste by weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weather', 'a.', 'Being toward the wind, or windward -- opposed to lee; as,\n   weather bow, weather braces, weather gauge, weather lifts, weather\n   quarter, weather shrouds, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weather-beaten', 'a.', 'Beaten or harassed by the weather; worn by exposure\n   to the weather, especially to severe weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weather-bit', 'n.', 'A turn of the cable about the end of the windlass,\n   without the bits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weatherbit', 'v. t.', 'To take another turn with, as a cable around a\n   windlass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weather-bitten', 'a.', 'Eaten into, defaced, or worn, by exposure to the\n   weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weatherboard', 'n.', 'That side of a vessel which is toward the wind; the\n   windward side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weatherboard', 'n.', 'A piece of plank placed in a porthole, or other\n   opening, to keep out water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weatherboard', 'n.', 'A board extending from the ridge to the eaves along\n   the slope of the gable, and forming a close junction between the\n   shingling of a roof and the side of the building beneath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weatherboard', 'n.', 'A clapboard or feather-edged board used in\n   weatherboarding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weather-board', 'v. t.', 'To nail boards upon so as to lap one over\n   another, in order to exclude rain, snow, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weatherboarding', 'n.', 'The covering or siding of a building, formed of\n   boards lapping over one another, to exclude rain, snow, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weatherboarding', 'n.', 'Boards adapted or intended for such use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weather-bound', 'a.', 'Kept in port or at anchor by storms; delayed by bad\n   weather; as, a weather-bound vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weathercock', 'n.', 'A vane, or weather vane; -- so called because\n   originally often in the figure of a cock, turning on the top of a spire\n   with the wind, and showing its direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weathercock', 'n.', 'Hence, any thing or person that turns easily and\n   frequently; one who veers with every change of current opinion; a\n   fickle, inconstant person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weathercock', 'v. t.', 'To supply with a weathercock; to serve as a\n   weathercock for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weather-driven', 'a.', 'Driven by winds or storms; forced by stress of\n   weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weathered', 'a.', 'Made sloping, so as to throw off water; as, a weathered\n   cornice or window sill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weathered', 'a.', 'Having the surface altered in color, texture, or\n   composition, or the edges rounded off by exposure to the elements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weather-fend', 'v. t.', 'To defend from the weather; to shelter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weatherglass', 'n.', 'An instrument to indicate the state of the\n   atmosphere, especially changes of atmospheric pressure, and hence\n   changes of weather, as a barometer or baroscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weathering', 'n.', 'The action of the elements on a rock in altering its\n   color, texture, or composition, or in rounding off its edges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weatherliness', 'n.', 'The quality of being weatherly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weatherly', 'a.', 'Working, or able to sail, close to the wind; as, a\n   weatherly ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weathermost', 'a.', 'Being farthest to the windward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weatherproof', 'a.', 'Proof against rough weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weatherwise', 'a.', 'Skillful in forecasting the changes of the weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weatherwiser', 'n.', 'Something that foreshows the weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weatherworn', 'a.', 'Worn by the action of, or by exposure to, the weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wove', 'imp.', 'of Weave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woven', 'p. p.', 'of Weave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wove', '', 'of Weave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weaving', 'p. pr. & vb. n.', 'of Weave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weaved', 'imp. & p. p.', 'of Weave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weave', 'v. t.', 'To unite, as threads of any kind, in such a manner as to\n   form a texture; to entwine or interlace into a fabric; as, to weave\n   wool, silk, etc.; hence, to unite by close connection or intermixture;\n   to unite intimately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weave', 'v. t.', 'To form, as cloth, by interlacing threads; to compose, as\n   a texture of any kind, by putting together textile materials; as, to\n   weave broadcloth; to weave a carpet; hence, to form into a fabric; to\n   compose; to fabricate; as, to weave the plot of a story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weave', 'v. i.', 'To practice weaving; to work with a loom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weave', 'v. i.', 'To become woven or interwoven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weave', 'n.', 'A particular method or pattern of weaving; as, the cassimere\n   weave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weaver', 'n.', 'One who weaves, or whose occupation is to weave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weaver', 'n.', 'A weaver bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weaver', 'n.', 'An aquatic beetle of the genus Gyrinus. See Whirling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weaverfish', 'n.', 'See Weever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weaving', 'n.', 'The act of one who, or that which, weaves; the act or art\n   of forming cloth in a loom by the union or intertexture of threads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weaving', 'n.', 'An incessant motion of a horses head, neck, and body,\n   from side to side, fancied to resemble the motion of a hand weaver in\n   throwing the shuttle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weazand', 'n.', 'See Weasand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weazen', 'a.', 'Thin; sharp; withered; wizened; as, a weazen face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weazeny', 'a.', 'Somewhat weazen; shriveled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'n.', 'A weaver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'n.', 'That which is woven; a texture; textile fabric; esp.,\n   something woven in a loom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'n.', 'A whole piece of linen cloth as woven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'n.', 'The texture of very fine thread spun by a spider for catching\n   insects at its prey; a cobweb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'n.', 'Fig.: Tissue; texture; complicated fabrication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'n.', 'A band of webbing used to regulate the extension of the hood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'n.', 'A thin metal sheet, plate, or strip, as of lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'n.', 'The blade of a sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'n.', 'The blade of a saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'n.', 'The thin, sharp part of a colter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'n.', 'The bit of a key.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'n.', 'A plate or thin portion, continuous or perforated, connecting\n   stiffening ribs or flanges, or other parts of an object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'n.', 'The thin vertical plate or portion connecting the upper and\n   lower flanges of an lower flanges of an iron girder, rolled beam, or\n   railroad rail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'n.', 'A disk or solid construction serving, instead of spokes, for\n   connecting the rim and hub, in some kinds of car wheels, sheaves, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'n.', 'The arm of a crank between the shaft and the wrist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'n.', 'The part of a blackmiths anvil between the face and the foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'n.', 'Pterygium; -- called also webeye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'n.', 'The membrane which unites the fingers or toes, either at their\n   bases, as in man, or for a greater part of their length, as in many\n   water birds and amphibians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'n.', 'The series of barbs implanted on each side of the shaft of a\n   feather, whether stiff and united together by barbules, as in ordinary\n   feathers, or soft and separate, as in downy feathers. See Feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Webbed', 'imp. & p. p.', 'of Web');
  INSERT INTO words (word, word_type, description) VALUES  (    'Webbing', 'p. pr. & vb. n.', 'of Web');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web', 'v. t.', 'To unite or surround with a web, or as if with a web; to\n   envelop; to entangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Webbed', 'a.', 'Provided with a web.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Webbed', 'a.', 'Having the toes united by a membrane, or web; as, the\n   webbed feet of aquatic fowls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Webber', 'n.', 'One who forms webs; a weaver; a webster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Webbing', 'n.', 'A woven band of cotton or flax, used for reins, girths,\n   bed bottoms, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Webby', 'a.', 'Of or pertaining to a web or webs; like a web; filled or\n   covered with webs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weber', 'n.', 'The standard unit of electrical quantity, and also of\n   current. See Coulomb, and Amp/re.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Webeye', 'n.', 'See Web, n., 8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web-fingered', 'a.', 'Having the fingers united by a web for a considerable\n   part of their length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Webfeet', 'pl. ', 'of Webfoot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Webfoot', 'n.', 'A foot the toes of which are connected by a membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Webfoot', 'n.', 'Any web-footed bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web-footed', 'a.', 'Having webbed feet; palmiped; as, a goose or a duck is\n   a web-footed fowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Webster', 'n.', 'A weaver; originally, a female weaver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Websterite', 'n.', 'A hydrous sulphate of alumina occurring in white\n   reniform masses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Web-toed', 'a.', 'Having the toes united by a web for a considerable part\n   of their length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Webform', 'n.', 'Any one of various species of moths whose gregarious\n   larvae eat the leaves of trees, and construct a large web to which they\n   retreat when not feeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wed', 'n.', 'A pledge; a pawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedded', 'imp.', 'of Wed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedded', 'p. p.', 'of Wed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wed', '', 'of Wed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedding', 'p. pr. & vb. n.', 'of Wed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wed', 'n.', 'To take for husband or for wife by a formal ceremony; to\n   marry; to espouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wed', 'n.', 'To join in marriage; to give in wedlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wed', 'n.', 'Fig.: To unite as if by the affections or the bond of\n   marriage; to attach firmly or indissolubly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wed', 'n.', 'To take to ones self and support; to espouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wed', 'v. i.', 'To contact matrimony; to marry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weddahs', 'n. pl.', 'See Veddahs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedded', 'a.', 'Joined in wedlock; married.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedded', 'a.', 'Of or pertaining to wedlock, or marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedder', 'n.', 'See Wether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedding', 'n.', 'Nuptial ceremony; nuptial festivities; marriage; nuptials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weder', 'n.', 'Weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedge', 'n.', 'A piece of metal, or other hard material, thick at one end,\n   and tapering to a thin edge at the other, used in splitting wood,\n   rocks, etc., in raising heavy bodies, and the like. It is one of the\n   six elementary machines called the mechanical powers. See Illust. of\n   Mechanical powers, under Mechanical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedge', 'n.', 'A solid of five sides, having a rectangular base, two\n   rectangular or trapezoidal sides meeting in an edge, and two triangular\n   ends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedge', 'n.', 'A mass of metal, especially when of a wedgelike form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedge', 'n.', 'Anything in the form of a wedge, as a body of troops drawn\n   up in such a form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedge', 'n.', 'The person whose name stands lowest on the list of the\n   classical tripos; -- so called after a person (Wedgewood) who occupied\n   this position on the first list of 1828.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedged', 'imp. & p. p.', 'of Wedge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedging', 'p. pr. & vb. n.', 'of Wedge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedge', 'v. t.', 'To cleave or separate with a wedge or wedges, or as with\n   a wedge; to rive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedge', 'v. t.', 'To force or drive as a wedge is driven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedge', 'v. t.', 'To force by crowding and pushing as a wedge does; as, to\n   wedge ones way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedge', 'v. t.', 'To press closely; to fix, or make fast, in the manner of\n   a wedge that is driven into something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedge', 'v. t.', 'To fasten with a wedge, or with wedges; as, to wedge a\n   scythe on the snath; to wedge a rail or a piece of timber in its place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedge', 'v. t.', 'To cut, as clay, into wedgelike masses, and work by\n   dashing together, in order to expel air bubbles, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedgebill', 'n.', 'An Australian crested insessorial bird (Sphenostoma\n   cristatum) having a wedge-shaped bill. Its color is dull brown, like\n   the earth of the plains where it lives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedge-formed', 'a.', 'Having the form of a wedge; cuneiform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedge-shaped', 'a.', 'Having the shape of a wedge; cuneiform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedge-shaped', 'a.', 'Broad and truncate at the summit, and tapering down\n   to the base; as, a wedge-shaped leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedge-shell', 'n.', 'Any one of numerous species of small marine bivalves\n   belonging to Donax and allied genera in which the shell is\n   wedge-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedge-tailed', 'a.', 'Having a tail which has the middle pair of feathers\n   longest, the rest successively and decidedly shorter, and all more or\n   less attenuate; -- said of certain birds. See Illust. of Wood hoopoe,\n   under Wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedgewise', 'adv.', 'In the manner of a wedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedgwood ware', '', 'A kind of fine pottery, the most remarkable being what\n   is called jasper, either white, or colored throughout the body, and\n   capable of being molded into the most delicate forms, so that fine and\n   minute bas-reliefs like cameos were made of it, fit even for being set\n   as jewels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedgy', 'a.', 'Like a wedge; wedge-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedlock', 'v. i.', 'The ceremony, or the state, of marriage; matrimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedlock', 'v. i.', 'A wife; a married woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wedlock', 'v. t.', 'To marry; to unite in marriage; to wed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wednesday', 'a.', 'The fourth day of the week; the next day after Tuesday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wee', 'n.', 'A little; a bit, as of space, time, or distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wee', 'a.', 'Very small; little.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weech-elm', 'n.', 'The wych-elm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weed', 'n.', 'A garment; clothing; especially, an upper or outer garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weed', 'n.', 'An article of dress worn in token of grief; a mourning\n   garment or badge; as, he wore a weed on his hat; especially, in the\n   plural, mourning garb, as of a woman; as, a widows weeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weed', 'n.', 'A sudden illness or relapse, often attended with fever, which\n   attacks women in childbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weed', 'n.', 'Underbrush; low shrubs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weed', 'n.', 'Any plant growing in cultivated ground to the injury of the\n   crop or desired vegetation, or to the disfigurement of the place; an\n   unsightly, useless, or injurious plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weed', 'n.', 'Fig.: Something unprofitable or troublesome; anything\n   useless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weed', 'n.', 'An animal unfit to breed from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weed', 'n.', 'Tobacco, or a cigar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weeded', 'imp. & p. p.', 'of Weed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weeding', 'p. pr. & vb. n.', 'of Weed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weed', 'v. t.', 'To free from noxious plants; to clear of weeds; as, to\n   weed corn or onions; to weed a garden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weed', 'v. t.', 'To take away, as noxious plants; to remove, as something\n   hurtful; to extirpate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weed', 'v. t.', 'To free from anything hurtful or offensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weed', 'v. t.', 'To reject as unfit for breeding purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weeder', 'n.', 'One who, or that which, weeds, or frees from anything\n   noxious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weedery', 'n.', 'Weeds, collectively; also, a place full of weeds or for\n   growing weeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weeding', '', 'a. & n. from Weed, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weeding-rhim', 'n.', 'A kind of implement used for tearing up weeds esp. on\n   summer fallows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weedless', 'a.', 'Free from weeds or noxious matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weedy', 'superl.', 'Of or pertaining to weeds; consisting of weeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weedy', 'superl.', 'Abounding with weeds; as, weedy grounds; a weedy\n   garden; weedy corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weedy', 'superl.', 'Scraggy; ill-shaped; ungainly; -- said of colts or\n   horses, and also of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weedy', 'a.', 'Dressed in weeds, or mourning garments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Week', 'n.', 'A period of seven days, usually that reckoned from one\n   Sabbath or Sunday to the next.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weekly', 'a.', 'Of or pertaining to a week, or week days; as, weekly labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weekly', 'a.', 'Coming, happening, or done once a week; hebdomadary; as, a\n   weekly payment; a weekly gazette.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weeklies', 'pl. ', 'of Weekly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weekly', 'n.', 'A publication issued once in seven days, or appearing once\n   a week.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weekly', 'adv.', 'Once a week; by hebdomadal periods; as, each performs\n   service weekly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weekwam', 'n.', 'See Wigwam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weel', 'a. & adv.', 'Well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weel', 'n.', 'A whirlpool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weel', '', 'Alt. of Weely');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weely', '', 'A kind of trap or snare for fish, made of twigs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ween', 'v. i.', 'To think; to imagine; to fancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weep', 'n.', 'The lapwing; the wipe; -- so called from its cry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weep', '', 'imp. of Weep, for wept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wept', 'imp. & p. p.', 'of Weep');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weeping', 'p. pr. & vb. n.', 'of Weep');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weep', 'v. i.', 'Formerly, to express sorrow, grief, or anguish, by outcry,\n   or by other manifest signs; in modern use, to show grief or other\n   passions by shedding tears; to shed tears; to cry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weep', 'v. i.', 'To lament; to complain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weep', 'v. i.', 'To flow in drops; to run in drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weep', 'v. i.', 'To drop water, or the like; to drip; to be soaked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weep', 'v. i.', 'To hang the branches, as if in sorrow; to be pendent; to\n   droop; -- said of a plant or its branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weep', 'v. t.', 'To lament; to bewail; to bemoan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weep', 'v. t.', 'To shed, or pour forth, as tears; to shed drop by drop, as\n   if tears; as, to weep tears of joy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weeper', 'n.', 'One who weeps; esp., one who sheds tears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weeper', 'n.', 'A white band or border worn on the sleeve as a badge of\n   mourning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weeper', 'n.', 'The capuchin. See Capuchin, 3 (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weepful', 'a.', 'Full of weeping or lamentation; grieving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weeping', 'n.', 'The act of one who weeps; lamentation with tears; shedding\n   of tears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weeping', 'a.', 'Grieving; lamenting; shedding tears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weeping', 'a.', 'Discharging water, or other liquid, in drops or very\n   slowly; surcharged with water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weeping', 'a.', 'Having slender, pendent branches; -- said of trees; as,\n   weeping willow; a weeping ash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weeping', 'a.', 'Pertaining to lamentation, or those who weep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weepingly', 'adv.', 'In a weeping manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weeping-ripe', 'a.', 'Ripe for weeping; ready to weep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weerish', 'a.', 'See Wearish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weesel', 'n.', 'See Weasel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weet', 'a. & n.', 'Wet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wot', 'imp.', 'of Weet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weet', 'v. i.', 'To know; to wit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weet-bird', 'n.', 'The wryneck; -- so called from its cry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weetingly', 'adv.', 'Knowingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weetless', 'a.', 'Unknowing; also, unknown; unmeaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weet-weet', 'n.', 'The common European sandpiper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weet-weet', 'n.', 'The chaffinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weever', 'n.', 'Any one of several species of edible marine fishes\n   belonging to the genus Trachinus, of the family Trachinidae. They have\n   a broad spinose head, with the eyes looking upward. The long dorsal fin\n   is supported by numerous strong, sharp spines which cause painful\n   wounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weevil', 'n.', 'Any one of numerous species of snout beetles, or\n   Rhynchophora, in which the head is elongated and usually curved\n   downward. Many of the species are very injurious to cultivated plants.\n   The larvae of some of the species live in nuts, fruit, and grain by\n   eating out the interior, as the plum weevil, or curculio, the nut\n   weevils, and the grain weevil (see under Plum, Nut, and Grain). The\n   larvae of other species bore under the bark and into the pith of trees\n   and various other plants, as the pine weevils (see under Pine). See\n   also Pea weevil, Rice weevil, Seed weevil, under Pea, Rice, and Seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weeviled', 'a.', 'Infested by weevils; as, weeviled grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weevily', 'a.', 'Having weevils; weeviled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weezel', 'n.', 'See Weasel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weft', '', 'imp. & p. p. of Wave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weft', 'n.', 'A thing waved, waived, or cast away; a waif.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weft', 'n.', 'The woof of cloth; the threads that cross the warp from\n   selvage to selvage; the thread carried by the shuttle in weaving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weft', 'n.', 'A web; a thing woven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weftage', 'n.', 'Texture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wegotism', 'n.', 'Excessive use of the pronoun we; -- called also weism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wehrgeld', 'n.', 'Alt. of Wehrgelt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wehrgelt', 'n.', 'See Weregild.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wehrwolf', 'n.', 'See Werewolf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weigela', 'n.', 'Alt. of Weigelia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weigelia', 'n.', 'A hardy garden shrub (Diervilla Japonica) belonging to\n   the Honeysuckle family, with white or red flowers. It was introduced\n   from China.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weigh', 'n.', 'A corruption of Way, used only in the phrase under weigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weighed', 'imp. & p. p.', 'of Weigh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weighing', 'p. pr. & vb. n.', 'of Weigh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weigh', 'v. t.', 'To bear up; to raise; to lift into the air; to swing up;\n   as, to weigh anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weigh', 'v. t.', 'To examine by the balance; to ascertain the weight of,\n   that is, the force with which a thing tends to the center of the earth;\n   to determine the heaviness, or quantity of matter of; as, to weigh\n   sugar; to weigh gold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weigh', 'v. t.', 'To be equivalent to in weight; to counterbalance; to have\n   the heaviness of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weigh', 'v. t.', 'To pay, allot, take, or give by weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weigh', 'v. t.', 'To examine or test as if by the balance; to ponder in the\n   mind; to consider or examine for the purpose of forming an opinion or\n   coming to a conclusion; to estimate deliberately and maturely; to\n   balance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weigh', 'v. t.', 'To consider as worthy of notice; to regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weigh', 'v. i.', 'To have weight; to be heavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weigh', 'v. i.', 'To be considered as important; to have weight in the\n   intellectual balance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weigh', 'v. i.', 'To bear heavily; to press hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weigh', 'v. i.', 'To judge; to estimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weigh', 'n.', 'A certain quantity estimated by weight; an English measure\n   of weight. See Wey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weighable', 'a.', 'Capable of being weighed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weighage', 'n.', 'A duty or toil paid for weighing merchandise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weighbeam', 'n.', 'A kind of large steelyard for weighing merchandise; --\n   also called weighmasters beam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weighboard', 'n.', 'Clay intersecting a vein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weighbridge', 'n.', 'A weighing machine on which loaded carts may be\n   weighed; platform scales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weigher', 'n.', 'One who weighs; specifically, an officer whose duty it is\n   to weigh commodities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weigh-houses', 'pl. ', 'of Weigh-house');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weigh-house', 'n.', 'A building at or within which goods, and the like, are\n   weighed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weighing', '', 'a. & n. from Weigh, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weighlock', 'n.', 'A lock, as on a canal, in which boats are weighed and\n   their tonnage is settled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weighmaster', 'n.', 'One whose business it is to weigh ore, hay,\n   merchandise, etc.; one licensed as a public weigher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weight', 'v. t.', 'The quality of being heavy; that property of bodies by\n   which they tend toward the center of the earth; the effect of\n   gravitative force, especially when expressed in certain units or\n   standards, as pounds, grams, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weight', 'v. t.', 'The quantity of heaviness; comparative tendency to the\n   center of the earth; the quantity of matter as estimated by the\n   balance, or expressed numerically with reference to some standard unit;\n   as, a mass of stone having the weight of five hundred pounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weight', 'v. t.', 'Hence, pressure; burden; as, the weight of care or\n   business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weight', 'v. t.', 'Importance; power; influence; efficacy; consequence;\n   moment; impressiveness; as, a consideration of vast weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weight', 'v. t.', 'A scale, or graduated standard, of heaviness; a mode of\n   estimating weight; as, avoirdupois weight; troy weight; apothecaries\n   weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weight', 'v. t.', 'A ponderous mass; something heavy; as, a clock weight; a\n   paper weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weight', 'v. t.', 'A definite mass of iron, lead, brass, or other metal, to\n   be used for ascertaining the weight of other bodies; as, an ounce\n   weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weight', 'v. t.', 'The resistance against which a machine acts, as opposed\n   to the power which moves it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weighted', 'imp. & p. p.', 'of Weight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weighting', 'p. pr. & vb. n.', 'of Weight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weight', 'v. t.', 'To load with a weight or weights; to load down; to make\n   heavy; to attach weights to; as, to weight a horse or a jockey at a\n   race; to weight a whip handle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weight', 'v. t.', 'To assign a weight to; to express by a number the\n   probable accuracy of, as an observation. See Weight of observations,\n   under Weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weightily', 'adv.', 'In a weighty manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weightiness', 'n.', 'The quality or state of being weighty; weight; force;\n   importance; impressiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weightless', 'a.', 'Having no weight; imponderable; hence, light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weighty', 'superl.', 'Having weight; heavy; ponderous; as, a weighty body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weighty', 'superl.', 'Adapted to turn the balance in the mind, or to\n   convince; important; forcible; serious; momentous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weighty', 'superl.', 'Rigorous; severe; afflictive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weir', 'n.', 'Alt. of Wear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wear', 'n.', 'A dam in a river to stop and raise the water, for the purpose\n   of conducting it to a mill, forming a fish pond, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wear', 'n.', 'A fence of stakes, brushwood, or the like, set in a stream,\n   tideway, or inlet of the sea, for taking fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wear', 'n.', 'A long notch with a horizontal edge, as in the top of a\n   vertical plate or plank, through which water flows, -- used in\n   measuring the quantity of flowing water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weird', 'n.', 'Fate; destiny; one of the Fates, or Norns; also, a\n   prediction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weird', 'n.', 'A spell or charm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weird', 'a.', 'Of or pertaining to fate; concerned with destiny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weird', 'a.', 'Of or pertaining to witchcraft; caused by, or suggesting,\n   magical influence; supernatural; unearthly; wild; as, a weird\n   appearance, look, sound, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weird', 'v. t.', 'To foretell the fate of; to predict; to destine to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weirdness', 'n.', 'The quality or state of being weird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weism', 'n.', 'Same as Wegotism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weive', 'v. t.', 'See Waive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weka', 'n.', 'A New Zealand rail (Ocydromus australis) which has wings so\n   short as to be incapable of flight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wekau', 'n.', 'A small New Zealand owl (Sceloglaux albifacies). It has\n   short wings and long legs, and lives chiefly on the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wekeen', 'n.', 'The meadow pipit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welaway', 'interj.', 'Alas!');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wel-begone', 'a.', 'Surrounded with happiness or prosperity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welch', 'a.', 'See Welsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welcher', 'n.', 'See Welsher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welchman', 'n.', 'See Welshman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welcome', 'n.', 'Received with gladness; admitted willingly to the house,\n   entertainment, or company; as, a welcome visitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welcome', 'n.', 'Producing gladness; grateful; as, a welcome present;\n   welcome news.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welcome', 'n.', 'Free to have or enjoy gratuitously; as, you are welcome to\n   the use of my library.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welcome', 'n.', 'Salutation to a newcomer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welcome', 'n.', 'Kind reception of a guest or newcomer; as, we entered the\n   house and found a ready welcome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welcomed', 'imp. & p. p.', 'of Welcome');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welcoming', 'p. pr. & vb. n.', 'of Welcome');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welcome', 'v. t.', 'To salute with kindness, as a newcomer; to receive and\n   entertain hospitably and cheerfully; as, to welcome a visitor; to\n   welcome a new idea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welcomely', 'adv.', 'In a welcome manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welcomeness', 'n.', 'The quality or state of being welcome; gratefulness;\n   agreeableness; kind reception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welcomer', 'n.', 'One who welcomes; one who salutes, or receives kindly, a\n   newcomer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weld', 'v. t.', 'To wield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weld', 'n.', 'An herb (Reseda luteola) related to mignonette, growing in\n   Europe, and to some extent in America; dyers broom; dyers rocket;\n   dyers weed; wild woad. It is used by dyers to give a yellow color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weld', 'n.', 'Coloring matter or dye extracted from this plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welded', 'imp. & p. p.', 'of Weld');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welding', 'p. pr. & vb. n.', 'of Weld');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weld', 'v. t.', 'To press or beat into intimate and permanent union, as two\n   pieces of iron when heated almost to fusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weld', 'v. t.', 'Fig.: To unite closely or intimately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weld', 'n.', 'The state of being welded; the joint made by welding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weldable', 'a.', 'Capable of being welded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welder', 'n.', 'One who welds, or unites pieces of iron, etc., by welding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welder', 'n.', 'One who welds, or wields.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welder', 'n.', 'A manager; an actual occupant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weldons process', '', 'A process for the recovery or regeneration of\n   manganese dioxide in the manufacture of chlorine, by means of milk of\n   lime and the oxygen of the air; -- so called after the inventor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wele', 'n.', 'Prosperity; happiness; well-being; weal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weleful', 'a.', 'Producing prosperity or happiness; blessed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welew', 'v. t.', 'To welk, or wither.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welfare', 'n.', 'Well-doing or well-being in any respect; the enjoyment of\n   health and the common blessings of life; exemption from any evil or\n   calamity; prosperity; happiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welfaring', 'a.', 'Faring well; prosperous; thriving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welked', 'imp. & p. p.', 'of Welk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welking', 'p. pr. & vb. n.', 'of Welk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welk', 'v. i.', 'To wither; to fade; also, to decay; to decline; to wane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welk', 'v. t.', 'To cause to wither; to wilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welk', 'v. t.', 'To contract; to shorten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welk', 'v. t.', 'To soak; also, to beat severely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welk', 'n.', 'A pustule. See 2d Whelk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welk', 'n.', 'A whelk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welked', 'v. t.', 'See Whelked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welkin', 'n.', 'The visible regions of the air; the vault of heaven; the\n   sky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'v. i.', 'An issue of water from the earth; a spring; a fountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'v. i.', 'A pit or hole sunk into the earth to such a depth as to\n   reach a supply of water, generally of a cylindrical form, and often\n   walled with stone or bricks to prevent the earth from caving in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'v. i.', 'A shaft made in the earth to obtain oil or brine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'v. i.', 'Fig.: A source of supply; fountain; wellspring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'v. i.', 'An inclosure in the middle of a vessels hold, around the\n   pumps, from the bottom to the lower deck, to preserve the pumps from\n   damage and facilitate their inspection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'v. i.', 'A compartment in the middle of the hold of a fishing\n   vessel, made tight at the sides, but having holes perforated in the\n   bottom to let in water for the preservation of fish alive while they\n   are transported to market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'v. i.', 'A vertical passage in the stern into which an auxiliary\n   screw propeller may be drawn up out of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'v. i.', 'A depressed space in the after part of the deck; -- often\n   called the cockpit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'v. i.', 'A hole or excavation in the earth, in mining, from which\n   run branches or galleries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'v. i.', 'An opening through the floors of a building, as for a\n   staircase or an elevator; a wellhole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'v. i.', 'The lower part of a furnace, into which the metal falls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welled', 'imp. & p. p.', 'of Well');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welling', 'p. pr. & vb. n.', 'of Well');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'v. i.', 'To issue forth, as water from the earth; to flow; to\n   spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'v. t.', 'To pour forth, as from a well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'v. t.', 'In a good or proper manner; justly; rightly; not ill or\n   wickedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'v. t.', 'Suitably to ones condition, to the occasion, or to a\n   proposed end or use; suitably; abundantly; fully; adequately;\n   thoroughly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'v. t.', 'Fully or about; -- used with numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'v. t.', 'In such manner as is desirable; so as one could wish;\n   satisfactorily; favorably; advantageously; conveniently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'v. t.', 'Considerably; not a little; far.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'a.', 'Good in condition or circumstances; desirable, either in a\n   natural or moral sense; fortunate; convenient; advantageous; happy; as,\n   it is well for the country that the crops did not fail; it is well that\n   the mistake was discovered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'a.', 'Being in health; sound in body; not ailing, diseased, or\n   sick; healthy; as, a well man; the patient is perfectly well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'a.', 'Being in favor; favored; fortunate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', 'a.', 'Safe; as, a chip warranted well at a certain day and place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welladay', 'interj.', 'Alas! Welaway!');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wellat', 'n.', 'The king parrakeet See under King.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-being', 'n.', 'The state or condition of being well; welfare;\n   happiness; prosperity; as, virtue is essential to the well-being of men\n   or of society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-born', 'a.', 'Born of a noble or respect able family; not of mean\n   birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-bred', 'a.', 'Having good breeding; refined in manners; polite;\n   cultivated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welldoer', 'n.', 'One who does well; one who does good to another; a\n   benefactor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welldoing', 'n.', 'A doing well; right performance of duties. Also used\n   adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welldrained', 'imp. & p. p.', 'of Welldrain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-draining', 'p. pr. & vb. n.', 'of Welldrain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welldrain', 'v. t.', 'To drain, as land; by means of wells, or pits, which\n   receive the water, and from which it is discharged by machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wellfare', 'n.', 'See Welfare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-favored', 'a.', 'Handsome; wellformed; beautiful; pleasing to the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wellhead', 'n.', 'A source, spring, or fountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wellhole', 'n.', 'The open space in a floor, to accommodate a staircase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wellhole', 'n.', 'The open space left beyond the ends of the steps of a\n   staircase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wellhole', 'n.', 'A cavity which receives a counterbalancing weight in\n   certain mechanical contrivances, and is adapted also for other\n   purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-informed', 'a.', 'Correctly informed; provided with information; well\n   furnished with authentic knowledge; intelligent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wellingtonia', 'n.', 'A name given to the \"big trees\" (Sequoia gigantea) of\n   California, and still used in England. See Sequoia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wellingtons', 'n. pl.', 'A kind of long boots for men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-intentioned', 'a.', 'Having upright intentions or honorable purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-known', 'a.', 'Fully known; generally known or acknowledged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-liking', 'a.', 'Being in good condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-mannered', 'a.', 'Polite; well-bred; complaisant; courteous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-meaner', 'n.', 'One whose intention is good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-meaning', 'a.', 'Having a good intention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-natured', 'a.', 'Good-natured; kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-nigh', 'adv.', 'Almost; nearly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-plighted', 'a.', 'Being well folded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-read', 'a.', 'Of extensive reading; deeply versed; -- often followed\n   by in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-seen', 'a.', 'Having seen much; hence, accomplished; experienced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-set', 'a.', 'Properly or firmly set.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-set', 'a.', 'Well put together; having symmetry of parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-sped', 'a.', 'Having good success.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-spoken', 'a.', 'Speaking well; speaking with fitness or grace;\n   speaking kindly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-spoken', 'a.', 'Spoken with propriety; as, well-spoken words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wellspring', 'n.', 'A fountain; a spring; a source of continual supply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-willer', 'n.', 'One who wishes well, or means kindly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well-wish', 'n.', 'A wish of happiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wellwisher', 'n.', 'One who wishes another well; one who is benevolently or\n   friendlily inclined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Well', '', 'Contraction for we will or we shall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wels', 'n.', 'The sheatfish; -- called also waller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welsh', 'a.', 'Of or pertaining to Wales, or its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welsh', 'n.', 'The language of Wales, or of the Welsh people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welsh', 'n.', 'The natives or inhabitants of Wales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welsher', 'n.', 'One who cheats at a horse race; one who bets, without a\n   chance of being able to pay; one who receives money to back certain\n   horses and absconds with it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welshmen', 'pl. ', 'of Welshman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welshman', 'n.', 'A native or inhabitant of Wales; one of the Welsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welshman', 'n.', 'A squirrel fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welshman', 'n.', 'The large-mouthed black bass. See Black bass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welsome', 'a.', 'Prosperous; well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welt', 'n.', 'That which, being sewed or otherwise fastened to an edge or\n   border, serves to guard, strengthen, or adorn it');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welt', 'n.', 'A small cord covered with cloth and sewed on a seam or border\n   to strengthen it; an edge of cloth folded on itself, usually over a\n   cord, and sewed down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welt', 'n.', 'A hem, border, or fringe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welt', 'n.', 'In shoemaking, a narrow strip of leather around a shoe,\n   between the upper leather and sole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welt', 'n.', 'In steam boilers and sheet-iron work, a strip riveted upon\n   the edges of plates that form a butt joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welt', 'n.', 'In carpentry, a strip of wood fastened over a flush seam or\n   joint, or an angle, to strengthen it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welt', 'n.', 'In machine-made stockings, a strip, or flap, of which the\n   heel is formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welt', 'n.', 'A narrow border, as of an ordinary, but not extending around\n   the ends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welted', 'imp. & p. p.', 'of Welt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welting', 'p. pr. & vb. n.', 'of Welt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welt', 'v. t.', 'To furnish with a welt; to sew or fasten a welt on; as, to\n   welt a boot or a shoe; to welt a sleeve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welt', 'v. t.', 'To wilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welte', '', 'imp. of Weld, to wield.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weltered', 'imp. & p. p.', 'of Welter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weltering', 'p. pr. & vb. n.', 'of Welter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welter', 'v. i.', 'To roll, as the body of an animal; to tumble about,\n   especially in anything foul or defiling; to wallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welter', 'v. i.', 'To rise and fall, as waves; to tumble over, as billows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welter', 'v. i.', 'To wither; to wilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welter', 'a.', 'Of, pertaining to, or designating, the most heavily\n   weighted race in a meeting; as, a welter race; the welter stakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welter', 'n.', 'That in which any person or thing welters, or wallows;\n   filth; mire; slough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welter', 'n.', 'A rising or falling, as of waves; as, the welter of the\n   billows; the welter of a tempest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Welwitschia', 'n.', 'An African plant (Welwitschia mirabilis) belonging to\n   the order Gnetaceae. It consists of a short, woody, topshaped stem, and\n   never more than two leaves, which are the cotyledons enormously\n   developed, and at length split into diverging segments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wem', 'n.', 'The abdomen; the uterus; the womb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wem', 'n.', 'Spot; blemish; harm; hurt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wem', 'v. t.', 'To stain; to blemish; to harm; to corrupt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wemless', 'a.', 'Having no wem, or blemish; spotless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wem', 'n.', 'An indolent, encysted tumor of the skin; especially, a\n   sebaceous cyst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wench', 'n.', 'A young woman; a girl; a maiden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wench', 'n.', 'A low, vicious young woman; a drab; a strumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wench', 'n.', 'A colored woman; a negress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wenched', 'imp. & p. p.', 'of Wench');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wenching', 'p. pr. & vb. n.', 'of Wench');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wench', 'v. i.', 'To frequent the company of wenches, or women of ill fame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wencher', 'n.', 'One who wenches; a lewd man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wenchless', 'a.', 'Being without a wench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wend', '', 'p. p. of Wene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wended', 'imp. & p. p.', 'of Wend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Went', '', 'of Wend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wending', 'p. pr. & vb. n.', 'of Wend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wend', 'v. i.', 'To go; to pass; to betake ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wend', 'v. i.', 'To turn round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wend', 'v. t.', 'To direct; to betake; -- used chiefly in the phrase to\n   wend ones way. Also used reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wend', 'n.', 'A large extent of ground; a perambulation; a circuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wende', '', 'imp. of Wene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wendic', 'a.', 'Alt. of Wendish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wendish', 'a.', 'Of or pertaining the Wends, or their language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wendic', 'n.', 'The language of the Wends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wends', 'n. pl.', 'A Slavic tribe which once occupied the northern and\n   eastern parts of Germany, of which a small remnant exists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wene', 'v. i.', 'To ween.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wenlock group', '', 'The middle subdivision of the Upper Silurian in Great\n   Britain; -- so named from the typical locality in Shropshire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wennel', 'n.', 'See Weanel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wennish', 'a.', 'Alt. of Wenny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wenny', 'a.', 'Having the nature of a wen; resembling a wen; as, a wennish\n   excrescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wenona', 'n.', 'A sand snake (Charina plumbea) of Western North America, of\n   the family Erycidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Went', '', 'imp. & p. p. of Wend; -- now obsolete except as the imperfect\n   of go, with which it has no etymological connection. See Go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Went', 'n.', 'Course; way; path; journey; direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wentletrap', 'n.', 'Any one of numerous species of elegant, usually white,\n   marine shells of the genus Scalaria, especially Scalaria pretiosa,\n   which was formerly highly valued; -- called also staircase shell. See\n   Scalaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wep', '', 'imp. of Weep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wepen', 'n.', 'Weapon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wept', '', 'imp. & p. p. of Weep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Werche', 'v. t. & i.', 'To work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Were', 'v. t. & i.', 'To wear. See 3d Wear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Were', 'n.', 'A weir. See Weir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Were', 'v. t.', 'To guard; to protect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Were', '', 'The imperfect indicative plural, and imperfect subjunctive\n   singular and plural, of the verb be. See Be.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Were', 'n.', 'A man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Were', 'n.', 'A fine for slaying a man; the money value set upon a mans\n   life; weregild.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weregild', 'n.', 'The price of a mans head; a compensation paid of a man\n   killed, partly to the king for the loss of a subject, partly to the\n   lord of a vassal, and partly to the next of kin. It was paid by the\n   murderer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Werewolves', 'pl. ', 'of Werewolf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Werewolf', 'n.', 'A person transformed into a wolf in form and appetite,\n   either temporarily or permanently, whether by supernatural influences,\n   by witchcraft, or voluntarily; a lycanthrope. Belief in werewolves,\n   formerly general, is not now extinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Werk', 'v.', 'Alt. of Werke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Werke', 'v.', 'See Work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wern', 'v. t.', 'To refuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wernerian', 'a.', 'Of or pertaining to A. G. Werner, The German\n   mineralogist and geologist, who classified minerals according to their\n   external characters, and advocated the theory that the strata of the\n   earths crust were formed by depositions from water; designating, or\n   according to, Werners system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wernerite', 'n.', 'The common grayish or white variety of soapolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weroole', 'n.', 'An Australian lorikeet (Ptilosclera versicolor) noted for\n   the variety of its colors; -- called also varied lorikeet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Werre', 'n.', 'War.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Werrey', 'v. t.', 'To warray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Werst', 'n.', 'See Verst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wert', '', 'The second person singular, indicative and subjunctive moods,\n   imperfect tense, of the verb be. It is formed from were, with the\n   ending -t, after the analogy of wast. Now used only in solemn or poetic\n   style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wert', 'n.', 'A wart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weryangle', 'n.', 'See Wariangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wesand', 'n.', 'See Weasand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wesh', 'imp.', 'Washed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wesil', 'n.', 'See Weasand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wesleyan', 'a.', 'Of or pertaining to Wesley or Wesleyanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wesleyan', 'n.', 'One who adopts the principles of Wesleyanism; a\n   Methodist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wesleyanism', 'n.', 'The system of doctrines and church polity inculcated\n   by John Wesley (b. 1703; d. 1791), the founder of the religious sect\n   called Methodist; Methodism. See Methodist, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'West', 'n.', 'The point in the heavens where the sun is seen to set at the\n   equinox; or, the corresponding point on the earth; that one of the four\n   cardinal points of the compass which is in a direction at right angles\n   to that of north and south, and on the left hand of a person facing\n   north; the point directly opposite to east.');
  INSERT INTO words (word, word_type, description) VALUES  (    'West', 'n.', 'A country, or region of country, which, with regard to some\n   other country or region, is situated in the direction toward the west.');
  INSERT INTO words (word, word_type, description) VALUES  (    'West', 'n.', 'The Westen hemisphere, or the New World so called, it having\n   been discovered by sailing westward from Europe; the Occident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'West', 'n.', 'Formerly, that part of the United States west of the\n   Alleghany mountains; now, commonly, the whole region west of the\n   Mississippi river; esp., that part which is north of the Indian\n   Territory, New Mexico, etc. Usually with the definite article.');
  INSERT INTO words (word, word_type, description) VALUES  (    'West', 'a.', 'Lying toward the west; situated at the west, or in a western\n   direction from the point of observation or reckoning; proceeding toward\n   the west, or coming from the west; as, a west course is one toward the\n   west; an east and west line; a west wind blows from the west.');
  INSERT INTO words (word, word_type, description) VALUES  (    'West', 'adv.', 'Westward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'West', 'v. i.', 'To pass to the west; to set, as the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'West', 'v. i.', 'To turn or move toward the west; to veer from the north or\n   south toward the west.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Westering', 'a.', 'Passing to the west.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Westerly', 'a.', 'Of or pertaining to the west; toward the west; coming\n   from the west; western.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Westerly', 'adv.', 'Toward the west; westward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Western', 'a.', 'Of or pertaining to the west; situated in the west, or in\n   the region nearly in the direction of west; being in that quarter where\n   the sun sets; as, the western shore of France; the western ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Western', 'a.', 'Moving toward the west; as, a ship makes a western course;\n   coming from the west; as, a western breeze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Westerner', 'n.', 'A native or inhabitant of the west.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Westernmost', 'a.', 'Situated the farthest towards the west; most western.');
  INSERT INTO words (word, word_type, description) VALUES  (    'West India', '', 'Alt. of West Indian');
  INSERT INTO words (word, word_type, description) VALUES  (    'West Indian', '', 'Belonging or relating to the West Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'West Indian', '', 'A native of, or a dweller in, the West Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Westing', 'n.', 'The distance, reckoned toward the west, between the two\n   meridians passing through the extremities of a course, or portion of a\n   ships path; the departure of a course which lies to the west of north.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Westling', 'n.', 'A westerner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Westminster Assembly', '', 'See under Assembly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Westmost', 'a.', 'Lying farthest to the west; westernmost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Westward', 'adv.', 'Alt. of Westwards');
  INSERT INTO words (word, word_type, description) VALUES  (    'Westwards', 'adv.', 'Toward the west; as, to ride or sail westward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Westward', 'a.', 'Lying toward the west.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Westward', 'n.', 'The western region or countries; the west.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Westwardly', 'adv.', 'In a westward direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Westy', 'a.', 'Dizzy; giddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wet', 'superl.', 'Containing, or consisting of, water or other liquid;\n   moist; soaked with a liquid; having water or other liquid upon the\n   surface; as, wet land; a wet cloth; a wet table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wet', 'superl.', 'Very damp; rainy; as, wet weather; a wet season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wet', 'superl.', 'Employing, or done by means of, water or some other\n   liquid; as, the wet extraction of copper, in distinction from dry\n   extraction in which dry heat or fusion is employed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wet', 'superl.', 'Refreshed with liquor; drunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wet', 'a.', 'Water or wetness; moisture or humidity in considerable degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wet', 'a.', 'Rainy weather; foggy or misty weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wet', 'a.', 'A dram; a drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wet', 'imp. & p. p.', 'of Wet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wetted', '', 'of Wet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wetting', 'p. pr. & vb. n.', 'of Wet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wet', 'v. t.', 'To fill or moisten with water or other liquid; to sprinkle;\n   to cause to have water or other fluid adherent to the surface; to dip\n   or soak in a liquid; as, to wet a sponge; to wet the hands; to wet\n   cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wetbird', 'n.', 'The chaffinch, whose cry is thought to foretell rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wether', 'n.', 'A castrated ram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Westness', 'n.', 'The quality or state of being wet; moisture; humidity;\n   as, the wetness of land; the wetness of a cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Westness', 'n.', 'A watery or moist state of the atmosphere; a state of\n   being rainy, foggy, or misty; as, the wetness of weather or the season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wet nurse', '', 'A nurse who suckles a child, especially the child of\n   another woman. Cf. Dry nurse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wet-shod', 'a.', 'Having the feet, or the shoes on the feet, wet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wettish', 'a.', 'Somewhat wet; moist; humid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wevil', 'n.', 'See Weevil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wex', 'v. t. & i.', 'To grow; to wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wex', 'imp.', 'Waxed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wex', 'n.', 'Wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wey', 'n.', 'Way; road; path.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wey', 'v. t. & i.', 'To weigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wey', 'n.', 'A certain measure of weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weyle', 'v. t. & i.', 'To wail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weyleway', 'interj.', 'See Welaway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Weyve', 'v. t.', 'To waive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wezand', 'n.', 'See Weasand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whaap', 'n.', 'The European curlew; -- called also awp, whaup, great whaup,\n   and stock whaup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whaap', 'n.', 'The whimbrel; -- called also May whaup, little whaup, and\n   tang whaup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whacked', 'imp. & p. p.', 'of Whack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whacking', 'p. pr. & vb. n.', 'of Whack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whack', 'v. t.', 'To strike; to beat; to give a heavy or resounding blow\n   to; to thrash; to make with whacks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whack', 'v. i.', 'To strike anything with a smart blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whack', 'n.', 'A smart resounding blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whacker', 'n.', 'One who whacks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whacker', 'n.', 'Anything very large; specif., a great lie; a whapper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whacking', 'a.', 'Very large; whapping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whahoo', 'n.', 'An American tree, the winged elm. (Ulmus alata).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whaled', 'imp. & p. p.', 'of Whala');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whaling', 'p. pr. & vb. n.', 'of Whala');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whala', 'v. t.', 'To lash with stripes; to wale; to thrash; to drub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whale', 'n.', 'Any aquatic mammal of the order Cetacea, especially any one\n   of the large species, some of which become nearly one hundred feet\n   long. Whales are hunted chiefly for their oil and baleen, or whalebone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whaleboat', 'n.', 'A long, narrow boat, sharp at both ends, used by\n   whalemen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whalebone', 'n.', 'A firm, elastic substance resembling horn, taken from\n   the upper jaw of the right whale; baleen. It is used as a stiffening in\n   stays, fans, screens, and for various other purposes. See Baleen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whalemen', 'pl. ', 'of Whaleman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whaleman', 'n.', 'A man employed in the whale fishery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whaler', 'n.', 'A vessel or person employed in the whale fishery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whaler', 'n.', 'One who whales, or beats; a big, strong fellow; hence,\n   anything of great or unusual size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whaling', 'n.', 'The hunting of whales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whaling', 'a.', 'Pertaining to, or employed in, the pursuit of whales; as,\n   a whaling voyage; a whaling vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whall', 'n.', 'A light color of the iris in horses; wall-eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whally', 'a.', 'Having the iris of light color; -- said of horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whame', 'n.', 'A breeze fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whammel', 'v. t.', 'To turn over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whan', 'adv.', 'When.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whang', 'n.', 'A leather thong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whang', 'v. t.', 'To beat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whanghee', 'n.', 'See Wanghee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whap', 'v. i.', 'Alt. of Whop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whop', 'v. i.', 'To throw ones self quickly, or by an abrupt motion; to\n   turn suddenly; as, she whapped down on the floor; the fish whapped\n   over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whapped', 'imp. & p. p.', 'of Whop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whapping', 'p. pr. & vb. n.', 'of Whop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whap', 'v. t.', 'Alt. of Whop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whop', 'v. t.', 'To beat or strike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whap', 'n.', 'Alt. of Whop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whop', 'n.', 'A blow, or quick, smart stroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whapper', 'n.', 'Alt. of Whopper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whopper', 'n.', 'Something uncommonly large of the kind; something\n   astonishing; -- applied especially to a bold lie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whapping', 'a.', 'Alt. of Whopping');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whopping', 'a.', 'Very large; monstrous; astonishing; as, a whapping story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wharfs', 'pl. ', 'of Wharf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wharves', 'pl. ', 'of Wharf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wharf', 'n.', 'A structure or platform of timber, masonry, iron, earth, or\n   other material, built on the shore of a harbor, river, canal, or the\n   like, and usually extending from the shore to deep water, so that\n   vessels may lie close alongside to receive and discharge cargo,\n   passengers, etc.; a quay; a pier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wharf', 'n.', 'The bank of a river, or the shore of the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wharfed', 'imp. & p. p.', 'of Wharf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wharfing', 'p. pr. & vb. n.', 'of Wharf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wharf', 'v. t.', 'To guard or secure by a firm wall of timber or stone\n   constructed like a wharf; to furnish with a wharf or wharfs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wharf', 'v. t.', 'To place upon a wharf; to bring to a wharf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wharfage', 'n.', 'The fee or duty paid for the privilege of using a wharf\n   for loading or unloading goods; pierage, collectively; quayage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wharfage', 'n.', 'A wharf or wharfs, collectively; wharfing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wharfing', 'n.', 'Wharfs, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wharfing', 'n.', 'A mode of facing sea walls and embankments with planks\n   driven as piles and secured by ties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wharfinger', 'n.', 'A man who owns, or has the care of, a wharf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wharl', 'n.', 'Alt. of Wharling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wharling', 'n.', 'A guttural pronunciation of the letter r; a burr. See\n   Burr, n., 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wharp', 'n.', 'A kind of fine sand from the banks of the Trent, used as a\n   polishing powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'What', 'pron., a., & adv.', 'As an interrogative pronoun, used in asking\n   questions regarding either persons or things; as, what is this? what\n   did you say? what poem is this? what child is lost?');
  INSERT INTO words (word, word_type, description) VALUES  (    'What', 'pron., a., & adv.', 'As an exclamatory word: -- (a) Used absolutely\n   or independently; -- often with a question following.');
  INSERT INTO words (word, word_type, description) VALUES  (    'What', 'pron., a., & adv.', 'Used adjectively, meaning how remarkable, or\n   how great; as, what folly! what eloquence! what courage!');
  INSERT INTO words (word, word_type, description) VALUES  (    'What', 'pron., a., & adv.', 'Sometimes prefixed to adjectives in an\n   adverbial sense, as nearly equivalent to how; as, what happy boys!');
  INSERT INTO words (word, word_type, description) VALUES  (    'What', 'pron., a., & adv.', 'As a relative pronoun');
  INSERT INTO words (word, word_type, description) VALUES  (    'What', 'pron., a., & adv.', 'Used substantively with the antecedent\n   suppressed, equivalent to that which, or those [persons] who, or those\n   [things] which; -- called a compound relative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'What', 'pron., a., & adv.', 'Used adjectively, equivalent to the . . .\n   which; the sort or kind of . . . which; rarely, the . . . on, or at,\n   which.');
  INSERT INTO words (word, word_type, description) VALUES  (    'What', 'pron., a., & adv.', 'Used adverbially in a sense corresponding to\n   the adjectival use; as, he picked what good fruit he saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'What', 'pron., a., & adv.', 'Whatever; whatsoever; what thing soever; --\n   used indefinitely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'What', 'pron., a., & adv.', 'Used adverbially, in part; partly; somewhat;\n   -- with a following preposition, especially, with, and commonly with\n   repetition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'What', 'n.', 'Something; thing; stuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'What', 'interrog. adv.', 'Why? For what purpose? On what account?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whateer', 'pron.', 'A contraction of what-ever; -- used in poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whatever', 'pron.', 'Anything soever which; the thing or things of any\n   kind; being this or that; of one nature or another; one thing or\n   another; anything that may be; all that; the whole that; all\n   particulars that; -- used both substantively and adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whatnot', 'n.', 'A kind of stand, or piece of furniture, having shelves for\n   books, ornaments, etc.; an etagere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whatso', 'indef. pron.', 'Whatsoever; whosoever; whatever; anything that.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whatsoeer', 'pron.', 'A contraction of whatsoever; -- used in poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whatsoever', 'pron. & a.', 'Whatever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whaul', 'n.', 'Same as Whall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whaup', 'n.', 'See Whaap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheal', 'n.', 'A pustule; a whelk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheal', 'n.', 'A more or less elongated mark raised by a stroke; also, a\n   similar mark made by any cause; a weal; a wale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheal', 'n.', 'Specifically (Med.), a flat, burning or itching eminence on\n   the skin, such as is produced by a mosquito bite, or in urticaria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheal', 'n.', 'A mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whealworm', 'n.', 'The harvest mite; -- so called from the wheals, caused\n   by its bite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheat', 'n.', 'A cereal grass (Triticum vulgare) and its grain, which\n   furnishes a white flour for bread, and, next to rice, is the grain most\n   largely used by the human race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheatbird', 'n.', 'A bird that feeds on wheat, especially the chaffinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheatear', 'n.', 'A small European singing bird (Saxicola /nanthe). The\n   male is white beneath, bluish gray above, with black wings and a black\n   stripe through each eye. The tail is black at the tip and in the\n   middle, but white at the base and on each side. Called also checkbird,\n   chickell, dykehopper, fallow chat, fallow finch, stonechat, and\n   whitetail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheaten', 'a.', 'Made of wheat; as, wheaten bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheatsel bird', '', 'The male of the chaffinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheatstones bridge', '', 'See under Bridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheatworm', 'n.', 'A small nematode worm (Anguillula tritici) which attacks\n   the grains of wheat in the ear. It is found in wheat affected with\n   smut, each of the diseased grains containing a large number of the\n   minute young of the worm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheder', 'pron. & conj.', 'Whether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheedled', 'imp. & p. p.', 'of Wheedle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheedling', 'p. pr. & vb. n.', 'of Wheedle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheedle', 'v. t.', 'To entice by soft words; to cajole; to flatter; to\n   coax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheedle', 'v. t.', 'To grain, or get away, by flattery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheedle', 'v. i.', 'To flatter; to coax; to cajole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel', 'n.', 'A circular frame turning about an axis; a rotating disk,\n   whether solid, or a frame composed of an outer rim, spokes or radii,\n   and a central hub or nave, in which is inserted the axle, -- used for\n   supporting and conveying vehicles, in machinery, and for various\n   purposes; as, the wheel of a wagon, of a locomotive, of a mill, of a\n   watch, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel', 'n.', 'Any instrument having the form of, or chiefly consisting of,\n   a wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel', 'n.', 'A spinning wheel. See under Spinning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel', 'n.', 'An instrument of torture formerly used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel', 'n.', 'A circular frame having handles on the periphery, and an\n   axle which is so connected with the tiller as to form a means of\n   controlling the rudder for the purpose of steering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel', 'n.', 'A potters wheel. See under Potter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel', 'n.', 'A firework which, while burning, is caused to revolve on an\n   axis by the reaction of the escaping gases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel', 'n.', 'The burden or refrain of a song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel', 'n.', 'A bicycle or a tricycle; a velocipede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel', 'n.', 'A rolling or revolving body; anything of a circular form; a\n   disk; an orb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel', 'n.', 'A turn revolution; rotation; compass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheeled', 'imp. & p. p.', 'of Wheel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheeling', 'p. pr. & vb. n.', 'of Wheel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel', 'v. t.', 'To convey on wheels, or in a wheeled vehicle; as, to\n   wheel a load of hay or wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel', 'v. t.', 'To put into a rotatory motion; to cause to turn or\n   revolve; to cause to gyrate; to make or perform in a circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel', 'v. i.', 'To turn on an axis, or as on an axis; to revolve; to more\n   about; to rotate; to gyrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel', 'v. i.', 'To change direction, as if revolving upon an axis or\n   pivot; to turn; as, the troops wheeled to the right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel', 'v. i.', 'To go round in a circuit; to fetch a compass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel', 'v. i.', 'To roll forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheelband', 'n.', 'The tire of a wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheelbarrow', 'n.', 'A light vehicle for conveying small loads. It has two\n   handles and one wheel, and is rolled by a single person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheelbird', 'n.', 'The European goatsucker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheeled', 'a.', 'Having wheels; -- used chiefly in composition; as, a\n   four-wheeled carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheeler', 'n.', 'One who wheels, or turns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheeler', 'n.', 'A maker of wheels; a wheelwright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheeler', 'n.', 'A wheel horse. See under Wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheeler', 'n.', 'A steam vessel propelled by a paddle wheel or by paddle\n   wheels; -- used chiefly in the terms side-wheeler and stern-wheeler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheeler', 'n.', 'A worker on sewed muslin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheeler', 'n.', 'The European goatsucker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheelhouse', 'n.', 'A small house on or above a vessels deck, containing\n   the steering wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheelhouse', 'n.', 'A paddle box. See under Paddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheeling', 'n.', 'The act of conveying anything, or traveling, on wheels,\n   or in a wheeled vehicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheeling', 'n.', 'The act or practice of using a cycle; cycling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheeling', 'n.', 'Condition of a road or roads, which admits of passing on\n   wheels; as, it is good wheeling, or bad wheeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheeling', 'n.', 'A turning, or circular movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheelmen', 'pl. ', 'of Wheelman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheelman', 'n.', 'One who rides a bicycle or tricycle; a cycler, or\n   cyclist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel-shaped', 'a.', 'Shaped like a wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel-shaped', 'a.', 'Expanding into a flat, circular border at top, with\n   scarcely any tube; as, a wheel-shaped corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheelswarf', 'n.', 'See Swarf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheelwork', 'n.', 'A combination of wheels, and their connection, in a\n   machine or mechanism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheel-worn', 'a.', 'Worn by the action of wheels; as, a wheel-worn road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheelwright', 'n.', 'A man whose occupation is to make or repair wheels and\n   wheeled vehicles, as carts, wagons, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheely', 'a.', 'Circular; suitable to rotation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheen', 'n.', 'A quantity; a goodly number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheezed', 'imp. & p. p.', 'of Wheeze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheezing', 'p. pr. & vb. n.', 'of Wheeze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheeze', 'v. i.', 'To breathe hard, and with an audible piping or whistling\n   sound, as persons affected with asthma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheeze', 'n.', 'A piping or whistling sound caused by difficult\n   respiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheeze', 'n.', 'An ordinary whisper exaggerated so as to produce the hoarse\n   sound known as the \"stage whisper.\" It is a forcible whisper with some\n   admixture of tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheezy', 'a.', 'Breathing with difficulty and with a wheeze; wheezing. Used\n   also figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheft', 'n.', 'See Waft, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whelk', 'n.', 'Any one numerous species of large marine gastropods\n   belonging to Buccinum and allied genera; especially, Buccinum undatum,\n   common on the coasts both of Europe and North America, and much used as\n   food in Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whelk', 'n.', 'A papule; a pustule; acne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whelk', 'n.', 'A stripe or mark; a ridge; a wale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whelked', 'a.', 'Having whelks; whelky; as, whelked horns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whelky', 'a.', 'Having whelks, ridges, or protuberances; hence, streaked;\n   striated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whelky', 'a.', 'Shelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whelmed', 'imp. & p. p.', 'of Whelm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whelming', 'p. pr. & vb. n.', 'of Whelm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whelm', 'v. t.', 'To cover with water or other fluid; to cover by immersion\n   in something that envelops on all sides; to overwhelm; to ingulf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whelm', 'v. t.', 'Fig.: To cover completely, as if with water; to immerse;\n   to overcome; as, to whelm one in sorrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whelm', 'v. t.', 'To throw (something) over a thing so as to cover it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whelp', 'n.', 'One of the young of a dog or a beast of prey; a puppy; a\n   cub; as, a lions whelps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whelp', 'n.', 'A child; a youth; -- jocosely or in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whelp', 'n.', 'One of the longitudinal ribs or ridges on the barrel of a\n   capstan or a windless; -- usually in the plural; as, the whelps of a\n   windlass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whelp', 'n.', 'One of the teeth of a sprocket wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whelped', 'imp. & p. p.', 'of Whelp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whelping', 'p. pr. & vb. n.', 'of Whelp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whelp', 'v. i.', 'To bring forth young; -- said of the female of the dog\n   and some beasts of prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whelp', 'v. t.', 'To bring forth, as cubs or young; to give birth to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'When', 'adv.', 'At what time; -- used interrogatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'When', 'adv.', 'At what time; at, during, or after the time that; at or\n   just after, the moment that; -- used relatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'When', 'adv.', 'While; whereas; although; -- used in the manner of a\n   conjunction to introduce a dependent adverbial sentence or clause,\n   having a causal, conditional, or adversative relation to the principal\n   proposition; as, he chose to turn highwayman when he might have\n   continued an honest man; he removed the tree when it was the best in\n   the grounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'When', 'adv.', 'Which time; then; -- used elliptically as a noun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whenas', 'conj.', 'Whereas; while');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whence', 'adv.', 'From what place; hence, from what or which source,\n   origin, antecedent, premise, or the like; how; -- used interrogatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whence', 'adv.', 'From what or which place, source, material, cause, etc.;\n   the place, source, etc., from which; -- used relatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whenceever', 'adv. & conj.', 'Whencesoever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whenceforth', 'adv.', 'From, or forth from, what or which place; whence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whencesoever', 'adv. & conj.', 'From what place soever; from what cause or\n   source soever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheneer', 'adv. & conj.', 'Whenever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whenever', 'adv. & conj.', 'At whatever time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whennes', 'adv.', 'Whence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whensoever', 'adv. & conj.', 'At what time soever; at whatever time;\n   whenever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Where', 'pron. & conj.', 'Whether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Where', 'adv.', 'At or in what place; hence, in what situation, position,\n   or circumstances; -- used interrogatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Where', 'adv.', 'At or in which place; at the place in which; hence, in the\n   case or instance in which; -- used relatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Where', 'adv.', 'To what or which place; hence, to what goal, result, or\n   issue; whither; -- used interrogatively and relatively; as, where are\n   you going?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Where', 'conj.', 'Whereas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Where', 'n.', 'Place; situation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereabout', 'adv.', 'Alt. of Whereabouts');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereabouts', 'adv.', 'About where; near what or which place; -- used\n   interrogatively and relatively; as, whereabouts did you meet him?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereabouts', 'adv.', 'Concerning which; about which.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereabout', 'n.', 'Alt. of Whereabouts');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereabouts', 'n.', 'The place where a person or thing is; as, they did not\n   know his whereabouts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereas', 'adv.', 'At which place; where.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereas', 'conj.', 'Considering that; it being the case that; since; --\n   used to introduce a preamble which is the basis of declarations,\n   affirmations, commands, requests, or like, that follow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereas', 'conj.', 'When in fact; while on the contrary; the case being in\n   truth that; although; -- implying opposition to something that\n   precedes; or implying recognition of facts, sometimes followed by a\n   different statement, and sometimes by inferences or something\n   consequent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereat', 'adv.', 'At which; upon which; whereupon; -- used relatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereat', 'adv.', 'At what; -- used interrogatively; as, whereat are you\n   offended?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereby', 'adv.', 'By which; -- used relatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereby', 'adv.', 'By what; how; -- used interrogatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereer', 'adv.', 'Wherever; -- a contracted and poetical form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wherefore', 'adv. & conj.', 'For which reason; so; -- used relatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wherefore', 'adv. & conj.', 'For what reason; why; -- used interrogatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wherefore', 'n.', 'the reason why.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereform', 'adv.', 'From which; from which or what place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wherein', 'adv.', 'In which; in which place, thing, time, respect, or the\n   like; -- used relatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wherein', 'adv.', 'In what; -- used interrogatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereinto', 'adv.', 'Into which; -- used relatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereinto', 'adv.', 'Into what; -- used interrogatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereness', 'n.', 'The quality or state of having a place; ubiety;\n   situation; position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereof', 'adv.', 'Of which; of whom; formerly, also, with which; -- used\n   relatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereof', 'adv.', 'Of what; -- used interrogatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereon', 'adv.', 'On which; -- used relatively; as, the earth whereon we\n   live.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereon', 'adv.', 'On what; -- used interrogatively; as, whereon do we\n   stand?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereout', 'adv.', 'Out of which.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereso', 'adv.', 'Wheresoever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheresoeer', 'adv.', 'Wheresoever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheresoever', 'adv.', 'In what place soever; in whatever place; wherever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wherethrough', 'adv.', 'Through which.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereto', 'adv.', 'To which; -- used relatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereto', 'adv.', 'To what; to what end; -- used interrogatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereunto', 'adv.', 'Same as Whereto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereupon', 'adv.', 'Upon which; in consequence of which; after which.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wherever', 'adv.', 'At or in whatever place; wheresoever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wherewith', 'adv.', 'With which; -- used relatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wherewith', 'adv.', 'With what; -- used interrogatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wherewith', 'n.', 'The necessary means or instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wherewithal', 'adv. & n.', 'Wherewith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereret', 'v. t.', 'To hurry; to trouble; to tease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whereret', 'v. t.', 'To box (one) on the ear; to strike or box. (the ear);\n   as, to wherret a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wherret', 'n.', 'A box on the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wherries', 'pl. ', 'of Wherry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wherry', 'n.', 'A passenger barge or lighter plying on rivers; also, a kind\n   of light, half-decked vessel used in fishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wherry', 'n.', 'A long, narrow, light boat, sharp at both ends, for fast\n   rowing or sailing; esp., a racing boat rowed by one person with sculls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wherry', 'n.', 'A liquor made from the pulp of crab apples after the\n   verjuice is expressed; -- sometimes called crab wherry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wherso', 'adv.', 'Wheresoever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whetted', 'imp. & p. p.', 'of Whet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whetting', 'p. pr. & vb. n.', 'of Whet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whet', 'v. t.', 'To rub or on with some substance, as a piece of stone, for\n   the purpose of sharpening; to sharpen by attrition; as, to whet a\n   knife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whet', 'v. t.', 'To make sharp, keen, or eager; to excite; to stimulate;\n   as, to whet the appetite or the courage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whet', 'n.', 'The act of whetting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whet', 'n.', 'That which whets or sharpens; esp., an appetizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whether', 'pron.', 'Which (of two); which one (of two); -- used\n   interrogatively and relatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whether', 'conj.', 'In case; if; -- used to introduce the first or two or\n   more alternative clauses, the other or others being connected by or, or\n   by or whether. When the second of two alternatives is the simple\n   negative of the first it is sometimes only indicated by the particle\n   not or no after the correlative, and sometimes it is omitted entirely\n   as being distinctly implied in the whether of the first.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whethering', 'n.', 'The retention of the afterbirth in cows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whetile', 'n.', 'The green woodpecker, or yaffle. See Yaffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whetstone', 'n.', 'A piece of stone, natural or artificial, used for\n   whetting, or sharpening, edge tools.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whetter', 'n.', 'One who, or that which, whets, sharpens, or stimulates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whetter', 'n.', 'A tippler; one who drinks whets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whettlebones', 'n. pl.', 'The vertebrae of the back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whew', 'n. & interj.', 'A sound like a half-formed whistle, expressing\n   astonishment, scorn, or dislike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whew', 'v. i.', 'To whistle with a shrill pipe, like a plover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whewellite', 'n.', 'Calcium oxalate, occurring in colorless or white\n   monoclinic crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whewer', 'n.', 'The European widgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whey', 'n.', 'The serum, or watery part, of milk, separated from the more\n   thick or coagulable part, esp. in the process of making cheese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheyey', 'a.', 'Of the nature of, or containing, whey; resembling whey;\n   wheyish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheyface', 'n.', 'One who is pale, as from fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whey-faced', 'a.', 'Having a pale or white face, as from fright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheyish', 'a.', 'Somewhat like whey; wheyey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Which', 'a.', 'Of what sort or kind; what; what a; who.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Which', 'a.', 'A interrogative pronoun, used both substantively and\n   adjectively, and in direct and indirect questions, to ask for, or refer\n   to, an individual person or thing among several of a class; as, which\n   man is it? which woman was it? which is the house? he asked which route\n   he should take; which is best, to live or to die? See the Note under\n   What, pron., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Which', 'pron.', 'A relative pronoun, used esp. in referring to an\n   antecedent noun or clause, but sometimes with reference to what is\n   specified or implied in a sentence, or to a following noun or clause\n   (generally involving a reference, however, to something which has\n   preceded). It is used in all numbers and genders, and was formerly used\n   of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Which', 'pron.', 'A compound relative or indefinite pronoun, standing for\n   any one which, whichever, that which, those which, the . . . which, and\n   the like; as, take which you will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whichever', 'pron. & a.', 'Alt. of Whichsoever');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whichsoever', 'pron. & a.', 'Whether one or another; whether one or the\n   other; which; that one (of two or more) which; as, whichever road you\n   take, it will lead you to town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whidah bird', '', 'Any one of several species of finchlike birds belonging\n   to the genus Vidua, native of Asia and Africa. In the breeding season\n   the male has very long, drooping tail feathers. Called also vida finch,\n   whidah finch, whydah bird, whydah finch, widow bird, and widow finch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whider', 'adv.', 'Whither.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiff', 'n.', 'A sudden expulsion of air from the mouth; a quick puff or\n   slight gust, as of air or smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiff', 'n.', 'A glimpse; a hasty view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiff', 'n.', 'The marysole, or sail fluke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiffed', 'imp. & p. p.', 'of Whiff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiffing', 'p. pr. & vb. n.', 'of Whiff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiff', 'v. t.', 'To throw out in whiffs; to consume in whiffs; to puff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiff', 'v. t.', 'To carry or convey by a whiff, or as by a whiff; to puff\n   or blow away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiff', 'v. i.', 'To emit whiffs, as of smoke; to puff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiffet', 'n.', 'A little whiff or puff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiffing', 'n.', 'The act of one who, or that which, whiffs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiffing', 'n.', 'A mode of fishing with a hand line for pollack, mackerel,\n   and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiffled', 'imp. & p. p.', 'of Whiffle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiffling', 'p. pr. & vb. n.', 'of Whiffle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiffle', 'v. i.', 'To waver, or shake, as if moved by gusts of wind; to\n   shift, turn, or veer about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiffle', 'v. i.', 'To change from one opinion or course to another; to use\n   evasions; to prevaricate; to be fickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiffle', 'v. t.', 'To disperse with, or as with, a whiff, or puff; to\n   scatter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiffle', 'v. t.', 'To wave or shake quickly; to cause to whiffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiffle', 'n.', 'A fife or small flute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiffler', 'n.', 'One who whiffles, or frequently changes his opinion or\n   course; one who uses shifts and evasions in argument; hence, a trifler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiffler', 'n.', 'One who plays on a whiffle; a fifer or piper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiffler', 'n.', 'An officer who went before procession to clear the way by\n   blowing a horn, or otherwise; hence, any person who marched at the head\n   of a procession; a harbinger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiffler', 'n.', 'The golden-eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiffletree', 'n.', 'Same as Whippletree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whig', 'n.', 'Acidulated whey, sometimes mixed with buttermilk and sweet\n   herbs, used as a cooling beverage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whig', 'n.', 'One of a political party which grew up in England in the\n   seventeenth century, in the reigns of Charles I. and II., when great\n   contests existed respecting the royal prerogatives and the rights of\n   the people. Those who supported the king in his high claims were called\n   Tories, and the advocates of popular rights, of parliamentary power\n   over the crown, and of toleration to Dissenters, were, after 1679,\n   called Whigs. The terms Liberal and Radical have now generally\n   superseded Whig in English politics. See the note under Tory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whig', 'n.', 'A friend and supporter of the American Revolution; -- opposed\n   to Tory, and Royalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whig', 'n.', 'One of the political party in the United States from about\n   1829 to 1856, opposed in politics to the Democratic party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whig', 'a.', 'Of or pertaining to the Whigs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiggamore', 'n.', 'A Whig; -- a cant term applied in contempt to Scotch\n   Presbyterians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiggarchy', 'n.', 'Government by Whigs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiggery', 'n.', 'The principles or practices of the Whigs; Whiggism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiggish', 'a.', 'Of or pertaining to Whigs; partaking of, or characterized\n   by, the principles of Whigs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiggishly', 'adv.', 'In a Whiggish manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiggism', 'n.', 'The principles of the Whigs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whigling', 'n.', 'A petty or inferior Whig; -- used in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'While', 'n.', 'Space of time, or continued duration, esp. when short; a\n   time; as, one while we thought him innocent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'While', 'n.', 'That which requires time; labor; pains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiled', 'imp. & p. p.', 'of While');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiling', 'p. pr. & vb. n.', 'of While');
  INSERT INTO words (word, word_type, description) VALUES  (    'While', 'v. t.', 'To cause to pass away pleasantly or without irksomeness\n   or disgust; to spend or pass; -- usually followed by away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'While', 'v. i.', 'To loiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'While', 'conj.', 'During the time that; as long as; whilst; at the same\n   time that; as, while I write, you sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'While', 'conj.', 'Hence, under which circumstances; in which case; though;\n   whereas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'While', 'prep.', 'Until; till.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whilere', 'adv.', 'A little while ago; recently; just now; erewhile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiles', 'n.', 'Meanwhile; meantime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiles', 'n.', 'sometimes; at times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiles', 'conj.', 'During the time that; while.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whilk', 'n.', 'A kind of mollusk, a whelk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whilk', 'n.', 'The scoter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whilk', 'pron.', 'Which.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whilom', 'n.', 'Formerly; once; of old; erewhile; at times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whilst', 'adv.', 'While.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whim', 'n.', 'The European widgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whim', 'n.', 'A sudden turn or start of the mind; a temporary eccentricity;\n   a freak; a fancy; a capricious notion; a humor; a caprice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whim', 'n.', 'A large capstan or vertical drum turned by horse power or\n   steam power, for raising ore or water, etc., from mines, or for other\n   purposes; -- called also whim gin, and whimsey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whim', 'v. i.', 'To be subject to, or indulge in, whims; to be whimsical,\n   giddy, or freakish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimbrel', 'n.', 'Any one of several species of small curlews, especially\n   the European species (Numenius phaeopus), called also Jack curlew, half\n   curlew, stone curlew, and tang whaup. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimling', 'n.', 'One given to whims; hence, a weak, childish person; a\n   child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimmy', 'a.', 'Full of whims; whimsical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimpered', 'imp. & p. p.', 'of Whimper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimpering', 'p. pr. & vb. n.', 'of Whimper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimper', 'v. i.', 'To cry with a low, whining, broken voice; to whine; to\n   complain; as, a child whimpers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimper', 'v. t.', 'To utter in alow, whining tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimper', 'n.', 'A low, whining, broken cry; a low, whining sound,\n   expressive of complaint or grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimperer', 'n.', 'One who whimpers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimple', 'v. t.', 'See Wimple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimple', 'v. i.', 'To whiffle; to veer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimseys', 'pl. ', 'of Whimsy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimsies', 'pl. ', 'of Whimsy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimsey', 'n.', 'Alt. of Whimsy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimsy', 'n.', 'A whim; a freak; a capricious notion, a fanciful or odd\n   conceit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimsy', 'n.', 'A whim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimsey', 'v. t.', 'To fill with whimseys, or whims; to make fantastic; to\n   craze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimsical', 'a.', 'Full of, or characterized by, whims; actuated by a whim;\n   having peculiar notions; queer; strange; freakish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimsical', 'a.', 'Odd or fantastic in appearance; quaintly devised;\n   fantastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimsicality', 'n.', 'The quality or state of being whimsical;\n   whimsicalness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimsically', 'adv.', 'In a whimsical manner; freakishly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimsicalness', 'n.', 'The quality or state of being whimsical;\n   freakishness; whimsical disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimsy', 'n.', 'A whimsey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimwham', 'n.', 'A whimsical thing; an odd device; a trifle; a trinket; a\n   gimcrack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whimwham', 'n.', 'A whim, or whimsey; a freak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whin', 'n.', 'Gorse; furze. See Furze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whin', 'n.', 'Woad-waxed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whin', 'n.', 'Same as Whinstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whinberry', 'n.', 'The English bilberry; -- so called because it grows on\n   moors among the whins, or furze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whinchat', 'n.', 'A small warbler (Pratincola rubetra) common in Europe; --\n   called also whinchacker, whincheck, whin-clocharet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whined', 'imp. & p. p.', 'of Whine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whining', 'p. pr. & vb. n.', 'of Whine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whine', 'v. i.', 'To utter a plaintive cry, as some animals; to moan with a\n   childish noise; to complain, or to tell of sorrow, distress, or the\n   like, in a plaintive, nasal tone; hence, to complain or to beg in a\n   mean, unmanly way; to moan basely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whine', 'v. t.', 'To utter or express plaintively, or in a mean, unmanly\n   way; as, to whine out an excuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whine', 'n.', 'A plaintive tone; the nasal, childish tone of mean\n   complaint; mean or affected complaint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiner', 'n.', 'One who, or that which, whines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whinge', 'v. i.', 'To whine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whinger', 'n.', 'A kind of hanger or sword used as a knife at meals and as\n   a weapon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiningly', 'adv.', 'In a whining manner; in a tone of mean complaint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whinner', 'v. i.', 'To whinny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whinnied', 'imp. & p. p.', 'of Whinny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whinnying', 'p. pr. & vb. n.', 'of Whinny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whinny', 'v. i.', 'To utter the ordinary call or cry of a horse; to neigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whinnies', 'pl. ', 'of Whinny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whinny', 'n.', 'The ordinary cry or call of a horse; a neigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whinny', 'a.', 'Abounding in whin, gorse, or furze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whinock', 'n.', 'The small pig of a litter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whinstone', 'n.', 'A provincial name given in England to basaltic rocks,\n   and applied by miners to other kind of dark-colored unstratified rocks\n   which resist the point of the pick. -- for example, to masses of chert.\n   Whin-dikes, and whin-sills, are names sometimes given to veins or beds\n   of basalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whinyard', 'n.', 'A sword, or hanger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whinyard', 'n.', 'The shoveler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whinyard', 'n.', 'The poachard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipped', 'imp. & p. p.', 'of Whip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipping', 'p. pr. & vb. n.', 'of Whip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'To strike with a lash, a cord, a rod, or anything slender\n   and lithe; to lash; to beat; as, to whip a horse, or a carpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'To drive with lashes or strokes of a whip; to cause to\n   rotate by lashing with a cord; as, to whip a top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'To punish with a whip, scourge, or rod; to flog; to beat;\n   as, to whip a vagrant; to whip one with thirty nine lashes; to whip a\n   perverse boy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'To apply that which hurts keenly to; to lash, as with\n   sarcasm, abuse, or the like; to apply cutting language to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'To thrash; to beat out, as grain, by striking; as, to whip\n   wheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'To beat (eggs, cream, or the like) into a froth, as with a\n   whisk, fork, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'To conquer; to defeat, as in a contest or game; to beat;\n   to surpass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'To overlay (a cord, rope, or the like) with other cords\n   going round and round it; to overcast, as the edge of a seam; to wrap;\n   -- often with about, around, or over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'To sew lightly; specifically, to form (a fabric) into\n   gathers by loosely overcasting the rolled edge and drawing up the\n   thread; as, to whip a ruffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'To take or move by a sudden motion; to jerk; to snatch; --\n   with into, out, up, off, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'To hoist or purchase by means of a whip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'To secure the end of (a rope, or the like) from untwisting\n   by overcasting it with small stuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'To fish (a body of water) with a rod and artificial fly,\n   the motion being that employed in using a whip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. i.', 'To move nimbly; to start or turn suddenly and do\n   something; to whisk; as, he whipped around the corner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'An instrument or driving horses or other animals, or for\n   correction, consisting usually of a lash attached to a handle, or of a\n   handle and lash so combined as to form a flexible rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'A coachman; a driver of a carriage; as, a good whip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'One of the arms or frames of a windmill, on which the\n   sails are spread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'The length of the arm reckoned from the shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'A small tackle with a single rope, used to hoist light\n   bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'The long pennant. See Pennant (a)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'A huntsman who whips in the hounds; whipper-in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'A person (as a member of Parliament) appointed to enforce\n   party discipline, and secure the attendance of the members of a\n   Parliament party at any important session, especially when their votes\n   are needed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip', 'v. t.', 'A call made upon members of a Parliament party to be in\n   their places at a given time, as when a vote is to be taken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipcord', 'n.', 'A kind of hard-twisted or braided cord, sometimes used\n   for making whiplashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipgrafted', 'imp. & p. p.', 'of Whipgraft');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipgrafting', 'p. pr. & vb. n.', 'of Whipgraft');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipgraft', 'v. t.', 'To graft by cutting the scion and stock in a certain\n   manner. See Whip grafting, under Grafting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiplash', 'n.', 'The lash of a whip, -- usually made of thongs of leather,\n   or of cords, braided or twisted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipparee', 'n.', 'A large sting ray (Dasybatis, / Trygon, Sayi) native of\n   the Southern United States. It is destitute of large spines on the body\n   and tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipparee', 'n.', 'A large sting ray (Rhinoptera bonasus, or R. quadriloba)\n   of the Atlantic coast of the United States. Its snout appears to be\n   four-lobed when viewed in front, whence it is also called cow-nosed\n   ray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipper', 'n.', 'One who whips; especially, an officer who inflicts the\n   penalty of legal whipping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipper', 'n.', 'One who raises coal or merchandise with a tackle from a\n   chips hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipper', 'n.', 'A kind of simple willow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipperin', 'n.', 'A huntsman who keeps the hounds from wandering, and\n   whips them in, if necessary, to the of chase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipperin', 'n.', 'Hence, one who enforces the discipline of a party, and\n   urges the attendance and support of the members on all necessary\n   occasions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whippersnapper', 'n.', 'A diminutive, insignificant, or presumptuous\n   person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipping', '', 'a & n. from Whip, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whippletree', 'n.', 'The pivoted or swinging bar to which the traces, or\n   tugs, of a harness are fastened, and by which a carriage, a plow, or\n   other implement or vehicle, is drawn; a whiffletree; a swingletree; a\n   singletree. See Singletree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whippletree', 'n.', 'The cornel tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip-poor-will', 'n.', 'An American bird (Antrostomus vociferus) allied to\n   the nighthawk and goatsucker; -- so called in imitation of the peculiar\n   notes which it utters in the evening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipsaw', 'n.', 'A saw for dividing timber lengthwise, usually set in a\n   frame, and worked by two persons; also, a fret saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip-shaped', 'a.', 'Shaped like the lash of a whip; long, slender, round,\n   and tapering; as, a whip-shaped root or stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipstaff', 'n.', 'A bar attached to the tiller, for convenience in\n   steering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipstalk', 'n.', 'A whipstock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipster', 'n.', 'A nimble little fellow; a whippersnapper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipstick', 'n.', 'Whip handle; whipstock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipstitch', 'n.', 'A tailor; -- so called in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipstitch', 'n.', 'Anything hastily put or stitched together; hence, a\n   hasty composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipstitch', 'n.', 'The act or process of whipstitching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipstitch', 'v. t.', 'To rafter; to plow in ridges, as land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipstock', 'n.', 'The rod or handle to which the lash of a whip is\n   fastened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipt', 'imp. & p. p.', 'Whipped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whip-tom-kelly', 'n.', 'A vireo (Vireo altiloquus) native of the West\n   Indies and Florida; -- called also black-whiskered vireo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whipworm', 'n.', 'A nematode worm (Trichocephalus dispar) often found\n   parasitic in the human intestine. Its body is thickened posteriorly,\n   but is very long and threadlike anteriorly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirred', 'imp. & p. p.', 'of Whir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirring', 'p. pr. & vb. n.', 'of Whir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whir', 'v. i.', 'To whirl round, or revolve, with a whizzing noise; to fly\n   or more quickly with a buzzing or whizzing sound; to whiz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whir', 'v. t.', 'To hurry a long with a whizzing sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whir', 'n.', 'A buzzing or whizzing sound produced by rapid or whirling\n   motion; as, the whir of a partridge; the whir of a spinning wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirled', 'imp. & p. p.', 'of Whirl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirling', 'p. pr. & vb. n.', 'of Whirl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirl', 'v. t.', 'To turn round rapidly; to cause to rotate with velocity;\n   to make to revolve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirl', 'v. t.', 'To remove or carry quickly with, or as with, a revolving\n   motion; to snatch; to harry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirl', 'v. i.', 'To be turned round rapidly; to move round with velocity;\n   to revolve or rotate with great speed; to gyrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirl', 'v. i.', 'To move hastily or swiftly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirl', 'v. t.', 'A turning with rapidity or velocity; rapid rotation or\n   circumvolution; quick gyration; rapid or confusing motion; as, the\n   whirl of a top; the whirl of a wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirl', 'v. t.', 'Anything that moves with a whirling motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirl', 'v. t.', 'A revolving hook used in twisting, as the hooked spindle\n   of a rope machine, to which the threads to be twisted are attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirl', 'v. t.', 'A whorl. See Whorl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirlabout', 'n.', 'Something that whirls or turns about in a rapid manner;\n   a whirligig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirlbat', 'n.', 'Anything moved with a whirl, as preparatory for a blow,\n   or to augment the force of it; -- applied by poets to the cestus of\n   ancient boxers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirl-blast', 'n.', 'A whirling blast or wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirlbone', 'n.', 'The huckle bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirlbone', 'n.', 'The patella, or kneepan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirler', 'n.', 'One who, or that which, whirls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirlicote', 'n.', 'An open car or chariot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirligig', 'n.', 'A childs toy, spun or whirled around like a wheel upon\n   an axis, or like a top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirligig', 'n.', 'Anything which whirls around, or in which persons or\n   things are whirled about, as a frame with seats or wooden horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirligig', 'n.', 'A mediaeval instrument for punishing petty offenders,\n   being a kind of wooden cage turning on a pivot, in which the offender\n   was whirled round with great velocity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirligig', 'n.', 'Any one of numerous species of beetles belonging to\n   Gyrinus and allied genera. The body is firm, oval or boatlike in form,\n   and usually dark colored with a bronzelike luster. These beetles live\n   mostly on the surface of water, and move about with great celerity in a\n   gyrating, or circular, manner, but they are also able to dive and swim\n   rapidly. The larva is aquatic. Called also weaver, whirlwig, and\n   whirlwig beetle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirling', '', 'a. & n. from Whirl, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirlpit', 'n.', 'A whirlpool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirlpool', 'n.', 'An eddy or vortex of water; a place in a body of water\n   where the water moves round in a circle so as to produce a depression\n   or cavity in the center, into which floating objects may be drawn; any\n   body of water having a more or less circular motion caused by its\n   flowing in an irregular channel, by the coming together of opposing\n   currents, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirlpool', 'n.', 'A sea monster of the whale kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirlwig', 'n.', 'A whirligig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirlwind', 'n.', 'A violent windstorm of limited extent, as the tornado,\n   characterized by an inward spiral motion of the air with an upward\n   current in the center; a vortex of air. It usually has a rapid\n   progressive motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirlwind', 'n.', 'Fig.: A body of objects sweeping violently onward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirry', 'v. i.', 'To whir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whirtle', 'n.', 'A perforated steel die through which wires or tubes are\n   drawn to form them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisk', 'n.', 'A game at cards; whist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisk', 'n.', 'The act of whisking; a rapid, sweeping motion, as of\n   something light; a sudden motion or quick puff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisk', 'n.', 'A small bunch of grass, straw, twigs, hair, or the like,\n   used for a brush; hence, a brush or small besom, as of broom corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisk', 'n.', 'A small culinary instrument made of wire, or the like, for\n   whisking or beating eggs, cream, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisk', 'n.', 'A kind of cape, forming part of a womans dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisk', 'n.', 'An impertinent fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisk', 'n.', 'A plane used by coopers for evening chines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisked', 'imp. & p. p.', 'of Whisk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisking', 'p. pr. & vb. n.', 'of Whisk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisk', 'n.', 'To sweep, brush, or agitate, with a light, rapid motion; as,\n   to whisk dust from a table; to whisk the white of eggs into a froth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisk', 'n.', 'To move with a quick, sweeping motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisk', 'v. i.', 'To move nimbly at with velocity; to make a sudden agile\n   movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisker', 'n.', 'One who, or that which, whisks, or moves with a quick,\n   sweeping motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisker', 'n.', 'Formerly, the hair of the upper lip; a mustache; --\n   usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisker', 'n.', 'That part of the beard which grows upon the sides of the\n   face, or upon the chin, or upon both; as, side whiskers; chin whiskers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisker', 'n.', 'A hair of the beard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisker', 'n.', 'One of the long, projecting hairs growing at the sides of\n   the mouth of a cat, or other animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisker', 'n.', 'Iron rods extending on either side of the bowsprit, to\n   spread, or guy out, the stays, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiskered', 'a.', 'Formed into whiskers; furnished with whiskers; having or\n   wearing whiskers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiskered', 'a.', 'Having elongated hairs, feathers, or bristles on the\n   cheeks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiskerless', 'a.', 'Being without whiskers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisket', 'n.', 'A basket; esp., a straw provender basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisket', 'n.', 'A small lathe for turning wooden pins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiskey', 'n.', 'Same as Whisky, a liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiskeys', 'pl. ', 'of Whisky');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiskies', 'pl. ', 'of Whisky');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiskey', 'n.', 'Alt. of Whisky');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisky', 'n.', 'A light carriage built for rapid motion; -- called also\n   tim-whiskey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiskin', 'n.', 'A shallow drinking bowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisking', 'a.', 'Sweeping along lightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisking', 'a.', 'Large; great.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisky', 'n.', 'Alt. of Whiskey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiskey', 'n.', 'An intoxicating liquor distilled from grain, potatoes,\n   etc., especially in Scotland, Ireland, and the United States. In the\n   United States, whisky is generally distilled from maize, rye, or wheat,\n   but in Scotland and Ireland it is often made from malted barley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiskyfied', 'a.', 'Alt. of Whiskeyfied');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiskeyfied', 'a.', 'Drunk with whisky; intoxicated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisp', 'n.', 'See Wisp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisp', 'n.', 'A flock of snipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whispered', 'imp. & p. p.', 'of Whisper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whispering', 'p. pr. & vb. n.', 'of Whisper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisper', 'v. i.', 'To speak softly, or under the breath, so as to be heard\n   only by one near at hand; to utter words without sonant breath; to talk\n   without that vibration in the larynx which gives sonorous, or vocal,\n   sound. See Whisper, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisper', 'n.', 'To make a low, sibilant sound or noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisper', 'n.', 'To speak with suspicion, or timorous caution; to converse\n   in whispers, as in secret plotting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisper', 'v. t.', 'To utter in a low and nonvocal tone; to say under the\n   breath; hence, to mention privately and confidentially, or in a\n   whisper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisper', 'v. t.', 'To address in a whisper, or low voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisper', 'v. t.', 'To prompt secretly or cautiously; to inform privately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisper', 'n.', 'A low, soft, sibilant voice or utterance, which can be\n   heard only by those near at hand; voice or utterance that employs only\n   breath sound without tone, friction against the edges of the vocal\n   cords and arytenoid cartilages taking the place of the vibration of the\n   cords that produces tone; sometimes, in a limited sense, the sound\n   produced by such friction as distinguished from breath sound made by\n   friction against parts of the mouth. See Voice, n., 2, and Guide to\n   Pronunciation, // 5, 153, 154.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisper', 'n.', 'A cautious or timorous speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisper', 'n.', 'Something communicated in secret or by whispering; a\n   suggestion or insinuation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisper', 'n.', 'A low, sibilant sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisperer', 'n.', 'One who whispers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisperer', 'n.', 'A tattler; one who tells secrets; a conveyer of\n   intelligence secretly; hence; a backbiter; one who slanders secretly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whispering', '', 'a. & n. from Whisper. v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisperingly', 'adv.', 'In a whisper, or low voice; in a whispering manner;\n   with whispers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whisperously', 'adv.', 'Whisperingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whist', 'interj.', 'Be silent; be still; hush; silence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whist', 'n.', 'A certain game at cards; -- so called because it requires\n   silence and close attention. It is played by four persons (those who\n   sit opposite each other being partners) with a complete pack of\n   fifty-two cards. Each player has thirteen cards, and when these are\n   played out, he hand is finished, and the cards are again shuffled and\n   distributed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whist', 'v. t.', 'To hush or silence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whist', 'v. i.', 'To be or become silent or still; to be hushed or mute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whist', 'a.', 'Not speaking; not making a noise; silent; mute; still;\n   quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistled', 'imp. & p. p.', 'of Whistle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistling', 'p. pr. & vb. n.', 'of Whistle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistle', 'v. i.', 'To make a kind of musical sound, or series of sounds,\n   by forcing the breath through a small orifice formed by contracting the\n   lips; also, to emit a similar sound, or series of notes, from the mouth\n   or beak, as birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistle', 'v. i.', 'To make a shrill sound with a wind or steam instrument,\n   somewhat like that made with the lips; to blow a sharp, shrill tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistle', 'v. i.', 'To sound shrill, or like a pipe; to make a sharp,\n   shrill sound; as, a bullet whistles through the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistle', 'v. t.', 'To form, utter, or modulate by whistling; as, to\n   whistle a tune or an air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistle', 'v. t.', 'To send, signal, or call by a whistle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistle', 'v. i.', 'A sharp, shrill, more or less musical sound, made by\n   forcing the breath through a small orifice of the lips, or through or\n   instrument which gives a similar sound; the sound used by a sportsman\n   in calling his dogs; the shrill note of a bird; as, the sharp whistle\n   of a boy, or of a boatswains pipe; the blackbirds mellow whistle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistle', 'v. i.', 'The shrill sound made by wind passing among trees or\n   through crevices, or that made by bullet, or the like, passing rapidly\n   through the air; the shrill noise (much used as a signal, etc.) made by\n   steam or gas escaping through a small orifice, or impinging against the\n   edge of a metallic bell or cup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistle', 'v. i.', 'An instrument in which gas or steam forced into a\n   cavity, or against a thin edge, produces a sound more or less like that\n   made by one who whistles through the compressed lips; as, a childs\n   whistle; a boatswains whistle; a steam whistle (see Steam whistle,\n   under Steam).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistle', 'v. i.', 'The mouth and throat; -- so called as being the organs\n   of whistling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistlefish', 'n.', 'A gossat, or rockling; -- called also whistler,\n   three-bearded rockling, sea loach, and sorghe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistler', 'n.', 'One who, or that which, whistles, or produces or a\n   whistling sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistler', 'n.', 'The ring ousel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistler', 'n.', 'The widgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistler', 'n.', 'The golden-eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistler', 'n.', 'The golden plover and the gray plover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistler', 'n.', 'The hoary, or northern, marmot (Arctomys pruinosus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistler', 'n.', 'The whistlefish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistlewing', 'n.', 'The American golden-eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistlewood', 'n.', 'The moosewood, or striped maple. See Maple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistling', '', 'a. & n. from Whistle, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistlingly', 'adv.', 'In a whistling manner; shrilly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whistly', 'adv.', 'In a whist manner; silently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whit', 'n.', 'The smallest part or particle imaginable; a bit; a jot; an\n   iota; -- generally used in an adverbial phrase in a negative sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White', 'superl.', 'Reflecting to the eye all the rays of the spectrum\n   combined; not tinted with any of the proper colors or their mixtures;\n   having the color of pure snow; snowy; -- the opposite of black or dark;\n   as, white paper; a white skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White', 'superl.', 'Destitute of color, as in the cheeks, or of the tinge\n   of blood color; pale; pallid; as, white with fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White', 'superl.', 'Having the color of purity; free from spot or blemish,\n   or from guilt or pollution; innocent; pure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White', 'superl.', 'Gray, as from age; having silvery hair; hoary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White', 'superl.', 'Characterized by freedom from that which disturbs, and\n   the like; fortunate; happy; favorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White', 'superl.', 'Regarded with especial favor; favorite; darling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White', 'n.', 'The color of pure snow; one of the natural colors of bodies,\n   yet not strictly a color, but a composition of all colors; the opposite\n   of black; whiteness. See the Note under Color, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White', 'n.', 'Something having the color of snow; something white, or\n   nearly so; as, the white of the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White', 'n.', 'Specifically, the central part of the butt in archery, which\n   was formerly painted white; the center of a mark at which a missile is\n   shot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White', 'n.', 'A person with a white skin; a member of the white, or\n   Caucasian, races of men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White', 'n.', 'A white pigment; as, Venice white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White', 'n.', 'Any one of numerous species of butterflies belonging to\n   Pieris, and allied genera in which the color is usually white. See\n   Cabbage butterfly, under Cabbage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whited', 'imp. & p. p.', 'of White');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiting', 'p. pr. & vb. n.', 'of White');
  INSERT INTO words (word, word_type, description) VALUES  (    'White', 'v. t.', 'To make white; to whiten; to whitewash; to bleach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiteback', 'n.', 'The canvasback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitebait', 'n.', 'The young of several species of herrings, especially of\n   the common herring, esteemed a great delicacy by epicures in England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitebait', 'n.', 'A small translucent fish (Salanx Chinensis) abundant at\n   certain seasons on the coasts of China and Japan, and used in the same\n   manner as the European whitebait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitebeam', 'n.', 'The common beam tree of England (Pyrus Aria); -- so\n   called from the white, woolly under surface of the leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitebeard', 'n.', 'An old man; a graybeard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitebelly', 'n.', 'The American widgeon, or baldpate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitebelly', 'n.', 'The prairie chicken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitebill', 'n.', 'The American coot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White-blaze', 'n.', 'See White-face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiteblow', 'n.', 'Same as Whitlow grass, under Whitlow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiteboy', 'n.', 'A favorite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiteboy', 'a.', 'One of an association of poor Roman catholics which arose\n   in Ireland about 1760, ostensibly to resist the collection of tithes,\n   the members of which were so called from the white shirts they wore in\n   their nocturnal raids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiteboyism', 'n.', 'The conduct or principle of the Whiteboys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitecap', 'n.', 'The European redstart; -- so called from its white\n   forehead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitecap', 'n.', 'The whitethroat; -- so called from its gray head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitecap', 'n.', 'The European tree sparrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitecap', 'n.', 'A wave whose crest breaks into white foam, as when the\n   wind is freshening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitecoat', 'n.', 'The skin of a newborn seal; also, the seal itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White-ear', 'n.', 'The wheatear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White-eye', 'n.', 'Any one of several species of small Old World singing of\n   the genus Zosterops, as Zosterops palpebrosus of India, and Z.\n   c/rulescens of Australia. The eyes are encircled by a ring of white\n   feathers, whence the name. Called also bush creeper, and white-eyed\n   tit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White-face', 'n.', 'A white mark in the forehead of a horse, descending\n   almost to the nose; -- called also white-blaze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitefish', 'n.', 'Any one of several species of Coregonus, a genus of\n   excellent food fishes allied to the salmons. They inhabit the lakes of\n   the colder parts of North America, Asia, and Europe. The largest and\n   most important American species (C. clupeiformis) is abundant in the\n   Great Lakes, and in other lakes farther north. Called also lake\n   whitefish, and Oswego bass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitefish', 'n.', 'The menhaden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitefish', 'n.', 'The beluga, or white whale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiteflaw', 'n.', 'A whitlow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White-foot', 'n.', 'A white mark on the foot of a horse, between the\n   fetlock and the coffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White friar', '', 'A mendicant monk of the Carmelite order, so called from\n   the white cloaks worn by the order. See Carmelite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White-fronted', 'a.', 'Having a white front; as, the white-fronted lemur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitehead', 'n.', 'The blue-winged snow goose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitehead', 'n.', 'The surf scoter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White-heart', 'n.', 'A somewhat heart-shaped cherry with a whitish skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White-hot', 'a.', 'White with heat; heated to whiteness, or incandescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White-limed', 'a.', 'Whitewashed or plastered with lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White-livered', 'a.', 'Having a pale look; feeble; hence, cowardly;\n   pusillanimous; dastardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitely', 'a.', 'Like, or coming near to, white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitened', 'imp. & p. p.', 'of Whiten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitening', 'p. pr. & vb. n.', 'of Whiten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiten', 'v. i.', 'To grow white; to turn or become white or whiter; as,\n   the hair whitens with age; the sea whitens with foam; the trees in\n   spring whiten with blossoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiten', 'v. t.', 'To make white; to bleach; to blanch; to whitewash; as,\n   to whiten a wall; to whiten cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitener', 'n.', 'One who, or that which, whitens; a bleacher; a blancher;\n   a whitewasher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiteness', 'n.', 'The quality or state of being white; white color, or\n   freedom from darkness or obscurity on the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiteness', 'n.', 'Want of a sanguineous tinge; paleness; as from terror,\n   grief, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiteness', 'n.', 'Freedom from stain or blemish; purity; cleanness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiteness', 'n.', 'Nakedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiteness', 'n.', 'A flock of swans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitening', 'n.', 'The act or process of making or becoming white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitening', 'n.', 'That which is used to render white; whiting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White-pot', 'n.', 'A kind of food made of milk or cream, eggs, sugar,\n   bread, etc., baked in a pot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiterump', 'n.', 'The American black-tailed godwit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whites', 'n. pl.', 'Leucorrh/a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whites', 'n. pl.', 'The finest flour made from white wheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whites', 'n. pl.', 'Cloth or garments of a plain white color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiteside', 'n.', 'The golden-eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitesmith', 'n.', 'One who works in tinned or galvanized iron, or white\n   iron; a tinsmith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitesmith', 'n.', 'A worker in iron who finishes or polishes the work, in\n   distinction from one who forges it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitester', 'n.', 'A bleacher of linen; a whitener; a whitster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitetail', 'n.', 'The Virginia deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitetail', 'n.', 'The wheatear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitethorn', 'n.', 'The hawthorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitethroat', 'n.', 'Any one of several species of Old World warblers, esp.\n   the common European species (Sylvia cinerea), called also strawsmear,\n   nettlebird, muff, and whitecap, the garden whitethroat, or golden\n   warbler (S. hortensis), and the lesser whitethroat (S. curruca).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitetop', 'n.', 'Fiorin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitewall', 'n.', 'The spotted flycatcher; -- so called from the white\n   color of the under parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitewash', 'n.', 'Any wash or liquid composition for whitening something,\n   as a wash for making the skin fair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitewash', 'n.', 'A composition of line and water, or of whiting size, and\n   water, or the like, used for whitening walls, ceilings, etc.; milk of\n   lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitewashed', 'imp. & p. p.', 'of Whitewash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitewashing', 'p. pr. & vb. n.', 'of Whitewash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitewash', 'v. t.', 'To apply a white liquid composition to; to whiten\n   with whitewash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitewash', 'v. t.', 'To make white; to give a fair external appearance to;\n   to clear from imputations or disgrace; hence, to clear (a bankrupt)\n   from obligation to pay debts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitewasher', 'n.', 'One who whitewashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'White-water', 'n.', 'A dangerous disease of sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiteweed', 'n.', 'A perennial composite herb (Chrysanthemum Leucanthemum)\n   with conspicuous white rays and a yellow disk, a common weed in grass\n   lands and pastures; -- called also oxeye daisy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitewing', 'n.', 'The chaffinch; -- so called from the white bands on the\n   wing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitewing', 'n.', 'The velvet duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitewood', 'n.', 'The soft and easily-worked wood of the tulip tree\n   (Liriodendron). It is much used in cabinetwork, carriage building, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitewort', 'n.', 'Wild camomile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitewort', 'n.', 'A kind of Solomons seal (Polygonum officinale).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitflaw', 'n.', 'Whitlow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whither', 'adv.', 'To what place; -- used interrogatively; as, whither\n   goest thou?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whither', 'adv.', 'To what or which place; -- used relatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whither', 'adv.', 'To what point, degree, end, conclusion, or design;\n   whereunto; whereto; -- used in a sense not physical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whithersoever', 'adv.', 'To whatever place; to what place soever;\n   wheresoever; as, I will go whithersoever you lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitherward', 'adv.', 'In what direction; toward what or which place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitile', 'v.', 'The yaffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiting', 'n.', 'A common European food fish (Melangus vulgaris) of the\n   Codfish family; -- called also fittin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiting', 'n.', 'A North American fish (Merlucius vulgaris) allied to the\n   preceding; -- called also silver hake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiting', 'n.', 'Any one of several species of North American marine\n   sciaenoid food fishes belonging to genus Menticirrhus, especially M.\n   Americanus, found from Maryland to Brazil, and M. littoralis, common\n   from Virginia to Texas; -- called also silver whiting, and surf\n   whiting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiting', 'n.', 'Chalk prepared in an impalpable powder by pulverizing and\n   repeated washing, used as a pigment, as an ingredient in putty, for\n   cleaning silver, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiting-mop', 'n.', 'A young whiting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiting-mop', 'n.', 'A fair lass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitish', 'a.', 'Somewhat white; approaching white; white in a moderate\n   degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitish', 'a.', 'Covered with an opaque white powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitishness', 'n.', 'The quality or state of being whitish or somewhat\n   white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitleather', 'n.', 'Leather dressed or tawed with alum, salt, etc.,\n   remarkable for its pliability and toughness; white leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitleather', 'n.', 'The paxwax. See Paxwax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitling', 'n.', 'A young full trout during its second season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitlow', 'a.', 'An inflammation of the fingers or toes, generally of the\n   last phalanx, terminating usually in suppuration. The inflammation may\n   occupy any seat between the skin and the bone, but is usually applied\n   to a felon or inflammation of the periosteal structures of the bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitlow', 'a.', 'An inflammatory disease of the feet. It occurs round the\n   hoof, where an acrid matter is collected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitlow-wort', 'n.', 'Same as Whitlow grass, under Whitlow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitmonday', 'n.', 'The day following Whitsunday; -- called also Whitsun\n   Monday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitneyite', 'n.', 'an arsenide of copper from Lake Superior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitson', 'a.', 'See Whitsun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitsour', 'n.', 'A sort of apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitster', 'n.', 'A whitener; a bleacher; a whitester.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitsun', 'a.', 'Of, pertaining to, or observed at, Whitsuntide; as,\n   Whitsun week; Whitsun Tuesday; Whitsun pastorals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitsunday', 'n.', 'The seventh Sunday, and the fiftieth day, after Easter;\n   a festival of the church in commemoration of the descent of the Holy\n   Spirit on the day of Pentecost; Pentecost; -- so called, it is said,\n   because, in the primitive church, those who had been newly baptized\n   appeared at church between Easter and Pentecost in white garments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitsunday', 'n.', 'See the Note under Term, n., 12.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitsuntide', 'n.', 'The week commencing with Whitsunday, esp. the first\n   three days -- Whitsunday, Whitsun Monday, and Whitsun Tuesday; the time\n   of Pentecost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitten tree', '', 'Either of two shrubs (Viburnum Lantana, and V. Opulus),\n   so called on account of their whitish branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitterick', 'n.', 'The curlew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whittle', 'n.', 'A grayish, coarse double blanket worn by countrywomen, in\n   the west of England, over the shoulders, like a cloak or shawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whittle', 'n.', 'Same as Whittle shawl, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whittle', 'n.', 'A knife; esp., a pocket, sheath, or clasp knife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whittled', 'imp. & p. p.', 'of Whittle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whittling', 'p. pr. & vb. n.', 'of Whittle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whittle', 'v. t.', 'To pare or cut off the surface of with a small knife;\n   to cut or shape, as a piece of wood held in the hand, with a clasp\n   knife or pocketknife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whittle', 'v. t.', 'To edge; to sharpen; to render eager or excited; esp.,\n   to excite with liquor; to inebriate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whittle', 'v. i.', 'To cut or shape a piece of wood with am small knife; to\n   cut up a piece of wood with a knife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whittlings', 'n. pl.', 'Chips made by one who whittles; shavings cut from a\n   stick with a knife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whittret', 'n.', 'A weasel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whittuesday', 'n.', 'The day following Whitmonday; -- called also Whitsun\n   Tuesday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitwall', 'n.', 'Same as Whetile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitworth ball', '', 'A prejectile used in the Whitworth gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whitworth gun', '', 'A form of rifled cannon and small arms invented by Sir\n   Joseph Whitworth, of Manchester, England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whity-brown', 'a.', 'Of a color between white and brown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whizzed', 'imp. & p. p.', 'of Whiz');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whizzing', 'p. pr. & vb. n.', 'of Whiz');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiz', 'v. i.', 'To make a humming or hissing sound, like an arrow or ball\n   flying through the air; to fly or move swiftly with a sharp hissing or\n   whistling sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whiz', 'n.', 'A hissing and humming sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whizzingly', 'adv.', 'With a whizzing sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Who', 'object.', 'Originally, an interrogative pronoun, later, a relative\n   pronoun also; -- used always substantively, and either as singular or\n   plural. See the Note under What, pron., 1. As interrogative pronouns,\n   who and whom ask the question: What or which person or persons? Who and\n   whom, as relative pronouns (in the sense of that), are properly used of\n   persons (corresponding to which, as applied to things), but are\n   sometimes, less properly and now rarely, used of animals, plants, etc.\n   Who and whom, as compound relatives, are also used especially of\n   persons, meaning the person that; the persons that; the one that;\n   whosoever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Who', 'pron.', 'One; any; one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whoa', 'interj.', 'Stop; stand; hold. See Ho, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whobub', 'n.', 'Hubbub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whoever', 'pron.', 'Whatever person; any person who; be or she who; any one\n   who; as, he shall be punished, whoever he may be.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whole', 'a.', 'Containing the total amount, number, etc.; comprising all\n   the parts; free from deficiency; all; total; entire; as, the whole\n   earth; the whole solar system; the whole army; the whole nation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whole', 'a.', 'Complete; entire; not defective or imperfect; not broken or\n   fractured; unimpaired; uninjured; integral; as, a whole orange; the egg\n   is whole; the vessel is whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whole', 'a.', 'Possessing, or being in a state of, heath and soundness;\n   healthy; sound; well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whole', 'n.', 'The entire thing; the entire assemblage of parts; totality;\n   all of a thing, without defect or exception; a thing complete in\n   itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whole', 'n.', 'A regular combination of parts; a system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whole-hoofed', 'a.', 'Having an undivided hoof, as the horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whole-length', 'a.', 'Representing the whole figure; -- said of a picture\n   or statue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whole-length', 'n.', 'A portrait or statue representing the whole figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wholeness', 'n.', 'The quality or state of being whole, entire, or sound;\n   entireness; totality; completeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wholesale', 'n.', 'Sale of goods by the piece or large quantity, as\n   distinguished from retail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wholesale', 'a.', 'Pertaining to, or engaged in, trade by the piece or\n   large quantity; selling to retailers or jobbers rather than to\n   consumers; as, a wholesale merchant; the wholesale price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wholesale', 'a.', 'Extensive and indiscriminate; as, wholesale slaughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wholesome', 'superl.', 'Tending to promote health; favoring health;\n   salubrious; salutary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wholesome', 'superl.', 'Contributing to the health of the mind; favorable\n   to morals, religion, or prosperity; conducive to good; salutary; sound;\n   as, wholesome advice; wholesome doctrines; wholesome truths; wholesome\n   laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wholesome', 'superl.', 'Sound; healthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whole-souled', 'a.', 'Thoroughly imbued with a right spirit; noble-minded;\n   devoted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wholly', 'adv.', 'In a whole or complete manner; entirely; completely;\n   perfectly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wholly', 'adv.', 'To the exclusion of other things; totally; fully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whom', 'pron.', 'The objective case of who. See Who.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whomsoever', 'pron.', 'The objective of whosoever. See Whosoever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whoobub', 'n.', 'Hubbub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whoop', 'n.', 'The hoopoe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whooped', 'imp. & p. p.', 'of Whoop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whooping', 'p. pr. & vb. n.', 'of Whoop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whoop', 'v. i.', 'To utter a whoop, or loud cry, as eagerness, enthusiasm,\n   or enjoyment; to cry out; to shout; to halloo; to utter a war whoop; to\n   hoot, as an owl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whoop', 'v. i.', 'To cough or breathe with a sonorous inspiration, as in\n   whooping cough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whoop', 'v. t.', 'To insult with shouts; to chase with derision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whoop', 'n.', 'A shout of pursuit or of war; a very of eagerness,\n   enthusiasm, enjoyment, vengeance, terror, or the like; an halloo; a\n   hoot, or cry, as of an owl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whoop', 'n.', 'A loud, shrill, prolonged sound or sonorous inspiration, as\n   in whooping cough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whooper', 'n.', 'One who, or that which, whooops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whooping', '', 'a. & n. from Whoop, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whoot', 'v. i.', 'To hoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whop', 'v. t.', 'Same as Whap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whop', 'n.', 'Same as Whap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whopper', 'n.', 'One who, or that which, whops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whopper', 'n.', 'Same as Whapper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whore', 'n.', 'A woman who practices unlawful sexual commerce with men,\n   especially one who prostitutes her body for hire; a prostitute; a\n   harlot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whored', 'imp. & p. p.', 'of Whore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whoring', 'p. pr. & vb. n.', 'of Whore');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whore', 'n.', 'To have unlawful sexual intercourse; to practice lewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whore', 'n.', 'To worship false and impure gods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whore', 'v. t.', 'To corrupt by lewd intercourse; to make a whore of; to\n   debauch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whoredom', 'n.', 'The practice of unlawful intercourse with the other sex;\n   fornication; lewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whoredom', 'n.', 'The sin of worshiping idols; idolatry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheremaster', 'n.', 'A man who practices lewdness; a lecher; a whoremonger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wheremaster', 'n.', 'One keeps or procures whores for others; a pimp; a\n   procurer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whoremasterly', 'a.', 'Having the character of a whoremaster; lecherous;\n   libidinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whoremonger', 'n.', 'A whoremaster; a lecher; a man who frequents the\n   society of whores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whoreson', 'n.', 'A bastard; colloquially, a low, scurvy fellow; -- used\n   generally in contempt, or in coarse humor. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whorish', 'a.', 'Resembling a whore in character or conduct; addicted to\n   unlawful pleasures; incontinent; lewd; unchaste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whorl', 'n. & v.', 'A circle of two or more leaves, flowers, or other\n   organs, about the same part or joint of a stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whorl', 'n. & v.', 'A volution, or turn, of the spire of a univalve shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whorl', 'n. & v.', 'The fly of a spindle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whorled', 'a.', 'Furnished with whorls; arranged in the form of a whorl or\n   whorls; verticillate; as, whorled leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whorler', 'n.', 'A potters wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whort', 'n.', 'The whortleberry, or bilberry. See Whortleberry (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whortle', 'n.', 'The whortleberry, or bilberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whortleberry', 'n.', 'In England, the fruit of Vaccinium Myrtillus; also,\n   the plant itself. See Bilberry, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whortleberry', 'n.', 'The fruit of several shrubby plants of the genus\n   Gaylussacia; also, any one of these plants. See Huckleberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whose', 'pron.', 'The possessive case of who or which. See Who, and Which.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whosesoever', 'pron.', 'The possessive of whosoever. See Whosoever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whoso', 'pron.', 'Whosoever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whosoever', 'pron.', 'Whatsoever person; any person whatever that; whoever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whot', 'a.', 'Hot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whur', 'v. i.', 'To make a rough, humming sound, like one who pronounces\n   the letter r with too much force; to whir; to birr.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whur', 'v. i.', 'To snarl or growl, as a dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whur', 'n.', 'A humming or whirring sound, like that of a body moving\n   through the air with velocity; a whir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whurry', 'v. t.', 'To whisk along quickly; to hurry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whurt', 'n.', 'See Whort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Why', 'adv.', 'For what cause, reason, or purpose; on what account;\n   wherefore; -- used interrogatively. See the Note under What, pron., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Why', 'adv.', 'For which; on account of which; -- used relatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Why', 'adv.', 'The reason or cause for which; that on account of which; on\n   what account; as, I know not why he left town so suddenly; -- used as a\n   compound relative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Why', 'n.', 'A young heifer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whydah bird', '', 'Alt. of Whydah finch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Whydah finch', '', 'The whidah bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Why-not', 'n.', 'A violent and peremptory procedure without any assigned\n   reason; a sudden conclusive happening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wich', 'n.', 'A variant of 1st Wick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wichitas', 'n. pl.', 'A tribe of Indians native of the region between the\n   Arkansas and Red rivers. They are related to the Pawnees. See Pawnees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wick', 'n.', 'Alt. of Wich');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wich', 'n.', 'A street; a village; a castle; a dwelling; a place of work,\n   or exercise of authority; -- now obsolete except in composition; as,\n   bailiwick, Warwick, Greenwick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wich', 'n.', 'A narrow port or passage in the rink or course, flanked by\n   the stones of previous players.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wick', 'n.', 'A bundle of fibers, or a loosely twisted or braided cord,\n   tape, or tube, usually made of soft spun cotton threads, which by\n   capillary attraction draws up a steady supply of the oil in lamps, the\n   melted tallow or wax in candles, or other material used for\n   illumination, in small successive portions, to be burned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wick', 'v. i.', 'To strike a stone in an oblique direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wicke', 'a.', 'Wicked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wicked', 'a.', 'Having a wick; -- used chiefly in composition; as, a\n   two-wicked lamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wicked', 'a.', 'Evil in principle or practice; deviating from morality;\n   contrary to the moral or divine law; addicted to vice or sin; sinful;\n   immoral; profligate; -- said of persons and things; as, a wicked king;\n   a wicked woman; a wicked deed; wicked designs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wicked', 'a.', 'Cursed; baneful; hurtful; bad; pernicious; dangerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wicked', 'a.', 'Ludicrously or sportively mischievous; disposed to\n   mischief; roguish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wickedly', 'adv.', 'In a wicked manner; in a manner, or with motives and\n   designs, contrary to the divine law or the law of morality; viciously;\n   corruptly; immorally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wickedness', 'n.', 'The quality or state of being wicked; departure from\n   the rules of the divine or the moral law; evil disposition or\n   practices; immorality; depravity; sinfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wickedness', 'n.', 'A wicked thing or act; crime; sin; iniquity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wicken tree', '', 'Same as Quicken tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wicker', 'n.', 'A small pliant twig or osier; a rod for making basketwork\n   and the like; a withe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wicker', 'n.', 'Wickerwork; a piece of wickerwork, esp. a basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wicker', 'n.', 'Same as 1st Wike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wicker', 'a.', 'Made of, or covered with, twigs or osiers, or wickerwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wickered', 'a.', 'Made of, secured by, or covered with, wickers or\n   wickerwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wickerwork', 'n.', 'A texture of osiers, twigs, or rods; articles made of\n   such a texture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wicket', 'n.', 'A small gate or door, especially one forming part of, or\n   placed near, a larger door or gate; a narrow opening or entrance cut in\n   or beside a door or gate, or the door which is used to close such\n   entrance or aperture. Piers Plowman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wicket', 'n.', 'A small gate by which the chamber of canal locks is\n   emptied, or by which the amount of water passing to a water wheel is\n   regulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wicket', 'n.', 'A small framework at which the ball is bowled. It consists\n   of three rods, or stumps, set vertically in the ground, with one or two\n   short rods, called bails, lying horizontally across the top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wicket', 'n.', 'The ground on which the wickets are set.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wicket', 'n.', 'A place of shelter made of the boughs of trees, -- used by\n   lumbermen, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wicket', 'n.', 'The space between the pillars, in postand-stall working.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wicking', 'n.', 'the material of which wicks are made; esp., a loosely\n   braided or twisted cord or tape of cotton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiclifite', 'n.', 'Alt. of Wickliffite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wickliffite', 'n.', 'See Wyclifite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wicopy', 'n.', 'See Leatherwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widdy', 'n.', 'A rope or halter made of flexible twigs, or withes, as of\n   birch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wide', 'superl.', 'Having considerable distance or extent between the\n   sides; spacious across; much extended in a direction at right angles to\n   that of length; not narrow; broad; as, wide cloth; a wide table; a wide\n   highway; a wide bed; a wide hall or entry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wide', 'superl.', 'Having a great extent every way; extended; spacious;\n   broad; vast; extensive; as, a wide plain; the wide ocean; a wide\n   difference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wide', 'superl.', 'Of large scope; comprehensive; liberal; broad; as, wide\n   views; a wide understanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wide', 'superl.', 'Of a certain measure between the sides; measuring in a\n   direction at right angles to that of length; as, a table three feet\n   wide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wide', 'superl.', 'Remote; distant; far.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wide', 'superl.', 'Far from truth, from propriety, from necessity, or the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wide', 'superl.', 'On one side or the other of the mark; too far side-wise\n   from the mark, the wicket, the batsman, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wide', 'superl.', 'Made, as a vowel, with a less tense, and more open and\n   relaxed, condition of the mouth organs; -- opposed to primary as used\n   by Mr. Bell, and to narrow as used by Mr. Sweet. The effect, as\n   explained by Mr. Bell, is due to the relaxation or tension of the\n   pharynx; as explained by Mr. Sweet and others, it is due to the action\n   of the tongue. The wide of / (/ve) is / (/ll); of a (ate) is / (/nd),\n   etc. See Guide to Pronunciation, / 13-15.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wide', 'adv.', 'To a distance; far; widely; to a great distance or extent;\n   as, his fame was spread wide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wide', 'adv.', 'So as to leave or have a great space between the sides; so\n   as to form a large opening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wide', 'adv.', 'So as to be or strike far from, or on one side of, an\n   object or purpose; aside; astray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wide', 'n.', 'That which is wide; wide space; width; extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wide', 'n.', 'That which goes wide, or to one side of the mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wide-awake', 'a.', 'Fully awake; not drowsy or dull; hence, knowing; keen;\n   alert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wide-awake', 'n.', 'A broad-brimmed, low-crowned felt hat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widegap', 'n.', 'The angler; -- called also widegab, and widegut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widely', 'adv.', 'In a wide manner; to a wide degree or extent; far;\n   extensively; as, the gospel was widely disseminated by the apostles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widely', 'adv.', 'Very much; to a great degree or extent; as, to differ\n   widely in opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widened', 'imp. & p. p.', 'of Widen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widening', 'p. pr. & vb. n.', 'of Widen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widen', 'v. t.', 'To make wide or wider; to extend in breadth; to increase\n   the width of; as, to widen a field; to widen a breach; to widen a\n   stocking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widen', 'v. i.', 'To grow wide or wider; to enlarge; to spread; to extend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wideness', 'n.', 'The quality or state of being wide; breadth; width; great\n   extent from side to side; as, the wideness of a room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wideness', 'n.', 'Large extent in all directions; broadness; greatness; as,\n   the wideness of the sea or ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widespread', 'a.', 'Spread to a great distance; widely extended; extending\n   far and wide; as, widespread wings; a widespread movement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widewhere', 'adv.', 'Widely; far and wide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widgeon', 'n.', 'Any one of several species of fresh-water ducks,\n   especially those belonging to the subgenus Mareca, of the genus Anas.\n   The common European widgeon (Anas penelope) and the American widgeon\n   (A. Americana) are the most important species. The latter is called\n   also baldhead, baldpate, baldface, baldcrown, smoking duck, wheat,\n   duck, and whitebelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widish', 'a.', 'Moderately wide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widmanstatten figures', '', 'Certain figures appearing on etched meteoric\n   iron; -- so called after A. B. Widmanstatten, of Vienna, who first\n   described them in 1808. See the Note and Illust. under Meteorite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widow', 'n.', 'A woman who has lost her husband by death, and has not\n   married again; one living bereaved of a husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widow', 'a.', 'Widowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widowed', 'imp. & p. p.', 'of Widow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widowing', 'p. pr. & vb. n.', 'of Widow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widow', 'v. t.', 'To reduce to the condition of a widow; to bereave of a\n   husband; -- rarely used except in the past participle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widow', 'v. t.', 'To deprive of one who is loved; to strip of anything\n   beloved or highly esteemed; to make desolate or bare; to bereave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widow', 'v. t.', 'To endow with a widows right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widow', 'v. t.', 'To become, or survive as, the widow of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widow bird', '', 'See Whidan bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widower', 'n.', 'A man who has lost his wife by death, and has not married\n   again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widowerhood', 'n.', 'The state of being a widower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widowhood', 'n.', 'The state of being a widow; the time during which a\n   woman is widow; also, rarely, the state of being a widower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widowhood', 'n.', 'Estate settled on a widow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widow-hunter', 'n.', 'One who courts widows, seeking to marry one with a\n   fortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widowly', 'a.', 'Becoming or like a widow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widow-maker', 'n.', 'One who makes widows by destroying husbands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widow-wail', 'n.', 'A low, narrowleaved evergreen shrub (Cneorum tricoccon)\n   found in Southern Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Width', 'n.', 'The quality of being wide; extent from side to side;\n   breadth; wideness; as, the width of cloth; the width of a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widual', 'a.', 'Of or pertaining to a widow; vidual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Widwe', 'n.', 'A widow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wielded', 'imp. & p. p.', 'of Wield');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wielding', 'p. pr. & vb. n.', 'of Wield');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wield', 'v. t.', 'To govern; to rule; to keep, or have in charge; also, to\n   possess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wield', 'v. t.', 'To direct or regulate by influence or authority; to\n   manage; to control; to sway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wield', 'v. t.', 'To use with full command or power, as a thing not too\n   heavy for the holder; to manage; to handle; hence, to use or employ;\n   as, to wield a sword; to wield the scepter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wieldable', 'a.', 'Capable of being wielded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wieldance', 'n.', 'The act or power of wielding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wielder', 'n.', 'One who wields or employs; a manager; a controller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wielding', 'n.', 'Power; authority; rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wieldless', 'a.', 'Not to be wielded; unmanageable; unwieldy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wieldsome', 'a.', 'Admitting of being easily wielded or managed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wieldy', 'a.', 'Capable of being wielded; manageable; wieldable; -- opposed\n   to unwieldy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wier', 'n.', 'Same as Weir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wierangle', 'n.', 'Same as Wariangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiery', 'a.', 'Wet; moist; marshy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiery', 'a.', 'Wiry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wives', 'pl. ', 'of Wife');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wife', 'n.', 'A woman; an adult female; -- now used in literature only in\n   certain compounds and phrases, as alewife, fishwife, goodwife, and the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wife', 'n.', 'The lawful consort of a man; a woman who is united to a man\n   in wedlock; a woman who has a husband; a married woman; -- correlative\n   of husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wifehood', 'n.', 'Womanhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wifehood', 'n.', 'The state of being a wife; the character of a wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wifeless', 'a.', 'Without a wife; unmarried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wifelike', 'a.', 'Of, pertaining to, or like, a wife or a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wifely', 'a.', 'Becoming or life; of or pertaining to a wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wig', 'n.', 'A covering for the head, consisting of hair interwoven or\n   united by a kind of network, either in imitation of the natural growth,\n   or in abundant and flowing curls, worn to supply a deficiency of\n   natural hair, or for ornament, or according to traditional usage, as a\n   part of an official or professional dress, the latter especially in\n   England by judges and barristers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wig', 'n.', 'An old seal; -- so called by fishermen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wigged', 'imp. & p. p.', 'of Wig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wigging', 'p. pr. & vb. n.', 'of Wig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wig', 'v. t.', 'To censure or rebuke; to hold up to reprobation; to scold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wigan', 'n.', 'A kind of canvaslike cotton fabric, used to stiffen and\n   protect the lower part of trousers and of the skirts of womens\n   dresses, etc.; -- so called from Wigan, the name of a town in\n   Lancashire, England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wigeon', 'n.', 'A widgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wigg', 'n.', 'Alt. of Wig');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wig', 'n.', 'A kind of raised seedcake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wigged', 'a.', 'Having the head covered with a wig; wearing a wig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiggery', 'n.', 'A wig or wigs; false hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiggery', 'n.', 'Any cover or screen, as red-tapism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiggle', 'v. t.', 'To move to and fro with a quick, jerking motion; to bend\n   rapidly, or with a wavering motion, from side to side; to wag; to\n   squirm; to wriggle; as, the dog wiggles his tail; the tadpole wiggles\n   in the water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiggle', 'n.', 'Act of wiggling; a wriggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiggler', 'n.', 'The young, either larva or pupa, of the mosquito; --\n   called also wiggletail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wigher', 'v. i.', 'To neigh; to whinny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wight', 'n.', 'Weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wight', 'n.', 'A whit; a bit; a jot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wight', 'n.', 'A supernatural being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wight', 'n.', 'A human being; a person, either male or female; -- now used\n   chiefly in irony or burlesque, or in humorous language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wight', 'a.', 'Swift; nimble; agile; strong and active.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wightly', 'adv.', 'Swiftly; nimbly; quickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wigless', 'a.', 'Having or wearing no wig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wigwag', 'v. t.', 'To signal by means of a flag waved from side to side\n   according to a code adopted for the purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wigwam', 'n.', 'An Indian cabin or hut, usually of a conical form, and made\n   of a framework of poles covered with hides, bark, or mats; -- called\n   also tepee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wike', 'n.', 'A temporary mark or boundary, as a bough of a tree set up in\n   marking out or dividing anything, as tithes, swaths to be mowed in\n   common ground, etc.; -- called also wicker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wike', 'n.', 'A home; a dwelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wikke', 'a.', 'Wicked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wild', 'superl.', 'Living in a state of nature; inhabiting natural haunts,\n   as the forest or open field; not familiar with, or not easily\n   approached by, man; not tamed or domesticated; as, a wild boar; a wild\n   ox; a wild cat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wild', 'superl.', 'Growing or produced without culture; growing or prepared\n   without the aid and care of man; native; not cultivated; brought forth\n   by unassisted nature or by animals not domesticated; as, wild parsnip,\n   wild camomile, wild strawberry, wild honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wild', 'superl.', 'Desert; not inhabited or cultivated; as, wild land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wild', 'superl.', 'Savage; uncivilized; not refined by culture; ferocious;\n   rude; as, wild natives of Africa or America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wild', 'superl.', 'Not submitted to restraint, training, or regulation;\n   turbulent; tempestuous; violent; ungoverned; licentious; inordinate;\n   disorderly; irregular; fanciful; imaginary; visionary; crazy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wild', 'superl.', 'Exposed to the wind and sea; unsheltered; as, a wild\n   roadstead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wild', 'superl.', 'Indicating strong emotion, intense excitement, or\n   /ewilderment; as, a wild look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wild', 'superl.', 'Hard to steer; -- said of a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wild', 'n.', 'An uninhabited and uncultivated tract or region; a forest or\n   desert; a wilderness; a waste; as, the wilds of America; the wilds of\n   Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wild', 'adv.', 'Wildly; as, to talk wild.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wild-cat', 'a.', 'Unsound; worthless; irresponsible; unsafe; -- said to\n   have been originally applied to the notes of an insolvent bank in\n   Michigan upon which there was the figure of a panther.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wild-cat', 'a.', 'Running without control; running along the line without a\n   train; as, a wild-cat locomotive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wildebeest', 'n.', 'The gnu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilded', 'a.', 'Become wild.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wildered', 'imp. & p. p.', 'of Wilder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wildering', 'p. pr. & vb. n.', 'of Wilder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilder', 'a.', 'To bewilder; to perplex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wildering', 'n.', 'A plant growing in a state of nature; especially, one\n   which has run wild, or escaped from cultivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilderment', 'n.', 'The state of being bewildered; confusion; bewilderment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilderness', 'v. t.', 'A tract of land, or a region, uncultivated and\n   uninhabited by human beings, whether a forest or a wide, barren plain;\n   a wild; a waste; a desert; a pathless waste of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilderness', 'v. t.', 'A disorderly or neglected place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilderness', 'v. t.', 'Quality or state of being wild; wildness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wildfire', 'n.', 'A composition of inflammable materials, which, kindled,\n   is very hard to quench; Greek fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wildfire', 'n.', 'An old name for erysipelas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wildfire', 'n.', 'A disease of sheep, attended with inflammation of the\n   skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wildfire', 'n.', 'A sort of lightning unaccompanied by thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wildgrave', 'n.', 'A waldgrave, or head forest keeper. See Waldgrave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilding', 'n.', 'A wild or uncultivated plant; especially, a wild apple\n   tree or crab apple; also, the fruit of such a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilding', 'a.', 'Not tame, domesticated, or cultivated; wild.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wildish', 'a.', 'Somewhat wild; rather wild.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wildly', 'adv.', 'In a wild manner; without cultivation; with disorder;\n   rudely; distractedly; extravagantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wildness', 'n.', 'The quality or state of being wild; an uncultivated or\n   untamed state; disposition to rove or go unrestrained; rudeness;\n   savageness; irregularity; distraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wildwood', 'n.', 'A wild or unfrequented wood. Also used adjectively; as,\n   wildwood flowers; wildwood echoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wile', 'n.', 'A trick or stratagem practiced for insnaring or deception; a\n   sly, insidious; artifice; a beguilement; an allurement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wile', 'v. t.', 'To practice artifice upon; to deceive; to beguile; to\n   allure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wile', 'v. t.', 'To draw or turn away, as by diversion; to while or while\n   away; to cause to pass pleasantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wileful', 'a.', 'Full of wiles; trickish; deceitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilful', 'n.', 'Alt. of Wilfulness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilfully', 'n.', 'Alt. of Wilfulness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilfulness', 'n.', 'See Willful, Willfully, and Willfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiliness', 'n.', 'The quality or state of being wily; craftiness; cunning;\n   guile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilk', 'n.', 'See Whelk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Will', 'v.', 'The power of choosing; the faculty or endowment of the soul\n   by which it is capable of choosing; the faculty or power of the mind by\n   which we decide to do or not to do; the power or faculty of preferring\n   or selecting one of two or more objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Will', 'v.', 'The choice which is made; a determination or preference which\n   results from the act or exercise of the power of choice; a volition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Will', 'v.', 'The choice or determination of one who has authority; a\n   decree; a command; discretionary pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Will', 'v.', 'Strong wish or inclination; desire; purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Will', 'v.', 'That which is strongly wished or desired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Will', 'v.', 'Arbitrary disposal; power to control, dispose, or determine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Will', 'v.', 'The legal declaration of a persons mind as to the manner in\n   which he would have his property or estate disposed of after his death;\n   the written instrument, legally executed, by which a man makes\n   disposition of his estate, to take effect after his death; testament;\n   devise. See the Note under Testament, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Would', 'imp.', 'of Will');
  INSERT INTO words (word, word_type, description) VALUES  (    'Will', 'adv.', 'To wish; to desire; to incline to have.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Will', 'adv.', 'As an auxiliary, will is used to denote futurity dependent\n   on the verb. Thus, in first person, \"I will\" denotes willingness,\n   consent, promise; and when \"will\" is emphasized, it denotes\n   determination or fixed purpose; as, I will go if you wish; I will go at\n   all hazards. In the second and third persons, the idea of distinct\n   volition, wish, or purpose is evanescent, and simple certainty is\n   appropriately expressed; as, \"You will go,\" or \"He will go,\" describes\n   a future event as a fact only. To emphasize will denotes (according to\n   the tone or context) certain futurity or fixed determination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Will', 'v. i.', 'To be willing; to be inclined or disposed; to be pleased;\n   to wish; to desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willed', 'imp. & p. p.', 'of Will');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willing', 'p. pr. & vb. n.', 'of Will');
  INSERT INTO words (word, word_type, description) VALUES  (    'Will', 'n.', 'To form a distinct volition of; to determine by an act of\n   choice; to ordain; to decree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Will', 'n.', 'To enjoin or command, as that which is determined by an act\n   of volition; to direct; to order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Will', 'n.', 'To give or direct the disposal of by testament; to bequeath;\n   to devise; as, to will ones estate to a child; also, to order or\n   direct by testament; as, he willed that his nephew should have his\n   watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Will', 'v. i.', 'To exercise an act of volition; to choose; to decide; to\n   determine; to decree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willemite', 'n.', 'A silicate of zinc, usually occurring massive and of a\n   greenish yellow color, also in reddish crystals (troostite) containing\n   manganese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willer', 'n.', 'One who wills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willet', 'n.', 'A large North American snipe (Symphemia semipalmata); --\n   called also pill-willet, will-willet, semipalmated tattler, or snipe,\n   duck snipe, and stone curlew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willful', 'a.', 'Of set purpose; self-determined; voluntary; as, willful\n   murder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willful', 'a.', 'Governed by the will without yielding to reason;\n   obstinate; perverse; inflexible; stubborn; refractory; as, a willful\n   man or horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willier', 'n.', 'One who works at a willying machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willing', 'v. t.', 'Free to do or to grant; having the mind inclined; not\n   opposed in mind; not choosing to refuse; disposed; not averse;\n   desirous; consenting; complying; ready.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willing', 'v. t.', 'Received of choice, or without reluctance; submitted to\n   voluntarily; chosen; desired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willing', 'v. t.', 'Spontaneous; self-moved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willingly', 'adv.', 'In a willing manner; with free will; without\n   reluctance; cheerfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willingness', 'n.', 'The quality or state of being willing; free choice or\n   consent of the will; freedom from reluctance; readiness of the mind to\n   do or forbear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Williwaw', 'n.', 'Alt. of Willywaw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willywaw', 'n.', 'A whirlwind, or whirlwind squall, encountered in the\n   Straits of Magellan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willock', 'n.', 'The common guillemot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willock', 'n.', 'The puffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Will-o-the-wisp', 'n.', 'See Ignis fatuus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willow', 'n.', 'Any tree or shrub of the genus Salix, including many\n   species, most of which are characterized often used as an emblem of\n   sorrow, desolation, or desertion. \"A wreath of willow to show my\n   forsaken plight.\" Sir W. Scott. Hence, a lover forsaken by, or having\n   lost, the person beloved, is said to wear the willow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willow', 'n.', 'A machine in which cotton or wool is opened and cleansed by\n   the action of long spikes projecting from a drum which revolves within\n   a box studded with similar spikes; -- probably so called from having\n   been originally a cylindrical cage made of willow rods, though some\n   derive the term from winnow, as denoting the winnowing, or cleansing,\n   action of the machine. Called also willy, twilly, twilly devil, and\n   devil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willow', 'v. t.', 'To open and cleanse, as cotton, flax, or wool, by means\n   of a willow. See Willow, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willowed', 'a.', 'Abounding with willows; containing willows; covered or\n   overgrown with willows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willower', 'n.', 'A willow. See Willow, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willow-herb', 'n.', 'A perennial herb (Epilobium spicatum) with narrow\n   willowlike leaves and showy rose-purple flowers. The name is sometimes\n   made to include other species of the same genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willowish', 'a.', 'Having the color of the willow; resembling the willow;\n   willowy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willow-thorn', 'n.', 'A thorny European shrub (Hippophae rhamnoides)\n   resembling a willow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willow-weed', 'n.', 'A European species of loosestrife (Lysimachia\n   vulgaris).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willow-weed', 'n.', 'Any kind of Polygonum with willowlike foliage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willow-wort', 'n.', 'Same as Willow-weed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willow-wort', 'n.', 'Any plant of the order Salicaceae, or the Willow\n   family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willowy', 'a.', 'Abounding with willows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willowy', 'a.', 'Resembling a willow; pliant; flexible; pendent; drooping;\n   graceful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willsome', 'a.', 'Willful; obstinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willsome', 'a.', 'Fat; indolent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willsome', 'a.', 'Doubtful; uncertain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willy', 'n.', 'A large wicker basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willy', 'n.', 'Same as 1st Willow, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willying', 'n.', 'The process of cleansing wool, cotton, or the like, with\n   a willy, or willow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Willy nilly', '', 'See Will I, nill I, etc., under 3d Will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilne', 'v. t.', 'To wish; to desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilt', '', '2d pers. sing. of Will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilting', 'imp. & p. p.', 'of Wilt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilt', 'v. i.', 'To begin to wither; to lose freshness and become flaccid,\n   as a plant when exposed when exposed to drought, or to great heat in a\n   dry day, or when separated from its root; to droop;. to wither.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilt', 'v. t.', 'To cause to begin to wither; to make flaccid, as a green\n   plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilt', 'v. t.', 'Hence, to cause to languish; to depress or destroy the\n   vigor and energy of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilton carpet', '', 'A kind of carpet woven with loops like the Brussels,\n   but differing from it in having the loops cut so as to form an elastic\n   velvet pile; -- so called because made originally at Wilton, England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wilwe', 'n.', 'Willow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wily', 'superl.', 'Full of wiles, tricks, or stratagems; using craft or\n   stratagem to accomplish a purpose; mischievously artful; subtle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wimble', 'n.', 'An instrument for boring holes, turned by a handle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wimble', 'n.', 'A gimlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wimble', 'n.', 'A stonecutters brace for boring holes in stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wimble', 'n.', 'An auger used for boring in earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wimbled', 'imp. & p. p.', 'of Wimble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wimbling', 'p. pr. & vb. n.', 'of Wimble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wimble', 'v. t.', 'To bore or pierce, as with a wimble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wimble', 'a.', 'Active; nimble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wimbrel', 'n.', 'The whimbrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wimple', 'n.', 'A covering of silk, linen, or other material, for the neck\n   and chin, formerly worn by women as an outdoor protection, and still\n   retained in the dress of nuns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wimple', 'n.', 'A flag or streamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wimpled', 'imp. & p. p.', 'of Wimple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wimpling', 'p. pr. & vb. n.', 'of Wimple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wimple', 'v. t.', 'To clothe with a wimple; to cover, as with a veil;\n   hence, to hoodwink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wimple', 'v. t.', 'To draw down, as a veil; to lay in folds or plaits, as a\n   veil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wimple', 'v. t.', 'To cause to appear as if laid in folds or plaits; to\n   cause to ripple or undulate; as, the wind wimples the surface of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wimple', 'v. i.', 'To lie in folds; also, to appear as if laid in folds or\n   plaits; to ripple; to undulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Won', 'imp. & p. p.', 'of Win');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wan', '', 'of Win');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winning', 'p. pr. & vb. n.', 'of Win');
  INSERT INTO words (word, word_type, description) VALUES  (    'Win', 'a.', 'To gain by superiority in competition or contest; to obtain by\n   victory over competitors or rivals; as, to win the prize in a gate; to\n   win money; to win a battle, or to win a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Win', 'a.', 'To allure to kindness; to bring to compliance; to gain or\n   obtain, as by solicitation or courtship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Win', 'a.', 'To gain over to ones side or party; to obtain the favor,\n   friendship, or support of; to render friendly or approving; as, to win\n   an enemy; to win a jury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Win', 'a.', 'To come to by toil or effort; to reach; to overtake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Win', 'a.', 'To extract, as ore or coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Win', 'v. i.', 'To gain the victory; to be successful; to triumph; to\n   prevail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winced', 'imp. & p. p.', 'of Wince');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wincing', 'p. pr. & vb. n.', 'of Wince');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wince', 'v. i.', 'To shrink, as from a blow, or from pain; to flinch; to\n   start back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wince', 'v. i.', 'To kick or flounce when unsteady, or impatient at a\n   rider; as, a horse winces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wince', 'n.', 'The act of one who winces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wince', 'n.', 'A reel used in dyeing, steeping, or washing cloth; a winch.\n   It is placed over the division wall between two wince pits so as to\n   allow the cloth to descend into either compartment. at will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wincer', 'n.', 'One who, or that which, winces, shrinks, or kicks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wincey', 'n.', 'Linsey-woolsey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winch', 'v. i.', 'To wince; to shrink; to kick with impatience or\n   uneasiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winch', 'n.', 'A kick, as of a beast, from impatience or uneasiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winch', 'n.', 'A crank with a handle, for giving motion to a machine, a\n   grindstone, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winch', 'n.', 'An instrument with which to turn or strain something\n   forcibly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winch', 'n.', 'An axle or drum turned by a crank with a handle, or by\n   power, for raising weights, as from the hold of a ship, from mines,\n   etc.; a windlass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winch', 'n.', 'A wince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wincing', 'n.', 'The act of washing cloth, dipping it in dye, etc., with a\n   wince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wincopipe', 'n.', 'A little red flower, no doubt the pimpernel, which, when\n   it opens in the morning, is supposed to bode a fair day. See Pimpernel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wound', 'imp. & p. p.', 'of Wind');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winded', '', 'of Wind');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winding', 'p. pr. & vb. n.', 'of Wind');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'v. t.', 'To turn completely, or with repeated turns; especially, to\n   turn about something fixed; to cause to form convolutions about\n   anything; to coil; to twine; to twist; to wreathe; as, to wind thread\n   on a spool or into a ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'v. t.', 'To entwist; to infold; to encircle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'v. t.', 'To have complete control over; to turn and bend at ones\n   pleasure; to vary or alter or will; to regulate; to govern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'v. t.', 'To introduce by insinuation; to insinuate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'v. t.', 'To cover or surround with something coiled about; as, to\n   wind a rope with twine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'v. i.', 'To turn completely or repeatedly; to become coiled about\n   anything; to assume a convolved or spiral form; as, vines wind round a\n   pole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'v. i.', 'To have a circular course or direction; to crook; to bend;\n   to meander; as, to wind in and out among trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'v. i.', 'To go to the one side or the other; to move this way and\n   that; to double on ones course; as, a hare pursued turns and winds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'n.', 'The act of winding or turning; a turn; a bend; a twist; a\n   winding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'n.', 'Air naturally in motion with any degree of velocity; a\n   current of air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'n.', 'Air artificially put in motion by any force or action; as,\n   the wind of a cannon ball; the wind of a bellows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'n.', 'Breath modulated by the respiratory and vocal organs, or by\n   an instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'n.', 'Power of respiration; breath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'n.', 'Air or gas generated in the stomach or bowels; flatulence;\n   as, to be troubled with wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'n.', 'Air impregnated with an odor or scent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'n.', 'A direction from which the wind may blow; a point of the\n   compass; especially, one of the cardinal points, which are often called\n   the four winds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'n.', 'A disease of sheep, in which the intestines are distended\n   with air, or rather affected with a violent inflammation. It occurs\n   immediately after shearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'n.', 'Mere breath or talk; empty effort; idle words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'n.', 'The dotterel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winded', 'imp. & p. p.', 'of Wind');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winding', 'p. pr. & vb. n.', 'of Wind');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'v. t.', 'To expose to the wind; to winnow; to ventilate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'v. t.', 'To perceive or follow by the scent; to scent; to nose; as,\n   the hounds winded the game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'v. t.', 'To drive hard, or force to violent exertion, as a horse,\n   so as to render scant of wind; to put out of breath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'v. t.', 'To rest, as a horse, in order to allow the breath to be\n   recovered; to breathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wound', 'imp. & p. p.', 'of Wind');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winded', '', 'of Wind');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winding', 'p. pr. & vb. n.', 'of Wind');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind', 'v. t.', 'To blow; to sound by blowing; esp., to sound with\n   prolonged and mutually involved notes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windage', 'n.', 'The difference between the diameter of the bore of a gun\n   and that of the shot fired from it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windage', 'n.', 'The sudden compression of the air caused by a projectile\n   in passing close to another body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windas', 'n.', 'See 3d Windlass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windbore', 'n.', 'The lower, or bottom, pipe in a lift of pumps in a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windbound', 'a.', 'prevented from sailing, by a contrary wind. See\n   Weatherbound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind-break', 'v. t.', 'To break the wind of; to cause to lose breath; to\n   exhaust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind-break', 'n.', 'A clump of trees serving for a protection against the\n   force of wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind-broken', 'a.', 'Having the power of breathing impaired by the rupture,\n   dilatation, or running together of air cells of the lungs, so that\n   while the inspiration is by one effort, the expiration is by two;\n   affected with pulmonary emphysema or with heaves; -- said of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winder', 'n.', 'One who, or that which, winds; hence, a creeping or winding\n   plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winder', 'n.', 'An apparatus used for winding silk, cotton, etc., on\n   spools, bobbins, reels, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winder', 'n.', 'One in a flight of steps which are curved in plan, so that\n   each tread is broader at one end than at the other; -- distinguished\n   from flyer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winder', 'v. t. & i.', 'To fan; to clean grain with a fan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winder', 'n.', 'A blow taking away the breath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winder', 'v. i.', 'To wither; to fail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windfall', 'n.', 'Anything blown down or off by the wind, as fruit from a\n   tree, or the tree itself, or a portion of a forest prostrated by a\n   violent wind, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windfall', 'n.', 'An unexpected legacy, or other gain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windfallen', 'a.', 'Blown down by the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind-fertilized', 'a.', 'Anemophilous; fertilized by pollen borne by the\n   wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windflower', 'n.', 'The anemone; -- so called because formerly supposed to\n   open only when the wind was blowing. See Anemone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windgall', 'n.', 'A soft tumor or synovial swelling on the fetlock joint of\n   a horse; -- so called from having formerly been supposed to contain\n   air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windhover', 'n.', 'The kestrel; -- called also windbibber, windcuffer,\n   windfanner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windiness', 'n.', 'The quality or state of being windy or tempestuous; as,\n   the windiness of the weather or the season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windiness', 'n.', 'Fullness of wind; flatulence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windiness', 'n.', 'Tendency to generate wind or gas; tendency to produce\n   flatulence; as, the windiness of vegetables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windiness', 'n.', 'Tumor; puffiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winding', 'n.', 'A call by the boatswains whistle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winding', 'a.', 'Twisting from a direct line or an even surface;\n   circuitous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winding', 'n.', 'A turn or turning; a bend; a curve; flexure; meander; as,\n   the windings of a road or stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winding', 'n.', 'A line- or ribbon-shaped material (as wire, string, or\n   bandaging) wound around an object; as, the windings (conducting wires)\n   wound around the armature of an electric motor or generator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windingly', 'adv.', 'In a winding manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windlace', 'n. & v.', 'See Windlass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windlass', 'n.', 'A winding and circuitous way; a roundabout course; a\n   shift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windlass', 'v. i.', 'To take a roundabout course; to work warily or by\n   indirect means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windlass', 'n.', 'A machine for raising weights, consisting of a horizontal\n   cylinder or roller moving on its axis, and turned by a crank, lever, or\n   similar means, so as to wind up a rope or chain attached to the weight.\n   In vessels the windlass is often used instead of the capstan for\n   raising the anchor. It is usually set upon the forecastle, and is\n   worked by hand or steam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windlass', 'n.', 'An apparatus resembling a winch or windlass, for bending\n   the bow of an arblast, or crossbow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windlass', 'v. t. & i.', 'To raise with, or as with, a windlass; to use a\n   windlass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windle', 'n.', 'A spindle; a kind of reel; a winch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windle', 'n.', 'The redwing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windless', 'a.', 'Having no wind; calm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windless', 'a.', 'Wanting wind; out of breath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windlestrae', 'n.', 'Alt. of Windlestraw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windlestraw', 'n.', 'A grass used for making ropes or for plaiting, esp.\n   Agrostis Spica-ventis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windmill', 'n.', 'A mill operated by the power of the wind, usually by the\n   action of the wind upon oblique vanes or sails which radiate from a\n   horizontal shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windore', 'n.', 'A window.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Window', 'n.', 'An opening in the wall of a building for the admission of\n   light and air, usually closed by casements or sashes containing some\n   transparent material, as glass, and capable of being opened and shut at\n   pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Window', 'n.', 'The shutter, casement, sash with its fittings, or other\n   framework, which closes a window opening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Window', 'n.', 'A figure formed of lines crossing each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windowed', 'imp. & p. p.', 'of Window');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windowing', 'p. pr. & vb. n.', 'of Window');
  INSERT INTO words (word, word_type, description) VALUES  (    'Window', 'v. t.', 'To furnish with windows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Window', 'v. t.', 'To place at or in a window.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windowed', 'a.', 'Having windows or openings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windowless', 'a.', 'Destitute of a window.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windowpane', 'n.', 'See Pane, n., (3) b.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windowpane', 'n.', 'A thin, spotted American turbot (Pleuronectes\n   maculatus) remarkable for its translucency. It is not valued as a food\n   fish. Called also spotted turbot, daylight, spotted sand flounder, and\n   water flounder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windowy', 'a.', 'Having little crossings or openings like the sashes of a\n   window.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windpipe', 'n.', 'The passage for the breath from the larynx to the lungs;\n   the trachea; the weasand. See Illust. under Lung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind-plant', 'n.', 'A windflower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind-rode', 'a.', 'Caused to ride or drive by the wind in opposition to the\n   course of the tide; -- said of a vessel lying at anchor, with wind and\n   tide opposed to each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windrow', 'n.', 'A row or line of hay raked together for the purpose of\n   being rolled into cocks or heaps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windrow', 'n.', 'Sheaves of grain set up in a row, one against another,\n   that the wind may blow between them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windrow', 'n.', 'The green border of a field, dug up in order to carry the\n   earth on other land to mend it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windrowed', 'imp. & p. p.', 'of Windrow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windrowing', 'p. pr. & vb. n.', 'of Windrow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windrow', 'v. t.', 'To arrange in lines or windrows, as hay when newly\n   made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windsor', 'n.', 'A town in Berkshire, England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windstorm', 'n.', 'A storm characterized by high wind with little or no\n   rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind-sucker', 'n.', 'A horse given to wind-sucking');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind-sucker', 'n.', 'The kestrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wind-sucking', 'n.', 'A vicious habit of a horse, consisting in the\n   swallowing of air; -- usually associated with crib-biting, or cribbing.\n   See Cribbing, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windtight', 'a.', 'So tight as to prevent the passing through of wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windward', 'n.', 'The point or side from which the wind blows; as, to ply\n   to the windward; -- opposed to leeward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windward', 'a.', 'Situated toward the point from which the wind blows; as,\n   the Windward Islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windward', 'adv.', 'Toward the wind; in the direction from which the wind\n   blows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windy', 'superl.', 'Consisting of wind; accompanied or characterized by\n   wind; exposed to wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windy', 'superl.', 'Next the wind; windward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windy', 'superl.', 'Tempestuous; boisterous; as, windy weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windy', 'superl.', 'Serving to occasion wind or gas in the intestines;\n   flatulent; as, windy food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windy', 'superl.', 'Attended or caused by wind, or gas, in the intestines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Windy', 'superl.', 'Fig.: Empty; airy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wine', 'n.', 'The expressed juice of grapes, esp. when fermented; a\n   beverage or liquor prepared from grapes by squeezing out their juice,\n   and (usually) allowing it to ferment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wine', 'n.', 'A liquor or beverage prepared from the juice of any fruit or\n   plant by a process similar to that for grape wine; as, currant wine;\n   gooseberry wine; palm wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wine', 'n.', 'The effect of drinking wine in excess; intoxication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wineberry', 'n.', 'The red currant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wineberry', 'n.', 'The bilberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wineberry', 'n.', 'A peculiar New Zealand shrub (Coriaria ruscifolia), in\n   which the petals ripen and afford an abundant purple juice from which a\n   kind of wine is made. The plant also grows in Chili.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winebibber', 'n.', 'One who drinks much wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wineglass', 'n.', 'A small glass from which to drink wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wineglassfuls', 'pl. ', 'of Wineglassful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wineglassful', '', 'As much as a wineglass will hold; enough to fill a\n   wineglass. It is usually reckoned at two fluid ounces, or four\n   tablespoonfuls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wineless', 'a.', 'destitute of wine; as, wineless life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winery', 'n.', 'A place where grapes are converted into wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'n.', 'One of the two anterior limbs of a bird, pterodactyl, or bat.\n   They correspond to the arms of man, and are usually modified for\n   flight, but in the case of a few species of birds, as the ostrich, auk,\n   etc., the wings are used only as an assistance in running or swimming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'n.', 'Any similar member or instrument used for the purpose of\n   flying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'n.', 'One of the two pairs of upper thoracic appendages of most\n   hexapod insects. They are broad, fanlike organs formed of a double\n   membrane and strengthened by chitinous veins or nervures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'n.', 'One of the large pectoral fins of the flying fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'n.', 'Passage by flying; flight; as, to take wing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'n.', 'Motive or instrument of flight; means of flight or of rapid\n   motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'n.', 'Anything which agitates the air as a wing does, or which is\n   put in winglike motion by the action of the air, as a fan or vane for\n   winnowing grain, the vane or sail of a windmill, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'n.', 'An ornament worn on the shoulder; a small epaulet or shoulder\n   knot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'n.', 'Any appendage resembling the wing of a bird or insect in\n   shape or appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'n.', 'One of the broad, thin, anterior lobes of the foot of a\n   pteropod, used as an organ in swimming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'n.', 'Any membranaceous expansion, as that along the sides of\n   certain stems, or of a fruit of the kind called samara.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'n.', 'Either of the two side petals of a papilionaceous flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'n.', 'One of two corresponding appendages attached; a sidepiece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'n.', 'A side building, less than the main edifice; as, one of the\n   wings of a palace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'n.', 'The longer side of crownworks, etc., connecting them with the\n   main work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'n.', 'A side shoot of a tree or plant; a branch growing up by the\n   side of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'n.', 'The right or left division of an army, regiment, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'n.', 'That part of the hold or orlop of a vessel which is nearest\n   the sides. In a fleet, one of the extremities when the ships are drawn\n   up in line, or when forming the two sides of a triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'n.', 'One of the sides of the stags in a theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winged', 'imp. & p. p.', 'of Wing');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winging', 'p. pr. & vb. n.', 'of Wing');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'v. t.', 'To furnish with wings; to enable to fly, or to move with\n   celerity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'v. t.', 'To supply with wings or sidepieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'v. t.', 'To transport by flight; to cause to fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'v. t.', 'To move through in flight; to fly through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing', 'v. t.', 'To cut off the wings of; to wound in the wing; to disable\n   a wing of; as, to wing a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winged', 'a.', 'Furnished with wings; transported by flying; having\n   winglike expansions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winged', 'a.', 'Soaring with wings, or as if with wings; hence, elevated;\n   lofty; sublime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winged', 'a.', 'Swift; rapid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winged', 'a.', 'Wounded or hurt in the wing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winged', 'a.', 'Furnished with a leaflike appendage, as the fruit of the\n   elm and the ash, or the stem in certain plants; alate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winged', 'a.', 'Represented with wings, or having wings, of a different\n   tincture from the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winged', 'a.', 'Fanned with wings; swarming with birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winger', 'n.', 'One of the casks stowed in the wings of a vessels hold,\n   being smaller than such as are stowed more amidships.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wingfish', 'n.', 'A sea robin having large, winglike pectoral fins. See Sea\n   robin, under Robin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing-footed', 'a.', 'Having wings attached to the feet; as, wing-footed\n   Mercury; hence, swift; moving with rapidity; fleet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing-footed', 'a.', 'Having part or all of the feet adapted for flying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing-footed', 'a.', 'Having the anterior lobes of the foot so modified as\n   to form a pair of winglike swimming organs; -- said of the pteropod\n   mollusks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing-handed', 'a.', 'Having the anterior limbs or hands adapted for flight,\n   as the bats and pterodactyls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing-leaved', 'a.', 'Having pinnate or pinnately divided leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wingless', 'a.', 'Having no wings; not able to ascend or fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winglet', 'n.', 'A little wing; a very small wing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winglet', 'n.', 'A bastard wing, or alula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wingmanship', 'n.', 'Power or skill in flying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing-shell', 'n.', 'Any one of various species of marine bivalve shells\n   belonging to the genus Avicula, in which the hinge border projects like\n   a wing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing-shell', 'n.', 'Any marine gastropod shell of the genus Strombus. See\n   Strombus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wing-shell', 'n.', 'Any pteropod shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wingy', 'a.', 'Having wings; rapid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wingy', 'a.', 'Soaring with wings, or as if with wings; volatile airy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winked', 'imp. & p. p.', 'of Wink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winking', 'p. pr. & vb. n.', 'of Wink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wink', 'v. i.', 'To nod; to sleep; to nap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wink', 'v. i.', 'To shut the eyes quickly; to close the eyelids with a\n   quick motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wink', 'v. i.', 'To close and open the eyelids quickly; to nictitate; to\n   blink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wink', 'v. i.', 'To give a hint by a motion of the eyelids, often those of\n   one eye only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wink', 'v. i.', 'To avoid taking notice, as if by shutting the eyes; to\n   connive at anything; to be tolerant; -- generally with at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wink', 'v. i.', 'To be dim and flicker; as, the light winks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wink', 'v. t.', 'To cause (the eyes) to wink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wink', 'n.', 'The act of closing, or closing and opening, the eyelids\n   quickly; hence, the time necessary for such an act; a moment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wink', 'n.', 'A hint given by shutting the eye with a significant cast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winker', 'n.', 'One who winks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winker', 'n.', 'A horses blinder; a blinker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winkingly', 'adv.', 'In a winking manner; with the eye almost closed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winkle', 'n.', 'Any periwinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winkle', 'n.', 'Any one of various marine spiral gastropods, esp., in the\n   United States, either of two species of Fulgar (F. canaliculata, and F.\n   carica).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winkle-hawk', 'n.', 'A rectangular rent made in cloth; -- called also\n   winkle-hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winnard 2', 'n.', 'The redwing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winnebagoes', 'n.', 'A tribe of North American Indians who originally\n   occupied the region about Green Bay, Lake Michigan, but were driven\n   back from the lake and nearly exterminated in 1640 by the IIlinnois.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winner', 'n.', 'One who wins, or gains by success in competition, contest,\n   or gaming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winning', 'a.', 'Attracting; adapted to gain favor; charming; as, a winning\n   address.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winning', 'n.', 'The act of obtaining something, as in a contest or by\n   competition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winning', 'n.', 'The money, etc., gained by success in competition or\n   contest, esp, in gambling; -- usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winning', 'n.', 'A new opening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winning', 'n.', 'The portion of a coal field out for working.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winningly', 'adv.', 'In a winning manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winningness', 'n.', 'The quality or state of being winning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winninish', 'n.', 'The land-locked variety of the common salmon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winnowed', 'imp. & p. p.', 'of Winnew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winnowing', 'p. pr. & vb. n.', 'of Winnew');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winnew', 'n.', 'To separate, and drive off, the chaff from by means of\n   wind; to fan; as, to winnow grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winnew', 'n.', 'To sift, as for the purpose of separating falsehood from\n   truth; to separate, as had from good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winnew', 'n.', 'To beat with wings, or as with wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winnow', 'v. i.', 'To separate chaff from grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winnower', 'n.', 'One who, or that which, winnows; specifically, a\n   winnowing machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winnowing', 'n.', 'The act of one who, or that which, winnows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winrow', 'n.', 'A windrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winsing', 'a.', 'Winsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winsome', 'a.', 'Cheerful; merry; gay; light-hearted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winsome', 'a.', 'Causing joy or pleasure; gladsome; pleasant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winsomeness', 'n.', 'The characteristic of being winsome; attractiveness of\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winter', 'n.', 'The season of the year in which the sun shines most\n   obliquely upon any region; the coldest season of the year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winter', 'n.', 'The period of decay, old age, death, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wintered', 'imp. & p. p.', 'of Winter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wintering', 'p. pr. & vb. n.', 'of Winter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winter', 'v. i.', 'To pass the winter; to hibernate; as, to winter in\n   Florida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winter', 'v. i.', 'To keep, feed or manage, during the winter; as, to\n   winter young cattle on straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winter-beaten', 'a.', 'Beaten or harassed by the severe weather of winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wintergreen', 'n.', 'A plant which keeps its leaves green through the\n   winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winter-ground', 'v. t.', 'To coved over in the season of winter, as for\n   protection or shelter; as, to winter-ground the roods of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winterkilled', 'imp. & p. p.', 'of Winterkill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winterkilling', 'p. pr. & vb. n.', 'of Winterkill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winterkill', 'v. t.', 'To kill by the cold, or exposure to the inclemency\n   of winter; as, the wheat was winterkilled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winterly', 'a.', 'Like winter; wintry; cold; hence, disagreeable,\n   cheerless; as, winterly news.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winter-proud', 'a.', 'Having too rank or forward a growth for winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winter-rig', 'v. t.', 'To fallow or till in winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winters bark', '', 'The aromatic bark of tree (Drimys, / Drymis, Winteri)\n   of the Magnolia family, which is found in Southern Chili. It was first\n   used as a cure for scurvy by its discoverer, Captain John Winter, vice\n   admiral to sir Francis Drake, in 1577.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wintertide', 'n.', 'Winter time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winterweed', 'n.', 'A kind of speedwell (Veronica hederifolia) which\n   spreads chiefly in winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wintery', 'a.', 'Wintry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wintry', 'a.', 'Suitable to winter; resembling winter, or what belongs to\n   winter; brumal; hyemal; cold; stormy; wintery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winy', 'a.', 'Having the taste or qualities of wine; vinous; as, grapes of\n   a winy taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Winze', 'n.', 'A small shaft sunk from one level to another, as for the\n   purpose of ventilation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wipe', 'n.', 'The lapwing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiped', 'imp. & p. p.', 'of Wipe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiping', 'p. pr. & vb. n.', 'of Wipe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wipe', 'v. t.', 'To rub with something soft for cleaning; to clean or dry\n   by rubbing; as, to wipe the hands or face with a towel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wipe', 'v. t.', 'To remove by rubbing; to rub off; to obliterate; --\n   usually followed by away, off or out. Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wipe', 'v. t.', 'To cheat; to defraud; to trick; -- usually followed by\n   out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wipe', 'n.', 'Act of rubbing, esp. in order to clean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wipe', 'n.', 'A blow; a stroke; a hit; a swipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wipe', 'n.', 'A gibe; a jeer; a severe sarcasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wipe', 'n.', 'A handkerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wipe', 'n.', 'Stain; brand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiper', 'n.', 'One who, or that which, wipes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiper', 'n.', 'Something used for wiping, as a towel or rag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiper', 'n.', 'A piece generally projecting from a rotating or swinging\n   piece, as an axle or rock shaft, for the purpose of raising stampers,\n   lifting rods, or the like, and leaving them to fall by their own\n   weight; a kind of cam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiper', 'n.', 'A rod, or an attachment for a rod, for holding a rag with\n   which to wipe out the bore of the barrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wirbled', 'imp. & p. p.', 'of Wirble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wirbling', 'p. pr. & vb. n.', 'of Wirble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wirble', 'v. i.', 'To whirl; to eddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wirche', 'v. i. & t.', 'To work');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wire', 'n.', 'A thread or slender rod of metal; a metallic substance formed\n   to an even thread by being passed between grooved rollers, or drawn\n   through holes in a plate of steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wire', 'n.', 'A telegraph wire or cable; hence, an electric telegraph; as,\n   to send a message by wire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wired', 'imp. & p. p.', 'of Wire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiring', 'p. pr. & vb. n.', 'of Wire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wire', 'v. t.', 'To bind with wire; to attach with wires; to apply wire to;\n   as, to wire corks in bottling liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wire', 'v. t.', 'To put upon a wire; as, to wire beads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wire', 'v. t.', 'To snare by means of a wire or wires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wire', 'v. t.', 'To send (a message) by telegraph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wire', 'v. i.', 'To pass like a wire; to flow in a wirelike form, or in a\n   tenuous stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wire', 'v. i.', 'To send a telegraphic message.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiredrew', 'imp.', 'of Wiredraw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiredrawn', 'p. p.', 'of Wiredraw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiredrawing', 'p. pr. & vb. n.', 'of Wiredraw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiredraw', 'v. t.', 'To form (a piece of metal) into wire, by drawing it\n   through a hole in a plate of steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiredraw', 'v. t.', 'Hence, to draw by art or violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiredraw', 'v. t.', 'Hence, also, to draw or spin out to great length and\n   tenuity; as, to wiredraw an argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiredraw', 'v. t.', 'To pass, or to draw off, (as steam) through narrow\n   ports, or the like, thus reducing its pressure or force by friction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wire-drawer', 'n.', 'One who draws metal into wire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wire-heel', 'n.', 'A disease in the feet of a horse or other beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wire-puller', 'n.', 'One who pulls the wires, as of a puppet; hence, one\n   who operates by secret means; an intriguer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wire-pulling', 'n.', 'The act of pulling the wires, as of a puppet; hence,\n   secret influence or management, especially in politics; intrigue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wire-tailed', 'a.', 'Having some or all of the tail quills terminated in a\n   long, slender, pointed shaft, without a web or barbules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wirework', 'n.', 'Work, especially openwork, formed of wires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wire-worker', 'n.', 'One who manufactures articles from wire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wireworm', 'n.', 'One of the larvae of various species of snapping beetles,\n   or elaters; -- so called from their slenderness and the uncommon\n   hardness of the integument. Wireworms are sometimes very destructive to\n   the roots of plants. Called also wire grub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wireworm', 'n.', 'A galleyworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiriness', 'n.', 'The quality of being wiry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiry', 'a.', 'Made of wire; like wire; drawn out like wire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiry', 'a.', 'Capable of endurance; tough; sinewy; as, a wiry frame or\n   constitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wis', 'adv.', 'Certainly; really; indeed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wis', 'v. t.', 'To think; to suppose; to imagine; -- used chiefly in the\n   first person sing. present tense, I wis. See the Note under Ywis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wisard', 'n.', 'See Wizard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wisdom', 'a.', 'The quality of being wise; knowledge, and the capacity to\n   make due use of it; knowledge of the best ends and the best means;\n   discernment and judgment; discretion; sagacity; skill; dexterity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wisdom', 'a.', 'The results of wise judgments; scientific or practical\n   truth; acquired knowledge; erudition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wise', 'v.', 'Having knowledge; knowing; enlightened; of extensive\n   information; erudite; learned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wise', 'v.', 'Hence, especially, making due use of knowledge; discerning\n   and judging soundly concerning what is true or false, proper or\n   improper; choosing the best ends and the best means for accomplishing\n   them; sagacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wise', 'v.', 'Versed in art or science; skillful; dexterous; specifically,\n   skilled in divination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wise', 'v.', 'Hence, prudent; calculating; shrewd; wary; subtle; crafty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wise', 'v.', 'Dictated or guided by wisdom; containing or exhibiting\n   wisdom; well adapted to produce good effects; judicious; discreet; as,\n   a wise saying; a wise scheme or plan; wise conduct or management; a\n   wise determination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wise', 'v.', 'Way of being or acting; manner; mode; fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiseacre', 'v.', 'A learned or wise man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiseacre', 'v.', 'One who makes undue pretensions to wisdom; a\n   would-be-wise person; hence, in contempt, a simpleton; a dunce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wise-hearted', 'a.', 'Wise; knowing; skillful; sapient; erudite; prudent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wise-like', 'a.', 'Resembling that which is wise or sensible; judicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiseling', 'n.', 'One who pretends to be wise; a wiseacre; a witling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wisely', 'adv.', 'In a wise manner; prudently; judiciously; discreetly;\n   with wisdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiseness', 'n.', 'Wisdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wished', 'imp. & p. p.', 'of Wish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wishing', 'p. pr. & vb. n.', 'of Wish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wish', 'v. t.', 'To have a desire or yearning; to long; to hanker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wish', 'v. t.', 'To desire; to long for; to hanker after; to have a mind or\n   disposition toward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wish', 'v. t.', 'To frame or express desires concerning; to invoke in favor\n   of, or against, any one; to attribute, or cal down, in desire; to\n   invoke; to imprecate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wish', 'v. t.', 'To recommend; to seek confidence or favor in behalf of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wish', 'n.', 'Desire; eager desire; longing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wish', 'n.', 'Expression of desire; request; petition; hence, invocation or\n   imprecation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wish', 'n.', 'A thing desired; an object of desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wishable', 'a.', 'Capable or worthy of being wished for; desirable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wishbone', 'n.', 'The forked bone in front of the breastbone in birds; --\n   called also merrythought, and wishing bone. See Merrythought, and\n   Furculum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wishedly', 'adv.', 'According to wish; conformably to desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wisher', 'n.', 'One who wishes or desires; one who expresses a wish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wishful', 'a.', 'Having desire, or ardent desire; longing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wishful', 'a.', 'Showing desire; as, wishful eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wishful', 'a.', 'Desirable; exciting wishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wishing', '', 'a. & n. from Wish, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wishly', 'adv.', 'According to desire; longingly; with wishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wishtonwish', 'n.', 'The prairie dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wish-wash', 'n.', 'Any weak, thin drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wishy-washy', 'a.', 'Thin and pale; weak; without strength or substance; --\n   originally said of liquids. Fig., weak-minded; spiritless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wishy-washy', 'n.', 'A weak or thin drink or liquor; wish-wash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wisket', 'n.', 'A whisket, or basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wisly', 'adv.', 'Certainly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wisp', 'n.', 'A small bundle, as of straw or other like substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wisp', 'n.', 'A whisk, or small broom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wisp', 'n.', 'A Will-o-the-wisp; an ignis fatuus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wisped', 'imp. & p. p.', 'of Wisp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wisping', 'p. pr. & vb. n.', 'of Wisp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wisp', 'v. t.', 'To brush or dress, an with a wisp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wisp', 'v. t.', 'To rumple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wispen', 'a.', 'Formed of a wisp, or of wisp; as, a wispen broom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wisse', 'a.', 'To show; to teach; to inform; to guide; to direct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wist', 'v.', 'Knew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wistaria', 'n.', 'A genus of climbing leguminous plants bearing long,\n   pendulous clusters of pale bluish flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wistful', 'a.', 'Longing; wishful; desirous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wistful', 'a.', 'Full of thought; eagerly attentive; meditative; musing;\n   pensive; contemplative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wistit', 'n.', 'A small South American monkey; a marmoset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wistly', 'adv.', 'Attentively; observingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wistonwish', 'n.', 'See Wishtonwish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wit', 'inf.', 'of Wit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wot', 'pres. sing.', 'of Wit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wite', 'pl.', 'of Wit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wist', 'e', '(imp.) of Wit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wist', 'p. p.', 'of Wit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wit', 't', 'ing (p. pr. & vb. n.) of Wit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wit', 'n.', 'To know; to learn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wit', 'v.', 'Mind; intellect; understanding; sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wit', 'v.', 'A mental faculty, or power of the mind; -- used in this sense\n   chiefly in the plural, and in certain phrases; as, to lose ones wits;\n   at ones wits end, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wit', 'v.', 'Felicitous association of objects not usually connected, so as\n   to produce a pleasant surprise; also. the power of readily combining\n   objects in such a manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wit', 'v.', 'A person of eminent sense or knowledge; a man of genius,\n   fancy, or humor; one distinguished for bright or amusing sayings, for\n   repartee, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witch', 'n.', 'A cone of paper which is placed in a vessel of lard or other\n   fat, and used as a taper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witch', 'n.', 'One who practices the black art, or magic; one regarded as\n   possessing supernatural or magical power by compact with an evil\n   spirit, esp. with the Devil; a sorcerer or sorceress; -- now applied\n   chiefly or only to women, but formerly used of men as well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witch', 'n.', 'An ugly old woman; a hag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witch', 'n.', 'One who exercises more than common power of attraction; a\n   charming or bewitching person; also, one given to mischief; -- said\n   especially of a woman or child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witch', 'n.', 'A certain curve of the third order, described by Maria\n   Agnesi under the name versiera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witch', 'n.', 'The stormy petrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witched', 'imp. & p. p.', 'of Witch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witching', 'p. pr. & vb. n.', 'of Witch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witch', 'v. t.', 'To bewitch; to fascinate; to enchant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witchcraft', 'n.', 'The practices or art of witches; sorcery; enchantments;\n   intercourse with evil spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witchcraft', 'n.', 'Power more than natural; irresistible influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witch-elm', 'n.', 'See Wych-elm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witcheries', 'pl. ', 'of Witchery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witchery', 'n.', 'Sorcery; enchantment; witchcraft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witchery', 'n.', 'Fascination; irresistible influence; enchantment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witch-hazel', 'n.', 'The wych-elm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witch-hazel', 'n.', 'An American shrub or small tree (Hamamelis Virginica),\n   which blossoms late in autumn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witching', 'a.', 'That witches or enchants; suited to enchantment or\n   witchcraft; bewitching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witch-tree', 'n.', 'The witch-hazel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witchuck', 'n.', 'The sand martin, or bank swallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wit-cracker', 'n.', 'One who breaks jests; a joker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witcraft', 'n.', 'Art or skill of the mind; contrivance; invention; wit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witcraft', 'n.', 'The art of reasoning; logic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wite', 'v.', 'To reproach; to blame; to censure; also, to impute as blame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wite', 'v.', 'Blame; reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witeless', 'a.', 'Blameless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witen', '', 'pl. pres. of Wit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witenagemote', 'n.', 'A meeting of wise men; the national council, or\n   legislature, of England in the days of the Anglo-Saxons, before the\n   Norman Conquest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witfish', 'n.', 'The ladyfish (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witful', 'a.', 'Wise; sensible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'With', 'n.', 'See Withe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'With', 'prep.', 'With denotes or expresses some situation or relation of\n   nearness, proximity, association, connection, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'With', 'prep.', 'To denote a close or direct relation of opposition or\n   hostility; -- equivalent to against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'With', 'prep.', 'To denote association in respect of situation or\n   environment; hence, among; in the company of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'With', 'prep.', 'To denote a connection of friendship, support, alliance,\n   assistance, countenance, etc.; hence, on the side of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'With', 'prep.', 'To denote the accomplishment of cause, means, instrument,\n   etc; -- sometimes equivalent to by.');
  INSERT INTO words (word, word_type, description) VALUES  (    'With', 'prep.', 'To denote association in thought, as for comparison or\n   contrast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'With', 'prep.', 'To denote simultaneous happening, or immediate succession\n   or consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'With', 'prep.', 'To denote having as a possession or an appendage; as, the\n   firmament with its stars; a bride with a large fortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withal', 'adv.', 'With this; with that.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withal', 'adv.', 'Together with this; likewise; at the same time; in\n   addition; also.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withal', 'prep.', 'With; -- put after its object, at the end of sentence or\n   clause in which it stands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withamite', 'n.', 'A variety of epidote, of a reddish color, found in\n   Scotland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withdrew', 'imp.', 'of Withdraw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withdrawn', 'p. p.', 'of Withdraw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withdrawing', 'p. pr. & vb. n.', 'of Withdraw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withdraw', 'v. t.', 'To take back or away, as what has been bestowed or\n   enjoyed; to draw back; to cause to move away or retire; as, to withdraw\n   aid, favor, capital, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withdraw', 'v. t.', 'To take back; to recall or retract; as, to withdraw\n   false charges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withdraw', 'v. i.', 'To retire; to retreat; to quit a company or place; to\n   go away; as, he withdrew from the company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withdrawal', 'n.', 'The act of withdrawing; withdrawment; retreat;\n   retraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withdrawer', 'n.', 'One who withdraws; one who takes back, or retracts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withdrawing-room', 'n.', 'A room for retirement from another room, as from\n   a dining room; a drawing-room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withdrawment', 'n.', 'The act of withdrawing; withdrawal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withe', 'n.', 'A flexible, slender twig or branch used as a band; a willow\n   or osier twig; a withy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withe', 'n.', 'A band consisting of a twig twisted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withe', 'n.', 'An iron attachment on one end of a mast or boom, with a\n   ring, through which another mast or boom is rigged out and secured; a\n   wythe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withe', 'n.', 'A partition between flues in a chimney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withed', 'imp. & p. p.', 'of Withe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withing', 'p. pr. & vb. n.', 'of Withe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withe', 'v. t.', 'To bind or fasten with withes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withered', 'imp. & p. p.', 'of Wither');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withering', 'p. pr. & vb. n.', 'of Wither');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wither', 'n.', 'To fade; to lose freshness; to become sapless; to become\n   sapless; to dry or shrivel up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wither', 'n.', 'To lose or want animal moisture; to waste; to pin/ away, as\n   animal bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wither', 'n.', 'To lose vigor or power; to languish; to pass away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wither', 'v. t.', 'To cause to fade, and become dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wither', 'v. t.', 'To cause to shrink, wrinkle, or decay, for want of\n   animal moisture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wither', 'v. t.', 'To cause to languish, perish, or pass away; to blight;\n   as, a reputation withered by calumny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witherband', 'n.', 'A piece of iron in a saddle near a horses withers, to\n   strengthen the bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withered', 'a.', 'Faded; dried up; shriveled; wilted; wasted; wasted away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withering', 'a.', 'Tending to wither; causing to shrink or fade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witherite', 'n.', 'Barium carbonate occurring in white or gray six-sided\n   twin crystals, and also in columnar or granular masses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witherling', 'n.', 'A withered person; one who is decrepit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withernam', 'n.', 'A second or reciprocal distress of other goods in lieu\n   of goods which were taken by a first distress and have been eloigned; a\n   taking by way of reprisal; -- chiefly used in the expression capias in\n   withernam, which is the name of a writ used in connection with the\n   action of replevin (sometimes called a writ of reprisal), which issues\n   to a defendant in replevin when he has obtained judgment for a return\n   of the chattels replevied, and fails to obtain them on the writ of\n   return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withe-rod', 'n.', 'A North American shrub (Viburnum nudum) whose tough\n   osierlike shoots are sometimes used for binding sheaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withers', 'prep.', 'The ridge between the shoulder bones of a horse, at the\n   base of the neck. See Illust. of Horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wither-wrung', 'a.', 'Injured or hurt in the withers, as a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withheld', 'imp.', 'of Withhold');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withheld', 'p. p.', 'of Withhold');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withholden', '', 'of Withhold');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withholding', 'p. pr. & vb. n.', 'of Withhold');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withhold', 'v. t.', 'To hold back; to restrain; to keep from action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withhold', 'v. t.', 'To retain; to keep back; not to grant; as, to withhold\n   assent to a proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withhold', 'v. t.', 'To keep; to maintain; to retain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withholder', 'n.', 'One who withholds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withholdment', 'n.', 'The act of withholding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Within', 'prep.', 'In the inner or interior part of; inside of; not\n   without; as, within doors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Within', 'prep.', 'In the limits or compass of; not further in length than;\n   as, within five miles; not longer in time than; as, within an hour; not\n   exceeding in quantity; as, expenses kept within ones income.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Within', 'prep.', 'Hence, inside the limits, reach, or influence of; not\n   going outside of; not beyond, overstepping, exceeding, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Within', 'adv.', 'In the inner part; inwardly; internally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Within', 'adv.', 'In the house; in doors; as, the master is within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withinforth', 'adv.', 'Within; inside; inwardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withinside', 'adv.', 'In the inner parts; inside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Without', 'prep.', 'On or at the outside of; out of; not within; as,\n   without doors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Without', 'prep.', 'Out of the limits of; out of reach of; beyond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Without', 'prep.', 'Not with; otherwise than with; in absence of,\n   separation from, or destitution of; not with use or employment of;\n   independently of; exclusively of; with omission; as, without labor;\n   without damage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Without', 'conj.', 'Unless; except; -- introducing a clause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Without', 'adv.', 'On or art the outside; not on the inside; not within;\n   outwardly; externally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Without', 'adv.', 'Outside of the house; out of doors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Without-door', 'a.', 'Outdoor; exterior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withouten', 'prep.', 'Without.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withoutforth', 'adv.', 'Without; outside outwardly. Cf. Withinforth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withsay', 'v. t.', 'To contradict; to gainsay; to deny; to renounce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withset', 'v. t.', 'To set against; to oppose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withstood', 'imp. & p. p.', 'of Withstand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withstanding', 'p. pr. & vb. n.', 'of Withstand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withstand', 'prep.', 'To stand against; to oppose; to resist, either with\n   physical or moral force; as, to withstand an attack of troops; to\n   withstand eloquence or arguments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withstander', 'n.', 'One who withstands, or opposes; an opponent; a\n   resisting power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withstood', 'imp. & p. p.', 'o/ Withstand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withvine', 'n.', 'Quitch grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withwind', 'n.', 'A kind of bindweed (Convolvulus arvensis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withwine', 'n.', 'Same as Withvine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withies', 'pl. ', 'of Withy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withy', 'n.', 'The osier willow (Salix viminalis). See Osier, n. (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withy', 'n.', 'A withe. See Withe, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Withy', 'a.', 'Made of withes; like a withe; flexible and tough; also,\n   abounding in withes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witing', 'v.', 'Knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witless', 'a.', 'Destitute of wit or understanding; wanting thought; hence,\n   indiscreet; not under the guidance of judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witling', 'n.', 'A person who has little wit or understanding; a pretender\n   to wit or smartness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witness', 'v. i.', 'Attestation of a fact or an event; testimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witness', 'v. i.', 'That which furnishes evidence or proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witness', 'v. i.', 'One who is cognizant; a person who beholds, or\n   otherwise has personal knowledge of, anything; as, an eyewitness; an\n   earwitness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witness', 'v. i.', 'One who testifies in a cause, or gives evidence before\n   a judicial tribunal; as, the witness in court agreed in all essential\n   facts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witness', 'v. i.', 'One who sees the execution of an instrument, and\n   subscribes it for the purpose of confirming its authenticity by his\n   testimony; one who witnesses a will, a deed, a marriage, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witnessed', 'imp. & p. p.', 'of Witness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witnessing', 'p. pr. & vb. n.', 'of Witness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witness', 'v. t.', 'To see or know by personal presence; to have direct\n   cognizance of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witness', 'v. t.', 'To give testimony to; to testify to; to attest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witness', 'v. t.', 'To see the execution of, as an instrument, and\n   subscribe it for the purpose of establishing its authenticity; as, to\n   witness a bond or a deed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witness', 'v. i.', 'To bear testimony; to give evidence; to testify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witnesser', 'n.', 'One who witness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wit-snapper', 'n.', 'One who affects repartee; a wit-cracker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wit-starved', 'a.', 'Barren of wit; destitute of genius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witted', 'a.', 'Having (such) a wit or understanding; as, a quick-witted\n   boy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witticaster', 'n.', 'A witling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witticism', 'n.', 'A witty saying; a sentence or phrase which is affectedly\n   witty; an attempt at wit; a conceit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wittified', 'a.', 'Possessed of wit; witty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witily', 'adv.', 'In a witty manner; wisely; ingeniously; artfully; with\n   it; with a delicate turn or phrase, or with an ingenious association of\n   ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wittiness', 'n.', 'The quality of being witty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wittingly', 'v.', 'Knowingly; with knowledge; by design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wittol', 'n.', 'The wheatear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wittol', 'n.', 'A man who knows his wifes infidelity and submits to it; a\n   tame cuckold; -- so called because the cuckoo lays its eggs in the\n   wittols nest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wittolly', 'a.', 'Like a wittol; cuckoldly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witts', 'n.', 'Tin ore freed from earthy matter by stamping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witty', 'n.', 'Possessed of wit; knowing; wise; skillful; judicious;\n   clever; cunning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witty', 'n.', 'Especially, possessing wit or humor; good at repartee;\n   droll; facetious; sometimes, sarcastic; as, a witty remark, poem, and\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witwal', 'n.', 'Alt. of Witwall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witwall', 'n.', 'The golden oriole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witwall', 'n.', 'The greater spotted woodpecker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Witworm', 'n.', 'One who, or that which, feeds on or destroys wit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wived', 'imp. & p. p.', 'of Wive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiving', 'p. pr. & vb. n.', 'of Wive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wive', 'v. i.', 'To marry, as a man; to take a wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wive', 'v. t.', 'To match to a wife; to provide with a wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wive', 'v. t.', 'To take for a wife; to marry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wivehood', 'n.', 'Wifehood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiveless', 'a.', 'Wifeless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wively', 'a.', 'Wifely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wiver', 'n.', 'Alt. of Wivern');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wivern', 'n.', 'A fabulous two-legged, winged creature, like a cockatrice,\n   but having the head of a dragon, and without spurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wivern', 'n.', 'The weever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wives', 'n.', 'pl. of Wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wizard', 'n.', 'A wise man; a sage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wizard', 'n.', 'One devoted to the black art; a magician; a conjurer; a\n   sorcerer; an enchanter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wizard', 'a.', 'Enchanting; charming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wizard', 'a.', 'Haunted by wizards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wizardly', 'a.', 'Resembling or becoming a wizard; wizardlike; weird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wizardry', 'n.', 'The character or practices o/ wizards; sorcery; magic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wizen', 'v. i.', 'To wither; to dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wizen', 'a.', 'Wizened; thin; weazen; withered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wizen', 'n.', 'The weasand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wizened', 'a.', 'Dried; shriveled; withered; shrunken; weazen; as, a\n   wizened old man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wizen-faced', 'a.', 'Having a shriveled, thin, withered face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wlatsome', 'a.', 'Loathsome; disgusting; hateful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wo', 'n. & a.', 'See Woe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woad', 'n.', 'An herbaceous cruciferous plant (Isatis tinctoria). It was\n   formerly cultivated for the blue coloring matter derived from its\n   leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woad', 'n.', 'A blue dyestuff, or coloring matter, consisting of the\n   powdered and fermented leaves of the Isatis tinctoria. It is now\n   superseded by indigo, but is somewhat used with indigo as a ferment in\n   dyeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woaded', 'a.', 'Colored or stained with woad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woad-waxen', 'n.', 'A leguminous plant (Genista tinctoria) of Europe and\n   Russian Asia, and adventitious in America; -- called also greenwood,\n   greenweed, dyers greenweed, and whin, wood-wash, wood-wax, and\n   wood-waxen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woald', 'n.', 'See Weld.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wobble', 'v. i.', 'See Wabble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wode', 'a.', 'Mad. See Wood, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wode', 'n.', 'Wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wodegeld', 'n.', 'A geld, or payment, for wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woden', 'n.', 'A deity corresponding to Odin, the supreme deity of the\n   Scandinavians. Wednesday is named for him. See Odin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woe', 'n.', 'Grief; sorrow; misery; heavy calamity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woe', 'n.', 'A curse; a malediction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woe', 'a.', 'Woeful; sorrowful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woe-begone', 'a.', 'Beset or overwhelmed with woe; immersed in grief or\n   sorrow; woeful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woeful', 'a.', 'Alt. of Woful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woful', 'a.', 'Full of woe; sorrowful; distressed with grief or calamity;\n   afflicted; wretched; unhappy; sad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woful', 'a.', 'Bringing calamity, distress, or affliction; as, a woeful\n   event; woeful want.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woful', 'a.', 'Wretched; paltry; miserable; poor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woefully', 'adv.', 'Alt. of Wofully');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wofully', 'adv.', 'In a woeful manner; sorrowfully; mournfully; miserably;\n   dolefully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woefulness', 'n.', 'Alt. of Wofulness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wofulness', 'n.', 'The quality or state of being woeful; misery;\n   wretchedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woesome', 'a.', 'Woeful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woke', 'imp. & p. p.', 'Wake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wol', 'v. t. & i.', 'See 2d Will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wold', 'n.', 'A wood; a forest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wold', 'n.', 'A plain, or low hill; a country without wood, whether hilly\n   or not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wold', 'n.', 'See Weld.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolde', '', 'imp. of Will. See Would.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolves', 'pl. ', 'of Wolf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolf', 'a.', 'Any one of several species of wild and savage carnivores\n   belonging to the genus Canis and closely allied to the common dog. The\n   best-known and most destructive species are the European wolf (Canis\n   lupus), the American gray, or timber, wolf (C. occidentalis), and the\n   prairie wolf, or coyote. Wolves often hunt in packs, and may thus\n   attack large animals and even man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolf', 'a.', 'One of the destructive, and usually hairy, larvae of several\n   species of beetles and grain moths; as, the bee wolf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolf', 'a.', 'Fig.: Any very ravenous, rapacious, or destructive person or\n   thing; especially, want; starvation; as, they toiled hard to keep the\n   wolf from the door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolf', 'a.', 'A white worm, or maggot, which infests granaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolf', 'a.', 'An eating ulcer or sore. Cf. Lupus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolf', 'a.', 'The harsh, howling sound of some of the chords on an organ or\n   piano tuned by unequal temperament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolf', 'a.', 'In bowed instruments, a harshness due to defective vibration\n   in certain notes of the scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolf', 'a.', 'A willying machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolfberry', 'n.', 'An American shrub (Symphoricarpus occidentalis) which\n   bears soft white berries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolffian', 'a.', 'Discovered, or first described, by Caspar Friedrich Wolff\n   (1733-1794), the founder of modern embryology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolfish', 'a.', 'Like a wolf; having the qualities or form of a wolf; as, a\n   wolfish visage; wolfish designs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolfkin', 'n.', 'A little or young wolf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolfling', 'n.', 'A young wolf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolfram', 'n.', 'Same as Wolframite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolframate', 'n.', 'A salt of wolframic acid; a tungstate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolframic', 'a.', 'Of or pertaining to wolframium. See Tungstic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolframite', 'n.', 'Tungstate of iron and manganese, generally of a\n   brownish or grayish black color, submetallic luster, and high specific\n   gravity. It occurs in cleavable masses, and also crystallized. Called\n   also wolfram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolframium', 'n.', 'The technical name of the element tungsten. See\n   Tungsten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolfsbane', 'n.', 'A poisonous plant (Aconitum Lycoctonum), a kind of\n   monkshood; also, by extension, any plant or species of the genus\n   Aconitum. See Aconite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolfs-claw', 'n.', 'A kind of club moss. See Lycopodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolfs-foot', 'n.', 'Club moss. See Lycopodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolfs-milk', 'n.', 'Any kind of spurge (Euphorbia); -- so called from its\n   acrid milky juice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woll', 'v. t. & i.', 'See 2d Will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wollastonite', 'n.', 'A silicate of lime of a white to gray, red, or yellow\n   color, occurring generally in cleavable masses, rarely in tabular\n   crystals; tabular spar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolle', 'n.', 'Wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolverene', 'n.', 'Alt. of Wolverine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolverine', 'n.', 'The glutton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolverine', 'n.', 'A nickname for an inhabitant of Michigan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolves', 'n.', 'pl. of Wolf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wolvish', 'a.', 'Wolfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Women', 'pl. ', 'of Woman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woman', 'n.', 'An adult female person; a grown-up female person, as\n   distinguished from a man or a child; sometimes, any female person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woman', 'n.', 'The female part of the human race; womankind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woman', 'n.', 'A female attendant or servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woman', 'v. t.', 'To act the part of a woman in; -- with indefinite it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woman', 'v. t.', 'To make effeminate or womanish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woman', 'v. t.', 'To furnish with, or unite to, a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Womanhead', 'n.', 'Alt. of Womanhede');
  INSERT INTO words (word, word_type, description) VALUES  (    'Womanhede', 'n.', 'Womanhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Womanhood', 'n.', 'The state of being a woman; the distinguishing character\n   or qualities of a woman, or of womankind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Womanhood', 'n.', 'Women, collectively; womankind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Womanish', 'a.', 'Suitable to a woman, having the qualities of a woman;\n   effeminate; not becoming a man; -- usually in a reproachful sense. See\n   the Note under Effeminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Womanize', 'v. t.', 'To make like a woman; to make effeminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Womankind', 'n.', 'The females of the human race; women, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Womanless', 'a.', 'Without a woman or women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Womanlike', 'a.', 'Like a woman; womanly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Womanliness', 'n.', 'The quality or state of being womanly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Womanly', 'a.', 'Becoming a woman; feminine; as, womanly behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Womanly', 'adv.', 'In the manner of a woman; with the grace, tenderness, or\n   affection of a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Womb', 'n.', 'The belly; the abdomen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Womb', 'n.', 'The uterus. See Uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Womb', 'n.', 'The place where anything is generated or produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Womb', 'n.', 'Any cavity containing and enveloping anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Womb', 'v. t.', 'To inclose in a womb, or as in a womb; to breed or hold in\n   secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wombat', 'n.', 'Any one of three species of Australian burrowing marsupials\n   of the genus Phascolomys, especially the common species (P. ursinus).\n   They are nocturnal in their habits, and feed mostly on roots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Womby', 'a.', 'Capacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Women', 'n.', 'pl. of Woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Won', '', 'imp. & p. p. of Win.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Won', 'v. i.', 'To dwell or abide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Won', 'n.', 'Dwelling; wone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonder', 'n.', 'That emotion which is excited by novelty, or the\n   presentation to the sight or mind of something new, unusual, strange,\n   great, extraordinary, or not well understood; surprise; astonishment;\n   admiration; amazement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonder', 'n.', 'A cause of wonder; that which excites surprise; a strange\n   thing; a prodigy; a miracle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wondered', 'imp. & p. p.', 'of Wonder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wondering', 'p. pr. & vb. n.', 'of Wonder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonder', 'v. i.', 'To be affected with surprise or admiration; to be struck\n   with astonishment; to be amazed; to marvel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonder', 'v. i.', 'To feel doubt and curiosity; to wait with uncertain\n   expectation; to query in the mind; as, he wondered why they came.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonder', 'a.', 'Wonderful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonder', 'adv.', 'Wonderfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wondered', 'a.', 'Having performed wonders; able to perform wonderful\n   things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonderer', 'n.', 'One who wonders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonderful', 'a.', 'Adapted to excite wonder or admiration; surprising;\n   strange; astonishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonderingly', 'adv.', 'In a wondering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonderland', 'n.', 'A land full of wonders, or marvels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonderly', 'adv.', 'Wonderfully; wondrously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonderment', 'n.', 'Surprise; astonishment; a wonderful appearance; a\n   wonder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonderous', 'a.', 'Same as Wondrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonders', 'adv.', 'See Wondrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonderstruck', 'a.', 'Struck with wonder, admiration, or surprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonderwork', 'n.', 'A wonderful work or act; a prodigy; a miracle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonder-worker', 'n.', 'One who performs wonders, or miracles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonder-working', 'a.', 'Doing wonders or surprising things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wondrous', 'n.', 'In a wonderful or surprising manner or degree;\n   wonderfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wondrous', 'a.', 'Wonderful; astonishing; admirable; marvelous; such as\n   excite surprise and astonishment; strange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wone', 'a.', 'To dwell; to abide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wone', 'a.', 'Dwelling; habitation; abode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wone', 'a.', 'Custom; habit; wont; use; usage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wong', 'n.', 'A field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonger', 'n.', 'See Wanger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woning', 'n.', 'Dwelling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wont', '', 'A colloquial contraction of woll not. Will not. See Will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wont', 'a.', 'Using or doing customarily; accustomed; habituated; used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wont', 'n.', 'Custom; habit; use; usage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wont', 'imp.', 'of Wont');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wont', 'p. p.', 'of Wont');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonted', '', 'of Wont');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonting', 'p. pr. & vb. n.', 'of Wont');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wont', 'v. i.', 'To be accustomed or habituated; to be used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wont', 'v. t.', 'To accustom; -- used reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wonted', 'a.', 'Accustomed; customary; usual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wontedness', 'n.', 'The quality or state of being accustomed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wontless', 'a.', 'Unaccustomed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wooed', 'imp. & p. p.', 'of Woo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wooing', 'p. pr. & vb. n.', 'of Woo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woo', 'v. t.', 'To solicit in love; to court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woo', 'v. t.', 'To court solicitously; to invite with importunity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woo', 'v. i.', 'To court; to make love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wood', 'a.', 'Mad; insane; possessed; rabid; furious; frantic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wood', 'v. i.', 'To grow mad; to act like a madman; to mad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wood', 'n.', 'A large and thick collection of trees; a forest or grove; --\n   frequently used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wood', 'n.', 'The substance of trees and the like; the hard fibrous\n   substance which composes the body of a tree and its branches, and which\n   is covered by the bark; timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wood', 'n.', 'The fibrous material which makes up the greater part of the\n   stems and branches of trees and shrubby plants, and is found to a less\n   extent in herbaceous stems. It consists of elongated tubular or\n   needle-shaped cells of various kinds, usually interwoven with the\n   shinning bands called silver grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wood', 'n.', 'Trees cut or sawed for the fire or other uses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wooded', 'imp. & p. p.', 'of Wood');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wooding', 'p. pr. & vb. n.', 'of Wood');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wood', 'v. t.', 'To supply with wood, or get supplies of wood for; as, to\n   wood a steamboat or a locomotive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wood', 'v. i.', 'To take or get a supply of wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodbind', 'n.', 'Woodbine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodbine', 'v. t.', 'A climbing plant having flowers of great fragrance\n   (Lonicera Periclymenum); the honeysuckle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodbine', 'v. t.', 'The Virginia creeper. See Virginia creeper, under\n   Virginia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wood-bound', 'a.', 'Incumbered with tall, woody hedgerows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodbury-type', 'n.', 'A process in photographic printing, in which a\n   relief pattern in gelatin, which has been hardened after certain\n   operations, is pressed upon a plate of lead or other soft metal. An\n   intaglio impression in thus produced, from which pictures may be\n   directly printed, but by a slower process than in common printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodbury-type', 'n.', 'A print from such a plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodchat', 'n.', 'Any one of several species of Asiatic singing birds\n   belonging to the genera Ianthia and Larvivora. They are closely allied\n   to the European robin. The males are usually bright blue above, and\n   more or less red or rufous beneath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodchat', 'n.', 'A European shrike (Enneoctonus rufus). In the male the\n   head and nape are rufous red; the back, wings, and tail are black,\n   varied with white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodchuck', 'n.', 'A common large North American marmot (Arctomys monax).\n   It is usually reddish brown, more or less grizzled with gray. It makes\n   extensive burrows, and is often injurious to growing crops. Called also\n   ground hog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodchuck', 'n.', 'The yaffle, or green woodpecker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodcock', 'n.', 'Any one of several species of long-billed limicoline\n   birds belonging to the genera Scolopax and Philohela. They are mostly\n   nocturnal in their habits, and are highly esteemed as game birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodcock', 'n.', 'Fig.: A simpleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodcracker', 'n.', 'The nuthatch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodcraft', 'n.', 'Skill and practice in anything pertaining to the woods,\n   especially in shooting, and other sports in the woods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodcut', 'n.', 'An engraving on wood; also, a print from it. Same as Wood\n   cut, under Wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodcutter', 'n.', 'A person who cuts wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodcutter', 'n.', 'An engraver on wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodcutting', 'n.', 'The act or employment of cutting wood or timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodcutting', 'n.', 'The act or art of engraving on wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wooded', 'a.', 'Supplied or covered with wood, or trees; as, land wooded\n   and watered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wooden', 'a.', 'Made or consisting of wood; pertaining to, or resembling,\n   wood; as, a wooden box; a wooden leg; a wooden wedding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wooden', 'a.', 'Clumsy; awkward; ungainly; stiff; spiritless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodenly', 'adv.', 'Clumsily; stupidly; blockishly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodenness', 'n.', 'Quality of being wooden; clumsiness; stupidity;\n   blockishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodhack', 'n.', 'Alt. of Woodhacker');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodhacker', 'n.', 'The yaffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodnewer', 'n.', 'A woodpecker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodhole', 'n.', 'A place where wood is stored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodhouse', 'n.', 'A house or shed in which wood is stored, and sheltered\n   from the weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodiness', 'n.', 'The quality or state of being woody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodknacker', 'n.', 'The yaffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodland', 'n.', 'Land covered with wood or trees; forest; land on which\n   trees are suffered to grow, either for fuel or timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodland', 'a.', 'Of or pertaining to woods or woodland; living in the\n   forest; sylvan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodlander', 'n.', 'A dweller in a woodland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wood-layer', 'n.', 'A young oak, or other timber plant, laid down in a\n   hedge among the whitethorn or other plants used in hedges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodless', 'a.', 'Having no wood; destitute of wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodly', 'adv.', 'In a wood, mad, or raving manner; madly; furiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodmen', 'pl. ', 'of Woodman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodman', 'n.', 'A forest officer appointed to take care of the kings\n   woods; a forester.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodman', 'n.', 'A sportsman; a hunter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodman', 'n.', 'One who cuts down trees; a woodcutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodman', 'n.', 'One who dwells in the woods or forest; a bushman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodmeil', 'n.', 'See Wadmol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodmonger', 'n.', 'A wood seller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodness', 'n.', 'Anger; madness; insanity; rage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wood-note', 'n.', 'A wild or natural note, as of a forest bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodpeck', 'n.', 'A woodpecker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodpecker', 'n.', 'Any one of numerous species of scansorial birds\n   belonging to Picus and many allied genera of the family Picidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodrock', 'n.', 'A compact woodlike variety of asbestus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodruff', 'n.', 'Alt. of Woodroof');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodroof', 'n.', 'A little European herb (Asperula odorata) having a\n   pleasant taste. It is sometimes used for flavoring wine. See Illust. of\n   Whorl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wood-sare', 'n.', 'A kind of froth seen on herbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wood-sere', 'n.', 'The time when there no sap in the trees; the winter\n   season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodsmen', 'pl. ', 'of Woodsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodsman', 'n.', 'A woodman; especially, one who lives in the forest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woods metal', '', 'A fusible alloy consisting of one or two parts of\n   cadmium, two parts of tin, four of lead, with seven or eight part of\n   bismuth. It melts at from 66¡ to 71¡ C. See Fusible metal, under\n   Fusible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodstone', 'n.', 'A striped variety of hornstone, resembling wood in\n   appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodsy', 'a.', 'Of or pertaining to the woods or forest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wood tick', '', 'Any one of several species of ticks of the genus Ixodes\n   whose young cling to bushes, but quickly fasten themselves upon the\n   bodies of any animal with which they come in contact. When they attach\n   themselves to the human body they often produce troublesome sores. The\n   common species of the Northern United States is Ixodes unipunctata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodwall', 'n.', 'The yaffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodward', 'n.', 'An officer of the forest, whose duty it was to guard the\n   woods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodwardia', 'n.', 'A genus of ferns, one species of which (Woodwardia\n   radicans) is a showy plant in California, the Azores, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wood-wash', 'n.', 'Alt. of Wood-waxen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wood-wax', 'n.', 'Alt. of Wood-waxen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wood-waxen', 'n.', 'Same as Woadwaxen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodwork', 'n.', 'Work made of wood; that part of any structure which is\n   wrought of wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woodworm', 'n.', 'See Wood worm, under Wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woody', 'a.', 'Abounding with wood or woods; as, woody land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woody', 'a.', 'Consisting of, or containing, wood or woody fiber; ligneous;\n   as, the woody parts of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woody', 'a.', 'Of or pertaining to woods; sylvan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wooer', 'v. t.', 'One who wooes; one who courts or solicits in love; a\n   suitor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woof', 'n.', 'The threads that cross the warp in a woven fabric; the weft;\n   the filling; the thread usually carried by the shuttle in weaving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woof', 'n.', 'Texture; cloth; as, a pall of softest woof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woofell', 'n.', 'The European blackbird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woofy', 'a.', 'Having a close texture; dense; as, a woofy cloud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woohoo', 'n.', 'The sailfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wooingly', 'adv.', 'In a wooing manner; enticingly; with persuasiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wook', 'imp.', 'Woke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wool', 'n.', 'The soft and curled, or crisped, species of hair which grows\n   on sheep and some other animals, and which in fineness sometimes\n   approaches to fur; -- chiefly applied to the fleecy coat of the sheep,\n   which constitutes a most essential material of clothing in all cold and\n   temperate climates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wool', 'n.', 'Short, thick hair, especially when crisped or curled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wool', 'n.', 'A sort of pubescence, or a clothing of dense, curling hairs\n   on the surface of certain plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolded', 'imp. & p. p.', 'of Woold');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolding', 'p. pr. & vb. n.', 'of Woold');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woold', 'v. t.', 'To wind, or wrap; especially, to wind a rope round, as a\n   mast or yard made of two or more pieces, at the place where it has been\n   fished or scarfed, in order to strengthen it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolder', 'n.', 'A stick used to tighten the rope in woolding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolder', 'n.', 'One of the handles of the top, formed by a wooden pin\n   passing through it. See 1st Top, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolding', 'n.', 'The act of winding or wrapping anything with a rope, as a\n   mast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolding', 'n.', 'A rope used for binding masts and spars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wool-dyed', 'a.', 'Dyed before being made into cloth, in distinction from\n   piece-dyed; ingrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wooled', 'a.', 'Having (such) wool; as, a fine-wooled sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolen', 'a.', 'Made of wool; consisting of wool; as, woolen goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolen', 'a.', 'Of or pertaining to wool or woolen cloths; as, woolen\n   manufactures; a woolen mill; a woolen draper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolen', 'n.', 'Cloth made of wool; woollen goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolenet', 'n.', 'A thin, light fabric of wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolert', 'n.', 'The barn owl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolfell', 'n.', 'A skin with the wool; a skin from which the wool has not\n   been sheared or pulled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolgathering', 'a.', 'Indulging in a vagrant or idle exercise of the\n   imagination; roaming upon a fruitless quest; idly fanciful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolgathering', 'n.', 'Indulgence in idle imagination; a foolish or useless\n   pursuit or design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolgrower', 'n.', 'One who raises sheep for the production of wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wool-hall', 'n.', 'A trade market in the woolen districts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolhead', 'n.', 'The buffel duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolliness', 'n.', 'The quality or state of being woolly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolly', 'a.', 'Consisting of wool; as, a woolly covering; a woolly fleece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolly', 'a.', 'Resembling wool; of the nature of wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolly', 'a.', 'Clothed with wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolly', 'a.', 'Clothed with a fine, curly pubescence resembling wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolly-head', 'n.', 'A negro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolmen', 'pl. ', 'of Woolman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolman', 'n.', 'One who deals in wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolpack', 'n.', 'A pack or bag of wool weighing two hundred and forty\n   pounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolsack', 'n.', 'A sack or bag of wool; specifically, the seat of the lord\n   chancellor of England in the House of Lords, being a large, square sack\n   of wool resembling a divan in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolsey', 'n.', 'Linsey-woolsey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolstock', 'n.', 'A heavy wooden hammer for milling cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolward', 'adv.', 'In wool; with woolen raiment next the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woolward-going', 'n.', 'A wearing of woolen clothes next the skin as a\n   matter of penance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woon', 'n.', 'Dwelling. See Wone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woorali', 'n.', 'Same as Curare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woosy', 'a.', 'Oozy; wet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wootz', 'n.', 'A species of steel imported from the East Indies, valued for\n   making edge tools; Indian steel. It has in combination a minute portion\n   of alumina and silica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wooyen', 'n.', 'See Yuen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wopen', 'p. p.', 'Wept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worble', 'n.', 'See Wormil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Word', 'n.', 'The spoken sign of a conception or an idea; an articulate or\n   vocal sound, or a combination of articulate and vocal sounds, uttered\n   by the human voice, and by custom expressing an idea or ideas; a single\n   component part of human speech or language; a constituent part of a\n   sentence; a term; a vocable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Word', 'n.', 'Hence, the written or printed character, or combination of\n   characters, expressing such a term; as, the words on a page.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Word', 'n.', 'Talk; discourse; speech; language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Word', 'n.', 'Account; tidings; message; communication; information; --\n   used only in the singular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Word', 'n.', 'Signal; order; command; direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Word', 'n.', 'Language considered as implying the faith or authority of the\n   person who utters it; statement; affirmation; declaration; promise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Word', 'n.', 'Verbal contention; dispute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Word', 'n.', 'A brief remark or observation; an expression; a phrase,\n   clause, or short sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Word', 'v. i.', 'To use words, as in discussion; to argue; to dispute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worded', 'imp. & p. p.', 'of Word');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wording', 'p. pr. & vb. n.', 'of Word');
  INSERT INTO words (word, word_type, description) VALUES  (    'Word', 'v. t.', 'To express in words; to phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Word', 'v. t.', 'To ply with words; also, to cause to be by the use of a\n   word or words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Word', 'v. t.', 'To flatter with words; to cajole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wordbook', 'n.', 'A collection of words; a vocabulary; a dictionary; a\n   lexicon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Word-catcher', 'n.', 'One who cavils at words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worder', 'n.', 'A speaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wordily', 'adv.', 'In a wordy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wordiness', 'n.', 'The quality or state of being wordy, or abounding with\n   words; verboseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wording', 'n.', 'The act or manner of expressing in words; style of\n   expression; phrasing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wordish', 'a.', 'Respecting words; full of words; wordy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wordle', 'n.', 'One of several pivoted pieces forming the throat of an\n   adjustable die used in drawing wire, lead pipe, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wordless', 'a.', 'Not using words; not speaking; silent; speechless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wordsman', 'n.', 'One who deals in words, or in mere words; a verbalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wordy', 'superl.', 'Of or pertaining to words; consisting of words; verbal;\n   as, a wordy war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wordy', 'superl.', 'Using many words; verbose; as, a wordy speaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wordy', 'superl.', 'Containing many words; full of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wore', '', 'imp. of Wear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wore', '', 'imp. of Ware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'n.', 'Exertion of strength or faculties; physical or intellectual\n   effort directed to an end; industrial activity; toil; employment;\n   sometimes, specifically, physically labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'n.', 'The matter on which one is at work; that upon which one\n   spends labor; material for working upon; subject of exertion; the thing\n   occupying one; business; duty; as, to take up ones work; to drop ones\n   work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'n.', 'That which is produced as the result of labor; anything\n   accomplished by exertion or toil; product; performance; fabric;\n   manufacture; in a more general sense, act, deed, service, effect,\n   result, achievement, feat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'n.', 'Specifically: (a) That which is produced by mental labor; a\n   composition; a book; as, a work, or the works, of Addison. (b) Flowers,\n   figures, or the like, wrought with the needle; embroidery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'n.', 'Structures in civil, military, or naval engineering, as\n   docks, bridges, embankments, trenches, fortifications, and the like;\n   also, the structures and grounds of a manufacturing establishment; as,\n   iron works; locomotive works; gas works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'n.', 'The moving parts of a mechanism; as, the works of a watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'n.', 'Manner of working; management; treatment; as, unskillful work\n   spoiled the effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'n.', 'The causing of motion against a resisting force. The amount\n   of work is proportioned to, and is measured by, the product of the\n   force into the amount of motion along the direction of the force. See\n   Conservation of energy, under Conservation, Unit of work, under Unit,\n   also Foot pound, Horse power, Poundal, and Erg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'n.', 'Ore before it is dressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'n.', 'Performance of moral duties; righteous conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worked', 'imp. & p. p.', 'of Work');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrought', '', 'of Work');
  INSERT INTO words (word, word_type, description) VALUES  (    'Working', 'p. pr. & vb. n.', 'of Work');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'n.', 'To exert ones self for a purpose; to put forth effort for\n   the attainment of an object; to labor; to be engaged in the performance\n   of a task, a duty, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'n.', 'Hence, in a general sense, to operate; to act; to perform;\n   as, a machine works well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'n.', 'Hence, figuratively, to be effective; to have effect or\n   influence; to conduce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'n.', 'To carry on business; to be engaged or employed customarily;\n   to perform the part of a laborer; to labor; to toil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'n.', 'To be in a state of severe exertion, or as if in such a\n   state; to be tossed or agitated; to move heavily; to strain; to labor;\n   as, a ship works in a heavy sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'n.', 'To make ones way slowly and with difficulty; to move or\n   penetrate laboriously; to proceed with effort; -- with a following\n   preposition, as down, out, into, up, through, and the like; as, scheme\n   works out by degrees; to work into the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'n.', 'To ferment, as a liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'n.', 'To act or operate on the stomach and bowels, as a cathartic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'v. t.', 'To labor or operate upon; to give exertion and effort to;\n   to prepare for use, or to utilize, by labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'v. t.', 'To produce or form by labor; to bring forth by exertion or\n   toil; to accomplish; to originate; to effect; as, to work wood or iron\n   into a form desired, or into a utensil; to work cotton or wool into\n   cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'v. t.', 'To produce by slow degrees, or as if laboriously; to bring\n   gradually into any state by action or motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'v. t.', 'To influence by acting upon; to prevail upon; to manage;\n   to lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'v. t.', 'To form with a needle and thread or yarn; especially, to\n   embroider; as, to work muslin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'v. t.', 'To set in motion or action; to direct the action of; to\n   keep at work; to govern; to manage; as, to work a machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Work', 'v. t.', 'To cause to ferment, as liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workable', 'a.', 'Capable of being worked, or worth working; as, a workable\n   mine; workable clay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workaday', 'n.', 'See Workyday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workbag', 'n.', 'A bag for holding implements or materials for work;\n   especially, a reticule, or bag for holding needlework, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workbasket', 'n.', 'A basket for holding materials for needlework, or the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workbench', 'n.', 'A bench on which work is performed, as in a carpenters\n   shop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workbox', 'n.', 'A box for holding instruments or materials for work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workday', 'n. & a.', 'A day on which work is performed, as distinguished\n   from Sunday, festivals, etc., a working day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worker', 'n.', 'One who, or that which, works; a laborer; a performer; as,\n   a worker in brass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worker', 'n.', 'One of the neuter, or sterile, individuals of the social\n   ants, bees, and white ants. The workers are generally females having\n   the sexual organs imperfectly developed. See Ant, and White ant, under\n   White.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workfellow', 'n.', 'One engaged in the same work with another; a companion\n   in work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workfolk', 'n.', 'People that labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workful', 'a.', 'Full of work; diligent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workhouses', 'pl. ', 'of Workhouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workhouse', 'n.', 'A house where any manufacture is carried on; a workshop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workhouse', 'n.', 'A house in which idle and vicious persons are confined\n   to labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workhouse', 'n.', 'A house where the town poor are maintained at public\n   expense, and provided with labor; a poorhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Working', '', 'a & n. from Work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Working-day', 'a.', 'Pertaining to, or characteristic of, working days, or\n   workdays; everyday; hence, plodding; hard-working.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workingmen', 'pl. ', 'of Workingman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workingman', 'n.', 'A laboring man; a man who earns his daily support by\n   manual labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workless', 'a.', 'Without work; not laboring; as, many people were still\n   workless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workless', 'a.', 'Not carried out in practice; not exemplified in fact; as,\n   workless faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workmen', 'pl. ', 'of Workman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workman', 'n.', 'A man employed in labor, whether in tillage or\n   manufactures; a worker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workman', 'n.', 'Hence, especially, a skillful artificer or laborer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workmanlike', 'a.', 'Becoming a workman, especially a skillful one;\n   skillful; well performed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workmanly', 'a.', 'Becoming a skillful workman; skillful; well performed;\n   workmanlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workmanly', 'adv.', 'In a skillful manner; in a manner becoming a skillful\n   workman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workmanship', 'n.', 'The art or skill of a workman; the execution or manner\n   of making anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workmanship', 'n.', 'That which is effected, made, or produced;\n   manufacture, something made by manual labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workmaster', 'n.', 'The performer of any work; a master workman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workroom', 'n.', 'Any room or apartment used especially for labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workship', 'n.', 'Workmanship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workshop', 'n.', 'A shop where any manufacture or handiwork is carried on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worktable', 'n.', 'A table for holding working materials and implements;\n   esp., a small table with drawers and other conveniences for needlework,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workwomen', 'pl. ', 'of Workwoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workwoman', 'n.', 'A woman who performs any work; especially, a woman\n   skilled in needlework.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Workyday', 'n.', 'A week day or working day, as distinguished from Sunday\n   or a holiday. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'World', 'n.', 'The earth and the surrounding heavens; the creation; the\n   system of created things; existent creation; the universe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'World', 'n.', 'Any planet or heavenly body, especially when considered as\n   inhabited, and as the scene of interests analogous with human\n   interests; as, a plurality of worlds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'World', 'n.', 'The earth and its inhabitants, with their concerns; the sum\n   of human affairs and interests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'World', 'n.', 'In a more restricted sense, that part of the earth and its\n   concerns which is known to any one, or contemplated by any one; a\n   division of the globe, or of its inhabitants; human affairs as seen\n   from a certain position, or from a given point of view; also, state of\n   existence; scene of life and action; as, the Old World; the New World;\n   the religious world; the Catholic world; the upper world; the future\n   world; the heathen world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'World', 'n.', 'The customs, practices, and interests of men; general\n   affairs of life; human society; public affairs and occupations; as, a\n   knowledge of the world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'World', 'n.', 'Individual experience of, or concern with, life; course of\n   life; sum of the affairs which affect the individual; as, to begin the\n   world with no property; to lose all, and begin the world anew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'World', 'n.', 'The inhabitants of the earth; the human race; people in\n   general; the public; mankind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'World', 'n.', 'The earth and its affairs as distinguished from heaven;\n   concerns of this life as distinguished from those of the life to come;\n   the present existence and its interests; hence, secular affairs;\n   engrossment or absorption in the affairs of this life; worldly\n   corruption; the ungodly or wicked part of mankind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'World', 'n.', 'As an emblem of immensity, a great multitude or quantity; a\n   large number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worldliness', 'n.', 'The quality of being worldly; a predominant passion\n   for obtaining the good things of this life; covetousness; addictedness\n   to gain and temporal enjoyments; worldly-mindedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worldling', '', 'A person whose soul is set upon gaining temporal\n   possessions; one devoted to this world and its enjoyments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worldly', 'a.', 'Relating to the world; human; common; as, worldly maxims;\n   worldly actions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worldly', 'a.', 'Pertaining to this world or life, in contradistinction\n   from the life to come; secular; temporal; devoted to this life and its\n   enjoyments; bent on gain; as, worldly pleasures, affections, honor,\n   lusts, men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worldly', 'a.', 'Lay, as opposed to clerical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worldly', 'adv.', 'With relation to this life; in a worldly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worldly-minded', 'a.', 'Devoted to worldly interests; mindful of the\n   affairs of the present life, and forgetful of those of the future;\n   loving and pursuing this worlds goods, to the exclusion of piety and\n   attention to spiritual concerns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'World-wide', 'a.', 'Extended throughout the world; as, world-wide fame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worldlywise', 'a.', 'Wise in regard to things of this world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm', 'n.', 'A creeping or a crawling animal of any kind or size, as a\n   serpent, caterpillar, snail, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm', 'n.', 'Any small creeping animal or reptile, either entirely without\n   feet, or with very short ones, including a great variety of animals;\n   as, an earthworm; the blindworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm', 'n.', 'Any helminth; an entozoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm', 'n.', 'Any annelid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm', 'n.', 'An insect larva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm', 'n.', 'Same as Vermes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm', 'n.', 'An internal tormentor; something that gnaws or afflicts ones\n   mind with remorse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm', 'n.', 'A being debased and despised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm', 'n.', 'Anything spiral, vermiculated, or resembling a worm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm', 'n.', 'The thread of a screw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm', 'n.', 'A spiral instrument or screw, often like a double corkscrew,\n   used for drawing balls from firearms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm', 'n.', 'A certain muscular band in the tongue of some animals, as the\n   dog; the lytta. See Lytta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm', 'n.', 'The condensing tube of a still, often curved and wound to\n   economize space. See Illust. of Still.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm', 'n.', 'A short revolving screw, the threads of which drive, or are\n   driven by, a worm wheel by gearing into its teeth or cogs. See Illust.\n   of Worm gearing, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wormed', 'imp. & p. p.', 'of Worm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worming', 'p. pr. & vb. n.', 'of Worm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm', 'v. i.', 'To work slowly, gradually, and secretly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm', 'v. t.', 'To effect, remove, drive, draw, or the like, by slow and\n   secret means; -- often followed by out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm', 'v. t.', 'To clean by means of a worm; to draw a wad or cartridge\n   from, as a firearm. See Worm, n. 5 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm', 'n.', 'To cut the worm, or lytta, from under the tongue of, as a\n   dog, for the purpose of checking a disposition to gnaw. The operation\n   was formerly supposed to guard against canine madness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm', 'n.', 'To wind rope, yarn, or other material, spirally round,\n   between the strands of, as a cable; to wind with spun yarn, as a small\n   rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wormal', 'n.', 'See Wormil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm-eaten', 'a.', 'Eaten, or eaten into, by a worm or by worms; as,\n   worm-eaten timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm-eaten', 'a.', 'Worn-out; old; worthless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wormed', 'a.', 'Penetrated by worms; injured by worms; worm-eaten; as,\n   wormed timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wormhole', 'n.', 'A burrow made by a worm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wormian', 'a.', 'Discovered or described by Olanus Wormius, a Danish\n   anatomist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wormil', 'n.', 'Any botfly larva which burrows in or beneath the skin of\n   domestic and wild animals, thus producing sores. They belong to various\n   species of Hypoderma and allied genera. Domestic cattle are often\n   infested by a large species. See Gadfly. Called also warble, and\n   worble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wormil', 'n.', 'See 1st Warble, 1 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wormling', 'n.', 'A little worm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wormseed', 'n.', 'Any one of several plants, as Artemisia santonica, and\n   Chenopodium anthelminticum, whose seeds have the property of expelling\n   worms from the stomach and intestines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm-shaped', 'a.', 'Shaped like a worm; /hick and almost cylindrical, but\n   variously curved or bent; as, a worm-shaped root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worm-shell', 'n.', 'Any species of Vermetus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wormul', 'n.', 'See Wornil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wormwood', 'n.', 'A composite plant (Artemisia Absinthium), having a bitter\n   and slightly aromatic taste, formerly used as a tonic and a vermifuge,\n   and to protect woolen garments from moths. It gives the peculiar flavor\n   to the cordial called absinthe. The volatile oil is a narcotic poison.\n   The term is often extended to other species of the same genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wormwood', 'n.', 'Anything very bitter or grievous; bitterness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wormy', 'superl.', 'Containing a worm; abounding with worms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wormy', 'superl.', 'Like or pertaining to a worm; earthy; groveling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worn', '', 'p. p. of Wear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wornil', 'n.', 'See Wormil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worn-out', 'a.', 'Consumed, or rendered useless, by wearing; as, worn-out\n   garments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worral', 'n.', 'Alt. of Worrel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worrel', 'n.', 'An Egyptian fork-tongued lizard, about four feet long when\n   full grown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worrier', 'n.', 'One who worries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worriment', 'n.', 'Trouble; anxiety; worry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worrisome', 'a.', 'Inclined to worry or fret; also, causing worry or\n   annoyance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worrit', 'v. t.', 'To worry; to annoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worrit', 'n.', 'Worry; anxiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worried', 'imp. & p. p.', 'of Worry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worrying', 'p. pr. & vb. n.', 'of Worry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worry', 'v. t.', 'To harass by pursuit and barking; to attack repeatedly;\n   also, to tear or mangle with the teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worry', 'v. t.', 'To harass or beset with importunity, or with care an\n   anxiety; to vex; to annoy; to torment; to tease; to fret; to trouble;\n   to plague.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worry', 'v. t.', 'To harass with labor; to fatigue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worry', 'v. i.', 'To feel or express undue care and anxiety; to manifest\n   disquietude or pain; to be fretful; to chafe; as, the child worries;\n   the horse worries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worries', 'pl. ', 'of Worry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worry', 'n.', 'A state of undue solicitude; a state of disturbance from\n   care and anxiety; vexation; anxiety; fret; as, to be in a worry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worryingly', 'adv.', 'In a worrying manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worse', 'compar.', 'Bad, ill, evil, or corrupt, in a greater degree; more\n   bad or evil; less good; specifically, in poorer health; more sick; --\n   used both in a physical and moral sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worse', 'n.', 'Loss; disadvantage; defeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worse', 'n.', 'That which is worse; something less good; as, think not the\n   worse of him for his enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worse', 'a.', 'In a worse degree; in a manner more evil or bad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worse', 'v. t.', 'To make worse; to put disadvantage; to discomfit; to\n   worst. See Worst, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worsen', 'v. t.', 'To make worse; to deteriorate; to impair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worsen', 'v. t.', 'To get the better of; to worst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worsen', 'v. i.', 'To grow or become worse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worser', 'a.', 'Worse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worship', 'a.', 'Excellence of character; dignity; worth; worthiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worship', 'a.', 'Honor; respect; civil deference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worship', 'a.', 'Hence, a title of honor, used in addresses to certain\n   magistrates and others of rank or station.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worship', 'a.', 'The act of paying divine honors to the Supreme Being;\n   religious reverence and homage; adoration, or acts of reverence, paid\n   to God, or a being viewed as God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worship', 'a.', 'Obsequious or submissive respect; extravagant admiration;\n   adoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worship', 'a.', 'An object of worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worshiped', 'imp. & p. p.', 'of Worship');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worshipped', '', 'of Worship');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worshiping', 'p. pr. & vb. n.', 'of Worship');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worshipping', '', 'of Worship');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worship', 'v. t.', 'To respect; to honor; to treat with civil reverence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worship', 'v. t.', 'To pay divine honors to; to reverence with supreme\n   respect and veneration; to perform religious exercises in honor of; to\n   adore; to venerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worship', 'v. t.', 'To honor with extravagant love and extreme submission,\n   as a lover; to adore; to idolize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worship', 'v. i.', 'To perform acts of homage or adoration; esp., to\n   perform religious service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worshipability', 'n.', 'The quality of being worthy to be worshiped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worshipable', 'a.', 'Capable of being worshiped; worthy of worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worshiper', 'n.', 'One who worships; one who pays divine honors to any\n   being or thing; one who adores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worshipful', 'a.', 'Entitled to worship, reverence, or high respect;\n   claiming respect; worthy of honor; -- often used as a term of respect,\n   sometimes ironically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worst', 'a.', 'Bad, evil, or pernicious, in the highest degree, whether in\n   a physical or moral sense. See Worse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worst', 'n.', 'That which is most bad or evil; the most severe, pernicious,\n   calamitous, or wicked state or degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worsted', 'imp. & p. p.', 'of Worst');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worsting', 'p. pr. & vb. n.', 'of Worst');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worst', 'a.', 'To gain advantage over, in contest or competition; to get\n   the better of; to defeat; to overthrow; to discomfit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worst', 'v. i.', 'To grow worse; to deteriorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worsted', 'n.', 'Well-twisted yarn spun of long-staple wool which has been\n   combed to lay the fibers parallel, used for carpets, cloth, hosiery,\n   gloves, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worsted', 'n.', 'Fine and soft woolen yarn, untwisted or lightly twisted,\n   used in knitting and embroidery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wort', 'n.', 'A plant of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wort', 'n.', 'Cabbages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wort', 'n.', 'An infusion of malt which is unfermented, or is in the act of\n   fermentation; the sweet infusion of malt, which ferments and forms\n   beer; hence, any similar liquid in a state of incipient fermentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worth', 'v. i.', 'To be; to become; to betide; -- now used only in the\n   phrases, woe worth the day, woe worth the man, etc., in which the verb\n   is in the imperative, and the nouns day, man, etc., are in the dative.\n   Woe be to the day, woe be to the man, etc., are equivalent phrases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worth', 'a.', 'Valuable; of worthy; estimable; also, worth while.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worth', 'a.', 'Equal in value to; furnishing an equivalent for; proper to\n   be exchanged for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worth', 'a.', 'Deserving of; -- in a good or bad sense, but chiefly in a\n   good sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worth', 'a.', 'Having possessions equal to; having wealth or estate to the\n   value of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worth', 'a.', 'That quality of a thing which renders it valuable or useful;\n   sum of valuable qualities which render anything useful and sought;\n   value; hence, often, value as expressed in a standard, as money;\n   equivalent in exchange; price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worth', 'a.', 'Value in respect of moral or personal qualities; excellence;\n   virtue; eminence; desert; merit; usefulness; as, a man or magistrate of\n   great worth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worthful', 'a.', 'Full of worth; worthy; deserving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worthily', 'adv.', 'In a worthy manner; excellently; deservedly; according\n   to merit; justly; suitably; becomingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worthiness', 'n.', 'The quality or state of being worthy; desert; merit;\n   excellence; dignity; virtue; worth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worthless', 'a.', 'Destitute of worth; having no value, virtue, excellence,\n   dignity, or the like; undeserving; valueless; useless; vile; mean; as,\n   a worthless garment; a worthless ship; a worthless man or woman; a\n   worthless magistrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'worthwhile', 'adj.', 'Worth the time or effort spent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worthy', 'n.', 'Having worth or excellence; possessing merit; valuable;\n   deserving; estimable; excellent; virtuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worthy', 'n.', 'Having suitable, adapted, or equivalent qualities or value;\n   -- usually with of before the thing compared or the object; more\n   rarely, with a following infinitive instead of of, or with that; as,\n   worthy of, equal in excellence, value, or dignity to; entitled to;\n   meriting; -- usually in a good sense, but sometimes in a bad one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worthy', 'n.', 'Of high station; of high social position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worthies', 'pl. ', 'of Worthy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worthy', 'n.', 'A man of eminent worth or value; one distinguished for\n   useful and estimable qualities; a person of conspicuous desert; -- much\n   used in the plural; as, the worthies of the church; political worthies;\n   military worthies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Worthy', 'v. t.', 'To render worthy; to exalt into a hero.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wost', '', '2d pers. sing. pres. of Wit, to know.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wot', '', '1st & 3d pers. sing. pres. of Wit, to know. See the Note under\n   Wit, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wotest', '', 'Alt. of Wottest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wottest', '', '2d pers. sing. pres. of Wit, to know.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woteth', '', 'Alt. of Wotteth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wotteth', '', '3d pers. sing. pres. of Wit, to know.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woul', 'v. i.', 'To howl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Would', 'v. t.', 'Commonly used as an auxiliary verb, either in the past\n   tense or in the conditional or optative present. See 2d & 3d Will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Would', 'n.', 'See 2d Weld.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Would-be', 'a.', 'Desiring or professing to be; vainly pretending to be;\n   as, a would-be poet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woulding', 'n.', 'Emotion of desire; inclination; velleity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wouldingness', 'n.', 'Willingness; desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woulfe bottle', 'n.', 'A kind of wash bottle with two or three necks; -- so\n   called after the inventor, Peter Woulfe, an English chemist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wound', '', 'imp. & p. p. of Wind to twist, and Wind to sound by blowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wound', 'n.', 'A hurt or injury caused by violence; specifically, a breach\n   of the skin and flesh of an animal, or in the substance of any creature\n   or living thing; a cut, stab, rent, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wound', 'n.', 'Fig.: An injury, hurt, damage, detriment, or the like, to\n   feeling, faculty, reputation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wound', 'n.', 'An injury to the person by which the skin is divided, or its\n   continuity broken; a lesion of the body, involving some solution of\n   continuity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wounded', 'imp. & p. p.', 'of Wound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wounding', 'p. pr. & vb. n.', 'of Wound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wound', 'n.', 'To hurt by violence; to produce a breach, or separation of\n   parts, in, as by a cut, stab, blow, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wound', 'n.', 'To hurt the feelings of; to pain by disrespect, ingratitude,\n   or the like; to cause injury to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woundable', 'a.', 'Capable of being wounded; vulnerable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wounder', 'n.', 'One who, or that which, wounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woundily', 'adv.', 'In a woundy manner; excessively; woundy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woundless', 'a.', 'Free from wound or hurt; exempt from being wounded;\n   invulnerable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woundwort', 'n.', 'Any one of certain plants whose soft, downy leaves have\n   been used for dressing wounds, as the kidney vetch, and several species\n   of the labiate genus Stachys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woundy', 'a.', 'Excessive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woundy', 'adv.', 'Excessively; extremely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wourali', 'n.', 'Same as Curare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wou-wou', 'n.', 'The agile, or silvery, gibbon; -- called also camper. See\n   Gibbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wove', '', 'p. pr. & rare vb. n. of Weave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woven', '', 'p. p. of Weave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wowe', 'v. t. & i.', 'To woo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wowf', 'a.', 'Disordered or unsettled in intellect; deranged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wowke', 'n.', 'Week.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wow-wow', 'n.', 'See Wou-wou.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wox', '', 'imp. of Wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Woxen', '', 'p. p. of Wax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrack', 'n.', 'A thin, flying cloud; a rack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrack', 'v. t.', 'To rack; to torment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrack', 'n.', 'Wreck; ruin; destruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrack', 'n.', 'Any marine vegetation cast up on the shore, especially\n   plants of the genera Fucus, Laminaria, and Zostera, which are most\n   abundant on northern shores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrack', 'n.', 'Coarse seaweed of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrack', 'v. t.', 'To wreck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrackful', 'a.', 'Ruinous; destructive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrain-bolt', 'n.', 'Same as Wringbolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wraith', 'n.', 'An apparition of a person in his exact likeness, seen\n   before death, or a little after; hence, an apparition; a specter; a\n   vision; an unreal image.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wraith', 'n.', 'Sometimes, improperly, a spirit thought to preside over the\n   waters; -- called also water wraith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrangled', 'imp. & p. p.', 'of Wrangle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrangling', 'p. pr. & vb. n.', 'of Wrangle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrangle', 'v. i.', 'To argue; to debate; to dispute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrangle', 'v. i.', 'To dispute angrily; to quarrel peevishly and noisily;\n   to brawl; to altercate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrangle', 'v. t.', 'To involve in a quarrel or dispute; to embroil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrangle', 'n.', 'An angry dispute; a noisy quarrel; a squabble; an\n   altercation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrangler', 'n.', 'An angry disputant; one who disputes with heat or\n   peevishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrangler', 'n.', 'One of those who stand in the first rank of honors in the\n   University of Cambridge, England. They are called, according to their\n   rank, senior wrangler, second wrangler, third wrangler, etc. Cf.\n   Optime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wranglership', 'n.', 'The honor or position of being a wrangler at the\n   University of Cambridge, England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wranglesome', 'a.', 'Contentious; quarrelsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrannock', 'n.', 'Alt. of Wranny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wranny', 'n.', 'The common wren.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrap', 'v. t.', 'To snatch up; transport; -- chiefly used in the p. p.\n   wrapt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrapped', 'imp. & p. p.', 'of Wrap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrapt', '', 'of Wrap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrapping', 'p. pr. & vb. n.', 'of Wrap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrap', 'v. t.', 'To wind or fold together; to arrange in folds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrap', 'v. t.', 'To cover by winding or folding; to envelop completely; to\n   involve; to infold; -- often with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrap', 'v. t.', 'To conceal by enveloping or infolding; to hide; hence, to\n   involve, as an effect or consequence; to be followed by.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrap', 'n.', 'A wrapper; -- often used in the plural for blankets, furs,\n   shawls, etc., used in riding or traveling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrappage', 'n.', 'The act of wrapping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrappage', 'n.', 'That which wraps; envelope; covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrapper', 'n.', 'One who, or that which, wraps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrapper', 'n.', 'That in which anything is wrapped, or inclosed; envelope;\n   covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrapper', 'n.', 'Specifically, a loose outer garment; an article of dress\n   intended to be wrapped round the person; as, a morning wrapper; a\n   gentlemans wrapper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wraprascal', 'n.', 'A kind of coarse upper coat, or overcoat, formerly\n   worn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrasse', 'n.', 'Any one of numerous edible, marine, spiny-finned fishes of\n   the genus Labrus, of which several species are found in the\n   Mediterranean and on the Atlantic coast of Europe. Many of the species\n   are bright-colored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrastle', 'v. i.', 'To wrestle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrath', 'a.', 'Violent anger; vehement exasperation; indignation; rage;\n   fury; ire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrath', 'a.', 'The effects of anger or indignation; the just punishment of\n   an offense or a crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrath', 'a.', 'See Wroth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrath', 'v. t.', 'To anger; to enrage; -- also used impersonally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrathful', 'a.', 'Full of wrath; very angry; greatly incensed; ireful;\n   passionate; as, a wrathful man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrathful', 'a.', 'Springing from, or expressing, wrath; as, a wrathful\n   countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrathily', 'adv.', 'In a wrathy manner; very angrily; wrathfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrathless', 'a.', 'Free from anger or wrath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrathy', 'a.', 'Very angry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wraw', 'a.', 'Angry; vexed; wrathful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrawful', 'a.', 'Ill-tempered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrawl', 'v. i.', 'To cry, as a cat; to waul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrawness', 'n.', 'Peevishness; ill temper; anger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wray', 'v. t.', 'To reveal; to disclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreak', 'v. i.', 'To reck; to care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreaked', 'imp. & p. p.', 'of Wreak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreaking', 'p. pr. & vb. n.', 'of Wreak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreak', 'v. t.', 'To revenge; to avenge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreak', 'v. t.', 'To execute in vengeance or passion; to inflict; to hurl\n   or drive; as, to wreak vengeance on an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreak', 'v. t.', 'Revenge; vengeance; furious passion; resentment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreaken', '', 'p. p. of Wreak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreaker', 'n.', 'Avenger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreakful', 'a.', 'Revengeful; angry; furious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreakless', 'a.', 'Unrevengeful; weak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreaths', 'pl. ', 'of Wreath');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreath', 'n.', 'Something twisted, intertwined, or curled; as, a wreath of\n   smoke; a wreath of flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreath', 'n.', 'A garland; a chaplet, esp. one given to a victor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreath', 'n.', 'An appendage to the shield, placed above it, and supporting\n   the crest (see Illust. of Crest). It generally represents a twist of\n   two cords of silk, one tinctured like the principal metal, the other\n   like the principal color in the arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreathed', 'imp.', 'of Wreathe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreathed', 'p. p.', 'of Wreathe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreathen', 'Archaic', 'of Wreathe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreathing', 'p. pr. & vb. n.', 'of Wreathe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreathe', 'n.', 'To cause to revolve or writhe; to twist about; to turn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreathe', 'n.', 'To twist; to convolve; to wind one about another; to\n   entwine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreathe', 'n.', 'To surround with anything twisted or convolved; to\n   encircle; to infold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreathe', 'n.', 'To twine or twist about; to surround; to encircle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreathe', 'v. i.', 'To be intewoven or entwined; to twine together; as, a\n   bower of wreathing trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreathen', 'a.', 'Twisted; made into a wreath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreathless', 'a.', 'Destitute of a wreath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreath-shell', 'n.', 'A marine shell of the genus Turbo. See Turbo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreathy', 'a.', 'Wreathed; twisted; curled; spiral; also, full of wreaths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrecche', 'n.', 'A wretch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrecche', 'a.', 'Wretched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreche', 'n.', 'Wreak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreck', 'v. t. & n.', 'See 2d & 3d Wreak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreck', 'v. t.', 'The destruction or injury of a vessel by being cast on\n   shore, or on rocks, or by being disabled or sunk by the force of winds\n   or waves; shipwreck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreck', 'v. t.', 'Destruction or injury of anything, especially by\n   violence; ruin; as, the wreck of a railroad train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreck', 'v. t.', 'The ruins of a ship stranded; a ship dashed against rocks\n   or land, and broken, or otherwise rendered useless, by violence and\n   fracture; as, they burned the wreck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreck', 'v. t.', 'The remain of anything ruined or fatally injured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreck', 'v. t.', 'Goods, etc., which, after a shipwreck, are cast upon the\n   land by the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrecked', 'imp. & p. p.', 'of Wreck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrecking', 'p. pr. & vb. n.', 'of Wreck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreck', 'v. t.', 'To destroy, disable, or seriously damage, as a vessel, by\n   driving it against the shore or on rocks, by causing it to become\n   unseaworthy, to founder, or the like; to shipwreck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreck', 'v. t.', 'To bring wreck or ruin upon by any kind of violence; to\n   destroy, as a railroad train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreck', 'v. t.', 'To involve in a wreck; hence, to cause to suffer ruin; to\n   balk of success, and bring disaster on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreck', 'v. i.', 'To suffer wreck or ruin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreck', 'v. i.', 'To work upon a wreck, as in saving property or lives, or\n   in plundering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreckage', 'n.', 'The act of wrecking, or state of being wrecked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreckage', 'n.', 'That which has been wrecked; remains of a wreck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrecker', 'n.', 'One who causes a wreck, as by false lights, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrecker', 'n.', 'One who searches fro, or works upon, the wrecks of\n   vessels, etc. Specifically: (a) One who visits a wreck for the purpose\n   of plunder. (b) One who is employed in saving property or lives from a\n   wrecked vessel, or in saving the vessel; as, the wreckers of Key West.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrecker', 'n.', 'A vessel employed by wreckers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreckfish', 'n.', 'A stone bass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreckful', 'a.', 'Causing wreck; involving ruin; destructive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrecking', '', 'a. & n. from Wreck, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreck-master', 'n.', 'A person appointed by law to take charge of goods,\n   etc., thrown on shore after a shipwreck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreke', 'v. t.', 'Alt. of Wreeke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wreeke', 'v. t.', 'See 2d Wreak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wren', 'n.', 'Any one of numerous species of small singing birds belonging\n   to Troglodytes and numerous allied of the family Troglodytidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wren', 'n.', 'Any one of numerous species of small singing birds more or\n   less resembling the true wrens in size and habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrench', 'v. t.', 'Trick; deceit; fraud; stratagem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrench', 'v. t.', 'A violent twist, or a pull with twisting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrench', 'v. t.', 'A sprain; an injury by twisting, as in a joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrench', 'v. t.', 'Means; contrivance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrench', 'v. t.', 'An instrument, often a simple bar or lever with jaws or\n   an angular orifice either at the end or between the ends, for exerting\n   a twisting strain, as in turning bolts, nuts, screw taps, etc.; a screw\n   key. Many wrenches have adjustable jaws for grasping nuts, etc., of\n   different sizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrench', 'v. t.', 'The system made up of a force and a couple of forces in\n   a plane perpendicular to that force. Any number of forces acting at any\n   points upon a rigid body may be compounded so as to be equivalent to a\n   wrench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrenched', 'imp. & p. p.', 'of Wrench');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrenching', 'p. pr. & vb. n.', 'of Wrench');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrench', 'n.', 'To pull with a twist; to wrest, twist, or force by\n   violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrench', 'n.', 'To strain; to sprain; hence, to distort; to pervert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrested', 'imp. & p. p.', 'of Wrest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wresting', 'p. pr. & vb. n.', 'of Wrest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrest', 'v. t.', 'To turn; to twist; esp., to twist or extort by violence;\n   to pull of force away by, or as if by, violent wringing or twisting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrest', 'v. t.', 'To turn from truth; to twist from its natural or proper\n   use or meaning by violence; to pervert; to distort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrest', 'v. t.', 'To tune with a wrest, or key.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrest', 'n.', 'The act of wresting; a wrench; a violent twist; hence,\n   distortion; perversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrest', 'n.', 'Active or moving power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrest', 'n.', 'A key to tune a stringed instrument of music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrest', 'n.', 'A partition in a water wheel, by which the form of the\n   buckets is determined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrester', 'n.', 'One who wrests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrestled', 'imp. & p. p.', 'of Wrestle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrestling', 'p. pr. & vb. n.', 'of Wrestle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrestle', 'v. t.', 'To contend, by grappling with, and striving to trip or\n   throw down, an opponent; as, they wrestled skillfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrestle', 'v. t.', 'Hence, to struggle; to strive earnestly; to contend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrestle', 'v. t.', 'To wrestle with; to seek to throw down as in wrestling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrestle', 'n.', 'A struggle between two persons to see which will throw the\n   other down; a bout at wrestling; a wrestling match; a struggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrestler', 'n.', 'One who wrestles; one who is skillful in wrestling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wretch', 'v. t.', 'A miserable person; one profoundly unhappy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wretch', 'v. t.', 'One sunk in vice or degradation; a base, despicable\n   person; a vile knave; as, a profligate wretch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wretched', 'a.', 'Very miserable; sunk in, or accompanied by, deep\n   affliction or distress, as from want, anxiety, or grief; calamitous;\n   woeful; very afflicting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wretched', 'a.', 'Worthless; paltry; very poor or mean; miserable; as, a\n   wretched poem; a wretched cabin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wretched', 'a.', 'Hatefully contemptible; despicable; wicked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wretchedly', 'adv.', 'In a wretched manner; miserably; despicable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wretchedness', 'n.', 'The quality or state of being wretched; utter misery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wretchedness', 'n.', 'A wretched object; anything despicably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wretchful', 'a.', 'Wretched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wretchless', 'a.', 'Reckless; hence, disregarded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrey', 'v. t.', 'See Wray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrie', 'a. & v.', 'See Wry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrig', 'v. i.', 'To wriggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wriggled', 'imp. & p. p.', 'of Wriggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wriggling', 'p. pr. & vb. n.', 'of Wriggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wriggle', 'v. i.', 'To move the body to and fro with short, writhing\n   motions, like a worm; to squirm; to twist uneasily or quickly about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wriggle', 'v. t.', 'To move with short, quick contortions; to move by\n   twisting and squirming; like a worm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wriggle', 'a.', 'Wriggling; frisky; pliant; flexible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wriggler', 'n.', 'One who, or that which, wriggles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wright', 'n.', 'One who is engaged in a mechanical or manufacturing\n   business; an artificer; a workman; a manufacturer; a mechanic; esp., a\n   worker in wood; -- now chiefly used in compounds, as in millwright,\n   wheelwright, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrightine', 'n.', 'A rare alkaloid found in the bark of an East Indian\n   apocynaceous tree (Wrightia antidysenterica), and extracted as a bitter\n   white crystalline substance. It was formerly used as a remedy for\n   diarrh/a. Called also conessine, and neriine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrung', 'imp. & p. p.', 'of Wring');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wringed', '', 'of Wring');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wringing', 'p. pr. & vb. n.', 'of Wring');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wring', 'v. t.', 'To twist and compress; to turn and strain with violence;\n   to writhe; to squeeze hard; to pinch; as, to wring clothes in washing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wring', 'v. t.', 'Hence, to pain; to distress; to torment; to torture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wring', 'v. t.', 'To distort; to pervert; to wrest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wring', 'v. t.', 'To extract or obtain by twisting and compressing; to\n   squeeze or press (out); hence, to extort; to draw forth by violence, or\n   against resistance or repugnance; -- usually with out or form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wring', 'v. t.', 'To subject to extortion; to afflict, or oppress, in order\n   to enforce compliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wring', 'v. t.', 'To bend or strain out of its position; as, to wring a\n   mast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wring', 'v. i.', 'To writhe; to twist, as with anguish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wring', 'n.', 'A writhing, as in anguish; a twisting; a griping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wringbolt', 'n.', 'A bolt used by shipwrights, to bend and secure the\n   planks against the timbers till they are fastened by bolts, spikes, or\n   treenails; -- not to be confounded with ringbolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wringer', 'n.', 'One who, or that which, wrings; hence, an extortioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wringer', 'n.', 'A machine for pressing water out of anything, particularly\n   from clothes after they have been washed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wringing', '', 'a. & n. from Wring, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wringstaves', 'pl. ', 'of Wringstaff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wringstaff', 'n.', 'A strong piece of plank used in applying wringbolts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrinkle', 'n.', 'A winkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrinkle', 'n.', 'A small ridge, prominence, or furrow formed by the\n   shrinking or contraction of any smooth substance; a corrugation; a\n   crease; a slight fold; as, wrinkle in the skin; a wrinkle in cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrinkle', 'n.', 'hence, any roughness; unevenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrinkle', 'n.', 'A notion or fancy; a whim; as, to have a new wrinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrinkled', 'imp. & p. p.', 'of Wrinkle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrinkling', 'p. pr. & vb. n.', 'of Wrinkle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrinkle', 'v. t.', 'To contract into furrows and prominences; to make a\n   wrinkle or wrinkles in; to corrugate; as, wrinkle the skin or the brow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrinkle', 'v. t.', 'Hence, to make rough or uneven in any way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrinkle', 'v. i.', 'To shrink into furrows and ridges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrinkly', 'a.', 'Full of wrinkles; having a tendency to be wrinkled;\n   corrugated; puckered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrist', 'n.', 'The joint, or the region of the joint, between the hand and\n   the arm; the carpus. See Carpus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrist', 'n.', 'A stud or pin which forms a journal; -- also called wrist\n   pin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wristband', 'n.', 'The band of the sleeve of a shirt, or other garment,\n   which covers the wrist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrister', 'n.', 'A covering for the wrist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wristlet', 'n.', 'An elastic band worn around the wrist, as for the purpose\n   of securing the upper part of a glove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writ', 'obs.', '3d pers. sing. pres. of Write, for writeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writ', '', 'imp. & p. p. of Write.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writ', 'n.', 'That which is written; writing; scripture; -- applied\n   especially to the Scriptures, or the books of the Old and New\n   testaments; as, sacred writ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writ', 'n.', 'An instrument in writing, under seal, in an epistolary form,\n   issued from the proper authority, commanding the performance or\n   nonperformance of some act by the person to whom it is directed; as, a\n   writ of entry, of error, of execution, of injunction, of mandamus, of\n   return, of summons, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writability', 'n.', 'Ability or capacity to write.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writable', 'a.', 'Capable of, or suitable for, being written down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writative', 'a.', 'Inclined to much writing; -- correlative to talkative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrote', 'imp.', 'of Write');
  INSERT INTO words (word, word_type, description) VALUES  (    'Written', 'p. p.', 'of Write');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writ', 'Archaic imp. & p. p.', 'of Write');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writing', 'p. pr. & vb. n.', 'of Write');
  INSERT INTO words (word, word_type, description) VALUES  (    'Write', 'v. t.', 'To set down, as legible characters; to form the\n   conveyance of meaning; to inscribe on any material by a suitable\n   instrument; as, to write the characters called letters; to write\n   figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Write', 'v. t.', 'To set down for reading; to express in legible or\n   intelligible characters; to inscribe; as, to write a deed; to write a\n   bill of divorcement; hence, specifically, to set down in an epistle; to\n   communicate by letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Write', 'v. t.', 'Hence, to compose or produce, as an author.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Write', 'v. t.', 'To impress durably; to imprint; to engrave; as, truth\n   written on the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Write', 'v. t.', 'To make known by writing; to record; to prove by ones\n   own written testimony; -- often used reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Write', 'v. i.', 'To form characters, letters, or figures, as\n   representative of sounds or ideas; to express words and sentences by\n   written signs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Write', 'v. i.', 'To be regularly employed or occupied in writing, copying,\n   or accounting; to act as clerk or amanuensis; as, he writes in one of\n   the public offices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Write', 'v. i.', 'To frame or combine ideas, and express them in written\n   words; to play the author; to recite or relate in books; to compose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Write', 'v. i.', 'To compose or send letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writer', 'n.', 'One who writes, or has written; a scribe; a clerk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writer', 'n.', 'One who is engaged in literary composition as a profession;\n   an author; as, a writer of novels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writer', 'n.', 'A clerk of a certain rank in the service of the late East\n   India Company, who, after serving a certain number of years, became a\n   factor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writership', 'n.', 'The office of a writer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writhed', 'imp.', 'of Writhe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writhed', 'p. p.', 'of Writhe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writhen', '', 'of Writhe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writhing', 'p. pr. & vb. n.', 'of Writhe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writhe', 'v. t.', 'To twist; to turn; now, usually, to twist or turn so as\n   to distort; to wring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writhe', 'v. t.', 'To wrest; to distort; to pervert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writhe', 'v. t.', 'To extort; to wring; to wrest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writhe', 'v. i.', 'To twist or contort the body; to be distorted; as, to\n   writhe with agony. Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writhen', 'a.', 'Having a twisted distorted from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writhle', 'v. t.', 'To wrinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writing', 'n.', 'The act or art of forming letters and characters on paper,\n   wood, stone, or other material, for the purpose of recording the ideas\n   which characters and words express, or of communicating them to others\n   by visible signs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writing', 'n.', 'Anything written or printed; anything expressed in\n   characters or letters');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writing', 'n.', 'Any legal instrument, as a deed, a receipt, a bond, an\n   agreement, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writing', 'n.', 'Any written composition; a pamphlet; a work; a literary\n   production; a book; as, the writings of Addison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writing', 'n.', 'An inscription.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Writing', 'n.', 'Handwriting; chirography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Written', '', 'p. p. of Write, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrizzle', 'v. t.', 'To wrinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wroken', '', 'p. p. of Wreak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrong', '', 'imp. of Wring. Wrung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrong', 'a.', 'Twisted; wry; as, a wrong nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrong', 'a.', 'Not according to the laws of good morals, whether divine or\n   human; not suitable to the highest and best end; not morally right;\n   deviating from rectitude or duty; not just or equitable; not true; not\n   legal; as, a wrong practice; wrong ideas; wrong inclinations and\n   desires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrong', 'a.', 'Not fit or suitable to an end or object; not appropriate for\n   an intended use; not according to rule; unsuitable; improper;\n   incorrect; as, to hold a book with the wrong end uppermost; to take the\n   wrong way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrong', 'a.', 'Not according to truth; not conforming to fact or intent;\n   not right; mistaken; erroneous; as, a wrong statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrong', 'a.', 'Designed to be worn or placed inward; as, the wrong side of\n   a garment or of a piece of cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrong', 'adv.', 'In a wrong manner; not rightly; amiss; morally ill;\n   erroneously; wrongly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrong', 'a.', 'That which is not right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrong', 'a.', 'Nonconformity or disobedience to lawful authority, divine or\n   human; deviation from duty; -- the opposite of moral right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrong', 'a.', 'Deviation or departure from truth or fact; state of falsity;\n   error; as, to be in the wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrong', 'a.', 'Whatever deviates from moral rectitude; usually, an act that\n   involves evil consequences, as one which inflicts injury on a person;\n   any injury done to, or received from; another; a trespass; a violation\n   of right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wronged', 'imp. & p. p.', 'of Wrong');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wronging', 'p. pr. & vb. n.', 'of Wrong');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrong', 'v. t.', 'To treat with injustice; to deprive of some right, or to\n   withhold some act of justice from; to do undeserved harm to; to deal\n   unjustly with; to injure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrong', 'v. t.', 'To impute evil to unjustly; as, if you suppose me capable\n   of a base act, you wrong me.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrongdoer', 'n.', 'One who injures another, or who does wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrongdoer', 'n.', 'One who commits a tort or trespass; a trespasser; a tort\n   feasor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrongdoing', 'n.', 'Evil or wicked behavior or action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wronger', 'n.', 'One who wrongs or injures another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrongful', 'a.', 'Full of wrong; injurious; unjust; unfair; as, a wrongful\n   taking of property; wrongful dealing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wronghead', 'n.', 'A person of a perverse understanding or obstinate\n   character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wronghead', 'a.', 'Wrongheaded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrongheaded', 'a.', 'Wrong in opinion or principle; having a perverse\n   understanding; perverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrongless', 'a.', 'Not wrong; void or free from wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrongly', 'adv.', 'In a wrong manner; unjustly; erroneously; wrong; amiss;\n   as, he judges wrongly of my motives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrongness', 'n.', 'The quality or state of being wrong; wrongfulness;\n   error; fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrongous', 'a.', 'Constituting, or of the nature of, a wrong; unjust;\n   wrongful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrongous', 'a.', 'Not right; illegal; as, wrongous imprisonment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrong-timed', 'a.', 'Done at an improper time; ill-timed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wroot', '', 'imp. of Write. Wrote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrote', 'v. i.', 'To root with the snout. See 1st Root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrote', '', 'imp. & archaic p. p. of Write.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wroth', 'a.', 'Full of wrath; angry; incensed; much exasperated; wrathful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrought', '', 'imp. & p. p. of Work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrought', 'a.', 'Worked; elaborated; not rough or crude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrung', '', 'imp. & p. p. of Wring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wry', 'v. t.', 'To cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wry', 'superl.', 'Turned to one side; twisted; distorted; as, a wry mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wry', 'superl.', 'Hence, deviating from the right direction; misdirected;\n   out of place; as, wry words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wry', 'superl.', 'Wrested; perverted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wry', 'v. i.', 'To twist; to writhe; to bend or wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wry', 'v. i.', 'To deviate from the right way; to go away or astray; to\n   turn side; to swerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wried', 'imp. & p. p.', 'of Wry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrying', 'p. pr. & vb. n.', 'of Wry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wry', 'a.', 'To twist; to distort; to writhe; to wrest; to vex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrybill', 'n.', 'See Crookbill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrymouth', 'n.', 'Any one of several species of large, elongated, marine\n   fishes of the genus Cryptacanthodes, especially C. maculatus of the\n   American coast. A whitish variety is called ghostfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wryneck', 'n.', 'A twisted or distorted neck; a deformity in which the neck\n   is drawn to one side by a rigid contraction of one of the muscles of\n   the neck; torticollis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wryneck', 'n.', 'Any one of several species of Old World birds of the genus\n   Jynx, allied to the woodpeckers; especially, the common European\n   species (J. torguilla); -- so called from its habit of turning the neck\n   around in different directions. Called also cuckoos mate, snakebird,\n   summer bird, tonguebird, and writheneck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrynecked', 'a.', 'Having a distorted neck; having the deformity called\n   wryneck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wryness', 'n.', 'The quality or state of being wry, or distorted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wrythen', 'p. p.', 'Writhen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wulfenite', 'n.', 'Native lead molybdate occurring in tetragonal crystals,\n   usually tabular, and of a bright orange-yellow to red, gray, or brown\n   color; -- also called yellow lead ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wull', 'v. t. & i.', 'See 2d Will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wung-out', 'a.', 'Having the sails set in the manner called wing-and-wing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wurbagool', 'n.', 'A fruit bat (Pteropus medius) native of India. It is\n   similar to the flying fox, but smaller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wurmal', 'n.', 'See Wormil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wurraluh', 'n.', 'The Australian white-quilled honey eater (Entomyza\n   albipennis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wust', '', 'Alt. of Wuste');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wuste', '', 'imp. of Wit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wyandots', 'n. pl.', 'Same as Hurons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wych-elm', 'n.', 'A species of elm (Ulmus montana) found in Northern and\n   Western Europe; Scotch elm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wych-hazel', 'n.', 'The wych-elm; -- so called because its leaves are like\n   those of the hazel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wyclifite', 'n.', 'Alt. of Wycliffite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wycliffite', 'n.', 'A follower of Wyclif, the English reformer; a Lollard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wyd', 'a.', 'Wide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wyes', 'pl. ', 'of Wye');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wye', 'n.', 'The letter Y.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wye', 'n.', 'A kind of crotch. See Y, n. (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wyke', 'n.', 'Week.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wyla', 'n.', 'A helmeted Australian cockatoo (Calyptorhynchus funereus); --\n   called also funeral cockatoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wynd', 'n.', 'A narrow lane or alley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wynkernel', 'n.', 'The European moor hen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wynn', 'n.', 'A kind of timber truck, or carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wype', 'n.', 'The wipe, or lapwing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wythe', 'n.', 'Same as Withe, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wys', 'a.', 'Wise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wyte', '', 'Alt. of Wyten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wyten', '', 'pl. pres. of Wit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Wyvern', 'n.', 'Same as Wiver.\n   X () X, the twenty-fourth letter of the English alphabet, has three\n   sounds; a compound nonvocal sound (that of ks), as in wax; a compound\n   vocal sound (that of gz), as in example; and, at the beginning of a\n   word, a simple vocal sound (that of z), as in xanthic. See Guide to\n   Pronunciation, // 217, 270, 271.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthamide', 'n.', 'An amido derivative of xanthic acid obtained as a white\n   crystalline substance, C2H5O.CS.NH2; -- called also xanthogen amide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthate', 'n.', 'A salt of xanthic; a xanthogenate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthelasma', 'n.', 'See Xanthoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthian', 'a.', 'Of or pertaining to Xanthus, an ancient town on Asia\n   Minor; -- applied especially to certain marbles found near that place,\n   and now in the British Museum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthic', 'a.', 'Tending toward a yellow color, or to one of those colors,\n   green being excepted, in which yellow is a constituent, as scarlet,\n   orange, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthic', 'a.', 'Possessing, imparting, or producing a yellow color; as,\n   xanthic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthic', 'a.', 'Of or pertaining to xanthic acid, or its compounds;\n   xanthogenic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthic', 'a.', 'Of or pertaining to xanthin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthide', 'n.', 'A compound or derivative of xanthogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthidia', 'pl. ', 'of Xanthidium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthidium', 'n.', 'A genus of minute unicellular algae of the desmids.\n   These algae have a rounded shape and are armed with glochidiate or\n   branched aculei. Several species occur in ditches, and others are found\n   fossil in flint or hornstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthin', 'n.', 'A crystalline nitrogenous body closely related to both\n   uric acid and hypoxanthin, present in muscle tissue, and occasionally\n   found in the urine and in some urinary calculi. It is also present in\n   guano. So called from the yellow color of certain of its salts\n   (nitrates).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthin', 'n.', 'A yellow insoluble coloring matter extracted from yellow\n   flowers; specifically, the coloring matter of madder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthin', 'n.', 'One of the gaseous or volatile decomposition products of\n   the xanthates, and probably identical with carbon disulphide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthinine', 'n.', 'A complex nitrogenous substance related to urea and\n   uric acid, produced as a white powder; -- so called because it forms\n   yellow salts, and because its solution forms a blue fluorescence like\n   quinine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthium', 'n.', 'A genus of composite plants in which the scales of the\n   involucre are united so as to form a kind of bur; cocklebur; clotbur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xantho-', '', 'A combining form from Gr. xanqo`s yellow; as in\n   xanthocobaltic salts. Used also adjectively in chemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthocarpous', 'a.', 'Having yellow fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthochroi', 'n. pl.', 'A division of the Caucasian races, comprising the\n   lighter-colored members.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthochroic', 'a.', 'Having a yellowish or fair complexion; of or\n   pertaining to the Xanthochroi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthodontous', 'a.', 'Having yellow teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthogen', 'n.', 'The hypothetical radical supposed to be characteristic\n   of xanthic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthogen', 'n.', 'Persulphocyanogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthogenate', 'n.', 'A salt of xanthic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthogenic', 'a.', 'Producing a yellow color or compound; xanthic. See\n   Xanthic acid, under Xanthic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthoma', 'n.', 'A skin disease marked by the development or irregular\n   yellowish patches upon the skin, especially upon the eyelids; -- called\n   also xanthelasma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthophane', 'n.', 'The yellow pigment present in the inner segments of\n   the retina in animals. See Chromophane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthophyll', 'n.', 'A yellow coloring matter found in yellow autumn\n   leaves, and also produced artificially from chlorophyll; -- formerly\n   called also phylloxanthin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthopous', 'a.', 'Having a yellow stipe, or stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthoproteic', 'a.', 'Pertaining to, or derived from, xanthoprotein;\n   showing the characters of xanthoprotein; as, xanthoproteic acid; the\n   xanthoproteic reaction for albumin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthoprotein', 'n.', 'A yellow acid substance formed by the action of hot\n   nitric acid on albuminous or proteid matter. It is changed to a deep\n   orange-yellow color by the addition of ammonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthopuccine', 'n.', 'One of three alkaloids found in the root of the\n   yellow puccoon (Hydrastis Canadensis). It is a yellow crystalline\n   substance, and resembles berberine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthorhamnin', 'n.', 'A glucoside extracted from Persian berries as a\n   yellow crystalline powder, used as a dyestuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthorhiza', 'n.', 'A genus of shrubby ranunculaceous plants of North\n   America, including only the species Xanthorhiza apiifolia, which has\n   roots of a deep yellow color; yellowroot. The bark is intensely bitter,\n   and is sometimes used as a tonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthorhoea', 'n.', 'A genus of endogenous plants, native to Australia,\n   having a thick, sometimes arborescent, stem, and long grasslike leaves.\n   See Grass tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthose', 'n.', 'An orange-yellow substance found in pigment spots of\n   certain crabs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthosis', 'n.', 'The yellow discoloration often observed in cancerous\n   tumors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthospermous', 'a.', 'Having yellow seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthous', 'a.', 'Yellow; specifically (Ethnol.), of or pertaining to those\n   races of man which have yellowish, red, auburn, or brown hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthoxylene', 'n.', 'A liquid hydrocarbon of the terpene series extracted\n   from the seeds of a Japanese prickly ash (Xanthoxylum pipertium) as an\n   aromatic oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xanthoxylum', 'n.', 'A genus of prickly shrubs or small trees, the bark and\n   rots of which are of a deep yellow color; prickly ash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xebec', 'n.', 'A small three-masted vessel, with projecting bow stern and\n   convex decks, used in the Mediterranean for transporting merchandise,\n   etc. It carries large square sails, or both. Xebecs were formerly armed\n   and used by corsairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xeme', 'n.', 'An Arctic fork-tailed gull (Xema Sabinii).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xenelasia', 'n.', 'A Spartan institution which prohibited strangers from\n   residing in Sparta without permission, its object probably being to\n   preserve the national simplicity of manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xenia', 'pl. ', 'of Xenium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xenium', 'n.', 'A present given to a guest or stranger, or to a foreign\n   ambassador.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xenodochium', 'n.', 'A house for the reception of strangers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xenodochium', 'n.', 'In the Middle Ages, a room in a monastery for the\n   reception and entertainment of strangers and pilgrims, and for the\n   relief of paupers. [Called also Xenodocheion.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xenodochy', 'n.', 'Reception of strangers; hospitality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xenogamy', 'n.', 'Cross fertilization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xenogenesis', 'n.', 'Same as Heterogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xenogenesis', 'n.', 'The fancied production of an organism of one kind by\n   an organism of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xenogenetic', 'a.', 'Of or pertaining to xenogenesis; as, the xenogenetic\n   origin of microzymes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xenomania', 'n.', 'A mania for, or an inordinate attachment to, foreign\n   customs, institutions, manners, fashions, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xenomi', 'n. pl.', 'A suborder of soft-rayed fresh-water fishes of which\n   the blackfish of Alaska (Dallia pectoralis) is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xenopterygii', 'n. pl.', 'A suborder of fishes including Gobiesox and\n   allied genera. These fishes have soft-rayed fins, and a ventral sucker\n   supported in front by the pectoral fins. They are destitute of scales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xenotime', 'n.', 'A native phosphate of yttrium occurring in\n   yellowish-brown tetragonal crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xenurine', 'n.', 'A cabassou.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xenyl', 'n.', 'The radical characteristic of xenylic compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xenylic', 'a.', 'Pertaining to, derived from, designating, certain amido\n   compounds obtained by reducing certain nitro derivatives of diphenyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xeraphim', 'n.', 'An old money of account in Bombay, equal to three fifths\n   of a rupee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xeres', 'n.', 'Sherry. See Sherry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xerif', 'n.', 'A shereef.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xeriff', 'n.', 'A gold coin formerly current in Egypt and Turkey, of the\n   value of about 9s. 6d., or about $2.30; -- also, in Morocco, a ducat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xeroderma', 'n.', 'Ichthyosis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xeroderma', 'n.', 'A skin disease characterized by the presence of numerous\n   small pigmented spots resembling freckles, with which are subsequently\n   mingled spots of atrophied skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xeronate', 'n.', 'A salt of xeronic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xeronic', 'a.', 'Pertaining to, or designating, an acid, C8H12O4, related\n   to fumaric acid, and obtained from citraconic acid as an oily substance\n   having a bittersweet taste; -- so called from its tendency to form its\n   anhydride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xerophagy', 'n.', 'Among the primitive Christians, the living on a diet of\n   dry food in Lent and on other fasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xerophilous', 'a.', 'Drought-loving; able withstand the absence or lack of\n   moisture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xerophthalmia', 'n.', 'An abnormal dryness of the eyeball produced usually\n   by long-continued inflammation and subsequent atrophy of the\n   conjunctiva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xerophthalmy', 'n.', 'Xerophthalmia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xiphias', 'n.', 'A genus of fishes comprising the common swordfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xiphias', 'n.', 'The constellation Dorado.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xiphias', 'n.', 'A comet shaped like a sword');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xiphidium', 'n.', 'A genus of plants of the order Haemodraceae, having\n   two-ranked, sword-shaped leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xiphioid', 'a.', 'Of, pertaining to, or resembling, a cetacean of the genus\n   Xiphius or family Xiphiidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xiphiplastra', 'pl. ', 'of Xiphiplastron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xiphiplastron', 'n.', 'The posterior, or fourth, lateral plate in the\n   plastron of turtles; -- called also xiphisternum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xiphisterna', 'pl. ', 'of Xiphisternum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xiphisternum', 'n.', 'The posterior segment, or extremity, of the sternum;\n   -- sometimes called metasternum, ensiform cartilage, ensiform process,\n   or xiphoid process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xiphisternum', 'n.', 'The xiphiplastron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xiphius', 'n.', 'A genus of cetaceans having a long, pointed, bony beak,\n   usually two tusklike teeth in the lower jaw, but no teeth in the upper\n   jaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xiphodon', 'n.', 'An extinct genus of artiodactylous mammals found in the\n   European Tertiary formations. It had slender legs, didactylous feet,\n   and small canine teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xiphoid', 'a.', 'Like a sword; ensiform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xiphoid', 'a.', 'Of or pertaining to the xiphoid process; xiphoidian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xiphoidian', 'a.', 'Xiphoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xiphophyllous', 'a.', 'Having sword-shaped leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xiphosura', 'n. pl.', 'See Xiphura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xiphura', 'n. pl.', 'Same as Limuloidea. Called also Xiphosura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylamide', 'n.', 'An acid amide derivative of xylic acid, obtained as a\n   white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylanthrax', 'n.', 'Wood coal, or charcoal; -- so called in distinction\n   from mineral coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylate', 'n.', 'A salt of xylic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylem', 'n.', 'That portion of a fibrovascular bundle which has developed,\n   or will develop, into wood cells; -- distinguished from phloem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylene', 'n.', 'Any of a group of three metameric hydrocarbons of the\n   aromatic series, found in coal and wood tar, and so named because found\n   in crude wood spirit. They are colorless, oily, inflammable liquids,\n   C6H4.(CH3)2, being dimethyl benzenes, and are called respectively\n   orthoxylene, metaxylene, and paraxylene. Called also xylol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylenol', 'n.', 'Any one of six metameric phenol derivatives of xylene,\n   obtained as crystalline substances, (CH3)2.C6H3.OH.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xyletic', 'a.', 'Pertaining to, or designating, a complex acid related to\n   mesitylenic acid, obtained as a white crystalline substance by the\n   action of sodium and carbon dioxide on crude xylenol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylic', 'a.', 'Pertaining to, derived from, or related to, xylene;\n   specifically, designating any one of several metameric acids produced\n   by the partial oxidation of mesitylene and pseudo-cumene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylidic', 'a.', 'Pertaining to, or designating, either one of two distinct\n   acids which are derived from xylic acid and related compounds, and are\n   metameric with uvitic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylidine', 'n.', 'Any one of six metameric hydrocarbons, (CH3)2.C6H3.NH2,\n   resembling aniline, and related to xylene. They are liquids, or easily\n   fusible crystalline substances, of which three are derived from\n   metaxylene, two from orthoxylene, and one from paraxylene. They are\n   called the amido xylenes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylindein', 'n.', 'A green or blue pigment produced by Peziza in certain\n   kinds of decayed wood, as the beech, oak, birch, etc., and extracted as\n   an amorphous powder resembling indigo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylite', 'n.', 'A liquid hydrocarbon found in crude wood spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylitone', 'n.', 'A yellow oil having a geraniumlike odor, produced as a\n   side product in making phorone; -- called also xylite oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylo-', '', 'A combining form from Gr. xy`lon wood; as in xylogen,\n   xylograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylobalsamum', 'n.', 'The dried twigs of a Syrian tree (Balsamodendron\n   Gileadense).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylocarpous', 'a.', 'Bearing fruit which becomes hard or woody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylocopa', 'n.', 'A genus of hymenopterous insects including the carpenter.\n   See Carpenter bee, under Carpenter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylogen', 'n.', 'Nascent wood; wood cells in a forming state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylogen', 'n.', 'Lignin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylograph', 'n.', 'An engraving on wood, or the impression from such an\n   engraving; a print by xylography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylographer', 'n.', 'One who practices xylography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylographic', 'a.', 'Alt. of Xylographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylographical', 'a.', 'Of or pertaining to xylography, or wood engraving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylography', 'n.', 'The art of engraving on wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylography', 'n.', 'The art of making prints from the natural grain of\n   wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylography', 'n.', 'A method pf printing in colors upon wood for purposes\n   of house decoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xyloid', 'a.', 'Resembling wood; having the nature of wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xyloidin', 'n.', 'A substance resembling pyroxylin, obtained by the action\n   of nitric acid on starch; -- called also nitramidin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylol', 'n.', 'Same as Xylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylonite', 'n.', 'See Zylonite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylophaga', 'n.', 'A genus of marine bivalves which bore holes in wood.\n   They are allied to Pholas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylophagan', 'n.', 'One of a tribe of beetles whose larvae bore or live in\n   wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylophagan', 'n.', 'Any species of Xylophaga.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylophagan', 'n.', 'Any one of the Xylophagides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylophagides', 'n. pl.', 'A tribe or family of dipterous flies whose larvae\n   live in decayed wood. Some of the tropical species are very large.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylophagous', 'a.', 'Eating, boring in, or destroying, wood; -- said\n   especially of certain insect larvae, crustaceans, and mollusks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylophagous', 'a.', 'Of or pertaining to the genus Xylophaga.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylophilan', 'n.', 'One of a tribe of beetles (Xylophili) whose larvae live\n   on decayed wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylophilous', 'a.', 'Of or pertaining to the xylophilans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylophone', 'n.', 'An instrument common among the Russians, Poles, and\n   Tartars, consisting of a series of strips of wood or glass graduated in\n   length to the musical scale, resting on belts of straw, and struck with\n   two small hammers. Called in Germany strohfiedel, or straw fiddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylophone', 'n.', 'An instrument to determine the vibrative properties of\n   different kinds of wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xyloplastic', 'a.', 'Formed of wood pulp by molds; relating to casts made\n   of wood pulp in molds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylopyrography', 'n.', 'The art or practice of burning pictures on wood\n   with a hot iron; -- called also poker painting. See Poker picture,\n   under Poker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xyloquinone', 'n.', 'Any one of a group of quinone compounds obtained\n   respectively by the oxidation of certain xylidine compounds. In general\n   they are yellow crystalline substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylorcin', 'n.', 'A derivative of xylene obtained as a white crystalline\n   substance which on exposure in the air becomes red; -- called also\n   betaorcin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylostein', 'n.', 'A glucoside found in the poisonous berries of a species\n   of honeysuckle (Lonicera xylosteum), and extracted as a bitter, white,\n   crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylotile', 'n.', 'Same as Parkesine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylotrya', 'n.', 'A genus of marine bivalves closely allied to Teredo, and\n   equally destructive to timber. One species (Xylotrya fimbriata) is very\n   common on the Atlantic coast of the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylyl', 'n.', 'Any one of three metameric radicals which are characteristic\n   respectively of the three xylenes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xylylene', 'n.', 'Any one of three metameric radicals, CH2.C6H4.CH2,\n   derived respectively from the three xylenes. Often used adjectively;\n   as, xylylene alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xyridaceous', 'a.', 'Of or pertaining to a natural order (Xyrideae) of\n   endogenous plants, of which Xyris is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xyris', 'n.', 'A genus of endogenous herbs with grassy leaves and small\n   yellow flowers in short, scaly-bracted spikes; yellow-eyed grass. There\n   are about seventeen species in the Atlantic United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xyst', 'n.', 'Alt. of Xystus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xystus', 'n.', 'A long and open portico, for athletic exercises, as\n   wrestling, running, etc., for use in winter or in stormy weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xystarch', 'n.', 'An office/ having the superintendence of the xyst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Xyster', 'n.', 'An instrument for scraping bones.\n   Y () Y, the twenty-fifth letter of the English alphabet, at the\n   beginning of a word or syllable, except when a prefix (see Y-), is\n   usually a fricative vocal consonant; as a prefix, and usually in the\n   middle or at the end of a syllable, it is a vowel. See Guide to\n   Pronunciation, // 145, 178-9, 272.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ys', 'pl. ', 'of Y');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ys', 'pl. ', 'of Y');
  INSERT INTO words (word, word_type, description) VALUES  (    'Y', 'n.', 'Something shaped like the letter Y; a forked piece resembling in\n   form the letter Y.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Y', 'n.', 'One of the forked holders for supporting the telescope of a\n   leveling instrument, or the axis of a theodolite; a wye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Y', 'n.', 'A forked or bifurcated pipe fitting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Y', 'n.', 'A portion of track consisting of two diverging tracks connected\n   by a cross track.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Y', 'pron.', 'I.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Y-', '', 'Alt. of I-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Y-', '', 'A prefix of obscure meaning, originally used with verbs, adverbs,\n   adjectives, nouns, and pronouns. In the Middle English period, it was\n   little employed except with verbs, being chiefly used with past\n   participles, though occasionally with the infinitive Ycleped, or\n   yclept, is perhaps the only word not entirely obsolete which shows this\n   use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ya', 'adv.', 'Yea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yacare', 'n.', 'A South American crocodilian (Jacare sclerops) resembling\n   the alligator in size and habits. The eye orbits are connected\n   together, and surrounded by prominent bony ridges. Called also\n   spectacled alligator, and spectacled cayman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yacca', 'n.', 'A West Indian name for two large timber trees (Podocarpus\n   coriaceus, and P. Purdicanus) of the Yew family. The wood, which is\n   much used, is pale brownish with darker streaks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yacht', 'n.', 'A light and elegantly furnished vessel, used either for\n   private parties of pleasure, or as a vessel of state to convey\n   distinguished persons from one place to another; a seagoing vessel used\n   only for pleasure trips, racing, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yacht', 'v. i.', 'To manage a yacht; to voyage in a yacht.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yachter', 'n.', 'One engaged in sailing a jacht.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yachting', 'n.', 'Sailing for pleasure in a yacht.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yachtman', 'n.', 'See Yachtsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yachtsmen', 'pl. ', 'of Yachtsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yachtsman', 'n.', 'One who owns or sails a yacht; a yachter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yaf', 'imp.', 'Gave. See Give.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yaffingale', 'n.', 'The yaffle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yaffle', 'n.', 'The European green woodpecker (Picus, / Genius, viridis).\n   It is noted for its loud laughlike note. Called also eccle, hewhole,\n   highhoe, laughing bird, popinjay, rain bird, yaffil, yaffler,\n   yaffingale, yappingale, yackel, and woodhack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yager', 'n.', 'In the German army, one belonging to a body of light\n   infantry armed with rifles, resembling the chasseur of the French army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yaguarundi', 'n.', 'Same as Jaguarondi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yajur-Veda', 'n.', 'See Veda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yak', 'n.', 'A bovine mammal (Poephagus grunnies) native of the high plains\n   of Central Asia. Its neck, the outer side of its legs, and its flanks,\n   are covered with long, flowing, fine hair. Its tail is long and bushy,\n   often white, and is valued as an ornament and for other purposes in\n   India and China. There are several domesticated varieties, some of\n   which lack the mane and the long hair on the flanks. Called also chauri\n   gua, grunting cow, grunting ox, sarlac, sarlik, and sarluc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yakamilk', 'n.', 'See Trumpeter, 3 (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yakare', 'n.', 'Same as Yacare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yakin', 'n.', 'A large Asiatic antelope (Budorcas taxicolor) native of the\n   higher parts of the Himalayas and other lofty mountains. Its head and\n   neck resemble those of the ox, and its tail is like that of the goat.\n   Called also budorcas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yakoots', 'n. pl.', '(Ethnol.) A nomadic Mongolian tribe native of Northern\n   Siberia, and supposed to be of Turkish stock. They are mainly pastoral\n   in their habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yaksha', 'n.', 'A kind of demigod attendant on Kuvera, the god of wealth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yalah', 'n.', 'The oil of the mahwa tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yam', 'n.', 'A large, esculent, farinaceous tuber of various climbing\n   plants of the genus Dioscorea; also, the plants themselves. Mostly\n   natives of warm climates. The plants have netted-veined, petioled\n   leaves, and pods with three broad wings. The commonest species is D.\n   sativa, but several others are cultivated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yama', 'n.', 'The king of the infernal regions, corresponding to the Greek\n   Pluto, and also the judge of departed souls. In later times he is more\n   exclusively considered the dire judge of all, and the tormentor of the\n   wicked. He is represented as of a green color, with red garments,\n   having a crown on his head, his eyes inflamed, and sitting on a\n   buffalo, with a club and noose in his hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yamma', 'n.', 'The llama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yamp', 'n.', 'An umbelliferous plant (Carum Gairdneri); also, its small\n   fleshy roots, which are eaten by the Indians from Idaho to California.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yang', 'n.', 'The cry of the wild goose; a honk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yang', 'v. i.', 'To make the cry of the wild goose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yank', 'n.', 'A jerk or twitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yanked', 'imp. & p. p.', 'of Yank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yanking', 'p. pr. & vb. n.', 'of Yank');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yank', 'v. t.', 'To twitch; to jerk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yank', 'n.', 'An abbreviation of Yankee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yankee', 'n.', 'A nickname for a native or citizen of New England,\n   especially one descended from old New England stock; by extension, an\n   inhabitant of the Northern States as distinguished from a Southerner;\n   also, applied sometimes by foreigners to any inhabitant of the United\n   States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yankee', 'a.', 'Of or pertaining to a Yankee; characteristic of the\n   Yankees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yankee-Doodle', 'n.', 'The name of a tune adopted popularly as one of the\n   national airs of the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yankee-Doodle', 'n.', 'Humorously, a Yankee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yankeeism', 'n.', 'A Yankee idiom, word, custom, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yaourt', 'n.', 'A fermented drink, or milk beer, made by the Turks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yap', 'v. i.', 'To bark; to yelp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yap', 'n.', 'A bark; a yelp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yapock', 'n.', 'A South American aquatic opossum (Chironectes variegatus)\n   found in Guiana and Brazil. Its hind feet are webbed, and its fore feet\n   do not have an opposable thumb for climbing. Called also water opossum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yapon', 'n.', 'Same as Yaupon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yarage', 'a.', 'The power of moving, or being managed, at sea; -- said with\n   reference to a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yard', 'v. i.', 'A rod; a stick; a staff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yard', 'v. i.', 'A branch; a twig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yard', 'v. i.', 'A long piece of timber, as a rafter, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yard', 'v. i.', 'A measure of length, equaling three feet, or thirty-six\n   inches, being the standard of English and American measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yard', 'v. i.', 'The penis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yard', 'v. i.', 'A long piece of timber, nearly cylindrical, tapering\n   toward the ends, and designed to support and extend a square sail. A\n   yard is usually hung by the center to the mast. See Illust. of Ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yard', 'n.', 'An inclosure; usually, a small inclosed place in front of, or\n   around, a house or barn; as, a courtyard; a cowyard; a barnyard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yard', 'n.', 'An inclosure within which any work or business is carried on;\n   as, a dockyard; a shipyard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yard', 'v. t.', 'To confine (cattle) to the yard; to shut up, or keep, in a\n   yard; as, to yard cows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yardarm', 'n.', 'Either half of a square-rigged vessels yard, from the\n   center or mast to the end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yardfuls', 'pl. ', 'of Yardful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yardful', 'n.', 'As much as a yard will contain; enough to fill a yard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yardland', 'n.', 'A measure of land of uncertain quantity, varying from\n   fifteen to forty acres; a virgate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yardstick', 'n.', 'A stick three feet, or a yard, in length, used as a\n   measure of cloth, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yardwand', 'n.', 'A yardstick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yare', 'n.', 'Ready; dexterous; eager; lively; quick to move.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yare', 'adv.', 'Soon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yarely', 'adv.', 'In a yare manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yark', 'v. t. & i.', 'To yerk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yarke', 'n.', 'Same as Saki.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yarn', 'n.', 'Spun wool; woolen thread; also, thread of other material, as\n   of cotton, flax, hemp, or silk; material spun and prepared for use in\n   weaving, knitting, manufacturing sewing thread, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yarn', 'n.', 'One of the threads of which the strands of a rope are\n   composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yarn', 'n.', 'A story told by a sailor for the amusement of his companions;\n   a story or tale; as, to spin a yarn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yarnen', 'a.', 'Made of yarn; consisting of yarn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yarnut', 'n.', 'See Yernut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yarr', 'v. i.', 'To growl or snarl as a dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yarrish', 'a.', 'Having a rough, dry taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yarrow', 'n.', 'An American and European composite plant (Achillea\n   Millefolium) with very finely dissected leaves and small white corymbed\n   flowers. It has a strong, and somewhat aromatic, odor and taste, and is\n   sometimes used in making beer, or is dried for smoking. Called also\n   milfoil, and nosebleed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yarwhip', 'n.', 'The European bar-tailed godwit; -- called also yardkeep,\n   and yarwhelp. See Godwit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yataghan', 'n.', 'A long knife, or short saber, common among Mohammedan\n   nations, usually having a double curve, sometimes nearly straight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yate', 'n.', 'A gate. See 1st Gate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yaud', 'n.', 'See Yawd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yaul', 'n.', 'See Yawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yaulp', 'v. i.', 'To yaup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yaup', 'v. i.', 'To cry out like a child; to yelp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yaup', 'n.', 'A cry of distress, rage, or the like, as the cry of a sickly\n   bird, or of a child in pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yaup', 'n.', 'The blue titmouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yauper', 'n.', 'One who, or that which, yaups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yaupon', 'n.', 'A shrub (Ilex Cassine) of the Holly family, native from\n   Virginia to Florida. The smooth elliptical leaves are used as a\n   substitute for tea, and were formerly used in preparing the black drink\n   of the Indians of North Carolina. Called also South-Sea tea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yawed', 'imp. & p. p.', 'of Yaw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yawing', 'p. pr. & vb. n.', 'of Yaw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yaw', 'v. i.', 'To rise in blisters, breaking in white froth, as cane juice\n   in the clarifiers in sugar works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yaw', 'v. i. & t.', 'To steer wild, or out of the line of her course; to\n   deviate from her course, as when struck by a heavy sea; -- said of a\n   ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yaw', 'n.', 'A movement of a vessel by which she temporarily alters her\n   course; a deviation from a straight course in steering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yawd', 'n.', 'A jade; an old horse or mare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yawl', 'n.', 'A small ships boat, usually rowed by four or six oars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yawl', 'v. i.', 'To cry out like a dog or cat; to howl; to yell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yawl-rigged', 'a.', 'Having two masts with fore-and-aft sails, but\n   differing from a schooner in that the after mast is very small, and\n   stepped as far aft as possible. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yawned', 'imp. & p. p.', 'of Yawn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yawning', 'p. pr. & vb. n.', 'of Yawn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yawn', 'v. i.', 'To open the mouth involuntarily through drowsiness,\n   dullness, or fatigue; to gape; to oscitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yawn', 'v. i.', 'To open wide; to gape, as if to allow the entrance or exit\n   of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yawn', 'v. i.', 'To open the mouth, or to gape, through surprise or\n   bewilderment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yawn', 'v. i.', 'To be eager; to desire to swallow anything; to express\n   desire by yawning; as, to yawn for fat livings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yawn', 'n.', 'An involuntary act, excited by drowsiness, etc., consisting\n   of a deep and long inspiration following several successive attempts at\n   inspiration, the mouth, fauces, etc., being wide open.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yawn', 'n.', 'The act of opening wide, or of gaping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yawn', 'n.', 'A chasm, mouth, or passageway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yawningly', 'adv.', 'In a yawning manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yawp', 'v. & n.', 'See Yaup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yaws', 'n.', 'A disease, occurring in the Antilles and in Africa,\n   characterized by yellowish or reddish tumors, of a contagious\n   character, which, in shape and appearance, often resemble currants,\n   strawberries, or raspberries. There are several varieties of this\n   disease, variously known as framboesia, pian, verrugas, and crab-yaws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yaw-weed', 'n.', 'A low, shrubby, rubiaceous plant (Morinda Royoc) growing\n   along the seacoast of the West Indies. It has small, white, odorous\n   flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ybe', 'p. p.', 'Been.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ycleped', 'p. p.', 'Called; named; -- obsolete, except in archaic or\n   humorous writings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ydo', 'p. p.', 'Done.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ydrad', 'p. p.', 'Dreaded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ye', '', 'Alt. of Ye');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ye', '', 'an old method of printing the article the (AS. /e), the \"y\" being\n   used in place of the Anglo-Saxon thorn (/). It is sometimes incorrectly\n   pronounced ye. See The, and Thorn, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yen', 'pl. ', 'of Ye');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ye', 'n.', 'An eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ye', 'pron.', 'The plural of the pronoun of the second person in the\n   nominative case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ye', 'adv.', 'Yea; yes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yea', 'adv.', 'Yes; ay; a word expressing assent, or an affirmative, or an\n   affirmative answer to a question, now superseded by yes. See Yes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yea', 'adv.', 'More than this; not only so, but; -- used to mark the\n   addition of a more specific or more emphatic clause. Cf. Nay, adv., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yea', 'n.', 'An affirmative vote; one who votes in the affirmative; as, a\n   vote by yeas and nays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yead', 'v. i.', 'Properly, a variant of the defective imperfect yode, but\n   sometimes mistaken for a present. See the Note under Yede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeaned', 'imp. & p. p.', 'of Yean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeaning', 'p. pr. & vb. n.', 'of Yean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yean', 'v. t. & i.', 'To bring forth young, as a goat or a sheep; to ean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeanling', 'n.', 'A lamb or a kid; an eanling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Year', 'n.', 'The time of the apparent revolution of the sun trough the\n   ecliptic; the period occupied by the earth in making its revolution\n   around the sun, called the astronomical year; also, a period more or\n   less nearly agreeing with this, adopted by various nations as a measure\n   of time, and called the civil year; as, the common lunar year of 354\n   days, still in use among the Mohammedans; the year of 360 days, etc. In\n   common usage, the year consists of 365 days, and every fourth year\n   (called bissextile, or leap year) of 366 days, a day being added to\n   February on that year, on account of the excess above 365 days (see\n   Bissextile).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Year', 'n.', 'The time in which any planet completes a revolution about the\n   sun; as, the year of Jupiter or of Saturn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Year', 'n.', 'Age, or old age; as, a man in years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeara', 'n.', 'The California poison oak (Rhus diversiloba). See under\n   Poison, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yearbook', 'n.', 'A book published yearly; any annual report or summary of\n   the statistics or facts of a year, designed to be used as a reference\n   book; as, the Congregational Yearbook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yearbook', 'n.', 'A book containing annual reports of cases adjudged in the\n   courts of England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeared', 'a.', 'Containing years; having existed or continued many years;\n   aged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yearling', 'n.', 'An animal one year old, or in the second year of its age;\n   -- applied chiefly to cattle, sheep, and horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yearling', 'a.', 'Being a year old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yearly', 'a.', 'Happening, accruing, or coming every year; annual; as, a\n   yearly income; a yearly feast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yearly', 'a.', 'Lasting a year; as, a yearly plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yearly', 'a.', 'Accomplished in a year; as, the yearly circuit, or\n   revolution, of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yearly', 'adv.', 'Annually; once a year to year; as, blessings yearly\n   bestowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yearned', 'imp. & p. p.', 'of Yearn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yearning', 'p. pr. & vb. n.', 'of Yearn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yearn', 'v. t.', 'To pain; to grieve; to vex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yearn', 'v. i.', 'To be pained or distressed; to grieve; to mourn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yearn', 'v. i. & t.', 'To curdle, as milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yearn', 'v. i.', 'To be filled with longing desire; to be harassed or\n   rendered uneasy with longing, or feeling the want of a thing; to strain\n   with emotions of affection or tenderness; to long; to be eager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yearnful', 'a.', 'Desirous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yearningly', 'adv.', 'With yearning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yearnings', 'n. pl.', 'The maws, or stomachs, of young calves, used as a\n   rennet for curdling milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yearth', 'n.', 'The earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeast', 'n.', 'The foam, or troth (top yeast), or the sediment (bottom\n   yeast), of beer or other in fermentation, which contains the yeast\n   plant or its spores, and under certain conditions produces fermentation\n   in saccharine or farinaceous substances; a preparation used for raising\n   dough for bread or cakes, and making it light and puffy; barm; ferment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeast', 'n.', 'Spume, or foam, of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeast', 'n.', 'A form of fungus which grows as indvidual rounded cells,\n   rather than in a mycelium, and reproduces by budding; esp. members of\n   the orders Endomycetales and Moniliales. Some fungi may grow both as a\n   yeast or as a mycelium, depending on the conditions of growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeast-bitten', 'a.', 'A term used of beer when the froth of the yeast has\n   reentered the body of the beer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeastiness', 'n.', 'The quality or state of being yeasty, or frothy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeasty', 'a.', 'Frothy; foamy; spumy, like yeast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yedding', 'n.', 'The song of a minstrel; hence, any song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yede', 'imp.', 'Went. See Yode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeel', 'n.', 'An eel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeldhall', 'n.', 'Guildhall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeldrin', 'n.', 'Alt. of Yeldrine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeldrine', 'n.', 'The yellow-hammer; -- called also yeldrock, and yoldrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yelk', 'n.', 'Same as Yolk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yelled', 'imp. & p. p.', 'of Yell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yelling', 'p. pr. & vb. n.', 'of Yell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yell', 'v. i.', 'To cry out, or shriek, with a hideous noise; to cry or\n   scream as with agony or horror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yell', 'v. t.', 'To utter or declare with a yell; to proclaim in a loud\n   tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yell', 'n.', 'A sharp, loud, hideous outcry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellow', 'superl.', 'Being of a bright saffronlike color; of the color of\n   gold or brass; having the hue of that part of the rainbow, or of the\n   solar spectrum, which is between the orange and the green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellow', 'n.', 'A bright golden color, reflecting more light than any other\n   except white; the color of that part of the spectrum which is between\n   the orange and green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellow', 'n.', 'A yellow pigment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowed', 'imp. & p. p.', 'of Yellow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowing', 'p. pr. & vb. n.', 'of Yellow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellow', 'v. t.', 'To make yellow; to cause to have a yellow tinge or\n   color; to dye yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellow', 'v. i.', 'To become yellow or yellower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowammer', 'n.', 'See Yellow-hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowbill', 'n.', 'The American scoter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowbird', 'n.', 'The American goldfinch, or thistle bird. See Goldfinch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowbird', 'n.', 'The common yellow warbler; -- called also summer\n   yellowbird. See Illust. of Yellow warbler, under Yellow, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellow-covered', 'a.', 'Covered or bound in yellow paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellow-eyed', 'a.', 'Having yellow eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowfin', 'n.', 'A large squeteague.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowfish', 'n.', 'A rock trout (Pleurogrammus monopterygius) found on the\n   coast of Alaska; -- called also striped fish, and Atka mackerel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellow-golds', 'n.', 'A certain plant, probably the yellow oxeye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowhammer', 'n.', 'A common European finch (Emberiza citrinella). The\n   color of the male is bright yellow on the breast, neck, and sides of\n   the head, with the back yellow and brown, and the top of the head and\n   the tail quills blackish. Called also yellow bunting, scribbling lark,\n   and writing lark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowhammer', 'n.', 'The flicker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowing', 'n.', 'The act or process of making yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowish', 'a.', 'Somewhat yellow; as, amber is of a yellowish color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowlegs', 'n.', 'Any one of several species of long-legged sandpipers of\n   the genus Totanus, in which the legs are bright yellow; -- called also\n   stone snipe, tattler, telltale, yellowshanks; and yellowshins. See\n   Tattler, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowness', 'n.', 'The quality or state of being yellow; as, the\n   yellowness of an orange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowness', 'n.', 'Jealousy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowroot', 'n.', 'Any one of several plants with yellow roots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowroot', 'n.', 'See Xanthorhiza.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowroot', 'n.', 'Same as Orangeroot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellows', 'n.', 'A disease of the bile in horses, cattle, and sheep,\n   causing yellowness of the eyes; jaundice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellows', 'n.', 'A disease of plants, esp. of peach trees, in which the\n   leaves turn to a yellowish color; jeterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellows', 'n.', 'A group of butterflies in which the predominating color is\n   yellow. It includes the common small yellow butterflies. Called also\n   redhorns, and sulphurs. See Sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowseed', 'n.', 'A kind of pepper grass (Lepidium campestre).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowshanks', 'n.', 'Alt. of Yellowshins');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowshins', 'n.', 'See Yellolegs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowtail', 'n.', 'Any one of several species of marine carangoid fishes\n   of the genus Seriola; especially, the large California species (S.\n   dorsalis) which sometimes weighs thirty or forty pounds, and is highly\n   esteemed as a food fish; -- called also cavasina, and white salmon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowtail', 'n.', 'The mademoiselle, or silver perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowtail', 'n.', 'The menhaden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowtail', 'n.', 'The runner, 12.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowtail', 'n.', 'A California rockfish (Sebastodes flavidus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowtail', 'n.', 'The sailors choice (Diplodus rhomboides).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowthroat', 'n.', 'Any one of several species of American ground\n   warblers of the genus Geothlypis, esp. the Maryland yellowthroat (G.\n   trichas), which is a very common species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowtop', 'n.', 'A kind of grass, perhaps a species of Agrostis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowwood', 'n.', 'The wood of any one of several different kinds of\n   trees; also, any one of the trees themselves. Among the trees so called\n   are the Cladrastis tinctoria, an American leguminous tree; the several\n   species of prickly ash (Xanthoxylum); the Australian Flindersia\n   Oxleyana, a tree related to the mahogany; certain South African species\n   of Podocarpus, trees related to the yew; the East Indian Podocarpus\n   latifolia; and the true satinwood (Chloroxylon Swietenia). All these\n   Old World trees furnish valuable timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yellowwort', 'n.', 'A European yellow-flowered, gentianaceous (Chlora\n   perfoliata). The whole plant is intensely bitter, and is sometimes used\n   as a tonic, and also in dyeing yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yelped', 'imp. & p. p.', 'of Yelp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yelping', 'p. pr. & vb. n.', 'of Yelp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yelp', 'v. i.', 'To boast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yelp', 'v. i.', 'To utter a sharp, quick cry, as a hound; to bark shrilly\n   with eagerness, pain, or fear; to yaup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yelp', 'n.', 'A sharp, quick cry; a bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yelper', 'n.', 'An animal that yelps, or makes a yelping noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yelper', 'n.', 'The avocet; -- so called from its sharp, shrill cry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yelper', 'n.', 'The tattler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeman', 'n.', 'A yeoman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yen', 'n.', 'The unit of value and account in Japan. Since Japans adoption\n   of the gold standard, in 1897, the value of the yen has been about 50\n   cents. The yen is equal to 100 sen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yend', 'v. t.', 'To throw; to cast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yenite', 'n.', 'A silicate of iron and lime occurring in black prismatic\n   crystals; -- also called ilvaite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeomen', 'pl. ', 'of Yeoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeoman', 'n.', 'A common man, or one of the commonly of the first or most\n   respectable class; a freeholder; a man free born.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeoman', 'n.', 'A servant; a retainer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeoman', 'n.', 'A yeoman of the guard; also, a member of the yeomanry\n   cavalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeoman', 'n.', 'An interior officer under the boatswain, gunner, or\n   carpenters, charged with the stowage, account, and distribution of the\n   stores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeomanlike', 'a.', 'Resembling, or suitable to, a yeoman; yeomanly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeomanly', 'a.', 'Pertaining to a yeoman; becoming or suitable to, a\n   yeoman; yeomanlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeomanry', 'n.', 'The position or rank of a yeoman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeomanry', 'n.', 'The collective body of yeomen, or freeholders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeomanry', 'n.', 'The yeomanry cavalry.');
