  INSERT INTO words (word, word_type, description) VALUES  (    'Yerba', 'n.', 'An herb; a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yerd', 'n.', 'See 1st & 2d Yard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yerked', 'imp. & p. p.', 'of Yerk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yerking', 'p. pr. & vb. n.', 'of Yerk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yerk', 'v. t.', 'To throw or thrust with a sudden, smart movement; to kick\n   or strike suddenly; to jerk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yerk', 'v. t.', 'To strike or lash with a whip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yerk', 'v. i.', 'To throw out the heels; to kick; to jerk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yerk', 'v. i.', 'To move a quick, jerking motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yerk', 'n.', 'A sudden or quick thrust or motion; a jerk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yern', 'v. i.', 'See 3d Yearn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yern', 'a.', 'Eager; brisk; quick; active.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yerne', 'a.', 'Eagerly; briskly; quickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yernut', 'n.', 'An earthnut, or groundnut. See Groundnut (d).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yerst', 'adv.', 'See Erst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yes', 'adv.', 'Ay; yea; -- a word which expresses affirmation or consent;\n   -- opposed to no.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yest', 'n.', 'See Yeast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yester', 'a.', 'Last; last past; next before; of or pertaining to\n   yesterday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yesterday', 'n.', 'The day last past; the day next before the present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yesterday', 'n.', 'Fig.: A recent time; time not long past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yesterday', 'adv.', 'On the day last past; on the day preceding to-day; as,\n   the affair took place yesterday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yestereve', 'n.', 'Alt. of Yester-evening');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yester-evening', 'n.', 'The evening of yesterday; the evening last past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yestermorn', 'n.', 'Alt. of Yester-morning');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yester-morning', 'n.', 'The morning of yesterday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yestern', 'a.', 'Of or pertaining to yesterday; relating to the day last\n   past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yesternight', 'n.', 'The last night; the night last past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yesternight', 'adv.', 'On the last night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yesternoon', 'n.', 'The noon of yesterday; the noon last past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yesterweek', 'n.', 'The week last past; last week.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yesteryear', 'n.', 'The year last past; last year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yestreen', 'n.', 'Yester-evening; yesternight; last night.');
