  INSERT INTO words (word, word_type, description) VALUES  (    'Yet', 'n.', 'Any one of several species of large marine gastropods\n   belonging to the genus Yetus, or Cymba; a boat shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yet', 'adv.', 'In addition; further; besides; over and above; still.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yet', 'adv.', 'At the same time; by continuance from a former state; still.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yet', 'adv.', 'Up to the present time; thus far; hitherto; until now; --\n   and with the negative, not yet, not up to the present time; not as soon\n   as now; as, Is it time to go? Not yet. See As yet, under As, conj.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yet', 'conj.', 'Before some future time; before the end; eventually; in\n   time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yet', 'conj.', 'Even; -- used emphatically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yet', 'conj.', 'Nevertheless; notwithstanding; however.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeve', 'v. i.', 'To give.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yeven', 'p. p.', 'Given.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yew', 'v. i.', 'See Yaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yew', 'n.', 'An evergreen tree (Taxus baccata) of Europe, allied to the\n   pines, but having a peculiar berrylike fruit instead of a cone. It\n   frequently grows in British churchyards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yew', 'n.', 'The wood of the yew. It is light red in color, compact,\n   fine-grained, and very elastic. It is preferred to all other kinds of\n   wood for bows and whipstocks, the best for these purposes coming from\n   Spain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yew', 'n.', 'A bow for shooting, made of the yew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yew', 'a.', 'Of or pertaining to yew trees; made of the wood of a yew tree;\n   as, a yew whipstock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yewen', 'a.', 'Made of yew; as, yewen bows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yex', 'v. i.', 'To hiccough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yex', 'v. i.', 'A hiccough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yezdegerdian', 'a.', 'Of or pertaining to Yezdegerd, the last Sassanian\n   monarch of Persia, who was overthrown by the Mohammedans; as, the\n   Yezdegerdian era, which began on the 16th of June, a. d. 632. The era\n   is still used by the Parsees.');
