  INSERT INTO words (word, word_type, description) VALUES  (    'Yghe', 'n.', 'Eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ygo', 'p. p.', 'Gone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yground', '', 'p. p. of Grind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yholde', '', 'p. p. of Hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yielded', 'imp. & p. p.', 'of Yield');
