  INSERT INTO words (word, word_type, description) VALUES  (    'Yield', 'v. t.', 'To give in return for labor expended; to produce, as\n   payment or interest on what is expended or invested; to pay; as, money\n   at interest yields six or seven per cent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yield', 'v. t.', 'To furnish; to afford; to render; to give forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yield', 'v. t.', 'To give up, as something that is claimed or demanded; to\n   make over to one who has a claim or right; to resign; to surrender; to\n   relinquish; as a city, an opinion, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yield', 'v. t.', 'To admit to be true; to concede; to allow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yield', 'v. t.', 'To permit; to grant; as, to yield passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yield', 'v. t.', 'To give a reward to; to bless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yield', 'v. i.', 'To give up the contest; to submit; to surrender; to\n   succumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yield', 'v. i.', 'To comply with; to assent; as, I yielded to his request.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yield', 'v. i.', 'To give way; to cease opposition; to be no longer a\n   hindrance or an obstacle; as, men readily yield to the current of\n   opinion, or to customs; the door yielded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yield', 'v. i.', 'To give place, as inferior in rank or excellence; as,\n   they will yield to us in nothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yield', 'n.', 'Amount yielded; product; -- applied especially to products\n   resulting from growth or cultivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yieldable', 'a.', 'Disposed to yield or comply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yieldance', 'n.', 'The act of producing; yield; as, the yieldance of the\n   earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yieldance', 'n.', 'The act of yielding; concession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yielder', 'n.', 'One who yields.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yielding', 'a.', 'Inclined to give way, or comply; flexible; compliant;\n   accommodating; as, a yielding temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yieldless', 'a.', 'Without yielding; unyielding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yift', 'n.', 'Gift.');
