  INSERT INTO words (word, word_type, description) VALUES  (    'Yodle', 'v. t. & i.', 'To sing in a manner common among the Swiss and\n   Tyrolese mountaineers, by suddenly changing from the head voice, or\n   falsetto, to the chest voice, and the contrary; to warble.');
