  INSERT INTO words (word, word_type, description) VALUES  (    'Yodler', 'n.', 'One who yodels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoga', 'n.', 'A species of asceticism among the Hindoos, which consists in\n   a complete abstraction from all worldly objects, by which the votary\n   expects to obtain union with the universal spirit, and to acquire\n   superhuman faculties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yogi', 'n.', 'A follower of the yoga philosophy; an ascetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoicks', 'interj.', 'A cry of encouragement to foxhounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoit', 'n.', 'The European yellow-hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yojan', 'n.', 'A measure of distance, varying from four to ten miles, but\n   usually about five.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoke', 'n.', 'A bar or frame of wood by which two oxen are joined at the\n   heads or necks for working together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoke', 'n.', 'A frame or piece resembling a yoke, as in use or shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoke', 'n.', 'A frame of wood fitted to a persons shoulders for carrying\n   pails, etc., suspended on each side; as, a milkmaids yoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoke', 'n.', 'A frame worn on the neck of an animal, as a cow, a pig, a\n   goose, to prevent passage through a fence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoke', 'n.', 'A frame or convex piece by which a bell is hung for ringing\n   it. See Illust. of Bell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoke', 'n.', 'A crosspiece upon the head of a boats rudder. To its ends\n   lines are attached which lead forward so that the boat can be steered\n   from amidships.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoke', 'n.', 'A bent crosspiece connecting two other parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoke', 'n.', 'A tie securing two timbers together, not used for part of a\n   regular truss, but serving a temporary purpose, as to provide against\n   unusual strain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoke', 'n.', 'A band shaped to fit the shoulders or the hips, and joined to\n   the upper full edge of the waist or the skirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoke', 'n.', 'Fig.: That which connects or binds; a chain; a link; a bond\n   connection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoke', 'n.', 'A mark of servitude; hence, servitude; slavery; bondage;\n   service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoke', 'n.', 'Two animals yoked together; a couple; a pair that work\n   together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoke', 'n.', 'The quantity of land plowed in a day by a yoke of oxen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoke', 'n.', 'A portion of the working day; as, to work two yokes, that is,\n   to work both portions of the day, or morning and afternoon.');
